(window.webpackJsonp=window.webpackJsonp||[]).push([[1695],{1718:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e5d\u8282 \u63a5\u8fd1\u4e8e",content:"<article><h2>\u7b2c\u5341\u4e5d\u8282 \u63a5\u8fd1\u4e8e</h2><p>\u743c\u6069\u6ca1\u6709\u56de\u7b54\uff0c\u76f4\u63a5\u5c06\u5979\u7684\u8eab\u4f53\u6273\u8fc7\u6765\uff0c\u9762\u5bf9\u81ea\u5df1\uff0c\u4eb2\u543b\u5979\u7684\u8138\u988a\u548c\u5634\u5507\uff0c\u820c\u5c16\u8f7b\u8f7b\u64ac\u5f00\u7259\u5173\uff0c\u63a2\u5165\u5c11\u5973\u53e3\u8154\uff0c\u6885\u83f2\u65af\u4e0b\u610f\u8bc6\u5730\u4f38\u51fa\u53cc\u81c2\u62b1\u7740\u4ed6\u7684\u8116\u9888\uff0c\u4e56\u4e56\u5410\u51fa\u9999\u820c\u8fce\u5408\u7740\uff0c\u539f\u672c\u521a\u521a\u6709\u4e9b\u6e05\u9192\u7684\u795e\u667a\u53c8\u9677\u5165\u8ff7\u9189\u4e4b\u4e2d\u3002\u4e24\u74e3\u67d4\u6ed1\u820c\u5c16\u50cf\u6e38\u9c7c\u822c\u7ea0\u7f20\uff0c\u542e\u5438\uff0c\u5f7c\u6b64\u54c1\u5c1d\u7740\u5bf9\u65b9\u7684\u6d25\u6db2\uff0c\u8eab\u4f53\u90fd\u6e10\u6e10\u53d8\u5f97\u6eda\u70eb\u8d77\u6765\u3002\u8fc7\u4e86\u5f88\u4e45\uff0c\u4ed6\u4eec\u624d\u7ed3\u675f\u8fd9\u4e2a\u6f2b\u957f\u7684\u543b\uff0c\u604b\u604b\u4e0d\u820d\u5730\u5206\u5f00\u3002\u5c11\u5973\u5b9a\u5b9a\u5730\u770b\u7740\u4ed6\uff0c\u7f8e\u4e3d\u7684\u78a7\u7eff\u53cc\u7738\u4e2d\u6ee1\u662f\u7231\u610f\u3002</p><p>\u201c\u743c\u6069\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u6211\u60f3\u4f60\u3002\u201d</p><p>\u5979\u7761\u68a6\u65b9\u9192\uff0c\u58f0\u97f3\u8f7b\u67d4\u4e2d\u900f\u7740\u4e1d\u4e1d\u6175\u61d2\uff0c\u8138\u988a\u6655\u7ea2\uff0c\u9999\u80a9\u8d64\u88f8\uff0c\u9165\u80f8\u534a\u906e\u534a\u63a9\uff0c\u522b\u6709\u4e00\u79cd\u683c\u5916\u8bf1\u4eba\u7684\u5a9a\u6001\u3002\u743c\u6069\u770b\u5f97\u6b32\u5ff5\u5927\u70bd\uff0c\u4e0b\u4f53\u5df2\u7ecf\u786c\u6da8\u5230\u4e86\u6781\u70b9\uff0c\u5fcd\u4e0d\u4f4f\u5c31\u8981\u4fef\u8eab\u538b\u4e0a\uff0c\u5374\u88ab\u5c11\u5973\u8f7b\u8f7b\u63a8\u5f00\u3002</p><p>\u201c\u4e0d\u8981\uff0c\u8eab\u4e0a\u810f\uff0c\u201d\u5979\u8f7b\u8f7b\u54ac\u7740\u5634\u5507\uff0c\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u201c\u6628\u591c\u56de\u6765\u592a\u665a\u4e86\uff0c\u5077\u61d2\u6ca1\u6d17\u6fa1\u3002\u201d</p><p>\u5973\u5b69\u5b50\u5927\u591a\u90fd\u6709\u6d01\u7656\uff0c\u53ea\u662f\u7a0b\u5ea6\u8f7b\u91cd\u7684\u95ee\u9898\uff0c\u6885\u83f2\u65af\u5e38\u5e74\u5728\u5916\u5954\u6ce2\uff0c\u98ce\u9910\u9732\u5bbf\uff0c\u51fa\u751f\u5165\u6b7b\uff0c\u8fd9\u65b9\u9762\u5012\u8fd8\u4e0d\u7b97\u7279\u522b\u5728\u610f\uff0c\u4f46\u548c\u743c\u6069\u6b22\u597d\u4e4b\u524d\u90a3\u662f\u80af\u5b9a\u8981\u6d17\u6fa1\u7684\u3002\u201c\u90a3\u4e00\u8d77\u53bb\u6d17\u6fa1\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u8d77\u8eab\u4e0b\u5e8a\uff0c\u7136\u540e\u5c06\u5979\u62b1\u8d77\u6765\uff0c\u201c\u6211\u6628\u665a\u4e5f\u5077\u61d2\u4e86\u5462\u3002\u201d</p><p>\u8fd9\u5ea7\u623f\u5b50\u91cc\u6709\u4e09\u95f4\u6d74\u5ba4\uff0c\u5176\u4e2d\u4e00\u95f4\u5c31\u5728\u9694\u58c1\uff0c\u743c\u6069\u62b1\u7740\u6885\u83f2\u65af\u8d70\u8fdb\u53bb\u3002\u6d74\u7f38\u5f88\u5927\uff0c\u91cc\u9762\u5df2\u7ecf\u653e\u6ee1\u4e86\u6e05\u6c34\uff0c\u6885\u83f2\u65af\u793a\u610f\u743c\u6069\u6309\u4e86\u65c1\u8fb9\u4e00\u4e2a\u6309\u94ae\uff0c\u968f\u5373\u4fbf\u770b\u89c1\u65e0\u6570\u6c14\u6ce1\u4ece\u6d74\u7f38\u5e95\u90e8\u4e0d\u65ad\u5347\u8d77\uff0c\u660e\u663e\u662f\u88ab\u5feb\u901f\u52a0\u70ed\u3002\u51e0\u5206\u949f\u540e\uff0c\u4ed6\u4eec\u4fbf\u5df2\u7ecf\u8212\u8212\u670d\u670d\u5730\u6d78\u6ce1\u5728\u70ed\u6c34\u4e2d\uff0c\u65e0\u6570\u73ab\u7470\u82b1\u74e3\u4ece\u5929\u82b1\u677f\u4e0a\u81ea\u52a8\u6d12\u843d\u4e0b\u6765\uff0c\u6f02\u6d6e\u5728\u6c34\u9762\u4e0a\uff0c\u88ab\u70ed\u6c14\u4e00\u84b8\uff0c\u900f\u51fa\u6d53\u90c1\u7684\u9999\u6c14\u3002</p><p>\u201c\u771f\u5962\u4f88\u5462\uff0c\u827e\u5f25\u8587\uff0c\u201d\u743c\u6069\u4eff\u4f5b\u968f\u53e3\u8bf4\uff0c\u201c\u5c45\u7136\u5728\u6d74\u7f38\u4e0a\u6052\u5b9a\u70ed\u7194\u672f\u3002\u201d</p><p>\u70ed\u7194\u672f\u7684\u96be\u5ea6\u5e76\u4e0d\u9ad8\uff0c\u53ea\u662f\u5851\u80fd\u5b66\u6d3e\u7684\u521d\u7ea7\u5165\u95e8\u9b54\u6cd5\uff0c\u53d1\u660e\u8005\u8fbe\u68ee\u662f\u4e00\u4f4d\u4ee5\u70ed\u7231\u7814\u7a76\u201c\u5bb6\u5c45\u9b54\u6cd5\u201d\u800c\u8457\u79f0\u7684\u5927\u5965\u672f\u5e08\u3002\u743c\u6069\u77e5\u9053\u6709\u4e9b\u5deb\u5e08\u5728\u91ce\u5916\u5192\u9669\u7684\u65f6\u5019\u5fd8\u4e86\u643a\u5e26\u706b\u77f3\uff0c\u751a\u81f3\u5c31\u7528\u8fd9\u9053\u6cd5\u672f\u6765\u716e\u5f00\u6c34\u3002\u4f46\u8fd9\u6837\u6ee1\u6ee1\u4e00\u6d74\u7f38\u7684\u51b7\u6c34\uff0c\u51e0\u5206\u949f\u5c31\u52a0\u70ed\u5230\u56db\u4e94\u5341\u5ea6\uff0c\u5c31\u7b97\u662f\u51db\u8fd9\u79cd\u9ad8\u9636\u5851\u80fd\u5e08\u90fd\u672a\u5fc5\u80fd\u529e\u5230\uff0c\u66f4\u522b\u63d0\u8fd8\u6052\u5b9a\u5728\u6d74\u7f38\u4e2d\u3002\u743c\u6069\u8bf4\u201c\u5962\u4f88\u201d\uff0c\u5176\u5b9e\u90fd\u5df2\u7ecf\u662f\u63aa\u8f9e\u5f88\u59d4\u5a49\u4e86\uff0c\u800c\u4e14\u4ed6\u4e5f\u786e\u5b9e\u5f88\u5947\u602a\uff0c\u4ee5\u6885\u83f2\u65af\u7684\u6027\u683c\uff0c\u4e0d\u50cf\u662f\u4f1a\u505a\u8fd9\u79cd\u65e0\u804a\u7684\u4e8b\u60c5\u554a\u3002</p><p>\u201c\u6211\u4e5f\u8fd9\u4e48\u89c9\u5f97\uff0c\u201d\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u5c11\u5973\u70b9\u5934\u8d5e\u540c\uff0c\u201c\u7528\u8d77\u6765\u662f\u5f88\u65b9\u4fbf\uff0c\u4f46\u786e\u5b9e\u592a\u5962\u4f88\u4e86\uff0c\u6211\u4e5f\u4e0d\u592a\u559c\u6b22\uff0c\u4e0d\u8fc7\u51db\u5012\u662f\u633a\u6ee1\u610f\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u4e0d\u662f\u4f60\u5bb6\u5417\uff1f\u201d</p><p>\u201c\u8fd9\u623f\u5b50\u53c8\u4e0d\u662f\u6211\u5efa\u7684\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u672c\u6765\u5c31\u662f\u522b\u4eba\u9001\u7ed9\u6211\u7684\u554a\u3002\u201d</p><p>\u743c\u6069\u8eab\u4f53\u5fae\u5fae\u4e00\u50f5\uff0c\u201c\u662f\u5417\uff1f\u201d\u4ed6\u52c9\u5f3a\u7b11\u7740\uff0c\u201c\u8c01\u8fd9\u4e48\u5927\u65b9\uff1f\u201d</p><p>\u201c\u4e00\u4e2a\u670b\u53cb\u3002\u201d</p><p>\u201c\u54ea\u4e2a\u670b\u53cb\uff1f\u201d\u743c\u6069\u8ffd\u95ee\uff0c\u201c\u7537\u6027\u5973\u6027\uff1f\u201d</p><p>\u4ed6\u7684\u8bed\u6c14\u660e\u663e\u6709\u4e9b\u5f02\u6837\uff0c\u6885\u83f2\u65af\u654f\u9510\u5730\u5bdf\u89c9\u5230\u4e86\uff0c\u5979\u5148\u662f\u6014\u4e86\u6014\uff0c\u968f\u5373\u4fbf\u660e\u767d\u8fc7\u6765\u3002\u201c\u4f60\u731c\u5462\uff1f\u201d\u5979\u5fcd\u4f4f\u7b11\uff0c\u6545\u610f\u53cd\u95ee\u3002</p><p>\u201c\u6211\u731c\u662f\u7537\u6027\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f1a\u9001\u8fd9\u4e48\u6602\u8d35\u7684\u793c\u7269\uff0c\u80af\u5b9a\u662f\u4f60\u7684\u67d0\u4f4d\u8ffd\u6c42\u8005\u5427\u3002\u201d</p><p>\u201c\u4e0d\u544a\u8bc9\u4f60\u3002\u201d\u5c11\u5973\u8bf4\u3002</p><p>\u201c\u5207\uff0c\u4e0d\u544a\u8bc9\u6211\u6211\u4e5f\u80fd\u731c\u5230\uff0c\u662f\u90a3\u4f4d\u5e0c\u6b27\xb7\u5e9e\u7f57\u5148\u751f\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u731c\u662f\u4ed6\u5462\uff1f\u201d</p><p>\u201c\u8fd9\u6709\u4ec0\u4e48\u96be\u731c\u7684\uff0c\u201d\u743c\u6069\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u80fd\u6709\u8fd9\u79cd\u5927\u624b\u7b14\u7684\uff0c\u975e\u5bcc\u5373\u8d35\uff0c\u7edd\u975e\u666e\u901a\u4eba\uff0c\u800c\u5982\u679c\u6211\u6240\u77e5\u4e0d\u9519\uff0c\u5e9e\u7f57\u5bb6\u65cf\u6b63\u662f\u5854\u745f\u8c37\u7684\u540d\u95e8\u4e4b\u4e00\u5427\u3002\u4ed6\u662f\u5e9e\u7f57\u5bb6\u65cf\u7684\u957f\u5b50\uff0c\u53c8\u662f\u4f60\u7684\u670b\u53cb\uff0c\u9664\u4e86\u4ed6\u8fd8\u6709\u8c01\u3002\u201d</p><p>\u6885\u83f2\u65af\u683c\u683c\u7b11\u8d77\u6765\uff0c\u201c\u5ac9\u5992\u4e86\uff1f\u201d</p><p>\u201c\u6709\u70b9\u3002\u201d\u743c\u6069\u627f\u8ba4\u3002</p><p>\u201c\u5c31\u7b97\u662f\u4ed6\u9001\u4e86\u6211\u4e00\u5957\u623f\u5b50\uff0c\u4e5f\u4e0d\u610f\u5473\u7740\u4ec0\u4e48\u5427\u3002\u201d</p><p>\u201c\u662f\u8fd9\u6837\u6ca1\u9519\uff0c\u4f46\u6211\u5fc3\u91cc\u8fd8\u662f\u6709\u70b9\u4e0d\u8212\u670d\u3002\u201d</p><p>\u201c\u771f\u5c0f\u6c14\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u662f\u6211\u5fc3\u7231\u7684\u5973\u4eba\uff0c\u6211\u4e0d\u559c\u6b22\u770b\u5230\u4f60\u548c\u4efb\u4f55\u522b\u7684\u7537\u4eba\u5173\u7cfb\u4eb2\u5bc6\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u7684\u5360\u6709\u6b32\u53ef\u662f\u5f88\u5f3a\u7684\u5462\u3002\u201d</p><p>\u201c\u6211\u4e5f\u4e00\u6837\u3002\u201d\u5c11\u5973\u4f4e\u58f0\u8bf4\u3002</p><p>\u201c\u55ef\uff1f\u201d\u5979\u58f0\u97f3\u592a\u8f7b\uff0c\u743c\u6069\u6ca1\u542c\u6e05\u695a\uff0c\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u201d\u6885\u83f2\u65af\u7b11\u4e86\u7b11\uff0c\u4e3b\u52a8\u8f6c\u79fb\u8bdd\u9898\uff0c\u201c\u4f60\u731c\u9519\u4e86\uff0c\u8fd9\u623f\u5b50\u7684\u539f\u4e3b\u4eba\u662f\u4e2a\u5973\u5b69\u5b50\uff0c\u4e0d\u662f\u5e0c\u6b27\uff0c\u521a\u624d\u6211\u662f\u548c\u4f60\u5f00\u73a9\u7b11\u7684\u3002\u201d</p><p>\u201c\u5973\u5b69\u5b50\uff1f\u201d\u743c\u6069\u677e\u4e86\u53e3\u6c14\uff0c\u597d\u5947\u5fc3\u5374\u662f\u5f53\u771f\u88ab\u63d0\u8d77\u6765\uff0c\u201c\u8c01\u554a\uff0c\u201d\u4ed6\u968f\u5373\u60f3\u8d77\u6628\u5929\u5e0c\u6b27\u534a\u5f00\u73a9\u7b11\u8bf4\u8fc7\u7684\u4e00\u53e5\u8bdd\uff0c\u201c\u4e0d\u4f1a\u771f\u662f\u67d0\u4f4d\u6697\u604b\u4f60\u7684\u5973\u6027\u5427\u3002\u827e\u5f25\u8587\uff0c\u4f60\u7684\u9b45\u529b\u679c\u7136\u662f\u7537\u5973\u901a\u6740\u5462\u3002\u201d</p><p>\u201c\u624d\u4e0d\u662f\uff01\u201d</p><p>\u5c11\u5973\u7684\u8138\u86cb\u4e0a\u6d6e\u8d77\u6de1\u6de1\u7ea2\u6655\uff0c\u7f8e\u4eba\u6d45\u55d4\u8584\u6012\uff0c\u770b\u8d77\u6765\u522b\u6709\u4e00\u756a\u4fcf\u4e3d\uff0c\u201c\u4e0d\u662f\u4e00\u5f00\u59cb\u5c31\u8bf4\u4e86\uff0c\u662f\u6211\u4e00\u4e2a\u670b\u53cb\u3002\u201d</p><p>\u201c\u7537\u670b\u53cb\u8fd8\u662f\u5973\u670b\u53cb\uff1f\u201d\u743c\u6069\u6545\u610f\u9017\u5979\u3002</p><p>\u201c\u53bb\u6b7b\uff01\u201d</p><p>\u5979\u6325\u62f3\u60f3\u6253\u743c\u6069\uff0c\u5374\u88ab\u4e00\u628a\u6402\u4f4f\u4e86\uff0c\u201c\u597d\u5566\u597d\u5566\uff0c\u6211\u77e5\u9053\u5566\uff0c\u662f\u5973\u6027\u670b\u53cb\uff0c\u4e0d\u662f\u5973\u670b\u53cb\uff0c\u201d\u743c\u6069\u7b11\u7740\uff0c\u201c\u5230\u5e95\u662f\u8c01\u554a\u3002\u201d</p><p>\u201c\u8bf4\u4e86\u4f60\u4e5f\u4e0d\u8ba4\u8bc6\uff0c\u95ee\u90a3\u4e48\u591a\u5e72\u561b\u3002\u201d</p><p>\u201c\u597d\u5947\u800c\u5df2\u3002\u201d</p><p>\u201c\u5f53\u7740\u81ea\u5df1\u5973\u670b\u53cb\u7684\u9762\u6253\u542c\u522b\u7684\u5973\u5b69\u5b50\uff0c\u8fd9\u5f88\u8fc7\u5206\u554a\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u53d1\u73b0\u6885\u83f2\u65af\u660e\u663e\u4e0d\u60f3\u591a\u63d0\u90a3\u4e2a\u5973\u5b69\u5b50\uff0c\u743c\u6069\u4e5f\u5c31\u8bc6\u8da3\u5730\u4e0d\u518d\u8ffd\u95ee\uff0c\u5f00\u59cb\u66ff\u5979\u6309\u6469\u80a9\u90e8\u3002\u4f46\u6309\u7740\u6309\u7740\uff0c\u4e0d\u77e5\u4e0d\u89c9\u624b\u5c31\u79fb\u5230\u4e86\u5c11\u5973\u7684\u80f8\u53e3\uff0c\u5c06\u90a3\u4e24\u56e2\u7c89\u5ae9\u63e1\u5728\u638c\u4e2d\uff0c\u8f7b\u8f7b\u63c9\u634f\uff0c\u8f6e\u6d41\u628a\u73a9\u7740\uff0c\u53ea\u89c9\u65e2\u65e0\u6bd4\u67d4\u8f6f\u53c8\u5145\u6ee1\u5f39\u6027\uff0c\u808c\u80a4\u7ec6\u817b\uff0c\u624b\u611f\u6781\u4f73\uff0c\u5176\u7f8e\u5999\u9500\u9b42\u4e4b\u5904\uff0c\u7b80\u76f4\u65e0\u6cd5\u7528\u8bed\u8a00\u63cf\u8ff0\u3002\u5c24\u5176\u5cf0\u9876\u4e0a\u90a3\u4e24\u70b9\u5ae3\u7ea2\uff0c\u5df2\u7ecf\u968f\u7740\u4ed6\u7684\u73a9\u5f04\u800c\u9010\u6e10\u53d8\u5f97\u575a\u633a\uff0c\u50cf\u4e24\u9897\u7ea2\u5b9d\u77f3\u9a84\u50b2\u5730\u7fd8\u7acb\u7740\uff0c\u5438\u5f15\u7740\u743c\u6069\u7684\u76ee\u5149\u3002</p><p>\u4ed6\u4e00\u65f6\u5174\u594b\uff0c\u624b\u4e0a\u529b\u9053\u4fbf\u5931\u4e86\u5206\u5bf8\uff0c\u7a0d\u7a0d\u6709\u4e9b\u91cd\u3002\u5c11\u5973\u5168\u8eab\u4e00\u98a4\uff0c\u201c\u8f7b\u2026\u2026\u8f7b\u70b9\uff0c\u201d\u5979\u54ac\u7740\u5634\u5507\uff0c\u201c\u4f60\u5f04\u75bc\u6211\u4e86\u3002\u201d</p><p>\u201c\u554a\uff0c\u5bf9\u4e0d\u8d77\u3002\u201d</p><p>\u743c\u6069\u9053\u6b49\u7740\uff0c\u4e0b\u610f\u8bc6\u5730\u5c31\u5f85\u653e\u624b\uff0c\u6885\u83f2\u65af\u5374\u6293\u4f4f\u4ed6\u7684\u624b\uff0c\u7ee7\u7eed\u6309\u5728\u81ea\u5df1\u80f8\u53e3\u3002\u201c\u559c\u6b22\u5417\uff1f\u201d\u5979\u6084\u58f0\u95ee\u3002</p><p>\u201c\u559c\u6b22\uff0c\u201d\u743c\u6069\u4eb2\u543b\u7740\u5979\u7684\u8116\u9888\uff0c\u201c\u559c\u6b22\u5f97\u8981\u547d\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u5f53\u7136\u662f\u771f\u7684\u3002\u201d</p><p>\u201c\u90a3\u2026\u2026\u6709\u6ca1\u6709\u89c9\u5f97\u5979\u4eec\u6709\u4ec0\u4e48\u53d8\u5316\uff1f\u201d</p><p>\u53d8\u5316\uff1f</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u63e1\u4f4f\u53c8\u4ed4\u7ec6\u628a\u73a9\u4e86\u7247\u523b\uff0c\u201c\u5514\uff0c\u597d\u50cf\u66f4\u67d4\u8f6f\u4e86\u70b9\uff0c\u4ee5\u524d\u7a0d\u5fae\u6709\u70b9\u7ed3\u5b9e\u3002\u201d</p><p>\u201c\u8fd8\u6709\u5462\u3002\u201d</p><p>\u201c\u8fd8\u6709\u2026\u2026\u597d\u50cf\u66f4\u654f\u611f\u4e86\u4e9b\uff0c\u4ee5\u524d\u53cd\u5e94\u6ca1\u8fd9\u4e48\u5feb\u7684\u3002\u201d</p><p>\u201c\u4e0d\u662f\u95ee\u4f60\u8fd9\u4e2a\uff01\u201d\u5c11\u5973\u6709\u70b9\u4e0d\u9ad8\u5174\uff0c\u201c\u8fd8\u6709\u5462\uff1f\u201d</p><p>\u201c\u8fd8\u6709\u2026\u2026\u597d\u50cf\u5176\u4ed6\u6ca1\u4ec0\u4e48\u53d8\u5316\u4e86\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u5c11\u5973\u5728\u6c89\u9ed8\uff0c\u6012\u6c14\u5728\u9ad8\u6da8\uff0c\u7136\u540e\u5728\u5979\u7684\u6012\u6c14\u503c\u5373\u5c06\u62b5\u8fbe\u9876\u70b9\u7684\u65f6\u5019\uff0c\u743c\u6069\u7ec8\u4e8e\u53cd\u5e94\u8fc7\u6765\u3002\u201c\u554a\uff0c\u5bf9\u4e86\uff0c\u201d\u4ed6\u8d76\u5feb\u8bf4\uff0c\u201c\u76ae\u80a4\u6bd4\u4ee5\u524d\u66f4\u7ec6\u817b\u5149\u6ed1\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u8fd9\u4e2a\u7b28\u86cb\u5927\u8272\u72fc\uff01\u201d\u6885\u83f2\u65af\u7ec8\u4e8e\u66b4\u8d70\u4e86\uff0c\u201c\u96be\u9053\u4f60\u5c31\u6ca1\u53d1\u73b0\u5979\u4eec\u53d8\u5927\u4e86\u5417\uff1f\u201d</p><p>\u8fd9\u4e2a\u5012\u8fd8\u771f\u6ca1\u53d1\u73b0\u3002</p><p>\u743c\u6069\u5fc3\u91cc\u5982\u6b64\u60f3\u7740\uff0c\u5f53\u7136\u5634\u4e0a\u7edd\u5bf9\u4e0d\u80fd\u8fd9\u4e48\u8bf4\u3002\u201c\u554a\uff0c\u5bf9\u4e86\u5bf9\u4e86\uff0c\u786e\u5b9e\u662f\u53d8\u5927\u4e86\u4e00\u4e9b\uff0c\u201d\u4ed6\u8d76\u7d27\u5f25\u8865\u8fc7\u9519\uff0c\u201c\u4f60\u4e0d\u8bf4\u6211\u8fd8\u6ca1\u6ce8\u610f\u2014\u2014\u54e6\uff0c\u4e0d\u4e0d\uff0c\u662f\u6211\u521a\u624d\u73a9\u5f97\u592a\u9ad8\u5174\u4e86\u6ca1\u6ce8\u610f\u2026\u2026\u201d</p><p>\u201c\u54fc\uff01\u201d</p><p>\u5973\u5b69\u5b50\u7684\u80f8\u90e8\u5927\u5c0f\uff0c\u4e25\u91cd\u5173\u7cfb\u5230\u5973\u5b69\u5b50\u7684\u81ea\u5c0a\u548c\u81ea\u4fe1\uff0c\u6885\u83f2\u65af\u8eab\u9ad8\u63a5\u8fd1\u743c\u6069\uff0c\u80f8\u90e8\u5c3a\u5bf8\u5374\u53ea\u6709B\uff0c\u5c5e\u4e8e\u6bd4\u8f83\u504f\u5c0f\u7684\uff0c\u5bf9\u6b64\u4e8b\u672c\u6765\u5c31\u5f88\u654f\u611f\uff0c\u522b\u770b\u5e73\u65f6\u5634\u4e0a\u4e0d\u8bf4\uff0c\u5fc3\u91cc\u8fd8\u662f\u633a\u5728\u4e4e\u7684\u2014\u2014\u5c24\u5176\u662f\u6574\u5929\u770b\u7740\u838e\u73de\u514b\u3001\u51db\u8fd9\u79cd\u6ce2\u6d9b\u6c79\u6d8c\u7684\u7c7b\u578b\u5728\u9762\u524d\u6643\u6765\u6643\u53bb\uff0c\u5fc3\u4e2d\u66f4\u662f\u90c1\u95f7\u3002\u672c\u6765\u81ea\u89c9\u8fd9\u6bb5\u65f6\u95f4\u6709\u6240\u589e\u5927\uff0c\u6ee1\u5fc3\u671f\u76fc\u7740\u743c\u6069\u5938\u5956\uff0c\u7ed3\u679c\u5374\u88ab\u6253\u51fb\u4e86\uff0c\u751f\u6c14\u53ef\u60f3\u800c\u77e5\u3002\u743c\u6069\u610f\u8bc6\u5230\u81ea\u5df1\u95ef\u4e86\u5927\u7978\uff0c\u5fd9\u4e0d\u8fed\u5730\u8d54\u793c\u9053\u6b49\uff0c\u751c\u8a00\u871c\u8bed\uff0c\u54c4\u4e86\u534a\u5929\u603b\u7b97\u624d\u8ba9\u5c11\u5973\u52c9\u5f3a\u6d88\u6c14\u3002</p><p>\u201c\u7b28\u86cb\u5927\u8272\u72fc\uff01\u201d\u5979\u6068\u6068\u5730\u8bf4\uff0c\u201c\u6789\u8d39\u6211\u8fd9\u4e24\u4e2a\u6708\u6765\u5929\u5929\u505a\u7761\u524d\u6309\u6469\uff0c\u4e00\u70b9\u90fd\u4e0d\u9886\u60c5\u3002\u201d</p><p>\u201c\u54ea\u6709\uff0c\u6211\u5f88\u9886\u60c5\uff0c\u975e\u5e38\u9886\u60c5\uff0c\u8d85\u7ea7\u9886\u60c5\u2026\u2026\u5443\uff0c\u4f60\u505a\u7761\u524d\u6309\u6469\uff1f\u81ea\u5df1\u505a\uff1f\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u51db\u5e2e\u6211\u505a\u3002\u201d</p><p>\u201c\u2026\u2026\u90a3\u5bb6\u4f19\u592a\u8fc7\u5206\u4e86\uff01\u201d</p><p>\u8d81\u7740\u81ea\u5df1\u4e0d\u5728\u7684\u673a\u4f1a\uff0c\u51db\u8fd8\u4e0d\u77e5\u9053\u5bf9\u6885\u83f2\u65af\u4f5c\u51fa\u4e86\u591a\u5c11\u8fc7\u5206\u7684\u4e8b\u60c5\uff0c\u4e00\u60f3\u5230\u8fd9\u70b9\u743c\u6069\u5c31\u607c\u706b\u3002\u7136\u800c\u8fc7\u5206\u5f52\u8fc7\u5206\uff0c\u51db\u73b0\u5728\u53c8\u4e0d\u5728\u8eab\u8fb9\uff0c\u60f3\u60e9\u7f5a\u5979\u90fd\u529e\u4e0d\u5230\u3002\u201c\u5582\uff0c\u8bf4\u5b9e\u8bdd\uff0c\u201d\u6885\u83f2\u65af\u78b0\u78b0\u4ed6\uff0c\u201c\u771f\u7684\u6ca1\u6709\u53d8\u5927\uff1f\u201d</p><p>\u5982\u679c\u8bf4\u5b9e\u8bdd\uff0c\u90a3\u786e\u5b9e\u662f\u6ca1\u6709\u53d8\u5927\uff0c\u81f3\u5c11\u743c\u6069\u6ca1\u6709\u611f\u89c9\u51fa\u6765\u2014\u2014\u4f46\u767d\u75f4\u624d\u4f1a\u5728\u8fd9\u79cd\u65f6\u5019\u8bf4\u5b9e\u8bdd\u3002\u201c\u662f\u53d8\u5927\u4e86\u70b9\uff0c\u201d\u4ed6\u5c3d\u91cf\u659f\u914c\u7740\u8bcd\u53e5\uff0c\u8ba9\u81ea\u5df1\u7684\u8bed\u6c14\u542c\u8d77\u6765\u6bd4\u8f83\u8bda\u6073\uff0c\u201c\u4e0d\u662f\u5f88\u660e\u663e\uff0c\u4f46\u4ed4\u7ec6\u611f\u89c9\u8fd8\u662f\u80fd\u611f\u89c9\u5f97\u51fa\u6765\u7684\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u6885\u83f2\u65af\u6709\u70b9\u5931\u671b\uff0c\u6548\u679c\u4e0d\u660e\u663e\u2026\u2026\u4e0d\u8fc7\u81f3\u5c11\u662f\u6709\u6548\u679c\u4e86\uff0c\u8fd8\u662f\u503c\u5f97\u5b89\u6170\u7684\u3002\u201c\u90a3\uff0c\u201d\u5979\u6b32\u8a00\u53c8\u6b62\uff0c\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u5730\u95ee\uff0c\u201c\u743c\u6069\uff0c\u73b0\u5728\u6211\u2026\u2026\u90a3\u91cc\u6709\u591a\u5927\uff1f\u7b97\u4ec0\u4e48\u7ea7\u522b\u7684\uff1f\u201d</p><p>\u201c\u5514\uff0c\u6839\u636e\u6211\u76ee\u6d4b\uff0c\u5927\u6982\u63a5\u8fd1\u4e8eC\u5427\u3002\u201d</p><p>\u6240\u8c13\u63a5\u8fd1\u4e8eC\uff0c\u8fd9\u662f\u4e2a\u5f88\u542b\u7cca\u7684\u6982\u5ff5\uff0c\u53ea\u5dee\u4e00\u70b9\u70b9\u662f\u63a5\u8fd1\uff0c\u5dee\u4e00\u4e9b\u4e5f\u8fd8\u662f\u63a5\u8fd1\uff0c\u6ca1\u6709\u4e00\u4e2a\u660e\u786e\u6807\u51c6\u3002\u5c31\u6885\u83f2\u65af\u76ee\u524d\u7684\u72b6\u51b5\u2026\u2026\u53cd\u6b63\u4e0d\u662f\u53ea\u5dee\u4e00\u70b9\u70b9\u3002</p><p>\u201c\u63a5\u8fd1\u4e8eC\u554a\uff0c\u201d\u5c11\u5973\u70b9\u70b9\u5934\uff0c\u201c\u90a3\u5c31\u53ef\u4ee5\u7b97\u662fC\u5427\uff1f\u201d</p><p>\u201c\u2026\u2026\u5dee\u4e0d\u591a\u5427\u3002\u201d</p><p>\u201c\u90a3\u4e48\uff0c\u50cf\u51db\u90a3\u79cd\u662f\u591a\u5927\uff1f\u201d</p><p>\u201c\u51db\u561b\uff0c\u201d\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u201c\u63a5\u8fd1\u4e8eD\u5427\u3002\u201d</p><p>\u201c\u63a5\u8fd1D\uff0c\u5c31\u662f\u8fd8\u6ca1\u5230D\uff0c\u6240\u4ee5\u662fC\uff0c\u5bf9\u5427\uff1f\u201d</p><p>\u201c\u2026\u2026\u662f\u3002\u201d</p><p>\u201c\u90a3\u4e5f\u5c31\u662f\u8bf4\uff0c\u6211\u548c\u51db\u662f\u540c\u4e00\u4e2a\u7ea7\u522b\u7684\uff0c\u662f\u5427\uff1f\u201d</p><p>\u201c\u4ece\u7406\u8bba\u4e0a\u6765\u8bf4\u2026\u2026\u662f\u8fd9\u6837\u7684\u3002\u201d</p><p>\u5c11\u5973\u6ee1\u610f\u5730\u70b9\u70b9\u5934\uff0c\u504e\u4f9d\u5728\u7537\u4eba\u6000\u91cc\u3002\u201c\u743c\u6069\u3002\u201d\u5979\u8f7b\u58f0\u53eb\u7740\u60c5\u4eba\u7684\u540d\u5b57\u3002</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u73ca\u5609\u59d0\u59d0\uff0c\u201d\u5979\u58f0\u97f3\u4f4e\u4f4e\u5730\u95ee\uff0c\u201c\u5df2\u7ecf\u88ab\u4f60\u6b3a\u8d1f\u4e86\u5427\u3002\u201d</p></article>"}}}]);
