(window.webpackJsonp=window.webpackJsonp||[]).push([[1697],{1720:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e00\u8282 \u53a8\u827a",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e00\u8282 \u53a8\u827a</h2><p>\u5bf9\u4e8e\u6885\u83f2\u65af\u6240\u8bf4\u7684\u793c\u7269\uff0c\u743c\u6069\u9887\u4e3a\u597d\u5947\uff0c\u4f46\u65e0\u8bba\u4ed6\u600e\u4e48\u5a01\u903c\u5229\u8bf1\uff0c\u5c11\u5973\u5c31\u662f\u575a\u51b3\u4e0d\u80af\u5410\u9732\u8be6\u60c5\uff0c\u6700\u540e\u53ea\u597d\u7f62\u4e86\u3002</p><p>\u201c\u771f\u4e0d\u4e56\u5462\uff0c\u201d\u4ed6\u8bf4\uff0c\u4e0d\u8f7b\u4e0d\u91cd\u5730\u5728\u5979\u633a\u7fd8\u7684\u7f8e\u81c0\u4e0a\u62cd\u4e86\u4e00\u5df4\u638c\uff0c\u201c\u5e94\u8be5\u597d\u597d\u60e9\u7f5a\u4e00\u4e0b\u3002\u201d</p><p>\u201c\u600e\u4e48\u60e9\u7f5a\u554a\uff1f\u201d\u6885\u83f2\u65af\u660e\u77e5\u6545\u95ee\u3002</p><p>\u743c\u6069\u6ca1\u6709\u8bf4\u8bdd\uff0c\u800c\u662f\u7528\u5b9e\u9645\u884c\u52a8\u56de\u7b54\u4e86\u8fd9\u4e2a\u95ee\u9898\u3002\u4ed6\u8d77\u8eab\u5750\u5728\u6d74\u7f38\u5bbd\u9614\u7684\u8fb9\u6cbf\u4e0a\uff0c\u671d\u5c11\u5973\u62db\u4e86\u62db\u624b\uff0c\u6885\u83f2\u65af\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u4f46\u8fd8\u662f\u4e56\u5de7\u5730\u8dea\u7740\u722c\u4e86\u8fc7\u6765\uff0c\u4f4e\u4e0b\u5934\uff0c\u5f20\u53e3\u542b\u4f4f\uff0c\u6eda\u70eb\u7684\u4fcf\u8138\u8d34\u7740\u7537\u4eba\u7684\u5c0f\u8179\u548c\u5927\u817f\u6839\u90e8\uff0c\u5356\u529b\u5730\u541e\u5410\u542e\u5438\u3002\u743c\u6069\u6ee1\u610f\u5730\u629a\u6478\u7740\u5979\u7684\u8138\u86cb\uff0c\u624b\u6307\u63d2\u5728\u6e7f\u6f09\u6f09\u7684\u79c0\u53d1\u4e2d\uff0c\u4eab\u53d7\u7740\u5c11\u5973\u6e29\u6696\u6e7f\u6da6\u7684\u53e3\u8154\u3002\u6885\u83f2\u65af\u88ab\u8c03\u6559\u4e86\u4e24\u5e74\uff0c\u7ecf\u9a8c\u53ef\u6bd4\u73ca\u5609\u4e30\u5bcc\u5f97\u591a\uff0c\u6280\u672f\u4e5f\u4e0d\u9519\uff0c\u6ca1\u8fc7\u591a\u4e45\u4fbf\u8ba9\u743c\u6069\u5728\u5979\u7684\u5c0f\u5634\u91cc\u4e00\u6cc4\u5982\u6ce8\u3002</p><p>\u5495\u565c\u5495\u565c\u5730\uff0c\u5979\u5c06\u767d\u6d4a\u7684\u6eda\u70eb\u6db2\u4f53\u5b8c\u5168\u541e\u54bd\u4e0b\u53bb\uff0c\u70b9\u6ef4\u4e0d\u5269\uff0c\u50cf\u662f\u54c1\u5c1d\u4e45\u8fdd\u7684\u9999\u751c\u7f8e\u5473\u3002\u201c\u611f\u89c9\u6bd4\u4ee5\u524d\u5c04\u5f97\u5feb\u4e86\u5462\uff0c\u201d\u5979\u53d6\u7b11\u7740\u743c\u6069\uff0c\u201c\u6301\u4e45\u529b\u53d8\u5dee\u4e86\u554a\u3002\u201d</p><p>\u5bf9\u4e8e\u7537\u4eba\u6765\u8bf4\uff0c\u8fd9\u53ef\u5b9e\u5728\u662f\u6700\u4e25\u91cd\u7684\u6311\u8845\uff0c\u743c\u6069\u5f53\u7136\u4e0d\u80fd\u5bb9\u5fcd\u3002\u5728\u5c11\u5973\u7684\u5c0f\u5634\u91cc\u4f11\u606f\u51e0\u5206\u949f\u540e\uff0c\u4ed6\u518d\u5ea6\u6062\u590d\u4e86\u7cbe\u529b\u3002\u6885\u83f2\u65af\u6309\u7167\u4ed6\u7684\u547d\u4ee4\uff0c\u4e56\u4e56\u8f6c\u8fc7\u8eab\uff0c\u8dea\u5728\u6d74\u7f38\u4e2d\uff0c\u4e0a\u534a\u8eab\u8db4\u4f0f\u5728\u8fb9\u6cbf\uff0c\u53cc\u624b\u4e3b\u52a8\u63b0\u5f00\u4e30\u6ee1\u7684\u81c0\u8089\uff0c\u65b9\u4fbf\u7537\u4eba\u4ece\u80cc\u540e\u53d1\u52a8\u5165\u4fb5\u3002\u5979\u611f\u89c9\u5230\u575a\u786c\u6eda\u70eb\u7684\u6775\u5c16\u6324\u8fdb\u81c0\u6c9f\uff0c\u62b5\u5728\u83ca\u7a74\u5165\u53e3\uff0c\u5c11\u5973\u7684\u8eab\u4f53\u4e0d\u7531\u81ea\u4e3b\u5730\u8f7b\u5fae\u6218\u6817\u8d77\u6765\uff0c\u65e2\u662f\u5bb3\u6015\u53c8\u662f\u671f\u5f85\u3002</p><p>\u201c\u8f7b\u70b9\u3002\u201d\u5979\u4f4e\u58f0\u6073\u6c42\u3002</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u7d27\u63e1\u7740\u5979\u7684\u7ea4\u8170\uff0c\u4e00\u5bf8\u4e00\u5bf8\u5730\u7f13\u7f13\u63a8\u8fdb\uff0c\u6bd5\u7adf\u662f\u5df2\u7ecf\u88ab\u73a9\u5f04\u8fc7\u65e0\u6570\u6b21\uff0c\u867d\u7136\u4ece\u5916\u8868\u4e0a\u770b\u4f9d\u65e7\u7cbe\u81f4\u800c\u7d27\u51d1\uff0c\u4f46\u771f\u6b63\u8fdb\u5165\u7684\u65f6\u5019\u5c31\u4f1a\u53d1\u73b0\u6ca1\u6709\u60f3\u8c61\u5f97\u90a3\u4e48\u8270\u96be\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u4ed6\u8fd8\u662f\u8db3\u8db3\u82b1\u4e86\u4e94\u516d\u5206\u949f\u624d\u5b8c\u5168\u6df1\u5165\u5230\u5e95\u3002\u77ed\u6682\u7684\u505c\u987f\uff0c\u8ba9\u6885\u83f2\u65af\u7a0d\u7a0d\u9002\u5e94\u4e4b\u540e\uff0c\u743c\u6069\u5f00\u59cb\u8fdb\u653b\u3002</p><p>\u6885\u83f2\u65af\u7ec8\u4e8e\u5f7b\u5e95\u660e\u767d\u4e86\u4ec0\u4e48\u662f\u771f\u6b63\u201c\u6301\u4e45\u529b\u201d\uff0c\u4ee5\u53ca\u81ea\u5df1\u6b64\u524d\u4e00\u53e5\u73a9\u7b11\u5bf9\u7537\u4eba\u7684\u523a\u6fc0\u4e4b\u5927\u3002\u63a5\u4e0b\u6765\u7684\u6574\u6574\u4e24\u4e2a\u5c0f\u65f6\u91cc\uff0c\u743c\u6069\u51e0\u4e4e\u662f\u4e00\u523b\u4e0d\u505c\u5730\u8e42\u8e8f\u7740\u5979\u7684\u5a07\u5ae9\u83ca\u9053\uff0c\u4e00\u5f00\u59cb\u662f\u8f7b\u67d4\u7f13\u6162\uff0c\u6e29\u60c5\u6b3e\u6b3e\uff0c\u7ee7\u800c\u662f\u4e0d\u7d27\u4e0d\u6162\uff0c\u8282\u594f\u7a33\u5b9a\uff0c\u6700\u540e\u53d8\u6210\u4e86\u75be\u98ce\u66b4\u96e8\uff0c\u8ba9\u5e73\u65f6\u603b\u662f\u82f1\u6c14\u51db\u51db\u7684\u5723\u6b66\u58eb\u5728\u4f4e\u4f4e\u54ed\u6ce3\u548c\u54c0\u5a49\u5a07\u541f\u4e2d\u63a5\u8fde\u88ab\u9001\u4e0a\u4e86\u56db\u6b21\u9ad8\u6f6e\u3002\u5f53\u743c\u6069\u6700\u540e\u5728\u5979\u4f53\u5185\u7206\u53d1\u7684\u65f6\u5019\uff0c\u5c11\u5973\u7684\u8eab\u4f53\u65e0\u6cd5\u6291\u5236\u5730\u7ef7\u7d27\uff0c\u75c9\u631b\u822c\u5267\u70c8\u98a4\u6296\u7740\uff0c\u8db3\u8db3\u6301\u7eed\u4e86\u5341\u51e0\u79d2\u949f\uff0c\u7136\u540e\u624d\u8f6f\u8f6f\u762b\u5012\u3002</p><p>\u743c\u6069\u5fc3\u6ee1\u610f\u8db3\u5730\u4ece\u5c11\u5973\u4f53\u5185\u9000\u51fa\uff0c\u5c06\u5979\u62b1\u51fa\u6d74\u7f38\uff0c\u64e6\u5e72\u8eab\u4f53\uff0c\u7136\u540e\u56de\u5230\u5367\u5ba4\u4e2d\u3002\u6885\u83f2\u65af\u4fa7\u5367\u5728\u5e8a\u4e0a\uff0c\u5168\u8eab\u8f6f\u7ef5\u7ef5\u5730\u6ca1\u6709\u4e00\u4e1d\u529b\u6c14\uff0c\u201c\u5feb\u8981\u88ab\u4f60\u6298\u817e\u6b7b\u4e86\uff0c\u201d\u5979\u534a\u771f\u534a\u5047\u5730\u62b1\u6028\uff0c\u201c\u4eca\u5929\u600e\u4e48\u575a\u6301\u8fd9\u4e48\u4e45\u3002\u201d</p><p>\u201c\u5f88\u4e45\u4e48\uff1f\u201d\u743c\u6069\u8bf4\uff0c\u201c\u548c\u4ee5\u524d\u5dee\u4e0d\u591a\u5427\u3002\u201d</p><p>\u201c\u54ea\u6709\uff0c\u4ee5\u524d\u7edd\u5bf9\u6ca1\u8fd9\u4e48\u5f3a\u2026\u2026\u4f60\u5728\u627e\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u836f\u3002\u201d</p><p>\u743c\u6069\u7b80\u6d01\u5730\u56de\u7b54\u7740\uff0c\u4ece\u5e8a\u8fb9\u957f\u888d\u7684\u53e3\u888b\u91cc\u6478\u51fa\u4e00\u4e2a\u5c0f\u5c0f\u7684\u6c34\u6676\u74f6\u5b50\uff0c\u5706\u9525\u5f62\uff0c\u91cc\u9762\u88c5\u7740\u5927\u534a\u74f6\u6d45\u84dd\u8272\u900f\u660e\u7684\u534a\u51dd\u56fa\u6db2\u4f53\uff0c\u50cf\u662f\u679c\u51bb\u3002\u743c\u6069\u5c06\u5b83\u5012\u51fa\u4e00\u4e9b\u5728\u624b\u6307\u4e0a\uff0c\u793a\u610f\u6885\u83f2\u65af\u8dea\u8d77\u8eab\u6765\u3002\u5c11\u5973\u4e0d\u89e3\u5176\u610f\uff0c\u4f46\u8fd8\u662f\u4e56\u4e56\u7167\u505a\uff0c\u5979\u611f\u89c9\u743c\u6069\u6765\u5230\u81ea\u5df1\u8eab\u540e\uff0c\u63a5\u7740\u4fbf\u53d1\u89c9\u7537\u4eba\u7684\u624b\u6307\u53c8\u91cd\u65b0\u63a2\u4e86\u8fdb\u6765\uff0c\u6324\u5165\u540e\u5ead\uff0c\u5c06\u90a3\u79cd\u6db2\u4f53\u6d82\u62b9\u5728\u9971\u53d7\u8e42\u8e8f\u7684\u5a07\u5ae9\u8089\u58c1\u4e0a\u3002</p><p>\u201c\u6709\u6ca1\u6709\u611f\u89c9\u597d\u4e9b\u3002\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u55ef\u3002\u201d</p><p>\u6db2\u4f53\u51b0\u51b0\u7684\uff0c\u51c9\u4e1d\u4e1d\u7684\uff0c\u8ba9\u539f\u672c\u706b\u8fa3\u8fa3\u75bc\u75db\u7684\u83ca\u6d1e\u786e\u5b9e\u8212\u670d\u4e86\u5f88\u591a\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\u836f\uff1f\u201d\u6885\u83f2\u65af\u95ee\uff0c\u201c\u4ee5\u524d\u600e\u4e48\u6ca1\u89c1\u4f60\u7528\u8fc7\u3002\u201d</p><p>\u201c\u524d\u6bb5\u65f6\u95f4\u505a\u5b9e\u9a8c\uff0c\u65e0\u610f\u95f4\u914d\u51fa\u6765\u7684\uff0c\u8fd8\u6ca1\u53d6\u540d\u5b57\u5462\u3002\u201d</p><p>\u5c11\u5973\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u5c11\u9a97\u4eba\uff01\u201d</p><p>\u786e\u5b9e\u662f\u9a97\u4eba\uff0c\u743c\u6069\u867d\u7136\u662f\u70bc\u91d1\u5e08\uff0c\u5728\u836f\u5242\u5b66\u4e0a\u7684\u9020\u8be3\u4e5f\u5c31\u53ea\u662f\u201c\u4e0d\u9519\u201d\u800c\u5df2\uff0c\u8ddd\u79bb\u201c\u4f18\u79c0\u201d\u90fd\u8fd8\u6709\u4e00\u6bb5\u8ddd\u79bb\uff0c\u66f4\u522b\u8bf4\u53d1\u660e\u65b0\u836f\u8fd9\u79cd\u9ad8\u96be\u5ea6\u5de5\u4f5c\u3002\u5373\u4fbf\u771f\u662f\u8bef\u6253\u8bef\u649e\u914d\u51fa\u4e86\u65b0\u5947\u836f\u5242\uff0c\u4ed6\u53c8\u5c82\u6562\u968f\u4fbf\u62ff\u6885\u83f2\u65af\u505a\u8bd5\u9a8c\u54c1\u3002\u5176\u5b9e\u8fd9\u662f\u4ed6\u4ee5\u524d\u5728\u5deb\u5e08\u5b66\u6821\u65f6\uff0c\u5076\u5c14\u4ece\u4e00\u672c\u53e4\u4e66\u91cc\u770b\u5230\u7684\u836f\u5242\u914d\u65b9\uff0c\u4e13\u95e8\u5c31\u662f\u4e3a\u73a9\u540e\u5ead\u82b1\u7684\u60c5\u4fa3\u6240\u51c6\u5907\u7684\uff0c\u6548\u679c\u636e\u8bf4\u6781\u597d\uff0c\u53ea\u662f\u8c03\u914d\u6240\u9700\u8981\u7684\u6750\u6599\u975e\u5e38\u591a\u800c\u4e14\u7f55\u89c1\uff0c\u743c\u6069\u4e00\u76f4\u6ca1\u80fd\u6536\u96c6\u9f50\u5168\u3002\u524d\u65e5\u5728\u661f\u9668\u57ce\u91cc\uff0c\u4ed6\u65e0\u610f\u95f4\u53d1\u73b0\u4e00\u95f4\u836f\u5242\u5ba4\uff0c\u4ece\u91cc\u9762\u627e\u5230\u4e86\u5927\u91cf\u7684\u73cd\u7a00\u836f\u6750\uff0c\u628a\u7f3a\u5c11\u7684\u6750\u6599\u90fd\u8865\u9f50\u4e86\uff0c\u7136\u540e\u82b1\u4e86\u534a\u4e2a\u4e0b\u5348\uff0c\u7ecf\u8fc7\u4e24\u6b21\u5931\u8d25\uff0c\u6d6a\u8d39\u4ef7\u503c\u5c06\u8fd1\u4e00\u767e\u91d1\u5e01\u7684\u6750\u6599\uff0c\u6700\u7ec8\u914d\u51fa\u4e86\u8fd9\u534a\u74f6\u836f\u6765\u3002\u4e0d\u8fc7\u8fd9\u4e9b\u8fc7\u7a0b\u7ec6\u8282\uff0c\u5c31\u6ca1\u5fc5\u8981\u5bf9\u6885\u83f2\u65af\u8bf4\u4e86\u3002</p><p>\u4e24\u4eba\u5728\u5e8a\u4e0a\u5e76\u80a9\u8eba\u7740\uff0c\u9759\u9759\u4f11\u606f\uff0c\u90fd\u6ca1\u6709\u518d\u8bf4\u8bdd\u3002\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u743c\u6069\u8179\u4e2d\u7a81\u7136\u53d1\u51fa\u201c\u5495\u201d\u5730\u4e00\u58f0\uff0c\u6253\u7834\u4e86\u5367\u5ba4\u91cc\u7684\u9759\u8c27\u3002\u6885\u83f2\u65af\u5148\u662f\u4e00\u6014\uff0c\u968f\u5373\u5657\u54e7\u7b11\u8d77\u6765\uff0c\u201c\u997f\u4e86\uff1f\u201d\u5979\u95ee\u3002</p><p>\u743c\u6069\u65e2\u6ca1\u5403\u65e9\u9910\uff0c\u53c8\u548c\u6885\u83f2\u65af\u505a\u4e86\u957f\u65f6\u95f4\u5267\u70c8\u8fd0\u52a8\uff0c\u73b0\u5728\u90fd\u5df2\u7ecf\u5feb\u5230\u4e2d\u5348\u5341\u4e8c\u70b9\u4e86\uff0c\u4e0d\u997f\u624d\u5947\u602a\u3002\u201c\u55ef\uff0c\u997f\u4e86\u2014\u2014\u5bf9\u4e86\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u968f\u53e3\u95ee\uff0c\u201c\u6211\u8fdb\u6765\u7684\u65f6\u5019\u611f\u89c9\u7a7a\u8361\u8361\u7684\u6ca1\u4eba\uff0c\u8fd9\u4e48\u5927\u623f\u5b50\uff0c\u5c31\u4f60\u4e00\u4e2a\u4eba\u4f4f\uff1f\u201d</p><p>\u6885\u83f2\u65af\u4e0d\u89e3\u5176\u610f\uff0c\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u4e5f\u6ca1\u6709\u4ec6\u4eba\uff0c\u6216\u8005\u53a8\u5e08\uff1f\u201d</p><p>\u5c11\u5973\u6447\u5934\u3002</p><p>\u201c\u8fd9\u4e48\u8bf4\uff0c\u4e00\u65e5\u4e09\u9910\u4f60\u662f\u81ea\u5df1\u52a8\u624b\u4e86\uff1f\u201d\u743c\u6069\u7b11\u563b\u563b\u5730\u8bf4\uff0c\u201c\u592a\u597d\u4e86\uff0c\u6211\u8fd8\u4ece\u6ca1\u54c1\u5c1d\u8fc7\u4f60\u7684\u53a8\u827a\u5462\uff0c\u4eca\u5929\u4e00\u5b9a\u8981\u89c1\u8bc6\u89c1\u8bc6\u2014\u2014\u5348\u9910\u7684\u83dc\u8c31\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u6885\u83f2\u65af\u8fd9\u624d\u660e\u767d\uff0c\u201c\u90a3\u4f60\u8981\u5931\u671b\u4e86\uff0c\u201d\u5979\u7b11\u7740\u8038\u8038\u80a9\uff0c\u201c\u6211\u7684\u53a8\u827a\u5f88\u5dee\u7684\uff0c\u51db\u603b\u662f\u5632\u7b11\u6211\u3002\u201d</p><p>\u201c\u5dee\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff1f\u201d\u743c\u6069\u95ee\uff0c\u60f3\u8d77\u4e86\u524d\u4e16\u770b\u8fc7\u7684\u5f88\u591a\u5c0f\u8bf4\uff0c\u91cc\u9762\u7684\u5973\u4e3b\u89d2\u5f80\u5f80\u7f8e\u4e3d\u82e5\u4ed9\u5b50\uff0c\u505a\u51fa\u6765\u7684\u996d\u83dc\u5374\u8db3\u4ee5\u8c0b\u6740\u4eba\uff0c\u5e38\u5e38\u628a\u7537\u4e3b\u89d2\u5f04\u5f97\u4e0a\u5410\u4e0b\u6cfb\u751a\u81f3\u9001\u533b\u9662\u6025\u6551\uff0c\u201c\u4e0d\u4f1a\u6bd2\u6b7b\u4eba\u5427\u3002\u201d\u4ed6\u5fd0\u5fd1\u4e0d\u5b89\u5730\u95ee\u3002</p><p>\u201c\u80e1\u626f\uff0c\u201d\u6885\u83f2\u65af\u4e0d\u9ad8\u5174\u8d77\u6765\uff0c\u201c\u600e\u4e48\u53ef\u80fd\u3002\u867d\u7136\u548c\u73ca\u5609\u59d0\u59d0\u662f\u6ca1\u6cd5\u6bd4\uff0c\u4f46\u6700\u591a\u4e5f\u5c31\u53ea\u662f\u4e0d\u592a\u597d\u5403\u800c\u5df2\uff0c\u54ea\u6709\u90a3\u4e48\u5938\u5f20\uff01\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u81ea\u5df1\u8bf4\u5f88\u5dee\u7684\u561b\u2026\u2026\u201d</p><p>\u201c\u90a3\u662f\u51db\u8bf4\u7684\u5566\uff01\u201d\u5c11\u5973\u7684\u4fcf\u8138\u6709\u4e9b\u6da8\u7ea2\uff0c\u201c\u51db\u90a3\u5bb6\u4f19\u4e00\u5411\u8d85\u7ea7\u6311\u98df\u7684\u3002\u201d</p><p>\u201c\u662f\u4e48\uff0c\u5e73\u65f6\u4e5f\u6ca1\u89c9\u5f97\u554a\uff0c\u90a3\u5979\u81ea\u5df1\u7684\u53a8\u827a\u5982\u4f55\uff1f\u201d</p><p>\u6885\u83f2\u65af\u4e0d\u5c51\u5730\u54fc\u4e86\u58f0\uff0c\u201c\u4e00\u584c\u7cca\u6d82\uff0c\u5c5e\u4e8e\u714e\u9e21\u86cb\u90fd\u4e0d\u4f1a\u7684\u90a3\u79cd\u3002\u201d</p><p>\u714e\u9e21\u86cb\u2026\u2026\u5176\u5b9e\u6211\u4e5f\u4e0d\u4f1a\u3002</p><p>\u201c\u90a3\u5c31\u597d\uff0c\u201d\u743c\u6069\u677e\u4e86\u53e3\u6c14\uff0c\u201c\u53a8\u827a\u5dee\u70b9\u6ca1\u5173\u7cfb\uff0c\u53cd\u6b63\u6211\u4e0d\u6311\u98df\u7684\u3002\u201d</p><p>\u201c\u624d\u4e0d\u5e72\u5462\uff0c\u60f3\u8ba9\u6211\u88ab\u73ca\u5609\u59d0\u59d0\u5632\u7b11\u4e48\u3002\u201d</p><p>\u201c\u6211\u6ca1\u6709\u8fd9\u610f\u601d\u2014\u2014\u800c\u4e14\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u6211\u603b\u7b97\u662f\u5ba2\u4eba\uff0c\u5ba2\u4eba\u997f\u4e86\uff0c\u4e3b\u4eba\u603b\u8981\u62db\u5f85\u5427\u3002\u201d</p><p>\u201c\u5207\uff0c\u4f60\u7b97\u4ec0\u4e48\u5ba2\u4eba\u3002\u201d</p><p>\u8bdd\u662f\u5982\u6b64\u8bf4\uff0c\u6885\u83f2\u65af\u8fd8\u662f\u8d77\u8eab\u7a7f\u8863\uff0c\u743c\u6069\u786e\u5b9e\u53ef\u4ee5\u4e0d\u7b97\u5ba2\u4eba\uff0c\u4f46\u8fd8\u6709\u73ca\u5609\u5462\uff0c\u81f3\u4e8e\u838e\u73de\u514b\u548c\u5965\u5609\u83b1\u65af\u5012\u65e0\u6240\u8c13\uff0c\u9b45\u9b54\u4e5f\u597d\uff0c\u5e7d\u7075\u4e5f\u597d\uff0c\u53cd\u6b63\u90fd\u4e0d\u9700\u8981\u996e\u98df\u3002</p><p>\u4e24\u4eba\u4e0b\u697c\u6765\uff0c\u5374\u6ca1\u770b\u89c1\u73ca\u5609\uff0c\u53ea\u6709\u838e\u73de\u514b\u60a0\u95f2\u5730\u5750\u5728\u5ba2\u5385\u7684\u6c99\u53d1\u4e2d\uff0c\u8863\u9970\u66b4\u9732\uff0c\u4e00\u53cc\u4fee\u957f\u7f8e\u817f\u4ea4\u53e0\u7740\uff0c\u7389\u767d\u7c89\u5ae9\uff0c\u4ee4\u743c\u6069\u7684\u76ee\u5149\u4e0d\u7531\u81ea\u4e3b\u5730\u5c31\u88ab\u5438\u5f15\u8fc7\u53bb\u3002\u5979\u4e00\u624b\u7aef\u7740\u6c34\u676f\uff0c\u6175\u61d2\u5730\u9760\u5728\u9760\u80cc\u4e0a\uff0c\u773c\u775b\u770b\u7740\u5929\u82b1\u677f\uff0c\u4e00\u52a8\u4e0d\u52a8\uff0c\u50cf\u662f\u5728\u53d1\u5446\u3002</p><p>\u542c\u89c1\u811a\u6b65\u58f0\uff0c\u9b45\u9b54\u8f6c\u8fc7\u8138\u6765\uff0c\u201c\u8fd9\u4e48\u65e9\u554a\uff0c\u4e24\u4f4d\uff0c\u201d\u5979\u683c\u683c\u5a07\u7b11\uff0c\u201c\u770b\u8d77\u6765\u5f88\u52b3\u7d2f\u7684\u6837\u5b50\u5462\u3002\u201d</p><p>\u743c\u6069\u88c5\u4f5c\u6ca1\u542c\u89c1\uff0c\u201c\u6211\u59d0\u59d0\u5462\uff1f\u201d</p><p>\u201c\u4e00\u65e9\u5c31\u53bb\u8bad\u7ec3\u573a\u4e86\uff0c\u8fd8\u6ca1\u56de\u6765\u3002\u201d</p><p>\u8bad\u7ec3\u573a\uff1f</p><p>\u6628\u665a\u5e0c\u6b27\u4ecb\u7ecd\u7684\u65f6\u5019\uff0c\u786e\u5b9e\u66fe\u7ecf\u63d0\u5230\u8fc7\uff0c\u8fd9\u5ea7\u5c0f\u697c\u540e\u9762\u6709\u4e00\u5904\u5f88\u5927\u7684\u5385\u5802\uff0c\u4e43\u662f\u8bad\u7ec3\u573a\u3002\u6b63\u5982\u5deb\u5e08\u7684\u5c45\u6240\u90fd\u4f1a\u6709\u56fe\u4e66\u9986\u548c\u5b9e\u9a8c\u5ba4\uff0c\u6885\u83f2\u65af\u8fd9\u79cd\u9ad8\u5f3a\u6b66\u8005\uff0c\u4f4f\u5904\u6709\u8bad\u7ec3\u573a\u4e5f\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\uff0c\u4e0d\u8db3\u4e3a\u5947\u3002\u7136\u800c\u73ca\u5609\u53bb\u8bad\u7ec3\u573a\u505a\u4ec0\u4e48\uff1f\u771f\u8981\u8bba\u8d77\u6765\uff0c\u5979\u81f3\u5c11\u8fd8\u52c9\u5f3a\u7b97\u662f\u4e2a\u521a\u5165\u95e8\u7684\u5deb\u5e08\uff0c\u5374\u80af\u5b9a\u4e0d\u662f\u6b66\u8005\u554a\u3002</p><p>\u201c\u53bb\u4e86\u8bad\u7ec3\u573a\uff1f\u201d\u6885\u83f2\u65af\u540c\u6837\u4e5f\u89c9\u5f97\u5947\u602a\uff0c\u201c\u90a3\u662f\u6211\u5e73\u65f6\u7ec3\u5251\u7684\u5730\u65b9\u554a\u3002\u743c\u6069\uff0c\u96be\u9053\u73ca\u5609\u59d0\u59d0\u4e5f\u64c5\u957f\u6b66\u6280\u5417\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u4f1a\uff0c\u5979\u957f\u8fd9\u4e48\u5927\u8fde\u5251\u90fd\u6ca1\u63e1\u8fc7\u2026\u2026\u5443\uff0c\u7b49\u7b49\u3002\u201d</p><p>\u743c\u6069\u7a81\u7136\u60f3\u4e86\u8d77\u6765\uff0c\u6628\u5929\u5728\u8def\u4e0a\uff0c\u6d88\u5931\u4e86\u51e0\u5929\u7684\u5965\u5609\u83b1\u65af\u7a81\u7136\u73b0\u8eab\uff0c\u5e76\u4e14\u4ece\u300a\u547d\u8fd0\u957f\u591c\u300b\u4e2d\u53d6\u51fa\u4e00\u67c4\u5c16\u5229\u7684\u523a\u5251\u4ea4\u7ed9\u73ca\u5609\uff0c\u8fd8\u8bf4\u8981\u6559\u5979\u5251\u672f\u3002\u743c\u6069\u5f53\u65f6\u6ca1\u592a\u5728\u610f\uff0c\u800c\u4e14\u63a5\u7740\u5c31\u906d\u9047\u4e86\u5343\u96f7\u4e07\u9706\u8f70\u51fb\uff0c\u7136\u540e\u4fbf\u662f\u4e0e\u82cd\u767d\u9886\u4e3b\u548c\u9aa8\u9f99\u7684\u4e00\u573a\u6076\u6218\uff0c\u65e9\u628a\u8fd9\u4e8b\u7ed9\u5fd8\u4e86\u3002\u73b0\u5728\u88ab\u4e00\u63d0\u9192\uff0c\u7a81\u7136\u60f3\u4e86\u8d77\u6765\u3002</p><p>\u4e0d\u4f1a\u5427\uff0c\u96be\u9053\u59d0\u59d0\u6628\u665a\u8bf4\u8981\u5f00\u59cb\u52aa\u529b\u953b\u70bc\uff0c\u5176\u4e2d\u8fd8\u771f\u5305\u62ec\u5251\u672f\uff1f\u743c\u6069\u5012\u662f\u4e0d\u53cd\u5bf9\u4e86\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u5f88\u559c\u6b22\u770b\u5973\u5b69\u5b50\u6301\u5251\uff0c\u6709\u4e00\u79cd\u67d4\u7f8e\u4e2d\u900f\u51fa\u7684\u82f1\u6b66\u6c14\u8d28\uff0c\u6b63\u662f\u4ed6\u6781\u559c\u6b22\u7684\u7c7b\u578b\u3002\u4f46\u60f3\u5230\u73ca\u5609\u59d0\u59d0\u6325\u5251\u780d\u4eba\u7684\u573a\u666f\uff0c\u603b\u89c9\u5f97\u8fd8\u662f\u6709\u70b9\u602a\u602a\u7684\u2026\u2026</p><p>\u7136\u800c\u838e\u73de\u514b\u7d27\u63a5\u7740\u5c31\u8bc1\u5b9e\u4e86\u4ed6\u7684\u731c\u6d4b\u3002\u201c\u662f\u554a\uff0c\u8bf4\u662f\u53bb\u7ec3\u5251\uff0c\u201d\u9b45\u9b54\u7b11\u541f\u541f\u5730\u8bf4\uff0c\u201c\u6211\u4e5f\u5f88\u5947\u602a\u5462\u3002\u201d</p><p>\u201c\u53bb\u770b\u770b\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u7a7f\u8fc7\u4e00\u5904\u56de\u5eca\uff0c\u6885\u83f2\u65af\u5e26\u7740\u743c\u6069\u8fdb\u5165\u8bad\u7ec3\u573a\uff0c\u8fd8\u5728\u5916\u9762\u5c31\u5df2\u7ecf\u542c\u89c1\u4e86\u5200\u5251\u78b0\u649e\u7684\u6e05\u8106\u58f0\u54cd\u3002\u8fdb\u53bb\u4e00\u770b\uff0c\u53d1\u73b0\u662f\u4e24\u4f4d\u5c11\u5973\u6b63\u5728\u5bf9\u6218\u2014\u2014\u6216\u8005\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u4e0d\u662f\u5bf9\u6218\uff0c\u800c\u662f\u5355\u65b9\u9762\u7684\u653b\u51fb\u548c\u5355\u65b9\u9762\u7684\u9632\u5fa1\u3002\u653b\u51fb\u8005\u662f\u73ca\u5609\uff0c\u624b\u6301\u90a3\u67c4\u88ab\u5979\u547d\u540d\u4e3a\u201c\u7d2b\u8346\u201d\u7684\u523a\u5251\uff0c\u800c\u9632\u5b88\u8005\u5219\u662f\u4e00\u4f4d\u4fcf\u4e3d\u7684\u77ed\u53d1\u5c11\u5973\uff0c\u8eab\u7a7f\u706b\u7ea2\u8272\u76ae\u7532\uff0c\u6234\u7740\u4e24\u53ea\u5bd2\u5149\u95ea\u95ea\u7684\u722a\u5957\uff0c\u6b63\u662f\u732b\u5973\u742a\u5a05\u3002\u800c\u5965\u5609\u83b1\u65af\u9759\u9759\u5730\u7ad9\u5728\u4e00\u65c1\uff0c\u9762\u65e0\u8868\u60c5\u5730\u89c2\u770b\u7740\u3002</p><p>\u6885\u83f2\u65af\u7ad9\u5728\u95e8\u53e3\uff0c\u76ee\u5149\u5728\u573a\u5185\u4e00\u63a0\u800c\u8fc7\uff0c\u5f53\u5979\u770b\u89c1\u732b\u5973\u7684\u65f6\u5019\uff0c\u4e0d\u7531\u5f97\u660e\u663e\u5730\u6014\u4e86\u4e00\u6014\u3002</p><p>\u201c\u742a\u5a05\xb7\u706b\u5203\uff1f\u201d\u5979\u4ed4\u7ec6\u770b\u4e86\u51e0\u79d2\u949f\uff0c\u4f4e\u58f0\u81ea\u8bed\u3002</p><p>\u201c\u4f60\u8ba4\u8bc6\u5979\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u517d\u795e\u739b\u62c9\u7684\u9009\u6c11\uff0c\u6211\u600e\u4e48\u4f1a\u4e0d\u8ba4\u8bc6\uff0c\u53ea\u662f\u2026\u2026\u201d\u5c11\u5973\u7528\u6000\u7591\u7684\u76ee\u5149\u6253\u91cf\u7740\u743c\u6069\uff0c\u201c\u5979\u4e3a\u4ec0\u4e48\u4f1a\u5728\u8fd9\u91cc\uff1f\u201d</p></article>"}}}]);
