(window.webpackJsonp=window.webpackJsonp||[]).push([[17],{40:function(p){p.exports={chapterTitle:null,title:"\u7b2c1009\u7ae0 \u516c\u8f66\u6a21\u62df",content:"<article><h2>\u7b2c1009\u7ae0 \u516c\u8f66\u6a21\u62df</h2><p>\u3000\u3000\u665a\u4e0a\uff0c\u5f20\u606a\u5c31\u5728\u521b\u4e1a\u56ed\u533a\u7684\u5458\u5de5\u9910\u5385\u8bf7\u6768\u4e91\u3001\u5434\u4e3a\u94ed\u4ed6\u4eec\u5403\u996d\uff0c\u521a\u5de7\u6d77\u5dde\u5de5\u4e1a\u65b0\u533a\u7ba1\u59d4\u4f1a\u515a\u5de5\u59d4\u526f\u4e66\u8bb0\u3001\u4e3b\u4efb\u674e\u4e49\u6c5f\u4e5f\u5728\u5efa\u90ba\uff0c\u4e5f\u8bf7\u6765\u4e00\u8d77\u5403\u996d\uff0c\u5728\u9910\u684c\u4e0a\uff0c\u8bf4\u8d77\u4e0b\u5348\u9762\u8bd5\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u7b11\u7740\u8ddf\u4ed6\u4eec\u8bf4\u9053\uff1a\u201c\u6211\u6709\u56de\u5728\u65e5\u672c\u5750\u7535\u8f66\uff0c\u90a3\u6b21\u662f\u4ece\u5c71\u7530\u7535\u5668\u5e97\u51fa\u6765\uff0c\u624b\u91cc\u8fd8\u63d0\u7740\u5c71\u7530\u7535\u5668\u5e97\u63d0\u4f9b\u7684\u73af\u4fdd\u888b\uff0c\u7535\u8f66\u4e0a\u6ca1\u6709\u7a7a\u4f4d\u5b50\uff0c\u6709\u4e9b\u4e2d\u5b66\u751f\u7684\u5973\u5b69\u5b50\u7ad9\u7740\uff0c\u65e5\u672c\u5973\u5b66\u751f\u6821\u670d\u5c31\u662f\u51ac\u5929\u4e5f\u662f\u88d9\u5b50\uff0c\u5973\u5b69\u5b50\u51fa\u4e86\u6821\u95e8\u53c8\u559c\u6b22\u5c06\u88d9\u5b50\u5f80\u4e0a\u63d0\uff0c\u770b\u4e0a\u53bb\u50cf\u8d85\u77ed\u88d9\uff0c\u9732\u51fa\u96ea\u767d\u7684\u5927\u817f\uff0c\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u522b\u4eba\u8fd8\u53ea\u5f53\u4ed6\u8981\u8bf4\u5728\u65e5\u672c\u7684\u8273\u9047\uff0c\u76ee\u5149\u70af\u70af\u7684\u671b\u8fc7\u6765\uff0c\u7fdf\u4e39\u9752\u4e00\u8138\u4e0d\u5c51\u7684\u522b\u8fc7\u8138\u53bb\u8ddf\u8d75\u6893\u7433\u8bf4\u8bdd\uff0c\u5374\u4e0d\u6599\u5f20\u606a\u8bdd\u950b\u4e00\u8f6c\uff0c\u201c\u4eca\u5929\u8ddf\u4f60\u4eec\u4e0d\u662f\u8bf4\u8fd9\u4e2a\u2026\u2026\u6211\u4e0a\u8f66\u540e\uff0c\u8f66\u4e0a\u5df2\u7ecf\u6ca1\u6709\u7a7a\u4f4d\u5b50\uff0c\u6211\u5c31\u7ad9\u5728\u8fc7\u9053\u91cc\uff0c\u5374\u6709\u4e2a\u56db\u5341\u591a\u5c81\u7684\u65e5\u672c\u4e2d\u5e74\u7537\u4eba\u4e3b\u52a8\u7ad9\u8d77\u6765\u7ed9\u6211\u8ba9\u5ea7\uff0c\u6211\u5f88\u5947\u602a\uff0c\u95ee\u4ed6\uff0c\u6211\u54ea\u91cc\u770b\u4e0a\u53bb\u50cf\u662f\u8981\u4ed6\u8ba9\u5ea7\u7684\u4eba\uff1f\u4ed6\u56de\u7b54\u6211\u8bf4\uff0c\u4ed6\u662f\u5c71\u7530\u7535\u5668\u7684\u5458\u5de5\uff0c\u6211\u624b\u91cc\u62ff\u7740\u5c71\u7530\u7535\u5668\u7684\u73af\u4fdd\u888b\uff0c\u4ed6\u8bf4\u6211\u662f\u5c71\u7530\u7535\u5668\u7684\u987e\u5ba2\uff0c\u4ed6\u5c31\u5e94\u8be5\u7ed9\u6211\u8ba9\u5ea7\u2014\u2014\u201d</p><p>\u3000\u3000\u5176\u4ed6\u4eba\u5012\u4e5f\u7f62\u4e86\uff0c\u60f3\u7b11\u4e5f\u8981\u514b\u5236\u7740\uff0c\u6768\u4e91\u4e0e\u674e\u4e49\u6c5f\u8ddf\u5f20\u606a\u5f88\u719f\uff0c\u90fd\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u6768\u4e91\u7b11\u7740\u8bf4\uff1a\u201c\u5356\u8fd9\u4e2a\u5173\u5b50\uff0c\u8fd8\u4ee5\u4e3a\u4f60\u8981\u5bf9\u65e5\u672c\u5973\u4e2d\u5b66\u751f\u7684\u5927\u817f\u53d1\u8868\u4e00\u756a\u8bae\u8bba\u5462\u2014\u2014\u5bb3\u6211\u5dee\u70b9\u5c06\u8bb0\u4e8b\u672c\u638f\u51fa\u6765\uff0c\u201d\u8f6c\u800c\u53c8\u8f7b\u53f9\uff0c\u201c\u5e73\u65f6\u8bf4\u8d77\u5c0f\u65e5\u672c\u6765\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u5728\u6709\u4e9b\u65b9\u9762\u5374\u4e0d\u5f97\u4e0d\u4f69\u670d\uff0c\u4ed6\u4eec\u80fd\u505a\u5230\u4e00\u79cd\u8fd1\u4f3c\u82db\u523b\u7684\u7a0b\u5ea6\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u7231\u8fbe\u96c6\u56e2\u4eba\u4e8b\u90e8\u95e8\u7684\u5434\u4e3a\u94ed\u8bf4\u9053\uff1a\u201c\u5458\u5de5\u5b88\u5219\u7684\u7f16\u5199\uff0c\u6211\u77e5\u9053\u662f\u4f60\u8d1f\u8d23\u7684\uff0c\u6211\u770b\u8fc7\u4e86\uff0c\u5bf9\u5185\u5bb9\u6682\u65f6\u4e0d\u53d1\u8868\u610f\u89c1\uff0c\u7ed9\u4f60\u4ea4\u5f85\u4e00\u4e2a\u4efb\u52a1\uff0c\u56de\u53bb\u4e4b\u540e\uff0c\u5c06\u96c6\u56e2\u7684\u4e8b\u52a1\u90e8\u526f\u603b\u7ecf\u7406\u4ee5\u4e0a\u7684\u9ad8\u5c42\u90fd\u63ea\u5230\u4e00\u8d77\uff0c\u5c06\u6211\u8bf4\u7684\u8bdd\u8f6c\u8ff0\u7ed9\u4ed6\u4eec\u542c\uff0c\u5f53\u7136\u4e86\uff0c\u65e5\u672c\u5973\u4e2d\u5b66\u751f\u96ea\u767d\u5927\u817f\u8fd9\u8282\u53ef\u4ee5\u7701\u7565\u6389\u4e0d\u8bb2\u2014\u2014\u4e5f\u8981\u5411\u666e\u901a\u5458\u5de5\u96c6\u601d\u5e7f\u76ca\uff0c\u770b\u4ed6\u4eec\u5bf9\u300a\u5458\u5de5\u5b88\u5219\u300b\u6709\u4ec0\u4e48\u610f\u89c1\uff0c\u8ba8\u8bba\u4e00\u4e0b\u6211\u4eec\u53ef\u4ee5\u505a\u5230\u54ea\u4e00\u6b65\uff0c\u4e0d\u540c\u7684\u90e8\u95e8\u80fd\u591f\u505a\u5230\u54ea\u4e00\u6b65\uff0c\u90fd\u4f1a\u6df1\u5165\u7684\u8ba8\u8bba\u3002\u516c\u53f8\u5f62\u8c61\u3001\u54c1\u724c\u5f62\u8c61\u7684\u5851\u9020\uff0c\u8c01\u8981\u5355\u7eaf\u7684\u8ba4\u4e3a\u662f\u54c1\u724c\u8425\u9500\u90e8\u95e8\u7684\u4e8b\u60c5\uff0c\u6216\u8005\u5355\u7eaf\u7684\u8ba4\u4e3a\u8fd9\u662f\u516c\u53f8\u7ba1\u7406\u5c42\u8981\u8003\u8651\u7684\u95ee\u9898\uff0c\u6211\u5c31\u4e0d\u5ba2\u6c14\u7684\u8bf7\u4ed6\u6eda\u86cb\u3002\u201d</p><p>\u3000\u3000\u5434\u4e3a\u94ed\u5fd9\u4e0d\u8fed\u7684\u5c06\u5f20\u606a\u7684\u8bdd\u8bb0\u4e0b\u6765\uff0c\u5e78\u597d\u6709\u6768\u4e91\u5728\u573a\uff0c\u4e0d\u7136\u56de\u53bb\u6307\u4e0d\u5b9a\u8c01\u8bf4\u4ed6\u5047\u4f20\u5723\u65e8\u5462\u3002</p><p>\u3000\u3000\u6768\u4e91\u7b11\u7740\u62cd\u4e86\u62cd\u5434\u4e3a\u94ed\u7684\u80a9\u8180\uff0c\u5f20\u606a\u5c06\u4efb\u52a1\u76f4\u63a5\u4ea4\u7ed9\u5434\u4e3a\u94ed\u6765\u505a\uff0c\u4e5f\u5e94\u662f\u770b\u91cd\u4ed6\u67d0\u65b9\u9762\u7684\u80fd\u529b\uff0c\u6709\u4e86\u8fd9\u67c4\u5c1a\u65b9\u5b9d\u5251\uff0c\u5434\u4e3a\u94ed\u5728\u8fd9\u4ef6\u4e8b\u4e0a\u5c31\u80fd\u5c06\u96c6\u56e2\u7684\u8001\u603b\u4eec\u534f\u8c03\u8d77\u6765\uff0c\u96be\u5f97\u7684\u953b\u70bc\u673a\u4f1a\u3002\u4eba\u8981\u4e00\u6b65\u767b\u5929\u5f88\u96be\uff0c\u4f46\u80fd\u5b9e\u9645\u7684\u63d0\u9ad8\u5434\u4e3a\u94ed\u5728\u96c6\u56e2\u91cc\u7684\u5730\u4f4d\uff0c\u5c31\u6709\u66f4\u591a\u7684\u6210\u957f\u673a\u4f1a\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7684\u89c4\u6a21\u5927\u4e86\uff0c\u7279\u522b\u662f\u56fd\u5185\u7684\u4f01\u4e1a\u6c1b\u56f4\uff0c\u523b\u610f\u7684\u8ffd\u6c42\u6241\u5e73\u5316\u7ba1\u7406\u4e5f\u5f88\u96be\uff0c\u7ba1\u7406\u5c42\u7684\u7b49\u7ea7\u5206\u5c42\u4e5f\u662f\u73b0\u9636\u6bb5\u6240\u5fc5\u9700\u7684\uff0c\u96c6\u56e2\u9ad8\u5c42\u5305\u62ec\u4ece\u96c6\u56e2\u603b\u88c1\u3001\u526f\u603b\u88c1\u3001\u4e00\u7ea7\u90e8\u95e8\u603b\u88c1\u3001\u4e8b\u52a1\u90e8\u603b\u7ecf\u7406\u3001\u526f\u603b\u7ecf\u7406\uff0c\u4e2d\u5c42\u7531\u7ecf\u7406\u3001\u526f\u7ecf\u7406\u3001\u90e8\u95e8\u4e3b\u7ba1\u7ea7\u7684\u90e8\u957f\u3001\u526f\u90e8\u957f\u7b49\u7ea7\u522b\uff0c\u4ee5\u53ca\u57fa\u5c42\u529e\u516c\u5ba4\u3001\u751f\u4ea7\u6bb5\u7684\u4e3b\u7ba1\u7b49\u804c\u3002</p><p>\u3000\u3000\u9664\u884c\u653f\u5206\u7ea7\u5916\uff0c\u5bf9\u7ba1\u7406\u5c42\u53ca\u9aa8\u5e72\u5458\u5de5\u8fd8\u8fdb\u884c\u6280\u672f\u5206\u7ea7\uff0c\u4ee5\u6a61\u6811\u56ed\u4e3a\u6838\u5fc3\u7684\u7814\u53d1\u4f53\u7cfb\u5bf9\u5458\u5de5\u7684\u7ba1\u7406\u4e3b\u8981\u4ee5\u6280\u672f\u5206\u7ea7\u4e3a\u4e3b\u3002</p><p>\u3000\u3000\u5434\u4e3a\u94ed\u5728\u96c6\u56e2\u5185\u90e8\u5b9a\u7ea7\u4e3a\u526f\u7ecf\u7406\uff0c\u5de5\u7a0b\u5e08\u516d\u7ea7\uff0c\u8ddd\u7ecf\u7406\u53ea\u5dee\u4e00\u7ea7\uff0c\u4f46\u662f\u96c6\u56e2\u73b0\u5728\u5df2\u6210\u89c4\u6a21\uff0c\u521d\u6b65\u6446\u8131\u5bf9\u4eba\u624d\u7684\u6e34\u6c42\uff0c\u7ba1\u7406\u4e5f\u65e5\u8d8b\u56fa\u5b9a\uff0c\u5bf9\u7ecf\u7406\u4e00\u7ea7\u7684\u7ba1\u7406\u5c42\u63d0\u5347\u8981\u6c42\u5f88\u9ad8\uff0c\u5fc5\u987b\u8981\u6709\u591a\u90e8\u95e8\u534f\u8c03\u5de5\u4f5c\u7684\u7ecf\u9a8c\uff0c\u624d\u80fd\u4ece\u526f\u7ecf\u7406\u63d0\u5230\u7ecf\u7406\u3002</p><p>\u3000\u3000\u4eba\u4e8b\u90e8\u95e8\u5c5e\u4e8e\u884c\u653f\u90e8\u95e8\uff0c\u4e00\u76f4\u5728\u4eba\u4e8b\u90e8\u95e8\u5de5\u4f5c\uff0c\u52e4\u52e4\u6073\u6073\u5e72\u5230\u9000\u4f11\uff0c\u4e00\u822c\u8bf4\u6765\u90fd\u6ca1\u6709\u673a\u4f1a\u63d0\u5230\u7ecf\u7406\u4e00\u7ea7\uff0c\u96c6\u56e2\u89c9\u5f97\u8c01\u6709\u57f9\u517b\u6f5c\u529b\uff0c\u4f1a\u5728\u90e8\u957f\u6216\u526f\u7ecf\u7406\u4e00\u7ea7\u65f6\u8fdb\u884c\u90e8\u95e8\u95f4\u7684\u804c\u52a1\u8c03\u52a8\uff0c\u5dee\u4e0d\u591a\u5c06\u6574\u4e2a\u884c\u653f\u7ba1\u7406\u5de5\u4f5c\u719f\u6089\u8d77\u6765\uff0c\u624d\u6709\u53ef\u80fd\u63d0\u5230\u7ecf\u7406\u4e00\u7ea7\u3002</p><p>\u3000\u3000\u4e5f\u4e0d\u662f\u6ca1\u6709\u7279\u4f8b\uff0c\u50cf\u5434\u4e3a\u94ed\u8fd9\u80fd\u7b97\u7279\u4f8b\u5427\uff0c\u5f20\u606a\u90fd\u5bf9\u4ed6\u7684\u80fd\u529b\u90fd\u8ba4\u53ef\uff0c\u63d0\u62d4\u7a0b\u5e8f\u5f53\u7136\u80fd\u7b80\u5316\u4e9b\u3002\u8bf4\u8d77\u6765\u8fd8\u662f\u59da\u575a\u3001\u8d75\u9e4f\u8fd9\u4e00\u6279\u4eba\u7684\u673a\u4f1a\u975e\u5e38\u597d\uff0c\u9526\u6e56\u90a3\u65f6\u5bf9\u4eba\u624d\u6781\u4e3a\u9965\u6e34\uff0c\u6709\u80fd\u529b\u7684\u4eba\uff0c\u51e0\u4e4e\u90fd\u63d0\u5230\u76f8\u5bf9\u91cd\u8981\u7684\u5c97\u4f4d\u4e0a\u53bb\u4e86\uff0c\u5bf9\u5de5\u4f5c\u7ecf\u9a8c\u7684\u8981\u6c42\u76f8\u5bf9\u4e0d\u9ad8\uff0c\u6210\u4e3a\u6b64\u65f6\u9526\u6e56\u7ba1\u7406\u5c42\u7684\u4e2d\u575a\u529b\u91cf\u3002\u8fd9\u6279\u4eba\u7684\u673a\u4f1a\u518d\u597d\uff0c\u4e5f\u6ca1\u6709\u50cf\u6768\u4e91\u3001\u5468\u4e00\u5e73\u3001\u848b\u8587\u8fd9\u4e00\u6279\u8ddf\u7740\u5f20\u606a\u4e00\u8d77\u521b\u4e1a\u7684\u5143\u8001\u4eec\u673a\u4f1a\u597d\u3002\u4e0d\u53ef\u5426\u8ba4\uff0c\u6768\u4e91\u3001\u5468\u4e00\u5e73\u3001\u848b\u8587\u90fd\u8981\u7b97\u4e00\u65f6\u4e4b\u9009\u7684\u7cbe\u82f1\u4eba\u7269\uff0c\u80fd\u529b\u7edd\u5bf9\u662f\u6709\u7684\uff0c\u4f46\u662f\u5c06\u4ed6\u4eec\u4e0e\u7ba1\u7406\u5c42\u7684\u4e2d\u575a\u529b\u91cf\u4ee5\u53ca\u540e\u6765\u8fdb\u96c6\u56e2\u7684\u4e00\u4e9b\u80fd\u529b\u7a81\u51fa\u7684\u9aa8\u5e72\u53bb\u6bd4\u8f83\uff0c\u80fd\u529b\u5230\u5e95\u5f3a\u591a\u5c11\uff0c\u4e5f\u5f88\u96be\u8bf4\uff0c\u773c\u754c\u66f4\u5f00\u9614\u5012\u4e5f\u80fd\u7b97\u5b9e\u5b9e\u5728\u5728\u7684\u4f18\u52bf\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u6768\u4e91\u3001\u5468\u4e00\u5e73\u8fd9\u4e9b\u8ddf\u968f\u5f20\u606a\u4e00\u8d77\u521b\u4e1a\u7684\u5143\u8001\uff0c\u4e5f\u4e0d\u662f\u6ca1\u6709\u538b\u529b\uff0c\u5f20\u606a\u5f80\u96c6\u56e2\u91cc\u5f15\u8fdb\u9ad8\u7ea7\u7ba1\u7406\u4eba\u5458\u662f\u4ece\u6765\u90fd\u4e0d\u7559\u4f59\u5730\u7684\uff0c\u50cf\u5f20\u4e9a\u5e73\u53bb\u5e74\u5341\u6708\u4efd\u624d\u8fdb\u9526\u6e56\uff0c\u5728\u7231\u8fbe\u96c6\u56e2\u5185\u90e8\uff0c\u5730\u4f4d\u4ec5\u6b21\u4e8e\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4e09\u4e2a\u8001\u5927\uff0c\u4e0e\u6210\u6c49\u748b\u3001\u8096\u664b\u6210\u7b49\u4eba\u76f8\u5f53\uff0c\u6bd4\u9c81\u5e86\u751f\u3001\u6768\u4e91\u7b49\u4eba\u7684\u5730\u4f4d\u90fd\u9ad8\u3002\u50cf\u5317\u7f8e\u516c\u53f8\u7684\u603b\u88c1\u827e\u745e\u514b\uff0c\u4e5f\u662f\u5728\u4e0e\u5f20\u606a\u89c1\u8fc7\u4e00\u9762\u4e4b\u540e\u5c31\u8fc5\u901f\u63d0\u5230\u76f8\u5f53\u4e8e\u4e8b\u52a1\u90e8\u526f\u603b\u7ecf\u7406\u7684\u56fd\u9645\u8425\u9500\u526f\u603b\u88c1\u7684\u804c\u4f4d\u4e0a\uff0c\u56fd\u9645\u8425\u9500\u603b\u88c1\u8fd8\u662f\u7531\u8096\u664b\u6210\u517c\u4efb\uff0c\u82e5\u662f\u827e\u745e\u514b\u5728\u526f\u603b\u88c1\u4f4d\u5b50\u7684\u8868\u73b0\u5f97\u5230\u80af\u5b9a\uff0c\u4e5f\u5c06\u987a\u7406\u6210\u7ae0\u7684\u63a5\u7ba1\u9664\u5927\u4e2d\u534e\u533a\u4e4b\u5916\u7684\u56fd\u9645\u5e02\u573a\u54c1\u724c\u8425\u9500\u5de5\u4f5c\uff0c\u90a3\u5c31\u4e0e\u6768\u4e91\u7b49\u7ea7\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u9910\u684c\u4e0a\u8c08\u9526\u6e56\u7684\u5185\u90e8\u4e8b\u52a1\uff0c\u674e\u4e49\u6c5f\u662f\u6ca1\u6709\u610f\u89c1\u7684\uff0c\u4ed6\u7ed9\u5f90\u5b66\u5e73\u5f53\u4e86\u51e0\u5e74\u79d8\u4e66\uff0c\u5728\u5f90\u5b66\u5e73\u8c03\u79bb\u4e1c\u6d77\u4e4b\u5916\uff0c\u4ed6\u5c31\u5230\u5730\u65b9\uff0c\u5230\u6d77\u5dde\u62c5\u4efb\u57ce\u5357\u533a\u59d4\u526f\u4e66\u8bb0\u3001\u533a\u957f\uff0c\u73b0\u5728\u53c8\u8c03\u6574\u5230\u5de5\u4e1a\u65b0\u533a\u62c5\u4efb\u7ba1\u59d4\u4f1a\u515a\u5de5\u59d4\u526f\u4e66\u8bb0\u3001\u4e3b\u4efb\u4e00\u804c\u3002</p><p>\u3000\u3000\u4ed6\u957f\u671f\u62c5\u4efb\u79d8\u4e66\u5de5\u4f5c\uff0c\u60f3\u8981\u5728\u4ed5\u9014\u4e0a\u6709\u53d1\u5c55\uff0c\u5728\u53bf\u5904\u7ea7\u7684\u4f4d\u5b50\u5fc5\u9700\u8981\u953b\u70bc\u76f8\u5bf9\u8f83\u957f\u7684\u65f6\u95f4\uff0c\u4e0d\u7136\u4ed6\u7684\u5c65\u5386\u5c06\u662f\u6709\u7f3a\u9677\u7684\uff0c\u603b\u4f1a\u5728\u4e00\u9053\u5361\u5b50\u4e0a\u7ed9\u5361\u4e0b\u6765\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u4e0a\u5348\u8ddf\u5b8b\u57f9\u660e\u901a\u7535\u8bdd\u65f6\u8bf4\u4f60\u5230\u7701\u91cc\u516c\u5e72\uff0c\u4ed6\u6ca1\u6709\u7ec6\u8bf4\u4f60\u6765\u516c\u5e72\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u95ee\u674e\u4e49\u6c5f\u3002</p><p>\u3000\u3000\u201c\u5de5\u4e1a\u65b0\u533a\u7684\u5de5\u4e1a\u50a8\u5907\u7528\u5730\u4e24\u5e74\u5df2\u7ecf\u7528\u6389\u4e00\u534a\uff0c\u518d\u4e0d\u589e\u52a0\u7528\u5730\uff0c\u6309\u7167\u5f53\u524d\u7684\u8d8b\u52bf\u53d1\u5c55\uff0c\u518d\u8fc7\u4e24\u4e09\u5e74\uff0c\u5de5\u4e1a\u65b0\u533a\u5c06\u65e0\u5730\u53ef\u7528\u4e86\u3002\u201d\u674e\u4e49\u6c5f\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f53\u521d\u5c06\u57ce\u5357\u533a\u7684\u76ca\u9686\u3001\u8c61\u5c71\u3001\u65b0\u6865\u4e09\u9547\u62ff\u51fa\u6765\uff0c\u5355\u72ec\u5728\u5f00\u53d1\u533a\u4e4b\u5916\u8bbe\u7acb\u4e86\u89c4\u6a21\u66f4\u5927\u7684\u6d77\u5dde\u5de5\u4e1a\u65b0\u533a\uff0c\u5de5\u4e1a\u65b0\u533a\u603b\u9762\u79ef\u6709\u56db\u5341\u516b\u5e73\u65b9\u516c\u91cc\uff0c\u5de5\u4e1a\u7528\u5730\u89c4\u5212\u9762\u79ef\u8d85\u8fc7\u56db\u4e07\u516d\u5343\u591a\u4ea9\uff0c\u751a\u81f3\u6bd4\u5efa\u90ba\u542f\u52a8\u6570\u5b57\u957f\u5eca\u6784\u60f3\u6218\u7565\u4e4b\u524d\u7684\u9ad8\u65b0\u533a\u90fd\u8981\u5927\u4e0a\u4e00\u500d\uff0c\u518d\u52a0\u4e0a\u5f53\u65f6\u6d77\u5dde\u5e02\u7ecf\u6d4e\u5f00\u53d1\u533a\u7684\u9762\u79ef\uff0c\u6d77\u5dde\u5e02\u7701\u7ea7\u7ecf\u6d4e\u4e13\u5c5e\u533a\u7684\u5de5\u4e1a\u89c4\u5212\u7528\u5730\u8d85\u8fc7\u4e03\u4e07\u4ea9\uff0c\u4ee5\u6d77\u5dde\u5e02\u4e5d\u56db\u3001\u4e5d\u4e94\u5e74\u7684\u7ecf\u6d4e\u53d1\u5c55\u901f\u5ea6\u6765\u770b\uff0c\u8fd9\u4e48\u591a\u5de5\u4e1a\u7528\u5730\uff0c\u7528\u4e0a\u4e8c\u5341\u5e74\u90fd\u6ca1\u6709\u95ee\u9898\uff0c\u90a3\u65f6\u6d77\u5dde\u7ecf\u6d4e\u5f00\u53d1\u533a\u7684\u5de5\u4e1a\u571f\u5730\u624d\u7528\u6389\u4e00\u4e07\u591a\u4ea9\u3002</p><p>\u3000\u3000\u674e\u4e49\u6c5f\u5e26\u7740\u4e9b\u65e0\u5948\u7684\u7b11\u5bb9\uff0c\u53eb\u82e6\u7684\u8bf4\u9053\uff1a\u201c\u5de5\u4e1a\u65b0\u533a\u7ed9\u7701\u91cc\u6279\u4e0b\u6765\u65f6\uff0c\u5de5\u4e1a\u65b0\u533a\u5185\u7684\u7535\u5b50\u5de5\u4e1a\u4e0e\u9020\u7eb8\u5de5\u4e1a\u89c4\u6a21\u5c31\u5df2\u7ecf\u8d85\u8fc7\u4e00\u4e07\u4ea9\uff0c\u8fd9\u4e24\u5e74\u6765\uff0c\u6bcf\u5e74\u65b0\u6279\u5de5\u4e1a\u7528\u5730\u90fd\u5728\u4e03\u516b\u5343\u4ea9\u5de6\u53f3\uff0c\u5de5\u4e1a\u65b0\u533a\u4e5f\u5c31\u5269\u4e0b\u4e0d\u5230\u4e24\u4e07\u50a8\u5907\u7528\u5730\u3002\u4e09\u6708\u4e2d\u65ec\uff0c\u4fe1\u4ea7\u90e8\u653e\u5f00\u53e3\u5b50\uff0c\u4e1c\u8054\u9ad8\u79d1\u3001\u5929\u666e\u7535\u5b50\u8981\u5728\u5de5\u4e1a\u65b0\u533a\u91cc\u5efa\u624b\u673a\u5236\u9020\u57fa\u5730\u3001\u7814\u53d1\u4e2d\u5fc3\uff0c\u4e00\u4e0b\u5b50\u5212\u8d70\u4e24\u5730\u5171\u4e00\u5343\u4e24\u767e\u4ea9\u7684\u6574\u5730\uff1b\u90a3\u4e9b\u6269\u5927\u914d\u5957\u4ea7\u4e1a\u6295\u5165\u7684\u5382\u5546\u53e3\u5b50\u66f4\u5927\uff0c\u4ec5\u914d\u5957\u4ea7\u4e1a\uff0c\u8fd1\u4e00\u4e2a\u591a\u6708\u65b0\u589e\u6279\u5730\u4e5f\u8fbe\u5230\u4e00\u5343\u516b\u767e\u591a\u4ea9\u3002\u9526\u6e56\u65e5\u540e\u82e5\u662f\u8003\u8651\u5c06\u5236\u6d46\u9879\u76ee\u653e\u5728\u6d77\u5dde\uff0c\u4ec5\u8fd9\u4e2a\u5236\u6d46\u9879\u76ee\u4e00\u4e0b\u5b50\u5c31\u7528\u6389\u4e94\u516d\u5343\u4ea9\u7684\u571f\u5730\uff0c\u5269\u4e0b\u4e00\u4e07\u4ea9\u5730\u8fd8\u80fd\u7528\u51e0\u5e74\uff1f\u201d</p><p>\u3000\u3000\u201c\u5e02\u91cc\u6709\u4ec0\u4e48\u8ba1\u5212\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u5e02\u91cc\u60f3\u5c06\u5f00\u53d1\u533a\u5e76\u5165\u5de5\u4e1a\u65b0\u533a\u4e00\u8d77\u89c4\u5212\uff0c\u201d\u674e\u4e49\u6c5f\u8bf4\u9053\uff0c\u201c\u5373\u4f7f\u5c06\u539f\u5f00\u53d1\u533a\u5408\u5e76\u8fc7\u6765\uff0c\u4e5f\u624d\u4e0d\u5230\u516b\u4e07\u4ea9\uff0c\u53ef\u5229\u7528\u7684\u5de5\u4e1a\u7528\u5730\u8d44\u6e90\u4e5f\u4e0d\u8db3\u4e09\u4e07\u4ea9\u2014\u2014\u6211\u770b\u8981\u5c06\u4e1c\u793e\u53bf\u7684\u7af9\u884c\u9547\u518d\u5212\u8fdb\u6765\uff0c\u624d\u80fd\u52c9\u5f3a\u5e94\u4ed8\u4e94\u5e74\u7684\u65f6\u95f4\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6837\u554a\uff0c\u201d\u5f20\u606a\u4f4e\u5934\u624b\u6491\u7740\u989d\u5934\u7a0d\u4f5c\u601d\u541f\uff0c\u8bf4\u9053\uff0c\u201c\u65b0\u89c4\u5212\u7684\u4e8b\u60c5\uff0c\u5e02\u91cc\u5e94\u8be5\u4e89\u53d6\u7684\uff0c\u53e6\u5916\uff0c\u4e5f\u8981\u63d0\u9ad8\u8fdb\u5165\u5de5\u4e1a\u65b0\u533a\u7684\u6807\u51c6\uff0c\u6bd4\u5982\u5de5\u4e1a\u7528\u5730\u4ef7\u683c\u4e5f\u53ef\u4ee5\u9002\u5f53\u7684\u63d0\u9ad8\u4e00\u4e9b\uff0c\u8fd9\u6837\u80fd\u8fbe\u5230\u4ea7\u4e1a\u5206\u6d41\u7684\u76ee\u7684\uff0c\u6ca1\u6709\u5fc5\u8981\u5c06\u6240\u6709\u7684\u4f01\u4e1a\u90fd\u5f80\u5de5\u4e1a\u65b0\u533a\u91cc\u62c9\uff0c\u4e00\u4e9b\u6280\u672f\u542b\u91cf\u4e0d\u592a\u9ad8\u3001\u9644\u52a0\u503c\u8f83\u4f4e\u7684\u4f01\u4e1a\u53ef\u4ee5\u5411\u533a\u53bf\u7684\u5e02\u7ea7\u7ecf\u6d4e\u5f00\u53d1\u533a\u5206\u6d41\u2026\u2026\u201d</p><p>\u3000\u3000\u5efa\u90ba\u4e5d\u4e03\u5e74\u542f\u52a8\u6570\u5b57\u957f\u5eca\u6784\u60f3\u8ba1\u5212\uff0c\u9ad8\u65b0\u533a\u4e00\u6b21\u65b0\u589e\u5de5\u4e1a\u89c4\u5212\u7528\u5730\u591a\u8fbe\u5341\u4e8c\u4e07\u4ea9\uff0c\u5012\u662f\u8fd8\u6709\u5f88\u5927\u53d1\u5c55\u7a7a\u95f4\uff0c\u5373\u4f7f\u8fd9\u4e48\u5927\u7684\u7528\u5730\u7a7a\u95f4\uff0c\u4e00\u4e9b\u914d\u5957\u4ea7\u4e1a\u4e0d\u5411\u65b0\u829c\u4ee5\u53ca\u5efa\u90ba\u90ca\u53bf\u5206\u6d41\uff0c\u5341\u4e07\u4ea9\u4e5f\u4f1a\u5f88\u5feb\u5c31\u8017\u5149\u3002</p><p>\u3000\u3000\u5efa\u90ba\u9ad8\u65b0\u533a\u773c\u4e0b\u5bf9\u6295\u8d44\u5bb9\u91cf\u3001\u73af\u4fdd\u80fd\u8017\u65b9\u9762\u90fd\u63d0\u51fa\u8f83\u9ad8\u7684\u8981\u6c42\uff0c\u5de5\u4e1a\u7528\u5730\u7684\u6bcf\u4ea9\u51fa\u8ba9\u4ef7\u683c\u4e5f\u63d0\u9ad8\u5230\u5341\u4e94\u4e07\u5143\u3002\u56e0\u4e3a\u65b0\u5efa\u4f01\u4e1a\u7a0e\u514d\u5f97\u5389\u5bb3\uff0c\u5de5\u4e1a\u7528\u5730\u5212\u62e8\u4e0a\u4e0d\u63d0\u4e9b\u94b1\u51fa\u6765\uff0c\u5e02\u91cc\u5728\u57fa\u5efa\u6295\u8d44\u7684\u538b\u529b\u4f1a\u5f88\u5927\u2014\u2014\u53ea\u6709\u4e00\u4e9b\u7279\u5b9a\u7684\u3001\u80fd\u63d0\u5347\u5efa\u90ba\u9ad8\u65b0\u533a\u4ea7\u4e1a\u7b49\u7ea7\u7684\u6295\u8d44\u624d\u80fd\u989d\u5916\u83b7\u5f97\u4f18\u60e0\u3002</p><p>\u3000\u3000\u6d77\u5dde\u7684\u5de5\u5730\u7528\u5730\u5730\u4ef7\u73b0\u5728\u5c31\u63d0\u9ad8\u5230\u6bcf\u4ea9\u5341\u4e8c\u4e07\u662f\u6ca1\u6709\u95ee\u9898\u7684\uff0c\u5f20\u606a\u9884\u8ba1\u516b\u4e5d\u540e\u6d77\u5dde\u5e02\u7684\u624b\u673a\u4ea7\u4e1a\u96c6\u7fa4\u624d\u8fbe\u5230\u5343\u4ebf\u89c4\u6a21\uff0c\u5b9e\u5728\u6ca1\u6709\u5fc5\u8981\u5c06\u7d27\u7f3a\u7684\u5de5\u4e1a\u7528\u5730\u6d88\u8017\u5728\u4f4e\u9644\u52a0\u503c\u7684\u4ea7\u4e1a\u4e0a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7528\u8fc7\u665a\u9910\uff0c\u5f20\u606a\u5c31\u6ca1\u6709\u518d\u633d\u7559\u6768\u4e91\u3001\u674e\u4e49\u6c5f\u4ed6\u4eec\uff0c\u5f20\u606a\u5f53\u7740\u4f17\u4eba\u7684\u9762\u8ddf\u7fdf\u4e39\u9752\u8bf4\uff1a\u201c\u6211\u6709\u4e8b\u8981\u8ddf\u4f60\u8c08\uff0c\u4e5f\u4e0d\u7528\u53bb\u529e\u516c\u5ba4\u4e86\uff0c\u5230\u5916\u9762\u8d70\u8d70\u5427\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e00\u526f\u516c\u4e8b\u516c\u529e\u4f7f\u5524\u4eba\u7684\u6837\u5b50\uff0c\u4ee4\u7fdf\u4e39\u9752\u65e0\u4ece\u62d2\u7edd\uff0c\u8fd8\u8981\u88c5\u4f5c\u4e00\u8138\u5766\u7136\u7684\u8ddf\u5f20\u606a\u4e0b\u4e86\u697c\u3002</p><p>\u3000\u3000\u5230\u697c\u4e0b\uff0c\u7fdf\u4e39\u9752\u624d\u5f00\u53e3\u95ee\u4ed6\uff1a\u201c\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u8bf4\uff0c\u6ca1\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6211\u8fd8\u6709\u597d\u4e9b\u4e8b\u60c5\u8981\u505a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u542d\u58f0\u7684\u5f80\u524d\u8d70\uff0c\u8d70\u5230\u6e56\u6ee8\u7eff\u5316\u5e26\uff0c\u624d\u4f38\u624b\u7275\u8fc7\u7fdf\u4e39\u9752\u7684\u624b\uff0c\u8146\u8138\u7b11\u7740\u8bf4\uff1a\u201c\u4eca\u5e74\u5728\u516c\u4ea4\u8f66\u4e0a\u505a\u9519\u4e86\u4e8b\uff0c\u4ee5\u4e3a\u4e0d\u53d7\u60e9\u7f5a\u5c31\u80fd\u9003\u8fc7\u53bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u60f3\u600e\u6837\uff1f\u201d\u7fdf\u4e39\u9752\u89c1\u9644\u8fd1\u90fd\u662f\u60c5\u4fa3\uff0c\u591c\u7a7a\u9634\u6c89\uff0c\u5730\u576a\u706f\u6d6e\u6697\uff0c\u4eba\u79bb\u5f97\u7a0d\u8fdc\u5c31\u770b\u4e0d\u5927\u6e05\u76f8\u8c8c\uff0c\u4fbf\u7ed9\u5f20\u606a\u7275\u7740\u624b\uff0c\u4e5f\u4e0d\u6323\u5f00\u3002</p><p>\u3000\u3000\u201c\u516c\u4ea4\u8f66\u4e0a\u6ca1\u505a\u5b8c\u7684\u4e8b\u60c5\uff0c\u6211\u4eec\u627e\u4e2a\u89d2\u843d\u505a\u5b8c\u5b83\u2026\u2026\u201d\u5f20\u606a\u90aa\u7b11\u7740\u5c06\u7fdf\u4e39\u9752\u6027\u611f\u4e30\u8174\u7684\u8eab\u5b50\u6402\u5728\u6000\u91cc\uff0c\u53cc\u624b\u629a\u7740\u5979\u9971\u6ee1\u6d51\u5706\u7684\u81c0\u90e8\uff0c\u4e0b\u8eab\u8d34\u7740\u5979\u67d4\u8f6f\u5e73\u6ed1\u7684\u5c0f\u8179\uff0c\u95fb\u7740\u5979\u8eab\u4e0a\u4f20\u6765\u6e05\u5e7d\u7684\u4f53\u9999\uff0c\u5e95\u95f4\u817e\u4e0a\u6765\u4e00\u80a1\u5b50\u90aa\u706b\u3002</p><p>\u3000\u3000\u201c\u5550\u2026\u2026\u8c01\u6709\u4f60\u8fd9\u4e48\u4e0b\u6d41\uff1f\u201d\u7fdf\u4e39\u9752\u8f7b\u9a82\u4e86\u4e00\u58f0\uff0c\u611f\u89c9\u5230\u6709\u6839\u68cd\u5b50\u62b5\u7740\u5c0f\u8179\u6e10\u6e10\u53d8\u7c97\u53d8\u786c\uff0c\u538b\u7740\u4e0a\u9762\uff0c\u5374\u4e5f\u6ca1\u6709\u79bb\u5f00\uff0c\u5634\u91cc\u8bf4\u9053\uff0c\u201c\u8054\u60f3\u3001\u4eac\u6e90\u7b49\u516c\u53f8\u660e\u5929\u5728\u4e1c\u5927\u4e3a\u624b\u673a\u7814\u53d1\u4e2d\u5fc3\u53ec\u5f00\u4e13\u573a\u62db\u8058\u4f1a\uff0c\u90fd\u5c06\u6761\u5e45\u6302\u5230\u5b9e\u9a8c\u56ed\u5927\u95e8\u8857\u5bf9\u9762\u4e86\uff0c\u4f60\u6709\u5fc3\u60c5\uff0c\u4e0d\u5982\u591a\u60f3\u60f3\u8fd9\u4e2a\u3002\u201d</p><p>\u3000\u3000\u201c\u6709\u79d1\u7814\u6c34\u5e73\u3001\u5de5\u4f5c\u6001\u5ea6\u3001\u5b66\u4e60\u6001\u5ea6\u4e0d\u600e\u4e48\u5408\u683c\u7684\u4eba\u5458\uff0c\u4e5f\u6b63\u597d\u501f\u8fd9\u4e2a\u673a\u4f1a\u8bf7\u51fa\u53bb\uff0c\u7531\u8054\u60f3\u3001\u4eac\u6e90\u8fd9\u4e9b\u5927\u516c\u53f8\u5e2e\u6211\u4eec\u63a5\u53d7\u2014\u2014\u57fa\u7840\u7684\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u6211\u4eec\u90fd\u5df2\u7ecf\u516c\u5e03\u51fa\u6765\u4e86\uff0c\u53ea\u8981\u4ed6\u4eec\u4e0d\u516c\u7136\u4fb5\u72af\u6211\u4eec\u72ec\u6709\u7684\u4e13\u5229\u6743\uff0c\u5373\u4f7f\u8fd8\u6709\u6280\u672f\u6d41\u5931\u7684\u987e\u8651\uff0c\u4ece\u53e6\u4e00\u65b9\u9762\u6765\u8bf4\uff0c\u4e5f\u662f\u6211\u4eec\u6280\u672f\u5728\u6269\u5927\u5f71\u54cd\uff0c\u201d\u5f20\u606a\u8138\u988a\u8d34\u7740\u7fdf\u4e39\u9752\u6e05\u9999\u817b\u6ed1\u5982\u7389\u7684\u8138\u988a\uff0c\u8f7b\u58f0\u7b11\u7740\u8bf4\uff0c\u201c\u8fd9\u65f6\u5019\u8c08\u8fd9\u4e2a\u662f\u4e0d\u662f\u592a\u715e\u98ce\u666f\u4e86\uff1f\u8fd8\u662f\u8c08\u4e9b\u522b\u7684\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd8\u8c08\u4ec0\u4e48\uff0c\u8c08\u65e5\u672c\u5973\u4e2d\u5b66\u751f\u7684\u96ea\u767d\u5927\u817f\uff1f\u201d\u7fdf\u4e39\u9752\u5a07\u7b11\u7740\u95ee\uff0c\u4e0d\u8ba9\u5f20\u606a\u624b\u5f80\u8ba9\u5979\u88e4\u5b50\u91cc\u94bb\uff0c\u201c\u4f60\u8fd9\u5bb6\u4f19\u771f\u8ba8\u538c\uff0c\u53c8\u4e0d\u662f\u8ba8\u8bba\u6211\u7684\u5927\u817f\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4e70\u6c34\u6bdb\u670d\u3001\u84dd\u88d9\u5b50\uff0c\u4f60\u7a7f\u4e0a\u7ed9\u6211\u770b\u770b\uff1f\u201d\u5f20\u606a\u671d\u65c1\u8fb9\u79fb\u4e86\uff0c\u8ba9\u7fdf\u4e39\u9752\u8de8\u5750\u5230\u4ed6\u5927\u817f\u4e0a\u3002</p><p>\u3000\u3000\u201c\u662f\u4e0d\u662f\u91cc\u9762\u8fd8\u8981\u7a7f\u767d\u8272\u7684\u7eaf\u68c9\u5185\u88e4\uff1f\u4f60\u60f3\u5f97\u7f8e\uff0c\u627e\u8bb8\u601d\u3001\u665a\u6674\u7a7f\u7ed9\u4f60\u770b\u53bb\u3002\u201d\u7fdf\u4e39\u9752\u5a07\u7b11\u7740\u6323\u624e\u7740\u8981\u7ad9\u8d77\u6765\uff0c\u8170\u5374\u7ed9\u5f20\u606a\u73af\u62b1\u7740\uff0c\u626d\u52a8\u4e86\u4e00\u4e0b\uff0c\u90a3\u6839\u786c\u7269\u6070\u597d\u9694\u7740\u51e0\u5c42\u8584\u5e03\u62b5\u7740\u4e24\u817f\u4e4b\u95f4\uff0c\u7ed9\u5f20\u606a\u624b\u6293\u4f4f\u81c0\u8089\uff0c\u542c\u4ed6\u8bf4\uff0c\u201c\u4e0d\u8981\u52a8\uff0c\u5c31\u8fd9\u6837\u2026\u2026\u201d\u4fbf\u4e0d\u518d\u52a8\uff0c\u90fd\u611f\u89c9\u5230\u70ed\u6c14\u900f\u8fc7\u6765\uff0c\u542c\u4ed6\u8bf4\uff0c\u201c\u52a8\u4e00\u52a8\u4e5f\u597d\u2026\u2026\u201d\u4e30\u81c0\u4fbf\u8d34\u7740\u4ed6\u7684\u5927\u817f\u8815\u52a8\u4e24\u4e0b\u3002</p></article>"}}}]);
