(window.webpackJsonp=window.webpackJsonp||[]).push([[170],{192:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e03\u8282 \u800d\u8d56\u548c\u8bc1\u636e\u95ee\u9898",content:"<article><h2>\u7b2c\u4e03\u8282 \u800d\u8d56\u548c\u8bc1\u636e\u95ee\u9898</h2><p>\u51fa\u95e8\u4e00\u770b\uff0c\u53ea\u89c1\u5168\u9547\u706f\u706b\u901a\u660e\uff0c\u4e00\u5806\u4eba\u4ece\u5bb6\u91cc\uff0c\u4ece\u9152\u9986\u91cc\u8dd1\u51fa\u6765\uff0c\u72c2\u543c\u4e71\u53eb\uff0c\u4e2a\u4e2a\u5174\u9ad8\u91c7\u70c8\u7684\u6a21\u6837\uff0c\u4e5f\u4e0d\u77e5\u9053\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\u3002\u743c\u6069\u6293\u4f4f\u8eab\u8fb9\u7ecf\u8fc7\u7684\u4e00\u4e2a\u4eba\u8be2\u95ee\uff0c\u5f97\u5230\u7684\u56de\u7b54\u7a0d\u7a0d\u6709\u4e9b\u51fa\u4e4e\u610f\u6599\u3002</p><p>\u201c\u4f60\u8fd8\u4e0d\u77e5\u9053\u5417\uff1f\u201d\u90a3\u4eba\u6ee1\u5634\u9152\u6c14\uff0c\u8138\u6da8\u5f97\u901a\u7ea2\uff0c\u4e00\u770b\u5c31\u662f\u521a\u4ece\u9152\u9986\u91cc\u51fa\u6765\uff0c\u201c\u6df1\u6c34\u57ce\u7684\u63f4\u519b\u5230\u4e86\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u6c11\u5175\u961f\u957f\u8bf4\u8fc7\uff0c\u56e0\u4e3a\u8725\u8734\u4eba\u6d3b\u52a8\u7a81\u7136\u7316\u7357\u7684\u5173\u7cfb\uff0c\u5315\u9996\u6ee9\u9547\u652f\u6301\u4e0d\u4f4f\uff0c\u5411\u6df1\u6c34\u57ce\u8bf7\u6c42\u63f4\u519b\u3002\u5315\u9996\u6ee9\u9547\u96b6\u5c5e\u4e8e\u6df1\u6c34\u57ce\uff0c\u9047\u5230\u9ebb\u70e6\u627e\u4e0a\u7ea7\u6c42\u52a9\uff0c\u8fd9\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\uff0c\u7136\u800c\u56e0\u4e3a\u5fd9\u4e8e\u5e94\u4ed8\u9634\u9b42\u57ce\u8fd9\u4e2a\u5927\u9ebb\u70e6\uff0c\u6df1\u6c34\u57ce\u538b\u6839\u6ca1\u7a7a\u7406\u776c\u8fd9\u8fb9\u3002\u5982\u4eca\u65e2\u7136\u8bf4\u63f4\u519b\u5230\u4e86\uff0c\u90a3\u4e48\u4e5f\u5c31\u610f\u5473\u7740\u2026\u2026</p><p>\u201c\u6218\u4e89\u7ed3\u675f\u4e86\u3002\u201d</p><p>\u867d\u7136\u5177\u4f53\u60c5\u51b5\u8fd8\u4e0d\u6e05\u695a\uff0c\u4f46\u6df1\u6c34\u57ce\u65b9\u9762\u7684\u63f4\u519b\u6240\u5e26\u6765\u7684\u6d88\u606f\u662f\uff1a\u9634\u9b42\u57ce\u521b\u9020\u51fa\u7684\u9634\u5f71\u5927\u5e55\u5df2\u7ecf\u88ab\u6467\u6bc1\uff0c\u878d\u5316\u81f3\u9ad8\u51b0\u5ddd\u7684\u4f01\u56fe\u5f7b\u5e95\u843d\u7a7a\u3002\u800c\u5728\u906d\u9047\u8fd9\u6837\u7684\u5931\u8d25\u540e\uff0c\u9634\u9b42\u57ce\u4f3c\u4e4e\u5f00\u59cb\u5168\u9762\u6536\u7f29\uff0c\u653e\u5f03\u4e86\u539f\u672c\u7684\u6269\u5c55\u59ff\u6001\uff1b\u4ee5\u6df1\u6c34\u57ce\u3001\u94f6\u6708\u8054\u90a6\u3001\u79d1\u7c73\u5c14\u738b\u56fd\u7b49\u4e3a\u4ee3\u8868\u7684\u8054\u76df\u519b\u4e5f\u5728\u6b64\u524d\u7684\u6218\u4e89\u4e2d\u635f\u5931\u6c89\u91cd\uff0c\u9700\u8981\u5598\u606f\u7684\u65f6\u95f4\u3002\u4e8e\u662f\u5728\u53cc\u65b9\u5fc3\u7167\u4e0d\u5ba3\u7684\u914d\u5408\u4e0b\uff0c\u867d\u7136\u672a\u66fe\u7b7e\u7f72\u4ec0\u4e48\u6b63\u5f0f\u7684\u505c\u6218\u534f\u8bae\uff0c\u4f46\u81f3\u5c11\uff0c\u548c\u5e73\u662f\u518d\u5ea6\u964d\u4e34\u4e86\u3002</p><p>\u8fd9\u662f\u4e2a\u597d\u6d88\u606f\uff0c\u5bf9\u4e8e\u5315\u9996\u6ee9\u9547\u6765\u8bf4\uff0c\u6df1\u6c34\u57ce\u7684\u63f4\u519b\u62b5\u8fbe\uff0c\u90a3\u4e9b\u8ba8\u538c\u7684\u8725\u8734\u4eba\u4eec\u5c31\u4e0d\u518d\u662f\u5a01\u80c1\u4e86\uff1b\u5bf9\u4e8e\u6765\u6765\u5f80\u5f80\u7684\u5546\u4eba\u6765\u8bf4\uff0c\u524d\u6bb5\u65f6\u95f4\u7684\u6218\u4e89\uff0c\u867d\u8bf4\u6ca1\u6709\u5b8c\u5168\u65ad\u7edd\u5546\u8def\uff0c\u4f46\u7ec8\u7a76\u8fd8\u662f\u9020\u6210\u4e86\u5f88\u5927\u7684\u5f71\u54cd\u3002\u5982\u4eca\u53cc\u65b9\u505c\u6218\uff0c\u81ea\u7136\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002</p><p>\u751a\u81f3\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u8fd9\u4e5f\u4e0d\u662f\u4ec0\u4e48\u574f\u6d88\u606f\u3002\u867d\u8bf4\u57fa\u4e8e\u672c\u80fd\u7684\u7acb\u573a\uff0c\u4ed6\u81ea\u7136\u4e0d\u4f1a\u671f\u671b\u9634\u9b42\u57ce\u88ab\u6253\u8d25\uff0c\u4f46\u4ed6\u4f9d\u7136\u89c9\u5f97\u8fd9\u6b21\u878d\u5316\u81f3\u9ad8\u51b0\u5ddd\u7684\u505a\u6cd5\uff0c\u662f\u4e00\u5927\u5931\u7b56\u3002\u5c31\u7b97\u5bf9\u81ea\u5df1\u7684\u5b9e\u529b\u518d\u6709\u81ea\u4fe1\uff0c\u5c31\u7b97\u518d\u8f7b\u89c6\u7269\u8d28\u754c\u7684\u9b54\u6cd5\u6c34\u51c6\uff0c\u4e5f\u6ca1\u5fc5\u8981\u5982\u6b64\u56a3\u5f20\u5427\u3002</p><p>\u5982\u679c\u60f3\u8981\u91cd\u5efa\u8010\u745f\u745e\u5c14\uff0c\u518d\u73b0\u6614\u65e5\u9b54\u6cd5\u5e1d\u56fd\u8f89\u714c\u7684\u8bdd\uff0c\u4ec5\u4ec5\u9760\u529b\u91cf\uff0c\u90a3\u662f\u8fdc\u8fdc\u4e0d\u591f\u7684\u5462\u3002\u743c\u6069\u9ed8\u60f3\u7740\uff0c\u8fd9\u79cd\u7406\u5ff5\u5176\u5b9e\u4e5f\u662f\u56e0\u4e3a\u4ed6\u7684\u5deb\u5e08\u8eab\u4efd\u3002\u5bf9\u4e8e\u5deb\u5e08\u6765\u8bf4\uff0c\u9b54\u6cd5\u5c31\u662f\u529b\u91cf\uff0c\u4f46\u5c3d\u53ef\u80fd\u907f\u514d\u4f7f\u7528\u9b54\u6cd5\uff0c\u624d\u662f\u5deb\u5e08\u771f\u6b63\u9ad8\u660e\u7684\u8c61\u5f81\u3002</p><p>\u529b\u91cf\u4e0d\u53ef\u8f7b\u7528\uff0c\u62e5\u6709\u8d8a\u5f3a\u7684\u529b\u91cf\uff0c\u8d8a\u8981\u61c2\u5f97\u81ea\u6211\u7ea6\u675f\u3002\u8fd9\u5e76\u975e\u662f\u4ec0\u4e48\u8c26\u900a\u6216\u8005\u793c\u8ba9\uff0c\u4e5f\u548c\u6b63\u4e49\u3001\u8363\u8a89\u3001\u9053\u5fb7\u4e4b\u7c7b\u7684\u8bcd\u5b8c\u5168\u65e0\u5173\uff0c\u8fd9\uff0c\u53ea\u4e0d\u8fc7\u662f\u667a\u6167\u7f62\u4e86\u3002\u743c\u6069\u867d\u7136\u6682\u65f6\u8fd8\u4e0d\u80fd\u5b8c\u5168\u9886\u4f1a\uff0c\u4f46\u4ed6\u786e\u4fe1\u65e0\u7591\u3002</p><p>\u8fd4\u56de\u623f\u95f4\uff0c\u6b63\u60f3\u544a\u8bc9\u6885\u83f2\u65af\u8fd9\u4e2a\u6d88\u606f\uff0c\u5374\u53d1\u73b0\u5979\u5df2\u7ecf\u7761\u7740\u4e86\uff0c\u5b89\u9759\u5f97\u50cf\u4e00\u53ea\u5c0f\u732b\u4f3c\u7684\u3002\u743c\u6069\u7b11\u4e86\u7b11\uff0c\u4e0a\u5e8a\u8eba\u5728\u5979\u8eab\u8fb9\uff0c\u4ece\u80cc\u540e\u5c06\u5979\u62b1\u7740\uff0c\u95fb\u8005\u5979\u8eab\u4e0a\u6de1\u6de1\u7684\u9999\u6c14\uff0c\u4e0d\u89c9\u7761\u610f\u6d8c\u6765\uff0c\u4e5f\u6c89\u6c89\u7761\u4e86\u8fc7\u53bb\u3002</p><p>\u7b2c\u4e8c\u5929\u4e00\u65e9\uff0c\u743c\u6069\u4ece\u7761\u68a6\u4e2d\u9192\u6765\uff0c\u8ff7\u8ff7\u7cca\u7cca\u7741\u5f00\u773c\uff0c\u5c31\u770b\u89c1\u4e00\u8eab\u94f6\u7532\u7684\u5c11\u5973\u6b63\u7ad9\u5728\u5e8a\u8fb9\uff0c\u4fef\u89c6\u7740\u4ed6\u3002\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u6491\u8d77\u8eab\u6765\uff0c\u63c9\u63c9\u773c\u775b\uff0c\u201c\u4f60\u9192\u4e86\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u6628\u665a\u559d\u5f97\u90a3\u4e48\u9189\uff0c\u5c45\u7136\u4e00\u5927\u65e9\u5c31\u7cbe\u795e\u5955\u5955\u5168\u526f\u6b66\u88c5\u5730\u7ad9\u5728\u5e8a\u524d\uff0c\u743c\u6069\u4e0d\u7531\u5f97\u611f\u53f9\u5723\u6b66\u58eb\u679c\u7136\u5c31\u662f\u975e\u6bd4\u5bfb\u5e38\u3002\u4ed6\u6447\u6447\u8111\u888b\uff0c\u6253\u8d77\u7cbe\u795e\u4e0b\u5e8a\u7a7f\u8863\u670d\uff0c\u201c\u51e0\u70b9\u4e86\uff1f\u201d\u4ed6\u968f\u53e3\u95ee\u3002</p><p>\u201c\u5feb\u516b\u70b9\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6211\u521a\u624d\u6b63\u5728\u8003\u8651\uff0c\u5982\u679c\u4f60\u518d\u4e0d\u9192\uff0c\u8981\u4e0d\u8981\u5f04\u76c6\u51b7\u6c34\u6765\u6d47\u4e00\u4e0b\u3002\u201d</p><p>\u201c\u4e0d\u7528\u8fd9\u6837\u5427\u3002\u201d\u743c\u6069\u62a5\u6028\u7740\uff0c\u4e00\u8fb9\u5957\u4e0a\u957f\u888d\uff0c\u7136\u540e\u4ed6\u7a81\u7136\u60f3\u8d77\u4ec0\u4e48\u4f3c\u7684\uff0c\u8f6c\u8fc7\u5934\u6765\u770b\u7740\u6885\u83f2\u65af\uff0c\u4eff\u4f5b\u4e0d\u8ba4\u8bc6\u4e00\u822c\uff0c\u4ed4\u7ec6\u6253\u91cf\u7740\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u6885\u83f2\u65af\u88ab\u4ed6\u770b\u5f97\u6709\u70b9\u4e0d\u81ea\u5728\u3002</p><p>\u201c\u827e\u5f25\u8587\uff0c\u6211\u89c9\u5f97\u4f60\u521a\u624d\u8bf4\u8bdd\u597d\u50cf\u6709\u70b9\u4e0d\u5bf9\u52b2\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u548c\u4ee5\u524d\u7684\u611f\u89c9\u5f88\u4e0d\u4e00\u6837\u5462\u3002\u201d</p><p>\u201c\u662f\u4e48\uff1f\u201d</p><p>\u201c\u662f\uff0c\u201d\u743c\u6069\u786e\u5b9a\uff0c\u201c\u5982\u679c\u662f\u4ee5\u524d\u7684\u8bdd\uff0c\u4f60\u5e94\u8be5\u4e0d\u4f1a\u548c\u6211\u8bf4\u8fd9\u4e48\u2026\u2026\u5443\uff0c\u201d\u4ed6\u76b1\u7740\u7709\u5934\uff0c\u4e00\u65f6\u627e\u4e0d\u51fa\u6070\u5f53\u7684\u8bcd\u8bed\u6765\u5f62\u5bb9\uff0c\u201c\u5c31\u662f\u8fd9\u4e48\u2026\u2026\u8f7b\u677e\u7684\u8bdd\u5427\u3002\u201d</p><p>\u201c\u8f7b\u677e\u7684\u8bdd\uff1f\u201d\u6885\u83f2\u65af\u4e5f\u6709\u4e9b\u83ab\u540d\u5176\u5999\u3002</p><p>\u201c\u5c31\u662f\u4e0d\u90a3\u4e48\u76f4\u622a\u4e86\u5f53\u7684\uff0c\u4e0d\u90a3\u4e48\u7b80\u6d01\u521a\u786c\u7684\uff0c\u4e0d\u90a3\u4e48\u5e72\u8106\u5229\u843d\u7684\u8bdd\u5427\uff0c\u201d\u743c\u6069\u6325\u6325\u624b\uff0c\u201c\u53cd\u6b63\uff0c\u5c31\u662f\u8fd9\u6837\u4e86\uff0c\u8981\u6211\u89e3\u91ca\u4e5f\u89e3\u91ca\u4e0d\u6e05\u695a\uff0c\u5c31\u662f\u79cd\u611f\u89c9\u7f62\u4e86\u3002\u201d</p><p>\u201c\u5514\u3002\u201d\u6885\u83f2\u65af\u4e5f\u5c31\u4e0d\u518d\u8ffd\u95ee\u3002</p><p>\u201c\u4e0d\u4f1a\u662f\u6628\u665a\u559d\u9189\u4e86\u8fd8\u6ca1\u6e05\u9192\u5427\u3002\u201d\u743c\u6069\u73a9\u7b11\u7740\u3002</p><p>\u201c\u6211\u6628\u665a\u559d\u9189\u4e86\u5417\uff1f\u201d</p><p>\u201c\u5bf9\u554a\uff0c\u9189\u5f97\u4e00\u584c\u7cca\u6d82\uff0c\u8bf4\u4e00\u4e9b\u83ab\u540d\u5176\u5999\u7684\u8bdd\u5462\uff0c\u5e78\u597d\u53ea\u6709\u6211\u542c\u89c1\uff0c\u5426\u5219\u4f20\u51fa\u53bb\u7684\u8bdd\uff0c\u4e00\u5b9a\u4f1a\u8ba9\u5927\u5bb6\u5413\u4e00\u8df3\u7684\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u6211\u90fd\u4e0d\u8bb0\u5f97\u6211\u8bf4\u8fc7\u4ec0\u4e48\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u4f1a\u5427\uff0c\u201d\u743c\u6069\u8f6c\u8fc7\u5934\u6765\uff0c\u6000\u7591\u5730\u770b\u7740\u6885\u83f2\u65af\uff0c\u201c\u4f60\u6628\u665a\u8fd8\u7b54\u5e94\u6211\u2026\u2026\u4f60\u4e0d\u4f1a\u662f\u53c8\u8981\u800d\u8d56\u5427\uff1f\u201d</p><p>\u6885\u83f2\u65af\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u5c31\u60e6\u8bb0\u7740\u8fd9\u4e2a\u4e48\u3002\u201d</p><p>\u201c\u8bb0\u5f97\u5c31\u597d\uff0c\u6211\u53ef\u662f\u671f\u5f85\u5f88\u4e45\u4e86\u5440\uff0c\u201d\u743c\u6069\u677e\u4e86\u53e3\u6c14\uff0c\u5fcd\u4e0d\u4f4f\u4f38\u624b\u629a\u6478\u6885\u83f2\u65af\u90a3\u5f20\u7cbe\u81f4\u7684\u8138\uff0c\u201c\u827e\u5f25\u8587\uff0c\u4f60\u4f3c\u4e4e\u6700\u8fd1\u7b11\u5f97\u6bd4\u4ee5\u524d\u591a\u4e86\u5462\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d\u5c11\u5973\u504f\u5934\u60f3\u4e86\u60f3\uff0c\u201c\u597d\u50cf\u662f\u5427\uff0c\u4ee5\u524d\u4e5f\u6ca1\u4ec0\u4e48\u6709\u8da3\u7684\u4e8b\u60c5\uff0c\u6240\u4ee5\u5c31\u61d2\u5f97\u7b11\u4e86\u3002\u201d</p><p>\u5f88\u6b63\u5e38\uff0c\u4ee5\u524d\u548c\u4f60\u6253\u4ea4\u9053\u7684\uff0c\u4e0d\u662f\u654c\u4eba\u5c31\u662f\u6559\u4f1a\u91cc\u7684\u7267\u5e08\uff0c\u60f3\u5fc5\u4e5f\u90fd\u6b7b\u677f\u5f97\u5f88\uff0c\u54ea\u91cc\u6709\u4ec0\u4e48\u6709\u8da3\u7684\u4e8b\u60c5\u3002\u743c\u6069\u60f3\u7740\uff0c\u201c\u5bf9\u4e86\uff0c\u827e\u5f25\u8587\uff0c\u4f60\u7684\u8eab\u4efd\uff0c\u4f60\u90a3\u4e9b\u6559\u53cb\u77e5\u9053\u4e48\uff1f\u201d</p><p>\u201c\u9ad8\u5c42\u7684\u4e00\u4e9b\u4eba\u77e5\u9053\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6240\u4ee5\u4ed6\u4eec\u90fd\u4e0d\u6562\u63a5\u8fd1\u6211\u5462\uff0c\u201d\u5979\u7684\u5634\u89d2\u5fae\u5fae\u4e0a\u7fd8\uff0c\u53c8\u52fe\u52d2\u51fa\u7565\u5e26\u5632\u8bbd\u7684\u5f27\u7ebf\u6765\uff0c\u201c\u8bf4\u8d77\u6765\uff0c\u743c\u6069\uff0c\u4e5f\u53ea\u6709\u50cf\u4f60\u8fd9\u6837\u5b8c\u5168\u6ca1\u6982\u5ff5\u7684\u4eba\uff0c\u624d\u4f1a\u5728\u77e5\u9053\u6211\u662f\u5df4\u5c14\u4e4b\u5b50\u540e\uff0c\u8fd8\u8fd9\u4e48\u4e0d\u5f53\u4e00\u56de\u4e8b\u5427\u3002\u201d</p><p>\u201c\u90a3\u6211\u5e94\u8be5\u600e\u4e48\u53cd\u5e94\u624d\u5bf9\uff1f\u201d\u743c\u6069\u8038\u8038\u80a9\uff0c\u201c\u5927\u60ca\u5931\u8272\uff1f\u5413\u5f97\u4ece\u7a97\u6237\u91cc\u8df3\u51fa\u53bb\uff1f\u5c31\u50cf\u4f60\u66fe\u7ecf\u8bf4\u7684\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6709\u4e00\u4e2a\u6240\u6765\u81ea\u7684\u5730\u65b9\uff0c\u6211\u6765\u81ea\u9634\u9b42\u57ce\uff0c\u6b63\u5982\u4f60\u6765\u81ea\u8ff7\u65af\u5353\u8bfa\uff0c\u6ca1\u4ec0\u4e48\u597d\u60ca\u8bb6\u7684\u3002\u540c\u6837\u7684\u9053\u7406\uff0c\u6bcf\u4e2a\u4eba\u90fd\u6709\u4e00\u4e2a\u7236\u4eb2\uff0c\u4f60\u7684\u7236\u4eb2\u662f\u4e2a\u795e\u7947\uff0c\u6b63\u5982\u6211\u7684\u7236\u4eb2\u662f\u4e2a\u5c0f\u5546\u4eba\uff0c\u6ca1\u4ec0\u4e48\u597d\u60ca\u8bb6\u7684\u3002\u201d</p><p>\u5c11\u5973\u82e5\u6709\u6240\u601d\u5730\u70b9\u5934\uff0c\u201c\u542c\u8d77\u6765\u597d\u50cf\u662f\u8fd9\u4e2a\u9053\u7406\u3002\u201d</p><p>\u201c\u6240\u4ee5\u5566\uff0c\u795e\u5b50\u5c31\u795e\u5b50\uff0c\u6211\u6709\u4ec0\u4e48\u597d\u60ca\u8bb6\u7684\uff0c\u201d\u743c\u6069\u4e00\u8fb9\u53bb\u6536\u62fe\u4e1c\u897f\u4e00\u8fb9\u8bf4\uff0c\u201c\u5176\u5b9e\u6211\u65e9\u8be5\u60f3\u5230\uff0c\u50cf\u4f60\u8fd9\u6837\u6f02\u4eae\u7684\u5973\u5b69\u5b50\uff0c\u5c31\u4e0d\u53ef\u80fd\u662f\u51e1\u4eba\u7684\u5b58\u5728\u554a\u3002\u201d</p><p>\u6885\u83f2\u65af\u7684\u8138\u4e0a\u5fae\u5fae\u7ea2\u4e86\u7ea2\uff0c\u201c\u6211\u4eec\u8be5\u8d70\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u65f6\u5019\u4e0d\u65e9\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u5403\u996d\u65e9\u996d\uff0c\u6b63\u51c6\u5907\u627e\u5546\u961f\u642d\u987a\u8def\u8f66\uff0c\u5374\u88ab\u4e24\u4e2a\u6c11\u5175\u627e\u6765\uff0c\u8bf4\u662f\u6df1\u6c34\u57ce\u6d3e\u9063\u6765\u7684\u63f4\u519b\u9996\u9886\uff0c\u8981\u4e86\u89e3\u4e00\u4e0b\u6709\u5173\u8725\u8734\u4eba\u7684\u60c5\u51b5\u2014\u2014\u4e3b\u8981\u662f\u8981\u4e86\u89e3\u8725\u8734\u4eba\u7267\u5e08\u7684\u8d44\u6599\u3002\u56e0\u4e3a\u5728\u6628\u65e5\u7684\u6218\u6597\u4e2d\uff0c\u6885\u83f2\u65af\u662f\u552f\u4e00\u6b63\u9762\u548c\u90a3\u4e2a\u8725\u8734\u4eba\u7267\u5e08\u4ea4\u8fc7\u624b\u7684\u4eba\uff0c\u5728\u573a\u5f88\u591a\u4eba\u90fd\u770b\u89c1\u4e86\uff0c\u4e8e\u662f\u81ea\u7136\u5c31\u627e\u5230\u5979\u5934\u4e0a\u3002</p><p>\u6885\u83f2\u65af\u6025\u7740\u8d76\u8def\uff0c\u672c\u4e0d\u60f3\u591a\u803d\u6401\uff0c\u4f46\u8fd9\u4e24\u4e2a\u6c11\u5175\u7f20\u4eba\u5f97\u5f88\uff0c\u6700\u540e\u8fd8\u662f\u53bb\u4e86\u4e00\u8d9f\u3002\u4e00\u756a\u6298\u817e\u4e4b\u540e\uff0c\u5df2\u7ecf\u5feb\u5230\u4e0a\u5348\u5341\u70b9\uff0c\u8be5\u4e0a\u8def\u7684\u5546\u961f\u90fd\u5df2\u7ecf\u51fa\u53d1\u4e86\uff0c\u5374\u4ece\u54ea\u91cc\u642d\u8f66\u53bb\u3002</p><p>\u201c\u90a3\u5c31\u53ea\u80fd\u9a91\u9a6c\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u9547\u4e0a\u4e5f\u6709\u9a6c\u8d29\u5b50\uff0c\u4e70\u4e0a\u4e24\u5339\u9a6c\u4e0d\u662f\u95ee\u9898\uff0c\u201c\u53cd\u6b63\u8d76\u65f6\u95f4\uff0c\u9a91\u9a6c\u6700\u5feb\u3002\u201d</p><p>\u201c\u4f46\u6211\u4e0d\u4f1a\u9a91\u9a6c\u3002\u201d\u743c\u6069\u63d0\u51fa\u6297\u8bae\u3002</p><p>\u201c\u5b66\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6211\u6559\u4f60\u3002\u201d</p><p>\u8bdd\u8bf4\u5f97\u662f\u5f88\u5e72\u8106\u5229\u843d\uff0c\u7136\u800c\u5b9e\u9645\u64cd\u4f5c\u8d77\u6765\u5c31\u5b8c\u5168\u4e24\u7801\u4e8b\u3002\u743c\u6069\u7684\u8fd0\u52a8\u795e\u7ecf\u5e76\u4e0d\u53d1\u8fbe\uff0c\u8fd9\u70b9\u4ece\u4ed6\u5f53\u5e74\u8ddf\u7530\u4f2f\u5149\u5b66\u6b66\u529f\u4e0d\u6210\u5c31\u53ef\u4ee5\u770b\u5f97\u51fa\u6765\uff0c\u6240\u4ee5\u9a91\u9a6c\u8fd9\u79cd\u9ad8\u96be\u5ea6\u7684\u8fd0\u52a8\uff0c\u5bf9\u4ed6\u6765\u8bf4\u4e5f\u5c31\u672a\u514d\u8270\u96be\u4e86\u70b9\u3002\u867d\u7136\u6709\u6885\u83f2\u65af\u8fd9\u4e2a\u4f18\u79c0\u7684\u8001\u5e08\u6307\u5bfc\uff0c\u52c9\u52c9\u5f3a\u5f3a\u7b97\u662f\u6ca1\u4ece\u9a6c\u4e0a\u6454\u4e0b\uff0c\u4f46\u4e00\u5929\u4e0b\u6765\uff0c\u8fd8\u662f\u5168\u8eab\u9aa8\u9abc\u90fd\u50cf\u6563\u4e86\u67b6\u4f3c\u7684\uff0c\u5927\u817f\u5185\u4fa7\u78e8\u5f97\u706b\u8fa3\u8fa3\u7684\u75bc\u3002</p><p>\u5f53\u665a\u62b5\u8fbe\u897f\u5eb7\u5e03\u9547\uff0c\u627e\u4e86\u5bb6\u9152\u5e97\uff0c\u743c\u6069\u5f80\u5e8a\u4e0a\u4e00\u8d9f\uff0c\u4fbf\u518d\u4e5f\u4e0d\u613f\u610f\u8d77\u8eab\u4e86\u3002\u201c\u4e0d\u884c\u4e86\uff0c\u201d\u4ed6\u62a5\u6028\u7740\uff0c\u201c\u592a\u7d2f\u4e86\uff0c\u6211\u4eec\u660e\u5929\u8fd8\u662f\u6539\u5750\u9a6c\u8f66\u5427\u3002\u201d</p><p>\u201c\u4e0d\u884c\uff0c\u201d\u6885\u83f2\u65af\u65ad\u7136\u5426\u51b3\u6389\u4ed6\u7684\u63d0\u8bae\uff0c\u201c\u8fd9\u5c0f\u9547\u4e0a\u54ea\u91cc\u96c7\u9a6c\u8f66\uff0c\u518d\u8bf4\u6211\u4eec\u73b0\u5728\u8981\u8d76\u65f6\u95f4\u3002\u522b\u5fd8\u4e86\uff0c\u6211\u4eec\u73b0\u5728\u8bf4\u4e0d\u5b9a\u5df2\u7ecf\u662f\u901a\u7f09\u72af\u4e86\uff0c\u535a\u5f97\u4e4b\u95e8\u7684\u8ffd\u5175\u6216\u8bb8\u5c31\u5728\u8def\u4e0a\u5462\u3002\u201d</p><p>\u597d\u5427\uff0c\u8fd9\u4e2a\u7406\u7531\u743c\u6069\u65e0\u6cd5\u53cd\u9a73\uff0c\u800c\u4e14\u4ed6\u73b0\u5728\u4e5f\u6ca1\u8fd9\u4e2a\u529b\u6c14\u3002\u201c\u90a3\u6211\u5148\u7761\u4e86\uff0c\u201d\u4ed6\u626f\u8fc7\u6795\u5934\uff0c\u201c\u5bf9\u4e86\uff0c\u4f60\u8fd8\u6ca1\u544a\u8bc9\u6211\u539f\u56e0\u5462\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u539f\u56e0\uff1f\u201d</p><p>\u201c\u4f60\u6628\u665a\u7b54\u5e94\u7684\u554a\uff0c\u4f60\u4e0d\u613f\u610f\u8ba9\u6211\u2026\u2026\u90a3\u4e2a\u7684\u539f\u56e0\u3002\u201d</p><p>\u201c\u5514\uff0c\u90a3\u4e2a\u554a\uff0c\u201d\u6885\u83f2\u65af\u751c\u751c\u5730\u7b11\u7740\uff0c\u8131\u4e0b\u94f6\u7532\uff0c\u4e0a\u5e8a\u8dea\u5728\u743c\u6069\u8eab\u65c1\uff0c\u53cc\u624b\u6258\u7740\u816e\uff0c\u4fef\u89c6\u7740\u4ed6\u3002\u5979\u7684\u886c\u886b\u7b2c\u4e00\u7c92\u7ebd\u6263\u662f\u89e3\u5f00\u7684\uff0c\u6709\u610f\u65e0\u610f\u5730\u9732\u51fa\u80f8\u53e3\u96ea\u767d\u7684\u808c\u80a4\uff0c\u5f15\u8bf1\u7740\u743c\u6069\u7684\u76ee\u5149\u5f80\u91cc\u770b\uff0c\u201c\u6211\u662f\u7b54\u5e94\u4e86\uff0c\u4f46\u6211\u5f88\u597d\u5947\u4e00\u4ef6\u4e8b\u5462\uff0c\u201d\u5979\u5728\u743c\u6069\u8033\u8fb9\u8f7b\u8f7b\u8bf4\uff0c\u5410\u6c14\u82e5\u5170\uff0c\u201c\u5c31\u7b97\u6211\u544a\u8bc9\u4f60\uff0c\u73b0\u5728\u4f60\u53ef\u4ee5\u78b0\u6211\u4e86\u2014\u2014\u4f46\u4f60\u8fd8\u6709\u4f53\u529b\u505a\u8fd9\u4ef6\u4e8b\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u6545\u610f\u7684\uff01\u201d\u743c\u6069\u6124\u6124\u4e0d\u5e73\u3002</p><p>\u201c\u6709\u8bc1\u636e\u5417\uff1f\u201d\u6885\u83f2\u65af\u53cd\u95ee\uff0c\u201c\u6211\u4eec\u5723\u6b66\u58eb\uff0c\u8bf4\u8bdd\u6700\u8bb2\u7a76\u8bc1\u636e\u7684\u3002\u6ca1\u6709\u8bc1\u636e\u5c31\u4e71\u8bf4\u8bdd\uff0c\u90a3\u53ef\u662f\u8bfd\u8c24\u54e6\u3002\u201d</p><p>\u743c\u6069\u4f38\u624b\u4e00\u628a\u5c06\u5979\u6402\u8fc7\u6765\uff0c\u4f46\u8fd9\u4e2a\u52a8\u4f5c\u7275\u52a8\u539f\u672c\u5c31\u5df2\u7ecf\u75bc\u75db\u7684\u808c\u8089\uff0c\u8ba9\u4ed6\u5012\u5438\u4e86\u53e3\u51b7\u6c14\u3002\u6885\u83f2\u65af\u987a\u52bf\u5728\u4ed6\u8eab\u65c1\u8eba\u4e0b\u6765\uff0c\u201c\u597d\u5566\uff0c\u201d\u5979\u5b89\u6170\u7740\uff0c\u201c\u6211\u7b54\u5e94\u4e86\uff0c\u5c31\u4e0d\u4f1a\u53cd\u6094\uff0c\u4f46\u4f60\u5c31\u975e\u8981\u6025\u5728\u8fd9\u51e0\u5929\u4e48\u2026\u2026\u201d\u5979\u5fae\u5fae\u6c89\u9ed8\u4e86\u4f1a\uff0c\u201c\u8ba9\u6211\u60f3\u60f3\u597d\u4e0d\u597d\u3002\u201d</p><p>\u743c\u6069\u8fd8\u80fd\u6709\u4ec0\u4e48\u53cd\u5bf9\u610f\u89c1\u4e48\u3002\u201c\u597d\uff0c\u201d\u4ed6\u65e0\u5948\u5730\u8bf4\uff0c\u201c\u968f\u4f60\u559c\u6b22\u3002\u201d</p><p>\u6885\u83f2\u65af\u5fae\u7b11\u8d77\u6765\uff0c\u5728\u4ed6\u989d\u5934\u4e0a\u8f7b\u8f7b\u4eb2\u4e86\u4e0b\uff0c\u201c\u4f60\u5148\u7761\u5427\uff0c\u6211\u770b\u770b\u5730\u56fe\u3002\u201d</p><p>\u743c\u6069\u4eca\u5929\u7740\u5b9e\u7d2f\u5f97\u5389\u5bb3\u4e86\uff0c\u5f88\u5feb\u5c31\u6c89\u6c89\u7761\u53bb\u3002\u5728\u7761\u7740\u4e4b\u524d\uff0c\u4ed6\u8ff7\u8ff7\u7cca\u7cca\u4e2d\u4f3c\u4e4e\u542c\u5230\u6885\u83f2\u65af\u8bf4\u4e86\u53e5\u8bdd\uff0c\u5979\u7684\u8bed\u6c14\u6709\u4e9b\u51dd\u91cd\u7684\u6837\u5b50\uff0c\u4eff\u4f5b\u662f\u4ef6\u5f88\u91cd\u8981\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u660e\u5929\uff0c\u5c31\u5f97\u7ecf\u8fc7\u8fd9\u72ec\u89d2\u517d\u4e4b\u6d41\u4e86\u3002\u201d</p></article>"}}}]);
