(window.webpackJsonp=window.webpackJsonp||[]).push([[1703],{1726:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e03\u8282 \u529b\u91cf",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e03\u8282 \u529b\u91cf</h2><p>\u79e6\u6a80\u662f\u4f55\u8bb8\u4eba\u4e5f\uff0c\u743c\u6069\u5e76\u4e0d\u77e5\u9053\uff0c\u5341\u4e8c\u9ec4\u91d1\u5de8\u50cf\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u743c\u6069\u66f4\u4e0d\u6e05\u695a\u3002\u56e0\u4e3a\u98df\u4eba\u9b54\u9b54\u50cf\u548c\u91d1\u5c5e\u602a\u7269\u5df2\u7ecf\u5f00\u59cb\u4ea4\u624b\uff0c\u800c\u4e14\u6253\u5f97\u8f70\u9686\u8f70\u9686\u9707\u5929\u54cd\uff0c\u6240\u4ee5\u5deb\u5996\u6700\u540e\u90a3\u4e00\u6bb5\u8bdd\uff0c\u4ed6\u5176\u5b9e\u771f\u6b63\u542c\u6e05\u695a\u7684\uff0c\u53ea\u6709\u4e09\u4e2a\u8bcd\u3002</p><p>\u201c\u6bbf\u4e0b\u3002\u201d</p><p>\u201c\u7fd4\u9f99\u3002\u201d</p><p>\u201c\u673a\u5173\u672f\u3002\u201d</p><p>\u7b2c\u4e00\u4e2a\u8bcd\u8ba9\u4ed6\u6014\u4e86\u6014\uff0c\u968f\u5373\u5ffd\u7565\u8fc7\u53bb\u3002\u81ea\u5df1\u65e0\u8bba\u5982\u4f55\u4e5f\u548c\u201c\u6bbf\u4e0b\u201d\u8fd9\u79cd\u8bcd\u8054\u7cfb\u4e0d\u8d77\u6765\uff0c\u6885\u83f2\u65af\u8fd8\u5dee\u4e0d\u591a\uff0c\u5979\u6bd5\u7adf\u662f\u795e\u5b50\uff0c\u6240\u4ee5\u80af\u5b9a\u662f\u542c\u9519\u4e86\u3002\u7b2c\u4e8c\u4e2a\u8bcd\u5012\u662f\u660e\u767d\u7684\uff0c\u53e4\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7687\u5ba4\u4e24\u5927\u6d3e\u7cfb\u4e4b\u4e00\uff0c\u5c31\u662f\u201c\u7fd4\u9f99\u201d\uff0c\u540e\u6765\u5185\u6218\u6218\u8d25\uff0c\u4ed6\u4eec\u643a\u5e26\u4e03\u79d8\u5668\u5176\u4e2d\u7684\u4e09\u4ef6\u524d\u5f80\u4e1c\u65b9\u5927\u9646\uff0c\u5f81\u670d\u539f\u4f4f\u6c11\uff0c\u5efa\u7acb\u8d77\u5e9e\u5927\u7684\u7fd4\u9f99\u5e1d\u56fd\u3002\u636e\u743c\u6069\u6240\u77e5\uff0c\u7ecf\u8fc7\u6570\u5343\u5e74\u4f20\u627f\uff0c\u5982\u4eca\u201c\u7fd4\u9f99\u201d\u7684\u56fd\u53f7\u867d\u7136\u4f9d\u65e7\uff0c\u4f46\u5947\u68b0\u5e08\u7684\u8840\u8109\u65e9\u5df2\u65ad\u7edd\uff0c\u4e09\u4ef6\u79d8\u5668\u5148\u540e\u9057\u5931\uff0c\u7edf\u6cbb\u8005\u6362\u6210\u4e86\u539f\u4f4f\u6c11\uff0c\u548c\u4f0a\u739b\u65af\u5361\u57fa\u672c\u4e0a\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\u4e86\u2014\u2014\u4f46\u5deb\u5996\u600e\u4e48\u4f1a\u7a81\u7136\u63d0\u53ca\u7fd4\u9f99\uff1f</p><p>\u96be\u9053\u8bf4\uff0c\u4ed6\u4e5f\u50cf\u6b27\u51ef\u4e00\u6837\uff0c\u53d1\u73b0\u81ea\u5df1\u6709\u6240\u8c13\u7684\u7fd4\u9f99\u5370\u8bb0\uff1f</p><p>\u743c\u6069\u5fc3\u4e2d\u4e00\u60ca\uff0c\u636e\u6b27\u51ef\u7684\u8bf4\u6cd5\uff0c\u8fd9\u79cd\u6807\u5fd7\u7740\u4f0a\u739b\u65af\u5361\u7687\u5ba4\u8eab\u4efd\u7684\u5370\u8bb0\u662f\u548c\u7075\u9b42\u4e00\u4f53\uff0c\u7c7b\u4f3c\u4e8e\u5deb\u5e08\u7684\u771f\u540d\uff0c\u770b\u4e0d\u89c1\u4e5f\u6478\u4e0d\u7740\uff0c\u5982\u679c\u540c\u4e3a\u7687\u5ba4\uff0c\u90a3\u4e48\u5f88\u5bb9\u6613\u8fa8\u522b\uff0c\u56e0\u4e3a\u5370\u8bb0\u4e4b\u95f4\u4f1a\u4ea7\u751f\u5171\u9e23\u3002\u6b27\u51ef\u4e4b\u6240\u4ee5\u80fd\u591f\u53d1\u73b0\uff0c\u56e0\u4e3a\u4ed6\u5728\u53d8\u6210\u6076\u9b54\u4e4b\u524d\uff0c\u66fe\u7ecf\u662f\u4f0a\u739b\u65af\u5361\u7684\u201c\u5b66\u8005\u201d\uff0c\u5bf9\u6b64\u6bd5\u7adf\u6709\u76f8\u5f53\u7684\u4e86\u89e3\uff0c\u5373\u4fbf\u5982\u6b64\uff0c\u4ed6\u4e00\u5f00\u59cb\u4e5f\u62ff\u4e0d\u51c6\uff0c\u7ecf\u8fc7\u591a\u756a\u8bd5\u63a2\u4e4b\u540e\u624d\u57fa\u672c\u786e\u5b9a\u3002\u8fd9\u4e2a\u8001\u5deb\u5996\u80fd\u4e00\u89c1\u9762\u5c31\u8ba4\u51fa\u6765\uff1f\u96be\u9053\u8bf4\u4ed6\u4e5f\u662f\u4f0a\u739b\u65af\u5361\u7684\u5947\u68b0\u5e08\uff0c\u751a\u81f3\u8fd8\u53ef\u80fd\u662f\u7687\u5ba4\uff1f</p><p>\u5982\u679c\u8bf4\u524d\u4e24\u4e2a\u8bcd\u867d\u7136\u7a81\u5140\uff0c\u603b\u7b97\u8fd8\u5c5e\u4e8e\u201c\u6b63\u5e38\u201d\u7684\u8303\u7574\uff0c\u53ef\u4ee5\u52c9\u5f3a\u7406\u89e3\u3002\u90a3\u4e48\u7b2c\u4e09\u4e2a\u8bcd\uff0c\u5c31\u5f7b\u5f7b\u5e95\u5e95\u628a\u743c\u6069\u7ed9\u9707\u4f4f\u4e86\u3002\u56e0\u4e3a\u901a\u7528\u8bed\u3001\u8010\u745f\u8bed\u91cc\u90fd\u6ca1\u6709\u5bf9\u5e94\u7684\u8bf4\u6cd5\uff0c\u5deb\u5996\u663e\u7136\u662f\u76f4\u63a5\u4f7f\u7528\u4e86\u4e00\u79cd\u7279\u522b\u7684\u8bed\u8a00\uff0c\u5927\u9646\u4e0a\u7684\u4eba\u4f30\u8ba1\u767e\u5206\u4e4b\u4e5d\u5341\u4e5d\u70b9\u4e5d\u90fd\u542c\u4e0d\u61c2\u4ed6\u5728\u8bf4\u4ec0\u4e48\uff0c\u504f\u504f\u743c\u6069\u5c31\u662f\u6700\u540e\u90a3\u96f6\u70b9\u4e00\u2014\u2014\u56e0\u4e3a\u90a3\u6839\u672c\u5c31\u662f\u6c49\u8bed\u3002</p><p>\u201c\u673a\u5173\u672f\u2026\u2026\u5f00\u73a9\u7b11\u7684\u5427\uff0c\u6211\u660e\u660e\u7a7f\u8d8a\u7684\u662f\u9b54\u6cd5\u5947\u5e7b\u4e16\u754c\uff0c\u4e0d\u662f\u8f69\u8f95\u5251\u554a\u3002\u201d</p><p>\u5c3d\u7ba1\u4e0a\u8f88\u5b50\u4e0d\u7b97\u592a\u5b85\uff0c\u52a8\u6f2b\u770b\u5f97\u5f88\u5c11\uff0c\u6e38\u620f\u73a9\u5f97\u4e5f\u4e0d\u591a\uff0c\u4f46\u5927\u5b87\u7684\u8f69\u8f95\u5251\u7cfb\u5217\u8fd8\u662f\u77e5\u9053\u7684\uff0c\u4f5c\u4e3a\u5176\u62db\u724c\u7279\u8272\u4e4b\u4e00\uff0c\u743c\u6069\u5bf9\u201c\u673a\u5173\u672f\u201d\u8fd9\u4e2a\u8bcd\u534a\u70b9\u90fd\u4e0d\u964c\u751f\uff0c\u901a\u4fd7\u5730\u8bf4\u5c31\u662f\u5236\u9020\u673a\u5668\u4eba\u7684\u6cd5\u95e8\uff0c\u800c\u4e14\u662f\u4e0d\u7528\u7535\u4e0d\u7528\u82af\u7247\u8d85\u7ea7\u80fd\u6253\u7684\u673a\u5668\u4eba\uff0c\u6709\u7684\u8fd8\u80fd\u53d8\u5f62\uff0c\u53ef\u8c13\u662f\u795e\u5947\u65e0\u6bd4\uff0c\u76f8\u6bd4\u8d77\u6765\u8fd9\u4e2a\u4e16\u754c\u7684\u4ec0\u4e48\u9b54\u50cf\u4e4b\u6d41\u4e0d\u8fc7\u662f\u50bb\u5927\u4e2a\u3002</p><p>\u53ef\u662f\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u4ece\u5deb\u5996\u53e3\u4e2d\u542c\u5230\u8fd9\u4e2a\u8bcd\u5462\uff1f\u4e00\u5b9a\u2026\u2026\u4e00\u5b9a\u4e5f\u662f\u542c\u9519\u4e86\u3002</p><p>\u7136\u800c\u773c\u524d\u51fa\u73b0\u7684\u4e8b\u5b9e\u5374\u65e0\u60c5\u5730\u7c89\u788e\u4e86\u743c\u6069\u7684\u5e7b\u60f3\u3002\u4ece\u5916\u5f62\u4e0a\u770b\uff0c\u5deb\u5996\u53ec\u51fa\u7684\u8fd9\u4e94\u4e2a\u4e1c\u897f\uff0c\u8fd8\u771f\u7684\u5f88\u50cf\u6e38\u620f\u4e2d\u7684\u201c\u673a\u5173\u517d\u201d\u3002\u5b83\u4eec\u5e76\u4e0d\u50cf\u9b54\u50cf\u90a3\u6837\u662f\u4e00\u4e2a\u5b9e\u5fc3\u6574\u5757\uff0c\u800c\u662f\u660e\u663e\u7531\u65e0\u6570\u4e2a\u96f6\u4ef6\u7ec4\u88c5\u800c\u6210\uff0c\u751a\u81f3\u80fd\u591f\u9690\u7ea6\u770b\u5230\u9f7f\u8f6e\u3001\u8f74\u627f\u3001\u5c65\u5e26\u4e4b\u7c7b\u7684\u88c5\u7f6e\uff0c\u7075\u6d3b\u673a\u52a8\uff0c\u8fd0\u8f6c\u81ea\u5982\u3002\u800c\u4e14\u5b83\u4eec\u5e76\u975e\u865a\u6709\u5176\u8868\uff0c\u7247\u523b\u4e4b\u95f4\uff0c\u4e94\u4e2a\u91d1\u5c5e\u602a\u7269\u5c31\u5df2\u7ecf\u628a\u98df\u4eba\u9b54\u9b54\u50cf\u4eec\u6740\u5f97\u8282\u8282\u8d25\u9000\uff0c\u843d\u82b1\u6d41\u6c34\u3002\u4ee5\u6570\u91cf\u800c\u8bba\uff0c\u4e94\u6bd4\u56db\uff0c\u5b83\u4eec\u591a\u4e00\u4e2a\uff1b\u4ee5\u529b\u91cf\u800c\u8bba\uff0c\u5b83\u4eec\u4e0d\u6bd4\u9b54\u50cf\u900a\u8272\uff1b\u4ee5\u901f\u5ea6\u800c\u8bba\uff0c\u743c\u6069\u7684\u8428\u74e6\u68cb\u9b54\u50cf\u5728\u9b54\u50cf\u4e2d\u5df2\u7ecf\u53ef\u4ee5\u7b97\u662f\u7f55\u89c1\u7684\u7075\u654f\uff0c\u4f46\u4e00\u6bd4\u8f83\u8d77\u6765\u5c31\u76f8\u5f62\u89c1\u7ecc\uff0c\u660e\u663e\u8fd8\u662f\u5dee\u4e86\u4e00\u622a\u3002\u6700\u4ee4\u4eba\u60ca\u8bb6\u7684\uff0c\u662f\u8fd9\u4e9b\u91d1\u5c5e\u602a\u7269\u7684\u653b\u51fb\u65b9\u5f0f\uff0c\u5b8c\u5168\u4e0d\u4f3c\u9b54\u50cf\u90a3\u822c\u5355\u8c03\uff0c\u800c\u662f\u5404\u79cd\u82b1\u6837\u5c42\u51fa\u4e0d\u7a77\u3002\u6251\u51fb\u3001\u63c9\u8fdb\u3001\u6454\u6253\u3001\u53fc\u5544\u3001\u7ede\u7f20\u3001\u5f39\u5c04\uff0c\u7b80\u76f4\u6bd4\u771f\u6b63\u7684\u52a8\u7269\u90fd\u8981\u7075\u6d3b\u591a\u53d8\uff0c\u53c8\u662f\u91d1\u5c5e\u8eab\u8eaf\uff0c\u6297\u51fb\u8010\u6253\u3002\u743c\u6069\u5c1d\u8bd5\u6027\u5730\u7528\u5377\u8f74\u671d\u5176\u4e2d\u4e00\u4e2a\u91ca\u653e\u4e86\u653b\u51fb\u6cd5\u672f\uff0c\u7ed3\u679c\u53ea\u9020\u6210\u4e86\u9884\u6599\u4e2d\u5341\u5206\u4e4b\u4e00\u7684\u4f24\u5bb3\uff0c\u8bf4\u660e\u8fd9\u4e9b\u91d1\u5c5e\u602a\u7269\u867d\u7136\u4e0d\u662f\u9b54\u6cd5\u514d\u75ab\uff0c\u4f46\u4e5f\u662f\u6709\u5f88\u9ad8\u7684\u62b5\u6297\u529b\u4e86\u3002</p><p>\u8fd8\u771f\u7684\u662f\u673a\u5173\u672f\u2026\u2026</p><p>\u5f62\u52bf\u6025\u8f6c\u800c\u4e0b\uff0c\u53d8\u5f97\u5bf9\u743c\u6069\u6781\u5ea6\u4e0d\u5229\u8d77\u6765\uff0c\u7136\u800c\u4ed6\u4e00\u65f6\u95f4\u65e0\u8ba1\u53ef\u65bd\uff0c\u5965\u672f\u67b7\u9501\u867d\u7136\u9e21\u808b\uff0c\u4f46\u5b58\u7eed\u671f\u95f4\u662f\u65e0\u6cd5\u9a71\u6563\u7684\u3002\u5deb\u5996\u7eb5\u58f0\u5927\u7b11\uff0c\u6307\u6325\u5176\u4ed6\u56db\u53ea\u673a\u5173\u517d\uff08\u59d1\u4e14\u5982\u6b64\u79f0\u547c\uff09\u548c\u9b54\u50cf\u7f20\u6597\uff0c\u7b2c\u4e94\u53ea\u201c\u87b3\u8782\u201d\u5374\u7eb5\u8dc3\u8d77\u6765\uff0c\u8df3\u51fa\u6218\u56e2\uff0c\u671d\u7740\u743c\u6069\u98de\u901f\u903c\u8fd1\u3002</p><p>\u201c\u4ec5\u6b64\u800c\u5df2\u5417\uff1f\u90a3\u53ef\u5b9e\u5728\u8ba9\u6211\u6709\u4e00\u70b9\u70b9\u5931\u671b\u554a\uff0c\u6bbf\u4e0b\uff0c\u201d\u5deb\u5996\u67af\u6da9\u7684\u51b0\u51b7\u55d3\u97f3\u5728\u743c\u6069\u8033\u8fb9\u76f4\u63a5\u54cd\u8d77\uff0c\u201c\u7fd4\u9f99\u7684\u529b\u91cf\uff0c\u4e0d\u5e94\u8be5\u4ec5\u4ec5\u53ea\u6709\u8fd9\u79cd\u7a0b\u5ea6\u5427\u3002\u201d</p><p>\u8fd9\u6b21\u743c\u6069\u542c\u5f97\u6e05\u6e05\u695a\u695a\uff0c\u5deb\u5996\u786e\u5b9e\u662f\u53eb\u81ea\u5df1\u201c\u6bbf\u4e0b\u201d\u6ca1\u9519\uff0c\u800c\u4e14\u542c\u53e3\u6c14\uff0c\u4ed6\u679c\u7136\u662f\u53d1\u73b0\u4e86\u81ea\u5df1\u7684\u7fd4\u9f99\u5370\u8bb0\u3002\u95ee\u9898\u5728\u4e8e\uff0c\u4ed6\u4f3c\u4e4e\u662f\u8bef\u4f1a\u4e86\u4ec0\u4e48\u2014\u2014\u743c\u6069\u867d\u7136\u6709\u7fd4\u9f99\u5370\u8bb0\uff0c\u5374\u5e76\u4e0d\u662f\u771f\u6b63\u7684\u7fd4\u9f99\u5947\u68b0\u5e08\u554a\u3002</p><p>\u201c\u6b27\u51ef\u90a3\u4e2a\u6b7b\u6076\u9b54\uff0c\u628a\u4f0a\u739b\u65af\u5361\u7687\u5ba4\u5439\u5f97\u795e\u4e4e\u5176\u795e\uff0c\u597d\u50cf\u6211\u6709\u8fd9\u4e2a\u7fd4\u9f99\u5370\u8bb0\u5c31\u662f\u591a\u4e48\u4e86\u4e0d\u5f97\u7684\u4e8b\u60c5\u4f3c\u7684\u3002\u7ed3\u679c\u4e5f\u6ca1\u89c1\u5b83\u7ed9\u6211\u6765\u70b9\u7279\u5f02\u529f\u80fd\u554a\uff0c\u552f\u4e00\u53d1\u6325\u4f5c\u7528\u7684\u5c31\u662f\u8ba9\u6211\u80fd\u591f\u4f7f\u7528\u8fd9\u5e45\u8428\u74e6\u68cb\u9b54\u50cf\uff0c\u603b\u4e0d\u4f1a\u6240\u8c13\u7684\u5370\u8bb0\uff0c\u5c31\u662f\u5355\u7eaf\u8d77\u4e2a\u8bc6\u522b\u7801\u7684\u4f5c\u7528\u5427\u3002\u201d</p><p>\u62b1\u6028\u5f52\u62b1\u6028\uff0c\u6076\u9b54\u4e5f\u542c\u4e0d\u89c1\uff0c\u8fd8\u662f\u5148\u60f3\u529e\u6cd5\u5e94\u4ed8\u773c\u524d\u7684\u96be\u5173\u518d\u8bf4\u3002\u743c\u6069\u53ef\u4e0d\u50cf\u5deb\u5996\uff0c\u600e\u4e48\u6253\u90fd\u6ca1\u4e8b\uff0c\u54ea\u6015\u8eab\u4f53\u6253\u788e\u4e86\u4e5f\u65e0\u6240\u8c13\uff0c\u53cd\u6b63\u80fd\u590d\u6d3b\u3002\u87b3\u8782\u98de\u5c04\u800c\u6765\uff0c\u88ab\u743c\u6069\u63b7\u51fa\u9f99\u9cde\u76fe\uff0c\u52c9\u5f3a\u6321\u4e86\u4e00\u6321\uff0c\u501f\u6b64\u7a7a\u9699\uff0c\u4ed6\u7684\u80f8\u53e3\u5149\u8292\u4e00\u95ea\uff0c\u50a8\u5b58\u5728\u9b54\u6cd5\u523a\u9752\u4e2d\u7684\u6cd5\u672f\u53d1\u52a8\uff0c\u5c06\u9762\u524d\u7684\u4e00\u5757\u5730\u9762\u5316\u4f5c\u6ce5\u6cbc\u3002</p><p>\u6cd5\u672f\u521a\u521a\u5b8c\u6210\uff0c\u87b3\u8782\u4fbf\u81ea\u7a7a\u4e2d\u843d\u4e0b\uff0c\u6b63\u597d\u8e0f\u5728\u6ce5\u6cbc\u4e2d\u3002\u4e94\u4e2a\u673a\u5173\u517d\u4e2d\uff0c\u5b83\u5df2\u7ecf\u662f\u4e2a\u5934\u6700\u5c0f\u7684\uff0c\u53ea\u6709\u8001\u864e\u7684\u4e00\u534a\uff0c\u5c3d\u7ba1\u5982\u6b64\uff0c\u5168\u8eab\u91d1\u5c5e\u7ed3\u6784\u7684\u5206\u91cf\u4e5f\u4e0d\u53ef\u80fd\u8f7b\u5230\u54ea\u91cc\u53bb\uff0c\u56db\u80a2\u521a\u4e00\u8e0f\u4e0a\u6ce5\u6d46\uff0c\u6574\u4e2a\u8eab\u8eaf\u5c31\u77ac\u95f4\u6c89\u6ca1\u4e0b\u53bb\u3002\u743c\u6069\u81ea\u5df1\u90fd\u6ca1\u60f3\u5230\u4f1a\u5982\u6b64\u987a\u5229\uff0c\u5927\u559c\u4e4b\u4e0b\u8fde\u5fd9\u5c55\u5f00\u65e9\u5df2\u51c6\u5907\u597d\u7684\u5377\u8f74\uff0c\u6025\u901f\u5ff5\u8bf5\uff0c\u5c06\u6ce5\u6d46\u518d\u5ea6\u51bb\u7ed3\u51dd\u56fa\u6210\u77f3\u5934\uff0c\u628a\u87b3\u8782\u5d4c\u5728\u5176\u4e2d\u3002\u4ed6\u8212\u4e86\u53e3\u6c14\uff0c\u53c8\u53d6\u51fa\u4e00\u5f20\u5377\u8f74\uff0c\u6253\u7b97\u53ec\u5524\u51e0\u53ea\u602a\u7269\u4e0a\u53bb\u7ed9\u9b54\u50cf\u5e2e\u5fd9\uff0c\u731b\u7136\u89c9\u5f97\u811a\u4e0b\u6643\u52a8\uff0c\u4eff\u4f5b\u5730\u9707\uff0c\u4e00\u9053\u88c2\u9699\u5728\u5730\u9762\u4e0a\u51fa\u73b0\uff0c\u5feb\u901f\u5ef6\u4f38\u6269\u5927\u3002\u743c\u6069\u8fd8\u6ca1\u6765\u5f97\u53ca\u4f5c\u51fa\u5e94\u5bf9\uff0c\u5c31\u89c1\u4e00\u56e2\u5bd2\u5149\u4ece\u88c2\u9699\u4e2d\u98de\u5c04\u800c\u51fa\uff0c\u6b63\u662f\u521a\u521a\u90a3\u53ea\u88ab\u5c01\u57cb\u77f3\u4e2d\u7684\u87b3\u8782\u3002</p><p>\u201c\u564c\uff01\u201d</p><p>\u87b3\u8782\u5728\u7a7a\u4e2d\u7a0d\u7a0d\u60ac\u505c\uff0c\u6c14\u52bf\u51f6\u731b\u5730\u4eae\u51fa\u4e24\u53ea\u5de8\u5203\u822c\u7684\u524d\u8db3\uff0c\u671d\u7740\u743c\u6069\u5feb\u82e5\u95ea\u7535\u5730\u5f53\u5934\u65a9\u4e0b\u3002\u5371\u6025\u5173\u5934\uff0c\u743c\u6069\u4e0b\u610f\u8bc6\u5730\u7ffb\u8eab\u5012\u5730\u4e00\u6eda\uff0c\u9669\u9669\u907f\u5f00\u3002\u87b3\u8782\u4e00\u51fb\u4e0d\u4e2d\uff0c\u968f\u5373\u8f6c\u8fc7\u8eab\u6765\uff0c\u5203\u8db3\u62ac\u8d77\uff0c\u6307\u5411\u743c\u6069\u3002</p><p>\u201c\u8bf7\u653e\u5fc3\uff0c\u6bbf\u4e0b\uff0c\u5728\u8fd9\u4e2a\u7a7a\u95f4\u91cc\u4e0d\u4f1a\u6709\u6b7b\u4ea1\uff0c\u201d\u5deb\u5996\u7684\u58f0\u97f3\u518d\u5ea6\u5728\u743c\u6069\u7684\u8033\u8fb9\u54cd\u8d77\uff0c\u201c\u4f46\u662f\u80fd\u591f\u51fb\u8d25\u4e00\u4f4d\u2018\u7fd4\u9f99\u2019\uff0c\u8fd9\u53ef\u5b9e\u5728\u4ee4\u6211\u5174\u594b\u4e0d\u5df2\u554a\u3002\u201d</p><p>\u87b3\u8782\u7684\u5203\u8db3\u9ad8\u4e3e\u8fc7\u9876\uff0c\u7167\u7740\u743c\u6069\u75be\u6325\u800c\u4e0b\u2014\u2014\u5c31\u5728\u8fd9\u7535\u5149\u706b\u77f3\u7684\u5239\u90a3\uff0c\u4e0d\u53ef\u601d\u8bae\u7684\u53d8\u5316\u53d1\u751f\u4e86\u3002</p><p>\u5149\u8292\uff01</p><p>\u91d1\u8272\u7684\u5149\u4ece\u743c\u6069\u7684\u8eab\u4f53\u4e0a\u8ff8\u53d1\u51fa\u6765\uff0c\u4eff\u4f5b\u521d\u5347\u7684\u65ed\u65e5\uff0c\u50cf\u662f\u707f\u70c2\u7684\u6668\u66e6\uff0c\u8000\u773c\u593a\u76ee\uff0c\u70ab\u4e3d\u5f97\u4ee4\u4eba\u7a92\u606f\uff0c\u77ac\u95f4\u5145\u6ee1\u6574\u4e2a\u7a7a\u95f4\u3002\u6570\u4ee5\u5343\u4e07\u8ba1\u7684\u7d2b\u8272\u7b26\u6587\u81ea\u91d1\u5149\u4e2d\u6d8c\u51fa\uff0c\u661f\u8fb0\u822c\u98de\u821e\u7740\uff0c\u6c47\u805a\u6210\u4e00\u4e2a\u7cbe\u5de7\u7e41\u590d\u5230\u65e0\u6cd5\u63cf\u8ff0\u7684\u534a\u5706\u5f62\u56fe\u6848\uff0c\u90a3\u50cf\u662f\u4e2a\u9b54\u6cd5\u9635\uff0c\u5374\u4eff\u4f5b\u53ea\u6709\u534a\u8fb9\uff0c\u6b8b\u7f3a\u4e0d\u5168\u3002\u80cc\u751f\u53cc\u7ffc\u7684\u91d1\u9cde\u9f99\u86c7\u81ea\u865a\u7a7a\u4e2d\u6d6e\u73b0\uff0c\u7f20\u7ed5\u7740\u743c\u6069\u7684\u8eab\u4f53\uff0c\u5728\u534a\u5706\u5f62\u56fe\u6848\u4e2d\u873f\u8712\u6e38\u52a8\uff0c\u5f20\u53e3\u541e\u98df\u4e00\u7c92\u7c92\u7b26\u6587\uff0c\u610f\u6001\u5f9c\u5f89\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u5b83\u5df2\u7ecf\u6602\u8d77\u5934\uff0c\u91d1\u78a7\u8272\u7684\u53cc\u773c\u76f4\u76f4\u5730\u51dd\u89c6\u7740\u87b3\u8782\u3002</p><p>\u7136\u540e\u87b3\u8782\u788e\u4e86\u3002</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u8fd9\u79cd\u8bf4\u6cd5\u5e76\u4e0d\u51c6\u786e\uff0c\u66f4\u51c6\u786e\u7684\u63cf\u8ff0\u662f\uff1a\u91d1\u9cde\u9f99\u86c7\u51dd\u89c6\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u4ee5\u87b3\u8782\u4e3a\u4e2d\u5fc3\uff0c\u65b9\u5706\u5341\u5c3a\u7684\u7a7a\u95f4\u731b\u7136\u9707\u52a8\u3001\u51dd\u56fa\uff0c\u4eff\u4f5b\u88ab\u786c\u751f\u751f\u4ece\u5468\u56f4\u72ec\u7acb\u5207\u5272\u5f00\u6765\uff0c\u53d8\u6210\u4e86\u4e00\u5757\u900f\u660e\u73bb\u7483\u3002\u7d27\u63a5\u7740\u5e9e\u5927\u65e0\u6bd4\u7684\u529b\u91cf\u4ece\u56db\u9762\u516b\u65b9\u6324\u538b\u8fc7\u6765\uff0c\u8fd9\u5757\u73bb\u7483\u7a7a\u95f4\u2014\u2014\u8fde\u5e26\u88ab\u51bb\u7ed3\u5728\u5176\u4e2d\u7684\u87b3\u8782\u2014\u2014\u88ab\u77ac\u95f4\u7c89\u788e\u6210\u5fae\u5c0f\u5982\u5c18\u57c3\u7684\u65e0\u6570\u5757\uff0c\u7136\u540e\uff0c\u5f7b\u5e95\u6d88\u5931\u4e86\u3002</p><p>\u5d29\u6e83\u7684\u5e76\u4e0d\u662f\u87b3\u8782\uff0c\u800c\u662f\u7a7a\u95f4\u3002</p><p>\u743c\u6069\u7f13\u7f13\u7ad9\u8d77\uff0c\u56e0\u4e3a\u521a\u624d\u5728\u5730\u4e0a\u7684\u7ffb\u6eda\u8eb2\u907f\uff0c\u4ed6\u7684\u5934\u53d1\u3001\u957f\u888d\u4e0a\u90fd\u6cbe\u6ee1\u4e86\u7070\u5c18\uff0c\u6a21\u6837\u770b\u8d77\u6765\u72fc\u72c8\u4e0d\u582a\uff0c\u7136\u800c\u6574\u4e2a\u4eba\u4eff\u4f5b\u8131\u80ce\u6362\u9aa8\u4e86\u4e00\u822c\uff0c\u7a33\u7a33\u5c79\u7acb\u5728\u5e7f\u573a\u4e2d\uff0c\u6563\u53d1\u7740\u51db\u51db\u6c14\u52bf\uff0c\u4eff\u4f5b\u6574\u4e2a\u5929\u5730\u90fd\u530d\u5310\u5728\u811a\u4e0b\uff0c\u76f4\u81f3\u9065\u8fdc\u65e0\u57a0\u7684\u5c3d\u5934\u3002\u5149\u8292\u3001\u6cd5\u9635\u548c\u91d1\u9cde\u9f99\u86c7\u90fd\u53d8\u5f97\u865a\u5316\u900f\u660e\uff0c\u6d88\u5931\u4e0d\u89c1\uff0c\u7136\u800c\u4ed6\u7684\u773c\u775b\u53d8\u5f97\u8d8a\u53d1\u660e\u4eae\uff0c\u4eff\u4f5b\u591c\u7a7a\u4e2d\u7480\u74a8\u7684\u661f\u8fb0\u3002</p><p>\u4e00\u6b65\u4e00\u6b65\uff0c\u743c\u6069\u6162\u6162\u671d\u524d\u65b9\u8d70\u6765\uff0c\u903c\u8fd1\u5deb\u5996\uff0c\u539f\u672c\u6b63\u7eb5\u58f0\u5927\u7b11\u7684\u5deb\u5996\u7b11\u58f0\u560e\u7136\u800c\u6b62\uff0c\u957f\u888d\u50cf\u662f\u88ab\u98ce\u9f13\u8d77\u4e00\u822c\u81a8\u80c0\u8d77\u6765\uff0c\u4ed6\u53d1\u51fa\u547d\u4ee4\uff0c\u4e00\u76f4\u6e38\u5203\u6709\u4f59\u5730\u548c\u9b54\u50cf\u7f20\u6597\u7684\u56db\u53ea\u673a\u5173\u517d\u9f50\u9f50\u653e\u5f03\u76ee\u6807\uff0c\u5b83\u4eec\u5486\u54ee\u3001\u5954\u8dd1\u3001\u98de\u7fd4\u3001\u75be\u5c04\uff0c\u671d\u743c\u6069\u56f4\u653b\u6251\u6765\u3002</p><p>\u743c\u6069\u62ac\u8d77\u53cc\u773c\u3002</p><p>\u5494\uff01\u5494\uff01\u5494\uff01\u5494\uff01</p><p>\u968f\u7740\u53ea\u5b58\u5728\u4e8e\u610f\u8bc6\u4e2d\u7684\u56db\u58f0\u8f7b\u54cd\uff0c\u56db\u53ea\u673a\u5173\u517d\u6beb\u65e0\u534a\u70b9\u62b5\u6297\u4e4b\u529b\uff0c\u5148\u540e\u5d29\u6e83\u3001\u7c89\u788e\uff0c\u88ab\u541e\u566c\u8fdb\u865a\u65e0\u3002\u201c\u4ec5\u6b64\u800c\u5df2\u5417\uff0c\u90a3\u53ef\u5b9e\u5728\u8ba9\u6211\u6709\u4e00\u70b9\u70b9\u5931\u671b\u554a\uff0c\u5927\u957f\u8001\u9601\u4e0b\uff0c\u201d\u743c\u6069\u8f7b\u58f0\u8bf4\uff0c\u201c\u90a3\u4e48\uff0c\u8ba9\u6211\u770b\u770b\u4f60\u7684\u529b\u91cf\uff0c\u662f\u5426\u4f1a\u7ed9\u6211\u5e26\u6765\u4e9b\u8bb8\u60ca\u559c\u5427\u3002\u201d</p><p>\u5deb\u5996\u53d1\u51fa\u4f4e\u4f4e\u7684\u5636\u9e23\uff0c\u53cc\u624b\u540c\u65f6\u6325\u821e\uff0c\u77ac\u95f4\u91ca\u653e\u51fa\u4e03\u516b\u9053\u6cd5\u672f\u3002\u5965\u672f\u67b7\u9501\u662f\u540c\u65f6\u5c01\u5370\u65bd\u6cd5\u8005\u548c\u5bf9\u624b\uff0c\u5176\u4e2d\u4efb\u4f55\u4e00\u65b9\u7684\u5c01\u5370\u6d88\u5931\uff0c\u53e6\u5916\u4e00\u65b9\u4e5f\u5c31\u83b7\u5f97\u89e3\u653e\uff0c\u6b64\u65f6\u743c\u6069\u8eab\u4e0a\u7684\u5c01\u5370\u5df2\u7ecf\u65e0\u5f71\u65e0\u8e2a\uff0c\u4ece\u800c\u8ba9\u5deb\u5996\u4e5f\u6062\u590d\u4e86\u5965\u672f\u65bd\u6cd5\u80fd\u529b\u3002\u7136\u800c\u4ed6\u6240\u6709\u7684\u6cd5\u672f\uff0c\u5168\u90fd\u6ca1\u6709\u8d77\u5230\u534a\u70b9\u6548\u679c\u3002</p><p>\u5e76\u975e\u62b5\u5fa1\uff0c\u4e5f\u5e76\u975e\u53cd\u5f39\uff0c\u66f4\u4e0d\u662f\u9b54\u6cd5\u6297\u529b\u3002\u4ee5\u81ea\u8eab\u4e3a\u4e2d\u5fc3\uff0c\u743c\u6069\u5468\u56f4\u7684\u7a7a\u95f4\u4eff\u4f5b\u53d8\u6210\u4e86\u4e00\u9762\u5de8\u5927\u7684\u900f\u955c\u3002\u5deb\u5996\u7684\u6cd5\u672f\u8fdb\u5165\u7a7a\u95f4\uff0c\u7136\u540e\u5168\u90e8\u53d1\u751f\u504f\u8f6c\uff0c\u5c31\u50cf\u5149\u7ebf\u7a7f\u8fc7\u900f\u955c\u53d1\u751f\u6298\u5c04\uff0c\u51c6\u5934\u5b8c\u5168\u4e27\u5931\u3002</p><p>\u201c\u8be5\u6211\u4e86\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u968f\u7740\u8bed\u97f3\u5410\u51fa\uff0c\u5deb\u5996\u7684\u8eab\u4f53\u5b8c\u5168\u50f5\u4f4f\uff0c\u548c\u524d\u9762\u7684\u4f8b\u5b50\u4e00\u6837\uff0c\u4ed6\u5468\u56f4\u7684\u65e0\u5f62\u7a7a\u95f4\u88ab\u201c\u51bb\u7ed3\u201d\uff0c\u7136\u540e\u5f00\u59cb\u788e\u88c2\u3002\u94f6\u767d\u8272\u7684\u707f\u70c2\u5723\u5149\u81ea\u5deb\u5996\u4f53\u5185\u8ff8\u51fa\uff0c\u5982\u6f6e\u6c34\u822c\u8f70\u7136\u56db\u6ea2\uff0c\u8bd5\u56fe\u6253\u7834\u675f\u7f1a\uff0c\u7136\u800c\u4e8b\u5b9e\u8bc1\u660e\u8fd9\u662f\u5f92\u52b3\u7684\u3002\u552f\u4e00\u7684\u7528\u9014\uff0c\u53ea\u662f\u8ba9\u7c89\u788e\u7684\u8fc7\u7a0b\u5ef6\u7f13\u4e86\u4e94\u79d2\u949f\u2014\u2014\u4e94\u79d2\u949f\u540e\uff0c\u4e00\u5207\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u743c\u6069\u6536\u56de\u68cb\u5b50\uff0c\u5e73\u5e73\u62ac\u8d77\u53f3\u624b\uff0c\u638c\u5fc3\u5f80\u4e0b\u865a\u6309\uff0c\u5929\u7a7a\u5f00\u59cb\u788e\u88c2\uff0c\u5927\u5730\u6025\u5267\u584c\u9677\uff0c\u5706\u73af\u5f62\u89d2\u6597\u573a\u88ab\u5e9e\u5927\u7684\u9ed1\u6697\u541e\u566c\uff0c\u6574\u4e2a\u4e16\u754c\u5206\u5d29\u79bb\u6790\uff0c\u5316\u4f5c\u4e4c\u6709\u3002\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u4ed6\u56de\u5230\u4e86\u5730\u4e0b\u5ba4\u4e2d\uff0c\u770b\u89c1\u795e\u60c5\u8bb6\u5f02\u7684\u6885\u83f2\u65af\uff0c\u4ee5\u53ca\u521a\u521a\u88ab\u4ed6\u7c89\u788e\u6389\u7684\u90a3\u4e2a\u5deb\u5996\u3002</p><p>\u201c\u679c\u7136\uff0c\u2018\u7edd\u5bf9\u4e0d\u8981\u5728\u4efb\u4f55\u534a\u4f4d\u9762\u4e2d\u548c\u7fd4\u9f99\u53d1\u751f\u51b2\u7a81\u2019\uff0c\u524d\u4eba\u7684\u8bdd\u603b\u5f52\u662f\u6709\u9053\u7406\u7684\u5440\uff0c\u201d\u5deb\u5996\u5634\u91cc\u561f\u56d4\u7740\u5947\u602a\u7684\u8bdd\uff0c\u201c\u5e78\u597d\u6211\u65e9\u6709\u9632\u5907\uff0c\u6d3e\u4e0a\u53bb\u7684\u5168\u662f\u62df\u50cf\u548c\u590d\u5236\u54c1\uff0c\u4e0d\u7136\u8fd9\u4e0b\u5b50\u5c31\u635f\u5931\u60e8\u91cd\u4e86\u3002\u201d</p><p>\u539f\u6765\u662f\u62df\u50cf\u5417\uff1f</p><p>\u521a\u624d\u6467\u6bc1\u5deb\u5996\u7684\u65f6\u5019\uff0c\u743c\u6069\u5c31\u5df2\u7ecf\u5bdf\u89c9\u5230\u4e00\u4e1d\u4e0d\u5bf9\u52b2\uff0c\u4f46\u4e5f\u6ca1\u6709\u7ec6\u60f3\u3002\u542c\u8001\u5deb\u5996\u4e00\u8bf4\u624d\u77e5\u9053\uff0c\u539f\u6765\u90a3\u53ea\u662f\u4e00\u5177\u62df\u50cf\uff0c\u5e76\u975e\u672c\u4f53\uff0c\u96be\u602a\u4ed6\u660e\u660e\u5df2\u7ecf\u7c89\u8eab\u788e\u9aa8\uff0c\u73b0\u5728\u53c8\u5b89\u7136\u65e0\u6059\u5730\u7ad9\u5728\u9762\u524d\u3002\u62df\u50cf\u662f\u6781\u9ad8\u660e\u7684\u5e7b\u672f\uff0c\u521b\u9020\u51fa\u6765\u7684\u201c\u5047\u8eab\u201d\u5e76\u975e\u5e7b\u5f71\uff0c\u800c\u662f\u5b9e\u5b9e\u5728\u5728\u7684\u7269\u8d28\u4f53\uff0c\u5e76\u4e14\u62e5\u6709\u672c\u4f53\u5927\u7ea6\u4e09\u5206\u4e4b\u4e00\u7684\u529b\u91cf\uff0c\u6700\u91cd\u8981\u7684\u662f\u5b83\u6ca1\u6709\u65f6\u95f4\u9650\u5236\uff0c\u53ea\u8981\u4e0d\u88ab\u6467\u6bc1\u5c31\u4f1a\u4e00\u76f4\u5b58\u5728\u3002\u5f88\u591a\u6df1\u5c45\u7b80\u51fa\u7684\u5927\u5deb\u5e08\u4eec\uff0c\u90fd\u559c\u6b22\u5f04\u4e00\u4e24\u4e2a\u62df\u50cf\u7ed9\u81ea\u5df1\u5f53\u52a9\u624b\uff0c\u65e2\u65b9\u4fbf\u53c8\u597d\u7528\u3002</p><p>\u7136\u800c\uff0c\u5c31\u7b97\u662f\u62df\u50cf\uff0c\u4e5f\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\u88ab\u6253\u8d25\u7684\u5427\u3002\u8fd9\u6837\u5e72\u8106\u5229\u843d\u5730\u89e3\u51b3\u6218\u6597\uff0c\u6467\u67af\u62c9\u673d\u5730\u6253\u57ae\u5bf9\u624b\uff0c\u6839\u672c\u5c31\u8d85\u51fa\u4e86\u743c\u6069\u201c\u5e94\u6709\u201d\u7684\u80fd\u529b\u8303\u56f4\uff0c\u800c\u4e14\u8fd9\u4e00\u5207\u53c8\u6765\u5f97\u5982\u6b64\u7a81\u7136\uff0c\u8f6c\u6298\u5982\u6b64\u5f3a\u70c8\uff0c\u5b8c\u5168\u8ba9\u4eba\u6ca1\u6709\u534a\u70b9\u73b0\u5b9e\u611f\u3002</p><p>\u5c31\u8fde\u743c\u6069\u81ea\u5df1\uff0c\u5176\u5b9e\u4e5f\u6ca1\u771f\u6b63\u660e\u767d\u8fd9\u5230\u5e95\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u5427\u3002\u4ed6\u4e0b\u610f\u8bc6\u5730\u8bf4\u8bdd\u3001\u884c\u52a8\uff0c\u53ea\u89c9\u81ea\u7136\u65e0\u6bd4\uff0c\u7136\u800c\u56de\u8fc7\u795e\u6765\uff0c\u53c8\u89c9\u5f97\u604d\u82e5\u68a6\u4e2d\u3002\u4f46\u662f\u90a3\u79cd\u529b\u91cf\uff0c\u90a3\u79cd\u638c\u63e1\u7740\u65e0\u6bd4\u7684\u201c\u529b\u91cf\u201d\u7684\u611f\u89c9\uff0c\u5374\u662f\u771f\u771f\u5207\u5207\uff0c\u4f9d\u7136\u8fd8\u5728\u4f53\u5185\u6d41\u52a8\u7740\uff0c\u534a\u70b9\u4e0d\u662f\u5e7b\u89c9\u3002</p><p>\u4e0d\uff0c\u4e0d\u662f\u6d41\u52a8\uff0c\u662f\u5728\u8df3\u8dc3\uff0c\u5728\u9707\u52a8\uff0c\u5728\u6cb8\u817e\uff0c\u50cf\u6c89\u7720\u5343\u5e74\u7684\u731b\u517d\u82cf\u9192\uff0c\u51b0\u5c01\u4e07\u91cc\u7684\u6c5f\u6cb3\u6d88\u878d\uff0c\u8feb\u4e0d\u53ca\u5f85\u5730\u60f3\u8981\u8212\u5f20\u8eaf\u4f53\uff0c\u5f20\u5f00\u722a\u7259\uff0c\u964d\u4e34\u8fd9\u4e45\u8fdd\u7684\u4e16\u754c\u3002</p><p>\u6b63\u81ea\u76f8\u5bf9\u65e0\u8a00\uff0c\u731b\u7136\u95f4\u542c\u5f97\u5730\u4e0b\u5ba4\u7684\u56db\u4e2a\u89d2\u843d\u91cc\u540c\u65f6\u4f20\u6765\u6025\u4fc3\u7684\u8b66\u62a5\u58f0\uff0c\u8fd9\u662f\u6709\u5f3a\u654c\u5165\u4fb5\u7684\u6807\u5fd7\u3002\u743c\u6069\u548c\u6885\u83f2\u65af\u90fd\u6709\u4e9b\u610f\u5916\uff0c\u5deb\u5996\u5374\u4eff\u4f5b\u65e9\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u5f39\u4e86\u5f39\u624b\u6307\uff0c\u5149\u7ebf\u4ece\u56db\u9762\u516b\u65b9\u5c04\u6765\uff0c\u5728\u7a7a\u4e2d\u6c47\u96c6\u6210\u4e00\u5e55\u753b\u9762\u3002</p><p>\u5728\u753b\u9762\u4e2d\uff0c\u4ed6\u4eec\u770b\u89c1\u4e86\u8352\u829c\u5e9f\u5f03\u7684\u5b85\u9662\u3001\u6742\u8349\u4e1b\u751f\u7684\u5730\u9762\uff0c\u8fd8\u6709\u4e00\u4f4d\u77ed\u53d1\u3001\u9ed1\u8863\uff0c\u5a07\u5c0f\u8eab\u59ff\u4e2d\u900f\u7740\u5200\u5251\u822c\u51db\u51db\u6740\u6c14\u7684\u5c11\u5973\uff0c\u9762\u65e0\u8868\u60c5\u5730\u4e00\u6b65\u6b65\u5411\u524d\u8d70\u6765\u3002</p></article>"}}}]);
