(window.webpackJsonp=window.webpackJsonp||[]).push([[1709],{1732:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e09\u8282 \u60c5\u8da3\u5185\u8863",content:"<article><h2>\u7b2c\u4e09\u5341\u4e09\u8282 \u60c5\u8da3\u5185\u8863</h2><p>\u770b\u7740\u5c11\u5973\u7709\u773c\u95f4\u7684\u7f9e\u6da9\uff0c\u743c\u6069\u7a81\u7136\u89c9\u5f97\u81ea\u5df1\u7684\u5fc3\u8df3\u7830\u7830\u52a0\u901f\u8d77\u6765\u3002\u8fd9\u79cd\u4e45\u8fdd\u7684\u7d27\u5f20\u611f\uff0c\u600e\u4e48\u89c9\u5f97\u6709\u70b9\u50cf\u524d\u4e16\u7b2c\u4e00\u6b21\u4e0a\u95e8\u89c1\u521d\u604b\u5973\u53cb\u7684\u7236\u6bcd\u2026\u2026</p><p>\u5bf9\u4e8e\u827e\u5f25\u8587\u800c\u8a00\uff0c\u4f3c\u4e4e\u5c31\u662f\u5982\u6b64\u3002</p><p>\u5979\u7684\u7236\u4eb2\u662f\u90aa\u795e\u5df4\u5c14\uff0c\u6bcd\u4eb2\u662f\u5df4\u5c14\u7684\u5973\u9009\u6c11\u3002\u5bf9\u4e8e\u6bcd\u4eb2\uff0c\u6885\u83f2\u65af\u7684\u611f\u89c9\u5e94\u8be5\u7231\u6068\u53c2\u534a\uff0c\u65e2\u65e0\u6cd5\u5fcd\u53d7\u5979\u628a\u81ea\u5df1\u4f5c\u4e3a\u90aa\u795e\u590d\u6d3b\u7684\u5de5\u5177\u770b\u5f85\uff0c\u8ba9\u5979\u7684\u7ae5\u5e74\u8bb0\u5fc6\u4e2d\u5145\u6ee1\u4e86\u8840\u6dcb\u6dcb\u7684\u6740\u622e\u548c\u9ed1\u6697\u51b0\u51b7\u7684\u5669\u68a6\uff0c\u76f4\u5230\u4e34\u7ec8\u524d\u8fd8\u5bf9\u5979\u4e0b\u4e86\u8840\u7684\u8bc5\u5492\uff1b\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u6bd5\u7adf\u662f\u6bcd\u4eb2\uff0c\u6709\u5341\u5e74\u7684\u517b\u80b2\u4e4b\u6069\uff0c\u5341\u5e74\u7684\u76f8\u6fe1\u4ee5\u6cab\u3002\u81f3\u4e8e\u7236\u4eb2\u2014\u2014\u4e0d\u597d\u610f\u601d\uff0c\u4f60\u8c01\u554a\uff0c\u6211\u4e0d\u8ba4\u8bc6\uff0c\u8bf7\u79bb\u6211\u8d8a\u8fdc\u8d8a\u597d\uff0c\u8c22\u8c22\u3002</p><p>\u5728\u5c11\u5973\u7684\u5fc3\u4e2d\uff0c\u201c\u7236\u4eb2\u201d\u8fd9\u4e2a\u4f4d\u7f6e\uff0c\u5176\u5b9e\u771f\u6b63\u7684\u5360\u636e\u8005\u662f\u90a3\u4f4d\u5927\u4e3b\u6559\u5427\u3002\u5c3d\u7ba1\u7d20\u672a\u8c0b\u9762\uff0c\u4f46\u4ece\u6885\u83f2\u65af\u5e73\u65f6\u7684\u8a00\u8c08\u4e2d\uff0c\u743c\u6069\u8fd8\u662f\u80fd\u591f\u5f88\u6e05\u695a\u5730\u5c31\u5f97\u51fa\u8fd9\u4e2a\u5224\u65ad\u3002\u6700\u7b80\u5355\u7684\u8bc1\u636e\u5c31\u662f\uff1a\u4ed6\u662f\u6885\u83f2\u65af\u5728\u95f2\u8c08\u65f6\u63d0\u53ca\u6700\u591a\u7684\u4e00\u4e2a\u4eba\uff0c\u6bd4\u51db\u8fd9\u4e2a\u9752\u6885\u7af9\u9a6c\u7684\u95fa\u4e2d\u5bc6\u53cb\u90fd\u591a\u3002\u5982\u679c\u4e0d\u662f\u5bf9\u5c11\u5973\u7684\u54c1\u683c\u6709\u4fe1\u5fc3\uff0c\u743c\u6069\u7b80\u76f4\u90fd\u8981\u6000\u7591\u5979\u662f\u4e0d\u662f\u60f3\u7236\u5ac1\u2014\u2014\u8bdd\u8bf4\u56de\u6765\uff0c\u7236\u5ac1\u4f3c\u4e4e\u4e5f\u4e0d\u7b97\u591a\u4e48\u90aa\u6076\uff0c\u6bd5\u7adf\u4ed6\u81ea\u5df1\u8fd8\u604b\u59d0\u5462\u3002</p><p>\u5728\u6bcd\u4eb2\u53bb\u4e16\u4e4b\u540e\uff0c\u6885\u83f2\u65af\u9762\u4e34\u7740\u4ec7\u654c\u7684\u8ffd\u6740\uff0c\u4e0d\u5f97\u4e0d\u5f00\u59cb\u6d41\u6d6a\u9003\u4ea1\uff0c\u552f\u4e00\u966a\u5728\u8eab\u8fb9\u7684\u53ea\u6709\u51db\uff0c\u5766\u767d\u5730\u8bf4\uff0c\u51db\u867d\u7136\u6bd4\u5979\u5e74\u9f84\u5927\uff0c\u5fc3\u6001\u4e0a\u5374\u66f4\u50cf\u59b9\u59b9\u800c\u975e\u59d0\u59d0\uff0c\u5c5e\u4e8e\u9700\u8981\u7167\u987e\u7684\u4e00\u65b9\uff0c\u800c\u4e0d\u662f\u80fd\u591f\u53cd\u8fc7\u6765\u7167\u987e\u4eba\u3002\u4f5c\u4e3a\u5c1a\u672a\u6210\u5e74\u7684\u5c11\u5973\uff0c\u6885\u83f2\u65af\u6240\u8981\u627f\u53d7\u7684\u538b\u529b\u53ef\u60f3\u800c\u77e5\uff0c\u66f4\u8981\u547d\u7684\u662f\uff0c\u56e0\u4e3a\u5bb9\u7eb3\u4e86\u592a\u591a\u592a\u591a\u7684\u5df4\u5c14\u795e\u529b\uff0c\u90aa\u795e\u5df2\u7ecf\u8822\u8822\u6b32\u52a8\uff0c\u5373\u5c06\u501f\u5979\u7684\u8eaf\u4f53\u590d\u6d3b\u3002\u5c31\u5728\u8fd9\u79cd\u8270\u96be\u5173\u5934\uff0c\u662f\u5927\u4e3b\u6559\u51fa\u73b0\uff0c\u5c06\u5979\u5e26\u56de\u6559\u4f1a\uff0c\u4e0d\u4f46\u6682\u65f6\u6027\u5730\u89e3\u9664\u4e86\u90aa\u795e\u590d\u6d3b\u7684\u9690\u60a3\uff0c\u800c\u4e14\u57f9\u517b\u5979\u6210\u4e3a\u5723\u6b66\u58eb\u3002\u53ef\u4ee5\u8bf4\uff0c\u662f\u5927\u4e3b\u6559\u6539\u53d8\u4e86\u6885\u83f2\u65af\u7684\u4eba\u751f\uff0c\u8ba9\u5979\u6446\u8131\u4e86\u9ed1\u6697\u68a6\u9b47\uff0c\u8d70\u4e0a\u5149\u660e\u7684\u9053\u8def\u3002\u5728\u5c11\u5973\u7684\u751f\u547d\u4e2d\uff0c\u4ed6\u6beb\u65e0\u7591\u95ee\u662f\u6700\u91cd\u8981\u7684\u4eba\u4e4b\u4e00\uff0c\u88ab\u6f5c\u610f\u8bc6\u4e2d\u5f53\u505a\u7236\u4eb2\u770b\u5f85\uff0c\u4e5f\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\u5427\u3002</p><p>\u6240\u4ee5\uff0c\u8fd9\u6b21\u771f\u7684\u662f\u53bb\u89c1\u5bb6\u957f\u554a\u2026\u2026</p><p>\u89c1\u5973\u65b9\u5bb6\u957f\u8fd9\u79cd\u4e8b\u60c5\uff0c\u743c\u6069\u5012\u4e5f\u4e0d\u7b97\u964c\u751f\uff0c\u524d\u4e16\u7684\u4e8b\u60c5\u4e0d\u63d0\uff0c\u5c31\u8fd9\u8f88\u5b50\u4e5f\u4e0d\u662f\u7b2c\u4e00\u6b21\u4e86\u3002\u8fd8\u5728\u5deb\u5e08\u5b66\u9662\u7684\u65f6\u5019\uff0c\u4ed6\u5c31\u4e0a\u95e8\u89c1\u8fc7\u8299\u857e\u72c4\u7684\u8001\u7238\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u4ed6\u8fd8\u662f\u4e0d\u7531\u81ea\u4e3b\u5730\u6709\u70b9\u5fc3\u4e2d\u53d1\u614c\uff0c\u5fd0\u5fd1\u4e0d\u5b89\u6218\u6218\u5162\u5162\u8d77\u6765\u3002</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u4e0d\u4ec5\u4ed6\u7d27\u5f20\uff0c\u6885\u83f2\u65af\u4f3c\u4e4e\u4e5f\u6709\u4e9b\u7d27\u5f20\u2014\u2014\u4e0d\u5bf9\uff0c\u662f\u660e\u663e\u6bd4\u4ed6\u66f4\u7d27\u5f20\u3002\u56e0\u4e3a\u63a5\u4e0b\u6765\u7684\u4e00\u8def\u4e0a\uff0c\u5979\u90fd\u5728\u4e0d\u65ad\u5730\u544a\u8bc9\u743c\u6069\u6709\u5173\u5927\u4e3b\u6559\u7684\u4e8b\u60c5\uff0c\u8bf4\u4ed6\u7684\u4e3a\u4eba\uff0c\u8bf4\u4ed6\u7684\u4e60\u60ef\uff0c\u8bf4\u4ed6\u559c\u6b22\u4ec0\u4e48\u8ba8\u538c\u4ec0\u4e48\u6b23\u8d4f\u4ec0\u4e48\u5bb3\u6015\u2026\u2026\u5443\uff0c\u5012\u6ca1\u6709\u4ec0\u4e48\u5bb3\u6015\u7684\u4e1c\u897f\u3002\u6709\u4e9b\u8bdd\u660e\u660e\u91cd\u590d\u8bf4\u4e86\u597d\u51e0\u6b21\uff0c\u5979\u81ea\u5df1\u90fd\u4e00\u76f4\u6ca1\u6ce8\u610f\u5230\uff0c\u8fd9\u5728\u5e73\u65f6\u662f\u7edd\u5bf9\u4e0d\u53ef\u60f3\u8c61\u7684\u3002</p><p>\u201c\u603b\u4e4b\u5462\uff0c\u4ed6\u662f\u4e2a\u5f88\u597d\u7684\u4eba\uff0c\u201d\u6885\u83f2\u65af\u6700\u540e\u603b\u7ed3\uff0c\u201c\u6e29\u548c\u3001\u5bbd\u539a\u3001\u5f88\u597d\u76f8\u5904\uff0c\u65e0\u8bba\u4f60\u72af\u4e86\u4ec0\u4e48\u9519\uff0c\u4ed6\u90fd\u4e0d\u4f1a\u75be\u8a00\u5389\u8272\u5730\u8bad\u65a5\uff0c\u6700\u591a\u63d0\u9192\u51e0\u53e5\u3002\u53e6\u5916\u5462\uff0c\u4ed6\u559c\u6b22\u8bda\u5b9e\u7684\u4eba\u2014\u2014\u4ed6\u80fd\u591f\u770b\u900f\u4eba\u7684\u5fc3\uff0c\u77e5\u9053\u4f60\u8bf4\u7684\u6bcf\u4e00\u53e5\u8bdd\u662f\u771f\u662f\u5047\uff0c\u6240\u4ee5\u4f60\u5343\u4e07\u8bb0\u4f4f\uff0c\u4e0d\u8981\u5728\u4ed6\u9762\u524d\u8bf4\u8c0e\u3002\u5982\u679c\u4f60\u8bf4\u8c0e\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u63ed\u7a7f\uff0c\u4f46\u5fc3\u91cc\u4f1a\u5bf9\u4f60\u6709\u770b\u6cd5\u7684\u3002\u201d</p><p>\u743c\u6069\u70b9\u5934\uff0c\u201c\u660e\u767d\u4e86\uff0c\u6211\u4fdd\u8bc1\u8001\u8001\u5b9e\u5b9e\uff0c\u6709\u4e00\u8bf4\u4e00\u3002\u201d</p><p>\u201c\u4e5f\u4e0d\u7528\u90a3\u4e48\u62d8\u675f\uff0c\u4ed6\u4e0d\u6b23\u8d4f\u62d8\u675f\u7684\u4eba\uff0c\u5c24\u5176\u662f\u7537\u5b69\u5b50\uff0c\u201d\u6885\u83f2\u65af\u8d76\u5feb\u53c8\u53ee\u5631\uff0c\u201c\u5176\u5b9e\u4ed6\u81ea\u5df1\u5c31\u662f\u4e2a\u5f88\u6709\u5e7d\u9ed8\u611f\u7684\u4eba\uff0c\u867d\u7136\u4e0d\u7ecf\u5e38\u5f00\u73a9\u7b11\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u6211\u4e3e\u4e2a\u4f8b\u5b50\u5427\uff0c\u201d\u5c11\u5973\u60f3\u4e86\u60f3\uff0c\u201c\u66fe\u7ecf\u6709\u4e00\u6bb5\u65f6\u95f4\uff0c\u6cf0\u745f\u5c14\u6559\u533a\u90a3\u8fb9\u5174\u8d77\u4e00\u80a1\u6f6e\u6d41\uff0c\u5927\u5bb6\u90fd\u559c\u6b22\u6ca1\u4e8b\u5728\u773c\u775b\u4e0a\u8499\u4e2a\u773c\u7f69\u2014\u2014\u4f60\u77e5\u9053\u6837\u5f0f\u5427\uff0c\u5c31\u662f\u4e24\u5757\u692d\u5706\u5f62\u7684\u5e03\uff0c\u4e2d\u95f4\u7528\u5e26\u5b50\u8fde\u7740\uff0c\u518d\u7528\u5e26\u5b50\u7cfb\u5728\u8111\u540e\u90a3\u79cd\uff0c\u5f53\u7136\u662f\u900f\u660e\u7684\uff0c\u4e0d\u5f71\u54cd\u89c6\u89c9\uff0c\u636e\u8bf4\u8d77\u521d\u7684\u539f\u56e0\u662f\u6a21\u4eff\u795e\u7947\uff0c\u5f3a\u8c03\u4ec0\u4e48\u2018\u76f2\u773c\u516c\u6b63\uff0c\u4e0d\u504f\u4e0d\u501a\u2019\u3002\u8fd9\u91cc\u4e5f\u6709\u51e0\u4e2a\u7267\u5e08\u8ddf\u7740\u5b66\u4e60\uff0c\u88ab\u5927\u4e3b\u6559\u770b\u89c1\u4e86\u3002\u4ed6\u62ff\u8fc7\u4e00\u4e2a\u4eba\u7684\u773c\u7f69\uff0c\u8bf4\u4e86\u4e00\u53e5\u8bdd\u3002\u201d</p><p>\u201c\u4ed6\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4ed6\u8bf4\uff0c\u5514\uff0c\u5f88\u6f02\u4eae\u7684\u60c5\u8da3\u5185\u8863\u554a\uff0c\u662f\u6587\u80f8\u5427\uff0c\u5c31\u662f\u5c3a\u7801\u592a\u5c0f\u4e86\u70b9\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u4ece\u6b64\u4e4b\u540e\u5c31\u518d\u4e5f\u6ca1\u6709\u4eba\u6234\u773c\u7f69\u4e86\uff0c\u5f53\u7136\u76f2\u4eba\u9664\u5916\uff0c\u201d\u6885\u83f2\u65af\u683c\u683c\u7b11\u7740\uff0c\u201c\u6240\u4ee5\u4f60\u5c31\u77e5\u9053\uff0c\u4ed6\u5176\u5b9e\u4e5f\u662f\u5f88\u6709\u8da3\u7684\u4eba\uff0c\u7edd\u5bf9\u4e0d\u662f\u4f60\u60f3\u8c61\u7684\u90a3\u6837\u53e4\u677f\u2014\u2014\u8981\u4e0d\u7136\u7684\u8bdd\uff0c\u4ed6\u5f53\u5e74\u4e5f\u4e0d\u4f1a\u88ab\u5927\u957f\u8001\u770b\u4e2d\uff0c\u70b9\u540d\u505a\u5927\u4e3b\u6559\u4e86\u3002\u201d</p><p>\u201c\u5443\uff0c\u4f60\u4eec\u7684\u5927\u4e3b\u6559\u662f\u90a3\u8001\u5deb\u5996\u6765\u4efb\u547d\u7684\uff1f\u201d</p><p>\u201c\u4e0d\u7b97\u4efb\u547d\uff0c\u4f46\u6309\u7167\u6559\u4f1a\u7684\u89c4\u77e9\uff0c\u5927\u4e3b\u6559\u4f4d\u7f6e\u7a7a\u7f3a\u65f6\uff0c\u957f\u8001\u548c\u5176\u4ed6\u526f\u4e3b\u6559\u90fd\u6709\u63a8\u8350\u5019\u9009\u4eba\u7684\u6743\u529b\u3002\u800c\u4e14\u6211\u4eec\u8fd9\u4f4d\u5927\u957f\u8001\u5730\u4f4d\u7279\u6b8a\uff0c\u6240\u4ee5\u6295\u7968\u7684\u65f6\u5019\uff0c\u522b\u4eba\u7684\u4e00\u7968\u5c31\u662f\u4e00\u7968\uff0c\u4ed6\u7684\u4e00\u7968\u76f8\u5f53\u4e8e\u4e09\u7968\u3002\u4f60\u77e5\u9053\u7684\uff0c\u4e00\u822c\u526f\u4e3b\u6559\u4e5f\u5c31\u4e24\u5230\u56db\u540d\uff0c\u52a0\u4e0a\u957f\u8001\u4e0d\u8fc7\u516d\u4e03\u4eba\uff0c\u6240\u4ee5\u53ea\u8981\u80fd\u5f97\u5230\u4ed6\u652f\u6301\uff0c\u57fa\u672c\u4e0a\u5c31\u80af\u5b9a\u5f53\u9009\u4e86\u3002\u201d</p><p>\u53cd\u8fc7\u6765\u8bf4\uff0c\u5982\u679c\u88ab\u4ed6\u5426\u5b9a\uff0c\u90a3\u5c31\u5c8c\u5c8c\u53ef\u5371\u4e86\u3002\u8fd9\u8001\u5deb\u5996\u7684\u6743\u529b\u8fd8\u771f\u5927\uff0c\u4fe8\u7136\u5c31\u662f\u5e55\u540e\u8001\u677f\uff0c\u5f53\u7136\u4e86\uff0c\u4ee5\u4ed6\u521b\u59cb\u5143\u8001\u7684\u8d44\u5386\u8eab\u4efd\uff0c\u6551\u4ea1\u7eed\u5b58\u7684\u83ab\u5927\u529f\u52cb\uff0c\u8fd9\u4e5f\u5728\u60c5\u7406\u4e4b\u4e2d\u5c31\u662f\uff0c\u4e0d\u8db3\u4e3a\u5947\u3002</p><p>\u201c\u96be\u602a\uff0c\u6709\u8fd9\u79cd\u5927\u957f\u8001\uff0c\u6240\u4ee5\u4f60\u4eec\u4e00\u4e2a\u6bd4\u4e00\u4e2a\u53e6\u7c7b\u2014\u2014\u4f30\u8ba1\u4e0d\u591f\u53e6\u7c7b\u7684\uff0c\u90fd\u88ab\u4ed6\u6253\u538b\u4e0b\u53bb\u4e86\u5427\u3002\u201d</p><p>\u201c\u53e6\u7c7b\uff1f\u201d\u6885\u83f2\u65af\u4e0d\u61c2\u8fd9\u4e2a\u65b0\u9c9c\u540d\u8bcd\u3002</p><p>\u201c\u53e6\u7c7b\u5c31\u662f\u2018\u975e\u5e38\u6001\u2019\uff0c\u6216\u8005\u8bf4\u53d8\u6001\u3002\u201d</p><p>\u201c\u4f60\u624d\u662f\u53d8\u6001\uff01\u201d</p><p>\u201c\u2026\u2026\u597d\u5427\uff0c\u6362\u4e2a\u8bf4\u6cd5\uff0c\u662f\u2018\u975e\u5178\u578b\u6027\u72b6\u6001\u2019\uff0c\u7b80\u79f0\u975e\u5178\u3002\u201d</p><p>\u6885\u83f2\u65af\u663e\u7136\u4e5f\u4e0d\u4f1a\u61c2\u975e\u5178\u7684\u610f\u601d\uff0c\u4f46\u5979\u4e5f\u65e0\u6687\u518d\u8ffd\u95ee\uff0c\u56e0\u4e3a\u5c31\u5728\u8bf4\u8bdd\u671f\u95f4\uff0c\u4ed6\u4eec\u5df2\u7ecf\u4ece\u540e\u95e8\u8d70\u8fdb\u795e\u6bbf\uff0c\u6765\u5230\u4e00\u5ea7\u4ece\u5916\u8868\u770b\u4e0d\u751a\u8d77\u773c\u7684\u623f\u5b50\u95e8\u524d\u3002\u201c\u5343\u4e07\u8bb0\u7740\uff0c\u4e0d\u8981\u6492\u8c0e\uff0c\u201d\u5979\u653e\u5fc3\u4e0d\u4e0b\u5730\u53ee\u5631\uff0c\u201c\u4ed6\u771f\u7684\u80fd\u770b\u51fa\u6765\u7684\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\u5566\uff0c\u827e\u5f25\u8587\uff0c\u4f60\u4e0d\u8981\u5bf9\u6211\u7684\u4eba\u54c1\u8fd9\u4e48\u4e0d\u653e\u5fc3\u561b\u3002\u201d</p><p>\u201c\u4e00\u4e2a\u5230\u5904\u6cbe\u82b1\u60f9\u8349\u7684\u7537\u670b\u53cb\uff0c\u6211\u653e\u5fc3\u624d\u5947\u602a\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u9519\u4e86\uff0c\u6211\u6709\u7f6a\uff0c\u6211\u5fcf\u6094\u3002\u201d</p><p>\u201c\u54fc\uff0c\u8c01\u4fe1\u4f60\u3002\u201d</p><p>\u201c\u597d\u5566\u597d\u5566\uff0c\u771f\u7684\u77e5\u9053\u4e86\uff0c\u6211\u4fdd\u8bc1\uff0c\u4ed6\u95ee\u4ec0\u4e48\u6211\u7b54\u4ec0\u4e48\uff0c\u54ea\u6015\u4ed6\u95ee\u6211\u6628\u5929\u665a\u4e0a\u548c\u4f60\u505a\u8fc7\u51e0\u6b21\uff0c\u6211\u90fd\u4e00\u4e94\u4e00\u5341\u5730\u56de\u7b54\u3002\u201d</p><p>\u201c\u53bb\u6b7b\uff01\u201d\u5c11\u5973\u7f9e\u7ea2\u7740\u8138\uff0c\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u7136\u540e\u5c48\u6307\u8f7b\u8f7b\u53e9\u95e8\u3002</p><p>\u51e0\u79d2\u79cd\u540e\uff0c\u743c\u6069\u542c\u5230\u4e86\u8f7b\u8f7b\u7684\u811a\u6b65\u58f0\u9760\u8fd1\uff0c\u7136\u540e\u95e8\u6253\u5f00\u4e86\uff0c\u51fa\u73b0\u4e00\u4e2a\u4eba\u5f71\u3002\u4ed6\u672c\u4ee5\u4e3a\u4f1a\u770b\u89c1\u4e00\u4f4d\u7cbe\u795e\u77cd\u94c4\u7684\u8001\u8005\uff0c\u8eab\u6750\u9ad8\u5927\uff0c\u987b\u53d1\u82b1\u767d\uff0c\u773c\u795e\u9510\u5229\uff08\u6885\u83f2\u65af\u5fd8\u4e86\u544a\u8bc9\u4ed6\u5927\u4e3b\u6559\u7684\u76f8\u8c8c\uff0c\u4f46\u743c\u6069\u5c31\u662f\u5982\u6b64\u731c\u6d4b\u7684\uff09\uff0c\u7136\u800c\u4ee4\u4ed6\u5927\u5403\u4e00\u60ca\u7684\u662f\uff0c\u95e8\u53e3\u51fa\u73b0\u7684\u538b\u6839\u4e0d\u662f\u4ec0\u4e48\u8001\u8005\uff0c\u8fde\u7537\u6027\u90fd\u4e0d\u662f\u2014\u2014\u800c\u662f\u4e00\u4f4d\u8eab\u6750\u5a07\u5c0f\u53ef\u7231\u7684\u5973\u5b69\u5b50\uff0c\u7a7f\u7740\u9ed1\u8272\u7684\u65e0\u8896\u8fde\u8863\u88d9\uff0c\u5916\u9762\u7f69\u7740\u9576\u6709\u82b1\u8fb9\u7684\u767d\u8272\u56f4\u88d9\uff0c\u5934\u4e0a\u7684\u5934\u9970\u4e5f\u9576\u7740\u767d\u8272\u82b1\u8fb9\uff0c\u6b63\u7b11\u5bb9\u751c\u7f8e\u5730\u671d\u4ed6\u4eec\u6253\u62db\u547c\u3002</p><p>\u201c\u4e0b\u5348\u597d\uff0c\u4e24\u4f4d\uff0c\u8bf7\u8fdb\u6765\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u743c\u6069\u7684\u610f\u8bc6\u6682\u65f6\u51fa\u73b0\u77ed\u8def\uff0c\u8fd8\u662f\u88ab\u6885\u83f2\u65af\u6084\u6084\u626f\u4e86\u4ed6\u4e00\u628a\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u8fde\u5fd9\u56de\u793c\uff0c\u7136\u540e\u61f5\u61f5\u61c2\u61c2\u5730\u8ddf\u7740\u8d70\u8fdb\u623f\u95f4\u3002\u201c\u5979\u662f\u8c01\uff1f\u201d\u4ed6\u5728\u80cc\u540e\u6084\u6084\u6307\u4e86\u6307\u5973\u5b69\uff0c\u7528\u624b\u52bf\u8be2\u95ee\u6885\u83f2\u65af\u3002</p><p>\u201c\u5973\u4ec6\u554a\uff0c\u201d\u6885\u83f2\u65af\u7528\u65e0\u58f0\u7684\u53e3\u578b\u56de\u7b54\uff0c\u201c\u4f60\u8fd9\u4e48\u60ca\u8bb6\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u6ca1\u4ec0\u4e48\uff0c\u6ca1\u4ec0\u4e48\uff0c\u53ea\u662f\u6ca1\u60f3\u5230\u4f1a\u770b\u89c1\u8fd9\u4e48\u534e\u4e3d\u7684\u59b9\u6296\u88c5\uff0c\u4e00\u65f6\u95f4\u5927\u8111\u53cd\u5e94\u4e0d\u8fc7\u6765\u2026\u2026</p><p>\u4eca\u5929\u771f\u662f\u795e\u5947\u7684\u4e00\u5929\uff0c\u751f\u6d3b\u4e2d\u5904\u5904\u5145\u6ee1\u4e86\u60ca\u559c\u548c\u610f\u5916\u554a\u3002</p><p>\u8ddf\u7740\u5973\u4ec6\u8d70\u8fdb\u5ba2\u5385\uff0c\u5728\u6c99\u53d1\u4e0a\u5750\u4e0b\uff0c\u88ab\u544a\u77e5\u5927\u4e3b\u6559\u6b63\u5728\u4e66\u623f\uff0c\u8bf7\u4ed6\u4eec\u7a0d\u5750\u7247\u523b\u3002\u6885\u83f2\u65af\u663e\u7136\u662f\u5e38\u5ba2\u4e86\uff0c\u4e0d\u9700\u8981\u5973\u4ec6\u52a8\u624b\uff0c\u719f\u95e8\u719f\u8def\u5730\u5c31\u53bb\u9910\u5385\u62ff\u51fa\u4e00\u6574\u5957\u5de5\u5177\u716e\u8d77\u4e86\u9ed1\u5496\u5561\uff0c\u5f88\u5feb\u9999\u98d8\u56db\u6ea2\u3002\u201c\u4ed6\u6700\u559c\u6b22\u6211\u716e\u7684\u5496\u5561\uff0c\u201d\u5c11\u5973\u5bf9\u743c\u6069\u8bf4\uff0c\u201c\u6bcf\u6b21\u6765\u90fd\u8981\u7ed9\u4ed6\u716e\u4e00\u58f6\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u4e3a\u4ec0\u4e48\u4ece\u6765\u6ca1\u7ed9\u6211\u716e\u8fc7\uff1f\u201d</p><p>\u201c\u4f60\u53c8\u4e0d\u7231\u559d\u5496\u5561\uff0c\u4e0d\u662f\u603b\u8bf4\u592a\u82e6\u4e48\u3002\u201d</p><p>\u201c\u662f\u5f88\u82e6\uff0c\u4f46\u5982\u679c\u662f\u4f60\u716e\u7684\uff0c\u518d\u82e6\u6211\u90fd\u613f\u610f\u559d\u554a\u3002\u201d</p><p>\u201c\u884c\uff0c\u4f60\u8bf4\u7684\uff0c\u7b49\u4ee5\u540e\u6bcf\u5929\u6211\u7ed9\u4f60\u51c6\u5907\u4e00\u58f6\u3002\u201d</p><p>\u201c\u2026\u2026\u54c8\u54c8\uff0c\u8bf4\u8bf4\u800c\u5df2\uff0c\u8bf4\u8bf4\u800c\u5df2\u5566\uff0c\u4e0d\u8981\u5f53\u771f\uff0c\u827e\u5f25\u8587\u3002\u201d</p><p>\u201c\u6211\u5c31\u662f\u5f53\u771f\u4e86\u2014\u2014\u5927\u4e3b\u6559\uff0c\u201d\u5979\u770b\u89c1\u5ba2\u5385\u95e8\u53e3\u51fa\u73b0\u7684\u4eba\u5f71\uff0c\u8fde\u5fd9\u7ad9\u4e86\u8d77\u6765\uff0c\u201c\u60a8\u597d\u3002\u201d</p><p>\u201c\u4e0b\u5348\u597d\u554a\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4e00\u4e2a\u8eab\u6750\u534a\u70b9\u4e0d\u9ad8\u5927\uff0c\u5934\u53d1\u4e4c\u9ed1\uff0c\u80e1\u987b\u522e\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u800c\u4e14\u7565\u7565\u6709\u4e9b\u77ee\u80d6\uff0c\u5e74\u9f84\u5927\u7ea6\u5728\u4e94\u5341\u5c81\u4e0a\u4e0b\u7684\u5706\u8138\u4e2d\u5e74\u4eba\u8d70\u8fdb\u5ba2\u5385\uff0c\u4ed6\u7684\u8138\u4e0a\u6302\u7740\u548c\u6c14\u7684\u7b11\u5bb9\uff0c\u770b\u5411\u743c\u6069\uff0c\u201c\u8fd9\u4f4d\u662f\u5170\u5c3c\u65af\u7279\u5148\u751f\u5bf9\u5427\u3002\u201d</p><p>\u201c\u662f\uff0c\u662f\u7684\uff0c\u56e0\u4e3a\u4ed6\u4eca\u5929\u8bf4\u5f88\u60f3\u62dc\u89c1\u60a8\uff0c\u6211\u5c31\u987a\u4fbf\u628a\u4ed6\u5e26\u8fc7\u6765\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8d76\u7d27\u56de\u7b54\uff0c\u201c\u6253\u6270\u60a8\u4f11\u606f\u4e86\uff0c\u5b9e\u5728\u662f\u4e0d\u597d\u610f\u601d\u3002\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u4e5f\u6b63\u597d\u521a\u521a\u7761\u9192\uff0c\u201d\u5927\u4e3b\u6559\u7b11\u7740\u6447\u6447\u5934\uff0c\u201c\u672c\u6765\u5934\u8fd8\u6709\u70b9\u6655\u6655\u6c89\u6c89\u7684\uff0c\u80fd\u591f\u770b\u89c1\u4f60\u4eec\u8fd9\u6837\u671d\u6c14\u52c3\u53d1\u7684\u5e74\u8f7b\u4eba\uff0c\u8ba9\u6211\u8001\u5934\u5b50\u7684\u7cbe\u795e\u90fd\u4e3a\u4e4b\u4e00\u632f\u5462\u3002\u201d</p><p>\u201c\u55ef\uff0c\u55ef\u3002\u201d</p><p>\u6885\u83f2\u65af\u660e\u663e\u5c40\u4fc3\u4e0d\u5b89\uff0c\u4e00\u65f6\u95f4\u90fd\u4e0d\u77e5\u9053\u8be5\u8bf4\u4e9b\u4ec0\u4e48\uff0c\u53cd\u5012\u662f\u5927\u4e3b\u6559\u4e3b\u52a8\u6311\u8d77\u8bdd\u9898\uff0c\u548c\u743c\u6069\u95f2\u804a\u8d77\u4ed6\u4e00\u8def\u6765\u7684\u89c1\u95fb\uff0c\u8ba9\u6c14\u6c1b\u5f88\u5feb\u53d8\u5f97\u878d\u6d3d\u8d77\u6765\u3002\u8bf4\u4e86\u7247\u523b\uff0c\u6885\u83f2\u65af\u8d77\u8eab\u53bb\u9910\u5385\uff0c\u770b\u7740\u5979\u7684\u8eab\u5f71\u79bb\u5f00\uff0c\u5927\u4e3b\u6559\u7a81\u7136\u4e2d\u6b62\u4e86\u8c08\u8bdd\uff0c\u5fae\u5fae\u7b11\u7740\u770b\u5411\u743c\u6069\u3002</p><p>\u743c\u6069\u88ab\u4ed6\u770b\u5f97\u624b\u8db3\u65e0\u63aa\uff0c\u5fc3\u4e2d\u53d1\u614c\uff0c\u201c\u600e\u2026\u2026\u600e\u4e48\u4e86\uff1f\u201d\u4ed6\u52c9\u5f3a\u7b11\u7740\uff0c\u201c\u6709\u4ec0\u4e48\u4e0d\u5bf9\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u6709\u51e0\u4e2a\u95ee\u9898\u60f3\u95ee\u4f60\u3002\u522b\u7d27\u5f20\uff0c\u6211\u4e0d\u4f1a\u95ee\u4f60\u4eec\u6628\u665a\u505a\u4e86\u51e0\u6b21\u2014\u2014\u56e0\u4e3a\u6211\u77e5\u9053\u827e\u5f25\u8587\u662f\u4eca\u5929\u51cc\u6668\u56db\u70b9\u949f\u624d\u56de\u53bb\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u60a8\u8bf7\u95ee\u5427\u3002\u201d</p><p>\u201c\u90a3\u6211\u5c31\u5192\u6627\u4e86\uff0c\u201d\u5927\u4e3b\u6559\u60a0\u7136\u8bf4\uff0c\u201c\u4f60\u559c\u6b22\u827e\u5f25\u8587\u5417\uff1f\u201d</p><p>\u201c\u559c\u6b22\u3002\u201d\u743c\u6069\u7acb\u523b\u56de\u7b54\u3002</p><p>\u201c\u7231\u5979\u5417\uff1f\u201d</p><p>\u201c\u7231\u3002\u201d</p><p>\u5927\u4e3b\u6559\u7b11\u8d77\u6765\uff0c\u201c\u56de\u7b54\u5f97\u771f\u5e72\u8106\u5462\uff0c\u5e74\u8f7b\u4eba\uff0c\u201d\u4ed6\u504f\u4e86\u504f\u5934\uff0c\u201c\u77e5\u9053\u7231\u7684\u610f\u601d\u5417\uff1f\u201d</p></article>"}}}]);
