(window.webpackJsonp=window.webpackJsonp||[]).push([[171],{194:function(p){p.exports={chapterTitle:null,title:"\u7b2c171\u7ae0 \u79bb\u95f4",content:"<article><h2>\u7b2c171\u7ae0 \u79bb\u95f4</h2><p>\u3000\u3000\u5468\u53d4\u60e0\u7559\u60dc\u5bb9\u3001\u60dc\u7fbd\u4e24\u59d0\u59b9\u591c\u91cc\u4f4f\u5728\u5c0f\u9526\u6e56\u7554\u7684\u522b\u5885\u91cc\uff0c\u5218\u6587\u5175\u9001\u5510\u5b66\u8c26\u4e00\u5bb6\u56de\u5e08\u9662\uff0c\u5085\u4fca\u8f7d\u7740\u5f20\u77e5\u884c\u4e00\u5bb6\u4e0e\u4ed6\u7231\u4eba\u674e\u660e\u745c\u5148\u56de\u5e02\u653f\u5e9c\u673a\u5173\u5927\u9662\uff0c\u5f20\u606a\u5230\u5730\u8ba9\u4ed6\u7238\u5988\u5148\u4e0b\u8f66\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u95ee\uff1a\u201c\u4eca\u5929\u600e\u4e48\u8fd8\u4e0d\u7761\u5bb6\u91cc\uff1f\u201d</p><p>\u3000\u3000\u201c\u672c\u6765\u6253\u7b97\u540e\u5929\u9001\u82b7\u5f64\u56de\u7701\u57ce\uff0c\u6211\u4e5f\u6b63\u597d\u5230\u7701\u57ce\u6709\u4e8b\uff1b\u660e\u5929\u8981\u8d76\u65e9\uff0c\u6211\u5148\u53bb\u628a\u8d44\u6599\u62ff\u8fc7\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u6881\u683c\u73cd\u62ac\u5934\u770b\u4e86\u591c\u5149\u8155\u8868\uff0c\u90fd\u8fc7\u5b50\u591c\u4e86\uff1a\u201c\u660e\u5929\u62ff\u4e0d\u884c\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u660e\u5929\u5fc3\u6025\u614c\u5fd9\u7684\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u6709\u4ec0\u4e48\u4e8b\u60c5\u7ed9\u843d\u4e0b\u3002\u201d\u5f20\u606a\u60f3\u5230\u4e00\u4ef6\u4e8b\uff0c\u95ee\u9053\uff0c\u201c\u519c\u673a\u5382\u7ade\u8058\u5382\u957f\u7684\u4e8b\u60c5\u8fd8\u6709\u6ca1\u6709\u7ed3\u679c\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u8fd9\u91cc\u5df2\u7ecf\u6709\u4e86\u521d\u6b65\u7684\u7ed3\u679c\uff0c\u540e\u6765\u4ece\u804c\u5de5\u4e2d\u63a8\u9009\u51fa\u6765\u7684\u56db\u540d\u7ade\u8058\u4eba\uff0c\u5168\u90e8\u5728\u91cc\u9762\uff0c\u73b0\u5728\u7684\u540d\u5355\u7b49\u7740\u519c\u673a\u5c40\u90a3\u8fb9\u786e\u8ba4\u4e4b\u540e\u5c31\u6b63\u5f0f\u516c\u5e03\u3002\u201d</p><p>\u3000\u3000\u6015\u4ed6\u7238\u7238\u591a\u5fc3\uff0c\u5f20\u606a\u6ca1\u6709\u95ee\u65bd\u536b\u5fe0\u5728\u4e0d\u5728\u786e\u9009\u540d\u5355\u91cc\u9762\u3002\u65bd\u536b\u5fe0\u539f\u6765\u5c31\u662f\u519c\u673a\u5382\u7684\u8f66\u95f4\u4e3b\u4efb\uff0c\u4ed6\u539f\u5148\u53ea\u80fd\u7ade\u8058\u526f\u5382\u957f\u7684\u804c\u52a1\u3002\u5468\u5065\u4e0e\u5176\u4ed6\u4e09\u4e2a\u526f\u5382\u957f\u56e0\u4e3a\u6d89\u5acc\u6e0e\u804c\u7f6a\u7ed9\u5e02\u68c0\u5bdf\u9662\u7acb\u6848\u8c03\u67e5\u4ece\u7ade\u8058\u4eba\u540d\u5355\u4e2d\u5212\u6389\u4e4b\u540e\uff0c\u6d77\u5dde\u63a7\u80a1\u653e\u5bbd\u7ade\u8058\u5382\u957f\u7684\u9650\u5236\u6761\u4ef6\uff0c\u65bd\u536b\u5fe0\u6539\u7ade\u8058\u519c\u673a\u5382\u7684\u5382\u957f\u3002\u542c\u8bb8\u601d\u8f6c\u8ff0\u519c\u673a\u5382\u5382\u957f\u7ade\u8058\u7684\u60c5\u51b5\uff0c\u65bd\u536b\u5fe0\u5728\u519c\u673a\u5382\u666e\u901a\u804c\u5de5\u4e2d\u7684\u5a01\u671b\u672c\u6765\u5c31\u5f88\u9ad8\uff0c\u5468\u5065\u5728\u4ed6\u751f\u65e5\u5bb4\u4e0a\u7ed9\u4e22\u758f\u6e2f\u6cb3\u91cc\u7684\u4e8b\u60c5\uff0c\u8ba9\u4ed6\u7684\u4eba\u6c14\u5728\u519c\u673a\u5382\u8fbe\u5230\u9876\u70b9\uff0c\u4ece\u804c\u5de5\u63a8\u9009\u60c5\u51b5\u6765\u770b\uff0c\u65bd\u536b\u5fe0\u7684\u524d\u666f\u5f88\u4e50\u89c2\uff0c\u4f46\u662f\u5382\u957f\u6700\u7ec8\u7684\u4eba\u9009\uff0c\u6d77\u5dde\u63a7\u80a1\u8fd8\u4f1a\u6709\u66f4\u5168\u9762\u7684\u8003\u8651\u3002</p><p>\u3000\u3000\u5468\u5065\u4e0e\u5176\u4ed6\u4e09\u540d\u526f\u5382\u957f\u6e0e\u804c\u81f4\u4f7f\u519c\u673a\u5382\u635f\u5931\u60e8\u91cd\u7684\u4e8b\u5b9e\u5f97\u5230\u8bc1\u5b9e\uff0c\u5f20\u606a\u5fc3\u60f3\u519c\u673a\u5c40\u8fd9\u65f6\u5019\u4e0d\u4f1a\u8df3\u51fa\u6765\u963b\u6321\u6d77\u5dde\u63a7\u80a1\u786e\u5b9a\u51fa\u6765\u7684\u7ba1\u7406\u5c42\u540d\u5355\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5dee\u4e0d\u591a\u5df2\u7ecf\u5b9a\u4e86\u4e0b\u6765\uff0c\u5c31\u8ba9\u65bd\u536b\u5fe0\u81ea\u5df1\u7b49\u540d\u5355\u6700\u7ec8\u6b63\u5f0f\u7684\u516c\u5e03\u5427\u3002</p><p>\u3000\u3000\u5e02\u68c0\u5bdf\u9662\u8fd8\u6ca1\u6709\u5bf9\u5468\u5065\u4e0e\u5176\u4ed6\u4e09\u540d\u526f\u5382\u957f\u91c7\u63aa\u63aa\u65bd\uff0c\u519c\u673a\u5c40\u5df2\u7ecf\u5ba3\u5e03\u5c06\u4ed6\u4eec\u5f00\u9664\u516c\u804c\uff0c\u7b49\u5e02\u68c0\u5bdf\u9662\u7684\u8fdb\u4e00\u6b65\u5904\u7f6e\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u4e3a\u6b64\u8fd8\u7279\u610f\u8bf7\u5f20\u606a\u51fa\u6765\u5403\u4e86\u987f\u996d\u8868\u8fbe\u8c22\u610f\uff0c\u9887\u4e3a\u6028\u6068\u7684\u8bf4\uff1a\u201c\u4ee5\u540e\u7684\u4e8b\u60c5\u5c31\u4ea4\u7ed9\u6211\u4e86\u3002\u201d</p><p>\u3000\u3000\u867d\u7136\u8bf4\u5468\u5065\u4e0e\u519c\u673a\u5382\u4e09\u540d\u526f\u5382\u957f\u8eab\u4e0a\u4e0d\u4f1a\u4ec5\u4ec5\u6e0e\u804c\u8fd9\u4e48\u7b80\u5355\uff0c\u4f46\u91d1\u56fd\u6d77\u53ea\u662f\u5230\u6d77\u5dde\u6302\u804c\u800c\u5df2\uff0c\u8fd8\u6ca1\u6709\u80fd\u529b\u63a8\u52a8\u6848\u4ef6\u7ee7\u7eed\u5f80\u6df1\u5904\u8c03\u67e5\uff0c\u5373\u4f7f\u662f\u6e0e\u804c\u7f6a\uff0c\u6700\u7ec8\u4e5f\u4f1a\u4ece\u8f7b\u5904\u7f6e\uff0c\u519c\u673a\u5c40\u515a\u7ec4\u751a\u81f3\u6ca1\u6709\u5c06\u8fd9\u4e09\u4eba\u5f00\u9664\u515a\u7c4d\uff0c\u63a5\u4e0b\u6765\u7684\u4e8b\u60c5\u81ea\u7136\u5c31\u7531\u90b5\u81f3\u521a\u63a5\u624b\u5904\u7f6e\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u540e\u6765\u4e5f\u4e3b\u52a8\u63d0\u53ca\u4ed6\u8ddf\u4e07\u52c7\u63a5\u89e6\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u610f\u8bc6\u5230\u4e07\u52c7\u6709\u8ba8\u597d\u9526\u6e56\u8fd9\u8fb9\u7684\u7528\u610f\u5728\u91cc\u9762\uff0c\u5fc3\u91cc\u60f3\uff1a\u8981\u662f\u8ba9\u5468\u5bcc\u660e\u77e5\u9053\uff0c\u4ed6\u5fc3\u91cc\u4f1a\u600e\u4e48\u7422\u78e8\u4e07\u52c7\u7684\u5fc3\u6001\uff1f\u4ed6\u4f1a\u4e0d\u4f1a\u62c5\u5fc3\u4e07\u52c7\u7ed9\u5510\u5b66\u8c26\u62c9\u62e2\u8fc7\u53bb\uff0c\u4ed6\u4f1a\u4e0d\u4f1a\u62c5\u5fc3\u4ed6\u6240\u6709\u7684\u6253\u7b97\u53ef\u80fd\u4f1a\u6210\u4e3a\u7af9\u7bee\u5b50\u6253\u6c34\u4e00\u573a\u7a7a\uff1f</p><p>\u3000\u3000\u53ea\u8981\u80fd\u8ba9\u5468\u5bcc\u660e\u4e0e\u4e07\u52c7\u4e4b\u95f4\u751f\u51fa\u4e00\u4e1d\u95f4\u9699\u7684\u673a\u4f1a\uff0c\u5f20\u606a\u81ea\u7136\u4e0d\u4f1a\u9519\u8fc7\uff0c\u7279\u522b\u662f\u90b5\u81f3\u521a\u8fdb\u5165\u5546\u4e1a\u5730\u4ea7\u7684\u51b3\u5fc3\u5f88\u575a\u5b9a\uff0c\u5f20\u606a\u7edd\u6ca1\u6709\u4e0d\u5229\u7528\u7684\u9053\u7406\uff0c\u4ed6\u5bf9\u90b5\u81f3\u521a\u8bf4\uff1a\u201c\u9526\u6e56\u7b79\u5efa\u4e4b\u521d\uff0c\u9526\u6e56\u5c31\u8ddf\u5f90\u7701\u957f\u3001\u5510\u5e02\u957f\u7b49\u4eba\u6709\u8fc7\u627f\u8bfa\uff0c\u4e0d\u4f1a\u8f7b\u6613\u6d89\u53ca\u654f\u611f\u3001\u5bb9\u6613\u5f15\u8d77\u8d1f\u9762\u8bae\u8bba\u7684\u9886\u57df\uff0c\u4f60\u8981\u505a\u5546\u4e1a\u5730\u4ea7\uff0c\u9526\u6e56\u867d\u7136\u4e0d\u53c2\u4e0e\uff0c\u8d22\u52a1\u4e0d\u4f1a\u6709\u5f80\u6765\uff0c\u4f46\u4e5f\u4f1a\u5c3d\u53ef\u80fd\u63d0\u4f9b\u5e2e\u52a9\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u6ca1\u6709\u8003\u8651\u5230\u5e02\u91cc\u590d\u6742\u7684\u5c40\u52bf\uff0c\u867d\u7136\u4e0d\u80fd\u8054\u5408\u9526\u6e56\u4e00\u8d77\u6324\u5165\u6c99\u7530\u65e7\u57ce\u6539\u9020\u9879\u76ee\u6709\u4e9b\u9057\u61be\uff0c\u4f46\u662f\u5f20\u606a\u7684\u8fd9\u756a\u8868\u6001\uff0c\u6bd4\u8d77\u4e4b\u524d\u575a\u51b3\u62d2\u7edd\u7684\u6001\u5ea6\u8981\u597d\u4e0a\u8bb8\u591a\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u5bf9\u4ed6\u8bf4\uff1a\u201c\u9526\u6e56\u8981\u589e\u8d44\u6539\u6210\u80a1\u4efd\u6709\u9650\u516c\u53f8\uff0c\u51fa\u8d44\u4eba\u8981\u589e\u52a0\u5230\u4e94\u4eba\u4ee5\u4e0a\uff0c\u4f60\u6709\u6ca1\u6709\u5174\u8da3\u8fdb\u6765\u2026\u2026\u201d\u90b5\u81f3\u521a\u8981\u641e\u5546\u4e1a\u5730\u4ea7\uff0c\u4ed6\u80fd\u62bd\u51fa\u7684\u94b1\u5f88\u6709\u9650\uff0c\u5c31\u7b97\u7ed9\u4ed6\u5728\u9526\u6e56\u5360\u4e9b\u80a1\u4efd\uff0c\u4e5f\u4e0d\u4f1a\u6709\u591a\u5c11\uff0c\u53ea\u662f\u8981\u51d1\u8db3\u4e94\u4eba\u540d\u989d\uff0c\u5173\u952e\u662f\u5728\u4ed6\u7684\u8eab\u4e0a\u6df1\u6df1\u7684\u70d9\u4e0b\u9526\u6e56\u7684\u70d9\u5370\u3002\u4e4b\u540e\u4ed6\u8ddf\u4e07\u52c7\u8d8a\u4eb2\u5bc6\uff0c\u8d8a\u6709\u53ef\u80fd\u5f15\u8d77\u5468\u5bcc\u660e\u7684\u7591\u5fc3\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u53c8\u54ea\u91cc\u4f1a\u62d2\u7edd\u5f20\u606a\u7684\u63d0\u8bae\uff1f\u4e0e\u8c22\u665a\u6674\u4e00\u8d77\u6210\u4e3a\u9526\u6e56\u5171\u540c\u51fa\u8d44\u4eba\u3001\u6210\u4e3a\u6539\u4e3a\u80a1\u4efd\u516c\u53f8\u4e4b\u540e\u7684\u9526\u6e56\u7684\u8463\u4e8b\uff0c\u662f\u90b5\u81f3\u521a\u4e4b\u524d\u60f3\u90fd\u4e0d\u6562\u60f3\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u660e\u5929\u4f1a\u76f4\u63a5\u4e58\u98de\u673a\u8d76\u56de\u7701\u57ce\uff0c\u8981\u8d76\u7740\u53bb\u63a5\u4ed6\u7684\u98de\u673a\uff0c\u660e\u5929\u4ece\u6d77\u5dde\u51fa\u53d1\u8981\u8d77\u65e9\u3002\u4e5f\u4e0d\u80fd\u5148\u5c06\u5085\u4fca\u7684\u7231\u4eba\u4e00\u4e2a\u4eba\u4e22\u5bb6\u91cc\u53bb\uff0c\u4fbf\u4e00\u8d77\u53bb\u76ca\u9686\u9547\u7684\u5de5\u5382\u3002</p><p>\u3000\u3000\u5f71\u789f\u673a\u5e02\u573a\u7684\u7206\u53d1\u5df2\u7ecf\u521d\u663e\u7aef\u502a\uff0c\u4e07\u71d5\u9677\u5165\u81ea\u8eab\u751f\u4ea7\u8d44\u91d1\u7684\u4e0d\u8db3\uff0c\u5e02\u573a\u4f9b\u8d27\u91cf\u4e0d\u8db3\uff0c\u76f8\u6bd4\u53bb\u5e74\uff0c\u7adf\u6709\u840e\u7f29\u3002\u97e9\u56fd\u4e09\u661f\u516c\u53f8\u5e74\u521d\u63a8\u51fa\u4e00\u6b3e\u5f71\u789f\u673a\u5f02\u519b\u7a81\u8d77\uff0c\u534a\u5e74\u9500\u552e\u91cf\u7d2f\u79ef\u7a81\u7834\u5341\u4e07\u53f0\u3002\u4e5d\u4e94\u5e74\u4e0a\u534a\u5e74\uff0c\u5f71\u789f\u673a\u7684\u5e02\u573a\u4ef7\u683c\u6bcf\u53f0\u7ef4\u6301\u5728\u56db\u5343\u5143\u4ee5\u4e0a\uff0c\u6bd4\u8d77\u5236\u9020\u6210\u672c\uff0c\u5dee\u4e0d\u591a\u6bcf\u53f0\u6709\u4e24\u5343\u5143\u7684\u5229\u5dee\uff0c\u4e0d\u7b97\u592a\u9ad8\u7684\u8fdb\u5165\u95e8\u69db\uff0c\u5bfc\u81f4\u5230\u516d\u6708\u4e0b\u65ec\uff0c\u6df1\u5733\u3001\u5e7f\u5dde\u4e00\u5e26\u4e00\u4e0b\u5b50\u6d8c\u51fa\u73b0\u5341\u51e0\u5bb6\u5f71\u789f\u673a\u5236\u9020\u5382\u5546\uff0c\u4ed6\u4eec\u4e2d\u6700\u5feb\u7684\u751a\u81f3\u53ea\u9700\u8981\u4e24\u4e2a\u6708\u5c31\u63a8\u51fa\u81ea\u5df1\u7684\u4ea7\u54c1\u3002</p><p>\u3000\u3000\u5e7f\u4e1c\u82b1\u90fd\u5e02\u751a\u81f3\u51fa\u73b0\u4e3a\u5f71\u789f\u673a\u4e13\u95e8\u751f\u4ea7\u7535\u8def\u677f\u7684\u5382\u5bb6\u3002</p><p>\u3000\u3000\u4ee5\u53f6\u5efa\u658c\u8fd9\u6bb5\u65f6\u95f4\u6765\u5bf9\u5f71\u789f\u673a\u5e02\u573a\u7684\u4e86\u89e3\uff0c\u4ed6\u4e5f\u77e5\u9053\u7b2c\u4e00\u4ee3\u89e3\u7801\u82af\u7247\u4e0e\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u7247\u4e4b\u95f4\u7684\u5de8\u5927\u5dee\u8ddd\u3002</p><p>\u3000\u3000\u4e0d\u4ec5\u4e07\u71d5\u3001\u4e09\u661f\u7684\u4ea7\u54c1\uff0c\u76ee\u524d\u5df2\u63a8\u51fa\u7684\u51e0\u6b3e\u5f71\u789f\u673a\uff0c\u90fd\u662f\u57fa\u4e8e\u7b2c\u4e00\u4ee3\u89e3\u7801\u82af\u7247\u62fc\u88c5\u8d77\u6765\u7684\uff0c\u4e07\u71d5\u5728\u4e5d\u4e09\u5e74\u5c31\u5df2\u7ecf\u5f00\u53d1\u57fa\u4e8e\u7b2c\u4e00\u4ee3\u89e3\u7801\u82af\u7247\u7684\u7cfb\u7edf\u63a7\u5236\u8f6f\u4ef6\u4e0e\u5b8c\u6574\u7684\u7ec4\u88c5\u6280\u672f\uff0c\u8fd9\u4e5f\u662f\u97e9\u56fd\u4e09\u661f\u4e0e\u90a3\u4e9b\u751a\u81f3\u6ca1\u6709\u6280\u672f\u529b\u91cf\u7684\u5382\u5546\u80fd\u8fc5\u901f\uff08\u751a\u81f3\u4e0d\u9700\u8981\u4e24\u4e2a\u6708\uff09\u5c31\u80fd\u63a8\u51fa\u4ea7\u54c1\u7684\u539f\u56e0\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u524d\u671f\u5728\u6280\u672f\u6295\u5165\u8fd9\u4e48\u591a\uff0c\u7ecf\u8fc7\u516d\u4e03\u4e2a\u6708\u7684\u7b79\u5907\uff0c\u8fd9\u65f6\u5019\u624d\u521a\u521a\u51c6\u5907\u597d\u8bd5\u751f\u4ea7\u7684\u6761\u4ef6\uff0c\u662f\u56e0\u4e3a\u76f4\u63a5\u91c7\u53d6\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u7247\u4e3a\u6838\u5fc3\u5143\u4ef6\uff0c\u4ec5\u72ec\u7acb\u5f00\u53d1\u7cfb\u7edf\u63a7\u5236\u8f6f\u4ef6\u5c31\u8017\u8d39\u5927\u91cf\u7684\u4eba\u529b\u4e0e\u8d22\u529b\u3002</p><p>\u3000\u3000\u5f20\u606a\u6b64\u4e3e\u5c31\u662f\u8981\u5728\u5f71\u789f\u673a\u5e02\u573a\u7206\u53d1\u7684\u521d\u671f\u5c31\u4e0e\u5176\u4ed6\u5f71\u789f\u673a\u5236\u9020\u5546\u62c9\u5f00\u8ddd\u79bb\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8ba4\u8bc6\u5230\u8fd9\u70b9\uff0c\u4f46\u662f\u5f20\u606a\u4e00\u76f4\u8fdf\u8fdf\u4e0d\u80af\u6df1\u5165\u7684\u8ba8\u8bba\u7231\u8fbe\u7535\u5b50\u4e0e\u76db\u946b\u5408\u4f5c\u7684\u4e8b\u60c5\uff0c\u4ed6\u5fc3\u91cc\u706b\u70e7\u706b\u71ce\u7684\u3002\u76ee\u524d\u5e02\u573a\u5df2\u7ecf\u63a8\u51fa\u4e03\u516b\u6b3e\u5f71\u789f\u673a\uff0c\u6709\u8bb8\u591a\u5382\u5546\u90fd\u4e3b\u52a8\u627e\u4e0a\u95e8\u5e0c\u671b\u76db\u946b\u6210\u4e3a\u4ed6\u4eec\u7684\u4ee3\u7406\u5546\uff0c\u53f6\u5efa\u658c\u90fd\u5cbf\u7136\u4e0d\u52a8\uff0c\u5750\u7b49\u5f20\u606a\u7ed9\u4ed6\u7b54\u590d\u3002</p><p>\u3000\u3000\u8bd5\u751f\u4ea7\u7684\u6761\u4ef6\u5df2\u7ecf\u5177\u5907\uff0c\u6700\u5feb\u4e0b\u4e2a\u6708\u7b2c\u4e00\u6279\u4ea7\u54c1\u5c31\u80fd\u95ee\u4e16\uff0c\u6b64\u65f6\u5230\u4e86\u5e02\u573a\u8fd0\u4f5c\u7684\u65f6\u673a\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d76\u5230\u76ca\u9686\u9547\u897f\u9996\u7684\u7231\u8fbe\u7535\u5b50\uff0c\u8fdb\u4e86\u5de5\u5382\u7684\u5927\u95e8\u624d\u7ed9\u82cf\u6d25\u4e1c\u7684\u6253\u7535\u8bdd\uff1a\u201c\u662f\u4e0d\u662f\u6709\u7761\u89c9\uff0c\u6211\u5df2\u7ecf\u8fdb\u4e86\u5382\u5b50\u2026\u2026\u60f3\u8ba9\u4f60\u591a\u7761\u4e00\u4f1a\u513f\uff0c\u624d\u8fdb\u4e86\u5382\u95e8\u7ed9\u4f60\u4eec\u6253\u7535\u8bdd\uff0c\u6211\u63d0\u524d\u660e\u5929\u5c31\u53bb\u7701\u57ce\uff0c\u8fc7\u6765\u62ff\u8d44\u6599\u3002\u201d</p><p>\u3000\u3000\u8fdb\u5230\u529e\u516c\u5ba4\uff0c\u4e01\u69d0\u6b63\u6536\u62fe\u5730\u94fa\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u5c31\u6446\u90a3\u91cc\uff0c\u6211\u8d70\u4e86\u4f60\u4eec\u8fd8\u5f97\u63a5\u7740\u7761\u2026\u2026\u201d\u82cf\u6d25\u4e1c\u4ece\u6587\u4ef6\u67dc\u91cc\u62ff\u8d44\u6599\uff0c\u56de\u8fc7\u5934\u95ee\u5f20\u606a\uff1a\u201c\u6211\u4e5f\u8981\u63d0\u524d\u53bb\u7701\u57ce\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u7528\u4e86\uff0c\u6211\u660e\u5929\u662f\u79c1\u4e8b\uff0c\u6709\u53ef\u80fd\u4f1a\u4e0e\u76db\u946b\u7684\u53f6\u5efa\u658c\u63d0\u524d\u78b0\u9762\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u591a\u803d\u6401\uff0c\u62ff\u4e86\u8d44\u6599\u5c31\u8fd4\u56de\u5e02\u533a\u3002</p><p>\u3000\u3000\u4e14\u8bf4\u5085\u4fca\u5230\u5f20\u606a\u8eab\u8fb9\u4e5f\u6ca1\u6709\u591a\u957f\u7684\u65f6\u95f4\uff0c\u674e\u660e\u745c\u5bf9\u5f20\u606a\u7684\u4e86\u89e3\uff0c\u4e5f\u53ea\u9650\u4e8e\u51e0\u6b21\u6709\u9650\u7684\u63a5\u89e6\uff0c\u5979\u8fd8\u4ee5\u4e3a\u7231\u8fbe\u7535\u5b50\u7684\u5de5\u5382\u662f\u9526\u6e56\u65d7\u4e0b\u7684\u5de5\u5382\uff0c\u5de5\u5382\u56f4\u5899\u5916\u6709\u5c0f\u5c04\u706f\u73af\u7ed5\uff0c\u5c06\u6574\u95f4\u5de5\u5382\u4ece\u591c\u8272\u91cc\u5265\u79bb\u51fa\u53bb\uff0c\u770b\u4e0a\u53bb\u6a21\u6837\u5f88\u5c0f\uff0c\u4e0e\u5979\u4e4b\u524d\u7ecf\u8fc7\u57ce\u5357\u770b\u5230\u65b0\u5149\u9020\u7eb8\u5382\u4e0d\u80fd\u76f8\u63d0\u5e76\u8bba\u3002</p><p>\u3000\u3000\u5750\u5728\u8f66\u4e0a\u5f80\u5929\u8fb9\u671b\u4e86\u8bb8\u591a\u56de\uff0c\u897f\u8fb9\u7684\u5929\u7a7a\u4e0a\u6302\u7740\u4e00\u94a9\u5982\u9570\u5200\u4f3c\u7684\u4e0b\u5f26\u6b8b\u6708\uff0c\u53ea\u80fd\u4e00\u9897\u4e24\u5f71\u7684\u521d\u590f\u6668\u661f\u9690\u7ea6\u82e5\u73b0\uff0c\u5f20\u606a\u62c5\u5fc3\u660e\u5929\u65e9\u6668\u8fd8\u4f1a\u6709\u4e00\u573a\u96e8\u964d\u843d\u3002</p><p>\u3000\u3000\u56de\u5230\u5bcc\u8d35\u56ed\u5c0f\u533a\u5df2\u7ecf\u51cc\u6668\u4e00\u70b9\uff0c\u5f20\u606a\u8ba9\u5085\u4fca\u8d76\u7d27\u56de\u53bb\u4f11\u606f\u3002\u65e9\u4e0a\u516d\u70b9\u534a\u5085\u4fca\u8f7d\u7740\u4ed6\u7231\u4eba\u8fc7\u6765\u63a5\u5f20\u606a\uff0c\u518d\u665a\u4ece\u5e02\u91cc\u5230\u4e0a\u9ad8\u901f\u7684\u90a3\u6bb5\u8def\u5c31\u4f1a\u5f88\u5835\u3002\u4e0e\u8c22\u665a\u6674\u3001\u5468\u53d4\u60e0\u6c47\u5408\u4e4b\u540e\uff0c\u4e24\u90e8\u8f66\u4e03\u70b9\u949f\u5c31\u4e0a\u4e86\u9ad8\u901f\uff0c\u8d76\u5728\u5341\u4e00\u70b9\u524d\u5230\u7701\u57ce\u4e1c\u90ca\u7684\u56fd\u9645\u673a\u573a\u3002</p><p>\u3000\u3000\u5230\u673a\u573a\u7ed9\u5f90\u5b66\u5e73\u63a5\u673a\u4e0d\u53ea\u5f20\u606a\u4ed6\u4eec\u4e00\u884c\u4eba\uff0c\u7701\u653f\u5e9c\u4e5f\u6709\u8f66\u5b50\u65e9\u65e9\u5c31\u5230\u4e86\u673a\u573a\u7b49\u5019\uff0c\u7701\u653f\u5e9c\u526f\u79d8\u4e66\u957f\u9646\u6587\u592b\u5728\u8f66\u91cc\u7b49\u5f90\u5b66\u5e73\u4e58\u7684\u90a3\u822c\u98de\u673a\u5230\u6765\uff0c\u8fd8\u662f\u5468\u53d4\u60e0\u8ba4\u51fa\u7701\u653f\u5e9c\u7684\u8f66\u5148\u8dd1\u8fc7\u53bb\u8ddf\u4ed6\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u98de\u673a\u5341\u4e00\u70b9\u534a\u51c6\u65f6\u964d\u843d\uff0c\u5f90\u5b66\u5e73\u4e09\u6b65\u5e76\u4e24\u6b65\u8fce\u7740\u5f20\u606a\u4ed6\u4eec\u8d70\u8fc7\u6765\uff0c\u5c06\u82b7\u5f64\u62b1\u5728\u6000\u91cc\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u51fa\u4efb\u7701\u957f\u8fd8\u4e0d\u8db3\u4e24\u4e2a\u6708\uff0c\u5f20\u606a\u8fd9\u6bb5\u65f6\u95f4\u5230\u7701\u57ce\u6709\u4e09\u56de\uff0c\u90fd\u6ca1\u4e0e\u4ed6\u78b0\u4e0a\u9762\u3002\u4ed6\u5f53\u653f\u6cd5\u59d4\u4e66\u8bb0\u65f6\u591a\u7ed9\u4eba\u4e25\u8083\u51b7\u51bd\u7684\u611f\u89c9\uff0c\u6b64\u65f6\u7684\u5f90\u5b66\u5e73\u770b\u4e0a\u53bb\u66f4\u6e29\u548c\u4e00\u4e9b\uff0c\u610f\u6c14\u98ce\u53d1\uff0c\u7adf\u770b\u4e0d\u51fa\u4e00\u4e1d\u8870\u8001\u7684\u8ff9\u8c61\u3002</p></article>"}}}]);
