(window.webpackJsonp=window.webpackJsonp||[]).push([[1714],{1737:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u516b\u8282 \u51db\u7684\u60ca\u6050\u548c\u8981\u6c42",content:"<article><h2>\u7b2c\u4e09\u5341\u516b\u8282 \u51db\u7684\u60ca\u6050\u548c\u8981\u6c42</h2><p>\u201c\u54e5\u54e5\u2026\u2026\u8f6c\u8d60\u7ed9\u2026\u2026\u59b9\u59b9\uff1f\u201d</p><p>\u743c\u6069\u5f7b\u5e95\u88ab\u9707\u4f4f\u4e86\uff0c\u8111\u4e2d\u4e00\u7247\u6df7\u4e71\uff0c\u53ea\u662f\u4e0b\u610f\u8bc6\u5730\u91cd\u590d\u6885\u83f2\u65af\u7684\u8bdd\uff0c\u8fc7\u4e86\u51e0\u79d2\u949f\u624d\u56de\u8fc7\u795e\u6765\u3002\u201c\u4f60\u662f\u8bf4\uff0c\u5e0c\u6b27\u662f\u6b4c\u66e6\u96c5\u7684\u54e5\u54e5\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u4eb2\u751f\u7684\uff0c\u6709\u8840\u7f18\u5173\u7cfb\uff1f\u201d</p><p>\u201c\u5f53\u7136\u3002\u201d</p><p>\u201c\u771f\u6ca1\u770b\u51fa\u6765\uff0c\u90a3\u5bb6\u4f19\u9053\u8c8c\u5cb8\u7136\u7684\u6837\u5b50\uff0c\u5c45\u7136\u662f\u4e2a\u6b7b\u59b9\u63a7\u2026\u2026\u201d</p><p>\u548c\u743c\u6069\u7684\u4e00\u8d77\u5446\u4e45\u4e86\uff0c\u591a\u591a\u5c11\u5c11\u4e5f\u5b66\u5230\u4e86\u70b9\u4e13\u4e1a\u672f\u8bed\u3002\u6885\u83f2\u65af\u867d\u7136\u4e0d\u61c2\u201c\u59b9\u63a7\u201d\u4e24\u4e2a\u5b57\u600e\u4e48\u5199\uff0c\u4f46\u5927\u81f4\u7684\u610f\u601d\u8fd8\u662f\u80fd\u731c\u5230\u7684\u3002\u201c\u59b9\u63a7\u4e2a\u9b3c\uff0c\u201d\u5c11\u5973\u767d\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u4f60\u4ee5\u4e3a\u4eba\u5bb6\u90fd\u548c\u4f60\u4e00\u6837\u5fc3\u7406\u53d8\u6001\uff1f\u201d</p><p>\u201c\u6211\u600e\u4e48\u5fc3\u7406\u53d8\u6001\u4e86\uff1f\u201d\u743c\u6069\u4e0d\u670d\u6c14\u3002</p><p>\u201c\u4f60\u559c\u6b22\u81ea\u5df1\u59d0\u59d0\uff0c\u8fd9\u4e0d\u662f\u5fc3\u7406\u53d8\u6001\u662f\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u6211\u662f\u59d0\u63a7\u6211\u5149\u8363\uff01\u201d</p><p>\u743c\u6069\u662f\u5426\u5fc3\u7406\u53d8\u6001\uff0c\u8fd9\u4e2a\u8bdd\u9898\u6682\u4e14\u653e\u4e0b\u4e0d\u8bba\uff0c\u8fd8\u662f\u5148\u8bf4\u5e0c\u6b27\u548c\u6b4c\u66e6\u96c5\u3002\u201c\u4ed6\u5c45\u7136\u6697\u604b\u81ea\u5df1\u59b9\u59b9\u5341\u5e74\uff0c\u8fd9\u4e0d\u662f\u6807\u51c6\u7684\u59b9\u63a7\u662f\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u56e0\u4e3a\u4ed6\u4eec\u7684\u5144\u59b9\u5173\u7cfb\u662f\u540e\u6765\u624d\u77e5\u9053\u7684\u2014\u2014\u51c6\u786e\u5730\u8bf4\uff0c\u6b4c\u66e6\u96c5\u65e9\u5c31\u77e5\u9053\uff0c\u4f46\u5e0c\u6b27\u662f\u76f4\u5230\u8868\u767d\u88ab\u62d2\u7edd\u4e86\u4e4b\u540e\u624d\u77e5\u9053\u3002\u201d</p><p>\u539f\u6765\u5f53\u65e5\u5e0c\u6b27\u5411\u6b4c\u66e6\u96c5\u8868\u767d\uff0c\u7ed3\u679c\u9886\u4e86\u4e00\u5f20\u6807\u51c6\u7684\u597d\u4eba\u5361\uff0c\u4f46\u4ed6\u5e76\u6ca1\u6709\u5c31\u6b64\u6b7b\u5fc3\uff0c\u60f3\u60f3\u4e5f\u662f\uff0c\u6bd5\u7adf\u662f\u5723\u6b66\u58eb\uff0c\u600e\u4e48\u53ef\u80fd\u8fd9\u4e48\u5bb9\u6613\u5c31\u88ab\u6253\u5012\u3002\u53cd\u6b63\u8bdd\u5df2\u7ecf\u8bf4\u51fa\u53e3\uff0c\u5e0c\u6b27\u7d22\u6027\u4ece\u6697\u604b\u53d8\u6210\u660e\u8ffd\uff0c\u9001\u82b1\u9001\u86cb\u7cd5\u9001\u5de7\u514b\u529b\u2014\u2014\u5443\uff0c\u8fd9\u4e2a\u4e16\u754c\u6ca1\u6709\u5de7\u514b\u529b\u2014\u2014\u7684\u4e8b\u60c5\u6bcf\u5929\u53d1\u751f\uff0c\u6885\u83f2\u65af\u56e0\u4e3a\u548c\u6b4c\u66e6\u96c5\u5173\u7cfb\u597d\uff0c\u6240\u4ee5\u90fd\u88ab\u201c\u8d3f\u8d42\u201d\u8fc7\u5f88\u591a\u6b21\uff0c\u53ea\u6c42\u5979\u5728\u6b4c\u66e6\u96c5\u9762\u524d\u7f8e\u8a00\u51e0\u53e5\u3002\u4e8b\u60c5\u8d8a\u6765\u8d8a\u516c\u5f00\uff0c\u5f04\u5f97\u5168\u9547\u4eba\u4eba\u7686\u77e5\uff0c\u6700\u7ec8\u6709\u4e00\u5929\uff0c\u6d88\u606f\u4f20\u5230\u5e0c\u6b27\u7684\u7236\u4eb2\u3001\u5e9e\u7f57\u5bb6\u65cf\u5f53\u4ee3\u5bb6\u4e3b\u6885\u68ee\u5148\u751f\u7684\u8033\u6735\u4e2d\u3002</p><p>\u4e8e\u662f\u6885\u68ee\u5148\u751f\u547d\u4eba\u628a\u5e0c\u6b27\u53eb\u6765\uff0c\u7236\u5b50\u4e24\u4eba\u8fdb\u884c\u4e86\u4e00\u573a\u7b80\u77ed\u7684\u8c08\u8bdd\u3002</p><p>\u201c\u542c\u8bf4\u4f60\u5728\u8ffd\u6c42\u6b4c\u66e6\u96c5\u5c0f\u59d0\uff1f\u201d\u6885\u68ee\u5148\u751f\u95ee\u3002</p><p>\u201c\u662f\u554a\uff0c\u53ef\u662f\u88ab\u5979\u62d2\u7edd\u4e86\uff0c\u201d\u5e0c\u6b27\u5782\u5934\u4e27\u6c14\u5730\u8bf4\uff0c\u4f46\u4ed6\u5f88\u5feb\u5c31\u632f\u4f5c\u8d77\u7cbe\u795e\uff0c\u201c\u5bf9\u4e86\u8001\u7238\uff0c\u4f60\u662f\u6b64\u9053\u9ad8\u624b\uff0c\u60f3\u5f53\u5e74\u7eb5\u6a2a\u82b1\u4e1b\uff0c\u6b20\u4e0b\u98ce\u6d41\u503a\u65e0\u6570\u2014\u2014\u8981\u4e0d\u4f60\u6765\u6307\u70b9\u6307\u70b9\u6211\uff1f\u201d</p><p>\u4e8e\u662f\u7236\u4eb2\u62cd\u4e86\u62cd\u513f\u5b50\u7684\u80a9\u8180\uff0c\u8bed\u91cd\u5fc3\u957f\u5730\u8bf4\uff1a\u201c\u6211\u552f\u4e00\u7684\u6307\u70b9\u5c31\u662f\uff1a\u4f60\u6b7b\u5fc3\u5427\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\uff0c\u201d\u6885\u68ee\u5148\u751f\u4f4e\u58f0\u8bf4\uff0c\u201c\u5979\u662f\u4f60\u59b9\u59b9\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u6b63\u5982\u5e0c\u6b27\u6240\u8bf4\uff0c\u4ed6\u7684\u7236\u4eb2\u6885\u68ee\u5148\u751f\u5e74\u8f7b\u65f6\u662f\u60c5\u573a\u9ad8\u624b\uff0c\u8eab\u65c1\u603b\u662f\u73af\u7ed5\u7740\u7f8e\u4e3d\u5c11\u5973\uff0c\u5373\u4fbf\u662f\u540e\u6765\u7ed3\u5a5a\u6210\u5bb6\u4e86\uff0c\u59bb\u5b50\u770b\u7ba1\u5f97\u4e25\uff0c\u90fd\u8fd8\u7167\u6837\u9694\u4e09\u5c94\u4e94\u5077\u5077\u6e9c\u51fa\u53bb\u548c\u60c5\u4eba\u5e7d\u4f1a\u3002\u800c\u6b4c\u66e6\u96c5\u5c31\u662f\u4ed6\u548c\u5176\u4e2d\u4e00\u4f4d\u60c5\u4eba\u7684\u7231\u60c5\u7ed3\u6676\u2026\u2026\u6216\u8005\u76f4\u767d\u70b9\u8bf4\u5c31\u662f\u79c1\u751f\u5973\u3002\u8fd9\u4e2a\u79d8\u5bc6\u4e00\u76f4\u88ab\u5f53\u4e8b\u4eba\u57cb\u85cf\u5728\u5fc3\u91cc\uff0c\u76f4\u5230\u6b4c\u66e6\u96c5\u5341\u4e5d\u5c81\u7684\u65f6\u5019\uff0c\u6bcd\u4eb2\u67d3\u75c5\u8eab\u6545\uff0c\u4e34\u7ec8\u524d\u624d\u5c06\u8fd9\u4ef6\u4e8b\u544a\u8bc9\u4e86\u5973\u513f\u3002\u4e5f\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u6b4c\u66e6\u96c5\u6beb\u4e0d\u72b9\u8c6b\u5730\u5c31\u62d2\u7edd\u4e86\u5e0c\u6b27\uff0c\u90a3\u53e5\u7ecf\u5178\u7684\u201c\u6211\u53ea\u662f\u628a\u4f60\u5f53\u54e5\u54e5\u770b\u5f85\u201d\uff0c\u539f\u6765\u5e76\u975e\u6258\u8f9e\uff0c\u800c\u662f\u771f\u6b63\u7684\u5fc3\u58f0\u2026\u2026</p><p>\u5982\u679c\u5e0c\u6b27\u771f\u7684\u662f\u4e2a\u59b9\u63a7\uff0c\u90a3\u4e48\u5012\u4e5f\u7f62\u4e86\uff0c\u504f\u504f\u4ed6\u4e0d\u662f\uff0c\u4ed6\u662f\u4e2a\u6709\u7406\u60f3\u6709\u9053\u5fb7\u6709\u6587\u5316\u6709\u7eaa\u5f8b\u7684\u5723\u6b66\u58eb\uff1b\u53cd\u8fc7\u6765\uff0c\u5982\u679c\u6b4c\u66e6\u96c5\u559c\u6b22\u54e5\u54e5\uff0c\u90a3\u4e48\u4e8b\u60c5\u4e5f\u672a\u5fc5\u6ca1\u6709\u8f6c\u673a\uff0c\u504f\u504f\u5979\u4e5f\u4e0d\u662f\uff0c\u8981\u662f\u7684\u8bdd\u65e9\u5c31\u7b54\u5e94\u4e86\u3002\u7b80\u5355\u6765\u8bf4\uff0c\u4ed6\u4eec\u90fd\u662f\u6b63\u5e38\u4eba\uff0c\u81f3\u5c11\u6709\u7740\u6b63\u5e38\u7684\u9053\u5fb7\u611f\u548c\u4f26\u7406\u89c2\uff0c\u4e0d\u50cf\u743c\u6069\u8fd9\u79cd\u5fc3\u7406\u53d8\u6001\u2014\u2014\u6240\u4ee5\u4ed6\u4eec\u5c31\u6ce8\u5b9a\u4e86\u60b2\u5267\u3002</p><p>\u7ecf\u6b64\u632b\u6298\uff0c\u5e0c\u6b27\u6027\u60c5\u5927\u53d8\uff0c\u5f80\u65e5\u90a3\u4e2a\u65b9\u6b63\u4e25\u8083\u7684\u5723\u6b66\u58eb\u6d88\u5931\u4e86\uff0c\u53d6\u800c\u4ee3\u4e4b\u662f\u73b0\u5728\u8fd9\u4f4d\u98ce\u6d41\u6d6a\u5b50\u3002\u6b4c\u66e6\u96c5\u76f8\u5bf9\u60c5\u51b5\u8f83\u597d\uff0c\u5979\u6bd5\u7adf\u65e9\u5c31\u77e5\u60c5\uff0c\u5fc3\u7406\u51c6\u5907\u5145\u8db3\uff0c\u4e00\u76f4\u5c31\u628a\u5e0c\u6b27\u5f53\u5144\u957f\u6765\u770b\u5f85\uff0c\u65e0\u6240\u8c13\u5931\u604b\u3002\u540e\u6765\u5e0c\u6b27\u628a\u81ea\u5df1\u7684\u5deb\u7075\u5251\u9001\u7ed9\u4e86\u6b4c\u66e6\u96c5\uff0c\u4e5f\u6ca1\u8bf4\u4ec0\u4e48\u7406\u7531\uff0c\u6216\u8bb8\u662f\u5bf9\u66fe\u7ecf\u4e00\u6bb5\u611f\u60c5\u7684\u7eaa\u5ff5\uff0c\u4e5f\u6216\u8bb8\u662f\u5bf9\u8fc7\u53bb\u7684\u5f7b\u5e95\u544a\u522b\u5427\u3002</p><p>\u201c\u771f\u662f\u7684\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u201c\u5144\u59b9\u53c8\u600e\u4e48\u4e86\uff0c\u65e2\u7136\u559c\u6b22\u5c31\u7167\u6837\u63a8\u5012\uff0c\u504f\u504f\u641e\u6210\u8fd9\u6837\uff0c\u771f\u662f\u4e00\u70b9\u7231\u90fd\u6ca1\u6709\u3002\u201d</p><p>\u201c\u4f60\u5728\u5600\u5600\u5495\u5495\u8bf4\u4ec0\u4e48\u5947\u602a\u7684\u8bdd\u5462\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u6ca1\u4ec0\u4e48\uff0c\u6211\u5728\u8c34\u8d23\u81ea\u5df1\u8fd9\u4e2a\u5fc3\u7406\u53d8\u6001\u3002\u201d</p><p>\u6885\u83f2\u65af\u660e\u77e5\u9053\u4ed6\u5728\u80e1\u626f\uff0c\u4f46\u4e5f\u4e0d\u591a\u95ee\u3002\u201c\u5bf9\u4e86\uff0c\u4f60\u662f\u8bf4\u5728\u8fdc\u5c71\u5e02\u78b0\u5230\u4e86\u6b4c\u66e6\u96c5\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u8fdc\u5c71\u5e02\u4e0d\u662f\u88ab\u6563\u63d0\u5c14\u5821\u6253\u4e0b\u6765\u4e86\u561b\uff0c\u6211\u5f53\u65f6\u6070\u597d\u5728\u57ce\u4e2d\uff0c\u8fdc\u8fdc\u770b\u89c1\u4e86\u5979\u3002\u540e\u6765\u5728\u53bb\u6851\u6bd4\u4e9a\u7684\u8def\u4e0a\u9047\u89c1\uff0c\u6253\u4e86\u4e00\u67b6\u2014\u2014\u201d</p><p>\u201c\u4f60\u548c\u5979\u8d77\u51b2\u7a81\u4e86\uff1f\u201d\u6885\u83f2\u65af\u4e00\u60ca\uff0c\u201c\u6ca1\u4e8b\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u827e\u5f25\u8587\uff0c\u5bf9\u81ea\u5df1\u7684\u7537\u53cb\u4e0d\u8981\u8fd9\u4e48\u6ca1\u4fe1\u5fc3\u597d\u4e0d\u597d\uff0c\u201d\u743c\u6069\u90c1\u95f7\u5730\u8bf4\uff0c\u201c\u90a3\u4e00\u6218\u662f\u6211\u6253\u8d62\u4e86\u5462\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d\u6885\u83f2\u65af\u60ca\u8bb6\uff0c\u201c\u90a3\u4f60\u6700\u8fd1\u8fdb\u6b65\u5f88\u5feb\u554a\u3002\u201d</p><p>\u201c\u90a3\u81ea\u7136\uff01\u201d</p><p>\u5e73\u5fc3\u800c\u8bba\uff0c\u743c\u6069\u786e\u5b9e\u4e0d\u662f\u6b4c\u66e6\u96c5\u7684\u5bf9\u624b\u3002\u4e0a\u6b21\u4ed6\u4e4b\u6240\u4ee5\u80fd\u591f\u53d6\u80dc\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u662f\u56e0\u4e3a\u5360\u4e86\u5b8c\u5168\u7684\u4e3b\u573a\u5730\u5229\uff08\u5965\u6c83\u9001\u7684\u9752\u94dc\u8c6a\u5b85\uff09\uff0c\u62e5\u6709\u8fd1\u4e4e\u65e0\u7a77\u65e0\u5c3d\u7684\u9ab7\u9ac5\u6b66\u58eb\u505a\u70ae\u7070\uff0c\u8fd8\u6709\u9b45\u9b54\u838e\u73de\u514b\u505a\u5e2e\u624b\uff0c\u771f\u8981\u662f\u5728\u516c\u5e73\u73af\u5883\u4e0b\u7684\u5355\u6253\u72ec\u6597\uff0c\u80dc\u7b97\u53ea\u6015\u4e0d\u4f1a\u8d85\u8fc7\u4e09\u6210\u3002\u6bd5\u7adf\u8981\u8bba\u8d77\u6765\uff0c\u6b4c\u66e6\u96c5\u662f\u6885\u83f2\u65af\u7684\u5e08\u59d0\u5462\uff0c\u867d\u7136\u5c11\u4e86\u90aa\u795e\u7684\u795e\u529b\u52a0\u6301\uff0c\u5374\u591a\u4e86\u5341\u5e74\u7684\u78e8\u7ec3\u548c\u7ecf\u9a8c\uff0c\u5b9e\u529b\u51b3\u4e0d\u80fd\u5c0f\u770b\u3002</p><p>\u6885\u83f2\u65af\u95ee\u8d77\u5f53\u65f6\u7684\u7ec6\u8282\uff0c\u743c\u6069\u4fbf\u4ece\u8fdc\u5c71\u5e02\u7684\u653b\u57ce\u5f00\u59cb\u8bb2\u8d77\uff0c\u628a\u6574\u4e2a\u8fc7\u7a0b\u8bf4\u4e86\u4e00\u904d\uff0c\u987a\u4fbf\u89e3\u91ca\u4e86\u81ea\u5df1\u83b7\u5f97\u96f7\u9706\u795e\u529b\u7684\u7531\u6765\u3002\u201c\u5409\u52d2\u4eca\u7684\u2018\u738b\u4e4b\u5b9d\u5e93\u2019\uff1f\u201d\u6885\u83f2\u65af\u6c89\u541f\uff0c\u201c\u6211\u597d\u50cf\u5728\u54ea\u672c\u4e66\u4e0a\u4e5f\u770b\u5230\u8fc7\u8fd9\u4e2a\u4f20\u8bf4\u3002\u4e00\u65f6\u60f3\u4e0d\u8d77\u6765\u4e86\uff0c\u6539\u5929\u6709\u7a7a\u53bb\u6863\u6848\u5ba4\u7ffb\u7ffb\u5427\u3002\u201d</p><p>\u201c\u65e0\u6240\u8c13\u4e86\uff0c\u201d\u743c\u6069\u5bf9\u5b9d\u85cf\u4ec0\u4e48\u7684\uff0c\u5012\u662f\u4e0d\u751a\u5728\u610f\uff0c\u201c\u53cd\u6b63\u94a5\u5319\u90fd\u5df2\u7ecf\u88ab\u6211\u541e\u566c\u4e86\uff0c\u5c31\u7b97\u627e\u5230\u5b9d\u5e93\u4e5f\u8fdb\u4e0d\u53bb\u3002\u201d</p><p>\u201c\u4e5f\u662f\u3002\u201d</p><p>\u5409\u52d2\u4eca\u5b9d\u5e93\u7684\u95ee\u9898\u6682\u65f6\u653e\u5728\u4e00\u8fb9\uff0c\u743c\u6069\u7ee7\u7eed\u8bf4\u6b4c\u66e6\u96c5\u7684\u4e8b\u60c5\u3002\u201c\u518d\u540e\u6765\uff0c\u5c31\u662f\u6628\u5929\u4e2d\u5348\uff0c\u6211\u8def\u8fc7\u5f13\u8c37\u7684\u65f6\u5019\uff0c\u5728\u5f13\u6865\u9547\u4e0a\u53c8\u770b\u89c1\u4e86\u5979\u3002\u201d</p><p>\u201c\u5f13\u6865\u9547\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u5728\u96c6\u5e02\u4e0a\u770b\u5230\u7684\uff0c\u5979\u5e94\u8be5\u6ca1\u53d1\u73b0\u6211\u3002\u6211\u8ba9\u742a\u5a05\u8ddf\u7740\u5979\uff0c\u6700\u540e\u53d1\u73b0\u5979\u8fdb\u4e86\u5f13\u8c37\u9886\u4e3b\u7684\u5e9c\u90b8\u3002\u201d</p><p>\u201c\u5f13\u8c37\u9886\u4e3b\u7684\u5e9c\u90b8\u5417\uff1f\u201d\u6885\u83f2\u65af\u82e5\u6709\u6240\u601d\uff0c\u201c\u8fd9\u8fd8\u771f\u662f\u6709\u70b9\u9ebb\u70e6\u4e86\u5462\u3002\u201d</p><p>\u201c\u600e\u4e48\uff1f\u201d</p><p>\u6885\u83f2\u65af\u7b80\u5355\u89e3\u91ca\u4e86\u4e00\u4e0b\uff0c\u539f\u6765\u5f13\u8c37\u548c\u5854\u745f\u8c37\uff0c\u5728\u5386\u53f2\u4e0a\u5c31\u6709\u5f88\u6df1\u7684\u77db\u76fe\uff0c\u867d\u7136\u6ca1\u53d1\u751f\u8fc7\u771f\u6b63\u7684\u6218\u4e89\uff0c\u4f46\u8fb9\u5883\u4e0a\u7684\u5c0f\u89c4\u6a21\u51b2\u7a81\u4e00\u76f4\u4e0d\u65ad\u3002\u5c24\u5176\u662f\u81ea\u4ece\u5f13\u8c37\u8fd9\u4e00\u4efb\u7684\u4e09\u5251\u9886\u4e3b\u574e\u65af\u83ab\u4e0a\u53f0\u540e\uff0c\u5f00\u59cb\u5927\u529b\u5bf9\u5916\u6269\u5f20\uff0c\u5341\u5e74\u5185\u6311\u8d77\u4e09\u6b21\u6218\u4e8b\uff0c\u5df2\u7ecf\u541e\u5e76\u4e86\u6bd7\u90bb\u7684\u9ad8\u8c37\u548c\u6df1\u6797\u8c37\u8fd1\u4e09\u5206\u4e4b\u4e00\u7684\u9886\u5730\uff0c\u63a5\u4e0b\u6765\u7684\u6ce8\u610f\u529b\u4fbf\u8f6c\u79fb\u5230\u5854\u745f\u8c37\u4e0a\u6765\uff0c\u4e0d\u65ad\u6311\u8d77\u4e8b\u7aef\uff0c\u5236\u9020\u51b2\u7a81\u501f\u53e3\u3002\u6839\u636e\u6559\u4f1a\u5185\u90e8\u7684\u7814\u5224\u5206\u6790\uff0c\u53ea\u6015\u4e09\u5e74\u4e4b\u5185\uff0c\u53cc\u65b9\u5c31\u4f1a\u6709\u4e00\u6218\u3002\u5f13\u8c37\u662f\u8c37\u5730\u5341\u4e00\u9547\u4e2d\u4eba\u53e3\u6700\u591a\u3001\u7ecf\u6d4e\u6700\u7e41\u534e\u7684\u4e09\u9547\u4e4b\u4e00\uff08\u53e6\u5916\u4e24\u9547\u662f\u4f24\u75d5\u8c37\u548c\u6218\u5f79\u8c37\uff09\uff0c\u6574\u4f53\u5b9e\u529b\u8fdc\u5728\u5854\u745f\u8c37\u4e4b\u4e0a\uff0c\u539f\u672c\u5c31\u5df2\u7ecf\u5f88\u96be\u5bf9\u4ed8\uff0c\u5982\u679c\u518d\u548c\u6563\u5854\u6797\u4f1a\u8054\u624b\uff0c\u90a3\u4e48\u9ebb\u70e6\u5c31\u5927\u4e86\u3002</p><p>\u5bf9\u4e8e\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u743c\u6069\u5176\u5b9e\u5e76\u4e0d\u600e\u4e48\u611f\u5174\u8da3\uff0c\u6885\u83f2\u65af\u77e5\u9053\u4ed6\u7684\u6027\u683c\uff0c\u4e5f\u5c31\u70b9\u5230\u4e3a\u6b62\uff0c\u4e0d\u518d\u591a\u63d0\u3002\u201c\u56de\u53bb\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u62ac\u5934\u770b\u770b\u897f\u65b9\u5929\u7a7a\uff0c\u592a\u9633\u5df2\u7ecf\u5feb\u8981\u5b8c\u5168\u843d\u5c71\u4e86\uff0c\u665a\u971e\u707f\u70c2\u5982\u706b\uff0c\u201c\u665a\u996d\u65f6\u95f4\u5230\u4e86\uff0c\u522b\u8ba9\u73ca\u5609\u59d0\u59d0\u7b49\u6211\u4eec\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u56de\u53bb\u7684\u8def\u4e0a\uff0c\u6070\u597d\u53c8\u9047\u5230\u5e0c\u6b27\u548c\u4ed6\u7684\u4e24\u4f4d\u5973\u53cb\uff0c\u6b63\u4ece\u91ce\u5916\u6e38\u73a9\u5f52\u6765\uff0c\u7ad9\u7740\u95f2\u804a\u4e86\u4e00\u4f1a\uff0c\u803d\u8bef\u4e86\u65f6\u95f4\u3002\u7ed3\u679c\u7b49\u4e24\u4eba\u56de\u5230\u6885\u83f2\u65af\u7684\u4f4f\u5904\u65f6\uff0c\u5df2\u7ecf\u662f\u665a\u4e0a\u516d\u70b9\u591a\u949f\uff0c\u5bb6\u91cc\u4f3c\u4e4e\u6765\u4e86\u5ba2\u4eba\uff0c\u8fd8\u5728\u95e8\u5916\u5c31\u9690\u7ea6\u542c\u89c1\u8bf4\u8bdd\u58f0\uff0c\u50cf\u662f\u73ca\u5609\u548c\u8c01\u5728\u4ea4\u8c08\uff0c\u58f0\u97f3\u6e05\u8106\u5982\u94c3\uff0c\u660e\u663e\u662f\u4e2a\u5973\u5b69\u5b50\u3002\u743c\u6069\u63a8\u5f00\u95e8\uff0c\u679c\u7136\u770b\u89c1\u4e24\u4f4d\u5c11\u5973\u5e76\u6392\u5750\u5728\u6c99\u53d1\u91cc\u804a\u5929\uff0c\u795e\u60c5\u4eb2\u5bc6\uff0c\u7b11\u8bed\u76c8\u76c8\uff0c\u4eff\u4f5b\u662f\u591a\u5e74\u672a\u89c1\u7684\u597d\u53cb\uff0c\u5176\u4e2d\u4e00\u4f4d\u662f\u4ed6\u7684\u59d0\u59d0\u73ca\u5609\uff0c\u53e6\u5916\u4e00\u4f4d\u7ea2\u8863\u3001\u9ed1\u88d9\u3001\u957f\u7b52\u889c\u3001\u53cc\u9a6c\u5c3e\uff0c\u6b63\u662f\u591a\u65e5\u4e0d\u89c1\u7684\u51db\u3002</p><p>\u201c\u4f60\u4eec\u53bb\u54ea\u4e86\u5440\uff0c\u8fd9\u4e48\u665a\u624d\u56de\u6765\uff0c\u6211\u90fd\u997f\u574f\u4e86\uff0c\u201d\u770b\u89c1\u743c\u6069\u548c\u6885\u83f2\u65af\u8d70\u8fdb\u6765\uff0c\u5c0f\u5973\u5deb\u53ef\u7231\u5730\u6485\u8d77\u5c0f\u5634\u62b1\u6028\uff0c\u4f46\u4e0b\u4e00\u79d2\u949f\u5979\u7684\u4fcf\u8138\u4e0a\u5c31\u662f\u7b11\u5bb9\u707f\u70c2\uff0c\u8dd1\u8fc7\u6765\u4e00\u4eba\u7ed9\u4e86\u4e00\u4e2a\u70ed\u60c5\u62e5\u62b1\uff0c\u201c\u827e\u5f25\u8587\uff0c\u6211\u597d\u60f3\u4f60\uff0c\u201d\u5979\u8bf4\uff0c\u7136\u540e\u8e2e\u8d77\u811a\u5c16\uff0c\u5634\u5507\u5728\u743c\u6069\u7684\u8138\u988a\u4e0a\u8f7b\u8f7b\u5370\u4e86\u4e00\u4e0b\uff0c\u201c\u987a\u4fbf\u4e5f\u6709\u4e00\u70b9\u70b9\u60f3\u4f60\uff0c\u743c\u6069\u3002\u201d</p><p>\u743c\u6069\u8138\u4e0a\u4e00\u9635\u6cdb\u7ea2\uff0c\u548c\u51db\u4eb2\u70ed\u6ca1\u4ec0\u4e48\uff0c\u53c8\u4e0d\u662f\u7b2c\u4e00\u6b21\uff0c\u5f53\u7740\u6885\u83f2\u65af\u7684\u9762\u4e5f\u65e0\u6240\u8c13\uff0c\u53cd\u6b63\u5979\u65e9\u5c31\u53f8\u7a7a\u89c1\u60ef\u4e86\uff0c\u4f46\u73ca\u5609\u73b0\u5728\u6b63\u5728\u65c1\u8fb9\u5462\uff0c\u603b\u89c9\u5f97\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u3002\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u56de\u6765\u4e86\uff1f\u201d\u4e3a\u4e86\u63a9\u9970\u5c34\u5c2c\uff0c\u743c\u6069\u968f\u53e3\u6ca1\u8bdd\u627e\u8bdd\uff0c\u201c\u4e0d\u662f\u6628\u5929\u624d\u53bb\u9634\u5f71\u8c37\u7684\u4e48\u3002\u201d</p><p>\u201c\u4e0b\u5348\u56de\u6765\u7684\uff0c\u548c\u8001\u5e08\u4e00\u8d77\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u5979\u8981\u53bb\u529e\u4e8b\uff0c\u6211\u61d2\u5f97\u8ddf\u7740\uff0c\u5c31\u81ea\u5df1\u5148\u56de\u8fd9\u91cc\u3002\u6ca1\u60f3\u5230\u4e00\u8fdb\u95e8\uff0c\u770b\u5230\u73ca\u5609\uff0c\u5f53\u65f6\u53ef\u5413\u4e86\u6211\u4e00\u5927\u8df3\u5462\u3002\u201d</p><p>\u201c\u4f60\u5413\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u96be\u9053\u662f\u4ee5\u4e3a\u5bb6\u91cc\u6765\u7a83\u8d3c\u4e86\uff1f\u4e14\u4e0d\u8bf4\u73ca\u5609\u54ea\u4e00\u70b9\u50cf\u7a83\u8d3c\uff0c\u5c31\u7b97\u771f\u7684\u662f\uff0c\u4f60\u8fd9\u79cd\u66b4\u529b\u5c0f\u5973\u5deb\u53c8\u54ea\u91cc\u4f1a\u5bb3\u6015\u554a\uff0c\u76f4\u63a5\u4e00\u53d1\u706b\u7403\u5c31\u7838\u8fc7\u53bb\u4e86\u5427\u3002</p><p>\u201c\u56e0\u4e3a\u5979\u628a\u6211\u5f53\u6210\u4f60\u4e86\u3002\u201d\u73ca\u5609\u5fae\u7b11\u7740\u63d2\u8bdd\u3002</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u6211\u5f53\u65f6\u7b2c\u4e00\u53cd\u5e94\u8fd8\u771f\u7684\u4ee5\u4e3a\u662f\u4f60\u5462\uff0c\u56e0\u4e3a\u77e5\u9053\u4f60\u8fd9\u4e24\u5929\u8981\u6765\u561b\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u5fc3\u60f3\u539f\u6765\u743c\u6069\u5c45\u7136\u6709\u8fd9\u79cd\u53d8\u6001\u7231\u597d\u2014\u2014\u8fd9\u53eb\u4ec0\u4e48\uff0c\u5f02\u88c5\u7656\u662f\u5427\u2014\u2014\u592a\u6050\u6016\u4e86\u592a\u6050\u6016\u4e86\uff0c\u5dee\u70b9\u5c31\u60f3\u8f6c\u8eab\u9003\u8dd1\u3002\u5e78\u597d\u8fd9\u65f6\u5019\u73ca\u5609\u53eb\u4f4f\u4e86\u6211\uff0c\u5979\u4e00\u8bf4\u8bdd\u6211\u603b\u7b97\u56de\u8fc7\u795e\u6765\uff0c\u8fd8\u597d\u8fd8\u597d\uff0c\u4f60\u4eec\u58f0\u97f3\u4e0d\u592a\u50cf\uff0c\u201d\u5979\u5938\u5f20\u5730\u62cd\u62cd\u80f8\u53e3\uff0c\u4eff\u4f5b\u5fc3\u6709\u4f59\u60b8\uff0c\u201c\u4e0d\u8fc7\u8bf4\u771f\u7684\uff0c\u743c\u6069\uff0c\u4f60\u548c\u73ca\u5609\u771f\u7684\u597d\u50cf\u554a\u3002\u201d</p><p>\u201c\u5e9f\u8bdd\uff0c\u59d0\u5f1f\u561b\uff0c\u5f53\u7136\u50cf\u4e86\u3002\u201d</p><p>\u201c\u53ef\u662f\u50cf\u5230\u8fd9\u79cd\u7a0b\u5ea6\uff0c\u5b9e\u5728\u8fd8\u662f\u5f88\u5c11\u89c1\u5440\uff0c\u201d\u51db\u7b11\u772f\u772f\u5730\u8bf4\uff0c\u773c\u4e2d\u95ea\u70c1\u7684\u72e1\u733e\u5149\u8292\uff0c\u663e\u793a\u5c0f\u5973\u5deb\u5fc3\u91cc\u6b63\u5728\u6253\u7740\u4ec0\u4e48\u574f\u4e3b\u610f\uff0c\u201c\u5450\uff0c\u743c\u6069\uff0c\u8981\u4e0d\uff0c\u771f\u7684\u7a7f\u4e00\u6b21\u5973\u88c5\u8ba9\u6211\u770b\u770b\u597d\u4e0d\u597d\uff1f\u201d</p></article>"}}}]);
