(window.webpackJsonp=window.webpackJsonp||[]).push([[1715],{1738:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e5d\u8282 \u9009\u62e9",content:"<article><h2>\u7b2c\u4e09\u5341\u4e5d\u8282 \u9009\u62e9</h2><p>\u201c\u743c\u6069\uff0c\u8bd5\u8bd5\u8fd9\u6761\u88d9\u5b50\u597d\u4e0d\u597d\uff0c\u6211\u6700\u8fd1\u65b0\u4e70\u7684\uff0c\u81ea\u5df1\u8fd8\u6ca1\u7a7f\u8fc7\u5462\uff0c\u4f60\u7a7f\u4e0a\u4e00\u5b9a\u5f88\u6f02\u4eae\u2014\u2014\u201d</p><p>\u201c\u4e0d\u884c\uff01\u201d</p><p>\u201c\u4f60\u4e0d\u559c\u6b22\u88d9\u5b50\uff1f\u6ca1\u5173\u7cfb\uff0c\u8fd9\u4ef6\u5c0f\u793c\u670d\u600e\u4e48\u6837\uff1f\u6709\u4f60\u6700\u559c\u6b22\u7684\u857e\u4e1d\u2014\u2014\u201d</p><p>\u201c\u857e\u4e1d\u4f60\u4e2a\u9b3c\uff0c\u4e0d\u884c\uff01\u201d</p><p>\u201c\u771f\u5c0f\u6c14\uff0c\u201d\u51db\u561f\u7740\u5c0f\u5634\uff0c\u201c\u597d\u5427\u597d\u5427\uff0c\u8ba9\u4f60\u770b\u770b\u6211\u7684\u7edd\u5bc6\u73cd\u85cf\uff0c\u8fd9\u53ef\u662f\u6211\u5988\u5988\u7559\u7ed9\u6211\u7684\uff0c\u8bf4\u8981\u7b49\u6700\u5e78\u798f\u7684\u65e5\u5b50\u624d\u80fd\u7a7f\uff0c\u53f7\u79f0\u662f\u6ca1\u6709\u7537\u4eba\u80fd\u591f\u62b5\u6321\u7684\u8d85\u7ea7\u795e\u79d8\u6b66\u5668\u3002\u201d</p><p>\u201c\u662f\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u7684\u597d\u5947\u5fc3\u88ab\u52fe\u4e86\u8d77\u6765\u3002</p><p>\u201c\u5c31\u662f\u5b83\uff0c\u201d\u51db\u4ece\u8eab\u540e\u62ff\u51fa\u4e00\u4ef6\u6d01\u767d\u7684\u5a5a\u7eb1\uff0c\u201c\u600e\u4e48\u6837\uff0c\u6f02\u4eae\u5427\uff0c\u6765\u8bd5\u8bd5\u770b\u597d\u4e0d\u597d\u2014\u2014\u201d</p><p>\u201c\u2026\u2026\uff2e\uff2f\uff37\uff21\uff39\uff01\u201d</p><p>\u201c\uff2e\uff2f\uff37\uff21\uff39\uff1f\u201d\u51db\u542c\u4e0d\u61c2\u82f1\u8bed\u3002</p><p>\u201c\u5c31\u662f\u4f11\u60f3\uff01\u6ca1\u95e8\uff01\u201d</p><p>\u81ea\u4ece\u770b\u5230\u73ca\u5609\u4e4b\u540e\uff0c\u597d\u5947\u5fc3\u8d85\u5f3a\u7684\u5c0f\u5973\u5deb\u5c31\u7f20\u7740\u743c\u6069\uff0c\u975e\u8981\u4ed6\u8bd5\u7a7f\u5973\u88c5\u770b\u770b\u3002\u5bf9\u4e8e\u8fd9\u79cd\u4e8b\u5173\u7537\u4eba\u5c0a\u4e25\u548c\u9762\u5b50\u7684\u65e0\u7406\u8981\u6c42\uff0c\u743c\u6069\u81ea\u7136\u662f\u6beb\u4e0d\u72b9\u8c6b\u5730\u4e00\u53e3\u62d2\u7edd\uff0c\u4f46\u51db\u53ef\u4e5f\u6ca1\u90a3\u4e48\u5bb9\u6613\u653e\u5f03\uff0c\u4e0d\u6298\u4e0d\u6320\u5730\u529d\u8bf4\u7740\uff0c\u4e00\u76f4\u5230\u4e86\u665a\u9910\u7684\u9910\u684c\u4e0a\u90fd\u8fd8\u6ca1\u653e\u4e0b\u3002</p><p>\u665a\u9910\u662f\u73ca\u5609\u4e0b\u53a8\uff0c\u6bd4\u5348\u9910\u4e30\u76db\u5f97\u591a\u2014\u2014\u6700\u91cd\u8981\u7684\u662f\u7ec8\u4e8e\u6709\u8089\u4e86\u3002\u56db\u4e2a\u5973\u5b69\u5b50\uff08\u73ca\u5609\u3001\u6885\u83f2\u65af\u3001\u51db\u548c\u838e\u73de\u514b\uff09\u548c\u743c\u6069\u56f4\u6210\u4e00\u684c\uff0c\u4ece\u8868\u9762\u4e0a\u770b\u5012\u4e5f\u662f\u6c14\u6c1b\u878d\u6d3d\uff0c\u4e00\u6d3e\u548c\u8c10\u3002\u201c\u8bf4\u771f\u7684\uff0c\u743c\u6069\uff0c\u201d\u51db\u5634\u91cc\u585e\u7740\u534a\u5757\u725b\u6392\uff0c\u542b\u542b\u7cca\u7cca\u5730\u8bf4\uff0c\u201c\u4f60\u7a7f\u5973\u88c5\u4e00\u5b9a\u5f88\u6f02\u4eae\u7684\uff0c\u4fdd\u8bc1\u662f\u503e\u56fd\u503e\u57ce\u7684\u5927\u7f8e\u5973\uff0c\u4e0d\u8bd5\u8bd5\u5b9e\u5728\u592a\u53ef\u60dc\u4e86\u5462\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u743c\u6069\u4e0d\u5c51\uff0c\u201c\u4f60\u53c8\u6ca1\u770b\u5230\u8fc7\u3002\u201d</p><p>\u201c\u770b\u73ca\u5609\u5c31\u77e5\u9053\u4e86\u5440\uff0c\u201d\u51db\u7528\u624b\u91cc\u7684\u53c9\u5b50\u6307\u4e86\u6307\u5750\u5728\u5bf9\u9762\u7684\u9ed1\u53d1\u5c11\u5973\uff0c\u201c\u4f60\u59d0\u59d0\u8fd9\u4e48\u6f02\u4eae\uff0c\u4f60\u548c\u5979\u957f\u5f97\u90a3\u4e48\u50cf\uff0c\u4e0d\u7528\u8bd5\u5c31\u77e5\u9053\uff0c\u80af\u5b9a\u7684\u3002\u201d</p><p>\u201c\u65e2\u7136\u4e0d\u7528\u8bd5\u5c31\u77e5\u9053\u2014\u2014\u90a3\u4f60\u5df2\u7ecf\u77e5\u9053\uff0c\u5c31\u6b63\u597d\u4e0d\u7528\u8bd5\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4e0d\u884c\uff01\u201d\u51db\u6447\u5934\uff0c\u201c\u81c6\u6d4b\u4e3a\u865a\uff0c\u773c\u89c1\u4e3a\u5b9e\u3002\u201d</p><p>\u743c\u6069\u61d2\u5f97\u7406\u5979\uff0c\u4e13\u5fc3\u81f4\u5fd7\u5730\u5bf9\u4ed8\u9762\u524d\u7684\u98df\u7269\u3002\u51db\u89c1\u743c\u6069\u4e0d\u642d\u7406\u5979\uff0c\u6c14\u9f13\u9f13\u5730\u628a\u9910\u76d8\u4e00\u63a8\uff0c\u6b63\u8981\u53d1\u813e\u6c14\uff0c\u7a81\u7136\u773c\u775b\u4e00\u8f6c\uff0c\u8138\u4e0a\u53c8\u9732\u51fa\u4e86\u751c\u7f8e\u53ef\u7231\u7684\u7b11\u5bb9\u3002\u201c\u5450\uff0c\u743c\u6069\uff0c\u201d\u5979\u7528\u5145\u6ee1\u8bf1\u60d1\u7684\u5a07\u5a9a\u58f0\u97f3\u8bf4\uff0c\u201c\u8fd9\u6837\u5427\uff0c\u6211\u4eec\u6765\u8868\u51b3\u4e00\u4e0b\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u8868\u51b3\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u5c31\u662f\u5927\u5bb6\u6295\u7968\uff0c\u5c11\u6570\u670d\u4ece\u591a\u6570\uff0c\u201d\u51db\u7b11\u772f\u772f\u5730\u8bf4\uff0c\u8fd8\u4e0d\u7b49\u743c\u6069\u56de\u7b54\u5c31\u53cc\u624b\u4e00\u62cd\uff0c\u201c\u597d\uff0c\u65e2\u7136\u4f60\u6ca1\u610f\u89c1\uff0c\u90a3\u5c31\u8fd9\u4e48\u5b9a\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u7b49\u7b49\uff0c\u8c01\u8bf4\u6211\u6ca1\u610f\u89c1\u7684\uff0c\u4f60\u6709\u4ec0\u4e48\u6743\u529b\u2014\u2014\u201d</p><p>\u201c\u4f5c\u4e3a\u6b64\u6b21\u4e34\u65f6\u4f1a\u8bae\u7684\u53ec\u96c6\u4eba\uff0c\u6211\u73b0\u5728\u5ba3\u5e03\uff0c\u4e0b\u9762\u5bf9\u2018\u743c\u6069\u662f\u5426\u5e94\u8be5\u7a7f\u5973\u88c5\u7ed9\u6211\u4eec\u770b\u2019\u8fd9\u9879\u8bae\u6848\u8fdb\u884c\u8868\u51b3\uff0c\u201d\u51db\u76f4\u63a5\u6253\u65ad\uff0c\u201c\u6211\u8d5e\u6210\uff01\u201d</p><p>\u73ca\u5609\u653e\u4e0b\u624b\u4e2d\u5200\u53c9\uff0c\u4eea\u6001\u4f18\u96c5\u5730\u7528\u9910\u5dfe\u62b9\u4e86\u62b9\u5634\u5507\uff0c\u201c\u5173\u4e8e\u8fd9\u4e2a\u95ee\u9898\u561b\uff0c\u5c0f\u5f1f\uff0c\u6211\u8bb0\u5f97\u6211\u66fe\u7ecf\u548c\u4f60\u6709\u4e00\u4e2a\u7ea6\u5b9a\u7684\uff0c\u662f\u4e0d\u662f\uff1f\u201d\u5979\u7b11\u7740\u770b\u5411\u743c\u6069\uff0c\u201c\u90a3\u4e2a\u7ea6\u5b9a\u73b0\u5728\u4f9d\u7136\u6709\u6548\u54e6\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u7ea6\u5b9a\uff1f\u201d\u51db\u83ab\u540d\u5176\u5999\uff0c\u201c\u73ca\u5609\u4f60\u5230\u5e95\u662f\u8d5e\u6210\u8fd8\u662f\u4e0d\u8d5e\u6210\u554a\uff1f\u201d</p><p>\u201c\u5f53\u7136\u8d5e\u6210\u5566\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u628a\u81ea\u5df1\u7684\u5f1f\u5f1f\u6253\u626e\u6210\u6f02\u4eae\u5973\u5b69\u5b50\uff0c\u8fd9\u662f\u4e16\u754c\u4e0a\u6bcf\u4e2a\u59d0\u59d0\u7684\u68a6\u60f3\u5440\u3002\u201d</p><p>\u2026\u2026\u8fd9\u5206\u660e\u53ea\u662f\u4f60\u8fd9\u4e2a\u90aa\u6076\u59d0\u59d0\u7684\u68a6\u60f3\u597d\u4e0d\u597d\uff01</p><p>\u201c\u5f88\u597d\uff0c\u73b0\u5728\u6709\u4e24\u7968\u8d5e\u6210\u4e86\uff0c\u201d\u51db\u5174\u9ad8\u91c7\u70c8\uff0c\u201c\u838e\u73de\u514b\uff0c\u4f60\u5462\uff1f\u201d\u5979\u95ee\u65c1\u8fb9\u7684\u9b45\u9b54\uff0c\u201c\u4f60\u4e0d\u4f1a\u53cd\u5bf9\u7684\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u6211\u8d5e\u6210\uff0c\u201d\u9b45\u9b54\u683c\u683c\u7b11\u7740\uff0c\u201c\u80fd\u770b\u5230\u4e3b\u4eba\u7a7f\u5973\u88c5\u7684\u6837\u5b50\uff0c\u8fd9\u79cd\u597d\u673a\u4f1a\u6211\u600e\u4e48\u80fd\u9519\u8fc7\u5462\uff1f\u201d</p><p>\u201c\u592a\u597d\u4e86\uff0c\u201d\u51db\u9ad8\u5174\u5730\u62b1\u4e86\u838e\u73de\u514b\u4e00\u4e0b\uff0c\u9b45\u9b54\u663e\u7136\u6709\u4e9b\u610f\u5916\uff0c\u8981\u77e5\u9053\u5979\u4eec\u5e73\u65f6\u5173\u7cfb\u5e76\u4e0d\u7b97\u597d\u3002\u4e0d\u77e5\u4e3a\u4f55\uff0c\u51db\u4e00\u76f4\u5c31\u4e0d\u592a\u559c\u6b22\u838e\u73de\u514b\uff0c\u5728\u6df1\u6e0a\u548c\u5e7d\u6697\u5730\u57df\u7684\u65f6\u5019\u5c31\u4e00\u76f4\u5982\u6b64\uff0c\u5012\u662f\u548c\u53cc\u80de\u80ce\u59d0\u59b9\u4fe9\u6bd4\u8f83\u8c08\u5f97\u6765\u3002\u201c\u827e\u5f25\u8587\uff0c\u8f6e\u5230\u4f60\u4e86\uff0c\u201d\u5979\u50ac\u4fc3\u6885\u83f2\u65af\uff0c\u201c\u5feb\u70b9\u5feb\u70b9\u3002\u201d</p><p>\u6885\u83f2\u65af\u72b9\u8c6b\u4e86\u51e0\u79d2\u949f\uff0c\u770b\u770b\u51db\uff0c\u53c8\u770b\u770b\u743c\u6069\uff0c\u201c\u6211\u5c31\u7b97\u4e86\u5427\uff0c\u201d\u5979\u7b11\u7b11\uff0c\u201c\u6211\u4e0d\u8d5e\u6210\u4e5f\u4e0d\u53cd\u5bf9\uff0c\u5f03\u6743\u597d\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u52b2\uff0c\u5c31\u77e5\u9053\u5e2e\u7740\u4ed6\uff0c\u201d\u51db\u6487\u6487\u5c0f\u5634\uff0c\u201c\u65e0\u6240\u8c13\u5566\u3002\u743c\u6069\uff0c\u8fd9\u91cc\u6709\u56db\u4e2a\u4eba\u2014\u2014\u54e6\uff0c\u4e5f\u7b97\u4e0a\u4f60\u81ea\u5df1\u5427\uff0c\u4e00\u5171\u4e94\u4e2a\uff0c\u5df2\u7ecf\u6709\u4e09\u7968\u8d5e\u6210\uff0c\u4e00\u7968\u5f03\u6743\uff0c\u5c31\u7b97\u4f60\u81ea\u5df1\u53cd\u5bf9\u4e5f\u6ca1\u7528\uff0c\u4f9d\u7136\u662f\u7edd\u5bf9\u591a\u6570\u4e86\u3002\u201d</p><p>\u201c\u65e0\u89c6\u4f60\uff0c\u201d\u743c\u6069\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u8fd9\u79cd\u591a\u6570\u4eba\u7684\u66b4\u653f\uff0c\u6211\u662f\u4e0d\u4f1a\u5c48\u670d\u7684\uff01\u201d</p><p>\u201c\u8fd9\u600e\u4e48\u80fd\u53eb\u591a\u6570\u4eba\u7684\u66b4\u653f\uff1f\u201d\u51db\u4e0d\u670d\u6c14\uff0c\u201c\u8fd9\u660e\u660e\u662f\u5927\u5bb6\u7684\u666e\u904d\u547c\u58f0\u3002\u4f60\u770b\uff0c\u53ea\u8981\u4f60\u613f\u610f\u8bd5\u8bd5\u5973\u88c5\uff0c\u5c31\u80fd\u8ba9\u6211\u4eec\u8fd9\u4e48\u591a\u4eba\u9ad8\u5174\u2014\u2014\u65e2\u7136\u5982\u6b64\uff0c\u4f60\u5c31\u5c0f\u5c0f\u5730\u727a\u7272\u4e00\u4e0b\uff0c\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u561b\u3002\u201d</p><p>\u8fd9\u4e2a\u4e48\uff0c\u5012\u8fd8\u52c9\u5f3a\u80fd\u7b97\u662f\u4e2a\u7406\u7531\uff0c\u5982\u679c\u505a\u67d0\u4e00\u4ef6\u4e8b\u60c5\uff0c\u80fd\u591f\u8ba9\u81ea\u5df1\u7684\u5973\u53cb\u4eec\u90fd\u5f88\u5f00\u5fc3\uff0c\u90a3\u4e48\u743c\u6069\u662f\u613f\u610f\u8003\u8651\u7684\u2014\u2014\u95ee\u9898\u5728\u4e8e\uff0c\u8fd9\u4e0d\u662f\u201c\u5c0f\u5c0f\u7684\u727a\u7272\u4e00\u4e0b\u201d\uff0c\u8fd9\u727a\u7272\u5f88\u5927\u554a\u3002</p><p>\u201c\u54ea\u91cc\u727a\u7272\u5927\u4e86\uff1f\u201d\u51db\u53cd\u95ee\uff0c\u201c\u827e\u5f25\u8587\u7ecf\u5e38\u7a7f\u7537\u88c5\uff0c\u4f60\u4e0d\u662f\u4e5f\u5f88\u559c\u6b22\u5417\uff1f\u65e2\u7136\u5973\u5b69\u5b50\u53ef\u4ee5\u7a7f\u7537\u88c5\uff0c\u7537\u4eba\u4e3a\u4ec0\u4e48\u5c31\u4e0d\u80fd\u7a7f\u5973\u88c5\u3002\u201d</p><p>\u201c\u90a3\u600e\u4e48\u80fd\u4e00\u6837\uff0c\u201d\u743c\u6069\u6709\u6c14\u65e0\u529b\u5730\u53cd\u9a73\uff0c\u201c\u5973\u5b69\u5b50\u7a7f\u7537\u88c5\uff0c\u90a3\u662f\u82f1\u6c14\uff0c\u662f\u4e2a\u6027\uff1b\u7537\u4eba\u7a7f\u5973\u88c5\uff0c\u90a3\u5c31\u662f\u5fc3\u7406\u53d8\u6001\u554a\u3002\u201d</p><p>\u201c\u4f60\u8fd9\u4e2a\u604b\u59d0\u72c2\uff0c\u672c\u6765\u5c31\u662f\u5fc3\u7406\u53d8\u6001\u597d\u4e0d\u597d\uff01\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u539f\u672c\u6b22\u58f0\u7b11\u8bed\u7684\u9910\u684c\u4e0a\uff0c\u6c14\u6c1b\u9661\u7136\u95f4\u51b7\u4e86\u4e0b\u6765\uff0c\u51e0\u4e4e\u6240\u6709\u4eba\u90fd\u6014\u4f4f\u4e86\uff0c\u800c\u51db\u81ea\u5df1\u5219\u60ca\u8bb6\u5730\u6342\u7740\u5634\uff0c\u77aa\u5927\u773c\u775b\uff0c\u4f3c\u4e4e\u4e0d\u6562\u76f8\u4fe1\u521a\u624d\u90a3\u53e5\u8bdd\u662f\u4ece\u81ea\u5df1\u53e3\u4e2d\u8bf4\u51fa\u7684\u3002\u743c\u6069\u559c\u6b22\u4ed6\u7684\u59d0\u59d0\uff0c\u4ee5\u53ca\u4ed6\u4eec\u4e4b\u95f4\u65e2\u662f\u59d0\u5f1f\u53c8\u662f\u60c5\u4eba\u7684\u53cc\u91cd\u5173\u7cfb\uff0c\u51db\u65e9\u5c31\u77e5\u9053\uff0c\u5bf9\u4e8e\u5979\u8fd9\u79cd\u9f99\u8109\u8005\u800c\u8a00\uff0c\u8fd1\u4eb2\u901a\u5a5a\u4ee5\u4fdd\u6301\u8840\u8109\u7eaf\u51c0\u90fd\u662f\u5f88\u5bfb\u5e38\u7684\u4e8b\u60c5\uff0c\u81ea\u7136\u4e5f\u4e0d\u89c9\u5f97\u743c\u6069\u8fd9\u6837\u505a\u6709\u4ec0\u4e48\u4e0d\u5bf9\u3002\u4f46\u5979\u4e5f\u77e5\u9053\u6309\u7167\u8fd9\u4e2a\u4e16\u754c\u4e0a\u7edd\u5927\u591a\u6570\u4eba\u9ed8\u8ba4\u7684\u9053\u5fb7\u6807\u51c6\uff0c\u743c\u6069\u7684\u505a\u6cd5\u5e76\u4e0d\u6b63\u786e\uff0c\u81f3\u5c11\u4e0d\u4f1a\u88ab\u516c\u5f00\u627f\u8ba4\u7684\u3002\u51db\u597d\u5947\u3001\u4efb\u6027\u3001\u5b69\u5b50\u6c14\u91cd\uff0c\u5374\u5e76\u4e0d\u662f\u771f\u7684\u4e0d\u61c2\u4e8b\uff0c\u4ec0\u4e48\u8bdd\u53ef\u4ee5\u516c\u5f00\u8bf4\uff0c\u4ec0\u4e48\u8bdd\u53ea\u80fd\u79c1\u4e0b\u8bf4\uff0c\u5979\u8fd8\u662f\u6e05\u695a\u7684\u3002\u53ea\u662f\u4e00\u4ef6\u4e8b\u60c5\u85cf\u5728\u5fc3\u91cc\uff0c\u603b\u4e0d\u53ef\u80fd\u548c\u771f\u7684\u4e0d\u77e5\u9053\u4e00\u6837\uff0c\u7ec8\u7a76\u662f\u6709\u75d5\u8ff9\u7684\uff0c\u5979\u5e73\u65f6\u53c8\u548c\u743c\u6069\u6597\u5634\u60ef\u4e86\uff0c\u8bf4\u8bdd\u4e0d\u4f1a\u592a\u6ce8\u610f\uff0c\u7ed3\u679c\u4e00\u6025\u4e4b\u4e0b\u53e3\u4e0d\u62e9\u8a00\uff0c\u5fd8\u4e86\u73ca\u5609\u5c31\u5750\u5728\u65c1\u8fb9\uff0c\u4fbf\u628a\u8fd9\u4ef6\u4e8b\u60c5\u8bf4\u4e86\u51fa\u6765\u3002</p><p>\u5176\u5b9e\u5728\u573a\u7684\u4e94\u4e2a\u4eba\uff0c\u5bf9\u8fd9\u4ef6\u4e8b\u60c5\u90fd\u662f\u4e00\u6e05\u4e8c\u695a\uff0c\u6839\u672c\u4e0d\u7b97\u4ec0\u4e48\u79d8\u5bc6\u2014\u2014\u5c31\u7b97\u662f\u79d8\u5bc6\uff0c\u90a3\u4e5f\u662f\u4f17\u6240\u5468\u77e5\u7684\u79d8\u5bc6\uff0c\u53ea\u4e0d\u8fc7\u662f\u5927\u5bb6\u90fd\u5b88\u7740\u9ed8\u5951\uff0c\u5fc3\u7167\u4e0d\u5ba3\uff0c\u4e0d\u5f53\u9762\u63d0\u8d77\u7f62\u4e86\u3002\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\u5c31\u662f\u8fd9\u6837\uff0c\u6709\u4e9b\u80fd\u8bf4\u4e0d\u80fd\u505a\uff0c\u6709\u4e9b\u80fd\u505a\u4e0d\u80fd\u8bf4\uff0c\u54ea\u6015\u5176\u5b9e\u4eba\u4eba\u90fd\u77e5\u9053\uff0c\u4f46\u662f\u5426\u516c\u5f00\u8bf4\u51fa\u6765\uff0c\u6233\u7a7f\u90a3\u5c42\u7a97\u6237\u7eb8\uff0c\u7ed3\u679c\u5c31\u5b8c\u5168\u662f\u4e24\u7801\u4e8b\u3002</p><p>\u201c\u9053\u6b49\uff0c\u51db\uff01\u201d\u6885\u83f2\u65af\u6700\u5148\u53cd\u5e94\u8fc7\u6765\uff0c\u5979\u7684\u8138\u4e0a\u9690\u9690\u6709\u4e86\u6012\u8272\uff0c\u201c\u4f60\u592a\u8fc7\u5206\u4e86\uff01\u201d</p><p>\u201c\u5bf9\u2026\u2026\u5bf9\u4e0d\u8d77\uff0c\u73ca\u5609\uff0c\u6211\u4e0d\u662f\u6545\u610f\u7684\u2026\u2026\u201d\u51db\u614c\u4e71\u5730\u9053\u6b49\uff0c\u6025\u5f97\u51e0\u4e4e\u8981\u54ed\u51fa\u6765\uff0c\u201c\u6211\u4e0d\u662f\u8bf4\u4f60\uff0c\u6211\u5176\u5b9e\u662f\u60f3\u8bf4\uff0c\u90a3\u4e2a\uff0c\u90a3\u4e2a\u2026\u2026\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u201d\u73ca\u5609\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u7f8e\u4e3d\u7684\u8138\u988a\u4e0a\u900f\u51fa\u51e0\u5206\u82cd\u767d\uff0c\u201c\u672c\u6765\u2026\u2026\u201d</p><p>\u94ff\uff01</p><p>\u6e05\u8106\u7684\u91d1\u5c5e\u649e\u51fb\u58f0\u6253\u65ad\u4e86\u73ca\u5609\u7684\u8bdd\uff0c\u4f17\u4eba\u5faa\u58f0\u770b\u53bb\uff0c\u6b63\u89c1\u743c\u6069\u5c06\u624b\u4e2d\u7684\u5200\u53c9\u6254\u5230\u9910\u76d8\u4e2d\uff0c\u7528\u9910\u5dfe\u64e6\u4e86\u64e6\u5634\uff0c\u6162\u6162\u62ac\u8d77\u5934\u6765\uff0c\u76f4\u76f4\u5730\u770b\u7740\u51db\uff0c\u201c\u5bf9\uff0c\u6211\u662f\u559c\u6b22\u6211\u59d0\u59d0\uff0c\u201d\u4ed6\u6de1\u6de1\u8bf4\uff0c\u201c\u6211\u4e0d\u4f46\u559c\u6b22\u5979\uff0c\u8fd8\u60f3\u5a36\u5979\u505a\u59bb\u5b50\uff0c\u4f60\u6709\u4ec0\u4e48\u610f\u89c1\u5417\uff1f\u51db\u5c0f\u59d0\u3002\u201d</p><p>\u201c\u6ca1\u3001\u6ca1\u6709\uff0c\u201d\u51db\u8d76\u7d27\u6447\u5934\uff0c\u5979\u5413\u5f97\u5a07\u8eaf\u5fae\u98a4\uff0c\u58f0\u97f3\u90fd\u6709\u4e9b\u53d1\u6296\uff0c\u201c\u6211\u4ec0\u4e48\u610f\u89c1\u90fd\u6ca1\u6709\u2026\u2026\u53ef\u662f\u2014\u2014\u201d</p><p>\u201c\u53ef\u662f\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u53ef\u662f\uff0c\u201d\u51db\u9f13\u8db3\u52c7\u6c14\uff0c\u201c\u4f60\u5982\u679c\u5a36\u4e86\u73ca\u5609\uff0c\u90a3\u827e\u5f25\u8587\u600e\u4e48\u529e\uff1f\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u5982\u679c\u8bf4\u5979\u524d\u4e00\u6b21\u5931\u8a00\u5df2\u7ecf\u9707\u4f4f\u4e86\u5168\u573a\uff0c\u90a3\u4e48\u73b0\u5728\u8fd9\u53e5\u8bdd\u4fbf\u662f\u628a\u7a7a\u6c14\u90fd\u5f7b\u5e95\u51bb\u7ed3\u4f4f\u4e86\uff0c\u6c14\u6c1b\u4e00\u65f6\u95f4\u8be1\u5f02\u5230\u4e86\u6781\u70b9\u3002\u743c\u6069\u4f4e\u4e0b\u5934\uff0c\u62ff\u8d77\u5200\u53c9\u7ee7\u7eed\u5bf9\u4ed8\u4ed6\u7684\u665a\u9910\uff0c\u51db\u624b\u8db3\u65e0\u63aa\u5730\u7ad9\u5728\u90a3\u91cc\uff0c\u4e00\u65f6\u95f4\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u529e\u597d\u3002\u6885\u83f2\u65af\u6697\u6697\u53f9\u4e86\u53e3\u6c14\uff0c\u54ac\u7740\u5634\u5507\u8d70\u8fc7\u6765\uff0c\u62c9\u7740\u51db\u79bb\u5f00\u9910\u5385\uff0c\u201c\u5bf9\u4e0d\u8d77\uff0c\u201d\u5979\u671d\u73ca\u5609\u70b9\u70b9\u5934\uff0c\u201c\u5148\u5931\u966a\u4e86\u3002\u201d</p><p>\u5979\u4eec\u79bb\u5f00\u4e4b\u540e\uff0c\u9910\u5385\u91cc\u4e00\u65f6\u95f4\u9677\u5165\u6c89\u9ed8\uff0c\u5269\u4e0b\u4e09\u4e2a\u4eba\u8c01\u90fd\u6ca1\u6709\u8bf4\u8bdd\u3002\u8fc7\u4e86\u534a\u664c\uff0c\u73ca\u5609\u5148\u5f00\u53e3\u4e86\uff0c\u201c\u5e72\u561b\u53d1\u8fd9\u4e48\u5927\u813e\u6c14\uff0c\u201d\u5979\u67d4\u58f0\u8bf4\uff0c\u201c\u4f60\u628a\u51db\u5c0f\u59d0\u90fd\u5413\u54ed\u4e86\u3002\u201d</p><p>\u201c\u6211\u53c8\u6ca1\u53d1\u706b\u3002\u201d\u743c\u6069\u54ac\u7740\u725b\u6392\uff0c\u542b\u7cca\u4e0d\u6e05\u5730\u8bf4\u3002</p><p>\u201c\u4f60\u521a\u624d\u90a3\u6837\u5b50\u6bd4\u53d1\u706b\u8fd8\u5413\u4eba\uff0c\u6211\u770b\u4e86\u90fd\u5bb3\u6015\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u53bb\u5411\u5979\u9053\u4e2a\u6b49\u5427\u3002\u201d</p><p>\u201c\u4e0d\u53bb\uff0c\u201d\u743c\u6069\u62d2\u7edd\uff0c\u201c\u6211\u53c8\u6ca1\u505a\u9519\u4ec0\u4e48\uff0c\u5e72\u561b\u8981\u9053\u6b49\u3002\u201d</p><p>\u201c\u7537\u4eba\u4e0d\u8981\u90a3\u4e48\u5fc3\u80f8\u72ed\u7a84\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5979\u6bd5\u7adf\u662f\u5973\u5b69\u5b50\uff0c\u53c8\u4e0d\u662f\u6545\u610f\u7684\uff0c\u4f60\u5c31\u8ba9\u7740\u5979\u70b9\uff0c\u4e5f\u662f\u5e94\u8be5\u7684\u3002\u201d</p><p>\u201c\u8981\u662f\u59d0\u59d0\u4f60\uff0c\u6211\u5c31\u8ba9\u7740\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5979\u6211\u624d\u4e0d\u8ba9\u7740\u5462\u3002\u201d</p><p>\u73ca\u5609\u5fae\u5fae\u4e00\u7b11\uff0c\u6b63\u8981\u7ee7\u7eed\u529d\u4ed6\uff0c\u4e00\u76f4\u6c89\u9ed8\u7684\u838e\u73de\u514b\u63d2\u8bdd\u8fdb\u6765\u3002\u201c\u9a6c\u4e0a\u8981\u5230\u516b\u70b9\u4e86\uff0c\u73ca\u5609\u59d0\u59d0\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8be5\u53bb\u5b66\u4e60\u5360\u661f\u672f\u4e86\uff0c\u8001\u5e08\u6b63\u5728\u540e\u9662\u7b49\u7740\u4f60\u5462\u3002\u201d</p><p>\u201c\u554a\uff1f\u201d\u73ca\u5609\u62ac\u5934\u770b\u770b\u89d2\u843d\u91cc\u7684\u6c99\u6f0f\uff0c\u201c\u65f6\u95f4\u600e\u4e48\u8fc7\u5f97\u8fd9\u4e48\u5feb\uff0c\u201d\u5979\u8d76\u7d27\u89e3\u4e0b\u9910\u5dfe\uff0c\u5306\u5306\u5fd9\u5fd9\u8d77\u8eab\u79bb\u5e2d\uff0c\u201c\u5c0f\u5f1f\u6211\u5148\u8d70\u4e86\u3002\u7b49\u4ed6\u5403\u5b8c\u8bb0\u5f97\u5e2e\u6211\u6536\u62fe\u4e00\u4e0b\uff0c\u838e\u841d\u3002\u201d\u6700\u540e\u4e00\u53e5\u8bdd\u5374\u662f\u5bf9\u9b45\u9b54\u8bf4\u7684\u3002</p><p>\u838e\u73de\u514b\u70b9\u5934\uff0c\u201c\u77e5\u9053\u3002\u201d</p><p>\u201c\u665a\u4e0a\u8fd8\u8981\u5b66\u4e60\u5360\u661f\u672f\uff1f\u201d\u743c\u6069\u95ee\u9b45\u9b54\uff0c\u201c\u59d0\u59d0\u73b0\u5728\u597d\u5fd9\u7684\u6837\u5b50\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u8001\u5e08\u7ed9\u5979\u5236\u5b9a\u7684\u8bfe\u7a0b\u8868\uff0c\u4e0a\u5348\u953b\u70bc\u5251\u672f\uff0c\u516b\u70b9\u5230\u5341\u4e8c\u70b9\uff0c\u56db\u4e2a\u5c0f\u65f6\uff1b\u4e0b\u5348\u5b66\u4e60\u9b54\u6cd5\uff0c\u4e00\u70b9\u5230\u4e94\u70b9\uff0c\u56db\u4e2a\u5c0f\u65f6\uff1b\u665a\u4e0a\u8ddf\u5979\u5b66\u4e60\u5360\u661f\u672f\uff0c\u516b\u70b9\u5230\u5341\u4e8c\u70b9\uff0c\u56db\u4e2a\u5c0f\u65f6\uff1b\u8fd9\u6837\u4e00\u5929\u5c31\u662f\u5341\u4e8c\u5c0f\u65f6\uff0c\u8fd8\u8981\u4f11\u606f\u3001\u7761\u7720\uff0c\u8fd8\u8981\u7ed9\u4f60\u2014\u2014\u8fd8\u6709\u4f60\u7684\u5c0f\u60c5\u4eba\u4eec\u2014\u2014\u505a\u996d\uff0c\u80fd\u4e0d\u5fd9\u5417\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u542c\u4e86\u9b45\u9b54\u7684\u8d23\u5907\uff0c\u743c\u6069\u53ea\u662f\u6de1\u6de1\u5e94\u4e86\u58f0\uff0c\u5012\u662f\u8ba9\u838e\u73de\u514b\u9887\u6709\u4e9b\u610f\u5916\u3002\u201c\u5582\uff0c\u4e3b\u4eba\uff0c\u201d\u5979\u4e0d\u89e3\uff0c\u201c\u96be\u9053\u8fd9\u79cd\u65f6\u5019\uff0c\u4f60\u4e0d\u5e94\u8be5\u8868\u793a\u4e00\u4e0b\u6127\u759a\u548c\u5173\u5fc3\u5417\uff1f\u201d</p><p>\u201c\u53c8\u4e0d\u7528\u8868\u73b0\u7ed9\u4f60\u770b\uff0c\u201d\u743c\u6069\u4e0d\u5ba2\u6c14\u5730\u8bf4\uff0c\u201c\u4e0d\u8fc7\u4f60\u521a\u624d\u53eb\u5965\u5609\u83b1\u65af\u5973\u58eb\u53eb\u4ec0\u4e48\uff1f\u8001\u5e08\uff1f\u201d</p><p>\u201c\u55ef\u554a\uff0c\u201d\u838e\u73de\u514b\u5f97\u610f\u5730\u70b9\u5934\uff0c\u201c\u73ca\u5609\u59d0\u59d0\u53eb\u5979\u8001\u5e08\u561b\uff0c\u6211\u4e5f\u5c31\u8ddf\u7740\u53eb\u3002\u800c\u4e14\u4e0b\u5348\u8001\u5e08\u8bf4\uff0c\u6709\u7a7a\u4e5f\u4f1a\u7ed9\u6211\u4e00\u4e9b\u6307\u70b9\u54e6\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u90a3\u4e0d\u9519\u554a\u3002\u201d</p><p>\u743c\u6069\u968f\u53e3\u5e94\u4e86\u58f0\uff0c\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4e09\u4e0b\u4e94\u9664\u4e8c\u628a\u81ea\u5df1\u90a3\u4efd\u5df2\u7ecf\u51b7\u6389\u7684\u665a\u9910\u5bf9\u4ed8\u5b8c\uff0c\u7136\u540e\u8d77\u8eab\u79bb\u5f00\u9910\u5385\u3002</p><p>\u203b\u203b\u203b</p><p>\u591c\u6df1\u4e86\u3002</p><p>\u743c\u6069\u61d2\u6d0b\u6d0b\u5730\u6ce1\u5728\u5bbd\u5927\u7684\u6d74\u7f38\u91cc\uff0c\u7ffb\u7740\u81ea\u5df1\u7684\u9b54\u6cd5\u4e66\uff0c\u81ea\u8116\u9888\u4ee5\u4e0b\uff0c\u8eab\u4f53\u7684\u6bcf\u4e00\u5bf8\u6bdb\u5b54\u90fd\u88ab\u70ed\u6c34\u6d78\u6ce1\u7740\uff0c\u4eab\u53d7\u8fd9\u65e0\u6bd4\u7684\u8212\u9002\u3002\u5468\u56f4\u9759\u6084\u6084\u7684\uff0c\u6d74\u5ba4\u91cc\u53ea\u6709\u4ed6\u4e00\u4e2a\u4eba\uff0c\u4eff\u4f5b\u6574\u4e2a\u4e16\u754c\u90fd\u5df2\u7ecf\u88ab\u9694\u7edd\u5728\u5916\u3002</p><p>\u7136\u540e\u6572\u95e8\u58f0\u54cd\u8d77\u3002</p><p>\u201c\u8bf7\u8fdb\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u95e8\u88ab\u63a8\u5f00\u4e86\uff0c\u4e00\u4e2a\u7a88\u7a95\u7f8e\u4e3d\u7684\u8eab\u5f71\u8d70\u8fdb\u6765\uff0c\u56e0\u4e3a\u6ee1\u5ba4\u5f25\u6f2b\u7740\u84b8\u817e\u6c34\u6c7d\uff0c\u743c\u6069\u770b\u4e0d\u6e05\u695a\u9762\u5bb9\uff0c\u4f46\u4ece\u8eab\u5f62\u8f6e\u5ed3\u4e0a\u4fbf\u77e5\u9053\u662f\u6885\u83f2\u65af\u3002\u5c11\u5973\u8d70\u5230\u4ed6\u65c1\u8fb9\uff0c\u89e3\u5f00\u6d74\u888d\uff0c\u9732\u51fa\u91cc\u9762\u4e00\u4e1d\u4e0d\u6302\u7684\u5a07\u7f8e\u88f8\u4f53\uff0c\u7136\u540e\u8de8\u5165\u6d74\u7f38\u4e2d\uff0c\u201c\u8fc7\u53bb\u70b9\uff0c\u201d\u5979\u63a8\u4e86\u63a8\u743c\u6069\u7684\u80a9\u8180\uff0c\u201c\u522b\u4e00\u4e2a\u4eba\u5360\u8fd9\u4e48\u5927\u4f4d\u7f6e\u3002\u201d</p><p>\u743c\u6069\u5f80\u91cc\u9762\u79fb\u4e86\u79fb\uff0c\u8ba9\u6885\u83f2\u65af\u5728\u4ed6\u8eab\u65c1\u8eba\u4e0b\uff0c\u5c11\u5973\u62ac\u624b\u89e3\u5f00\u53d1\u9afb\uff0c\u8ba9\u91d1\u53d1\u5782\u4e0b\uff0c\u6f02\u6d6e\u5728\u6c34\u9762\u4e0a\u3002\u201c\u5934\u53d1\u771f\u7684\u957f\u957f\u4e86\u5f88\u591a\u5462\uff0c\u201d\u5979\u8f7b\u58f0\u8bf4\uff0c\u201c\u521a\u8ba4\u8bc6\u4f60\u7684\u65f6\u5019\uff0c\u53ea\u80fd\u78b0\u5230\u80a9\u8180\u5427\uff0c\u90fd\u5df2\u7ecf\u5feb\u5230\u8170\u4e86\u3002\u201d</p><p>\u201c\u6bd5\u7adf\u5df2\u7ecf\u4e24\u5e74\u4e86\u561b\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u662f\u554a\uff0c\u4e24\u5e74\u4e86\u5462\uff0c\u201d\u6885\u83f2\u65af\u8f7b\u8f7b\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u65f6\u95f4\u8fc7\u5f97\u597d\u5feb\uff0c\u6709\u65f6\u5019\u56de\u60f3\u8d77\u90a3\u4e9b\u4e8b\u60c5\uff0c\u5c31\u50cf\u6628\u5929\u7684\u53d1\u751f\u4e00\u6837\u3002\u201d</p><p>\u201c\u56de\u5fc6\u548c\u6000\u65e7\uff0c\u90a3\u662f\u53d8\u8001\u7684\u6807\u5fd7\u554a\u3002\u201d\u743c\u6069\u548c\u5979\u5f00\u73a9\u7b11\u3002</p><p>\u201c\u786e\u5b9e\u662f\u957f\u5927\u4e86\u561b\uff0c\u201d\u5c11\u5973\u4e00\u7b11\uff0c\u201c\u957f\u5927\u4e00\u5c81\uff0c\u5c31\u662f\u53d8\u8001\u4e00\u5c81\uff0c\u6ca1\u533a\u522b\u3002\u201d</p><p>\u201c\u8bdd\u867d\u5982\u6b64\uff0c\u4f60\u4e00\u4e2a\u5341\u516d\u5c81\u7684\u5973\u5b69\u5b50\uff0c\u8bf4\u4ec0\u4e48\u53d8\u8001\u4e4b\u7c7b\u7684\uff0c\u8fd8\u662f\u8ba9\u4eba\u89c9\u5f97\u597d\u53e4\u602a\u554a\uff0c\u201d\u743c\u6069\u8f7b\u8f7b\u63fd\u4f4f\u5979\u67d4\u8f6f\u7684\u8170\u80a2\uff0c\u201c\u600e\u4e48\u4e86\uff0c\u827e\u5f25\u8587\uff0c\u6709\u5fc3\u4e8b\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u201d\u5c11\u5973\u8f7b\u58f0\u8bf4\uff0c\u201c\u6700\u8fd1\u4e0d\u77e5\u9053\u600e\u4e48\u641e\u7684\uff0c\u5e38\u5e38\u4e0d\u7531\u81ea\u4e3b\u5730\u60f3\u8d77\u4ee5\u524d\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u201c\u60f3\u8d77\u4ee5\u524d\u54ea\u4e9b\u4e8b\u60c5\uff1f\u201d</p><p>\u201c\u5f88\u591a\u554a\uff0c\u6211\u4eec\u7b2c\u4e00\u6b21\u89c1\u9762\u7684\u60c5\u5f62\uff0c\u4f60\u591c\u91cc\u5077\u770b\u6211\u6d17\u6fa1\uff0c\u5728\u53e4\u57ce\u91cc\u548c\u6211\u505a\u4ea4\u6613\uff0c\u53bb\u4eba\u9762\u72ee\u90a3\u91cc\u593a\u56de\u9ece\u660e\u4e4b\u77f3\uff0c\u78b0\u5230\u683c\u62c9\u5179\u7279\uff0c\u8fd8\u6709\uff0c\u201d\u5c11\u5973\u4fcf\u8138\u53d1\u70e7\uff0c\u201c\u8fd8\u6709\u4f60\u8fd9\u5927\u574f\u86cb\uff0c\u5c31\u5728\u90a3\u79cd\u5730\u65b9\u6b3a\u8d1f\u6211\uff0c\u8ba9\u6211\u7ed9\u4f60\u505a\u90a3\u79cd\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u201c\u540e\u6094\u4e86\uff1f\u201d\u743c\u6069\u7b11\u7740\u95ee\uff0c\u201c\u5982\u679c\u80fd\u591f\u91cd\u56de\u8fc7\u53bb\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u628a\u6211\u4e00\u5251\u6740\u4e86\uff1f\u201d</p><p>\u201c\u4e0d\u4f1a\u3002\u201d\u6885\u83f2\u65af\u5f88\u8ba4\u771f\u5730\u8bf4\u3002</p><p>\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u73b0\u5728\u5f88\u5feb\u4e50\u554a\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6240\u4ee5\u4e0d\u540e\u6094\uff0c\u4e5f\u4e0d\u60f3\u6539\u53d8\u8fc7\u53bb\u3002\u201d</p><p>\u201c\u6216\u8bb8\u6539\u53d8\u4e86\uff0c\u73b0\u5728\u4f1a\u66f4\u5feb\u4e50\u5462\uff1f\u201d</p><p>\u201c\u8fd9\u79cd\u2018\u53ef\u80fd\u2019\u7684\u4e8b\u60c5\uff0c\u6211\u53ef\u6ca1\u5174\u8da3\u53bb\u5c1d\u8bd5\uff0c\u201d\u5c11\u5973\u683c\u683c\u7b11\u7740\uff0c\u201c\u73b0\u5728\u5c31\u5df2\u7ecf\u5f88\u597d\u4e86\uff0c\u867d\u7136\u8fd8\u4e0d\u591f\u5b8c\u7f8e\u3002\u6211\u4e0d\u8d2a\u5fc3\u7684\u3002\u201d</p><p>\u201c\u6709\u4e48\uff0c\u201d\u743c\u6069\u6545\u610f\u9017\u5979\uff0c\u201c\u6211\u600e\u4e48\u89c9\u5f97\u4f60\u8d2a\u5fc3\u5f97\u8981\u547d\uff0c\u6bcf\u6b21\u5728\u5e8a\u4e0a\u90fd\u50cf\u5582\u4e0d\u9971\u4f3c\u7684\u3002\u201d</p><p>\u201c\u54ea\u6709\uff01\u201d\u5c11\u5973\u6da8\u7ea2\u7740\u8138\u6297\u8bae\uff0c\u201c\u660e\u660e\u662f\u4f60\u8086\u610f\u6b3a\u8d1f\u4eba\u5bb6\uff0c\u6bcf\u6b21\u90fd\u88ab\u4f60\u5f04\u5230\u5168\u8eab\u6ca1\u529b\u6c14\u2026\u2026\u201d</p><p>\u201c\u53ef\u662f\u5c31\u7b97\u5df2\u7ecf\u5168\u8eab\u6ca1\u529b\u6c14\u4e86\uff0c\u53ea\u8981\u6211\u95ee\u4f60\u8fd8\u60f3\u4e0d\u60f3\u518d\u6765\u4e00\u6b21\uff0c\u4f60\u90fd\u662f\u8bf4\u60f3\u2014\u2014\u201d</p><p>\u201c\u8ba8\u538c\uff01\u201d\u5c11\u5973\u4e00\u62f3\u6253\u8fc7\u6765\uff0c\u201c\u4e0d\u8bb8\u8bf4\uff01\u201d</p><p>\u743c\u6069\u67b6\u4f4f\u5979\u7684\u62f3\u5934\uff0c\u987a\u52bf\u5c06\u5979\u62c9\u5230\u81ea\u5df1\u6000\u4e2d\u3002\u4e24\u4eba\u7b11\u95f9\u4e86\u4e00\u9635\uff0c\u201c\u5bf9\u4e86\uff0c\u743c\u6069\uff0c\u4eca\u5929\u51db\u7684\u4e8b\u60c5\uff0c\u5bf9\u4e0d\u8d77\uff0c\u201d\u6885\u83f2\u65af\u8f7b\u58f0\u8bf4\uff0c\u201c\u5979\u771f\u7684\u4e0d\u662f\u6545\u610f\uff0c\u6211\u4ee3\u5979\u5411\u4f60\u9053\u6b49\uff0c\u522b\u751f\u6c14\u4e86\u597d\u4e48\u3002\u201d</p><p>\u743c\u6069\u6447\u6447\u5934\uff0c\u201c\u6211\u6ca1\u751f\u5979\u7684\u6c14\u3002\u201d</p><p>\u201c\u53ef\u662f\u4f60\u5f53\u65f6\u770b\u8d77\u6765\u771f\u7684\u5f88\u751f\u6c14\u554a\u3002\u201d\u6885\u83f2\u65af\u4eff\u4f5b\u5f88\u7591\u60d1\u5730\u8bf4\u3002</p><p>\u743c\u6069\u7b11\u7740\u4f38\u624b\uff0c\u5728\u5979\u7684\u67d4\u5ae9\u8138\u988a\u4e0a\u8f7b\u8f7b\u634f\u4e86\u4e00\u634f\uff0c\u201c\u5c0f\u4e2b\u5934\uff0c\u522b\u8ddf\u6211\u88c5\u50bb\u3002\u201d</p><p>\u5c11\u5973\u683c\u683c\u5a07\u7b11\uff0c\u201c\u6211\u662f\u771f\u7684\u4e0d\u660e\u767d\u554a\u3002\u201d</p><p>\u201c\u975e\u8981\u6211\u81ea\u5df1\u8bf4\u4e48\uff1f\u201d\u743c\u6069\u7b11\u4e86\u7b11\uff0c\u201c\u6211\u662f\u975e\u5e38\u751f\u6c14\uff0c\u4f46\u4e0d\u662f\u751f\u2018\u5979\u2019\u7684\u6c14\uff0c\u6211\u53ea\u662f\u5728\u751f\u81ea\u5df1\u7684\u6c14\u800c\u5df2\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u751f\u81ea\u5df1\u7684\u6c14\u5462\uff1f\u201d\u5c11\u5973\u8f7b\u58f0\u95ee\u3002</p><p>\u201c\u56e0\u4e3a\u89c9\u5f97\u81ea\u5df1\u5f88\u5dee\u52b2\u3002\u201d</p><p>\u201c\u5dee\u52b2\u5728\u54ea\u91cc\u5462\uff1f\u201d\u5c11\u5973\u53c8\u95ee\u3002</p><p>\u201c\u4f5c\u4e3a\u4e00\u4e2a\u7537\u4eba\uff0c\u4e0d\u80fd\u7ed9\u4e88\u81ea\u5df1\u559c\u6b22\u7684\u4eba\u5e78\u798f\uff0c\u8fd9\u5e94\u8be5\u5c31\u662f\u6700\u5927\u7684\u5dee\u52b2\u4e86\u5427\u3002\u201d</p><p>\u201c\u90a3\u662f\u56e0\u4e3a\u4f60\u60f3\u8981\u7ed9\u4e88\u5e78\u798f\u7684\u4eba\u592a\u591a\u4e86\u3002\u201d\u5c11\u5973\u51b7\u9759\u5730\u8bf4\u3002</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u627f\u8ba4\u4e86\u6885\u83f2\u65af\u7684\u8bc4\u8bba\u3002\u201c\u786e\u5b9e\u5982\u6b64\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u4e0d\u8d2a\u5fc3\uff0c\u800c\u6211\u975e\u5e38\u8d2a\u5fc3\u3002\u201d</p><p>\u6c89\u9ed8\u4e86\u7247\u523b\uff0c\u743c\u6069\u518d\u5ea6\u5f00\u53e3\uff0c\u201c\u827e\u5f25\u8587\uff0c\u6709\u4ef6\u4e8b\u60c5\u6211\u4e00\u76f4\u6ca1\u544a\u8bc9\u4f60\uff0c\u6211\u5df2\u7ecf\u5411\u73ca\u5609\u2026\u2026\u201d</p><p>\u201c\u6c42\u5a5a\u4e86\uff0c\u73ca\u5609\u59d0\u59d0\u4e5f\u7b54\u5e94\u4e86\uff0c\u662f\u5427\uff0c\u201d\u6885\u83f2\u65af\u5fae\u7b11\uff0c\u201c\u6211\u65e9\u5c31\u77e5\u9053\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u6212\u6307\u554a\uff0c\u201d\u5c11\u5973\u63d0\u9192\uff0c\u201c\u73ca\u5609\u59d0\u59d0\u7684\u5de6\u624b\u65e0\u540d\u6307\u4e0a\uff0c\u6234\u7740\u4f60\u90a3\u679a\u6cd5\u672f\u62a4\u7f69\u6212\u6307\u2014\u2014\u7a0d\u6709\u5e38\u8bc6\u7684\u4eba\u5c31\u660e\u767d\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u5427\u3002\u201d</p><p>\u201c\u4e5f\u662f\u554a\uff0c\u201d\u743c\u6069\u81ea\u5632\u4e00\u7b11\uff0c\u201c\u6211\u4ee5\u4e3a\u522b\u4eba\u90fd\u548c\u6211\u4e00\u6837\u8fdf\u949d\u5462\u3002\u201d</p><p>\u201c\u4e0d\u662f\u8fdf\u949d\uff0c\u53ea\u662f\u4e0d\u60f3\u53bb\u9762\u5bf9\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u90a3\u4e48\u73b0\u5728\u4f60\u544a\u8bc9\u6211\u8fd9\u4ef6\u4e8b\uff0c\u662f\u4ec0\u4e48\u610f\u601d\u5462\uff1f\u201d\u5979\u76ef\u7740\u743c\u6069\uff0c\u201c\u63a5\u4e0b\u6765\u662f\u8981\u6211\u505a\u9009\u62e9\u5417\uff1f\u201d</p><p>\u201c\u9009\u62e9\uff1f\u201d</p><p>\u201c\u6216\u8005\uff0c\u7559\u4e0b\u6765\uff0c\u6210\u4e3a\u4f60\u7684\u2018\u59bb\u5b50\u2019\u4e4b\u4e00\uff1b\u6216\u8005\uff0c\u8f6c\u8eab\u79bb\u5f00\u2014\u2014\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u8fd8\u6709\u5176\u4ed6\u9009\u62e9\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u56e0\u4e3a\u6211\u5e76\u4e0d\u662f\u8981\u8ba9\u4f60\u505a\u9009\u62e9\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u6765\u505a\u9009\u62e9\u3002\u201d</p><p>\u201c\u9009\u62e9\u4ec0\u4e48\u5462\uff1f\u201d\u5c11\u5973\u95ee\u3002</p><p>\u201c\u6216\u8005\uff0c\u9009\u62e9\u4f60\uff0c\u653e\u5f03\u5979\u4eec\uff1b\u6216\u8005\uff0c\u653e\u5f03\u4f60\u2014\u2014\u9664\u6b64\u4e4b\u5916\uff0c\u6211\u8fd8\u6709\u5176\u4ed6\u9009\u62e9\u5417\uff1f\u201d</p><p>\u5c11\u5973\u9759\u9759\u5730\u770b\u7740\u4ed6\uff0c\u539f\u672c\u5c16\u9510\u7684\u76ee\u5149\u53d8\u5f97\u67d4\u548c\uff0c\u201c\u5bf9\u4e0d\u8d77\uff0c\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u201c\u6211\u4e5f\u8bd5\u7740\u52aa\u529b\u8fc7\uff0c\u60f3\u8bf4\u670d\u81ea\u5df1\uff0c\u53ef\u4ee5\u63a5\u53d7\u4f60\u8eab\u8fb9\u6709\u522b\u7684\u5973\u5b69\u5b50\uff0c\u53ef\u4ee5\u63a5\u53d7\u548c\u5979\u4eec\u5171\u540c\u5206\u4eab\uff0c\u5c31\u50cf\u548c\u51db\u4e00\u6837\uff0c\u53ef\u662f\u6211\u771f\u7684\u505a\u4e0d\u5230\u3002\u6211\u4e5f\u5728\u5fc3\u91cc\u53cd\u590d\u544a\u8beb\u81ea\u5df1\uff0c\u4e0d\u8981\u63d0\u8fd9\u4ef6\u4e8b\uff0c\u4e0d\u8981\u8c08\u8fd9\u4e2a\u8bdd\u9898\uff0c\u5c31\u5f53\u5b83\u4e0d\u5b58\u5728\uff0c\u5c31\u5f53\u505a\u4e0d\u77e5\u9053\uff0c\u80fd\u8fc7\u4e00\u5929\u7b97\u4e00\u5929\uff0c\u56e0\u4e3a\u6211\u77e5\u9053\u4e00\u65e6\u6311\u660e\uff0c\u5c31\u5fc5\u987b\u8981\u505a\u9009\u62e9\uff0c\u800c\u6211\u65e0\u6cd5\u9884\u6599\u5230\u7ed3\u679c\u4f1a\u662f\u600e\u6837\uff0c\u6240\u4ee5\u6211\u5bb3\u6015\u5b83\u7684\u5230\u6765\uff0c\u201d\u5979\u7d27\u7d27\u62b1\u4f4f\u743c\u6069\uff0c\u201c\u6211\u77e5\u9053\u4f60\u6709\u591a\u4e48\u559c\u6b22\u73ca\u5609\u59d0\u59d0\uff0c\u4e5f\u77e5\u9053\u73ca\u5609\u59d0\u59d0\u5f88\u591a\u4e48\u559c\u6b22\u4f60\uff0c\u6211\u4e0d\u60f3\u903c\u4f60\u505a\u9009\u62e9\uff0c\u4e0d\u60f3\u4f60\u4e3a\u96be\uff0c\u53ef\u7ed3\u679c\u8fd8\u662f\u8fd9\u6837\u2026\u2026\u201d</p><p>\u743c\u6069\u8f7b\u8f7b\u629a\u6478\u7740\u5979\u7684\u91d1\u53d1\u3002</p><p>\u201c\u603b\u6709\u8fd9\u4e00\u5929\uff0c\u6216\u8fdf\u6216\u65e9\u7684\u95ee\u9898\u800c\u5df2\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6628\u65e5\u4e4b\u56e0\uff0c\u4eca\u65e5\u4e4b\u679c\uff0c\u8fd9\u5c31\u662f\u5bbf\u547d\uff0c\u9003\u4e0d\u6389\u7684\u3002\u662f\u6211\u5f04\u51fa\u6765\u7684\u9ebb\u70e6\uff0c\u81ea\u7136\u8fd8\u662f\u6211\u6765\u89e3\u51b3\uff0c\u65e0\u8bba\u7ed3\u679c\u600e\u6837\uff0c\u603b\u8981\u7ed9\u4e2a\u4ea4\u4ee3\u3002\u201d</p><p>\u662f\u554a\uff0c\u597d\u4e5f\u7f62\uff0c\u574f\u4e5f\u7f62\uff0c\u8fdd\u7ea6\u4e5f\u7f62\uff0c\u80cc\u76df\u4e5f\u7f62\uff0c\u8584\u5e78\u4e5f\u7f62\uff0c\u8f9c\u8d1f\u4e5f\u597d\uff0c\u5230\u5934\u6765\uff0c\u7ec8\u7a76\u662f\u8981\u7ed9\u4e2a\u4ea4\u4ee3\u5427\u3002</p><p>\u201c\u4e0d\u8fc7\u51db\u90a3\u4e2a\u5bb6\u4f19\uff0c\u771f\u662f\u8ba9\u4eba\u5934\u75db\u554a\uff0c\u201d\u4ed6\u82e6\u7b11\uff0c\u201c\u4e00\u56de\u6765\u5c31\u7ed9\u6211\u60f9\u51fa\u8fd9\u4e48\u5927\u9ebb\u70e6\u3002\u201d</p><p>\u8fd9\u6b21\u6765\u5854\u745f\u8c37\uff0c\u5176\u5b9e\u743c\u6069\u5c31\u5df2\u7ecf\u6709\u4e86\u5fc3\u7406\u51c6\u5907\uff0c\u77e5\u9053\u5341\u6709\u516b\u4e5d\u4f1a\u51fa\u73b0\u8fd9\u79cd\u5c40\u9762\uff0c\u56e0\u4e3a\u73ca\u5609\u4e5f\u597d\uff0c\u6885\u83f2\u65af\u4e5f\u7f62\uff0c\u8fd9\u4e24\u4eba\u90fd\u592a\u201c\u5f3a\u201d\u4e86\uff0c\u5c31\u50cf\u738b\u5bf9\u738b\uff0c\u4e0d\u7206\u53d1\u6218\u4e89\u90a3\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u6700\u591a\u53ea\u662f\u6682\u65f6\u4f11\u6218\u3002\u800c\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u4ed6\u65e2\u7136\u8d70\u8fd9\u4e00\u8d9f\uff0c\u5c31\u610f\u5473\u7740\u4ed6\u5df2\u7ecf\u4e0d\u6253\u7b97\u518d\u642a\u585e\uff0c\u518d\u542b\u7cca\uff0c\u50cf\u4ee5\u524d\u4e00\u6837\u5f97\u8fc7\u4e14\u8fc7\uff0c\u56e0\u4e3a\u8fd9\u4e9b\u90fd\u5df2\u7ecf\u6ca1\u6709\u610f\u4e49\u3002\u8be5\u6765\u7684\u5230\u5e95\u4f1a\u6765\uff0c\u8be5\u9762\u5bf9\u7684\u8fdf\u65e9\u8981\u9762\u5bf9\uff0c\u8be5\u644a\u724c\u7684\u59cb\u7ec8\u8981\u644a\u724c\uff0c\u8be5\u9009\u62e9\u7684\u6bd5\u7adf\u8981\u9009\u62e9\uff0c\u9003\u907f\u7ec8\u7a76\u4e0d\u662f\u529e\u6cd5\u3002</p><p>\u867d\u7136\u5982\u6b64\uff0c\u4eba\u7ec8\u7a76\u662f\u4eba\uff0c\u4e8b\u5230\u4e34\u5934\u7684\u65f6\u5019\uff0c\u672c\u80fd\u5730\u8fd8\u662f\u4f1a\u9000\u7f29\uff0c\u4f1a\u754f\u60e7\uff0c\u4f1a\u5bb3\u6015\uff0c\u4f1a\u60f3\u7740\u5f97\u8fc7\u4e14\u8fc7\uff0c\u7b49\u7b49\u518d\u8bf4\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4e0d\u4ec5\u743c\u6069\u5982\u6b64\uff0c\u6885\u83f2\u65af\u540c\u6837\u4e5f\u662f\u5982\u6b64\uff0c\u5c31\u50cf\u5927\u4e3b\u6559\u8bf4\u7684\uff0c\u5979\u559c\u6b22\u743c\u6069\uff0c\u7231\u7740\u743c\u6069\uff0c\u5728\u4ed6\u8eab\u4e0a\u83b7\u5f97\u4e86\u4ece\u672a\u5f97\u5230\u8fc7\u7684\u6e29\u6696\u548c\u5feb\u4e50\uff0c\u6240\u4ee5\u5979\u820d\u4e0d\u5f97\u653e\u5f03\uff0c\u66f4\u5bb3\u6015\u5931\u53bb\u3002\u6240\u4ee5\u665a\u9910\u65f6\u51db\u4e00\u65f6\u5931\u8a00\uff0c\u5979\u624d\u4f1a\u56e0\u6b64\u52a8\u6012\u2014\u2014\u5979\u5e76\u4e0d\u662f\u771f\u7684\u751f\u51db\u7684\u6c14\uff0c\u800c\u662f\u56e0\u4e3a\u81ea\u8eab\u7684\u6050\u60e7\uff1b\u56e0\u4e3a\u51db\u7684\u5931\u8a00\uff0c\u5c06\u539f\u672c\u5fc3\u7167\u4e0d\u5ba3\u7684\u4e8b\u5b9e\u6311\u5f00\u5230\u660e\u5904\uff0c\u8ba9\u6885\u83f2\u65af\u4e5f\u597d\uff0c\u73ca\u5609\u4e5f\u597d\uff0c\u743c\u6069\u4e5f\u597d\uff0c\u90fd\u65e0\u6cd5\u518d\u81ea\u6b3a\u6b3a\u4eba\uff0c\u518d\u7ee7\u7eed\u7ef4\u6301\u8fd9\u6837\u81f3\u5c11\u8868\u9762\u548c\u8c10\u7684\u751f\u6d3b\uff0c\u800c\u662f\u8981\u5728\u6ca1\u6709\u5145\u8db3\u51c6\u5907\u548c\u5fc5\u80dc\u628a\u63e1\u7684\u60c5\u51b5\u4e0b\uff0c\u4ed3\u4fc3\u53d1\u52a8\u51b3\u6218\u3002</p><p>\u53ea\u662f\u73b0\u5728\u8bf4\u8fd9\u4e9b\uff0c\u5df2\u7ecf\u6ca1\u6709\u4ec0\u4e48\u610f\u4e49\u4e86\u3002</p><p>\u201c\u7ed9\u6211\u70b9\u65f6\u95f4\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u8f7b\u58f0\u8bf4\uff0c\u201c\u53ef\u4ee5\u5417\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u5c11\u5973\u70b9\u5934\uff0c\u5e76\u6ca1\u6709\u95ee\u5230\u5e95\u662f\u591a\u4e45\uff0c\u5979\u7684\u624b\u6084\u6084\u8d34\u7740\u7537\u4eba\u7684\u8179\u90e8\uff0c\u6ed1\u5230\u817f\u95f4\uff0c\u8f7b\u8f7b\u63e1\u4f4f\u7136\u540e\u4e0a\u4e0b\u5957\u5f04\uff0c\u5f88\u5feb\u8ba9\u5b83\u53d8\u5f97\u81a8\u80c0\u575a\u786c\u3002\u201c\u5b83\u60f3\u8981\u4e86\u5462\uff1f\u201d\u5c11\u5973\u8f7b\u8f7b\u5728\u60c5\u4eba\u8033\u8fb9\u8bf4\uff0c\u5410\u6c14\u5982\u5170\u3002</p><p>\u201c\u8dea\u7740\u3002\u201d\u743c\u6069\u547d\u4ee4\u3002</p><p>\u5c11\u5973\u4e56\u4e56\u5730\u8dea\u4f0f\u5728\u6d74\u7f38\u4e2d\uff0c\u4e0a\u534a\u8eab\u4f0f\u5728\u8fb9\u6cbf\uff0c\u4e3b\u52a8\u5c06\u81c0\u90e8\u9ad8\u9ad8\u7fd8\u8d77\u3002\u743c\u6069\u9ed8\u4e0d\u4f5c\u58f0\u5730\u6765\u5230\u5979\u8eab\u540e\uff0c\u7d2b\u7ea2\u8272\u7684\u6775\u5c16\u6469\u64e6\u7740\u5c11\u5973\u5a07\u5ae9\u7684\u808c\u80a4\uff0c\u62b5\u4e0a\u90a3\u6735\u7cbe\u81f4\u83ca\u82b1\uff0c\u6b63\u5f85\u7834\u95e8\u800c\u5165\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u549a\u3001\u549a\u3001\u549a\u7684\u6572\u95e8\u58f0\u6709\u8282\u594f\u5730\u54cd\u8d77\uff0c\u7d27\u63a5\u7740\u6d74\u5ba4\u7684\u95e8\u88ab\u63a8\u5f00\uff0c\u4e00\u4e2a\u7a88\u7a95\u7684\u8d64\u88f8\u8eab\u5f71\u8d70\u4e86\u8fdb\u6765\u3002</p></article>"}}}]);
