(window.webpackJsonp=window.webpackJsonp||[]).push([[1716],{1739:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u8282 \u5355\u9009\u548c\u5168\u9009",content:"<article><h2>\u7b2c\u56db\u5341\u8282 \u5355\u9009\u548c\u5168\u9009</h2><p>\u201c\u665a\u4e0a\u597d\uff0c\u4e24\u4f4d\uff0c\u201d\u96fe\u6c14\u6726\u80e7\u4e2d\uff0c\u8d64\u88f8\u5973\u5b50\u8d70\u5230\u6d74\u7f38\u8fb9\uff0c\u201c\u6211\u53ef\u4ee5\u52a0\u5165\u5417\uff1f\u201d</p><p>\u201c\u838e\u73de\u514b\uff1f\u201d</p><p>\u8d70\u8fdb\u6765\u7684\u8fd9\u540d\u5973\u5b50\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u9b45\u9b54\u838e\u73de\u514b\uff0c\u5979\u5728\u8fdb\u6765\u4e4b\u524d\u5c31\u65e9\u5df2\u5c06\u5168\u8eab\u8863\u7269\u5c3d\u6570\u892a\u4e0b\uff0c\u8fd1\u4e4e\u5b8c\u7f8e\u7684\u5973\u4f53\u4e00\u4e1d\u4e0d\u6302\u5730\u88f8\u9732\u7740\uff0c\u4e30\u6ee1\u575a\u633a\u7684\u7f8e\u4e73\uff0c\u66f2\u7ebf\u67d4\u7f8e\u7684\u7ea4\u8170\uff0c\u5e73\u5766\u7ed3\u5b9e\u7684\u5c0f\u8179\uff0c\u4fee\u957f\u7b14\u633a\u7684\u7389\u817f\uff0c\u4ee5\u53ca\u5c0f\u8179\u4e0b\u65b9\u90a3\u5757\u9971\u6ee1\u9f13\u8d77\u7684\u4e09\u89d2\u533a\u57df\uff0c\u5149\u6d01\u5a07\u5ae9\u5f97\u4eff\u4f5b\u521d\u751f\u5a74\u513f\uff0c\u6b63\u6beb\u65e0\u4fdd\u7559\u5730\u5c55\u793a\u5728\u7a7a\u6c14\u4e2d\uff0c\u5411\u7537\u4eba\u9a84\u50b2\u5730\u8bc1\u660e\u81ea\u5df1\u7684\u9b45\u529b\u3002</p><p>\u201c\u4f60\u2026\u2026\u4f60\u6765\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u4eff\u4f5b\u6c38\u8fdc\u90fd\u662f\u9547\u9759\u6c89\u7740\u7684\u91d1\u53d1\u5c11\u5973\uff0c\u773c\u4e2d\u96be\u5f97\u5730\u51fa\u73b0\u4e00\u4e1d\u60ca\u60f6\u4e4b\u8272\uff0c\u6655\u7ea2\u5982\u706b\u7684\u4fcf\u8138\u4e0a\uff0c\u53c8\u5206\u660e\u900f\u7740\u51e0\u5206\u7f9e\u607c\u2014\u2014\u56e0\u4e3a\u5979\u6b64\u65f6\u6b63\u50cf\u4e00\u53ea\u7f8e\u4e3d\u6bcd\u72ac\u822c\uff0c\u6e29\u987a\u4e56\u5de7\u8dea\u4f0f\u7740\uff0c\u81c0\u90e8\u9ad8\u9ad8\u7fd8\u8d77\uff0c\u7b49\u5f85\u7740\u8eab\u540e\u7537\u4eba\u7684\u201c\u5ba0\u5e78\u201d\u3002\u7531\u4e8e\u90aa\u795e\u590d\u6d3b\u7684\u5a01\u80c1\uff0c\u743c\u6069\u548c\u5979\u6b22\u597d\u65f6\u4ece\u6765\u4e0d\u6562\u8d70\u6b63\u9053\uff0c\u53ea\u80fd\u73a9\u540e\u5ead\u82b1\uff0c\u8fd9\u79cd\u59ff\u52bf\u6700\u4e3a\u5bb9\u6613\u7701\u529b\uff0c\u81ea\u7136\u4e5f\u7528\u5f97\u6700\u591a\u3002\u4f46\u5373\u4fbf\u5bf9\u4e8e\u666e\u901a\u5973\u5b69\u5b50\u6765\u8bf4\uff0c\u80cc\u5165\u5f0f\u672c\u5c31\u5e26\u6709\u975e\u5e38\u5f3a\u70c8\u7684\u5c48\u8fb1\u610f\u5473\uff0c\u4f55\u51b5\u662f\u6027\u683c\u6781\u5ea6\u521a\u5f3a\u7684\u6885\u83f2\u65af\u3002\u53ea\u6709\u5728\u5fc3\u7231\u7684\u4eba\u2014\u2014\u6216\u8005\u662f\u51db\u8fd9\u6837\u7684\u95fa\u4e2d\u5bc6\u53cb\u2014\u2014\u9762\u524d\uff0c\u5979\u624d\u80fd\u52c9\u5f3a\u653e\u4e0b\u77dc\u6301\u548c\u9ad8\u50b2\uff0c\u4e56\u4e56\u670d\u4ece\u3002\u73b0\u5728\u5374\u88ab\u838e\u73de\u514b\u770b\u89c1\uff0c\u987f\u65f6\u65e2\u7f9e\u4e14\u607c\uff0c\u8138\u4e0a\u4e00\u9635\u9635\u706b\u8fa3\u8fa3\u5730\u53d1\u70e7\uff0c\u4e0b\u610f\u8bc6\u5730\u53cc\u624b\u63a9\u5728\u80f8\u524d\uff0c\u8eab\u4f53\u4fbf\u5f80\u540e\u9000\u3002</p><p>\u7136\u800c\u5979\u5fd8\u4e86\u4e00\u4ef6\u4e8b\uff1a\u743c\u6069\u6b64\u65f6\u5c31\u5728\u8eab\u540e\uff0c\u90a3\u6839\u575a\u633a\u5982\u94c1\u7684\u72f0\u72de\u5de8\u7269\uff0c\u6b63\u9876\u5728\u5979\u7684\u4e30\u8174\u81c0\u80a1\u95f4\uff0c\u7d27\u7d27\u5730\u62b5\u7740\u83ca\u854a\u5165\u53e3\u3002\u6885\u83f2\u65af\u60f6\u6025\u4e4b\u4e0b\uff0c\u4e0d\u5047\u601d\u7d22\u5730\u5f80\u540e\u4e00\u9000\uff0c\u5c31\u7b49\u4e8e\u662f\u81ea\u5df1\u7528\u529b\u649e\u4e86\u4e0a\u6765\u3002\u4e8b\u51fa\u7a81\u7136\uff0c\u743c\u6069\u8fd8\u538b\u6839\u6ca1\u6765\u5f97\u53ca\u53cd\u5e94\uff0c\u4fbf\u542c\u5f97\u201c\u5657\u201d\u5730\u4e00\u58f0\u6781\u8f7b\u5fae\u54cd\uff0c\u5df2\u7ecf\u6709\u56db\u5206\u4e4b\u4e00\u5de6\u53f3\u51f6\u72e0\u5730\u95ef\u5165\u4e86\u5c11\u5973\u7684\u5a07\u5ae9\u80a0\u9053\u4e2d\u3002</p><p>\u75bc\u2026\u2026</p><p>\u7a81\u5982\u5176\u6765\u7684\u88ad\u51fb\uff0c\u8ba9\u5c11\u5973\u75bc\u5f97\u51e0\u4e4e\u7a92\u606f\uff0c\u773c\u524d\u4e00\u9635\u9635\u5730\u91d1\u661f\u4e71\u5192\uff0c\u4eff\u4f5b\u8fc7\u4e86\u597d\u534a\u5929\u2014\u2014\u5176\u5b9e\u53ea\u6709\u51e0\u79d2\u79cd\u2014\u2014\u624d\u52c9\u5f3a\u56de\u8fc7\u795e\u3002\u5979\u6b7b\u6b7b\u54ac\u7740\u5634\u5507\uff0c\u6293\u7740\u6d74\u7f38\u8fb9\u6cbf\u7684\u53cc\u624b\u6307\u5173\u8282\u5df2\u7ecf\u56e0\u4e3a\u6781\u5ea6\u7528\u529b\u800c\u53d8\u5f97\u82cd\u767d\uff0c\u5a07\u8eaf\u90fd\u5728\u60c5\u4e0d\u81ea\u7981\u5730\u5fae\u5fae\u98a4\u6296\uff0c\u773c\u6cea\u5df2\u7ecf\u4e0d\u7531\u81ea\u4e3b\u5730\u6d41\u4e86\u51fa\u6765\u3002\u5df2\u7ecf\u6709\u8fc7\u65e0\u6570\u6b21\u540e\u5ead\u7ecf\u9a8c\uff0c\u88ab\u743c\u6069\u65e5\u591c\u8e42\u8e8f\uff0c\u5c11\u5973\u7684\u83ca\u7a74\u6e10\u6e10\u53d8\u5f97\u5a07\u5ae9\u67d4\u8f6f\uff0c\u5145\u6ee1\u5f39\u6027\uff0c\u4e0d\u518d\u50cf\u6700\u521d\u90a3\u6837\u7684\u65e0\u6bd4\u7d27\u81f4\uff0c\u4f46\u76f8\u5bf9\u4e8e\u8eab\u540e\u7537\u4eba\u7684\u5c3a\u5bf8\u6765\u8bf4\uff0c\u5b83\u7ec8\u7a76\u8fd8\u662f\u592a\u201c\u72ed\u7a84\u201d\u4e86\u3002\u5e73\u5e38\u6b22\u597d\uff0c\u743c\u6069\u4e00\u5f00\u59cb\u8fdb\u5165\u65f6\u90fd\u662f\u5c0f\u5fc3\u7ffc\u7ffc\uff0c\u4e00\u70b9\u70b9\u6df1\u5165\u5230\u5e95\uff0c\u81f3\u5c11\u8981\u82b1\u534a\u4e2a\u5c0f\u65f6\u8ba9\u5979\u6162\u6162\u9002\u5e94\uff0c\u7136\u540e\u624d\u6562\u8086\u610f\u9700\u7d22\uff0c\u4eab\u53d7\u54c1\u5c1d\uff0c\u54ea\u6562\u50cf\u8fd9\u6837\u86ee\u6a2a\u7c97\u66b4\uff0c\u90a3\u975e\u628a\u5973\u5b69\u5f04\u4f24\u4e0d\u53ef\u3002</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u6885\u83f2\u65af\u5df2\u7ecf\u88ab\u5f04\u4f24\u4e86\u3002\u7537\u6027\u60ca\u4eba\u7684\u7c97\u58ee\uff0c\u6beb\u65e0\u9884\u5146\u5730\u7834\u95e8\u800c\u5165\uff0c\u4e0d\u7531\u5206\u8bf4\u5730\u6df1\u9677\u5728\u5979\u4f53\u5185\uff0c\u90a3\u79cd\u65e0\u6cd5\u60f3\u8c61\u7684\u5de8\u5927\uff0c\u4eff\u4f5b\u8981\u5c06\u5979\u7684\u4e0b\u4f53\u6491\u88c2\u5f00\u6765\uff0c\u706b\u8fa3\u8fa3\u7684\u75bc\u75db\u6f6e\u6c34\u822c\u4ece\u4e24\u4eba\u8eab\u4f53\u7684\u4ea4\u5408\u5904\u4f20\u6765\uff0c\u51b2\u51fb\u7740\u5927\u8111\u610f\u8bc6\uff0c\u8ba9\u5979\u77e5\u9053\u81ea\u5df1\u7684\u5a07\u5ae9\u80a0\u58c1\u4e0a\uff0c\u4e00\u5b9a\u5df2\u7ecf\u88ab\u6495\u5f00\u4e86\u5343\u767e\u4e2a\u7ec6\u5c0f\u7684\u4f24\u53e3\u3002\u5979\u4e0d\u6562\u518d\u6709\u4e1d\u6beb\u52a8\u5f39\uff0c\u56e0\u4e3a\u90a3\u4f1a\u8ba9\u5979\u66f4\u52a0\u75db\u695a\uff0c\u53ea\u80fd\u5c48\u8fb1\u5730\u7ef4\u6301\u7740\u73b0\u5728\u7684\u59ff\u52bf\uff0c\u6012\u89c6\u7740\u7ad9\u5728\u9762\u524d\u7684\u9b45\u9b54\u3002</p><p>\u7136\u540e\u9b45\u9b54\u8e72\u4e0b\u8eab\uff0c\u683c\u683c\u7b11\u7740\uff0c\u5728\u5979\u88ab\u6c57\u73e0\u6e7f\u900f\u7684\u9161\u7ea2\u8138\u988a\u4e0a\u8f7b\u8f7b\u4eb2\u4e86\u4e00\u53e3\u3002</p><p>\u201c\u8fd9\u6837\u5b50\u771f\u8bf1\u4eba\u5462\uff0c\u827e\u5f25\u8587\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u90fd\u5fcd\u4e0d\u4f4f\u52a8\u5fc3\u4e86\u3002\u201d</p><p>\u201c\u4f60\u6765\u5e72\u561b\uff1f\u201d\u743c\u6069\u4e0d\u5feb\u5730\u95ee\u3002</p><p>\u6b63\u51c6\u5907\u548c\u5973\u53cb\u6b22\u597d\u65f6\u88ab\u6253\u6270\uff0c\u6362\u4e86\u8c01\u90fd\u4e0d\u4f1a\u5fc3\u60c5\u597d\u3002\u800c\u4e14\u4f5c\u4e3a\u653b\u51fb\u7684\u4e00\u65b9\uff0c\u4ed6\u5c3d\u7ba1\u4e0d\u50cf\u6885\u83f2\u65af\u90a3\u6837\u75db\u695a\u96be\u5f53\uff0c\u4f46\u611f\u89c9\u5176\u5b9e\u4e5f\u5e76\u4e0d\u662f\u5f88\u597d\u3002\u5c11\u5973\u7684\u83ca\u6d1e\u8fd8\u662f\u5b9e\u5728\u592a\u7d27\u8feb\u4e86\uff0c\u8fd9\u6837\u7c97\u66b4\u5730\u95ef\u5165\uff0c\u81ea\u5df1\u90fd\u6709\u4e9b\u5403\u4e0d\u6d88\u3002\u201c\u6709\u4e8b\u60c5\u5f85\u4f1a\u518d\u8bf4\uff0c\u201d\u4ed6\u4f38\u624b\u63e1\u4f4f\u6885\u83f2\u65af\u7684\u8170\u80a2\uff0c\u5c11\u5973\u5df2\u7ecf\u6ca1\u6709\u529b\u6c14\u652f\u6491\u8eab\u4f53\uff0c\u8f6f\u7ef5\u7ef5\u5730\u762b\u5012\uff0c\u201c\u6ca1\u770b\u5230\u6211\u6b63\u5fd9\u7740\u4e48\u3002\u201d</p><p>\u201c\u6211\u6765\u6d17\u6fa1\u554a\uff0c\u201d\u838e\u73de\u514b\u7406\u6240\u5f53\u7136\u5730\u56de\u7b54\uff0c\u201c\u5426\u5219\u8fdb\u6d74\u5ba4\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u80e1\u8bf4\uff0c\u201d\u6885\u83f2\u65af\u5fcd\u4e0d\u4f4f\u9a73\u65a5\uff0c\u201c\u697c\u4e0b\u660e\u660e\u5c31\u6709\u6d74\u5ba4\uff0c\u5e72\u561b\u975e\u8981\u8dd1\u5230\u697c\u4e0a\u6765\uff1f\u201d</p><p>\u201c\u54ce\u5440\u5440\uff0c\u88ab\u4f60\u770b\u7a7f\u4e86\u5462\uff0c\u771f\u4e0d\u597d\u610f\u601d\uff0c\u201d\u838e\u73de\u514b\u683c\u683c\u5a07\u7b11\uff0c\u773c\u6ce2\u6d41\u8f6c\uff0c\u5a9a\u60d1\u65e0\u9650\uff0c\u201c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u5c31\u8001\u5b9e\u627f\u8ba4\u5427\uff0c\u5176\u5b9e\u6211\u662f\u548c\u67d0\u4f4d\u5c0f\u59d1\u5a18\u4e00\u6837\uff0c\u60f3\u548c\u60c5\u4eba\u505a\u7231\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u838e\u73de\u514b\u7684\u8bdd\uff0c\u8868\u9762\u4e0a\u542c\u6ca1\u4ec0\u4e48\uff0c\u4ed4\u7ec6\u63a8\u6572\u7684\u8bdd\u5374\u53e6\u6709\u610f\u5473\u3002\u5979\u4e00\u76f4\u90fd\u662f\u79f0\u547c\u743c\u6069\u4e3a\u201c\u4e3b\u4eba\u201d\uff0c\u65b9\u624d\u5374\u8bf4\u201c\u60c5\u4eba\u201d\uff0c\u8fd9\u5176\u4e2d\u4fbf\u5927\u6709\u8bb2\u7a76\u3002\u5f53\u65e5\u5728\u6df1\u6e0a\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u53d7\u5230\u4f4d\u9762\u529b\u91cf\u7684\u538b\u5236\uff0c\u865a\u5f31\u4e0d\u582a\uff0c\u751a\u81f3\u6709\u751f\u547d\u5371\u9669\uff0c\u4fbf\u5728\u6b64\u65f6\u6070\u597d\u9047\u5230\u838e\u73de\u514b\uff0c\u743c\u6069\u4e3a\u4e86\u6551\u6885\u83f2\u65af\u548c\u540c\u4f34\uff0c\u548c\u9b45\u9b54\u7b7e\u8ba2\u4e86\u771f\u540d\u5951\u7ea6\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u838e\u73de\u514b\u8fd8\u9644\u52a0\u4e86\u4e00\u4e2a\u6761\u4ef6\uff0c\u4fbf\u662f\u8981\u548c\u6885\u83f2\u65af\u4e00\u6837\uff0c\u6210\u4e3a\u743c\u6069\u7684\u60c5\u4eba\u3002</p><p>\u5728\u5f53\u65f6\u7684\u60c5\u5f62\u4e0b\uff0c\u6885\u83f2\u65af\u522b\u65e0\u9009\u62e9\uff0c\u53ea\u80fd\u540c\u610f\u8fd9\u4e2a\u6761\u4ef6\uff0c\u968f\u540e\u838e\u73de\u514b\u4e5f\u786e\u5b9e\u5c65\u884c\u627f\u8bfa\uff0c\u5c06\u4ed6\u4eec\u5b89\u5168\u5e26\u5230\u65ad\u57df\u9547\uff0c\u53ef\u4ee5\u8bf4\u662f\u633d\u6551\u4e86\u743c\u6069\u4e00\u884c\u4eba\u7684\u6027\u547d\u3002\u6885\u83f2\u65af\u7684\u5904\u4e8b\u51c6\u5219\uff0c\u4e0d\u63a5\u53d7\u8981\u631f\uff0c\u4f46\u627f\u8ba4\u516c\u5e73\u4ea4\u6613\uff0c\u534f\u8bae\u65e2\u7136\u5df2\u7ecf\u8fbe\u6210\uff0c\u5c31\u4e0d\u5c51\u4e8e\u518d\u800d\u8d56\u53cd\u6094\u3002\u4f5c\u4e3a\u4e00\u4e2a\u8fc7\u8def\u4eba\uff0c\u751a\u81f3\u662f\u6614\u65e5\u7684\u4ec7\u654c\uff0c\u838e\u73de\u514b\u5e76\u6ca1\u6709\u6551\u52a9\u743c\u6069\u548c\u6885\u83f2\u65af\u7b49\u4eba\u7684\u5929\u7136\u6216\u8005\u6cd5\u5b9a\u4e49\u52a1\uff0c\u65e2\u7136\u6ca1\u6709\u4e49\u52a1\uff0c\u90a3\u4e48\u5979\u505a\u4e86\uff0c\u8fd9\u5c31\u662f\u4e00\u79cd\u4ed8\u51fa\u3002\u65e2\u7136\u5979\u5df2\u7ecf\u4ed8\u51fa\uff0c\u4fbf\u6709\u6743\u529b\u6839\u636e\u534f\u8bae\u8981\u6c42\u62a5\u916c\uff0c\u6240\u4ee5\u6885\u83f2\u65af\u5c31\u7b97\u518d\u4e0d\u60c5\u613f\uff0c\u4e5f\u53ea\u80fd\u9ed8\u8ba4\u3002</p><p>\u4e00\u76f4\u4ee5\u6765\uff0c\u838e\u73de\u514b\u90fd\u8868\u73b0\u5f97\u5f88\u201c\u81ea\u89c9\u201d\uff0c\u4ece\u672a\u771f\u6b63\u5730\u4e3b\u5f20\u8fc7\u81ea\u5df1\u8eab\u4e3a\u60c5\u4eba\u7684\u6743\u5229\uff0c\u800c\u662f\u4e3b\u52a8\u5730\u9000\u5c45\u5230\u52a9\u624b\u7684\u4f4d\u7f6e\u3002\u4e45\u800c\u4e45\u4e4b\uff0c\u5927\u5bb6\u90fd\u5df2\u7ecf\u4e60\u60ef\u4e86\u8fd9\u79cd\u72b6\u6001\uff0c\u5305\u62ec\u743c\u6069\u548c\u6885\u83f2\u65af\u8fd9\u4e24\u4e2a\u5f53\u4e8b\u4eba\u90fd\u662f\u5982\u6b64\u3002\u7136\u800c\u6743\u5229\u8fd9\u79cd\u4e1c\u897f\uff0c\u5e76\u4e0d\u4f1a\u56e0\u4e3a\u4e0d\u7528\u5c31\u81ea\u52a8\u6d88\u706d\uff0c\u8fd9\u4e2a\u4e16\u754c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u8bc9\u8bbc\u65f6\u6548\u7684\u8bf4\u6cd5\uff0c\u5982\u4eca\u838e\u73de\u514b\u5f53\u771f\u4ee5\u60c5\u4eba\u7684\u8eab\u4efd\u800c\u6765\uff0c\u5927\u5927\u65b9\u65b9\u63d0\u51fa\u8981\u6c42\uff0c\u6885\u83f2\u65af\u4e00\u65f6\u6014\u4f4f\uff0c\u8fd8\u771f\u6ca1\u529e\u6cd5\u53cd\u5bf9\u3002</p><p>\u201c\u597d\u50cf\u4e0d\u592a\u5de7\u5462\uff0c\u6070\u597d\u8d76\u5728\u4e86\u4e00\u8d77\uff0c\u201d\u9b45\u9b54\u7b11\u563b\u563b\u5730\u8bf4\uff0c\u201c\u4e0d\u8fc7\u65e0\u6240\u8c13\u4e86\uff0c\u53cd\u6b63\u6211\u4e0d\u5403\u918b\u7684\uff0c\u4e0d\u4f1a\u50cf\u67d0\u4eba\u90a3\u6837\u8981\u6c42\u72ec\u5360\u3002\u6240\u4ee5\u4e0d\u7528\u62c5\u5fc3\uff0c\u827e\u5f25\u8587\uff0c\u53ea\u8981\u4f60\u613f\u610f\uff0c\u5c3d\u53ef\u4ee5\u7559\u4e0b\u6765\uff0c\u6211\u4eec\u4e00\u8d77\u5bf9\u4ed8\u8fd9\u5bb6\u4f19\u5c31\u662f\uff1b\u5f53\u7136\u5982\u679c\u4f60\u5bb3\u7f9e\uff0c\u6211\u4e5f\u6ca1\u4ec0\u4e48\u610f\u89c1\uff0c\u90a3\u5c31\u795d\u4f60\u665a\u5b89\u4e86\u3002\u653e\u5fc3\u597d\u4e86\uff0c\u6211\u4eec\u4f1a\u6ce8\u610f\u7684\uff0c\u4e0d\u4f1a\u5f04\u51fa\u592a\u5927\u52a8\u9759\u5435\u5230\u4f60\u3002\u201d</p><p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u73b0\u5728\u6885\u83f2\u65af\u9762\u4e34\u4e24\u79cd\u9009\u62e9\uff1a\u6216\u8005\uff0c\u7559\u4e0b\u6765\u548c\u838e\u73de\u514b\u4e00\u8d77\uff1b\u6216\u8005\uff0c\u81ea\u5df1\u79bb\u5f00\u3002\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u4ed6\u5012\u8fd8\u771f\u6709\u4e9b\u671f\u671b\u524d\u8005\uff0c\u6885\u83f2\u65af\u52a0\u838e\u73de\u514b\uff0c\u8fd9\u79cd\u53cc\u98de\u7ec4\u5408\u8fd8\u6ca1\u5c1d\u8bd5\u8fc7\uff0c\u800c\u4e14\u8981\u8bf4\u8d77\u6765\uff0c\u540c\u4e3a\u5df4\u5c14\u5b50\u55e3\uff0c\u5979\u4eec\u5176\u5b9e\u8fd8\u662f\u540c\u7236\u5f02\u6bcd\u7684\u59d0\u59b9\u5462\u3002\u53ef\u60dc\u4ed6\u77e5\u9053\uff0c\u6885\u83f2\u65af\u7684\u9009\u62e9\u80af\u5b9a\u662f\u540e\u8005\u3002</p><p>\u6885\u83f2\u65af\u6c89\u7740\u8138\uff0c\u8d1d\u9f7f\u7d27\u54ac\u5634\u5507\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002\u5979\u7f13\u7f13\u547c\u5438\u7740\uff0c\u6062\u590d\u4e86\u70b9\u529b\u6c14\uff0c\u7136\u540e\u4e00\u70b9\u70b9\u5730\u79fb\u52a8\u8eab\u4f53\uff0c\u8ba9\u81ea\u5df1\u548c\u743c\u6069\u201c\u8131\u79bb\u201d\u5f00\u6765\u3002\u51e0\u7f15\u9c9c\u8273\u7684\u8840\u4e1d\u4ece\u5979\u7684\u817f\u80a1\u95f4\u6ea2\u51fa\uff0c\u6563\u6eb6\u5728\u6c34\u4e2d\uff0c\u8f6c\u773c\u6d88\u5931\u4e0d\u89c1\u3002\u5c11\u5973\u52c9\u5f3a\u7ad9\u8d77\u8eab\u6765\uff0c\u8de8\u51fa\u6d74\u7f38\uff0c\u4ece\u65c1\u8fb9\u8863\u67b6\u4e0a\u62ff\u8d77\u6d74\u888d\u62ab\u4e0a\uff0c\u201c\u660e\u5929\u89c1\u3002\u201d\u5979\u8f7b\u58f0\u5bf9\u743c\u6069\u8bf4\uff0c\u7136\u540e\u8fc8\u7740\u6709\u4e9b\u5f02\u6837\u7684\u6b65\u4f10\u8d70\u51fa\u6d74\u5ba4\u3002</p><p>\u743c\u6069\u5410\u4e86\u53e3\u6c14\uff0c\u61d2\u61d2\u5730\u8eba\u56de\u6d74\u7f38\u4e2d\uff0c\u5934\u9760\u5728\u58c1\u4e0a\uff0c\u201c\u8bf4\u5427\uff0c\u201d\u4ed6\u95ee\u9b45\u9b54\uff0c\u201c\u5230\u5e95\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u6211\u5df2\u7ecf\u8bf4\u4e86\u554a\uff0c\u201d\u838e\u73de\u514b\u4e00\u8138\u65e0\u8f9c\uff0c\u201c\u60f3\u548c\u4f60\u505a\u7231\u4e86\u3002\u201d</p><p>\u201c\u8bf4\u5b9e\u8bdd\uff0c\u201d\u743c\u6069\u6253\u65ad\uff0c\u201c\u522b\u8ddf\u6211\u7ed5\u5708\u5b50\u3002\u201d</p><p>\u201c\u8fd9\u5c31\u662f\u5b9e\u8bdd\u5440\uff0c\u201d\u9b45\u9b54\u5a07\u7b11\uff0c\u201c\u53ea\u4e0d\u8fc7\u4e0d\u662f\u5168\u90e8\u800c\u5df2\u3002\u201d</p><p>\u201c\u90a3\u5269\u4e0b\u7684\u90e8\u5206\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u561b\uff0c\u5f85\u4f1a\u518d\u544a\u8bc9\u4f60\uff0c\u201d\u5979\u7728\u4e86\u7728\u6c34\u6c6a\u6c6a\u7684\u773c\u775b\uff0c\u201c\u4e0d\u8fc7\u6211\u53ef\u4ee5\u4fdd\u8bc1\uff0c\u4f60\u4e00\u5b9a\u4f1a\u559c\u6b22\u7684\u3002\u201d</p><p>\u201c\u662f\u4e48\u3002\u201d</p><p>\u743c\u6069\u5e76\u6ca1\u6709\u628a\u9b45\u9b54\u7684\u8bdd\u592a\u5f53\u56de\u4e8b\uff0c\u4ed6\u6b64\u65f6\u60c5\u7eea\u4e0d\u9ad8\uff0c\u597d\u5728\u6b32\u671b\u8fd9\u79cd\u4e1c\u897f\uff0c\u66f4\u591a\u53d6\u51b3\u4e8e\u751f\u7406\u800c\u975e\u5fc3\u7406\uff0c\u6240\u4ee5\u4ed6\u7684\u80ef\u4e0b\u4f9d\u65e7\u662f\u4e00\u67f1\u64ce\u5929\uff0c\u6b63\u6740\u6c14\u817e\u817e\u5730\u77d7\u7acb\u7740\uff0c\u521a\u624d\u88ab\u6885\u83f2\u65af\u6311\u8d77\u7684\u6b32\u706b\u6b63\u8feb\u5207\u5730\u60f3\u8981\u53d1\u6cc4\u3002\u838e\u73de\u514b\u683c\u683c\u7b11\u7740\uff0c\u8d70\u8fdb\u6d74\u7f38\uff0c\u5206\u5f00\u4e00\u53cc\u957f\u817f\uff0c\u8de8\u5728\u7537\u4eba\u8170\u4e0a\uff0c\u8dea\u7740\u6162\u6162\u653e\u4f4e\u8eab\u4f53\uff0c\u5bf9\u51c6\u4f4d\u7f6e\uff0c\u4e00\u70b9\u70b9\u5730\u5750\u4e0b\u53bb\u3002</p><p>\u201c\u8fdb\u3001\u8fdb\u6765\u4e86\u2026\u2026\u597d\u80c0\u2026\u2026\u201d\u5979\u674f\u773c\u534a\u7741\u534a\u95ed\uff0c\u4f4e\u4f4e\u547b\u541f\uff0c\u58f0\u97f3\u65e2\u5a07\u4e14\u5a9a\uff0c\u8138\u4e0a\u795e\u60c5\u4f3c\u559c\u4f3c\u6028\uff0c\u4f3c\u75db\u4f3c\u7f8e\uff0c\u8bf4\u4e0d\u51fa\u7684\u8bf1\u4eba\uff0c\u201c\u600e\u4e48\u8fd9\u4e48\u5927\u2026\u2026\u53d7\u4e0d\u4e86\u2026\u2026\u201d</p><p>\u743c\u6069\u60a0\u95f2\u5730\u53cc\u624b\u6795\u5728\u8111\u540e\uff0c\u6b23\u8d4f\u7740\u8eab\u4e0a\u7f8e\u4eba\u513f\u7684\u5a9a\u6001\uff0c\u4ee5\u53ca\u5979\u90a3\u5b8c\u7f8e\u65e0\u6687\u7684\u80f4\u4f53\u3002\u8bba\u5bb9\u8c8c\u7684\u8bdd\uff0c\u838e\u73de\u514b\u4e0d\u7b97\u7279\u522b\u51fa\u8272\uff0c\u4f46\u8981\u770b\u8eab\u6750\u7684\u8bdd\uff0c\u90a3\u5c31\u7edd\u5bf9\u662f\u65e0\u53ef\u6311\u5254\u4e86\u3002\u8fd9\u5ea7\u623f\u5b50\u91cc\u73b0\u5728\u6709\u516d\u4f4d\u5973\u6027\uff0c\u9664\u6389\u5e7d\u7075\u3001\u732b\u5973\u548c\u838e\u73de\u514b\u81ea\u5df1\u5916\uff0c\u5269\u4e0b\u7684\u4e09\u4f4d\u90fd\u662f\u8d85\u4e00\u6d41\u7684\u7f8e\u4eba\uff0c\u4f46\u76f8\u6bd4\u8f83\u8d77\u6765\uff0c\u51db\u4e2a\u5934\u7a0d\u77ee\uff0c\u6885\u83f2\u65af\u80f8\u90e8\u4e0d\u591f\u5927\uff0c\u73ca\u5609\u7f8e\u5219\u7f8e\u77e3\uff0c\u7ec8\u7a76\u4e0d\u662f\u838e\u73de\u514b\u90a3\u6837\u7684\u9ad8\u5f3a\u6b66\u8005\uff0c\u5343\u9524\u767e\u70bc\u7684\u67d4\u8f6f\u5a07\u8eaf\u4e2d\uff0c\u81ea\u6709\u4e00\u79cd\u9690\u7136\u900f\u51fa\u7684\u575a\u97e7\u5f20\u529b\uff0c\u4ee5\u53ca\u90a3\u79cd\u591a\u5e74\u6740\u624b\u751f\u6daf\u6240\u5e26\u6765\u7684\u950b\u5229\u611f\uff0c\u8eab\u4f53\u7684\u6bcf\u4e2a\u90e8\u4f4d\u90fd\u4eff\u4f5b\u6f5c\u4f0f\u7740\u6740\u673a\uff0c\u968f\u65f6\u4f1a\u6709\u4e00\u628a\u6697\u5203\u6216\u8005\u6dec\u6bd2\u5315\u9996\u523a\u51fa\u6765\u4f3c\u7684\uff0c\u8fd9\u79cd\u5371\u9669\u7684\u60f3\u8c61\uff0c\u53cd\u800c\u66f4\u8ba9\u5979\u5e73\u6dfb\u4e86\u51e0\u5206\u65e0\u6cd5\u63cf\u8ff0\u7684\u9b45\u60d1\u3002</p><p>\u5e73\u5fc3\u800c\u8bba\uff0c\u548c\u838e\u73de\u514b\u505a\u7231\u786e\u5b9e\u662f\u4e00\u4ef6\u4ee4\u4eba\u6109\u5feb\u7684\u4eab\u53d7\uff0c\u4f5c\u4e3a\u4eba\u7c7b\u65f6\u7684\u9605\u5386\uff0c\u4ee4\u5979\u62e5\u6709\u4e30\u5bcc\u65e0\u6bd4\u7684\u6b22\u597d\u7ecf\u9a8c\uff0c\u6df1\u77e5\u5982\u4f55\u8fd0\u7528\u81ea\u5df1\u7684\u8eab\u4f53\uff0c\u66f2\u610f\u9022\u8fce\uff0c\u6700\u5927\u7a0b\u5ea6\u5730\u53d6\u60a6\u7537\u6027\uff0c\u800c\u4e14\u4e00\u5207\u90fd\u53ef\u4ee5\u7531\u5979\u6765\u4e3b\u52a8\uff0c\u6839\u672c\u65e0\u9700\u7537\u4eba\u8d39\u534a\u70b9\u529b\u6c14\uff1b\u800c\u53d8\u6210\u9b45\u9b54\u540e\u83b7\u5f97\u7684\u65b0\u8eab\u4f53\uff0c\u5374\u53c8\u5728\u6210\u719f\u6027\u611f\u4e2d\u8fd8\u4fdd\u7559\u7740\u9752\u6da9\uff0c\u56e0\u4e3a\u6bd5\u7adf\u6ca1\u6709\u88ab\u201c\u7528\u201d\u8fc7\u591a\u5c11\u6b21\u3002\u8fd9\u4e24\u79cd\u660e\u660e\u662f\u77db\u76fe\u7684\u6df7\u5408\uff0c\u8ba9\u5979\u6210\u4e3a\u6240\u6709\u7537\u6027\u6240\u68a6\u5bd0\u4ee5\u6c42\u7684\u5e8a\u4e0a\u5999\u7269\uff0c\u743c\u6069\u540c\u6837\u4e5f\u65e0\u6cd5\u62b5\u6321\uff0c\u4e0d\u5230\u534a\u5c0f\u65f6\uff0c\u4ed6\u4fbf\u5728\u838e\u73de\u514b\u7684\u5a07\u5ae9\u871c\u81a3\u91cc\u5267\u70c8\u5730\u55b7\u53d1\u51fa\u6765\u3002</p><p>\u201c\u5514\u2026\u2026\u201d</p><p>\u51e0\u4e4e\u662f\u548c\u743c\u6069\u540c\u65f6\uff0c\u9b45\u9b54\u4e5f\u88ab\u63a8\u4e0a\u4e86\u9ad8\u6f6e\uff0c\u5979\u7684\u8eab\u4f53\u5267\u70c8\u98a4\u6296\u7740\uff0c\u8db3\u8db3\u6301\u7eed\u4e86\u5341\u51e0\u79d2\u949f\uff0c\u65b9\u624d\u8f6f\u8f6f\u762b\u5012\uff0c\u8db4\u4f0f\u5728\u743c\u6069\u80f8\u53e3\u3002\u4e24\u4eba\u9759\u9759\u5730\u5598\u606f\u7740\uff0c\u4eab\u53d7\u7740\u7f8e\u5999\u7684\u4f59\u97f5\uff0c\u611f\u53d7\u5f7c\u6b64\u547c\u5438\u3002\u8fc7\u4e86\u5f88\u4e45\uff0c\u838e\u73de\u514b\u8f7b\u8f7b\u5f00\u53e3\u3002</p><p>\u201c\u4e3b\u4eba\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u4f60\u4eca\u5929\u7684\u5fc3\u60c5\uff0c\u597d\u50cf\u4e0d\u592a\u597d\u554a\u3002\u201d</p><p>\u201c\u6709\u4e48\uff1f\u201d</p><p>\u201c\u6709\u554a\uff0c\u4f60\u6bd4\u5e73\u65f6\u5c04\u5f97\u5feb\u591a\u4e86\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u660e\u663e\u7684\u6ce8\u610f\u529b\u4e0d\u96c6\u4e2d\uff0c\u4e00\u526f\u5fc3\u4e0d\u5728\u7109\u7684\u6837\u5b50\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u201c\u90a3\u4e48\uff0c\u662f\u4e3a\u4ec0\u4e48\u4e8b\u60c5\u5462\uff1f\u201d\u9b45\u9b54\u95ee\uff0c\u8c03\u76ae\u5730\u5410\u51fa\u820c\u5c16\uff0c\u5728\u743c\u6069\u7684\u80f8\u53e3\u8f7b\u8f7b\u6311\u9017\uff0c\u201c\u8bf4\u7ed9\u6211\u542c\u542c\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u743c\u6069\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u4e0d\u60f3\u8bf4\uff0c\u8bf4\u4e0d\u6e05\u3002\u201d</p><p>\u201c\u662f\u89c9\u5f97\u8bf4\u7ed9\u6211\u542c\u4e86\u4e5f\u6ca1\u7528\u5427\u3002\u201d</p><p>\u201c\u7b97\u662f\u5427\uff0c\u201d\u743c\u6069\u5e76\u4e0d\u5426\u8ba4\uff0c\u201c\u8bf4\u4e86\u4e5f\u6ca1\u7528\uff0c\u6240\u4ee5\u61d2\u5f97\u8bf4\u3002\u201d</p><p>\u201c\u4e0d\u4e00\u5b9a\u54e6\uff0c\u8bf4\u4e0d\u5b9a\u6211\u80fd\u7ed9\u4f60\u51fa\u70b9\u4e3b\u610f\u5462\u3002\u201d</p><p>\u743c\u6069\u7b11\u8d77\u6765\uff0c\u201c\u4e0d\u7528\u4e86\uff0c\u4f60\u51fa\u7684\u4e3b\u610f\uff0c\u4ece\u6765\u90fd\u662f\u542c\u8d77\u6765\u5934\u5934\u662f\u9053\uff0c\u4e00\u5b9e\u65bd\u8d77\u6765\u5c31\u5b8c\u5168\u4e0d\u5bf9\uff0c\u6bcf\u6b21\u90fd\u628a\u6211\u6298\u817e\u5f97\u591f\u545b\u3002\u201d</p><p>\u201c\u6211\u771f\u8fc7\u5206\u5462\uff0c\u4eba\u5bb6\u597d\u5fc3\u6765\u95ee\u4f60\uff0c\u4f60\u5374\u53d6\u7b11\u6211\u554a\uff0c\u201d\u838e\u73de\u514b\u6492\u5a07\uff0c\u201c\u5c31\u7b97\u662f\u4e3b\u4eba\uff0c\u8fd9\u6837\u5bf9\u5f85\u5973\u5974\u4e5f\u592a\u8fc7\u5206\u4e86\u5427\u3002\u201d</p><p>\u201c\u522b\u95f9\u4e86\uff0c\u6211\u771f\u7684\u6ca1\u7cbe\u795e\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u65e9\u4e86\uff0c\u4f60\u56de\u53bb\u4f11\u606f\u5427\u3002\u201d</p><p>\u201c\u521a\u505a\u5b8c\u5c31\u8d76\u4eba\u8d70\uff0c\u771f\u662f\u7edd\u60c5\u5462\uff0c\u201d\u9b45\u9b54\u53ef\u7231\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u5176\u5b9e\u5566\uff0c\u5c31\u7b97\u4e3b\u4eba\u4f60\u4e0d\u8bf4\uff0c\u6211\u4e5f\u77e5\u9053\u4f60\u4e3a\u4ec0\u4e48\u5fc3\u60c5\u4e0d\u597d\u3002\u201d</p><p>\u201c\u662f\u4e48\uff1f\u201d</p><p>\u201c\u5f53\u7136\u554a\uff0c\u73ca\u5609\u59d0\u59d0\u4e5f\u597d\uff0c\u827e\u5f25\u8587\u4e5f\u597d\uff0c\u90fd\u662f\u90a3\u4e48\u5b8c\u7f8e\u7684\u5973\u5b69\u5b50\uff0c\u6bd4\u6211\u8fd9\u6837\u7684\u5f3a\u8fc7\u767e\u500d\u5343\u500d\u3002\u80fd\u591f\u5f97\u5230\u4e00\u4e2a\uff0c\u90a3\u5c31\u5df2\u7ecf\u662f\u5929\u5927\u7684\u5e78\u8fd0\uff0c\u4f60\u4e24\u4e2a\u90fd\u5f97\u5230\u4e86\uff0c\u800c\u4e14\u73b0\u5728\u90fd\u966a\u5728\u8eab\u8fb9\uff0c\u8fd8\u6709\u4ec0\u4e48\u4e0d\u6ee1\u8db3\u7684\uff1f\u552f\u4e00\u80fd\u8ba9\u4f60\u5728\u8fd9\u91cc\u5782\u5934\u4e27\u6c14\u7684\uff0c\u53ea\u53ef\u80fd\u6709\u4e00\u4e2a\u539f\u56e0\u2014\u2014\u90a3\u5c31\u662f\uff1a\u8fd9\u4e24\u4e2a\u4eba\u4e2d\uff0c\u4f60\u5fc5\u987b\u8981\u505a\u9009\u62e9\u653e\u5f03\u4e00\u4e2a\u4e86\uff0c\u201d\u5979\u5ae3\u7136\u5fae\u7b11\uff0c\u201c\u6211\u8bf4\u5f97\u5bf9\u5427\u3002\u201d</p><p>\u743c\u6069\u9759\u9759\u5730\u770b\u7740\u5979\uff0c\u51e0\u79d2\u79cd\u540e\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u6ca1\u9519\u3002\u201d</p><p>\u201c\u771f\u53ef\u601c\uff0c\u201d\u9b45\u9b54\u7b11\u7740\uff0c\u62ac\u8d77\u624b\u5728\u743c\u6069\u7684\u8138\u4e0a\u634f\u4e86\u634f\uff0c\u201c\u96be\u602a\u6211\u7684\u4e3b\u4eba\u8fd9\u4e48\u65e0\u7cbe\u6253\u91c7\u7684\uff0c\u8fde\u505a\u7231\u90fd\u6ca1\u5174\u81f4\u3002\u786e\u5b9e\u554a\uff0c\u9762\u5bf9\u8fd9\u4e48\u8270\u96be\u7684\u9009\u62e9\uff0c\u5c31\u8fde\u6211\u60f3\u4e00\u60f3\uff0c\u90fd\u89c9\u5f97\u597d\u5934\u75bc\u7684\u6837\u5b50\u5462\u3002\u201d</p><p>\u201c\u6240\u4ee5\u4f60\u5c31\u522b\u5728\u8fd9\u91cc\u70e6\u6211\u4e86\uff0c\u201d\u743c\u6069\u6446\u6446\u624b\uff0c\u201c\u8d70\u5427\u8d70\u5427\uff0c\u8ba9\u6211\u9759\u4e00\u9759\u3002\u201d</p><p>\u838e\u73de\u514b\u5fae\u5fae\u7b11\u7740\uff0c\u624b\u8098\u6491\u5728\u4ed6\u7684\u80f8\u819b\u4e0a\uff0c\u4ef0\u8d77\u8138\uff0c\u53f3\u624b\u6258\u7740\u5de6\u816e\uff0c\u9759\u9759\u5730\u770b\u7740\u743c\u6069\u3002\u201c\u4e3b\u4eba\uff0c\u201d\u5979\u8f7b\u58f0\u8bf4\uff0c\u201c\u4f60\u662f\u771f\u7684\u6253\u7b97\u8981\u653e\u5f03\u5979\u4eec\u4e2d\u7684\u4e00\u4e2a\u5417\uff1f\u201d</p><p>\u201c\u4e0d\u7136\u8fd8\u80fd\u600e\u4e48\u529e\uff1f\u201d\u743c\u6069\u6ca1\u4ec0\u4e48\u7cbe\u795e\u5730\u56de\u7b54\uff0c\u201c\u5355\u9009\u9898\uff0c\u4e8c\u9009\u4e00\uff0c\u603b\u662f\u8981\u653e\u5f03\u4e00\u4e2a\u7684\u3002\u201d</p><p>\u201c\u4e0d\u80fd\u5168\u9009\u5417\uff1f\u201d</p><p>\u201c\u5982\u679c\u80fd\u7684\u8bdd\uff0c\u6211\u8fd8\u5728\u8fd9\u91cc\u5934\u75bc\u4ec0\u4e48\uff1f\u201d</p><p>\u838e\u73de\u514b\u683c\u683c\u5730\u7b11\u8d77\u6765\uff0c\u201c\u54ea\uff0c\u4e3b\u4eba\uff0c\u5982\u679c\u6211\u6709\u529e\u6cd5\u80fd\u591f\u8ba9\u4f60\u5168\u9009\u5462\uff1f\u201d</p></article>"}}}]);
