(window.webpackJsonp=window.webpackJsonp||[]).push([[1718],{1741:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e8c\u8282 \u5b9d\u77f3\u9f99",content:"<article><h2>\u7b2c\u56db\u5341\u4e8c\u8282 \u5b9d\u77f3\u9f99</h2><p>\u6709\u5173\u540e\u5bab\u6218\u7565\u7684\u8c08\u8bdd\uff0c\u5230\u6b64\u4fbf\u544a\u4e00\u6bb5\u843d\uff0c\u4e0d\u518d\u63d0\u53ca\u3002\u5176\u5b9e\u56de\u60f3\u8d77\u6765\uff0c\u838e\u73de\u514b\u5e76\u6ca1\u6709\u8bf4\u51fa\u591a\u5c11\u771f\u6b63\u6709\u7528\u7684\u5efa\u8bae\uff0c\u5979\u7684\u4e0a\u7b56\u6700\u4e3a\u7b80\u5355\u6613\u884c\uff0c\u4f46\u743c\u6069\u4e0d\u53ef\u80fd\u63a5\u53d7\uff1b\u4e2d\u7b56\u4e5f\u6709\u4e00\u5b9a\u53ef\u884c\u6027\uff0c\u90a3\u5957\u201c\u4eba\u683c\u8865\u5b8c\u201d\u7684\u9053\u7406\u8bf4\u8d77\u6765\u4e5f\u50cf\u6a21\u50cf\u6837\uff0c\u4f46\u743c\u6069\u8fd8\u662f\u89c9\u5f97\u4e0d\u5bf9\u52b2\uff0c\u653e\u5f03\u4e86\uff1b\u81f3\u4e8e\u4e0b\u7b56\u2026\u2026\u786e\u5b9e\u5982\u5979\u81ea\u5df1\u6240\u8a00\uff0c\u53ea\u662f\u201c\u6218\u7565\u201d\uff0c\u4e0d\u662f\u5177\u4f53\u7684\u201c\u6218\u672f\u201d\u3002\u4f46\u603b\u5f97\u8bf4\u8d77\u6765\uff0c\u8fd9\u573a\u8fd1\u4e00\u5c0f\u65f6\u7684\u8c08\u8bdd\u8fd8\u662f\u6709\u6210\u679c\u7684\uff0c\u9b45\u9b54\u5bf9\u8fdc\u671f\u7684\u76ee\u6807\uff0c\u8fd1\u671f\u7684\u65b9\u7565\uff0c\u654c\u6211\u7684\u5f62\u52bf\uff0c\u5f7c\u6b64\u7684\u5fc3\u6001\uff0c\u90fd\u8fdb\u884c\u4e86\u8be6\u5c3d\u7684\u5206\u6790\u5224\u65ad\uff0c\u8ba9\u743c\u6069\u8111\u7b4b\u6e05\u695a\u4e86\u5f88\u591a\uff0c\u60c5\u7eea\u4e5f\u4e0d\u50cf\u4e00\u5f00\u59cb\u90a3\u6837\u4f4e\u843d\u6cae\u4e27\u4e86\u3002</p><p>\u800c\u4e14\uff0c\u9b45\u9b54\u6700\u540e\u8f6c\u8ff0\u7684\u90a3\u53e5\u683c\u62c9\u5179\u7279\u7684\u8bdd\uff0c\u7ec6\u7ec6\u5480\u56bc\u54c1\u5473\u8d77\u6765\uff0c\u4f3c\u4e4e\u4e5f\u8574\u542b\u7740\u67d0\u4e9b\u81f3\u7406\u5462\u3002</p><p>\u201c\u6211\u4ee5\u524d\u542c\u8fc7\u4e00\u4e2a\u6545\u4e8b\uff0c\u8bf4\u4ece\u524d\u6709\u4e2a\u843d\u9b44\u7684\u7687\u5ba4\uff0c\u82e6\u5fc3\u79ef\u8651\u60f3\u8981\u91cd\u632f\u5df2\u7ecf\u540d\u5b58\u5b9e\u4ea1\u7684\u5e1d\u56fd\uff0c\u4f46\u5230\u5904\u78b0\u58c1\uff0c\u4e00\u7b79\u83ab\u5c55\u3002\u76f4\u5230\u6709\u4e00\u5929\uff0c\u4ed6\u5728\u4e00\u5ea7\u7834\u623f\u5b50\u91cc\u9047\u5230\u4e00\u4e2a\u4eba\uff0c\u5bf9\u4ed6\u8bf4\u4e86\u4e00\u756a\u8bdd\u3002\u843d\u9b44\u7687\u5ba4\u542c\u4e86\u4e4b\u540e\u5927\u559c\u8fc7\u671b\uff0c\u4e8e\u662f\u5949\u6b64\u4eba\u4e3a\u5e08\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4e00\u76f4\u89c9\u5f97\u90a3\u756a\u8bdd\u8fc7\u4e8e\u7a7a\u6cdb\uff0c\u5f92\u6709\u5927\u7565\uff0c\u6ca1\u6709\u5177\u4f53\u7684\u64cd\u4f5c\u65b9\u6848\uff0c\u4e0d\u660e\u767d\u90a3\u4f4d\u843d\u9b44\u7687\u5ba4\u6709\u4ec0\u4e48\u53ef\u9ad8\u5174\u7684\u2014\u2014\u73b0\u5728\u6211\u603b\u7b97\u660e\u767d\u4e86\u3002\u201d</p><p>\u201c\u660e\u767d\u4ec0\u4e48\uff1f\u201d\u9b45\u9b54\u95ee\u3002</p><p>\u201c\u4e00\u4ef6\u4e8b\u60c5\uff0c\u4f60\u4e00\u76f4\u89c9\u5f97\u2018\u5df2\u7ecf\u6ca1\u5e0c\u671b\u4e86\u2019\uff0c\u5fc3\u7070\u610f\u51b7\uff0c\u5fd7\u6c14\u6d88\u6c89\uff0c\u73b0\u5728\u6709\u4eba\u8ddf\u4f60\u8bf4\u4e00\u901a\uff0c\u522b\u7ba1\u5230\u5e95\u6709\u591a\u5c11\u9053\u7406\uff0c\u4f46\u81f3\u5c11\u4ed6\u6210\u529f\u5730\u8ba9\u4f60\u89c9\u5f97\u2018\u539f\u6765\u4e8b\u60c5\u8fd8\u6709\u5e0c\u671b\uff0c\u5f62\u52bf\u8fd8\u5927\u6709\u53ef\u4e3a\u2019\u2014\u2014\u80fd\u505a\u5230\u8fd9\u4e00\u70b9\uff0c\u90a3\u5c31\u8db3\u591f\u4e86\u3002\u201d</p><p>\u838e\u73de\u514b\u683c\u683c\u7b11\u7740\uff0c\u201c\u6240\u4ee5\u5462\uff1f\u4f60\u51c6\u5907\u4e5f\u5949\u6211\u4e3a\u5e08\uff1f\u201d</p><p>\u201c\u4e5f\u4e0d\u9519\u554a\uff0c\u201d\u743c\u6069\u5f00\u73a9\u7b11\uff0c\u201c\u63a8\u5012\u6f02\u4eae\u7684\u5973\u6559\u5e08\uff0c\u8fd9\u79cd\u6e38\u620f\u6211\u8fd8\u4ece\u6ca1\u73a9\u8fc7\u5462\u3002\u201d</p><p>\u201c\u90a3\u8981\u4e0d\u8981\u73b0\u5728\u5c31\u6765\u8bd5\u8bd5\uff1f\u201d\u838e\u73de\u514b\u5a9a\u773c\u5982\u4e1d\uff0c\u58f0\u97f3\u751c\u751c\u7684\uff0c\u4eff\u4f5b\u52fe\u4eba\u9b42\u9b44\uff0c\u201c\u521a\u624d\u4f60\u53ea\u5c04\u4e86\u4e00\u53d1\uff0c\u80af\u5b9a\u8fd8\u4e0d\u591f\u5427\u3002\u201d</p><p>\u4e24\u4eba\u4e00\u76f4\u4fdd\u6301\u7740\u4ea4\u5408\u72b6\u6001\uff0c\u743c\u6069\u5f53\u65f6\u53d1\u6cc4\u4e4b\u540e\u4e5f\u6ca1\u6709\u62bd\u51fa\u6765\uff0c\u800c\u662f\u7559\u5728\u5979\u7684\u8eab\u4f53\u91cc\uff0c\u4eab\u53d7\u7740\u9b45\u9b54\u82b1\u5f84\u7684\u6e29\u6696\u548c\u7d27\u8feb\u3002\u838e\u73de\u514b\u8bf4\u7740\u8bdd\uff0c\u7f13\u7f13\u626d\u52a8\u8170\u80a2\uff0c\u52a8\u4f5c\u8f7b\u67d4\uff0c\u82e5\u6709\u82e5\u65e0\uff0c\u4f53\u5185\u5374\u4eff\u4f5b\u81ea\u884c\u201c\u6d3b\u201d\u8d77\u6765\u4e00\u822c\uff0c\u5bc6\u5bc6\u531d\u531d\u5730\u7f20\u7ed5\u7740\u3001\u5305\u88f9\u7740\u3001\u6536\u7f29\u7740\u3001\u542e\u5438\u7740\uff0c\u6df1\u78e8\u6d45\u65cb\uff0c\u65f6\u8f7b\u65f6\u91cd\uff0c\u8f6c\u773c\u95f4\u5c31\u8ba9\u743c\u6069\u90a3\u6839\u539f\u672c\u5904\u4e8e\u534a\u8f6f\u72b6\u6001\u7684\u5bb6\u4f19\u53c8\u786c\u633a\u8d77\u6765\uff0c\u6025\u5267\u5347\u9ad8\u7684\u6e29\u5ea6\u4ece\u5185\u90e8\u900f\u5f80\u8868\u9762\uff0c\u707c\u70eb\u7740\u5973\u5b69\u5a07\u5ae9\u7684\u871c\u81a3\u8f6f\u8089\uff0c\u8ba9\u5979\u7981\u4e0d\u4f4f\u8f7b\u5fae\u98a4\u6296\u8d77\u6765\uff0c\u65ad\u65ad\u7eed\u7eed\u7684\u547b\u541f\u4ece\u5589\u95f4\u4f20\u51fa\uff0c\u6f6e\u7ea2\u7684\u8138\u988a\u548c\u9010\u6e10\u53d8\u5f97\u7c97\u91cd\u7684\u547c\u5438\u58f0\uff0c\u663e\u793a\u5979\u4e5f\u5df2\u7ecf\u5f00\u59cb\u52a8\u60c5\u3002</p><p>\u201c\u6211\u521a\u624d\u8bf4\u7684\u90a3\u4e2a\u6545\u4e8b\uff0c\u843d\u9b44\u7687\u5ba4\u5f97\u5230\u8001\u5e08\u4e4b\u540e\uff0c\u5173\u7cfb\u975e\u5e38\u5bc6\u5207\uff0c\u539f\u672c\u7684\u4eb2\u4fe1\u7eb7\u7eb7\u8868\u793a\u4e0d\u6ee1\uff0c\u201d\u743c\u6069\u5728\u9b45\u9b54\u8033\u8fb9\u8f7b\u58f0\u8bf4\u7740\uff0c\u4e00\u8fb9\u633a\u8170\u8038\u52a8\uff0c\u6162\u6162\u52a0\u5feb\u51b2\u523a\u901f\u5ea6\uff0c\u201c\u4f60\u77e5\u9053\uff0c\u90a3\u4f4d\u843d\u9b44\u7687\u5ba4\u600e\u4e48\u56de\u7b54\u7684\u4e48\uff1f\u201d</p><p>\u201c\u4e0d\u2026\u2026\u4e0d\u77e5\u9053\uff0c\u201d\u9b45\u9b54\u54ac\u7740\u5634\u5507\uff0c\u201c\u600e\u4e48\u56de\u7b54\u7684\uff1f\u201d</p><p>\u201c\u4ed6\u8bf4\uff0c\u6211\u5f97\u5230\u8001\u5e08\uff0c\u5c31\u50cf\u662f\u9c7c\u5f97\u5230\u4e86\u6c34\u4e00\u6837\u5fc3\u60c5\u6109\u5feb\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u73b0\u5728\u89c9\u5f97\uff0c\u8fd9\u4e2a\u6bd4\u55bb\u5b9e\u5728\u662f\u5f88\u8d34\u5207\u3002\u201d</p><p>\u786e\u5b9e\u975e\u5e38\u8d34\u5207\uff0c\u6240\u4ee5\u743c\u6069\u548c\u9b45\u9b54\u5f00\u59cb\u4eab\u53d7\u9c7c\u6c34\u4e4b\u6b22\uff0c\u987a\u4fbf\u4e5f\u5f53\u4f5c\u662f\u5bf9\u5979\u7684\u201c\u5956\u8d4f\u201d\u3002\u4e24\u4eba\u8f6e\u6d41\u6362\u5404\u79cd\u59ff\u52bf\uff0c\u4e00\u76f4\u6298\u817e\u5230\u6df1\u591c\uff0c\u7d2f\u5f97\u7cbe\u75b2\u529b\u7aed\uff0c\u6700\u540e\u90fd\u4e0d\u77e5\u9053\u600e\u4e48\u8ff7\u8ff7\u7cca\u7cca\u5730\u51fa\u4e86\u6d74\u5ba4\uff0c\u56de\u5230\u5367\u623f\u4e2d\uff0c\u4e0a\u5e8a\u7761\u89c9\u3002</p><p>\u5fc3\u5934\u6682\u65f6\u653e\u4e0b\u5fe7\u8651\uff0c\u6000\u91cc\u62b1\u7740\u9999\u8f6f\u4e2d\u5145\u6ee1\u9752\u6625\u6d3b\u529b\u7684\u5c11\u5973\u8eab\u4f53\uff0c\u743c\u6069\u8fd9\u4e00\u89c9\u7761\u5f97\u5f88\u6c89\u3002\u5927\u7ea6\u662f\u65e5\u6709\u6240\u601d\u7684\u7f18\u6545\uff0c\u4ed6\u53c8\u505a\u4e86\u4e2a\u548c\u4e0a\u6b21\u7c7b\u4f3c\u7684\u7f8e\u68a6\uff1a\u7ff1\u7fd4\u4e91\u7aef\u7684\u6d6e\u7a7a\u57ce\uff0c\u767e\u82b1\u9f50\u653e\u7684\u540e\u5bab\u4f73\u4e3d\uff0c\u6216\u8005\u82f1\u59ff\u98d2\u723d\uff0c\u6216\u8005\u6e29\u5a49\u5927\u65b9\uff0c\u6216\u8005\u5a07\u4fcf\u53ef\u7231\uff0c\u6216\u8005\u806a\u6167\u72e1\u9ee0\uff0c\u4f46\u65e0\u4e00\u4f8b\u5916\u7684\u662f\uff0c\u5979\u4eec\u90fd\u662f\u540c\u6837\u7684\u660e\u8273\u65e0\u4fe6\uff0c\u800c\u4e14\u5bf9\u743c\u6069\u5343\u4f9d\u767e\u987a\u3002</p><p>\u201c\u4f3c\u4e4e\u592a\u7f8e\u597d\u4e86\uff0c\u6709\u70b9\u50cf\u662f\u5728\u505a\u68a6\u2026\u2026\u5514\uff1f\u505a\u68a6\uff1f\u201d</p><p>\u610f\u8bc6\u5230\u53ef\u80fd\u662f\u68a6\u5883\uff0c\u90a3\u4e9b\u98ce\u5149\u65d6\u65ce\u7684\u753b\u9762\u987f\u65f6\u4fbf\u7eb7\u7eb7\u7834\u788e\uff0c\u4eff\u4f5b\u6d41\u5149\u822c\u56db\u6563\u800c\u53bb\u3002\u743c\u6069\u6e10\u6e10\u6e05\u9192\u8fc7\u6765\uff0c\u5374\u611f\u89c9\u4e00\u9635\u9635\u6696\u6d0b\u6d0b\u7684\u611f\u89c9\u81ea\u4e0b\u4f53\u4f20\u6765\uff0c\u4e24\u817f\u4e4b\u95f4\u7684\u90e8\u4f4d\uff0c\u50cf\u662f\u88ab\u67d0\u4e2a\u6e29\u6696\u6e7f\u6da6\u7684\u5a07\u5ae9\u8154\u9053\u5305\u88f9\u7740\uff0c\u5c16\u7aef\u8fd8\u88ab\u4e0d\u65ad\u5730\u6469\u64e6\u542e\u5438\uff0c\u4eff\u4f5b\u9b42\u9b44\u90fd\u8981\u88ab\u52fe\u51fa\u6765\u4f3c\u7684\u3002</p><p>\u8fd9\u79cd\u719f\u6089\u65e0\u6bd4\u7684\u4f53\u9a8c\uff0c\u743c\u6069\u5df2\u7ecf\u4e0d\u662f\u7b2c\u4e00\u6b21\u7ecf\u5386\u4e86\uff0c\u5f88\u5feb\u4fbf\u53cd\u5e94\u8fc7\u6765\u53d1\u751f\u4e86\u4ec0\u4e48\u3002\u4ed6\u7741\u5f00\u773c\u775b\uff0c\u6380\u5f00\u6bdb\u6bef\uff0c\u4fbf\u770b\u89c1\u9ed1\u53d1\u7ea2\u8863\u7684\u5c0f\u5973\u5deb\uff0c\u6b63\u4e56\u5de7\u5730\u8dea\u5728\u817f\u95f4\uff0c\u4e3a\u4ed6\u505a\u9999\u8273\u7684\u5507\u820c\u4f8d\u5949\u3002\u56e0\u4e3a\u662f\u8dea\u4f0f\u7684\u59ff\u52bf\uff0c\u7f8e\u81c0\u7fd8\u8d77\uff0c\u8d85\u77ed\u88d9\u7684\u88d9\u8fb9\u5012\u7ffb\u5728\u8170\u4e0a\uff0c\u9732\u51fa\u4e86\u7c89\u7ea2\u8272\u7684\u5c0f\u5185\u88e4\uff0c\u53ef\u7231\u4e2d\u53c8\u900f\u7740\u51e0\u5206\u6deb\u9761\uff0c\u770b\u5f97\u743c\u6069\u5fc3\u795e\u52a8\u6447\uff0c\u6070\u597d\u53c8\u88ab\u5979\u7528\u529b\u5f80\u54bd\u5589\u91cc\u541e\u4e86\u51e0\u4e0b\uff0c\u8212\u723d\u65e0\u6bd4\uff0c\u9669\u4e9b\u5c31\u5f53\u573a\u5c04\u51fa\u6765\u3002</p><p>\u201c\u51db\uff1f\u201d</p><p>\u201c\u65e9\uff0c\u743c\u6069\uff0c\u201d\u51db\u5410\u51fa\u53e3\u4e2d\u7684\u5e9e\u7136\u5927\u7269\uff0c\u7b11\u772f\u772f\u5730\u62ac\u8d77\u8138\u6765\uff0c\u201c\u7ec8\u4e8e\u9192\u4e86\u554a\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u51db\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4f4e\u4e0b\u5934\u7ee7\u7eed\u8fdb\u884c\u81ea\u5df1\u672a\u5b8c\u6210\u7684\u5de5\u4f5c\uff0c\u743c\u6069\u5750\u8d77\u8eab\u6765\uff0c\u629a\u6478\u7740\u5979\u7684\u5934\u53d1\uff0c\u4eab\u53d7\u5c0f\u5973\u5deb\u70ed\u60c5\u4e3b\u52a8\u7684\u65e9\u5b89\u54ac\u670d\u52a1\u3002\u51db\u660e\u663e\u6709\u4e3a\u6628\u65e5\u4e8b\u60c5\u9053\u6b49\u7684\u610f\u601d\uff0c\u4f7f\u51fa\u6d51\u8eab\u89e3\u6570\uff0c\u53e3\u5507\u542e\u5438\u8214\u8210\uff0c\u9999\u820c\u8f7b\u6311\u6162\u62b9\uff0c\u4e0a\u4e0a\u4e0b\u4e0b\u52aa\u529b\u5957\u5f04\uff0c\u751a\u81f3\u65e0\u5e08\u81ea\u901a\u5730\u5b66\u4f1a\u4e86\u7528\u4e00\u5bf9\u4e30\u6ee1\u67d4\u8f6f\u7684\u7f8e\u4e73\u5305\u5939\u6775\u8eab\uff0c\u8fde\u5c1a\u672a\u719f\u7ec3\u7684\u6df1\u5589\u6280\u5de7\u90fd\u9891\u9891\u7528\u51fa\u6765\uff0c\u5018\u82e5\u662f\u5e73\u65e5\uff0c\u743c\u6069\u65e9\u5df2\u7ecf\u53d7\u4e0d\u4f4f\uff0c\u53ef\u60dc\u6628\u591c\u4ed6\u548c\u9b45\u9b54\u5b9e\u5728\u73a9\u5f97\u592a\u8fc7\u706b\uff0c\u51e0\u4e4e\u628a\u79ef\u84c4\u7684\u7cbe\u529b\u5168\u90fd\u91ca\u653e\u5149\u4e86\uff0c\u6b64\u65f6\u8212\u723d\u5219\u8212\u723d\uff0c\u51db\u8981\u60f3\u628a\u4ed6\u5438\u51fa\u6765\uff0c\u5374\u5b9e\u5728\u4e0d\u662f\u4ef6\u5bb9\u6613\u4e8b\u60c5\u3002</p><p>\u6b63\u5728\u6b64\u65f6\uff0c\u5367\u5ba4\u7684\u95e8\u88ab\u63a8\u5f00\u534a\u6247\uff0c\u9732\u51fa\u4e00\u4f4d\u91d1\u53d1\u620e\u88c5\u5c11\u5973\u7684\u4e0a\u534a\u8eab\uff0c\u671d\u91cc\u9762\u770b\u8fc7\u6765\u3002\u201c\u600e\u4e48\u8fd8\u6ca1\u8d77\u6765\u2014\u2014\u5582\uff0c\u51db\uff0c\u201d\u6885\u83f2\u65af\u76b1\u7709\uff0c\u5979\u624d\u521a\u521a\u770b\u6e05\u5e8a\u4e0a\u4e24\u4eba\u7684\u60c5\u5f62\uff0c\u201c\u6211\u662f\u8ba9\u4f60\u6765\u53eb\u4ed6\u8d77\u5e8a\u7684\uff0c\u4e0d\u662f\u8ba9\u4f60\u2026\u2026\u201d</p><p>\u201c\u53ef\u662f\u827e\u5f25\u8587\u4f60\u4ee5\u524d\u8bf4\u8fc7\u554a\uff0c\u201d\u51db\u542b\u542b\u7cca\u7cca\u5730\u56de\u7b54\uff0c\u201c\u8fd9\u5c31\u662f\u4ed6\u6700\u559c\u6b22\u7684\u65e9\u4e0a\u88ab\u53eb\u8d77\u5e8a\u7684\u65b9\u5f0f\u3002\u201d</p><p>\u201c\u90a3\u4f60\u4eec\u52a8\u4f5c\u5feb\u70b9\uff0c\u4eca\u5929\u8fd8\u6709\u6b63\u4e8b\u8981\u505a\u5462\u3002\u201d</p><p>\u201c\u6211\u4e5f\u60f3\u5feb\u70b9\u641e\u5b9a\u554a\uff0c\u53ef\u662f\u8fd9\u5bb6\u4f19\u4eca\u5929\u5c31\u662f\u633a\u7740\u4e0d\u51fa\u6765\uff0c\u201d\u51db\u6485\u7740\u5634\uff0c\u201c\u8981\u4e0d\u827e\u5f25\u8587\u4f60\u4e5f\u6765\u5e2e\u5fd9\u5427\uff0c\u5426\u5219\u6211\u6015\u5230\u665a\u4e0a\u90fd\u522b\u60f3\u52a8\u8eab\u51fa\u53d1\u3002\u201d</p><p>\u201c\u2026\u2026\u771f\u662f\u62ff\u4f60\u6ca1\u529e\u6cd5\u3002\u201d</p><p>\u5728\u6885\u83f2\u65af\u4e5f\u52a0\u5165\u4e4b\u540e\uff0c\u53c8\u8d39\u4e86\u4e0d\u5c11\u529f\u592b\uff0c\u4e24\u4f4d\u5c11\u5973\u7ec8\u4e8e\u6210\u529f\u5730\u628a\u743c\u6069\u201c\u54ac\u201d\u4e86\u51fa\u6765\uff0c\u7d2f\u5f97\u5979\u4eec\u6c14\u5598\u5401\u5401\u3002\u6b64\u65f6\u5df2\u7ecf\u662f\u4e0a\u5348\u5341\u70b9\u949f\uff0c\u62c9\u5f00\u7a97\u5e18\uff0c\u707f\u70c2\u7684\u9633\u5149\u4fbf\u7167\u5c04\u8fdb\u6765\uff0c\u5c06\u539f\u672c\u9634\u6697\u7684\u623f\u95f4\u6620\u5f97\u4eae\u5802\u5802\u4e00\u7247\u3002\u743c\u6069\u5fc3\u6ee1\u610f\u8db3\u5730\u8d77\u8eab\u7a7f\u8863\uff0c\u7a81\u7136\u53d1\u73b0\u90a3\u672c\u300a\u540e\u5bab\u6218\u7565\u300b\u6b63\u653e\u5728\u5e8a\u8fb9\u67dc\u5b50\u4e0a\uff0c\u987f\u65f6\u5413\u4e86\u4e00\u8df3\uff0c\u8d76\u7d27\u4f38\u624b\u5c06\u5b83\u5f80\u6795\u5934\u5e95\u4e0b\u4e00\u585e\u3002\u8fd9\u4e1c\u897f\u53ef\u5343\u4e07\u4e0d\u80fd\u8ba9\u5979\u4eec\u770b\u89c1\uff0c\u5426\u5219\u5c31\u9ebb\u70e6\u5927\u4e86\u3002</p><p>\u5e78\u597d\uff0c\u6885\u83f2\u65af\u548c\u51db\u90fd\u5fd9\u7740\u6574\u7406\u81ea\u5df1\u88ab\u4e71\u5f04\u7684\u8863\u88f3\uff0c\u4ee5\u53ca\u6f31\u53e3\u6e05\u6d17\u5507\u9f7f\u95f4\u6b8b\u7559\u7684\u767d\u6d4a\u6db2\u4f53\uff0c\u538b\u6839\u6ca1\u6ce8\u610f\u743c\u6069\u7684\u52a8\u4f5c\u3002\u201c\u73b0\u5728\u6ee1\u610f\u4e86\u5427\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8d76\u7d27\u53bb\u6d17\u8138\u5237\u7259\uff0c\u65e9\u9910\u90fd\u8981\u51b7\u4e86\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u743c\u6069\u5fc3\u865a\uff0c\u6015\u88ab\u6885\u83f2\u65af\u53d1\u73b0\u6795\u5934\u4e0b\u90a3\u672c\u4e66\uff0c\u4e5f\u4e0d\u6562\u591a\u8bf4\uff0c\u8d76\u7d27\u4e56\u4e56\u542c\u8bdd\uff0c\u6d17\u6f31\u5b8c\u6bd5\u4e0b\u697c\u3002\u65e9\u9910\u5f88\u7b80\u5355\uff0c\u660e\u663e\u662f\u6885\u83f2\u65af\u7684\u624b\u827a\u3002\u201c\u521a\u624d\u6211\u53bb\u627e\u4e86\u5e0c\u6b27\uff0c\u4ece\u4ed6\u5bb6\u6682\u65f6\u501f\u4e00\u4f4d\u53a8\u5e08\u8fc7\u6765\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4ed6\u7b54\u5e94\u4e86\uff0c\u4e2d\u5348\u5e94\u8be5\u5c31\u80fd\u5230\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u72fc\u541e\u864e\u54bd\u5730\u5c06\u65e9\u9910\u586b\u8fdb\u809a\u5b50\uff0c\u7136\u540e\u8ddf\u7740\u4e24\u4f4d\u5c11\u5973\u51fa\u53d1\u3002\u76f4\u5230\u8d70\u8fdb\u9547\u4e2d\uff0c\u4ed6\u624d\u731b\u7136\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u5443\uff0c\u7b49\u7b49\uff0c\u201d\u4ed6\u4e0d\u597d\u610f\u601d\u5730\u95ee\uff0c\u201c\u6211\u4eec\u8fd9\u662f\u8981\u53bb\u54ea\u554a\uff1f\u201d</p><p>\u201c\u6211\u8981\u53bb\u770b\u671b\u51b0\u8679\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f60\u548c\u51db\u966a\u6211\u53bb\u3002\u201d</p><p>\u201c\u51b0\u8679\u2026\u2026\u54e6\uff0c\u60f3\u8d77\u6765\u4e86\u3002\u201d</p><p>\u6628\u65e5\u5728\u5927\u4e3b\u6559\u5bb6\u91cc\uff0c\u4ed6\u66fe\u7ecf\u548c\u6885\u83f2\u65af\u63d0\u8d77\u8fc7\u201c\u51b0\u8679\u201d\u8fd9\u4e2a\u540d\u5b57\uff0c\u8054\u7cfb\u524d\u540e\u6240\u8bf4\u7684\u8bdd\u6765\u63a8\u6d4b\uff0c\u5e94\u8be5\u662f\u6761\u9f99\u3002\u9f99\u8fd9\u79cd\u751f\u7269\uff0c\u743c\u6069\u5012\u4e0d\u7b97\u964c\u751f\uff0c\u6b7b\u7684\u6d3b\u7684\u90fd\u89c1\u8fc7\uff0c\u8eab\u8fb9\u6b64\u523b\u5c31\u6709\u4e2a\u9f99\u5973\u5462\u3002\u201c\u5bf9\u4e86\uff0c\u51db\uff0c\u201d\u6885\u83f2\u65af\u95ee\uff0c\u201c\u5b9d\u77f3\u90fd\u5e26\u4e86\u5427\u3002\u201d</p><p>\u201c\u5e26\u5566\u5e26\u5566\uff0c\u201d\u51db\u9887\u6709\u4e9b\u4e0d\u60c5\u4e0d\u613f\u7684\u6837\u5b50\uff0c\u4eff\u4f5b\u5f88\u820d\u4e0d\u5f97\uff0c\u201c\u90fd\u5728\u53e3\u888b\u91cc\u5462\u3002\u201d</p><p>\u201c\u5e26\u5b9d\u77f3\u505a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u5947\u602a\u3002</p><p>\u201c\u5582\u51b0\u8679\u7684\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u5b83\u6700\u8d2a\u5403\u4e86\u3002\u201d</p><p>\u201c\u5403\u5b9d\u77f3\uff1f\u201d\u743c\u6069\u5148\u662f\u4e00\u6014\uff0c\u968f\u5373\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u96be\u9053\u662f\u5b9d\u77f3\u9f99\u4e48\uff1f\u201d</p><p>\u6885\u83f2\u65af\u70b9\u5934\uff0c\u201c\u662f\u554a\u3002\u201d</p><p>\u9f99\u662f\u8fd9\u4e2a\u4e16\u754c\u4e0a\u4f4d\u5c45\u6700\u9876\u7aef\u7684\u751f\u7269\u4e4b\u4e00\uff0c\u5386\u53f2\u60a0\u4e45\uff0c\u79cd\u7c7b\u7e41\u6742\uff0c\u5176\u4e2d\u6700\u5178\u578b\u7684\u81ea\u7136\u662f\u4e94\u8272\u9f99\uff08\u7ea2\u9f99\u3001\u84dd\u9f99\u3001\u9ed1\u9f99\u3001\u7eff\u9f99\u3001\u767d\u9f99\uff09\u548c\u91d1\u5c5e\u9f99\uff08\u91d1\u9f99\u3001\u94f6\u9f99\u3001\u9752\u94dc\u9f99\u3001\u9ec4\u94dc\u9f99\u3001\u8d64\u94dc\u9f99\uff09\uff0c\u8fd9\u5341\u79cd\u9f99\u5927\u6982\u5360\u4e86\u6240\u6709\u9f99\u65cf\u6570\u91cf\u7684\u767e\u5206\u4e4b\u4e5d\u5341\uff0c\u5269\u4e0b\u7684\u767e\u5206\u4e4b\u5341\u5219\u662f\u4e9b\u751f\u50fb\u7f55\u89c1\u7684\u9f99\u7c7b\uff0c\u57fa\u672c\u90fd\u4e0d\u5c45\u4f4f\u5728\u7269\u8d28\u754c\uff0c\u4f8b\u5982\u8679\u5f69\u9f99\u3001\u529b\u80fd\u9f99\u3001\u5149\u8000\u9f99\u3001\u5730\u72f1\u708e\u9f99\u4e4b\u7c7b\u3002\u5b9d\u77f3\u9f99\u5219\u662f\u4e00\u79cd\u65e2\u5c45\u4f4f\u5728\u7269\u8d28\u754c\uff0c\u53c8\u4e0d\u5c5e\u4e8e\u4e94\u8272\u9f99\u548c\u91d1\u5c5e\u9f99\u884c\u5217\u7684\u7f55\u89c1\u9f99\u7c7b\uff0c\u5b83\u4eec\u7684\u6700\u5927\u7279\u5f81\u5c31\u662f\u98df\u8c31\u8d85\u7ea7\u534e\u4e3d\uff0c\u559c\u6b22\u5403\u5404\u79cd\u5404\u6837\u7684\u5b9d\u77f3\u3002\u636e\u8bf4\u5b83\u4eec\u7684\u5916\u8c8c\u4f1a\u968f\u7740\u98df\u8c31\u800c\u53d8\u5316\uff0c\u6bd4\u5982\u6700\u8fd1\u7ea2\u5b9d\u77f3\u5403\u591a\u4e86\uff0c\u9cde\u7532\u76ae\u80a4\u90fd\u4f1a\u53d8\u6210\u7ea2\u5b9d\u77f3\u8272\uff1b\u7d2b\u6c34\u6676\u5403\u591a\u4e86\uff0c\u5c31\u4f1a\u53d8\u6210\u7d2b\u6c34\u6676\u8272\uff1b\u5982\u679c\u662f\u5404\u79cd\u5b9d\u77f3\u6df7\u7740\u5403\uff0c\u90a3\u5c31\u5f80\u5f80\u53d8\u5f97\u4e94\u989c\u516d\u8272\u4e03\u5f69\u6591\u6593\uff0c\u6f02\u4eae\u81f3\u6781\u3002</p><p>\u51b0\u8679\u5c31\u662f\u53ea\u5b9d\u77f3\u9f99\uff0c\u5c45\u4f4f\u5728\u5854\u745f\u8c37\u5df2\u7ecf\u4e00\u5343\u591a\u5e74\uff0c\u65e2\u61d2\u60f0\u53c8\u8d2a\u5403\uff0c\u4e0d\u8fc7\u6027\u683c\u5927\u81f4\u8fd8\u7b97\u6e29\u548c\uff0c\u800c\u4e14\u767e\u5206\u4e4b\u4e5d\u5341\u4e5d\u7684\u65f6\u95f4\u90fd\u5728\u6d1e\u7a9f\u91cc\u7761\u5927\u89c9\uff0c\u57fa\u672c\u4e0a\u5c5e\u4e8e\u4eba\u755c\u65e0\u5bb3\u7684\u5b85\u9f99\u4e00\u53ea\u3002\u63d0\u5c14\u795e\u6bbf\u5728\u6b64\u5730\u5efa\u7acb\u4e4b\u540e\uff0c\u548c\u51b0\u8679\u6253\u8fc7\u51e0\u6b21\u4ea4\u9053\uff0c\u4f46\u65e0\u6df1\u4ea4\uff0c\u5f7c\u6b64\u76f8\u5b89\u65e0\u4e8b\u3002\u540e\u6765\u5361\u7f2a\u5c14\u5973\u58eb\u901d\u4e16\uff0c\u6559\u4f1a\u9762\u4e34\u5f3a\u654c\u538b\u8feb\uff0c\u98ce\u96e8\u98d8\u6447\uff0c\u6c49\u5bc6\u5c14\u987f\u4e3a\u4e86\u5bf9\u6297\u5f3a\u654c\uff0c\u5b64\u8eab\u4e0a\u5c71\u62dc\u8bbf\u51b0\u8679\uff0c\u53cc\u65b9\u8fbe\u6210\u4e86\u4e00\u9879\u534f\u8bae\uff0c\u5927\u81f4\u5185\u5bb9\u662f\uff1a\u63d0\u5c14\u6559\u4f1a\u5b9a\u671f\u5411\u51b0\u8679\u63d0\u4f9b\u7f8e\u98df\uff08\u4e5f\u5c31\u662f\u5b9d\u77f3\uff09\uff0c\u800c\u51b0\u8679\u5219\u5728\u63d0\u5c14\u6559\u4f1a\u9700\u8981\u7684\u65f6\u5019\u63d0\u4f9b\u5e2e\u52a9\u3002</p><p>\u901a\u4fd7\u5730\u8bf4\uff0c\u5c31\u662f\u63d0\u5c14\u795e\u6bbf\u82b1\u91cd\u91d1\u8bf7\u4e86\u4e2a\u9ad8\u7ea7\u6253\u624b\uff0c\u800c\u4e8b\u5b9e\u8bc1\u660e\u8fd9\u7b14\u4ea4\u6613\u975e\u5e38\u6210\u529f\u3002\u51ed\u501f\u51b0\u8679\u8fd9\u4e2a\u5f3a\u529b\u5916\u63f4\uff0c\u6b64\u540e\u7684\u51e0\u6b21\u5173\u952e\u5927\u6218\uff0c\u63d0\u5c14\u6559\u4f1a\u90fd\u662f\u5728\u6700\u540e\u5173\u5934\u731b\u7136\u7ffb\u76d8\uff0c\u6f02\u4eae\u5730\u5b8c\u6210\u5927\u9006\u8f6c\uff0c\u6700\u7ec8\u5960\u5b9a\u80dc\u5c40\u3002\u5f53\u65f6\u90a3\u4efd\u534f\u8bae\u4e0a\u6ca1\u6709\u8bbe\u5b9a\u671f\u9650\uff0c\u800c\u53cc\u65b9\u4e5f\u7b97\u5408\u4f5c\u6109\u5feb\uff0c\u4e8e\u662f\u8fd9\u79cd\u5173\u7cfb\u4fbf\u4e00\u76f4\u4fdd\u6301\u4e86\u4e0b\u6765\uff0c\u5ef6\u7eed\u81f3\u4eca\u3002</p><p>\u201c\u522b\u4eba\u6700\u591a\u517b\u72d7\u517b\u732b\uff0c\u4f60\u4eec\u6559\u4f1a\u5c45\u7136\u517b\u6761\u9f99\uff0c\u771f\u662f\u6709\u521b\u610f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u517b\u8fd9\u79cd\u9ad8\u7ea7\u5ba0\u7269\uff0c\u6240\u8d39\u4e00\u5b9a\u4e0d\u83f2\u5427\u3002\u201d</p><p>\u201c\u662f\u554a\uff0c\u201d\u6885\u83f2\u65af\u53f9\u6c14\uff0c\u201c\u4ee5\u524d\u5012\u8fd8\u7f62\u4e86\u3002\u4f46\u8fd1\u5341\u5e74\u6765\uff0c\u5b83\u8d8a\u6765\u8d8a\u8d2a\u5403\uff0c\u80c3\u53e3\u8d8a\u6765\u8d8a\u597d\uff0c\u5982\u4eca\u5dee\u4e0d\u591a\u6bcf\u5e74\u5168\u6559\u533a\u6536\u5165\u7684\u767e\u5206\u4e4b\u4e8c\u5341\u90fd\u88ab\u5b83\u5403\u6389\uff0c\u518d\u8fd9\u6837\u4e0b\u53bb\u771f\u8981\u517b\u4e0d\u8d77\u4e86\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u4e0d\u7ec8\u6b62\u534f\u8bae\u5462\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u53cd\u6b63\u5f53\u521d\u53c8\u6ca1\u8bbe\u5b9a\u671f\u9650\u3002\u201d</p><p>\u201c\u56e0\u4e3a\uff0c\u201d\u6885\u83f2\u65af\u8f7b\u58f0\u8bf4\uff0c\u201c\u5b83\u4e5f\u5dee\u4e0d\u591a\u2026\u2026\u8981\u5230\u5927\u9650\u4e86\u3002\u201d</p><p>\u201c\u54e6\uff0c\u5bf9\u554a\u3002\u201d</p><p>\u9f99\u867d\u7136\u957f\u5bff\uff0c\u7ec8\u7a76\u4e0d\u80fd\u6c38\u6052\uff0c\u51b0\u8679\u5df2\u903e\u5343\u5c81\uff0c\u5c5e\u4e8e\u592a\u53e4\u9f99\u7684\u7ea7\u522b\uff0c\u672c\u5c31\u5782\u5782\u8001\u77e3\uff0c\u800c\u80c3\u53e3\u5267\u589e\u6b63\u662f\u9f99\u7c7b\u5c06\u6b7b\u7684\u5178\u578b\u5f81\u5146\u3002\u636e\u6c49\u5bc6\u5c14\u987f\u957f\u8001\u5224\u65ad\uff0c\u6700\u591a\u4e09\u5e74\uff0c\u51b0\u8679\u7684\u5927\u9650\u5c31\u8981\u5230\u6765\u3002\u6240\u8c13\u5584\u59cb\u5584\u7ec8\uff0c\u65e2\u7136\u516b\u767e\u591a\u5e74\u90fd\u5df2\u7ecf\u5582\u8fc7\u6765\uff0c\u4e5f\u5c31\u4e0d\u5dee\u6700\u540e\u8fd9\u70b9\u65f6\u95f4\u4e86\u3002</p><p>\u201c\u4e0d\u8fc7\u827e\u5f25\u8587\uff0c\u4f60\u6ca1\u53d1\u73b0\u81ea\u5df1\u5f88\u5947\u602a\u4e48\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u660e\u660e\u662f\u4e2a\u4eba\u7c7b\uff0c\u4e3a\u4ec0\u4e48\u603b\u662f\u548c\u9f99\u7684\u5173\u7cfb\u7279\u522b\u597d\u5462\u3002\u201d</p><p>\u201c\u8c01\u77e5\u9053\u3002\u201d\u6885\u83f2\u65af\u968f\u53e3\u56de\u7b54\uff0c\u671d\u524d\u65b9\u626c\u4e86\u626c\u624b\uff0c\u4e00\u4f4d\u4f8d\u4ece\u6a21\u6837\u7684\u5c11\u5e74\u7275\u7740\u4e09\u5339\u9a6c\u5c0f\u8dd1\u8fc7\u6765\uff0c\u5c06\u7f30\u7ef3\u9012\u7ed9\u6885\u83f2\u65af\u3002\u201c\u4e0a\u9a6c\u5427\uff0c\u201d\u5c11\u5973\u5bf9\u743c\u6069\u548c\u51db\u8bf4\uff0c\u201c\u8def\u5f88\u8fdc\u5462\u3002\u201d</p><p>\u786e\u5b9e\u633a\u8fdc\uff0c\u4e09\u4eba\u7b56\u9a6c\u800c\u884c\uff0c\u867d\u7136\u8bf4\u4e0d\u4e0a\u98ce\u9a70\u7535\u63a3\uff0c\u901f\u5ea6\u8fd8\u662f\u633a\u5feb\u7684\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u4e0b\u5348\u4e24\u70b9\u591a\u949f\uff0c\u4ed6\u4eec\u624d\u7ec8\u4e8e\u62b5\u8fbe\u4e86\u6b64\u884c\u7684\u76ee\u7684\u5730\uff1a\u86c7\u708e\u5c71\u3002\u6885\u83f2\u65af\u8bf4\uff0c\u51b0\u8679\u5c31\u4f4f\u5728\u9760\u8fd1\u5c71\u9876\u7684\u4e00\u4e2a\u6d1e\u7a9f\u4e2d\u3002</p><p>\u5c06\u9a6c\u7684\u7f30\u7ef3\u7cfb\u597d\uff0c\u4e09\u4eba\u4fbf\u5f00\u59cb\u4e0a\u5c71\u3002\u5c71\u9053\u4e0d\u7b97\u9661\u5ced\uff0c\u4f46\u4ed6\u4eec\u8d70\u5f97\u5e76\u4e0d\u5feb\uff0c\u56e0\u4e3a\u65e0\u9700\u7740\u6025\uff0c\u7528\u51db\u7684\u8bdd\u8bf4\uff0c\u5979\u8fd9\u4e00\u8d9f\u539f\u672c\u5c31\u662f\u6765\u90ca\u6e38\u7684\u3002\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u5bf9\u89c1\u4e00\u53ea\u5b9d\u77f3\u9f99\u6ca1\u4ec0\u4e48\u5174\u8da3\uff0c\u4f46\u8fd9\u6837\u98ce\u548c\u65e5\u4e3d\u7684\u521d\u6625\u4e0b\u5348\uff0c\u966a\u7740\u4e24\u4f4d\u5fc3\u7231\u7684\u7f8e\u4eba\u767b\u5c71\uff0c\u4e5f\u662f\u79cd\u975e\u5e38\u4e0d\u9519\u7684\u4eab\u53d7\u3002</p><p>\u4e00\u8def\u968f\u53e3\u95f2\u8c08\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\u4fbf\u5df2\u7ecf\u5230\u4e86\u534a\u5c71\u8170\uff0c\u51db\u6709\u4e9b\u5598\u6c14\uff0c\u63d0\u8bae\u5750\u4e0b\u6765\u6b47\u606f\u7247\u523b\uff0c\u743c\u6069\u8d76\u7d27\u9644\u548c\uff0c\u6885\u83f2\u65af\u4e5f\u6ca1\u4ec0\u4e48\u5f02\u8bae\uff0c\u6b63\u8981\u627e\u5757\u5e72\u51c0\u7684\u77f3\u5934\u5750\u4e0b\uff0c\u731b\u7136\u79c0\u7709\u4e00\u6311\uff0c\u624b\u4e2d\u5149\u8292\u7efd\u653e\uff0c\u94f6\u5251\u201c\u7737\u604b\u201d\u8131\u624b\u98de\u51fa\uff0c\u9ad8\u901f\u65cb\u8f6c\u7740\uff0c\u65a9\u5165\u65c1\u8fb9\u7684\u6811\u6797\u4e2d\u3002</p></article>"}}}]);
