(window.webpackJsonp=window.webpackJsonp||[]).push([[1721],{1744:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e94\u8282 \u8840\u74f6",content:"<article><h2>\u7b2c\u56db\u5341\u4e94\u8282 \u8840\u74f6</h2><p>\u201c\u770b\u6211\u4e0d\u987a\u773c\u65e0\u6240\u8c13\uff0c\u6211\u4e5f\u6ca1\u5174\u8da3\u8ba8\u7537\u4eba\u559c\u6b22\uff0c\u201d\u743c\u6069\u8f7b\u58f0\u8bf4\uff0c\u201c\u4f46\u662f\u6562\u5f53\u9762\u52fe\u642d\u6211\u7684\u5973\u53cb\uff0c\u8fd9\u5c31\u5b9e\u5728\u662f\u8fc7\u5206\u4e86\u70b9\u554a\u3002\u201d</p><p>\u4e0d\u8fc7\u8bf4\u8d77\u6765\uff0c\u51db\u4f3c\u4e4e\u683c\u5916\u62db\u5438\u8840\u9b3c\u7684\u559c\u6b22\u5462\u3002\u5728\u5730\u72f1\u7684\u65f6\u5019\uff0c\u5979\u88ab\u67d0\u4e2a\u5438\u8840\u9b3c\u6027\u9a9a\u6270\u8fc7\uff1b\u5728\u5e7d\u6697\u5730\u57df\u7684\u65f6\u5019\uff0c\u5fb7\u53e4\u62c9\u4e5f\u5bf9\u5979\u7ecf\u5e38\u732e\u6bb7\u52e4\uff1b\u521a\u624d\u8fd9\u53ea\u5438\u8840\u9b3c\uff0c\u521d\u6b21\u89c1\u9762\u5c31\u70ed\u60c5\u5411\u51db\u53d1\u51fa\u9080\u8bf7\uff0c\u5374\u5bf9\u540c\u6837\u7f8e\u8c8c\u7684\u6885\u83f2\u65af\u89c6\u800c\u4e0d\u89c1\u2014\u2014\u8fd9\u7b97\u4e0d\u7b97\u662f\u79cd\u7279\u522b\u7684\u5929\u8d4b\u5462\uff1f\u6bd4\u5982\u8bf4\uff0c\u53ef\u4ee5\u547d\u540d\u4e3a\uff1a\u201c\u51db\u4e4b\u65e0\u5dee\u522b\u5a9a\u60d1\u5438\u8840\u9b3c\u5149\u73af\uff1f\u201d</p><p>\u597d\u5427\uff0c\u51b3\u5b9a\u4e86\uff0c\u4ece\u4eca\u5929\u8d77\u628a\u5168\u4e16\u754c\u6240\u6709\u7684\u96c4\u6027\u5438\u8840\u9b3c\u90fd\u5217\u4e3a\u6f5c\u5728\u60c5\u654c\uff0c\u89c1\u4e00\u4e2a\u6740\u4e00\u4e2a\u3002</p><p>\u4ed6\u62ac\u4e86\u62ac\u624b\uff0c\u8ba9\u9b54\u50cf\u53d8\u56de\u68cb\u5b50\uff0c\u6b63\u5f85\u4e0a\u524d\u62e3\u56de\uff0c\u8d70\u51fa\u4e24\u6b65\u7a81\u7136\u53c8\u6014\u4f4f\u4e86\u3002\u5728\u9633\u5149\u7684\u707f\u70c2\u7167\u5c04\u4e0b\uff0c\u9ed1\u96fe\u5df2\u7ecf\u5b8c\u5168\u84b8\u53d1\u6d88\u6563\uff0c\u9732\u51fa\u5176\u4e2d\u7684\u5c38\u4f53\uff0c\u5b83\u6b63\u9677\u5165\u88ab\u9b54\u6cd5\u8f6f\u5316\u7684\u5730\u9762\u4e2d\u7f13\u7f13\u4e0b\u6c89\uff0c\u800c\u4e14\u786e\u5b9e\u88ab\u780d\u6210\u4e86\u56db\u6bb5\u3002\u5c31\u7b97\u662f\u5438\u8840\u9b3c\uff0c\u88ab\u780d\u6210\u56db\u6bb5\u4e5f\u6ca1\u6cd5\u518d\u590d\u6d3b\uff0c\u8fd9\u70b9\u743c\u6069\u53ef\u4ee5\u786e\u4fe1\u2014\u2014\u4f46\u95ee\u9898\u5728\u4e8e\uff0c\u90a3\u660e\u663e\u5e76\u4e0d\u662f\u5438\u8840\u9b3c\u7684\u5c38\u4f53\uff0c\u800c\u662f\u67d0\u4e2a\u964c\u751f\u4eba\uff0c\u8fde\u8863\u670d\u7684\u989c\u8272\u90fd\u4e0d\u5bf9\u3002</p><p>\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f</p><p>\u5f3a\u70c8\u65e0\u6bd4\u7684\u9b54\u529b\u6ce2\u52a8\u7a81\u5140\u5730\u4ece\u80cc\u540e\u88ad\u6765\uff0c\u950b\u9510\u5982\u5251\uff0c\u51db\u51db\u51b0\u5bd2\uff0c\u523a\u5f97\u743c\u6069\u7684\u80cc\u4e0a\u5bd2\u6bdb\u90fd\u4e0d\u7531\u81ea\u4e3b\u5730\u609a\u8d77\uff0c\u51e0\u4e4e\u662f\u4e0b\u610f\u8bc6\u5730\uff0c\u4ed6\u542f\u52a8\u4e86\u6cd5\u672f\u9006\u8f6c\u6212\u6307\u3002\u201c\u55e4\u55e4\u55e4\u201d\u8fde\u58f0\u9510\u54cd\uff0c\u4e09\u652f\u78a7\u706b\u8426\u7f2d\u7684\u87ba\u65cb\u9aa8\u77db\u81ea\u865a\u7a7a\u4e2d\u7a7f\u51fa\uff0c\u63a5\u8fde\u5c04\u5728\u743c\u6069\u7684\u80cc\u4e0a\uff0c\u7136\u540e\u88ab\u53cd\u5f39\u4e86\u51fa\u53bb\uff0c\u5728\u8fd4\u56de\u7684\u9014\u4e2d\u81ea\u884c\u7206\u70b8\u5f00\u6765\uff0c\u5316\u4f5c\u65e0\u6570\u5c16\u9510\u7684\u788e\u7247\u56db\u6563\u8ff8\u5c04\u3002</p><p>\u743c\u6069\u5feb\u901f\u8f6c\u8fc7\u8eab\u6765\uff0c\u6b63\u770b\u89c1\u9065\u8fdc\u7684\u6811\u836b\u4e0b\uff0c\u6570\u5341\u9053\u624b\u81c2\u7c97\u7ec6\u7684\u9ed1\u8272\u96fe\u6c14\u5728\u7a7a\u4e2d\u50cf\u87d2\u86c7\u4e00\u6837\u4ea4\u9519\u7f20\u7ed5\u7740\uff0c\u51dd\u805a\u6210\u5438\u8840\u9b3c\u970d\u6587\u3002\u4ed6\u770b\u8d77\u6765\u6a21\u6837\u5341\u5206\u53ef\u6016\uff0c\u8863\u88f3\u7834\u88c2\uff0c\u6240\u6709\u66b4\u9732\u5728\u7a7a\u6c14\u4e2d\u7684\u808c\u80a4\uff0c\u5305\u62ec\u9762\u90e8\u3001\u8116\u9888\u3001\u624b\u81c2\uff0c\u90fd\u904d\u5e03\u7740\u5751\u5751\u6d3c\u6d3c\u7684\u4f24\u53e3\uff0c\u6709\u7684\u5f88\u6d45\uff0c\u6709\u7684\u6df1\u53ef\u89c1\u9aa8\uff0c\u5374\u6ca1\u6709\u534a\u6ef4\u8840\u6d41\u51fa\uff0c\u66f4\u50cf\u662f\u88ab\u70c8\u7130\u711a\u70e7\u8fc7\u4e00\u822c\uff0c\u4f24\u5904\u5468\u56f4\u7684\u8840\u8089\u90fd\u5df2\u7ecf\u5b8c\u5168\u51dd\u7ed3\u6210\u7126\u9ed1\u8272\uff0c\u4eff\u4f5b\u7070\u70ec\u2014\u2014\u800c\u5728\u8fd9\u7070\u70ec\u4e4b\u4e0b\uff0c\u53c8\u6709\u4eff\u4f5b\u86c7\u866b\u822c\u7684\u6d3b\u7269\u5728\u7f13\u7f13\u8815\u52a8\uff0c\u50cf\u8981\u6323\u624e\u7740\u9f13\u51fa\u6765\u3002\u90a3\u662f\u5438\u8840\u9b3c\u7684\u5f3a\u608d\u81ea\u6108\u529f\u80fd\u5728\u53d1\u6325\u4f5c\u7528\uff0c\u6700\u591a\u5341\u79d2\u949f\uff0c\u8fd9\u70b9\u4f24\u52bf\u5c31\u80fd\u5b8c\u5168\u6062\u590d\u5982\u521d\u5427\u3002</p><p>\u4f46\u5438\u8840\u9b3c\u663e\u7136\u88ab\u5f7b\u5e95\u6fc0\u6012\u4e86\u3002\u78a7\u5e7d\u5e7d\u7684\u201c\u8840\u5203\u201d\u5728\u624b\u4e2d\u6d88\u5931\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u4e00\u67c4\u95ea\u7535\u5f62\u72b6\u7684\u9ed1\u8272\u6cd5\u6756\uff0c\u9876\u7aef\u9576\u5d4c\u7740\u692d\u5706\u5f62\u7684\u5929\u84dd\u8272\u5b9d\u77f3\uff0c\u5728\u9ed1\u6697\u4e2d\u6cdb\u7740\u7f8e\u4e3d\u7684\u91d1\u5c5e\u5149\u6cfd\uff0c\u504f\u53c8\u8574\u542b\u7740\u68ee\u68ee\u6b7b\u6c14\uff0c\u4ee4\u4eba\u4e0d\u5bd2\u800c\u6817\u3002\u201c\u4f60\u662f\u5728\u81ea\u5bfb\u6b7b\u8def\uff0c\u5c0f\u5b50\uff01\u201d\u4ed6\u6076\u72e0\u72e0\u5730\u8bf4\uff0c\u4e3e\u8d77\u6cd5\u6756\u6307\u5411\u743c\u6069\uff0c\u201c\u6211\u8ba8\u538c\u6652\u592a\u9633\uff01\u201d</p><p>\u743c\u6069\u521a\u624d\u8bbe\u8ba1\uff0c\u8868\u9762\u4e0a\u662f\u7528\u98d3\u98ce\u653b\u51fb\uff0c\u5374\u5728\u6700\u540e\u5173\u5934\u731b\u7136\u6269\u6563\u51fa\u53bb\uff0c\u9707\u5012\u5468\u56f4\u7684\u6811\u6728\u906e\u853d\uff0c\u8ba9\u5438\u8840\u9b3c\u5b8c\u5168\u66b4\u9732\u5728\u9633\u5149\u4e0b\uff0c\u9669\u4e9b\u5c06\u4ed6\u7167\u5f97\u5f53\u573a\u7070\u98de\u70df\u706d\u3002\u5bf9\u4e8e\u4e00\u540d\u5438\u8840\u9b3c\u800c\u8a00\uff0c\u8fd9\u4e0d\u4ec5\u4ec5\u662f\u8eab\u4f53\u4e0a\u7684\u75db\u82e6\u548c\u4f24\u5bb3\uff0c\u540c\u65f6\u8fd8\u662f\u4e00\u79cd\u7cbe\u795e\u4e0a\u7684\u6298\u78e8\u3002\u5982\u679c\u53ef\u4ee5\u9009\u62e9\u7684\u8bdd\uff0c\u5f88\u591a\u5438\u8840\u9b3c\u5b81\u613f\u88ab\u5200\u5251\u780d\u6389\u80a2\u4f53\uff0c\u4e5f\u4e0d\u613f\u88ab\u9633\u5149\u7167\u5c04\u534a\u79d2\u949f\u3002\u5c3d\u7ba1\u970d\u6587\u5df2\u7ecf\u662f\u957f\u8001\u4f4d\u9636\u7684\u5438\u8840\u9b3c\uff0c\u8ddd\u79bb\u201c\u65e5\u884c\u8005\u201d\u7684\u5883\u754c\u4e5f\u53ea\u5dee\u4e00\u6b65\uff0c\u4f46\u5bf9\u9633\u5149\u7684\u672c\u80fd\u538c\u6076\u548c\u6050\u60e7\uff0c\u65e9\u5df2\u7ecf\u6df1\u6df1\u94ed\u523b\u5728\u4e86\u7075\u9b42\u91cc\uff0c\u8fd9\u662f\u5438\u8840\u9b3c\u7684\u672c\u8d28\u6240\u51b3\u5b9a\u7684\u3002\u5c31\u5982\u9f99\u6709\u9006\u9cde\uff0c\u89e6\u4e4b\u5219\u6012\uff0c\u6012\u5219\u6740\u4eba\u2014\u2014\u5bf9\u4e8e\u5438\u8840\u9b3c\u800c\u8a00\uff0c\u743c\u6069\u6b64\u4e3e\u4fbf\u662f\u89e6\u4e86\u4ed6\u7684\u9006\u9cde\u3002\u5982\u679c\u8bf4\u5728\u6b64\u4e4b\u524d\uff0c\u970d\u6587\u8fd8\u5b58\u4e86\u51e0\u5206\u8bd5\u63a2\u4e4b\u610f\uff0c\u5176\u5b9e\u5e76\u672a\u5f53\u771f\u8d77\u6740\u5fc3\u7684\u8bdd\uff0c\u73b0\u5728\u4ed6\u5c31\u662f\u771f\u6b63\u60f3\u8981\u743c\u6069\u7684\u547d\u4e86\u3002</p><p>\u7136\u800c\uff0c\u743c\u6069\u5e76\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\u6740\u7684\u3002</p><p>\u76db\u6012\u4e4b\u4e0b\u7684\u5438\u8840\u9b3c\u9ad8\u58f0\u5ff5\u8bf5\u5492\u8bed\uff0c\u6c79\u6d8c\u7684\u9b54\u529b\u81ea\u6cd5\u6756\u4e2d\u91ca\u653e\u51fa\u6765\uff0c\u51dd\u805a\u6210\u5404\u79cd\u5404\u6837\u7684\u5371\u9669\u6b66\u5668\uff1a\u75be\u98ce\u66b4\u96e8\u822c\u7684\u98ce\u66b4\u98de\u5f39\u3001\u9707\u8033\u6b32\u804b\u7684\u96f7\u9e23\u97f3\u6ce2\u3001\u8000\u773c\u593a\u76ee\u7684\u707f\u70c2\u95ea\u7535\u3001\u51a5\u706b\u8426\u7ed5\u7684\u68ee\u9510\u9aa8\u77db\u3001\u4ea4\u9519\u65a9\u5207\u7684\u900f\u660e\u98ce\u5203\uff0c\u4ee5\u53ca\u9690\u853d\u5176\u4e2d\u65e0\u5f62\u65e0\u5f71\u7684\u5404\u79cd\u9ed1\u6697\u8bc5\u5492\u2014\u2014\u7136\u800c\u8fd9\u4e00\u5207\u5168\u90fd\u88ab\u6321\u4f4f\u4e86\u3002\u6d3b\u5316\u98de\u884c\u7684\u9f99\u9cde\u76fe\u3001\u514d\u75ab\u4e00\u5207\u5373\u6b7b\u6cd5\u672f\u7684\u5f71\u706b\u3001\u963b\u9694\u6240\u6709\u7cbe\u795e\u653b\u51fb\u7684\u610f\u5fd7\u5c4f\u969c\uff0c\u8fd8\u6709\u5965\u6c83\u8d60\u9001\u7684\u6cd5\u672f\u9006\u8f6c\u6212\u6307\uff0c\u8ba9\u743c\u6069\u5728\u77ed\u65f6\u95f4\u5185\u62e5\u6709\u4e86\u8fd1\u4e4e\u5b8c\u7f8e\u7684\u9b54\u6cd5\u9632\u5fa1\u3002\u5438\u8840\u9b3c\u6240\u6709\u7684\u653b\u51fb\uff0c\u6216\u8005\u88ab\u683c\u6321\uff0c\u6216\u8005\u88ab\u95ea\u907f\uff0c\u6216\u8005\u88ab\u5438\u6536\uff0c\u6216\u8005\u88ab\u53cd\u5f39\uff0c\u6216\u8005\u5e72\u8106\u5c31\u77f3\u6c89\u5927\u6d77\uff0c\u6084\u65e0\u58f0\u606f\u5730\u6d88\u5931\u3002</p><p>\u743c\u6069\u6c89\u9ed8\u5730\u65bd\u6cd5\uff0c\u78a7\u7eff\u7684\u9b54\u6cd5\u80fd\u91cf\u5728\u7d2b\u6c34\u6676\u6cd5\u6756\u7684\u9876\u7aef\u84c4\u79ef\uff0c\u51c6\u5907\u53d1\u52a8\u53cd\u51fb\u3002\u4e24\u5e74\u7684\u8270\u96be\u78e8\u7ec3\uff0c\u4e0a\u767e\u6b21\u7684\u751f\u6b7b\u640f\u6740\uff0c\u8ba9\u4ed6\u4e5f\u5df2\u7ecf\u5728\u4e0d\u77e5\u4e0d\u89c9\u95f4\u6210\u957f\u8d77\u6765\uff0c\u4e0d\u518d\u662f\u5f53\u5e74\u90a3\u4e2a\u521d\u51fa\u8305\u5e90\u7684\u83dc\u9e1f\u4e86\u3002\u9b54\u6cd5\u9020\u8be3\u7684\u589e\u957f\u4e14\u4e0d\u8bba\uff0c\u4e34\u654c\u7684\u7ecf\u9a8c\uff0c\u5bf9\u9635\u7684\u76f4\u89c9\uff0c\u5f62\u52bf\u7684\u5224\u65ad\uff0c\u8fd9\u4e9b\u90fd\u6709\u7740\u957f\u8db3\u7684\u8fdb\u6b65\u3002\u4ed6\u4e4b\u6240\u4ee5\u6562\u63a5\u53d7\u5438\u8840\u9b3c\u7684\u63d0\u8bae\uff0c\u5355\u72ec\u7559\u4e0b\u6765\u4e00\u5bf9\u4e00\u6218\u6597\uff0c\u5e76\u975e\u5934\u8111\u53d1\u70ed\uff0c\u8981\u5728\u5973\u53cb\u9762\u524d\u8868\u73b0\u82f1\u96c4\u6c14\u6982\uff0c\u4e5f\u662f\u6709\u5176\u81ea\u4fe1\u548c\u628a\u63e1\u7684\u3002</p><p>\u56e0\u4e3a\u4ed6\u9762\u5bf9\u7684\u662f\u4e00\u53ea\u5438\u8840\u9b3c\u3002</p><p>\u8bf8\u591a\u4ea1\u7075\u4e4b\u4e2d\uff0c\u5438\u8840\u9b3c\u7b97\u662f\u975e\u5e38\u9ad8\u7ea7\u7684\u8d27\u8272\uff0c\u4f46\u4e5f\u662f\u5f31\u70b9\u6700\u591a\u7684\u3002\u4e0d\u4ec5\u6015\u8d85\u5ea6\uff0c\u6015\u5723\u5149\u5723\u6c34\uff08\u8fd9\u662f\u6240\u6709\u4ea1\u7075\u7684\u5171\u6027\uff09\uff0c\u989d\u5916\u8fd8\u6015\u592a\u9633\uff0c\u6015\u94f6\u5236\u6b66\u5668\uff0c\u6015\u65a9\u9996\uff0c\u6015\u88ab\u957f\u9489\u9489\u5165\u5fc3\u810f\uff0c\u4f4e\u7ea7\u5438\u8840\u9b3c\u8fd8\u6015\u6d41\u52a8\u7684\u6cb3\u6c34\uff0c\u6015\u955c\u5b50\uff0c\u7b49\u7b49\u7b49\u7b49\u4e00\u5806\u6bdb\u75c5\u3002\u4ed6\u4eec\u5fc5\u987b\u5b9a\u671f\u5438\u98df\u65b0\u9c9c\u8840\u6db2\uff0c\u5426\u5219\u4e0d\u4f46\u4f1a\u529b\u91cf\u8870\u7aed\uff0c\u8fde\u81ea\u8eab\u7684\u201c\u5b58\u5728\u201d\u90fd\u65e0\u6cd5\u7ef4\u6301\uff0c\u54ea\u50cf\u5176\u4ed6\u4ea1\u7075\u90a3\u4e48\u575a\u633a\uff0c\u6254\u5893\u7a74\u91cc\u5173\u4e2a\u51e0\u767e\u5e74\u90fd\u6ca1\u4e8b\u3002\u5f53\u7136\uff0c\u5438\u8840\u9b3c\u6f02\u4eae\u3001\u7f8e\u578b\uff0c\u80fd\u591f\u4fdd\u6301\u751f\u8005\u7684\u5bb9\u8c8c\u5f62\u6001\uff0c\u8fd8\u80fd\u548c\u5f02\u6027\u505a\u7231\uff0c\u8fd9\u5c31\u662f\u6700\u5927\u7684\u4f18\u70b9\uff0c\u8db3\u4ee5\u63a9\u76d6\u4e00\u5207\u4e0d\u8db3\u3002\u95ee\u9898\u5728\u4e8e\uff0c\u6f02\u4eae\u7ec8\u7a76\u662f\u4e0d\u80fd\u5f53\u996d\u5403\u7684\uff0c\u81f3\u5c11\u5728\u6218\u573a\u4e0a\u4e0d\u80fd\u2014\u2014\u81f3\u5c11\uff0c\u5728\u9762\u5bf9\u540c\u6027\u654c\u4eba\u7684\u65f6\u5019\u4e0d\u80fd\u3002</p><p>\u6b64\u65f6\u662f\u4e0b\u5348\u4e24\u70b9\u591a\u949f\uff0c\u4e0d\u5230\u4e09\u70b9\uff0c\u70c8\u65e5\u5f53\u7a7a\uff0c\u9633\u5149\u707f\u70c2\uff0c\u4e07\u91cc\u65e0\u4e91\u3002\u8eab\u4e3a\u4e00\u53ea\u5438\u8840\u9b3c\uff0c\u8fd9\u79cd\u65f6\u5019\u5c31\u8be5\u4e56\u4e56\u8eb2\u5728\u68fa\u6750\u91cc\u7761\u89c9\u517b\u795e\u624d\u5bf9\uff0c\u5c45\u7136\u8dd1\u51fa\u6765\u6e9c\u8fbe\uff0c\u7eaf\u5c5e\u81ea\u5df1\u627e\u6b7b\u3002\u53e4\u4eba\u8bf4\uff0c\u4f5c\u6218\u4e09\u8981\u7d20\uff0c\u5929\u65f6\u3001\u5730\u5229\u3001\u4eba\u548c\uff0c\u540e\u4e24\u9879\u6682\u4e14\u4e0d\u8bba\uff0c\u81f3\u5c11\u5728\u201c\u5929\u65f6\u201d\u4e0a\uff0c\u81ea\u5df1\u5c31\u5df2\u7ecf\u662f\u5360\u636e\u7edd\u5bf9\u4f18\u52bf\u4e86\u3002</p><p>\u800c\u8fd9\u79cd\u4f18\u52bf\u6b63\u5728\u53d8\u6210\u73b0\u5b9e\u3002</p><p>\u5438\u8840\u9b3c\u8eaf\u4f53\u5f3a\u608d\uff0c\u529b\u5927\u65e0\u7a77\uff0c\u5982\u679c\u8fd1\u8eab\u683c\u6597\u7684\u8bdd\uff0c\u5c31\u51ed\u743c\u6069\u90a3\u4e09\u811a\u732b\u7684\u62f3\u811a\u529f\u592b\uff0c\u53ea\u6015\u6491\u4e0d\u8fc7\u4e09\u62db\u2014\u2014\u4f46\u73b0\u5728\u5c31\u4e0d\u7528\u62c5\u5fc3\u4e86\u3002\u88ab\u98d3\u98ce\u6a2a\u626b\u4e4b\u540e\uff0c\u539f\u672c\u8302\u5bc6\u7684\u6811\u6797\u4e2d\u51fa\u73b0\u4e86\u4e00\u5757\u76f4\u5f84\u8d85\u8fc7\u4e0a\u767e\u82f1\u5c3a\u7684\u7a7a\u5730\uff0c\u743c\u6069\u4fbf\u6b63\u7ad9\u5728\u8fd9\u5757\u7a7a\u5730\u7684\u4e2d\u5fc3\uff0c\u56e0\u4e3a\u754f\u60e7\u9633\u5149\u7684\u7167\u5c04\uff0c\u5438\u8840\u9b3c\u4e0d\u6562\u9760\u8fd1\uff0c\u53ea\u662f\u7ad9\u5728\u8fdc\u5904\u7684\u6811\u836b\u4e2d\u7528\u6cd5\u672f\u653b\u51fb\uff0c\u8fd9\u7b49\u4e8e\u76f4\u63a5\u5c31\u524a\u5f31\u4e86\u4ed6\u4e00\u5927\u534a\u7684\u5b9e\u529b\u3002\u81f3\u4e8e\u9b54\u6cd5\u5bf9\u8f70\u7684\u8bdd\uff0c\u743c\u6069\u5e76\u4e0d\u62c5\u5fc3\uff0c\u5355\u7eaf\u8bba\u5965\u672f\u4e0a\u7684\u9020\u8be3\uff0c\u5438\u8840\u9b3c\u786e\u5b9e\u662f\u6bd4\u4ed6\u8981\u5f3a\u4e00\u4e9b\uff0c\u4f46\u4e5f\u5f3a\u5f97\u6709\u9650\uff0c\u81f3\u5c11\u5e76\u6ca1\u6709\u5f62\u6210\u975e\u5e38\u660e\u663e\u7684\u4f18\u52bf\u3002\u501f\u52a9\u88c5\u5907\u548c\u795e\u529b\u7684\u52a0\u6301\uff0c\u743c\u6069\u53ea\u5b88\u4e0d\u653b\uff0c\u8c28\u614e\u5e94\u5bf9\uff0c\u53ef\u4ee5\u8bf4\u662f\u7a33\u7a33\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\u3002</p><p>\u5728\u9876\u8fc7\u524d\u51e0\u8f6e\u7684\u75af\u72c2\u8fdb\u653b\u540e\uff0c\u743c\u6069\u5f00\u59cb\u8d81\u9699\u53d1\u52a8\u53cd\u51fb\u3002\u5965\u6c83\u4f20\u6388\u7684\u8d85\u9b54\u6280\u5de7\uff0c\u8fd9\u79cd\u65f6\u5019\u4fbf\u5c55\u73b0\u51fa\u5176\u60ca\u4eba\u5a01\u529b\u6765\uff0c\u660e\u660e\u662f\u9b54\u6cd5\u9020\u8be3\u76f8\u5bf9\u900a\u8272\u7684\u4e00\u65b9\uff0c\u4f46\u743c\u6069\u7684\u65bd\u6cd5\u901f\u5ea6\u5374\u6bd4\u5438\u8840\u9b3c\u8fd8\u8981\u5feb\uff0c\u800c\u4e14\u66f4\u52a0\u9690\u853d\uff0c\u65e0\u9700\u8bf5\u5492\uff0c\u4e5f\u6ca1\u6709\u8db3\u4ee5\u8ba9\u4eba\u8b66\u60d5\u7684\u7e41\u590d\u52a8\u4f5c\uff0c\u7834\u574f\u529b\u5de8\u5927\u7684\u6cd5\u672f\u4fbf\u77ac\u95f4\u91ca\u653e\u51fa\u6765\u3002\u970d\u6587\u731d\u4e0d\u53ca\u9632\u4e4b\u4e0b\uff0c\u53f3\u81c2\u81ea\u80a9\u4ee5\u4e0b\u88ab\u89e3\u79bb\u5c04\u7ebf\u5b8c\u5168\u7c89\u788e\uff0c\u8179\u90e8\u5219\u88ab\u77f3\u5f39\u7838\u51fa\u4e86\u4e00\u4e2a\u62f3\u5934\u5927\u7684\u8840\u6d1e\uff0c\u5df2\u7ecf\u80fd\u591f\u6e05\u695a\u770b\u89c1\u5185\u810f\u3002\u773c\u770b\u5bf9\u624b\u7684\u653b\u51fb\u6cd5\u672f\u518d\u5ea6\u88ad\u6765\uff0c\u970d\u6587\u4e0d\u5f97\u4e0d\u634f\u788e\u4e86\u4e00\u7c92\u9ed1\u8272\u73cd\u73e0\uff0c\u4f7f\u7528\u4e86\u67d0\u79cd\u7c7b\u4f3c\u4e8e\u201c\u9634\u5f71\u8df3\u8dc3\u201d\u4e4b\u7c7b\u7684\u6cd5\u672f\uff0c\u5728\u6811\u836b\u4e2d\u63a5\u8fde\u53d8\u6362\u4e09\u6b21\u65b9\u4f4d\uff0c\u6210\u529f\u907f\u5f00\u743c\u6069\u7684\u8ffd\u88ad\u3002\u800c\u5f53\u4ed6\u6700\u7ec8\u7ad9\u5b9a\u65f6\uff0c\u53f3\u81c2\u5df2\u7ecf\u957f\u51fa\u4e00\u5927\u534a\uff0c\u53ea\u5269\u4e0b\u8155\u90e8\u548c\u624b\u638c\u4e86\uff0c\u4f46\u8179\u90e8\u7684\u4f24\u53e3\u5219\u4f9d\u65e7\u7ef4\u6301\u539f\u72b6\u3002</p><p>\u201c\u5207\uff0c\u771f\u9ebb\u70e6\u3002\u201d</p><p>\u743c\u6069\u5934\u75bc\u5730\u770b\u7740\u5bf9\u624b\uff0c\u653e\u5f03\u653b\u51fb\u518d\u5ea6\u8f6c\u4e3a\u9632\u5fa1\u3002\u957f\u8001\u7ea7\u5438\u8840\u9b3c\uff0c\u62e5\u6709\u8fbe\u5230\u201c\u65ad\u80a2\u91cd\u751f\u201d\u7a0b\u5ea6\u7684\u5f3a\u608d\u81ea\u6108\u80fd\u529b\uff0c\u53ea\u8981\u5934\u90e8\u548c\u5fc3\u810f\u4e0d\u88ab\u7834\u574f\uff0c\u4e24\u8005\u4e4b\u95f4\u7684\u8054\u7ed3\u4e0d\u88ab\u5207\u65ad\uff0c\u5373\u4fbf\u906d\u5230\u518d\u4e25\u91cd\u7684\u4f24\u52bf\u90fd\u80fd\u591f\u5feb\u901f\u590d\u539f\uff0c\u582a\u79f0\u662f\u4e0d\u6b7b\u4e4b\u8eab\u3002</p><p>\u4e0d\u8fc7\uff0c\u8fd9\u79cd\u81ea\u6108\u80fd\u529b\u4e5f\u5e76\u4e0d\u662f\u6beb\u65e0\u9650\u5236\u7684\u5427\u3002</p><p>\u5982\u679c\u8bf4\u201c\u7834\u574f\u201d\u9700\u8981\u5f88\u5927\u7684\u80fd\u91cf\uff0c\u90a3\u4e48\u201c\u4fee\u590d\u201d\u65e0\u7591\u9700\u8981\u66f4\u591a\u7684\u80fd\u91cf\uff0c\u5bf9\u4e8e\u5438\u8840\u9b3c\u6765\u8bf4\uff0c\u80fd\u91cf\u7684\u6765\u6e90\u5c31\u662f\u9c9c\u8840\u3002\u4ed6\u4eec\u5c06\u5438\u98df\u5230\u7684\u9c9c\u8840\u50a8\u5b58\u5728\u4f53\u5185\uff0c\u7ef4\u6301\u8eaf\u4f53\u7684\u5b58\u5728\u4e0d\u673d\uff0c\u652f\u6491\u5404\u79cd\u5f02\u80fd\u7684\u8fd0\u7528\u6d88\u8017\u3002\u518d\u5f3a\u7684\u5438\u8840\u9b3c\uff0c\u201c\u50a8\u5b58\u201d\u7684\u5bb9\u91cf\u7ec8\u7a76\u662f\u6709\u9650\u7684\uff0c\u662f\u4f1a\u8017\u5c3d\u7684\uff0c\u6240\u4ee5\u624d\u9700\u8981\u5b9a\u671f\u8865\u5145\uff0c\u5c31\u50cf\u4eba\u9700\u8981\u4e00\u65e5\u4e09\u9910\uff0c\u8fd9\u987f\u5403\u9971\u4e86\uff0c\u4e0b\u987f\u4f9d\u65e7\u4f1a\u997f\u3002\u6240\u4ee5\u4ece\u7406\u8bba\u4e0a\u8bf4\uff0c\u4f24\u5bb3\u79ef\u7d2f\u5230\u4e00\u5b9a\u7a0b\u5ea6\uff0c\u8d85\u51fa\u4e86\u5438\u8840\u9b3c\u5269\u4f59\u50a8\u5b58\u7684\u201c\u91cf\u201d\uff0c\u4ed6\u5c31\u65e0\u529b\u81ea\u6108\u4e86\u3002\u743c\u6069\u4e0d\u77e5\u9053\u957f\u8001\u7ea7\u5438\u8840\u9b3c\u7684\u50a8\u5b58\u201c\u5bb9\u91cf\u201d\u7a76\u7adf\u662f\u591a\u5c11\uff0c\u8fd8\u80fd\u652f\u6491\u51e0\u6b21\u7c7b\u4f3c\u7a0b\u5ea6\u81ea\u6108\u7684\u6d88\u8017\uff0c\u4e0d\u8fc7\u2026\u2026\u5148\u662f\u201c\u8840\u5203\u201d\u88ab\u827e\u5f25\u8587\u76f4\u63a5\u51fb\u6e83\uff0c\u63a5\u7740\u53c8\u88ab\u9633\u5149\u7167\u5c04\uff0c\u7136\u540e\u662f\u6cd5\u672f\u7684\u4f24\u5bb3\uff0c\u635f\u5931\u4e86\u8d56\u4ee5\u4f5c\u6218\u7684\u53f3\u81c2\uff0c\u4e0d\u5f97\u4e0d\u5f53\u573a\u81ea\u6108\u3002\u4ece\u4ed6\u653e\u4efb\u8179\u90e8\u7684\u4f24\u53e3\u4e0d\u7ba1\u8fd9\u70b9\u6765\u770b\uff0c\u5c31\u7b97\u672a\u5230\u201c\u6781\u9650\u201d\uff0c\u5e94\u8be5\u4e5f\u6240\u5269\u4e0d\u591a\u4e86\u5462\u3002</p><p>\u6211\u8fd8\u6709\u4e00\u53d1\u89e3\u79bb\u672f\uff0c\u5982\u679c\u8fd0\u6c14\u597d\u7684\u8bdd\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u80fd\u5e72\u6389\u4ed6\u4e86\u2026\u2026</p><p>\u6b63\u81ea\u76d8\u7b97\u7740\uff0c\u5c31\u89c1\u5438\u8840\u9b3c\u4f38\u624b\u4ece\u6000\u91cc\u6478\u51fa\u4e86\u4e00\u4e2a\u7cbe\u81f4\u7684\u900f\u660e\u5c0f\u74f6\u5b50\uff0c\u4ef0\u5934\u5c06\u5176\u4e2d\u7684\u7ea2\u8272\u6db2\u4f53\u2014\u2014\u660e\u663e\u662f\u8840\u6db2\u2014\u2014\u4e00\u996e\u800c\u5c3d\uff0c\u539f\u672c\u5df2\u7ecf\u505c\u6ede\u7684\u4f24\u5904\u8840\u8089\u7acb\u523b\u53c8\u91cd\u65b0\u751f\u957f\u8d77\u6765\uff0c\u773c\u770b\u7acb\u523b\u5c31\u80fd\u75ca\u6108\u3002\u743c\u6069\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u4e00\u65f6\u95f4\u90fd\u5fd8\u4e86\u52a8\u624b\u3002</p><p>\u201c\u2026\u2026\u5582\u5582\uff0c\u5c45\u7136\u6218\u6597\u7684\u65f6\u5019\u559d\u8840\u74f6\uff0c\u4e0d\u5e26\u8fd9\u4e48\u4f5c\u5f0a\u7684\u5427\u3002\u201d</p><p>\u5438\u8840\u9b3c\u7684\u529b\u91cf\u6765\u81ea\u9c9c\u8840\uff0c\u6309\u9053\u7406\u8bf4\u662f\u5fc5\u987b\u76f4\u63a5\u4ece\u6d3b\u7269\u8eab\u4e0a\u5438\u53d6\u624d\u6709\u7528\uff0c\u88c5\u8fdb\u74f6\u5b50\u50a8\u5b58\u8d77\u6765\uff0c\u90a3\u5c31\u5df2\u7ecf\u4e27\u5931\u4e86\u5176\u4e2d\u7684\u7075\u6c14\uff0c\u6210\u4e86\u201c\u6b7b\u8840\u201d\uff0c\u5bf9\u5438\u8840\u9b3c\u800c\u8a00\u662f\u5e9f\u7269\u3002\u4f46\u5e38\u7406\u5f52\u5e38\u7406\uff0c\u773c\u524d\u8fd9\u5bb6\u4f19\u660e\u663e\u5c31\u662f\u4e2a\u4f8b\u5916\uff0c\u4e0d\u77e5\u7528\u4e86\u4ec0\u4e48\u65b9\u6cd5\u5c45\u7136\u505a\u6210\u4e86\u8840\u74f6\u2026\u2026\u8fd9\u53ef\u771f\u662f\u540d\u526f\u5176\u5b9e\u7684\u201c\u8840\u74f6\u201d\u4e86\u3002</p><p>\u5206\u522b\u7ad9\u5728\u707f\u70c2\u9633\u5149\u4e0b\u548c\u9634\u6697\u6811\u836b\u4e2d\uff0c\u743c\u6069\u548c\u5438\u8840\u9b3c\u9065\u9065\u76f8\u5bf9\uff0c\u4e00\u65f6\u95f4\u8c01\u4e5f\u6ca1\u6709\u4e3b\u52a8\u653b\u51fb\uff0c\u5c40\u9762\u50f5\u6301\u4f4f\u4e86\u2014\u2014\u4f46\u50f5\u5c40\u5e76\u6ca1\u6709\u7ef4\u6301\u592a\u4e45\u3002\u5341\u79d2\u949f\u540e\uff0c\u5438\u8840\u9b3c\u7684\u624b\u638c\u5df2\u7ecf\u91cd\u65b0\u957f\u51fa\uff0c\u8179\u90e8\u4f24\u52bf\u4e5f\u5b8c\u5168\u590d\u539f\u3002\u4ed6\u51b7\u51b7\u5730\u770b\u4e86\u743c\u6069\u4e00\u773c\uff0c\u6536\u56de\u6cd5\u6756\uff0c\u7136\u540e\u5411\u524d\u8e0f\u51fa\uff0c\u8d70\u51fa\u6811\u836b\u3002</p><p>\u4ed6\u8d70\u8fdb\u4e86\u9633\u5149\u4e2d\u3002</p><p>\u201c\u6211\u627f\u8ba4\uff0c\u4e00\u5f00\u59cb\u592a\u5c0f\u770b\u4f60\u4e86\uff0c\u201d\u5438\u8840\u9b3c\u8bf4\uff0c\u7f13\u6b65\u903c\u8fd1\uff0c\u201c\u8fd9\u786e\u5b9e\u662f\u6211\u7684\u5931\u8bef\uff0c\u80fd\u591f\u8bbe\u8ba1\u4ee4\u9f99\u5deb\u6559\u4e3b\u91cd\u521b\u9003\u9041\u7684\u4eba\u7269\uff0c\u65e0\u8bba\u56e0\u4e3a\u4ec0\u4e48\u7406\u7531\uff0c\u6211\u90fd\u4e0d\u5e94\u8be5\u8f7b\u89c6\u2014\u2014\u4e0d\u8fc7\uff0c\u4eca\u5929\u7684\u6e38\u620f\u65f6\u95f4\u4e5f\u5c31\u5230\u6b64\u4e3a\u6b62\u4e86\u3002\u201d</p><p>\u8840\u7ea2\u8272\u7684\u6de1\u6de1\u96fe\u6c14\u4e0d\u65ad\u5730\u4ece\u4ed6\u4f53\u5185\u84b8\u53d1\u51fa\u6765\uff0c\u5f62\u6210\u4e86\u4e00\u5c42\u6a21\u7cca\u7684\u62a4\u7f69\uff0c\u867d\u7136\u5728\u9633\u5149\u7684\u7167\u5c04\u4e0b\u4e0d\u65ad\u6d88\u878d\uff0c\u5374\u53c8\u4e0d\u65ad\u5730\u8865\u5145\uff0c\u59cb\u7ec8\u7ef4\u6301\u7740\u5e73\u8861\uff0c\u4ee4\u4ed6\u4e0d\u53d7\u4f24\u5bb3\u3002\u201c\u9633\u5149\u662f\u6211\u4eec\u7684\u5929\u654c\uff0c\u80fd\u591f\u501f\u52a9\u5b83\u6765\u4f5c\u6218\uff0c\u5012\u4e5f\u52c9\u5f3a\u53ef\u4ee5\u7528\u2018\u660e\u667a\u2019\u6765\u79f0\u8d5e\u4e86\u3002\u4f46\u5982\u679c\u8ba4\u4e3a\u8eb2\u5728\u9633\u5149\u4e0b\uff0c\u6211\u5c31\u771f\u7684\u5bf9\u4f60\u4e00\u7b79\u83ab\u5c55\uff0c\u90a3\u53ef\u662f\u5927\u9519\u7279\u9519\uff0c\u201d\u4ed6\u6df1\u6df1\u5730\u5438\u6c14\uff0c\u78a7\u5e7d\u5e7d\u7684\u957f\u5200\u518d\u6b21\u4ece\u4f53\u5185\u5f39\u51fa\uff0c\u843d\u5165\u638c\u5fc3\uff0c\u201c\u6211\u53ef\u4e0d\u662f\u90a3\u4e9b\u8106\u5f31\u7684\u4f4e\u7ea7\u8d27\u8272\u554a\u3002\u201d</p><p>\u9762\u5bf9\u5438\u8840\u9b3c\u7684\u5ba3\u8a00\uff0c\u743c\u6069\u9ed8\u4e0d\u4f5c\u58f0\u5730\u6536\u8d77\u6cd5\u6756\uff0c\u4ece\u8170\u95f4\u62d4\u51fa\u4e86\u90a3\u67c4\u6628\u65e5\u521a\u521a\u83b7\u5f97\u7684\u5315\u9996\uff0c\u56db\u5143\u7d20\u5251\u4e2d\u7684\u98ce\u4e4b\u5203\uff0c\u63e1\u5728\u53f3\u624b\u4e2d\uff0c\u7528\u4e00\u79cd\u6311\u8845\u7684\u59ff\u52bf\u6307\u5411\u5bf9\u624b\u3002</p><p>\u5438\u8840\u9b3c\u5148\u662f\u4e00\u6014\uff0c\u968f\u5373\u5927\u7b11\u8d77\u6765\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u8981\u548c\u6211\u8f83\u91cf\u5251\u672f\u5417\uff1f\u201d</p><p>\u201c\u6b63\u6709\u6b64\u610f\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u5438\u8840\u9b3c\u7684\u773c\u775b\u772f\u7f1d\u8d77\u6765\uff0c\u5e76\u975e\u56e0\u4e3a\u9633\u5149\u7684\u7167\u5c04\uff0c\u800c\u662f\u5bf9\u743c\u6069\u5947\u602a\u4e3e\u6b62\u7684\u4e0d\u89e3\u3002\u201c\u770b\u6765\u4f60\u5bf9\u81ea\u5df1\u7684\u5251\u672f\u5f88\u81ea\u4fe1\uff1f\u6216\u8005\u8bf4\uff0c\u4f60\u8fd8\u9690\u85cf\u4e86\u522b\u7684\u5e95\u724c\uff1f\u201d</p><p>\u201c\u8bd5\u8bd5\u5c31\u77e5\u9053\u3002\u201d</p><p>\u201c\u5f88\u597d\u3002\u201d\u5438\u8840\u9b3c\u7b80\u77ed\u5730\u56de\u7b54\u3002</p><p>\u4ece\u4f53\u5185\u84b8\u817e\u51fa\u7684\u8840\u8272\u96fe\u6c14\u5df2\u7ecf\u5f00\u59cb\u51dd\u7ed3\uff0c\u53d8\u6210\u4e00\u526f\u65e2\u8584\u53c8\u900f\u660e\uff0c\u5b8c\u5168\u8d34\u8eab\u7684\u76d4\u7532\uff0c\u5c06\u5438\u8840\u9b3c\u5168\u8eab\u6240\u6709\u7684\u90e8\u4f4d\u90fd\u4e25\u5bc6\u5730\u906e\u853d\u8d77\u6765\uff0c\u4e0d\u88ab\u9633\u5149\u7684\u7167\u5c04\u4f24\u5bb3\u3002\u4ed6\u7f13\u6b65\u903c\u8fd1\u7740\uff0c\u5728\u8ddd\u79bb\u743c\u6069\u8fd8\u6709\u4e8c\u5341\u6b65\u8fdc\u7684\u5730\u65b9\uff0c\u731b\u7136\u53d1\u529b\uff0c\u8eab\u4f53\u52a0\u901f\u524d\u51b2\u3002</p><p>\u4e24\u9053\u5de8\u5927\u7684\u9ed1\u5f71\u4ece\u5730\u9762\u5347\u8d77\uff0c\u4e00\u5de6\u4e00\u53f3\u624b\u6301\u76fe\u724c\uff0c\u4eff\u4f5b\u4e24\u6247\u5927\u95e8\u5408\u62e2\u6321\u5728\u5438\u8840\u9b3c\u7684\u8eab\u524d\uff0c\u540c\u65f6\u53c8\u6709\u4e24\u67c4\u5de8\u5251\u5f53\u5934\u5288\u4e0b\uff0c\u6b63\u662f\u743c\u6069\u7684\u98df\u4eba\u9b54\u9b54\u50cf\u3002\u5b83\u4eec\u6b64\u524d\u88ab\u91ca\u653e\u51fa\u6765\uff0c\u7136\u540e\u88ab\u53d8\u56de\u68cb\u5b50\uff0c\u5374\u6ca1\u6765\u5f97\u53ca\u6536\u56de\uff0c\u800c\u662f\u4e00\u76f4\u9759\u9759\u5730\u7559\u5728\u5730\u4e0a\uff0c\u6240\u7b49\u5f85\u7684\u4fbf\u6b63\u662f\u8fd9\u4e2a\u673a\u4f1a\u3002\u5438\u8840\u9b3c\u52a8\u4f5c\u7075\u654f\uff0c\u62e5\u6709\u6781\u9ad8\u7684\u673a\u52a8\u529b\uff0c\u901a\u5e38\u60c5\u51b5\u4e0b\u9b54\u50cf\u6839\u672c\u8ffd\u8d76\u4e0d\u4e0a\uff0c\u4f46\u51fa\u5176\u4e0d\u610f\u5730\u5077\u88ad\uff0c\u4fbf\u53ef\u4ee5\u56de\u907f\u8fd9\u4e2a\u5f31\u70b9\u4e86\u3002\u5bf9\u624b\u6b63\u5728\u9ad8\u901f\u76f4\u7ebf\u524d\u51b2\uff0c\u907f\u65e0\u53ef\u907f\uff0c\u53ea\u80fd\u786c\u63a5\u8fd9\u5f53\u5934\u4e00\u51fb\u5427\u3002\u800c\u4e24\u5c0a\u9b54\u50cf\u7684\u6050\u6016\u5de8\u529b\uff0c\u5c31\u7b97\u662f\u5438\u8840\u9b3c\u4e5f\u7edd\u5bf9\u5403\u4e0d\u6d88\u7684\u3002</p><p>\u7136\u800c\u2026\u2026\u4e8b\u60c5\u5e76\u4e0d\u50cf\u9884\u6599\u5f97\u90a3\u4e48\u7b80\u5355\u3002</p><p>\u4eff\u4f5b\u65e9\u6709\u9884\u6599\uff0c\u5438\u8840\u9b3c\u5728\u5de8\u5251\u5288\u4e0b\u7684\u90a3\u4e00\u5239\u90a3\u7a81\u5140\u6b62\u6b65\uff0c\u7136\u540e\u9ad8\u9ad8\u8dc3\u8d77\uff0c\u4ee5\u6beb\u5398\u4e4b\u5dee\u8eb2\u8fc7\u653b\u51fb\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u539f\u672c\u575a\u5b9e\u7684\u5730\u9762\u9661\u7136\u53d8\u6210\u4e86\u5de8\u5927\u7684\u6ce5\u6cbc\uff0c\u6c69\u6c69\u6cdb\u7740\u9ed1\u8272\u7684\u6c14\u6ce1\uff0c\u4e24\u5c0a\u6c89\u91cd\u7684\u9b54\u50cf\u8fde\u6323\u624e\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\uff0c\u8f6c\u773c\u95f4\u4fbf\u6df1\u9677\u4e0b\u53bb\uff0c\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002\u5438\u8840\u9b3c\u5219\u4e00\u4e2a\u5e72\u8106\u5229\u843d\u7684\u51cc\u7a7a\u7ffb\u8eab\uff0c\u8f7b\u8f7b\u5de7\u5de7\u5730\u8d8a\u8fc7\u6ce5\u6f6d\uff0c\u671d\u7740\u743c\u6069\u7ee7\u7eed\u903c\u8fd1\u3002</p><p>\u201c\u5982\u679c\u8fd9\u5c31\u662f\u4f60\u7684\u5e95\u724c\uff0c\u90a3\u4e48\u6211\u53ef\u771f\u6709\u70b9\u5931\u671b\u5462\uff0c\u201d\u5438\u8840\u9b3c\u5e26\u7740\u5f97\u610f\u5730\u5fae\u7b11\uff0c\u770b\u5411\u743c\u6069\uff0c\u540e\u8005\u52c9\u5f3a\u7ef4\u6301\u7740\u9547\u5b9a\uff0c\u4f46\u4f9d\u65e7\u63a9\u9970\u4e0d\u4f4f\u795e\u60c5\u4e2d\u7684\u4e00\u4e1d\u614c\u4e71\uff0c\u201c\u533a\u533a\u9b54\u50cf\u800c\u5df2\uff0c\u5c31\u60f3\u6321\u4f4f\u6211\u5417\uff1f\u5316\u77f3\u4e3a\u6ce5\u4e4b\u7c7b\u7684\u6cd5\u672f\uff0c\u5e76\u4e0d\u662f\u53ea\u6709\u4f60\u4e00\u4e2a\u4eba\u624d\u4f1a\u7528\u554a\u3002\u201d</p><p>\u743c\u6069\u54fc\u4e86\u4e00\u58f0\u3002</p><p>\u5728\u5438\u8840\u9b3c\u7684\u773c\u4e2d\uff0c\u8fd9\u79cd\u53cd\u5e94\u610f\u5473\u7740\u754f\u60e7\uff0c\u4ee5\u53ca\u65e0\u8a00\u4ee5\u5bf9\uff0c\u4ed6\u6ee1\u610f\u5730\u7b11\u7740\uff0c\u4e3e\u8d77\u624b\u4e2d\u7684\u957f\u5200\u3002\u201c\u9b54\u50cf\u4e5f\u597d\uff0c\u673a\u5173\u4e5f\u7f62\uff0c\u6216\u8005\u5176\u4ed6\u4ec0\u4e48\u7684\u4e5f\u597d\uff0c\u5305\u62ec\u6240\u8c13\u7684\u5947\u68b0\u672f\u4e4b\u7c7b\uff0c\u5168\u90fd\u662f\u6beb\u65e0\u4ef7\u503c\u7684\u73a9\u5177\uff0c\u201d\u5438\u8840\u9b3c\u8bf4\uff0c\u201c\u552f\u6709\u81ea\u8eab\u7684\u529b\u91cf\uff0c\u624d\u662f\u6700\u771f\u5b9e\u7684\u6839\u57fa\u6240\u5728\u2014\u2014\u8bb0\u7740\u6211\u8fd9\u53e5\u8bdd\uff0c\u7136\u540e\u4e0b\u5730\u72f1\u53bb\u5427\u3002\u201d</p><p>\u78a7\u5e7d\u957f\u5200\u8fce\u9762\u65a9\u6765\uff0c\u743c\u6069\u5306\u5fd9\u4e3e\u8d77\u98ce\u4e4b\u5203\u4e00\u683c\uff0c\u60ac\u6b8a\u7684\u529b\u91cf\u5dee\u8ddd\u8ba9\u4ed6\u76f4\u63a5\u5f80\u540e\u6454\u4e86\u51fa\u53bb\u3002\u5438\u8840\u9b3c\u6c89\u7a33\u5730\u4e0a\u524d\u903c\u8fd1\uff0c\u743c\u6069\u5c31\u5730\u4e00\u6eda\uff0c\u72fc\u72c8\u65e0\u6bd4\u5730\u4ece\u5730\u4e0a\u722c\u8d77\u6765\uff0c\u582a\u582a\u6321\u4f4f\u4e86\u5438\u8840\u9b3c\u5288\u6765\u7684\u7b2c\u4e8c\u5200\u3002\u5df2\u7ecf\u9178\u9ebb\u7684\u53f3\u624b\u518d\u4e5f\u63e1\u6301\u4e0d\u4f4f\uff0c\u98ce\u4e4b\u5203\u88ab\u9707\u5f97\u8131\u624b\uff0c\u6253\u7740\u65cb\u98de\u51fa\uff0c\u659c\u63d2\u5728\u8fdc\u5904\u7684\u6ce5\u571f\u4e2d\u3002</p><p>\u5728\u5315\u9996\u8131\u624b\u7684\u540c\u65f6\uff0c\u743c\u6069\u671d\u5438\u8840\u9b3c\u731b\u6251\u8fc7\u6765\u3002</p><p>\u4e24\u9053\u5149\u8292\u5728\u53cc\u817f\u4e0a\u540c\u65f6\u70c1\u8d77\uff0c\u90a3\u662f\u50a8\u5b58\u5728\u9b54\u6cd5\u523a\u9752\u4e2d\u7684\u6cd5\u672f\u53d1\u52a8\uff0c\u201c\u732b\u4e4b\u4f18\u96c5\u201d\u548c\u201c\u52a0\u901f\u201d\u6548\u679c\u7684\u53e0\u52a0\uff0c\u8ba9\u743c\u6069\u7684\u52a8\u4f5c\u77ac\u95f4\u52a0\u5feb\u4e86\u4e00\u500d\u3002\u5438\u8840\u9b3c\u4e07\u4e07\u6ca1\u6599\u5230\u5728\u5931\u53bb\u6b66\u5668\u7684\u60c5\u51b5\u4e0b\uff0c\u5bf9\u624b\u4e0d\u9000\u53cd\u8fdb\uff0c\u63aa\u624b\u4e0d\u53ca\u4e4b\u4e0b\u88ab\u62b1\u4e86\u4e2a\u6b63\u7740\u3002\u743c\u6069\u4e00\u51fb\u5f97\u624b\uff0c\u7acb\u523b\u53d1\u52a8\u4e86\u65e9\u5df2\u6697\u4e2d\u51c6\u5907\u5b8c\u6bd5\u7684\u6cd5\u672f\u3002</p><p>\u201c\u56db\u81c2\u7329\u7329\u4e4b\u795d\u798f\uff01\u201d</p><p>\u4eff\u4f5b\u5e7b\u672f\u4e00\u822c\uff0c\u743c\u6069\u7684\u808b\u4e0b\u518d\u5ea6\u751f\u51fa\u4e24\u53ea\u624b\u81c2\uff0c\u5c06\u9519\u6115\u4e2d\u7684\u5438\u8840\u9b3c\u6b7b\u6b7b\u62b1\u4f4f\u3002\u8fd9\u662f\u4ed6\u5728\u4e0b\u5c42\u754c\u7684\u8840\u6218\u4e2d\u4e60\u5f97\u7684\u90aa\u9b54\u6cd5\u672f\uff0c\u6a21\u4eff\u70bc\u72f1\u56db\u81c2\u7329\u7329\uff0c\u8fd9\u8fd8\u662f\u7b2c\u4e00\u6b21\u771f\u6b63\u5728\u6218\u6597\u4e2d\u52a8\u7528\u3002\u5438\u8840\u9b3c\u521d\u65f6\u60ca\u6115\uff0c\u968f\u5373\u4fbf\u9547\u5b9a\u4e0b\u6765\uff0c\u5c3d\u7ba1\u53cc\u81c2\u88ab\u743c\u6069\u6b7b\u6b7b\u675f\u7f1a\uff0c\u4f46\u8981\u6323\u8131\u5e76\u4e0d\u4e3a\u96be\u3002\u201c\u4e0d\u9700\u8981\u8fd9\u4e48\u70ed\u60c5\u5427\uff0c\u201d\u4ed6\u7b11\u7740\uff0c\u201c\u6211\u5bf9\u7537\u4eba\u4e0d\u611f\u5174\u8da3\u554a\u3002\u201d</p><p>\u201c\u90a3\u4e48\u5979\u4eec\u5462\uff1f\u201d\u743c\u6069\u8bf4\u3002</p><p>\u4ec0\u4e48\uff1f\u201c\u5979\u4eec\u201d\uff1f</p><p>\u5438\u8840\u9b3c\u7684\u7591\u95ee\u8fd8\u6ca1\u8bf4\u51fa\u53e3\uff0c\u5c31\u5df2\u7ecf\u770b\u89c1\u4e86\u7b54\u6848\uff1a\u4e24\u4f4d\u7f8e\u8273\u52a8\u4eba\u7684\u5353\u5c14\u5973\u796d\u53f8\u4e0d\u77e5\u4f55\u65f6\u51fa\u73b0\u5728\u8eab\u65c1\uff0c\u9ad8\u9ad8\u4e3e\u8d77\u7684\u8718\u86db\u5723\u8f89\u4e0a\uff0c\u707f\u70c2\u7684\u767d\u5149\u8f70\u7136\u7efd\u653e\uff0c\u5982\u6d77\u6d0b\u4e00\u822c\u6df9\u6ca1\u4e0b\u6765\u3002</p></article>"}}}]);
