(window.webpackJsonp=window.webpackJsonp||[]).push([[173],{196:function(p){p.exports={chapterTitle:null,title:"\u7b2c1150\u7ae0 \u6545\u4eba\u53bb\u7559",content:"<article><h2>\u7b2c1150\u7ae0 \u6545\u4eba\u53bb\u7559</h2><p>\u3000\u3000\u5353\u57df\u7f51\u63a8\u51fa\u5546\u5e97\u8857\u9891\u9053\u534a\u4e2a\u6708\u4e4b\u540e\uff0c8818\u7f51\u4e0a\u5546\u5e97\u5c31\u63a8\u8fdb\u76f8\u4f3c\u7684\u5e97\u94fa\u8857\u9891\u9053\uff0c\u53ea\u662f\u5e73\u53f0\u7cfb\u7edf\u6bd4\u5353\u57df\u7f51\u8981\u4ed3\u4fc3\u4e00\u4e9b\uff0c\u4e5f\u6ca1\u6709\u7f6e\u5165\u7f51\u7edc\u5373\u65f6\u901a\u8baf\u5de5\u5177\uff0c\u66f4\u6ca1\u6709\u8ddf\u5176\u4ed6\u95e8\u6237\u7f51\u7ad9\u5408\u4f5c\u521b\u8bbe\u8d2d\u7269\u9891\u9053\u4f5c\u4e3a\u63a8\u5e7f\u5e73\u53f0\u3002</p><p>\u3000\u3000\u5373\u4f7f\u5982\u6b64\uff0c\u675c\u98de\u3001\u65f6\u5b66\u658c\u3001\u9a6c\u529b\u3001\u6768\u4f1f\u6bc5\u7b49\u4eba\u4e5f\u662f\u4e49\u6124\u586b\u81ba\uff0c\u89c9\u5f97\u81ea\u5df1\u515c\u91cc\u7684\u679c\u5b50\u7ed9\u522b\u4eba\u5077\u6389\u4e00\u628a\u3002</p><p>\u3000\u3000\u5728\u5f20\u606a\u96be\u5f97\u5217\u5e2d\u7684\u4e1c\u5927\u521b\u57df\u5185\u90e8\u4f1a\u8bae\u4e0a\uff0c\u4ed6\u4eec\u8c08\u8d77\u6b64\u4e8b\uff0c\u96be\u514d\u4f1a\u6028\u58f0\u8f7d\u9053\u3002</p><p>\u3000\u3000\u201c\u56fd\u5185\u7684\u7535\u5b50\u5546\u52a1\u8fd8\u4e0d\u662f\u5728\u6a21\u4eff\u4e9a\u9a6c\u900a\u3001Ebay\u6a21\u5f0f\u7684\u57fa\u7840\u4e0a\u53d1\u5c55\u8d77\u6765\u7684\uff1f\u73b0\u5728\u53ea\u662f\u6839\u636e\u56fd\u5185\u7684\u5b9e\u60c5\u8fdb\u884c\u8c03\u6574\uff0c\u5f7c\u6b64\u95f4\u501f\u9274\uff0c\u5b9e\u5728\u7b97\u4e0d\u4e0a\u4ec0\u4e48\uff0c\u201d\u5f20\u606a\u5374\u4e0d\u4ee5\u4e3a\u610f\uff0c\u544a\u8bc9\u4ed6\u4eec\uff0c\u201c\u4e4b\u524d\uff0c\u5353\u8d8a\u590d\u5236\u4e9a\u9a6c\u5b59\u7684\u7f51\u4e0a\u5546\u57ce\u6a21\u5f0f\uff0c\u91cd\u70b9\u662f\u57f9\u517b\u4e92\u8054\u7f51\u7528\u6237\u7684\u6d88\u8d39\u610f\u8bc6\u4e0e\u4e60\u60ef\u3002\u73b0\u5728\uff0c\u5353\u57df\u53d1\u5c55\u65b9\u5411\u8c03\u6574\uff0c\u8981\u4e3a\u6bcf\u4e2a\u60f3\u5728\u7535\u5b50\u5546\u52a1\u8fd9\u4e2a\u9886\u57df\u6709\u6240\u53d1\u5c55\u7684\u6295\u8d44\u8005\u63d0\u4f9b\u4e92\u8054\u7f51\u8d38\u6613\u5e73\u53f0\uff0c\u9664\u4e86\u7ee7\u7eed\u57f9\u517b\u4e92\u8054\u7f51\u7528\u6237\u7684\u6d88\u8d39\u610f\u8bc6\u4e4b\u5916\uff0c\u8fd8\u9700\u8981\u57f9\u8bad\u4e92\u8054\u7f51\u7528\u6237\u7684\u7535\u5b50\u5546\u52a1\u610f\u8bc6\u2026\u2026\u4e0e\u5305\u62ec\u4e92\u8054\u7f51\u8d38\u6613\u4e2d\u7684\u8bda\u4fe1\u6f0f\u6d1e\u7b49\u8bf8\u591a\u95ee\u9898\u4e00\u6837\uff0c\u90fd\u5f88\u96be\u5728\u77ed\u65f6\u95f4\u91cc\u5f7b\u5e95\u89e3\u51b3\u6389\u3002\u8fd9\u65f6\u5019\u5728\u540c\u4e00\u4e2a\u65b9\u5411\u4e0a\u805a\u96c6\u66f4\u591a\u7684\u53c2\u4e0e\u8005\uff0c\u65e0\u7591\u80fd\u66f4\u8fc5\u901f\u7684\u5c06\u8fd9\u5757\u5e02\u573a\u505a\u8d77\u6765\u3002\u6211\u77e5\u9053\u4f60\u4eec\u62c5\u5fc3\u4ec0\u4e48\uff0c8818\u76ee\u524d\u5df2\u7ecf\u5728\u56fd\u5185\u7535\u5b50\u5546\u52a1\u5e02\u573a\u6392\u540d\u7b2c\u4e00\uff0c\u4ed6\u4eec\u8981\u7d27\u8ddf\u7740\u5353\u57df\u8fdb\u884c\u4e1a\u52a1\u8c03\u6574\uff0c\u5353\u57df\u6210\u529f\u5d1b\u8d77\u7684\u673a\u4f1a\u53ef\u80fd\u4f1a\u51cf\u5c11\u3002\u73b0\u5728\u56fd\u5185\u4e92\u8054\u7f51\u7528\u6237\u8fd8\u4e0d\u8db3\u603b\u4eba\u53e3\u76842\uff05\uff0c\u8fd8\u8fdc\u8fdc\u672a\u5230\u5206\u51fa\u80dc\u8d1f\u7684\u65f6\u5019\uff0c\u4efb\u4f55\u4e00\u6b21\u5076\u5c14\u7684\u673a\u9047\u90fd\u53ef\u80fd\u8ba9\u4e00\u5bb6\u4e92\u8054\u7f51\u4f01\u4e1a\u5d1b\u8d77\uff0c\u4efb\u4f55\u4e00\u6b21\u7ec6\u5fae\u7684\u5931\u8bef\u90fd\u53ef\u80fd\u8ba9\u4e00\u5bb6\u8f89\u714c\u4e00\u65f6\u7684\u4e92\u8054\u7f51\u4f01\u4e1a\u6ca1\u843d\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5b50\u5609\u6700\u8fd1\u7684\u5174\u81f4\u90fd\u5728\u8fd9\u8fb9\uff0c\u79d1\u738b\u9ad8\u79d1\u5411\u4e1c\u5927\u521b\u57df\u6ce8\u8d44\u6301\u80a1\uff0c\u5979\u62c5\u4efb\u8d77\u80a1\u6743\u8463\u4e8b\u4ee3\u8868\uff0c\u867d\u7136\u6ca1\u6709\u5728\u4e1c\u5927\u521b\u57df\u5185\u90e8\u62c5\u4efb\u5177\u4f53\u7684\u804c\u52a1\uff0c\u4e0d\u8fc7\u91cd\u8981\u7684\u53d1\u5c55\u4f1a\u8bae\u5979\u90fd\u4f1a\u5217\u5e2d\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u8bf4\u7740\uff0c\u8c22\u5b50\u5609\u5fae\u9f13\u7740\u816e\u5e2e\u5b50\uff0c\u4e0d\u7f6e\u53ef\u5426\u3002\u5979\u4e5f\u65e0\u6cd5\u8bf4\u4ec0\u4e48\uff0c\u76db\u946b\u7535\u8baf\u4e0e\u5353\u57df\u5408\u4f5c\u5728\u5353\u57df\u7f51\u521b\u8bbe\u624b\u673a\u76f4\u8425\u7f51\u5e97\uff0c8818\u501f\u9274\u5353\u57df\u7684\u65b0\u6a21\u5f0f\uff0c\u5df2\u627e\u5230\u6d77\u6cf0\u7535\u5668\u95e8\u4e0a\u5bfb\u6c42\u5408\u4f5c\u3002</p><p>\u3000\u3000\u9274\u4e8e\u76db\u946b\u7535\u5668\u9886\u5148\u4e00\u6b65\u7684\u5e03\u5c40\uff0c\u6d77\u6cf0\u7535\u5668\u7684\u5b9e\u4f53\u95e8\u5e97\u5f88\u96be\u51b2\u51fa\u76db\u946b\u7535\u5668\u7684\u5c01\u9501\uff0c\u9762\u5bf9\u4e92\u8054\u7f51\u8fd9\u4e2a\u5d2d\u65b0\u7684\u5e73\u53f0\uff0c\u8c22\u610f\u4e5f\u6709\u8dc3\u8dc3\u6b32\u8bd5\u4e4b\u610f\u3002\u5f53\u7136\uff0c\u8c22\u610f\u7684\u601d\u8c0b\u8981\u66f4\u6df1\u8fdc\u4e00\u4e9b\uff0c\u4ed6\u5e0c\u671b\u6d77\u6cf0\u7535\u5668\u80fd\u6210\u7acb\u72ec\u7acb\u7684\u4e92\u8054\u7f51\u5e73\u53f0\uff0c\u7136\u540e\u4ee5\u9891\u9053\u5408\u4f5c\u7684\u6a21\u5f0f\u5c06\u6d77\u6cf0\u7684\u4e92\u8054\u7f51\u5e73\u53f0\u5d4c\u51658818\u7f51\u7ad9\u4e4b\u5185\uff0c\u6253\u7740\u80fd\u8fdb\u80fd\u9000\u7684\u6ce8\u610f\u3002\u66f4\u957f\u8fdc\u7684\u91ce\u5fc3\u5c31\u662f\u7b49\u56fd\u5185\u7684\u7535\u5b50\u5546\u52a1\u73af\u5883\u6210\u719f\u4e4b\u5916\uff0c\u6d77\u6cf0\u53ef\u4ee5\u5c06\u8fd9\u4e2a\u4e92\u8054\u7f51\u5e73\u53f0\u4ece8818\u72ec\u7acb\u51fa\u6765\uff0c\u4e13\u6ce8\u505a\u5927\u7535\u5668\u3001\u7535\u5b50\u4ea7\u54c1\u7684\u4e92\u8054\u7f51\u96f6\u552e\u4e1a\u52a1\u2014\u2014\u73b0\u5728\u8fd9\u4e00\u5757\u7684\u4e1a\u52a1\u8fd8\u662f\u592a\u6709\u9650\u4e86\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u8fd8\u8fdc\u8fdc\u672a\u5230\u5f15\u8d77\u91cd\u89c6\u7684\u65f6\u5019\u3002\u76db\u946b\u7535\u8baf\u4e0e\u5353\u57df\u5408\u4f5c\u4e5f\u662f\u65d7\u4e0b\u8d44\u6e90\u7684\u5148\u671f\u6574\u5408\uff0c\u5e76\u4e0d\u6307\u671b\u8fd9\u4e00\u5757\u80fd\u5f88\u5feb\u5c31\u63d0\u4f9b\u8f83\u5927\u7684\u4e1a\u52a1\u6d41\u91cf\u2014\u2014\u5012\u662f\u76db\u946b\u7535\u8baf\u7684\u7ebf\u4e0b\u5b9e\u4f53\u95e8\u5e97\u4f53\u7cfb\u80fd\u5bf9\u5353\u57df\u7684\u4e92\u8054\u7f51\u5e73\u53f0\u6709\u5f88\u5f3a\u7684\u4fc3\u8fdb\u4f5c\u7528\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u773c\u73e0\u5b50\u63a0\u8fc7\u4f1a\u8bae\u684c\u524d\u7684\u4f17\u4eba\uff0c\u8c22\u610f\u627e\u5979\u5546\u8bae\u8fc7\u8fd9\u4ef6\u4e8b\uff0c\u518d\u8bf4\u8fd9\u4ef6\u4e8b\u8c22\u610f\u4e5f\u5df2\u7ecf\u8c0b\u5212\u6210\u719f\uff0c\u4e0d\u7528\u591a\u4e45\u5c31\u4f1a\u6709\u5b9e\u8d28\u7684\u52a8\u4f5c\uff0c\u9690\u4e0d\u9690\u7792\u90fd\u6ca1\u6709\u592a\u5927\u7684\u5fc5\u8981\uff0c\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u4fbf\u5c06\u5f20\u6587\u4fe1\u627e\u8fc7\u8c22\u610f\u7684\u4e8b\u60c5\u8bf4\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u5f20\u606a\u8f7b\u8f7b\u7684\u5e94\u4e86\u4e00\u58f0\uff0c\u89c6\u4f5c\u4e00\u4ef6\u5f88\u5e73\u5e38\u4e0d\u8fc7\u7684\u4e8b\u60c5\u3002\u8981\u4e0d\u662f\u5353\u57df\u5c31\u662f\u9526\u6e56\u65d7\u4e0b\u7684\u4f01\u4e1a\uff0c\u4e1c\u5927\u521b\u57df\u5bf9\u5353\u57df\u6301\u80a1\u8d85\u8fc760\uff05\uff0c\u8981\u662f\u4e0d\u8003\u8651\u5f7c\u6b64\u95f4\u590d\u6742\u7684\u5173\u7cfb\uff0c\u76db\u946b\u7535\u8baf\u4f3c\u4e4e\u66f4\u5e94\u8be5\u627e8818\u5408\u4f5c\u2014\u2014\u6d77\u6cf0\u7535\u5668\u4e0e8818\u5408\u4f5c\uff0c\u5e76\u4e0d\u4f1a\u8ba9\u4eba\u89c9\u5f97\u6709\u4e1d\u6beb\u7684\u610f\u5916\u3002</p><p>\u3000\u3000\u5915\u9633\u5149\u8f89\u4ece\u7a97\u53e3\u659c\u5c04\u8fdb\u6765\uff0c\u5f20\u606a\u770b\u4e86\u770b\u8155\u8868\uff0c\u4f4e\u58f0\u95ee\u675c\u98de\uff1a\u201c\u8499\u4e50\u4ed6\u4eec\u8be5\u5230\u5efa\u90ba\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u7ba1\u4ed6\u5230\u6ca1\u5230\uff0c\u6211\u4eec\u5148\u53bb\u65b0\u697c\u770b\u4e00\u770b\u3002\u201d\u675c\u98de\u8bf4\u9053\u3002</p><p>\u3000\u3000\u675c\u98de\u4ed6\u4eec\u8fd9\u6bb5\u65f6\u95f4\u5fd9\u5f97\u591f\u545b\uff0c\u9664\u4e86\u4e92\u8054\u7f51\u8d44\u6e90\u6574\u5408\u4e4b\u5916\uff0c\u4e1c\u5927\u521b\u57df\u642c\u529e\u516c\u5730\u70b9\uff0c\u4ece\u4e4b\u524d\u7684\u4e1c\u5927\u79d1\u6280\u521b\u4e1a\u56ed\u642c\u8fdb\u6e56\u4e1c\u5546\u5708\u5185\u3002</p><p>\u3000\u3000\u4f1a\u8bae\u7ed3\u675f\u4e4b\u540e\uff0c\u8c22\u5b50\u5609\u63d0\u524d\u79bb\u5f00\u53bb\uff1b\u5f20\u606a\u8ddf\u675c\u98de\u4ed6\u4eec\u4ece\u4e1c\u5927\u79d1\u6280\u521b\u4e1a\u56ed\u5750\u8f66\u53bb\u4e1c\u5927\u521b\u57df\u5728\u6e56\u4e1c\u5546\u5708\u7684\u65b0\u697c\u8f6c\u4e00\u5708\u3002\u8d76\u8fc7\u53bb\uff0c\u624d\u53d1\u73b0\u8499\u4e50\u4e0e\u5e2d\u82e5\u7433\u5df2\u7ecf\u5148\u76f4\u63a5\u5230\u8fd9\u8fb9\u4e86\u3002</p><p>\u3000\u3000\u5728\u6e56\u4e1c\u516c\u5bd3\u4e1c\u8fb9\uff0c\u9694\u8857\u76f8\u671b\uff0c\u4e00\u680b\u4e8c\u5341\u516d\u5c42\u94fa\u8d34\u6df1\u7ea2\u8272\u5899\u7816\u7684\u65b9\u578b\u5199\u5b57\u697c\u5728\u5915\u9633\u4e0b\u7167\u5c04\u4e0b\uff0c\u843d\u4e0b\u4e00\u9053\u5de8\u5927\u7684\u9634\u5f71\uff0c\u906e\u5728\u8857\u9053\u4e0a\u3002\u4e5f\u5b66\u5317\u4eac\u7231\u8fbe\u5927\u53a6\u7684\u98ce\u683c\uff0c\u5199\u5b57\u697c\u7684\u524d\u5ead\u4e00\u89d2\u7ad6\u7740\u6bd4\u8def\u706f\u67f1\u7565\u9ad8\u7684\u6df1\u7ea2\u8272\u73bb\u7483\u94a2\u4e09\u89d2\u5f62\u697c\u6807\uff0c\u659c\u89d2\u9762\u5199\u7740\u201c\u4e1c\u5927\u521b\u57df\u201d\u2014\u2014\u8fd9\u91cc\u662f\u4e1c\u5927\u521b\u57df\u7684\u65b0\u529e\u516c\u697c\u3002</p><p>\u3000\u3000\u201c\u672c\u6765\u7b49\u5317\u9762\u7684\u90a3\u680b\u5730\u6807\u5efa\u597d\u4e4b\u540e\u4e00\u8d77\u642c\u8fdb\u53bb\uff0c\u540e\u6765\u60f3\u60f3\u4f60\u4e5f\u4e0d\u53ef\u80fd\u8ba9\u6211\u5728\u5730\u6807\u524d\u7ad6\u8fd9\u4e48\u6839\u4e09\u89d2\u67f1\u5b50\uff0c\u8fd8\u4e0d\u5982\u63d0\u524d\u642c\u8fc7\u6765\u3002\u201d\u675c\u98de\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u9526\u6e56\u627f\u8bfa\u6295\u8d44\u5efa\u9020\u7684\u71d5\u5f52\u6e56\u5730\u6807\u5efa\u7b51\uff0c\u6625\u8282\u524d\u5c31\u5b9a\u4e0b\u8bbe\u8ba1\u65b9\u6848\uff0c\u4e0a\u4e2a\u6708\u5e95\u624d\u51fa\u5b8c\u56fe\u3002\u7531\u4e8e\u662f\u8bbe\u8ba1\u8fbe\u4e24\u767e\u56db\u5341\u516d\u7c73\u6807\u9ad8\u7684\u8d85\u9ad8\u5efa\u7b51\uff0c\u4e0b\u4e00\u6b65\u5c31\u662f\u8981\u63a5\u53d7\u5efa\u7b51\u8bbe\u8ba1\u8d85\u9650\u5ba1\u9650\uff0c\u80fd\u8d76\u5728\u660e\u5e74\u6625\u8282\u540e\u7834\u571f\u52a8\u5de5\u5c31\u7b97\u662f\u795e\u901f\u4e86\uff0c\u7b49\u771f\u6b63\u5efa\u6210\u8fd8\u8981\u518d\u7b49\u4e24\u5e74\u3002</p><p>\u3000\u3000\u4e1c\u5927\u79d1\u6280\u521b\u4e1a\u56ed\u90a3\u8fb9\u7684\u529e\u516c\u6761\u4ef6\u6bd4\u8f83\u5dee\uff0c\u7edd\u5927\u90e8\u5206\u529e\u516c\u5ba4\u90fd\u6ca1\u6709\u7a7a\u8c03\uff0c\u8fdc\u8fdc\u4e0d\u5982\u6a61\u6811\u56ed\u4e3d\u4e30\u56ed\uff0c\u66f4\u4e0d\u80fd\u8ddf\u6e56\u4e1c\u5546\u5708\u65b0\u5efa\u7684\u5199\u5b57\u697c\u76f8\u6bd4\uff0c\u5f20\u606a\u624d\u4e0d\u4fe1\u675c\u98de\u4ed6\u4eec\u80fd\u71ac\u5230\u5317\u9762\u7684\u5730\u6807\u5efa\u7b51\u5efa\u6210\u6295\u5165\u4f7f\u7528\u4e4b\u540e\u518d\u642c\u529e\u516c\u697c\u3002</p><p>\u3000\u3000\u8499\u4e50\u6ca1\u6709\u4ec0\u4e48\u5f62\u8c61\u7684\u8e72\u5728\u8def\u7259\uff0c\u772f\u773c\u770b\u7740\u4e1c\u5927\u521b\u57df\u6c14\u52bf\u9887\u96c4\u7684\u95e8\u5ead\uff0c\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u5f53\u521d\u51b3\u5b9a\u5c06\u5199\u5b57\u697c\u6574\u4e2a\u524d\u5385\u90fd\u7528\u6765\u5f53\u524d\u53f0\u7684\u51b3\u5b9a\u592a\u6b63\u786e\u7684\uff0c\u6211\u4eec\u4e0b\u4e00\u6b65\u6700\u91cd\u8981\u7684\u4e8b\u60c5\u662f\u4e0d\u662f\u7ed9\u8fd9\u6c14\u52bf\u4e0d\u51e1\u7684\u524d\u53f0\u6311\u4e24\u540d\u5408\u683c\u7684\u524d\u53f0\u63a5\u5f85\u5458\u554a\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u680b\u5199\u5b57\u697c\u7684\u4e34\u8857\u524d\u5385\u672c\u6765\u8981\u79df\u7ed9\u4e2d\u56fd\u94f6\u884c\u5f53\u8425\u4e1a\u5385\u7528\u7684\uff0c\u675c\u98de\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u8ba4\u4e3a\u4f5c\u4e3a\u5c06\u6765\u4e2d\u56fd\u6700\u5927\u7684\u4e92\u8054\u7f51\u4ea7\u4e1a\u6295\u8d44\u96c6\u56e2\uff0c\u4e1c\u5927\u521b\u57df\u7684\u95e8\u5ead\u8fd8\u662f\u8981\u8ddf\u697c\u91cc\u5176\u4ed6\u5165\u9a7b\u4f01\u4e1a\u6324\u5728\u5546\u52a1\u7535\u68af\u95e8\u5385\u91cc\u5c31\u592a\u4e0d\u5149\u9c9c\u4e86\uff0c\u5c31\u5c06\u5199\u5b57\u697c\u5e95\u5c42\u6574\u4e2a\u524d\u534a\u7247\u90fd\u5360\u8fc7\u6765\u4f5c\u4e3a\u4e1c\u5927\u521b\u57df\u7684\u524d\u53f0\u4e0e\u63a5\u5f85\u5385\u3002</p><p>\u3000\u3000\u8499\u4e50\u4e0e\u5e2d\u82e5\u7433\u5728\u5317\u7f8e\u516c\u53f8\u5de5\u4f5c\u4e86\u4e00\u5e74\u534a\uff0c\u8fd9\u4e24\u5929\u624d\u56de\u5230\u56fd\u5185\u6765\uff0c\u56de\u65b0\u4ead\u4f4f\u4e86\u4e24\u5929\uff0c\u53c8\u966a\u5e2d\u82e5\u7433\u56de\u6d59\u4e1c\u6e05\u6e90\u4f4f\u4e86\u4e00\u5929\uff0c\u4eca\u5929\u624d\u56de\u5efa\u90ba\u6765\u3002\u5de5\u4f5c\u804c\u52a1\u4ec0\u4e48\u90fd\u6ca1\u6709\u5b9a\u4e0b\u6765\uff0c\u56de\u5230\u5efa\u90ba\u4e5f\u6ca1\u6709\u6253\u6270\u675c\u98de\u4ed6\u4eec\u53ec\u5f00\u4e1c\u5927\u521b\u57df\u7684\u5185\u90e8\u4f1a\u8bae\uff0c\u8ddf\u5e2d\u82e5\u7433\u76f4\u63a5\u5230\u65b0\u697c\u8fd9\u8fb9\u6765\u7b49\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u5728\u6d77\u5916\u751f\u6d3b\u4e86\u4e00\u5e74\u534a\uff0c\u8ba9\u8499\u4e50\u6c14\u8d28\u53d1\u751f\u4e9b\u6539\u53d8\uff0c\u4e5f\u8bb8\u662f\u5e38\u522e\u80e1\u987b\u7684\u7f18\u6545\uff0c\u4e0b\u5df4\u8fd4\u9752\uff0c\u770b\u4e0a\u53bb\u6bd4\u5728\u5b66\u6821\u65f6\u66f4\u6210\u719f\u4e00\u4e9b\uff0c\u6709\u7740\u6bd4\u540c\u9f84\u4eba\u66f4\u9510\u5229\u7684\u77b3\u5149\uff0c\u6c14\u8d28\u4e0a\u4e5f\u66f4\u4ece\u5bb9\u95f2\u6de1\u4e86\u3002\u65f6\u5b66\u658c\u770b\u5230\u4ed6\u5174\u594b\u7684\u63fd\u8fc7\u4ed6\u7684\u80a9\u8180\uff0c\u8bf4\uff1a\u201c\u6d77\u5f52\u4eba\u58eb\u5c31\u5e94\u8be5\u662f\u8fd9\u8303\u513f\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6309\u7740\u8499\u4e50\u7684\u80a9\u8180\uff0c\u8ba9\u4ed6\u7ad9\u8d77\u6765\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u201c\u4f60\u56de\u6765\u5c31\u8ddf\u675c\u98de\u4e00\u8d77\u51fa\u4efb\u4e1c\u5927\u521b\u57df\u7684\u8054\u5408\u603b\u88c1\uff0c\u7b49\u660e\u5e74\u6bd5\u4e1a\u540e\u5c31\u65b9\u4fbf\u6211\u5c06\u675c\u98de\u4e00\u811a\u8e22\u5230\u6d77\u5916\u53bb\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u60f3\u8981\u6709\u66f4\u5e7f\u9614\u7684\u89c6\u91ce\u4e0e\u66f4\u6df1\u523b\u7684\u89c1\u89e3\uff0c\u9664\u4e86\u5929\u8d4b\u4e4b\u5916\uff0c\u9605\u5386\u4e0e\u7ecf\u9a8c\u4e5f\u662f\u5fc5\u987b\u7684\u3002\u8981\u4e0d\u662f\u8003\u8651\u8981\u675c\u98de\u660e\u5e74\u5c31\u8981\u6bd5\u4e1a\u8003\u8651\u8ba9\u675c\u98de\u5230\u6d77\u5916\u953b\u70bc\u51e0\u5e74\uff0c\u5f20\u606a\u8fd8\u60f3\u8499\u4e50\u6d77\u5916\u591a\u7559\u4e24\u5e74\u3002\u5c31\u662f\u5f20\u606a\u4ed6\u81ea\u5df1\uff0c\u5e73\u65f6\u4e5f\u5f88\u5c11\u53bb\u78b0\u7ba1\u7406\u4e0a\u7684\u7410\u788e\u4e8b\u52a1\uff0c\u7559\u4e0b\u65f6\u95f4\u7528\u4e8e\u5b66\u4e60\u3002\u524d\u5c18\u5f80\u4e16\u91cc\u8bb8\u591a\u6d6e\u5149\u63a0\u5f71\u5f0f\u7684\u8bb0\u5fc6\u6709\u65f6\u5019\u5e76\u4e0d\u8db3\u4ee5\u63d0\u4f9b\u8db3\u591f\u7684\u5224\u65ad\u4f9d\u636e\uff0c\u5bf9\u6574\u4e2a\u4ea7\u4e1a\u4e86\u89e3\u8d8a\u662f\u6df1\u523b\uff0c\u8d8a\u662f\u6e05\u6670\u7684\u770b\u5230\u672a\u6765\u7684\u53d1\u5c55\u8d8b\u52bf\uff0c\u751a\u81f3\u6bd4\u6d6e\u5149\u63a0\u5f71\u5f0f\u7684\u8bb0\u5fc6\u8fd8\u8981\u6e05\u6670\u51e0\u5206\u3002</p><p>\u3000\u3000\u8fd9\u6bb5\u65f6\u95f4\u4e5f\u6070\u6070\u662f\u4e1c\u5927\u521b\u57df\u53d1\u5c55\u7684\u6700\u5173\u952e\u65f6\u671f\uff0c\u867d\u8bf4\u8c22\u5b50\u5609\u63d0\u4f9b\u4e86\u8bb8\u4eba\u60ca\u8273\u7edd\u4f26\u7684\u4e1a\u52a1\u53d1\u5c55\u6784\u60f3\uff0c\u4f46\u6b63\u662f\u5979\u5c42\u51fa\u4e0d\u7a77\u3001\u5947\u601d\u5999\u60f3\u7684\u65b0\u65b9\u6848\u624d\u8ba9\u5927\u5bb6\u624b\u5fd9\u811a\u4e71\u5e94\u5bf9\u65e0\u6687\uff1b\u8ba9\u8499\u4e50\u56de\u6765\uff0c\u4e5f\u80fd\u66ff\u675c\u98de\u591a\u5206\u62c5\u4e00\u4e9b\u7ba1\u7406\u4e0a\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u50cf\u4ee4\u5c0f\u71d5\u3001\u79e6\u521a\u3001\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u3001\u65bd\u65b0\u98de\u3001\u9a6c\u529b\u3001\u6768\u4f1f\u6bc5\u7b49\u4eba\u90fd\u8d1f\u8d23\u5177\u4f53\u7684\u4e8b\u52a1\u90e8\u95e8\u6216\u8005\u4e0b\u5c5e\u516c\u53f8\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u8499\u4e50\u563f\u7136\u4e00\u7b11\uff0c\u8ddf\u675c\u98de\u8bf4\u9053\uff0c\u201c\u6211\u8fd9\u7b97\u662f\u4ece\u4f60\u6000\u91cc\u6458\u679c\u5b50\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e5f\u522b\u5f97\u610f\u592a\u65e9\uff0c\u4e0d\u662f\u8fd8\u6709\u4e00\u5e74\u8bd5\u7528\u671f\u5462\uff1f\u201d\u675c\u98de\u8bf4\u9053\uff0c\u53c8\u7b11\u7740\u95ee\u5e2d\u82e5\u7433\uff0c\u201c\u8001\u8499\u5728\u4f60\u8fd9\u8fb9\u7684\u8bd5\u7528\u671f\u8fc7\u4e86\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u201c\u72d7\u5634\u91cc\u5410\u4e0d\u51fa\u8c61\u7259\uff0c\u76db\u590f\u600e\u4e48\u5c31\u7ed9\u4f60\u552c\u5f04\u7559\u5728\u9999\u6e2f\u4e86\uff1f\u201d\u5e2d\u82e5\u7433\u7c89\u8138\u5fae\u7ea2\u7684\u7b11\u9a82\u9053\uff0c\u8d70\u51fa\u5b66\u6821\uff0c\u5728\u6d77\u5916\u751f\u6d3b\u4e86\u4e00\u5e74\u591a\u65f6\u95f4\uff0c\u5bb9\u8c8c\u672a\u53d1\u751f\u4ec0\u4e48\u53d8\u5316\uff0c\u4e00\u6837\u7684\u9752\u6625\u660e\u8273\uff0c\u6c14\u8d28\u66f4\u5a34\u6de1\u606c\u9759\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u660e\u5e74\u5c31\u8981\u7ed9\u8e22\u51fa\u53bb\u4e86\uff0c\u5979\u8d76\u7740\u56de\u6765\u6709\u4ec0\u4e48\u610f\u4e49\uff1f\u201d\u675c\u98de\u7b11\u7740\u8bf4\uff0c\u201c\u6839\u672c\u5c31\u4e0d\u7528\u552c\u5f04\u3002\u201d</p><p>\u3000\u3000\u76db\u590f\u521a\u521a\u4ece\u9999\u6e2f\u5927\u5b66\u6bd5\u4e1a\uff0c\u5f20\u606a\u4e0e\u675c\u98de\u4e94\u6708\u5e95\u8fd8\u8d76\u5230\u9999\u6e2f\u53c2\u52a0\u5979\u7684\u6bd5\u4e1a\u5178\u793c\uff0c\u76db\u590f\u6700\u7ec8\u51b3\u5b9a\u7559\u5728\u9999\u6e2f\u3002</p><p>\u3000\u3000\u6700\u521d\u4e3a\u65b9\u4fbf\u76db\u590f\u3001\u5510\u5a67\u5230\u9999\u6e2f\u7559\u5b66\uff0c\u5f20\u606a\u5728\u9999\u6e2f\u8bbe\u7acb\u4e86\u4e00\u4e2a\u5411\u5185\u5730\u5b66\u751f\u63d0\u4f9b\u5230\u9999\u6e2f\u7559\u5b66\u52a9\u5b66\u91d1\u7684\u57fa\u91d1\u4f1a\uff0c\u540e\u6765\u8bb8\u591a\u4ece\u9999\u6e2f\u6350\u56de\u5185\u5730\u7684\u516c\u76ca\u6b3e\u9879\uff0c\u90fd\u4ece\u8fd9\u4e2a\u57fa\u91d1\u4f1a\u91cc\u8d70\u3002\u6682\u65f6\u7b97\u662f\u9526\u6e56\u65d7\u4e0b\u76f4\u5c5e\u7684\u516c\u76ca\u57fa\u91d1\uff0c\u9526\u6e56\u5546\u4e8b\u6210\u7acb\u540e\uff0c\u5c31\u6302\u6210\u9526\u6e56\u5546\u4e8b\u7684\u540d\u4e0b\uff0c\u7531\u848b\u8587\u8d1f\u8d23\u7ba1\u7406\u3002\u5546\u4e1a\u673a\u6784\u4e0e\u516c\u76ca\u6027\u673a\u6784\u603b\u5f52\u8981\u754c\u7ebf\u5206\u660e\u4e00\u4e9b\uff0c\u624d\u66f4\u7b26\u5408\u7ba1\u7406\u7684\u51c6\u5219\uff1b\u76db\u590f\u4fbf\u8fdb\u53bb\u62c5\u5f53\u7ba1\u7406\u804c\u52a1\u3002\u9999\u6e2f\u5927\u5b66\u4ece\u516d\u6708\u5f00\u59cb\u6691\u5047\uff0c\u5510\u5a67\u4e5f\u7559\u5728\u57fa\u91d1\u4f1a\u91cc\u8fdb\u884c\u5de5\u4f5c\u5b9e\u4e60\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u4e0e\u4f55\u5f26\u5728\u9999\u6e2f\u9526\u6e56\u5546\u4e8b\u603b\u90e8\u5b9e\u4e60\u4e86\u534a\u5e74\uff0c\u8fd9\u6b21\u4e5f\u8ddf\u7740\u5f20\u606a\u3001\u675c\u98de\u4ece\u9999\u6e2f\u56de\u6765\uff1b\u53ea\u4e0d\u8fc7\u5979\u4eec\u518d\u6709\u534a\u4e2a\u6708\u5c31\u8981\u6b63\u5f0f\u4ece\u5404\u81ea\u7684\u5b66\u6821\u6bd5\u4e1a\uff0c\u6bd5\u4e1a\u524d\u8fd8\u8981\u8bb8\u591a\u4e8b\u60c5\u8981\u505a\uff0c\u6682\u65f6\u8fd8\u4e0d\u4f1a\u6b63\u5f0f\u5230\u53f6\u5c0f\u6850\u8ddf\u524d\u53bb\u62a5\u9053\uff0c\u5f20\u606a\u8fd9\u51e0\u5929\u4e5f\u4e0d\u5927\u770b\u5230\u5979\u4eec\u7684\u4eba\u5f71\u3002</p><p>\u3000\u3000\u591c\u91cc\u66ff\u8499\u4e50\u3001\u5e2d\u82e5\u7433\u63a5\u98ce\u6d17\u5c18\uff0c\u6ca1\u6709\u53bb\u4ec0\u4e48\u9ad8\u7ea7\u9910\u5385\u3001\u9152\u5e97\uff0c\u5f20\u606a\u8bf7\u5927\u5bb6\u5230\u6559\u5de5\u98df\u5802\u5403\u996d\uff0c\u5341\u51e0\u4e2a\u4eba\u56f4\u7740\u4e24\u5f20\u957f\u6761\u684c\u559d\u9152\u5403\u996d\u3002\u8fd9\u8fb9\u9152\u8db3\u9971\u6ee1\uff0c\u5927\u5bb6\u56b7\u56b7\u7740\u53bb1978\u63a5\u7740\u559d\u9152\uff0c\u4ece\u6559\u5de5\u98df\u5802\u51fa\u6765\uff0c\u7ecf\u8fc7\u4e09\u798f\u9152\u5e97\u524d\uff0c\u9648\u5983\u84c9\u4e5f\u6070\u597d\u8ddf\u51e0\u540d\u540c\u5b66\u51fa\u6765\u3002\u5979\u4fcf\u8138\u5fae\u7ea2\uff0c\u4f3c\u4e4e\u559d\u4e86\u4e9b\u9152\uff0c\u770b\u7740\u5f20\u606a\u4ed6\u4eec\u7ecf\u8fc7\uff0c\u5fd9\u8ddf\u540c\u5b66\u544a\u522b\uff0c\u52a0\u5165\u5230\u5f20\u606a\u4ed6\u4eec\u8fd9\u8fb9\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u89c1\u9648\u5983\u84c9\u5979\u540c\u5b66\u95ee\u5979\u8981\u4e0d\u8981\u4e00\u8d77\u53bb\u5531\u6b4c\uff0c\u4fa7\u8fc7\u5934\u95ee\u5979\uff1a\u201c\u4f60\u4eec\u73ed\u4e0a\u665a\u4e0b\u63a5\u4e0b\u6765\u8fd8\u6709\u6d3b\u52a8\uff0c\u4f60\u4e0d\u53c2\u52a0\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u8981\u4e00\u8d77\u53bb\u65b0\u5e02\u8857\u5531\u6b4c\u53bb\uff0c\u6211\u4e0d\u60f3\u53bb\u2026\u2026\u4f60\u4eec\u53bb\u54ea\u91cc\uff1f\u201d\u9648\u5983\u84c9\u6447\u4e86\u6447\u5934\u3002</p><p>\u3000\u3000\u201c\u53bb1978\u63a5\u7740\u7ed9\u8499\u4e50\u3001\u5e2d\u5e08\u59d0\u63a5\u98ce\u6d17\u5c18\uff0c\u4f60\u4e5f\u5e94\u8be5\u8fc7\u6765\u7684\u3002\u201d\u5f20\u606a\u7b11\u9053\u3002</p><p>\u3000\u3000\u201c\u9648\u7f8e\u5973\u4eca\u5929\u7ed9\u591a\u5c11\u4eba\u8868\u767d\u4e86\uff1f\u201d\u65f6\u5b66\u658c\u5934\u51d1\u8fc7\u6765\u5f00\u73a9\u7b11\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u53bb\uff01\u201d\u9648\u5983\u84c9\u77aa\u4e86\u65f6\u5b66\u658c\u4e00\u773c\uff0c\u63a5\u7740\u53c8\u9759\u9759\u7684\u8d70\u5728\u5f20\u606a\u7684\u8eab\u8fb9\u3002\u8fd9\u4e9b\u5e74\u5979\u90fd\u4e60\u60ef\u5728\u5f20\u606a\u8eab\u8fb9\u4e86\uff0c\u9664\u4e86\u5bbf\u820d\u540c\u5b66\u5916\uff0c\u4e0e\u5176\u4ed6\u540c\u5b66\u76f8\u5904\u90fd\u4e0d\u5bc6\u5207\u3002\u8fd9\u79cd\u6de1\u6de1\u7684\u6027\u5b50\u4f3c\u4e4e\u4ece\u9ad8\u4e2d\u5c31\u517b\u6210\u4e86\uff0c\u9ad8\u4e2d\u540c\u5b66\u91cc\uff0c\u9664\u4e86\u4e0e\u82cf\u4e00\u5a77\u7684\u5173\u7cfb\u5206\u5916\u5bc6\u5207\u5916\uff0c\u5176\u4ed6\u540c\u5b66\u90fd\u5df2\u7ecf\u6ca1\u6709\u4e86\u4ec0\u4e48\u8054\u7cfb\u3002</p><p>\u3000\u3000\u8d70\u5230\u5408\u6b22\u5c71\u4e1c\u9e93\u65f6\uff0c\u6070\u597d\u80e1\u91d1\u661f\u3001\u9648\u52c7\u4e24\u4e2a\u4eba\u4e00\u8d77\u4ece\u5317\u9762\u8d70\u8fc7\u6765\uff0c\u4e5f\u671d\u4e1c\u95e8\u8d70\u53bb\u3002\u80e1\u91d1\u661f\u8fd8\u597d\u4e00\u4e9b\uff0c\u9648\u52c7\u770b\u4e0a\u53bb\u9762\u5bb9\u6194\u60b4\uff0c\u5934\u53d1\u84ec\u4e71\u3001\u5634\u5507\u4e0a\u7684\u80e1\u987b\u5bc6\u5bc6\u7684\u4e00\u5c42\uff0c\u4f3c\u4e4e\u597d\u4e9b\u5929\u6ca1\u6709\u6536\u62fe\u8fc7\u6765\u3002\u4ed6\u4eec\u770b\u5230\u5f20\u606a\u7b49\u4eba\u8d70\u8fc7\u6765\uff0c\u4e0b\u610f\u8bc6\u7684\u60f3\u8981\u907f\u5f00\uff0c\u811a\u4e0b\u8fdf\u7591\u4e86\u51e0\u79d2\u949f\uff0c\u7ec8\u662f\u62a2\u5728\u5f20\u606a\u4ed6\u4eec\u4e4b\u524d\u8d70\u51fa\u4e1c\u95e8\uff1b\u5f20\u606a\u4ed6\u4eec\u8ddf\u5728\u540e\u9762\uff0c\u770b\u5230\u80e1\u91d1\u661f\u3001\u9648\u52c7\u4e24\u4eba\u8d70\u8fdb1978\u9694\u58c1\u4e00\u5bb6\u540d\u4e3a\u6df1\u7ea2\u5370\u8c61\u7684\u9152\u5427\u91cc\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u770b\u4e0a\u53bb\u6709\u4e9b\u72fc\u72c8\u5462\uff1f\u201d\u9648\u5983\u84c9\u7591\u60d1\u7684\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u53ef\u80fd\u683d\u4e86\u4e2a\u8ddf\u5934\u2026\u2026\u201d\u5f20\u606a\u5c06\u80e1\u91d1\u661f\u3001\u9648\u52c7\u53ef\u80fd\u501f\u5e55\u540e\u6d88\u606f\u7092\u80a1\u7684\u4e8b\u60c5\u7565\u8ddf\u9648\u5983\u84c9\u89e3\u91ca\u4e86\u4e00\u4e0b\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u5bf9\u4e86\uff0c\u4f60\u7ed9\u6211\u5f53\u79c1\u4eba\u52a9\u7406\uff0c\u5f88\u591a\u4e8b\u60c5\uff0c\u4f60\u90fd\u8981\u5148\u719f\u6089\u8d77\u6765\uff1b\u9526\u6e56\u7684\u85aa\u6c34\u53ef\u6ca1\u6709\u90a3\u4e48\u597d\u5e94\u4ed8\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u9648\u5983\u84c9\u62ff\u7740\u5ae9\u82e5\u9752\u8471\u7684\u624b\u6307\u62c8\u7740\u7709\u5fc3\uff0c\u6c89\u541f\u7740\u4e0d\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u5988\u559c\u6b22\u95ee\u4e1c\u95ee\u897f\u7684\uff0c\u6211\u5728\u60f3\u7740\u7f16\u4ec0\u4e48\u8bdd\u8499\u8fc7\u5979\uff1f\u201d</p><p>\u3000\u3000\u201c\u76f4\u63a5\u8bf4\u4e0d\u884c\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5012\u662f\u9ad8\u5174\u770b\u6211\u5988\u90a3\u526f\u6068\u4e0d\u6211\u5c06\u65a9\u788e\u96f6\u5356\u7684\u6837\u5b50\uff1f\u201d\u9648\u5983\u84c9\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5979\u9f3b\u7ffc\u5fae\u76b1\u4e9b\u8d77\u6765\uff0c\u6709\u7740\u4fcf\u76ae\u7684\u795e\u8272\u3002\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u4e24\u4eba\u6700\u5927\u7684\u533a\u522b\u5c31\u662f\u5510\u5a67\u5a07\u5a9a\u800c\u9648\u5983\u84c9\u660e\u8273\uff0c\u50cf\u9648\u5983\u84c9\u5076\u5c14\u6d41\u9732\u7684\u4fcf\u76ae\u795e\u8272\u5c24\u4e3a\u52a8\u4eba\u3002</p><p>\u3000\u3000\u8d70\u8fdb1978\uff0c\u770b\u5230\u82cf\u4e00\u5a77\u4e0e\u4f55\u5f26\u90fd\u7ad9\u5728\u67dc\u53f0\u540e\uff0c\u5f20\u606a\u8be7\u5f02\u7684\u95ee\uff1a\u201c\u4eca\u5929\u4e0d\u662f\u4f60\u4eec\u5bbf\u820d\u540c\u5b66\u805a\u4f1a\u5417\uff1f\u201d</p><p>\u3000\u3000\u82cf\u4e00\u5a77\u5c06\u5927\u5385\u91cc\u89d2\u5476\u5476\u5634\uff0c\u90a3\u8fb9\u684c\u5b50\u56f4\u7740\u4e00\u7fa4\u5973\u5b69\u5b50\uff0c\u770b\u7740\u773c\u719f\uff0c\u90fd\u662f\u82cf\u4e00\u5a77\u3001\u4f55\u5f26\u5bbf\u820d\u91cc\u7684\u5973\u5b69\u5b50\u3002\u82cf\u4e00\u5a77\u8bf4\u9053\uff1a\u201c\u6709\u4eba\u8bf7\u5047\uff0c\u8fd9\u8fb9\u7f3a\u4eba\u624b\uff0c\u6211\u4eec\u5c31\u5c06\u805a\u4f1a\u7684\u5730\u70b9\u6539\u5230\u8fd9\u91cc\u6765\u4e86\u2026\u2026\u4f60\u653e\u5fc3\uff0c\u4eca\u5929\u7684\u6d88\u8d39\u62b5\u6211\u4eec\u4fe9\u7684\u52a0\u73ed\u8d39\u597d\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u4fe9\u7684\u52a0\u73ed\u8d39\u52a0\u5728\u4e00\u8d77\u624d\u6709\u591a\u5c11\u94b1\uff1f\u201d\u5f20\u606a\u8d70\u5230\u5427\u53f0\u540e\uff0c\u5c06\u82cf\u4e00\u5a77\u4e0e\u4f55\u5f26\u8d76\u51fa\u53bb\uff0c\u8ba9\u5979\u4eec\u8fc7\u53bb\u966a\u540c\u5b66\u805a\u4f1a\u53bb\uff0c\u518d\u8fc7\u534a\u4e2a\u6708\u5c31\u8981\u5404\u5954\u4e1c\u897f\uff0c\u6bd5\u4e1a\u524d\u7684\u76f8\u805a\uff0c\u5bf9\u76f8\u5904\u56db\u5904\u7684\u670b\u53cb\u540c\u5b66\u6765\u8bf4\u5c31\u663e\u5f97\u683c\u5916\u503c\u5f97\u73cd\u60dc\u2014\u2014\u5f20\u606a\u4eb2\u81ea\u7ad9\u5230\u5427\u53f0\u540e\u5c06\u9152\u6c34\u996e\u6599\u62ff\u51fa\u6765\u5206\u7ed9\u5927\u5bb6\uff0c\u5f53\u8d77\u62db\u5f85\u6765\u3002\u675c\u98de\u4ed6\u4eec\u62ff\u8d77\u9152\u6c34\u5230\u91cc\u9762\u627e\u4f4d\u5b50\u5750\u4e0b\u6765\uff0c\u8fd9\u4f1a\u513f\u8fdb\u6765\u7684\u5ba2\u4eba\u5f88\u591a\uff0c\u4e5f\u65e0\u6cd5\u90fd\u6324\u5728\u5427\u53f0\u524d\u804a\u5929\u3002</p><p>\u3000\u3000\u201c\u5bf9\u4e86\uff0c\u201d\u82cf\u4e00\u5a77\u8f6c\u8eab\u53c8\u51d1\u5230\u5427\u53f0\u524d\u8ddf\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4f60\u6709\u6ca1\u6709\u7269\u8272\u5230\u5408\u9002\u7684\u4eba\u624b\u6765\u6253\u74061978\uff1f\u6211\u53ef\u662f\u5230\u4e86\u4e03\u6708\u5c31\u8981\u51c6\u65f6\u79bb\u5f00\u5efa\u90ba\u7684\uff0c\u518d\u4e5f\u4e0d\u60f3\u8ddf\u4f60\u4eec\u518d\u6709\u4ec0\u4e48\u74dc\u845b\u3002\u4f60\u4eec\u8bf4\u8bf4\u770b\uff0c\u76f8\u5904\u4e86\u4e09\u5e74\u7684\u7537\u670b\u53cb\uff0c\u6700\u540e\u7adf\u7136\u8ddf\u6211\u8bf4\u8ddf\u6211\u8c08\u604b\u7231\u662f\u4e3a\u4e86\u63a5\u8fd1\u4f55\u5f26\uff0c\u4f60\u4eec\u8bf4\uff0c\u6211\u8fd9\u4e00\u9897\u5fc3\u8be5\u7834\u788e\u4ec0\u4e48\u6837\u5b50\u624d\u80fd\u8868\u8fbe\u6211\u7684\u5fc3\u788e\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u662f\u5e38\u8bf4\u4e0d\u503c\u5f97\u4e3a\u4e4b\u4f24\u5fc3\u7684\u7537\u4eba\u65e9\u4e22\u6389\u624d\u4e0d\u4f1a\u59a8\u788d\u4eba\u751f\u554a\uff0c\u201d\u4f55\u5f26\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u518d\u8bf4\u4f60\u73b0\u5728\u54ea\u91cc\u60f3\u662f\u4f24\u5fc3\u7684\u6837\u5b50\uff1f\u4e5f\u65e9\u8ddf\u4f60\u8bf4\u7537\u4eba\u6ca1\u4e00\u4e2a\u597d\u4e1c\u897f\uff0c\u4f60\u8fd8\u4f24\u5fc3\uff0c\u5927\u4e0d\u4e86\u6539\u5929\u52fe\u5f15\u4e00\u4e2a\u5e05\u54e5\u8d54\u4f60\u5c31\u662f\u3002\u201d</p><p>\u3000\u3000\u201c\u5c31\u662f\u4f60\u8fd9\u7978\u6c34\u60f9\u7684\u4e8b\uff0c\u8fd9\u65f6\u5019\u8fd8\u5e78\u707e\u4e50\u7978\uff1f\u201d\u82cf\u4e00\u5a77\u4f38\u624b\u6390\u7740\u4f55\u5f26\u7684\u8170\u80a2\u8ba9\u5979\u4e3e\u8d77\u624b\u6c42\u9976\uff0c\u770b\u7740\u9648\u5983\u84c9\u53c8\u5728\u90a3\u91cc\u62bf\u5634\uff0c\u4e00\u70b9\u90fd\u6ca1\u6709\u540c\u60c5\u5979\u7684\u610f\u8bc6\uff0c\u4f38\u624b\u53c8\u5c06\u9648\u5983\u84c9\u7684\u8170\u6390\u4e0a\uff0c\u8ddf\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u8fd9\u4e24\u53ea\u5996\u7cbe\uff0c\u4f60\u8981\u770b\u7d27\u4e9b\uff0c\u514d\u5f97\u5979\u4eec\u53c8\u968f\u4fbf\u53bb\u7978\u5bb3\u5176\u4ed6\u51e1\u95f4\u5973\u5b50\uff0c\u53cd\u6b63\u4f60\u8eab\u4e0a\u8671\u5b50\u591a\u4e86\u4e0d\u6015\u54ac\u2026\u2026\u4f60\u8981\u627e\u4e0d\u5230\u5408\u9002\u7684\u4eba\u9009\uff0c\u90a3\u4f60\u4e5f\u53ef\u4ee5\u6bcf\u5929\u5e26\u7740\u8fd9\u4e24\u53ea\u5996\u7cbe\u6765\u6253\u74061978\u3002\u201d\u5c06\u9648\u5983\u84c9\u4e0e\u4f55\u5f26\u5f80\u5427\u53f0\u91cc\u63a8\uff0c\u5979\u8dd1\u8fc7\u53bb\u8ddf\u5bbf\u820d\u540c\u5b66\u76f8\u805a\u53bb\u4e86\u3002</p><p>\u3000\u3000\u201c\u4e00\u8f88\u5b50\u7ad9\u5728\u5427\u53f0\u540e\u770b\u7740\u4eba\u6765\u4eba\u5f80\uff0c\u4e5f\u662f\u4e00\u79cd\u503c\u5f97\u5411\u5f80\u7684\u4eba\u751f\u554a\uff0c\u201d\u4f55\u5f26\u80cc\u8f6c\u8fc7\u8eab\uff0c\u7ec6\u8170\u62b5\u5728\u5427\u53f0\u4e0a\u4f38\u4e86\u61d2\u8170\uff0c\u5979\u8eab\u6750\u7a88\u7a95\u9ad8\u6311\uff0c\u5728\u9999\u6e2f\u65f6\u526a\u4e86\u77ed\u53d1\uff0c\u770b\u4e0a\u53bb\u4fcf\u76ae\u53ef\u7231\u4e9b\uff0c\u79c0\u7709\u4e0b\u4e00\u53cc\u6df1\u9083\u7684\u5927\u773c\u775b\u900f\u7740\u6e05\u79bb\u8ff7\u4eba\u7684\u5149\u5f69\uff0c\u9f3b\u6881\u79c0\u76f4\u7cbe\u81f4\uff0c\u900f\u7740\u5bf9\u672a\u6765\u7684\u65e0\u9650\u7684\u5411\u5f80\u4e0e\u81ea\u4fe1\uff0c\u770b\u7740\u5f20\u606a\uff0c\u201c\u4f60\u771f\u8981\u627e\u4e2a\u5408\u9002\u7684\u4eba\u624b\u6765\u6253\u7406\u8fd9\u91cc\uff0c\u8981\u4e0d\u6211\u660e\u5929\u5e2e\u4f60\u5c06\u62db\u8058\u542f\u4e8b\u8d34\u51fa\u53bb\uff1f\u201d</p><p>\u3000\u3000\u82cf\u4e00\u5a77\u4e4b\u5916\uff0c1978\u672c\u6765\u8fd8\u6709\u4e24\u540d\u7528\u719f\u7684\u5b66\u751f\uff0c\u82cf\u4e00\u5a77\u6bd5\u4e1a\u540e\u4f1a\u79bb\u5f00\u4e4b\u540e\uff0c\u8ba9\u8fd9\u4e24\u4eba\u6765\u8d1f\u8d23\u6253\u74061978\u4e5f\u53ef\u4ee5\uff0c\u53ea\u662f\u8fd9\u4e24\u4eba\u90fd\u51b3\u5b9a\u4e0b\u5b66\u671f\u8981\u8003\u7814\uff0c\u6240\u4ee5\u8fd8\u8981\u7269\u8272\u65b0\u7684\u4eba\u624b\u6765\u3002</p><p>\u3000\u3000\u82cf\u4e00\u5a77\u5634\u91cc\u8bf4\u4e0d\u8981\u8ddf\u4ed6\u4eec\u518d\u53d1\u751f\u5173\u7cfb\uff0c\u5979\u8003\u4e0a\u5e08\u5927\u7684\u7814\u7a76\u751f\u53c8\u51b3\u5b9a\u5de5\u4f5c\uff0c\u6682\u65f6\u4e0d\u77e5\u9053\u5979\u6240\u5de5\u4f5c\u7684\u6d77\u5dde\u5546\u4e1a\u94f6\u884c\u80cc\u540e\u7684\u63a7\u5236\u4eba\u8fd8\u662f\u9526\u6e56\u3002</p></article>"}}}]);
