(window.webpackJsonp=window.webpackJsonp||[]).push([[1732],{1755:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u516d\u8282 \u9c7c\u548c\u718a\u638c\u7684\u95ee\u9898",content:"<article><h2>\u7b2c\u4e94\u5341\u516d\u8282 \u9c7c\u548c\u718a\u638c\u7684\u95ee\u9898</h2><p>\u80fd\u591f\u7ad9\u5728\u53f0\u4e0a\u7684\u5973\u5b50\uff0c\u7eb5\u7136\u4e0d\u662f\u4e07\u91cc\u6311\u4e00\uff0c\u7d20\u8d28\u4e5f\u7edd\u4e0d\u4f1a\u5dee\u5230\u54ea\u91cc\u53bb\u3002\u867d\u7136\u8bf4\u662f\u8bf4\u201c\u661f\u4e4b\u82b1\u6ca1\u6709\u9650\u5236\uff0c\u4efb\u4f55\u4eba\u90fd\u80fd\u62a5\u540d\u201d\uff0c\u4f46\u5176\u5b9e\u8fd8\u662f\u6709\u6697\u4e2d\u7684\u9884\u5148\u9009\u62d4\u7684\uff0c\u5426\u5219\u54ea\u80fd\u8fd9\u6837\u6ee1\u5802\u4f73\u4e3d\uff0c\u8ba9\u4eba\u5927\u9971\u773c\u798f\uff0c\u65e9\u5c31\u5192\u51fa\u4e00\u5806\u81ea\u6211\u611f\u89c9\u826f\u597d\u7684\u8299\u84c9\u51e4\u59d0\u4e86\u3002\u8fd9\u540d\u76f2\u773c\u5973\u5b50\u9762\u5bb9\u79c0\u4e3d\uff0c\u8eab\u6750\u9ad8\u6311\uff0c\u7a84\u80a9\u3001\u7ea4\u8170\u3001\u4e30\u81c0\u3001\u957f\u817f\uff0c\u7684\u786e\u662f\u4f4d\u6807\u81f4\u7f8e\u4eba\uff0c\u56e0\u4e3a\u4e0d\u80fd\u89c6\u7269\uff0c\u884c\u52a8\u81ea\u7136\u6709\u4e9b\u6ede\u788d\uff0c\u4e0d\u5982\u5e38\u4eba\u7075\u654f\uff0c\u4f46\u5979\u4e3e\u624b\u6295\u8db3\u4e4b\u95f4\uff0c\u6709\u4e00\u79cd\u4e45\u7ecf\u8bad\u7ec3\u624d\u80fd\u5f62\u6210\u7684\u4f18\u96c5\u4eea\u6001\uff0c\u6c14\u5ea6\u4ece\u5bb9\u4e0d\u8feb\uff0c\u663e\u7136\u51fa\u8eab\u975e\u5bcc\u5373\u8d35\uff0c\u53d7\u8fc7\u975e\u5e38\u826f\u597d\u7684\u793c\u4eea\u6559\u80b2\u3002\u4e0d\u8fc7\u771f\u8981\u8bf4\u6709\u591a\u4e48\u4ee4\u4eba\u60ca\u8273\uff0c\u5176\u5b9e\u4e5f\u672a\u5fc5\u89c1\u5f97\uff0c\u81f3\u5c11\u6bd4\u4e0d\u4e0a\u524d\u9762\u7684\u51db\uff0c\u6309\u7167\u743c\u6069\u7684\u4e2a\u4eba\u6807\u51c6\uff0c\u5927\u81f4\u5c5e\u4e8e\u7b2c\u4e8c\u7ea7\uff0c\u4e5f\u5373\u662f\u201c\u5f88\u6f02\u4eae\uff0c\u4e0d\u7b97\u7edd\u8272\u201d\u8fd9\u79cd\u7a0b\u5ea6\u3002\u771f\u6b63\u4ee4\u4ed6\u6ce8\u610f\uff0c\u4ee4\u4ed6\u88ab\u5438\u5f15\u7684\uff0c\u662f\u8fd9\u4f4d\u76f2\u773c\u5973\u5b50\u8eab\u4e0a\u900f\u51fa\u7684\u795e\u79d8\u6c14\u8d28\uff0c\u5a07\u67d4\u3001\u8106\u5f31\u3001\u695a\u695a\u52a8\u4eba\uff0c\u5374\u53c8\u4eff\u4f5b\u6df1\u9083\u65e0\u6bd4\uff0c\u4e0d\u53ef\u6d4b\u5ea6\uff0c\u8ba9\u4eba\u60c5\u4e0d\u81ea\u7981\u5730\u7740\u8ff7\uff0c\u60f3\u8981\u9760\u8fd1\u5979\uff0c\u60f3\u548c\u5979\u4ea4\u8c08\uff0c\u60f3\u63a2\u5bfb\u5979\u7684\u79d8\u5bc6\uff0c\u4e86\u89e3\u5979\u7684\u6545\u4e8b\u3002</p><p>\u5979\u662f\u8c01\uff1f</p><p>\u4e00\u65f6\u4e4b\u95f4\uff0c\u743c\u6069\u8111\u4e2d\u53ea\u5269\u4e0b\u8fd9\u4e2a\u5ff5\u5934\uff0c\u7ffb\u6765\u8986\u53bb\u5730\u76d8\u65cb\uff0c\u8fde\u5468\u906d\u7684\u4eba\u5728\u8bf4\u4ec0\u4e48\u90fd\u6ca1\u542c\u89c1\uff0c\u76f4\u5230\u51db\u53eb\u4ed6\u624d\u56de\u8fc7\u795e\u6765\u3002\u201c\u4f60\u600e\u4e48\u4e86\uff1f\u201d\u51db\u5947\u602a\u5730\u95ee\uff0c\u201c\u5927\u767d\u5929\u7684\u53d1\u4ec0\u4e48\u5446\u5462\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u3002\u201d</p><p>\u743c\u6069\u518d\u770b\u53f0\u4e0a\uff0c\u90a3\u540d\u76f2\u773c\u5973\u5b50\u65e9\u5df2\u7ecf\u4e0b\u573a\uff0c\u4e0d\u77e5\u53bb\u5411\u3002\u4ed6\u6709\u5fc3\u6253\u542c\uff0c\u5f53\u7740\u51db\u7684\u9762\u7ec8\u7a76\u4e0d\u597d\u592a\u653e\u8086\uff0c\u53ea\u5f97\u7f62\u4e86\u3002\u53cd\u6b63\u4ee5\u5979\u7684\u7d20\u8d28\uff0c\u664b\u7ea7\u4e0b\u4e00\u8f6e\u5e94\u8be5\u662f\u6beb\u65e0\u7591\u95ee\u7684\uff0c\u660e\u5929\u518d\u6765\u770b\u5427\u3002</p><p>\u661f\u4e4b\u82b1\u7684\u7b2c\u4e00\u65e5\u53ea\u662f\u4eae\u76f8\u52a0\u521d\u9009\uff0c\u771f\u6b63\u7cbe\u5f69\u7684\u5185\u5bb9\u5728\u540e\u4e24\u65e5\uff0c\u743c\u6069\u4e5f\u6ca1\u4ec0\u4e48\u5174\u81f4\u3002\u51db\u987a\u5229\u664b\u7ea7\u4e4b\u540e\uff0c\u770b\u770b\u65f6\u95f4\u5df2\u7ecf\u662f\u4e2d\u5348\uff0c\u4e24\u4eba\u4fbf\u5148\u56de\u53bb\u3002\u201c\u600e\u4e48\u6837\uff1f\u201d\u51db\u5728\u8def\u4e0a\u95ee\uff0c\u201c\u770b\u5230\u8fd9\u4e48\u591a\u7f8e\u5973\uff0c\u5fc3\u60c5\u4e00\u5b9a\u5f88\u597d\u5427\u3002\u201d</p><p>\u201c\u4e00\u822c\u822c\uff0c\u6bd4\u8f83\u5931\u671b\u3002\u201d</p><p>\u8fd9\u4e2a\u56de\u7b54\u5927\u51fa\u51db\u7684\u610f\u6599\uff0c\u201c\u5931\u671b\uff1f\u4e0d\u4f1a\u5427\u3002\u201d</p><p>\u201c\u771f\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u672c\u4ee5\u4e3a\u80fd\u770b\u5230\u6bd4\u57fa\u5c3c\uff0c\u7ed3\u679c\u4f60\u4eec\u4e00\u4e2a\u7a7f\u5f97\u6bd4\u4e00\u4e2a\u591a\uff0c\u4e00\u4e2a\u906e\u5f97\u6bd4\u4e00\u4e2a\u4e25\u5b9e\uff0c\u8fd9\u8fd8\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u5f53\u7136\u5931\u671b\u4e86\u3002\u201d</p><p>\u201c\u6bd4\u57fa\u5c3c\uff1f\u201d\u51db\u597d\u5947\uff0c\u201c\u90a3\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u6bd4\u57fa\u5c3c\u662f\u4e00\u79cd\u5973\u5f0f\u670d\u88c5\u7684\u540d\u5b57\u3002\u201d</p><p>\u201c\u5f88\u6f02\u4eae\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u90a3\u662f\u4e16\u754c\u4e0a\u6700\u795e\u5947\uff0c\u6700\u6709\u521b\u9020\u529b\uff0c\u6700\u80fd\u51f8\u663e\u5973\u6027\u7f8e\u4e3d\u548c\u6027\u611f\u7684\u8863\u670d\uff0c\u5c31\u50cf\u7cbe\u7075\u65cf\u4f20\u8bf4\u4e2d\u7684\u6d41\u7fbd\u4e4b\u8863\uff0c\u5e73\u51e1\u7684\u5973\u5b50\u7a7f\u4e0a\u5b83\u5c31\u4f1a\u53d8\u5f97\u6709\u9b45\u529b\uff0c\u7f8e\u4e3d\u7684\u5973\u5b50\u7a7f\u4e0a\u5b83\u5c31\u4f1a\u5149\u5f69\u7167\u4eba\uff0c\u800c\u50cf\u4f60\u8fd9\u6837\u7684\u7edd\u8272\u7f8e\u4eba\u513f\uff0c\u7a7f\u4e0a\u5b83\u90a3\u5c31\u662f\u5973\u795e\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u5389\u5bb3\uff1f\u201d\u51db\u88ab\u4ed6\u8bf4\u5f97\u6709\u4e9b\u5fc3\u52a8\uff0c\u201c\u662f\u4ec0\u4e48\u6837\u5b50\u7684\uff1f\u63cf\u8ff0\u4e00\u4e0b\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u561b\uff0c\u53ea\u53ef\u610f\u4f1a\uff0c\u96be\u4ee5\u8a00\u4f20\u2014\u2014\u8fd9\u6837\u5427\uff0c\u7b49\u6211\u54ea\u5929\u6709\u7a7a\u505a\u4e00\u5957\u51fa\u6765\uff0c\u8ba9\u4f60\u7a7f\u4e0a\u8bd5\u8bd5\u5c31\u77e5\u9053\u4e86\u3002\u201d</p><p>\u201c\u597d\u554a\u597d\u554a\uff0c\u201d\u51db\u53cc\u624b\u4e00\u62cd\uff0c\u9ad8\u5174\u5730\u8bf4\uff0c\u201c\u6211\u8981\u7a7f\u7740\u5b83\u53bb\u901b\u8857\uff0c\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u4e0d\u597d\uff0c\u201d\u743c\u6069\u677f\u8d77\u8138\uff0c\u201c\u53ea\u51c6\u5728\u5bb6\u91cc\u7a7f\u7ed9\u6211\u770b\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u592a\u6f02\u4eae\u4e86\uff0c\u5982\u679c\u8ba9\u522b\u7684\u7537\u4eba\u770b\u5230\u4f60\u7a7f\u6bd4\u57fa\u5c3c\u7684\u6837\u5b50\uff0c\u6211\u4f1a\u5ac9\u5992\u7684\u3002\u201d</p><p>\u201c\u771f\u5c0f\u6c14\u3002\u201d</p><p>\u201c\u6211\u4e00\u5411\u5c31\u5c0f\u6c14\uff0c\u53c8\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u54fc\uff01\u201d</p><p>\u4e25\u683c\u8bf4\u8d77\u6765\uff0c\u743c\u6069\u548c\u51db\u66f4\u50cf\u662f\u670b\u53cb\u800c\u975e\u60c5\u4eba\uff0c\u53cc\u65b9\u56e0\u4e3a\u6885\u83f2\u65af\u800c\u76f8\u8bc6\uff0c\u56e0\u4e3a\u6885\u83f2\u65af\u800c\u53d1\u751f\u8054\u7cfb\uff0c\u6700\u7ec8\u4e5f\u662f\u56e0\u4e3a\u6885\u83f2\u65af\u624d\u8d70\u5230\u4e00\u8d77\uff0c\u5f62\u6210\u73b0\u5728\u8fd9\u79cd\u5fae\u5999\u800c\u5947\u7279\u7684\u72b6\u6001\u3002\u4f46\u4e5f\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u53cc\u65b9\u76f8\u5904\u65f6\u53cd\u800c\u66f4\u52a0\u95f2\u9002\uff0c\u6709\u4e00\u79cd\u65e0\u62d8\u65e0\u675f\u7684\u81ea\u5728\u611f\uff0c\u60f3\u4ec0\u4e48\u8bf4\u4ec0\u4e48\uff0c\u60f3\u5230\u54ea\u8bf4\u5230\u54ea\u3002\u73ca\u5609\u4e5f\u597d\uff0c\u6885\u83f2\u65af\u4e5f\u597d\uff0c\u81ea\u6211\u4eba\u683c\u90fd\u592a\u8fc7\u4e8e\u575a\u786c\u800c\u5f3a\u52bf\uff0c\u743c\u6069\u5bf9\u5979\u4eec\u662f\u65e2\u7231\u53c8\u656c\uff0c\u751a\u81f3\u6709\u9690\u9690\u7684\u754f\u60e7\u611f\uff0c\u548c\u51db\u5728\u4e00\u8d77\u7684\u65f6\u5019\u5219\u5c31\u8f7b\u677e\u591a\u4e86\u3002</p><p>\u201c\u53c8\u6f02\u4eae\uff0c\u53c8\u53ef\u7231\uff0c\u867d\u7136\u6709\u4e9b\u5b69\u5b50\u813e\u6c14\uff0c\u4f46\u603b\u4f53\u800c\u8a00\u6027\u683c\u8fd8\u662f\u4e0d\u9519\u7684\uff0c\u5728\u5e8a\u4e0a\u4e5f\u7b97\u4e56\u5de7\uff0c\u800c\u4e14\u8fd8\u4e0d\u5403\u918b\u2014\u2014\u8fd9\u7b80\u76f4\u5c31\u662f\u5b8c\u7f8e\u554a\u3002\u201d</p><p>\u51db\u5e76\u4e0d\u77e5\u9053\u81ea\u5df1\u5728\u743c\u6069\u5fc3\u91cc\u5df2\u7ecf\u6210\u4e3a\u4e86\u767e\u5206\u767e\u5973\u53cb\u7684\u4ee3\u8868\uff0c\u5979\u4f9d\u7136\u8fd8\u662f\u5f88\u597d\u5947\u90a3\u201c\u6bd4\u57fa\u5c3c\u201d\u5230\u5e95\u662f\u4ec0\u4e48\u6837\u5b50\u7684\uff0c\u4f46\u743c\u6069\u53e3\u98ce\u5f88\u7d27\uff0c\u65e0\u8bba\u5c0f\u5973\u5deb\u5982\u4f55\u5a01\u903c\u5229\u8bf1\uff0c\u575a\u51b3\u4e0d\u80af\u518d\u900f\u9732\u66f4\u591a\u7684\u5177\u4f53\u7ec6\u8282\uff0c\u6700\u540e\u51db\u4e5f\u53ea\u5f97\u653e\u5f03\u3002\u201c\u4f60\u771f\u6ca1\u52b2\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4e00\u70b9\u90fd\u4e0d\u597d\u73a9\u3002\u201d</p><p>\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u597d\u73a9\u3002\u201d</p><p>\u201c\u4f5c\u4e3a\u7537\u4eba\uff0c\u8ba9\u5973\u5b69\u5b50\u89c9\u5f97\u597d\u73a9\uff0c\u8ba9\u5979\u65f6\u65f6\u523b\u523b\u89c9\u5f97\u5f00\u5fc3\uff0c\u8fd9\u662f\u6700\u57fa\u672c\u7684\u7d20\u8d28\u548c\u8981\u6c42\u561b\u3002\u201d</p><p>\u201c\u4f60\u8bf4\u7684\u4e0d\u662f\u7537\u4eba\uff0c\u662f\u5ba0\u7269\u3002\u201d</p><p>\u201c\u54a6\uff0c\u8fd9\u4e24\u8005\u96be\u9053\u4e0d\u662f\u540c\u4e49\u8bcd\u5417\uff1f\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u56e0\u4e3a\u4e3e\u529e\u661f\u4e4b\u82b1\u7684\u7f18\u6545\uff0c\u539f\u672c\u6e05\u9759\u5bc2\u5be5\u7684\u7279\u52a0\u5c14\u9547\u53d8\u5f97\u70ed\u95f9\u975e\u51e1\uff0c\u5927\u91cf\u5916\u5730\u4eba\u6d8c\u5165\uff0c\u628a\u8857\u9053\u585e\u5f97\u6ee1\u6ee1\u5f53\u5f53\u3002\u5546\u94fa\u91cc\u7684\u5e97\u4e3b\u4eec\u4e5f\u90fd\u7eb7\u7eb7\u6253\u8d77\u7cbe\u795e\uff0c\u4f7f\u52b2\u5406\u559d\uff0c\u63a8\u9500\u5404\u5f0f\u5404\u6837\u7a00\u5947\u53e4\u602a\u7684\u7eaa\u5ff5\u54c1\uff0c\u60f3\u8981\u8d81\u8fd9\u4e2a\u673a\u4f1a\u5c0f\u8d5a\u4e00\u7b14\u3002\u743c\u6069\u5bf9\u6b64\u5168\u7136\u65e0\u89c6\uff0c\u51db\u5374\u88ab\u6210\u529f\u5730\u5438\u5f15\u4f4f\u4e86\uff0c\u9a7b\u8db3\u4e0d\u524d\uff0c\u6d41\u8fde\u5fd8\u8fd4\uff0c\u4e00\u5bb6\u4e00\u5bb6\u5730\u770b\u8fc7\u6765\uff0c\u96f6\u96f6\u788e\u788e\u7684\u5c0f\u73a9\u610f\u4e70\u4e86\u4e00\u5806\uff0c\u8fd8\u6709\u4e9b\u610f\u72b9\u672a\u5c3d\u3002\u5982\u679c\u4e0d\u662f\u743c\u6069\u518d\u4e09\u63d0\u9192\uff0c\u4f30\u8ba1\u90fd\u4e0d\u60f3\u56de\u53bb\u4e86\u3002</p><p>\u56de\u53bb\u7684\u8def\u4e0a\u53c8\u53d1\u751f\u70b9\u5c0f\u63d2\u66f2\u3002\u51db\u8fd9\u6837\u660e\u8273\u65e0\u4fe6\u7684\u7f8e\u4e3d\u5c11\u5973\uff0c\u65e0\u8bba\u8d70\u5230\u54ea\u91cc\u90fd\u662f\u4f17\u4eba\u77a9\u76ee\u7684\u7126\u70b9\uff0c\u76f8\u5e94\u7684\u743c\u6069\u4e5f\u5c31\u6210\u4e3a\u6700\u788d\u773c\u7684\u5b58\u5728\uff0c\u4e0d\u77e5\u9053\u88ab\u5728\u6697\u5730\u91cc\u8bc5\u5492\u4e86\u591a\u5c11\u6b21\u3002\u5f53\u7136\uff0c\u7edd\u5927\u591a\u6570\u4eba\u4e5f\u90fd\u53ea\u662f\u770b\u770b\u4fbf\u7f62\uff0c\u4f46\u4e16\u754c\u4e0a\u4e5f\u4ece\u6765\u4e0d\u7f3a\u5c11\u81ea\u6211\u611f\u89c9\u826f\u597d\u7684\u7b28\u86cb\u5bb6\u4f19\uff0c\u4fbf\u6709\u4e00\u4f4d\u5e74\u8f7b\u7537\u5b50\uff0c\u5e26\u7740\u56db\u4e2a\u968f\u4ece\uff0c\u9c9c\u8863\u6012\u9a6c\uff0c\u8dbe\u9ad8\u6c14\u626c\uff0c\u81ea\u79f0\u662f\u201c\u6218\u5f79\u8c37\u7684\u5a01\u7075\u987f\xb7\u67e5\u5c14\u65af\u9a91\u58eb\u201d\uff0c\u5f53\u8857\u62e6\u4f4f\u4e86\u743c\u6069\u548c\u51db\uff0c\u539a\u7740\u8138\u76ae\u4e0a\u6765\u642d\u8baa\uff0c\u7ee7\u800c\u8a00\u8bed\u6311\u8845\uff0c\u52a8\u624b\u52a8\u811a\u3002\u743c\u6069\u5f00\u59cb\u65f6\u4e60\u60ef\u6027\u4f4e\u8c03\uff0c\u9ed8\u4e0d\u4f5c\u58f0\uff0c\u5230\u540e\u6765\u5fcd\u65e0\u53ef\u5fcd\uff0c\u4fbf\u6253\u7b97\u7ed9\u8fd9\u5c0f\u5b50\u70b9\u6559\u8bad\u3002\u4ed6\u9ed8\u8bf5\u5492\u8bed\uff0c\u7070\u767d\u8272\u7684\u9b54\u6cd5\u80fd\u91cf\u7403\u5728\u638c\u5fc3\u6084\u6084\u51dd\u805a\uff0c\u4e00\u8bb0\u77f3\u5316\u672f\u6b63\u5f85\u53d1\u51fa\uff0c\u5c31\u5728\u6b64\u65f6\uff0c\u51e0\u4e2a\u5168\u526f\u6b66\u88c5\u7684\u536b\u5175\u5206\u5f00\u56f4\u89c2\u7684\u4eba\u7fa4\u51b2\u4e86\u8fdb\u6765\uff0c\u4e09\u4e0b\u4e94\u9664\u4e8c\u628a\u8fd9\u4f4d\u201c\u5a01\u7075\u987f\xb7\u67e5\u5c14\u65af\u9a91\u58eb\u201d\u548c\u4ed6\u7684\u968f\u4ece\u4eec\u5236\u670d\uff0c\u4ee5\u201c\u6270\u4e71\u793e\u4f1a\u6cbb\u5b89\u201d\u4e3a\u7531\uff0c\u5ba3\u5e03\u5168\u4f53\u62d8\u7559\u4e09\u5341\u65e5\uff0c\u5373\u65e5\u8d77\u9001\u5230\u77ff\u5c71\u4e2d\u53bb\u6316\u7164\u2014\u2014\u5982\u679c\u4e0d\u60f3\u88ab\u62d8\u7559\uff0c\u53ef\u4ee5\u7f34\u7eb3\u8d4e\u91d1\uff0c\u6bcf\u4eba\u6bcf\u91d1\u5e01\u8d4e\u91d1\u6298\u62b5\u4e00\u65e5\u3002</p><p>\u5728\u5927\u591a\u6570\u5730\u533a\uff0c\u4e00\u4e2a\u666e\u901a\u56db\u53e3\u4e4b\u5bb6\u4e00\u65e5\u7684\u82b1\u8d39\u5f00\u9500\u5927\u7ea6\u662f\u4e00\u94f6\u5e01\u3002\u8fd9\u4f4d\u5012\u9709\u7684\u9a91\u58eb\u4e00\u884c\u4e94\u4eba\uff0c\u8d4e\u91d1\u5171\u8ba1\u4e00\u767e\u4e94\u5341\u91d1\u5e01\uff0c\u76f8\u5f53\u4e8e\u4e00\u5343\u4e94\u767e\u94f6\u5e01\uff0c\u7b97\u5f97\u4e0a\u662f\u4e00\u7b14\u5de8\u6b3e\uff0c\u62b5\u5f97\u4e0a\u5168\u9547\u5546\u94fa\u4e00\u5929\u7684\u8425\u4e1a\u989d\u4e86\u3002\u8fd9\u7b80\u76f4\u5c31\u662f\u8d64\u88f8\u88f8\u7684\u6572\u8bc8\u52d2\u7d22\uff0c\u4f46\u8981\u771f\u88ab\u9001\u53bb\u6316\u7164\uff0c\u90a3\u53c8\u662f\u5802\u5802\u7684\u9a91\u58eb\u5927\u4eba\u6240\u51b3\u4e0d\u80fd\u5bb9\u8bb8\u7684\uff0c\u9762\u5b50\u4e0a\u9996\u5148\u5c31\u4e0b\u4e0d\u6765\uff0c\u66f4\u522b\u8bf4\u8fd8\u6709\u6027\u547d\u4e4b\u5fe7\u2014\u2014\u4f17\u6240\u5468\u77e5\uff0c\u7164\u77ff\u662f\u4e16\u754c\u4e0a\u6700\u5371\u9669\u7684\u5730\u65b9\uff0c\u548c\u770b\u5b88\u6240\u5e76\u5217\u7b2c\u4e00\u3002\u72b9\u8c6b\u518d\u4e09\uff0c\u4ed6\u6700\u540e\u8fd8\u662f\u7f34\u7eb3\u4e86\u8d4e\u91d1\uff0c\u7070\u6e9c\u6e9c\u5730\u79bb\u53bb\uff0c\u536b\u5175\u4eec\u5219\u80cc\u7740\u91d1\u5e01\u6ee1\u8f7d\u800c\u5f52\u3002</p><p>\u201c\u771f\u53ef\u601c\u3002\u201d\u51db\u8bc4\u4ef7\uff0c\u4eff\u4f5b\u5168\u7136\u6ca1\u6709\u610f\u8bc6\u5230\u81ea\u5df1\u6b63\u662f\u8fd9\u8d77\u4e8b\u4ef6\u7684\u5bfc\u706b\u7d22\u3002</p><p>\u743c\u6069\u70b9\u5934\u8868\u793a\u8d5e\u540c\uff0c\u201c\u6240\u4ee5\u8bf4\uff0c\u7ea2\u989c\u7978\u6c34\u561b\u3002\u201d</p><p>\u201c\u5582\uff0c\u4f60\u4ec0\u4e48\u610f\u601d\u554a\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u6ca1\u4ec0\u4e48\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53ea\u662f\u5728\u5938\u5956\u4f60\u6709\u9b45\u529b\u800c\u5df2\u3002\u201d</p><p>\u4e3a\u4e86\u907f\u514d\u51fa\u73b0\u66f4\u591a\u7c7b\u4f3c\u60b2\u5267\uff0c\u8ba9\u5854\u745f\u8c37\u7684\u536b\u5175\u4eec\u83b7\u5f97\u66f4\u591a\u7684\u521b\u6536\u673a\u4f1a\uff0c\u743c\u6069\u62c9\u7740\u51db\u8d76\u5feb\u79bb\u5f00\u4eba\u7fa4\u3002\u56e0\u4e3a\u8fd9\u4e00\u803d\u6401\uff0c\u5f53\u4ed6\u4eec\u56de\u5230\u4f4f\u5904\u65f6\uff0c\u5df2\u7ecf\u8fc7\u4e86\u5348\u9910\u65f6\u95f4\uff0c\u597d\u5728\u73ca\u5609\u9884\u5148\u5631\u5490\u53a8\u623f\uff0c\u7ed9\u4ed6\u4eec\u7559\u4e86\u4e00\u4efd\u3002</p><p>\u201c\u8bdd\u8bf4\uff0c\u73ca\u5609\u4e3a\u4ec0\u4e48\u4e0d\u53bb\u53c2\u52a0\u661f\u4e4b\u82b1\uff1f\u201d\u51db\u4e00\u8fb9\u5f80\u5634\u91cc\u585e\u98df\u7269\u4e00\u8fb9\u542b\u542b\u7cca\u7cca\u5730\u95ee\uff0c\u6beb\u65e0\u534a\u70b9\u6dd1\u5973\u98ce\u8303\uff0c\u201c\u6211\u672c\u4ee5\u4e3a\u5979\u4f1a\u53bb\u5462\u3002\u5979\u90a3\u4e48\u6f02\u4eae\uff0c\u5982\u679c\u53bb\u7684\u8bdd\uff0c\u593a\u51a0\u5e94\u8be5\u5927\u6709\u5e0c\u671b\u5427\u3002\u201d</p><p>\u201c\u662f\u4e48\uff1f\u6211\u5f88\u5c11\u542c\u89c1\u5973\u5b69\u5b50\u79f0\u8d5e\u522b\u7684\u5973\u5b69\u5b50\u6f02\u4eae\u5462\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u5979\u771f\u7684\u5f88\u6f02\u4eae\u554a\u3002\u201d</p><p>\u201c\u6bd4\u4f60\u6f02\u4eae\uff1f\u201d\u743c\u6069\u6545\u610f\u95ee\u3002</p><p>\u51db\u60f3\u4e86\u60f3\uff0c\u201c\u8fd9\u4e2a\u4e0d\u597d\u8bf4\u3002\u201d\u5979\u5766\u8bda\u5730\u56de\u7b54\u3002</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u8981\u5355\u7eaf\u8bba\u5bb9\u8c8c\u8bba\u8eab\u6750\uff0c\u6211\u8fd8\u662f\u633a\u6709\u81ea\u4fe1\u7684\u3002\u4f46\u5979\u4e0d\u4ec5\u6f02\u4eae\uff0c\u800c\u4e14\u8fd8\u975e\u5e38\u6709\u5973\u4eba\u5473\uff0c\u5728\u8fd9\u70b9\u4e0a\u6211\u548c\u827e\u5f25\u8587\u5c31\u90fd\u6bd4\u4e0d\u4e86\u4e86\u3002\u201d</p><p>\u8fd9\u5012\u662f\u5b9e\u8bdd\uff0c\u867d\u7136\u90fd\u662f\u4ee4\u4eba\u60ca\u53f9\u7684\u7f8e\u4eba\uff0c\u4f46\u98ce\u683c\u5374\u662f\u8fe5\u5f02\uff0c\u6885\u83f2\u65af\u504f\u4e8e\u82f1\u6b66\u9510\u5229\uff0c\u51db\u53c8\u592a\u8fc7\u4fcf\u4e3d\u53ef\u7231\uff0c\u8981\u8bba\u5973\u6027\u5316\u7684\u6e29\u5a49\u5a34\u96c5\uff0c\u67d4\u7f8e\u5a07\u5a9a\uff0c\u8fd8\u662f\u73ca\u5609\u660e\u663e\u80dc\u51fa\u3002</p><p>\u201c\u4f46\u4f60\u6bd4\u8f83\u53ef\u7231\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u6ca1\u52b2\uff0c\u201d\u51db\u6241\u6241\u5c0f\u5634\uff0c\u201c\u4ece\u5c0f\u5230\u5927\u90fd\u88ab\u4eba\u5938\u53ef\u7231\uff0c\u65e9\u542c\u817b\u4e86\u3002\u201d</p><p>\u201c\u53ef\u7231\u4e0d\u597d\u4e48\uff1f\u201d</p><p>\u201c\u5988\u5988\u8bf4\uff1a\u53ef\u7231\u662f\u5f62\u5bb9\u5b69\u5b50\u7684\uff0c\u5973\u4eba\u5e94\u8be5\u5f97\u5230\u7684\u8bc4\u8bed\u662f\u6f02\u4eae\uff1b\u5988\u5988\u8fd8\u8bf4\uff1a\u53ef\u7231\u7684\u5973\u4eba\u672a\u5fc5\u6f02\u4eae\uff0c\u6f02\u4eae\u7684\u5973\u4eba\u4e00\u5b9a\u53ef\u7231\u3002\u201d</p><p>\u201c\u4f60\u4e5f\u5f88\u6f02\u4eae\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u65e2\u6f02\u4eae\u53c8\u53ef\u7231\u3002\u201d</p><p>\u51db\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u7a81\u7136\u610f\u5174\u9611\u73ca\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u4ee5\u524d\u5e38\u542c\u827e\u5f25\u8587\u5938\u5956\uff0c\u8bf4\uff1a\u2018\u743c\u6069\u7684\u59d0\u59d0\u662f\u4f4d\u7edd\u8272\u5927\u7f8e\u4eba\u2019\uff0c\u4e00\u76f4\u4e0d\u600e\u4e48\u76f8\u4fe1\uff0c\u76f4\u5230\u524d\u5929\u89c1\u5230\u672c\u4eba\u624d\u660e\u767d\u2026\u2026\u201d</p><p>\u201c\u624d\u660e\u767d\uff1f\u201d\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u201c\u4f60\u660e\u767d\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u660e\u767d\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u641e\u4e0a\u81ea\u5df1\u59d0\u59d0\u3002\u201d</p><p>\u201c\u2026\u2026\u600e\u4e48\u8bf4\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u6709\u8fd9\u4e48\u6f02\u4eae\u7684\u59d0\u59d0\uff0c\u5982\u679c\u662f\u6211\u7684\u8bdd\uff0c\u4e5f\u4e00\u5b9a\u4f1a\u559c\u6b22\u4e0a\u7684\u5440\u3002\u201d</p><p>\u201c\u6211\u8bf4\uff0c\u4f60\u8fd9\u5bb6\u4f19\u8111\u888b\u91cc\u5230\u5e95\u90fd\u5728\u60f3\u4ec0\u4e48\u554a\uff01\u201d</p><p>\u743c\u6069\u9690\u9690\u6709\u4e9b\u607c\u7f9e\u6210\u6012\uff0c\u4f46\u51db\u4f3c\u4e4e\u5e76\u6ca1\u53d1\u89c9\uff0c\u201c\u6ca1\u60f3\u4ec0\u4e48\u554a\uff0c\u201d\u5979\u81ea\u987e\u81ea\u5730\u8bf4\uff0c\u201c\u6211\u53ea\u662f\u7a81\u7136\u5728\u60f3\uff0c\u4f60\u559c\u6b22\u827e\u5f25\u8587\uff0c\u4e5f\u559c\u6b22\u73ca\u5609\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u90a3\u4f60\u6700\u559c\u6b22\u8c01\u5462\uff1f\u201d</p><p>\u201c\u2026\u2026\u8fd9\u5173\u4f60\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u827e\u5f25\u8587\u662f\u6211\u7684\u597d\u670b\u53cb\uff0c\u6211\u5173\u5fc3\u4e00\u4e0b\u5979\u7684\u611f\u60c5\u751f\u6d3b\uff0c\u4e5f\u662f\u5f88\u5408\u7406\u7684\u4e8b\u60c5\u561b\u3002\u201d</p><p>\u4f60\u4eec\u7684\u5173\u7cfb\uff0c\u4e0d\u4ec5\u4ec5\u662f\u201c\u597d\u670b\u53cb\u201d\u90a3\u4e48\u5355\u7eaf\u5427\u3002</p><p>\u743c\u6069\u54fc\u4e86\u58f0\uff0c\u201c\u4e0d\u7ba1\u6211\u6700\u559c\u6b22\u7684\u662f\u8c01\uff0c\u53cd\u6b63\u4e0d\u662f\u4f60\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\u554a\u3002\u201d\u51db\u5e76\u4e0d\u4ecb\u610f\u3002</p><p>\u201c\u90a3\u4f60\u77e5\u9053\u539f\u56e0\u4e48\uff1f\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u5979\u4eec\u90fd\u6bd4\u4f60\u806a\u660e\uff0c\u81f3\u5c11\u4e0d\u4f1a\u95ee\u6211\u8fd9\u79cd\u65e0\u804a\u7684\u95ee\u9898\uff01\u201d</p><p>\u201c\u556a\u201d\u5730\u4e00\u58f0\uff0c\u51db\u628a\u624b\u4e2d\u7684\u9910\u5200\u91cd\u91cd\u62cd\u5728\u684c\u4e0a\uff0c\u6c14\u9f13\u9f13\u5730\u77aa\u7740\u743c\u6069\uff0c\u6b32\u5f85\u53d1\u6012\uff0c\u5374\u53c8\u6df1\u5438\u51e0\u53e3\u6c14\uff0c\u5f3a\u81ea\u6309\u637a\u4e0b\u53bb\u3002\u201c\u8fd9\u4e2a\u95ee\u9898\u600e\u4e48\u65e0\u804a\u4e86\uff1f\u201d\u5979\u8d28\u95ee\u3002</p><p>\u201c\u5f88\u7b80\u5355\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5047\u8bbe\u4f60\u65e2\u7231\u5403\u9c7c\uff0c\u53c8\u7231\u5403\u718a\u638c\uff0c\u4e24\u6837\u90fd\u975e\u5e38\u559c\u6b22\u3002\u73b0\u5728\u4e24\u76d8\u83dc\u90fd\u6446\u5728\u4f60\u9762\u524d\uff0c\u4f60\u600e\u4e48\u505a\uff1f\u201d</p><p>\u201c\u5f53\u7136\u662f\u90fd\u5403\u4e86\u554a\u3002\u201d</p><p>\u201c\u5982\u679c\u6709\u4eba\u963b\u6b62\u4f60\uff0c\u53ea\u51c6\u4f60\u5403\u5176\u4e2d\u4e00\u6837\u5462\uff1f\u201d</p><p>\u51db\u601d\u8003\u4e86\u534a\u79d2\u949f\uff0c\u201c\u8c01\u963b\u6b62\u6211\u5403\u4e24\u6837\uff0c\u6211\u5c31\u6740\u8c01\u3002\u201d</p><p>\u201c\u6240\u4ee5\u554a\uff0c\u8fd9\u4e2a\u95ee\u9898\u6beb\u65e0\u610f\u4e49\u3002\u201d</p><p>\u201c\u5207\uff01\u201d\u51db\u51b7\u7b11\uff0c\u201c\u743c\u6069\uff0c\u5728\u6253\u8fd9\u4e2a\u6bd4\u65b9\u4e4b\u524d\uff0c\u4f60\u5148\u786e\u5b9a\u5979\u4eec\u4e24\u4eba\u662f\u6446\u5728\u76d8\u5b50\u91cc\u7684\u83dc\u518d\u8bf4\u5427\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u83ab\u540d\u5176\u5999\u7684\uff0c\u4e24\u4eba\u8d8a\u8bf4\u8d8a\u50f5\uff0c\u4e0d\u6b22\u800c\u6563\u3002\u743c\u6069\u56de\u5230\u623f\u95f4\u4e2d\uff0c\u53ea\u89c9\u5fc3\u60c5\u70e6\u8e81\u65e0\u6bd4\uff0c\u770b\u4ec0\u4e48\u90fd\u4e0d\u987a\u773c\uff0c\u5750\u5728\u8eba\u6905\u4e2d\u751f\u4e86\u534a\u5929\u95f7\u6c14\uff0c\u72b9\u81ea\u4e0d\u80fd\u5e73\u590d\u3002\u5de6\u53f3\u65e0\u4e8b\uff0c\u4ece\u6000\u4e2d\u6b21\u5143\u888b\u4e2d\u53d6\u51fa\u6574\u5e45\u8428\u74e6\u68cb\uff0c\u653e\u5728\u684c\u4e0a\u4ed4\u7ec6\u7aef\u8be6\u3002</p><p>\u6570\u5343\u5e74\u524d\uff0c\u67d0\u4f4d\u4f0a\u739b\u65af\u5361\u5947\u68b0\u5e08\u56e0\u4e3a\u89e6\u72af\u5e1d\u56fd\u5f8b\u6cd5\uff0c\u6d41\u4ea1\u81f3\u5e7d\u6697\u5730\u57df\u7684\u74dc\u7406\u5fb7\u65af\u57ce\uff0c\u4f2a\u88c5\u6210\u9ed1\u6697\u7cbe\u7075\u5e76\u5316\u540d\u68b5\u94ce\u5c14\uff0c\u5f53\u4e0a\u7b2c\u4e00\u5bb6\u65cf\uff08\u83f2\u5c14\u4f26\uff09\u7684\u9996\u5e2d\u5deb\u5e08\u3002\u4ed6\u52fe\u7ed3\u6076\u9b54\u541b\u738b\u72c4\u6469\u9ad8\u6839\uff0c\u7cc5\u5408\u4f0a\u739b\u65af\u5361\u7684\u5947\u68b0\u672f\u548c\u6076\u9b54\u79d8\u6cd5\uff0c\u6a21\u4eff\u5353\u5c14\u7684\u98ce\u4fd7\u5236\u4f5c\u51fa\u8fd9\u526f\u8428\u74e6\u68cb\u3002\u540e\u6765\u8fd9\u540d\u5947\u68b0\u5e08\u88ab\u5353\u5c14\u4eec\u6240\u6740\uff0c\u7075\u9b42\u5760\u5165\u6df1\u6e0a\u53d8\u6210\u6076\u9b54\uff0c\u81ea\u79f0\u201c\u6b27\u51ef\u201d\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u5426\u771f\u540d\u30021373DR\uff0c\u5f53\u65f6\u4f4d\u4e8e\u5e7d\u6697\u5730\u57df\u6668\u70bc\u57ce\u7684\u743c\u6069\u81ea\u6b27\u51ef\u624b\u4e2d\u5f97\u5230\u8fd9\u526f\u6218\u65d7\uff0c\u5171\u8ba1\u4e8c\u5341\u56db\u679a\u68cb\u5b50\uff0c\u53ef\u4ee5\u53d8\u6210\u76f8\u5e94\u7684\u4e8c\u5341\u56db\u5c0a\u9b54\u50cf\uff0c\u6750\u8d28\u662f\u7ea2\u7389\u9ad3\u548c\u9ed1\u66dc\u77f3\uff0c\u4e0d\u5982\u91d1\u5c5e\u9b54\u50cf\u575a\u56fa\uff0c\u4e5f\u4e0d\u80fd\u514d\u75ab\u9b54\u6cd5\uff0c\u4f46\u53d7\u521b\u540e\u80fd\u591f\u81ea\u6211\u4fee\u590d\u3002\u53ef\u60dc\u5728\u9634\u9b42\u57ce\u7684\u838e\u5c14\u795e\u6bbf\u4e2d\uff0c\u743c\u6069\u906d\u9047\u90a3\u4f4d\u75af\u72c2\u796d\u53f8\u827e\u5361\u8bfa\uff0c\u6253\u6597\u4e2d\u88ab\u4ed6\u7684\u5251\u5203\u58c1\u969c\u5c06\u4e24\u679a\u201c\u98df\u4eba\u9b54\u201d\u68cb\u5b50\u5207\u6210\u7c89\u788e\uff0c\u5f7b\u5e95\u6bc1\u635f\u65e0\u6cd5\u590d\u539f\u3002\u6628\u65e5\u5728\u86c7\u708e\u5c71\u78b0\u4e0a\u5438\u8840\u9b3c\u970d\u6587\uff0c\u5bf9\u65b9\u7528\u201c\u5316\u77f3\u4e3a\u6ce5\u201d\u6cd5\u672f\u628a\u5730\u9762\u53d8\u6210\u6cbc\u6cfd\uff0c\u8ba9\u4e24\u679a\u201c\u6218\u58eb\u201d\u68cb\u5b50\u6df1\u9677\u5176\u4e2d\u3002\u743c\u6069\u6253\u8d25\u5438\u8840\u9b3c\u540e\uff0c\u6025\u4e8e\u4e0e\u6885\u83f2\u65af\u548c\u51db\u4f1a\u5408\uff0c\u6765\u4e0d\u53ca\u53d6\u68cb\u5b50\uff0c\u56de\u6765\u65f6\u60f3\u518d\u53d6\uff0c\u5374\u53d1\u73b0\u5df2\u7ecf\u6d88\u5931\u4e86\u3002\u5982\u6b64\u4e00\u6765\uff0c\u539f\u672c\u5168\u5957\u4e8c\u5341\u56db\u679a\u68cb\u5b50\uff0c\u5982\u4eca\u53ea\u5269\u4e8c\u5341\u679a\u4e86\u3002\u8fd9\u81ea\u7136\u633a\u53ef\u60dc\uff0c\u4f46\u5176\u5b9e\u4e5f\u6ca1\u6709\u591a\u5c11\u771f\u6b63\u7684\u5b9e\u9645\u5f71\u54cd\uff0c\u53cd\u6b63\u743c\u6069\u4e5f\u5e76\u4e0d\u80fd\u540c\u65f6\u4f7f\u7528\u6240\u6709\u68cb\u5b50\u3002\u800c\u4e14\u968f\u7740\u9b54\u6cd5\u9020\u8be3\u7684\u4e0d\u65ad\u63d0\u5347\uff0c\u4ee5\u540e\u8f83\u591a\u4f7f\u7528\u7684\u5e94\u8be5\u662f\u9ad8\u9636\u7684\u5deb\u5e08\u3001\u796d\u53f8\u68cb\u5b50\uff0c\u539f\u672c\u62c5\u4efb\u4e3b\u529b\u7684\u6218\u58eb\u3001\u98df\u4eba\u9b54\u68cb\u5b50\u6e10\u6e10\u4f1a\u6ca6\u4e3a\u70ae\u7070\uff0c\u751a\u81f3\u4e0d\u518d\u4e0a\u573a\u3002\u6b64\u6b21\u5bf9\u4ed8\u5438\u8840\u9b3c\uff0c\u6700\u540e\u771f\u6b63\u5efa\u529f\u7684\u4fbf\u662f\u90a3\u4e24\u679a\u201c\u5973\u796d\u53f8\u201d\uff0c\u81f3\u4e8e\u4e4b\u524d\u7684\u6218\u58eb\u9b54\u50cf\uff0c\u53ea\u662f\u8bf1\u654c\u800c\u5df2\uff0c\u5e76\u6ca1\u53d1\u6325\u592a\u5927\u4f5c\u7528\u3002</p><p>\u201c\u4e0d\u8fc7\u8bf4\u8d77\u6765\uff0c\u6709\u70b9\u5947\u602a\u554a\uff0c\u6211\u4ec0\u4e48\u65f6\u5019\u80fd\u591f\u540c\u65f6\u53ec\u5524\u4e24\u540d\u5973\u796d\u53f8\u4e86\uff1f\u201d</p><p>\u4e8c\u5341\u56db\u679a\u8428\u74e6\u68cb\u5206\u6210\u4e94\u7c7b\uff1a\u8718\u86db\u4e3b\u6bcd\uff08\u672a\u5b8c\u6210\uff09\u3001\u5deb\u5e08\u3001\u796d\u53f8\u3001\u6218\u58eb\u548c\u98df\u4eba\u9b54\u5974\u96b6\u3002\u4ee5\u743c\u6069\u76ee\u524d\u7684\u9b54\u6cd5\u9020\u8be3\uff0c\u539f\u672c\u662f\u4e0d\u8db3\u4ee5\u540c\u65f6\u63a7\u5236\u4e24\u540d\u201c\u5973\u796d\u53f8\u201d\u7684\uff0c\u8fd8\u5dee\u4e86\u4e00\u70b9\u3002\u5f53\u65f6\u5728\u6218\u6597\u4e2d\uff0c\u4ed6\u539f\u672c\u7684\u6253\u7b97\u662f\u53ec\u5524\u4e00\u540d\u5973\u796d\u53f8\u548c\u4e00\u540d\u5353\u5c14\u6218\u58eb\uff0c\u4f46\u624b\u6307\u78b0\u5230\u68cb\u5b50\u65f6\uff0c\u7a81\u7136\u4fbf\u5fc3\u6709\u611f\u5e94\u4e00\u822c\uff0c\u9b3c\u4f7f\u795e\u5dee\u5730\u540c\u65f6\u6254\u51fa\u4e24\u679a\u5973\u796d\u53f8\u51fa\u6765\uff0c\u7ed3\u679c\u8fd8\u5f53\u771f\u6210\u529f\u3002\u8fd9\u5176\u4e2d\u7684\u7f18\u7531\uff0c\u743c\u6069\u5f53\u65f6\u65e0\u6687\u7ec6\u60f3\uff0c\u5982\u4eca\u56de\u987e\u8d77\u6765\uff0c\u4f9d\u65e7\u4e5f\u4e0d\u786e\u5b9a\u5230\u5e95\u662f\u4e3a\u4ec0\u4e48\uff0c\u4f46\u76f4\u89c9\u544a\u8bc9\u4ed6\u548c\u524d\u65e5\u90a3\u4e00\u6218\u6709\u5173\u3002</p><p>\u524d\u65e5\u4e0b\u5348\uff0c\u743c\u6069\u966a\u540c\u6885\u83f2\u65af\u9047\u89c1\u4e86\u5deb\u5996\u957f\u8001\u6c49\u5f25\u5c14\u987f\uff0c\u53cc\u65b9\u6253\u4e86\u4e00\u67b6\u3002\u743c\u6069\u5728\u6218\u6597\u4e2d\u7a81\u5140\u5730\u83b7\u5f97\u4e86\u532a\u5937\u6240\u601d\u7684\u7a7a\u95f4\u638c\u63a7\u80fd\u529b\uff0c\u4e3e\u624b\u95f4\u4fbf\u7c89\u788e\u4e86\u534a\u4f4d\u9762\uff0c\u751a\u81f3\u6321\u4e0b\u4e86\u6885\u83f2\u65af\u548c\u9ed1\u8863\u5973\u5b69\u7684\u8054\u624b\u4e00\u51fb\uff0c\u540e\u6765\u8fd9\u79cd\u529b\u91cf\u5374\u53c8\u83ab\u540d\u5176\u5999\u5730\u6d88\u6563\u2014\u2014\u4f46\u4f3c\u4e4e\u5e76\u6ca1\u6709\u5f7b\u5e95\u4e27\u5931\u6b86\u5c3d\uff0c\u8fd8\u7559\u4e86\u4e9b\u8bb8\u9690\u9690\u7ea6\u7ea6\u7684\u6b8b\u4f59\u3002\u800c\u6b63\u662f\u8fd9\u70b9\u6b8b\u4f59\u75d5\u8ff9\uff0c\u8ba9\u4ed6\u5bf9\u8fd9\u526f\u8428\u74e6\u68cb\u9b54\u50cf\u62e5\u6709\u4e86\u66f4\u5f3a\u7684\u63a7\u5236\u529b\u2014\u2014\u81f3\u4e8e\u5230\u5e95\u589e\u5f3a\u4e86\u591a\u5c11\uff0c\u8fd9\u5c31\u9700\u8981\u8ba1\u7b97\u4e86\u3002</p><p>\u638f\u51fa\u7eb8\u7b14\uff0c\u743c\u6069\u5f00\u59cb\u57cb\u5934\u6f14\u7b97\uff0c\u7ecf\u8fc7\u53cd\u590d\u9a8c\u8bc1\uff0c\u5341\u5206\u949f\u540e\u4ed6\u5f97\u51fa\u786e\u5207\u7ed3\u8bba\u3002\u9664\u6389\u201c\u8718\u86db\u4e3b\u6bcd\u201d\u662f\u672a\u5b8c\u6210\u54c1\uff0c\u65e0\u6cd5\u53ec\u5524\u5916\uff0c\u5176\u4ed6\u56db\u79cd\u68cb\u5b50\u90fd\u6709\u4e00\u4e2a\u76f8\u5e94\u7684\u201c\u53ec\u5524\u503c\u201d\uff0c\u743c\u6069\u4ee5\u524d\u8ba1\u7b97\u51fa\u7684\u7ed3\u679c\u662f\uff1a\u5deb\u5e08\u4e5d\u3001\u5973\u796d\u53f8\u4e03\u3001\u6218\u58eb\u4e94\u3001\u98df\u4eba\u9b54\u4e09\uff0c\u73b0\u5728\u5219\u662f\u6240\u6709\u201c\u53ec\u5524\u503c\u201d\u90fd\u4e0b\u8c03\u4e86\u4e00\u7ea7\u3002</p><p>\u201c\u6240\u8c13\u7684\u7687\u5ba4\u8840\u8109\u3001\u7fd4\u9f99\u5370\u8bb0\uff0c\u7ec8\u4e8e\u5f00\u59cb\u53d1\u6325\u4f5c\u7528\u4e86\u4e48\uff1f\u201d\u743c\u6069\u534a\u662f\u81ea\u5632\u5730\u60f3\u3002</p><p>\u4e00\u60f3\u5230\u8fd9\u70b9\uff0c\u4fbf\u53c8\u52fe\u8d77\u6ee1\u8179\u5fc3\u4e8b\uff0c\u8fd9\u4e2a\u83ab\u540d\u5176\u5999\u5f97\u6765\u7684\u4f0a\u739b\u65af\u5361\u7687\u5ba4\u8eab\u4efd\uff0c\u4ee5\u53ca\u5b83\u80cc\u540e\u6240\u7275\u626f\u5230\u7684\u5e9e\u5927\u8c1c\u56e2\uff0c\u5df2\u7ecf\u591f\u8ba9\u743c\u6069\u5934\u75bc\uff0c\u6349\u6478\u4e0d\u900f\u4e86\u3002\u4ed6\u63c9\u7740\u989d\u5934\u4e24\u4fa7\uff0c\u6b63\u89c9\u8111\u888b\u91cc\u9690\u9690\u4f5c\u75bc\uff0c\u5367\u5ba4\u7684\u95e8\u88ab\u8f7b\u8f7b\u6572\u54cd\u3002</p><p>\u201c\u8fdb\u6765\u3002\u201d</p><p>\u95e8\u88ab\u63a8\u5f00\u4e86\uff0c\u8910\u53d1\u957f\u817f\u7684\u7f8e\u4eba\u6b3e\u6b3e\u8d70\u8fdb\u6765\u3002\u201c\u4e0b\u5348\u597d\uff0c\u4e3b\u4eba\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u58f0\u97f3\u4e2d\u4f3c\u4e4e\u6709\u4e9b\u9690\u7ea6\u7684\u7b11\u610f\uff0c\u201c\u600e\u4e48\u4e00\u4e2a\u4eba\u8eb2\u5728\u623f\u95f4\u91cc\u751f\u95f7\u6c14\uff1f\u548c\u51db\u5c0f\u59d0\u5435\u67b6\u4e86\uff1f\u201d</p><p>\u201c\u65e2\u7136\u77e5\u9053\u5c31\u4e0d\u7528\u518d\u95ee\u4e86\u5427\u3002\u201d\u743c\u6069\u6ca1\u597d\u6c14\u5730\u8bf4\u3002</p><p>\u201c\u770b\u8d77\u6765\u5fc3\u60c5\u771f\u7684\u5f88\u7cdf\u7cd5\u5462\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u90a3\u4e48\uff0c\u9700\u4e0d\u9700\u8981\u60a8\u7684\u5c0f\u5973\u5974\u4e3a\u60a8\u505a\u4e00\u4e9b\u80fd\u591f\u632f\u594b\u7cbe\u795e\u7684\u4e8b\u60c5\u5462\uff1f\u201d</p><p>\u7f8e\u4eba\u4e3b\u52a8\u63d0\u8bae\uff0c\u743c\u6069\u81ea\u7136\u6ca1\u6709\u62d2\u7edd\u7684\u7406\u7531\uff0c\u53cd\u6b63\u53cc\u65b9\u65e9\u5c31\u914d\u5408\u9ed8\u5951\uff0c\u4e5f\u4e0d\u7528\u518d\u591a\u8bf4\u4ec0\u4e48\u3002\u838e\u73de\u514b\u5f13\u8eab\u94bb\u5230\u4e66\u684c\u4e0b\uff0c\u719f\u7ec3\u5730\u89e3\u5f00\u743c\u6069\u7684\u8170\u5e26\uff0c\u4e00\u6839\u70ed\u6c14\u817e\u817e\u7684\u7d2b\u7ea2\u8272\u5de8\u9f99\u4ece\u4e2d\u5f39\u51fa\uff0c\u556a\u5730\u6253\u5728\u5979\u7684\u8138\u988a\u4e0a\uff0c\u52a8\u9759\u6deb\u9761\u81f3\u6781\u3002\u9b45\u9b54\u5f20\u53e3\u542b\u4f4f\uff0c\u5f00\u59cb\u5356\u529b\u5730\u542e\u5438\u541e\u5410\uff0c\u9999\u820c\u5728\u67f1\u8eab\u4e0a\u7075\u6d3b\u5730\u7f20\u7ed5\u52fe\u6311\uff0c\u4eff\u4f5b\u6f14\u594f\u4e50\u5668\u3002\u4e0d\u5f97\u4e0d\u8bf4\u5979\u7684\u53e3\u6280\u771f\u7684\u5f88\u9ad8\u660e\uff0c\u660e\u660e\u540c\u6837\u7684\u4e8b\u60c5\u5df2\u7ecf\u505a\u8fc7\u65e0\u6570\u6b21\uff0c\u4f46\u5979\u6bcf\u6b21\u90fd\u80fd\u53c8\u52a0\u5165\u65b0\u7684\u82b1\u6837\uff0c\u8ba9\u7537\u4eba\u83b7\u5f97\u548c\u6b64\u524d\u4e0d\u4e00\u6837\u7684\u611f\u53d7\u548c\u4f53\u9a8c\u3002\u743c\u6069\u5fc3\u7eea\u4e0d\u4f73\uff0c\u4e5f\u6b63\u60f3\u501f\u6b64\u53d1\u6cc4\uff0c\u5e76\u672a\u523b\u610f\u63a7\u5236\u5fcd\u8010\uff0c\u5341\u5206\u949f\u4e0d\u5230\uff0c\u4ed6\u4fbf\u7528\u529b\u6309\u4f4f\u9b45\u9b54\u7684\u5934\uff0c\u5728\u5979\u7684\u5589\u7ba1\u4e2d\u5267\u70c8\u5730\u55b7\u53d1\u51fa\u6765\uff0c\u6d53\u6d4a\u6eda\u70eb\u7684\u725b\u5976\u76f4\u63a5\u6ce8\u5165\u80c3\u4e2d\uff0c\u8ba9\u5979\u51e0\u4e4e\u7a92\u606f\u3002</p><p>\u201c\u771f\u591a\u3002\u201d\u838e\u73de\u514b\u8bc4\u4ef7\uff0c\u8ba9\u56e0\u4e3a\u53d1\u5c04\u800c\u53d8\u5f97\u534a\u8f6f\u7684\u5b9d\u8d1d\u5728\u81ea\u5df1\u6e29\u6696\u53e3\u8154\u4e2d\u4f11\u606f\u3002</p><p>\u75db\u75db\u5feb\u5feb\u5730\u53d1\u5c04\u4e86\u4e00\u6b21\uff0c\u6ee1\u8154\u7684\u70e6\u95f7\u4f3c\u4e4e\u4e5f\u968f\u4e4b\u6cc4\u53bb\u5927\u534a\uff0c\u743c\u6069\u7684\u5fc3\u60c5\u987f\u65f6\u53d8\u5f97\u597d\u4e86\u5f88\u591a\u3002\u201c\u7ee7\u7eed\u5427\uff0c\u201d\u4ed6\u547d\u4ee4\uff0c\u201c\u8fd9\u6b21\u6162\u70b9\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u838e\u73de\u514b\u653e\u7f13\u8282\u594f\uff0c\u8ba9\u743c\u6069\u5904\u4e8e\u4e00\u79cd\u8212\u723d\u4f46\u5e76\u4e0d\u5341\u5206\u6fc0\u70c8\u7684\u7f8e\u5999\u5feb\u611f\u4e2d\uff0c\u4ed6\u6ee1\u8db3\u5730\u53f9\u4e86\u53e3\u6c14\uff0c\u6536\u8d77\u8428\u74e6\u68cb\uff0c\u53d6\u51fa\u9b54\u6cd5\u4e66\uff0c\u4e00\u8fb9\u4eab\u53d7\u7740\u9b45\u9b54\u7684\u5507\u820c\u4f8d\u5949\u2014\u2014\u6216\u8005\u66f4\u51a0\u5195\u5802\u7687\u7684\u8bf4\u6cd5\uff0c\u662f\u201c\u4e13\u6ce8\u8bad\u7ec3\u201d\uff0c\u4e00\u8fb9\u5f00\u59cb\u4eca\u5929\u7684\u9b54\u6cd5\u5b66\u4e60\u3002</p><p>\u6765\u5854\u745f\u8c37\u7684\u8def\u4e0a\uff0c\u4ed6\u66fe\u7ecf\u5236\u5b9a\u4e86\u4e00\u5957\u8be6\u7ec6\u7684\u5b66\u4e60\u8ba1\u5212\uff0c\u76ee\u7684\u662f\u7a33\u624e\u7a33\u6253\u5730\u63d0\u5347\u81ea\u5df1\u7684\u5b9e\u529b\u3002\u4e0d\u8fc7\u8fd9\u51e0\u65e5\u4f3c\u4e4e\u90fd\u5fd9\u4e8e\u966a\u5973\u53cb\uff0c\u6c89\u9189\u5728\u6e29\u67d4\u4e61\u91cc\uff0c\u8bf4\u8d77\u6765\u5b9e\u5728\u60ed\u6127\uff0c\u73b0\u5728\u6b63\u597d\u6709\u7a7a\uff0c\u8d76\u5feb\u6293\u7d27\u65f6\u95f4\u5f25\u8865\u3002\u6309\u7167\u8bfe\u7a0b\u5b89\u6392\uff0c\u743c\u6069\u4eca\u5929\u8981\u5b66\u4e60\u5e76\u91cd\u6e29\u5de9\u56fa\u7684\u662f\u4e09\u4e2a\u6cd5\u672f\uff0c\u5206\u522b\u4e3a\u201c\u5c3c\u67cf\u4e4b\u97ad\u7b1e\u201d\u3001\u201c\u4e03\u5f69\u55b7\u5c04\u201d\u548c\u201c\u6b7b\u4e91\u672f\u201d\u3002</p><p>\u5deb\u5e08\u7684\u9b54\u6cd5\u529b\u91cf\uff0c\u4e3b\u8981\u4f53\u73b0\u5728\u4e24\u4e2a\u65b9\u9762\uff0c\u5176\u4e00\u662f\u9020\u8be3\u7684\u6df1\u6d45\uff0c\u6700\u76f4\u63a5\u7684\u8868\u5f81\u5c31\u662f\u201c\u80fd\u591f\u89e6\u6478\u9b54\u7f51\u7b2c\u51e0\u5c42\u201d\uff0c\u5176\u4e8c\u5219\u662f\u638c\u63e1\u6cd5\u672f\u6570\u91cf\u7684\u591a\u5c11\u3002\u5982\u679c\u6bd4\u55bb\u7684\u8bdd\uff0c\u5c31\u50cf\u6b66\u4fa0\u5c0f\u8bf4\u4e2d\u7684\u5185\u529b\u548c\u62db\u5f0f\uff0c\u662f\u9ad8\u624b\u5fc5\u987b\u517c\u5907\u7684\u7d20\u8d28\uff0c\u7f3a\u4e00\u4e0d\u53ef\u3002\u6ca1\u6709\u8db3\u591f\u7684\u9020\u8be3\uff0c\u5c31\u7b97\u628a\u4f20\u8bf4\u4e2d\u7684\u300a\u4e07\u6cd5\u5168\u4e66\u300b\u5012\u80cc\u5982\u6d41\uff0c\u7167\u6837\u65e0\u6cd5\u65bd\u5c55\u51fa\u6cd5\u672f\uff0c\u514b\u654c\u5236\u80dc\uff1b\u76f8\u53cd\uff0c\u5982\u679c\u4e0d\u638c\u63e1\u8db3\u591f\u591a\u7684\u6cd5\u672f\uff0c\u5373\u4fbf\u9020\u8be3\u9ad8\u6df1\u582a\u6bd4\u5927\u5965\u672f\u5e08\uff0c\u4e5f\u662f\u6709\u529b\u65e0\u5904\u4f7f\u3002\u743c\u6069\u5728\u77ed\u77ed\u6570\u5e74\u4e4b\u5185\u8dc3\u5347\u5230\u5982\u4eca\u7684\u5730\u6b65\uff0c\u5df2\u7ecf\u80fd\u591f\u521d\u6b65\u89e6\u6478\u9b54\u7f51\u7684\u7b2c\u4e03\u5c42\uff0c\u9020\u8be3\u4e0d\u53ef\u8c13\u4e0d\u5f3a\uff0c\u4f46\u5374\u5f88\u5927\u7a0b\u5ea6\u4e0a\u662f\u88ab\u201c\u50ac\u5316\u201d\u7684\u3002\u8fd9\u5bfc\u81f4\u7684\u7ed3\u679c\uff0c\u662f\u4ed6\u7684\u6cd5\u672f\u638c\u63e1\u7a0b\u5ea6\u4e25\u91cd\u4e0d\u8db3\uff0c\u5bf9\u654c\u65f6\u53ef\u4f9b\u9009\u62e9\u7684\u4f59\u5730\u592a\u5c0f\uff0c\u4f5c\u6218\u65b9\u5f0f\u4e5f\u76f8\u5bf9\u5355\u4e00\uff0c\u5f88\u5bb9\u6613\u88ab\u654c\u4eba\u6293\u4f4f\u7834\u7efd\u3002</p><p>\u4f5c\u4e3a\u4e00\u540d\u5deb\u5e08\uff0c\u5982\u679c\u4e0d\u80fd\u8fd0\u7528\u51fa\u5343\u53d8\u4e07\u5316\u7684\u6cd5\u672f\u7ec4\u5408\uff0c\u642d\u914d\u51fa\u5404\u79cd\u51fa\u4eba\u610f\u6599\u7684\u5236\u80dc\u65b9\u6cd5\uff0c\u90a3\u5c31\u672a\u514d\u592a\u4e0d\u79f0\u804c\u4e86\u2014\u2014\u751a\u81f3\uff0c\u79bb\u6b7b\u671f\u4e5f\u4e0d\u8fdc\u4e86\u3002</p><p>\u743c\u6069\u5148\u662f\u91cd\u6e29\u4e86\u201c\u5c3c\u67cf\u4e4b\u97ad\u7b1e\u201d\uff0c\u8fd9\u9053\u6cd5\u672f\u662f\u8457\u540d\u7684\u7ea2\u888d\u5deb\u5e08\u3001\u66fe\u7ecf\u4f4d\u5c45\u9644\u9b54\u5b66\u6d3e\u9996\u5e2d\u7684\u5c3c\u67cf\u6240\u521b\uff0c\u5206\u201c\u67d4\u548c\u63d0\u9192\u3001\u6e29\u7f13\u8b66\u544a\u3001\u4e25\u5389\u65a5\u8d23\u3001\u6124\u6012\u60e9\u6212\u201d\u56db\u4e2a\u9636\u6bb5\uff0c\u8f7b\u53ef\u9020\u6210\u4e9b\u5fae\u75db\u695a\u9ebb\u75f9\uff0c\u91cd\u53ef\u76f4\u63a5\u81f4\u4eba\u6b7b\u547d\uff0c\u4e0d\u8fc7\u4ee5\u743c\u6069\u76ee\u524d\u7684\u9020\u8be3\uff0c\u6700\u96be\u7684\u201c\u6124\u6012\u60e9\u6212\u201d\u6682\u65f6\u8fd8\u65e0\u6cd5\u638c\u63e1\uff0c\u53ea\u80fd\u5b66\u524d\u4e09\u4e2a\u9636\u6bb5\u3002\u5f53\u65f6\u4ed6\u4ece\u8fdc\u5c71\u57ce\u5230\u6851\u6bd4\u4e9a\u7684\u9014\u4e2d\uff0c\u906d\u9047\u90a3\u4f4d\u4f2a\u88c5\u6210\u585e\u5c14\u5974\u96b6\u8d29\u5b50\u5f17\u96f7\u65af\u7684\u82db\u5f8b\u4f8d\u8005\uff08\u6069\u745f\u4e3b\u795e\u5409\u52d2\u4eca\u7684\u796d\u53f8\uff09\uff0c\u4ece\u4ed6\u8eab\u4e0a\u641c\u51fa\u4e00\u672c\u9b54\u6cd5\u7b14\u8bb0\uff0c\u5176\u4e2d\u4fbf\u8bb0\u8f7d\u4e86\u8fd9\u4e2a\u6cd5\u672f\u3002\u743c\u6069\u5728\u8def\u4e0a\u5df2\u7ecf\u9010\u5b57\u9010\u53e5\u5730\u7814\u8bfb\uff0c\u5492\u6587\u5012\u80cc\u5982\u6d41\uff0c\u6240\u6709\u5173\u8282\u7a8d\u8981\u4e4b\u5904\u90fd\u70c2\u719f\u4e8e\u5fc3\uff0c\u5982\u4eca\u518d\u91cd\u6e29\u4e00\u904d\uff0c\u95ed\u76ee\u51a5\u601d\u7247\u523b\uff0c\u786e\u4fe1\u5df2\u7ecf\u5b8c\u5168\u638c\u63e1\u4e86\u3002</p><p>\u5148\u8bd5\u8bd5\u6548\u679c\u5427\u3002</p><p>\u201c\u838e\u73de\u514b\uff0c\u5f85\u4f1a\u53ef\u80fd\u4f1a\u6709\u70b9\u75bc\uff0c\u201d\u743c\u6069\u629a\u6478\u7740\u8dea\u5728\u817f\u95f4\u7684\u9b45\u9b54\u79c0\u53d1\uff0c\u8f7b\u58f0\u8bf4\uff0c\u201c\u4e0d\u8fc7\u522b\u62c5\u5fc3\uff0c\u4e0d\u4f1a\u6709\u5b9e\u9645\u4f24\u5bb3\u7684\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u9ed8\u8bf5\u5492\u8bed\uff0c\u9b54\u6cd5\u80fd\u91cf\u98de\u901f\u6c47\u96c6\uff0c\u5728\u6307\u5c16\u51dd\u805a\u6210\u4e00\u6761\u957f\u957f\u7684\u6df1\u7ea2\u8272\u900f\u660e\u7ec6\u97ad\uff0c\u7136\u540e\u556a\u5730\u4e00\u58f0\u8106\u54cd\uff0c\u62bd\u5728\u838e\u73de\u514b\u534a\u88f8\u7684\u7c89\u80cc\u4e0a\u3002\u957f\u97ad\u5728\u63a5\u89e6\u5230\u76ae\u80a4\u7684\u4e00\u77ac\u95f4\u5316\u4f5c\u65e0\u6570\u706b\u7c89\u56db\u6563\uff0c\u9b45\u9b54\u5c11\u5973\u5168\u8eab\u731b\u5730\u4e00\u98a4\uff0c\u8d1d\u9f7f\u54ac\u7d27\u5634\u5507\uff0c\u4eff\u4f5b\u906d\u53d7\u5267\u75db\uff0c\u4f46\u80cc\u4e0a\u5374\u770b\u4e0d\u51fa\u534a\u70b9\u97ad\u75d5\uff0c\u8fde\u9053\u7ea2\u5370\u90fd\u672a\u7559\u4e0b\u3002</p><p>\u201c\u5f88\u75bc\uff1f\u201d\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u8d76\u5fd9\u95ee\uff0c\u4ed6\u4f7f\u7528\u7684\u662f\u6700\u4f4e\u7ea7\u7684\u201c\u67d4\u548c\u63d0\u9192\u201d\uff0c\u636e\u6cd5\u672f\u8bf4\u660e\u4e0a\u8bb2\uff0c\u6548\u679c\u53ea\u662f\u201c\u7ed9\u666e\u901a\u4eba\u9020\u6210\u975e\u5e38\u8f7b\u5fae\u7684\u77ed\u6682\u75bc\u75db\u611f\u201d\uff0c\u6ca1\u60f3\u5230\u838e\u73de\u514b\u53cd\u5e94\u4f1a\u8fd9\u4e48\u5927\u3002</p><p>\u201c\u8fd8\u597d\uff0c\u201d\u838e\u73de\u514b\u6447\u6447\u5934\uff0c\u795e\u8272\u6062\u590d\u5982\u5e38\uff0c\u201c\u653e\u5fc3\u597d\u4e86\uff0c\u8fd9\u70b9\u7a0b\u5ea6\u5bf9\u6211\u800c\u8a00\u8fd8\u4e0d\u7b97\u4ec0\u4e48\u3002\u201d</p><p>\u5979\u662f\u94c1\u738b\u5ea7\u4e13\u4e1a\u8bad\u7ec3\u51fa\u7684\u6740\u624b\uff0c\u5fcd\u53d7\u75bc\u75db\u7684\u80fd\u529b\u81ea\u7136\u8fdc\u8d85\u5e38\u4eba\uff0c\u4f46\u743c\u6069\u4e5f\u4e0d\u6562\u518d\u8bd5\u3002\u67d4\u58f0\u5b89\u6170\u51e0\u53e5\uff0c\u5c06\u8fd9\u9053\u201c\u5c3c\u67cf\u4e4b\u60e9\u6212\u201d\u653e\u5230\u4e00\u8fb9\uff0c\u63a5\u7740\u5f00\u59cb\u5b66\u4e60\u4e03\u5f69\u55b7\u5c04\u548c\u6b7b\u4e91\u672f\u3002</p><p>\u8fd9\u4e24\u9053\u6cd5\u672f\u5c5e\u4e8e\u4e2d\u4f4e\u9636\uff0c\u96be\u5ea6\u90fd\u4e0d\u7b97\u9ad8\uff0c\u6362\u4f5c\u5176\u4ed6\u4e0e\u743c\u6069\u9020\u8be3\u76f8\u5f53\u751a\u81f3\u7a0d\u900a\u7684\u5deb\u5e08\u90fd\u65e9\u5e94\u8be5\u5b66\u4e60\u638c\u63e1\uff0c\u743c\u6069\u73b0\u5728\u5df2\u7ecf\u5c5e\u4e8e\u5728\u8865\u4e60\u4e86\u3002\u597d\u5728\u51dd\u6210\u771f\u540d\u4e4b\u540e\uff0c\u5deb\u5e08\u5b66\u4e60\u5965\u672f\u7684\u901f\u5ea6\u4e5f\u4f1a\u5927\u5e45\u5ea6\u63d0\u5347\uff0c\u82b1\u4e86\u6574\u6574\u4e09\u4e2a\u5c0f\u65f6\uff0c\u743c\u6069\u5c06\u8fd9\u4e24\u9053\u6cd5\u672f\u7684\u5492\u6587\u9010\u5b57\u9010\u53e5\u7814\u8bfb\u5b8c\uff0c\u5168\u76d8\u80cc\u719f\uff0c\u53c8\u8c03\u6574\u4e86\u51e0\u904d\u53d1\u8a00\uff0c\u81ea\u89c9\u7b97\u662f\u521d\u6b65\u638c\u63e1\u3002</p><p>\u201c\u5dee\u4e0d\u591a\u4e86\uff0c\u4e0b\u5348\u7684\u8bfe\u7a0b\u5c31\u5230\u8fd9\u91cc\u5427\uff0c\u665a\u4e0a\u518d\u7ee7\u7eed\u3002\u201d</p><p>\u65e2\u7136\u5b66\u4e60\u7ed3\u675f\uff0c\u53e6\u5916\u4e00\u4ef6\u4e8b\u60c5\u4e5f\u8be5\u505a\u5b8c\u4e86\u3002\u743c\u6069\u8f7b\u8f7b\u6478\u4e86\u6478\u838e\u73de\u514b\u7684\u957f\u53d1\uff0c\u793a\u610f\u5979\u628a\u81ea\u5df1\u201c\u5438\u201d\u51fa\u6765\u3002\u9b45\u9b54\u6536\u5230\u6307\u793a\uff0c\u539f\u672c\u67d4\u7f13\u7684\u52a8\u4f5c\u9661\u7136\u53d8\u5f97\u6fc0\u70c8\u8d77\u6765\uff0c\u5934\u524d\u540e\u79fb\u52a8\u7740\uff0c\u541e\u5410\u901f\u5ea6\u77ac\u95f4\u52a0\u5feb\u6570\u500d\uff0c\u53e3\u8154\u4e2d\u7684\u5438\u529b\u4e5f\u660e\u663e\u53d8\u5f3a\u4e86\u51e0\u7ea7\uff0c\u800c\u4e14\u6b21\u6b21\u90fd\u8ba9\u743c\u6069\u7684\u6775\u5c16\u6324\u5165\u5589\u7ba1\u3002\u4e00\u53cc\u7389\u624b\u4e5f\u6ca1\u6709\u95f2\u7740\uff0c\u5728\u743c\u6069\u817f\u95f4\u7075\u5de7\u5730\u6311\u9017\u63c9\u634f\u3002\u5728\u8fd9\u6837\u5f3a\u5927\u7684\u706b\u529b\u653b\u51fb\u4e4b\u4e0b\uff0c\u5c3d\u7ba1\u743c\u6069\u6b64\u524d\u5df2\u7ecf\u53d1\u5c04\u8fc7\u4e00\u6b21\uff0c\u4f46\u8fd8\u662f\u5f88\u5feb\u5c31\u53c8\u5230\u4e86\u5373\u5c06\u5d29\u6e83\u7684\u8fb9\u7f18\u3002</p><p>\u201c\u4e0d\u3001\u4e0d\u884c\u4e86\u2026\u2026\u201d</p><p>\u743c\u6069\u5598\u606f\u7740\uff0c\u770b\u7740\u9b45\u9b54\u5a07\u5a9a\u8bf1\u4eba\u7684\u4fcf\u8138\uff0c\u4ee5\u53ca\u81ea\u5df1\u7684\u7d2b\u8272\u5e9e\u5927\u5de8\u9f99\u5728\u7ea2\u8273\u8273\u7684\u6a31\u5507\u4e2d\u62bd\u63d2\u9690\u73b0\uff0c\u4e0d\u7531\u5f97\u5174\u8d77\u4e00\u4e2a\u6709\u4e9b\u90aa\u6076\u7684\u5ff5\u5934\u3002\u4ed6\u4f38\u624b\u6309\u4f4f\u9b45\u9b54\u7684\u5934\uff0c\u8ba9\u5979\u505c\u6b62\u52a8\u4f5c\uff0c\u7136\u540e\u81ea\u5df1\u9000\u4e86\u51fa\u6765\u3002\u838e\u73de\u514b\u6b63\u81ea\u83ab\u540d\u5176\u5999\uff0c\u5c31\u89c9\u773c\u524d\u767d\u82b1\u82b1\u4e00\u7247\uff0c\u5979\u4e0b\u610f\u8bc6\u5730\u95ed\u773c\uff0c\u53ea\u89c9\u4e00\u5927\u6ce2\u6eda\u70eb\u707c\u70ed\u7684\u6db2\u4f53\u731b\u70c8\u55b7\u53d1\u51fa\u6765\uff0c\u6d82\u5f97\u5979\u6ee1\u8138\u90fd\u662f\u3002</p><p>\u201c\u539f\u6765\u4f60\u559c\u6b22\u73a9\u8fd9\u79cd\u82b1\u6837\u554a\uff0c\u4e3b\u4eba\uff0c\u201d\u867d\u7136\u4e8b\u51fa\u7a81\u7136\uff0c\u9b45\u9b54\u5374\u5168\u7136\u6ca1\u6709\u4e0d\u60a6\uff0c\u5979\u5a07\u7b11\u7740\uff0c\u7528\u624b\u6307\u5c06\u8138\u4e0a\u7684\u725b\u5976\u522e\u4e0b\u6765\uff0c\u4f38\u5165\u53e3\u4e2d\u541e\u54bd\uff0c\u4e00\u8fb9\u8fd8\u4e0d\u5fd8\u5a9a\u773c\u5982\u4e1d\u5730\u770b\u7740\u743c\u6069\u3002\u201c\u65e9\u8ddf\u6211\u8bf4\u561b\uff0c\u7a81\u7136\u6765\u8fd9\u4e48\u4e00\u4e0b\uff0c\u5012\u5413\u4e86\u6211\u4e00\u8df3\u3002\u201d</p><p>\u743c\u6069\u7684\u5168\u8eab\u529b\u6c14\u4eff\u4f5b\u90fd\u88ab\u62bd\u7a7a\uff0c\u762b\u8f6f\u5728\u6905\u4e2d\uff0c\u6b63\u8981\u56de\u7b54\uff0c\u7a81\u7136\u95f4\u542c\u5f97\u623f\u95e8\u5494\u5730\u4e00\u58f0\u8f7b\u54cd\uff0c\u7d27\u63a5\u7740\u88ab\u63a8\u5f00\uff0c\u91d1\u53d1\u78a7\u773c\u7684\u7f8e\u4e3d\u5c11\u5973\u8d70\u4e86\u8fdb\u6765\u3002</p></article>"}}}]);
