(window.webpackJsonp=window.webpackJsonp||[]).push([[1733],{1756:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u4e03\u8282 \u7ea6\u6cd5\u4e09\u7ae0",content:"<article><h2>\u7b2c\u4e94\u5341\u4e03\u8282 \u7ea6\u6cd5\u4e09\u7ae0</h2><p>\u6885\u83f2\u65af\u4ece\u5916\u5f52\u6765\uff0c\u660e\u663e\u6709\u4e9b\u5fc3\u4e0d\u5728\u7109\uff0c\u538b\u6839\u6ca1\u6ce8\u610f\u5230\u623f\u95f4\u91cc\u7684\u52a8\u9759\uff0c\u76f4\u5230\u63a8\u95e8\u8d70\u8fdb\u6765\u624d\u53d1\u73b0\u8fd8\u6709\u522b\u4eba\u5728\uff0c\u800c\u4e14\u8fd8\u662f\u8fd9\u6837\u4e00\u5e45\u90aa\u6076\u573a\u666f\uff0c\u767b\u65f6\u6014\u4f4f\u3002\u743c\u6069\u4e5f\u6ca1\u60f3\u5230\u6885\u83f2\u65af\u4f1a\u7a81\u7136\u56de\u6765\uff0c\u81ea\u5df1\u4e0b\u534a\u8eab\u8d64\u88f8\u7740\u5750\u5728\u6905\u4e2d\uff0c\u521a\u521a\u55b7\u53d1\u51fa\u7684\u201c\u7f6a\u8bc1\u201d\u8fd8\u6b8b\u7559\u5728\u838e\u73de\u514b\u8138\u4e0a\uff0c\u7a7a\u6c14\u4e2d\u6f02\u6d6e\u7740\u5947\u602a\u7684\u6deb\u9761\u5473\u9053\uff0c\u60f3\u63a9\u9970\u4e5f\u4e0d\u53ef\u80fd\u3002\u4e24\u4eba\u56db\u76ee\u76f8\u5bf9\uff0c\u623f\u95f4\u91cc\u7684\u6c14\u6c1b\u767b\u65f6\u5c34\u5c2c\u65e0\u6bd4\uff0c\u53cd\u5012\u662f\u838e\u73de\u514b\u6700\u662f\u9547\u5b9a\uff0c\u82e5\u65e0\u5176\u4e8b\u800c\u4e14\u6162\u6761\u65af\u7406\u5730\u7528\u624b\u6307\u5c06\u8138\u4e0a\u7684\u5976\u6db2\u522e\u5f97\u5e72\u5e72\u51c0\u51c0\uff0c\u9001\u5165\u53e3\u4e2d\u8214\u8210\u541e\u54bd\uff0c\u7136\u540e\u4ece\u4e66\u684c\u4e0b\u94bb\u51fa\uff0c\u7ad9\u8d77\u8eab\u6765\u3002</p><p>\u201c\u4e0b\u5348\u597d\uff0c\u827e\u5f25\u8587\uff0c\u201d\u5979\u751c\u751c\u5730\u7b11\u7740\uff0c\u5148\u548c\u91d1\u53d1\u5c11\u5973\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u7136\u540e\u8f6c\u5411\u743c\u6069\uff0c\u201c\u6211\u5148\u544a\u9000\u4e86\uff0c\u4e3b\u4eba\uff0c\u4eca\u665a\u8fd8\u662f\u6309\u539f\u8ba1\u5212\u8981\u6211\u6765\u4f8d\u5bdd\u5417\uff1f\u201d</p><p>\u201c\u2026\u2026\u4ec0\u4e48\u65f6\u5019\u6709\u4e86\u8fd9\u4e2a\u8ba1\u5212\uff1f\u201d</p><p>\u660e\u77e5\u9053\u662f\u838e\u73de\u514b\u5728\u7ed9\u81ea\u5df1\u4e0b\u5957\uff0c\u4f46\u4e00\u65f6\u95f4\u4e5f\u65e0\u6cd5\u53bb\u5206\u8fa9\uff0c\u603b\u4e0d\u80fd\u5f53\u7740\u6885\u83f2\u65af\u7684\u9762\u4e3a\u8fd9\u79cd\u4e8b\u60c5\u548c\u5979\u4e89\u6267\uff0c\u90a3\u6837\u53ea\u4f1a\u8d8a\u8bf4\u8d8a\u4e71\uff0c\u8d8a\u5f04\u8d8a\u7cdf\u3002\u743c\u6069\u5f53\u673a\u7acb\u65ad\uff0c\u4e8c\u8bdd\u4e0d\u8bf4\u628a\u838e\u73de\u514b\u76f4\u63a5\u8d76\u51fa\u623f\u95f4\uff0c\u5c06\u5c40\u9762\u7b80\u5316\uff0c\u7136\u540e\u518d\u6765\u548c\u6885\u83f2\u65af\u89e3\u91ca\u3002</p><p>\u7136\u540e\u4ed6\u53d1\u73b0\u5176\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\u597d\u89e3\u91ca\u7684\u2026\u2026</p><p>\u201c\u4e09\u4ef6\u4e8b\u3002\u201d\u6885\u83f2\u65af\u5e73\u9759\u5730\u8bf4\uff0c\u770b\u7740\u4ed6\u7684\u773c\u775b\u3002</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u7b2c\u4e00\uff0c\u4ee5\u540e\u4f60\u641e\u522b\u7684\u5973\u4eba\uff0c\u4e0d\u8981\u5728\u6211\u7684\u623f\u95f4\u91cc\uff0c\u6211\u4e0d\u559c\u6b22\uff1b\u7b2c\u4e8c\uff0c\u4ee5\u540e\u505a\u8fd9\u79cd\u4e8b\u60c5\u4e4b\u524d\uff0c\u8bf7\u5148\u9501\u95e8\uff0c\u514d\u5f97\u518d\u88ab\u6211\u649e\u89c1\uff1b\u7b2c\u4e09\uff0c\u201d\u5c11\u5973\u987f\u4e86\u987f\uff0c\u201c\u4f60\u4e0d\u51c6\u5bf9\u6211\u505a\u540c\u6837\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u201c\u2026\u2026\u524d\u4e24\u70b9\u6ca1\u95ee\u9898\uff0c\u7b2c\u4e09\u70b9\u6709\u6ca1\u6709\u901a\u878d\u7684\u4f59\u5730\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff01\u201d</p><p>\u5176\u5b9e\u771f\u8bf4\u8d77\u6765\uff0c\u743c\u6069\u5bf9\u6b64\u5e76\u65e0\u7279\u522b\u504f\u597d\uff0c\u521a\u624d\u4e5f\u53ea\u662f\u5076\u7136\u5174\u8d77\uff0c\u8bb0\u8d77\u4ee5\u524d\u770b\u8fc7\u7684\u65e5\u672c\u548c\u8c10\u7247\u573a\u666f\uff0c\u4e00\u65f6\u5fc3\u8840\u6765\u6f6e\u800c\u5df2\uff0c\u4ece\u6765\u6ca1\u60f3\u8fc7\u8981\u5bf9\u6885\u83f2\u65af\u201c\u505a\u540c\u6837\u7684\u4e8b\u60c5\u201d\u3002\u7136\u800c\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\u5f80\u5f80\u5c31\u662f\u8fd9\u6837\u5947\u602a\uff0c\u6885\u83f2\u65af\u5018\u82e5\u4e0d\u8bf4\uff0c\u4f30\u8ba1\u4e5f\u5c31\u8fc7\u53bb\u4e86\uff0c\u5979\u8fd9\u6837\u90d1\u91cd\u5176\u4e8b\u5730\u7279\u522b\u58f0\u660e\uff0c\u53cd\u800c\u8ba9\u743c\u6069\u6709\u4e86\u683c\u5916\u7684\u5174\u81f4\uff0c\u5728\u5934\u8111\u91cc\u60f3\u8c61\u7740\u90a3\u526f\u573a\u666f\uff0c\u8d8a\u60f3\u5c31\u8d8a\u89c9\u5f97\u4e00\u5b9a\u8981\u627e\u4e2a\u673a\u4f1a\u8bd5\u8bd5\u770b\uff0c\u5426\u5219\u592a\u53ef\u60dc\u4e86\u3002\u4e0d\u8fc7\u4ed6\u4e5f\u6e05\u695a\uff0c\u6b64\u4e3e\u5bf9\u4e8e\u7537\u6027\u800c\u8a00\u56fa\u7136\u7f8e\u5999\uff0c\u5bf9\u4e8e\u5973\u6027\u800c\u8a00\u5374\u6709\u5f3a\u70c8\u7684\u5c48\u8fb1\u610f\u5473\uff0c\u6885\u83f2\u65af\u4f1a\u62d2\u7edd\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\u3002\u81ea\u5df1\u5982\u679c\u4e00\u5b9a\u8981\u5f3a\u786c\u575a\u6301\uff0c\u867d\u7136\u4e5f\u672a\u5fc5\u5c31\u4e0d\u4f1a\u6210\u529f\uff0c\u5374\u5fc5\u5b9a\u4f1a\u62db\u81f4\u5c11\u5973\u7684\u53cd\u611f\u4e0d\u60a6\u3002\u5728\u743c\u6069\u5fc3\u4e2d\uff0c\u838e\u73de\u514b\u53ea\u662f\u5973\u5974\uff0c\u5206\u91cf\u5f88\u8f7b\uff0c\u53ec\u4e4b\u5373\u6765\uff0c\u6325\u4e4b\u5373\u53bb\uff0c\u65e0\u9700\u592a\u987e\u5fcc\uff1b\u6885\u83f2\u65af\u5374\u662f\u771f\u6b63\u7684\u60c5\u4eba\uff0c\u540c\u65f6\u8fd8\u662f\u65e2\u656c\u53c8\u754f\u7684\u670b\u53cb\uff0c\u5982\u679c\u53ef\u80fd\u7684\u8bdd\uff0c\u4ed6\u662f\u51b3\u4e0d\u613f\u610f\u60f9\u5979\u4e0d\u9ad8\u5174\u7684\u3002</p><p>\u6240\u4ee5\u8fd9\u4e2a\u95ee\u9898\u4e48\uff0c\u5c31\u5148\u6401\u7f6e\u5427\u3002\u4ee5\u540e\u6709\u7a7a\u518d\u8bf4\uff0c\u591a\u8bb2\u51e0\u53e5\u751c\u8a00\u871c\u8bed\uff0c\u6162\u6162\u529d\u5979\u6539\u53d8\u4e3b\u610f\u5c31\u662f\u3002</p><p>\u76f8\u8bc6\u65e5\u4e45\uff0c\u5f7c\u6b64\u719f\u6089\uff0c\u6885\u83f2\u65af\u81ea\u7136\u80fd\u591f\u731c\u5230\u7537\u53cb\u5fc3\u91cc\u7684\u76d8\u7b97\uff0c\u5979\u54fc\u4e86\u58f0\uff0c\u4e5f\u6ca1\u518d\u5728\u8fd9\u4e2a\u8bdd\u9898\u4e0a\u591a\u505a\u7ea0\u7f20\u3002\u201c\u4f60\u8ba1\u5212\u5728\u8fd9\u91cc\u518d\u5f85\u51e0\u5929\uff1f\u201d\u5979\u7a81\u7136\u95ee\u3002</p><p>\u201c\u65e0\u6240\u8c13\u5427\uff0c\u5341\u5929\u534a\u6708\u90fd\u53ef\u4ee5\uff0c\u4e00\u4e24\u4e2a\u6708\u4e5f\u4e0d\u662f\u4e0d\u884c\uff0c\u53cd\u6b63\u6211\u8fd8\u6709\u5047\u671f\uff0c\u201d\u743c\u6069\u5947\u602a\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u7a81\u7136\u95ee\u8fd9\u4e2a\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u6709\u53ef\u80fd\u8fc7\u51e0\u5929\u8981\u53bb\u5916\u5730\u3002\u201d</p><p>\u743c\u6069\u4e00\u6014\uff0c\u201c\u53bb\u54ea\u91cc\uff1f\u6211\u966a\u4f60\u4e00\u8d77\u53bb\u597d\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u7b11\u8d77\u6765\uff0c\u201c\u8fd9\u6b21\u4f60\u6050\u6015\u53bb\u4e0d\u4e86\u5462\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\uff0c\u6211\u9884\u8ba1\u8981\u53bb\u7684\u5730\u65b9\u662f\u9634\u5f71\u8c37\u3002\u201d</p><p>\u2026\u2026\u9634\u5f71\u8c37\uff1f</p><p>\u743c\u6069\u6b64\u6b21\u79bb\u5f00\u9634\u9b42\u57ce\uff0c\u5411\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u8bf7\u4e86\u4e09\u4e2a\u6708\u7684\u5047\uff0c\u5982\u4eca\u624d\u7528\u6389\u5927\u534a\u4e2a\u6708\uff0c\u65f6\u95f4\u5012\u662f\u5145\u88d5\u5f97\u5f88\u3002\u6885\u83f2\u65af\u5982\u679c\u53bb\u522b\u7684\u5730\u65b9\uff0c\u4ed6\u90fd\u53ef\u4ee5\u540c\u884c\u966a\u4f34\uff0c\u4f46\u9634\u5f71\u8c37\u662f\u9b54\u6cd5\u5973\u795e\u5bc6\u65af\u62c9\u7684\u5927\u672c\u8425\u4e4b\u4e00\uff0c\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u5728\u90a3\u91cc\u4e5f\u5efa\u6709\u4e00\u5ea7\u5b8f\u4f1f\u5723\u6bbf\uff0c\u8fd9\u4e24\u4f4d\u795e\u660e\u6070\u597d\u90fd\u662f\u591c\u5973\u58eb\u838e\u5c14\u7684\u6b7b\u654c\uff0c\u6885\u83f2\u65af\u662f\u63d0\u5c14\u5723\u6b66\u58eb\uff0c\u8981\u53bb\u5c31\u53bb\uff0c\u5012\u6ca1\u4ec0\u4e48\u59a8\u788d\uff0c\u743c\u6069\u5982\u679c\u8ddf\u7740\u53bb\uff0c\u90a3\u5c31\u662f\u7eaf\u7cb9\u627e\u6253\u4e86\u3002</p><p>\u201c\u600e\u4e48\u7a81\u7136\u8981\u53bb\u9634\u5f71\u8c37\uff1f\u201d\u743c\u6069\u95ee\uff0c\u4f46\u4ed6\u5176\u5b9e\u5df2\u7ecf\u5927\u7ea6\u731c\u5230\u539f\u56e0\u3002</p><p>\u6885\u83f2\u65af\u65e9\u4e0a\u53bb\u89c1\u5927\u4e3b\u6559\uff0c\u5c06\u6628\u65e5\u5728\u86c7\u708e\u5c71\u53d1\u751f\u7684\u4e8b\u60c5\u505a\u4e86\u6c47\u62a5\u3002\u8bef\u6740\u6751\u6c11\u7684\u4e8b\u60c5\u5012\u7b80\u5355\uff0c\u5224\u5b9a\u4e3a\u201c\u610f\u5916\u201d\uff0c\u7ed9\u5bb6\u5c5e\u53d1\u4e00\u7b14\u629a\u6064\u91d1\u4fbf\u662f\u3002\u63d0\u5c14\u6559\u4f1a\u5728\u6b64\u5730\u7ecf\u8425\u6570\u767e\u5e74\uff0c\u58f0\u671b\u6781\u76db\uff0c\u8981\u6446\u5e73\u8fd9\u70b9\u5c0f\u4e8b\u4e0d\u8fc7\u662f\u4e3e\u624b\u4e4b\u52b3\u3002\u5012\u662f\u51b0\u8679\u88ab\u53d8\u6210\u9f99\u5deb\u5996\u4e00\u4e8b\uff0c\u8ba9\u6559\u4f1a\u9ad8\u5c42\u90fd\u975e\u5e38\u5934\u75bc\uff0c\u6574\u6574\u4e00\u4e0a\u5348\u90fd\u5728\u8ba8\u8bba\u8fd9\u4ef6\u4e8b\u60c5\u8be5\u5982\u4f55\u5904\u7406\u3002</p><p>\u5176\u5b9e\u771f\u8981\u8bf4\u8d77\u6765\uff0c\u51b0\u8679\u88ab\u53d8\u6210\u9f99\u5deb\u5996\uff0c\u548c\u63d0\u5c14\u6559\u4f1a\u5e76\u6ca1\u6709\u592a\u591a\u7684\u76f4\u63a5\u5229\u5bb3\u5173\u7cfb\u3002\u8428\u739b\u65af\u7279\u6b64\u6b21\u5f52\u6765\uff0c\u9996\u5148\u8981\u5bf9\u4ed8\u7684\u76ee\u6807\u6beb\u65e0\u7591\u95ee\u662f\u9b54\u6cd5\u5973\u795e\u6559\u4f1a\uff0c\u5176\u6b21\u5219\u662f\u6668\u66e6\u4e4b\u795e\u6559\u4f1a\uff0c\u4ee5\u53ca\u4ee5\u8fd9\u4e24\u5927\u6559\u4f1a\u795e\u804c\u4eba\u5458\u4e3a\u4e3b\u5e72\u7684\u7ad6\u7434\u624b\u7ec4\u7ec7\u3002\u81f3\u4e8e\u63d0\u5c14\u6559\u4f1a\uff0c\u548c\u8428\u739b\u65af\u7279\u5728\u5386\u53f2\u4e0a\u5e76\u6ca1\u6709\u591a\u5c11\u51b2\u7a81\u6069\u6028\uff0c\u548c\u9b54\u6cd5\u5973\u795e\u6559\u4f1a\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5173\u7cfb\u5bc6\u5207\u7684\u76df\u53cb\uff0c\u5854\u745f\u8c37\u548c\u9634\u5f71\u8c37\u867d\u7136\u540c\u5c5e\u8c37\u5730\uff0c\u4f46\u8ddd\u79bb\u9887\u8fdc\uff0c\u6765\u5f80\u4e5f\u5f88\u5c11\uff0c\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u4e00\u635f\u4ff1\u635f\uff0c\u5507\u4ea1\u9f7f\u5bd2\u3002\u8428\u739b\u65af\u7279\u624b\u4e0b\u591a\u4e86\u4e00\u53ea\u592a\u53e4\u9f99\u5deb\u5996\uff0c\u6700\u5934\u75bc\u7684\u5e94\u8be5\u662f\u90a3\u4e9b\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\u4eec\u3002\u4f46\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u51b0\u8679\u662f\u63d0\u5c14\u6559\u4f1a\u7684\u201c\u62a4\u6559\u5723\u517d\u201d\uff0c\u5728\u5386\u53f2\u4e0a\u66fe\u7ecf\u591a\u6b21\u4e3a\u63d0\u5c14\u6559\u4f1a\u51fa\u6218\uff0c\u62b5\u5fa1\u5916\u654c\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5168\u8c37\u5730\u7684\u4eba\u90fd\u77e5\u9053\u3002\u5982\u4eca\u51fa\u4e86\u8fd9\u4ef6\u4e8b\uff0c\u5018\u82e5\u63d0\u5c14\u6559\u4f1a\u653e\u4efb\u4e0d\u7ba1\uff0c\u7f6e\u8eab\u4e8b\u5916\uff0c\u5bf9\u5f62\u8c61\u65e0\u7591\u5927\u5927\u6709\u635f\uff0c\u8bf4\u51fa\u53bb\u540d\u58f0\u90fd\u4e0d\u597d\u542c\u3002\u5f62\u8c61\u3001\u540d\u58f0\u8fd9\u79cd\u4e1c\u897f\uff0c\u8bf4\u4e0d\u91cd\u8981\u4e5f\u91cd\u8981\uff0c\u90aa\u795e\u7684\u6559\u4f1a\u81ea\u7136\u53ef\u4ee5\u4e0d\u8981\u9762\u5b50\uff0c\u6b63\u4e49\u4e4b\u795e\u7684\u6559\u4f1a\u5374\u4e07\u4e07\u4e0d\u80fd\u3002</p><p>\u51b0\u8679\u5df2\u7ecf\u53d8\u6210\u9f99\u5deb\u5996\uff0c\u6b64\u4e8b\u65e0\u6cd5\u633d\u56de\uff0c\u4e16\u754c\u4e0a\u53ea\u6709\u751f\u8005\u53d8\u6210\u4ea1\u7075\u7684\uff0c\u4ece\u6ca1\u6709\u53d8\u6210\u4e86\u4ea1\u7075\u8fd8\u80fd\u518d\u6062\u590d\u6210\u751f\u8005\u7684\u2014\u2014\u62c9\u6c83\u514b\u60f3\u6311\u6218\u8fd9\u4e00\u5b9a\u5f8b\uff0c\u4f46\u4ed6\u7684\u201c\u751f\u6b7b\u8f6c\u5316\u201d\u5b9e\u9a8c\u4e00\u76f4\u90fd\u5931\u8d25\uff0c\u81f3\u4eca\u8fd8\u6ca1\u6210\u529f\u3002\u800c\u4ea1\u7075\u88ab\u8d1f\u80fd\u91cf\u4fb5\u67d3\uff0c\u9664\u4e86\u6781\u5c11\u6570\u7279\u4f8b\uff0c\u5815\u5165\u9ed1\u6697\u90aa\u6076\u90fd\u662f\u6ce8\u5b9a\u7684\u7ed3\u5c40\uff0c\u53ea\u662f\u65f6\u95f4\u957f\u77ed\u3001\u8fc7\u7a0b\u5feb\u6162\u7684\u95ee\u9898\u7f62\u4e86\u3002\u65e2\u7136\u5982\u6b64\uff0c\u89e3\u51b3\u65b9\u6cd5\u4e5f\u5c31\u53ea\u5269\u4e0b\u4e00\u79cd\uff1a\u6d3e\u4eba\u53bb\u628a\u51b0\u8679\u5e72\u6389\u3002</p><p>\u8fd9\u4e2a\u4efb\u52a1\u5e76\u4e0d\u5bb9\u6613\uff0c\u51b0\u8679\u4ee5\u524d\u662f\u4e2a\u5782\u5782\u5c06\u6b7b\u5944\u5944\u5f85\u6bd9\u7684\u8001\u8005\uff0c\u73b0\u5728\u5374\u662f\u53ea\u592a\u53e4\u9f99\u5deb\u5996\uff0c\u6218\u6597\u529b\u7ffb\u4e86\u5341\u500d\u3002\u8fd9\u5012\u8fd8\u7f62\u4e86\uff0c\u66f4\u91cd\u8981\u7684\u662f\u5b83\u73b0\u5728\u5df2\u7ecf\u6295\u5165\u8428\u739b\u65af\u7279\u9635\u8425\uff0c\u8981\u5e72\u6389\u51b0\u8679\uff0c\u5c31\u610f\u5473\u7740\u548c\u9f99\u5deb\u6559\u53d1\u751f\u6b63\u9762\u51b2\u7a81\u2014\u2014\u800c\u8fd9\u662f\u63d0\u5c14\u6559\u4f1a\u6240\u4e00\u76f4\u529b\u56fe\u907f\u514d\u7684\u3002</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u6709\u4e9b\u5947\u602a\uff0c\u63d0\u5c14\u6559\u4f1a\u4f5c\u4e3a\u6b63\u4e49\u7ec4\u7ec7\uff0c\u65a9\u9664\u90aa\u6076\u96be\u9053\u4e0d\u662f\u5929\u804c\u4e48\uff1f\u4e3a\u4f55\u53cd\u800c\u8981\u907f\u514d\u548c\u9f99\u5deb\u6559\u548c\u8428\u739b\u65af\u7279\u53d1\u751f\u51b2\u7a81\uff0c\u8fd9\u4e5f\u672a\u514d\u592a\u4e0d\u8c61\u8bdd\u4e86\u5427\u3002</p><p>\u6885\u83f2\u65af\u5fae\u5fae\u76b1\u7709\uff0c\u663e\u7136\u4e0d\u613f\u591a\u8bf4\u8fd9\u4e2a\u8bdd\u9898\u3002\u201c\u5927\u4e3b\u6559\u4ed6\u4eec\u53e6\u6709\u8003\u8651\uff0c\u201d\u5979\u7b80\u5355\u5730\u8bf4\uff0c\u60f3\u4e86\u60f3\u53c8\u8865\u5145\u4e86\u53e5\uff0c\u201c\u548c\u8ff7\u65af\u5353\u8bfa\u7684\u90a3\u5e2e\u7cbe\u7075\u4eec\u6709\u5173\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u5c11\u5973\u8bed\u7109\u4e0d\u8be6\uff0c\u4f30\u8ba1\u6d89\u53ca\u6559\u4f1a\u7684\u673a\u5bc6\uff0c\u743c\u6069\u4f5c\u4e3a\u5916\u4eba\u4e5f\u4e0d\u597d\u591a\u95ee\u3002\u63d0\u5c14\u6559\u4f1a\u7684\u9ad8\u5c42\u4eba\u5458\u8ba8\u8bba\u4e86\u534a\u5929\uff0c\u591a\u6570\u4eba\u8fd8\u662f\u503e\u5411\u4e8e\u4e0d\u53bb\u7406\u776c\uff0c\u5c11\u6570\u4eba\u5219\u8ba4\u4e3a\u4e0d\u80fd\u653e\u4efb\u4e0d\u7ba1\uff0c\u53cc\u65b9\u5404\u6267\u4e00\u8bcd\uff0c\u4e89\u6267\u5f97\u975e\u5e38\u6fc0\u70c8\uff0c\u773c\u770b\u4eca\u5929\u662f\u5f97\u4e0d\u51fa\u7ed3\u8bba\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u6b23\u5e03\u6765\u4e86\u3002</p><p>\u201c\u6b23\u5e03\u2026\u2026\u5bf9\u4e86\uff0c\u6211\u4e00\u76f4\u5fd8\u4e86\u95ee\uff0c\u5979\u6765\u5854\u745f\u8c37\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u505a\u4ec0\u4e48\uff1f\u201d\u6885\u83f2\u65af\u5fae\u5fae\u51b7\u7b11\uff0c\u201c\u7b97\u662f\u6c42\u63f4\u5427\u3002\u201d</p><p>\u201c\u6c42\u63f4\uff1f\u201d\u743c\u6069\u4e00\u6014\uff0c\u201c\u5f62\u52bf\u5df2\u7ecf\u8fd9\u4e48\u4e25\u5cfb\u4e86\u5417\uff1f\u201d</p><p>\u4ed6\u77e5\u9053\u6b63\u4e49\u4e4b\u795e\u63d0\u5c14\u7684\u6559\u4f1a\u548c\u9b54\u6cd5\u5973\u795e\u5bc6\u65af\u62c9\u7684\u6559\u4f1a\u5411\u6765\u5173\u7cfb\u5e73\u5e73\uff0c\u53cc\u65b9\u6559\u4e49\u3001\u7406\u5ff5\u4e0a\u7684\u5206\u6b67\u9887\u5927\uff0c\u867d\u7136\u4e0d\u662f\u654c\u5bf9\uff0c\u5374\u4e5f\u7edd\u5bf9\u8c08\u4e0d\u4e0a\u591a\u4e48\u4eb2\u8fd1\u3002\u5f53\u5e74\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\u4eec\u547c\u5401\u5021\u5bfc\u6210\u7acb\u201c\u7ad6\u7434\u624b\u540c\u76df\u201d\uff0c\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u3001\u6708\u5973\u795e\u82cf\u4f26\u3001\u5e78\u8fd0\u5973\u795e\u6cf0\u6469\u62c9\u7b49\u5404\u5927\u5584\u795e\u6559\u4f1a\u7eb7\u7eb7\u8868\u793a\u652f\u6301\uff0c\u5e76\u6d3e\u9063\u7cbe\u9510\u4eba\u5458\u52a0\u5165\uff0c\u552f\u72ec\u4ee5\u63d0\u5c14\u4e3a\u9996\u7684\u201c\u4e09\u5723\u201d\u4e88\u4ee5\u62d2\u7edd\uff0c\u5e76\u660e\u786e\u8868\u793a\u4e0d\u8d5e\u6210\u7684\u6001\u5ea6\u3002\u6240\u4ee5\u8fd9\u4e24\u5927\u52bf\u529b\u867d\u7136\u90fd\u5728\u8c37\u5730\u5efa\u6709\u603b\u90e8\uff0c\u5374\u51e0\u767e\u5e74\u6765\u90fd\u57fa\u672c\u6ca1\u6709\u4ec0\u4e48\u5b98\u65b9\u6765\u5f80\u3002\u5982\u679c\u4e0d\u662f\u5f62\u52bf\u975e\u5e38\u4e25\u5cfb\u7684\u8bdd\uff0c\u5f88\u96be\u60f3\u8c61\u6b23\u5e03\u4f1a\u6765\u5854\u745f\u8c37\u5411\u63d0\u5c14\u6559\u4f1a\u6c42\u63f4\u3002</p><p>\u6885\u83f2\u65af\u6ca1\u6709\u56de\u7b54\uff0c\u800c\u662f\u9012\u7ed9\u743c\u6069\u4e24\u9875\u7eb8\uff0c\u743c\u6069\u63a5\u8fc7\u4e00\u770b\uff0c\u53d1\u73b0\u7b2c\u4e00\u9875\u662f\u4efd\u6218\u62a5\u3002\u539f\u6765\u5c31\u5728\u524d\u5929\u4e0a\u5348\uff0c\u4e00\u76f4\u76d8\u8e1e\u5728\u63d0\u51e1\u987f\u57ce\u7684\u9f99\u5deb\u6559\u5f92\u7a81\u7136\u503e\u5de2\u800c\u51fa\uff0c\u5411\u96f7\u9e23\u5173\u53d1\u52a8\u653b\u51fb\u3002\u96f7\u9e23\u5173\u662f\u63d0\u51e1\u987f\u5230\u9634\u5f71\u8c37\u4e4b\u95f4\u7684\u552f\u4e00\u5173\u9698\uff0c\u5730\u7406\u4f4d\u7f6e\u975e\u5e38\u91cd\u8981\uff0c\u65e9\u5728\u8428\u739b\u65af\u7279\u521a\u521a\u653b\u9677\u63d0\u51e1\u987f\u65f6\uff0c\u7ad6\u7434\u624b\u540c\u76df\u7684\u4e09\u5927\u9886\u8896\u4e4b\u4e00\u585e\u83b1\u8389\u5a05\u5c31\u7387\u9886\u90e8\u5c5e\u6765\u5230\u6b64\u5730\u9a7b\u5b88\u3002\u53cc\u65b9\u6fc0\u6218\u4e24\u5c0f\u65f6\uff0c\u8d5b\u83b1\u8389\u5a05\u6218\u6b7b\uff0c\u624b\u4e0b\u7684\u7ad6\u7434\u624b\u5168\u519b\u8986\u6ca1\u3002\u9f99\u5deb\u6559\u5f92\u653b\u4e0b\u96f7\u9e23\u5173\u540e\uff0c\u9634\u5f71\u8c37\u6d3e\u6765\u7684\u63f4\u519b\u65b9\u81f3\uff0c\u53cc\u65b9\u7206\u53d1\u4e86\u7b2c\u4e8c\u6b21\u6218\u6597\u3002\u9f99\u5deb\u6559\u636e\u5173\u575a\u5b88\uff0c\u9634\u5f71\u8c37\u65b9\u8fdb\u653b\u4e0d\u5229\uff0c\u635f\u5931\u4e86\u4e03\u767e\u591a\u540d\u58eb\u5175\u548c\u516d\u540d\u9ad8\u9636\u5deb\u5e08\u3002\u6218\u6597\u5931\u5229\u540e\uff0c\u9634\u5f71\u8c37\u65b9\u9762\u9000\u519b\uff0c\u5f53\u665a\u7531\u6df1\u6c34\u57ce\u4e3b\u3001\u8457\u540d\u7684\u5927\u5deb\u5e08\u51ef\u5c14\u672c\u592b\u59bb\u7387\u9886\u591a\u540d\u9ad8\u9636\u5deb\u5e08\u5bf9\u96f7\u9e23\u5173\u53d1\u52a8\u7a81\u88ad\u53cd\u51fb\uff0c\u4ee5\u5f88\u5c0f\u7684\u4ee3\u4ef7\u51fb\u6740\u4e00\u540d\u82cd\u767d\u9886\u4e3b\u548c\u4e00\u5934\u4e0a\u53e4\u9f99\u5deb\u5996\uff0c\u7b97\u662f\u633d\u56de\u4e86\u4e00\u4e9b\u5c40\u9762\u3002\u4f46\u82cd\u767d\u9886\u4e3b\u5012\u7f62\u4e86\uff0c\u9f99\u5deb\u5996\u548c\u4eba\u7c7b\u5deb\u5996\u4e00\u6837\uff0c\u547d\u5323\u4e0d\u788e\uff0c\u5373\u80fd\u590d\u6d3b\uff0c\u53ea\u80fd\u7b97\u662f\u201c\u6682\u65f6\u201d\u88ab\u6467\u6bc1\uff0c\u5e76\u975e\u771f\u6b63\u88ab\u5e72\u6389\u3002\u603b\u4f53\u800c\u8a00\uff0c\u8fd8\u662f\u9634\u5f71\u8c37\u65b9\u53d7\u635f\u66f4\u5927\u3002</p><p>\u201c\u5947\u602a\u3002\u201d</p><p>\u743c\u6069\u4ed4\u7ec6\u5c06\u6218\u62a5\u770b\u4e86\u51e0\u904d\uff0c\u6c89\u541f\u4e0d\u8bed\u3002\u8fd9\u4efd\u6218\u62a5\u9063\u8bcd\u9020\u53e5\u5341\u5206\u7684\u7b80\u7565\u800c\u7cbe\u51c6\uff0c\u51e0\u4e4e\u6ca1\u6709\u534a\u70b9\u591a\u4f59\u7684\u5e9f\u8bdd\uff0c\u901a\u7bc7\u4e0d\u8fc7\u5343\u5b57\uff0c\u5374\u5c06\u4e09\u573a\u6218\u6597\u7684\u5168\u8fc7\u7a0b\u8bb0\u8ff0\u5f97\u6e05\u6e05\u695a\u695a\u3002\u5982\u679c\u4ed4\u7ec6\u7814\u8bfb\u7684\u8bdd\uff0c\u4ece\u90a3\u4e9b\u770b\u4f3c\u4e00\u7b14\u5e26\u8fc7\u7684\u7ec6\u8282\u4e2d\uff0c\u4fbf\u80fd\u53d1\u73b0\u5f88\u591a\u9690\u85cf\u5728\u80cc\u540e\u7684\u4fe1\u606f\u3002\u6bd4\u5982\u8bf4\uff0c\u9634\u5f71\u8c37\u65b9\u9762\u7684\u6307\u6325\u7cfb\u7edf\u663e\u7136\u51fa\u4e86\u67d0\u79cd\u95ee\u9898\uff0c\u8d5b\u83b1\u8389\u5a05\u5f88\u65e9\u5c31\u8fdb\u9a7b\u96f7\u9e23\u5173\uff0c\u603b\u90e8\u5374\u8fdf\u8fdf\u6ca1\u6709\u589e\u52a0\u9632\u5b88\u5175\u529b\uff0c\u4e34\u6218\u65f6\u624d\u6d3e\u9063\u63f4\u519b\uff0c\u5374\u53c8\u8fdf\u8fdf\u4e0d\u81f3\uff0c\u5bfc\u81f4\u5979\u4ee5\u52a3\u52bf\u5175\u529b\u5bf9\u6297\u9f99\u5deb\u6559\uff0c\u8fd9\u662f\u7b2c\u4e00\u6218\u5931\u5229\u7684\u91cd\u8981\u539f\u56e0\uff1b\u800c\u63f4\u519b\u65e2\u7136\u6765\u8fdf\uff0c\u5173\u9698\u5df2\u7ecf\u4e3a\u5bf9\u624b\u6240\u5360\uff0c\u5c31\u4e0d\u5e94\u8be5\u518d\u8d38\u7136\u8fdb\u653b\uff0c\u81f4\u4f7f\u7b2c\u4e8c\u6218\u53c8\u5931\u5229\u3002\u53ea\u6709\u7b2c\u4e09\u6218\uff0c\u4e5f\u5373\u662f\u665a\u4e0a\u7684\u90a3\u6b21\u53cd\u51fb\u8fd8\u7b97\u52c9\u5f3a\u5408\u683c\u3002\u6574\u4e2a\u8fc7\u7a0b\u4e2d\uff0c\u9634\u5f71\u8c37\u65b9\u90fd\u5b58\u5728\u7740\u660e\u663e\u7684\u6307\u6325\u5931\u7b56\uff0c\u81f3\u5c11\u662f\u76df\u53cb\u4e4b\u95f4\u7684\u914d\u5408\u4e0a\u5b58\u5728\u5f88\u5927\u95ee\u9898\u3002</p><p>\u201c\u6d3b\u4e86\u6570\u767e\u5c81\u7684\u9009\u6c11\uff0c\u4e0d\u81f3\u4e8e\u6218\u672f\u6280\u80fd\u5982\u6b64\u4f4e\u52a3\u5427\uff1f\u8fd9\u573a\u6218\u600e\u4e48\u770b\u600e\u4e48\u89c9\u5f97\u8e4a\u8df7\u554a\u3002\u201d</p><p>\u53e6\u5916\u4e00\u4e2a\u8ba9\u743c\u6069\u611f\u5230\u5947\u602a\u7684\u5730\u65b9\uff0c\u662f\u8fd9\u4efd\u6218\u62a5\u4e2d\u7684\u67d0\u4e9b\u7ec6\u8282\uff0c\u5b9e\u5728\u662f\u7cbe\u786e\u8be6\u5c3d\u5f97\u8fc7\u5206\uff0c\u7edd\u4e0d\u662f\u4e00\u4e2a\u65c1\u89c2\u8005\u6240\u80fd\u5f97\u77e5\u3002\u5373\u4fbf\u662f\u53c2\u6218\u8005\uff0c\u53ea\u6015\u4e5f\u672a\u5fc5\u80fd\u4e86\u89e3\u5f97\u8fd9\u4e48\u6e05\u695a\u3002\u743c\u6069\u975e\u5e38\u6000\u7591\u63d0\u5c14\u6559\u4f1a\u5728\u9f99\u5deb\u6559\u548c\u9634\u5f71\u8c37\u90fd\u5b89\u63d2\u6709\u95f4\u8c0d\uff0c\u6216\u8005\u6709\u5176\u4ed6\u79d8\u5bc6\u7684\u5730\u4e0b\u6d88\u606f\u6765\u6e90\u3002</p><p>\u4e0d\u8fc7\u743c\u6069\u5e76\u6ca1\u6709\u63d0\u51fa\u8fd9\u4e9b\u7591\u95ee\uff0c\u56e0\u4e3a\u6b64\u65f6\u6b64\u523b\uff0c\u4ed6\u7684\u5168\u526f\u6ce8\u610f\u529b\u90fd\u88ab\u6885\u83f2\u65af\u7ed9\u4ed6\u7684\u7b2c\u4e8c\u9875\u7eb8\u5438\u5f15\u4f4f\u4e86\u3002</p><p>\u7b2c\u4e8c\u9875\u7eb8\u4e5f\u662f\u6218\u62a5\uff0c\u4f46\u5e76\u975e\u53d9\u4e8b\uff0c\u4e3b\u8981\u7bc7\u5e45\u96c6\u4e2d\u5728\u53cc\u65b9\u9635\u8425\u7684\u53c2\u6218\u8005\u8eab\u4e0a\uff0c\u5176\u4e2d\u591a\u6b21\u63d0\u53ca\u4e00\u4f4d\u201c\u9ed1\u8272\u77ed\u53d1\u3001\u9ed1\u8863\u3001\u624b\u6301\u8840\u7ea2\u8272\u5de8\u9570\u7684\u5c11\u5973\u201d\u3002\u5728\u524d\u4e24\u6b21\u7684\u6218\u6597\u4e2d\u5979\u90fd\u6709\u51fa\u573a\uff0c\u6240\u5411\u62ab\u9761\u65e0\u5f80\u4e0d\u80dc\uff0c\u800c\u4e14\u51fa\u624b\u6781\u5176\u72e0\u8fa3\uff0c\u6240\u6709\u4e0e\u5979\u4ea4\u6218\u8fc7\u7684\u5bf9\u624b\u975e\u6b7b\u5373\u6b8b\u3002\u5728\u7b2c\u4e8c\u6218\u63a5\u8fd1\u5c3e\u58f0\u7684\u65f6\u5019\uff0c\u5979\u50cf\u662f\u7a81\u7136\u6536\u5230\u4ec0\u4e48\u8baf\u606f\uff0c\u5f84\u76f4\u8131\u79bb\u6218\u573a\uff0c\u9ad8\u901f\u671d\u4e1c\u5357\u65b9\u98de\u53bb\uff0c\u4e4b\u540e\u518d\u65e0\u8e2a\u5f71\u3002\u665a\u4e0a\u9634\u5f71\u8c37\u7684\u53cd\u51fb\u6218\u4e2d\u5979\u4e5f\u6ca1\u6709\u51fa\u73b0\uff0c\u5426\u5219\u51ef\u5c14\u672c\u7b49\u4eba\u8fd8\u672a\u5fc5\u80fd\u8f7b\u6613\u5f97\u624b\u3002</p><p>\u51ef\u745f\u7433\uff1f</p><p>\u5854\u745f\u8c37\u6b63\u4f4d\u4e8e\u96f7\u9e23\u5173\u7684\u4e1c\u5357\u65b9\uff0c\u800c\u4e14\u4ed4\u7ec6\u6838\u5bf9\u7684\u8bdd\uff0c\u9ed1\u8863\u5c11\u5973\u8131\u79bb\u6218\u573a\u548c\u51ef\u745f\u7433\u51fa\u73b0\u5728\u5854\u745f\u8c37\uff0c\u5dee\u4e0d\u591a\u662f\u540c\u4e00\u65f6\u95f4\u3002\u800c\u4e14\u5916\u8c8c\u3001\u6b66\u5668\u7279\u5f81\u90fd\u5b8c\u5168\u7b26\u5408\uff0c\u6bd5\u7adf\u5de8\u9570\u8fd9\u79cd\u6b66\u5668\u53ef\u4e0d\u662f\u968f\u4fbf\u4ec0\u4e48\u4eba\u80fd\u7528\u7684\u3002\u518d\u60f3\u8d77\u6628\u65e5\u5728\u86c7\u708e\u5c71\u7684\u60c5\u5f62\uff0c\u8428\u739b\u65af\u7279\u6d3e\u4eba\u5c06\u51b0\u8679\u8f6c\u5316\u4e3a\u9f99\u5deb\u5996\uff0c\u800c\u51ef\u745f\u7433\u4e5f\u6070\u597d\u51fa\u73b0\uff0c\u8fd9\u663e\u7136\u4e0d\u662f\u5355\u7eaf\u7684\u5de7\u5408\u3002</p><p>\u201c\u5979\u662f\u8428\u739b\u65af\u7279\u7684\u90e8\u4e0b\uff1f\u201d\u743c\u6069\u76b1\u7709\u3002</p><p>\u4ece\u76ee\u524d\u7684\u4fe1\u606f\u4e0a\u5224\u65ad\uff0c\u8fd9\u662f\u987a\u7406\u6210\u7ae0\u7684\u89e3\u91ca\uff0c\u4f46\u4ed6\u603b\u89c9\u5f97\u54ea\u91cc\u4e0d\u5bf9\u52b2\u3002\u5e76\u6ca1\u6709\u4ec0\u4e48\u786e\u5207\u7684\u8bc1\u636e\uff0c\u53ea\u662f\u5355\u7eaf\u7684\u76f4\u89c9\u3002\u51ef\u745f\u7433\u8eab\u4e0a\u900f\u51fa\u90a3\u79cd\u51db\u51db\u7684\u5a01\u4e25\u6c14\u8d28\uff0c\u5e76\u975e\u5355\u7eaf\u7684\u82f1\u6b66\u3001\u521a\u5f3a\uff0c\u66f4\u6709\u4e00\u79cd\u9ad8\u9ad8\u5728\u4e0a\u3001\u5802\u5802\u6b63\u6b63\u7684\u7687\u8005\u6c14\u6d3e\u3002\u50cf\u5979\u8fd9\u6837\u7684\u5973\u5b50\uff0c\u4e0d\u4f1a\u662f\u5c48\u5c45\u4eba\u4e0b\u4e4b\u8f88\uff0c\u81f3\u5c11\u4e0d\u662f\u8428\u739b\u65af\u7279\u90a3\u4e2a\u8001\u9ab7\u9ac5\u6240\u80fd\u6307\u6325\u9a7e\u9a6d\u7684\u3002</p><p>\u6885\u83f2\u65af\u663e\u7136\u548c\u743c\u6069\u7684\u770b\u6cd5\u76f8\u540c\u3002\u201c\u5e94\u8be5\u4e0d\u662f\u90e8\u4e0b\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u53ef\u80fd\u662f\u76df\u53cb\u3002\u8428\u739b\u65af\u7279\u6b64\u6b21\u8fdb\u653b\u9634\u5f71\u8c37\uff0c\u9664\u4e86\u9f99\u5deb\u6559\u7684\u65e7\u90e8\u4e4b\u5916\uff0c\u8fd8\u62c9\u62e2\u4e86\u4e0d\u5c11\u540c\u4f34\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u7ee7\u7eed\u770b\u7740\u6218\u62a5\uff0c\u4e00\u4e2a\u53c8\u4e00\u4e2a\u4eba\u540d\u4ece\u773c\u524d\u63a0\u8fc7\uff0c\u53ef\u60dc\u4ed6\u9605\u5386\u4e0d\u591f\uff0c\u89c1\u95fb\u4e0d\u5e7f\uff0c\u7edd\u5927\u90e8\u5206\u90fd\u4e0d\u8ba4\u8bc6\u3002\u770b\u7740\u770b\u7740\uff0c\u7a81\u7136\u4e00\u4e2a\u540d\u5b57\u6620\u5165\u773c\u5e18\u3002</p><p>\u201c\u5854\u62c9\u590f\xb7\u7ef4\u82e5\u62c9\uff0c\u8fd9\u540d\u5b57\u6709\u70b9\u719f\u2026\u2026\u827e\u5f25\u8587\uff0c\u6211\u8bb0\u5f97\u8fd9\u4f4d\u7ef4\u82e5\u62c9\u5973\u58eb\uff0c\u662f\u4f20\u9053\u5deb\u5e08\u5427\u3002\u201d</p><p>\u201c\u55ef\uff0c\u5deb\u5e08\u4e4b\u795e\u7684\u9009\u6c11\uff0c\u73b0\u4efb\u4f20\u9053\u5deb\u5e08\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5979\u88ab\u8428\u739b\u65af\u7279\u4fd8\u864f\u4e86\u3002\u201d</p></article>"}}}]);
