(window.webpackJsonp=window.webpackJsonp||[]).push([[1734],{1757:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u516b\u8282 \u5c0f\u6885\u7684\u9080\u8bf7",content:"<article><h2>\u7b2c\u4e94\u5341\u516b\u8282 \u5c0f\u6885\u7684\u9080\u8bf7</h2><p>\u5deb\u5e08\u4e4b\u795e\u963f\u7956\u65af\uff0c\u66fe\u7ecf\u662f\u4eba\u7c7b\u5386\u53f2\u4e0a\u8457\u540d\u7684\u5927\u5deb\u5e08\uff0c\u751a\u81f3\u88ab\u8ba4\u4e3a\u662f\u8010\u745f\u745e\u5c14\u4e4b\u540e\u8bde\u751f\u7684\u6700\u5f3a\u5965\u672f\u8005\uff0c\u540e\u6765\u88ab\u9b54\u6cd5\u5973\u795e\u770b\u4e2d\uff0c\u64e2\u5347\u4e3a\u5176\u4ece\u795e\u3002\u5176\u6559\u4e49\u6781\u7aef\u5f3a\u8c03\u903b\u8f91\u548c\u7406\u667a\uff0c\u5145\u6ee1\u7ade\u4e89\u6027\uff0c\u963f\u7956\u65af\u66fe\u4e8e147DR\u4ee5\u5316\u8eab\u5f62\u6001\u964d\u4e34\u4e8e\u521a\u521a\u7ae3\u5de5\u7684\u201c\u81f3\u9ad8\u81f3\u4e0a\u4e4b\u6bbf\u201d\uff08\u6559\u4f1a\u603b\u90e8\uff09\uff0c\u5ba3\u79f0\u201c\u5965\u672f\u4e4b\u9053\uff0c\u5728\u4e8e\u7cbe\u8fdb\uff1b\u5deb\u5e08\u6240\u6c42\uff0c\u552f\u6709\u66f4\u5f3a\u201d\u3002\u4f5c\u4e3a\u4e00\u4f4d\u534a\u795e\uff0c\u4ed6\u7684\u6559\u4f1a\u89c4\u6a21\u975e\u5e38\u5c0f\uff0c\u5168\u90e8\u795e\u804c\u4eba\u5458\u4e0d\u8db3\u767e\u4eba\uff0c\u4f46\u65e0\u4e00\u4f8b\u5916\u90fd\u662f\u795e\u5965\u53cc\u4fee\uff0c\u65e2\u662f\u7267\u5e08\uff0c\u66f4\u662f\u5f3a\u529b\u7684\u5deb\u5e08\uff0c\u9886\u8896\u79f0\u201c\u7b2c\u4e00\u4eba\u201d\uff0c\u53c8\u79f0\u201c\u81f3\u9ad8\u4f20\u9053\u8005\u201d\uff0c\u5916\u4eba\u901a\u5e38\u79f0\u4e4b\u4e3a\u201c\u4f20\u9053\u5deb\u5e08\u201d\uff0c\u4e43\u662f\u963f\u7956\u65af\u7684\u9009\u6c11\u3002</p><p>\u8d39\u4f26\u6709\u8fd1\u767e\u4f4d\u795e\u7947\uff0c\u5176\u4e2d\u534a\u6570\u5de6\u53f3\u90fd\u6709\u9009\u6c11\uff0c\u5f80\u5f80\u8fd8\u4e0d\u6b62\u4e00\u4e2a\uff0c\u6709\u7684\u540d\u9707\u5929\u4e0b\uff0c\u6709\u7684\u9ed8\u9ed8\u65e0\u95fb\uff0c\u6709\u7684\u529b\u91cf\u5353\u7edd\uff0c\u6709\u7684\u4e5f\u4e0d\u8fc7\u5c14\u5c14\uff0c\u4f46\u5176\u5171\u901a\u7684\u7279\u70b9\u662f\u201c\u7a33\u5b9a\u201d\uff0c\u8f7b\u6613\u4e0d\u4f1a\u6362\u4eba\u3002\u6bd5\u7adf\u9009\u6c11\u5c31\u662f\u795e\u7947\u9009\u4e2d\u7684\u51e1\u95f4\u4ee3\u8a00\u4eba\uff0c\u5018\u82e5\u6574\u5929\u53d8\u6765\u66f4\u53bb\u7684\uff0c\u90a3\u50cf\u4ec0\u4e48\u8bdd\u3002\u4efb\u4f55\u4e00\u4e2a\u7ec4\u7ec7\uff0c\u9886\u8896\u6362\u5f97\u9891\u7e41\uff0c\u8ddd\u79bb\u57ae\u53f0\u4e5f\u5c31\u4e0d\u8fdc\u4e86\u3002\u7136\u800c\u5deb\u5e08\u4e4b\u795e\u7684\u9009\u6c11\u5374\u662f\u4e2a\u7279\u4f8b\uff0c\u6218\u62a5\u4e0a\u63d0\u5230\u7684\u8fd9\u4f4d\u5854\u62c9\u590f\xb7\u7ef4\u82e5\u62c9\uff0c\u4e43\u662f\u73b0\u4efb\u4f20\u9053\u5deb\u5e08\u2014\u2014\u540c\u65f6\u4e5f\u662f\u963f\u7956\u65af\u6559\u4f1a\u81ea\u6210\u7acb\u4ee5\u6765\u4e00\u5343\u4e8c\u767e\u591a\u5e74\u7684\u5386\u53f2\u4e0a\uff0c\u7b2c\u516b\u5341\u4e03\u4efb\u4f20\u9053\u5deb\u5e08\u3002</p><p>\u4ea7\u751f\u8fd9\u79cd\u72b6\u51b5\u7684\u539f\u56e0\u5176\u5b9e\u5f88\u7b80\u5355\uff0c\u522b\u7684\u6559\u4f1a\u7684\u9009\u6c11\u57fa\u672c\u90fd\u662f\u795e\u660e\u6307\u5b9a\u7684\uff0c\u4f20\u9053\u5deb\u5e08\u7684\u4f4d\u7f6e\u5374\u771f\u6b63\u662f\u201c\u6740\u201d\u51fa\u6765\u7684\u3002147DR\uff0c\u963f\u7956\u65af\u6559\u4f1a\u6b63\u5f0f\u6210\u7acb\u4e4b\u65e5\uff0c\u795e\u7947\u81ea\u5929\u754c\u964d\u4e34\u51e1\u95f4\uff0c\u6307\u5b9a\u5f53\u65f6\u7684\u6559\u4f1a\u9886\u8896\u4e3a\u7b2c\u4e00\u4efb\u4f20\u9053\u5deb\u5e08\uff0c\u6388\u4e88\u795e\u529b\uff0c\u5e76\u5f53\u4f17\u5ba3\u5e03\u4f20\u9053\u5deb\u5e08\u6bcf\u4ee3\u4e00\u4eba\uff0c\u4ee3\u4ee3\u627f\u7ee7\uff0c\u51e1\u662f\u672c\u6559\u4f1a\u6210\u5458\uff0c\u90fd\u6709\u6743\u5411\u73b0\u4efb\u4f20\u9053\u5deb\u5e08\u53d1\u51fa\u540d\u4e3a\u201c\u6dec\u70bc\u201d\u7684\u6311\u6218\uff0c\u540e\u8005\u5fc5\u987b\u5e94\u6218\uff0c\u4e0d\u5f97\u62d2\u7edd\u3002\u201c\u6dec\u70bc\u201d\u5e76\u975e\u6bd4\u8bd5\u8f83\u91cf\uff0c\u800c\u662f\u751f\u6b7b\u51b3\u6597\uff0c\u7ed3\u679c\u8981\u4e48\u662f\u73b0\u4efb\u4f20\u9053\u5deb\u5e08\u6210\u529f\u536b\u5195\uff0c\u8981\u4e48\u662f\u6311\u6218\u8005\u7ee7\u627f\u5b9d\u5ea7\uff0c\u5931\u8d25\u8005\u7684\u552f\u4e00\u4e0b\u573a\u5c31\u662f\u6b7b\u3002\u5982\u679c\u6253\u6bd4\u65b9\u7684\u8bdd\uff0c\u5f88\u50cf\u662f\u72fc\u7fa4\u4e2d\u8bde\u751f\u72fc\u738b\u7684\u6a21\u5f0f\u3002\u5982\u6b64\u6b8b\u9177\u7684\u9009\u62d4\u65b9\u5f0f\uff0c\u81ea\u7136\u5bfc\u81f4\u4e86\u6b8b\u9177\u7684\u540e\u679c\uff0c\u6700\u6781\u7aef\u7684\u60c5\u5f62\u662f709DR\uff0c\u4f20\u9053\u5deb\u5e08\u7684\u4f4d\u7f6e\u66fe\u7ecf\u5728\u4e00\u591c\u4e4b\u95f4\u8fde\u7eed\u66f4\u8fed\u4e86\u5341\u56db\u6b21\uff0c\u6b64\u4e8b\u53d1\u751f\u540e\uff0c\u9b54\u6cd5\u5973\u795e\u4f5c\u51fa\u4e86\u5e72\u6d89\uff0c\u5979\u8ba9\u6bcf\u4f4d\u4f20\u9053\u5deb\u5e08\u5728\u65b0\u7ee7\u4f4d\u65f6\u81ea\u52a8\u83b7\u5f97\u4e00\u79cd\u6682\u65f6\u6027\u7684\u4fdd\u62a4\u529b\u91cf\uff0c\u8ba9\u4ed6\u6709\u5145\u8db3\u7684\u65f6\u95f4\u5b8c\u5168\u9002\u5e94\u5e76\u5de9\u56fa\u81ea\u5df1\u7684\u9009\u6c11\u8eab\u4efd\uff0c\u4ee5\u53ca\u65b0\u83b7\u5f97\u7684\u795e\u529b\uff0c\u4ece\u800c\u5927\u5927\u964d\u4f4e\u4e86\u6b7b\u4ea1\u9891\u7387\u3002</p><p>\u5c3d\u7ba1\u5982\u6b64\uff0c\u4f20\u9053\u5deb\u5e08\u4f9d\u65e7\u8fd8\u662f\u4e00\u4efd\u867d\u7136\u5f88\u6709\u524d\u9014\uff0c\u4f46\u98ce\u9669\u9ad8\u5f97\u79bb\u8c31\u7684\u5de5\u4f5c\u3002\u76f4\u81f31358DR\uff08\u52a8\u8361\u4e4b\u5e74\uff09\u4ee5\u540e\uff0c\u65b0\u4efb\u9b54\u6cd5\u5973\u795e\u8ba4\u4e3a\u8fd9\u79cd\u4ea7\u751f\u4f20\u9053\u5deb\u5e08\u7684\u65b9\u5f0f\u592a\u8fc7\u4e8e\u6b8b\u9177\u8840\u8165\uff0c\u8981\u6c42\u963f\u7956\u65af\u5c06\u5176\u5e9f\u9664\uff0c\u6539\u6210\u795e\u660e\u76f4\u63a5\u6307\u5b9a\uff0c\u636e\u8bf4\u4e24\u4f4d\u795e\u7947\u4e3a\u6b64\u4ea7\u751f\u4e86\u6fc0\u70c8\u7684\u4e89\u6267\uff0c\u4f46\u6700\u7ec8\u963f\u7956\u65af\u8fd8\u662f\u670d\u4ece\u4e86\u547d\u4ee4\u30021370DR\uff0c\u5f53\u65f6\u7684\u4f20\u9053\u5deb\u5e08\u5728\u4e00\u6b21\u75af\u72c2\u7684\u9b54\u6cd5\u8bd5\u9a8c\u4e2d\u4e27\u751f\uff08\u4ed6\u60f3\u7814\u7a76\u51fa\u4e00\u79cd\u80fd\u591f\u540c\u65f6\u91ca\u653e\u516b\u5341\u4e2a\u6cd5\u672f\u7684\u795e\u8bdd\u7ea7\u89e6\u53d1\u9b54\u6cd5\uff09\uff0c\u5728\u6570\u4f4d\u5019\u9009\u4eba\u4e2d\uff0c\u5854\u62c9\u590f\xb7\u7ef4\u82e5\u62c9\u5973\u58eb\u8131\u9896\u800c\u51fa\uff0c\u6210\u4e3a\u65b0\u4e00\u4efb\u4f20\u9053\u5deb\u5e08\u3002</p><p>\u8fd9\u4e9b\u5386\u53f2\u7ec6\u8282\u4e3b\u8981\u662f\u6885\u83f2\u65af\u8bb2\u8ff0\uff0c\u743c\u6069\u5e76\u4e0d\u5f88\u6e05\u695a\uff0c\u4ed6\u4e4b\u6240\u4ee5\u4f1a\u5bf9\u8fd9\u4e2a\u540d\u5b57\u6709\u5370\u8c61\uff0c\u53ea\u662f\u56e0\u4e3a\u66fe\u7ecf\u542c\u51db\u63d0\u53ca\u8fc7\u3002\u98ce\u66b4\u5973\u738b\u6b23\u5e03\u813e\u6c14\u66b4\u8e81\uff0c\u6027\u683c\u4e56\u50fb\uff0c\u867d\u7136\u76f8\u8bc6\u6ee1\u5929\u4e0b\uff0c\u771f\u6b63\u80fd\u8c08\u5fc3\u7684\u670b\u53cb\u5374\u5be5\u5be5\u53ef\u6570\uff0c\u800c\u7ef4\u82e5\u62c9\u4fbf\u662f\u5176\u4e2d\u4e4b\u4e00\u3002\u4e24\u4eba\u7684\u5173\u7cfb\u4ea6\u5e08\u4ea6\u53cb\uff0c\u7ef4\u82e5\u62c9\u66fe\u7ecf\u5728\u6b23\u5e03\u7684\u6307\u5bfc\u4e0b\u5b66\u4e60\u8fc7\u4e00\u5e74\u7684\u9b54\u6cd5\uff0c\u4e25\u683c\u7b97\u8d77\u6765\u8fd8\u662f\u51db\u7684\u5e08\u59d0\uff0c\u4e0d\u8fc7\u5e76\u6ca1\u89c1\u8fc7\u9762\u3002</p><p>\u201c\u88ab\u8428\u739b\u65af\u7279\u4fd8\u864f\uff0c\u90a3\u80af\u5b9a\u662f\u51f6\u591a\u5409\u5c11\u4e86\uff0c\u5341\u6709\u516b\u4e5d\u88ab\u505a\u6210\u5c38\u5993\uff0c\u201d\u743c\u6069\u5fc3\u60f3\uff0c\u201c\u771f\u53ef\u60dc\uff0c\u542c\u8bf4\u8fd8\u662f\u4f4d\u7d20\u8d28\u4e0d\u9519\u7684\u91d1\u53d1\u7f8e\u4eba\u5462\u3002\u201d</p><p>\u4e0d\u8fc7\u7f8e\u4eba\u4e5f\u7f62\uff0c\u5c38\u5993\u4e5f\u7f62\uff0c\u8ddd\u79bb\u743c\u6069\u90fd\u6bd4\u8f83\u9065\u8fdc\uff0c\u6240\u4ee5\u4ed6\u5f88\u5feb\u5c06\u8fd9\u4ef6\u4e8b\u629b\u4e4b\u8111\u540e\uff0c\u7ee7\u7eed\u6b64\u524d\u7684\u8bdd\u9898\u3002\u9634\u5f71\u8c37\u548c\u9f99\u5deb\u6559\u4ea4\u6218\u5931\u5229\uff0c\u635f\u5931\u9887\u91cd\uff0c\u6240\u4ee5\u6d3e\u6b23\u5e03\u6765\u5854\u745f\u8c37\u5411\u63d0\u5c14\u6559\u4f1a\u6c42\u63f4\u2014\u2014\u867d\u7136\u4f3c\u4e4e\u4e5f\u8bf4\u5f97\u901a\uff0c\u4f46\u743c\u6069\u8fd8\u662f\u89c9\u5f97\u6709\u4e9b\u8e4a\u8df7\u3002\u201c\u6668\u66e6\u4e4b\u795e\u7684\u7267\u5e08\u4eec\u5462\uff1f\u201d\u4ed6\u4ed4\u4ed4\u7ec6\u7ec6\u5730\u5c06\u6218\u62a5\u53c8\u770b\u4e86\u4e00\u904d\uff0c\u95ee\u6885\u83f2\u65af\uff0c\u201c\u4ed6\u4eec\u5728\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u5982\u679c\u8981\u6570\u8428\u739b\u65af\u7279\u7684\u4ec7\u654c\uff0c\u9b54\u6cd5\u5973\u795e\u6559\u4f1a\u81ea\u7136\u4f4d\u5c45\u7b2c\u4e00\uff0c\u63a5\u4e0b\u6765\u5e94\u8be5\u5c31\u662f\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u7684\u6559\u4f1a\u3002\u5f53\u5e74\u7ad6\u7434\u624b\u540c\u76df\u4f0f\u51fb\u8428\u739b\u65af\u7279\u5374\u906d\u9047\u60e8\u8d25\uff0c\u6700\u540e\u5c31\u662f\u5170\u68ee\u5fb7\u5c14\u7267\u5e08\u4eec\u8bf7\u795e\u660e\u4e0b\u51e1\u5e72\u6389\u4e86\u8fd9\u4f4d\u8001\u5deb\u5996\uff0c\u76db\u6781\u4e00\u65f6\u7684\u9f99\u5deb\u6559\u968f\u4e4b\u8986\u706d\u3002\u800c\u4e14\u8fd9\u4e24\u5927\u6559\u4f1a\u7684\u5173\u7cfb\u5411\u6765\u4e0d\u9519\uff0c\u6668\u66e6\u4e4b\u795e\u5728\u9634\u5f71\u8c37\u4e2d\u4e5f\u5efa\u6709\u795e\u6bbf\uff0c\u542c\u8bf4\u89c4\u6a21\u4e0d\u5c0f\uff0c\u9ad8\u9636\u7267\u5e08\u6570\u5341\u540d\uff0c\u8fd8\u6709\u4e00\u4f4d\u6668\u5149\u5723\u4f7f\u5750\u9547\u3002\u4f17\u6240\u5468\u77e5\uff0c\u6668\u66e6\u4e4b\u795e\u662f\u6700\u618e\u6076\u4ea1\u7075\u7684\u795e\u660e\uff0c\u4ed6\u7684\u7267\u5e08\u4eec\u6700\u64c5\u957f\u4e5f\u6700\u70ed\u8877\u4e8e\u6467\u6bc1\u4ea1\u7075\uff0c\u8fd9\u662f\u4ed6\u4eec\u7684\u5929\u804c\u548c\u4f7f\u547d\uff0c\u6b63\u662f\u8428\u739b\u65af\u7279\u548c\u9f99\u5deb\u6559\u7684\u6b7b\u654c\u3002\u7136\u800c\u5728\u6218\u62a5\u4e2d\uff0c\u5c45\u7136\u6ca1\u6709\u63d0\u5230\u6709\u4efb\u4f55\u4e00\u4f4d\u5170\u68ee\u5fb7\u5c14\u7267\u5e08\u53c2\u6218\uff0c\u8fd9\u5b9e\u5728\u662f\u5f88\u5947\u602a\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u5728\u505a\u4ec0\u4e48\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6211\u53ea\u77e5\u9053\u4ed6\u4eec\u4e0d\u5728\u9634\u5f71\u8c37\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u539f\u6765\u5728\u4e0a\u4e2a\u6708\u5e95\uff0c\u5170\u68ee\u5fb7\u5c14\u6559\u4f1a\u7684\u516d\u5927\u6668\u5149\u5723\u4f7f\u9f50\u805a\u6df1\u6c34\u57ce\u5bc6\u8c08\uff0c\u968f\u540e\u5404\u5730\u9ece\u660e\u795e\u6bbf\u7684\u7cbe\u82f1\u5206\u5b50\u90fd\u5728\u6084\u65e0\u58f0\u606f\u95f4\u5931\u8e2a\u4e86\uff0c\u6ca1\u6709\u4eba\u77e5\u9053\u4ed6\u4eec\u5230\u5e95\u53bb\u4e86\u54ea\u91cc\uff0c\u8981\u505a\u4ec0\u4e48\uff0c\u6559\u4f1a\u5bf9\u6b64\u4e5f\u4e0d\u4e88\u56de\u7b54\u3002\u5916\u4eba\u552f\u4e00\u6240\u80fd\u63a8\u6d4b\u7684\uff0c\u5c31\u662f\u4ed6\u4eec\u6b63\u5728\u8fdb\u884c\u4e00\u9879\u6781\u5176\u91cd\u5927\u7684\u79d8\u5bc6\u8ba1\u5212\uff0c\u81f3\u4e8e\u5177\u4f53\u7ec6\u8282\uff0c\u6682\u65f6\u4e00\u65e0\u6240\u77e5\u3002\u9634\u5f71\u8c37\u7684\u9ece\u660e\u795e\u6bbf\u4e2d\uff0c\u6b64\u65f6\u4e5f\u4ec5\u5269\u4e00\u4f4d\u526f\u4e3b\u6559\u548c\u4e09\u56db\u540d\u4f4e\u9636\u7267\u5e08\u9a7b\u5b88\uff0c\u5176\u4f59\u4eba\u7b49\u65e9\u5c31\u79bb\u5f00\u4e86\u3002</p><p>\u201c\u9b3c\u9b3c\u795f\u795f\u7684\uff0c\u4e00\u5b9a\u4e0d\u662f\u4ec0\u4e48\u597d\u4e8b\uff0c\u201d\u743c\u6069\u9887\u5e26\u6076\u610f\u5730\u8bc4\u4ef7\u7740\uff0c\u201c\u90a3\u4e0d\u662f\u8fd8\u6709\u4e00\u4f4d\u526f\u4e3b\u6559\u5417\uff0c\u600e\u4e48\u4e5f\u6ca1\u770b\u89c1\u4ed6\u51fa\u573a\u5462\uff1f\u201d</p><p>\u201c\u90a3\u4f4d\u526f\u4e3b\u6559\u662f\u4e2a\u6587\u804c\u4eba\u5458\uff0c\u542c\u8bf4\u5728\u9b54\u6cd5\u673a\u5173\u7684\u7f16\u6784\u548c\u89e3\u6790\u65b9\u9762\u662f\u4e13\u5bb6\uff0c\u81f3\u4e8e\u6218\u6597\u529b\u4e48\u2026\u2026\u201d\u6885\u83f2\u65af\u60f3\u4e86\u60f3\uff0c\u8c28\u614e\u5730\u4e0b\u4e86\u4e2a\u5224\u65ad\uff0c\u201c\u6bd4\u8f83\u5f31\uff0c\u6ca1\u6cd5\u4e0a\u6218\u573a\u3002\u201d</p><p>\u91cd\u8981\u7684\u76df\u53cb\u7a81\u7136\u5f00\u6e9c\u4e0d\u89c1\uff0c\u6218\u573a\u4e0a\u53c8\u63a5\u8fde\u906d\u9047\u91cd\u632b\uff0c\u6b23\u5e03\u524d\u6765\u5854\u745f\u8c37\u4e5f\u5c31\u987a\u7406\u6210\u7ae0\u4e86\u3002\u4e0d\u8fc7\u65e2\u7136\u63d0\u5c14\u6559\u4f1a\u4e0d\u613f\u610f\u4e0e\u9f99\u5deb\u6559\u53d1\u751f\u6b63\u9762\u51b2\u7a81\uff0c\u5e94\u5141\u7684\u53ef\u80fd\u6027\u5e94\u8be5\u5f88\u5c0f\u5427\u3002</p><p>\u201c\u55ef\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f60\u731c\u5f97\u6ca1\u9519\u3002\u201d</p><p>\u6b63\u5982\u743c\u6069\u6240\u6599\uff0c\u63d0\u5c14\u6559\u4f1a\u7684\u9ad8\u5c42\u666e\u904d\u6301\u7f6e\u8eab\u4e8b\u5916\u7684\u6001\u5ea6\uff0c\u4e0d\u613f\u5377\u5165\u8fd9\u573a\u6218\u4e89\uff0c\u53ea\u662f\u5728\u8003\u8651\u5982\u4f55\u5bfb\u627e\u4e00\u4e2a\u5408\u9002\u7684\u501f\u53e3\u800c\u5df2\u3002\u4eca\u65e5\u7ea6\u89c1\u6b23\u5e03\uff0c\u539f\u672c\u5c31\u662f\u6253\u7b97\u5a49\u62d2\u7684\u3002\u6b23\u5e03\u662f\u7b2c\u4e00\u6d41\u7684\u5927\u5deb\u5e08\uff0c\u5374\u5e76\u4e0d\u662f\u5408\u683c\u7684\u8bf4\u5ba2\uff0c\u773c\u770b\u5c31\u8981\u65e0\u529f\u800c\u8fd4\uff0c\u6070\u5728\u6b64\u65f6\uff0c\u4f20\u6765\u4e86\u51b0\u8679\u88ab\u8428\u739b\u65af\u7279\u8f6c\u5316\u4e3a\u9f99\u5deb\u5996\u7684\u6d88\u606f\u3002\u6b64\u4e8b\u4e00\u51fa\uff0c\u6559\u4f1a\u9ad8\u5c42\u7684\u6001\u5ea6\u968f\u4e4b\u53d1\u751f\u6539\u53d8\uff0c\u4e00\u4e9b\u539f\u672c\u575a\u5b9a\u7684\u201c\u5149\u8363\u5b64\u7acb\u201d\u4e3b\u4e49\u8005\u7acb\u573a\u6709\u6240\u52a8\u6447\u3002\u7ecf\u8fc7\u957f\u65f6\u95f4\u7684\u626f\u76ae\u548c\u4e89\u6267\uff0c\u6700\u540e\u5f62\u6210\u7684\u7ed3\u8bba\u662f\uff1a\u201c\u6709\u9650\u5ea6\u5730\u652f\u63f4\u9634\u5f71\u8c37\u201d\u3002</p><p>\u6240\u8c13\u6709\u9650\u5ea6\u5730\u652f\u63f4\uff0c\u8bf4\u5f97\u5177\u4f53\u70b9\u5c31\u662f\uff1a\u65e2\u8981\u63f4\u52a9\uff0c\u53c8\u4e0d\u80fd\u6df1\u9677\u5176\u4e2d\uff0c\u65e2\u8981\u5bf9\u51b0\u8679\u7684\u4e8b\u60c5\u6709\u6240\u4ea4\u4ee3\uff0c\u5bf9\u9634\u5f71\u8c37\u65b9\u9762\u6709\u6240\u8868\u793a\uff0c\u53c8\u4e0d\u80fd\u771f\u628a\u6559\u4f1a\u62d6\u8fdb\u8fd9\u573a\u6218\u4e89\uff0c\u5176\u5fc3\u6001\u5927\u7ea6\u7c7b\u4f3c\u4e8e\u4e16\u754c\u5927\u6218\u524d\u671f\u7684\u7f8e\u56fd\u3002</p><p>\u6240\u4ee5\u6885\u83f2\u65af\u5c31\u6210\u4e86\u6700\u4f73\u7684\u4eba\u9009\u3002\u5979\u662f\u795e\u9009\u8005\u3001\u5723\u6b66\u58eb\uff0c\u5728\u6559\u4f1a\u4e2d\u5730\u4f4d\u4ec5\u6b21\u4e8e\u5927\u4e3b\u6559\uff0c\u800c\u4e14\u5a01\u540d\u663e\u8d6b\uff0c\u529b\u91cf\u5f3a\u5927\uff0c\u5bf9\u9634\u5f71\u8c37\u65b9\u9762\u8db3\u4ee5\u4ea4\u4ee3\u8fc7\u53bb\uff1b\u7136\u800c\u5979\u4e0a\u6b21\u53c8\u56e0\u4e3a\u9ece\u660e\u4e4b\u77f3\u6bc1\u635f\u7684\u4e8b\u60c5\u53d7\u5230\u5904\u7f5a\uff0c\u88ab\u5f00\u9664\u6559\u7c4d\uff0c\u4ece\u7406\u8bba\u4e0a\u6765\u8bf4\u5df2\u7ecf\u4e0d\u662f\u63d0\u5c14\u6559\u4f1a\u7684\u6210\u5458\u2014\u2014\u867d\u7136\u5927\u5bb6\u90fd\u5fc3\u77e5\u809a\u660e\u8fd9\u53ea\u662f\u8868\u9762\u6587\u7ae0\uff0c\u53ea\u8981\u627e\u4e2a\u673a\u4f1a\u5c31\u4f1a\u64a4\u9500\u5904\u7f5a\uff0c\u4f46\u6682\u65f6\u4e0d\u662f\u8fd8\u6ca1\u64a4\u9500\u4e48\u3002\u8868\u9762\u6587\u7ae0\u5728\u5f88\u591a\u65f6\u5019\u6ca1\u6709\u610f\u4e49\uff0c\u4f46\u540c\u6837\u5728\u5f88\u591a\u65f6\u5019\u5374\u4e5f\u662f\u6709\u610f\u4e49\u7684\uff0c\u6d89\u53ca\u5230\u519b\u653f\u5c42\u9762\u5c24\u5176\u5982\u6b64\uff0c\u5426\u5219\u89e3\u653e\u519b\u5728\u6e21\u8fc7\u9e2d\u7eff\u6c5f\u524d\uff0c\u4f55\u5fc5\u975e\u8981\u5148\u6539\u540d\u5fd7\u613f\u519b\u5462\u3002</p><p>\u201c\u800c\u4e14\u4f60\u6070\u597d\u53c8\u548c\u51b0\u8679\u5173\u7cfb\u4e0d\u9519\u662f\u5427\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u6885\u83f2\u65af\u548c\u51b0\u8679\u5173\u7cfb\u53cb\u5584\uff0c\u662f\u6781\u5c11\u6570\u80fd\u591f\u5f97\u5230\u5b9d\u77f3\u9f99\u7684\u51c6\u8bb8\uff0c\u8fdb\u5165\u5b83\u5de2\u7a74\u7684\u4eba\u4e4b\u4e00\u3002\u51b0\u8679\u662f\u53d8\u6210\u4e86\u9f99\u5deb\u5996\uff0c\u4e0d\u662f\u9aa8\u9f99\u4e5f\u4e0d\u662f\u50f5\u5c38\u9f99\uff0c\u6027\u8d28\u867d\u7136\u53d8\u4e86\uff0c\u795e\u667a\u4f9d\u65e7\u6e05\u9192\uff0c\u8bb0\u5fc6\u4f9d\u65e7\u4fdd\u7559\uff0c\u591a\u591a\u5c11\u5c11\u4f1a\u987e\u5ff5\u70b9\u65e7\u60c5\u5427\u2026\u2026\u5e0c\u671b\u5982\u6b64\u3002</p><p>\u201c\u662f\u554a\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4e0d\u8fc7\u5176\u5b9e\u771f\u8981\u8bba\u8d77\u6765\uff0c\u548c\u51b0\u8679\u5173\u7cfb\u6700\u597d\u7684\u4e0d\u662f\u6211\u3002\u201d</p><p>\u201c\u90a3\u662f\u8c01\uff1f\u5927\u957f\u8001\uff1f\u201d</p><p>\u6885\u83f2\u65af\u6447\u5934\uff0c\u201c\u662f\u6b4c\u66e6\u96c5\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u6b4c\u66e6\u96c5\u5df2\u7ecf\u79bb\u5f00\u6559\u4f1a\uff0c\u5982\u4eca\u662f\u6563\u63d0\u5c14\u5821\u7684\u7edf\u5e05\uff0c\u548c\u51b0\u8679\u5173\u7cfb\u518d\u597d\u4e5f\u6ca1\u7528\u3002\u4e0d\u8fc7\u542c\u5230\u8fd9\u4e2a\u540d\u5b57\uff0c\u743c\u6069\u8111\u6d77\u4e2d\u4fbf\u987f\u65f6\u6d6e\u73b0\u51fa\u90a3\u4f4d\u7d2b\u53d1\u5973\u5b50\u7684\u82f1\u59ff\uff0c\u6ca1\u529e\u6cd5\uff0c\u4ed6\u5411\u6765\u5bf9\u8fd9\u79cd\u7c7b\u578b\u7684\u5973\u6027\u6ca1\u6709\u4ec0\u4e48\u62b5\u6297\u529b\u3002\u73b0\u5728\u770b\u8d77\u6765\uff0c\u90a3\u4f4d\u5b9d\u77f3\u9f99\u51b0\u8679\u5927\u6982\u548c\u743c\u6069\u7684\u7231\u597d\u5dee\u4e0d\u591a\uff0c\u5927\u5bb6\u662f\u540c\u9053\uff0c\u4ee5\u540e\u6709\u673a\u4f1a\u7684\u8bdd\uff0c\u4e00\u5b9a\u8981\u4ea4\u6d41\u4ea4\u6d41\u5f7c\u6b64\u7684\u5fc3\u5f97\u4f53\u4f1a\u3002</p><p>\u597d\u5427\uff0c\u8fd9\u4e2a\u95ee\u9898\u5148\u6254\u5230\u4e00\u8fb9\u3002</p><p>\u201c\u4ec0\u4e48\u65f6\u5019\u52a8\u8eab\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u8fd8\u6ca1\u51b3\u5b9a\uff0c\u4e0d\u8fc7\u5e94\u8be5\u8981\u7b49\u5230\u661f\u4e4b\u82b1\u7ed3\u675f\u4e4b\u540e\u5427\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u201c\u5bf9\u4e86\uff0c\u827e\u5f25\u8587\uff0c\u201d\u8bf4\u8d77\u8fd9\u91cc\uff0c\u5012\u662f\u8ba9\u743c\u6069\u60f3\u8d77\u4e00\u4ef6\u4e8b\u3002\u201c\u6211\u4e00\u76f4\u6ca1\u641e\u61c2\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u53bb\u53c2\u52a0\u8fd9\u79cd\u6bd4\u8d5b\u554a\u3002\u201d</p><p>\u9009\u7f8e\u6bd4\u8d5b\u8fd9\u79cd\u4e8b\u60c5\uff0c\u662f\u597d\u662f\u574f\u743c\u6069\u4e0d\u60f3\u8bc4\u4ef7\uff0c\u81f3\u5c11\u8fd9\u4e2a\u4e16\u754c\u4e0a\u8fd8\u6ca1\u6709\u4e09\u70b9\u5f0f\u6bd4\u57fa\u5c3c\uff0c\u5973\u5b69\u5b50\u4eec\u4e0a\u53f0\u65f6\u8863\u670d\u7a7f\u5f97\u90fd\u8fd8\u7b97\u591a\u3002\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u743c\u6069\u53ef\u4ee5\u80af\u5b9a\u6885\u83f2\u65af\u662f\u7edd\u4e0d\u4f1a\u559c\u6b22\u8fd9\u79cd\u4e8b\u60c5\u7684\uff0c\u5979\u4e0d\u4f1a\u559c\u6b22\u8fd9\u6837\u8d70\u5230\u53f0\u4e0a\uff0c\u6414\u9996\u5f04\u59ff\uff0c\u5c06\u81ea\u5df1\u7684\u7f8e\u4e3d\u523b\u610f\u5c55\u73b0\u7ed9\u5927\u5bb6\u770b\u3002\u5f88\u591a\u5973\u5b69\u5b50\u4f1a\u559c\u6b22\uff0c\u5979\u4eec\u559c\u6b22\u88ab\u5173\u6ce8\uff0c\u88ab\u8d5e\u8d4f\uff0c\u88ab\u559c\u7231\uff1b\u51db\u4e5f\u4f1a\u559c\u6b22\uff0c\u56e0\u4e3a\u5979\u89c9\u5f97\u5f88\u70ed\u95f9\uff0c\u5f88\u6709\u8da3\uff0c\u5f88\u597d\u73a9\u2014\u2014\u4f46\u6885\u83f2\u65af\u662f\u80af\u5b9a\u6ca1\u5174\u8da3\u7684\u3002\u4ec0\u4e48\u661f\u4e4b\u82b1\u7684\u5934\u8854\uff0c\u5bf9\u4e8e\u5979\u800c\u8a00\uff0c\u6ca1\u6709\u534a\u70b9\u610f\u4e49\u5427\u3002</p><p>\u4e0d\u4ec5\u4ec5\u6885\u83f2\u65af\uff0c\u8fd8\u6709\u4ee5\u524d\u8fde\u5f97\u56db\u5c4a\u51a0\u519b\u7684\u6b4c\u66e6\u96c5\uff0c\u743c\u6069\u867d\u7136\u548c\u5979\u53ea\u6253\u8fc7\u4e24\u4e2a\u7167\u9762\uff0c\u6253\u8fc7\u4e00\u67b6\uff0c\u6ca1\u6709\u4ec0\u4e48\u6df1\u4ea4\u3002\u4f46\u4ece\u6c14\u8d28\u4e0a\u6765\u5224\u65ad\uff0c\u4e5f\u5b9e\u5728\u4e0d\u50cf\u662f\u4f1a\u70ed\u8877\u4e8e\u8fd9\u79cd\u9009\u7f8e\u6bd4\u8d5b\u7684\u5973\u5b50\u3002\u8fd9\u5176\u4e2d\u4e00\u5b9a\u6709\u67d0\u79cd\u7f18\u6545\u624d\u5bf9\u3002</p><p>\u201c\u6211\u662f\u4e0d\u559c\u6b22\uff0c\u4e0d\u8fc7\u8fd9\u662f\u6559\u4f1a\u4ea4\u6d3e\u4e0b\u6765\u7684\u4efb\u52a1\uff0c\u201d\u6885\u83f2\u65af\u76b1\u76b1\u7709\u5934\uff0c\u6709\u4e9b\u610f\u5174\u9611\u73ca\u5730\u56de\u7b54\uff0c\u201c\u6559\u4f1a\u5e0c\u671b\u85c9\u6b64\u589e\u5f3a\u53f7\u53ec\u529b\uff0c\u5927\u4e3b\u6559\u4e5f\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u201d</p><p>\u539f\u6765\u5982\u6b64\uff0c\u7c7b\u4f3c\u4e8e\u516c\u5b89\u90e8\u95e8\u627e\u4e2a\u6f02\u4eae\u5973\u8b66\u82b1\u505a\u5f62\u8c61\u4ee3\u8a00\u4eba\u7684\u610f\u601d\uff0c\u786e\u5b9e\u662f\u4e2a\u4e0d\u9519\u7684\u65b9\u5f0f\u3002\u4ee5\u524d\u662f\u6b4c\u66e6\u96c5\uff0c\u540e\u6765\u5728\u4e0a\u4e00\u5c4a\u661f\u4e4b\u82b1\u7684\u5f00\u5e55\u524d\u5915\uff0c\u6b4c\u66e6\u96c5\u7a81\u7136\u79bb\u53bb\uff0c\u6559\u4f1a\u4e00\u65f6\u95f4\u63aa\u624b\u4e0d\u53ca\uff0c\u4e8e\u662f\u53ea\u597d\u7531\u6885\u83f2\u65af\u6765\u63a5\u66ff\u8fd9\u4e2a\u5de5\u4f5c\u3002\u65e2\u7136\u662f\u5de5\u4f5c\uff0c\u90a3\u5c31\u65e0\u6240\u8c13\u559c\u6b22\u4e0d\u559c\u6b22\uff0c\u53cd\u6b63\u90fd\u5f97\u505a\u3002\u5176\u5b9e\u4e25\u683c\u8bf4\u8d77\u6765\uff0c\u6885\u83f2\u65af\u5e76\u4e0d\u662f\u5f88\u9002\u5408\u8fd9\u79cd\u9009\u7f8e\u6bd4\u8d5b\uff0c\u5979\u592a\u7f3a\u4e4f\u90a3\u79cd\u8868\u73b0\u6b32\u671b\uff0c\u597d\u5728\u81ea\u8eab\u7d20\u8d28\u8db3\u591f\u4f18\u79c0\uff0c\u54ea\u6015\u4ec0\u4e48\u90fd\u4e0d\u505a\uff0c\u5355\u7eaf\u5f80\u53f0\u4e0a\u4e00\u7ad9\uff0c\u4e5f\u5c31\u8db3\u591f\u8273\u538b\u7fa4\u82b3\uff0c\u95ee\u9f0e\u51a0\u519b\u4e86\u3002</p><p>\u4e0d\u8fc7\u4e0a\u4e00\u5c4a\u662f\u4e0a\u4e00\u5c4a\uff0c\u8fd9\u4e00\u5c4a\u53ea\u6015\u5c31\u6ca1\u90a3\u4e48\u5bb9\u6613\u3002\u522b\u7684\u4e0d\u8bf4\uff0c\u5979\u80fd\u4e0d\u80fd\u80dc\u8fc7\u51db\u5c31\u662f\u4e2a\u95ee\u9898\uff0c\u8bba\u5bb9\u8c8c\u90fd\u662f\u7edd\u8272\uff0c\u8bba\u6c14\u8d28\u5404\u6709\u5343\u79cb\uff0c\u8bba\u8eab\u6750\u51db\u53ef\u5c31\u7a33\u7a33\u80dc\u51fa\u4e86\u3002\u800c\u4e14\u542c\u6885\u83f2\u65af\u8bf4\u8fc7\uff0c\u51db\u591a\u624d\u591a\u827a\uff0c\u80fd\u6b4c\u5584\u821e\uff0c\u76f8\u6bd4\u8f83\u800c\u8a00\uff0c\u6885\u83f2\u65af\u4f3c\u4e4e\u53ea\u4f1a\u62d4\u5251\u780d\u4eba\u2026\u2026</p><p>\u5bf9\u4e86\uff0c\u8fd8\u6709\u4eca\u5929\u770b\u5230\u7684\u90a3\u4f4d\u76f2\u5973\uff0c\u867d\u7136\u4f3c\u4e4e\u6bd4\u4e0d\u4e0a\u6885\u83f2\u65af\u548c\u51db\u8273\u5149\u7167\u4eba\uff0c\u4f46\u5374\u81ea\u6709\u4e00\u79cd\u8bf4\u4e0d\u51fa\u7684\u795e\u79d8\u6c14\u8d28\uff0c\u8ba9\u743c\u6069\u5bf9\u5979\u683c\u5916\u5173\u6ce8\uff0c\u89c9\u5f97\u5e94\u8be5\u4e5f\u662f\u6700\u540e\u661f\u4e4b\u82b1\u5934\u8854\u7684\u6709\u529b\u7ade\u4e89\u8005\u4e4b\u4e00\u3002</p><p>\u7136\u800c\u743c\u6069\u6682\u65f6\u65e0\u6687\u548c\u6885\u83f2\u65af\u8bf4\u8fd9\u4ef6\u4e8b\uff0c\u4ed6\u66f4\u591a\u8003\u8651\u7684\u662f\uff1a\u6885\u83f2\u65af\u8981\u53bb\u9634\u5f71\u8c37\uff0c\u81ea\u5df1\u600e\u4e48\u529e\uff1f\u7ee7\u7eed\u7559\u5728\u8fd9\u91cc\u81ea\u7136\u6ca1\u9053\u7406\uff0c\u548c\u5979\u4e00\u8d77\u53bb\u4f3c\u4e4e\u4e5f\u4e0d\u73b0\u5b9e\uff0c\u56de\u9634\u9b42\u57ce\uff1f\u90a3\u662f\u743c\u6069\u6781\u529b\u907f\u514d\u7684\u9009\u62e9\uff0c\u4ed6\u6b64\u6b21\u4e4b\u6240\u4ee5\u51fa\u57ce\uff0c\u4e0d\u5c31\u662f\u60f3\u5c3d\u53ef\u80fd\u79bb\u5f00\u90a3\u4e2a\u662f\u975e\u4e4b\u5730\u4e48\u3002</p><p>\u90a3\u4e48\uff0c\u5c31\u8fd9\u6837\u5148\u5728\u5916\u9762\u6e38\u8361\uff0c\u7b49\u5047\u671f\u7ed3\u675f\u4e86\u518d\u4f5c\u6253\u7b97\uff1f\u81ea\u5df1\u5012\u662f\u65e0\u6240\u8c13\uff0c\u4f46\u96be\u9053\u8ba9\u59d0\u59d0\u73ca\u5609\u4e5f\u8ddf\u7740\u81ea\u5df1\u8fd9\u6837\u5954\u6ce2\u6d41\u6d6a\uff0c\u5c45\u65e0\u5b9a\u6240\uff1f</p><p>\u6b63\u5f53\u4ed6\u5de6\u53f3\u4e3a\u96be\u4e4b\u9645\uff0c\u6885\u83f2\u65af\u9759\u9759\u5f00\u53e3\u4e86\u3002</p><p>\u201c\u5176\u5b9e\u5982\u679c\u4f60\u4e0d\u4ecb\u610f\u7684\u8bdd\uff0c\u743c\u6069\uff0c\u6211\u60f3\u9080\u8bf7\u73ca\u5609\u59d0\u59d0\u5728\u6211\u8fd9\u91cc\u591a\u4f4f\u4e00\u6bb5\u65f6\u95f4\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u5979\u6b63\u5728\u5b66\u4e60\u9b54\u6cd5\uff0c\u4e0d\u9002\u5408\u8ddf\u7740\u4f60\u5728\u5916\u5954\u6ce2\u3002\u5854\u745f\u8c37\u98ce\u666f\u4e0d\u9519\uff0c\u5f88\u5b89\u9759\uff0c\u800c\u4e14\u5b89\u5168\uff0c\u662f\u4e2a\u6f5c\u5fc3\u953b\u70bc\u7684\u597d\u5730\u65b9\uff0c\u6bd4\u9634\u9b42\u57ce\u597d\u5f97\u591a\uff0c\u751f\u6d3b\u4e0a\u4e5f\u65b9\u4fbf\u3002\u6211\u5f88\u4e45\u6ca1\u89c1\u5230\u73ca\u5609\u59d0\u59d0\u4e86\uff0c\u8fd9\u51e0\u5929\u53c8\u5fd9\uff0c\u90fd\u6ca1\u62bd\u7a7a\u4e13\u95e8\u53bb\u770b\u5979\uff0c\u633a\u4e0d\u597d\u610f\u601d\u7684\uff0c\u4e0d\u5982\u8bf7\u5979\u591a\u4f4f\u4e00\u6bb5\u65f6\u95f4\uff0c\u7b49\u6211\u89e3\u51b3\u4e86\u9634\u5f71\u8c37\u90a3\u8fb9\u7684\u4e8b\u60c5\u5c31\u9a6c\u4e0a\u56de\u6765\u966a\u5979\uff0c\u201d\u5979\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u597d\u4e48\uff0c\u743c\u6069\uff1f\u201d</p></article>"}}}]);
