(window.webpackJsonp=window.webpackJsonp||[]).push([[1735],{1758:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u4e5d\u8282 \u76f2\u5973\u7684\u6545\u4e8b",content:"<article><h2>\u7b2c\u4e94\u5341\u4e5d\u8282 \u76f2\u5973\u7684\u6545\u4e8b</h2><p>\u743c\u6069\u6c89\u9ed8\u4e86\u7247\u523b\uff0c\u201c\u8c22\u8c22\u3002\u201d\u4ed6\u4f4e\u58f0\u8bf4\u3002</p><p>\u81ea\u4ece\u79bb\u5f00\u9634\u9b42\u57ce\uff0c\u73ca\u5609\u5982\u4f55\u5b89\u7f6e\uff0c\u4e00\u76f4\u662f\u6a2a\u4e98\u5728\u4ed6\u5fc3\u5934\u7684\u96be\u9898\u3002\u8ba9\u5979\u8ddf\u968f\u81ea\u5df1\u56db\u5904\u5954\u6ce2\uff0c\u65e2\u8f9b\u82e6\u53c8\u5371\u9669\uff0c\u800c\u4e14\u5e76\u975e\u957f\u4e45\u4e4b\u8ba1\uff0c\u81f3\u5c11\u5728\u5979\u9b54\u6cd5\u771f\u6b63\u5927\u6210\u4e4b\u524d\u662f\u5982\u6b64\uff1b\u4f46\u76ee\u524d\u9664\u4e86\u9634\u9b42\u57ce\u4e4b\u5916\uff0c\u53c8\u6ca1\u6709\u5176\u4ed6\u5408\u9002\u5e76\u4e14\u5b89\u5168\u7684\u5c45\u6240\u3002\u4ed6\u6765\u5854\u745f\u8c37\uff0c\u5176\u5b9e\u5fc3\u91cc\u5c31\u5b58\u4e86\u4e00\u4e2a\u9690\u853d\u7684\u60f3\u6cd5\uff0c\u5373\u662f\u8ba9\u73ca\u5609\u5728\u6885\u83f2\u65af\u5bb6\u501f\u4f4f\u4e00\u6bb5\u65f6\u95f4\uff0c\u7b49\u5f62\u52bf\u5b89\u5b9a\u540e\u518d\u505a\u6253\u7b97\u3002\u53ea\u662f\u8fd9\u4ef6\u4e8b\u672a\u514d\u6709\u70b9\u592a\u5c34\u5c2c\uff0c\u6885\u83f2\u65af\u53c8\u4e0d\u662f\u4e0d\u77e5\u9053\u743c\u6069\u548c\u73ca\u5609\u7684\u771f\u5b9e\u5173\u7cfb\u3002\u8eab\u4e3a\u7537\u4eba\uff0c\u65e0\u6cd5\u4e3a\u81ea\u5df1\u7684\u59d0\u59d0\u60c5\u4eba\u63d0\u4f9b\u5b89\u5168\u3001\u53ef\u9760\u7684\u4fdd\u62a4\uff0c\u53cd\u800c\u8981\u501f\u52a9\u5973\u53cb\u7684\u529b\u91cf\uff0c\u8fd9\u5df2\u7ecf\u4e0d\u662f\u65e0\u80fd\uff0c\u5dee\u4e0d\u591a\u662f\u65e0\u803b\u4e86\u3002\u743c\u6069\u8138\u76ae\u4e00\u5411\u5f88\u539a\uff0c\u4f46\u8fd8\u6ca1\u539a\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u59cb\u7ec8\u4e0d\u6562\u5f53\u771f\u5411\u6885\u83f2\u65af\u63d0\u8d77\u3002\u6b63\u81ea\u8e0c\u8e87\u65e0\u8ba1\uff0c\u5374\u4e0d\u6599\u6885\u83f2\u65af\u4e3b\u52a8\u63d0\u8bae\uff0c\u9080\u8bf7\u73ca\u5609\u5728\u6b64\u201c\u591a\u4f4f\u4e00\u6bb5\u65f6\u95f4\u201d\uff0c\u89e3\u9664\u4e86\u743c\u6069\u6700\u5927\u7684\u540e\u987e\u4e4b\u5fe7\u3002</p><p>\u6885\u83f2\u65af\u8fd9\u4e48\u505a\uff0c\u81ea\u7136\u4e0d\u662f\u56e0\u4e3a\u5979\u8bf4\u7684\u90a3\u6837\uff0c\u771f\u7684\u662f\u56e0\u4e3a\u60f3\u5ff5\u73ca\u5609\u3002\u5979\u4e0e\u73ca\u5609\u5173\u7cfb\u5c1a\u53ef\uff0c\u4f46\u4e5f\u4ec5\u4ec5\u5c31\u662f\u201c\u5c1a\u53ef\u201d\u800c\u5df2\uff0c\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u6df1\u4ea4\uff0c\u60c5\u654c\u7684\u8eab\u4efd\uff0c\u5c31\u5df2\u7ecf\u6ce8\u5b9a\u4e86\u5148\u5929\u7acb\u573a\u4e0a\u7684\u5bf9\u7acb\u3002\u66f4\u522b\u63d0\u73ca\u5609\u7684\u8eab\u540e\uff0c\u8fd8\u6709\u4e00\u4f4d\u66fe\u7ecf\u8bd5\u56fe\u8c0b\u6740\u5979\u7684\u5e7d\u7075\u5973\u5deb\u3002\u5979\u4e4b\u6240\u4ee5\u8fd9\u4e48\u505a\uff0c\u53ea\u53ef\u80fd\u6709\u4e00\u4e2a\u539f\u56e0\uff0c\u4fbf\u662f\u770b\u51fa\u4e86\u743c\u6069\u7684\u5fc3\u601d\u3002</p><p>\u542c\u5230\u4ed6\u7684\u9053\u8c22\uff0c\u5c11\u5973\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u7b28\u86cb\uff0c\u8c22\u4ec0\u4e48\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5979\u662f\u4f60\u59d0\u59d0\u561b\u3002\u201d</p><p>\u4e0d\u5351\u4e0d\u4ea2\u7684\u63aa\u8f9e\u548c\u59ff\u6001\uff0c\u65e2\u4f53\u8d34\u5730\u4e3b\u52a8\u66ff\u7537\u53cb\u89e3\u56f4\uff0c\u53c8\u4e0d\u5931\u950b\u8292\u5730\u70b9\u660e\u81ea\u5df1\u7684\u6001\u5ea6\uff1a\u201c\u6211\u4e4b\u6240\u4ee5\u4f1a\u5e2e\u5fd9\uff0c\u53ea\u56e0\u4e3a\u5979\u662f\u4f60\u59d0\u59d0\u201d\u3002\u6210\u529f\u5730\u8d62\u53d6\u7537\u53cb\u7684\u611f\u6fc0\u548c\u6b49\u610f\uff0c\u52a0\u6df1\u5f7c\u6b64\u611f\u60c5\uff0c\u540c\u65f6\u4e5f\u6ca1\u6709\u8868\u73b0\u51fa\u4efb\u4f55\u9000\u8ba9\u548c\u59a5\u534f\uff0c\u786e\u5b9e\u662f\u975e\u5e38\u5de7\u5999\u7684\u505a\u6cd5\u3002\u770b\u6765\u51db\u4f3c\u4e4e\u6ca1\u8bf4\u9519\uff0c\u827e\u5f25\u8587\u786e\u5b9e\u662f\u67d0\u4e9b\u5730\u65b9\u6539\u53d8\u4e86\uff1b\u4ee5\u524d\u7684\u5979\u867d\u7136\u806a\u660e\uff0c\u5374\u4e3b\u8981\u504f\u4e8e\u5bf9\u201c\u4e8b\u201d\uff0c\u5728\u5904\u7406\u4eba\u9645\u5173\u7cfb\u4e0a\uff0c\u5979\u662f\u4ece\u6765\u90fd\u61d2\u5f97\u82b1\u5fc3\u601d\u7684\u5427\u3002</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u89e3\u51b3\u4e86\u4e00\u6869\u5fc3\u4e8b\uff0c\u743c\u6069\u8fd8\u662f\u5f88\u9ad8\u5174\u7684\u3002\u6885\u83f2\u65af\u65e2\u7136\u5df2\u7ecf\u4e3b\u52a8\u53d1\u51fa\u9080\u8bf7\uff0c\u63a5\u4e0b\u6765\u53ea\u8981\u8bf4\u670d\u73ca\u5609\u540c\u610f\u5c31\u597d\uff0c\u8fd9\u4e2a\u5de5\u4f5c\u6709\u4e9b\u96be\u5ea6\uff0c\u4f46\u4e0d\u9ad8\uff0c\u5c5e\u4e8e\u743c\u6069\u8ba4\u4e3a\u201c\u53ef\u4ee5\u89e3\u51b3\u201d\u7684\u8303\u7574\u3002\u770b\u770b\u65f6\u95f4\uff0c\u5df2\u7ecf\u662f\u4e0b\u5348\u56db\u70b9\u591a\u949f\uff0c\u53a8\u623f\u91cc\u7684\u665a\u9910\u4e5f\u5feb\u8981\u51c6\u5907\u597d\u4e86\u3002\u743c\u6069\u6536\u8d77\u9b54\u6cd5\u4e66\uff0c\u4e24\u4eba\u968f\u53e3\u95f2\u804a\u3002\u201c\u4eca\u5929\u966a\u51db\u53bb\u661f\u4e4b\u82b1\uff0c\u6709\u4ec0\u4e48\u6536\u83b7\uff1f\u201d\u6885\u83f2\u65af\u4f3c\u4e4e\u4e0d\u7ecf\u610f\u5730\u95ee\uff0c\u201c\u6709\u53d1\u73b0\u4e2d\u610f\u7684\u5973\u5b69\u5417\uff1f\u201d</p><p>\u201c\u2026\u2026\u6ca1\u6709\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u771f\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\u5012\u662f\u6709\u4e2a\u4eba\uff0c\u6211\u89c9\u5f97\u6709\u70b9\u7279\u522b\uff0c\u4e0d\u77e5\u9053\u4f60\u662f\u5426\u8ba4\u8bc6\u3002\u201d</p><p>\u201c\u600e\u4e48\u4e2a\u7279\u522b\u6cd5\uff1f\u201d</p><p>\u201c\u5979\u662f\u4e2a\u76f2\u4eba\u3002\u201d</p><p>\u201c\u76f2\u4eba\uff1f\u201d\u6885\u83f2\u65af\u4e00\u6014\uff0c\u201c\u838e\u7433\u5a1c\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u77e5\u9053\u5979\u7684\u540d\u5b57\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e2a\u5b50\u633a\u9ad8\uff0c\u817f\u5c24\u5176\u957f\uff0c\u6bd4\u4f8b\u5f88\u5b8c\u7f8e\uff0c\u80f8\u4e0d\u5927\uff0c\u5e94\u8be5\u6709\u70b9\u7cbe\u7075\u8840\u7edf\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u9759\u9759\u542c\u4ed6\u63cf\u8ff0\u5b8c\uff0c\u7136\u540e\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u662f\u838e\u7433\u5a1c\uff0c\u201d\u5979\u8bf4\uff0c\u5ffd\u7136\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u6ca1\u60f3\u5230\u2026\u2026\u5979\u7ec8\u4e8e\u80af\u8d70\u51fa\u90a3\u95f4\u623f\u5b50\u4e86\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d\u743c\u6069\u4e0d\u89e3\uff0c\u201c\u4f60\u548c\u5979\u5f88\u719f\u6089\uff1f\u201d</p><p>\u201c\u4e0d\u719f\u6089\uff0c\u201d\u6885\u83f2\u65af\u6447\u6447\u5934\uff0c\u201c\u5176\u5b9e\u6211\u548c\u5979\u53ea\u89c1\u8fc7\u4e00\u6b21\u9762\uff0c\u90a3\u8fd8\u662f\u4e94\u5e74\u524d\u7684\u4e8b\u60c5\u4e86\u3002\u201d</p><p>\u201c\u90a3\u4f60\u521a\u624d\u8bf4\u7684\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u6885\u83f2\u65af\u6c89\u9ed8\u7247\u523b\uff0c\u201c\u838e\u7433\u5a1c\u2026\u2026\u4f60\u77e5\u9053\u5979\u7684\u773c\u775b\u662f\u600e\u4e48\u76f2\u6389\u7684\u5417\uff1f\u201d</p><p>\u201c\u4e0d\u662f\u5929\u751f\u7684\uff1f\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u5c11\u5973\u4f4e\u58f0\u8bf4\uff0c\u201c\u662f\u88ab\u4eba\u525c\u6389\u7684\u3002\u201d</p><p>\u201c\u8c01\u8fd9\u4e48\u6b8b\u5fcd\uff1f\u201d</p><p>\u201c\u5979\u7236\u4eb2\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u8bed\u6c14\u5e73\u9759\uff0c\u4eff\u4f5b\u53ea\u662f\u5728\u8bf4\u4e00\u4ef6\u5e73\u5e38\u4e4b\u6781\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u5374\u542c\u5f97\u6bdb\u9aa8\u609a\u7136\uff0c\u7981\u4e0d\u4f4f\u6fc0\u7075\u7075\u5730\u6253\u4e86\u4e2a\u5bd2\u98a4\uff0c\u201c\u5979\u7236\u4eb2\uff1f\u201d\u4ed6\u6709\u4e9b\u4e0d\u6562\u7f6e\u4fe1\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u5c11\u5973\u7684\u8138\u4e0a\u9732\u51fa\u660e\u663e\u7684\u618e\u6076\u4e4b\u8272\uff0c\u743c\u6069\u548c\u5979\u76f8\u8bc6\u8fd9\u4e48\u4e45\uff0c\u8fd8\u4ece\u6ca1\u770b\u5230\u8fc7\u5979\u5bf9\u4e00\u4e2a\u4eba\u8868\u73b0\u51fa\u5982\u6b64\u5f3a\u70c8\u7684\u53cd\u611f\u6001\u5ea6\uff0c\u201c\u56e0\u4e3a\u90a3\u4e2a\u4eba\u662f\u4e2a\u767d\u75f4\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u7b80\u660e\u627c\u8981\u5730\uff0c\u6885\u83f2\u65af\u544a\u8bc9\u4e86\u743c\u6069\u6709\u5173\u838e\u7433\u5a1c\u7684\u6545\u4e8b\u3002</p><p>\u838e\u7433\u5a1c\u7684\u539f\u540d\u662f\u201c\u838e\u7433\u5a1c\xb7\u514b\u91cc\u65af\u591a\u592b\u201d\uff0c\u514b\u91cc\u65af\u591a\u592b\u66fe\u7ecf\u662f\u5854\u745f\u8c37\u7684\u540d\u95e8\uff0c\u5176\u5148\u7956\u201c\u65e0\u754f\u8005\u201d\u5965\u5170\xb7\u514b\u91cc\u65af\u591a\u592b\u662f\u5f53\u5e74\u8ffd\u968f\u5361\u7f2a\u5c14\u5973\u58eb\u65a9\u9b54\u5c60\u9f99\uff0c\u5728\u6b64\u5730\u5efa\u7acb\u63d0\u5c14\u795e\u6bbf\u7684\u56db\u4f4d\u5927\u82f1\u96c4\u4e4b\u4e00\uff0c\u53e6\u5916\u4e09\u4eba\u5206\u522b\u662f\u201c\u96c4\u72ee\u201d\u5229\u7ef4\u65af\xb7\u5e9e\u7f57\u3001\u201c\u5929\u4e4b\u5203\u201d\u683c\u745e\u65af\xb7\u4e4c\u745f\u5c14\u548c\u201c\u7f04\u9ed8\u7684\u5deb\u5e08\u201d\u6258\u9a6c\u65af\xb7\u6c49\u5f25\u5c14\u987f\u3002\u5965\u5170\u4e3a\u4eba\u6027\u683c\u575a\u6bc5\uff0c\u767e\u6298\u4e0d\u6320\uff0c\u65e0\u8bba\u8eab\u5904\u4f55\u7b49\u8270\u96be\u9006\u5883\uff0c\u9762\u5bf9\u4f55\u79cd\u632b\u6298\u9669\u963b\uff0c\u6c38\u8fdc\u4e3e\u5251\u5411\u524d\uff0c\u7edd\u65e0\u9000\u7f29\uff0c\u6c38\u4e0d\u653e\u5f03\uff0c\u88ab\u5361\u7f2a\u5c14\u5973\u58eb\u8d5e\u8bb8\u4e3a\u201c\u6700\u53ef\u9760\u7684\u4f19\u4f34\u201d\u3002\u6559\u4f1a\u5efa\u7acb\u540e\uff0c\u5965\u5170\u62c5\u4efb\u526f\u4e3b\u6559\uff0c\u83b7\u8d60\u56db\u5143\u7d20\u5251\u4e2d\u7684\u201c\u706b\u4e4b\u5251\u201d\uff0c\u5176\u5730\u4f4d\u58f0\u671b\u4ec5\u6b21\u4e8e\u5361\u7f2a\u5c14\uff0c\u8fd8\u5728\u5176\u4ed6\u4e09\u82f1\u96c4\u4e4b\u4e0a\u3002</p><p>\u7136\u800c\u968f\u7740\u65f6\u5149\u6d41\u901d\uff0c\u5148\u7956\u7684\u8f89\u714c\u4e5f\u5728\u4e0d\u77e5\u4e0d\u89c9\u95f4\u9eef\u6de1\u78e8\u706d\u3002\u51e0\u767e\u5e74\u6765\uff0c\u514b\u91cc\u65af\u591a\u592b\u5bb6\u65cf\u4e0d\u65ad\u5f0f\u5fae\u8870\u843d\uff0c\u5230\u540e\u6765\u751a\u81f3\u51e0\u4ee3\u90fd\u51fa\u4e0d\u4e86\u4e00\u4f4d\u9ad8\u9636\u7267\u5e08\uff0c\u53cd\u89c2\u5f53\u5e74\u56db\u82f1\u96c4\u4e2d\u7684\u5176\u4ed6\u4e09\u4f4d\uff0c\u5e9e\u7f57\u3001\u4e4c\u745f\u5c14\u5bb6\u65cf\u84b8\u84b8\u65e5\u4e0a\uff0c\u6559\u4f1a\u9ad8\u5c42\u7684\u4e00\u534a\u4ee5\u4e0a\u6210\u5458\u5747\u51fa\u81ea\u8fd9\u4e24\u5927\u5bb6\u65cf\uff0c\u6c49\u5f25\u5c14\u987f\u628a\u81ea\u5df1\u53d8\u6210\u4e86\u5deb\u5996\uff0c\u6df1\u5c45\u7b80\u51fa\uff0c\u57fa\u672c\u5df2\u7ecf\u88ab\u9057\u5fd8\uff0c\u4f3c\u4e4e\u4e5f\u6df7\u5f97\u5f88\u5dee\uff0c\u4f46\u4ed6\u81f3\u5c11\u8fd8\u662f\u6559\u4f1a\u7684\u5927\u957f\u8001\u3002\u9762\u5bf9\u8fd9\u79cd\u5c40\u9762\uff0c\u514b\u91cc\u65af\u591a\u592b\u7684\u5b50\u5b59\u4eec\u81ea\u7136\u4e0d\u80fd\u7518\u5fc3\uff0c\u838e\u7433\u5a1c\u7684\u7236\u4eb2\u5eb7\u94ce\u4e43\u662f\u5bb6\u65cf\u76f4\u7cfb\uff0c\u81ea\u5e7c\u4fbf\u6000\u6709\u5927\u5fd7\uff0c\u60f3\u8981\u91cd\u632f\u7956\u4e0a\u58f0\u5a01\uff0c\u6210\u4e3a\u4e3e\u4e16\u77a9\u76ee\u7684\u82f1\u96c4\u3002\u53ef\u60dc\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u5f80\u5f80\u5e76\u4e0d\u662f\u52aa\u529b\u5c31\u4e00\u5b9a\u6709\u56de\u62a5\u7684\u3002\u5eb7\u94ce\u80fd\u529b\u5e73\u5e73\uff0c\u5954\u6ce2\u534a\u751f\u4e5f\u4e00\u65e0\u6240\u83b7\uff0c\u773c\u770b\u81ea\u8eab\u5df2\u7ecf\u65e0\u671b\uff0c\u4fbf\u628a\u4e3b\u610f\u6253\u5230\u4e86\u5b50\u5973\u8eab\u4e0a\u3002</p><p>\u514b\u91cc\u65af\u591a\u592b\u5bb6\u65cf\u8870\u8d25\u7684\u4e00\u4e2a\u91cd\u8981\u539f\u56e0\u5c31\u662f\u5b50\u55e3\u4e0d\u65fa\uff0c\u5eb7\u94ce\u5e74\u8fc7\u56db\u5341\uff0c\u819d\u4e0b\u4ec5\u6709\u838e\u7433\u5a1c\u8fd9\u4e00\u4e2a\u5973\u513f\uff0c\u522b\u65e0\u5b50\u55e3\u3002\u838e\u7433\u5a1c\u806a\u660e\u7f8e\u4e3d\uff0c\u5374\u5e76\u6ca1\u6709\u4ec0\u4e48\u6b66\u6280\u6216\u8005\u9b54\u6cd5\u4e0a\u7684\u5929\u8d4b\uff0c\u53ea\u662f\u4e2a\u666e\u901a\u5973\u5b50\uff0c\u5bf9\u4ec0\u4e48\u632f\u5174\u5bb6\u4e1a\u3001\u6210\u4e3a\u82f1\u96c4\u4e4b\u7c7b\u7684\u4e8b\u60c5\u4e5f\u5168\u65e0\u534a\u70b9\u5174\u8da3\uff0c\u5c3d\u7ba1\u5728\u7236\u4eb2\u7684\u5f3a\u70c8\u8981\u6c42\u4e0b\uff0c\u5979\u52a0\u5165\u6559\u4f1a\uff0c\u975e\u5e38\u52aa\u529b\u5730\u5b66\u4e60\u3001\u953b\u70bc\uff0c\u7136\u800c\u8fdb\u6b65\u6781\u5fae\uff0c\u5728\u51e0\u6b21\u8003\u6838\u4e2d\uff0c\u838e\u7433\u5a1c\u90fd\u672a\u80fd\u5982\u540c\u4f34\u4e00\u822c\u664b\u9636\uff0c\u59cb\u7ec8\u505c\u7559\u5728\u201c\u89c1\u4e60\u201d\u7684\u5c42\u7ea7\u4e0a\uff0c\u8fd9\u8ba9\u5979\u7684\u7236\u4eb2\u6781\u5ea6\u5931\u671b\u3002</p><p>1363DR\uff0c\u838e\u7433\u5a1c\u7684\u6bcd\u4eb2\u56e0\u75c5\u53bb\u4e16\uff0c\u53d7\u6b64\u6253\u51fb\uff0c\u5eb7\u94ce\u7684\u7cbe\u795e\u72b6\u6001\u5f00\u59cb\u53d8\u5f97\u660e\u663e\u4e0d\u6b63\u5e38\u3002\u5f3a\u70c8\u7684\u523a\u6fc0\u548c\u5bf9\u672a\u6765\u7684\u5f7b\u5e95\u5931\u671b\uff0c\u8ba9\u8fd9\u4f4d\u63d0\u5c14\u7267\u5e08\u9010\u6e10\u5760\u5165\u75af\u72c2\uff0c\u6700\u7ec8\u505a\u51fa\u4e86\u9a87\u4eba\u542c\u95fb\u7684\u6076\u884c\u3002</p><p>\u201c\u4ed6\u53d1\u75af\u4e86\uff0c\u7136\u540e\u628a\u81ea\u5df1\u5973\u513f\u7684\u773c\u775b\u7ed9\u525c\u6389\u4e86\uff1f\u201d\u743c\u6069\u60ca\u95ee\u3002</p><p>\u6885\u83f2\u65af\u6447\u6447\u5934\uff0c\u201c\u6ca1\u90a3\u4e48\u7b80\u5355\u3002\u201d</p><p>\u4e8b\u540e\u4eba\u4eec\u624d\u77e5\u9053\uff0c\u539f\u6765\u5eb7\u94ce\u7684\u75af\u72c2\uff0c\u62db\u6765\u4e86\u90aa\u6076\u7684\u89ca\u89ce\uff0c\u4e00\u53ea\u6765\u81ea\u4e5d\u5c42\u5730\u72f1\u7684\u9b54\u9b3c\u6ce8\u610f\u5230\u4ed6\uff0c\u5e76\u901a\u8fc7\u5de7\u5999\u7684\u624b\u6bb5\u6210\u4e3a\u4ed6\u7684\u201c\u670b\u53cb\u201d\u3002\u8fd9\u4f4d\u65b0\u670b\u53cb\u544a\u8bc9\u5eb7\u94ce\uff1a\u4ed6\u548c\u838e\u7433\u5a1c\u7236\u5973\u4e24\u4eba\u5176\u5b9e\u90fd\u62e5\u6709\u975e\u5e38\u4f18\u79c0\u7684\u6f5c\u8d28\uff0c\u5b8c\u5168\u5e94\u8be5\u6210\u4e3a\u6700\u4f18\u79c0\u7684\u5927\u7267\u5e08\uff0c\u4e4b\u6240\u4ee5\u8868\u73b0\u4e0d\u4f73\uff0c\u4e43\u662f\u56e0\u4e3a\u6ca1\u6709\u5411\u795e\u7947\u8868\u73b0\u51fa\u8db3\u591f\u7684\u8654\u8bda\u548c\u575a\u5b9a\uff0c\u6240\u4ee5\u672a\u80fd\u5f97\u5230\u8ba4\u53ef\u3002\u800c\u6240\u8c13\u201c\u8db3\u591f\u7684\u8654\u8bda\u548c\u575a\u5b9a\u201d\uff0c\u5373\u662f\u50cf\u63d0\u5c14\u4e00\u6837\uff0c\u525c\u53bb\u53cc\u773c\uff0c\u5e76\u4e14\u65a9\u65ad\u53f3\u624b\u3002</p><p>\u201c\u75af\u4e86\uff01\u201d\u743c\u6069\u4e0d\u53ef\u601d\u8bae\u5730\u6447\u5934\uff0c\u201c\u8fd9\u79cd\u8352\u8bde\u7684\u8bdd\uff0c\u5c45\u7136\u4e5f\u4f1a\u6709\u4eba\u76f8\u4fe1\u5417\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u6240\u4ee5\u4ed6\u5df2\u7ecf\u4e0d\u80fd\u7b97\u662f\u4eba\u4e86\u3002\u201d</p><p>\u4e8b\u5b9e\u8bc1\u660e\uff0c\u8fd9\u4e2a\u4e16\u754c\u6c38\u8fdc\u6bd4\u60f3\u8c61\u5f97\u66f4\u8352\u8bde\uff0c\u66f4\u79bb\u5947\u2014\u2014\u6216\u8005\u8bf4\uff0c\u6c38\u8fdc\u4e0d\u8981\u9ad8\u4f30\u4eba\u7c7b\u667a\u5546\u7684\u4e0b\u9650\u3002\u5df2\u7ecf\u88ab\u9b54\u9b3c\u5f7b\u5e95\u8ff7\u60d1\u7684\u5eb7\u94ce\uff0c\u5f53\u771f\u76f8\u4fe1\u4e86\u8fd9\u79cd\u9b3c\u8bdd\uff0c\u5e76\u4e14\u4ed8\u8bf8\u5b9e\u65bd\u3002\u4ed6\u5f53\u771f\u525c\u53bb\u81ea\u5df1\u7684\u53cc\u773c\uff0c\u65a9\u65ad\u53f3\u624b\uff0c\u7136\u540e\u5728\u6781\u5ea6\u7684\u75db\u82e6\u4e2d\uff0c\u4ed6\u771f\u7684\u770b\u89c1\u4e86\u795e\u7947\u5728\u707f\u70c2\u7684\u5149\u8f89\u4e2d\u51fa\u73b0\uff0c\u8d50\u4e88\u4ed6\u524d\u6240\u672a\u6709\u7684\u5e9e\u5927\u529b\u91cf\u3002</p><p>\u201c\u90a3\u662f\u9b54\u9b3c\u5427\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u5f53\u7136\u3002\u201d</p><p>\u663e\u800c\u6613\u89c1\u7684\u9a97\u5c40\uff0c\u7136\u800c\u5df2\u7ecf\u9677\u5165\u75af\u72c2\u4e2d\u7684\u5eb7\u94ce\u5e76\u672a\u8bc6\u7834\uff0c\u9075\u4ece\u201c\u795e\u7947\u201d\u7684\u8c15\u793a\uff0c\u4ed6\u5e26\u7740\u838e\u7433\u5a1c\u6765\u5230\u7279\u52a0\u5c14\u9547\u5916\u7684\u4e00\u5ea7\u8352\u5c71\u4e0a\uff0c\u6253\u7b97\u8fdb\u884c\u4e00\u9879\u795e\u5723\u4eea\u5f0f\uff0c\u5c06\u81ea\u5df1\u7684\u5973\u513f\u8f6c\u5316\u4e3a\u5149\u660e\u5929\u4f7f\u2014\u2014\u5b9e\u9645\u4e0a\uff0c\u5b83\u4f1a\u8ba9\u838e\u7433\u5a1c\u53d8\u6210\u4e00\u53ea\u6b32\u9b54\u3002</p><p>\u201c\u5e78\u597d\uff0c\u8fd9\u4e2a\u8ba1\u5212\u88ab\u6b4c\u66e6\u96c5\u53d1\u73b0\u4e86\u3002\u201d</p><p>\u6b4c\u66e6\u96c5\u4e0e\u838e\u7433\u5a1c\u90fd\u662f\u5854\u745f\u8c37\u8fdc\u8fd1\u95fb\u540d\u7684\u7f8e\u4eba\uff0c\u5e74\u9f84\u76f8\u4eff\uff0c\u5e76\u79f0\u201c\u53cc\u661f\u201d\uff0c\u8981\u8bba\u53d7\u6b22\u8fce\u7a0b\u5ea6\uff0c\u540e\u8005\u8fd8\u8981\u7565\u80dc\u4e00\u7b79\uff0c\u6bd5\u7adf\u6b4c\u66e6\u96c5\u548c\u6885\u83f2\u65af\u4e00\u6837\uff0c\u6c14\u8d28\u90fd\u504f\u4e8e\u521a\u786c\u51b0\u51b7\uff0c\u7f8e\u5219\u7f8e\u77e3\uff0c\u5374\u8ba9\u4eba\u4e0d\u6562\u4eb2\u8fd1\uff0c\u838e\u7433\u5a1c\u5219\u8981\u5f00\u6717\u6d3b\u6cfc\u5f97\u591a\u3002\u5979\u4eec\u7684\u79c1\u4ea4\u4e5f\u4e0d\u9519\uff0c\u662f\u5f88\u8981\u597d\u7684\u670b\u53cb\uff0c\u6240\u4ee5\u838e\u7433\u5a1c\u66fe\u7ecf\u5bf9\u6b4c\u66e6\u96c5\u63d0\u53ca\u8fc7\u4e00\u4e9b\u81ea\u5df1\u7236\u4eb2\u7684\u201c\u5947\u602a\u201d\u884c\u4e3a\u3002\u90a3\u4e00\u65e5\uff0c\u6b4c\u66e6\u96c5\u89c1\u838e\u7433\u5a1c\u6ca1\u6709\u50cf\u5f80\u5e38\u4e00\u6837\u6765\u795e\u6bbf\uff0c\u4ee5\u4e3a\u5979\u8eab\u4f53\u4e0d\u9002\uff0c\u4fbf\u53bb\u4e86\u5979\u5bb6\u770b\u671b\uff0c\u7ed3\u679c\u610f\u8bc6\u5230\u4e0d\u5bf9\u52b2\uff0c\u5faa\u8ff9\u8ffd\u81f3\u5c71\u4e0a\uff0c\u53d1\u73b0\u4e86\u4e8b\u5b9e\u7684\u771f\u76f8\u3002\u53ef\u60dc\u7ec8\u7a76\u8fd8\u662f\u665a\u4e86\u4e00\u6b65\uff0c\u838e\u7433\u5a1c\u5df2\u7ecf\u88ab\u525c\u53bb\u53cc\u76ee\uff0c\u65a9\u65ad\u53f3\u624b\uff0c\u6fd2\u4e34\u6b7b\u4ea1\uff0c\u800c\u5eb7\u94ce\u4e5f\u5df2\u7ecf\u88ab\u9b54\u9b3c\u5b8c\u5168\u9644\u4f53\u3002</p><p>\u4e00\u573a\u6076\u6218\u540e\uff0c\u6b4c\u66e6\u96c5\u65a9\u6740\u4e86\u53d8\u6210\u90aa\u9b54\u7684\u5eb7\u94ce\uff0c\u6551\u56de\u838e\u7433\u5a1c\u3002\u201c\u518d\u751f\u672f\u201d\u6210\u529f\u7eed\u63a5\u4e0a\u838e\u7433\u5a1c\u7684\u53f3\u624b\uff0c\u4f46\u5bf9\u5979\u5df2\u7ecf\u88ab\u5730\u72f1\u90aa\u529b\u5b8c\u5168\u4fb5\u8680\u7684\u53cc\u773c\uff0c\u518d\u9ad8\u660e\u7684\u7267\u5e08\u4e5f\u56de\u5929\u4e4f\u672f\uff0c\u4fee\u590d\u4e0d\u80fd\u3002</p><p>\u906d\u6b64\u52ab\u96be\uff0c\u838e\u7433\u5a1c\u6027\u60c5\u5927\u53d8\uff0c\u539f\u672c\u90a3\u4e2a\u6d3b\u6cfc\u5f00\u6717\u3001\u9633\u5149\u707f\u70c2\u7684\u5c11\u5973\u5f7b\u5e95\u6d88\u5931\u4e86\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u9634\u90c1\u548c\u81ea\u95ed\u3002\u5979\u628a\u81ea\u5df1\u5173\u5728\u9634\u68ee\u68ee\u7684\u5bb6\u65cf\u53e4\u5b85\u91cc\uff0c\u534a\u6b65\u4e0d\u51fa\uff0c\u4e5f\u4e0d\u89c1\u4efb\u4f55\u6765\u8bbf\u8005\uff0c\u5305\u62ec\u6614\u65e5\u7684\u597d\u53cb\u6b4c\u66e6\u96c5\u3002\u6885\u83f2\u65af\u662f\u4e94\u5e74\u524d\u7b2c\u4e00\u6b21\u6765\u5854\u745f\u8c37\u65f6\uff0c\u8d70\u9519\u4e86\u8def\u8bef\u5165\u5979\u5bb6\u4e2d\uff0c\u4e24\u4eba\u6253\u4e86\u4e2a\u7167\u9762\uff0c\u6b64\u540e\u518d\u6ca1\u6253\u8fc7\u4ea4\u9053\u3002\u636e\u8bf4\u5c31\u8fde\u5bb6\u4e2d\u7684\u4ec6\u4eba\u5e73\u65f6\u90fd\u89c1\u4e0d\u5230\u5979\uff0c\u4e00\u65e5\u4e09\u9910\u76f4\u63a5\u9001\u81f3\u623f\u95f4\u91cc\uff0c\u800c\u4e14\u5979\u7684\u623f\u95f4\u91cc\u4ece\u4e0d\u5f00\u706f\uff0c\u7a97\u6237\u7528\u5c01\u6761\u9489\u6b7b\uff0c\u906e\u4e0a\u539a\u539a\u7684\u7a97\u5e18\uff0c\u65e0\u8bba\u767d\u5929\u9ed1\u591c\uff0c\u6c38\u8fdc\u90fd\u662f\u9ed1\u6697\u4e00\u7247\uff0c\u9634\u68ee\u53ef\u6016\u3002</p><p>\u201c\u5f88\u6b63\u5e38\u5427\uff0c\u6b63\u503c\u5999\u9f84\u7684\u5c11\u5973\uff0c\u7a81\u7136\u773c\u775b\u778e\u4e86\uff0c\u8fd8\u662f\u88ab\u81ea\u5df1\u7236\u4eb2\u2026\u2026\u8fd9\u79cd\u4e8b\u60c5\uff0c\u4e0d\u7ba1\u53d1\u751f\u5728\u8c01\u8eab\u4e0a\u90fd\u53d7\u4e0d\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u4ec5\u4ec5\u662f\u773c\u775b\u778e\u4e86\uff0c\u636e\u8bf4\u2026\u2026\u201d\u6885\u83f2\u65af\u8fdf\u7591\u4e86\u51e0\u79d2\u949f\uff0c\u201c\u53cd\u6b63\u786e\u5b9e\u662f\u53d7\u5230\u4e86\u975e\u5e38\u4e25\u91cd\u7684\u521b\u4f24\uff0c\u6240\u4ee5\u4e00\u76f4\u6062\u590d\u4e0d\u8fc7\u6765\u3002\u201d</p><p>\u838e\u7433\u5a1c\u8fd9\u4e00\u95ed\u95e8\u5c31\u662f\u6574\u6574\u5341\u5e74\uff0c\u51e0\u4e4e\u88ab\u4eba\u9057\u5fd8\u3002\u8fd9\u6bb5\u6545\u4e8b\u592a\u8fc7\u8840\u8165\u6b8b\u5fcd\uff0c\u77e5\u60c5\u8005\u5927\u591a\u4e0d\u613f\u63d0\u53ca\uff0c\u800c\u4e14\u4e00\u4f4d\u540d\u95e8\u4e4b\u540e\u3001\u63d0\u5c14\u7267\u5e08\u88ab\u9b54\u9b3c\u8bf1\u60d1\u5815\u843d\uff0c\u505a\u51fa\u8fd9\u6837\u7684\u66b4\u884c\uff0c\u4e5f\u7edd\u4e0d\u662f\u4ec0\u4e48\u5149\u5f69\u7684\u4e8b\u60c5\uff0c\u6559\u4f1a\u65b9\u9762\u81ea\u7136\u4e5f\u4e0d\u4f1a\u5230\u5904\u5ba3\u4f20\u3002\u5982\u4eca\u5e74\u8f7b\u4e00\u4e9b\u7684\u5c45\u6c11\uff0c\u5f88\u591a\u90fd\u5df2\u7ecf\u4e0d\u77e5\u9053\u8fc7\u53bb\u8fd8\u6709\u8fd9\u6837\u4e00\u4e2a\u4eba\u5b58\u5728\uff0c\u5f53\u4ed6\u4eec\u4e3a\u6b4c\u66e6\u96c5\u8fde\u5f97\u56db\u5c4a\u661f\u4e4b\u82b1\u800c\u6b22\u547c\u7684\u65f6\u5019\uff0c\u4e5f\u4e0d\u4f1a\u518d\u60f3\u8d77\u90a3\u4f4d\u5f53\u5e74\u4e0e\u5979\u5e76\u79f0\u201c\u53cc\u661f\u201d\u7684\u7f8e\u4e3d\u5c11\u5973\u3002\u6240\u4ee5\u6885\u83f2\u65af\u7a81\u7136\u542c\u8bf4\u5979\u4e5f\u6765\u53c2\u8d5b\uff0c\u624d\u4f1a\u611f\u5230\u90a3\u6837\u60ca\u8bb6\u3002</p><p>\u201c\u770b\u6765\u5979\u7ec8\u4e8e\u8d70\u51fa\u5f80\u65e5\u7684\u9634\u5f71\u4e86\u3002\u201d\u743c\u6069\u8bf4\uff0c\u4f46\u4ed6\u89c9\u5f97\u4e8b\u60c5\u53ea\u6015\u6ca1\u6709\u4ed6\u60f3\u8c61\u5f97\u90a3\u6837\u4e50\u89c2\u3002</p><p>\u201c\u5e0c\u671b\u5982\u6b64\u5427\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u4e24\u4eba\u90fd\u9677\u5165\u6c89\u9ed8\uff0c\u867d\u7136\u5e76\u975e\u81ea\u5df1\u7684\u4eb2\u53cb\uff0c\u4f46\u770b\u5230\u4e00\u4f4d\u539f\u672c\u5982\u82b1\u4f3c\u7389\u7684\u7f8e\u4e3d\u5973\u5b50\u906d\u9047\u8fd9\u6837\u7684\u60b2\u60e8\u5384\u8fd0\uff0c\u5fc3\u60c5\u96be\u514d\u603b\u662f\u6709\u4e9b\u6c89\u91cd\u3002\u201c\u5bf9\u838e\u7433\u5a1c\u7684\u4e8b\u60c5\uff0c\u6b4c\u66e6\u96c5\u4e00\u76f4\u5f88\u81ea\u8d23\uff0c\u201d\u6885\u83f2\u65af\u8f7b\u58f0\u8bf4\uff0c\u201c\u5979\u51e0\u6b21\u5bf9\u6211\u8bf4\uff1a\u5982\u679c\u5979\u5f53\u65f6\u80fd\u65e9\u53d1\u73b0\u7247\u523b\uff0c\u6216\u8005\u80fd\u65e9\u8d76\u5230\u7247\u523b\uff0c\u54ea\u6015\u662f\u4e09\u5206\u949f\uff0c\u90a3\u8fd9\u79cd\u7ed3\u679c\u5c31\u4f1a\u88ab\u907f\u514d\u2026\u2026\u201d</p><p>\u201c\u7b28\u86cb\uff01\u201d\u743c\u6069\u51b7\u51b7\u8bc4\u4ef7\u3002</p><p>\u6885\u83f2\u65af\u4e0d\u9ad8\u5174\u5730\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u60f3\u5f00\u53e3\u53cd\u9a73\uff0c\u5374\u662f\u6b32\u8a00\u53c8\u6b62\uff0c\u6700\u7ec8\u6c89\u6c89\u53f9\u4e86\u53e3\u6c14\u3002\u201c\u6b4c\u66e6\u96c5\u603b\u662f\u5bf9\u81ea\u5df1\u592a\u8fc7\u82db\u523b\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5979\u4e60\u60ef\u4e86\u3002\u201d</p><p>\u201c\u6240\u4ee5\u6211\u8bf4\u662f\u7b28\u86cb\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u54ea\u6709\u90a3\u4e48\u591a\u2018\u5982\u679c\u2019\uff1f\u62b1\u7740\u8fd9\u79cd\u5e7c\u7a1a\u60f3\u6cd5\uff0c\u96be\u602a\u5979\u901a\u4e0d\u8fc7\u5723\u6b66\u58eb\u8bd5\u70bc\u3002\u201d</p><p>\u73b0\u5b9e\u5c31\u662f\u73b0\u5b9e\uff0c\u5df2\u7ecf\u53d1\u751f\u7684\u4e0d\u4f1a\u518d\u6539\u53d8\uff0c\u5df2\u7ecf\u8fc7\u53bb\u7684\u4e0d\u4f1a\u518d\u91cd\u6765\uff0c\u4eba\u6240\u9700\u8981\u505a\u7684\uff0c\u662f\u5766\u7136\u5730\u9762\u5bf9\u3001\u5e73\u9759\u5730\u627f\u8ba4\uff0c\u7136\u540e\u62ac\u8d77\u5934\u5411\u524d\u770b\u3002\u6b4c\u66e6\u96c5\u7684\u60f3\u6cd5\uff0c\u8bf4\u597d\u542c\u70b9\u662f\u5b8c\u7f8e\u4e3b\u4e49\uff0c\u8bf4\u96be\u542c\u70b9\u5c31\u662f\u8111\u7b4b\u6253\u7ed3\uff0c\u53cd\u6b63\u743c\u6069\u662f\u7edd\u4e0d\u4f1a\u6b23\u8d4f\u8fd9\u79cd\u6027\u683c\u3002\u6b63\u56e0\u4e3a\u62b1\u7740\u8fd9\u79cd\u4e0d\u5207\u5b9e\u9645\u7684\u60f3\u6cd5\uff0c\u6240\u4ee5\u5728\u5723\u6b66\u58eb\u8bd5\u70bc\u4e0a\uff0c\u9762\u5bf9\u63d0\u5c14\u63d0\u51fa\u7684\u4e24\u96be\u95ee\u9898\u624d\u4f1a\u5f97\u51fa\u201c\u8981\u8ffd\u6c42\u66f4\u5f3a\u5927\u529b\u91cf\u201d\u7684\u7b54\u6848\uff0c\u6240\u4ee5\u624d\u4f1a\u548c\u6885\u83f2\u65af\u5206\u9053\u626c\u9573\uff0c\u8d70\u5230\u4eca\u5929\u7684\u5730\u6b65\u5427\u3002</p><p>\u6240\u4ee5\u8bf4\uff0c\u6027\u683c\u51b3\u5b9a\u547d\u8fd0\u3002</p><p>\u4ece\u9053\u7406\u4e0a\u8bf4\uff0c\u6885\u83f2\u65af\u7ad9\u5728\u743c\u6069\u8fd9\u8fb9\uff0c\u4f46\u4ece\u611f\u60c5\u4e0a\u6765\u8bf4\uff0c\u5979\u663e\u7136\u4e0d\u559c\u6b22\u542c\u4eba\u8fd9\u6837\u8bc4\u4ef7\u81ea\u5df1\u7684\u6614\u65e5\u670b\u53cb\uff0c\u5c3d\u7ba1\u73b0\u5728\u5df2\u7ecf\u53d8\u6210\u654c\u4eba\u3002\u201c\u4e0d\u8c08\u8fd9\u4e2a\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u65f6\u95f4\u5dee\u4e0d\u591a\uff0c\u8be5\u4e0b\u53bb\u5403\u996d\u4e86\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u8d70\u4e0b\u697c\u68af\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u7a81\u7136\u8bf4\uff1a\u201c\u8fd9\u5c4a\u661f\u4e4b\u82b1\uff0c\u662f\u5e0c\u6b27\u505a\u4e3b\u6301\u4eba\uff1f\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u90a3\u4ed6\u5e94\u8be5\u4e5f\u5f88\u9ad8\u5174\u5427\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u6bd5\u7adf\uff0c\u838e\u7433\u5a1c\u66fe\u7ecf\u662f\u4ed6\u7684\u672a\u5a5a\u59bb\u3002\u201d</p></article>"}}}]);
