(window.webpackJsonp=window.webpackJsonp||[]).push([[174],{197:function(p){p.exports={chapterTitle:null,title:"\u7b2c1151\u7ae0 \u8fd9\u51fa\u620f\u6f14\u7ed9\u8c01\u770b",content:"<article><h2>\u7b2c1151\u7ae0 \u8fd9\u51fa\u620f\u6f14\u7ed9\u8c01\u770b</h2><p>\u3000\u3000\u5f20\u606a\u80cc\u9760\u7740\u5427\u53f0\uff0c\u770b\u7740\u9152\u67dc\u4e0a\u7433\u7405\u6ee1\u76ee\u7684\u9152\u74f6\u4e0e\u60ac\u6302\u5728\u540a\u67b6\u4e0a\u6676\u83b9\u5254\u900f\u7684\u73bb\u7483\u9152\u676f\u3002\u867d\u8bf4\u662f\u53d7\u8fdc\u5728\u65b0\u52a0\u5761\u7684\u9759\u6aac\u7684\u6258\u4ed8\uff0c\u8ffd\u6839\u7a76\u5e95\u8fd8\u662f\u4ed6\u81ea\u5df1\u4e0d\u613f\u610f\u5c061978\u5173\u95ed\u3002</p><p>\u3000\u3000\u4eca\u65e5\u7684\u5b66\u5e9c\u5df7\u65e9\u5c31\u7269\u975e\u4eba\u4ea6\u975e\uff0c\u66fe\u7ecf\u53d1\u751f\u7684\u5386\u53f2\u4e2d\uff0c\u7531\u4e8e\u5b66\u5e9c\u5df7\u7684\u8870\u843d\uff0c1978\u5230\u8fd9\u65f6\u5019\u4e5f\u4f1a\u56e0\u4e3a\u7ecf\u8425\u4e0d\u4e0b\u53bb\u800c\u5173\u95ed\u3002</p><p>\u3000\u3000\u627e\u4e0d\u5230\u5408\u9002\u7684\u4eba\u6253\u7406\uff0c\u4e5f\u8bb8\u5c061978\u5173\u95ed\u6389\u662f\u5408\u9002\u7684\u9009\u62e9\u3002</p><p>\u3000\u3000\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u9648\u5983\u84c9\u89c1\u5f20\u606a\u53d1\u6123\u7684\u770b\u7740\u9152\u67dc\uff0c\u7591\u60d1\u7684\u95ee\u4ed6\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6211\u8d70\u795e\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u81ea\u5df1\u8bf4\u5462\uff1f\u201d\u9648\u5983\u84c9\u5ae3\u7136\u800c\u7b11\uff0c\u8ba9\u5f20\u606a\u5e2e\u7740\u5236\u6a59\u6c41\uff0c\u4eca\u591c\u70b9\u9c9c\u679c\u6c41\u7684\u5ba2\u4eba\u5f88\u591a\uff0c\u8ba9\u5979\u4eec\u75b2\u4e8e\u5e94\u4ed8\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u521a\u624d\u7684\u5ff5\u5934\u629b\u4e0b\uff0c\u5373\u4f7f1978\u5bf9\u524d\u5c18\u5f80\u4e16\u7684\u8bb0\u5fc6\u518d\u6ca1\u6709\u591a\u5c11\u610f\u4e49\uff0c\u4e5f\u662f\u5b59\u9759\u6aac\u82b1\u4e86\u8bb8\u591a\u5fc3\u601d\u7684\u5730\u65b9\uff0c\u7b54\u5e94\u8fc7\u8981\u597d\u597d\u7167\u6599\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5916\u9762\u4f20\u6765\u73bb\u7483\u7834\u788e\u7684\u58f0\u97f3\uff0c\u4f3c\u4e4e\u6709\u4eba\u5728\u4e89\u5435\uff0c\u9648\u5983\u84c9\u63a2\u5934\u5f80\u5916\u770b\u4e86\u770b\uff0c\u65cb\u8f6c\u95e8\u5f00\u9616\u4e0d\u4f11\uff0c\u5df7\u5b50\u91cc\u7684\u60c5\u5f62\u5c31\u50cf\u4e00\u5e45\u5e45\u9759\u7269\u753b\u5728\u5feb\u901f\u7684\u7ffb\u52a8\uff0c\u770b\u4e0d\u89c1\u5916\u9762\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u7591\u60d1\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\u3002</p><p>\u3000\u3000\u5b66\u5e9c\u5df7\u662f\u5efa\u90ba\u5c11\u6570\u51e0\u5904\u6709\u5de1\u8b66\u4e13\u95e8\u5de1\u903b\u7684\u533a\u57df\uff0c\u5e73\u65e5\u91cc\u7684\u6cbb\u5b89\u5f88\u597d\uff0c\u4f46\u662f\u4e5f\u4e0d\u610f\u5473\u5c31\u6ca1\u6709\u6c14\u8840\u65fa\u76db\u7684\u5c0f\u9752\u5e74\u5728\u9152\u58ee\u6002\u4eba\u80c6\u4e4b\u540e\u5bfb\u8845\u6ecb\u4e8b\u3002\u9152\u5eca\u3001\u9152\u5427\u8fd9\u79cd\u573a\u5408\u7684\u662f\u975e\u672c\u6765\u5c31\u591a\uff0c\u5f20\u606a\u542c\u7740\u58f0\u97f3\u4f3c\u4e4e\u662f\u4ece\u9694\u58c1\u5e97\u7684\u95e8\u524d\u4f20\u6765\uff0c\u4e5f\u6ca1\u6709\u8dd1\u51fa\u53bb\u770b\u70ed\u95f9\u7684\u5fc3\u601d\u3002</p><p>\u3000\u3000\u65f6\u5b66\u658c\u8fb9\u63a5\u624b\u673a\u8fb9\u5f80\u5916\u8d70\uff0c\u4e0d\u77e5\u9053\u8c01\u7ed9\u4ed6\u4f20\u9012\u6d88\u606f\uff0c\u7ecf\u8fc7\u5427\u53f0\u65f6\u8ddf\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u597d\u50cf\u662f\u9648\u52c7\u3001\u80e1\u91d1\u661f\u5728\u9694\u58c1\u9152\u5427\u91cc\u7ed9\u8ba8\u503a\u7684\u4eba\u63ea\u4f4f\uff0c\u6211\u4eec\u51fa\u53bb\u770b\u70ed\u95f9\u3002\u201d\u675c\u98de\u3001\u8499\u4e50\u4ed6\u4eec\u90fd\u8ddf\u5728\u540e\u9762\u3002</p><p>\u3000\u3000\u65f6\u5b66\u658c\u8ddf\u80e1\u91d1\u661f\u3001\u9648\u52c7\u4e24\u4eba\u5bbf\u6028\u9887\u6df1\uff0c\u542c\u5230\u4ed6\u4eec\u4fe9\u9ebb\u70e6\u7f20\u8eab\uff0c\u795e\u8272\u95f4\u9887\u4e3a\u5174\u594b\u3002\u5f20\u606a\u5c06\u624b\u5934\u7684\u4e1c\u897f\u4e22\u4e0b\u6765\uff0c\u8ddf\u7740\u65f6\u5b66\u658c\u4ed6\u4eec\u8d70\u51fa\u9152\u5427\u770b\u70ed\u95f9\uff0c\u53ea\u89c1\u9694\u58c1\u7684\u6df1\u7ea2\u5370\u8c61\u9152\u5427\u524d\u56f4\u4e86\u4e00\u5708\u4eba\u3002\u80e1\u91d1\u661f\u5012\u662f\u65e0\u788d\u7684\u7ad9\u5728\u4e00\u8fb9\uff0c\u53ea\u662f\u8138\u8272\u9634\u9634\u7684\uff0c\u770b\u4e0d\u51fa\u4ed6\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\u3002\u9648\u52c7\u7ed9\u4e24\u4e2a\u5f6a\u5f62\u5927\u6c49\u903c\u5728\u5899\u89d2\u91cc\u65e0\u6cd5\u52a8\u5f39\uff0c\u4e24\u4e2a\u5f6a\u5f62\u5927\u6c49\u80f3\u818a\u4e0a\u8fd8\u7ee3\u7740\u9f99\u864e\u56fe\u6848\u7684\u7eb9\u8eab\u3002\u80e1\u91d1\u661f\u60f3\u4e0a\u524d\u5206\u8bf4\u4ec0\u4e48\uff0c\u7ed9\u4e00\u4e2a\u5c0f\u4e2a\u5b50\u897f\u670d\u9752\u5e74\u6321\u5728\u5916\u56f4\u2014\u2014\u5efa\u90ba\u516d\u6708\u5c31\u8fdb\u5165\u708e\u70ed\u7684\u590f\u5b63\uff0c\u591c\u95f4\u5929\u6c14\u7a0d\u51c9\u4e00\u4e9b\uff0c\u7a7f\u886c\u886b\u6b63\u9002\u5b9c\uff0c\u5c0f\u4e2a\u5b50\u9752\u5e74\u91cc\u9762\u7a7f\u7740\u5706\u9886\u7684T\u6064\uff0c\u5916\u9762\u4e00\u672c\u6b63\u7ecf\u7684\u7a7f\u7740\u897f\u670d\uff0c\u672a\u5fc5\u6709\u4e9b\u88c5\u8154\u4f5c\u52bf\u4e86\u3002\u5730\u4e0a\u788e\u4e86\u4e00\u5730\u7684\u73bb\u7483\u6e23\uff0c\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u6253\u788e\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e73\u65f6\u4e0d\u8ba9\u4fdd\u9556\u8ddf\u5728\u8eab\u8fb9\uff0c\u8fd9\u8fb9\u9152\u5427\u95f9\u51fa\u7ea0\u7eb7\uff0c\u5085\u4fca\u4fbf\u4e0e\u4e24\u540d\u4fdd\u9556\u53ca\u65f6\u51fa\u73b0\u4e86\uff0c\u5c31\u7ad9\u57281978\u4e0e\u6df1\u7ea2\u5370\u8c61\u4e24\u95f4\u9152\u5427\u4e4b\u95f4\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u6ca1\u6709\u4eba\u62a5\u8b66\uff1f\u201d\u5f20\u606a\u76b1\u7740\u7709\u5934\u95ee\u5085\u4fca\uff0c\u201c\u4ec0\u4e48\u4eba\u80f3\u818a\u4e0a\u7ee3\u4e9b\u523a\u9752\u5c31\u6765\u5192\u5145\u9ed1\u793e\u4f1a\u8ba8\u503a\uff1f\u201d</p><p>\u3000\u3000\u65f6\u5b66\u658c\u56de\u5934\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u8ba8\u503a\u7684\u9ed1\u793e\u4f1a\u627e\u9648\u52c7\u9ebb\u70e6\uff0c\u4ed6\u4eec\u9ad8\u5174\u8fd8\u6765\u4e0d\u53ca\uff0c\u600e\u4e48\u53ef\u80fd\u62a5\u8b66\u5e2e\u4ed6\u8131\u8eab\u5462\uff1f</p><p>\u3000\u3000\u201c\u6211\u4e0d\u60f3\u770b\u5230\u5b66\u5e9c\u5df7\u6709\u4ec0\u4e48\u4e71\u4e03\u516b\u7cdf\u7684\u4e1c\u897f\u51fa\u73b0\u3002\u201d\u5f20\u606a\u7565\u89e3\u91ca\u4e86\u4e00\u53e5\uff0c\u6bd4\u8d77\u627e\u9648\u52c7\u8fd9\u79cd\u5c0f\u89d2\u8272\u7684\u9ebb\u70e6\uff0c\u4ed6\u66f4\u5728\u610f\u5b66\u5e9c\u5df7\u7684\u6cbb\u5b89\u73af\u5883\u4e0d\u4f1a\u7ed9\u7834\u574f\u6389\u3002</p><p>\u3000\u3000\u4e24\u540d\u5f6a\u5f62\u5927\u6c49\u663e\u7136\u662f\u60f3\u5c06\u9648\u52c7\u5e26\u5230\u504f\u50fb\u7684\u89d2\u843d\u91cc\u65b9\u4fbf\u95ee\u8bdd\uff0c\u4e24\u4eba\u4e00\u5de6\u4e00\u53f3\u4f38\u624b\u5361\u4f4f\u4ed6\u7684\u8116\u5b50\u5f80\u5916\u62d6\uff0c\u5085\u4fca\u793a\u610f\u4e24\u540d\u5de5\u4f5c\u4eba\u5458\u5c06\u4ed6\u4eec\u62e6\u4f4f\uff0c\u8d70\u8fc7\u53bb\u8ddf\u90a3\u4e2a\u770b\u4e0a\u53bb\u50cf\u662f\u5e26\u5934\u7684\u5c0f\u4e2a\u5b50\u897f\u670d\u9752\u5e74\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u7ba1\u4f60\u4eec\u4e4b\u95f4\u6709\u4ec0\u4e48\u7ea0\u7eb7\uff0c\u5728\u5b66\u5e9c\u5df7\u4efb\u4f55\u4e00\u4e2a\u4eba\u90fd\u4e0d\u80fd\u65e0\u7f18\u65e0\u6545\u7684\u7ed9\u4f60\u4eec\u8fd9\u6837\u5e26\u8d70\uff01\u201d</p><p>\u3000\u3000\u8ddf\u5728\u540e\u9762\u7684\u80e1\u91d1\u661f\u4ee5\u53ca\u7ed9\u4e24\u4e2a\u5f6a\u5f62\u5927\u6c49\u5361\u7740\u8116\u5b50\u7684\u9648\u52c7\u90fd\u610f\u5916\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u6ca1\u60f3\u5230\u5f20\u606a\u4f1a\u5e2e\u4ed6\u4eec\u8131\u8eab\uff1b\u5f20\u606a\u6ca1\u6709\u7406\u4f1a\u4ed6\u4eec\uff0c\u5f84\u76f4\u8d70\u56de1978\u3002</p><p>\u3000\u3000\u5de1\u8b66\u5f88\u5feb\u5c31\u51fa\u73b0\u4e86\uff0c\u4e0d\u7531\u5206\u8bf4\u7684\u5c31\u5c06\u4e24\u540d\u5f6a\u5f62\u5927\u6c49\u8ddf\u90a3\u4e2a\u5c0f\u4e2a\u5b50\u897f\u670d\u9752\u5e74\u94d0\u8fdb\u8b66\u8f66\u91cc\u5e26\u8d70\u3002\u89c1\u5de1\u8b66\u53ca\u65f6\u7ed9\u9648\u52c7\u89e3\u4e86\u56f4\uff0c\u65f6\u5b66\u658c\u4ed6\u4eec\u4e5f\u65e0\u8da3\u7684\u8f6c\u56de\u9152\u5427\u91cc\uff0c\u675c\u98de\u3001\u8499\u4e50\u3001\u65f6\u5b66\u658c\u5c31\u5750\u5728\u5427\u53f0\u524d\u8ddf\u5f20\u606a\u8bf4\u8bdd\uff1a\u201c\u600e\u4e48\u770b\u4e0a\u53bb\u8ddf\u80e1\u91d1\u661f\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e8b\u60c5\u6ca1\u90a3\u4e48\u7b80\u5355\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u80e1\u91d1\u661f\u85cf\u5728\u6697\u5904\uff0c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u7531\u9648\u52c7\u51fa\u9762\u529e\u7406\u7684\uff0c\u5305\u62ec\u501f\u4fdd\u8bc1\u91d1\u5e76\u5411\u4e91\u6797\u8bc1\u5238\u878d\u8d44\u7b49\u7b49\u8bf8\u591a\u4e8b\u60c5\uff0c\u90fd\u662f\u9648\u52c7\u51fa\u9762\u505a\u7684\uff0c\u73b0\u5728\u6709\u8ba8\u503a\u7684\uff0c\u81ea\u7136\u4e5f\u662f\u8ffd\u5230\u9648\u52c7\u5934\u4e0a\u3002\u4f46\u662f\u8fd9\u4e00\u5207\u8981\u6ca1\u6709\u80e1\u91d1\u661f\u5728\u80cc\u5730\u91cc\u914d\u5408\uff0c\u9648\u52c7\u4e5f\u505a\u4e0d\u6765\u3002\u73b0\u5728\u9648\u52c7\u60f9\u4e0a\u7529\u4e0d\u8131\u7684\u9ebb\u70e6\uff0c\u80e1\u91d1\u661f\u60f3\u8131\u8eab\u4e5f\u96be\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u65e2\u7136\u80e1\u91d1\u661f\u6ca1\u6709\u51fa\u9762\uff0c\u90a3\u4ed6\u4e0d\u662f\u53ef\u4ee5\u5c06\u6240\u6709\u7684\u9ebb\u70e6\u90fd\u63a8\u5230\u9648\u52c7\u8eab\u4e0a\uff1f\u201d\u65f6\u5b66\u658c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u8981\u9648\u52c7\u6709\u80fd\u529b\u5c06\u8fd9\u4e9b\u9ebb\u70e6\u90fd\u63fd\u4e0b\u6765\u624d\u884c\u3002\u201d\u675c\u98de\u7b11\u9053\uff0c\u201c\u9664\u4e86\u56db\u767e\u4e07\u7684\u4fdd\u8bc1\u91d1\uff0c\u4ed6\u4eec\u5728\u4e91\u6797\u8bc1\u5238\u8fd8\u6345\u51fa\u4e00\u5343\u4e24\u767e\u4e07\u7684\u7a9f\u7abf\uff0c\u8fd9\u56db\u767e\u4e07\u7684\u4fdd\u8bc1\u91d1\u8fd8\u53ea\u662f\u9ed1\u793e\u4f1a\u8ffd\u8ba8\uff0c\u4e91\u6797\u8bc1\u5238\u4e00\u5343\u4e24\u767e\u4e07\u7684\u7a9f\u7abf\u5012\u4e0d\u77e5\u9053\u4ed6\u4eec\u600e\u4e48\u586b\uff1f\u201d</p><p>\u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u5085\u4fca\u8d70\u8fc7\u6765\uff0c\u51d1\u5230\u5427\u53f0\u524d\u8ddf\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u9648\u52c7\u4e3a\u4e86\u51d1\u8db3\u4fdd\u8bc1\u91d1\u8ddf\u9053\u4e0a\u501f\u4e86\u516b\u5341\u4e07\u7684\u9ad8\u5229\u8d37\u2014\u2014\u8fd9\u8fd8\u662f\u5176\u6b21\uff0c\u5f00\u9ad8\u5229\u8d37\u516c\u53f8\u7684\u8001\u677f\u542c\u9648\u52c7\u8bf4\u7092\u6d77\u7c9f\u79d1\u6280\u80a1\u7968\u4e0a\u6709\u5229\u53ef\u56fe\uff0c\u5728\u9ad8\u4f4d\u8ddf\u8fdb\uff0c\u4e8f\u4e86\u4e0a\u5343\u4e07\uff0c\u5927\u6982\u662f\u6c14\u6781\u4e86\uff0c\u4e5f\u987e\u4e0d\u4e0a\u9648\u52c7\u8001\u5b50\u9648\u5176\u658c\u7684\u8eab\u4efd\uff0c\u8fd9\u624d\u6d3e\u4e09\u540d\u9a6c\u4ed4\u8ffd\u503a\u8ffd\u5230\u5b66\u5e9c\u5df7\u6765\u4e86\u2026\u2026\u90a3\u4e2a\u5f00\u9ad8\u5229\u8d37\u516c\u53f8\u7684\u8001\u677f\u7b49\u4e09\u4e2a\u9a6c\u4ed4\u7ed9\u8b66\u65b9\u5e26\u8d70\u4e86\uff0c\u624d\u60f3\u8d77\u5b66\u5e9c\u5df7\u662f\u4ec0\u4e48\u5730\u65b9\u6765\uff0c\u521a\u624d\u6258\u4eba\u6253\u7535\u8bdd\u6765\u6253\u62db\u547c\u3002\u201d</p><p>\u3000\u3000\u675c\u98de\u8bf4\u9053\uff1a\u201c\u8fd9\u4e9b\u5f00\u9ad8\u5229\u8d37\u7684\u4e0d\u6562\u60f9\u80e1\u5b97\u5e86\uff0c\u4f46\u662f\u6c14\u6025\u4e4b\u4e0b\uff0c\u672a\u5fc5\u4e0d\u6562\u60f9\u9648\u5176\u658c\u2026\u2026\u201d</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u662f\u5efa\u90ba\u5e02\u59d4\u526f\u4e66\u8bb0\u3001\u5e38\u52a1\u526f\u5e02\u957f\uff0c\u5927\u6743\u5728\u63e1\uff0c\u5efa\u90ba\u5e02\u90a3\u4e9b\u4e09\u6559\u4e5d\u6d41\u3001\u6b6a\u95e8\u90aa\u9053\u4e4b\u5f92\uff0c\u6839\u672c\u6ca1\u6709\u80fd\u529b\u3001\u4e5f\u6ca1\u6709\u80c6\u5b50\u60f9\u5230\u80e1\u5b97\u5e86\u5934\u4e0a\u3002\u9648\u5176\u658c\u53ea\u662f\u7701\u6559\u80b2\u5385\u7684\u4e00\u540d\u526f\u5385\u957f\uff0c\u867d\u8bf4\u5728\u5efa\u90ba\u7684\u4eba\u8109\u5173\u7cfb\u4e5f\u4e0d\u4f1a\u592a\u6d45\uff0c\u4f46\u6bd5\u7adf\u957f\u671f\u5904\u5728\u673a\u5173\u91cc\uff0c\u6d89\u53ca\u9762\u72ed\u7a84\uff0c\u53ea\u6bd4\u80e1\u5b97\u5e86\u4f4e\u4e00\u7ea7\uff0c\u6743\u52bf\u5374\u5929\u5dee\u5730\u522b\uff0c\u6562\u5728\u5efa\u90ba\u5f00\u9ad8\u5229\u8d37\u516c\u53f8\u7684\u5730\u5934\u86c7\u672a\u5fc5\u4e0d\u6562\u54ac\u4ed6\u4e00\u53e3\u3002</p><p>\u3000\u3000\u4e5f\u8bb8\u8fd9\u4e9b\u4eba\u77e5\u9053\u80e1\u91d1\u661f\u4e0e\u9648\u52c7\u5728\u6d77\u7c9f\u79d1\u6280\u7684\u4e8b\u4ef6\u4e2d\u6c86\u7023\u4e00\u6c14\uff0c\u4ed6\u4eec\u53ea\u627e\u9648\u52c7\u7684\u9ebb\u70e6\uff0c\u800c\u5c06\u80e1\u91d1\u661f\u6487\u5728\u4e00\u65c1\u3002</p><p>\u3000\u3000\u201c\u8bf4\u4e0d\u5b9a\u6709\u5176\u4ed6\u9690\u60c5\uff0c\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u95ee\u5085\u4fca\uff0c\u201c\u662f\u8c01\u6253\u7535\u8bdd\u6765\u6253\u62db\u547c\uff1f\u201d</p><p>\u3000\u3000\u201c\u542f\u51e4\u5730\u4ea7\u7684\u8001\u677f\u987e\u795d\u540c\uff0c\u90a3\u5bb6\u9ad8\u5229\u8d37\u516c\u53f8\u542c\u8bf4\u80cc\u540e\u4e5f\u6709\u4ed6\u7684\u80a1\u4efd\u5728\u91cc\u9762\u3002\u201d\u5085\u4fca\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u5bf9\u987e\u795d\u540c\u6ca1\u6709\u4ec0\u4e48\u5370\u8c61\uff0c\u4e94\u767e\u4e07\u4eba\u53e3\u7684\u5efa\u90ba\u5e02\uff0c\u9f99\u86c7\u6df7\u6742\uff0c\u6709\u5927\u86c7\uff0c\u6709\u4e2d\u86c7\uff0c\u4e5f\u6709\u5c0f\u86c7\uff0c\u4ed6\u54ea\u91cc\u80fd\u8bb0\u5f97\u90a3\u4e48\u591a\uff1f\u5429\u5490\u5085\u4fca\u8bf4\u9053\uff1a\u201c\u6211\u4f30\u8ba1\u7740\u80cc\u540e\u8fd8\u85cf\u7740\u5176\u4ed6\u4ec0\u4e48\u620f\uff0c\u6709\u6e20\u9053\u6253\u542c\u4e00\u4e0b\u987e\u795d\u540c\u4ec0\u4e48\u6765\u8def\u2026\u2026\u6559\u80b2\u5385\u90a3\u8fb9\uff0c\u5385\u957f\u9a6c\u8d5f\u8eab\u4f53\u5f88\u5dee\uff0c\u4e00\u5e74\u5012\u6709\u534a\u5e74\u65f6\u95f4\u5728\u4fee\u517b\uff0c\u51e0\u4e2a\u526f\u5385\u957f\u6597\u5f97\u5389\u5bb3\uff0c\u4e5f\u9887\u6709\u4e9b\u5b9e\u6743\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u4f60\u5012\u662f\u591f\u670b\u53cb\u554a\uff01\u597d\u50cf\u6574\u4ef6\u4e8b\u60c5\u8ddf\u4f60\u6ca1\u6709\u4e1d\u6beb\u5173\u7cfb\u4f3c\u7684\uff0c\u6700\u540e\u7adf\u7136\u662f\u4e0d\u76f8\u5173\u7684\u4eba\u5e2e\u4e86\u6211\u4e00\u628a\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff01\u4e0d\u8981\u4ee5\u4e3a\u4f60\u88c5\u4f5c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u8ddf\u4f60\u6ca1\u6709\u5173\u7cfb\u5c31\u771f\u7684\u6ca1\u6709\u5173\u7cfb\u4e86\uff01\u201d\u9648\u52c7\u5fff\u6068\u7684\u76ef\u4e86\u80e1\u91d1\u661f\u4e00\u773c\uff0c\u5927\u58f0\u56b7\u56b7\u9053\u3002\u8116\u5b50\u4e0a\u9690\u9690\u4f5c\u75db\uff0c\u770b\u7740\u81ea\u5df1\u5728\u8f66\u7a97\u4e0a\u5012\u5f71\uff0c\u770b\u5230\u8116\u5b50\u6897\u7ed9\u90a3\u4e24\u4e2a\u9ed1\u793e\u4f1a\u5361\u5f97\u6709\u4e9b\u7ea2\u80bf\uff0c\u8eab\u4f53\u4e0a\u5012\u6ca1\u6709\u53d7\u591a\u5927\u7684\u59d4\u5c48\uff0c\u4f46\u662f\u5728\u5927\u5ead\u5e7f\u4f17\u4e4b\u4e0b\u53d7\u5230\u8fd9\u6837\u7684\u5f85\u9047\uff0c\u8ba9\u5fc3\u91cc\u7f9e\u6124\u4e0d\u582a\uff0c\u4e5f\u66f4\u60f3\u63a9\u9970\u5185\u5fc3\u7684\u4ed3\u7687\uff0c\u56b7\u8d77\u6765\u6709\u4e9b\u58f0\u5636\u529b\u7aed\u3002</p><p>\u3000\u3000\u201c\u6211\u6709\u4e22\u4e0b\u4f60\u4e0d\u7ba1\uff1f\u201d\u80e1\u91d1\u661f\u5fc3\u60c5\u4e5f\u76f8\u5f53\u90c1\u95f7\uff0c\u538b\u7740\u55d3\u5b50\u8bf4\u9053\uff0c\u201c\u4e8b\u60c5\u8f7b\u91cd\u7f13\u6025\uff0c\u4e5f\u8ddf\u4f60\u8bf4\u6e05\u695a\u4e86\uff0c\u4e0d\u80fd\u52a8\u4e0d\u52a8\u5c31\u5c06\u6211\u53d4\u642c\u51fa\u6765\u5413\u552c\u8c01\u3002\u518d\u8bf4\u4f60\u7238\u8fd8\u662f\u6559\u80b2\u5385\u526f\u5385\u957f\uff0c\u4f60\u89c1\u4eba\u5bb6\u642d\u7406\u4e86\u5417\uff1f\u6211\u5df2\u7ecf\u5c3d\u4e00\u5207\u53ef\u80fd\u5c06\u4e91\u6797\u8bc1\u5238\u90a3\u8fb9\u7684\u4e8b\u538b\u4e0b\u6765\uff0c\u4fdd\u8bc1\u91d1\u7684\u4e8b\u60c5\uff0c\u4f60\u5c31\u4e0d\u80fd\u591a\u60f3\u60f3\u529e\u6cd5\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u60f3\u529e\u6cd5\uff0c\u6211\u6709\u4ec0\u4e48\u529e\u6cd5\u597d\u60f3\uff1f\u5f53\u521d\u53ef\u662f\u4f60\u4fe1\u8a93\u65e6\u65e6\u7684\u8bf4\u8fd9\u6b21\u94c1\u5b9a\u80fd\u635e\u4e00\u5927\u7b14\uff0c\u4f60\u770b\u770b\u73b0\u5728\uff01\u5148\u4e0d\u7ba1\u89e3\u5957\u4e0d\u89e3\u5957\u7684\u4e8b\u60c5\uff0c\u5148\u8981\u5c06\u4fdd\u8bc1\u91d1\u7684\u95ee\u9898\u89e3\u51b3\u6389\uff0c\u9664\u4e86\u738b\u6d77\u7c9f\u7684\u94b1\u6682\u65f6\u4e0d\u7ba1\uff0c\u8fd9\u7b14\u9ad8\u5229\u8d37\uff0c\u8fd8\u6709\u5176\u4ed6\u9ad8\u606f\u63fd\u8fc7\u6765\u7684\u94b1\uff0c\u4e00\u5171\u662f\u4e09\u767e\u4e8c\u5341\u4e07\uff0c\u518d\u4e0d\u89e3\u51b3\uff0c\u8ba9\u90a3\u4e9b\u4eba\u95f9\u5230\u6211\u7238\u5355\u4f4d\u90a3\u91cc\u53bb\uff0c\u8c01\u90fd\u62b9\u4e0d\u5e72\u51c0\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u80e1\u91d1\u661f\u542c\u7740\u9648\u52c7\u5e26\u5a01\u80c1\u7684\u8bdd\u5f88\u662f\u523a\u8033\uff0c\u76b1\u7740\u7709\u5934\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u4e00\u53e3\u6c14\u6ca1\u5fcd\u4f4f\uff0c\u8bf4\u9053\uff0c\u201c\u738b\u6d77\u7c9f\u662f\u770b\u5728\u8c01\u7684\u9762\u5b50\u4e0a\u4e0d\u63d0\u4ed6\u90a3\u7b14\u94b1\uff0c\u4e91\u6797\u8bc1\u5238\u90a3\u8fb9\u662f\u8c01\u538b\u4e0b\u6765\u7684\u2026\u2026\u73b0\u5728\u662f\u51fa\u4e86\u4e9b\u4e8b\u60c5\uff0c\u6211\u6709\u9003\u5417\uff1f\u4f46\u662f\u4f60\u4e0d\u80fd\u53eb\u6240\u6709\u4e8b\u60c5\u90fd\u8ba9\u6211\u6765\u89e3\u51b3\uff01\u201d\u4ed6\u624b\u642d\u7740\u8f66\u95e8\u9501\u628a\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u73b0\u5728\u4e0d\u8ddf\u4f60\u4e89\u4ec0\u4e48\uff0c\u6211\u56de\u53bb\u518d\u60f3\u529e\u6cd5\uff0c\u4f60\u4e5f\u52a8\u52a8\u8111\u5b50\uff0c\u71ac\u8fc7\u8fd9\u6b21\uff0c\u603b\u6709\u6211\u4eec\u7ffb\u8eab\u7684\u65f6\u5019\uff0c\u53ea\u8981\u6211\u53d4\u3001\u4f60\u7238\u90fd\u5728\u4f4d\u5b50\uff0c\u6c42\u4ed6\u4eec\u529e\u4e8b\u7684\u4eba\u591a\u4e86\uff0c\u5343\u513f\u516b\u767e\u4e07\u5c41\u5927\u4e8b\u60c5\uff01\u201d\u4e22\u4e0b\u8fd9\u53e5\u8bdd\uff0c\u80e1\u91d1\u661f\u4e0b\u8f66\u53bb\uff0c\u5c06\u9648\u52c7\u4e22\u5728\u8fd9\u8f86\u521a\u4e70\u4e0d\u4e45\u7684\u94f6\u8272\u5965\u8fea\u8f66\u91cc\u3002</p><p>\u3000\u3000\u9648\u52c7\u770b\u7740\u80e1\u91d1\u661f\u5df2\u7ecf\u8d70\u51fa\u5df7\u5b50\u53e3\uff0c\u4ed6\u5fff\u6068\u7684\u62ff\u62f3\u5934\u7838\u4e86\u4e00\u8bb0\u65b9\u5411\u76d8\uff0c\u8fc7\u4e86\u8bb8\u4e45\uff0c\u624d\u53d1\u52a8\u8f66\u56de\u5bb6\u53bb\u3002</p><p>\u3000\u3000\u9648\u52c7\u5bb6\u4f4f\u5728\u536b\u9675\u8def\uff0c\u7701\u6559\u80b2\u5385\u5c31\u5728\u524d\u9762\u7684\u4e2d\u5c71\u4e1c\u8def\u4e0a\u3002</p><p>\u3000\u3000\u8d76\u5728\u623f\u6539\u524d\uff0c\u7701\u6559\u80b2\u5385\u51fa\u8d44\u5c06\u673a\u5173\u80cc\u540e\u67f4\u6cb9\u673a\u5382\u7684\u5730\u62ff\u4e0b\u6765\u5efa\u4e86\u4e00\u680b\u4e09\u5341\u5c42\u697c\u9ad8\u7684\u6559\u80b2\u5927\u53a6\uff0c\u6559\u80b2\u5927\u53a6\u51fa\u79df\u7ed9\u516c\u53f8\u3001\u4f01\u4e1a\u529e\u516c\u3002\u53e6\u5916\u53c8\u5728\u673a\u5173\u4e0e\u6559\u80b2\u5927\u53a6\u4e4b\u95f4\u5efa\u56db\u680b\u5c0f\u9ad8\u5c42\u516c\u5bd3\u5206\u7ed9\u4e0b\u5c5e\u804c\u5de5\uff0c\u9648\u5176\u658c\u4f5c\u4e3a\u526f\u5385\u957f\uff0c\u5206\u5230\u4e00\u767e\u516b\u5341\u5e73\u7c73\u7684\u5927\u5957\u3002\u540d\u4e49\u4e0a\u662f\u96c6\u8d44\u623f\uff0c\u9648\u5176\u658c\u624d\u638f\u51fa\u516b\u4e07\u5143\u5c31\u5c06\u90a3\u5957\u623f\u5212\u5f52\u5230\u4e2a\u4eba\u540d\u4e0b\uff0c\u53ea\u6709\u8fd9\u7247\u5730\u57df\u623f\u4ef7\u7684\u51e0\u5206\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u9648\u52c7\u5c06\u8f66\u505c\u8fdb\u5730\u4e0b\u8f66\u5e93\u91cc\uff0c\u770b\u5230\u4e00\u8f86\u5954\u9a70\u4ece\u8eab\u8fb9\u5f00\u8fc7\u53bb\uff0c\u80fd\u611f\u89c9\u5954\u9a70\u540e\u5ea7\u91cc\u7684\u4eba\u76ef\u7740\u81ea\u5df1\u770b\u3002\u9648\u52c7\u770b\u4e0d\u6e05\u5954\u9a70\u8f66\u91cc\u662f\u8c01\uff0c\u5728\u8f66\u91cc\u5750\u4e86\u7247\u523b\uff0c\u786e\u8ba4\u4e0d\u4f1a\u9047\u5230\u4ec0\u4e48\u719f\u4eba\uff0c\u9b3c\u9b3c\u795f\u795f\u7684\u5750\u7535\u68af\u4e0a\u4e86\u697c\uff0c\u6253\u5f00\u95e8\u770b\u89c1\u4ed6\u8001\u5b50\u9648\u5176\u658c\u5750\u5728\u5ba2\u5385\u91cc\uff0c\u5947\u602a\u7684\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u600e\u4e48\u56de\u6765\u8fd9\u4e48\u65e9\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u53e5\u8bdd\u5e94\u8be5\u6211\u6765\u95ee\u4f60\uff01\u201d\u9648\u5176\u658c\u9634\u6c89\u7740\u8138\u76ef\u7740\u513f\u5b50\u9648\u52c7\u770b\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff0c\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u4e86\uff1f\u201d\u9648\u52c7\u89c1\u8001\u5b50\u795e\u8272\u6709\u5f02\uff0c\u5fc3\u865a\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u81ea\u5df1\u505a\u7684\u597d\u4e8b\uff0c\u8fd8\u8981\u95ee\u6211\u3002\u201d\u9648\u5176\u658c\u5c06\u624b\u4e0b\u538b\u7740\u7684\u4e00\u672c\u4e66\u62ff\u8d77\u6765\u5c31\u671d\u513f\u5b50\u7684\u8138\u4e0a\u7838\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u9648\u52c7\u5413\u4e86\u4e00\u8df3\uff0c\u95ea\u6ca1\u95ea\u5f00\uff0c\u7ed9\u4e66\u810a\u7838\u5230\u5634\u89d2\u4e0a\uff0c\u4e0d\u77e5\u9053\u4ec0\u4e48\u4e8b\u60c5\u72af\u4e86\u592a\u5c81\uff0c\u6ca1\u6562\u542d\u58f0\uff0c\u4e66\u6389\u5728\u5730\u677f\u4e0a\uff0c\u540c\u65f6\u8fd8\u6709\u51e0\u9875\u7eb8\u98d8\u4e0b\u6765\uff0c\u4ed6\u77a5\u4e86\u4e00\u773c\uff0c\u5374\u662f\u4ed6\u5411\u9ad8\u5229\u8d37\u516c\u53f8\u7684\u501f\u8d37\u5408\u540c\uff0c\u60f3\u8d77\u521a\u624d\u5728\u5730\u4e0b\u8f66\u5e93\u770b\u5230\u7684\u90a3\u8f86\u5954\u9a70\u8f66\uff0c\u77e5\u9053\u4eba\u5bb6\u627e\u4e0a\u95e8\u6765\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u8fd9\u6b21\u4e5f\u662f\u770b\u8d70\u4e86\u773c\uff0c\u4e5f\u5c31\u516b\u5341\u4e07\uff0c\u6211\u5f53\u591a\u5927\u7684\u4e8b\u60c5\uff0c\u660e\u5929\u6211\u7b79\u94b1\u8fd8\u7ed9\u4ed6\u4eec\u5c31\u662f\u2026\u2026\u201d\u9648\u52c7\u865a\u5f20\u58f0\u52bf\u7684\u60f3\u5c06\u773c\u4e0b\u7684\u4e8b\u552c\u5f04\u8fc7\u53bb\uff0c\u201c\u5988\u5979\u4eba\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e5f\u5c31\u516b\u5341\u4e07\u2026\u2026\u201d\u9648\u5176\u658c\u53d1\u6068\u7684\u51b7\u7b11\u7740\u76ef\u7740\u513f\u5b50\u770b\uff0c\u518d\u8d70\u8fd1\u4e00\u6b65\u4f1a\u5fcd\u4e0d\u4f4f\u4f38\u624b\u62bd\u4ed6\u8033\u522e\u5b50\uff0c\u5fcd\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff0c\u201c\u516b\u5341\u4e07\u7684\u4e8b\u60c5\uff0c\u4f60\u8bf4\u7684\u8f7b\u677e\uff0c\u4eba\u5bb6\u770b\u4e0a\u540e\u9762\u7684\u6559\u80b2\u5927\u53a6\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u516b\u5341\u4e07\u5c31\u60f3\u62ff\u8d70\u6559\u80b2\u5927\u53a6\uff0c\u4ed6\u4eec\u5012\u662f\u60f3\u5f97\u7f8e\uff0c\u9648\u52c7\u5fc3\u91cc\u60f3\u9053\uff0c\u516b\u5341\u4e07\u5f53\u7136\u62ff\u4e0d\u8d70\u6559\u80b2\u5927\u53a6\uff0c\u8fd9\u53ea\u662f\u4fc3\u6210\u6b64\u4e8b\u7ed9\u4ed6\u8001\u5b50\u9648\u5176\u658c\u7684\u62a5\u916c\uff0c\u4e0d\u8fc7\u8fd9\u5012\u7ed9\u4ed6\u4e00\u4e2a\u63d0\u9192\uff0c\u773c\u775b\u4e00\u4eae\uff0c\u60f3\u5230\u6446\u8131\u773c\u4e0b\u9ebb\u70e6\u7684\u6cd5\u5b50\uff0c\u5c06\u886c\u886b\u9886\u5f80\u4e0b\u626f\u4e86\u626f\uff0c\u9732\u51fa\u8116\u5b50\u4e0a\u7684\u6de4\u75d5\uff1a\u201c\u6211\u4e5f\u662f\u4e0a\u4e86\u80e1\u91d1\u661f\u7684\u5f53\uff0c\u8fd8\u4e0d\u4e0a\u94b1\uff0c\u4ed6\u4eec\u5230\u5b66\u6821\u91cc\u6765\u5835\u6211\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u5176\u658c\u770b\u4e86\u4e00\u773c\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u4ed6\u8eb2\u5728\u95e8\u5916\u7684\u59bb\u5b50\u4e0d\u5fcd\u5fc3\u770b\u513f\u5b50\u53d7\u82e6\uff0c\u8df3\u51fa\u6765\u8bf4\u9053\uff1a\u201c\u9a6c\u8d5f\u63d0\u524d\u75c5\u9000\uff0c\u4f60\u80af\u5b9a\u4f60\u5c31\u80fd\u5750\u4e0a\u5385\u957f\u7684\u4f4d\u5b50\uff1f\u6211\u5c31\u4e00\u4e2a\u513f\u5b50\uff0c\u4ed6\u8981\u6709\u4e09\u957f\u4e24\u77ed\uff0c\u4f60\u8ddf\u5f20\u4e3d\u90a3\u4e2a\u9a9a\u8d27\u4e5f\u522b\u60f3\u8fc7\u4ec0\u4e48\u5b89\u7a33\u65e5\u5b50\uff01\u201d</p><p>\u3000\u3000\u201c\u80e1\u8bf4\u516b\u9053\u4ec0\u4e48\uff01\u4f60\u56de\u623f\u95f4\u53bb\u3002\u201d\u9648\u5176\u658c\u671d\u4ed6\u59bb\u5b50\u5927\u58f0\u56b7\u56b7\u3002</p><p>\u3000\u3000\u201c\u4f60\u5f53\u6211\u4ec0\u4e48\u90fd\u4e0d\u77e5\u9053\uff1f\u5f20\u4e3d\u90a3\u4e2a\u9a9a\u8d27\u7684\u4e8b\u60c5\uff0c\u6211\u90fd\u5fcd\u4e86\uff0c\u5c0f\u52c7\u7684\u4e8b\u60c5\uff0c\u4f60\u7ba1\u4e0d\u7ba1\uff0c\u90a3\u4e9b\u4eba\u90fd\u662f\u9ed1\u793e\u4f1a\uff0c\u4fdd\u4e0d\u5b9a\u4ec0\u4e48\u4e8b\u60c5\u90fd\u505a\u5f97\u51fa\u6765\uff0c\u4ed6\u4eec\u8981\u90a3\u680b\u697c\uff0c\u4f60\u7ed9\u4ed6\u4eec\u5c31\u662f\uff0c\u4e0d\u7ed9\u4ed6\u4eec\uff0c\u90a3\u680b\u697c\u8fd8\u80fd\u7559\u7ed9\u4f60\u554a\u3002\u73b0\u5728\u9a6c\u8d5f\u5927\u6743\u4e0d\u6293\u4e86\uff0c\u4f60\u624b\u91cc\u597d\u4e0d\u5bb9\u6613\u6709\u4e9b\u5b9e\u6743\uff0c\u7b49\u6362\u4e86\u65b0\u5385\u957f\uff0c\u4f60\u53c8\u8981\u7ed9\u6253\u56de\u539f\u5f62\uff0c\u4f60\u4e0d\u8d81\u7740\u8fd9\u65f6\u5019\u66ff\u5c0f\u52c7\u591a\u60f3\u60f3\uff1f\u201d</p><p>\u3000\u3000\u9648\u52c7\u8fd9\u624d\u77e5\u9053\u90a3\u4e9b\u4eba\u8d70\u4e0a\u95e8\u5c06\u501f\u8d37\u5408\u540c\u653e\u4e0b\u6765\uff0c\u5e94\u8be5\u4e5f\u8bf4\u4e86\u4e00\u4e9b\u5a01\u80c1\u7684\u8bdd\u3002\u9a6c\u8d5f\u5385\u957f\u8fd9\u4e24\u5e74\u6765\u957f\u671f\u60a3\u75c5\u4fee\u517b\uff0c\u5c31\u4e0d\u5927\u6293\u6743\u4e86\uff0c\u6559\u80b2\u5385\u57fa\u5efa\u3001\u56fa\u5b9a\u8d44\u4ea7\u7b49\u65b9\u9762\u90fd\u5f52\u4ed6\u8001\u5b50\u9648\u5176\u658c\u7ba1\u7406\uff0c\u6cb9\u6c34\u662f\u5f88\u8db3\uff0c\u4f46\u662f\u4e3a\u65f6\u5c1a\u6d45\uff0c\u53c8\u6ca1\u6709\u4ec0\u4e48\u5927\u7684\u57fa\u5efa\u9879\u76ee\uff0c\u4e5f\u6ca1\u6709\u635e\u5230\u4ec0\u4e48\u6cb9\u6c34\u3002\u7b49\u9a6c\u658c\u63d0\u524d\u75c5\u9000\uff0c\u6765\u4e00\u4e2a\u5927\u6743\u72ec\u63fd\u7684\u65b0\u5385\u957f\uff0c\u90a3\u65f6\u5019\u60f3\u635e\u4ec0\u4e48\u5927\u94b1\u90fd\u6ca1\u95e8\u3002</p><p>\u3000\u3000\u201c\u4f60\u77e5\u9053\u4ec0\u4e48\uff0c\u8fd9\u65f6\u5019\u5927\u5bb6\u90fd\u76ef\u7740\u5385\u957f\u7684\u4f4d\u5b50\uff0c\u5c31\u6307\u671b\u8c01\u5148\u72af\u9519\u8bef\u3002\u201d\u9648\u5176\u658c\u82e6\u53e3\u5a46\u5fc3\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u7238\uff0c\u4f60\u5927\u4e0d\u4e86\u4e0d\u4e89\u8fd9\u4e48\u4f4d\u5b50\u3002\u8c01\u80fd\u62b9\u5f97\u5e72\u51c0\u81ea\u5df1\u7684\u5c41\u80a1\uff1f\u201d\u9648\u52c7\u89c1\u4ed6\u7238\u7684\u8bed\u6c14\u8f6f\u4e86\u4e0b\u6765\uff0c\u624d\u58ee\u7740\u80c6\u5b50\u529d\u9053\uff0c\u201c\u8fc7\u4e86\u8fd9\u6751\u5c31\u6ca1\u6709\u8fd9\u5e97\uff0c\u8fd9\u6b21\u4e0a\u4e86\u80e1\u91d1\u661f\u7684\u5f53\uff0c\u8fd8\u4e0d\u6b62\u6b20\u9ad8\u5229\u8d37\u516b\u5341\u4e07\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\uff01\u201d\u9648\u5176\u658c\u542c\u5230\u513f\u5b50\u8fd9\u8bdd\uff0c\u6c14\u8840\u53c8\u8981\u5f80\u5934\u9876\u51b2\uff0c\u89c1\u59bb\u5b50\u53c8\u662f\u6446\u51fa\u4e00\u526f\u6492\u6ce2\u7684\u6a21\u6837\uff0c\u6068\u6068\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u5f84\u76f4\u8d70\u5230\u4e66\u623f\u91cc\uff0c\u5c06\u623f\u95e8\u91cd\u91cd\u7684\u5173\u4e0a\u3002</p><p>\u3000\u3000\u9648\u52c7\u89c1\u7236\u4eb2\u4ec0\u4e48\u90fd\u6ca1\u6709\u95ee\uff0c\u8fd8\u4ee5\u4e3a\u6307\u671b\u4e0d\u5927\uff0c\u53ef\u601c\u5df4\u5df4\u7684\u770b\u4e86\u4ed6\u5988\u4e00\u773c\u3002</p><p>\u3000\u3000\u201c\u8ba9\u4f60\u7238\u518d\u60f3\u60f3\uff0c\u4f60\u505a\u8fd9\u4e9b\u4e8b\u60c5\u600e\u4e48\u4e0d\u52a8\u52a8\u8111\u5b50\uff0c\u5929\u4e0b\u54ea\u6709\u8fd9\u4e48\u597d\u8d5a\u7684\u94b1\uff1f\u201d</p><p>\u3000\u3000\u9648\u52c7\u8fd9\u624d\u77e5\u9053\u4ed6\u505a\u7684\u4e8b\u60c5\uff0c\u4ed6\u7238\u5988\u90fd\u4ece\u522b\u4eba\u5634\u91cc\u77e5\u9053\u4e86\uff0c\u5750\u5230\u6c99\u53d1\u4e0a\uff0c\u4ece\u53e3\u888b\u91cc\u638f\u51fa\u70df\u6765\u62bd\u3002</p><p>\u3000\u3000\u201c\u4f60\u624d\u591a\u5927\u7684\u5e74\u7eaa\uff0c\u5c31\u77e5\u9053\u62bd\u70df\u3002\u201d</p><p>\u3000\u3000\u9648\u52c7\u53d7\u4e0d\u4f4f\u4ed6\u5988\u7684\u5600\u5495\uff0c\u6362\u4f5c\u5e73\u65e5\uff0c\u4ed6\u65e9\u5c31\u9876\u5634\u56de\u53bb\uff0c\u73b0\u5728\u53ea\u662f\u4fa7\u8fc7\u8eab\u5b50\uff0c\u7ee7\u7eed\u62bd\u4ed6\u7684\u70df\uff0c\u77e5\u9053\u4ed6\u5988\u4e0d\u4f1a\u771f\u7ba1\u4ed6\u3002\u5373\u4f7f\u5230\u73b0\u5728\uff0c\u644a\u4e0a\u8fd9\u4e48\u5927\u7684\u9ebb\u70e6\uff0c\u4ed6\u4e5f\u53ea\u662f\u89c9\u5f97\u81ea\u5df1\u65f6\u8fd0\u4e0d\u6d4e\u7f62\u4e86\uff0c\u9047\u4e0a\u4e92\u8054\u7f51\u7ecf\u6d4e\u5371\u673a\uff0c\u8fde\u80cc\u540e\u8d22\u529b\u96c4\u539a\u7684\u5b8f\u4fe1\u7cfb\u90fd\u675f\u624b\u65e0\u7b56\uff0c\u4ed6\u8c01\u90fd\u4e0d\u6028\uff0c\u4f46\u662f\u5fc3\u91cc\u6e05\u695a\u80e1\u91d1\u661f\u6709\u8ba9\u4ed6\u4e00\u4e2a\u4eba\u80cc\u9ed1\u7978\u7684\u5fc3\u601d\uff0c\u60f3\u5230\u81ea\u5df1\u5e73\u65f6\u53c8\u7ed9\u4ed6\u5f53\u9a6c\u4ed4\u4f3c\u7684\u4f7f\u5524\uff0c\u5fc3\u91cc\u6068\u6068\u7684\u60f3\u9053\uff1a\u603b\u6709\u4e00\u5929\u8ba9\u4f60\u77e5\u9053\u6211\u7684\u624b\u6bb5\u3002</p><p>\u3000\u3000\u4ed6\u5c06\u8ddf\u9ad8\u5229\u8d37\u516c\u53f8\u7684\u501f\u8d37\u5408\u540c\u62ff\u8fc7\u6765\u770b\u4e86\u4e00\u773c\uff0c\u5fc3\u91cc\u4e0d\u660e\u767d\u9ad8\u5229\u8d37\u516c\u53f8\u600e\u4e48\u4f1a\u5bf9\u6559\u80b2\u5927\u53a6\u8d77\u8d2a\u5fc3\uff0c\u62bd\u4e86\u4e00\u4f1a\u70df\uff0c\u60f3\u4e0d\u900f\u5176\u4e2d\u7684\u5173\u8282\uff0c\u624d\u95ee\u4ed6\u5988\uff1a\u201c\u521a\u624d\u662f\u8c01\u8fc7\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u542f\u51e4\u5730\u4ea7\u7684\u516c\u5173\u90e8\u7ecf\u7406\uff0c\u5c06\u4f60\u7684\u501f\u8d37\u5408\u540c\u8fd8\u56de\u6765\uff0c\u558f\uff0c\u8fd8\u6709\u4e00\u53ea\u9ed1\u5851\u6599\u888b\uff0c\u73b0\u5728\u8fd8\u6765\u5f97\u53ca\u6ca1\u6709\u62c6\u5f00\u6765\u2026\u2026\u201d\u9648\u52c7\u4ed6\u5988\u671d\u8336\u51e0\u5e95\u4e0b\u5476\u5476\u5634\u3002</p><p>\u3000\u3000\u9648\u52c7\u5c06\u5851\u6599\u888b\u62ff\u8d77\u6765\uff0c\u611f\u89c9\u5f88\u538b\u624b\uff0c\u6253\u5f00\u4e00\u770b\uff0c\u5dee\u4e0d\u591a\u6709\u4e8c\u5341\u624e\u94b1\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u4e0d\u8981\u52a8\uff0c\u201d\u9648\u5176\u658c\u4ece\u4e66\u623f\u8d70\u51fa\u6765\uff0c\u5c06\u5851\u6599\u888b\u62ff\u8fc7\u6765\u585e\u5230\u59bb\u5b50\u624b\u91cc\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u5148\u6536\u8d77\u6765\uff0c\u4e8b\u60c5\u8fd8\u4e0d\u77e5\u9053\u80fd\u4e0d\u80fd\u529e\u6210\uff0c\u529e\u4e0d\u6210\uff0c\u8fd9\u94b1\u8fd8\u8981\u9000\u56de\u53bb\u3002\u201d</p><p>\u3000\u3000\u9648\u52c7\u77e5\u9053\u6709\u620f\u4e86\uff0c\u7ad9\u8d77\u6765\u8bf4\u9053\uff1a\u201c\u4e00\u767e\u4e07\u5c31\u60f3\u62ff\u6559\u80b2\u5927\u53a6\uff0c\u8fd9\u53ef\u4e0d\u6210\u2026\u2026\u201d\u6559\u80b2\u5927\u53a6\u4e09\u5341\u5c42\u56db\u4e07\u591a\u5e73\u65b9\u7c73\uff0c\u53c8\u662f\u4f4d\u4e8e\u4e2d\u5fc3\u533a\u7684\u536b\u9675\u8857\uff0c\u5e02\u573a\u4f30\u4ef7\u5c11\u8bf4\u4e5f\u503c\u4e24\u4e09\u4e2a\u4ebf\uff0c\u50cf\u62ff\u6746\u79e4\u79f0\u4e1c\u897f\u4e00\u6837\uff0c\u624b\u5c3e\u6307\u8f7b\u8f7b\u538b\u4e00\u4e0b\uff0c\u5c31\u8fdc\u8fdc\u4e0d\u6b62\u4e00\u767e\u4e07\u3002</p><p>\u3000\u3000\u201c\u6211\u5fc3\u91cc\u6e05\u695a\uff0c\u4f60\u8fd9\u6bb5\u65f6\u95f4\u7ed9\u6211\u8001\u8001\u5b9e\u5b9e\u7684\u5728\u5b66\u6821\u91cc\u5446\u7740\uff0c\u7136\u540e\u7ed9\u6211\u89c4\u89c4\u77e9\u77e9\u7684\u53bb\u5e02\u6559\u80b2\u5c40\u62a5\u9053\u4e0a\u73ed\u53bb\u3002\u201d\u9648\u5176\u658c\u58f0\u4e25\u8272\u5389\u7684\u8bf4\u9053\u3002</p></article>"}}}]);
