(window.webpackJsonp=window.webpackJsonp||[]).push([[1747],{1770:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u8282 \u5766\u767d\u4ece\u5bbd",content:"<article><h2>\u7b2c\u4e8c\u8282 \u5766\u767d\u4ece\u5bbd</h2><p>\u51db\u7684\u65b9\u5411\u611f\u6781\u5ea6\u4e0d\u9760\u8c31\uff0c\u6700\u540e\u8fd8\u662f\u7ef4\u82e5\u62c9\u6765\u89e3\u51b3\u95ee\u9898\u3002\u5728\u5979\u7684\u6307\u5f15\u4e0b\uff0c\u4e00\u884c\u4eba\u7a7f\u8fc7\u4e1b\u6797\uff0c\u5230\u8fbe\u963f\u6c99\u5df4\u6cb3\u8fb9\uff0c\u743c\u6069\u4ece\u547d\u8fd0\u957f\u591c\u4e2d\u53d6\u51fa\u7eb8\u8239\u653e\u5165\u6c34\u4e2d\uff0c\u4f17\u4eba\u4f9d\u6b21\u767b\u8239\u521a\u8981\u8d77\u822a\uff0c\u4e00\u4e2a\u5c0f\u5c0f\u7684\u9ed1\u5f71\u4ece\u8fdc\u5904\u8349\u4e1b\u91cc\u7a9c\u51fa\u6765\uff0c\u8df3\u5230\u8239\u4e0a\u3002</p><p>\u90a3\u662f\u4e00\u53ea\u9ed1\u732b\uff0c\u76ae\u6bdb\u6cb9\u6ed1\u5149\u4eae\uff0c\u8116\u5b50\u4e0a\u6263\u7740\u706b\u7ea2\u8272\u9879\u5708\uff0c\u5c3e\u5df4\u4e0a\u7cfb\u7740\u4e00\u53ea\u6de1\u84dd\u8272\u8774\u8776\u7ed3\u2014\u2014\u8fd9\u4e24\u4e2a\u88c5\u9970\u8ba9\u743c\u6069\u8fa8\u8ba4\u51fa\u5979\u7684\u8eab\u4efd\uff1a\u742a\u5a05\xb7\u706b\u5203\uff0c\u517d\u795e\u7684\u9009\u6c11\uff0c\u539f\u672c\u88ab\u5965\u5609\u83b1\u65af\u6536\u670d\uff0c\u7ed9\u73ca\u5609\u505a\u5ba0\u7269\u548c\u62a4\u536b\u3002\u5728\u8fdb\u5165\u7b2c\u4e94\u79d8\u5668\u4e4b\u524d\uff0c\u5965\u5609\u83b1\u65af\u8ba9\u5979\u7559\u5728\u6b64\u5730\u7b49\u5019\u3002\u742a\u5a05\u8fd9\u6bb5\u65f6\u95f4\u6765\u4e00\u76f4\u5728\u9644\u8fd1\u6e38\u8361\uff0c\u7b49\u5f85\u7740\uff0c\u76f4\u5230\u743c\u6069\u51fa\u73b0\u3002</p><p>\u7eb8\u8239\u987a\u6d41\u800c\u4e0b\uff0c\u591c\u665a\u7684\u98ce\u975e\u5e38\u51c9\uff0c\u8352\u91ce\u4e2d\u5bc2\u9759\u65e0\u58f0\uff0c\u552f\u6709\u866b\u9e23\u3002\u51db\u627e\u7ef4\u82e5\u62c9\u804a\u5929\u53bb\u4e86\uff0c\u838e\u73de\u514b\u4e5f\u4e0d\u77e5\u9053\u9690\u8eab\u5728\u54ea\u4e2a\u89d2\u843d\uff0c\u628a\u743c\u6069\u4e00\u4e2a\u4eba\u7559\u5728\u8239\u5934\u7532\u677f\u4e0a\uff0c\u966a\u4f34\u4ed6\u7684\u53ea\u6709\u9ed1\u732b\uff0c\u522b\u65e0\u5176\u4ed6\u3002</p><p>\u743c\u6069\u4e0e\u742a\u5a05\u4e0d\u7b97\u5f88\u719f\uff0c\u5355\u72ec\u76f8\u5904\u53cd\u800c\u5c34\u5c2c\uff0c\u7d22\u6027\u76f4\u63a5\u5c06\u5979\u6536\u8fdb\u4e66\u4e2d\uff0c\u4ea4\u7ed9\u73ca\u5609\u3002\u59d0\u59d0\u7684\u7cbe\u795e\u72b6\u6001\u770b\u8d77\u6765\u7a0d\u7a0d\u6062\u590d\uff0c\u548c\u743c\u6069\u804a\u4e86\u4e00\u4f1a\uff0c\u201c\u743c\u6069\uff0c\u201d\u5979\u7a81\u7136\u95ee\uff0c\u201c\u90a3\u4e2a\u624e\u745e\u5c14\uff0c\u548c\u4f60\u5173\u7cfb\u5f88\u6df1\u662f\u5427\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d\u743c\u6069\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u201c\u8001\u5e08\u7684\u53bb\u4e16\uff0c\u867d\u7136\u662f\u81ea\u8eab\u8c0b\u7b97\u5931\u8d25\uff0c\u906d\u9047\u53cd\u566c\uff0c\u4f46\u4e5f\u6709\u5979\u5728\u5176\u95f4\u63a8\u6ce2\u52a9\u6f9c\u7684\u7f18\u6545\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u5c3d\u7ba1\u5df2\u7ecf\u77e5\u9053\u81ea\u5df1\u548c\u5965\u5609\u83b1\u65af\u7684\u5173\u7cfb\uff0c\u5979\u4ecd\u7136\u4e60\u60ef\u6027\u5730\u79f0\u547c\u4e3a\u8001\u5e08\u800c\u975e\u6bcd\u4eb2\uff0c\u201c\u6211\u4e0d\u4f1a\u628a\u6240\u6709\u7684\u8d23\u4efb\u90fd\u5f52\u548e\u4e8e\u5979\uff0c\u4f46\u6211\u4e5f\u4e0d\u4f1a\u539f\u8c05\u5979\u7684\u3002\u201d</p><p>\u743c\u6069\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u6211\u77e5\u9053\uff0c\u59d0\u59d0\u3002\u201d</p><p>\u624e\u745e\u5c14\u8fde\u73af\u5e03\u5c40\uff0c\u5c06\u9664\u4e86\u743c\u6069\u4e4b\u5916\u7684\u6240\u6709\u4eba\u90fd\u7b97\u8ba1\u4e86\uff0c\u6362\u53e5\u8bdd\u8bf4\uff0c\u5979\u4e5f\u628a\u6240\u6709\u4eba\u90fd\u5f97\u7f6a\u4e86\u3002\u6709\u4e9b\u4eba\u5f97\u7f6a\u4e86\u65e0\u59a8\uff0c\u53cd\u6b63\u5df2\u7ecf\u662f\u654c\u4eba\uff0c\u6216\u8005\u5df2\u7ecf\u662f\u6b7b\u4eba\uff1b\u4f46\u6709\u4e9b\u4eba\u5f97\u7f6a\u4e86\u5c31\u5f88\u9ebb\u70e6\u2014\u2014\u5012\u4e0d\u662f\u624e\u745e\u5c14\u9ebb\u70e6\uff0c\u800c\u662f\u743c\u6069\u4f1a\u6709\u9ebb\u70e6\uff0c\u6bd4\u5982\u73ca\u5609\u3002\u4e24\u8fb9\u90fd\u662f\u5fc3\u7231\u7684\u5973\u4eba\uff0c\u54ea\u4e00\u8fb9\u90fd\u4e0d\u597d\u504f\u8892\uff0c\u771f\u662f\u4ee4\u4eba\u5934\u75bc\u7684\u4e8b\u60c5\u3002</p><p>\u8bf4\u8d77\u6765\uff0c\u624e\u745e\u5c14\u5bf9\u5f85\u73ca\u5609\u7684\u6001\u5ea6\u660e\u663e\u4e0e\u5bf9\u5f85\u5176\u4ed6\u4eba\u4e0d\u540c\uff0c\u867d\u7136\u5979\u4e0d\u80af\u660e\u8bf4\uff0c\u4f46\u743c\u6069\u53c8\u4e0d\u7b28\uff0c\u7ed3\u5408\u79cd\u79cd\u8ff9\u8c61\uff0c\u5fc3\u4e2d\u81ea\u7136\u6709\u6240\u731c\u6d4b\u3002\u53ef\u662f\u5982\u679c\u731c\u6d4b\u5c5e\u5b9e\uff0c\u90a3\u95ee\u9898\u5c31\u66f4\u4e25\u91cd\uff0c\u4e8b\u60c5\u5c31\u66f4\u9ebb\u70e6\u4e86\u3002</p><p>\u7b97\u4e86\uff0c\u53cd\u6b63\u624e\u745e\u5c14\u90fd\u56de\u5730\u72f1\u53bb\u4e86\uff0c\u77ed\u65f6\u95f4\u5185\u4e5f\u4e0d\u4f1a\u518d\u78b0\u5230\u3002\u8fd8\u662f\u5b66\u5b66\u9e35\u9e1f\uff0c\u5f97\u8fc7\u4e14\u8fc7\u5427\uff0c\u8fd9\u4e2a\u95ee\u9898\u5c31\u4e0d\u8981\u6df1\u7a76\u4e86\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u59d0\u59d0\uff0c\u6211\u51c6\u5907\u53bb\u4e00\u8d9f\u4e1c\u57df\uff0c\u6709\u70b9\u4e8b\u60c5\u8981\u529e\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u966a\u6211\u4e00\u8d77\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u4e1c\u57df\uff1f\u201d</p><p>\u201c\u5728\u5760\u661f\u6d77\u897f\u5357\u8fb9\uff0c\u201d\u743c\u6069\u60f3\u591a\u4ecb\u7ecd\u51e0\u53e5\uff0c\u7ed3\u679c\u53d1\u73b0\u81ea\u5df1\u5bf9\u4e1c\u57df\u5176\u5b9e\u4e5f\u8fd1\u4e4e\u4e00\u65e0\u6240\u77e5\uff0c\u201c\u6709\u70b9\u8fdc\uff0c\u4e0d\u8fc7\u542c\u8bf4\u98ce\u666f\u633a\u597d\u7684\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\u4e1c\u57df\uff0c\u8001\u5e08\u8ddf\u6211\u63d0\u8fc7\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u90a3\u5c31\u53bb\u5427\uff0c\u6211\u5f53\u7136\u548c\u4f60\u4e00\u8d77\u3002\u201d</p><p>\u73ca\u5609\u7684\u53cd\u5e94\u8ba9\u743c\u6069\u89c9\u5f97\u4f3c\u4e4e\u6709\u4e9b\u5f02\u6837\uff0c\u4f46\u53c8\u8bf4\u4e0d\u51fa\u54ea\u91cc\u6709\u95ee\u9898\u3002\u59d0\u5f1f\u4fe9\u90fd\u6ca1\u6709\u518d\u8bf4\u8bdd\uff0c\u6c14\u6c1b\u4e00\u65f6\u95f4\u53d8\u5f97\u6709\u4e9b\u5c34\u5c2c\u3002\u201c\u53bb\u770b\u770b\u827e\u5f25\u8587\u5427\uff0c\u201d\u73ca\u5609\u5782\u4e0b\u773c\u5e18\uff0c\u201c\u6211\u60f3\u4f11\u606f\u4e00\u4f1a\uff0c\u5934\u6709\u70b9\u6655\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u6885\u83f2\u65af\u4ecd\u7136\u5728\u6c89\u7761\uff0c\u66e6\u5929\u4f7f\u5b88\u62a4\u7740\u5979\uff0c\u8868\u793a\u4e00\u5207\u72b6\u51b5\u6b63\u5e38\uff0c\u5e76\u65e0\u5927\u788d\uff0c\u4f30\u8ba1\u4e0d\u4e45\u5c31\u4f1a\u82cf\u9192\u3002\u6b23\u5e03\u90a3\u8fb9\u5219\u6bd4\u8f83\u9ebb\u70e6\uff0c\u94f6\u706b\u4ecd\u7136\u71c3\u70e7\u4e0d\u7184\uff0c\u4e1d\u6beb\u6ca1\u6709\u4ec0\u4e48\u597d\u8f6c\u7684\u8ff9\u8c61\uff0c\u4e0d\u8fc7\u5012\u4e5f\u6ca1\u6709\u8fdb\u4e00\u6b65\u6076\u5316\u7684\u5f81\u5146\uff0c\u6682\u65f6\u5e94\u8be5\u8fd8\u80fd\u7ef4\u6301\u3002\u6309\u7167\u7ef4\u82e5\u62c9\u7684\u8bf4\u6cd5\uff0c\u547d\u8fd0\u957f\u591c\u8fd9\u672c\u4e66\u9694\u7edd\u5185\u5916\uff0c\u7ed9\u6b23\u5e03\u5236\u9020\u4e86\u4e00\u4e2a\u76f8\u5bf9\u7a33\u5b9a\u800c\u72ec\u7acb\u7684\u73af\u5883\uff0c\u53ea\u8981\u5728\u4e66\u4e2d\uff0c\u5979\u5c31\u662f\u5b89\u5168\u7684\uff0c\u4f46\u8fd9\u6bd5\u7adf\u4e0d\u662f\u957f\u4e45\u4e4b\u8ba1\u3002</p><p>\u521a\u521a\u7ecf\u5386\u4e00\u573a\u5927\u6218\uff0c\u743c\u6069\u7684\u7cbe\u795e\u4e5f\u5df2\u7ecf\u975e\u5e38\u75b2\u5026\uff0c\u4ed6\u61d2\u5f97\u56de\u623f\u95f4\uff0c\u76f4\u63a5\u8eba\u5728\u7532\u677f\u4e0a\u4f11\u606f\uff0c\u8ff7\u8ff7\u7cca\u7cca\u95f4\u5df2\u7ecf\u6709\u4e86\u7761\u610f\uff0c\u5c31\u542c\u89c1\u7ec6\u788e\u7684\u811a\u6b65\u58f0\u7531\u8fdc\u53ca\u8fd1\uff0c\u5728\u4ed6\u8eab\u8fb9\u505c\u4e0b\u6765\u3002\u201c\u5582\uff0c\u743c\u6069\uff0c\u201d\u51db\u7528\u811a\u5c16\u8e22\u4e86\u8e22\u4ed6\uff0c\u201c\u5feb\u8d77\u6765\uff0c\u6211\u6709\u4e8b\u95ee\u4f60\u3002\u201d</p><p>\u743c\u6069\u61d2\u6d0b\u6d0b\u5730\u7ffb\u4e86\u4e2a\u8eab\uff0c\u201c\u4f60\u8bf4\u5427\uff0c\u6211\u542c\u7740\u5462\uff0c\u4ec0\u4e48\u4e8b\u3002\u201d</p><p>\u201c\u4f60\u548c\u5854\u59d0\u59d0\uff0c\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u201c\u2026\u2026\u6ca1\u4ec0\u4e48\u56de\u4e8b\u554a\uff0c\u4e0d\u662f\u8bf4\u4e86\u5417\uff0c\u5979\u662f\u6211\u65b0\u4ea4\u7684\u5973\u670b\u53cb\u3002\u201d</p><p>\u201c\u9a97\u4eba\uff01\u201d\u51db\u8bf4\uff0c\u201c\u6211\u8981\u542c\u771f\u8bdd\u3002\u201d</p><p>\u201c\u662f\u4e0d\u662f\u5979\u8bf4\u4e86\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u5979\u4ec0\u4e48\u90fd\u6ca1\u8bf4\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4e5f\u4e0d\u9700\u8981\u5979\u8bf4\u554a\uff0c\u6211\u4e00\u5f00\u59cb\u5c31\u77e5\u9053\u4f60\u5728\u6492\u8c0e\uff0c\u53ea\u662f\u5f53\u65f6\u5979\u5728\u573a\uff0c\u6211\u7ed9\u4f60\u7559\u70b9\u9762\u5b50\uff0c\u6ca1\u6709\u5f53\u573a\u6233\u7a7f\u800c\u5df2\u3002\u4f60\u8d76\u5feb\u8001\u5b9e\u4ea4\u4ee3\uff0c\u8fd8\u6709\u5766\u767d\u4ece\u5bbd\u7684\u4f59\u5730\uff0c\u4e0d\u7136\u6211\u5c31\u53bb\u5411\u827e\u5f25\u8587\u6253\u5c0f\u62a5\u544a\u4e86\u3002\u201d</p><p>\u201c\u4f60\u4e00\u5f00\u59cb\u5c31\u77e5\u9053\u6211\u5728\u6492\u8c0e\uff1f\u201d</p><p>\u201c\u5e9f\u8bdd\uff0c\u4f60\u771f\u4ee5\u4e3a\u6211\u80f8\u5927\u65e0\u8111\u4e48\u3002\u201d</p><p>\u8bf4\u5b9e\u8bdd\uff0c\u6211\u7684\u786e\u4e00\u76f4\u662f\u8fd9\u4e48\u4ee5\u4e3a\u7684\u2026\u2026</p><p>\u201c\u6211\u7684\u8bf4\u6cd5\u54ea\u91cc\u6709\u95ee\u9898\uff0c\u201d\u743c\u6069\u5750\u8d77\u8eab\u6765\uff0c\u95ee\u51db\uff0c\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u5728\u6492\u8c0e\u5462\uff1f\u201d</p><p>\u201c\u95ee\u9898\u591a\u4e86\u53bb\u4e86\uff0c\u201d\u51db\u4e0d\u5c51\u5730\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u6211\u95ee\u4f60\uff0c\u4f60\u4ec0\u4e48\u65f6\u5019\u8ba4\u8bc6\u5979\u7684\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u5c31\u5728\u524d\u4e0d\u4e45\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u4eec\u4e00\u89c1\u949f\u60c5\uff0c\u4e0d\u884c\u554a\u3002\u201d</p><p>\u201c\u4e00\u89c1\u949f\u60c5\u5f88\u6b63\u5e38\uff0c\u5c31\u50cf\u4f60\u5bf9\u6211\uff0c\u7b2c\u4e00\u773c\u770b\u89c1\u5c31\u9677\u5165\u7231\u6cb3\u65e0\u6cd5\u81ea\u62d4\u5bf9\u5427\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u95ee\u9898\u5728\u4e8e\u4f60\u662f\u5728\u54ea\u91cc\u548c\u5979\u4e00\u89c1\u949f\u60c5\u7684\uff1f\u201d</p><p>\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u201c\u5f53\u7136\u662f\u5728\u8428\u9a6c\u65af\u7279\u90a3\u8fb9\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5979\u4e0d\u662f\u88ab\u4fd8\u864f\u4e86\u5417\u3002\u6211\u90a3\u6b21\u53bb\u62dc\u8bbf\u51ef\u745f\u7433\uff0c\u987a\u4fbf\u89c1\u4e86\u89c1\u8428\u9a6c\u65af\u7279\uff0c\u7136\u540e\u5076\u7136\u5de7\u9047\u4e86\u5979\uff0c\u7136\u540e\u5c31\u4e00\u89c1\u949f\u60c5\u4e86\u2014\u2014\u5bf9\uff0c\u5c31\u662f\u8fd9\u6837\u6ca1\u9519\u3002\u201d</p><p>\u201c\u6ca1\u9519\u4f60\u4e2a\u5934\uff01\u201d\u51db\u62cd\u6848\u800c\u8d77\uff0c\u201c\u4f60\u53bb\u89c1\u90a3\u4e2a\u8c01\uff0c\u54e6\uff0c\u51ef\u745f\u7433\uff0c\u53bb\u4e86\u591a\u4e45\uff1f\u4e5f\u5c31\u4e00\u5929\u534a\u5de6\u53f3\u5bf9\u5427\u3002\u201d</p><p>\u201c\u55ef\uff0c\u5dee\u4e0d\u591a\u3002\u201d</p><p>\u201c\u90a3\u4f60\u524d\u9762\u8bf4\u548c\u5979\u671d\u5915\u76f8\u5904\u4e5d\u5929\u4e5d\u591c\uff0c\u53c8\u600e\u4e48\u89e3\u91ca\uff1f\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u743c\u6069\u5fc3\u4e2d\u6697\u53eb\u4e0d\u5999\uff0c\u5c45\u7136\u65e0\u610f\u95f4\u9732\u51fa\u8fd9\u4e48\u5927\u4e00\u4e2a\u7834\u7efd\uff0c\u96be\u602a\u51db\u4e00\u542c\u5c31\u89c9\u5f97\u4e0d\u5bf9\u3002\u5f53\u65f6\u51ef\u745f\u7433\u626d\u66f2\u65f6\u7a7a\uff0c\u786c\u751f\u751f\u628a\u4e00\u5929\u65f6\u95f4\u5ef6\u957f\u6210\u4e86\u4e5d\u5929\uff0c\u8ba9\u743c\u6069\u5c3d\u53ef\u80fd\u4e45\u5730\u548c\u7ef4\u82e5\u62c9\u76f8\u5904\uff0c\u5438\u53d6\u795e\u529b\u3002\u4f46\u8fd9\u4ef6\u4e8b\u9664\u4e86\u624e\u745e\u5c14\uff0c\u5176\u4ed6\u4eba\u662f\u4e0d\u77e5\u9053\u7684\u3002\u73b0\u5728\u8981\u89e3\u91ca\u4e5f\u4e0d\u592a\u5bb9\u6613\uff0c\u743c\u6069\u987f\u65f6\u82e6\u607c\u8d77\u6765\uff0c\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u8bf4\u3002</p><p>\u201c\u8d76\u5feb\u5982\u5b9e\u62db\u4f9b\uff01\u201d\u51db\u7cbe\u81f4\u7684\u8138\u5e9e\u8d34\u8fd1\u8fc7\u6765\uff0c\u6d45\u7eff\u8272\u7684\u5927\u773c\u775b\u5ffd\u95ea\u5ffd\u95ea\uff0c\u201c\u4e0d\u51c6\u518d\u7f16\u7406\u7531\uff0c\u5426\u5219\u6211\u771f\u8981\u751f\u6c14\u4e86\u3002\u201d</p><p>\u743c\u6069\u5bdf\u8a00\u89c2\u8272\uff0c\u610f\u8bc6\u5230\u5c0f\u5973\u5deb\u5e76\u4e0d\u5b8c\u5168\u662f\u5728\u6050\u5413\uff0c\u5979\u786e\u5b9e\u5df2\u7ecf\u6709\u4e9b\u4e0d\u60a6\u3002\u4ee5\u5979\u7684\u6027\u683c\uff0c\u5012\u4e0d\u89c1\u5f97\u662f\u56e0\u4e3a\u743c\u6069\u82b1\u5fc3\u4e71\u641e\uff0c\u66f4\u6709\u53ef\u80fd\u662f\u56e0\u4e3a\u7ef4\u82e5\u62c9\u7684\u8eab\u4efd\u3002\u5c3d\u7ba1\u6b64\u524d\u4ece\u6ca1\u89c1\u8fc7\uff0c\u4f46\u7ef4\u82e5\u62c9\u6bd5\u7adf\u662f\u6b23\u5e03\u7684\u5b66\u751f\uff0c\u548c\u51db\u662f\u5e08\u59d0\u59b9\u7684\u5173\u7cfb\uff0c\u51db\u7236\u6bcd\u5f88\u65e9\u53bb\u4e16\uff0c\u8fd9\u4e16\u754c\u4e0a\u7684\u4eb2\u4eba\u5e76\u4e0d\u591a\uff0c\u6885\u83f2\u65af\u662f\u4e00\u4e2a\uff0c\u73b0\u5728\u5728\u6c89\u7761\uff0c\u6b23\u5e03\u7b97\u4e00\u4e2a\uff0c\u73b0\u5728\u72b6\u51b5\u5f88\u5371\u9669\uff0c\u8bf4\u4e0d\u5b9a\u4ec0\u4e48\u65f6\u5019\u5c31\u62a2\u6551\u4e0d\u8fc7\u6765\u3002\u51db\u8868\u9762\u4e0a\u82e5\u65e0\u5176\u4e8b\uff0c\u5fc3\u91cc\u4e0d\u53ef\u80fd\u6ca1\u6709\u5fe7\u8651\u7126\u6025\uff0c\u504f\u504f\u5c31\u5728\u8fd9\u79cd\u65f6\u5019\uff0c\u743c\u6069\u53c8\u5e26\u56de\u6765\u4e00\u4e2a\u5973\u4eba\uff0c\u800c\u8fd9\u4e2a\u5973\u4eba\u8fd8\u6070\u597d\u662f\u81ea\u5df1\u5e08\u59d0\uff0c\u4e24\u4eba\u4e4b\u95f4\u7684\u5173\u7cfb\u53c8\u660e\u663e\u6709\u95ee\u9898\uff0c\u8003\u8651\u5230\u743c\u6069\u4ee5\u5f80\u7684\u52a3\u8ff9\uff0c\u51db\u6ca1\u6709\u7acb\u523b\u53d1\u98d9\uff0c\u7684\u786e\u7b97\u662f\u50cf\u5979\u6240\u8a00\uff0c\u5728\u201c\u5916\u4eba\u201d\u9762\u524d\u7ed9\u81ea\u5df1\u7537\u4eba\u7559\u9762\u5b50\u4e86\u3002</p><p>\u610f\u8bc6\u5230\u8fd9\u4e00\u70b9\uff0c\u743c\u6069\u518d\u65e0\u72b9\u8c6b\uff0c\u4e3e\u624b\u6295\u964d\uff0c\u201c\u6211\u8bf4\uff0c\u6211\u5168\u62db\u4f9b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u522b\u7740\u6025\uff0c\u542c\u6211\u89e3\u91ca\u3002\u201d</p><p>\u743c\u6069\u628a\u4e8b\u60c5\u7ecf\u8fc7\u5927\u81f4\u8bf4\u4e86\u4e00\u904d\uff0c\u5f53\u7136\uff0c\u6709\u4e9b\u7ec6\u8282\u8fd8\u662f\u5c3d\u91cf\u80fd\u542b\u7cca\u5c31\u542b\u7cca\u8fc7\u53bb\u4e86\uff0c\u5438\u53d6\u795e\u529b\u4ec0\u4e48\u7684\u4e5f\u6ca1\u8bf4\uff0c\u53ea\u8bf4\u81ea\u5df1\u770b\u89c1\u7ef4\u82e5\u62c9\u5c31\u8272\u5fc3\u6d8c\u52a8\uff0c\u4e8e\u662f\u627e\u8428\u9a6c\u65af\u7279\u505a\u4ea4\u6613\uff0c\u628a\u5979\u501f\u8fc7\u6765\u73a9\u4e86\u51e0\u5929\u3002\u201c\u4e8b\u60c5\u5c31\u662f\u8fd9\u6837\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u522b\u751f\u6c14\u5566\uff0c\u6211\u77e5\u9053\u6211\u505a\u5f97\u4e0d\u5bf9\uff0c\u4e0d\u5e94\u8be5\u786c\u4e0a\u2014\u2014\u201d</p><p>\u51db\u7684\u8138\u8272\u4e00\u5f00\u59cb\u5f88\u4e0d\u597d\u770b\uff0c\u542c\u5230\u4e00\u534a\u540e\u5374\u53d8\u5f97\u5f88\u53e4\u602a\uff0c\u4e00\u526f\u60f3\u7b11\u53c8\u52c9\u5f3a\u5fcd\u4f4f\u7684\u6a21\u6837\uff0c\u6700\u540e\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u4e86\uff0c\u5657\u55e4\u4e00\u58f0\u7b11\u51fa\u6765\uff0c\u6253\u65ad\u4e86\u743c\u6069\u7684\u9053\u6b49\uff0c\u201c\u4f60\u662f\u8bf4\uff0c\u4f60\u88ab\u5979\u4e0b\u4e86\u4e00\u4e2a\u4e0d\u4e3e\u8bc5\u5492\uff1f\u201d</p><p>\u201c\u662f\u2018\u4e13\u60c5\u8bc5\u5492\u2019\uff0c\u4e0d\u662f\u4e0d\u4e3e\uff0c\u201d\u743c\u6069\u600f\u600f\u5730\u8bf4\uff0c\u201c\u8fd8\u662f\u80fd\u4e3e\u7684\uff0c\u53ea\u662f\u5bf9\u8c61\u4ec5\u9650\u4e8e\u5979\u3002\u201d</p><p>\u201c\u90a3\u4e0d\u5c31\u662f\u4e0d\u4e3e\uff0c\u54c8\u54c8\uff01\u54c8\u54c8\u54c8\u54c8\uff01\u201d\u51db\u7b11\u5f97\u5dee\u70b9\u76f4\u4e0d\u8d77\u8170\uff0c\u201c\u592a\u6709\u8da3\u4e86\uff0c\u5854\u59d0\u59d0\u771f\u662f\u6709\u521b\u610f\uff0c\u5c45\u7136\u80fd\u60f3\u5230\u8fd9\u529e\u6cd5\u6765\u60e9\u6cbb\u4f60\uff0c\u54ce\u54df\uff0c\u4e0d\u884c\u4e86\uff0c\u6211\u5c94\u6c14\u4e86\uff0c\u6211\u8981\u6b47\u4f1a\u3002\u201d</p><p>\u201c\u4f60\u8fd8\u7b11\uff0c\u201d\u743c\u6069\u677f\u7740\u8138\uff0c\u201c\u6211\u8981\u662f\u771f\u7684\u4e0d\u4e3e\uff0c\u4f60\u4ee5\u540e\u7684\u6027\u798f\u751f\u6d3b\u4e5f\u6ca1\u6709\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u5173\u7cfb\u554a\uff0c\u6211\u6709\u827e\u5f25\u8587\u5462\uff0c\u201d\u51db\u8868\u793a\u5b8c\u5168\u4e0d\u4ecb\u610f\uff0c\u201c\u6b63\u597d\u5c11\u4e86\u4f60\u8fd9\u4e2a\u969c\u788d\uff0c\u6211\u548c\u827e\u5f25\u8587\u5c31\u53ef\u4ee5\u6bcf\u5929\u5728\u4e00\u8d77\u4e86\uff0c\u8fd9\u753b\u9762\u60f3\u4e00\u60f3\u5c31\u7f8e\u597d\uff0c\u771f\u662f\u503c\u5f97\u5411\u5f80\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u662f\u4e0d\u662f\u5e94\u8be5\u8868\u793a\u795d\u8d3a\u3002\u201d</p><p>\u51db\u7b11\u4e86\u4e00\u901a\uff0c\u7ec8\u4e8e\u7f13\u8fc7\u6c14\u6765\u3002\u201c\u597d\u5566\uff0c\u4e8b\u60c5\u6211\u6e05\u695a\u4e86\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5230\u5904\u4e71\u641e\u662f\u4f60\u4e0d\u5bf9\uff0c\u81f3\u5c11\u4e5f\u8be5\u548c\u6211\u4eec\u8bf4\u4e00\u58f0\u554a\uff0c\u672c\u6765\u4f60\u5e94\u8be5\u5411\u5854\u59d0\u59d0\u9053\u6b49\uff0c\u4e0d\u8fc7\u65e2\u7136\u4f60\u90fd\u5df2\u7ecf\u8fd9\u4e48\u60e8\u4e86\uff0c\u90a3\u5c31\u5148\u7b97\u4e86\uff0c\u7b49\u8bc5\u5492\u89e3\u9664\u4e86\u518d\u8bf4\u3002\u827e\u5f25\u8587\u90a3\u8fb9\uff0c\u6211\u53ef\u4ee5\u4fdd\u8bc1\u4ec0\u4e48\u90fd\u4e0d\u4f1a\u8bf4\uff0c\u4f46\u5979\u770b\u5230\u5854\u59d0\u59d0\uff0c\u95ee\u8d77\u6765\uff0c\u6211\u53ef\u4e0d\u4f1a\u5e2e\u4f60\u906e\u63a9\uff0c\u4f60\u81ea\u5df1\u60f3\u529e\u6cd5\u89e3\u91ca\uff0c\u80fd\u4e0d\u80fd\u7792\u8fc7\u53bb\uff0c\u5c31\u770b\u4f60\u7684\u672c\u4e8b\u4e86\u3002\u201d</p><p>\u201c\u597d\u5427\u3002\u201d\u743c\u6069\u8bf4\uff0c\u5bf9\u7792\u8fc7\u6885\u83f2\u65af\u4e0d\u62a5\u4efb\u4f55\u671f\u671b\u3002\u8fde\u4f60\u8fd9\u79cd\u80f8\u5927\u65e0\u8111\u7684\u5bb6\u4f19\u90fd\u9a97\u4e0d\u8fc7\uff0c\u8fd8\u60f3\u9a97\u827e\u5f25\u8587\uff0c\u4e0d\u662f\u641e\u7b11\u4e48\u3002</p><p>\u201c\u53e6\u5916\uff0c\u4e0d\u51c6\u4f60\u518d\u6b3a\u8d1f\u5854\u59d0\u59d0\uff0c\u201d\u51db\u53c8\u8865\u5145\u4e86\u4e00\u53e5\uff0c\u201c\u9664\u975e\u5979\u81ea\u613f\uff0c\u5426\u5219\u4f60\u4e0d\u8bb8\u53bb\u9a9a\u6270\u5979\u3002\u201d</p><p>\u201c\u90a3\u6211\u600e\u4e48\u529e\uff0c\u201d\u743c\u6069\u8868\u793a\u6297\u8bae\uff0c\u201c\u5979\u8fd9\u4e2a\u8bc5\u5492\u4e00\u65f6\u534a\u4f1a\u53c8\u89e3\u4e0d\u6389\u3002\u96be\u9053\u8981\u6211\u4e00\u76f4\u5fcd\u7740\u5417\uff1f\u201d</p><p>\u201c\u5fcd\u7740\uff01\u201d\u51db\u8bf4\uff0c\u201c\u8c01\u8ba9\u4f60\u5230\u5904\u4e71\u641e\uff0c\u6d3b\u8be5\u3002\u201d</p><p>\u201c\u2026\u2026\u597d\u5427\u3002\u201d</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u51db\u7b97\u662f\u6682\u65f6\u5b89\u629a\u4e0b\u6765\u3002\u4e24\u4eba\u80a9\u5e76\u80a9\u5730\u8eba\u5728\u7532\u677f\u4e0a\uff0c\u542c\u7740\u8033\u7554\u7684\u6cb3\u6d41\u6c34\u58f0\uff0c\u770b\u7740\u7e41\u661f\u70b9\u70b9\u7684\u591c\u7a7a\uff0c\u743c\u6069\u53ea\u89c9\u7761\u610f\u6e10\u6d53\uff0c\u773c\u76ae\u5df2\u7ecf\u6c89\u91cd\u7684\u62ac\u4e0d\u8d77\u6765\u3002\u201c\u743c\u6069\uff0c\u201d\u8ff7\u8ff7\u7cca\u7cca\u4e4b\u95f4\uff0c\u4ed6\u7a81\u7136\u542c\u89c1\u51db\u95ee\uff0c\u201c\u4f60\u559c\u6b22\u6211\u5417\uff1f\u201d</p><p>\u201c\u559c\u6b22\u3002\u201d\u743c\u6069\u968f\u53e3\u56de\u7b54\u3002</p><p>\u201c\u6709\u591a\u559c\u6b22\uff1f\u201d</p><p>\u201c\u5f88\u559c\u6b22\uff0c\u975e\u5e38\u559c\u6b22\uff0c\u5c31\u50cf\u2026\u2026\u5c31\u50cf\u4f60\u559c\u6b22\u5b9d\u77f3\u90a3\u6837\u559c\u6b22\u3002\u201d</p><p>\u201c\u4eba\u5bb6\u624d\u4e0d\u559c\u6b22\u5b9d\u77f3\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4eba\u5bb6\u559c\u6b22\u7684\u662f\u827e\u5f25\u8587\u3002\u201d</p><p>\u201c\u90a3\u5c31\u662f\u50cf\u4f60\u559c\u6b22\u827e\u5f25\u8587\u90a3\u6837\u559c\u6b22\u3002\u201d</p><p>\u201c\u90a3\u4f60\u662f\u66f4\u559c\u6b22\u827e\u5f25\u8587\uff0c\u8fd8\u662f\u66f4\u559c\u6b22\u6211\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u5c3d\u7ba1\u8fd8\u5728\u534a\u7761\u534a\u9192\u4e4b\u4e2d\uff0c\u743c\u6069\u8fd8\u662f\u672c\u80fd\u5730\u5bdf\u89c9\u5230\u8fd9\u4e2a\u95ee\u9898\u7684\u6050\u6016\u7a0b\u5ea6\uff0c\u4e0b\u610f\u8bc6\u5730\u4e0d\u6562\u4f5c\u7b54\u3002\u51db\u89c1\u4ed6\u4e0d\u7b54\uff0c\u4e8e\u662f\u53c8\u6362\u4e86\u4e2a\u95ee\u9898\uff0c\u201c\u90a3\u4f60\u5230\u5e95\u559c\u6b22\u6211\u54ea\u4e00\u70b9\u5462\uff1f\u201d</p><p>\u201c\u5f88\u591a\u554a\uff0c\u201d\u8fd9\u4e2a\u95ee\u9898\u5c31\u6bd4\u8f83\u7b80\u5355\u4e86\uff0c\u201c\u559c\u6b22\u4f60\u7684\u7f8e\u8c8c\uff0c\u559c\u6b22\u4f60\u7684\u58f0\u97f3\uff0c\u559c\u6b22\u4f60\u7684\u53ef\u7231\uff0c\u559c\u6b22\u4f60\u7684\u6d3b\u529b\u56db\u5c04\uff0c\u53cd\u6b63\u7edf\u7edf\u90fd\u559c\u6b22\u5566\u3002\u201d</p><p>\u201c\u90a3\u4f60\u4f1a\u4e0d\u4f1a\u4e00\u76f4\u966a\u7740\u6211\u3002\u201d</p><p>\u201c\u4f1a\u554a\uff0c\u53ea\u8981\u4f60\u613f\u610f\uff0c\u4e0d\u5bf9\uff0c\u5c31\u7b97\u4f60\u4e0d\u613f\u610f\uff0c\u6211\u4e5f\u8981\u7f20\u7740\u4f60\u3002\u201d</p><p>\u201c\u8fd9\u8fd8\u5dee\u4e0d\u591a\u3002\u201d</p><p>\u51db\u4f3c\u4e4e\u7ec8\u4e8e\u6ee1\u610f\u4e86\uff0c\u4e0d\u518d\u53d1\u95ee\u3002\u743c\u6069\u5f88\u5feb\u9677\u5165\u68a6\u60f3\uff0c\u5728\u5165\u7761\u4e4b\u524d\uff0c\u4ed6\u611f\u89c9\u6709\u4ec0\u4e48\u4e1c\u897f\u76d6\u5728\u8eab\u4e0a\uff0c\u7136\u540e\u4e00\u5177\u9999\u6ed1\u706b\u70ed\u7684\u8eab\u4f53\u8d34\u8fd1\u8fc7\u6765\uff0c\u62b1\u7740\u4ed6\uff0c\u4ed6\u7684\u8033\u5782\u88ab\u8f7b\u8f7b\u7684\u54ac\u556e\u7740\uff0c\u5c0f\u5973\u5deb\u7684\u58f0\u97f3\u4eff\u4f5b\u4ece\u975e\u5e38\u9065\u8fdc\u7684\u5730\u65b9\u4f20\u6765\uff0c\u9690\u9690\u7ea6\u7ea6\uff0c\u82e5\u6709\u82e5\u65e0\uff0c\u201c\u90a3\u6211\u4e5f\u8981\u7f20\u7740\u4f60\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4e0d\u8bb8\u4f60\u9003\u8dd1\u3002\u201d</p><p>\u6211\u624d\u4e0d\u4f1a\u9003\u8dd1\u3002\u743c\u6069\u60f3\u8fd9\u4e48\u8bf4\uff0c\u4f46\u5df2\u7ecf\u5b9e\u5728\u6ca1\u529b\u6c14\u8bf4\u51fa\u53e3\uff0c\u53c8\u4e00\u9635\u6d53\u91cd\u7684\u7761\u610f\u88ad\u6765\uff0c\u4ed6\u771f\u6b63\u8fdb\u5165\u4e86\u68a6\u4e61\u3002</p><p>\u4e0d\u51fa\u610f\u5916\u5730\uff0c\u4ed6\u770b\u89c1\u4e86\u838e\u5c14\u3002</p><p>\u51c6\u786e\u5730\u8bf4\uff0c\u743c\u6069\u5e76\u6ca1\u6709\u771f\u7684\u201c\u770b\u89c1\u201d\u838e\u5c14\uff0c\u548c\u524d\u51e0\u6b21\u4e00\u6837\uff0c\u591c\u5973\u58eb\u4ecd\u7136\u6ca1\u6709\u663e\u9732\u771f\u5bb9\uff0c\u800c\u662f\u501f\u7528\u4e86\u4ed6\u4eba\u7684\u5f62\u8c61\u51fa\u73b0\u3002\u7d2b\u8272\u7684\u8fde\u8863\u77ed\u88d9\uff0c\u767d\u8272\u7684\u9ad8\u8ddf\u51c9\u978b\uff0c\u8eab\u6750\u5a07\u5c0f\u7684\u5c11\u5973\u4ece\u9ed1\u6697\u4e2d\u8d70\u51fa\u6765\uff0c\u4ef0\u8d77\u7cbe\u81f4\u7684\u9762\u5e9e\uff0c\u7b11\u76c8\u76c8\u5730\u770b\u7740\u4ed6\u3002</p><p>\u201c\u665a\u4e0a\u597d\uff0c\u743c\u6069\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u600e\u4e48\u4e86\uff0c\u8138\u8272\u5f88\u5947\u602a\u7684\u6837\u5b50\u3002\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u53ea\u662f\u2026\u2026\u6709\u70b9\u4e0d\u592a\u9002\u5e94\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u7528\u4e86\u5979\u7684\u6a21\u6837\uff1f\u201d\u5c11\u5973\u5728\u539f\u5730\u4f18\u96c5\u5730\u8f6c\u4e86\u4e2a\u5708\uff0c\u201c\u5f88\u6f02\u4eae\u554a\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4f1a\u559c\u6b22\u5462\u3002\u5bf9\u4e86\uff0c\u4f60\u89c9\u5f97\u8fd9\u662f\u59d0\u59d0\u8fd8\u662f\u59b9\u59b9\uff1f\u201d</p><p>\u743c\u6069\u5728\u5979\u7684\u80f8\u53e3\u770b\u4e86\u4e00\u4f1a\uff0c\u201c\u662f\u59d0\u59d0\uff0c\u201d\u4ed6\u5f88\u6709\u628a\u63e1\u5730\u8bf4\uff0c\u201c\u8299\u8389\u5a05\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5c11\u5973\u5f88\u5947\u602a\uff0c\u201c\u4f60\u600e\u4e48\u770b\u51fa\u6765\u7684\uff1f\u5979\u4eec\u660e\u660e\u4e00\u6a21\u4e00\u6837\u5427\u3002\u201d</p><p>\u201c\u8299\u8389\u5a05\u7684\u80f8\u8981\u5927\u4e00\u70b9\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u5c11\u5973\u70b9\u70b9\u5934\uff0c\u201c\u4e0d\u8fc7\u4f60\u9519\u4e86\uff0c\u8fd9\u662f\u8299\u857e\u72c4\u2014\u2014\u5979\u6700\u8fd1\u80f8\u56f4\u53d1\u80b2\uff0c\u5df2\u7ecf\u8ffd\u4e0a\u59d0\u59d0\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u597d\u4e86\uff0c\u4e0d\u9017\u4f60\u4e86\uff0c\u201d\u838e\u5c14\u7b11\u7740\u8bf4\uff0c\u201c\u8fd8\u662f\u5148\u8bf4\u6b63\u4e8b\uff0c\u743c\u6069\uff0c\u8fd9\u4e00\u6b21\u4f60\u8fd8\u771f\u662f\u8ba9\u6211\u51fa\u4e4e\u610f\u6599\u5462\uff0c\u505a\u5f97\u6f02\u4eae\u3002\u201d</p><p>\u201c\u5176\u5b9e\u6211\u4ec0\u4e48\u90fd\u6ca1\u505a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u57fa\u672c\u4e0a\u5c31\u662f\u5728\u8896\u624b\u65c1\u89c2\u3002\u201d</p><p>\u201c\u9ad8\u4f4d\u8005\u4ece\u4e0d\u4eb2\u8eab\u4e0b\u573a\uff0c\u90fd\u662f\u8896\u624b\u65c1\u89c2\uff0c\u770b\u81ea\u5df1\u7684\u4ec6\u4eba\u4e92\u76f8\u4e89\u6597\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u89c4\u5219\u672c\u5c31\u5982\u6b64\u3002\u201d</p><p>\u201c\u624e\u745e\u5c14\u4e0d\u662f\u6211\u4ec6\u4eba\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5176\u4ed6\u4eba\u4e5f\u4e0d\u662f\uff0c\u5979\u4eec\u90fd\u4e0d\u662f\u3002\u201d</p><p>\u5973\u795e\u7b11\u8d77\u6765\uff0c\u773c\u89d2\u5f2f\u8d77\uff0c\u663e\u5f97\u5206\u5916\u7684\u59a9\u5a9a\u8bf1\u60d1\uff0c\u201c\u7684\u786e\u4e0d\u662f\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f46\u5979\u4eec\u90fd\u613f\u610f\u4e3a\u4f60\u800c\u6218\uff0c\u8fd9\u5c31\u8db3\u591f\u4e86\u3002\u201d</p><p>\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u51b3\u5b9a\u4e0d\u518d\u7ea0\u7f20\u8fd9\u4e2a\u8bdd\u9898\uff0c\u201c\u5973\u795e\u59d0\u59d0\uff0c\u201d\u4ed6\u76f4\u622a\u4e86\u5f53\u5730\u8bf4\uff0c\u201c\u4f60\u8981\u6211\u505a\u7684\u4e8b\u60c5\uff0c\u6211\u7b97\u662f\u505a\u5230\u4e86\uff0c\u800c\u4e14\u4f60\u4e5f\u627f\u8ba4\u8fd9\u662f\u6211\u7684\u529f\u52b3\u2014\u2014\u65e2\u7136\u5982\u6b64\uff0c\u6211\u662f\u4e0d\u662f\u5e94\u8be5\u6709\u4ec0\u4e48\u5956\u8d4f\u5462\uff1f\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d\u838e\u5c14\u4eff\u4f5b\u5f88\u8be7\u5f02\uff0c\u201c\u6211\u60f3\u60f3\uff0c\u55ef\uff0c\u6211\u7684\u786e\u662f\u8ba9\u4f60\u5e2e\u6211\u505a\u4ef6\u4e8b\u60c5\u3002\u53ef\u662f\u6211\u8bb0\u5f97\uff0c\u90a3\u662f\u56e0\u4e3a\u4f60\u4e4b\u524d\u60f9\u6211\u751f\u6c14\uff0c\u6240\u4ee5\u624d\u7b54\u5e94\u5e2e\u6211\u505a\u4ef6\u4e8b\u60c5\uff0c\u6362\u53d6\u6211\u7684\u539f\u8c05\uff0c\u4e0d\u662f\u5417\uff1f\u201d</p><p>\u201c\u2026\u2026\u662f\u8fd9\u6837\u6ca1\u9519\u3002\u201d</p><p>\u201c\u800c\u4e14\u4f60\u7684\u4e8b\u60c5\u4e5f\u6ca1\u6709\u505a\u6210\u554a\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u6211\u5f53\u65f6\u8ba9\u4f60\u9009\u62e9\uff0c\u6216\u8005\u53bb\u5e72\u6389\u5bc6\u65af\u62c9\u2014\u2014\u4e8b\u5b9e\u4e0a\u4f60\u53ea\u662f\u65a9\u6740\u4e86\u5979\u7684\u4e00\u4e2a\u5723\u8005\uff0c\u5979\u4ecd\u7136\u5b58\u5728\uff1b\u6216\u8005\u53bb\u5e72\u6389\u5979\u4e09\u4e94\u4e2a\u9009\u6c11\u2014\u2014\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u53ea\u6b7b\u4e86\u4e24\u4e2a\uff1b\u8fd9\u6837\u600e\u4e48\u80fd\u7b97\u662f\u4f60\u505a\u6210\u4e86\u5462\uff1f\u201d</p><p>\u201c\u53ea\u6b7b\u4e86\u4e24\u4e2a\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u54ea\u4e24\u4e2a\uff1f\u201d</p><p>\u201c\u83b1\u62c9\u548c\u591a\u8299\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u4e0d\u8fc7\u6b23\u5e03\u597d\u50cf\u5728\u4f60\u624b\u4e0a\uff1f\u5982\u679c\u4f60\u613f\u610f\u7684\u8bdd\uff0c\u53ef\u4ee5\u628a\u5979\u6740\u6389\uff0c\u8fd9\u6837\u5c31\u51d1\u591f\u4e09\u4e2a\u4e86\uff0c\u6211\u53ef\u4ee5\u52c9\u5f3a\u7b97\u4f60\u5408\u683c\u3002\u201d</p><p>\u201c\u90a3\u6211\u80af\u5b9a\u4f1a\u5148\u88ab\u4e00\u5934\u7ea2\u9f99\u55b7\u6b7b\u3002\u201d\u743c\u6069\u65e0\u7cbe\u6253\u91c7\u5730\u8bf4\u3002</p><p>\u201c\u5f00\u73a9\u7b11\u5566\uff0c\u201d\u838e\u5c14\u7b11\u7740\u8bf4\uff0c\u201c\u59d0\u59d0\u600e\u4e48\u4f1a\u8fd9\u4e48\u4e0d\u8fd1\u4eba\u60c5\u5462\uff1f\u867d\u8bf4\u7ed3\u679c\u4e0d\u591f\u5b8c\u7f8e\uff0c\u4f46\u4e5f\u5df2\u7ecf\u5927\u5927\u51fa\u4e4e\u6211\u7684\u610f\u6599\u4e86\uff0c\u6240\u4ee5\u59d0\u59d0\u5f53\u7136\u8981\u597d\u597d\u5730\u5956\u52b1\u4f60\u2014\u2014\u4f60\u60f3\u8981\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u5f71\u9b54\u7f51\uff0c\u201d\u743c\u6069\u7acb\u523b\u8bf4\uff0c\u201c\u6211\u5e0c\u671b\u80fd\u591f\u4f7f\u7528\u5f71\u9b54\u7f51\u3002\u201d</p><p>\u4ed6\u8fd9\u6b21\u548c\u9b54\u6cd5\u5973\u795e\u7b97\u662f\u7ed3\u4e0b\u4e86\u6b7b\u4ec7\u3002\u9b54\u6cd5\u5973\u795e\u662f\u9b54\u7f51\u7684\u638c\u63a7\u8005\uff0c\u7406\u8bba\u4e0a\u8bf4\u53ef\u4ee5\u7981\u6b62\u4efb\u4f55\u4e00\u4e2a\u4eba\u63a5\u89e6\u3001\u4f7f\u7528\u9b54\u7f51\uff0c\u5bf9\u4e8e\u666e\u901a\u4eba\u800c\u8a00\u8fd9\u4e0d\u7b97\u4ec0\u4e48\uff0c\u4f46\u5bf9\u4e8e\u5deb\u5e08\u800c\u8a00\uff0c\u8fd9\u662f\u81f4\u547d\u7684\u5a01\u80c1\u3002\u5deb\u5e08\u7684\u529b\u91cf\u5e76\u4e0d\u5b8c\u5168\u57fa\u4e8e\u81ea\u8eab\uff0c\u4ed6\u4eec\u82e5\u60f3\u6210\u529f\u65bd\u5c55\u9b54\u6cd5\uff0c\u5fc5\u987b\u501f\u52a9\u9b54\u7f51\u624d\u80fd\u529e\u5230\uff1b\u4e00\u65e6\u88ab\u5973\u795e\u7981\u6b62\u63a5\u89e6\u9b54\u7f51\uff0c\u5deb\u5e08\u5c31\u76f8\u5f53\u4e8e\u4e27\u5931\u4e86\u65bd\u6cd5\u80fd\u529b\u3002</p><p>\u5728\u5f53\u65f6\u7684\u60c5\u51b5\u4e0b\uff0c\u743c\u6069\u522b\u65e0\u9009\u62e9\uff0c\u53ea\u80fd\u6325\u5251\u3002\u4ed6\u82e5\u662f\u6210\u529f\u65a9\u6740\u5973\u795e\uff0c\u6839\u636e\u8fc7\u53bb\u7684\u5386\u53f2\u7ecf\u9a8c\uff0c\u9b54\u7f51\u5fc5\u5c06\u5d29\u6e83\uff0c\u5927\u5bb6\u90fd\u6ca1\u5f97\u7528\uff1b\u82e5\u662f\u65a9\u6740\u4e0d\u6210\uff0c\u81ea\u7136\u4f1a\u5f7b\u5e95\u5f97\u7f6a\u5973\u795e\uff0c\u540c\u6837\u9762\u4e34\u201c\u65ad\u7f51\u201d\u7684\u5371\u9669\u3002\u65e0\u8bba\u54ea\u4e00\u79cd\uff0c\u53cd\u6b63\u4ee5\u540e\u9b54\u7f51\u662f\u6ca1\u6cd5\u7528\u4e86\uff0c\u743c\u6069\u5f53\u7136\u8981\u8003\u8651\u540e\u8def\u3002</p><p>\u540e\u8def\u5c31\u662f\u201c\u5f71\u9b54\u7f51\u201d\u3002</p><p>\u201c\u4f60\u5728\u62c5\u5fc3\u5bc6\u65af\u62c9\u7684\u62a5\u590d\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u5979\u5f53\u7136\u4f1a\u62a5\u590d\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u4f46\u5979\u4e0d\u4f1a\u7981\u6b62\u4f60\u4e0e\u9b54\u7f51\u7684\u8054\u63a5\u3002\u201d</p><p>\u743c\u6069\u8be7\u5f02\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u516d\u5e74\u524d\uff0c\u9ad8\u7b49\u795e\u8054\u5e2d\u4f1a\u8bae\u6c38\u8fdc\u7981\u6b62\u4e86\u5979\u52a8\u7528\u8fd9\u9879\u80fd\u529b\uff0c\u6240\u4ee5\u4f60\u65e0\u987b\u62c5\u5fc3\u3002\u5f53\u7136\uff0c\u5982\u679c\u4f60\u4ecd\u7136\u5e0c\u671b\u4f7f\u7528\u5f71\u9b54\u7f51\uff0c\u6211\u4e5f\u5f88\u9ad8\u5174\u3002\u4f46\u5728\u6b64\u4e4b\u524d\uff0c\u6211\u60f3\u5148\u95ee\u4e00\u53e5\uff1a\u4f60\u5bf9\u5f71\u9b54\u7f51\u4e86\u89e3\u591a\u5c11\uff1f\u201d</p><p>\u201c\u4e0d\u591a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53ea\u77e5\u9053\u5b83\u662f\u9b54\u7f51\u7684\u4eff\u5236\u54c1\u3002\u201d</p><p>\u5965\u6c83\u66fe\u7ecf\u5bf9\u4ed6\u8bf4\u8fc7\uff0c\u838e\u5c14\u4e3a\u4e86\u5bf9\u6297\u5bc6\u65af\u62c9\uff0c\u6084\u6084\u521b\u9020\u51fa\u4e86\u4e00\u79cd\u201c\u5f71\u9b54\u7f51\u201d\uff0c\u5f71\u9b54\u7f51\u662f\u79d8\u5bc6\u7684\uff0c\u53ea\u6709\u6781\u5c11\u6570\u4eba\u77e5\u6653\u5e76\u4f7f\u7528\uff0c\u5b83\u662f\u9b54\u7f51\u7684\u4eff\u5236\u54c1\uff0c\u5176\u529f\u80fd\u7c7b\u4f3c\uff0c\u4f46\u6ca1\u6709\u771f\u6b63\u610f\u4e49\u4e0a\u7684\u201c\u6e90\u201d\uff0c\u76f8\u5bf9\u800c\u8a00\u5f88\u4e0d\u5b8c\u5584\uff0c\u6027\u80fd\u4e0d\u7a33\u5b9a\u3002\u5982\u679c\u6709\u9009\u62e9\u7684\u8bdd\uff0c\u743c\u6069\u5f53\u7136\u4e0d\u4f1a\u8003\u8651\u5f71\u9b54\u7f51\uff0c\u4f46\u5728\u6ca1\u6709\u9009\u62e9\u7684\u60c5\u51b5\u4e0b\uff0c\u4e5f\u5c31\u4e0d\u9700\u8981\u6311\u4e09\u62e3\u56db\u4e86\u3002</p><p>\u201c\u524d\u6bb5\u65f6\u95f4\uff0c\u9ad8\u7b49\u795e\u4f1a\u8bae\u5df2\u7ecf\u901a\u8fc7\u51b3\u8bae\uff0c\u8bf8\u795e\u53ef\u4ee5\u81ea\u7531\u9009\u62e9\u901a\u8fc7\u9b54\u7f51\u6216\u8005\u5f71\u9b54\u7f51\u6388\u4e88\u4fe1\u5f92\u795e\u672f\uff0c\u6240\u4ee5\u5f71\u9b54\u7f51\u5df2\u7ecf\u4e0d\u5c5e\u4e8e\u2018\u79d8\u5bc6\u2019\u4e86\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u4f46\u6211\u5fc5\u987b\u8bf4\u660e\u7684\u662f\uff1a\u5f71\u9b54\u7f51\u548c\u9b54\u7f51\u7684\u786e\u662f\u6709\u6240\u4e0d\u540c\u7684\uff0c\u5b83\u672a\u5fc5\u9002\u5408\u4f60\u3002\u201d</p><p>\u201c\u6709\u54ea\u4e9b\u4e0d\u540c\u5462\uff1f\u201d</p><p>\u201c\u76f8\u5bf9\u9b54\u7f51\u800c\u8a00\uff0c\u5f71\u9b54\u7f51\u504f\u4e8e\u8d1f\u80fd\u91cf\uff0c\u957f\u671f\u63a5\u89e6\u5b83\u4f1a\u635f\u5bb3\u4eba\u7684\u795e\u667a\uff0c\u5f53\u7136\u4e86\uff0c\u8fd9\u4e00\u70b9\u4f60\u65e0\u987b\u62c5\u5fc3\uff0c\u6211\u4f1a\u5e87\u62a4\u4f60\u7684\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u4f46\u53e6\u4e00\u4e2a\u95ee\u9898\uff0c\u662f\u5373\u4fbf\u6211\u4e5f\u6682\u65f6\u65e0\u6cd5\u89e3\u51b3\uff1a\u5f71\u9b54\u7f51\u5bf9\u5851\u80fd\u672f\u548c\u53d8\u5316\u672f\u4e0d\u5229\u3002\u5982\u679c\u4e00\u4e2a\u5deb\u5e08\u8981\u7528\u5f71\u9b54\u7f51\u6765\u4f7f\u7528\u5851\u80fd\u6216\u8005\u53d8\u5316\u6cd5\u672f\uff0c\u4ed6\u4f1a\u4e8b\u500d\u529f\u534a\u3002\u201d</p><p>\u743c\u6069\u6b63\u662f\u4e00\u4e2a\u4e13\u7cbe\u53d8\u5316\u7684\u5deb\u5e08\u3002</p><p>\u201c\u8fd9\u6837\u554a\u3002\u201d\u4ed6\u6709\u4e9b\u5931\u671b\u3002</p><p>\u201c\u5c31\u662f\u8fd9\u6837\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u5f53\u7136\uff0c\u6211\u7684\u5f71\u9b54\u7f51\u4e5f\u81ea\u6709\u4f18\u8d8a\u4e4b\u5904\uff0c\u6bd4\u5982\u8bf4\uff0c\u5b83\u6bd4\u9b54\u7f51\u8986\u76d6\u66f4\u5e7f\uff0c\u66f4\u52a0\u4e25\u5bc6\uff0c\u4e0d\u5b58\u5728\u4ec0\u4e48\u6b7b\u9b54\u6cd5\u533a\u6216\u8005\u6df7\u4e71\u9b54\u6cd5\u533a\u3002\u5982\u679c\u7528\u5f71\u9b54\u7f51\u6765\u5851\u9020\u5e7b\u672f\u3001\u9644\u9b54\u672f\u6216\u8005\u4ea1\u7075\u672f\uff0c\u6548\u679c\u4f1a\u6bd4\u4f7f\u7528\u9b54\u7f51\u8981\u597d\u5f97\u591a\u3002\u201d</p><p>\u743c\u6069\u4e0d\u4f1a\u4efb\u4f55\u4ea1\u7075\u672f\uff0c\u4f46\u5728\u5e7b\u672f\u548c\u9644\u9b54\u672f\u65b9\u9762\u5012\u8fd8\u7b97\u662f\u6bd4\u8f83\u7cbe\u901a\u3002</p><p>\u201c\u6240\u4ee5\u4f60\u8981\u5982\u4f55\u9009\u62e9\u5462\uff1f\u201d\u838e\u5c14\u95ee\u3002</p><p>\u743c\u6069\u60f3\u4e86\u4e00\u60f3\uff0c\u201c\u53ef\u4ee5\u540c\u65f6\u4f7f\u7528\u5417\uff1f\u201d</p><p>\u838e\u5c14\u4f3c\u4e4e\u4e5f\u6014\u4e86\u4e00\u4e0b\uff0c\u7136\u540e\u5657\u55e4\u4e00\u58f0\u7b11\u51fa\u6765\uff0c\u201c\u4f60\u8fd8\u771f\u8d2a\u5fc3\u5462\uff0c\u201d\u5979\u8bf4\uff0c\u8bed\u6c14\u4e2d\u5374\u5e76\u65e0\u4e1d\u6beb\u8d23\u602a\u7684\u610f\u5473\uff0c\u201c\u65e2\u60f3\u8981\u5bc6\u65af\u62c9\uff0c\u53c8\u60f3\u8981\u6211\uff1f\u5c31\u50cf\u4f60\u90a3\u4e9b\u5c0f\u60c5\u4eba\u4e00\u6837\uff0c\u8fd9\u4e2a\u4e5f\u559c\u6b22\uff0c\u90a3\u4e2a\u4e5f\u7231\uff0c\u54ea\u4e2a\u90fd\u820d\u4e0d\u5f97\u653e\u624b\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u4e24\u7801\u4e8b\u5427\u3002\u201d\u743c\u6069\u7565\u89c9\u5c34\u5c2c\u3002</p><p>\u201c\u672c\u8d28\u4e0a\u6ca1\u6709\u533a\u522b\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u5982\u679c\u522b\u4eba\u6709\u8fd9\u79cd\u60f3\u6cd5\uff0c\u8fd8\u6562\u5f53\u7740\u6211\u7684\u9762\u8bf4\u51fa\u6765\uff0c\u6211\u4e00\u5b9a\u4f1a\u597d\u597d\u60e9\u7f5a\u4ed6\uff1b\u4e0d\u8fc7\u65e2\u7136\u662f\u4f60\uff0c\u90a3\u5c31\u53e6\u5f53\u522b\u8bba\uff0c\u201d\u5979\u505c\u987f\u4e86\u4e00\u4f1a\uff0c\u4f3c\u4e4e\u5728\u601d\u8003\u4ec0\u4e48\uff0c\u201c\u6211\u53ef\u4ee5\u6388\u4e88\u6743\u9650\uff0c\u8ba9\u4f60\u5728\u4f7f\u7528\u9b54\u7f51\u7684\u540c\u65f6\u4e5f\u80fd\u89e6\u6478\u5230\u5f71\u9b54\u7f51\uff0c\u4f46\u8fd9\u53ea\u4f1a\u662f\u6682\u65f6\u7684\u3002\u5e76\u975e\u6211\u4e0d\u613f\u901a\u878d\uff0c\u800c\u662f\u5f71\u9b54\u7f51\u4e0e\u9b54\u7f51\uff0c\u5b83\u4eec\u6027\u8d28\u5929\u7136\u76f8\u6096\uff0c\u65e0\u6cd5\u76f8\u5bb9\uff0c\u4f60\u6700\u7ec8\u5fc5\u987b\u9009\u62e9\u5176\u4e00\uff0c\u5426\u5219\u53ea\u4f1a\u5bf9\u4f60\u81ea\u5df1\u4e0d\u5229\u3002\u201d</p><p>\u743c\u6069\u677e\u4e86\u53e3\u6c14\uff0c\u201c\u8c22\u8c22\u3002\u201d</p><p>\u201c\u611f\u8c22\u5c31\u4e0d\u7528\u4e86\uff0c\u65b9\u4fbf\u7684\u8bdd\uff0c\u518d\u5e2e\u59d0\u59d0\u4e00\u70b9\u5fd9\u5427\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u542c\u8bf4\u4f60\u6253\u7b97\u53bb\u4e1c\u57df\uff1f\u201d</p><p>\u201c\u521d\u6b65\u662f\u8fd9\u4e2a\u6253\u7b97\uff0c\u201d\u743c\u6069\u8c28\u614e\u5730\u8bf4\uff0c\u201c\u8fd8\u6ca1\u6700\u7ec8\u786e\u5b9a\u3002\u201d</p><p>\u201c\u4e1c\u57df\u7684\u8bf8\u4f4d\u795e\u738b\uff0c\u5012\u4e5f\u90fd\u7b97\u662f\u59d0\u59d0\u7684\u6545\u4eba\uff0c\u5df2\u7ecf\u5f88\u591a\u5e74\u6ca1\u89c1\u8fc7\u4e86\u3002\u542c\u8bf4\u6709\u7684\u5df2\u7ecf\u6b7b\u4e86\uff0c\u6709\u7684\u8fd8\u5728\u82df\u5ef6\u6b8b\u5598\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u4eba\u751f\u5982\u6b64\u8270\u96be\uff0c\u4f55\u5fc5\u52c9\u5f3a\u7559\u604b\u3002\u5982\u679c\u4f60\u80fd\u9047\u5230\uff0c\u5c31\u5e2e\u4ed6\u4eec\u53ca\u65f6\u89e3\u8131\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u743c\u6069\u539f\u672c\u60f3\u8981\u63a8\u6258\uff0c\u60f3\u4e86\u60f3\uff0c\u6700\u7ec8\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u6211\u4f1a\u5c3d\u529b\u7684\uff0c\u59d0\u59d0\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90a3\u4e48\uff0c\u8fd9\u6b21\u6709\u6ca1\u6709\u4ec0\u4e48\u5956\u8d4f\u5462\uff1f\u201d</p><p>\u201c\u5956\u8d4f\u81ea\u7136\u662f\u6709\u7684\uff0c\u59d0\u59d0\u600e\u4e48\u4f1a\u8ba9\u4f60\u767d\u767d\u5e2e\u5fd9\u5462\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u4e0d\u8fc7\u8fd8\u662f\u6682\u65f6\u4fdd\u5bc6\u5427\uff0c\u5426\u5219\u5c31\u6ca1\u6709\u60ca\u559c\u4e86\u3002\u5230\u4e86\u65f6\u5019\uff0c\u4f60\u5c31\u81ea\u7136\u4f1a\u77e5\u9053\u3002\u201d</p><p>\u60ca\u559c\u4ec0\u4e48\u7684\uff0c\u743c\u6069\u5012\u662f\u65e0\u6240\u8c13\uff0c\u5982\u679c\u53ef\u4ee5\u7684\u8bdd\uff0c\u4ed6\u5b81\u613f\u5148\u8981\u773c\u524d\u5229\u76ca\u3002\u201c\u6211\u4e2d\u4e86\u4e00\u4e2a\u8bc5\u5492\uff0c\u662f\u4f20\u9053\u5deb\u5e08\u4e0b\u7684\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u59d0\u59d0\u4f60\u80fd\u89e3\u5f00\u5417\uff1f\u201d</p><p>\u838e\u5c14\u7b11\u7740\u6447\u4e86\u6447\u5934\uff0c\u201c\u8fd9\u4e2a\u59d0\u59d0\u8fd8\u771f\u662f\u505a\u4e0d\u5230\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u743c\u6069\u539f\u672c\u4e5f\u6ca1\u62b1\u592a\u9ad8\u671f\u671b\uff0c\u6240\u4ee5\u4e5f\u8c08\u4e0d\u4e0a\u5931\u671b\u3002\u8bc5\u5492\u7684\u786e\u662f\u5f88\u9ebb\u70e6\uff0c\u4f46\u6bd5\u7adf\u4e0d\u50cf\u9b54\u7f51\u90a3\u6837\uff0c\u662f\u8feb\u5728\u7709\u776b\u7684\u5371\u673a\uff0c\u53cd\u6b63\u73b0\u5728\u4e5f\u7b97\u662f\u6709\u4e86\u89e3\u51b3\u529e\u6cd5\uff0c\u81f3\u5c11\u662f\u6709\u4e86\u65b9\u5411\uff0c\u90a3\u5c31\u53bb\u4e00\u8d9f\u4e1c\u57df\u518d\u8bf4\u5427\u3002</p><p>\u201c\u90a3\u59d0\u59d0\u8d70\u4e86\uff0c\u4e0b\u6b21\u518d\u89c1\uff0c\u201d\u838e\u5c14\u8bf4\uff0c\u201c\u505a\u4e2a\u597d\u68a6\u3002\u201d</p><p>\u5979\u8e2e\u8d77\u811a\u5c16\uff0c\u5728\u743c\u6069\u7684\u8138\u988a\u4e0a\u8f7b\u8f7b\u4e00\u543b\u3002\u9ed1\u6697\u4ece\u56db\u9762\u516b\u65b9\u6d8c\u6765\uff0c\u5c06\u4e24\u4eba\u5b8c\u5168\u6df9\u6ca1\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u8fd9\u4e00\u89c9\u7761\u5f97\u5f88\u957f\uff0c\u743c\u6069\u9192\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u5df2\u7ecf\u662f\u7b2c\u4e8c\u65e5\u7684\u4e0a\u5348\uff0c\u51db\u4e0d\u5728\u8eab\u8fb9\u3002\u4ece\u592a\u9633\u7684\u4f4d\u7f6e\u5224\u65ad\uff0c\u5927\u7ea6\u662f\u5341\u70b9\u534a\u5de6\u53f3\u3002\u4ed6\u4eec\u8fd8\u5728\u8239\u4e0a\uff0c\u4f46\u5468\u56f4\u7684\u666f\u8272\u663e\u793a\u4f3c\u4e4e\u5df2\u7ecf\u51fa\u4e86\u8c37\u5730\uff0c\u4e0d\u518d\u662f\u4e1b\u6797\u5bc6\u5e03\uff0c\u6709\u4e86\u5927\u7247\u5927\u7247\u7684\u65f7\u91ce\uff0c\u96f6\u96f6\u661f\u661f\u7684\u623f\u5c4b\u548c\u519c\u7530\u3002\u201c\u5230\u54ea\u4e86\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u5df2\u7ecf\u8fdb\u5165\u6851\u6bd4\u4e9a\u7684\u5883\u5185\u4e86\uff0c\u201d\u838e\u73de\u514b\u4ece\u540e\u9762\u8d70\u8fc7\u6765\uff0c\u201c\u5927\u6982\u518d\u8fc7\u4e00\u5c0f\u65f6\uff0c\u6211\u4eec\u5c31\u8981\u767b\u5cb8\uff0c\u8d70\u6708\u6d77\u516c\u8def\uff0c\u7a7f\u8fc7\u6b27\u675c\u6797\uff0c\u9884\u8ba1\u660e\u5929\u665a\u4e0a\u6216\u8005\u540e\u5929\u51cc\u6668\u5230\u8fbe\u53f6\u5f18\u57ce\uff0c\u4ece\u90a3\u91cc\u53ef\u4ee5\u96c7\u6d77\u8239\uff0c\u6e21\u8fc7\u5760\u661f\u6d77\u62b5\u8fbe\u4e1c\u57df\u2014\u2014\u4f60\u89c9\u5f97\u8fd9\u4e2a\u5b89\u6392\u5982\u4f55\u3002\u201d</p><p>\u201c\u55ef\uff0c\u5c31\u8fd9\u6837\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5148\u5230\u53f6\u5f18\u57ce\u518d\u8bf4\u3002\u201d</p><p>\u4ed6\u8d70\u5230\u7ef4\u82e5\u62c9\u7684\u623f\u95f4\u524d\uff0c\u5c48\u6307\u6572\u4e86\u6572\u95e8\uff0c\u201c\u6211\u53ef\u4ee5\u8fdb\u6765\u5417\uff1f\u201d</p><p>\u201c\u7b49\u7b49\uff01\u7b49\u4e00\u4e0b\uff01\u201d\u5c0f\u5973\u5deb\u7684\u58f0\u97f3\u4ece\u91cc\u9762\u4f20\u51fa\u6765\uff0c\u660e\u663e\u6709\u4e9b\u60ca\u614c\uff0c\u201c\u6211\u8fd8\u6ca1\u7a7f\u8863\u670d\u5462\u3002\u201d</p><p>\u201c\u51db\uff1f\u201d\u743c\u6069\u4e00\u6014\uff0c\u201c\u4f60\u7684\u623f\u95f4\u4e0d\u662f\u5e94\u8be5\u5728\u9694\u58c1\u5417\uff1f\u201d</p><p>\u201c\u6211\u6628\u591c\u68a6\u6e38\u8d70\u9519\u5730\u65b9\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u743c\u6069\u7b49\u4e86\u5927\u6982\u4e94\u5206\u949f\uff0c\u5c31\u542c\u89c1\u623f\u95f4\u91cc\u4e00\u901a\u6298\u817e\uff0c\u7136\u540e\u7ec8\u4e8e\u95e8\u88ab\u6253\u5f00\u4e86\u3002\u51db\u548c\u7ef4\u82e5\u62c9\u90fd\u5728\uff0c\u5979\u4eec\u8863\u7740\u6574\u9f50\uff0c\u4f46\u8138\u988a\u901a\u7ea2\uff0c\u800c\u4e14\u76ee\u5149\u8eb2\u8eb2\u95ea\u95ea\uff0c\u4e0d\u6562\u4e0e\u743c\u6069\u5bf9\u89c6\uff0c\u660e\u663e\u4e00\u526f\u505a\u4e86\u574f\u4e8b\u7684\u5fc3\u865a\u6a21\u6837\u3002\u201c\u90a3\u4e2a\uff0c\u6211\u8fd8\u6ca1\u5403\u65e9\u9910\u5462\uff0c\u611f\u89c9\u597d\u997f\u554a\uff0c\u201d\u51db\u5de6\u987e\u53f3\u76fc\uff0c\u201c\u743c\u6069\uff0c\u6709\u6ca1\u6709\u7ed9\u6211\u51c6\u5907\u714e\u86cb\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u5728\u8fd9\u91cc\u5e72\u561b\uff1f\u201d</p><p>\u201c\u6211\u4eec\u5e08\u59d0\u59b9\u591a\u5e74\u6ca1\u89c1\uff0c\u5f7b\u591c\u957f\u8c08\u3001\u4e92\u8bc9\u522b\u60c5\uff0c\u662f\u5f88\u6b63\u5e38\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u6b63\u5e38\u4f60\u4e2a\u9b3c\uff0c\u4f60\u4eec\u540d\u4e3a\u5e08\u59d0\u59b9\uff0c\u5b9e\u9645\u4e0a\u4e4b\u524d\u5c31\u6ca1\u89c1\u8fc7\uff0c\u54ea\u6709\u4ec0\u4e48\u522b\u60c5\u53ef\u8bc9\u7684\u3002</p><p>\u201c\u603b\u4e4b\u6211\u5148\u8d70\u4e86\uff0c\u201d\u51db\u76f4\u63a5\u5f00\u6e9c\uff0c\u201c\u6709\u4ec0\u4e48\u4e8b\u60c5\u665a\u70b9\u518d\u8bf4\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u76ee\u9001\u51db\u7684\u80cc\u5f71\u4ece\u95e8\u53e3\u6d88\u5931\uff0c\u743c\u6069\u5c06\u89c6\u7ebf\u8f6c\u56de\u5230\u7ef4\u82e5\u62c9\u8eab\u4e0a\u3002\u201c\u6211\u6839\u672c\u6ca1\u60f3\u5230\u4f60\u4eec\u4e5f\u4f1a\u53d1\u751f\u51fa\u8fd9\u79cd\u5173\u7cfb\u2014\u2014\u5373\u4fbf\u5982\u6b64\uff0c\u662f\u4e0d\u662f\u8fdb\u5c55\u4e5f\u592a\u5feb\u4e86\u70b9\u3002\u201d</p><p>\u201c\u4f60\u6216\u8bb8\u6709\u6240\u8bef\u4f1a\uff0c\u201d\u5973\u5deb\u5e08\u52c9\u5f3a\u8fa9\u89e3\uff0c\u201c\u5176\u5b9e\u6211\u4eec\u5e76\u6ca1\u6709\u53d1\u751f\u4ec0\u4e48\u2026\u2026\u201d</p><p>\u743c\u6069\u4e3e\u8d77\u4e00\u6839\u624b\u6307\uff0c\u6447\u4e86\u6447\uff0c\u201c\u8863\u670d\u7a7f\u5f97\u5f88\u6574\u9f50\uff0c\u5e8a\u5355\u4e5f\u6362\u4e86\u65b0\u7684\uff0c\u4f46\u4f60\u4eec\u4ecd\u7136\u5fd8\u4e86\u4e00\u4ef6\u4e8b\uff0c\u800c\u8fd9\u5c31\u662f\u81f4\u547d\u7684\u7834\u7efd\u3002\u201d</p><p>\u201c\u5fd8\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u201c\u6c14\u5473\u554a\uff0c\u201d\u743c\u6069\u8ba5\u8bbd\u5730\u7b11\u4e86\u7b11\uff0c\u201c\u4f60\u4eec\u5728\u8fd9\u4e2a\u623f\u95f4\u5f85\u5f97\u592a\u4e45\u4e86\uff0c\u55c5\u89c9\u65e9\u5df2\u7ecf\u9002\u5e94\uff0c\u6240\u4ee5\u6ca1\u6709\u5bdf\u89c9\uff1b\u4f46\u6211\u4ece\u5916\u9762\u8fdb\u6765\uff0c\u7a7a\u6c14\u4e2d\u7684\u90a3\u79cd\u5473\u9053\u53ef\u662f\u975e\u5e38\u6e05\u6670\u5462\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u4e0d\u8bf4\u8bdd\u4e86\u3002</p><p>\u5982\u679c\u8bf4\u5728\u8fdb\u95e8\u4e4b\u524d\u8fd8\u53ea\u662f\u731c\u6d4b\uff0c\u4f46\u521a\u521a\u8e0f\u5165\u623f\u95e8\uff0c\u743c\u6069\u5c31\u77e5\u9053\u81ea\u5df1\u7684\u731c\u6d4b\u4e00\u70b9\u90fd\u6ca1\u9519\u3002\u7a7a\u6c14\u4e2d\u90a3\u79cd\u6d53\u90c1\u7684\u5473\u9053\uff0c\u663e\u793a\u8fd9\u4e24\u4eba\u6628\u665a\u4e0d\u77e5\u6298\u817e\u4e86\u591a\u4e45\uff0c\u8bf4\u4e0d\u5b9a\u6574\u591c\u6ca1\u7761\u90fd\u6709\u53ef\u80fd\u3002\u51db\u7684\u7231\u6db2\u5473\u9053\u743c\u6069\u5f53\u7136\u5f88\u719f\u6089\uff0c\u81f3\u4e8e\u7ef4\u82e5\u62c9\u7684\uff0c\u4ed6\u4e5f\u540c\u6837\u4e0d\u964c\u751f\u3002\u552f\u4e00\u4e0d\u660e\u767d\u7684\uff0c\u662f\u8fd9\u4e24\u4eba\u660e\u660e\u624d\u8ba4\u8bc6\u534a\u5929\uff0c\u5c45\u7136\u5f53\u5929\u665a\u4e0a\u5c31\u641e\u5728\u4e00\u8d77\uff0c\u611f\u60c5\u201c\u6df1\u5165\u201d\u5230\u8fd9\u4e2a\u5730\u6b65\uff0c\u901f\u5ea6\u5b9e\u5728\u592a\u8fc7\u60ca\u4eba\uff0c\u7b80\u76f4\u5c31\u662f\u5e72\u67f4\u70c8\u706b\uff0c\u4e00\u62cd\u5373\u5408\u3002\u51db\u4e5f\u5c31\u7b97\u4e86\uff0c\u65e9\u6709\u524d\u79d1\uff0c\u4f46\u7ef4\u82e5\u62c9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u96be\u9053\u5979\u7684\u6027\u53d6\u5411\u5176\u5b9e\u4e5f\u662f\u4e2a\u857e\u4e1d\u8fb9\uff1f\u8fd9\u4e5f\u672a\u514d\u592a\u51d1\u5de7\u4e86\u70b9\uff0c\u51db\u548c\u7ef4\u82e5\u62c9\u540d\u4e3a\u5e08\u59d0\u59b9\uff0c\u4f46\u65e2\u65e0\u8840\u7f18\u5173\u7cfb\uff0c\u5bb6\u5ead\u80cc\u666f\u3001\u4eba\u751f\u7ecf\u5386\u4e5f\u5b8c\u5168\u4e0d\u540c\uff0c\u53cc\u65b9\u4e4b\u524d\u4ece\u672a\u89c1\u8fc7\u9762\uff0c\u4e24\u4eba\u4e4b\u95f4\u552f\u4e00\u7684\u8054\u7cfb\uff0c\u5c31\u662f\u6709\u4e00\u4e2a\u5171\u540c\u7684\u8001\u5e08\uff0c\u5374\u90fd\u6709\u7740\u8fe5\u5f02\u4e8e\u5e38\u4eba\u7684\u6027\u53d6\u5411\u2014\u2014\u5982\u679c\u5f53\u771f\u53d1\u751f\u8fd9\u79cd\u4e8b\u60c5\uff0c\u743c\u6069\u89c9\u5f97\u6700\u5408\u7406\u7684\u63a8\u6d4b\uff0c\u5c31\u662f\u5979\u4eec\u7684\u8001\u5e08\u80af\u5b9a\u6709\u95ee\u9898\u3002</p><p>\u5514\uff0c\u539f\u6765\u6b23\u5e03\u4e5f\u662f\u4e2a\u857e\u4e1d\u8fb9\uff1f\u8fd9\u4e2a\u53ef\u771f\u6ca1\u770b\u51fa\u6765\u3002</p><p>\u743c\u6069\u6b63\u5728\u53d1\u6563\u6027\u601d\u7ef4\uff0c\u7ef4\u82e5\u62c9\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u201c\u4f60\u627e\u6211\u5230\u5e95\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u201d\u743c\u6069\u56de\u8fc7\u795e\u6765\uff0c\u201c\u662f\u8fd9\u6837\uff0c\u6211\u4eec\u5f97\u8c08\u4e00\u8c08\u3002\u201d</p><p>\u201c\u8c08\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5927\u81f4\u4e0a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5c31\u662f\u5173\u4e8e\u6211\u4eec\u4e4b\u95f4\u7684\u5173\u7cfb\u7684\u5b9a\u4f4d\u95ee\u9898\u3002\u201d</p><p>\u201c\u6211\u4eec\u4e4b\u95f4\u7684\u5173\u7cfb\u7684\u5b9a\u4f4d\u95ee\u9898\uff1f\u201d\u5973\u5deb\u5e08\u91cd\u590d\u4e86\u4e00\u904d\u8fd9\u4e2a\u6709\u4e9b\u62d7\u53e3\u7684\u8bf4\u6cd5\uff0c\u201c\u8fd9\u4e0d\u662f\u663e\u800c\u6613\u89c1\u7684\u5417\uff1f\u4f60\u662f\u5f3a\u5978\u72af\uff0c\u6211\u662f\u53d7\u5bb3\u8005\uff0c\u5f88\u6e05\u695a\u3002\u201d</p><p>\u201c\u5582\u5582\uff0c\u90a3\u4f60\u8fd8\u5bf9\u6211\u4e0b\u8bc5\u5492\u5462\uff0c\u201d\u743c\u6069\u4e0d\u6ee1\u5730\u8bf4\uff0c\u201c\u6211\u4e5f\u662f\u53d7\u5bb3\u8005\u554a\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u6cd5\u4fb5\u5bb3\u5728\u5148\uff0c\u6211\u4e0b\u8bc5\u5492\u5728\u540e\uff0c\u8fd9\u5c5e\u4e8e\u6b63\u5f53\u9632\u536b\u3002\u201d</p><p>\u201c\u9519\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u6709\u4e00\u4e2a\u57fa\u672c\u7684\u6982\u5ff5\u6df7\u6dc6\u3002\u6240\u8c13\u6b63\u5f53\u9632\u536b\uff0c\u662f\u6307\u4f60\u4e3a\u4e86\u5236\u6b62\u3001\u62b5\u5fa1\u4e0d\u6cd5\u4fb5\u5bb3\u800c\u91c7\u53d6\u7684\u5fc5\u8981\u4e4b\u884c\u4e3a\u3002\u6211\u627f\u8ba4\uff0c\u6211\u5f53\u65f6\u662f\u5bf9\u4f60\u5b58\u5728\u4e0d\u6cd5\u4fb5\u5bb3\uff0c\u4f46\u4f60\u5bf9\u6211\u4e0b\u8bc5\u5492\uff0c\u662f\u4e3a\u4e86\u5236\u6b62\u6211\u5417\uff1f\u4e0d\u662f\u5427\u3002\u4f60\u4e0b\u8bc5\u5492\u7684\u76ee\u7684\uff0c\u662f\u4e3a\u4e86\u80c1\u8feb\u6211\uff0c\u8981\u6211\u5e2e\u52a9\u4f60\u8131\u79bb\u8428\u9a6c\u65af\u7279\u7684\u63a7\u5236\u2014\u2014\u800c\u8fd9\u53ef\u5e76\u975e\u6211\u7684\u4e49\u52a1\uff0c\u5bf9\u5427\u3002\u6240\u4ee5\u8fd9\u91cc\u4e0d\u5b58\u5728\u4ec0\u4e48\u6b63\u5f53\u9632\u536b\uff0c\u6211\u4eec\u90fd\u662f\u52a0\u5bb3\u8005\uff0c\u4e5f\u90fd\u662f\u53d7\u5bb3\u8005\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u5230\u5e95\u662f\u5deb\u5e08\u8fd8\u662f\u5f8b\u5e08\uff1f\u201d</p><p>\u201c\u4e24\u8005\u90fd\u662f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u559c\u6b22\u6cd5\u672f\uff0c\u4e5f\u559c\u6b22\u6cd5\u5f8b\uff0c\u5b83\u4eec\u9887\u6709\u5171\u901a\u4e4b\u5904\uff1a\u90fd\u9700\u8981\u60f3\u8c61\u529b\uff0c\u4e5f\u90fd\u9700\u8981\u9075\u5b88\u89c4\u5219\u3002\u8a00\u5f52\u6b63\u4f20\uff0c\u6211\u521a\u624d\u7684\u8bf4\u6cd5\u6709\u4ec0\u4e48\u9519\u8bef\u5417\uff1f\u201d</p><p>\u7ef4\u82e5\u62c9\u601d\u8003\u4e86\u4e00\u4f1a\uff0c\u201c\u6ca1\u6709\uff0c\u201d\u5979\u8bda\u5b9e\u5730\u8bf4\uff0c\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u6211\u5f53\u65f6\u4e0b\u8bc5\u5492\uff0c\u76ee\u7684\u5e76\u975e\u53cd\u6297\uff0c\u800c\u662f\u4e3a\u4e86\u80c1\u8feb\u4f60\u2014\u2014\u4ece\u8fd9\u70b9\u6765\u8bf4\uff0c\u6211\u662f\u52a0\u5bb3\u8005\uff0c\u4f60\u662f\u53d7\u5bb3\u8005\uff0c\u8fd9\u4e2a\u6211\u627f\u8ba4\u3002\u201d</p><p>\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4eec\u4e92\u76f8\u8c05\u89e3\u5982\u4f55\uff1f\u201d\u743c\u6069\u8bf4\u51fa\u6765\u610f\uff0c\u201c\u5f53\u7136\uff0c\u8fd9\u5e76\u4e0d\u610f\u5473\u7740\u6211\u4eec\u4e4b\u95f4\u5951\u7ea6\u7684\u5931\u6548\uff0c\u4f60\u4ecd\u7136\u8981\u4e3a\u6211\u89e3\u9664\u8bc5\u5492\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u76b1\u7709\uff1a\u201c\u6211\u4e0d\u660e\u767d\u4f60\u7684\u76ee\u7684\u4f55\u5728\uff0c\u4f60\u6240\u8c13\u7684\u4e92\u76f8\u8c05\u89e3\uff0c\u6709\u4ec0\u4e48\u5fc5\u8981\u5417\uff1f\u65e0\u8bba\u4f60\u8c05\u89e3\u4e0e\u5426\uff0c\u6211\u90fd\u4e0d\u4f1a\u540e\u6094\u81ea\u5df1\u7684\u505a\u6cd5\uff1b\u540c\u6837\uff0c\u65e0\u8bba\u6211\u8c05\u89e3\u4e0e\u5426\uff0c\u4f60\u4e5f\u4e0d\u4f1a\u56e0\u6b64\u800c\u611f\u5230\u4e1d\u6beb\u6127\u759a\u5427\u3002\u65e2\u7136\u5982\u6b64\uff0c\u4e3a\u4ec0\u4e48\u8981\u591a\u6b64\u4e00\u4e3e\u5462\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u662f\u591a\u6b64\u4e00\u4e3e\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u6211\u6709\u4e00\u4f4d\u5973\u53cb\uff0c\u5979\u53eb\u6885\u83f2\u65af\uff0c\u662f\u4e00\u4f4d\u5723\u6b66\u58eb\u3002\u5979\u4e0d\u559c\u6b22\u6211\u505a\u4e00\u4e9b\u4e8b\u60c5\uff0c\u6240\u4ee5\u5982\u679c\u6211\u4e0d\u5c0f\u5fc3\u505a\u4e86\uff0c\u4e00\u822c\u5c31\u4f1a\u7792\u7740\u5979\uff0c\u6bd4\u5982\u6211\u548c\u4f60\u4e4b\u95f4\u7684\u8fd9\u4e00\u5207\u3002\u5982\u679c\u65e0\u6cd5\u9690\u7792\uff0c\u90a3\u4e48\u6211\u5c31\u5f97\u63d0\u524d\u505a\u4e00\u4e9b\u51c6\u5907\u3002\u201d</p><p>\u201c\u6240\u4ee5\u4f60\u662f\u8981\u6211\u5e2e\u4f60\u6492\u8c0e\uff1f\u201d\u7ef4\u82e5\u62c9\u53cd\u95ee\uff0c\u201c\u600e\u4e48\u8bf4\uff1f\u8bf4\u6211\u5bf9\u4f60\u4e00\u89c1\u949f\u60c5\uff0c\u4e3b\u52a8\u6295\u6000\u9001\u62b1\uff1f\u201d</p><p>\u201c\u90a3\u5012\u4e0d\u5fc5\uff0c\u4f60\u8bf4\u4e86\u5979\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\uff0c\u5982\u679c\u5979\u95ee\u8d77\uff0c\u4f60\u5b9e\u8bdd\u5b9e\u8bf4\u5c31\u884c\u2014\u2014\u5f53\u7136\u6211\u76f8\u4fe1\u5979\u4e5f\u4e0d\u4f1a\u6765\u95ee\u4f60\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8c05\u89e3\u5c31\u8db3\u591f\u4e86\u3002\u201d</p><p>\u201c\u4f46\u6211\u4e3a\u4ec0\u4e48\u8981\u8c05\u89e3\uff1f\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u53cd\u6b63\u6211\u53c8\u4e0d\u5728\u4e4e\u4f60\u7684\u8c05\u89e3\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u6709\u6c42\u4e8e\u6211\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u8981\u8ddf\u968f\u6211\u53bb\u4e1c\u57df\uff0c\u53bb\u5b9e\u73b0\u4f60\u7684\u68a6\u60f3\uff0c\u8fd9\u662f\u5deb\u5e08\u4e4b\u795e\u7ed9\u4f60\u7684\u8c15\u793a\uff0c\u5bf9\u5427\u3002\u4f46\u6211\u4e3a\u4ec0\u4e48\u8981\u5728\u4e4e\u4f60\u7684\u68a6\u60f3\u5462\uff1f\u201d</p><p>\u4f20\u9053\u5deb\u5e08\u6c89\u541f\u4e86\u5927\u7ea6\u4e94\u5206\u949f\uff0c\u201c\u597d\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u90a3\u4e48\u6211\u4eec\u505a\u4e2a\u4ea4\u6613\u3002\u6211\u540c\u610f\u4e92\u76f8\u8c05\u89e3\uff1b\u6211\u4e5f\u627f\u8bfa\u7ee7\u7eed\u9075\u5b88\u5951\u7ea6\uff0c\u4e3a\u4f60\u89e3\u9664\u8bc5\u5492\uff0c\u76f8\u5bf9\u5e94\u7684\uff0c\u4f60\u4e5f\u5fc5\u987b\u627f\u8bfa\u5c3d\u91cf\u5e2e\u52a9\u6211\u3002\u201d</p><p>\u201c\u5f88\u597d\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d</p><p>\u201c\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d</p><p>\u201c\u90a3\u4e48\u4e4b\u524d\u79cd\u79cd\uff0c\u4e00\u7b14\u52fe\u9500\uff1b\u4e4b\u540e\u6211\u4eec\u2026\u2026\u201d\u4ed6\u60f3\u4e86\u60f3\uff0c\u201c\u7b97\u662f\u76df\u53cb\u5427\uff0c\u6211\u4f1a\u4ee5\u793c\u76f8\u5f85\uff0c\u8fd9\u70b9\u4f60\u53ef\u4ee5\u653e\u5fc3\u3002\u201d</p><p>\u201c\u90a3\u5012\u4e0d\u7528\uff0c\u201d\u5973\u5deb\u5e08\u8bf4\uff0c\u5979\u5ffd\u7136\u59a9\u5a9a\u5730\u7b11\u8d77\u6765\uff0c\u201c\u5b9e\u8bdd\u5b9e\u8bf4\uff0c\u4f60\u7684\u6280\u672f\u633a\u4e0d\u9519\uff0c\u81f3\u5c11\u6bd4\u51db\u5f3a\u591a\u4e86\u3002\u201d</p><p>\u743c\u6069\u770b\u4e86\u5979\u51e0\u79d2\u949f\uff0c\u7136\u540e\u4f38\u624b\u5c06\u5979\u62c9\u8fc7\u6765\uff0c\u7ef4\u82e5\u62c9\u5e76\u672a\u6297\u62d2\uff0c\u987a\u4ece\u5730\u88ab\u63fd\u5728\u6000\u4e2d\u3002\u7537\u4eba\u7684\u624b\u4ece\u5973\u5deb\u5e08\u7684\u957f\u888d\u4e2d\u63a2\u8fdb\u53bb\uff0c\u53d1\u73b0\u5979\u6ca1\u6709\u7a7f\u5185\u8863\uff0c\u4e0b\u4f53\u5df2\u7ecf\u5b8c\u5168\u6e7f\u900f\u3002\u4e8e\u662f\u743c\u6069\u5c06\u5979\u62e6\u8170\u62b1\u8d77\u6765\uff0c\u6254\u5230\u5e8a\u4e0a\uff0c\u7136\u540e\u538b\u4e86\u4e0a\u53bb\u3002</p><p>\u867d\u7136\u5176\u5b9e\u76f8\u8bc6\u5e76\u4e0d\u4e45\uff0c\u4f46\u4e24\u4eba\u4e4b\u95f4\u5df2\u7ecf\u505a\u8fc7\u65e0\u6570\u6b21\uff0c\u7cbe\u795e\u6216\u8bb8\u8fd8\u6bd4\u8f83\u758f\u8fdc\uff0c\u8089\u4f53\u65e9\u5c31\u5df2\u7ecf\u719f\u6089\u5f97\u4e0d\u80fd\u518d\u719f\u6089\u3002\u743c\u6069\u7528\u5404\u79cd\u59ff\u52bf\u5c06\u7ef4\u82e5\u62c9\u63a5\u8fde\u9001\u4e0a\u56db\u4e94\u6b21\u9ad8\u6f6e\uff0c\u8fd8\u8ba9\u5979\u5c1d\u8bd5\u4e86\u4e00\u4e0b\u5973\u4e0a\u4f4d\u5f0f\u3002\u8fd9\u79cd\u59ff\u52bf\u5bf9\u5973\u65b9\u800c\u8a00\u96be\u5ea6\u8f83\u9ad8\uff0c\u6885\u83f2\u65af\u3001\u51db\u90fd\u575a\u6301\u4e0d\u4e86\u592a\u4e45\uff0c\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u7ef4\u82e5\u62c9\u770b\u4f3c\u5a07\u67d4\uff0c\u97e7\u6027\u5374\u5f3a\u5f97\u51fa\u5947\uff0c\u5c45\u7136\u5728\u743c\u6069\u7684\u6276\u6258\u4e4b\u4e0b\uff0c\u73a9\u4e86\u5c06\u8fd1\u534a\u4e2a\u5c0f\u65f6\uff0c\u6210\u529f\u8ba9\u4ed6\u53d1\u5c04\u51fa\u6765\u3002</p><p>\u5b8c\u4e8b\u4e4b\u540e\uff0c\u4e24\u4eba\u8eba\u5728\u5e8a\u4e0a\u4f11\u606f\uff0c\u968f\u53e3\u804a\u5929\u3002\u201c\u4f60\u600e\u4e48\u548c\u51db\u641e\u5230\u4e00\u8d77\u4e86\uff0c\u201d\u743c\u6069\u95ee\uff0c\u201c\u4f60\u4eec\u4e4b\u524d\u660e\u660e\u90fd\u4e0d\u8ba4\u8bc6\u7684\u5427\u3002\u201d</p><p>\u201c\u5c31\u662f\u5728\u4e00\u8d77\u8c08\u5fc3\uff0c\u8c08\u7740\u8c08\u7740\u8863\u670d\u5c31\u8131\u6389\u4e86\uff0c\u7136\u540e\u5c31\u641e\u5230\u4e00\u8d77\u4e86\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\u5f97\u7406\u6240\u5f53\u7136\uff0c\u201c\u51db\u90a3\u5c0f\u5bb6\u4f19\u7684\u6280\u672f\u8fd8\u4e0d\u9519\uff0c\u6211\u4eec\u4e5f\u73a9\u5f97\u633a\u5f00\u5fc3\u7684\uff0c\u4e0d\u8fc7\u7ec8\u7a76\u8fd8\u662f\u7537\u4eba\u6bd4\u8f83\u597d\u3002\u201d</p><p>\u201c\u4f60\u8ba9\u6211\u6709\u4e9b\u51fa\u4e4e\u610f\u6599\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u201c\u6709\u5417\uff1f\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u6211\u662f\u4e2a\u6b63\u5e38\u5973\u6027\uff0c\u5f53\u7136\u6709\u6b63\u5e38\u7684\u751f\u7406\u9700\u6c42\uff0c\u8fd9\u6ca1\u4ec0\u4e48\u597d\u5947\u602a\u7684\u5427\u3002\u201d</p><p>\u201c\u8bdd\u662f\u8fd9\u4e48\u8bf4\u5566\uff0c\u53ea\u662f\u2026\u2026\u201d\u743c\u6069\u4e5f\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u5f62\u5bb9\uff0c\u7ef4\u82e5\u62c9\u8fd9\u8bdd\u5f53\u7136\u6ca1\u9519\uff0c\u5979\u662f\u4e8c\u5341\u4f59\u5c81\u7684\u5973\u5b50\uff0c\u8eab\u4f53\u5df2\u7ecf\u5b8c\u5168\u6210\u719f\uff0c\u521a\u521a\u88ab\u743c\u6069\u5f7b\u5e95\u5f00\u53d1\u8fc7\uff0c\u6709\u751f\u7406\u9700\u6c42\u7684\u786e\u5f88\u6b63\u5e38\u2014\u2014\u4f46\u6709\u751f\u7406\u9700\u6c42\u4e5f\u4e0d\u81f3\u4e8e\u8fd9\u6837\u6025\u5207\u5427\u3002\u4ed4\u7ec6\u56de\u5fc6\u521a\u624d\u4ea4\u5408\u65f6\u7684\u4e00\u4e9b\u7ec6\u8282\uff0c\u743c\u6069\u5927\u81f4\u5fc3\u91cc\u6709\u4e86\u5e95\uff0c\u201c\u662f\u90a3\u4e9b\u7c89\u7ea2\u6cd5\u672f\u7684\u6548\u679c\u8fd8\u6ca1\u5b8c\u5168\u6d88\u9000\u5427\u3002\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u4f60\u8bf4\u5462\uff1f\u201d\u5973\u5deb\u5e08\u77aa\u4e86\u4ed6\u4e00\u773c\u3002</p><p>\u6b64\u524d\u4e24\u4eba\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u7ef4\u82e5\u62c9\u6559\u7ed9\u743c\u6069\u5171\u8ba1\u5341\u4e09\u9053\u201c\u7c89\u7ea2\u201d\u6cd5\u672f\uff0c\u5168\u6570\u7528\u5230\u4e86\u5979\u81ea\u5df1\u8eab\u4e0a\u3002\u7ef4\u82e5\u62c9\u7684\u76ee\u7684\u81ea\u7136\u662f\u501f\u6b64\u5b8c\u6210\u8bc5\u5492\uff0c\u4f46\u8fd9\u4e9b\u7c89\u7ea2\u6cd5\u672f\u5374\u4e5f\u5e76\u975e\u865a\u5047\uff0c\u5176\u529f\u6548\u662f\u5b9e\u5b9e\u5728\u5728\u7684\uff0c\u6beb\u65e0\u534a\u70b9\u6c34\u5206\u3002\u743c\u6069\u539f\u672c\u4ee5\u4e3a\u90a3\u4e9b\u6cd5\u672f\u90fd\u662f\u4e34\u65f6\u6027\u7684\uff0c\u7528\u5b8c\u4e4b\u540e\u81ea\u7136\u6d88\u5931\uff0c\u73b0\u5728\u770b\u6765\u5e76\u975e\u8fd9\u4e48\u7b80\u5355\uff0c\u81f3\u5c11\u77ed\u671f\u4e4b\u5185\uff0c\u8fd8\u662f\u6709\u4e00\u4e9b\u540e\u9057\u75c7\u7684\u3002</p><p>\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u7ef4\u82e5\u62c9\u6709\u751f\u7406\u9700\u6c42\u4e0d\u9519\uff0c\u5374\u4e5f\u672a\u5fc5\u975e\u8981\u627e\u743c\u6069\u89e3\u51b3\u5427\uff0c\u4e16\u754c\u4e0a\u7537\u6027\u591a\u5f97\u662f\uff0c\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u9009\u62e9\u4ed6\u5462\u3002</p><p>\u201c\u56e0\u4e3a\u4f60\u6280\u672f\u786e\u5b9e\u4e0d\u9519\u554a\uff0c\u5f04\u5f97\u6211\u5f88\u8212\u670d\uff0c\u800c\u4e14\u4ed4\u7ec6\u770b\u770b\uff0c\u4f60\u8fd8\u633a\u5e05\u7684\uff0c\u7b26\u5408\u6211\u7684\u5ba1\u7f8e\u89c2\uff0c\u201d\u5973\u5deb\u5e08\u8bf4\uff0c\u201c\u53cd\u6b63\u8981\u627e\u7537\u4eba\u6765\u6ee1\u8db3\u751f\u7406\u9700\u8981\uff0c\u5f53\u7136\u8981\u627e\u4e00\u4e2a\u4e2d\u770b\u53c8\u4e2d\u7528\u7684\uff1b\u6211\u53c8\u4e0d\u53ef\u80fd\u4e00\u4e2a\u4e00\u4e2a\u7537\u4eba\u53bb\u5c1d\u8bd5\uff0c\u6240\u4ee5\u5c31\u9009\u4e00\u4e2a\u5df2\u7ecf\u7ecf\u8fc7\u5b9e\u8df5\u68c0\u9a8c\uff0c\u8bc1\u660e\u8d28\u91cf\u5408\u683c\u7684\uff0c\u8fd9\u5f88\u597d\u7406\u89e3\u5427\u3002\u201d</p><p>\u4f60\u8fd9\u4e48\u8bf4\u662f\u6ca1\u9519\u5566\uff0c\u4f46\u603b\u89c9\u5f97\u54ea\u91cc\u8fd8\u662f\u4e0d\u592a\u5bf9\u52b2\u3002</p><p>\u201c\u6211\u660e\u767d\u4f60\u7684\u610f\u601d\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u597d\u5427\uff0c\u65e2\u7136\u6211\u4eec\u73b0\u5728\u662f\u76df\u53cb\uff0c\u90a3\u4e48\u6211\u5c31\u5766\u8bda\u4e00\u4e9b\u5427\u3002\u6211\u544a\u8bc9\u4f60\u4e00\u4ef6\u4e8b\u60c5\uff1a\u5173\u4e8e\u90a3\u4e2a\u4e13\u60c5\u8bc5\u5492\uff0c\u5b9e\u9645\u4e0a\uff0c\u6211\u9690\u7792\u4e86\u4e00\u4e2a\u7ec6\u8282\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u7ec6\u8282\uff1f\u201d</p><p>\u201c\u90a3\u662f\u4e00\u4e2a\u53cc\u5411\u8bc5\u5492\u3002\u201d\u5973\u5deb\u5e08\u8bf4\u3002</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u201c\u4f60\u662f\u8bf4\uff0c\u4f60\u4e5f\u88ab\u8bc5\u5492\u4e86\uff1f\u201d</p><p>\u201c\u5bf9\u3002\u201d</p><p>\u6240\u8c13\u53cc\u5411\u8bc5\u5492\uff0c\u5c31\u662f\u8bf4\u8fd9\u79cd\u8bc5\u5492\u975e\u5e38\u5371\u9669\uff0c\u50cf\u4e00\u628a\u53cc\u5203\u5251\uff0c\u4e0d\u5206\u654c\u6211\uff0c\u4e00\u6982\u901a\u6740\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u743c\u6069\u4e2d\u8bc5\u5492\u7684\u540c\u65f6\uff0c\u7ef4\u82e5\u62c9\u4e5f\u4e2d\u4e86\u8bc5\u5492\uff0c\u743c\u6069\u4e0d\u80fd\u4e0e\u7ef4\u82e5\u62c9\u4ee5\u5916\u7684\u5973\u4eba\u505a\u7231\uff0c\u7ef4\u82e5\u62c9\u4e5f\u540c\u6837\u4e0d\u80fd\u4e0e\u743c\u6069\u4ee5\u5916\u7684\u7537\u4eba\u4e0a\u5e8a\u2014\u2014\u8fd9\u624d\u662f\u201c\u4e13\u60c5\u201d\u8bc5\u5492\u7684\u771f\u6b63\u542b\u4e49\u3002\u4e0d\u4ec5\u4ec5\u662f\u675f\u7f1a\u4e00\u65b9\uff0c\u800c\u662f\u5c06\u53cc\u65b9\u90fd\u9650\u5236\u4f4f\u4e86\u3002</p><p>\u201c\u6240\u4ee5\u4f60\u4e0d\u662f\u975e\u8981\u627e\u6211\uff0c\u800c\u662f\u56e0\u4e3a\u4f60\u522b\u65e0\u9009\u62e9\uff0c\u201d\u743c\u6069\u604d\u7136\u5927\u609f\uff0c\u201c\u4e0d\u5bf9\uff0c\u90a3\u4f60\u548c\u51db\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u201c\u4ec0\u4e48\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5973\u5deb\u5e08\u53cd\u95ee\uff0c\u201c\u4f60\u4ed4\u7ec6\u56de\u5fc6\u4e00\u4e0b\u8bc5\u5492\u7684\u5185\u5bb9\u3002\u201d</p><p>\u201c\u5185\u5bb9\u5f88\u7b80\u5355\u554a\uff0c\u6211\u8fd9\u8fb9\u5c31\u662f\u4e0d\u80fd\u548c\u4f60\u4ee5\u5916\u7684\u4efb\u4f55\u5973\u6027\u505a\u7231\uff0c\u7531\u6b64\u7c7b\u63a8\uff0c\u4f60\u5e94\u8be5\u4e5f\u4e0d\u80fd\u548c\u9664\u6211\u4e4b\u5916\u7684\u4efb\u4f55\u7537\u6027\u505a\u7231\u624d\u5bf9\u2026\u2026\u597d\u5427\uff0c\u6211\u660e\u767d\u4e86\u3002\u201d</p><p>\u7ef4\u82e5\u62c9\u7684\u786e\u4e0d\u80fd\u548c\u743c\u6069\u4e4b\u5916\u7684\u4efb\u4f55\u7537\u6027\u505a\u7231\uff0c\u4f46\u5374\u6ca1\u6709\u9650\u5236\u5979\u548c\u5973\u6027\u641e\u857e\u4e1d\u8fb9\u554a\uff0c\u539f\u6765\u8fd9\u8bc5\u5492\u770b\u4f3c\u4e25\u82db\uff0c\u5176\u5b9e\u8fd8\u7559\u4e0b\u4e86\u8fd9\u4e48\u5927\u4e00\u4e2a\u6f0f\u6d1e\u2014\u2014\u4f46\u8fd9\u4e2a\u6f0f\u6d1e\u5bf9\u743c\u6069\u800c\u8a00\u6beb\u65e0\u610f\u4e49\uff0c\u4ed6\u5b81\u613f\u4e00\u8f88\u5b50\u4e0d\u4e3e\u4e5f\u4e0d\u53ef\u80fd\u53bb\u8ddf\u7537\u4eba\u641e\u57fa\u3002</p><p>\u201c\u90a3\u4e5f\u672a\u5fc5\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u8bf4\u4e0d\u5b9a\u4f60\u6362\u79cd\u601d\u8def\uff0c\u4e5f\u80fd\u4f53\u9a8c\u5230\u4e0d\u540c\u7684\u5feb\u4e50\u3002\u6211\u770b\u8fc7\u4e00\u4e9b\u6545\u4e8b\uff0c\u91cc\u9762\u4e5f\u4e0d\u4e4f\u7537\u6027\u548c\u7537\u6027\u7684\u771f\u631a\u7231\u60c5\uff0c\u4e00\u6837\u5f88\u552f\u7f8e\u611f\u4eba\u3002\u8981\u4e0d\u8981\u6211\u7ed9\u4f60\u63a8\u8350\u51e0\u672c\uff1f\u201d</p><p>\u201c\u2026\u2026\u6211\u4eec\u80fd\u4e0d\u8bf4\u8fd9\u4e2a\u5417\uff1f\u201d</p><p>\u4e3a\u4e86\u8ba9\u7ef4\u82e5\u62c9\u95ed\u5634\uff0c\u743c\u6069\u4e0d\u5f97\u4e0d\u518d\u5ea6\u63d0\u67aa\u4e0a\u9a6c\uff0c\u53c8\u641e\u4e86\u5c06\u8fd1\u4e00\u4e2a\u5c0f\u65f6\uff0c\u76f4\u5230\u5979\u4e24\u773c\u7ffb\u767d\u5168\u8eab\u762b\u8f6f\u65b9\u624d\u7f62\u624b\uff0c\u6700\u540e\u7a7f\u8863\u51fa\u95e8\u3002</p><p>\u89e3\u51b3\u4e86\u7ef4\u82e5\u62c9\u7684\u95ee\u9898\uff0c\u743c\u6069\u5fc3\u60c5\u5927\u597d\uff0c\u8d70\u5230\u7532\u677f\u4e0a\uff0c\u6b63\u770b\u89c1\u51db\u6b63\u501a\u7740\u8239\u8237\u53d1\u5446\u3002\u743c\u6069\u8d70\u5230\u65c1\u8fb9\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u5728\u60f3\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u51db\u5413\u4e86\u4e00\u8df3\uff0c\u770b\u89c1\u662f\u4ed6\u624d\u8212\u4e86\u53e3\u6c14\uff0c\u201c\u5bf9\u4e0d\u8d77\u554a\uff0c\u743c\u6069\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u4e0d\u662f\u6545\u610f\u7684\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e0d\u662f\u6545\u610f\u7684\uff1f\u201d\u743c\u6069\u4e00\u65f6\u89c9\u5f97\u6709\u4e9b\u83ab\u540d\u5176\u5999\u3002</p><p>\u201c\u5c31\u662f\u548c\u5854\u59d0\u59d0\u554a\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u6211\u672c\u6765\u6ca1\u6253\u7b97\u7684\uff0c\u4f46\u4e0d\u77e5\u9053\u600e\u4e48\u56de\u4e8b\uff0c\u6628\u665a\u611f\u89c9\u7279\u522b\u60f3\u8981\uff0c\u60f3\u5f97\u6839\u672c\u53d7\u4e0d\u4e86\uff0c\u504f\u504f\u4f60\u53c8\u2026\u2026\u5c31\u4e0d\u77e5\u4e0d\u89c9\u548c\u5979\u5f04\u5230\u4e00\u8d77\u4e86\u3002\u201d</p><p>\u4e0d\u81f3\u4e8e\u5427\uff0c\u743c\u6069\u6320\u4e86\u6320\u5934\uff0c\u4f60\u5e73\u5e38\u4e5f\u6ca1\u8fd9\u4e48\u6b32\u6c42\u4e0d\u6ee1\u554a\u3002\u8eab\u8fb9\u8fd9\u4e9b\u5973\u4eba\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4e00\u4e2a\u4e00\u4e2a\u90fd\u4e0d\u592a\u6b63\u5e38\uff0c\u7ef4\u82e5\u62c9\u5c31\u7b97\u4e86\uff0c\u5979\u662f\u88ab\u7c89\u7ea2\u6cd5\u672f\u5f71\u54cd\uff0c\u51db\u53c8\u6ca1\u4e2d\u62db\uff0c\u4e3a\u4ec0\u4e48\u4e5f\u8fd9\u6837\u5462\u3002\u96be\u9053\u662f\u56e0\u4e3a\u6625\u5929\u5230\u4e86\u7684\u7f18\u6545\u5417\u3002</p><p>\u201c\u5bf9\u4e0d\u8d77\u5566\uff0c\u743c\u6069\uff0c\u6211\u4fdd\u8bc1\u4ee5\u540e\u4e0d\u4f1a\u4e86\uff0c\u4f60\u522b\u751f\u6c14\u597d\u4e0d\u597d\u3002\u201d</p><p>\u201c\u6211\u6ca1\u751f\u6c14\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u548c\u5979\u73a9\u73a9\u800c\u5df2\uff0c\u6211\u53c8\u4e0d\u4ecb\u610f\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u53ef\u662f\u4f60\u4e00\u5411\u90fd\u5f88\u5c0f\u6c14\u554a\uff0c\u53ea\u8bb8\u81ea\u5df1\u5077\u60c5\uff0c\u4e0d\u8bb8\u522b\u4eba\u5916\u9047\u2014\u2014\u4e0d\u662f\u5417\uff1f\u201d</p><p>\u201c\u2026\u2026\u8fd9\u662f\u8c01\u8bf4\u7684\uff1f\u201d</p><p>\u201c\u827e\u5f25\u8587\u3002\u201d</p><p>\u201c\u597d\u5427\uff0c\u201d\u743c\u6069\u627f\u8ba4\uff0c\u201c\u6211\u662f\u5f88\u5c0f\u6c14\u3002\u4f46\u4f60\u548c\u827e\u5f25\u8587\u4e5f\u7ecf\u5e38\u641e\u5728\u4e00\u8d77\uff0c\u6211\u4e5f\u6ca1\u8bf4\u4ec0\u4e48\u554a\u3002\u201d</p><p>\u201c\u4e5f\u662f\u54e6\uff0c\u201d\u51db\u60f3\u4e86\u60f3\uff0c\u201c\u4e5f\u5c31\u662f\u8bf4\u4f60\u4e0d\u4ecb\u610f\u5973\u53cb\u6709\u5916\u9047\uff1f\u201d</p><p>\u201c\u4ed6\u5f53\u7136\u4ecb\u610f\u5566\uff0c\u201d\u838e\u73de\u514b\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u5192\u51fa\u6765\uff0c\u201c\u4e0d\u8fc7\u8981\u770b\u5bf9\u8c61\u800c\u5b9a\u3002\u5982\u679c\u4f60\u5916\u9047\u7684\u5bf9\u8c61\u662f\u5973\u4eba\uff0c\u4ed6\u53ef\u4ee5\u5bb9\u5fcd\uff1b\u5982\u679c\u4f60\u5916\u9047\u7684\u5bf9\u8c61\u662f\u4e2a\u4ed6\u4e5f\u53ef\u4ee5\u4e0a\u7684\u5973\u4eba\uff0c\u90a3\u4ed6\u5c31\u5b8c\u5168\u4e0d\u4ecb\u610f\u4e86\u3002\u201d</p><p>\u201c\u4f60\u8fd8\u771f\u662f\u4e86\u89e3\u6211\uff0c\u201d\u743c\u6069\u6ca1\u597d\u6c14\u5730\u8bf4\uff0c\u201c\u73b0\u5728\u5230\u54ea\u4e86\uff1f\u201d</p><p>\u201c\u9a6c\u4e0a\u9760\u5cb8\uff0c\u8fd8\u6709\u4e94\u5206\u949f\uff0c\u8db3\u591f\u4f60\u56de\u53bb\u548c\u7ef4\u82e5\u62c9\u5c0f\u59d0\u518d\u6765\u4e00\u53d1\u4e86\u3002\u201d</p><p>\u201c\u6211\u53ef\u4ee5\u628a\u4f60\u8fd9\u53e5\u8bdd\u7406\u89e3\u4e3a\u6311\u8845\u5417\uff1f\u201d</p><p>\u201c\u90a3\u53c8\u600e\u4e48\u6837\uff1f\u201d\u9b45\u9b54\u683c\u683c\u5a07\u7b11\uff0c\u201c\u5982\u679c\u4e3b\u4eba\u89c9\u5f97\u4e0d\u670d\u6c14\uff0c\u5c31\u6765\u8bc1\u660e\u7ed9\u6211\u770b\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u4eec\u804a\uff0c\u6211\u6709\u4e8b\u5148\u8d70\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u4e0a\u5cb8\u4e4b\u540e\uff0c\u743c\u6069\u5c06\u7eb8\u8239\u6536\u56de\u547d\u8fd0\u957f\u591c\uff0c\u53c8\u53d6\u4e86\u4e00\u8f86\u56db\u8f6e\u9a6c\u8f66\u51fa\u6765\uff0c\u4e00\u884c\u4eba\u5750\u4e86\u8fdb\u53bb\u3002\u4e00\u8def\u65e0\u60ca\u65e0\u9669\uff0c\u4ec0\u4e48\u90fd\u6ca1\u53d1\u751f\uff0c\u987a\u987a\u5229\u5229\u5730\u62b5\u8fbe\u4e86\u53f6\u5f18\u57ce\u3002</p><p>\u53f6\u5f18\u57ce\u4f4d\u4e8e\u5760\u661f\u6d77\u7684\u4e1c\u5317\u5cb8\uff0c\u662f\u6851\u6bd4\u4e9a\u738b\u56fd\u6392\u540d\u7b2c\u4e8c\u7684\u5927\u6d77\u6e2f\uff0c\u6bcf\u5929\u90fd\u6709\u65e0\u6570\u7684\u5546\u8239\u5728\u6e2f\u53e3\u505c\u822a\u9760\u5cb8\uff0c\u6216\u8005\u626c\u5e06\u51fa\u6d77\uff0c\u9a76\u5411\u5404\u5730\u3002\u4ece\u53f6\u5f18\u7684\u6e2f\u53e3\u4e58\u8239\u51fa\u53d1\uff0c\u81ea\u4e1c\u5317\u5f80\u897f\u5357\uff0c\u659c\u5411\u7a7f\u8fc7\u5760\u661f\u6d77\uff0c\u4fbf\u53ef\u4ee5\u62b5\u8fbe\u4e1c\u57df\u3002</p><p>\u867d\u7136\u540d\u4e3a\u201c\u57ce\u201d\uff0c\u4f46\u53f6\u5f18\u5e76\u6ca1\u6709\u57ce\u4e3b\uff0c\u4e5f\u6ca1\u6709\u57ce\u5899\uff0c\u5b83\u540d\u4e49\u4e0a\u76f4\u5c5e\u4e8e\u56fd\u738b\u7edf\u9886\uff0c\u5177\u4f53\u7684\u884c\u653f\u4e8b\u52a1\u5219\u662f\u7531\u57ce\u4e2d\u5341\u51e0\u540d\u5927\u5546\u4eba\u7ec4\u6210\u7684\u201c\u8bc4\u8bae\u4f1a\u201d\u8d1f\u8d23\uff0c\u5b83\u4eec\u5236\u5b9a\u6cd5\u5f8b\u548c\u89c4\u5219\uff0c\u62db\u52df\u519b\u961f\u548c\u536b\u5175\uff0c\u4ee5\u53ca\u6536\u7a0e\u3002\u8bc4\u8bae\u4f1a\u6210\u5458\u6bcf\u516d\u5e74\u66f4\u6362\u4e09\u5206\u4e4b\u4e00\uff0c\u7531\u5168\u4f53\u5e02\u6c11\u6295\u7968\u9009\u4e3e\u4ea7\u751f\uff0c\u9887\u6709\u51e0\u5206\u6c11\u4e3b\u98ce\u8303\u2014\u2014\u5982\u679c\u4e0d\u8003\u8651\u201c\u5019\u9009\u4eba\u5fc5\u987b\u6709\u76f8\u5f53\u4e8e\u4e24\u5343\u4e94\u767e\u91d1\u5e01\u4ee5\u4e0a\u4e4b\u8eab\u5bb6\u201d\u8fd9\u4e2a\u9650\u5236\u89c4\u5b9a\u7684\u8bdd\u3002</p><p>\u743c\u6069\u5e76\u672a\u8fdb\u5165\u5e02\u533a\uff0c\u800c\u662f\u5728\u57ce\u90ca\u627e\u4e86\u5757\u65e0\u4e3b\u7a7a\u5730\uff0c\u5c55\u5f00\u9752\u94dc\u8c6a\u5b85\uff0c\u5c06\u9632\u5fa1\u8b66\u6212\u8c03\u6574\u5230\u6700\u9ad8\u7ea7\u522b\uff0c\u7136\u540e\u6d3e\u838e\u73de\u514b\u53bb\u6253\u63a2\u6d88\u606f\u3002\u201c\u8981\u6536\u96c6\u54ea\u65b9\u9762\u7684\u8d44\u6599\uff1f\u201d\u9b45\u9b54\u95ee\u3002</p><p>\u201c\u4e00\u5207\u6709\u5173\u4e1c\u57df\u7684\u4fe1\u606f\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53e6\u5916\u95ee\u95ee\u6709\u6ca1\u6709\u54ea\u8258\u8239\u6b63\u597d\u8981\u53bb\u4e1c\u57df\u7684\u2014\u2014\u4e0d\u8fc7\u8fd9\u4e2a\u4e0d\u7528\u6025\uff0c\u53cd\u6b63\u6211\u4eec\u8981\u5728\u8fd9\u91cc\u4f11\u6574\u4e00\u6bb5\u65f6\u95f4\u3002\u201d</p><p>\u201c\u9075\u547d\u3002\u201d</p><p>\u838e\u73de\u514b\u5fc3\u4e2d\u7591\u60d1\uff0c\u4e0d\u660e\u767d\u4e3a\u4ec0\u4e48\u8981\u5728\u8fd9\u91cc\u591a\u505a\u505c\u7559\uff0c\u53f6\u5f18\u53c8\u6ca1\u6709\u4ec0\u4e48\u98ce\u666f\u540d\u80dc\u53ef\u770b\u3002\u4f46\u743c\u6069\u4e5f\u6ca1\u6709\u591a\u505a\u89e3\u91ca\uff0c\u53ea\u662f\u6325\u6325\u624b\uff0c\u8ba9\u5979\u8d76\u5feb\u53bb\u529e\u4e8b\u3002</p><p>\u8f6c\u773c\u4e4b\u95f4\uff0c\u56db\u5929\u8fc7\u53bb\u4e86\u3002</p><p>\u8fd9\u56db\u5929\u8fc7\u5f97\u5f88\u5e73\u9759\uff0c\u838e\u73de\u514b\u6df7\u8ff9\u4e8e\u6d77\u6e2f\u7801\u5934\u7684\u5404\u4e2a\u9152\u9986\u4e2d\uff0c\u548c\u90a3\u4e9b\u5192\u9669\u8005\u548c\u6c34\u624b\u4eec\u6253\u6210\u4e00\u7247\uff0c\u641c\u96c6\u5404\u79cd\u6709\u5173\u4e1c\u57df\u7684\u60c5\u62a5\uff0c\u5206\u95e8\u522b\u7c7b\u6574\u7406\u6210\u6587\u5b57\u8d44\u6599\u9001\u7ed9\u743c\u6069\u3002\u5979\u662f\u6740\u624b\u51fa\u8eab\uff0c\u8fd9\u79cd\u4e8b\u60c5\u4e5f\u7b97\u662f\u672c\u884c\uff0c\u505a\u8d77\u6765\u9a7e\u8f7b\u5c31\u719f\u3002\u4f46\u53e6\u4e00\u9879\u5de5\u4f5c\u7684\u8fdb\u5c55\u5c31\u4e0d\u600e\u4e48\u987a\u5229\u4e86\uff0c\u53f6\u5f18\u57ce\u6e2f\u53e3\u8fdb\u8fdb\u51fa\u51fa\u7684\u6d77\u8239\u5f88\u591a\uff0c\u5230\u54ea\u4e2a\u5730\u65b9\u7684\u90fd\u6709\uff0c\u552f\u72ec\u53bb\u4e1c\u57df\u7684\u6bd4\u8f83\u96be\u627e\uff0c\u90a3\u91cc\u5b9e\u5728\u662f\u592a\u504f\u8fdc\u4e86\uff0c\u800c\u4e14\u5c40\u52bf\u6bd4\u8f83\u6df7\u4e71\uff0c\u7ecf\u5e38\u6253\u4ed7\uff0c\u4e00\u822c\u4eba\u90fd\u4e0d\u613f\u610f\u53bb\uff0c\u51fa\u9ad8\u4ef7\u4e5f\u4e0d\u884c\u3002</p><p>\u201c\u53bb\u4e1c\u57df\u7684\u8239\u5012\u4e5f\u4e0d\u662f\u6ca1\u6709\uff0c\u4f46\u5f88\u5c11\uff0c\u201d\u4e00\u4e2a\u6c34\u624b\u8bf4\uff0c\u89c6\u7ebf\u4e0b\u610f\u8bc6\u5730\u5728\u838e\u73de\u514b\u7684\u80f8\u53e3\u90e8\u4f4d\u6e38\u79fb\uff0c\u201c\u800c\u4e14\u90fd\u662f\u4e03\u6708\u521d\u624d\u51fa\u53d1\uff0c\u8981\u7b49\u5b63\u98ce\u548c\u6d0b\u6d41\u3002\u201d</p><p>\u73b0\u5728\u662f\u4e8c\u6708\u4e2d\u65ec\uff0c\u8ddd\u79bb\u4e03\u6708\u521d\u8fd8\u6709\u56db\u4e2a\u6765\u6708\uff0c\u8fd9\u4e2a\u7b49\u5f85\u65f6\u95f4\u663e\u7136\u592a\u957f\u4e86\u3002</p><p>\u201c\u90a3\u53ef\u7cdf\u4e86\uff0c\u201d\u838e\u73de\u514b\u5fae\u5fae\u8e59\u7709\uff0c\u9762\u9732\u5fe7\u8272\uff0c\u201c\u6211\u5bb6\u4e3b\u4eba\u6709\u6025\u4e8b\u8981\u53bb\u4e1c\u57df\uff0c\u547d\u4ee4\u6211\u4e00\u5b9a\u8981\u627e\u5230\u8239\uff0c\u627e\u4e0d\u5230\u7684\u8bdd\uff0c\u4ed6\u4f1a\u91cd\u91cd\u60e9\u7f5a\u6211\u7684\u3002\u201d</p><p>\u201c\u6211\u5012\u662f\u77e5\u9053\u6709\u4e00\u8258\u8239\uff0c\u6bcf\u5e74\u90fd\u4f1a\u5728\u8fd9\u4e2a\u65f6\u5019\u53bb\u4e00\u8d9f\u4e1c\u57df\u3002\u201d\u53e6\u4e00\u4e2a\u6c34\u624b\u8bf4\u3002</p><p>\u201c\u4f60\u662f\u8bf4\u98de\u7fd4\u86c7\u5973\u53f7\uff1f\u201d\u524d\u4e00\u4e2a\u6c34\u624b\u8bf4\uff0c\u201c\u5567\uff0c\u90a3\u9b3c\u8239\u54ea\u6709\u4eba\u6562\u5750\u3002\u201d</p><p>\u201c\u600e\u4e48\uff1f\u201d\u838e\u73de\u514b\u95ee\uff0c\u7ed9\u4ed6\u9012\u4e86\u4e00\u676f\u9152\u3002</p><p>\u201c\u98de\u7fd4\u86c7\u5973\u53f7\u662f\u666e\u6717\u514b\u7684\u8239\uff0c\u5b83\u2026\u2026\u633a\u602a\u7684\uff0c\u6211\u4e5f\u8bf4\u4e0d\u597d\uff0c\u4f60\u81ea\u5df1\u770b\u5230\u5c31\u5168\u660e\u767d\u4e86\uff0c\u53cd\u6b63\u5927\u5bb6\u90fd\u8bf4\u90a3\u662f\u4e00\u8258\u9b3c\u8239\uff0c\u4e0a\u53bb\u7684\u4eba\u5c31\u522b\u60f3\u4e0b\u6765\uff0c\u8fde\u6211\u4eec\u90fd\u4e0d\u6562\u8ddf\u5b83\u6253\u4ea4\u9053\u3002\u800c\u4e14\u5b83\u795e\u51fa\u9b3c\u6ca1\uff0c\u8c01\u4e5f\u4e0d\u77e5\u9053\u5b83\u7684\u884c\u8e2a\uff0c\u4f1a\u4e0d\u4f1a\u6765\u53f6\u5f18\u4e5f\u662f\u8bf4\u4e0d\u51c6\u7684\u4e8b\u3002\u201d</p><p>\u201c\u8fd9\u4f60\u5c31\u4e0d\u77e5\u9053\u4e86\u5427\uff0c\u201d\u53e6\u4e00\u4e2a\u6c34\u624b\u8bf4\uff0c\u201c\u98de\u7fd4\u86c7\u5973\u53f7\u662f\u6bcf\u5e74\u7684\u4e8c\u6708\u5e95\u6216\u8005\u4e09\u6708\u521d\uff0c\u90fd\u4f1a\u4ece\u5766\u745e\u65af\u57ce\u51fa\u53d1\uff0c\u9a76\u5411\u8f9b\u5df4\u57ce\uff0c\u9014\u4e2d\u5fc5\u5b9a\u4f1a\u7ecf\u8fc7\u8fd9\u91cc\u3002\u8fc7\u53bb\u8fd9\u5341\u4e94\u516d\u5e74\u90fd\u662f\u8fd9\u6837\uff0c\u4ece\u65e0\u4f8b\u5916\u3002\u7b97\u7b97\u65f6\u95f4\uff0c\u5b83\u5e94\u8be5\u4e5f\u5feb\u5230\u4e86\uff0c\u5c0f\u59b9\u59b9\uff0c\u4f60\u7559\u4e2a\u8054\u7cfb\u65b9\u5f0f\u5427\uff0c\u7b49\u5230\u4e86\u6211\u7b2c\u4e00\u65f6\u95f4\u901a\u77e5\u4f60\u3002\u201d</p><p>\u201c\u90a3\u53ef\u592a\u611f\u8c22\u4e86\uff0c\u201d\u838e\u73de\u514b\u7b11\u76c8\u76c8\u5730\u8bf4\uff0c\u4ece\u6000\u91cc\u638f\u51fa\u4e00\u5f20\u624b\u5e15\uff0c\u5728\u4e0a\u9762\u5199\u4e0b\u5730\u5740\uff0c\u201c\u4e00\u5b9a\u8981\u8bb0\u5f97\u54e6\uff0c\u4eba\u5bb6\u4f1a\u6709\u62a5\u7b54\u7684\u3002\u201d</p><p>\u8239\u7684\u95ee\u9898\u603b\u7b97\u662f\u6709\u4e86\u7740\u843d\uff0c\u838e\u73de\u514b\u677e\u4e86\u53e3\u6c14\uff0c\u8d76\u5feb\u56de\u53bb\u6c47\u62a5\u3002\u201c\u666e\u6717\u514b\uff1f\u201d\u542c\u4e86\u8fd9\u4e2a\u540d\u5b57\uff0c\u743c\u6069\u7a0d\u5fae\u6014\u4e86\u6014\uff0c\u201c\u505a\u5f97\u4e0d\u9519\uff0c\u201d\u4ed6\u79f0\u8d5e\uff0c\u201c\u4e0d\u8fc7\u518d\u53bb\u67e5\u67e5\u8fd9\u4e2a\u4eba\u7684\u5e95\u7ec6\uff0c\u4ed6\u6709\u53ef\u80fd\u662f\u4e2a\u6d77\u76d7\u3002\u201d</p><p>\u201c\u6d77\u76d7\uff1f\u201d\u838e\u73de\u514b\u8be7\u5f02\uff0c\u201c\u4f60\u8ba4\u8bc6\u4ed6\uff1f\u201d</p><p>\u201c\u4e0d\u8ba4\u8bc6\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u8fd9\u4e2a\u540d\u5b57\u542c\u8d77\u6765\u5c31\u5f88\u50cf\u6d77\u76d7\u8239\u957f\u2014\u2014\u603b\u4e4b\u4ed4\u7ec6\u8c03\u67e5\u4e00\u4e0b\u3002\u201d</p><p>\u201c\u54e6\uff0c\u597d\u3002\u201d</p><p>\u838e\u73de\u514b\u6ee1\u5934\u96fe\u6c34\u5730\u8d70\u4e86\uff0c\u743c\u6069\u5173\u4e0a\u95e8\uff0c\u7ee7\u7eed\u8fdb\u5165\u51a5\u60f3\u3002\u4e03\u5f69\u7f24\u7eb7\u7684\u96fe\u6c14\u81ea\u4ed6\u7684\u6307\u5c16\u6ea2\u51fa\uff0c\u7f13\u6162\u5730\u8513\u5ef6\u5f00\u6765\uff0c\u5c06\u4ed6\u6574\u4e2a\u4eba\u6df9\u6ca1\u5176\u4e2d\u3002</p><p>\u9634\u5f71\u8c37\u4e00\u6218\uff0c\u743c\u6069\u5728\u51ef\u745f\u7433\u7684\u5e2e\u52a9\u4e0b\uff0c\u4ece\u7ef4\u82e5\u62c9\u5904\u7a83\u53d6\u5230\u4e00\u4e9b\u5deb\u5e08\u4e4b\u795e\u7684\u795e\u529b\u201c\u8679\u96fe\u201d\uff0c\u539f\u672c\u662f\u5e0c\u671b\u501f\u52a9\u5b83\u5feb\u901f\u589e\u5f3a\u5b9e\u529b\uff0c\u5bf9\u6297\u540e\u9762\u7684\u8fde\u573a\u5927\u6218\u3002\u7ed3\u679c\u4e8b\u4e0e\u613f\u8fdd\uff0c\u201c\u8679\u96fe\u201d\u867d\u7136\u4ef7\u503c\u975e\u51e1\uff0c\u4f46\u5176\u4e09\u5927\u529f\u80fd\uff08\u4f20\u9053\u3001\u590d\u523b\u548c\u865a\u62df\uff09\u5374\u90fd\u504f\u5411\u4e8e\u8f85\u52a9\u4e00\u7c7b\uff0c\u5e76\u4e0d\u80fd\u76f4\u63a5\u800c\u5feb\u901f\u5730\u63d0\u5347\u529b\u91cf\uff0c\u743c\u6069\u4e0d\u4f46\u767e\u5fd9\u4e00\u573a\uff0c\u53cd\u800c\u8fd8\u56e0\u6b64\u4e2d\u4e86\u201c\u4e13\u60c5\u201d\u8bc5\u5492\uff0c\u5230\u73b0\u5728\u90fd\u6ca1\u89e3\u9664\uff0c\u7b97\u8d77\u6765\u5b9e\u5728\u6bd4\u8f83\u4e8f\u3002\u4e0d\u8fc7\u6b64\u4e00\u65f6\uff0c\u5f7c\u4e00\u65f6\uff0c\u5982\u4eca\u6218\u6597\u5df2\u7ecf\u6253\u5b8c\uff0c\u8679\u96fe\u5c31\u6d3e\u4e0a\u7528\u573a\u4e86\u3002</p><p>\u8679\u96fe\u662f\u5deb\u5e08\u4e4b\u795e\u7684\u795e\u529b\uff0c\u8d50\u4e88\u5176\u9009\u6c11\u4f7f\u7528\uff0c\u5176\u201c\u5206\u91cf\u201d\u662f\u76f8\u5bf9\u56fa\u5b9a\u7684\uff0c\u4f7f\u7528\u4e00\u70b9\u5c31\u6d88\u8017\u4e00\u70b9\uff0c\u4f46\u53ef\u4ee5\u901a\u8fc7\u7948\u7977\u4e0e\u795e\u660e\u4ea4\u6d41\u800c\u5f97\u5230\u8865\u5145\u3002\u743c\u6069\u7ecf\u8fc7\u957f\u8fbe\u4e5d\u4e2a\u663c\u591c\u7684\u4e0d\u61c8\u52aa\u529b\uff0c\u5c06\u7ef4\u82e5\u62c9\u5f53\u65f6\u6240\u62e5\u6709\u7684\u8679\u96fe\u63a0\u593a\u4e86\u5c06\u8fd1\u4e03\u6210\uff0c\u6536\u83b7\u4e0d\u53ef\u8c13\u4e0d\u4e30\u3002\u5f53\u7136\uff0c\u7ef4\u82e5\u62c9\u662f\u529b\u6218\u4e4b\u540e\u88ab\u8428\u9a6c\u65af\u7279\u4fd8\u864f\uff0c\u8679\u96fe\u5df2\u7ecf\u6d88\u8017\u6389\u5f88\u5927\u4e00\u90e8\u5206\uff0c\u6298\u7b97\u4e0b\u6765\uff0c\u743c\u6069\u6240\u5f97\u4e4b\u91cf\uff0c\u5927\u7ea6\u76f8\u5f53\u4e8e\u7ef4\u82e5\u62c9\u6b63\u5e38\u72b6\u6001\u7684\u4e09\u5206\u4e4b\u4e00\u5de6\u53f3\u3002</p><p>\u8fd9\u79cd\u7a0b\u5ea6\u7684\u8679\u96fe\uff0c\u5c1a\u4e0d\u8db3\u4ee5\u53d1\u6325\u5b83\u7684\u201c\u4f20\u9053\u201d\u548c\u201c\u590d\u523b\u201d\u529f\u80fd\uff0c\u4f46\u201c\u865a\u62df\u201d\u5df2\u7ecf\u53ef\u4ee5\u4f7f\u7528\uff0c\u5177\u4f53\u7684\u4e00\u4e9b\u6280\u672f\u7ec6\u8282\uff0c\u743c\u6069\u8fd9\u51e0\u5929\u4e5f\u5df2\u7ecf\u8be6\u7ec6\u95ee\u8fc7\u7ef4\u82e5\u62c9\u3002\u6240\u8c13\u201c\u865a\u62df\u201d\uff0c\u5373\u662f\u501f\u52a9\u8679\u96fe\uff0c\u5851\u9020\u51fa\u4e00\u4e2a\u53ef\u4ee5\u8ba9\u5deb\u5e08\u65e0\u9650\u5236\u65bd\u6cd5\u7684\u865a\u5e7b\u8bad\u7ec3\u573a\uff0c\u5bf9\u4e8e\u953b\u70bc\u6cd5\u672f\u6781\u6709\u4ef7\u503c\u3002\u6309\u7167\u7ef4\u82e5\u62c9\u7684\u8bf4\u6cd5\uff0c\u5979\u4f7f\u7528\u8679\u96fe\u7684\u865a\u62df\u529f\u80fd\uff0c\u6bcf\u5929\u53ef\u4ee5\u6700\u591a\u7ef4\u6301\u56db\u4e2a\u5c0f\u65f6\uff0c\u743c\u6069\u5f53\u7136\u6bd4\u4e0d\u4e0a\u5979\uff0c\u4f46\u4e00\u4e2a\u5c0f\u65f6\u5de6\u53f3\u8fd8\u662f\u6ca1\u95ee\u9898\u7684\u3002</p><p>\u4e00\u4e2a\u5c0f\u65f6\u8bf4\u8d77\u6765\u4e0d\u957f\uff0c\u4f46\u5bf9\u4e8e\u5deb\u5e08\u800c\u8a00\uff0c\u8fde\u7eed\u4e0d\u95f4\u65ad\u5730\u65bd\u6cd5\uff0c\u8fd9\u5f3a\u5ea6\u8fdc\u8fdc\u8d85\u4e4e\u5bfb\u5e38\u4eba\u7684\u60f3\u8c61\u3002\u5f53\u743c\u6069\u7ed3\u675f\u51a5\u60f3\uff0c\u4ece\u865a\u62df\u8bad\u7ec3\u573a\u4e2d\u9000\u51fa\u65f6\uff0c\u4ed6\u611f\u89c9\u81ea\u5df1\u7cbe\u75b2\u529b\u5c3d\uff0c\u4eff\u4f5b\u51e0\u5929\u51e0\u591c\u90fd\u6ca1\u6709\u7761\u89c9\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u4ed6\u8fd8\u662f\u5f3a\u5fcd\u7740\u5934\u75bc\uff0c\u62ff\u51fa\u7b14\u8bb0\uff0c\u628a\u81ea\u5df1\u65b9\u624d\u7684\u4e00\u4e9b\u4f53\u609f\u548c\u5fc3\u5f97\u8bb0\u5f55\u4e0b\u6765\u3002\u9b54\u6cd5\u5b66\u4e60\u6ce8\u91cd\u953b\u70bc\uff0c\u66f4\u9700\u8981\u4e0d\u65ad\u7684\u603b\u7ed3\u53cd\u601d\uff0c\u5982\u6b64\u624d\u8c08\u5f97\u4e0a\u63d0\u9ad8\u548c\u8fdb\u6b65\u3002</p><p>\u7b14\u8d70\u9f99\u86c7\u5730\u5199\u5b8c\uff0c\u743c\u6069\u957f\u957f\u5730\u5410\u4e86\u53e3\u6c14\uff0c\u53d1\u89c9\u5185\u8863\u5df2\u7ecf\u88ab\u6c57\u6c34\u6d78\u900f\uff0c\u4e8e\u662f\u51b3\u5b9a\u53bb\u6d17\u4e2a\u6fa1\u3002</p><p>\u5965\u6c83\u662f\u4e2a\u70ed\u7231\u6d17\u6fa1\u7684\u5927\u80d6\u5b50\uff0c\u4ed6\u505a\u51fa\u6765\u7684\u9752\u94dc\u8c6a\u5b85\uff0c\u91cc\u9762\u7684\u6d74\u7f38\u65e2\u6df1\u53c8\u5927\uff0c\u743c\u6069\u7b80\u76f4\u53ef\u4ee5\u5728\u91cc\u9762\u6e38\u6cf3\u3002\u8212\u8212\u670d\u670d\u5730\u6ce1\u4e86\u4e2a\u6fa1\uff0c\u743c\u6069\u88f9\u7740\u6d74\u5dfe\u51fa\u6765\uff0c\u521a\u521a\u63a8\u5f00\u623f\u95e8\uff0c\u7a81\u7136\u77a5\u89c1\u7a97\u6237\u7684\u73bb\u7483\u4e0a\u6620\u51fa\u4e00\u4e2a\u6a21\u7cca\u7684\u4eba\u5f71\u3002</p><p>\u4ed6\u5fae\u5fae\u4e00\u60ca\uff0c\u53f3\u624b\u865a\u63e1\uff0c\u638c\u5fc3\u5df2\u7ecf\u51dd\u805a\u8d77\u4e00\u4e2a\u7206\u88c2\u7b26\u6587\uff0c\u968f\u65f6\u51c6\u5907\u5f39\u5c04\u51fa\u53bb\u3002\u7136\u540e\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u743c\u6069\u5168\u8eab\u653e\u677e\u4e0b\u6765\uff0c\u201c\u4f60\u9192\u4e86\uff0c\u827e\u5f25\u8587\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u91d1\u53d1\u7684\u5c11\u5973\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u5979\u7a7f\u7740\u6d45\u84dd\u8272\u7684\u886c\u886b\u548c\u7c73\u767d\u8272\u957f\u88e4\uff0c\u5934\u53d1\u7528\u4e1d\u5e26\u62e2\u6210\u4e00\u4e2a\u7b80\u5355\u7684\u9a6c\u5c3e\uff0c\u6574\u4e2a\u4eba\u770b\u8d77\u6765\u65e2\u5e72\u7ec3\u53c8\u5e05\u6c14\uff0c\u7cbe\u795e\u5955\u5955\uff0c\u5b8c\u5168\u770b\u4e0d\u51fa\u4efb\u4f55\u53d7\u4f24\u7684\u75d5\u8ff9\u3002\u5979\u624b\u91cc\u62ff\u7740\u4e00\u672c\u4e66\uff0c\u6b63\u968f\u610f\u7ffb\u770b\uff0c\u90a3\u662f\u743c\u6069\u521a\u521a\u653e\u5728\u684c\u4e0a\u7684\u9b54\u6cd5\u7b14\u8bb0\uff0c\u201c\u8fd9\u662f\u54ea\u91cc\u554a\uff1f\u201d\u5979\u95ee\uff0c\u671b\u4e86\u671b\u7a97\u5916\u7684\u666f\u8272\uff0c\u201c\u6709\u70b9\u50cf\u662f\u6851\u6bd4\u4e9a\u7684\u53f6\u5f18\u3002\u201d</p><p>\u201c\u662f\u53f6\u5f18\u57ce\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u4ee5\u524d\u6765\u8fc7\uff1f\u201d</p><p>\u201c\u66fe\u7ecf\u6709\u4e00\u6b21\u8def\u8fc7\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u51e0\u5e74\u524d\u7684\u4e8b\u60c5\u4e86\u3002\u51db\u5462\uff1f\u201d</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u597d\u50cf\u662f\u548c\u7ef4\u82e5\u62c9\u4e00\u8d77\u901b\u8857\u53bb\u4e86\u3002\u201d</p><p>\u8fd9\u51e0\u5929\u6765\uff0c\u743c\u6069\u5fd9\u4e8e\u5b66\u4e60\uff0c\u838e\u73de\u514b\u5fd9\u4e8e\u6253\u63a2\u60c5\u62a5\uff0c\u800c\u51db\u5219\u5fd9\u4e8e\u6574\u5929\u62c9\u7740\u7ef4\u82e5\u62c9\u53bb\u901b\u8857\u8d2d\u7269\uff0c\u628a\u57ce\u4e2d\u5927\u5927\u5c0f\u5c0f\u7684\u5546\u5e97\u90fd\u8f6c\u4e86\u4e00\u904d\uff0c\u5927\u5305\u5c0f\u5305\u62ce\u56de\u6765\u65e0\u6570\uff0c\u6709\u65f6\u5019\u8fd8\u591c\u4e0d\u5f52\u5bbf\uff0c\u81f3\u4e8e\u5979\u4eec\u662f\u4e0d\u662f\u8d81\u673a\u53bb\u5f00\u623f\u5077\u60c5\uff0c\u743c\u6069\u65e2\u4e0d\u60f3\u95ee\uff0c\u4e5f\u4e0d\u60f3\u77e5\u9053\u3002</p><p>\u201c\u7ef4\u82e5\u62c9\uff1f\u201d\u5c11\u5973\u6014\u4e86\u4e0b\uff0c\u201c\u4f20\u9053\u5deb\u5e08\uff1f\u5979\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\uff1f\u201d</p><p>\u743c\u6069\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u4e3e\u8d77\u53cc\u624b\uff0c\u201c\u6211\u5766\u767d\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8fd9\u4e2a\u8bf4\u6765\u8bdd\u957f\u3002\u201d</p></article>"}}}]);
