(window.webpackJsonp=window.webpackJsonp||[]).push([[175],{197:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e8c\u8282 \u80a5\u5deb\u5996\u7684\u7231\u60c5",content:"<article><h2>\u7b2c\u5341\u4e8c\u8282 \u80a5\u5deb\u5996\u7684\u7231\u60c5</h2><p>\u6d17\u5b8c\u6fa1\uff0c\u743c\u6069\u6b63\u60f3\u8d76\u7d27\u544a\u8f9e\u56de\u53bb\uff0c\u8fd8\u8981\u4e3a\u660e\u5929\u7684\u9003\u8dd1\u5927\u8ba1\u505a\u51c6\u5907\u5462\uff0c\u7136\u800c\u8001\u5bb6\u4f19\u53c8\u628a\u4ed6\u53eb\u4f4f\u4e86\u3002</p><p>\u201c\u6765\uff0c\u6765\uff0c\u743c\u6069\uff0c\u201d\u5965\u6c83\u5f88\u70ed\u60c5\u5730\u9080\u8bf7\u7740\uff0c\u201c\u5e26\u4f60\u53bb\u53c2\u89c2\u53c2\u89c2\u6211\u7684\u85cf\u5b9d\u5e93\u3002\u201d</p><p>\u201c\u554a\u3002\u201d</p><p>\u201c\u544a\u8bc9\u4f60\uff0c\u4f60\u8001\u5e08\u6211\u53ef\u662f\u7b2c\u4e00\u6d41\u7684\u5b9d\u7269\u6536\u85cf\u5bb6\u5462\uff0c\u773c\u5149\u53c8\u9ad8\uff0c\u9274\u8d4f\u6c34\u5e73\u4e00\u6d41\uff0c\u201d\u5965\u6c83\u8d70\u5728\u524d\u9762\uff0c\u4e00\u8def\u4e0a\u5411\u743c\u6069\u81ea\u5439\u81ea\u64c2\u7740\uff0c\u201c\u5f53\u4eca\u4e16\u4e0a\u7684\u6536\u85cf\u5bb6\u867d\u591a\uff0c\u4f46\u8981\u8bba\u85cf\u5b9d\u6570\u91cf\u4e4b\u591a\uff0c\u8d28\u91cf\u4e4b\u7cbe\uff0c\u6211\u80af\u5b9a\u662f\u9996\u5c48\u4e00\u6307\u7684\u5440\u3002\u4eca\u5929\u5c31\u8ba9\u4f60\u6765\u5f00\u5f00\u773c\u754c\u3002\u201d</p><p>\u743c\u6069\u65e0\u8bed\u5730\u8ddf\u5728\u540e\u9762\uff0c\u4ed6\u60f3\u8d77\u8d44\u6599\u4e0a\u63d0\u53ca\u5965\u6c83\u7684\u65f6\u5019\uff0c\u5bf9\u6027\u683c\u7684\u8bc4\u4ef7\uff0c\u9664\u4e86\u201c\u66b4\u8e81\u201d\u3001\u201c\u6709\u6d3b\u529b\u201d\u3001\u201c\u5bcc\u6709\u5e7d\u9ed8\u611f\u201d\u4e4b\u5916\uff0c\u6700\u540e\u8fd8\u6709\u4e24\u4e2a\u8bcd\uff1a\u559c\u6b22\u70ab\u8000\u3001\u7231\u5439\u5618\u2026\u2026</p><p>\u597d\u5427\uff0c\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u8fd9\u4e9b\u54c1\u8d28\u4e5f\u7b97\u4e0d\u4e0a\u591a\u4e48\u6076\u52a3\u2014\u2014\u81f3\u5c11\u5bf9\u4e8e\u4e00\u4e2a\u5927\u5965\u672f\u5e08\u6765\u8bf4\u662f\u5b8c\u5168\u53ef\u4ee5\u88ab\u539f\u8c05\u7684\uff0c\u603b\u6bd4\u62c9\u6c83\u514b\u90a3\u79cd\u534a\u5929\u4e0d\u8bf4\u4e00\u53e5\u8bdd\u7684\u6b7b\u4eba\u9aa8\u5934\u8981\u597d\u591a\u4e86\u3002</p><p>\u5982\u679c\u4ed6\u4e0d\u662f\u559c\u6b22\u62ff\u5b66\u751f\u505a\u8bd5\u9a8c\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u771f\u4f1a\u662f\u4e2a\u597d\u8001\u5e08\uff0c\u743c\u6069\u5982\u6b64\u60f3\u7740\u3002</p><p>\u5965\u6c83\u7684\u85cf\u5b9d\u5ba4\u9020\u578b\u72ec\u7279\uff0c\u4e00\u73af\u5957\u7740\u4e00\u73af\uff0c\u5982\u679c\u8ba9\u743c\u6069\u6765\u5f62\u5bb9\u7684\u8bdd\uff0c\u5c31\u50cf\u662f\u868a\u9999\u5708\u3002\u636e\u5965\u6c83\u8bf4\uff0c\u4ed6\u7684\u5b9d\u7269\u6839\u636e\u91cd\u8981\u7a0b\u5ea6\u6392\u5217\uff0c\u8d8a\u5f80\u4e2d\u5fc3\u7684\u5c31\u8d8a\u73cd\u8d35\uff0c\u4ed6\u6700\u5f97\u610f\u7684\u51e0\u4ef6\u6536\u85cf\uff0c\u5c31\u653e\u5728\u8fd9\u868a\u9999\u5708\u7248\u85cf\u5b9d\u5e93\u7684\u4e2d\u5fc3\u3002</p><p>\u743c\u6069\u662f\u7cbe\u7814\u53d8\u5316\u7684\u5deb\u5e08\uff0c\u53c8\u53ef\u4ee5\u79f0\u4e3a\u70bc\u91d1\u5e08\uff0c\u9b54\u6cd5\u9020\u7269\u5b66\u662f\u4ed6\u7684\u4e13\u4e1a\u9886\u57df\uff0c\u6240\u4ee5\u5bf9\u9b54\u6cd5\u7269\u54c1\u5012\u4e5f\u662f\u9887\u4e3a\u719f\u6089\u3002\u653e\u773c\u671b\u53bb\uff0c\u6446\u653e\u5728\u6700\u5916\u56f4\u7684\uff0c\u65e0\u975e\u4e5f\u5c31\u662f\u70bd\u708e\u5251\u3001\u98de\u7ffc\u76fe\u3001\u8679\u5149\u6cd5\u888d\u4e4b\u7c7b\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u5927\u591a\u90fd\u5728\u56fe\u9274\u4e0a\u89c1\u8bc6\u8fc7\uff0c\u867d\u7136\u786e\u5b9e\u5f88\u73cd\u8d35\uff0c\u4f46\u8fd8\u4e0d\u81f3\u4e8e\u5230\u8ba9\u4eba\u9707\u60ca\u7684\u7a0b\u5ea6\u3002\u968f\u7740\u9010\u6e10\u6df1\u5165\uff0c\u4fbf\u5f00\u59cb\u51fa\u73b0\u9ed1\u6697\u5934\u9aa8\u3001\u771f\u77e5\u5b9d\u77f3\u3001\u661f\u5149\u6cd5\u888d\u3001\u541e\u566c\u76fe\u8fd9\u79cd\u53d8\u6001\u4e1c\u897f\u4e86\uff1b\u518d\u5230\u540e\u6765\uff0c\u751a\u81f3\u8fde\u96f7\u9706\u6218\u9524\u3001\u7cbe\u7075\u738b\u795e\u5f13\u3001\u5149\u8f89\u5934\u76d4\u8fd9\u79cd\u4f20\u5947\u7ea7\u7684\u7269\u54c1\u90fd\u5192\u4e86\u51fa\u6765\u3002</p><p>\u201c\u8fd9\u4e2a\u2026\u2026\u201d\u743c\u6069\u62c8\u8d77\u4e00\u679a\u767d\u91d1\u6212\u6307\uff0c\u4ece\u9020\u578b\u4e0a\u770b\u9887\u4e3a\u7d20\u6734\uff0c\u4f3c\u4e4e\u6ca1\u4ec0\u4e48\u7279\u522b\u7684\uff0c\u4f46\u80fd\u548c\u5468\u56f4\u8fd9\u4e9b\u8fd1\u4f3c\u795e\u5668\u7ea7\u522b\u7684\u4e1c\u897f\u6446\u653e\u5728\u4e00\u8d77\uff0c\u4e0d\u7528\u8111\u5b50\u60f3\u4e5f\u77e5\u9053\u5fc5\u975e\u5bfb\u5e38\uff0c\u201c\u4e0d\u4f1a\u662f\u6cd5\u672f\u9006\u8f6c\u6212\u6307\u5427\uff1f\u201d\u4ed6\u5728\u8bb0\u5fc6\u4e2d\u52aa\u529b\u641c\u5bfb\u7740\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u8bf4\u51fa\u731c\u6d4b\u3002</p><p>\u201c\u5bf9\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u5c31\u662f\u6cd5\u672f\u9006\u8f6c\u6212\u6307\u3002\u201d</p><p>\u743c\u6069\u54bd\u4e86\u53e3\u53e3\u6c34\uff0c\u604b\u604b\u4e0d\u820d\u5730\u628a\u6212\u6307\u653e\u56de\u539f\u5904\uff0c\u8ddf\u7740\u5965\u6c83\u7ee7\u7eed\u524d\u8fdb\u3002\u53c8\u8f6c\u8fc7\u4e00\u5708\uff0c\u743c\u6069\u770b\u5230\u4e86\u5927\u91cf\u7684\u6302\u6bef\u548c\u96d5\u5851\uff0c\u4f5c\u5de5\u7cbe\u81f4\u534e\u7f8e\uff0c\u4e0a\u9762\u63cf\u7ed8\u6709\u5927\u91cf\u7684\u56fe\u6848\uff0c\u5168\u662f\u793e\u4f1a\u98ce\u60c5\u56fe\uff0c\u6709\u7684\u662f\u753b\u5bb6\u5c45\u751f\u6d3b\uff0c\u6709\u7684\u662f\u753b\u5e7f\u573a\u805a\u4f1a\uff0c\u6709\u7684\u662f\u753b\u96c6\u5e02\u8d38\u6613\uff0c\u751a\u81f3\u6709\u4e00\u526f\u753b\u7684\u662f\u4e00\u5bb6\u56db\u53e3\u56f4\u684c\u5403\u996d\u7684\u60c5\u5f62\uff0c\u4eba\u7269\u9020\u578b\u6829\u6829\u5982\u751f\uff0c\u5c31\u7b97\u743c\u6069\u8fd9\u79cd\u5b8c\u5168\u4e0d\u61c2\u827a\u672f\u6b23\u8d4f\u7684\u5bb6\u4f19\u4e5f\u77e5\u9053\u8fd9\u4e9b\u662f\u6781\u4e0a\u7b49\u7684\u5de5\u827a\u54c1\u3002</p><p>\u7136\u800c\uff0c\u4ed6\u4ece\u8fd9\u4e9b\u6302\u6bef\u548c\u96d5\u5851\u4e0a\uff0c\u611f\u89c9\u4e0d\u5230\u534a\u70b9\u9b54\u6cd5\u7684\u529b\u91cf\uff0c\u8fd9\u4e9b\u4e1c\u897f\u5e94\u8be5\u90fd\u662f\u666e\u901a\u7269\u54c1\uff0c\u5965\u6c83\u8fd9\u4e48\u90d1\u91cd\u5730\u6446\u5728\u8fd9\u91cc\u9762\u505a\u4ec0\u4e48\uff1f</p><p>\u201c\u8fd9\u4e9b\uff0c\u201d\u5965\u6c83\u6307\u7740\u6302\u6bef\u548c\u96d5\u5851\u4e0a\u7684\u753b\uff0c\u201c\u5c31\u662f\u6211\u5e74\u8f7b\u7684\u65f6\u5019\uff0c\u8010\u745f\u7684\u6a21\u6837\uff0c\u201d\u4ed6\u7684\u58f0\u97f3\u4f4e\u6c89\u4e0b\u6765\uff0c\u8bed\u6c14\u4e2d\u7f55\u6709\u5730\u5e26\u4e0a\u4e86\u51e0\u5206\u9eef\u7136\uff0c\u201c\u6211\u51fa\u751f\u7684\u65f6\u5019\uff0c\u5e1d\u56fd\u6b63\u5904\u4e8e\u2018\u9ec4\u91d1\u5e74\u4ee3\u2019\u54ea\uff0c\u6700\u9f0e\u76db\u7684\u5dc5\u5cf0\u65f6\u671f\uff0c\u8f89\u714c\u707f\u70c2\uff0c\u4e0d\u53ef\u4e00\u4e16\u2026\u2026\u53ef\u60dc\u554a\u3002\u201d</p><p>\u4eff\u4f5b\u4e00\u4e2a\u8001\u4eba\u822c\uff0c\u5965\u6c83\u5f00\u59cb\u6000\u5ff5\u6614\u65e5\u8010\u745f\u745e\u5c14\u7684\u8f89\u714c\uff0c\u6000\u5ff5\u4ed6\u6614\u65e5\u7684\u5c81\u6708\uff0c\u7d6e\u7d6e\u53e8\u53e8\u53cd\u53cd\u590d\u590d\uff0c\u542c\u5f97\u743c\u6069\u5934\u8111\u53d1\u75bc\u3002\u4ed6\u867d\u7136\u4ece\u8840\u7edf\u4e0a\u8bf4\u662f\u7eaf\u6b63\u7684\u8010\u745f\u540e\u88d4\uff0c\u4f46\u7075\u9b42\u4e0a\u5374\u5176\u5b9e\u662f\u4e2a\u7a7f\u8d8a\u8005\uff0c\u5bf9\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u6ca1\u6709\u90a3\u4e48\u5f3a\u7684\u8ba4\u540c\u611f\u548c\u5f52\u5c5e\u611f\uff0c\u542c\u5965\u6c83\u8bf4\u8fd9\u4e9b\u4e0d\u514d\u5174\u8da3\u7f3a\u7f3a\u3002\u7136\u800c\u5728\u8001\u5deb\u5996\u558b\u558b\u4e0d\u4f11\u7684\u7f57\u55e6\u4e2d\uff0c\u743c\u6069\u8fd8\u662f\u654f\u9510\u5730\u628a\u63e1\u5230\u4e86\u4e00\u4e9b\u4fe1\u606f\uff1a\u5965\u6c83\u53e3\u4e2d\u6240\u8bf4\u7684\u201c\u5927\u707e\u53d8\u201d\uff0c\u5c31\u662f\u6307\u8010\u745f\u9668\u706d\u8fd9\u4ef6\u4e8b\uff0c\u800c\u4ed6\u5e94\u8be5\u5c31\u662f\u4ece\u6b64\u4e4b\u540e\u5c31\u8f6c\u5316\u6210\u4e86\u5deb\u5996\uff0c\u4ece\u5b64\u5bc2\u8352\u539f\u79fb\u5c45\u5230\u8fd9\u5893\u7a74\u91cc\u3002</p><p>\u8fd9\u4e48\u8bf4\uff0c\u8001\u5bb6\u4f19\u548c\u62c9\u6c83\u514b\u4e00\u6837\uff0c\u4e00\u5b85\u5c31\u662f\u4e00\u5343\u4e03\u767e\u591a\u5e74\u5462\uff0c\u5deb\u5996\u679c\u7136\u90fd\u662f\u4e86\u4e0d\u8d77\u7684\u5b58\u5728\u3002\u4e0d\u8fc7\u770b\u4ed6\u628a\u8fd9\u4e9b\u827a\u672f\u54c1\u73cd\u800c\u91cd\u4e4b\u5730\u6446\u653e\u5728\u8fd9\u91cc\uff0c\u550f\u5618\u4e0d\u5df2\u5730\u7f05\u6000\u8fc7\u53bb\uff0c\u743c\u6069\u7a81\u7136\u89c9\u5f97\u9762\u524d\u8fd9\u80a5\u50f5\u5c38\u6bd4\u62c9\u6c83\u514b\u90a3\u9aa8\u5934\u67b6\u5b50\u4f3c\u4e4e\u8981\u53ef\u7231\u4e9b\u3002</p><p>\u6700\u540e\uff0c\u5965\u6c83\u7ec8\u4e8e\u7ed3\u675f\u4e86\u4ed6\u7684\u957f\u7bc7\u5927\u8bba\uff0c\u5e26\u7740\u743c\u6069\u8fdb\u5165\u85cf\u5b9d\u5ba4\u7684\u6700\u4e2d\u5fc3\u3002</p><p>\u203b\u203b\u203b</p><p>\u94f6\u8272\u7684\u652f\u67b6\u4e0a\uff0c\u6258\u7740\u5341\u51e0\u4ef6\u5f62\u6001\u5404\u5f02\u7684\u7269\u54c1\uff0c\u6216\u8005\u6d41\u5149\u6ea2\u5f69\uff0c\u6216\u8005\u5149\u534e\u5185\u655b\uff0c\u743c\u6069\u6e05\u695a\u5730\u611f\u53d7\u5230\u6c79\u6d8c\u6f8e\u6e43\u7684\u529b\u91cf\u51db\u51db\u8feb\u6765\uff0c\u8fd9\u4e9b\u2026\u2026\u5e94\u8be5\u90fd\u79f0\u5f97\u4e0a\u662f\u201c\u795e\u5668\u201d\u4e86\u5427\u3002</p><p>\u795e\u5668\u5e76\u4e0d\u662f\u6307\u795e\u7947\u7684\u9020\u7269\uff08\u5f53\u7136\u795e\u7947\u7684\u9020\u7269\u5f88\u591a\u4e5f\u786e\u5b9e\u662f\u795e\u5668\uff0c\u4f8b\u5982\u9ece\u660e\u4e4b\u77f3\uff09\uff0c\u800c\u662f\u5bf9\u90a3\u4e9b\u529b\u91cf\u5f3a\u5927\u5230\u4e0d\u53ef\u601d\u8bae\u7a0b\u5ea6\u7684\u9b54\u6cd5\u7269\u54c1\u7684\u4e00\u79cd\u901a\u79f0\uff0c\u6570\u91cf\u6781\u5176\u7a00\u5c11\u3002\u5965\u6c83\u8fd9\u91cc\u4e00\u4e0b\u5b50\u5c31\u6446\u51fa\u4e03\u516b\u4ef6\u6765\uff0c\u786e\u5b9e\u662f\u975e\u6bd4\u5bfb\u5e38\u4e86\uff0c\u96be\u602a\u8fd9\u80a5\u50f5\u5c38\u6709\u5439\u725b\u7684\u81ea\u4fe1\u3002</p><p>\u5965\u6c83\u4e00\u4e00\u4e3a\u743c\u6069\u6307\u70b9\u7740\u3002</p><p>\u201c\u590f\u68b5\u72ec\u89d2\u517d\u51a0\u3002\u201d</p><p>\u8fd9\u662f\u4e00\u9876\u975e\u5e38\u7cbe\u81f4\u7684\u738b\u51a0\uff0c\u79d8\u94f6\u5236\u6210\uff0c\u7f00\u4ee5\u788e\u94bb\uff0c\u524d\u989d\u90e8\u4f4d\u9576\u5d4c\u7740\u4e00\u6839\u83b9\u6da6\u900f\u660e\u7684\u7b14\u76f4\u5c16\u89d2\uff0c\u659c\u659c\u633a\u51fa\uff0c\u4e00\u53ea\u94f6\u767d\u8272\u7684\u96cc\u6027\u72ec\u89d2\u517d\u5f71\u50cf\u5728\u5176\u4e2d\u9690\u73b0\uff0c\u5149\u660e\u800c\u5723\u6d01\u7684\u529b\u91cf\u9690\u9690\u900f\u51fa\u6765\uff0c\u743c\u6069\u4f38\u624b\u629a\u6478\u4e0a\u53bb\uff0c\u7136\u540e\u4fbf\u89c9\u5f97\u4e00\u9635\u6e29\u6696\u7684\u611f\u89c9\u6d41\u904d\u5168\u8eab\uff0c\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\u3002</p><p>\u201c\u590f\u68b5\uff1f\u201d\u743c\u6069\u601d\u7d22\u7740\u8fd9\u4e2a\u540d\u5b57\uff0c\u201c\u53e4\u7cbe\u7075\u738b\u56fd\uff1f\u201d</p><p>\u201c\u5bf9\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u8fd9\u662f\u590f\u68b5\u738b\u56fd\u7684\u4f20\u56fd\u81f3\u5b9d\uff0c\u53e6\u5916\u8fd8\u6709\u4e00\u9876\u590f\u68b5\u9f99\u6cea\u51a0\uff0c\u548c\u8fd9\u72ec\u89d2\u517d\u51a0\u662f\u4e00\u5bf9\u3002\u590f\u68b5\u7684\u5386\u4ee3\u7cbe\u7075\u738b\uff0c\u81ea\u5df1\u6234\u9f99\u6cea\u51a0\uff0c\u738b\u540e\u5219\u6234\u8fd9\u72ec\u89d2\u517d\u51a0\u3002\u201d</p><p>\u201c\u53ef\u60dc\u590f\u68b5\u6e83\u706d\uff0c\u8fd9\u4e24\u9876\u738b\u51a0\u90fd\u6d41\u843d\u6563\u5931\uff0c\u6211\u82b1\u4e86\u597d\u5927\u529b\u6c14\u624d\u627e\u5230\u8fd9\u72ec\u89d2\u517d\u51a0\uff0c\u53e6\u5916\u7684\u9f99\u6cea\u51a0\u4e0d\u77e5\u9053\u5728\u54ea\u91cc\uff0c\u201d\u5965\u6c83\u60cb\u60dc\u7740\uff0c\u4e00\u526f\u9887\u4e3a\u9057\u61be\u7684\u6837\u5b50\uff0c\u201c\u636e\u8bf4\u4e24\u9876\u738b\u51a0\u5728\u4e00\u8d77\uff0c\u4fbf\u66f4\u6709\u8bb8\u591a\u4e0d\u53ef\u601d\u8bae\u7684\u6548\u80fd\uff0c\u53ef\u60dc\u7cbe\u7075\u4eec\u4e0d\u8457\u53f2\u4e66\uff0c\u8d44\u6599\u4e0a\u5bf9\u6b64\u4e5f\u5c31\u8bed\u7109\u4e0d\u8be6\u3002\u6211\u60f3\u4eb2\u773c\u89c1\u8bc6\u89c1\u8bc6\uff0c\u5374\u53c8\u603b\u627e\u4e0d\u5230\u9f99\u6cea\u51a0\u3002\u201d</p><p>\u6309\u9053\u7406\u8bf4\uff0c\u5965\u6c83\u8fd9\u79cd\u5927\u5965\u672f\u5e08\uff0c\u8981\u627e\u67d0\u4ef6\u6709\u8be6\u7ec6\u8d44\u6599\u7684\u7269\u54c1\u662f\u5f88\u5bb9\u6613\u7684\uff0c\u5360\u535c\u5b9a\u4f4d\u5c31\u662f\uff1b\u8d44\u6599\u8d8a\u8be6\u5b9e\uff0c\u5b9a\u4f4d\u5c31\u8d8a\u51c6\u786e\u3002\u7136\u800c\u8fd9\u4ec5\u4ec5\u662f\u9488\u5bf9\u4e00\u822c\u7684\u4e1c\u897f\uff0c\u9f99\u6cea\u51a0\u8fd9\u79cd\u795e\u5668\uff0c\u672c\u8eab\u8574\u542b\u6709\u592a\u5f3a\u5927\u7684\u529b\u91cf\uff0c\u4f1a\u963b\u9694\u6389\u9884\u8a00\u63a2\u6d4b\u9b54\u6cd5\uff0c\u5360\u535c\u5b9a\u4f4d\u662f\u6839\u672c\u4e0d\u51c6\u7684\u3002</p><p>\u4e0d\u8fc7\uff0c\u5c31\u7b97\u627e\u5230\u90a3\u9876\u9f99\u6cea\u51a0\uff0c\u5c31\u7b97\u771f\u6709\u8bb8\u591a\u201c\u4e0d\u53ef\u601d\u8bae\u7684\u6548\u80fd\u201d\uff0c\u5bf9\u5965\u6c83\u5176\u5b9e\u4e5f\u4e0d\u8fc7\u662f\u955c\u82b1\u6c34\u6708\uff0c\u80fd\u770b\u4e0d\u80fd\u7528\u5427\u3002\u743c\u6069\u867d\u7136\u4e0d\u77e5\u9053\u8fd9\u72ec\u89d2\u517d\u51a0\u7684\u4f5c\u7528\u5982\u4f55\uff0c\u4f46\u4ed6\u80fd\u6e05\u695a\u611f\u89c9\u5230\u5176\u4e2d\u6ee1\u8574\u7740\u5149\u660e\u5723\u6d01\u7684\u529b\u91cf\uff0c\u5965\u6c83\u8fd9\u79cd\u4ea1\u7075\u5982\u679c\u6562\u4f7f\u7528\uff0c\u4e0d\u4f46\u53d1\u6325\u4e0d\u4e86\u4f5c\u7528\uff0c\u53ea\u6015\u8fd8\u8981\u53d7\u5230\u4f24\u5bb3\uff1b\u90a3\u9876\u9f99\u6cea\u51a0\u65e2\u7136\u548c\u72ec\u89d2\u517d\u51a0\u662f\u4e00\u5bf9\uff0c\u6599\u60f3\u4e5f\u5dee\u4e0d\u591a\u4e86\u3002</p><p>\u201c\u6765\uff0c\u743c\u6069\uff0c\u518d\u770b\u8fd9\u628a\u5251\uff0c\u201d\u5965\u6c83\u5f97\u610f\u6d0b\u6d0b\u5730\u6307\u70b9\u7740\uff0c\u201c\u731c\u731c\u5b83\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u5f88\u6709\u540d\u7684\u3002\u201d</p><p>\u8fd9\u662f\u4e00\u67c4\u6781\u5176\u534e\u4e3d\u7684\u53cc\u624b\u5de8\u5251\uff0c\u6574\u4e2a\u5251\u8eab\u8fde\u540c\u5251\u67c4\uff0c\u5168\u90fd\u662f\u7528\u4e00\u79cd\u4e0d\u77e5\u540d\u7684\u706b\u7ea2\u8272\u91d1\u5c5e\u5236\u6210\uff0c\u9690\u9690\u900f\u7740\u8840\u7ea2\u8272\u7684\u51b7\u5149\uff0c\u5728\u5251\u67c4\u672b\u7aef\uff0c\u9576\u5d4c\u7740\u4e00\u9897\u7ea2\u9506\u77f3\uff0c\u5728\u62a4\u624b\u90e8\u4f4d\uff0c\u7f00\u7740\u4e09\u9897\u5b8c\u7f8e\u65e0\u6687\u7684\u7eef\u7ea2\u4e4b\u6cea\u5b9d\u77f3\uff0c\u5448\u7130\u7ea2\u8272\uff0c\u95ea\u95ea\u53d1\u5149\u3002</p><p>\u7eef\u7ea2\u4e4b\u6cea\uff0c\u53c8\u79f0\u201c\u6218\u795e\u5766\u5e15\u65af\u7684\u54ed\u6ce3\u201d\uff0c\u4e43\u662f\u6218\u6597\u4e4b\u77f3\u3001\u52c7\u6c14\u4e4b\u77f3\u3001\u590d\u4ec7\u4e4b\u77f3\uff0c\u662f\u6781\u5176\u73cd\u8d35\u7684\u5b9d\u77f3\uff0c\u7528\u9b54\u6cd5\u9576\u5d4c\u878d\u5408\u5728\u6b66\u5668\u4e0a\uff0c\u80fd\u8ba9\u6301\u6709\u8005\u7206\u53d1\u51fa\u4e0d\u53ef\u601d\u8bae\u7684\u52c7\u6c14\u3001\u72c2\u70ed\u548c\u6218\u6597\u529b\u3002</p><p>\u743c\u6069\u51dd\u89c6\u7740\u90a3\u4e09\u9897\u7eef\u7ea2\u4e4b\u6cea\uff0c\u4f38\u624b\u63e1\u4f4f\u5251\u67c4\uff0c\u63d0\u4e86\u8d77\u6765\u3002\u5728\u6307\u5c16\u89e6\u5230\u5251\u67c4\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u4ed6\u7684\u8111\u6d77\u4e2d\u4eff\u4f5b\u77ac\u95f4\u95ea\u8fc7\u65e0\u6570\u6c99\u573a\u5f81\u6218\u7684\u60e8\u70c8\u573a\u9762\uff0c\u9ad8\u6602\u7684\u6b22\u547c\u3001\u6124\u6012\u7684\u6218\u543c\u3001\u51c4\u5389\u7684\u60b2\u9e23\u3001\u5200\u5251\u78b0\u649e\u7684\u94ff\u9535\u9707\u54cd\uff0c\u4eff\u4f5b\u8de8\u8d8a\u91cd\u91cd\u65f6\u7a7a\u7684\u963b\u9694\uff0c\u4e00\u9f50\u671d\u743c\u6069\u88ad\u6765\u3002\u4ed6\u5fc3\u795e\u4e00\u6447\uff0c\u604d\u7136\u82e5\u5931\uff0c\u968f\u5373\u7acb\u523b\u6e05\u9192\u8fc7\u6765\uff0c\u4f4e\u5934\u770b\u53bb\uff0c\u53d1\u73b0\u5251\u5df2\u7ecf\u88ab\u81ea\u5df1\u63e1\u5728\u624b\u4e2d\u3002</p><p>\u4e8e\u662f\u743c\u6069\u77e5\u9053\u4e86\u5b83\u7684\u540d\u5b57\u3002</p><p>\u201c\u795e\u5723\u590d\u4ec7\u3002\u201d</p><p>\u201c\u6ca1\u9519\uff0c\u4f60\u5f88\u6709\u773c\u5149\u561b\uff0c\u201d\u5965\u6c83\u5f88\u9ad8\u5174\uff0c\u4f3c\u4e4e\u4e3a\u81ea\u5df1\u7684\u6536\u85cf\u54c1\u88ab\u8d4f\u8bc6\u800c\u6109\u5feb\uff0c\u201c\u8fd9\u5c31\u662f\u90a3\u67c4\u4f20\u8bf4\u4e2d\u7684\u5723\u5251\uff1a\u795e\u5723\u590d\u4ec7\u3002\u201d</p><p>\u201c\u7136\u800c\u5b83\u5df2\u7ecf\u4e0d\u662f\u5723\u5251\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u5c06\u5251\u653e\u56de\u539f\u5904\uff0c\u201c\u5426\u5219\u7684\u8bdd\uff0c\u6211\u6839\u672c\u4e0d\u53ef\u80fd\u62ff\u8d77\u6765\u2026\u2026\u590d\u4ec7\uff0c\u539f\u672c\u5c31\u4e0d\u53ef\u80fd\u662f\u795e\u5723\u7684\u5427\u3002\u201d</p><p>\u201c\u590d\u4ec7\u66fe\u7ecf\u662f\u795e\u5723\u7684\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u795e\u5723\u7684\u4e49\u52a1\u3002\u201d</p><p>\u201c\u55ef\uff0c\u66fe\u7ecf\u662f\uff0c\u4f46\u73b0\u5728\u4e0d\u662f\u4e86\u3002\u6240\u4ee5\u8fd9\u67c4\u5251\u4e5f\u5c31\u4e0d\u518d\u662f\u5723\u5251\u4e86\u3002\u201d</p><p>\u201c\u603b\u8fd8\u662f\u4f20\u8bf4\u4e2d\u7684\u5723\u5251\u561b\u3002\u201d\u5965\u6c83\u8bf4\uff0c\u6307\u70b9\u4ed6\u770b\u4e0b\u4e00\u4ef6\u6536\u85cf\u54c1\u3002</p><p>\u8001\u5deb\u5996\u7684\u6536\u85cf\u786e\u5b9e\u5f88\u4e30\u5bcc\uff0c\u4ee4\u743c\u6069\u5927\u5f00\u773c\u754c\uff0c\u4e0d\u8fc7\u8f6c\u4e86\u4e00\u5708\uff0c\u4ed6\u53d1\u73b0\u4e86\u4e24\u4e2a\u95ee\u9898\u3002</p><p>\u7b2c\u4e00\u4e2a\u95ee\u9898\uff0c\u662f\u6709\u4e00\u5ea7\u94f6\u67b6\u4e0a\u662f\u7a7a\u7684\u3002</p><p>\u201c\u8001\u5e08\uff0c\u8fd9\u91cc\u2026\u2026\u201d</p><p>\u201c\u54e6\uff0c\u8fd9\u91cc\u672c\u6765\u662f\u653e\u89d2\u4e4b\u51a0\u7684\uff0c\u53ef\u60dc\u5f04\u4e22\u4e86\u3002\u201d</p><p>\u201c\u89d2\u4e4b\u51a0\uff1f\u201d\u743c\u6069\u6ca1\u542c\u8fc7\u8fd9\u540d\u5b57\u3002</p><p>\u201c\u5c31\u662f\u7c73\u5c14\u5bc7\u94f8\u9020\u7684\u90a3\u4e2a\u738b\u51a0\u561b\u3002\u201d</p><p>\u201c\u7c73\u5c14\u5bc7\uff1f\u60a8\u662f\u8bf4\u524d\u4efb\u2014\u2014\u4e0d\uff0c\u662f\u524d\u524d\u4efb\u6b7b\u4ea1\u4e4b\u795e\uff1f\u201d</p><p>\u8010\u745f\u5e1d\u56fd\u65f6\u4ee3\uff0c\u6b7b\u4ea1\u4e4b\u795e\u662f\u8036\u683c\uff1b\u800c\u5728\u8010\u745f\u9668\u706d\u540e\uff0c\u8036\u683c\u7a81\u7136\u653e\u5f03\u4e86\u6b7b\u4ea1\u795e\u4f4d\uff0c\u5c06\u7edd\u5927\u90e8\u5206\u795e\u529b\u8d60\u4e88\u4e09\u4e2a\u51e1\u4eba\uff1a\u73ed\u6069\u3001\u7c73\u5c14\u5bc7\u548c\u5df4\u5c14\uff0c\u5176\u4e2d\u7c73\u5c14\u5bc7\u7ee7\u4efb\u6b7b\u4ea1\u4e4b\u795e\u3002\u8fd9\u4e09\u4eba\u90fd\u6210\u4e3a\u795e\u7947\uff0c\u6b7b\u4ea1\u4e4b\u795e\u7c73\u5c14\u5bc7\u548c\u6050\u60e7\u4e4b\u795e\u73ed\u6069\u3001\u6740\u622e\u4e4b\u795e\u5df4\u5c14\uff0c\u88ab\u5e76\u79f0\u4e3a\u201c\u6b7b\u4ea1\u4e09\u795e\u201d\u3002\u5341\u4e94\u5e74\u524d\u7684\u201c\u52a8\u8361\u4e4b\u5e74\u201d\uff0c\u6b7b\u4ea1\u4e09\u795e\u5168\u6570\u88ab\u6467\u6bc1\uff0c\u7531\u51e1\u4eba\u5e0c\u745e\u514b\u7ee7\u627f\u6b7b\u4ea1\u795e\u4f4d\u3002\u4f46\u5728\u56db\u5e74\u524d\uff0c\u51e1\u4eba\u514b\u5170\u6c83\u81ea\u5e0c\u745e\u514b\u624b\u4e2d\u593a\u53d6\u4e86\u6b7b\u4ea1\u795e\u4f4d\uff0c\u6210\u4e3a\u6700\u65b0\u4e00\u4efb\u6b7b\u795e\u3002</p><p>\u6240\u4ee5\u743c\u6069\u8bf4\uff0c\u7c73\u5c14\u5bc7\u5df2\u7ecf\u662f\u524d\u524d\u4efb\u6b7b\u4ea1\u4e4b\u795e\u4e86\u3002</p><p>\u201c\u55ef\uff0c\u5c31\u662f\u90a3\u5bb6\u4f19\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u201c\u4ed6\u8fd8\u6ca1\u6b7b\u7684\u65f6\u5019\uff0c\u94f8\u9020\u4e86\u89d2\u4e4b\u51a0\uff0c\u540e\u6765\u6d41\u8f6c\u5230\u6211\u624b\u91cc\u3002\u53ef\u60dc\uff0c\u56e0\u4e3a\u4e00\u6b21\u610f\u5916\uff0c\u6211\u628a\u5b83\u7ed9\u5f04\u4e22\u4e86\u3002\u201d</p><p>\u743c\u6069\u5f88\u597d\u5947\uff0c\u5230\u5e95\u662f\u4ec0\u4e48\u610f\u5916\uff0c\u8ba9\u8fd9\u4f4d\u5927\u5965\u672f\u5e08\u635f\u5931\u4e86\u8fd9\u6837\u4e00\u4ef6\u73cd\u8d35\u7684\u795e\u5668\u3002</p><p>\u201c\u54e6\uff0c\u5176\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\u4e86\uff0c\u201d\u5965\u6c83\u6325\u7740\u80a5\u80d6\u7684\u624b\u638c\uff0c\u201c\u5c31\u662f\u6709\u4e00\u6b21\uff0c\u6211\u7528\u6c34\u6676\u7403\u89c2\u5bdf\u5916\u9762\uff0c\u5076\u7136\u770b\u89c1\u4e86\u83b1\u62c9\xb7\u94f6\u624b\u2026\u2026\u4f60\u77e5\u9053\u5979\u662f\u8c01\u5427\uff1f\u201d</p><p>\u77e5\u9053\uff0c\u743c\u6069\u5728\u70db\u5821\u91cc\u7ffb\u9605\u8fc7\u7269\u8d28\u754c\u8457\u540d\u5deb\u5e08\u7684\u8d44\u6599\uff0c\u5370\u8c61\u91cc\u6709\u8fd9\u4f4d\u5973\u58eb\u2014\u2014\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\uff0c\u4e03\u59d0\u59b9\u4e4b\u4e00\uff0c\u8457\u540d\u7684\u5927\u5deb\u5e08\uff0c\u6709\u201c\u5317\u5730\u7684\u5deb\u672f\u5973\u7687\u201d\u4e4b\u79f0\uff0c\u73b0\u5728\u542c\u8bf4\u662f\u6df1\u6c34\u57ce\u4e3b\u51ef\u5c14\u672c\u5927\u5deb\u5e08\u7684\u59bb\u5b50\u3002</p><p>\u201c\u60a8\u548c\u5979\u6253\u8d77\u6765\u4e86\uff1f\u201d\u743c\u6069\u95ee\uff0c\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\u7d20\u6765\u5c5e\u4e8e\u5584\u826f\u4eba\u58eb\uff0c\u649e\u4e0a\u5965\u6c83\u8fd9\u8001\u5deb\u5996\uff0c\u6253\u8d77\u6765\u4e5f\u662f\u7406\u6240\u5f53\u7136\u3002\u53ea\u662f\u83b1\u62c9\u7eb5\u7136\u662f\u8457\u540d\u7684\u5927\u5deb\u5e08\uff0c\u603b\u4e5f\u4e0d\u53ef\u80fd\u6bd4\u5f97\u4e0a\u5965\u6c83\u8fd9\u79cd\u8001\u5deb\u5996\uff0c\u600e\u4e48\u4f1a\u628a\u89d2\u4e4b\u51a0\u90fd\u7ed9\u5f04\u4e22\u4e86\u3002</p><p>\u201c\u6253\u8d77\u6765\uff1f\u5f53\u7136\u6ca1\uff0c\u201d\u5965\u6c83\u77aa\u773c\uff0c\u201c\u6211\u5bf9\u5979\u4e00\u89c1\u949f\u60c5\uff0c\u4e00\u5fc3\u60f3\u5a36\u5979\u505a\u59bb\u5b50\uff0c\u600e\u4e48\u4f1a\u548c\u5979\u6253\u67b6\u3002\u201d</p><p>\u4ec0\u2026\u2026\u4ec0\u4e48\uff01</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u8fd9\u6b21\u88ab\u9707\u60ca\u5f97\u51e0\u4e4e\u8db4\u5012\uff0c\u201c\u4e00\u89c1\u949f\u60c5\u201d\uff1f\u201c\u5a36\u5979\u505a\u59bb\u5b50\u201d\uff1f\u8bf8\u795e\u5728\u4e0a\u554a\uff0c\u8fd9\u8001\u5deb\u5996\u5230\u5e95\u8111\u5b50\u8fd8\u6b63\u5e38\u4e0d\u6b63\u5e38\uff0c\u4ed6\u5230\u5e95\u77e5\u4e0d\u77e5\u9053\u81ea\u5df1\u5df2\u7ecf\u662f\u4e2a\u6b7b\u4eba\u4e86\u2026\u2026</p><p>\u5982\u679c\u8bf4\u4ed6\u662f\u4e2a\u5438\u8840\u9b3c\uff0c\u90a3\u743c\u6069\u5012\u8fd8\u4e0d\u4f1a\u8fd9\u4e48\u60ca\u8bb6\uff0c\u5438\u8840\u9b3c\u662f\u4ea1\u7075\u4e2d\u6700\u8fd1\u4f3c\u6d3b\u4eba\u7684\u5b58\u5728\u3002\u7136\u800c\u8fd9\u8001\u5bb6\u4f19\uff0c\u5206\u660e\u662f\u4e2a\u5deb\u5996\u554a\u3002</p><p>\u201c\u90a3\u2026\u2026\u90a3\u540e\u6765\u5462\uff1f\u201d\u743c\u6069\u6218\u6218\u5162\u5162\u5730\u95ee\u3002</p><p>\u201c\u5f53\u65f6\u5979\u6b63\u5728\u4e00\u652f\u5192\u9669\u961f\u4f0d\u91cc\uff0c\u201d\u5965\u6c83\u56de\u5fc6\u7740\uff0c\u201c\u4e5d\u4eba\u5c0f\u961f\uff0c\u5b9e\u529b\u90fd\u8fd8\u4e0d\u9519\u3002\u6211\u628a\u89d2\u4e4b\u51a0\u653e\u5728\u4ed6\u4eec\u7684\u5fc5\u7ecf\u4e4b\u8def\u4e0a\uff0c\u6545\u610f\u8ba9\u4ed6\u4eec\u53d1\u73b0\uff0c\u5f15\u8bf1\u83b1\u62c9\u6234\u4e0a\u4e86\u89d2\u4e4b\u51a0\u3002\u201d</p><p>\u201c\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u4e0d\u89e3\u3002</p><p>\u201c\u54e6\uff0c\u6211\u662f\u5927\u5965\u672f\u5e08\uff0c\u5979\u4f5c\u4e3a\u6211\u7684\u738b\u540e\uff0c\u81ea\u7136\u9700\u8981\u6709\u738b\u51a0\u561b\u3002\u201d</p><p>\u5728\u8010\u745f\u65f6\u4ee3\uff0c\u5927\u5965\u672f\u5e08\u6709\u8d44\u683c\u5efa\u7acb\u81ea\u5df1\u7684\u6d6e\u7a7a\u57ce\uff0c\u6210\u4e3a\u4e00\u57ce\u4e4b\u4e3b\uff0c\u5730\u4f4d\u76f8\u5f53\u4e8e\u56fd\u738b\uff0c\u4f8b\u5982\u9634\u9b42\u57ce\u4e3b\u4fbf\u662f\uff0c\u83b1\u62c9\u5982\u679c\u771f\u6210\u4e86\u5965\u6c83\u7684\u59bb\u5b50\uff0c\u90a3\u786e\u5b9e\u53ef\u4ee5\u79f0\u4e3a\u201c\u738b\u540e\u201d\u4e86\u3002</p><p>\u7136\u800c\u8fd9\u4e0d\u662f\u4e3b\u8981\u539f\u56e0\u5427\u3002</p><p>\u201c\u5514\uff0c\u8fd9\u4e5f\u662f\u79cd\u5fc5\u8981\u7684\u4fdd\u969c\u63aa\u65bd\u561b\uff0c\u201d\u5965\u6c83\u6478\u6478\u81ea\u5df1\u7684\u8111\u888b\uff0c\u201c\u4f60\u77e5\u9053\uff0c\u795e\u79d8\u5973\u58eb\u7684\u90a3\u4e9b\u9009\u6c11\u2026\u2026\u600e\u4e48\u8bf4\u5462\uff0c\u548c\u6211\u4eec\u4e0d\u662f\u4e00\u8def\u4eba\u3002\u4e3a\u4e86\u4fdd\u8bc1\u6211\u4eec\u672a\u6765\u7684\u5a5a\u59fb\u5e78\u798f\uff0c\u6240\u4ee5\u7528\u70b9\u624b\u6bb5\u8fd8\u662f\u5fc5\u8981\u7684\u3002\u201d</p><p>\u89d2\u4e4b\u51a0\u662f\u90aa\u795e\u7c73\u5c14\u5bc7\u6240\u9020\uff0c\u51e1\u4eba\u53ea\u8981\u6234\u4e0a\uff0c\u5fc3\u667a\u4fbf\u4f1a\u6536\u5230\u4fb5\u8680\uff0c\u65e0\u8bba\u539f\u672c\u518d\u5584\u826f\u7684\u4eba\uff0c\u4e5f\u4f1a\u5815\u5165\u9ed1\u6697\uff0c\u800c\u4e14\u4f1a\u9010\u6e10\u88ab\u53d8\u6210\u4ea1\u7075\u3002\u8fd9\u5c31\u662f\u5965\u6c83\u7684\u6253\u7b97\uff0c\u8ba9\u83b1\u62c9\u6234\u4e0a\u89d2\u4e4b\u51a0\uff0c\u90a3\u4e48\u4ed6\u5c31\u66f4\u6709\u628a\u63e1\u80fd\u638c\u63e1\u8fd9\u4f4d\u201c\u5317\u5730\u7684\u5deb\u672f\u5973\u7687\u201d\u4e86\u3002</p><p>\u201c\u53ef\u60dc\u554a\uff0c\u201d\u5965\u6c83\u518d\u6b21\u611f\u53f9\u8d77\u6765\uff0c\u201c\u6211\u7b97\u6765\u7b97\u53bb\uff0c\u81ea\u8ba4\u4e3a\u8ba1\u5212\u65e0\u61c8\u53ef\u51fb\uff0c\u5c31\u662f\u6ca1\u9884\u6599\u5230\u4e00\u4ef6\u4e8b\uff0c\u7ed3\u679c\u529f\u8d25\u5782\u6210\uff0c\u53cd\u800c\u628a\u89d2\u4e4b\u51a0\u90fd\u4e22\u4e86\u3002\u201d</p><p>\u5965\u6c83\u7684\u8ba1\u5212\u8fdb\u884c\u5f97\u5f88\u987a\u5229\uff0c\u83b1\u62c9\u786e\u5b9e\u6234\u4e0a\u4e86\u89d2\u4e4b\u51a0\u2014\u2014\u7136\u540e\uff0c\u9b54\u6cd5\u5973\u795e\u8d4b\u4e88\u5979\u7684\u795e\u529b\u201c\u94f6\u706b\u201d\u548c\u89d2\u4e4b\u51a0\u7684\u9ed1\u6697\u529b\u91cf\u53d1\u751f\u4e86\u731b\u70c8\u51b2\u7a81\uff0c\u7ed3\u679c\u5bfc\u81f4\u83b1\u62c9\u7cbe\u795e\u9519\u4e71\uff0c\u75af\u72c2\u5730\u653b\u51fb\u81ea\u5df1\u7684\u540c\u4f34\uff0c\u4e8b\u6001\u5b8c\u5168\u5931\u53bb\u4e86\u63a7\u5236\u3002\u4e00\u756a\u6df7\u4e71\u4e4b\u540e\uff0c\u6700\u7ec8\u53e6\u5916\u4e00\u4f4d\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\uff0c\u6df1\u6c34\u57ce\u4e3b\u51ef\u5c14\u672c\u5927\u5deb\u5e08\u524d\u6765\u76f8\u52a9\uff0c\u7528\u4ed6\u8457\u540d\u7684\u201c\u9ed1\u6756\u201d\u51fb\u788e\u4e86\u89d2\u4e4b\u51a0\uff0c\u6551\u51fa\u83b1\u62c9\u3002\u8fd9\u82f1\u96c4\u6551\u7f8e\u7684\u4e3e\u52a8\u83b7\u5f97\u4e86\u7f8e\u4eba\u82b3\u5fc3\uff0c\u83b1\u62c9\u5f88\u5feb\u4e0e\u51ef\u5c14\u672c\u6210\u5a5a\uff0c\u6210\u4e3a\u6df1\u6c34\u57ce\u7b2c\u4e00\u592b\u4eba\u3002\u800c\u53ef\u601c\u7684\u5965\u6c83\u5219\u662f\u529f\u4e8f\u4e00\u7bd1\uff0c\u6807\u51c6\u7684\u8d54\u4e86\u592b\u4eba\u53c8\u6298\u5175\uff0c\u59bb\u5b50\u6ca1\u5a36\u5230\uff0c\u53cd\u800c\u635f\u5931\u4e86\u4e00\u4ef6\u795e\u5668\u3002</p><p>\u201c\u771f\u53ef\u60dc\u554a\uff0c\u201d\u5965\u6c83\u9057\u61be\u7740\uff0c\u8fd9\u597d\u50cf\u662f\u4ed6\u7684\u53e3\u5934\u7985\uff0c\u603b\u662f\u91cd\u590d\uff0c\u201c\u4e0d\u8fc7\u4e5f\u65e0\u6240\u8c13\u4e86\uff0c\u53cd\u6b63\u73b0\u5728\u6211\u4e5f\u4e0d\u60f3\u5a36\u5979\u4e86\uff0c\u7a81\u7136\u6ca1\u5174\u81f4\u4e86\u3002\u5012\u662f\u89d2\u4e4b\u51a0\uff0c\u6709\u65f6\u5019\u5f97\u518d\u627e\u56de\u6765\u3002\u201d</p><p>\u201c\u5b83\u4e0d\u662f\u88ab\u51fb\u788e\u4e86\u4e48\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u51ef\u5c14\u672c\u5f53\u65f6\u662f\u628a\u5b83\u6253\u788e\u4e86\uff0c\u628a\u788e\u7247\u5e26\u56de\u4ed6\u7684\u9ed1\u6756\u5854\u3002\u4f46\u5341\u4e94\u5e74\u524d\uff0c\u7c73\u5c14\u5bc7\u88ab\u6467\u6bc1\u65f6\uff0c\u6b8b\u9b42\u4e0d\u706d\uff0c\u53c8\u5c06\u788e\u7247\u91cd\u94f8\uff0c\u8eb2\u5728\u91cc\u9762\uff0c\u4f20\u9001\u51fa\u4e86\u9ed1\u6756\u5854\u3002\u73b0\u5728\u4e5f\u4e0d\u77e5\u9053\u6d41\u843d\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u201d</p><p>\u201c\u8001\u5e08\uff0c\u4f60\u662f\u8bf4\u2026\u2026\u7c73\u5c14\u5bc7\u6ca1\u6b7b\uff1f\u201d</p><p>\u201c\u6ca1\u6b7b\u5e72\u51c0\uff0c\u8fd8\u5728\u82df\u5ef6\u6b8b\u5598\uff0c\u201d\u5965\u6c83\u5e72\u8106\u5229\u843d\u5730\u56de\u7b54\uff0c\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8fd9\u53ef\u662f\u7edd\u5bc6\u4fe1\u606f\uff0c\u4e16\u754c\u4e0a\u6ca1\u51e0\u4e2a\u4eba\u77e5\u9053\uff0c\u4f60\u53ef\u5343\u4e07\u522b\u4e71\u8bf4\u51fa\u53bb\uff0c\u4f1a\u60f9\u9ebb\u70e6\u7684\u3002\u201d</p><p>\u201c\u5443\uff0c\u662f\uff0c\u591a\u8c22\u8001\u5e08\u63d0\u9192\uff0c\u201d\u743c\u6069\u8d76\u5fd9\u8bf4\uff0c\u63a5\u7740\u8f6c\u79fb\u8bdd\u9898\uff0c\u201c\u8001\u5e08\uff0c\u6211\u8fd8\u6709\u4e2a\u95ee\u9898\u8bf7\u6559\uff0c\u201d\u4ed6\u6307\u7740\u623f\u95f4\u6700\u5185\u4fa7\u89d2\u843d\u7684\u4e00\u4e2a\u94f6\u67b6\uff0c\u4e0a\u9762\u6446\u653e\u7740\u51e0\u9875\u4eff\u4f5b\u8584\u91d1\u5236\u6210\u7684\u7eb8\u5f20\uff0c\u5176\u4e2d\u6709\u4e24\u9875\u4f3c\u4e4e\u8fd8\u662f\u6b8b\u7f3a\u7684\uff0c\u201c\u60a8\u521a\u624d\u6f0f\u4e86\u4ecb\u7ecd\u5b83\u3002\u201d</p><p>\u5965\u6c83\u521a\u624d\u5f97\u610f\u5730\u5411\u743c\u6069\u4ecb\u7ecd\u4e86\u8fd9\u85cf\u5b9d\u5ba4\u91cc\u6240\u6709\u7684\u795e\u5668\uff0c\u8bb2\u8ff0\u81ea\u5df1\u662f\u591a\u4e48\u8f9b\u52e4\u5730\u5c06\u5b83\u4eec\u4ece\u56db\u9762\u516b\u65b9\u641c\u522e\u6765\uff0c\u8bb2\u8ff0\u5b83\u4eec\u662f\u591a\u4e48\u7684\u4f18\u79c0\u591a\u4e48\u7684\u4e86\u4e0d\u8d77\u2014\u2014\u7136\u800c\u4ed6\u5374\u6ca1\u63d0\u90a3\u51e0\u9875\u8584\u91d1\u7eb8\u5f20\u662f\u4ec0\u4e48\uff0c\u4eff\u4f5b\u6ca1\u770b\u89c1\u4f3c\u7684\u3002</p><p>\u743c\u6069\u5f88\u597d\u5947\uff0c\u4ed6\u60f3\u4e0d\u51fa\u8fd9\u51e0\u9875\u7eb8\u5230\u5e95\u662f\u4ec0\u4e48\uff0c\u80fd\u548c\u8fd9\u4f17\u591a\u795e\u5668\u6446\u653e\u5728\u4e00\u8d77\u3002</p><p>\u5965\u6c83\u8f6c\u8fc7\u8138\uff0c\u76ef\u7740\u4ed6\uff0c\u6ca1\u8bf4\u8bdd\u3002</p><p>\u743c\u6069\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u4e0d\u77e5\u9053\u81ea\u5df1\u662f\u4e0d\u662f\u65e0\u610f\u95f4\u8bf4\u9519\u4e86\u4ec0\u4e48\u8bdd\uff0c\u5f97\u7f6a\u4e86\u8fd9\u80d6\u5deb\u5996\uff0c\u7136\u800c\u8fc7\u4e86\u4f1a\uff0c\u5965\u6c83\u7a81\u7136\u5475\u5475\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u201c\u4e5f\u597d\uff0c\u201d\u8001\u5bb6\u4f19\u7684\u8bed\u6c14\u91cc\u4f3c\u4e4e\u5e26\u7740\u4e9b\u6349\u5f04\u4eba\u7684\u610f\u5473\uff0c\u201c\u65e2\u7136\u4f60\u63d0\u8d77\u6765\uff0c\u90a3\u5c31\u6b63\u597d\u8ba9\u4f60\u89c1\u8bc6\u89c1\u8bc6\uff0c\u8fd9\uff0c\u53ef\u662f\u6211\u8fd9\u91cc\u6700\u73cd\u8d35\u7684\u4e1c\u897f\u4e86\u5462\u3002\u201d</p><p>\u4ed6\u4f38\u624b\uff0c\u4ece\u94f6\u67b6\u4e0a\u53d6\u8fc7\u4e00\u9875\u7eb8\u6765\uff0c\u9012\u7ed9\u743c\u6069\uff0c\u201c\u770b\u770b\u4e0a\u9762\u5199\u7740\u4ec0\u4e48\u3002\u201d</p><p>\u743c\u6069\u63a5\u8fc7\uff0c\u4f4e\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u8584\u91d1\u7eb8\u5f20\u4e0a\uff0c\u4e00\u6392\u6392\u94f6\u8272\u7684\u6587\u5b57\u5982\u6d41\u6c34\u822c\u5728\u4e0a\u9762\u7f13\u7f13\u6d41\u52a8\u7740\u3002\u4ed6\u51dd\u795e\u53bb\u8fa8\u8bc6\u90a3\u4e9b\u6587\u5b57\uff0c\u521a\u770b\u4e86\u4e24\u53e5\uff0c\u731b\u7136\u89c9\u5f97\u8111\u4e2d\u4e00\u9635\u5267\u75db\uff0c\u773c\u524d\u4eff\u4f5b\u5929\u65cb\u5730\u8f6c\u4e00\u822c\uff0c\u4e0b\u610f\u8bc6\u5730\u5f80\u540e\u5c31\u5012\u3002</p><p>\u5f53\u4ed6\u6e05\u9192\u8fc7\u6765\u7684\u65f6\u5019\uff0c\u53d1\u73b0\u81ea\u5df1\u6b63\u8eba\u5728\u5730\u6bef\u4e0a\uff0c\u5965\u6c83\u5728\u65c1\u8fb9\u7ad9\u7740\uff0c\u4f4e\u5934\u770b\u4ed6\uff0c\u201c\u8d77\u6765\u5427\uff0c\u201d\u8001\u5bb6\u4f19\u8bf4\uff0c\u201c\u662f\u4e0d\u662f\u89c9\u5f97\u5934\u5f88\u75bc\uff1f\u201d</p><p>\u743c\u6069\u722c\u8d77\u8eab\u6765\uff0c\u53ea\u89c9\u5f97\u8111\u888b\u8fd8\u5728\u9690\u9690\u4f5c\u75bc\uff0c\u773c\u524d\u4f3c\u4e4e\u8fd8\u4e00\u9635\u9635\u5730\u5192\u91d1\u661f\uff0c\u90a3\u5f20\u8584\u91d1\u7eb8\u5f20\u8fd8\u5728\u624b\u91cc\uff0c\u4f46\u4ed6\u53ef\u518d\u4e0d\u6562\u591a\u770b\u4e00\u773c\u4e86\uff0c\u8fde\u5fd9\u8fd8\u7ed9\u5965\u6c83\u3002\u201c\u8001\u5e08\uff0c\u6211\u521a\u624d\u8fd9\u662f\u2026\u2026\u201d</p><p>\u5965\u6c83\u622a\u65ad\u4e86\u4ed6\u7684\u8bdd\uff0c\u201c\u4f60\u5148\u544a\u8bc9\u6211\uff0c\u4f60\u8ba4\u8bc6\u8fd9\u4e0a\u9762\u7684\u6587\u5b57\u5417\uff1f\u201d</p><p>\u201c\u8ba4\u8bc6\uff0c\u4f46\u521a\u8bfb\u4e86\u4e24\u53e5\u5c31\u2026\u2026\u201d</p><p>\u201c\u8fd9\u4e0a\u9762\u662f\u4ec0\u4e48\u6587\u5b57\uff1f\u201d</p><p>\u201c\u662f\u2026\u2026\u201d\u743c\u6069\u6115\u7136\u6014\u4f4f\u4e86\uff0c\u56e0\u4e3a\u4ed6\u7a81\u7136\u53d1\u89c9\uff0c\u90a3\u7eb8\u4e0a\u7684\u6587\u5b57\u5c45\u7136\u4e0d\u662f\u4ed6\u5b66\u8fc7\u7684\u4efb\u4f55\u4e00\u79cd\u8bed\u8a00\uff0c\u65e2\u4e0d\u662f\u9f99\u8bed\uff0c\u4e5f\u4e0d\u662f\u8010\u745f\u8bed\uff0c\u4e5f\u4e0d\u662f\u901a\u7528\u8bed\uff0c\u5e94\u8be5\u4e5f\u4e0d\u50cf\u662f\u7cbe\u7075\u8bed\u77ee\u4eba\u8bed\u4e4b\u7c7b\u3002\u5965\u6c83\u95ee\u4ed6\u662f\u4ec0\u4e48\u6587\u5b57\uff0c\u4ed6\u8fd8\u771f\u8bf4\u4e0d\u4e0a\u6765\u3002</p><p>\u7136\u800c\u8fd9\u5c31\u5f88\u5947\u602a\uff0c\u65e2\u7136\u81ea\u5df1\u660e\u660e\u6ca1\u5b66\u8fc7\u8fd9\u79cd\u8bed\u8a00\uff0c\u4e3a\u4ec0\u4e48\u5374\u53c8\u80fd\u6e05\u695a\u5730\u770b\u61c2\u610f\u601d\u2026\u2026</p><p>\u201c\u6362\u4e86\u4e00\u822c\u4eba\uff0c\u54ea\u6015\u662f\u7b2c\u4e00\u6d41\u7684\u8bed\u8a00\u5b66\u8005\uff0c\u90a3\u5230\u8fd9\u5f20\u7eb8\uff0c\u4ed6\u4e5f\u770b\u4e0d\u61c2\u4ec0\u4e48\u5199\u7740\u4ec0\u4e48\uff0c\u201d\u5965\u6c83\u6162\u60a0\u60a0\u5730\u8bf4\uff0c\u201c\u5f53\u7136\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u50cf\u4f60\u4e00\u6837\u5934\u75bc\u773c\u82b1\u3002\u53ea\u6709\u6781\u5c11\u6570\u4eba\uff0c\u624d\u80fd\u770b\u61c2\u4e0a\u9762\u7684\u6587\u5b57\uff0c\u6bd4\u5982\u4f60\u6211\u3002\u4f60\u662f\u80fd\u770b\u61c2\u7684\uff0c\u4f46\u9020\u8be3\u4e0d\u591f\uff0c\u6240\u4ee5\u627f\u53d7\u4e0d\u4f4f\u3002\u201d</p><p>\u201c\u60a8\u7684\u610f\u601d\u662f\u2026\u2026\u201d\u743c\u6069\u9690\u9690\u7ea6\u7ea6\u4f3c\u4e4e\u660e\u767d\u4e86\u4e9b\u4ec0\u4e48\u3002</p><p>\u201c\u901a\u5e38\u60c5\u51b5\u4e0b\uff0c\u552f\u6709\u5927\u5965\u672f\u5e08\u6216\u8005\u5373\u5c06\u6210\u4e3a\u5927\u5965\u672f\u5e08\u7684\u4eba\uff0c\u624d\u80fd\u770b\u61c2\u8fd9\u4e0a\u9762\u7684\u6587\u5b57\uff0c\u5728\u6b64\u4e4b\u524d\u662f\u529e\u4e0d\u5230\u7684\uff0c\u201d\u5965\u6c83\u8bf4\uff0c\u4ed6\u7684\u773c\u7736\u91cc\u6a58\u7ea2\u8272\u5fae\u5149\u95ea\u70c1\u523a\u76ee\uff0c\u76ef\u7740\u743c\u6069\uff0c\u201c\u4f46\u4f60\u5374\u662f\u4e2a\u4f8b\u5916\uff0c\u6240\u4ee5\u6211\u8bf4\uff0c\u4f60\u662f\u4e2a\u5929\u751f\u7684\u5927\u5965\u672f\u5e08\u6750\u6599\u2014\u2014\u5f53\u7136\uff0c\u4f60\u5e76\u975e\u662f\u552f\u4e00\u7684\u4f8b\u5916\u3002\u201d</p></article>"}}}]);
