(window.webpackJsonp=window.webpackJsonp||[]).push([[1750],{1773:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u8282 \u6d77\u4e0a",content:"<article><h2>\u7b2c\u4e94\u8282 \u6d77\u4e0a</h2><p>\u743c\u6069\u5f88\u5feb\u5c31\u770b\u5230\u4e86\u838e\u73de\u514b\u6240\u8bf4\u7684\u201c\u5c0f\u9ebb\u70e6\u201d\u662f\u4ec0\u4e48\u3002</p><p>\u4e00\u7fa4\u6c99\u534e\u9c7c\u4eba\uff0c\u6570\u91cf\u5927\u7ea6\u6709\u4e00\u767e\u591a\u4e2a\uff0c\u4e71\u7cdf\u7cdf\u5730\u5f62\u6210\u4e00\u4e2a\u5305\u56f4\u5708\uff0c\u5c06\u6d77\u8239\u56f4\u4f4f\u3002\u6c99\u534e\u9c7c\u4eba\u662f\u6d77\u4e2d\u9c7c\u4eba\u65cf\u7684\u4e00\u7c7b\u4e9a\u79cd\uff0c\u6076\u540d\u662d\u8457\uff0c\u6700\u559c\u6b22\u6210\u7fa4\u7ed3\u961f\u5730\u51fa\u52a8\uff0c\u6253\u52ab\u6d77\u4e0a\u8239\u53ea\u3002\u548c\u4eba\u7c7b\u6d77\u76d7\u4e0d\u540c\uff0c\u6c99\u534e\u9c7c\u4eba\u4e0d\u8981\u94b1\u8d22\uff0c\u56e0\u4e3a\u4eba\u7c7b\u7684\u94b1\u8d22\u5bf9\u5b83\u4eec\u800c\u8a00\u610f\u4e49\u4e0d\u5927\uff0c\u5b83\u4eec\u559c\u6b22\u7684\u662f\u5404\u79cd\u827a\u672f\u54c1\uff08\u6700\u597d\u4e0e\u6d77\u6d0b\u6709\u5173\uff0c\u7528\u4e8e\u796d\u7940\u5b83\u4eec\u7684\u795e\u660e\u201c\u745f\u5bc7\u62c9\u201d\uff09\uff0c\u4ee5\u53ca\u6d3b\u4eba\uff08\u4f5c\u4e3a\u98df\u7269\uff09\u3002\u7531\u4e8e\u8fd9\u79cd\u7f18\u6545\uff0c\u6c99\u534e\u9c7c\u4eba\u662f\u6240\u6709\u6d77\u76d7\u4e2d\u6700\u62db\u4eba\u75db\u6068\u7684\u4e00\u79cd\uff0c\u4f46\u5b83\u4eec\u4e0d\u4ec5\u51f6\u6b8b\uff0c\u800c\u4e14\u5f88\u96be\u5bf9\u4ed8\uff0c\u9c7c\u4eba\u5728\u6d77\u4e2d\u539f\u672c\u5c31\u6709\u5148\u5929\u4f18\u52bf\uff0c\u6c99\u534e\u9c7c\u4eba\u8fd8\u5c5e\u4e8e\u667a\u6167\u6bd4\u8f83\u9ad8\u3001\u4f53\u683c\u6bd4\u8f83\u5f3a\u58ee\u7684\u4e9a\u79cd\uff0c\u80fd\u591f\u4e0e\u795e\u660e\u6c9f\u901a\uff0c\u6709\u7267\u5e08\uff0c\u6570\u91cf\u8fd8\u591a\u3002\u743c\u6069\u542c\u8bf4\u6709\u4e9b\u8239\u4e3b\u5728\u51fa\u6d77\u524d\uff0c\u4f1a\u53bb\u5974\u96b6\u5e02\u573a\u4e70\u4e00\u4e9b\u4fbf\u5b9c\u7684\u5974\u96b6\u5e26\u7740\uff0c\u9047\u5230\u6c99\u534e\u9c7c\u4eba\u5c31\u4e22\u51e0\u4e2a\u4e0b\u53bb\u3002\u6c99\u534e\u9c7c\u4eba\u8fd8\u7b97\u8bb2\u201c\u9053\u7406\u201d\uff0c\u65e2\u7136\u76ee\u7684\u8fbe\u5230\uff0c\u5f80\u5f80\u4e5f\u5c31\u4e0d\u518d\u7ea0\u7f20\u3002</p><p>\u8fd9\u4e9b\u6c99\u534e\u9c7c\u4eba\u7684\u9886\u8896\u662f\u4e00\u4e2a\u5973\u6027\uff0c\u6216\u8005\u8bf4\u96cc\u6027\uff08\u56e0\u4e3a\u9c7c\u4eba\u4e0d\u7a7f\u8863\u670d\uff0c\u5f88\u5bb9\u6613\u5206\u8fa8\uff09\uff0c\u770b\u4e0d\u51fa\u5e74\u7eaa\uff0c\u76ae\u80a4\u662f\u9752\u84dd\u8272\u7684\uff0c\u6709\u4e00\u9053\u4e00\u9053\u7684\u9ed1\u8272\u6591\u7eb9\uff0c\u4e00\u8bf4\u8bdd\uff0c\u5c31\u9732\u51fa\u6ee1\u53e3\u5229\u5203\u822c\u7684\u7259\u9f7f\uff0c\u767d\u6643\u6643\u7684\uff0c\u5dee\u70b9\u95ea\u778e\u743c\u6069\u7684\u72d7\u773c\u3002\u9c7c\u4eba\u7684\u8bed\u8a00\u81ea\u6210\u4f53\u7cfb\uff0c\u743c\u6069\u5b8c\u5168\u4e0d\u61c2\uff0c\u53ea\u89c1\u6770\u59c6\u7ad9\u5728\u8239\u8237\u8fb9\uff0c\u548c\u9c7c\u4eba\u9886\u8896\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u7136\u540e\u4f3c\u4e4e\u662f\u8c08\u5d29\u4e86\u3002\u9c7c\u4eba\u9886\u8896\u9ad8\u9ad8\u4e3e\u8d77\u624b\u4e2d\u7684\u957f\u621f\uff0c\u5411\u524d\u4e00\u6307\uff0c\u6240\u6709\u7684\u9c7c\u4eba\u540c\u65f6\u53d1\u51fa\u201c\u55ec\u201d\u3001\u201c\u55ec\u201d\u7684\u602a\u53eb\u58f0\uff0c\u671d\u7740\u6d77\u8239\u51b2\u8fc7\u6765\u3002</p><p>\u8fd9\u8258\u8239\u6562\u8fdc\u6d0b\u51fa\u6d77\uff0c\u5f53\u7136\u4e5f\u662f\u6709\u6240\u4f9d\u4ed7\u3002\u8239\u4e0a\u7684\u6c34\u624b\u4eec\u65e9\u5c31\u5404\u5c31\u5404\u4f4d\uff0c\u6570\u5341\u5f20\u5f3a\u5f29\u540c\u65f6\u53d1\u5c04\uff0c\u51e0\u4f4d\u968f\u8239\u5deb\u5e08\u4e5f\u5f00\u59cb\u9882\u5531\u5492\u6587\uff0c\u4ed6\u4eec\u7684\u9b54\u6cd5\u9020\u8be3\u90fd\u4e0d\u7b97\u5f88\u9ad8\uff0c\u81f3\u5c11\u4ee5\u743c\u6069\u7684\u773c\u5149\u6765\u770b\u4e0d\u8fc7\u5c14\u5c14\uff0c\u4f46\u7ecf\u9a8c\u4e30\u5bcc\uff0c\u53cd\u5e94\u8fc5\u901f\uff0c\u4f7f\u7528\u7684\u6cd5\u672f\u5a01\u529b\u90fd\u4e0d\u5f3a\uff0c\u5374\u6070\u597d\u514b\u5236\u9c7c\u4eba\u7684\u7279\u6027\u3002\u4e0d\u8fc7\u7247\u523b\uff0c\u9c7c\u4eba\u5c31\u5012\u4e0b\u4e86\u4e94\u5206\u4e4b\u4e00\u5de6\u53f3\uff0c\u773c\u770b\u81ea\u5df1\u8fd9\u8fb9\u5927\u5360\u4e0a\u98ce\uff0c\u743c\u6069\u4e5f\u7a0d\u7a0d\u653e\u5fc3\uff0c\u770b\u6765\u662f\u4e0d\u9700\u8981\u81ea\u5df1\u51fa\u624b\u4e86\u3002</p><p>\u201c\u4e0d\u5bf9\uff01\u201d</p><p>\u6885\u83f2\u65af\u4e5f\u5728\u4ed6\u8eab\u65c1\u89c2\u6218\uff0c\u7a81\u7136\u8138\u8272\u4e00\u53d8\uff0c\u4e5f\u4e0d\u77e5\u9053\u53d1\u73b0\u4e86\u4ec0\u4e48\uff0c\u5979\u731b\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u4ece\u8239\u4e0a\u8df3\u4e86\u4e0b\u53bb\u3002\u91d1\u8272\u7684\u900f\u660e\u5149\u7ffc\u5728\u5979\u7684\u80cc\u540e\u5f20\u5f00\uff0c\u8ba9\u5979\u6ca1\u6709\u7b14\u76f4\u5411\u6d77\u4e2d\u5760\u843d\uff0c\u800c\u662f\u5982\u5229\u7bad\u822c\u671d\u90a3\u540d\u5973\u6027\u9c7c\u4eba\u9886\u8896\u4fef\u51b2\u8fc7\u53bb\u3002\u743c\u6069\u4e0d\u660e\u6240\u4ee5\uff0c\u4f46\u4ed6\u4e0b\u610f\u8bc6\u5730\u7ed9\u6885\u83f2\u65af\u52a0\u6301\u4e86\u4e00\u9053\u9632\u62a4\u6cd5\u672f\uff0c\u968f\u5373\u6d6e\u7a7a\u800c\u8d77\uff0c\u7d27\u968f\u5176\u540e\u3002</p><p>\u90a3\u540d\u9c7c\u4eba\u9886\u8896\u6b63\u4e24\u53ea\u524d\u80a2\u9ad8\u4e3e\uff0c\u659c\u6258\u7740\u957f\u621f\uff0c\u6446\u51fa\u4e00\u4e2a\u5947\u602a\u7684\u59ff\u52bf\u3002\u773c\u89c1\u6885\u83f2\u65af\u9ad8\u901f\u51b2\u6765\uff0c\u5979\u5c06\u957f\u621f\u4e00\u6446\uff0c\u4e00\u9053\u8840\u7ea2\u8272\u7684\u95ea\u7535\u4ece\u621f\u5203\u4e2d\u53d1\u51fa\u6765\uff0c\u671d\u654c\u4eba\u5c04\u53bb\u3002\u6885\u83f2\u65af\u5728\u7a7a\u4e2d\u4e00\u4e2a\u4fa7\u8eab\uff0c\u907f\u5f00\u95ea\u7535\uff0c\u968f\u5373\u53cc\u624b\u4e3e\u5251\uff0c\u9694\u7a7a\u65a9\u4e0b\u3002</p><p>\u201c\u7834\u90aa\uff01\u201d</p><p>\u767d\u8272\u7684\u5149\u4ece\u5251\u4e2d\u8ff8\u53d1\uff0c\u8f7b\u800c\u6613\u4e3e\u5730\u5c06\u957f\u621f\u8fde\u540c\u9c7c\u4eba\u7684\u53f3\u524d\u80a2\u4e00\u8d77\u65a9\u65ad\u3002\u9c7c\u4eba\u53d1\u51fa\u4e00\u58f0\u6124\u6012\u7684\u5636\u53eb\u58f0\uff0c\u5c31\u8981\u94bb\u5165\u6d77\u4e2d\uff0c\u743c\u6069\u6b64\u65f6\u5df2\u7ecf\u8d76\u5230\uff0c\u4ed6\u4f38\u624b\u5728\u7a7a\u6c14\u4e2d\u865a\u6293\u4e86\u4e00\u4e0b\uff0c\u6d77\u6c34\u9aa4\u7136\u5347\u8d77\uff0c\u5316\u4f5c\u4e00\u53ea\u5de8\u638c\uff0c\u5c06\u9c7c\u4eba\u63e1\u5728\u5176\u4e2d\uff0c\u4ee4\u5979\u52a8\u5f39\u4e0d\u5f97\u3002</p><p>\u773c\u89c1\u65e0\u6cd5\u9003\u8131\uff0c\u9c7c\u4eba\u4eff\u4f5b\u53d1\u52a8\u4e86\u67d0\u79cd\u90aa\u672f\uff0c\u4f53\u578b\u9661\u7136\u6025\u5267\u81a8\u80c0\u8d77\u6765\uff0c\u4e0d\u5230\u4e00\u79d2\u949f\u4fbf\u6da8\u5927\u4e86\u5c06\u8fd1\u5341\u500d\uff0c\u5c06\u6d77\u6c34\u5de8\u638c\u6491\u5f00\uff0c\u7136\u540e\u8f70\u7136\u7206\u70b8\u5f00\u6765\u3002\u65e0\u6570\u9053\u8840\u7ea2\u8272\u7684\u95ea\u7535\u4ece\u5979\u7684\u4f53\u5185\u6d8c\u51fa\uff0c\u5411\u56db\u9762\u516b\u65b9\u5c04\u53bb\uff0c\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u743c\u6069\u91ca\u653e\u51fa\u4e86\u96f7\u9706\u795e\u529b\uff0c\u5f62\u6210\u4e00\u9053\u73af\u5f62\u98d3\u98ce\u5c4f\u969c\uff0c\u5c06\u5927\u90e8\u5206\u95ea\u7535\u90fd\u5438\u6536\u8fdb\u6765\uff0c\u53ea\u6709\u5c11\u91cf\u6f0f\u7f51\u4e4b\u9c7c\uff0c\u5df2\u7ecf\u9020\u4e0d\u6210\u4ec0\u4e48\u4e25\u91cd\u635f\u5bb3\u3002</p><p>\u6885\u83f2\u65af\u5728\u6d77\u6c34\u4e2d\u5bfb\u627e\u4e86\u4e00\u4f1a\uff0c\u7528\u5251\u5c16\u6311\u8d77\u67d0\u4ef6\u4e1c\u897f\uff0c\u7136\u540e\u6325\u52a8\u5149\u7ffc\uff0c\u8fd4\u56de\u8239\u4e0a\u3002</p><p>\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u770b\u7740\u6885\u83f2\u65af\u62ff\u56de\u6765\u7684\u4e1c\u897f\uff0c\u90a3\u50cf\u662f\u4e00\u4e2a\u505a\u5de5\u7c97\u7cd9\u7684\u5de5\u827a\u54c1\uff0c\u638c\u5fc3\u5927\u5c0f\uff0c\u76fe\u724c\u5f62\u72b6\uff0c\u84dd\u767d\u7684\u5e95\u8272\u4e0a\uff0c\u7ed8\u7740\u51e0\u9053\u7206\u88c2\u72b6\u7ea2\u8272\u6761\u7eb9\u3002\u8fd9\u56fe\u6848\u4f3c\u4e4e\u6709\u70b9\u773c\u719f\uff0c\u50cf\u662f\u5728\u54ea\u91cc\u89c1\u8fc7\uff0c\u4f46\u743c\u6069\u4e00\u65f6\u60f3\u4e0d\u8d77\u6765\u3002</p><p>\u201c\u72ec\u773c\u795e\u7684\u5723\u5fbd\u3002\u201d\u6770\u59c6\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u6765\u5230\u65c1\u8fb9\u3002</p><p>\u201c\u72ec\u773c\u795e\uff1f\u201d\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u4ed6\u4ece\u6ca1\u542c\u8fc7\u8fd9\u4e2a\u795e\uff0c\u800c\u4e14\u53c8\u6709\u54ea\u4f4d\u795e\u7957\u4f1a\u53d6\u8fd9\u79cd\u4e0d\u96c5\u7684\u540d\u5b57\u3002</p><p>\u201c\u5c31\u662f\u5854\u6d1b\u65af\u5427\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u5854\u6d1b\u65af\u662f\u4e2a\u5927\u90aa\u795e\uff0c\u6885\u83f2\u65af\u63d0\u8d77\u4ed6\u81ea\u7136\u5168\u65e0\u534a\u70b9\u656c\u610f\uff0c\u76f4\u547c\u5176\u540d\u3002\u6770\u59c6\u5374\u6ca1\u8fd9\u4e2a\u80c6\u91cf\uff0c\u201c\u5e94\u8be5\u5c31\u662f\u4ed6\uff0c\u201d\u6770\u59c6\u8bf4\uff0c\u201c\u6211\u4e5f\u662f\u542c\u95fb\uff0c\u8bf4\u8fd1\u51e0\u5e74\u6765\uff0c\u5760\u661f\u6d77\u91cc\u7684\u5f88\u591a\u9c7c\u4eba\u90e8\u843d\u90fd\u6539\u4fe1\u4e86\u4e00\u4e2a\u72ec\u773c\u795e\uff0c\u4e0d\u4fe1\u745f\u5bc7\u62c9\u4e86\u3002\u8fd8\u8bf4\u90a3\u4f4d\u72ec\u773c\u795e\u795e\u529b\u975e\u51e1\uff0c\u80fd\u632f\u52a8\u6d77\u6d0b\uff0c\u53f7\u4ee4\u98ce\u66b4\uff0c\u638c\u63e1\u96f7\u9706\uff0c\u65e0\u6240\u4e0d\u80fd\u3002\u540e\u6765\u6709\u4eba\u6253\u63a2\u51fa\u6765\uff0c\u8bf4\u6240\u8c13\u72ec\u773c\u795e\uff0c\u5176\u5b9e\u5c31\u662f\u98ce\u66b4\u4e4b\u795e\uff0c\u6539\u4e86\u4e2a\u540d\u5b57\u3002\u201d</p><p>\u8bf4\u72ec\u773c\u795e\u743c\u6069\u4e0d\u77e5\u9053\uff0c\u4f46\u8981\u8bf4\u98ce\u66b4\u4e4b\u795e\u5854\u6d1b\u65af\uff0c\u743c\u6069\u5c31\u6e05\u695a\u4e86\uff0c\u524d\u4e0d\u4e45\u5728\u5854\u745f\u8c37\u5e72\u6389\u7684\u90a3\u4f4d\u5149\u5934\u5927\u6c49\u5361\u65af\u56fe\uff0c\u5c31\u662f\u5854\u6d1b\u65af\u7684\u9009\u6c11\uff0c\u4ed6\u7684\u5934\u9885\u8fd8\u5728\u6b21\u5143\u888b\u91cc\uff0c\u838e\u73de\u514b\u60f3\u62ff\u53bb\u9886\u8d4f\uff0c\u4e00\u76f4\u6ca1\u6709\u673a\u4f1a\u3002\u5854\u6d1b\u65af\u4e5f\u662f\u8fd9\u4e2a\u4e16\u754c\u6700\u53e4\u8001\u7684\u795e\u7957\u4e4b\u4e00\uff0c\u5728\u8010\u745f\u65f6\u4ee3\u5c31\u5df2\u7ecf\u4f4d\u5c45\u9ad8\u7b49\u795e\u4e4b\u5217\uff08\u90a3\u65f6\u5019\u4ed6\u53eb\u67ef\u8428\uff0c\u540e\u6765\u6539\u540d\u5854\u6d1b\u65af\uff09\uff0c\u5728\u8bf8\u795e\u4e4b\u4e2d\uff0c\u6570\u4ed6\u6700\u559c\u6b22\u63d0\u643a\u540e\u8fdb\uff0c\u7ecf\u5e38\u5f88\u70ed\u5fc3\u5730\u57f9\u517b\u4e00\u4e2a\u51e1\u4eba\u767b\u4e0a\u795e\u4f4d\uff0c\u9010\u6e10\u53d1\u5c55\u58ee\u5927\u2014\u2014\u7136\u540e\u5728\u5408\u9002\u7684\u65f6\u673a\u5c06\u5176\u541e\u566c\u3002\u636e\u743c\u6069\u6240\u77e5\uff0c\u517d\u795e\u3001\u5bd2\u51ac\u5973\u795e\u3001\u6d77\u6d0b\u5973\u795e\u4e09\u4e2a\u795e\u5ea7\u90fd\u5df2\u7ecf\u6210\u4e86\u4ed6\u7684\u56ca\u4e2d\u4e4b\u7269\uff0c\u9c7c\u4eba\u662f\u6d77\u6d0b\u751f\u7269\uff0c\u539f\u672c\u6709\u4e00\u4e9b\u5c31\u5d07\u62dc\u6d77\u795e\uff0c\u5854\u6d1b\u65af\u5e72\u6389\u4e86\u6d77\u795e\uff0c\u63a5\u7ba1\u5176\u5168\u90e8\u795e\u4f4d\u3001\u795e\u804c\u548c\u529b\u91cf\uff0c\u4e58\u673a\u5728\u9c7c\u4eba\u4e2d\u53d1\u5c55\u4fe1\u4ef0\uff0c\u5b9e\u5728\u662f\u987a\u7406\u6210\u7ae0\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u6ca1\u60f3\u5230\u90a3\u4e2a\u751f\u9c7c\u7247\u5c45\u7136\u8fd8\u662f\u98ce\u66b4\u4e4b\u795e\u7684\u796d\u53f8\uff0c\u5e78\u597d\u4e24\u4f4d\u53cd\u5e94\u8fc5\u901f\uff0c\u6ca1\u6709\u8ba9\u5979\u653e\u51fa\u6cd5\u672f\uff0c\u5426\u5219\u635f\u5931\u5c31\u6bd4\u8f83\u4e25\u91cd\u4e86\u3002\u201d\u6770\u59c6\u4e00\u518d\u611f\u8c22\uff0c\u5e76\u4e14\u6177\u6168\u8868\u793a\u4ece\u4eca\u5929\u5f00\u59cb\uff0c\u743c\u6069\u4e00\u884c\u4eba\u7684\u4f19\u98df\u8d39\u5168\u514d\u3002\u5bf9\u6b64\u743c\u6069\u8868\u793a\u6beb\u65e0\u610f\u4e49\uff0c\u56e0\u4e3a\u8239\u4e0a\u7684\u4f19\u98df\u592a\u96be\u5403\u4e86\uff0c\u4ed6\u4eec\u5e73\u65f6\u90fd\u662f\u81ea\u5df1\u5f00\u5c0f\u7076\u7684\uff0c\u539f\u6599\u76f4\u63a5\u4ece\u8239\u5458\u624b\u91cc\u4e70\uff0c\u73ca\u5609\u4eb2\u81ea\u4e0b\u53a8\uff0c\u539f\u672c\u4e5f\u5c31\u4e0d\u7528\u4ea4\u4ec0\u4e48\u4f19\u98df\u8d39\u3002</p><p>\u9886\u8896\u6218\u6b7b\uff0c\u9c7c\u4eba\u4eec\u4e5f\u4e0d\u518d\u901e\u5f3a\uff0c\u8f6c\u773c\u95f4\u4f5c\u9e1f\u517d\u6563\u3002\u6d77\u8239\u7ee7\u7eed\u524d\u884c\uff0c\u743c\u6069\u6b63\u60f3\u56de\u623f\u95f4\uff0c\u5374\u89c1\u6885\u83f2\u65af\u76b1\u7740\u7709\u5934\uff0c\u7ffb\u6765\u8986\u53bb\u5730\u770b\u90a3\u679a\u5723\u5fbd\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u5854\u6d1b\u65af\u5f04\u51e0\u4e2a\u9c7c\u4eba\u4fe1\u5f92\uff0c\u5f88\u6b63\u5e38\u5427\uff0c\u4ed6\u4e0d\u662f\u4e00\u76f4\u8fd9\u4e48\u5e72\u4e48\u3002\u201d</p><p>\u9c7c\u4eba\u52c9\u5f3a\u4e5f\u7b97\u667a\u6167\u751f\u7269\uff0c\u4f46\u667a\u5546\u4e0d\u9ad8\uff0c\u5176\u5b9e\u5e76\u4e0d\u662f\u4e0a\u7b49\u7684\u4fe1\u5f92\u6750\u6599\uff0c\u4f46\u5854\u6d1b\u65af\u5bf9\u8fd9\u65b9\u9762\u4f3c\u4e4e\u5b8c\u5168\u4e0d\u6311\u5254\u3002\u743c\u6069\u8fd8\u8bb0\u5f97\u5f88\u65e9\u4ee5\u524d\uff0c\u4ed6\u548c\u6885\u83f2\u65af\u4ece\u70db\u5821\u56de\u9634\u9b42\u57ce\uff0c\u5728\u8def\u4e0a\u9047\u5230\u4e00\u7fa4\u8725\u8734\u4eba\u62e6\u8def\u6253\u52ab\uff0c\u8fd9\u4e9b\u8725\u8734\u4eba\u4e2d\u5c31\u6709\u5854\u6d1b\u65af\u7267\u5e08\u3002\u8725\u8734\u4eba\u3001\u9c7c\u4eba\u2026\u2026\u5854\u6d1b\u65af\u96be\u9053\u5bf9\u8fd9\u4e9b\u7c7b\u4eba\u751f\u7269\u6709\u7279\u6b8a\u504f\u597d\u4e48\uff0c\u4ed6\u7684\u4e0b\u4e00\u4e2a\u53d1\u5c55\u5bf9\u8c61\u4e0d\u4f1a\u662f\u72d7\u5934\u4eba\u5427\u3002</p><p>\u201c\u603b\u89c9\u5f97\u4e0d\u592a\u5bf9\u52b2\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u90a3\u9053\u7ea2\u5149\u2026\u2026\u4f3c\u4e4e\u548c\u4e4b\u524d\u89c1\u8fc7\u7684\u4e0d\u592a\u4e00\u6837\u3002\u201d</p><p>\u201c\u4e0d\u4e00\u6837\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u4e0d\u8fc7\u4e5f\u53ef\u80fd\u662f\u6211\u7684\u9519\u89c9\u5427\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u7b97\u4e86\uff0c\u522b\u7ba1\u4e86\uff0c\u53cd\u6b63\u548c\u6211\u4eec\u5e94\u8be5\u6ca1\u4ec0\u4e48\u5173\u7cfb\u3002\u201d</p><p>\u743c\u6069\u5f53\u7136\u66f4\u4e0d\u5728\u610f\u3002\u867d\u8bf4\u662f\u4ed6\u6740\u4e86\u5854\u6d1b\u65af\u7684\u9009\u6c11\uff0c\u4f46\u98ce\u66b4\u4e4b\u795e\u8981\u590d\u4ec7\uff0c\u603b\u4e5f\u4e0d\u81f3\u4e8e\u6d3e\u4e00\u7fa4\u9c7c\u4eba\u8fc7\u6765\uff0c\u5e94\u8be5\u53ea\u662f\u5076\u9047\u800c\u5df2\uff0c\u4e0d\u5fc5\u592a\u7591\u795e\u7591\u9b3c\u3002</p><p>\u4e8b\u5b9e\u5f88\u5feb\u8bc1\u660e\u4ed6\u9519\u4e86\u3002</p><p>\u5728\u9047\u5230\u7b2c\u4e00\u6ce2\u9c7c\u4eba\u6d77\u76d7\u4e4b\u540e\u7684\u7b2c\u56db\u5929\uff0c\u98de\u7fd4\u86c7\u5973\u53f7\u906d\u9047\u4e86\u7b2c\u4e8c\u6ce2\u9c7c\u4eba\u5f3a\u76d7\uff0c\u6b64\u540e\u6bcf\u9694\u4e09\u5230\u56db\u5929\uff0c\u5c31\u5fc5\u7136\u4f1a\u906d\u9047\u4e00\u6ce2\u3002\u867d\u8bf4\u8fd9\u4e9b\u9c7c\u4eba\u4eec\u5b9e\u529b\u5e73\u5e73\uff0c\u6ca1\u6709\u9020\u6210\u4ec0\u4e48\u5a01\u80c1\uff0c\u4f46\u6240\u6709\u4eba\u8fd8\u662f\u89c9\u5f97\u5f88\u5947\u602a\u3002\u6770\u59c6\u8bf4\u8fd9\u6761\u822a\u7ebf\u4ed6\u4eec\u5df2\u7ecf\u8d70\u8fc7\u5f88\u591a\u6b21\uff0c\u4e00\u76f4\u4ee5\u6765\u90fd\u662f\u76f8\u5bf9\u6bd4\u8f83\u548c\u5e73\u5b89\u5168\u7684\uff0c\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u7edd\u65e0\u53ef\u80fd\u5982\u6b64\u9891\u7e41\u5730\u88ab\u6253\u52ab\uff0c\u800c\u4e14\u8fd8\u90fd\u662f\u88ab\u9c7c\u4eba\u6253\u52ab\uff0c\u8fd9\u4e5f\u592a\u5947\u602a\u4e86\u3002</p><p>\u201c\u6216\u8bb8\u662f\u6211\u4eec\u6700\u8fd1\u5403\u9c7c\u5403\u591a\u4e86\uff0c\u6240\u4ee5\u9c7c\u4eba\u4eec\u6765\u62a5\u4ec7\uff1f\u201d\u51db\u63d0\u51fa\u731c\u6d4b\u3002</p><p>\u201c\u2026\u2026\u9c7c\u4eba\u548c\u9c7c\u6ca1\u6709\u4efb\u4f55\u5173\u7cfb\u3002\u201d</p><p>\u5c31\u50cf\u4f2a\u9f99\u4e0d\u662f\u9f99\uff0c\u9c7c\u4eba\u4e5f\u4e0d\u662f\u9c7c\uff0c\u867d\u8bf4\u5916\u5f62\u6709\u8fd1\u4f3c\u4e4b\u5904\uff0c\u5176\u5b9e\u4e00\u70b9\u8840\u7f18\u5173\u7cfb\u90fd\u6ca1\u6709\u3002\u9c7c\u4eba\u81ea\u5df1\u4e5f\u5403\u9c7c\uff0c\u800c\u4e14\u5403\u5f97\u66f4\u591a\uff0c\u600e\u4e48\u4f1a\u56e0\u4e3a\u8fd9\u79cd\u7406\u7531\u627e\u4eba\u7c7b\u9ebb\u70e6\uff1f\u8981\u8bf4\u5b83\u4eec\u662f\u4e3a\u4e4b\u524d\u6b7b\u5728\u743c\u6069\u548c\u6885\u83f2\u65af\u624b\u91cc\u7684\u90a3\u4f4d\u5854\u6d1b\u65af\u5973\u796d\u53f8\u62a5\u4ec7\u8fd8\u5dee\u4e0d\u591a\u3002</p><p>\u201c\u4e5f\u4e0d\u50cf\u662f\u6765\u62a5\u4ec7\u7684\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u66f4\u50cf\u662f\u5728\u8bd5\u63a2\u6211\u4eec\u3002\u201d</p><p>\u201c\u8bd5\u63a2\u4ec0\u4e48\uff1f\u201d</p><p>\u6885\u83f2\u65af\u6447\u6447\u5934\uff0c\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u53ea\u662f\u6709\u8fd9\u79cd\u611f\u89c9\u3002\u201d</p><p>\u4e0d\u4f1a\u771f\u7684\u662f\u5854\u6d1b\u65af\u53d1\u73b0\u743c\u6069\u6740\u4e86\u4ed6\u7684\u9009\u6c11\uff0c\u6d3e\u624b\u4e0b\u8fc7\u6765\u627e\u573a\u5b50\u5427\u3002</p><p>\u9c7c\u4eba\u4e0d\u8db3\u4e3a\u8651\uff0c\u6765\u5f97\u518d\u591a\u518d\u9891\u7e41\u743c\u6069\u4e5f\u4e0d\u6015\uff0c\u65e0\u975e\u6536\u62fe\u8d77\u6765\u9ebb\u70e6\u70b9\u800c\u5df2\u3002\u4f46\u82e5\u662f\u5f53\u771f\u80cc\u540e\u6709\u4e00\u4f4d\u90aa\u795e\u5728\u6ce8\u89c6\uff0c\u90a3\u5c31\u5f88\u4e0d\u6709\u8da3\u4e86\u3002\u6885\u83f2\u65af\u65e2\u7136\u8fd9\u6837\u8bf4\u4e86\uff0c\u743c\u6069\u4fbf\u63d0\u9ad8\u8b66\u60d5\uff0c\u5c31\u8fd9\u6837\u53c8\u8fc7\u4e86\u4e09\u5929\uff0c\u610f\u6599\u4e4b\u4e2d\u7684\u5f3a\u654c\u7ec8\u4e8e\u627e\u4e0a\u4e86\u95e8\u3002</p><p>\u203b\u203b\u203b</p><p>\u8fd9\u662f\u767b\u8239\u51fa\u6d77\u4e4b\u540e\u7684\u7b2c\u4e8c\u5341\u4e00\u5929\uff0c\u98de\u7fd4\u86c7\u5973\u53f7\u6b63\u7a7f\u8fc7\u201c\u5929\u9a6c\u201d\u5c9b\u7fa4\uff0c\u822a\u884c\u65b9\u5411\u4e5f\u81ea\u6b64\u7531\u4e1c\u8f6c\u5357\u3002\u5760\u661f\u6d77\u4e2d\u6709\u56db\u5927\u8457\u540d\u5c9b\u7fa4\uff0c\u5929\u9a6c\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u5b83\u7531\u4e00\u767e\u591a\u4e2a\u5c0f\u5c9b\u7ec4\u6210\uff0c\u4ece\u9ad8\u7a7a\u4fef\u77b0\uff0c\u5f62\u5982\u4e00\u5339\u957f\u7740\u53cc\u7ffc\u7684\u9a6c\uff0c\u7531\u6b64\u800c\u5f97\u540d\u3002\u5c9b\u4e0a\u6709\u4eba\u7c7b\u5c45\u4f4f\uff0c\u4f46\u5f88\u5c11\uff0c\u4e0d\u5230\u56db\u5343\u4eba\uff0c\u4e3b\u8981\u96c6\u4e2d\u5728\u4e00\u5ea7\u201c\u62c9\u739b\u5c9b\u201d\u4e0a\uff0c\u4ee5\u6355\u9c7c\u4e3a\u751f\uff0c\u5176\u4ed6\u5c9b\u5c7f\u51e0\u4e4e\u90fd\u88ab\u8302\u5bc6\u7684\u539f\u59cb\u68ee\u6797\u6240\u8986\u76d6\u3002</p><p>\u5f53\u654c\u4eba\u51fa\u73b0\u65f6\uff0c\u743c\u6069\u6b63\u53ec\u96c6\u4ed6\u7684\u6240\u6709\u961f\u5458\u5728\u5f00\u4f1a\u3002</p><p>\u8fd9\u662f\u6885\u83f2\u65af\u63d0\u8bae\u7684\uff0c\u5979\u8ba4\u4e3a\u65e2\u7136\u8981\u53bb\u4e1c\u57df\uff0c\u90a3\u4e48\u6709\u5fc5\u8981\u5f00\u4e2a\u4f1a\uff0c\u5411\u6240\u6709\u4eba\u901a\u62a5\u4e00\u4e0b\u60c5\u51b5\uff0c\u542c\u53d6\u5927\u5bb6\u7684\u8ba8\u8bba\u610f\u89c1\uff0c\u786e\u5b9a\u5177\u4f53\u884c\u7a0b\u89c4\u5212\u3002\u743c\u6069\u89c9\u5f97\u591a\u6b64\u4e00\u4e3e\uff0c\u6ca1\u4ec0\u4e48\u5fc5\u8981\uff0c\u4f46\u5c11\u5973\u975e\u5e38\u575a\u6301\uff0c\u4e8e\u662f\u7406\u6240\u5f53\u7136\u7684\uff0c\u4ed6\u8ba9\u6b65\u4e86\u3002</p><p>\u201c\u4f5c\u4e3a\u4e00\u4e2a\u961f\u4f0d\uff0c\u7edf\u4e00\u8ba4\u8bc6\u662f\u975e\u5e38\u6709\u5fc5\u8981\u7684\u3002\u5982\u679c\u6709\u53ef\u80fd\u7684\u8bdd\uff0c\u5728\u91cd\u5927\u51b3\u7b56\u4e4b\u524d\u7406\u5f53\u5e7f\u6cdb\u5f81\u6c42\u610f\u89c1\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5373\u4fbf\u4f60\u5df2\u7ecf\u505a\u51fa\u4e86\u51b3\u5b9a\uff0c\u4e5f\u8981\u8ba9\u6240\u6709\u4eba\u611f\u53d7\u5230\u88ab\u5c0a\u91cd\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u5c11\u5973\u7684\u8a00\u4e0b\u4e4b\u610f\uff0c\u5176\u5b9e\u5c31\u662f\u5728\u8d23\u5907\u743c\u6069\u8fc7\u4e8e\u8f7b\u7387\u3002\u743c\u6069\u8ba4\u771f\u60f3\u4e86\u60f3\uff0c\u89c9\u5f97\u6885\u83f2\u65af\u8bf4\u5f97\u4e5f\u786e\u5b9e\u6709\u9053\u7406\u3002\u53bb\u4e1c\u57df\u662f\u4ed6\u4e00\u4e2a\u4eba\u7684\u51b3\u5b9a\uff0c\u4e8b\u5148\u6ca1\u6709\u548c\u4efb\u4f55\u4eba\u5546\u91cf\u8fc7\uff0c\u867d\u7136\u5176\u4ed6\u4eba\u77e5\u9053\u540e\u90fd\u8868\u793a\u8d5e\u540c\u2014\u2014\u6216\u8005\u8bf4\u6ca1\u6709\u8868\u793a\u53cd\u5bf9\u2014\u2014\u4f46\u6bd5\u7adf\u8fd8\u662f\u6709\u4e9b\u4e0d\u59a5\u3002\u800c\u4e14\u4e1c\u57df\u662f\u4e2a\u964c\u751f\u6240\u5728\uff0c\u743c\u6069\u8981\u53bb\u54ea\u4e9b\u5730\u65b9\uff0c\u8981\u505a\u54ea\u4e9b\u4e8b\u60c5\uff0c\u53ea\u548c\u6885\u83f2\u65af\u5546\u91cf\u8fc7\uff0c\u5176\u4ed6\u4eba\u4f5c\u4e3a\u540c\u4f34\u4e5f\u7406\u5f53\u77e5\u6653\uff0c\u8fd9\u5c31\u662f\u6885\u83f2\u65af\u6240\u8bf4\u7684\u201c\u5c0a\u91cd\u201d\u95ee\u9898\u4e86\u3002</p><p>\u201c\u90a3\u597d\u5427\uff0c\u6211\u4eec\u5f00\u4f1a\u3002\u201d</p><p>\u5f00\u4f1a\u5f88\u7b80\u5355\uff0c\u53cd\u6b63\u4e00\u5171\u4e5f\u5c31\u90a3\u4e48\u51e0\u4e2a\u4eba\uff0c\u53ec\u96c6\u8d77\u6765\u5f88\u65b9\u4fbf\u3002\u201c\u4eba\u90fd\u5230\u9f50\u4e86\uff0c\u201d\u743c\u6069\u73af\u89c6\u4e00\u5708\uff0c\u201c\u90a3\u4e48\u5c31\u5f00\u59cb\u5427\uff0c\u9996\u5148\u7531\u6211\u6765\u8bf4\u4e00\u4e0b\u2014\u2014\u201d</p><p>\u201c\u7b49\u7b49\uff0c\u201d\u51db\u4e3e\u624b\u6253\u65ad\uff0c\u201c\u4eba\u8fd8\u6ca1\u5230\u9f50\u5462\u3002\u201d</p><p>\u201c\u4e0d\u662f\u90fd\u6765\u4e86\u5417\uff1f\u201d\u743c\u6069\u4e00\u4e2a\u4e00\u4e2a\u70b9\u540d\uff0c\u201c\u59d0\u59d0\u3001\u827e\u5f25\u8587\u3001\u838e\u73de\u514b\uff0c\u8fd8\u6709\u4f60\u548c\u6211\uff0c\u4e00\u5171\u4e94\u4e2a\u4eba\uff0c\u5168\u90fd\u5728\u554a\u3002\u201d</p><p>\u201c\u8fd8\u6709\u5854\u59d0\u59d0\u5462\uff0c\u201d\u51db\u4e0d\u9ad8\u5174\u5730\u8bf4\uff0c\u201c\u4f60\u628a\u5979\u5fd8\u4e86\uff1f\u7537\u4eba\u771f\u662f\u8584\u60c5\uff0c\u6628\u665a\u6211\u8fd8\u770b\u89c1\u4f60\u6084\u6084\u6e9c\u8fdb\u5979\u623f\u95f4\u91cc\u5077\u6b22\uff0c\u8fd9\u624d\u534a\u5929\u5c31\u629b\u4e4b\u8111\u540e\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u8981\u4e71\u9020\u8c23\uff0c\u6211\u6628\u665a\u660e\u660e\u6ca1\u53bb\uff0c\u201d\u743c\u6069\u8d76\u5fd9\u6f84\u6e05\uff0c\u201c\u6211\u5df2\u7ecf\u63a5\u8fde\u4e09\u5929\u90fd\u6ca1\u53bb\u4e86\u3002\u201d</p><p>\u201c\u6240\u4ee5\u4f60\u662f\u5df2\u7ecf\u73a9\u817b\u4e86\uff0c\u6253\u7b97\u59cb\u4e71\u7ec8\u5f03\u53e6\u89c5\u65b0\u6b22\uff1f\u201d\u51db\u8bc4\u4ef7\uff0c\u201c\u679c\u7136\u8fd8\u662f\u8584\u60c5\u3002\u201d</p><p>\u201c\u6839\u672c\u6ca1\u8fd9\u56de\u4e8b\uff01\u201d\u743c\u6069\u6297\u8bae\uff0c\u201c\u53ea\u662f\u8fd9\u51e0\u5929\u5979\u4e0d\u65b9\u4fbf\u800c\u5df2\u3002\u201d</p><p>\u201c\u5979\u4e0d\u80fd\u966a\u4f60\u4e0a\u5e8a\uff0c\u6240\u4ee5\u4f60\u5c31\u4e0d\u7406\u5979\u4e86\uff1f\u201d\u51db\u8d28\u95ee\uff0c\u201c\u6240\u4ee5\u5bf9\u4e8e\u4f60\u800c\u8a00\uff0c\u5979\u5c31\u53ea\u662f\u4e00\u4e2a\u6027\u4f34\u4fa3\u5417\u2014\u2014\u539f\u6765\u5982\u6b64\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\u3002\u201d\u73ca\u5609\u8bf4\uff0c\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u201c\u539f\u6765\u5982\u6b64\u3002\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u743c\u6069\u611f\u89c9\u81ea\u5df1\u843d\u5165\u9677\u9631\uff0c\u201c\u2026\u2026\u6211\u4e0d\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u201d</p><p>\u201c\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\uff01\u201d\u6240\u6709\u4eba\u5f02\u53e3\u540c\u58f0\u5730\u8bf4\u3002</p><p>\u743c\u6069\u4f4e\u5934\u6c89\u9ed8\u4e86\u4e09\u79d2\u949f\uff0c\u7136\u540e\u4e3e\u624b\u8868\u793a\u6295\u964d\uff0c\u201c\u597d\u5427\uff0c\u6211\u627f\u8ba4\uff0c\u6211\u65e2\u4e0d\u4e13\u60c5\uff0c\u4e5f\u4e0d\u8bda\u5b9e\uff0c\u89c1\u5f02\u601d\u8fc1\uff0c\u98ce\u6d41\u597d\u8272\u3002\u603b\u800c\u8a00\u4e4b\uff0c\u6211\u5c31\u662f\u4e2a\u6df7\u86cb\u2014\u2014\u68c0\u8ba8\u5b8c\u6bd5\u3002\u201d</p><p>\u73ca\u5609\u683c\u683c\u7b11\u8d77\u6765\uff0c\u201c\u6ca1\u4e8b\u5566\uff0c\u743c\u6069\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u786e\u5b9e\u662f\u4e2a\u5c0f\u6df7\u86cb\uff0c\u4f46\u4f60\u662f\u6211\u5f1f\u5f1f\uff0c\u6240\u4ee5\u65e0\u8bba\u4f60\u505a\u4ec0\u4e48\u6211\u90fd\u4f1a\u539f\u8c05\u4f60\u7684\u3002\u201d</p><p>\u201c\u6211\u4e5f\u4f1a\u539f\u8c05\u4f60\u7684\uff0c\u201d\u51db\u4eff\u4f5b\u4e0d\u7518\u793a\u5f31\u5730\u8bf4\uff0c\u201c\u8fd8\u6709\u827e\u5f25\u8587\u4e5f\u4f1a\u3002\u201d</p><p>\u6885\u83f2\u65af\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u6211\u53ef\u6ca1\u90a3\u4e48\u5bbd\u5bb9\uff0c\u201d\u5979\u62bf\u4e86\u62bf\u5507\uff0c\u201c\u4e0d\u8fc7\u6682\u65f6\u61d2\u5f97\u7ba1\u4ed6\u800c\u5df2\u3002\u201d</p><p>\u743c\u6069\u4e24\u624b\u4e00\u644a\uff0c\u201c\u90a3\u6211\u4eec\u53ef\u4ee5\u7ee7\u7eed\u5f00\u4f1a\u4e86\u5427\u3002\u201d</p><p>\u7b03\u3001\u7b03\uff0c\u6709\u4eba\u5728\u5916\u9762\u6572\u95e8\u3002</p><p>\u838e\u73de\u514b\u53bb\u5f00\u4e86\u95e8\uff0c\u53d1\u73b0\u662f\u4e00\u4e2a\u5e74\u8f7b\u6c34\u624b\uff0c\u4e2a\u5934\u6709\u70b9\u77ee\uff0c\u6709\u4e9b\u9762\u719f\uff0c\u4e4b\u524d\u89c1\u8fc7\u51e0\u6b21\uff0c\u636e\u8bf4\u662f\u6770\u59c6\u7684\u4e00\u4e2a\u8fdc\u623f\u8868\u5f1f\uff0c\u8fd9\u662f\u7b2c\u4e00\u6b21\u968f\u4ed6\u51fa\u6d77\u3002\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u5728\u5417\uff1f\u201d\u4ed6\u95ee\uff0c\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u5730\u79fb\u5f00\u76ee\u5149\uff0c\u4e0d\u6562\u76f4\u89c6\u9b45\u9b54\u7684\u96ea\u767d\u80f8\u53e3\u3002</p><p>\u201c\u6211\u5728\uff0c\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u5916\u9762\u6765\u4e86\u4e00\u4e2a\u4eba\uff0c\u4ed6\u6307\u540d\u9053\u59d3\u60f3\u89c1\u4f60\u3002\u201d\u6c34\u624b\u8bf4\u3002</p><p>\u743c\u6069\u548c\u6885\u83f2\u65af\u5bf9\u89c6\u4e00\u773c\uff0c\u5fc3\u4e2d\u5927\u81f4\u6709\u4e86\u731c\u6d4b\u3002\u201c\u597d\u7684\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u53bb\u770b\u770b\u3002\u201d</p><p>\u7136\u540e\u4ed6\u5728\u6d77\u9762\u4e0a\u770b\u89c1\u4e86\u4e00\u53ea\u5de8\u5927\u7684\u9ed1\u8272\u4e4c\u8d3c\u3002</p><p>\u203b\u203b\u203b</p><p>\u201c\u743c\u6069\xb7\u5170\u5c3c\u65af\u7279\uff1f\u201d\u7528\u4e00\u5f20\u7ea2\u8272\u9ab7\u9ac5\u9762\u5177\u906e\u4f4f\u534a\u8fb9\u8138\u7684\u7537\u4eba\u8bf4\uff0c\u4ed6\u7ad9\u5728\u4e4c\u8d3c\u7684\u80cc\u4e0a\uff0c\u51b7\u51b7\u5730\u51dd\u89c6\u7740\u743c\u6069\uff0c\u201c\u6765\u81ea\u9634\u9b42\u57ce\uff0c\u51c6\u5907\u524d\u5f80\u4e1c\u57df\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u4f60\u662f\u8c01\uff1f\u6211\u4eec\u5f88\u719f\u5417\uff1f</p><p>\u743c\u6069\u8fd8\u5728\u83ab\u540d\u5176\u5999\uff0c\u6885\u83f2\u65af\u5df2\u7ecf\u8ba4\u51fa\u4e86\u5bf9\u65b9\u7684\u8eab\u4efd\u3002\u201c\u590f\u9c81\u5e15\u514b\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u590f\u9c81\u5e15\u514b\uff1f\u201d\u743c\u6069\u4e00\u6014\uff0c\u7acb\u523b\u5728\u8111\u6d77\u4e2d\u641c\u5bfb\u51fa\u76f8\u5173\u8d44\u6599\uff0c\u201c\u90a3\u4e0d\u662f\u6069\u745f\u795e\u738b\u5409\u52d2\u4eca\u7684\u9009\u6c11\u5417\uff1f\u201d</p><p>\u201c\u8fd9\u4f4d\u662f\u6885\u83f2\u65af\u5c0f\u59d0\u5bf9\u5427\uff0c\u5f8b\u6cd5\u4e4b\u795e\u7684\u5723\u6b66\u58eb\uff0c\u6211\u542c\u8bf4\u8fc7\u4f60\u7684\u540d\u5b57\u3002\u2018\u9009\u6c11\u2019\u662f\u4f60\u4eec\u7684\u7ffb\u8bd1\uff0c\u5e76\u4e0d\u51c6\u786e\uff0c\u201d\u590f\u9c81\u5e15\u514b\u8bf4\uff0c\u201c\u6b63\u786e\u7684\u8bf4\u6cd5\u5e94\u8be5\u662f\uff0c\u6211\u662f\u5409\u52d2\u4eca\u965b\u4e0b\u7684\u2018\u4ee3\u884c\u8005\u2019\u2014\u2014\u5f53\u7136\u8fd9\u662f\u4ee5\u524d\u7684\u4e8b\u60c5\u4e86\u3002\u201d</p><p>\u743c\u6069\u4e5f\u77e5\u9053\u4ee3\u884c\u8005\u7684\u610f\u601d\u3002\u8fd9\u4e9b\u65e5\u5b50\uff0c\u5728\u6885\u83f2\u65af\u4e25\u683c\u76d1\u7763\u4e0b\uff0c\u4ed6\u53ef\u662f\u4e1d\u6beb\u6ca1\u6709\u61c8\u6020\uff0c\u6bcf\u5929\u8ba4\u771f\u5b66\u4e60\uff0c\u90a3\u51e0\u5927\u53e0\u4e1c\u57df\u7684\u8d44\u6599\u4ed6\u5df2\u7ecf\u8bfb\u5b8c\u4e86\u5927\u534a\uff0c\u4e0d\u5938\u5f20\u5730\u8bf4\uff0c\u5982\u679c\u56de\u5230\u4e2d\u571f\uff0c\u743c\u6069\u4e5f\u7b97\u662f\u4e2a\u201c\u4e1c\u57df\u5b66\u201d\u4e13\u5bb6\u4e86\u3002</p><p>\u4e1c\u57df\u7684\u795e\u738b\u4eec\u975e\u5e38\u7279\u6b8a\uff0c\u4ed6\u4eec\u4f3c\u795e\u53c8\u975e\u795e\uff0c\u6709\u5f3a\u5927\u7684\u529b\u91cf\uff0c\u5374\u5e76\u4e0d\u80fd\u5168\u77e5\u5168\u80fd\uff0c\u8033\u76ee\u6240\u89c1\u6240\u53ca\uff0c\u4e5f\u4e0d\u6bd4\u51e1\u4eba\u5f3a\u591a\u5c11\uff0c\u8fd9\u5c31\u9700\u8981\u4f9d\u9760\u624b\u4e0b\u7684\u796d\u53f8\u4eec\u5904\u7406\u653f\u52a1\uff0c\u63a7\u5236\u653f\u6743\uff1b\u4e3a\u4e86\u907f\u514d\u796d\u53f8\u4e92\u76f8\u52fe\u7ed3\uff0c\u8150\u5316\u5815\u843d\uff0c\u795e\u738b\u90fd\u4f1a\u9009\u62e9\u6700\u4eb2\u4fe1\u53ef\u9760\u7684\u5c5e\u4e0b\uff0c\u6388\u4e88\u4ed6\u4eec\u7279\u522b\u7684\u80fd\u529b\uff0c\u7531\u4ed6\u4eec\u4ee3\u8868\u795e\u738b\u6765\u56db\u5904\u5de1\u89c6\uff0c\u76d1\u5bdf\u6559\u4f1a\uff0c\u8fd9\u79cd\u4eba\u5c31\u88ab\u79f0\u4e3a\u201c\u4ee3\u884c\u8005\u201d\u3002</p><p>\u901a\u5e38\u6bcf\u4e2a\u795e\u738b\u4f1a\u6709\u4e09\u5230\u4e03\u540d\u4e0d\u7b49\u7684\u4ee3\u884c\u8005\uff0c\u89c6\u5176\u80fd\u529b\u800c\u5b9a\uff0c\u5f3a\u8005\u591a\u4e00\u4e9b\uff0c\u5f31\u8005\u5c11\u4e00\u4e9b\uff0c\u6069\u745f\u795e\u738b\u5409\u52d2\u4eca\u5374\u662f\u4e2a\u7279\u4f8b\uff0c\u4ed6\u53ea\u6709\u4e00\u4f4d\u4ee3\u884c\u8005\uff0c\u800c\u4e14\u4e00\u76f4\u4fdd\u6301\u7740\u8fd9\u4e2a\u8bb0\u5f55\uff0c\u53ea\u6709\u524d\u4efb\u6b7b\u4ea1\uff0c\u4ed6\u624d\u4f1a\u6307\u5b9a\u65b0\u7684\u4ee3\u884c\u8005\u3002\u590f\u9c81\u5e15\u514b\u5c31\u662f\u6700\u8fd1\u4e00\u4f4d\u5409\u52d2\u4eca\u7684\u4ee3\u884c\u8005\uff0c\u7ecf\u5e38\u6234\u7740\u4e00\u4e2a\u906e\u4f4f\u5de6\u534a\u8fb9\u8138\u7684\u7ea2\u8272\u9ab7\u9ac5\u9762\u5177\uff0c\u5728\u4e1c\u57df\u5c5e\u4e8e\u8457\u540d\u4eba\u7269\u4e4b\u4e00\u3002\u4ed6\u539f\u672c\u662f\u4e2a\u5c0f\u9886\u4e3b\uff0c\u5728\u88ab\u5409\u52d2\u4eca\u9009\u4e2d\u540e\uff0c\u4e00\u8dc3\u6210\u4e3a\u6069\u745f\u4e43\u81f3\u4e8e\u4e1c\u57df\u6700\u6709\u6743\u52bf\u7684\u51e1\u4eba\u4e4b\u4e00\u3002\u5341\u516d\u5e74\u524d\uff0c\u5409\u52d2\u4eca\u7a81\u7136\u66b4\u6bd9\uff0c\u590f\u9c81\u5e15\u514b\u5931\u53bb\u9760\u5c71\uff0c\u5883\u9047\u4e00\u843d\u5343\u4e08\uff0c\u5341\u5e74\u524d\u7a81\u7136\u4ece\u81ea\u5df1\u7684\u57ce\u5821\u91cc\u5931\u8e2a\uff0c\u636e\u5c0f\u9053\u6d88\u606f\u8bf4\u4ed6\u662f\u88ab\u7a46\u7f55\u4eba\u523a\u6740\u4e86\u3002\u6ca1\u60f3\u5230\u8fd8\u6ca1\u6b7b\uff0c\u4e0d\u4f46\u6ca1\u6b7b\uff0c\u4f3c\u4e4e\u8fd8\u627e\u5230\u4e86\u65b0\u7684\u8001\u677f\u3002</p><p>\u201c\u5409\u52d2\u4eca\u965b\u4e0b\u5df2\u901d\uff0c\u5982\u4eca\u6211\u4e3a\u5854\u6d1b\u65af\u965b\u4e0b\u6548\u529b\uff0c\u201d\u590f\u9c81\u5e15\u514b\u8bf4\uff0c\u201c\u4e0d\u4e45\u524d\u521a\u521a\u88ab\u965b\u4e0b\u64e2\u5347\u4e3a\u9009\u6c11\uff0c\u7ee7\u4efb\u2018\u98d3\u98ce\u4f7f\u8005\u2019\u4e4b\u4f4d\uff0c\u542c\u8bf4\u662f\u7531\u4e8e\u524d\u4efb\u98d3\u98ce\u4f7f\u5361\u65af\u56fe\u88ab\u4f60\u7ed9\u6740\u4e86\uff1f\u201d</p><p>\u201c\u6ca1\u6709\u6ca1\u6709\uff0c\u201d\u743c\u6069\u8d76\u5feb\u8fa9\u89e3\uff0c\u201c\u98d3\u98ce\u4f7f\u5927\u4eba\u7684\u60c5\u62a5\u53ef\u80fd\u6709\u8bef\uff0c\u5176\u5b9e\u5361\u65af\u56fe\u4e0d\u662f\u6211\u6740\u7684\u3002\u5f53\u65f6\u4ed6\u88ab\u63d0\u5c14\u6559\u4f1a\u7684\u4eba\u56f4\u653b\uff0c\u5df2\u7ecf\u5944\u5944\u4e00\u606f\uff0c\u53ea\u5269\u4e00\u53e3\u6c14\uff0c\u6211\u6070\u597d\u8def\u8fc7\uff0c\u4ed6\u8bf7\u6211\u5e2e\u5fd9\u7ed9\u4ed6\u4e2a\u75db\u5feb\uff0c\u514d\u5f97\u5f53\u4fd8\u864f\u3002\u6211\u5929\u6027\u52a9\u4eba\u4e3a\u4e50\uff0c\u6240\u4ee5\u5c31\u6d3e\u6211\u7684\u52a9\u624b\u4e0a\u53bb\u2014\u2014\u201d</p><p>\u201c\u965b\u4e0b\u7684\u9009\u6c11\u6700\u591a\u53ea\u6709\u4e09\u4e2a\u540d\u989d\uff0c\u73b0\u5728\u96f7\u9706\u3001\u70c8\u7130\u3001\u98d3\u98ce\u4e09\u4f7f\u8005\u90fd\u5df2\u7ecf\u6709\u4e3b\u3002\u82e5\u4e0d\u662f\u5361\u65af\u56fe\u6070\u597d\u6b7b\u4e86\uff0c\u6211\u54ea\u6709\u8fd9\u4e2a\u673a\u4f1a\u9012\u8865\uff0c\u201d\u590f\u9c81\u5e15\u514b\u8bf4\uff0c\u201c\u6240\u4ee5\u6211\u4e00\u76f4\u90fd\u5f88\u611f\u8c22\u90a3\u4e2a\u6740\u6389\u5361\u65af\u56fe\u7684\u4eba\uff0c\u60f3\u8981\u5f53\u9762\u8868\u8fbe\u8c22\u610f\u3002\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u4f60\u592a\u5ba2\u6c14\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5176\u5b9e\u6211\u5f53\u65f6\u8def\u8fc7\uff0c\u4e00\u770b\u662f\u4ed6\uff0c\u5fc3\u60f3\u8fd9\u5bb6\u4f19\u65e0\u6076\u4e0d\u4f5c\u52a3\u8ff9\u7d2f\u7d2f\uff0c\u4e8e\u662f\u679c\u65ad\u62d4\u5200\u628a\u4ed6\u780d\u4e86\uff0c\u4eba\u5934\u73b0\u5728\u8fd8\u5728\u6211\u8fd9\u91cc\u5462\u2014\u2014\u8bdd\u8bf4\u6211\u4e5f\u7b97\u662f\u6709\u70b9\u529f\u52b3\uff0c\u98d3\u98ce\u4f7f\u5927\u4eba\u5982\u679c\u6709\u4ec0\u4e48\u5956\u8d4f\uff0c\u6211\u5c31\u4e0d\u63a8\u8f9e\u4e86\u3002\u201d</p><p>\u201c\u5956\u8d4f\u81ea\u7136\u662f\u6709\u7684\uff0c\u201d\u590f\u9c81\u5e15\u514b\u6ca1\u6709\u88ab\u9762\u5177\u906e\u4f4f\u7684\u534a\u8fb9\u8138\u9732\u51fa\u9634\u6c89\u6c89\u7684\u7b11\u5bb9\uff0c\u201c\u53ea\u8981\u4f60\u7acb\u523b\u81ea\u5bab\uff0c\u518d\u628a\u8eab\u8fb9\u8fd9\u51e0\u4f4d\u5973\u5b50\u732e\u7ed9\u6211\uff0c\u6211\u5c31\u9976\u4f60\u6027\u547d\u3002\u201d</p><p>\u201c\u54a6\uff0c\u4f60\u5bf9\u5973\u4eba\u4e5f\u6709\u5174\u8da3\u5417\uff1f\u201d\u51db\u7684\u5c0f\u8111\u888b\u4ece\u743c\u6069\u8eab\u540e\u5192\u51fa\u6765\uff0c\u201c\u6211\u542c\u8bf4\u5409\u52d2\u4eca\u662f\u4e2a\u540c\u6027\u604b\uff0c\u53ea\u559c\u6b22\u7537\u4eba\uff0c\u4f60\u662f\u4ed6\u7684\u4ee3\u884c\u8005\uff0c\u5e94\u8be5\u4e5f\u662f\u540c\u6027\u604b\u624d\u5bf9\u5427\u3002\u201d</p><p>\u2026\u2026\u4f60\u522b\u7684\u90fd\u4e0d\u8bb0\u5f97\uff0c\u600e\u4e48\u5c31\u8bb0\u5f97\u8fd9\u79cd\u516b\u5366\uff1f</p><p>\u5409\u52d2\u4eca\u7684\u786e\u662f\u6709\u540c\u6027\u604b\u7684\u540d\u58f0\uff0c\u8fd9\u70b9\u5728\u5f88\u591a\u8d44\u6599\u4e0a\u90fd\u6709\u8bb0\u8f7d\u3002\u8fd9\u4e9b\u5929\u6765\uff0c\u743c\u6069\u6bcf\u65e5\u7ffb\u9605\u4e1c\u57df\u7684\u8d44\u6599\uff0c\u51db\u89c9\u5f97\u597d\u5947\uff0c\u4e5f\u8dd1\u8fc7\u6765\u770b\u8fc7\u51e0\u773c\uff0c\u4e0d\u77e5\u4ec0\u4e48\u65f6\u5019\u5c31\u628a\u8fd9\u4e8b\u7ed9\u8bb0\u4f4f\u4e86\u3002\u5979\u8bf4\u8fd9\u8bdd\u5012\u8fd8\u771f\u6ca1\u4ec0\u4e48\u6076\u610f\uff0c\u7eaf\u7cb9\u597d\u5947\u800c\u5df2\uff0c\u5979\u53c8\u4e0d\u6b67\u89c6\u540c\u6027\u604b\u3002\u4f46\u542c\u5728\u590f\u9c81\u5e15\u514b\u8033\u4e2d\uff0c\u8fd9\u5c31\u662f\u6700\u5927\u7684\u7f9e\u8fb1\uff0c\u800c\u4e14\u8fd8\u662f\u5728\u5982\u6b64\u5927\u5ead\u5e7f\u4f17\u4e4b\u4e0b\uff0c\u4ed6\u7684\u534a\u8fb9\u8138\u987f\u65f6\u6da8\u6210\u8840\u7ea2\u8272\uff0c\u548c\u9762\u5177\u51e0\u4e4e\u4e00\u6a21\u4e00\u6837\uff0c\u201c\u4f60\u627e\u6b7b\uff01\u201d</p><p>\u4f34\u968f\u7740\u65b0\u4efb\u5854\u6d1b\u65af\u9009\u6c11\u7684\u6012\u559d\uff0c\u4e00\u9053\u5de8\u5927\u7684\u95ea\u7535\u4ece\u9ad8\u7a7a\u4e2d\u8f70\u7136\u4e0b\u51fb\u3002</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u539f\u672c\u8fd8\u60f3\u4ea4\u6d89\u51e0\u53e5\uff0c\u770b\u770b\u80fd\u4e0d\u80fd\u548c\u5e73\u89e3\u51b3\u95ee\u9898\uff0c\u8c01\u77e5\u9053\u51db\u7a81\u7136\u5192\u51fa\u6765\u653e\u5632\u8bbd\uff0c\u77ac\u95f4\u5bf9\u65b9\u5c31\u773c\u7ea2\u4e86\u3002\u800c\u4e14\u5979\u653e\u5b8c\u5632\u8bbd\u7acb\u523b\u8eb2\u5230\u540e\u9762\uff0c\u773c\u770b\u90a3\u9053\u95ea\u7535\u7b14\u76f4\u5730\u5c31\u671d\u7740\u743c\u6069\u8f70\u4e0b\u6765\u3002</p><p>\u201c\u6ca1\u529e\u6cd5\u3002\u201d</p><p>\u5e78\u597d\u743c\u6069\u65e9\u6709\u51c6\u5907\uff0c\u63d0\u524d\u91ca\u653e\u51fa\u5143\u7d20\u62a4\u76fe\uff0c\u4ed6\u5728\u8fd9\u65b9\u9762\u4e0d\u7b97\u7279\u522b\u7cbe\u901a\uff0c\u4f46\u81ea\u4ece\u5438\u6536\u4e86\u5409\u52d2\u4eca\u7684\u4e00\u90e8\u5206\u795e\u529b\u4e4b\u540e\uff0c\u5e73\u65f6\u4e5f\u5e38\u5e38\u52a0\u4ee5\u953b\u70bc\uff0c\u53d1\u73b0\u5b83\u80fd\u591f\u6781\u5927\u5730\u5f3a\u5316\u6709\u5173\u56db\u5927\u5143\u7d20\u7684\u9b54\u6cd5\uff0c\u65e0\u8bba\u653b\u51fb\u8fd8\u662f\u9632\u5fa1\u3002\u7a76\u5176\u539f\u56e0\uff0c\u5e94\u8be5\u662f\u7531\u4e8e\u5409\u52d2\u4eca\u7684\u795e\u80fd\u4e4b\u4e2d\uff0c\u4e5f\u5305\u62ec\u96f7\u9706\u3001\u98ce\u66b4\u7b49\u9886\u57df\uff0c\u6240\u4ee5\u6709\u6b64\u6548\u679c\u3002\u95ea\u7535\u8f70\u7136\u51fb\u4e0b\uff0c\u88ab\u5143\u7d20\u62a4\u76fe\u963b\u6321\uff0c\u5206\u88c2\u6210\u65e0\u6570\u9053\u5c0f\u7684\u95ea\u7535\uff0c\u56db\u6563\u5f39\u5f00\u3002</p><p>\u590f\u9c81\u5e15\u514b\u539f\u672c\u56e0\u4e3a\u51db\u7684\u4e00\u53e5\u8bdd\u9677\u5165\u72c2\u6012\uff0c\u770b\u89c1\u743c\u6069\u8fd9\u4e00\u9053\u5143\u7d20\u62a4\u76fe\uff0c\u4eff\u4f5b\u53d1\u73b0\u4e86\u4ec0\u4e48\uff0c\u795e\u60c5\u6162\u6162\u53d8\u5f97\u5e73\u9759\u4e0b\u6765\u3002\u201c\u679c\u7136\u662f\u4f60\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u535a\u6c99\u8fe6\u662f\u4f60\u6740\u7684\u5427\uff1f\u201d</p><p>\u535a\u6c99\u8fe6\u662f\u8c01\uff1f</p><p>\u743c\u6069\u832b\u7136\uff0c\u4ed6\u4e8b\u60c5\u591a\uff0c\u8fd8\u771f\u4e0d\u8bb0\u5f97\u8fd9\u4e2a\u540d\u5b57\uff0c\u53ea\u9690\u7ea6\u6709\u90a3\u4e48\u4e00\u70b9\u70b9\u8033\u719f\u7684\u611f\u89c9\uff0c\u5e94\u8be5\u662f\u542c\u8fc7\uff0c\u4f46\u4e5f\u4ec5\u4ec5\u53ea\u662f\u542c\u8fc7\u800c\u5df2\u3002\u201c\u90a3\u4e2a\u5409\u52d2\u4eca\u7684\u82db\u5f8b\u4f8d\u8005\uff0c\u201d\u838e\u73de\u514b\u5728\u80cc\u540e\u63d0\u9192\uff0c\u201c\u5c31\u662f\u5192\u5145\u8fdc\u5c71\u57ce\u57ce\u4e3b\u5f17\u96f7\u65af\u7684\u90a3\u4e2a\u3002\u201d</p><p>\u201c\u54e6\uff0c\u90a3\u4e2a\u80d6\u5b50\u554a\u3002\u201d</p><p>\u743c\u6069\u60f3\u4e86\u8d77\u6765\uff0c\u524d\u6bb5\u65f6\u95f4\uff0c\u4ed6\u5e26\u7740\u73ca\u5609\u3001\u8299\u857e\u72c4\u59d0\u59b9\u548c\u838e\u73de\u514b\u79bb\u5f00\u9634\u9b42\u57ce\uff0c\u53bb\u6b27\u675c\u6797\u57ce\u7684\u8def\u4e0a\uff0c\u9014\u7ecf\u8fdc\u5c71\u57ce\u3002\u8fdc\u5c71\u57ce\u662f\u585e\u5c14\u5728\u6708\u6d77\u5730\u533a\u7684\u4e00\u4e2a\u636e\u70b9\uff0c\u57ce\u4e3b\u540d\u53eb\u5f17\u96f7\u65af\uff0c\u4ee5\u524d\u548c\u743c\u6069\u6709\u6253\u8fc7\u4ea4\u9053\u3002\u743c\u6069\u7b49\u4eba\u62b5\u8fbe\u65f6\uff0c\u6070\u597d\u78b0\u5230\u6563\u4f53\u5c14\u5821\u7684\u519b\u961f\u653b\u57ce\uff0c\u7edf\u5e05\u5c31\u662f\u6b4c\u66e6\u96c5\uff0c\u6885\u83f2\u65af\u7684\u90a3\u4f4d\u5e08\u59d0\uff0c\u56e0\u4e3a\u5723\u6b66\u58eb\u8bd5\u70bc\u88ab\u62d2\uff0c\u4e00\u6012\u4e4b\u4e0b\u53bb\u6295\u5954\u4e86\u73ed\u6069\u3002\u5f17\u96f7\u65af\u5f03\u57ce\u9003\u8dd1\uff0c\u88ab\u6b4c\u66e6\u96c5\u4e00\u8def\u8ffd\u6740\uff0c\u53c8\u9047\u5230\u4e86\u743c\u6069\u3002\u743c\u6069\u5e2e\u4ed6\u6253\u9000\u6b4c\u66e6\u96c5\uff0c\u53d1\u73b0\u4ed6\u4e4b\u6240\u4ee5\u88ab\u8ffd\u6740\uff0c\u662f\u7531\u4e8e\u4e00\u679a\u94a5\u5319\u3002\u6700\u540e\u624d\u77e5\u9053\uff0c\u8fd9\u4e2a\u5f17\u96f7\u65af\u6839\u672c\u5c31\u662f\u4e00\u4e2a\u53eb\u535a\u6c99\u8fe6\u7684\u82db\u5f8b\u4f8d\u8005\u6613\u5bb9\u5047\u626e\u7684\uff0c\u90a3\u679a\u94a5\u5319\u4e43\u662f\u6069\u745f\u795e\u738b\u5409\u52d2\u4eca\u7684\u795e\u529b\u51dd\u805a\uff0c\u53ef\u4ee5\u6253\u5f00\u795e\u738b\u7684\u5b9d\u85cf\u3002\u743c\u6069\u4e8e\u662f\u6740\u4eba\u593a\u5b9d\uff0c\u5c06\u535a\u6c99\u8fe6\u5e72\u6389\uff0c\u94a5\u5319\u5219\u88ab\u4ed6\u76f4\u63a5\u541e\u566c\u4e86\u3002</p><p>\u4ed6\u5176\u5b9e\u538b\u6839\u5c31\u6ca1\u5728\u610f\u8fd9\u4e2a\u6b7b\u8005\u53eb\u4ec0\u4e48\u540d\u5b57\uff0c\u838e\u73de\u514b\u597d\u50cf\u8ddf\u4ed6\u63d0\u8fc7\u4e00\u6b21\uff0c\u4e5f\u6ca1\u653e\u5728\u5fc3\u4e0a\uff0c\u6240\u4ee5\u4e00\u65f6\u60f3\u4e0d\u8d77\u6765\u3002\u5982\u4eca\u65e2\u7136\u60f3\u8d77\u6765\u4e86\uff0c\u4ed6\u6ca1\u6709\u62b5\u8d56\u7684\u610f\u601d\uff0c\u4f46\u4e5f\u4e0d\u60f3\u7acb\u523b\u627f\u8ba4\uff0c\u201c\u6211\u662f\u9047\u5230\u8fc7\u4e00\u4e2a\u53eb\u535a\u6c99\u8fe6\u7684\u4eba\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u96be\u9053\u4ed6\u662f\u4f60\u7684\u624b\u4e0b\uff1f\u201d</p><p>\u743c\u6069\u867d\u7136\u4e0d\u592a\u8bb0\u5f97\u90a3\u4f4d\u82db\u5f8b\u4f8d\u8005\u7684\u540d\u5b57\uff0c\u4f46\u81f3\u5c11\u8bb0\u5f97\u5176\u6765\u5386\uff0c\u8bf4\u662f\u5409\u52d2\u4eca\u7684\u738b\u540e\uff0c\u4e3a\u4e86\u5185\u5e73\u5206\u88c2\uff0c\u5916\u6297\u5f3a\u654c\uff08\u7a46\u7f55\u745e\u5fb7\u7684\u5165\u4fb5\u8005\uff09\uff0c\u8bd5\u56fe\u627e\u5230\u795e\u738b\u7559\u4e0b\u7684\u94a5\u5319\uff0c\u6253\u5f00\u5b9d\u85cf\u3002\u738b\u540e\u901a\u8fc7\u67d0\u79cd\u624b\u6bb5\u5b9a\u4f4d\u5230\u94a5\u5319\u7684\u5927\u81f4\u4e0b\u843d\uff0c\u7136\u540e\u6d3e\u9063\u90a3\u4f4d\u535a\u6c99\u8fe6\u524d\u6765\u8fdc\u5c71\u57ce\u3002\u8fd9\u5e94\u8be5\u5c31\u662f\u8fd1\u4e00\u4e24\u5e74\u5185\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u590f\u9c81\u5e15\u514b\u5219\u662f\u5341\u5e74\u524d\u5c31\u5df2\u7ecf\u4ece\u6069\u745f\u201c\u5931\u8e2a\u201d\uff0c\u4f30\u8ba1\u90a3\u65f6\u5019\u5c31\u5df2\u7ecf\u8f6c\u6295\u5854\u6d1b\u65af\u6559\u4f1a\uff0c\u7406\u5f53\u548c\u535a\u6c99\u8fe6\u6ca1\u4ec0\u4e48\u5173\u7cfb\u5427\u3002</p><p>\u590f\u9c81\u5e15\u514b\u6ca1\u6709\u591a\u505a\u89e3\u91ca\u7684\u610f\u601d\uff0c\u201c\u62ff\u6765\uff01\u201d\u4ed6\u4f4e\u559d\u3002</p><p>\u201c\u4ec0\u4e48\u62ff\u6765\uff1f\u201d\u743c\u6069\u53cd\u95ee\u3002</p><p>\u201c\u94a5\u5319\uff0c\u201d\u590f\u9c81\u5e15\u514b\u8bf4\uff0c\u201c\u795e\u738b\u5b9d\u5e93\u7684\u94a5\u5319\u5728\u4f60\u624b\u91cc\u5bf9\u5427\uff0c\u5426\u5219\u4f60\u600e\u4e48\u53ef\u80fd\u501f\u7528\u5409\u52d2\u4eca\u965b\u4e0b\u7684\u529b\u91cf\u3002\u201d</p><p>\u743c\u6069\u603b\u7b97\u77e5\u9053\u95ee\u9898\u51fa\u5728\u54ea\u91cc\u4e86\u3002\u201c\u62b1\u6b49\uff0c\u94a5\u5319\u5df2\u7ecf\u88ab\u6211\u5403\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6ca1\u529e\u6cd5\u8fd8\u7ed9\u4f60\u3002\u201d</p><p>\u201c\u5403\u4e86\uff1f\u201d\u590f\u9c81\u5e15\u514b\u663e\u7136\u4e0d\u76f8\u4fe1\uff0c\u53ea\u4ee5\u4e3a\u743c\u6069\u5728\u5f00\u73a9\u7b11\u3002</p><p>\u201c\u5bf9\uff0c\u5403\u4e86\uff0c\u9e21\u8089\u5473\uff0c\u560e\u5623\u8106\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5c31\u662f\u5206\u91cf\u5c11\u4e86\u70b9\u3002\u201d</p><p>\u590f\u9c81\u5e15\u514b\u76b1\u7709\uff0c\u7136\u540e\u5c06\u76ee\u5149\u8f6c\u5411\u6885\u83f2\u65af\uff0c\u201c\u4f60\u662f\u5f8b\u6cd5\u4e4b\u795e\u7684\u5723\u6b66\u58eb\u5bf9\u5427\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8fd9\u4e2a\u4eba\u6740\u4eba\u593a\u8d22\uff0c\u800c\u4e14\u62d2\u4e0d\u5f52\u8fd8\uff0c\u663e\u7136\u5df2\u7ecf\u4e25\u91cd\u8fdd\u53cd\u5927\u9646\u901a\u884c\u7684\u6cd5\u5f8b\u548c\u516c\u7406\uff0c\u4f60\u96be\u9053\u80fd\u8896\u624b\u65c1\u89c2\u5417\uff1f\u201d</p><p>\u201c\u9996\u5148\uff0c\u6211\u6ca1\u6709\u6740\u4ed6\u3002\u201d\u62a2\u5728\u6885\u83f2\u65af\u56de\u7b54\u4e4b\u524d\uff0c\u743c\u6069\u8d76\u5fd9\u8bf4\uff0c\u4ed6\u540c\u65f6\u5728\u5fc3\u91cc\u8865\u5145\u4e86\u4e00\u53e5\uff1a\u53ea\u4e0d\u8fc7\u662f\u6307\u4f7f\u838e\u73de\u514b\u53bb\u6740\u7684\u3002</p><p>\u201c\u5176\u6b21\uff0c\u90a3\u4e2a\u535a\u6c99\u8fe6\u539f\u672c\u5c31\u662f\u6740\u4e86\u8fdc\u5c71\u57ce\u7684\u771f\u6b63\u57ce\u4e3b\uff0c\u5192\u540d\u9876\u66ff\u4e0a\u4efb\uff0c\u4e5f\u5373\u662f\u8bf4\u4ed6\u662f\u4e2a\u7a77\u51f6\u6781\u6076\u4e4b\u5f92\uff0c\u900d\u9065\u6cd5\u5916\u4e4b\u8f88\u3002\u6309\u7167\u76ee\u524d\u901a\u884c\u5927\u9646\u7684\u6cd5\u5f8b\u539f\u5219\uff0c\u51fb\u6740\u4e00\u4e2a\u91cd\u7f6a\u9003\u72af\u6709\u529f\u65e0\u7f6a\u3002\u6240\u4ee5\u5373\u4fbf\u6211\u6740\u4e86\u4ed6\uff0c\u4e5f\u53ea\u7b97\u662f\u7ef4\u62a4\u6b63\u4e49\u2014\u2014\u6253\u7740\u6b63\u4e49\u7684\u65d7\u53f7\u6740\u4eba\uff0c\u90a3\u5c31\u4e0d\u53eb\u6740\u4eba\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u77a5\u4e86\u4ed6\u4e00\u773c\u3002</p><p>\u201c\u6700\u540e\uff0c\u6309\u7167\u5927\u9646\u901a\u884c\u7684\u7ee7\u627f\u6cd5\uff0c\u5409\u52d2\u4eca\u65e2\u6b7b\uff0c\u5176\u9057\u4ea7\u7406\u5f53\u7531\u5176\u76f4\u7cfb\u4eb2\u5c5e\u7ee7\u627f\uff0c\u4ed6\u7236\u6bcd\u80af\u5b9a\u662f\u4e0d\u5728\u4e86\uff0c\u4e5f\u6ca1\u542c\u8bf4\u6709\u5b50\u5973\uff0c\u6240\u4ee5\u5c31\u7b97\u8981\u6211\u5f52\u8fd8\u94a5\u5319\uff0c\u4e5f\u662f\u5e94\u8be5\u4ed6\u7684\u738b\u540e\u63d0\u51fa\u6765\u624d\u5bf9\uff0c\u65e0\u5173\u95f2\u6742\u4eba\u7b49\u6ca1\u6709\u8d44\u683c\u8bf4\u4e09\u9053\u56db\u3002\u4f60\u53ef\u80fd\u5e73\u65f6\u4e0d\u592a\u8bfb\u6cd5\u5f8b\uff0c\u6240\u4ee5\u6211\u89e3\u91ca\u4e00\u4e0b\uff0c\u6240\u8c13\u76f4\u7cfb\u4eb2\u5c5e\uff0c\u5c31\u662f\u6307\u7236\u6bcd\u3001\u914d\u5076\u548c\u5b50\u5973\u3002\u6211\u77e5\u9053\u4f60\u662f\u4ed6\u7684\u57fa\u53cb\uff0c\u6216\u8bb8\u4f60\u4eec\u4e4b\u95f4\u4e5f\u6709\u771f\u7231\uff0c\u4f46\u4f60\u4eec\u7684\u5173\u7cfb\u662f\u4e0d\u53d7\u6cd5\u5f8b\u6240\u8ba4\u53ef\u7684\uff0c\u6240\u4ee5\uff0c\u201d\u743c\u6069\u644a\u5f00\u624b\uff0c\u201c\u6211\u65e0\u80fd\u4e3a\u529b\u3002\u201d</p><p>\u590f\u9c81\u5e15\u514b\u51b7\u7b11\uff0c\u4ece\u80cc\u540e\u62d4\u51fa\u4e00\u652f\u9ed1\u8272\u957f\u621f\uff0c\u9065\u9065\u6307\u5411\u6d77\u9762\uff0c\u9ad8\u559d\u4e00\u58f0\uff0c\u201c\u4e4c\u7490\u68ee\uff01\u201d</p><p>\u8fd9\u4e0d\u662f\u5492\u8bed\uff0c\u800c\u662f\u67d0\u79cd\u4e1c\u897f\u7684\u540d\u5b57\u3002\u6d77\u6c34\u5982\u716e\u6cb8\u822c\u7ffb\u817e\u4e2d\uff0c\u4e00\u53ea\u5de8\u5927\u7684\u3001\u51e0\u4e4e\u53ef\u4ee5\u548c\u6d77\u8239\u76f8\u5ab2\u7f8e\u7684\u602a\u517d\u4ece\u6c34\u4e2d\u6d6e\u51fa\u8eab\u5f71\u3002\u5b83\u770b\u8d77\u6765\u50cf\u4e00\u4e2a\u6241\u5e73\u7684\u7403\uff0c\u6216\u8005\u4e00\u53ea\u5408\u8d77\u6765\u7684\u868c\uff0c\u4e24\u4fa7\u957f\u7740\u4e24\u6839\u53c8\u7c97\u53c8\u957f\u7684\u9ed1\u8272\u89e6\u624b\uff0c\u5b9b\u5982\u624b\u81c2\uff0c\u4ed4\u7ec6\u770b\u5176\u5b9e\u662f\u4e24\u6761\u86c7\uff0c\u6709\u5934\u6709\u773c\uff0c\u53e3\u4e2d\u68ee\u68ee\u5410\u7740\u7ea2\u4fe1\u3002\u8fd9\u662f\u4e00\u79cd\u5760\u661f\u6d77\u4e1c\u90e8\u6d77\u57df\u7279\u6709\u7684\u4e00\u79cd\u6d77\u602a\uff0c\u5f53\u5730\u4eba\u79f0\u4e3a\u201c\u897f\u65af\u5854\u201d\uff0c\u4f20\u8bf4\u662f\u6d77\u795e\u7684\u5ba0\u7269\uff0c\u603b\u662f\u5728\u6df1\u6d77\u4e2d\u5b88\u536b\u7740\u6d77\u795e\u7684\u5b9d\u85cf\u3002\u897f\u65af\u5854\u76ae\u7cd9\u8089\u539a\uff0c\u529b\u5927\u65e0\u6bd4\uff0c\u800c\u4e14\u4e24\u4e2a\u86c7\u5934\u90fd\u80fd\u53d1\u51fa\u77f3\u5316\u5c04\u7ebf\uff0c\u662f\u5f88\u96be\u7f20\u7684\u654c\u4eba\uff0c\u4f46\u901a\u5e38\u4e5f\u5f88\u96be\u78b0\u89c1\u2014\u2014\u8fd9\u4e9b\u5728\u6885\u83f2\u65af\u7ed9\u743c\u6069\u7684\u90a3\u4e00\u5806\u8d44\u6599\u91cc\u90fd\u6709\u8be6\u7ec6\u8bb0\u8f7d\uff0c\u743c\u6069\u6628\u5929\u6070\u597d\u770b\u8fc7\uff0c\u6240\u4ee5\u5370\u8c61\u5f88\u6df1\u3002</p><p>\u6d77\u602a\u534a\u6d6e\u51fa\u6d77\u9762\uff0c\u4e24\u53ea\u86c7\u81c2\u4e00\u4e0a\u4e00\u4e0b\u671d\u7740\u6d77\u8239\u88ad\u6765\uff0c\u770b\u5b83\u7684\u67b6\u52bf\uff0c\u660e\u663e\u662f\u8981\u5c06\u8239\u8eab\u7f20\u4f4f\uff0c\u7136\u540e\u76f4\u63a5\u7ffb\u5012\u8fc7\u6765\uff0c\u8fd9\u662f\u897f\u65af\u5854\u6700\u559c\u6b22\u4e5f\u6700\u64c5\u957f\u7684\u653b\u51fb\u65b9\u5f0f\u3002\u8239\u4e0a\u7eb7\u7eb7\u53d1\u5c04\u51fa\u5f29\u7bad\u548c\u6cd5\u672f\uff0c\u4f46\u8fd9\u4e9b\u653b\u51fb\u5bf9\u4e8e\u6d77\u602a\u800c\u8a00\u6839\u672c\u5c31\u662f\u4e0d\u75db\u4e0d\u75d2\uff0c\u6ca1\u6709\u4efb\u4f55\u5a01\u80c1\u3002\u6b63\u5982\u683c\u6597\u6bd4\u8d5b\u8981\u5206\u91cd\u91cf\u7ea7\uff0c\u4f53\u578b\u3001\u91cd\u91cf\u8fd9\u79cd\u4e1c\u897f\uff0c\u4e00\u65e6\u5dee\u8ddd\u5230\u4e00\u5b9a\u5730\u6b65\uff0c\u8db3\u4ee5\u78be\u538b\u4efb\u4f55\u5b9e\u529b\uff0c\u6d77\u602a\u539f\u672c\u5c31\u6bd4\u9646\u5730\u602a\u7269\u5bb9\u6613\u957f\u5f97\u5927\u53ea\uff0c\u897f\u65af\u5854\u5728\u6d77\u602a\u4e2d\u4e5f\u5c5e\u4e8e\u201c\u5de8\u578b\u201d\uff0c\u743c\u6069\u770b\u4e86\u770b\u81ea\u5df1\uff0c\u89c9\u5f97\u6050\u6015\u53ea\u52c9\u5f3a\u591f\u585e\u5b83\u7684\u7259\u7f1d\uff0c\u4e8e\u662f\u4ed6\u5f53\u673a\u7acb\u65ad\uff0c\u201c\u86ee\u725b\u4e4b\u529b\u201d\u77ac\u95f4\u53d1\u52a8\uff0c\u4e00\u628a\u6293\u8d77\u8db4\u5728\u8239\u8237\u8fb9\u770b\u70ed\u95f9\u7684\u5c0f\u5973\u5deb\u629b\u4e86\u51fa\u53bb\u3002</p><p>\u201c\u4e0a\u5427\uff0c\u76ae\u5361\u4e18\uff01\u54e6\u4e0d\uff0c\u5c0f\u706b\u9f99\uff01\u201d</p><p>\u51db\u4e0d\u6ee1\u5730\u561f\u56d4\u4e86\u4e00\u53e5\uff0c\u6574\u4e2a\u4eba\u5728\u534a\u7a7a\u4e2d\u718a\u718a\u71c3\u70e7\u8d77\u6765\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\uff0c\u5c11\u5973\u6d88\u5931\u4e86\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u4e00\u5934\u9cde\u7532\u4eae\u95ea\u95ea\u7684\u7ea2\u9f99\uff0c\u867d\u7136\u4f53\u578b\u4e0d\u5982\u6d77\u602a\u5e9e\u5927\uff0c\u4f46\u6c14\u52bf\u8fdc\u8fdc\u8fc7\u4e4b\u3002\u6d77\u602a\u611f\u53d7\u5230\u4e86\u5a01\u80c1\uff0c\u987e\u4e0d\u4e0a\u53bb\u653b\u51fb\u8239\u53ea\uff0c\u4e24\u53ea\u86c7\u81c2\u8f6c\u4e86\u4e2a\u5f2f\uff0c\u671d\u7740\u7ea2\u9f99\u5377\u53bb\u3002</p><p>\u7ea2\u9f99\u55b7\u5410\u51fa\u9525\u5f62\u7684\u70c8\u7130\uff0c\u4e0e\u6d77\u602a\u6218\u6210\u4e00\u56e2\u3002\u590f\u9c81\u5e15\u514b\u663e\u7136\u60c5\u62a5\u5de5\u4f5c\u6ca1\u6709\u505a\u5230\u5bb6\uff0c\u4ed6\u53ea\u77e5\u9053\u743c\u6069\u548c\u6885\u83f2\u65af\uff0c\u538b\u6839\u6ca1\u6599\u5230\u5bf9\u65b9\u8fd8\u6f5c\u4f0f\u7740\u4e00\u5934\u7ea2\u9f99\uff0c\u800c\u4e14\u8fd9\u53ea\u7ea2\u9f99\u867d\u7136\u4f53\u578b\u4e0d\u7b97\u5927\uff0c\u6309\u7167\u9f99\u7684\u6807\u51c6\u8fde\u201c\u9752\u5e74\u201d\u90fd\u7b97\u4e0d\u4e0a\uff08\u9f99\u7684\u4f53\u578b\u4e0e\u5e74\u9f84\u76f4\u63a5\u6302\u94a9\uff09\uff0c\u529b\u91cf\u5374\u6781\u4e3a\u5f3a\u52b2\uff0c\u4e00\u9053\u9f99\u606f\u55b7\u51fa\u6765\uff0c\u6d77\u602a\u7684\u8eaf\u4f53\u4e0a\u7acb\u523b\u5c31\u88ab\u70e4\u7126\u4e86\u4e00\u5927\u7247\uff0c\u518d\u591a\u88ab\u55b7\u4e2d\u51e0\u6b21\uff0c\u53ea\u6015\u5c31\u8981\u53d8\u6210\u70e4\u58a8\u9c7c\u4e86\u3002\u6d77\u602a\u7684\u4e24\u4e2a\u86c7\u5934\u4e0d\u65ad\u53d1\u5c04\u77f3\u5316\u5c04\u7ebf\uff0c\u5374\u90fd\u88ab\u7ea2\u9f99\u7075\u5de7\u5730\u8eb2\u4e86\u5f00\u53bb\uff0c\u5f92\u52b3\u65e0\u529f\u3002</p><p>\u4ed6\u6325\u52a8\u9ed1\u8272\u957f\u621f\uff0c\u6b63\u8981\u4e0a\u524d\uff0c\u5374\u89c1\u4e00\u4f4d\u80cc\u540e\u5f20\u5f00\u900f\u660e\u5149\u7ffc\u7684\u91d1\u53d1\u94f6\u7532\u5c11\u5973\u5982\u95ea\u7535\u822c\u5c04\u6765\uff0c\u94f6\u5251\u4e0a\u5149\u8292\u56db\u5c04\uff0c\u7eb5\u6a2a\u65a9\u51fb\uff0c\u77ed\u77ed\u51e0\u4e2a\u56de\u5408\u5c31\u5c06\u4ed6\u538b\u5236\u4f4f\u4e86\u3002\u590f\u9c81\u5e15\u514b\u5728\u6210\u4e3a\u5409\u52d2\u4eca\u7684\u201c\u4ee3\u8a00\u4eba\u201d\u4e4b\u524d\uff0c\u5c31\u662f\u6069\u745f\u6709\u540d\u7684\u52c7\u58eb\uff0c\u51e1\u4eba\u4e4b\u4e2d\u4ece\u6765\u672a\u9022\u654c\u624b\uff0c\u5982\u4eca\u5374\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\u7684\u9762\uff0c\u88ab\u4e00\u4e2a\u5341\u516d\u4e03\u5c81\u7684\u5c11\u5973\u6253\u5f97\u8fde\u8fde\u540e\u9000\uff0c\u4e0d\u7531\u5f97\u65e2\u60ca\u4e14\u6012\uff0c\u7b80\u76f4\u6709\u4e9b\u4e0d\u6562\u7f6e\u4fe1\u3002</p><p>\u201c\u592a\u5927\u610f\u4e86\u3002\u201d\u4ed6\u60f3\u3002</p><p>\u4e00\u4e2a\u6708\u524d\uff0c\u201c\u98d3\u98ce\u4f7f\u8005\u201d\u5361\u65af\u56fe\u5728\u5854\u745f\u8c37\u8eab\u4ea1\uff0c\u8fd9\u4e2a\u6d88\u606f\u4f20\u56de\u672c\u90e8\u540e\uff0c\u5854\u6d1b\u65af\u6559\u4f1a\u5148\u662f\u4e00\u7247\u54d7\u7136\uff0c\u5f88\u5feb\u5c31\u6062\u590d\u5e73\u9759\u3002\u5e76\u6ca1\u6709\u591a\u5c11\u4eba\u60f3\u7740\u8981\u53bb\u7ed9\u4ed6\u62a5\u4ec7\uff0c\u5927\u5bb6\u8be5\u5e72\u561b\u8fd8\u662f\u7ee7\u7eed\u5e72\u561b\uff0c\u5c11\u6570\u9ad8\u5c42\u66f4\u662f\u628a\u7cbe\u529b\u5168\u90fd\u653e\u5728\u4e92\u76f8\u653b\u51fb\u3001\u53d6\u60a6\u795e\u7957\u4e0a\uff0c\u529b\u56fe\u8ba9\u81ea\u5df1\u63a5\u624b\u5361\u65af\u56fe\u7559\u4e0b\u7684\u7a7a\u7f3a\uff0c\u8c01\u8fd8\u987e\u5f97\u4e0a\u5176\u4ed6\u3002\u590f\u9c81\u5e15\u514b\u52a0\u5165\u5854\u6d1b\u65af\u6559\u4f1a\u7684\u65f6\u95f4\u5e76\u4e0d\u7b97\u5f88\u4e45\uff0c\u867d\u8bf4\u88ab\u4efb\u547d\u4e3a\u4e1c\u57df\u5730\u533a\u7684\u8d1f\u8d23\u4eba\uff0c\u4e5f\u6302\u4e86\u4e2a\u4e3b\u6559\u5934\u8854\uff0c\u4f46\u4e00\u76f4\u6ca1\u6709\u8fdb\u5165\u6700\u6838\u5fc3\u7684\u51b3\u7b56\u5c42\uff0c\u5730\u4f4d\u6392\u5728\u4ed6\u524d\u9762\u7684\u4eba\u6709\u5341\u4e03\u516b\u4e2a\u3002\u8c01\u6599\u5230\u534a\u4e2a\u6708\u524d\uff0c\u98ce\u66b4\u4e4b\u795e\u7a81\u7136\u964d\u4e0b\u795e\u8c15\uff0c\u4efb\u547d\u590f\u9c81\u5e15\u514b\u4e3a\u65b0\u4efb\u98d3\u98ce\u4f7f\uff0c\u6210\u4e3a\u4e09\u9009\u6c11\u4e4b\u4e00\uff0c\u5e76\u4e14\u5168\u9762\u63a5\u638c\u5361\u65af\u56fe\u751f\u524d\u7684\u5168\u90e8\u6743\u529b\u3002\u5361\u65af\u56fe\u662f\u5927\u6d77\u76d7\u51fa\u8eab\uff0c\u4e00\u76f4\u81f4\u529b\u4e8e\u6574\u5408\u3001\u63a7\u5236\u5760\u661f\u6d77\u6cbf\u5cb8\u7684\u6d77\u6e2f\uff0c\u5176\u6210\u6548\u663e\u8457\uff0c\u5982\u4eca\u90fd\u843d\u5230\u4e86\u590f\u9c81\u5e15\u514b\u624b\u4e2d\u3002</p><p>\u590f\u9c81\u5e15\u514b\u867d\u8bf4\u5728\u4e1c\u57df\u7b97\u662f\u4e00\u53f7\u4eba\u7269\uff0c\u4f46\u4e1c\u57df\u539f\u672c\u5c31\u662f\u4e2a\u975e\u5e38\u95ed\u585e\u7684\u5730\u65b9\uff0c\u522b\u8bf4\u533a\u533a\u4e00\u4e2a\u51e1\u4eba\uff0c\u5c31\u7b97\u662f\u795e\u738b\uff0c\u5728\u4e2d\u571f\u5927\u9646\u7684\u4eba\u770b\u6765\u4e5f\u672a\u89c1\u5f97\u5982\u4f55\u9ad8\u8d35\u3002\u8fd9\u6837\u4e00\u4e2a\u4eba\u9aa4\u7136\u7a7a\u964d\u6210\u4e3a\u81ea\u5df1\u4e0a\u53f8\uff0c\u90a3\u4e9b\u5361\u65af\u56fe\u7684\u65e7\u90e8\u5f53\u7136\u4e2a\u4e2a\u4e0d\u670d\u3002\u590f\u9c81\u5e15\u514b\u521d\u6765\u4e4d\u5230\uff0c\u4e5f\u6307\u6325\u4e0d\u52a8\uff0c\u4ed6\u5728\u4e1c\u57df\u6a2a\u884c\u60ef\u4e86\uff0c\u539f\u672c\u5c31\u4e0d\u591a\u7684\u653f\u6cbb\u624b\u8155\u65e9\u5c31\u5fd8\u5f97\u4e00\u5e72\u4e8c\u51c0\uff0c\u601d\u8003\u95ee\u9898\u5bb9\u6613\u76f4\u6765\u76f4\u53bb\uff0c\u4fbf\u5bfb\u601d\u7740\u8981\u505a\u4e00\u4ef6\u5927\u4e8b\uff0c\u7acb\u4e00\u4ef6\u529f\u52b3\uff0c\u65b9\u53ef\u4ee5\u663e\u793a\u81ea\u5df1\u672c\u9886\uff0c\u6151\u670d\u8fd9\u4e9b\u624b\u4e0b\u3002\u8fd8\u6ca1\u60f3\u597d\u8981\u505a\u4ec0\u4e48\uff0c\u8fd9\u4e00\u65e5\u7a81\u7136\u6709\u624b\u4e0b\u56de\u62a5\uff0c\u8bf4\u662f\u5728\u6d77\u4e0a\u53d1\u73b0\u4e86\u7ebf\u7d22\u3002\u539f\u6765\u90a3\u4e00\u65e5\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u51fb\u6740\u9c7c\u4eba\u796d\u53f8\u65f6\uff0c\u52a8\u7528\u4e86\u4e00\u70b9\u5409\u52d2\u4eca\u7684\u795e\u529b\uff0c\u663e\u9732\u4e86\u75d5\u8ff9\u3002</p><p>\u9c7c\u4eba\u7684\u667a\u529b\u5e76\u4e0d\u9ad8\uff0c\u4f46\u6709\u4e00\u4e9b\u5929\u8d4b\u7684\u76f4\u89c9\u975e\u5e38\u53d1\u8fbe\uff0c\u5b83\u4eec\u5f53\u7136\u8fa8\u8ba4\u4e0d\u51fa\u4ec0\u4e48\u5409\u52d2\u4eca\u7684\u795e\u529b\uff0c\u5374\u672c\u80fd\u5730\u611f\u89c9\u743c\u6069\u6240\u4f7f\u7528\u7684\u529b\u91cf\uff0c\u548c\u65cf\u4e2d\u796d\u53f8\u81ea\u72ec\u773c\u795e\u5904\u83b7\u5f97\u7684\u529b\u91cf\uff0c\u65e2\u76f8\u4f3c\u53c8\u4e0d\u540c\uff0c\u5b58\u5728\u975e\u5e38\u5fae\u5999\u7684\u533a\u522b\u3002\u590f\u9c81\u5e15\u514b\u4ee5\u524d\u662f\u5409\u52d2\u4eca\u7684\u4ee3\u8a00\u4eba\uff0c\u73b0\u5728\u662f\u5854\u6d1b\u65af\u7684\u4fe1\u5f92\uff0c\u8eab\u517c\u4e24\u5bb6\uff0c\u4e00\u542c\u5c31\u89c9\u5f97\u4e0d\u5bf9\u52b2\uff0c\u53c8\u51e0\u6b21\u6d3e\u51fa\u9c7c\u4eba\u6765\u8bd5\u63a2\uff0c\u540c\u65f6\u591a\u65b9\u641c\u96c6\u5230\u4e00\u4e9b\u4fe1\u606f\uff0c\u4e24\u76f8\u5370\u8bc1\uff0c\u5927\u81f4\u5c31\u6709\u4e86\u7b54\u6848\u3002\u590f\u9c81\u5e15\u514b\u8c03\u52a8\u4e0d\u4e86\u90a3\u4e9b\u6840\u9a9c\u4e0d\u9a6f\u7684\u624b\u4e0b\uff0c\u4ed6\u81ea\u89c9\u51ed\u81ea\u5df1\u7684\u672c\u9886\uff0c\u53c8\u6709\u897f\u65af\u5854\u6d77\u602a\u76f8\u52a9\uff08\u897f\u65af\u5854\u662f\u98ce\u66b4\u795e\u7684\u4e00\u4e2a\u5c0f\u5ba0\u7269\uff0c\u53ea\u542c\u547d\u4e8e\u201c\u98d3\u98ce\u4f7f\u201d\uff09\uff0c\u800c\u4e14\u5360\u636e\u6d77\u4e0a\u7684\u5730\u5229\uff0c\u8981\u5bf9\u4ed8\u743c\u6069\u4e0d\u96be\u3002\u8c01\u77e5\u654c\u4eba\u5c45\u7136\u8fd9\u822c\u68d8\u624b\uff0c\u5012\u662f\u5927\u5927\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\u3002</p><p>\u590f\u9c81\u5e15\u514b\u5e76\u4e0d\u662f\u4e2a\u91cd\u89c6\u6240\u8c13\u6218\u58eb\u8363\u8a89\u7684\u4eba\uff0c\u4f46\u4ed6\u4e5f\u4e0d\u4f1a\u5982\u6b64\u5bb9\u6613\u5c31\u8ba4\u8f93\uff0c\u9ed1\u8272\u7684\u957f\u621f\u8fce\u51fb\u7740\u94f6\u5251\uff0c\u6bcf\u4e00\u6b21\u78b0\u649e\uff0c\u957f\u621f\u4e0a\u90fd\u4f1a\u8ff8\u51fa\u6697\u7ea2\u8272\u7684\u95ea\u7535\uff0c\u5b83\u4eec\u5f88\u96be\u76f4\u63a5\u5bf9\u6885\u83f2\u65af\u9020\u6210\u4f24\u5bb3\uff0c\u4f46\u4e00\u6b21\u53c8\u4e00\u6b21\u5730\u79ef\u7d2f\u8d77\u6765\uff0c\u5374\u53ef\u4ee5\u660e\u663e\u5730\u8ba9\u5c11\u5973\u7684\u52a8\u4f5c\u53d8\u5f97\u8fdf\u949d\u3002\u6885\u83f2\u65af\u5fae\u5fae\u76b1\u7709\uff0c\u632f\u52a8\u5149\u7ffc\u5f80\u540e\u9000\u4e86\u51e0\u6b65\uff0c\u7a0d\u7a0d\u62c9\u5f00\u8ddd\u79bb\uff0c\u540c\u65f6\u6d88\u89e3\u95ea\u7535\u7684\u9ebb\u75f9\u529b\u91cf\u3002\u590f\u9c81\u5e15\u514b\u4e58\u6b64\u673a\u4f1a\uff0c\u624b\u4e2d\u53d1\u51fa\u4e00\u675f\u9ed1\u8272\u7684\u5149\uff0c\u6ce8\u5165\u6d77\u602a\u7684\u4f53\u5185\u3002\u6d77\u602a\u731b\u7136\u53d1\u51fa\u5636\u54d1\u7684\u543c\u58f0\uff0c\u5b83\u5706\u6eda\u6eda\u7684\u8eaf\u4f53\u4e0a\u5239\u90a3\u95f4\u6d6e\u73b0\u51fa\u4e0a\u767e\u4e2a\u5706\u5f62\u5149\u6591\uff0c\u6bcf\u4e2a\u5149\u6591\u4e2d\u90fd\u53d1\u5c04\u51fa\u4e00\u9053\u7ea2\u8272\u5c04\u7ebf\uff0c\u5bc6\u5bc6\u9ebb\u9ebb\u5730\u5c04\u5411\u7ea2\u9f99\u3002\u7ea2\u9f99\u867d\u7136\u4f53\u578b\u6bd4\u6d77\u602a\u5c0f\u5f97\u591a\uff0c\u4e5f\u7075\u5de7\u5f97\u591a\uff0c\u4f46\u8fd9\u6837\u5bc6\u96c6\u7684\u5927\u8303\u56f4\u653b\u51fb\u8fd8\u662f\u6ca1\u529e\u6cd5\u8eb2\u8fc7\uff0c\u5979\u88ab\u5c04\u4e2d\u4e86\uff0c\u575a\u97e7\u7684\u9cde\u7532\u62b5\u6321\u4e86\u6240\u6709\u4f24\u5bb3\uff0c\u4f46\u5374\u65e0\u6cd5\u5b8c\u5168\u6d88\u89e3\u5c04\u7ebf\u4e2d\u7684\u9ebb\u75f9\u529b\u91cf\uff0c\u7ea2\u9f99\u4ece\u7a7a\u4e2d\u6454\u843d\u4e0b\u6765\uff0c\u7830\u5730\u4e00\u58f0\u5760\u5165\u6d77\u4e2d\uff0c\u6e85\u8d77\u5de8\u5927\u7684\u6c34\u82b1\u3002</p><p>\u201c\u51db\uff01\u201d</p><p>\u743c\u6069\u5927\u5403\u4e00\u60ca\uff0c\u5982\u679c\u51db\u771f\u6709\u4ec0\u4e48\u95ea\u5931\uff0c\u6885\u83f2\u65af\u80af\u5b9a\u8981\u627e\u4ed6\u7b97\u8d26\u3002\u5deb\u5e08\u77ac\u95f4\u4f20\u9001\u5230\u6d77\u9762\u4e0a\uff0c\u5feb\u901f\u7ed9\u81ea\u5df1\u65bd\u52a0\u4e86\u4e00\u4e2a\u6c34\u4e0b\u547c\u5438\u7684\u6cd5\u672f\uff0c\u6b63\u51c6\u5907\u6f5c\u5165\u6d77\u4e2d\u53bb\u67e5\u770b\uff0c\u4e00\u53ea\u5de8\u5927\u7684\u7ea2\u9f99\u8111\u888b\u4ece\u6c34\u4e0b\u5347\u8d77\uff0c\u6b63\u597d\u5c06\u4ed6\u6258\u5728\u989d\u5934\u4e0a\uff0c\u201c\u8ba9\u5f00\u70b9\uff0c\u201d\u51db\u4e0d\u9ad8\u5174\u5730\u8bf4\uff0c\u201c\u4f60\u8e29\u5230\u6211\u7684\u5218\u6d77\u4e86\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u743c\u6069\u8fde\u5fd9\u79fb\u5f00\uff0c\u7ea2\u9f99\u4ece\u6c34\u4e0b\u98de\u8d77\uff0c\u6012\u6c14\u51b2\u51b2\u5730\u518d\u5ea6\u671d\u6d77\u602a\u98de\u53bb\u3002\u6d77\u602a\u5168\u8eab\u5149\u8292\u95ea\u70c1\uff0c\u773c\u770b\u5c31\u8981\u518d\u5ea6\u53d1\u5c04\u7ea2\u8272\u5c04\u7ebf\uff0c\u7a81\u7136\u5b83\u660e\u663e\u5730\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u4e24\u4e2a\u86c7\u5934\u50cf\u662f\u7761\u7740\u4e86\u4f3c\u7684\uff0c\u8f6f\u7ef5\u7ef5\u5730\u5782\u4e86\u4e0b\u53bb\uff0c\u8eab\u4e0a\u7684\u5149\u6591\u4e5f\u6e10\u6e10\u53d8\u5f97\u9eef\u6de1\u3001\u6d88\u5931\u3002</p><p>\u6240\u6709\u4eba\u5bf9\u6b64\u90fd\u5341\u5206\u60ca\u8bb6\uff0c\u4e0d\u77e5\u9053\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\uff0c\u590f\u9c81\u5e15\u514b\u7528\u529b\u903c\u9000\u6885\u83f2\u65af\uff0c\u770b\u4e86\u770b\u6d77\u602a\uff0c\u53c8\u671d\u8239\u4e0a\u770b\u4e86\u4e00\u773c\uff0c\u4ed6\u6325\u52a8\u957f\u621f\uff0c\u81ea\u4e0a\u800c\u4e0b\u5730\u5728\u7a7a\u6c14\u4e2d\u5212\u4e86\u4e00\u9053\u3002\u8f6c\u773c\u4e4b\u95f4\uff0c\u539f\u672c\u5c1a\u79f0\u98ce\u548c\u65e5\u4e3d\u7684\u6d77\u9762\u4e0a\u9661\u7136\u72c2\u98ce\u5927\u4f5c\uff0c\u6380\u8d77\u5de8\u6d6a\u60ca\u6d9b\uff0c\u9ad8\u7a7a\u4e2d\u4e4c\u4e91\u5bc6\u5e03\uff0c\u906e\u5929\u853d\u65e5\uff0c\u80fd\u89c1\u5ea6\u53d8\u5f97\u6781\u4f4e\u3002\u8fd9\u79cd\u60c5\u5f62\u5e76\u6ca1\u6709\u6301\u7eed\u592a\u4e45\uff0c\u5927\u7ea6\u56db\u4e94\u5206\u949f\u540e\uff0c\u4e00\u5207\u6d88\u6563\uff0c\u590f\u9c81\u5e15\u514b\u548c\u6d77\u602a\u5df2\u7ecf\u6d88\u5931\u4e0d\u89c1\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u51db\u56de\u5230\u8239\u4e0a\uff0c\u5979\u6062\u590d\u4e86\u4eba\u7c7b\u5c11\u5973\u7684\u5f62\u6001\uff0c\u7528\u706b\u7130\u5c06\u81ea\u5df1\u7684\u88f8\u4f53\u5305\u88f9\uff0c\u51b2\u8fdb\u623f\u95f4\uff0c\u8fc7\u4e86\u51e0\u5206\u949f\u540e\uff0c\u5979\u7a7f\u597d\u8863\u670d\u51b2\u4e86\u51fa\u6765\u3002\u201c\u743c\u6069\uff01\u201d\u5979\u4e0d\u9ad8\u5174\u5730\u8bf4\uff0c\u201c\u4f60\u521a\u624d\u592a\u8fc7\u5206\u4e86\uff01\u201d</p><p>\u201c\u5bf9\u4e0d\u8d77\u5bf9\u4e0d\u8d77\uff0c\u201d\u743c\u6069\u9053\u6b49\uff0c\u201c\u6211\u53ea\u662f\u7a81\u7136\u60f3\u8d77\u4e00\u4e2a\u5f88\u597d\u73a9\u7684\u6e38\u620f\uff0c\u4e00\u65f6\u6000\u5ff5\uff0c\u6240\u4ee5\u5c31\u8bd5\u4e86\u4e00\u4e0b\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u6e38\u620f\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u53eb\u53e3\u888b\u5996\u602a\uff0c\u53c8\u53eb\u795e\u5947\u5b9d\u8d1d\uff0c\u5f88\u6709\u8da3\u7684\u3002\u91cc\u9762\u6709\u53ea\u653e\u7535\u8001\u9f20\uff0c\u5c31\u957f\u8fd9\u6837\uff0c\u201d\u743c\u6069\u51ed\u8bb0\u5fc6\u7528\u5e7b\u672f\u52fe\u52d2\u51fa\u6765\uff0c\u51dd\u6210\u5b9e\u4f53\uff0c\u201c\u53ef\u7231\u5427\u3002\u201d</p><p>\u201c\u54c7\uff0c\u597d\u53ef\u7231\uff01\u201d\u51db\u4e00\u628a\u62a2\u4e86\u8fc7\u6765\uff0c\u5de6\u770b\u53f3\u770b\u7231\u4e0d\u91ca\u624b\uff0c\u201c\u5b83\u53eb\u4ec0\u4e48\u540d\u5b57\uff1f\u201d</p><p>\u201c\u76ae\u5361\u4e18\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5b83\u6bcf\u6b21\u51fa\u573a\u90fd\u6709\u4e00\u6bb5\u53f0\u8bcd\uff0c\u8d85\u7ea7\u7ecf\u5178\u7684\uff0c\u6211\u80cc\u7ed9\u4f60\u542c\uff1a\u65e2\u7136\u4f60\u8bda\u5fc3\u8bda\u610f\u5730\u95ee\u4e86\uff0c\u90a3\u6211\u5c31\u5927\u53d1\u6148\u60b2\u5730\u544a\u8bc9\u4f60\uff0c\u4e3a\u4e86\u9632\u6b62\u4e16\u754c\u88ab\u7834\u574f\u2014\u2014\u201d</p><p>\u201c\u55ef\u55ef\uff01\u201d\u6885\u83f2\u65af\u6253\u65ad\u4e86\u8fd9\u4e24\u4e2a\u4e0d\u9760\u8c31\u7684\u5bb6\u4f19\uff0c\u201c\u76ae\u5361\u4e18\u4ec0\u4e48\u7684\u665a\u70b9\u518d\u8bf4\uff0c\u73b0\u5728\u5728\u5f00\u4f1a\u5462\u3002\u201d</p><p>\u201c\u54e6\uff0c\u597d\u3002\u201d</p><p>\u51db\u8fd8\u662f\u5f88\u542c\u6885\u83f2\u65af\u7684\u8bdd\uff0c\u4e56\u4e56\u5730\u627e\u4e86\u628a\u6905\u5b50\u5750\u4e0b\u6765\uff0c\u5c06\u76ae\u5361\u4e18\u653e\u5728\u8eab\u540e\u3002</p><p>\u6b64\u524d\u5927\u5bb6\u6b63\u5728\u5f00\u4f1a\uff0c\u7ed3\u679c\u521a\u5f00\u4e86\u4e2a\u5934\u5c31\u88ab\u590f\u9c81\u5e15\u514b\u6253\u65ad\u4e86\uff0c\u73b0\u5728\u7ee7\u7eed\u3002\u4e0d\u8fc7\u4e0e\u4f1a\u8005\u6709\u70b9\u5c0f\u5c0f\u7684\u53d8\u5316\uff0c\u589e\u52a0\u4e86\u4e00\u4e2a\u4eba\u2014\u2014\u743c\u6069\u8003\u8651\u518d\u4e09\uff0c\u89c9\u5f97\u51db\u8bf4\u5f97\u4e5f\u4e0d\u65e0\u9053\u7406\uff0c\u628a\u7ef4\u82e5\u62c9\u6392\u65a5\u5728\u5916\u7684\u786e\u4e0d\u592a\u5408\u9002\u3002\u4ed6\u672c\u6765\u7684\u987e\u8651\uff0c\u662f\u6015\u7ef4\u82e5\u62c9\u7684\u5b58\u5728\u4f1a\u8ba9\u73ca\u5609\u3001\u6885\u83f2\u65af\u4e0d\u9ad8\u5174\uff0c\u4f46\u73b0\u5728\u60f3\u60f3\uff0c\u53cd\u6b63\u5df2\u7ecf\u662f\u5927\u5bb6\u90fd\u5fc3\u7167\u4e0d\u5ba3\u7684\u4e8b\u60c5\uff0c\u63a9\u8033\u76d7\u94c3\u53c8\u6709\u4ec0\u4e48\u610f\u4e49\u3002\u6240\u4ee5\u6700\u7ec8\u8fd8\u662f\u9080\u8bf7\u7ef4\u82e5\u62c9\u4e00\u8d77\u53c2\u52a0\u4f1a\u8bae\uff0c\u4f20\u9053\u5deb\u5e08\u5bf9\u6b64\u65e0\u53ef\u65e0\u4e0d\u53ef\uff0c\u5230\u573a\u540e\u548c\u6240\u6709\u4eba\u7b80\u5355\u5730\u6253\u4e86\u4e2a\u62db\u547c\uff0c\u4fbf\u5728\u89d2\u843d\u91cc\u627e\u4e86\u4e2a\u4f4d\u7f6e\u5750\u4e0b\u6765\uff0c\u4eff\u4f5b\u5168\u65e0\u5b58\u5728\u611f\u3002</p><p>\u4f1a\u8bae\u7684\u7b2c\u4e00\u9879\u5185\u5bb9\uff0c\u662f\u743c\u6069\u5411\u5927\u5bb6\u6b63\u5f0f\u901a\u62a5\u6b64\u884c\u7684\u76ee\u7684\u5730\uff0c\u5373\u662f\u201c\u7edd\u5883\u4e1c\u57df\u201d\u3002\u5bf9\u6b64\u6240\u6709\u4eba\u90fd\u6ca1\u4ec0\u4e48\u610f\u89c1\uff0c\u53cd\u6b63\u90fd\u5df2\u7ecf\u5728\u534a\u8def\u4e0a\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff0c\u76f4\u63a5\u901a\u8fc7\u3002\u7b2c\u4e8c\u9879\u5185\u5bb9\uff0c\u5219\u662f\u901a\u62a5\u6709\u5173\u4e1c\u57df\u7684\u5404\u79cd\u8d44\u6599\uff0c\u8ba9\u5927\u5bb6\u6709\u6240\u4e86\u89e3\u3002\u7531\u743c\u6069\u8d1f\u8d23\u4e3b\u8bb2\uff0c\u6885\u83f2\u65af\u5728\u65c1\u8fb9\u8865\u5145\u3002</p><p>\u6240\u8c13\u4e1c\u57df\uff0c\u5b9e\u9645\u4e0a\u5e76\u4e0d\u662f\u4e00\u4e2a\u4e25\u683c\u800c\u6e05\u6670\u7684\u5730\u7406\u6982\u5ff5\uff0c\u5728\u4e0d\u540c\u4eba\u3001\u4e0d\u540c\u65f6\u4ee3\u7684\u8ba4\u77e5\u4e2d\uff0c\u5b83\u7684\u8303\u56f4\u662f\u4e0d\u5b8c\u5168\u4e00\u81f4\u7684\u3002\u53c2\u7167\u76ee\u524d\u6700\u901a\u884c\u7684\u8bf4\u6cd5\uff0c\u4e1c\u57df\u662f\u6307\u5760\u661f\u6d77\u4e1c\u5357\u65b9\u7684\u4e00\u5904\u571f\u5730\uff0c\u5b83\u5e45\u5458\u8fbd\u9614\uff0c\u5317\u8fb9\u4e0e\u83b1\u745f\u66fc\u63a5\u58e4\uff0c\u5357\u90e8\u4e0e\u590f\u4e9a\u6bd7\u90bb\uff0c\u4e1c\u81f3\u7d2b\u5bb8\u6c99\u6f20\u548c\u661f\u4e4b\u6e56\uff0c\u897f\u81f3\u963f\u80af\u7eb3\u5c71\u8109\u3001\u743c\u8fbe\u5c14\u68ee\u6797\u4e00\u7ebf\uff0c\u6c14\u5019\u6e29\u6696\u6e7f\u6da6\uff0c\u6cb3\u6d41\u6c34\u7cfb\u53d1\u8fbe\uff0c\u5c45\u6c11\u4eba\u53e3\u7ea6\u6709\u4e00\u5343\u591a\u4e07\uff0c\u4e3b\u8981\u7684\u653f\u6cbb\u52bf\u529b\u6709\u56db\u4e2a\uff0c\u5206\u522b\u662f\u4f4d\u4e8e\u5317\u90e8\u7684\u585e\u5c14\u3001\u4e2d\u90e8\u7684\u7a46\u7f55\u745e\u5fb7\uff08\u901a\u5e38\u7b80\u79f0\u7a46\u7f55\uff09\u3001\u5357\u90e8\u7684\u6069\u745f\u548c\u897f\u90e8\u7684\u5f7b\u68ee\u5854\u3002</p><p>\u201c\u7a46\u7f55\u548c\u6069\u745f\uff0c\u90fd\u662f\u2018\u795e\u738b\u4e4b\u56fd\u2019\uff0c\u4e5f\u5373\u662f\u7531\u795e\u738b\u4eec\u8fdb\u884c\u7edf\u6cbb\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u795e\u738b\u662f\u4e1c\u57df\u7684\u4e00\u79cd\u7279\u4ea7\uff0c\u7c7b\u4f3c\u4e8e\u795e\u7957\u7684\u5723\u8005\u964d\u4e34\uff0c\u4f46\u53c8\u6709\u6240\u4e0d\u540c\u2014\u2014\u5177\u4f53\u54ea\u91cc\u4e0d\u540c\uff0c\u6211\u4e5f\u8bf4\u4e0d\u6e05\u695a\u3002\u201d</p><p>\u6309\u7167\u4f20\u8bf4\u8bb0\u8f7d\uff0c\u5927\u7ea6\u662f\u56db\u5343\u5e74\u524d\uff0c\u6570\u91cf\u4f17\u591a\u7684\u795e\u738b\u81ea\u865a\u7a7a\u4e2d\u964d\u4e34\u4e1c\u57df\uff0c\u4ed6\u4eec\u6253\u8d25\u4e86\u52fe\u7ed3\u90aa\u9b54\u7684\u5deb\u5e08\uff0c\u5efa\u7acb\u8d77\u7a46\u7f55\u548c\u6069\u745f\u4e24\u4e2a\u56fd\u5bb6\u3002\u795e\u738b\u4f3c\u795e\u53c8\u975e\u795e\uff0c\u529b\u91cf\u5f3a\u5927\uff0c\u5bff\u547d\u7ef5\u957f\uff0c\u4f46\u4ecd\u7136\u4f1a\u50cf\u51e1\u4eba\u4e00\u6837\u751f\u8001\u75c5\u6b7b\uff0c\u4f46\u4ed6\u4eec\u638c\u63e1\u4e00\u79cd\u795e\u5947\u7684\u79d8\u6cd5\uff0c\u80fd\u591f\u8f6c\u4e16\u8f6e\u56de\uff0c\u6b7b\u800c\u91cd\u751f\uff0c\u4e16\u4e16\u4ee3\u4ee3\u5730\u7edf\u6cbb\u7740\u8fd9\u7247\u571f\u5730\u3002\u76ee\u524d\u7a46\u7f55\u5e1d\u56fd\u7531\u4ee5\u201c\u9e70\u795e\u201d\u8377\u9c81\u65af\u4e3a\u9996\u7684\u4e94\u4f4d\u795e\u738b\u5171\u540c\u7edf\u6cbb\uff0c\u6069\u745f\u5e1d\u56fd\u7684\u6700\u540e\u4e00\u4f4d\u795e\u738b\u5409\u52d2\u4eca\u5df2\u7ecf\u4e8e\u5341\u516d\u5e74\u524d\u6b7b\u4ea1\uff0c\u73b0\u5728\u662f\u4ed6\u7684\u738b\u540e\u6444\u653f\u3002</p><p>\u201c\u90a3\u585e\u5c14\u548c\u5f7b\u68ee\u5854\u5462\uff1f\u201d\u73ca\u5609\u95ee\u3002</p><p>\u585e\u5c14\u548c\u5f7b\u68ee\u5854\uff0c\u60c5\u51b5\u53c8\u6709\u6240\u4e0d\u540c\u3002\u585e\u5c14\u5728\u5386\u53f2\u4e0a\u539f\u672c\u96b6\u5c5e\u4e8e\u7a46\u7f55\uff0c\u56db\u767e\u4e94\u5341\u5e74\u524d\u5206\u88c2\u51fa\u6765\uff0c\u72ec\u7acb\u5efa\u56fd\uff0c\u7edf\u6cbb\u8005\u662f\u7ea2\u888d\u5deb\u5e08\u3002\u585e\u5c14\u662f\u975e\u5e38\u8457\u540d\u7684\u9b54\u6cd5\u5e1d\u56fd\uff0c\u5728\u5ea7\u7684\u90fd\u4e0d\u7b97\u964c\u751f\uff0c\u81f3\u5c11\u6709\u6240\u4e86\u89e3\uff0c\u51db\u751a\u81f3\u8fd8\u5728\u90a3\u91cc\u5f85\u8fc7\u51e0\u5e74\u3002\u81f3\u4e8e\u5f7b\u68ee\u5854\uff0c\u76f8\u5bf9\u800c\u8a00\u5c31\u8981\u9ed8\u9ed8\u65e0\u95fb\u8bb8\u591a\uff0c\u6240\u4ee5\u5c5e\u4e8e\u743c\u6069\u7684\u91cd\u70b9\u4ecb\u7ecd\u5bf9\u8c61\u3002</p><p>\u201c\u5f7b\u68ee\u5854\u4f4d\u4e8e\u4e1c\u57df\u7684\u897f\u90e8\uff0c\u4e24\u56fd\u4ee5\u873f\u8712\u6cb3\u4e3a\u754c\u3002\u5b83\u539f\u672c\u5176\u5b9e\u4e5f\u662f\u6069\u745f\u7684\u4e00\u90e8\u5206\uff0c\u540e\u6765\u72ec\u7acb\uff0c\u8fd9\u70b9\u548c\u585e\u5c14\u5f88\u50cf\uff0c\u4f46\u2026\u2026\u53c8\u5f88\u4e0d\u4e00\u6837\u3002\u201d</p><p>\u4e0d\u4e00\u6837\u4e4b\u5904\u5728\u4e8e\uff0c\u585e\u5c14\u7684\u72ec\u7acb\u662f\u901a\u8fc7\u6218\u4e89\uff0c\u800c\u5f7b\u68ee\u5854\u5219\u4e0d\u662f\u3002</p><p>\u636e\u8bf4\uff0c\u7a46\u7f55\u795e\u738b\u4eec\u662f\u4e00\u7fa4\u5351\u52a3\u800c\u65e0\u80fd\u7684\u7edf\u6cbb\u8005\uff0c\u4e3a\u4e86\u7ef4\u62a4\u5176\u7edf\u6cbb\uff0c\u4ed6\u4eec\u5bf9\u5916\u95ed\u5173\u9501\u56fd\u3001\u5bf9\u5185\u6a2a\u5f81\u66b4\u655b\u3001\u751f\u6d3b\u9a84\u5962\u6deb\u9038\u3001\u7f54\u987e\u5b50\u6c11\u6b7b\u6d3b\uff0c\u5e76\u4e14\u6781\u5ea6\u611a\u6627\u65e0\u77e5\uff0c\u5bf9\u4ee3\u8868\u7740\u5148\u8fdb\u6587\u5316\u548c\u6280\u672f\u8fdb\u6b65\u7684\u5deb\u5e08\u4eec\u6b8b\u9177\u6253\u538b\uff0c\u6fc0\u8d77\u4e86\u793e\u4f1a\u9769\u547d\u3002\u5fcd\u65e0\u53ef\u5fcd\u7684\u5deb\u5e08\u4eec\u56e2\u7ed3\u8d77\u6765\uff0c\u5360\u636e\u585e\u5c14\u5730\u533a\uff0c\u6740\u6b7b\u3001\u9a71\u9010\u4e86\u539f\u672c\u7684\u5b98\u540f\uff0c\u5e76\u4e14\u6570\u6b21\u51fb\u8d25\u4e86\u7a46\u7f55\u5e1d\u56fd\u7684\u8ba8\u4f10\u519b\uff0c\u6700\u7ec8\u6210\u529f\u5efa\u56fd\uff0c\u53d1\u5c55\u51fa\u5149\u8f89\u707f\u70c2\u7684\u9b54\u6cd5\u6587\u660e\u2014\u2014\u5f53\u7136\uff0c\u8fd9\u662f\u585e\u5c14\u7684\u5b98\u65b9\u8bf4\u6cd5\uff0c\u7a46\u7f55\u60f3\u5fc5\u53e6\u6709\u4e00\u79cd\u7248\u672c\uff0c\u4f46\u53cd\u6b63\u5927\u81f4\u610f\u601d\u5c31\u662f\u5982\u6b64\u3002</p><p>\u5f7b\u68ee\u5854\u5219\u4e0d\u540c\u3002\u65e9\u5728\u6069\u745f\u5e1d\u56fd\u521a\u521a\u5efa\u7acb\u4e4b\u65f6\uff0c\u5b83\u5c31\u662f\u201c\u534a\u72ec\u7acb\u201d\u7684\u6027\u8d28\uff0c\u540d\u4e49\u4e0a\u5c5e\u4e8e\u6069\u745f\uff0c\u4f46\u5b9e\u9645\u4e0a\u662f\u5272\u636e\u52bf\u529b\uff0c\u7531\u963f\u666e\u82cf\u548c\u63d0\u4e9a\u739b\u7279\u4e24\u5927\u795e\u738b\u5171\u540c\u7edf\u6cbb\u3002\u540e\u6765\u963f\u666e\u82cf\u9668\u843d\uff0c\u63d0\u4e9a\u739b\u7279\u6b7b\u800c\u590d\u6d3b\uff0c\u5374\u8f6c\u53d8\u4e3a\u4e94\u8272\u9f99\u795e\uff0c\u653e\u5f03\u4e86\u5f7b\u68ee\u5854\u3002\u5947\u602a\u7684\u662f\uff0c\u6069\u745f\u7684\u4e2d\u592e\u653f\u5e9c\u4e5f\u5e76\u672a\u8d81\u673a\u63d2\u624b\uff0c\u91cd\u65b0\u5efa\u7acb\u8d77\u6709\u6548\u7edf\u6cbb\uff0c\u53cd\u800c\u653e\u4efb\u81ea\u6d41\uff0c\u4eff\u4f5b\u5168\u7136\u5fd8\u8bb0\u4e86\u8fd9\u5757\u5730\u65b9\u3002\u5728\u4e4b\u540e\u7684\u4e24\u5343\u591a\u5e74\u91cc\uff0c\u5f7b\u68ee\u5854\u4e00\u76f4\u5904\u4e8e\u57ce\u90a6\u6797\u7acb\u5f7c\u6b64\u5f81\u6218\uff0c\u5730\u65b9\u8c6a\u5f3a\u5404\u81ea\u4e3a\u653f\u7684\u6df7\u4e71\u5c40\u9762\u3002\u76f4\u5230\u4e09\u767e\u591a\u5e74\u524d\uff0c\u7ea2\u9f99\u5bdf\u65af\u8428\u7ed3\u675f\u6218\u4e71\uff0c\u7edf\u4e00\u4e86\u5f7b\u68ee\u5854\uff0c\u52a0\u5195\u4e3a\u738b\uff0c\u5e76\u4e0e\u5409\u52d2\u4eca\uff08\u5f53\u65f6\u6069\u745f\u552f\u4e00\u7684\u795e\u738b\uff09\u5728\u873f\u8712\u6cb3\u4e0a\u76f8\u4f1a\u5bc6\u8c08\u3002\u4e24\u4f4d\u541b\u738b\u7684\u4f1a\u9762\u7a76\u7adf\u8ba8\u8bba\u4e86\u4ec0\u4e48\uff0c\u4e16\u4eba\u65e0\u4ece\u77e5\u6653\uff0c\u4e5f\u6ca1\u6709\u7559\u4e0b\u4efb\u4f55\u8bb0\u5f55\uff0c\u4f46\u4ece\u6b64\u4e4b\u540e\uff0c\u5f7b\u68ee\u5854\u4fbf\u771f\u6b63\u610f\u4e49\u4e0a\u4ece\u6069\u745f\u5206\u79bb\u51fa\u6765\u3002</p><p>\u201c\u6240\u4ee5\u6240\u8c13\u4e1c\u57df\u8fd9\u4e2a\u6982\u5ff5\uff0c\u5176\u5b9e\u4e5f\u662f\u6709\u5e7f\u4e49\u548c\u72ed\u4e49\u4e24\u79cd\uff0c\u201d\u6885\u83f2\u65af\u8865\u5145\u8bf4\u660e\uff0c\u201c\u5e7f\u4e49\u4e0a\u7684\u4e1c\u57df\uff0c\u662f\u5305\u62ec\u7a46\u7f55\u3001\u6069\u745f\u3001\u585e\u5c14\u548c\u5f7b\u68ee\u5854\u90fd\u5728\u5185\uff1b\u4f46\u5728\u5f88\u591a\u8d44\u6599\u4e2d\uff0c\u63d0\u5230\u4e1c\u57df\u8fd9\u4e2a\u8bcd\u7684\u65f6\u5019\uff0c\u662f\u5c06\u585e\u5c14\u548c\u5f7b\u68ee\u5854\u6392\u9664\u5728\u5916\u7684\uff0c\u53ea\u8ba4\u53ef\u7a46\u7f55\u548c\u6069\u745f\uff0c\u8fd9\u7b97\u662f\u72ed\u4e49\u4e0a\u7684\u4e1c\u57df\u3002\u201d</p><p>\u5e7f\u4e49\u6216\u8005\u72ed\u4e49\uff0c\u53ea\u4e0d\u8fc7\u662f\u5b66\u7406\u4e0a\u7684\u533a\u5206\uff0c\u5bf9\u4e8e\u743c\u6069\u7b49\u4eba\u6765\u8bf4\u6ca1\u6709\u4ec0\u4e48\u5b9e\u9645\u610f\u4e49\uff0c\u4ed6\u4eec\u771f\u6b63\u8981\u5173\u6ce8\u7684\u662f\u53e6\u5916\u4e00\u4e9b\u4e8b\u60c5\u3002</p><p>\u201c\u6211\u4eec\u8fd9\u91cc\u6709\u4e09\u4e2a\u5deb\u5e08\uff0c\u5982\u679c\u662f\u5728\u6069\u745f\u6216\u8005\u7a46\u7f55\uff0c\u90a3\u4e48\u4e00\u5b9a\u8981\u5c0f\u5fc3\u9690\u85cf\u8eab\u4efd\uff0c\u5c24\u5176\u662f\u7a46\u7f55\uff0c\u5bf9\u2018\u5deb\u5e08\u2019\u8fd9\u4e2a\u8bcd\u7279\u522b\u654f\u611f\uff0c\u4f46\u5982\u679c\u662f\u5728\u5f7b\u68ee\u5854\uff0c\u90a3\u5c31\u8fd8\u597d\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5f7b\u68ee\u5854\u4eba\u5bf9\u5deb\u5e08\u7684\u6001\u5ea6\uff0c\u57fa\u672c\u4e0a\u662f\u4e0d\u53cb\u597d\uff0c\u4f46\u4e5f\u4e0d\u4ec7\u89c6\uff0c\u603b\u4e4b\u8fd8\u80fd\u548c\u5e73\u5171\u5904\u3002\u201d</p><p>\u743c\u6069\u53d6\u51fa\u4e00\u5f20\u5730\u56fe\uff0c\u8ba9\u5b83\u6f02\u6d6e\u8d77\u6765\uff0c\u7ad6\u5728\u7a7a\u6c14\u4e2d\uff0c\u201c\u4e1c\u57df\u5927\u81f4\u5c31\u662f\u8fd9\u4e00\u5757\u5730\u65b9\uff0c\u201d\u4ed6\u7528\u7d2b\u6c34\u6676\u6cd5\u6756\u5728\u5730\u56fe\u4e0a\u70b9\u4e86\u70b9\uff0c\u88ab\u9009\u4e2d\u7684\u533a\u57df\u987f\u65f6\u4fbf\u95ea\u95ea\u53d1\u4eae\u8d77\u6765\uff0c\u201c\u8fd9\u91cc\u662f\u585e\u5c14\uff0c\u5728\u6700\u5317\u90e8\uff0c\u5b83\u7684\u5357\u8fb9\u662f\u7a46\u7f55\uff0c\u4e24\u56fd\u4e4b\u95f4\u4ee5\u7d2b\u94dc\u5c71\u8109\u4e3a\u754c\uff1b\u7a46\u7f55\u548c\u6069\u745f\u4e4b\u95f4\u662f\u4ee5\u5251\u4e4b\u6cb3\u4e3a\u754c\uff1b\u6069\u745f\u548c\u5f7b\u68ee\u5854\u4e4b\u95f4\u4ee5\u873f\u8712\u6cb3\u4e3a\u754c\u3002\u201d</p><p>\u4e1c\u57df\u56db\u56fd\uff0c\u4ece\u4e1c\u5317\u5411\u897f\u5357\u6392\u5217\uff0c\u5982\u679c\u5ffd\u7565\u8fb9\u7f18\u7684\u4e0d\u89c4\u5219\uff0c\u5927\u4f53\u8fd1\u4f3c\u4e00\u4e2a\u5f2f\u6708\u5f62\u3002\u585e\u5c14\u548c\u7a46\u7f55\u6784\u6210\u4e0a\u534a\u90e8\uff0c\u6069\u745f\u548c\u5f7b\u68ee\u5854\u6784\u6210\u4e0b\u534a\u90e8\u3002\u5355\u770b\u9762\u79ef\u5341\u5206\u8fbd\u9614\uff0c\u4f46\u4ece\u5730\u56fe\u4e0a\u770b\uff0c\u5927\u90e8\u5206\u56fd\u571f\u90fd\u88ab\u6c99\u6f20\u3001\u6cbc\u6cfd\u3001\u9ad8\u539f\u548c\u68ee\u6797\u6240\u8986\u76d6\uff0c\u771f\u6b63\u9002\u5408\u4eba\u7c7b\u5b9a\u5c45\u7684\u5730\u65b9\u5176\u5b9e\u4e5f\u4e0d\u591a\u3002\u201c\u4e1c\u57df\u5f88\u5927\u561b\uff0c\u201d\u838e\u73de\u514b\u95ee\uff0c\u201c\u6211\u4eec\u8981\u53bb\u7684\u5730\u65b9\u5177\u4f53\u662f\u54ea\u91cc\u5462\uff1f\u201d</p><p>\u201c\u8ba1\u5212\u4e4b\u4e2d\u7684\u76ee\u7684\u5730\u6709\u4e24\u4e2a\uff0c\u7b2c\u4e00\u4e2a\u662f\u7d2b\u5bb8\u6c99\u6f20\uff0c\u201d\u743c\u6069\u5728\u5730\u56fe\u4e0a\u6307\u51fa\u4f4d\u7f6e\uff0c\u201c\u5b83\u5728\u9f99\u5251\u5c71\u8109\u7684\u4e1c\u90e8\uff0c\u661f\u4e4b\u6e56\u4ee5\u5357\uff0c\u5c5e\u4e8e\u7a46\u7f55\u5883\u5185\u3002\u201d</p><p>\u201c\u770b\u8d77\u6765\u5f88\u8fdc\u554a\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4f60\u8981\u53bb\u90a3\u91cc\u5e72\u561b\u5462\u3002\u201d</p><p>\u201c\u6709\u70b9\u79c1\u4e8b\u2014\u2014\u201d</p><p>\u201c\u53bb\u548c\u65e7\u60c5\u4eba\u5e7d\u4f1a\uff0c\u201d\u73ca\u5609\u76f4\u63a5\u63ed\u7a7f\uff0c\u201c\u90a3\u4f4d\u51ef\u745f\u7433\u5c0f\u59d0\u3002\u201d</p><p>\u201c\u55ef\uff0c\u201d\u743c\u6069\u627f\u8ba4\uff0c\u201c\u624e\u745e\u5c14\u8bf4\u5979\u5728\u90a3\u91cc\u7b49\u6211\uff0c\u201d\u4ed6\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u201c\u6211\u6000\u7591\uff0c\u5979\u53ef\u80fd\u662f\u88ab\u56f0\u5728\u7d2b\u5bb8\u6c99\u6f20\u91cc\uff0c\u81f3\u5c11\u662f\u9047\u5230\u4e86\u4e00\u4e9b\u9ebb\u70e6\uff0c\u9700\u8981\u6211\u7684\u5e2e\u52a9\u3002\u201d</p><p>\u9b54\u59ec\u7684\u4ea4\u4ee3\u5f88\u7b80\u5355\uff0c\u5e76\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\uff0c\u4eff\u4f5b\u53ea\u662f\u4f20\u4e2a\u53e3\u4fe1\uff0c\u4f46\u743c\u6069\u8fd8\u662f\u610f\u8bc6\u5230\u4e86\u4e00\u4e9b\u95ee\u9898\u3002\u51ef\u745f\u7433\u60f3\u89c1\u743c\u6069\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u76f4\u63a5\u6765\u627e\u4ed6\uff0c\u5374\u8981\u7528\u8fd9\u79cd\u65b9\u5f0f\uff1f\u51ef\u745f\u7433\u5e76\u4e0d\u50cf\u662f\u90a3\u79cd\u81ea\u77dc\u8eab\u4efd\u7684\u5973\u5b69\u5b50\uff0c\u81f3\u5c11\u4ece\u5df2\u6709\u7684\u51e0\u6b21\u6253\u4ea4\u9053\u6765\u770b\uff0c\u5979\u884c\u4e8b\u98ce\u683c\u4e3b\u52a8\u800c\u76f4\u622a\u4e86\u5f53\u3002\u7d2b\u5bb8\u6c99\u6f20\u5e76\u4e0d\u662f\u4ec0\u4e48\u98ce\u666f\u79c0\u4e3d\u7684\u5730\u65b9\uff0c\u6839\u636e\u743c\u6069\u770b\u5230\u7684\u8d44\u6599\u8bb0\u8f7d\uff0c\u90a3\u91cc\u5728\u51e0\u5343\u5e74\u524d\u66fe\u7ecf\u662f\u4e00\u7247\u5e73\u539f\u6c83\u91ce\uff0c\u4e0a\u53e4\u9b54\u6cd5\u5e1d\u56fd\u201c\u4f0a\u739b\u65af\u5361\u201d\u7684\u7edf\u6cbb\u4e2d\u5fc3\u5373\u5728\u4e8e\u6b64\u3002\u5927\u7ea6\u56db\u5343\u5e74\u524d\uff0c\u795e\u738b\u51fb\u8d25\u5947\u68b0\u5e08\uff0c\u8986\u706d\u4e86\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\uff0c\u4e3a\u4e86\u6c38\u7edd\u540e\u60a3\uff0c\u795e\u738b\u4eec\u53d1\u52a8\u67d0\u79cd\u5f3a\u5927\u7684\u6cd5\u672f\uff0c\u62db\u6765\u6050\u6016\u7684\u5f02\u754c\u9b54\u517d\uff0c\u6467\u6bc1\u6240\u6709\u57ce\u5e02\uff0c\u5e76\u5c06\u8fd9\u7247\u571f\u5730\u53d8\u6210\u4e86\u8352\u829c\u6c99\u6f20\u3002\u51ef\u745f\u7433\u8bf4\u5728\u8fd9\u79cd\u5730\u65b9\u7b49\u4ed6\uff0c\u743c\u6069\u5b9e\u5728\u6ca1\u529e\u6cd5\u4e0d\u591a\u60f3\u4e00\u60f3\u3002</p><p>\u8fd9\u5c31\u50cf\u4e00\u4e2a\u5973\u5b69\u5b50\u5bf9\u7537\u53cb\u8bf4\u201c\u6211\u5728\u516c\u56ed\u7b49\u4f60\u201d\uff0c\u8fd9\u662f\u6b63\u5e38\u7684\u7ea6\u4f1a\u6a21\u5f0f\uff1b\u4f46\u5982\u679c\u5979\u8bf4\u201c\u6211\u5728\u4e00\u4e2a\u5e9f\u5f03\u4ed3\u5e93\u91cc\u7b49\u4f60\u201d\uff0c\u90a3\u5c31\u53ea\u80fd\u8ba4\u4e3a\u5979\u88ab\u6b79\u5f92\u52ab\u6301\u4e86\u3002\u5965\u5609\u83b1\u65af\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u6b64\u524d\u51fa\u73b0\u7684\u51ef\u745f\u7433\u53ea\u662f\u201c\u5bb9\u5668\u201d\uff0c\u5e76\u975e\u201c\u672c\u4f53\u201d\u2014\u2014\u90a3\u4e48\u5979\u7684\u672c\u4f53\u662f\u5728\u54ea\u91cc\uff1f</p><p>\u9634\u5f71\u8c37\u4e00\u6218\uff0c\u5728\u5ea7\u6240\u6709\u4eba\u662f\u4eb2\u8eab\u7ecf\u5386\u3002\u5173\u4e8e\u51ef\u745f\u7433\u7684\u8eab\u4efd\u6765\u5386\uff0c\u4ee5\u53ca\u5979\u548c\u743c\u6069\u7684\u5173\u7cfb\uff0c\u5927\u591a\u90fd\u5df2\u7ecf\u5fc3\u4e2d\u6709\u6570\uff0c\u731c\u4e5f\u80fd\u731c\u4e2a\u516b\u4e5d\u4e0d\u79bb\u5341\u3002\u743c\u6069\u7684\u731c\u6d4b\u4e0d\u65e0\u9053\u7406\uff0c\u5979\u4eec\u4e5f\u4e0d\u4f1a\u53cd\u5bf9\uff0c\u4f46\u542c\u5230\u81ea\u5df1\u7537\u53cb\u8bf4\u8981\u53bb\u89c1\u65e7\u60c5\u4eba\uff0c\u6ca1\u6709\u54ea\u4e2a\u5973\u5b69\u5b50\u4f1a\u5f00\u5fc3\u2014\u2014\u53ea\u6709\u51db\u662f\u4f8b\u5916\u3002</p><p>\u201c\u771f\u597d\uff0c\u201d\u5c0f\u5973\u5deb\u6258\u7740\u816e\uff0c\u201c\u5c31\u50cf\u7ae5\u8bdd\u6545\u4e8b\u91cc\u8bf4\u7684\u4e00\u6837\uff1a\u88ab\u5deb\u672f\u7b3c\u7f69\u7684\u53e4\u8001\u57ce\u5821\uff0c\u7f8e\u4e3d\u7684\u516c\u4e3b\u5bc2\u5bde\u5730\u5750\u5728\u7a97\u524d\uff0c\u7b49\u5f85\u7740\u5979\u7684\u738b\u5b50\u8dcb\u5c71\u6d89\u6d77\u3001\u62ab\u8346\u65a9\u68d8\uff0c\u4e0d\u8fdc\u5343\u91cc\u6765\u62ef\u6551\u5979\u2014\u2014\u60f3\u4e00\u60f3\u5c31\u89c9\u5f97\u597d\u6d6a\u6f2b\u3002\u201d</p><p>\u201c\u2026\u2026\u662f\u633a\u6d6a\u6f2b\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\u6211\u8bb0\u5f97\u6bd4\u8f83\u6d41\u884c\u7684\u6545\u4e8b\u60c5\u8282\uff0c\u662f\u516c\u4e3b\u88ab\u6076\u9f99\u6293\u8d70\uff0c\u7136\u540e\u7b49\u5f85\u9a91\u58eb\u53bb\u6551\u5979\u5427\u3002\u201d</p><p>\u201c\u8fd9\u79cd\u60c5\u8282\u4e25\u91cd\u7f3a\u4e4f\u5408\u7406\u6027\uff0c\u201d\u51db\u6446\u6446\u624b\uff0c\u201c\u516c\u4e3b\u5982\u679c\u88ab\u9f99\u6293\u8d70\uff0c\u80af\u5b9a\u4ee5\u8eab\u76f8\u8bb8\u4e86\uff0c\u8fd8\u6709\u9a91\u58eb\u4ec0\u4e48\u4e8b\u5462\u3002\u201d</p><p>\u201c\u600e\u4e48\u53ef\u80fd\u2014\u2014\u54e6\uff0c\u5bf9\uff0c\u4f60\u6bcd\u4eb2\u5c31\u662f\u3002\u201d</p><p>\u201c\u4f60\u592a\u843d\u4f0d\u4e86\uff0c\u4e3b\u4eba\uff0c\u81f3\u5c11\u5df2\u7ecf\u843d\u540e\u65f6\u4ee3\u4e00\u767e\u5e74\uff0c\u201d\u838e\u73de\u514b\u7b11\u7740\u63d2\u5634\uff0c\u201c\u4f60\u8bf4\u7684\u90a3\u79cd\u4f20\u7edf\u6545\u4e8b\u6a21\u5f0f\u65e9\u5c31\u6ca1\u4eba\u770b\u4e86\uff0c\u73b0\u5728\u6d41\u884c\u7684\u662f\u9006\u53cd\u8bbe\u5b9a\uff0c\u6bd4\u5982\u7eaf\u6d01\u5584\u826f\u7684\u5353\u5c14\u554a\uff0c\u6b63\u76f4\u6587\u660e\u6709\u826f\u597d\u6559\u517b\u7684\u517d\u4eba\u554a\uff0c\u597d\u8272\u800c\u4e14\u6deb\u8361\u7684\u7cbe\u7075\u554a\u2014\u2014\u201d</p><p>\u201c\u7b49\u7b49\uff0c\u201d\u743c\u6069\u4e0d\u7981\u83ab\u540d\u60ca\u8be7\uff0c\u201c\u7eaf\u6d01\u5584\u826f\u7684\u5353\u5c14\u6211\u77e5\u9053\uff0c\u4f46\u6b63\u76f4\u6587\u660e\u6709\u826f\u597d\u6559\u517b\u7684\u517d\u4eba\uff0c\u8fd8\u6709\u597d\u8272\u6deb\u8361\u7684\u7cbe\u7075\uff0c\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff1f\u201d</p><p>\u201c\u5c31\u662f\u5b57\u9762\u610f\u601d\u561b\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u4ece\u53e3\u888b\u91cc\u6478\u51fa\u4e00\u672c\u4e66\uff0c\u201c\u6bd4\u5982\u8fd9\u672c\u8fde\u8f7d\u5c0f\u8bf4\uff0c\u6700\u8fd1\u5728\u5357\u65b9\u7279\u522b\u6d41\u884c\uff0c\u8bb2\u8ff0\u4e00\u4e2a\u517d\u4eba\u738b\u5b50\u4f5c\u4e3a\u4ea4\u6362\u751f\uff0c\u8fdb\u5165\u7cbe\u7075\u5b66\u6821\uff0c\u6bcf\u5929\u88ab\u5404\u79cd\u6deb\u8361\u7684\u5973\u6027\u7cbe\u7075\u9a9a\u6270\u7684\u6545\u4e8b\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u786e\u5b9a\u8fd9\u4e0d\u662f\u8272\u60c5\u5c0f\u8bf4\u5417\uff1f\u201d</p><p>\u201c\u7edd\u5bf9\u4e0d\u662f\uff0c\u201d\u9b45\u9b54\u5f88\u80af\u5b9a\u5730\u8bf4\uff0c\u201c\u81f3\u5c11\u5230\u76ee\u524d\u7684\u7ae0\u8282\u4e3a\u6b62\uff0c\u4e3b\u89d2\u4e00\u76f4\u5b88\u8eab\u5982\u7389\uff0c\u8fd8\u6ca1\u6709\u88ab\u90a3\u4e9b\u5973\u6027\u7cbe\u7075\u63a8\u5012\u5462\u3002\u201d</p><p>\u201c\u679c\u7136\u662f\u6b63\u76f4\u6587\u660e\u6709\u826f\u597d\u6559\u517b\u7684\u517d\u4eba\u3002\u201d\u743c\u6069\u611f\u53f9\u3002</p><p>\u201c\u6240\u4ee5\u6309\u7167\u8fd9\u79cd\u65b0\u6a21\u5f0f\uff0c\u9f99\u6293\u8d70\u516c\u4e3b\u7136\u540e\u88ab\u9a91\u58eb\u62ef\u6551\u7684\u5267\u60c5\u5c31\u8981\u6539\u4e00\u6539\u4e86\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u6bd4\u65b9\u8bf4\uff0c\u90a3\u53ea\u9f99\u5176\u5b9e\u662f\u5973\u6027\uff0c\u5979\u4e00\u76f4\u6697\u604b\u7740\u67d0\u4f4d\u9a91\u58eb\u2014\u2014\u9a91\u58eb\u4e5f\u53ef\u4ee5\u662f\u5973\u6027\uff0c\u4f46\u9a91\u58eb\u65e9\u5df2\u5fc3\u6709\u6240\u5c5e\uff0c\u4e0d\u80af\u56de\u5e94\u9f99\u7684\u7231\u610f\uff0c\u4e8e\u662f\u9f99\u5c31\u5c06\u9a91\u58eb\u7684\u7231\u4eba\u2014\u2014\u6bd4\u5982\u8bf4\u662f\u4e00\u4f4d\u7537\u6027\u5deb\u5e08\u2014\u2014\u6293\u8d70\u4e86\u3002\u201d</p><p>\u201c\u7136\u540e\u5462\uff1f\u201d</p><p>\u201c\u7136\u540e\uff1f\u55ef\uff0c\u53ef\u4ee5\u8bbe\u5b9a\u7ecf\u8fc7\u671d\u5915\u76f8\u5904\uff0c\u65e5\u4e45\u751f\u60c5\uff0c\u9f99\u548c\u5deb\u5e08\u4ea7\u751f\u4e86\u611f\u60c5\u3002\u4e8e\u662f\u5f53\u5973\u9a91\u58eb\u5386\u7ecf\u8270\u9669\u6765\u5230\u9f99\u7a9f\u65f6\uff0c\u770b\u89c1\u7684\u662f\u9f99\u548c\u5979\u7684\u7231\u4eba\u5df2\u7ecf\u5feb\u4e50\u5730\u751f\u6d3b\u5728\u4e00\u8d77\u3002\u201d</p><p>\u201c\u7136\u540e\u5462\uff1f\u201d</p><p>\u201c\u7136\u540e\u5927\u6982\u5c31\u662f\u4e09\u4e2a\u4eba\u4e00\u8d77\u5feb\u4e50\u5730\u751f\u6d3b\u5728\u4e00\u8d77\u5427\u3002\u6216\u8005\u53ef\u4ee5\u5199\u7eed\u96c6\uff0c\u6bd4\u5982\u8bf4\u5deb\u5e08\u6709\u4e00\u4f4d\u7f8e\u4e3d\u7684\u59d0\u59d0\uff0c\u4e3a\u4e86\u5bfb\u627e\u5931\u8e2a\u7684\u5f1f\u5f1f\u800c\u8e0f\u4e0a\u65c5\u9014\u7684\u6545\u4e8b\u3002\u201d</p><p>\u201c\u838e\u73de\u514b\uff0c\u4f60\u4e0d\u53bb\u5199\u5c0f\u8bf4\u771f\u662f\u592a\u53ef\u60dc\u4e86\u3002\u201d</p><p>\u201c\u6211\u81ea\u5df1\u4e5f\u8fd9\u4e48\u89c9\u5f97\uff0c\u201d\u9b45\u9b54\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\uff0c\u201c\u5176\u5b9e\u5f88\u4e45\u4e4b\u524d\u6211\u5c31\u60f3\u8fc7\uff0c\u6709\u4e00\u5929\u9000\u4f11\u4e86\uff0c\u4e0d\u505a\u6740\u624b\u4e86\uff0c\u6211\u5c31\u6539\u884c\u5199\u5c0f\u8bf4\u548c\u5267\u672c\u53bb\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u5f88\u51fa\u540d\u5462\u3002\u201d</p><p>\u201c\u4f60\u73b0\u5728\u5c31\u53ef\u4ee5\u5199\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5199\u5b8c\u4e86\u6211\u5e2e\u4f60\u51fa\u7248\u3002\u201d</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u838e\u73de\u514b\u7684\u6253\u5c94\uff0c\u6210\u529f\u5730\u5c06\u5927\u5bb6\u7684\u6ce8\u610f\u529b\u4ece\u51ef\u745f\u7433\u8eab\u4e0a\u8f6c\u79fb\u5f00\u6765\u3002\u201c\u8fd8\u6709\u4e00\u4e2a\u8981\u53bb\u7684\u5730\u65b9\u662f\u54ea\uff1f\u201d\u51db\u95ee\u3002</p><p>\u201c\u8fd9\u91cc\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u6307\u7740\u5730\u56fe\u4e0a\u7684\u4e00\u4e2a\u4f4d\u7f6e\uff0c\u201c\u8ff7\u5931\u68ee\u6797\u3002\u201d</p><p>\u8ff7\u5931\u68ee\u6797\u4f4d\u4e8e\u873f\u8712\u6cb3\u4e1c\u4fa7\uff0c\u5c5e\u6069\u745f\u5883\u5185\uff0c\u4e43\u662f\u6574\u4e2a\u4e1c\u57df\u73b0\u5b58\u9762\u79ef\u6700\u5927\u7684\u53e4\u68ee\u6797\uff0c\u636e\u8bf4\u662f\u4e0a\u53e4\u7cbe\u7075\u738b\u56fd\u7684\u9057\u5740\uff0c\u5982\u4eca\u4ecd\u7136\u6709\u4e00\u4e9b\u7cbe\u7075\u5c45\u4f4f\uff0c\u53c8\u6709\u4e0d\u5c11\u602a\u7269\u3001\u731b\u517d\u6e38\u8361\u5176\u4e2d\uff0c\u751a\u81f3\u8fd8\u6709\u9f99\u7684\u4f20\u95fb\u3002\u5343\u767e\u5e74\u6765\uff0c\u6069\u745f\u4eba\u90fd\u89c6\u8fd9\u7247\u68ee\u6797\u4e3a\u5371\u9669\u7981\u533a\uff0c\u7b49\u95f2\u4e0d\u613f\u9760\u8fd1\uff0c\u8fde\u780d\u67f4\u3001\u6253\u730e\u90fd\u4e0d\u6562\uff0c\u9664\u4e86\u5386\u4ee3\u795e\u738b\u5076\u6709\u9a7e\u4e34\u7684\u8bb0\u5f55\uff0c\u4f20\u8bf4\u51e1\u4eba\u53ea\u8981\u8fdb\u5165\u68ee\u6797\uff0c\u4fbf\u4f1a\u88ab\u6c38\u8fdc\u56f0\u5728\u5176\u4e2d\uff0c\u518d\u4e5f\u56de\u4e0d\u6765\uff0c\u6545\u6b64\u53c8\u79f0\u4e3a\u201c\u8ff7\u5931\u201d\u68ee\u6797\u3002</p><p>\u201c\u8ff7\u5931\u68ee\u6797\u91cc\u6709\u4e00\u5ea7\u8010\u745f\u6d6e\u7a7a\u57ce\uff0c\u201d\u743c\u6069\u5c06\u8428\u9a6c\u65af\u7279\u7559\u7ed9\u4ed6\u7684\u5730\u56fe\u4e5f\u644a\u5f00\u5230\u684c\u4e0a\uff0c\u201c\u636e\u8bf4\u5b83\u867d\u7136\u8352\u5e9f\uff0c\u4f46\u6838\u5fc3\u533a\u57df\u4fdd\u5b58\u5b8c\u597d\uff0c\u6700\u91cd\u8981\u7684\u662f\uff0c\u5b83\u7684\u8ff7\u9501\u4ecd\u7136\u5728\u8fd0\u4f5c\u3002\u201d</p><p>\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u6bcf\u4e00\u5ea7\u6d6e\u7a7a\u57ce\u90fd\u4f1a\u6709\u4ee5\u5bc6\u745f\u80fd\u6838\u4e3a\u6838\u5fc3\u7684\u8ff7\u9501\u7b3c\u7f69\u4fdd\u62a4\uff0c\u4f8b\u5982\u9634\u9b42\u57ce\u3001\u661f\u9668\u57ce\u90fd\u662f\u5982\u6b64\u3002\u65e2\u7136\u8ff7\u9501\u4ecd\u5728\u8fd0\u4f5c\uff0c\u90a3\u5c31\u8bf4\u660e\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\u7684\u5bc6\u745f\u80fd\u6838\u5b8c\u597d\u65e0\u635f\u7684\u53ef\u80fd\u6027\u975e\u5e38\u5927\uff0c\u6d6e\u7a7a\u57ce\u7684\u5173\u952e\u5c31\u5728\u4e8e\u5bc6\u745f\u80fd\u6838\uff0c\u53ea\u8981\u5b83\u8fd8\u5728\uff0c\u5c31\u7b97\u6574\u5ea7\u57ce\u5e02\u90fd\u6bc1\u4e86\u4e5f\u4e0d\u7b97\u4ec0\u4e48\uff0c\u4f9d\u7136\u53ef\u4ee5\u91cd\u5efa\uff1b\u5bc6\u745f\u80fd\u6838\u5374\u662f\u65e0\u6cd5\u518d\u9020\u7684\uff0c\u81f3\u5c11\u5728\u73b0\u4eca\u662f\u5982\u6b64\u3002</p><p>\u201c\u4f60\u8981\u53bb\u627e\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\uff1f\u201d\u73ca\u5609\u95ee\uff0c\u201c\u662f\u50cf\u4e0a\u6b21\u4e00\u6837\u7684\u4efb\u52a1\u5417\uff1f\u201d</p><p>\u524d\u6bb5\u65f6\u95f4\uff0c\u9634\u9b42\u57ce\u5728\u5760\u661f\u6d77\u4e2d\u5bfb\u627e\u6253\u635e\u661f\u9668\u57ce\uff0c\u743c\u6069\u4e5f\u66fe\u7ecf\u524d\u53bb\u5e2e\u5fd9\uff0c\u6240\u4ee5\u73ca\u5609\u6709\u6b64\u4e00\u95ee\u3002\u201c\u4e0d\u662f\u4efb\u52a1\uff0c\u201d\u743c\u6069\u6447\u5934\uff0c\u201c\u662f\u6211\u60f3\u53bb\u627e\u5230\u5b83\u3002\u201d</p><p>\u201c\u4f60\u627e\u5230\u5b83\u8981\u505a\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u4f5c\u4e3a\u81ea\u5df1\u7684\u9886\u5730\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u60f3\u5efa\u7acb\u81ea\u5df1\u7684\u57ce\u5e02\uff0c\u62e5\u6709\u81ea\u5df1\u7684\u529b\u91cf\uff0c\u4e89\u53d6\u81ea\u5df1\u7684\u9053\u8def\uff0c\u638c\u63e1\u81ea\u5df1\u7684\u547d\u8fd0\u2014\u2014\u8fd9\u4e00\u5207\u7684\u5f00\u7aef\uff0c\u5c31\u4ece\u627e\u5230\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\u5f00\u59cb\u5427\u3002\u201d</p><p>\u6d6e\u7a7a\u57ce\u53ef\u662f\u4e2a\u597d\u4e1c\u897f\uff0c\u9634\u9b42\u57ce\u81ea\u4ece\u8fd4\u56de\u7269\u8d28\u754c\u4ee5\u6765\uff0c\u96c4\u8e1e\u864e\u89c6\uff0c\u4fe8\u7136\u4e00\u65b9\u8bf8\u4faf\uff0c\u5176\u6700\u5927\u7684\u4f9d\u4ed7\u6b63\u662f\u4e00\u5ea7\u6d6e\u7a7a\u57ce\u3002\u5982\u679c\u4ee5\u6b64\u4e3a\u57fa\u5730\uff0c\u6316\u77ff\u3001\u5efa\u623f\u5b50\u3001\u9020\u5175\u8425\u3001\u51fa\u5175\u3001\u6500\u79d1\u6280\uff0c\u5230\u65f6\u5019\u4e00\u8def\u5e73\u63a8\u8fc7\u53bb\uff0c\u79f0\u9738\u5927\u9646\u6307\u65e5\u53ef\u5f85\u3002</p><p>\u552f\u4e00\u7684\u95ee\u9898\u5728\u4e8e\uff1a\u81ea\u5df1\u4e00\u5411\u6bd4\u8f83\u559c\u6b22\u5355\u673a\u89d2\u8272\u626e\u6f14\u6e38\u620f\uff0c\u6216\u8005\u5982\u5927\u83e0\u841d\u822c\u8fc7\u5173\u780d\u602a\u6e38\u620f\uff0c\u6253\u661f\u9645\u9b54\u517d\u4ec0\u4e48\u7684\u90fd\u5f88\u70c2\uff0c\u9664\u975e\u4f5c\u5f0a\uff0c\u5426\u5219\u8fde\u75af\u72c2\u7535\u8111\u90fd\u5355\u6311\u4e0d\u8fc7\u3002</p><p>\u603b\u800c\u8a00\u4e4b\uff0c\u627e\u5230\u6d6e\u7a7a\u57ce\u80af\u5b9a\u6ca1\u9519\u3002</p><p>\u201c\u6ca1\u52b2\uff0c\u201d\u51db\u6241\u6241\u5634\uff0c\u201c\u6211\u8fd8\u4ee5\u4e3a\u90a3\u91cc\u53c8\u6709\u4e00\u4e2a\u4f60\u7684\u65e7\u60c5\u4eba\uff0c\u7b49\u7740\u4f60\u53bb\u62ef\u6551\u5462\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u54ea\u6709\u90a3\u4e48\u591a\u65e7\u60c5\u4eba\uff01\u201d</p><p>\u8bdd\u521a\u51fa\u53e3\uff0c\u743c\u6069\u7a81\u7136\u60f3\u8d77\u4e00\u4ef6\u4e8b\u3002\u624e\u745e\u5c14\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u4ed6\u201c\u524d\u4e16\u201d\u66fe\u7ecf\u548c\u4e00\u4f4d\u7cbe\u7075\u516c\u4e3b\u53d1\u751f\u8fc7\u8d85\u53cb\u8c0a\u5173\u7cfb\uff0c\u8fd8\u628a\u4eba\u5bb6\u641e\u6000\u5b55\u4e86\uff0c\u6700\u540e\u662f\u516c\u4e3b\u81ea\u6740\u8eab\u4ea1\uff0c\u516c\u4e3b\u7684\u59b9\u59b9\u7c73\u65af\u5170\u8fbe\u5c14\u8981\u627e\u4ed6\u62a5\u4ec7\u2014\u2014\u4e5f\u5c31\u662f\u9634\u5f71\u8c37\u91cc\u9047\u5230\u8fc7\u7684\u90a3\u4f4d\u201c\u8ff7\u96fe\u5927\u5e08\u201d\u4e86\u3002</p><p>\u7cbe\u7075\u65cf\u5728\u4e0a\u53e4\u65f6\u4ee3\u79f0\u96c4\u5927\u9646\u65f6\uff0c\u5e76\u975e\u50cf\u5982\u4eca\u8fd9\u6837\u5f52\u4e8e\u4e00\u7edf\uff0c\u800c\u662f\u5206\u88c2\u4e3a\u516d\u5927\u738b\u56fd\uff0c\u5176\u4e2d\u6700\u4e1c\u90e8\u7684\u4e00\u56fd\u201c\u83ab\u5c9a\u201d\uff0c\u4fbf\u662f\u5728\u8fd9\u8ff7\u5931\u68ee\u6797\u4e2d\u3002\u624e\u745e\u5c14\u8bf4\u7684\u65f6\u5019\uff0c\u5e76\u672a\u6307\u660e\u743c\u6069\u641e\u4e0a\u7684\u662f\u54ea\u4e00\u56fd\u7684\u7cbe\u7075\u516c\u4e3b\uff0c\u4f46\u4ee5\u5e38\u7406\u63a8\u6d4b\uff0c\u5e94\u8be5\u4fbf\u662f\u83ab\u5c9a\uff0c\u56e0\u4e3a\u76f8\u9694\u6700\u8fd1\uff0c\u53ef\u80fd\u5b58\u5728\u4ea4\u5f80\uff0c\u5176\u4ed6\u4e94\u5927\u7cbe\u7075\u56fd\u90fd\u8ddd\u79bb\u5b9e\u5728\u592a\u8fdc\u4e86\u3002\u5018\u82e5\u8fd9\u4e2a\u731c\u6d4b\u4e3a\u771f\uff0c\u90a3\u4e48\u8ff7\u5931\u68ee\u6797\u91cc\u7684\u786e\u662f\u6709\u743c\u6069\u7684\u65e7\u60c5\u4eba\uff0c\u53ea\u4e0d\u8fc7\u5df2\u7ecf\u6545\u53bb\u6570\u5343\u5e74\u4e86\u3002</p><p>\u7cbe\u7075\u516c\u4e3b\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u5e76\u672a\u5bf9\u5176\u4ed6\u4eba\u900f\u9732\uff0c\u53ea\u548c\u6885\u83f2\u65af\u8bf4\u8fc7\u3002\u4ed6\u4e0b\u610f\u8bc6\u5730\u671d\u6885\u83f2\u65af\u770b\u53bb\uff0c\u6070\u597d\u5c11\u5973\u4e5f\u6b63\u8f6c\u773c\u770b\u8fc7\u6765\uff0c\u8138\u4e0a\u795e\u60c5\u4f3c\u7b11\u975e\u7b11\uff0c\u663e\u7136\u4e5f\u662f\u60f3\u5230\u4e86\u8fd9\u4e00\u8282\u3002\u743c\u6069\u7565\u89c9\u5c34\u5c2c\uff0c\u54b3\u55fd\u4e00\u58f0\uff0c\u8bf4\uff1a\u201c\u603b\u4e4b\uff0c\u5c31\u662f\u8fd9\u6837\u4e86\u3002\u201d</p><p>\u201c\u4e3b\u4eba\u60f3\u8981\u81ea\u5efa\u52bf\u529b\u5417\uff1f\u201d\u838e\u73de\u514b\u4f3c\u4e4e\u9887\u4e3a\u611f\u5174\u8da3\uff0c\u201c\u542c\u8d77\u6765\u5f88\u6709\u610f\u601d\u7684\u6837\u5b50\u3002\u201d</p><p>\u73ca\u5609\u6b32\u8a00\u53c8\u6b62\uff0c\u6700\u7ec8\u6ca1\u6709\u8bf4\u4ec0\u4e48\u3002\u201c\u5dee\u4e0d\u591a\u662f\u8fd9\u4e2a\u610f\u601d\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5373\u4fbf\u4e0d\u8003\u8651\u90a3\u4e48\u957f\u8fdc\u3002\u6211\u4eec\u5728\u4e1c\u57df\u6bd5\u7adf\u8981\u505c\u7559\u4e00\u6bb5\u65f6\u95f4\uff0c\u53ef\u80fd\u662f\u4e00\u5e74\uff0c\u751a\u81f3\u662f\u4e24\u5e74\u3001\u4e09\u5e74\u6216\u8005\u66f4\u4e45\uff0c\u542c\u8bf4\u4e1c\u57df\u73b0\u5728\u6b63\u5904\u4e8e\u6218\u4e71\u72b6\u6001\uff0c\u6211\u4eec\u9700\u8981\u4e00\u4e2a\u6bd4\u8f83\u5b89\u5168\u7a33\u5b9a\u7684\u957f\u671f\u636e\u70b9\u3002\u6240\u4ee5\u6211\u8ba4\u4e3a\u5e94\u8be5\u5148\u627e\u5230\u8fd9\u5ea7\u6d6e\u7a7a\u57ce\uff0c\u518d\u8003\u8651\u5176\u4ed6\u3002\u201d</p><p>\u201c\u6240\u4ee5\u76ee\u7684\u5730\u5c31\u662f\u8fd9\u4e24\u4e2a\uff1a\u7d2b\u5bb8\u6c99\u6f20\u548c\u8ff7\u5931\u68ee\u6797\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u5b83\u4eec\u770b\u8d77\u6765\u76f8\u9694\u5f88\u8fdc\u3002\u201d</p><p>\u201c\u55ef\uff0c\u662f\u633a\u8fdc\uff0c\u800c\u4e14\u5206\u5c5e\u4e24\u56fd\u5883\u5185\uff0c\u7d2b\u5bb8\u6c99\u6f20\u5728\u7a46\u7f55\uff0c\u8ff7\u5931\u68ee\u6797\u5374\u5728\u6069\u745f\uff0c\u633a\u9ebb\u70e6\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u4e24\u56fd\u662f\u4e16\u4ec7\uff0c\u542c\u8bf4\u73b0\u5728\u6b63\u5728\u5f00\u6218\u5462\u3002\u6211\u7684\u6253\u7b97\u662f\u5148\u53bb\u8ff7\u5931\u68ee\u6797\uff0c\u81f3\u4e8e\u7d2b\u5bb8\u6c99\u6f20\uff0c\u770b\u60c5\u51b5\u518d\u8bf4\u3002\u201d</p><p>\u5927\u5bb6\u5bf9\u6b64\u6ca1\u4ec0\u4e48\u5f02\u8bae\uff0c\u5c31\u50cf\u743c\u6069\u6240\u8a00\uff0c\u4e1c\u57df\u662f\u5f02\u4e61\uff0c\u800c\u4e14\u5175\u8352\u9a6c\u4e71\uff0c\u6709\u4e00\u4e2a\u7a33\u5b9a\u7684\u6839\u636e\u5730\u8fd8\u662f\u6bd4\u8f83\u91cd\u8981\u7684\u3002\u201c\u6240\u4ee5\u6211\u4eec\u63a5\u4e0b\u6765\u7684\u8def\u7ebf\u662f\u5148\u5230\u5f7b\u68ee\u5854\u7684\u8f9b\u5df4\u57ce\uff0c\u7136\u540e\u4e00\u76f4\u5411\u4e1c\uff0c\u8d8a\u8fc7\u873f\u8712\u6cb3\uff0c\u5c31\u5230\u8ff7\u5931\u68ee\u6797\u4e86\u3002\u201d</p><p>\u201c\u73b0\u5728\u98ce\u5411\u548c\u6d0b\u6d41\u90fd\u4e0d\u9519\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u6211\u95ee\u8fc7\u8239\u957f\uff0c\u4ed6\u8bf4\u987a\u5229\u7684\u8bdd\uff0c\u534a\u4e2a\u6708\u4e4b\u540e\u5c31\u4f1a\u62b5\u8fbe\u8f9b\u5df4\u57ce\u3002\u201d</p><p>\u201c\u8fd8\u8981\u534a\u4e2a\u6708\u554a\u3002\u201d\u51db\u82e6\u7740\u8138\uff0c\u5979\u73b0\u5728\u662f\u4e0d\u6655\u8239\u4e86\uff0c\u4f46\u521a\u51fa\u6d77\u65f6\u7684\u65b0\u9c9c\u611f\u4e5f\u8fc7\u4e86\uff0c\u6574\u65e5\u5728\u8239\u4e0a\u5f85\u7740\u5b9e\u5728\u662f\u65e0\u804a\uff0c\u6068\u4e0d\u5f97\u65e9\u70b9\u770b\u5230\u9646\u5730\u3002</p><p>\u201c\u534a\u4e2a\u6708\u662f\u6307\u4e00\u5207\u987a\u5229\u7684\u60c5\u51b5\u4e0b\uff0c\u201d\u838e\u73de\u514b\u7ed9\u5979\u6cfc\u51b7\u6c34\uff0c\u201c\u6d77\u4e0a\u6c14\u8c61\u53d8\u5e7b\u83ab\u6d4b\uff0c\u5982\u679c\u4e0d\u987a\u5229\u7684\u8bdd\uff0c\u4e00\u4e24\u4e2a\u6708\u4e5f\u5f88\u6b63\u5e38\u3002\u201d</p><p>\u201c\u98ce\u5411\u6d0b\u6d41\u4ec0\u4e48\u7684\u90fd\u5728\u5176\u6b21\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u590f\u9c81\u5e15\u514b\u548c\u5854\u6d1b\u65af\u6559\u4f1a\u624d\u662f\u5927\u95ee\u9898\u3002\u201d</p><p>\u590f\u9c81\u5e15\u514b\u6218\u8d25\u9000\u8d70\uff0c\u4f46\u6ca1\u6709\u4eba\u8ba4\u4e3a\u4ed6\u4e0d\u4f1a\u5377\u571f\u91cd\u6765\uff0c\u800c\u4e14\u4e0b\u6b21\u6765\u7684\u65f6\u5019\u6050\u6015\u5c31\u4e0d\u662f\u5355\u67aa\u5339\u9a6c\u4e86\u3002\u8fd9\u91cc\u662f\u832b\u832b\u5927\u6d77\uff0c\u5854\u6d1b\u65af\u6559\u4f1a\u5360\u636e\u4e86\u5730\u5229\u4f18\u52bf\uff0c\u5e94\u4ed8\u8d77\u6765\u662f\u5f88\u4ee4\u4eba\u5934\u75bc\u7684\uff0c\u743c\u6069\u60f3\u6765\u60f3\u53bb\uff0c\u4f3c\u4e4e\u4e5f\u6ca1\u4ec0\u4e48\u597d\u529e\u6cd5\u3002</p><p>\u201c\u590f\u9c81\u5e15\u514b\u5176\u5b9e\u4e5f\u4e0d\u8fc7\u5982\u6b64\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u9ebb\u70e6\u7684\u662f\u4ed6\u90a3\u53ea\u6d77\u602a\u2014\u2014\u5e78\u597d\u73ca\u5609\u59d0\u59d0\u80fd\u591f\u7528\u97f3\u5f8b\u63a7\u5236\u5b83\uff0c\u5426\u5219\u4eca\u5929\u8fd8\u771f\u6709\u70b9\u9ebb\u70e6\u3002\u201d</p><p>\u65b9\u624d\u4e00\u6218\uff0c\u73ca\u5609\u5728\u6700\u540e\u5173\u5934\u7a81\u7136\u51fa\u624b\uff0c\u5979\u4ee5\u957f\u7b1b\u5439\u51fa\u8574\u542b\u9b54\u529b\u7684\u97f3\u7b26\uff0c\u6210\u529f\u50ac\u7720\u4e86\u6d77\u602a\u3002\u8fd9\u79cd\u7b1b\u58f0\u4eba\u7c7b\u7684\u8033\u6735\u662f\u542c\u4e0d\u89c1\u7684\uff0c\u4f46\u6d77\u602a\u5374\u5bf9\u6b64\u6781\u4e3a\u654f\u611f\uff0c\u6700\u7ec8\u590f\u9c81\u5e15\u514b\u4e0d\u5f97\u4e0d\u9000\u8d70\u3002\u201c\u5982\u679c\u4e00\u53ea\u6d77\u602a\uff0c\u90a3\u4e48\u6211\u5e94\u8be5\u662f\u53ef\u4ee5\u5e94\u4ed8\u7684\uff0c\u201d\u73ca\u5609\u8c28\u614e\u5730\u8bf4\uff0c\u201c\u4f46\u6211\u540c\u4e00\u65f6\u95f4\u53ea\u80fd\u50ac\u7720\u4e00\u53ea\u6d77\u602a\uff0c\u5982\u679c\u4ed6\u624b\u4e0b\u8fd8\u6709\u66f4\u591a\u6d77\u602a\uff0c\u90a3\u6211\u5c31\u6ca1\u529e\u6cd5\u4e86\u3002\u201d</p><p>\u201c\u5e94\u8be5\u53ea\u6709\u4e00\u53ea\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6211\u4e4b\u524d\u542c\u8bf4\u8fc7\uff0c\u6d77\u795e\u6559\u4f1a\u517b\u4e86\u4e00\u53ea\u897f\u65af\u5854\uff0c\u60f3\u5fc5\u5c31\u662f\u8fd9\u5bb6\u4f19\u3002\u5982\u679c\u6709\u66f4\u591a\u7684\uff0c\u590f\u9c81\u5e15\u514b\u5e94\u8be5\u4f1a\u5e26\u51fa\u6765\u3002\u201d</p><p>\u201c\u5e0c\u671b\u5982\u6b64\u5427\u3002\u201d</p><p>\u201c\u5bf9\u4e86\uff0c\u6211\u6709\u4e2a\u91cd\u5927\u53d1\u73b0\u3002\u201d\u51db\u5ffd\u7136\u4e3e\u8d77\u624b\uff0c\u4e00\u8138\u8dc3\u8dc3\u6b32\u8bd5\u7684\u795e\u60c5\u8868\u793a\u8981\u53d1\u8a00\u3002</p><p>\u201c\u4f60\u53d1\u73b0\u4ec0\u4e48\u4e86\uff1f\u201d\u743c\u6069\u95ee\u3002\u4ed6\u5bf9\u51db\u80fd\u63d0\u51fa\u4ec0\u4e48\u6709\u4ef7\u503c\u7684\u5efa\u8bae\u4e0d\u62a5\u4efb\u4f55\u671f\u671b\uff0c\u4e0d\u8fc7\u542c\u542c\u4e5f\u65e0\u59a8\u3002</p><p>\u201c\u6839\u636e\u590f\u9c81\u5e15\u514b\u7684\u53cd\u5e94\uff0c\u6211\u5df2\u7ecf\u53ef\u4ee5\u80af\u5b9a\u5409\u52d2\u4eca\u662f\u4e2a\u540c\u6027\u604b\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u5982\u679c\u5409\u52d2\u4eca\u4e0d\u662f\u540c\u6027\u604b\uff0c\u90a3\u4e48\u4ed6\u7406\u5f53\u7acb\u523b\u53cd\u9a73\uff0c\u800c\u4e0d\u662f\u607c\u7f9e\u6210\u6012\uff0c\u201d\u51db\u7ee7\u7eed\u5206\u6790\uff0c\u201c\u4ed6\u607c\u7f9e\u6210\u6012\uff0c\u8bf4\u660e\u4e24\u70b9\uff1a\u7b2c\u4e00\uff0c\u5409\u52d2\u4eca\u662f\u4e2a\u540c\u6027\u604b\uff0c\u800c\u4ed6\u662f\u5176\u7537\u5ba0\uff1b\u7b2c\u4e8c\uff0c\u4ed6\u4e0d\u662f\u540c\u6027\u604b\uff0c\u6240\u4ee5\u5bf9\u6b64\u611f\u5230\u803b\u8fb1\uff0c\u6211\u4e00\u63d0\u4ed6\u5c31\u66b4\u8df3\u5982\u96f7\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u743c\u6069\u5df2\u7ecf\u4e0d\u77e5\u9053\u8be5\u8bf4\u4ec0\u4e48\u597d\uff0c\u660e\u660e\u5927\u5bb6\u662f\u5728\u8ba8\u8bba\u6b63\u4e8b\uff0c\u4f60\u5374\u8dd1\u6765\u5206\u6790\u5409\u52d2\u4eca\u662f\u4e0d\u662f\u540c\u6027\u604b\uff0c\u8fd8\u8bf4\u5f97\u8fd9\u4e48\u4e00\u672c\u6b63\u7ecf\uff0c\u90a3\u5bb6\u4f19\u7684\u6027\u53d6\u5411\u95ee\u9898\u6709\u8c01\u4f1a\u5173\u5fc3\u554a\u3002\u6885\u83f2\u65af\u5374\u6447\u4e86\u6447\u5934\uff0c\u201c\u4e0d\u4e00\u5b9a\u554a\uff0c\u51db\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u7684\u63a8\u6d4b\u5b58\u5728\u6f0f\u6d1e\uff0c\u6709\u5176\u4ed6\u7684\u53ef\u80fd\u6027\u3002\u6bd4\u5982\u8bf4\uff0c\u4ed6\u4e4b\u6240\u4ee5\u53d1\u6012\uff0c\u5e76\u975e\u607c\u7f9e\u6210\u6012\uff0c\u800c\u662f\u56e0\u4e3a\u5409\u52d2\u4eca\u5e76\u975e\u540c\u6027\u604b\uff0c\u4f60\u4fae\u8fb1\u4ed6\u7684\u795e\u7957\uff0c\u4ed6\u5f53\u7136\u8981\u751f\u6c14\uff1b\u518d\u6bd4\u5982\u8bf4\uff0c\u5409\u52d2\u4eca\u786e\u5b9e\u662f\u540c\u6027\u604b\uff0c\u4ed6\u81ea\u5df1\u4e5f\u662f\uff0c\u4f46\u8fd9\u5c5e\u4e8e\u4e2a\u4eba\u9690\u79c1\uff0c\u4ed6\u4e0d\u613f\u610f\u88ab\u4f60\u516c\u5f00\u8fd9\u6837\u8bf4\uff0c\u6240\u4ee5\u5f88\u751f\u6c14\u3002\u201d</p><p>\u201c\u4f60\u8bf4\u7684\u8fd9\u4e9b\u6211\u65e9\u5c31\u90fd\u8003\u8651\u8fc7\u5566\uff0c\u7ed3\u8bba\u662f\u5b83\u4eec\u7edf\u7edf\u4e0d\u6210\u7acb\uff0c\u201d\u51db\u795e\u91c7\u98de\u626c\u5730\u8bf4\uff0c\u201c\u540c\u6027\u604b\u662f\u5f88\u5149\u660e\u6b63\u5927\u7684\u4e8b\u60c5\u554a\uff0c\u53ea\u662f\u4e00\u79cd\u6027\u53d6\u5411\u800c\u5df2\uff0c\u65e0\u6240\u8c13\u597d\u574f\u3002\u6211\u8bf4\u5409\u52d2\u4eca\u662f\u540c\u6027\u604b\uff0c\u600e\u4e48\u80fd\u7b97\u4fae\u8fb1\uff0c\u96be\u9053\u6211\u8bf4\u5409\u52d2\u4eca\u662f\u5f02\u6027\u604b\u4e5f\u662f\u4fae\u8fb1\u5417\uff1f\u4ed6\u5982\u679c\u662f\u540c\u6027\u604b\uff0c\u88ab\u6211\u8bf4\u4e2d\uff0c\u5e94\u8be5\u5f88\u9ad8\u5174\u624d\u5bf9\uff0c\u56e0\u4e3a\u540c\u6027\u604b\u6bd5\u7adf\u662f\u5c11\u6570\u7fa4\u4f53\uff0c\u867d\u7136\u6ca1\u4ec0\u4e48\u4e0d\u597d\uff0c\u4f46\u8fd8\u662f\u96be\u514d\u5fc3\u7075\u5b64\u72ec\u5427\uff0c\u73b0\u5728\u6709\u4eba\u80fd\u591f\u8d4f\u8bc6\u4ed6\uff0c\u8ba4\u53ef\u4ed6\uff0c\u4e00\u773c\u770b\u51fa\u4ed6\u7684\u672c\u8d28\uff0c\u4ed6\u611f\u6fc0\u8fd8\u6765\u4e0d\u53ca\uff0c\u53c8\u600e\u4e48\u4f1a\u751f\u6c14\u5462\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u8bf4\u5f97\u771f\u6709\u9053\u7406\uff0c\u6211\u5df2\u7ecf\u65e0\u8a00\u4ee5\u5bf9\u3002\u201d</p><p>\u201c\u4f46\u5409\u52d2\u4eca\u4e0d\u662f\u6709\u4e00\u4f4d\u738b\u540e\u5417\uff1f\u201d\u838e\u73de\u514b\u63d2\u5634\u3002</p><p>\u201c\u6709\u4e00\u4f4d\u738b\u540e\u5e76\u4e0d\u80fd\u8bf4\u660e\u4ec0\u4e48\uff0c\u5f88\u591a\u540c\u6027\u604b\u4e3a\u4e86\u63a9\u4eba\u8033\u76ee\uff0c\u4e5f\u4f1a\u5a36\u4e00\u4f4d\u59bb\u5b50\u505a\u6446\u8bbe\u3002\u5409\u52d2\u4eca\u8d35\u4e3a\u795e\u738b\uff0c\u5f53\u7136\u4e5f\u4e0d\u80fd\u514d\u4fd7\uff0c\u201d\u51db\u5206\u6790\u5f97\u5934\u5934\u662f\u9053\uff0c\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u90a3\u4f4d\u738b\u540e\u771f\u662f\u4e00\u4f4d\u4e0d\u5e78\u7684\u5973\u5b50\uff0c\u201d\u5979\u6df1\u8868\u540c\u60c5\u5730\u8bf4\uff0c\u201c\u60f3\u60f3\u770b\uff0c\u9ad8\u9ad8\u5174\u5174\u5730\u51fa\u5ac1\uff0c\u6ee1\u5fc3\u6b22\u559c\u5730\u61a7\u61ac\u7740\u672a\u6765\uff0c\u4ee5\u4e3a\u81ea\u5df1\u7684\u4e08\u592b\u662f\u4e00\u4f4d\u5a01\u6b66\u96c4\u58ee\u7684\u5927\u82f1\u96c4\uff0c\u8c01\u6599\u5230\u5c45\u7136\u662f\u4e2a\u4e0d\u6562\u516c\u5f00\u6027\u53d6\u5411\u7684\u602f\u61e6\u4e4b\u5f92\u3002\u4ece\u6b64\u591c\u591c\u7a7a\u623f\u72ec\u5b88\uff0c\u5b64\u6795\u96be\u7720\uff0c\u8f97\u8f6c\u53cd\u4fa7\uff0c\u6cea\u6e7f\u8863\u895f\u2014\u2014\u201d</p><p>\u201c\u6253\u4f4f\u6253\u4f4f\uff0c\u201d\u51db\u7684\u60f3\u8c61\u529b\u5411\u6765\u72b9\u5982\u6c5f\u6cb3\u6cdb\u6ee5\uff0c\u4e00\u65e6\u6253\u5f00\u5c31\u4e00\u53d1\u4e0d\u53ef\u6536\u62fe\uff0c\u743c\u6069\u4e0d\u5f97\u4e0d\u6253\u65ad\uff0c\u201c\u4f60\u8fde\u4eba\u5bb6\u9762\u90fd\u6ca1\u89c1\u8fc7\uff0c\u540d\u5b57\u90fd\u4e0d\u77e5\u9053\uff0c\u600e\u4e48\u77e5\u9053\u4eba\u5bb6\u7684\u5a5a\u59fb\u751f\u6d3b\u5e78\u798f\u4e0d\u5e78\u798f\uff1f\u201d</p><p>\u201c\u6ca1\u6709\u7537\u4eba\u600e\u4e48\u4f1a\u5e78\u798f\uff01\u201d</p><p>\u4f60\u8fd9\u4e2a\u857e\u4e1d\u8fb9\uff0c\u54ea\u6765\u7684\u7acb\u573a\u8bf4\u8fd9\u79cd\u8bdd\uff1f</p><p>\u743c\u6069\u77a5\u4e86\u5979\u4e00\u773c\uff0c\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u51e0\u4e4e\u6240\u6709\u7684\u4eba\u90fd\u77a5\u4e86\u5979\u4e00\u773c\uff0c\u9664\u4e86\u6885\u83f2\u65af\u4e4b\u5916\u3002\u51db\u663e\u7136\u7acb\u523b\u610f\u8bc6\u5230\u81ea\u5df1\u7684\u5931\u8bef\uff0c\u201c\u5979\u53c8\u4e0d\u662f\u540c\u6027\u604b\u2014\u2014\u597d\u5427\uff0c\u6216\u8bb8\u5979\u662f\uff0c\u4f46\u795e\u738b\u603b\u4e0d\u4f1a\u5141\u8bb8\u81ea\u5df1\u59bb\u5b50\u53bb\u5077\u60c5\u5427\uff0c\u4e0d\u7ba1\u5bf9\u8c61\u662f\u7537\u4eba\u8fd8\u662f\u5973\u4eba\u3002\u201d</p><p>\u201c\u8c01\u77e5\u9053\uff0c\u8bf4\u4e0d\u5b9a\u4eba\u5bb6\u5bb6\u5ead\u6c11\u4e3b\uff0c\u592b\u59bb\u611f\u60c5\u72ec\u7acb\uff0c\u4e92\u4e0d\u5e72\u6d89\u5462\u3002\u201d</p><p>\u51db\u504f\u7740\u5c0f\u8111\u888b\uff0c\u60f3\u4e86\u51e0\u79d2\u949f\uff0c\u201c\u4f60\u8fd9\u4e48\u5173\u5fc3\u4eba\u5bb6\u592b\u59bb\u7684\u611f\u60c5\u751f\u6d3b\u505a\u4ec0\u4e48\uff0c\u83ab\u975e\u6709\u4e0d\u8f68\u4f01\u56fe\uff0c\u60f3\u8981\u4e58\u865a\u800c\u5165\uff1f\u201d</p><p>\u660e\u660e\u662f\u4f60\u4e00\u76f4\u5728\u5173\u5fc3\u597d\u5427\u3002</p><p>\u201c\u6211\u662f\u5973\u5b69\u5b50\uff0c\u5173\u5fc3\u8fd9\u4e9b\u5bb6\u957f\u91cc\u77ed\u5f88\u6b63\u5e38\uff1b\u4f60\u4e00\u4e2a\u7537\u4eba\uff0c\u96be\u9053\u4e0d\u5e94\u8be5\u8003\u8651\u70b9\u6b63\u7ecf\u4e8b\u5417\uff1f\u201d</p><p>\u6211\u4eec\u672c\u6765\u4e0d\u5c31\u662f\u5728\u8ba8\u8bba\u6b63\u7ecf\u4e8b\u5417\uff0c\u5230\u5e95\u662f\u8c01\u5728\u4e0d\u505c\u7684\u6b6a\u697c\u8dd1\u9898\u554a\uff01</p><p>\u201c\u8fd8\u662f\u8bf4\u8bf4\u5bfb\u627e\u6d6e\u7a7a\u57ce\u7684\u4e8b\u60c5\u5427\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u6211\u5bf9\u8fd9\u4e2a\u5f88\u611f\u5174\u8da3\u3002\u201d</p><p>\u743c\u6069\u671d\u9b45\u9b54\u70b9\u4e86\u70b9\u5934\uff0c\u6b23\u6170\u4e8e\u8bdd\u9898\u603b\u7b97\u53c8\u88ab\u62c9\u56de\u5230\u6b63\u8f68\uff0c\u7136\u540e\u4ed6\u5c31\u542c\u89c1\u51db\u5174\u81f4\u52c3\u52c3\u7684\u53d1\u8a00\uff1a\u201c\u7b49\u6211\u4eec\u627e\u5230\u90a3\u5ea7\u6d6e\u7a7a\u57ce\uff0c\u743c\u6069\u4f60\u5c31\u53ef\u4ee5\u5f53\u57ce\u4e3b\u4e86\uff0c\u751a\u81f3\u81ea\u5c01\u4e3a\u738b\u4e5f\u53ef\u4ee5\u5427\uff0c\u201d\u5979\u4e0d\u7531\u81ea\u4e3b\u5730\u9732\u51fa\u5f00\u5fc3\u7684\u7b11\u5bb9\uff0c\u201c\u90a3\u6211\u5c82\u4e0d\u5c31\u662f\u738b\u540e\u4e86\uff1f\u738b\u540e\u51db\u5c0f\u59d0\uff0c\u4e00\u542c\u8d77\u6765\u5c31\u89c9\u5f97\u5f88\u9ad8\u8d35\u7684\u6837\u5b50\u3002\u201d</p><p>\u2026\u2026\u6211\u5c31\u7b97\u771f\u7684\u505a\u4e86\u57ce\u4e3b\uff0c\u81ea\u5c01\u4e3a\u738b\uff0c\u4e5f\u4e0d\u4f1a\u7acb\u4f60\u505a\u738b\u540e\u554a\u3002\u8981\u662f\u7ed9\u540e\u5bab\u6392\u5e8f\u7684\u8bdd\uff0c\u600e\u4e48\u4e5f\u8f6e\u4e0d\u5230\u4f60\uff0c\u522b\u4eba\u4e14\u4e0d\u8bf4\uff0c\u81f3\u5c11\u73ca\u5609\u548c\u827e\u5f25\u8587\u80af\u5b9a\u6bd4\u4f60\u987a\u4f4d\u4f18\u5148\u5427\u3002</p><p>\u90a3\u4f60\u8981\u9009\u8c01\uff1f\u827e\u5f25\u8587\u8fd8\u662f\u73ca\u5609\uff1f\u51db\u6311\u8845\u5730\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u4eff\u4f5b\u5728\u8bf4\uff1a\u4f60\u6562\u4ece\u4e2d\u9009\u4e00\u4e2a\u5417\uff1f</p><p>\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u51b3\u5b9a\u6682\u65f6\u653e\u5f03\u7ea0\u7f20\u8fd9\u4e2a\u95ee\u9898\u3002</p><p>\u201c\u80fd\u4e0d\u80fd\u627e\u5230\u5148\u4e0d\u8bf4\u3002\u5c31\u7b97\u627e\u5230\u6d6e\u7a7a\u57ce\uff0c\u4e5f\u53ea\u662f\u4e00\u5ea7\u7a7a\u57ce\u800c\u5df2\uff0c\u201d\u838e\u73de\u514b\u660e\u663e\u8981\u51b7\u9759\u8bb8\u591a\uff0c\u201c\u6ca1\u6709\u4eba\uff0c\u6ca1\u6709\u94b1\uff0c\u5373\u4fbf\u5bc6\u745f\u80fd\u6838\u5b8c\u597d\uff0c\u4ec5\u51ed\u6211\u4eec\u8fd9\u51e0\u4e2a\u4eba\uff0c\u4e5f\u662f\u5360\u4e0d\u4f4f\u7684\u3002\u201d</p><p>\u8fd9\u8bdd\u662f\u8bf4\u5f97\u6ca1\u9519\uff0c\u8981\u80fd\u591f\u5efa\u7acb\u8d77\u4e00\u65b9\u52bf\u529b\uff0c\u6709\u4e09\u4e2a\u57fa\u672c\u8981\u7d20\uff1a\u5730\u3001\u4eba\u3001\u94b1\u3002\u5730\u76d8\u5f53\u7136\u91cd\u8981\uff0c\u6ca1\u6709\u5b83\uff0c\u4e00\u5207\u5c31\u662f\u65e0\u6839\u6d6e\u840d\uff0c\u4f46\u4eba\u53e3\u3001\u94b1\u8d22\u4e5f\u540c\u6837\u5fc5\u4e0d\u53ef\u5c11\u3002\u743c\u6069\u73b0\u5728\u662f\u4e09\u6837\u90fd\u6ca1\u6709\uff0c\u5047\u8bbe\u80fd\u591f\u987a\u5229\u627e\u5230\u6d6e\u7a7a\u57ce\uff0c\u90a3\u4e48\u5730\u76d8\u8fd9\u4e00\u9879\u8981\u6c42\u7b97\u662f\u57fa\u672c\u6ee1\u8db3\uff0c\u4f46\u4eba\u53e3\u3001\u94b1\u8d22\u4ecd\u7136\u662f\u4e2a\u5927\u95ee\u9898\uff0c\u8fde\u89e3\u51b3\u7684\u601d\u8def\u548c\u65b9\u5411\u90fd\u6ca1\u6709\u3002</p><p>\u201c\u5148\u627e\u5230\u6d6e\u7a7a\u57ce\u518d\u8bf4\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53cd\u6b63\u65e0\u8bba\u5982\u4f55\uff0c\u6211\u4eec\u90fd\u5f97\u6709\u4e00\u4e2a\u636e\u70b9\u3002\u81f3\u4e8e\u627e\u5230\u4ee5\u540e\u7684\u4e8b\u60c5\uff0c\u5148\u627e\u5230\u518d\u8bf4\u3002\u53e6\u5916\uff0c\u63a5\u4e0b\u6765\u8981\u7279\u522b\u5c0f\u5fc3\uff0c\u590f\u9c81\u5e15\u514b\u968f\u65f6\u53ef\u80fd\u5377\u571f\u91cd\u6765\uff0c\u654c\u6697\u6211\u660e\uff0c\u4e0b\u6b21\u672a\u5fc5\u5c31\u8fd9\u4e48\u597d\u6253\u53d1\u4e86\u3002\u201d</p><p>\u5bf9\u4e8e\u743c\u6069\u7684\u8bf4\u6cd5\uff0c\u5927\u5bb6\u90fd\u6ca1\u6709\u4ec0\u4e48\u5f02\u8bae\uff0c\u7b97\u662f\u57fa\u672c\u8fbe\u6210\u5171\u8bc6\u3002</p><p>\u51fa\u4e4e\u6240\u6709\u4eba\u7684\u610f\u6599\uff0c\u63a5\u4e0b\u6765\u7684\u822a\u7a0b\u6bd4\u60f3\u8c61\u4e2d\u8981\u987a\u5229\u8bb8\u591a\uff0c\u590f\u9c81\u5e15\u514b\u518d\u4e5f\u6ca1\u6709\u51fa\u73b0\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u9c7c\u4eba\u3001\u6d77\u602a\u9732\u9762\uff0c\u4eff\u4f5b\u5168\u90fd\u77e5\u96be\u800c\u9000\u4e86\u3002\u6070\u597d\u98ce\u5411\u548c\u6d0b\u6d41\u4e5f\u6bd4\u8f83\u987a\uff0c\u534a\u4e2a\u6708\u4e4b\u540e\uff0c\u4ed6\u4eec\u7ec8\u4e8e\u770b\u89c1\u4e86\u8f9b\u5df4\u57ce\u7684\u6807\u5fd7\u6027\u5efa\u7b51\u7269\uff1a\u9752\u94dc\u5927\u706f\u5854\u3002</p></article>"}}}]);
