(window.webpackJsonp=window.webpackJsonp||[]).push([[1751],{1774:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u516d\u8282 \u8bc5\u5492",content:"<article><h2>\u7b2c\u516d\u8282 \u8bc5\u5492</h2><p>\u62b5\u8fbe\u8f9b\u5df4\u57ce\u7684\u65f6\u5019\u662f\u4e0b\u5348\u56db\u70b9\u949f\uff0c\u98de\u7fd4\u86c7\u5973\u53f7\u5728\u9886\u822a\u5458\u5728\u6307\u5f15\u4e0b\u987a\u5229\u505c\u9760\u5728\u7801\u5934\uff0c\u4f46\u743c\u6069\u7b49\u4eba\u5e76\u4e0d\u80fd\u7acb\u523b\u4e0b\u8239\u3002\u4ed6\u4eec\u662f\u5916\u7c4d\u4eba\u58eb\uff0c\u5165\u5883\u9700\u8981\u5148\u529e\u7406\u767b\u8bb0\uff0c\u8981\u586b\u5199\u4e00\u5927\u5806\u8868\u683c\uff0c\u7b7e\u4e00\u5927\u5806\u6587\u4ef6\uff0c\u603b\u4e4b\u4e00\u770b\u5c31\u8ba9\u4eba\u5934\u5927\uff0c\u743c\u6069\u4e8e\u662f\u6307\u6d3e\u838e\u73de\u514b\u53bb\u89e3\u51b3\uff0c\u5176\u4ed6\u4eba\u5219\u5728\u623f\u95f4\u91cc\uff0c\u542c\u6885\u83f2\u65af\u8bb2\u8bfe\u3002</p><p>\u5230\u4e86\u5f7b\u68ee\u5854\uff0c\u5c31\u7b97\u662f\u8fdb\u5165\u4e1c\u57df\u4e86\u3002\u4e1c\u57df\u5c01\u95ed\u9694\u7edd\uff0c\u5176\u98ce\u571f\u4eba\u60c5\u3001\u6587\u5316\u5386\u53f2\uff0c\u65b9\u65b9\u9762\u9762\u548c\u4e2d\u571f\u90fd\u6709\u7740\u975e\u5e38\u5927\u7684\u5dee\u5f02\uff0c\u743c\u6069\u7b49\u4eba\u4f5c\u4e3a\u5916\u6765\u6e38\u5ba2\uff0c\u63d0\u524d\u5b66\u4e60\u4e00\u4e9b\u76f8\u5173\u77e5\u8bc6\uff0c\u8fd8\u662f\u5f88\u6709\u5fc5\u8981\u7684\u3002\u5f88\u81ea\u7136\uff0c\u8bb2\u8bfe\u7684\u8d23\u4efb\u5c31\u843d\u5230\u4e86\u6885\u83f2\u65af\u8eab\u4e0a\u3002</p><p>\u5176\u5b9e\u6885\u83f2\u65af\u539f\u672c\u5bf9\u201c\u4e1c\u57df\u201d\u7684\u4e86\u89e3\u4e5f\u4e0d\u7b97\u5f88\u591a\uff0c\u5979\u867d\u7136\u662f\u51fa\u751f\u5728\u5f7b\u68ee\u5854\uff0c\u4f46\u4ece\u5c0f\u968f\u6bcd\u4eb2\u5c45\u4f4f\u5728\u6df1\u5c71\u91cc\uff0c\u5bf9\u5916\u9762\u7684\u4e16\u754c\u5e76\u4e0d\u5982\u4f55\u719f\u6089\uff0c\u6bcd\u4eb2\u53bb\u4e16\u540e\u624d\u548c\u51db\u51fa\u6765\u95ef\u8361\uff0c\u6ca1\u591a\u4e45\u5c31\u88ab\u5927\u4e3b\u6559\u5e26\u5230\u4e86\u4e2d\u571f\u3002\u4f46\u5728\u51fa\u6d77\u4e4b\u524d\uff0c\u5979\u5728\u53f6\u5f18\u57ce\u4ece\u77e5\u8bc6\u6559\u4f1a\u91cc\u5f04\u4e86\u4e00\u5927\u6279\u8d44\u6599\uff0c\u8fd9\u6bb5\u65f6\u95f4\u5728\u8239\u4e0a\u65e0\u4e8b\uff0c\u4ed4\u7ec6\u7814\u8bfb\uff0c\u7ed3\u5408\u4e4b\u524d\u7684\u7ecf\u5386\u89c1\u95fb\uff0c\u4fe8\u7136\u5df2\u7ecf\u6210\u4e86\u4e13\u5bb6\u4e86\u3002</p><p>\u8fd9\u6bb5\u65f6\u95f4\u4ee5\u6765\uff0c\u6709\u5173\u4e1c\u57df\u5386\u53f2\u3001\u5730\u7406\u7b49\u65b9\u9762\u7684\u77e5\u8bc6\uff0c\u6885\u83f2\u65af\u90fd\u5df2\u7ecf\u7ed9\u5927\u5bb6\u8865\u4e60\u5f97\u5dee\u4e0d\u591a\u4e86\uff0c\u4eca\u5929\u53ea\u5269\u4e0b\u6700\u540e\u4e00\u8bfe\uff0c\u662f\u201c\u5b97\u6559\u201d\u548c\u201c\u795e\u738b\u201d\u3002</p><p>\u201c\u4e2d\u571f\u4eba\u5f88\u96be\u7406\u89e3\u4e1c\u57df\u7684\u5b97\u6559\uff0c\u4e5f\u5f88\u96be\u7406\u89e3\u4e1c\u57df\u7684\u795e\u738b\uff0c\u56e0\u4e3a\u90a3\u662f\u4e00\u79cd\u548c\u6211\u4eec\u6240\u719f\u77e5\u7684\u6982\u5ff5\u5b8c\u5168\u683c\u683c\u4e0d\u5165\u7684\u4e1c\u897f\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8fd9\u4e00\u5207\uff0c\u8981\u5148\u4ece\u4e1c\u57df\u4eba\u7684\u4e16\u754c\u89c2\u8bf4\u8d77\u3002\u201d</p><p>\u5bf9\u4e8e\u4e00\u540d\u4e2d\u571f\u4eba\u2014\u2014\u5c24\u5176\u662f\u6709\u8f83\u9ad8\u5b66\u8bc6\u548c\u89c1\u95fb\u7684\u4e2d\u571f\u4eba\u2014\u2014\u6765\u8bf4\uff0c\u201c\u4e16\u754c\u201d\u662f\u591a\u91cd\u7684\uff0c\u662f\u590d\u6742\u7684\uff0c\u4e30\u5bcc\u591a\u5f69\u7684\uff0c\u51e1\u4eba\u5c45\u4f4f\u7684\u662f\u7269\u8d28\u754c\uff0c\u795e\u7957\u5927\u591a\u90fd\u5c45\u4f4f\u5728\u5929\u754c\uff0c\u4e0d\u540c\u7684\u795e\u6709\u4e0d\u540c\u7684\u795e\u56fd\uff0c\u6bcf\u4e2a\u795e\u56fd\u90fd\u662f\u4e00\u4e2a\u72ec\u7acb\u7684\u4e16\u754c\uff0c\u90aa\u9b54\u5c45\u4f4f\u5728\u4e0b\u5c42\u754c\uff0c\u5176\u4e2d\u53c8\u5206\u5730\u72f1\u548c\u6df1\u6e0a\uff0c\u5143\u7d20\u5728\u5185\u5c42\u754c\uff0c\u5728\u7269\u8d28\u754c\u3001\u5929\u754c\u3001\u4e0b\u5c42\u754c\u3001\u5185\u5c42\u754c\u4e4b\u95f4\uff0c\u53c8\u6709\u661f\u754c\uff0c\u7b49\u7b49\uff0c\u603b\u4e4b\u5f88\u591a\u5f88\u6df7\u4e71\u3002\u4f46\u5728\u4e00\u540d\u4e1c\u57df\u4eba\u7684\u89c2\u5ff5\u91cc\uff0c\u4e16\u754c\u5f88\u7b80\u5355\uff0c\u4e00\u5206\u4e3a\u4e8c\uff0c\u6e05\u695a\u660e\u4e86\uff1a\u51e1\u95f4\u548c\u51a5\u754c\u3002</p><p>\u51e1\u95f4\u662f\u751f\u8005\u7684\u5bb6\u56ed\uff0c\u51a5\u754c\u662f\u6b7b\u8005\u7684\u56fd\u5ea6\u3002</p><p>\u201c\u795e\u738b\u4eec\u5171\u540c\u7edf\u6cbb\u7740\u51e1\u95f4\uff0c\u800c\u51a5\u754c\u5219\u7531\u4e00\u4f4d\u2018\u51a5\u795e\u2019\u638c\u63e1\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u51a5\u795e\u6c38\u5c45\u51a5\u754c\uff0c\u4e0d\u80fd\u6765\u5230\u51e1\u95f4\uff0c\u5176\u624b\u4e0b\u6709\u8bf8\u591a\u90aa\u9b54\uff0c\u64c5\u957f\u5404\u79cd\u8be1\u79d8\u7684\u5deb\u672f\u3002\u51e1\u4eba\u6b7b\u540e\uff0c\u7075\u9b42\u5760\u5165\u51a5\u754c\uff0c\u5373\u6210\u4e3a\u51a5\u795e\u7684\u81e3\u6c11\uff0c\u4f9b\u5176\u9a71\u9063\uff0c\u6c38\u8fdc\u65e0\u6cd5\u518d\u8fd4\u56de\u51e1\u95f4\u3002\u201d</p><p>\u201c\u51e1\u95f4\u5c31\u662f\u6211\u4eec\u8bf4\u7684\u7269\u8d28\u754c\uff0c\u51a5\u754c\u2026\u2026\u5c31\u662f\u6b7b\u795e\u7684\u795e\u56fd\uff1f\u201d\u743c\u6069\u8bd5\u7740\u7406\u89e3\u3002</p><p>\u201c\u5bf9\uff0c\u4e5f\u4e0d\u5bf9\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6211\u8bf4\u4e86\uff0c\u4f60\u4e0d\u80fd\u7528\u5df2\u6709\u7684\u6982\u5ff5\u53bb\u7406\u89e3\u3002\u201d</p><p>\u201c\u6709\u4ec0\u4e48\u533a\u522b\uff1f\u201d</p><p>\u201c\u533a\u522b\u5f88\u5927\uff0c\u4f60\u80fd\u60f3\u8c61\u4e00\u4e2a\u51e1\u4eba\uff0c\u767d\u5929\u5728\u7269\u8d28\u754c\uff0c\u591c\u91cc\u5728\u5929\u754c\u6216\u8005\u4e0b\u5c42\u754c\u5417\uff1f\u201d</p><p>\u6240\u6709\u4eba\u90fd\u88ab\u5f04\u7cca\u6d82\u4e86\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5728\u4e1c\u57df\u4eba\u7684\u89c2\u5ff5\u91cc\uff0c\u4ed6\u4eec\u767d\u5929\u9192\u7740\u7684\u65f6\u5019\uff0c\u7075\u9b42\u5728\u51e1\u95f4\uff0c\u591c\u665a\u7761\u7740\u7684\u65f6\u5019\uff0c\u7075\u9b42\u5c31\u8fdb\u5165\u4e86\u51a5\u754c\uff0c\u7b2c\u4e8c\u5929\u65e9\u6668\u9192\u6765\uff0c\u7075\u9b42\u53c8\u4ece\u51a5\u754c\u56de\u5230\u51e1\u95f4\uff0c\u5982\u6b64\u5faa\u73af\uff0c\u76f4\u5230\u6b7b\u4ea1\uff0c\u7075\u9b42\u624d\u629b\u5f03\u8eaf\u58f3\uff0c\u8fdb\u5165\u51a5\u754c\uff0c\u6c38\u4e0d\u8fd4\u56de\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u8fd8\u771f\u662f\u6298\u817e\u3002\u201d</p><p>\u5bf9\u4e8e\u4e1c\u57df\u4eba\u6765\u8bf4\uff0c\u51e1\u95f4\u548c\u51a5\u754c\uff0c\u5e76\u4e0d\u662f\u50cf\u7269\u8d28\u754c\u548c\u5929\u754c\u3001\u4e0b\u5c42\u754c\u90a3\u6837\u622a\u7136\u533a\u5206\uff0c\u4e92\u4e0d\u5e72\u6d89\uff0c\u800c\u662f\u5bc6\u5207\u4ea4\u9519\u7684\u3002\u51e1\u4eba\u4eff\u4f5b\u65c5\u5ba2\uff0c\u5728\u51e1\u95f4\u548c\u51a5\u754c\u4e2d\u4e0d\u65ad\u7a7f\u68ad\uff0c\u76f4\u5230\u6b7b\u4ea1\uff0c\u624d\u662f\u65c5\u9014\u7684\u7ed3\u675f\u3002</p><p>\u4f46\u795e\u738b\u4f8b\u5916\u3002</p><p>\u5982\u679c\u6309\u7167\u4e2d\u571f\u5927\u9646\u7684\u5b97\u6559\u6982\u5ff5\uff0c\u4e1c\u57df\u662f\u6839\u672c\u5c31\u6ca1\u6709\u201c\u795e\u660e\u201d\u7684\uff0c\u53ea\u6709\u4e00\u4e9b\u4f3c\u795e\u53c8\u975e\u795e\uff0c\u4f3c\u4eba\u53c8\u975e\u4eba\u7684\u5b58\u5728\uff0c\u4e1c\u57df\u4eba\u79f0\u4e4b\u4e3a\u201c\u795e\u738b\u201d\u3002\u795e\u738b\u548c\u51e1\u4eba\u4e00\u6837\uff0c\u4f1a\u6210\u957f\uff0c\u4f1a\u8870\u8001\uff0c\u4f1a\u6b7b\u4ea1\u2014\u2014\u4f46\u8fd8\u4f1a\u91cd\u751f\u3002</p><p>\u201c\u795e\u738b\u6709\u79d8\u6cd5\uff0c\u5176\u6b7b\u540e\uff0c\u8eaf\u58f3\u548c\u4e00\u90e8\u5206\u8f83\u4f4e\u7684\u7075\u9b42\u7559\u5728\u51e1\u95f4\u8f6e\u56de\uff0c\u4ece\u5a74\u513f\u91cd\u65b0\u5f00\u59cb\uff0c\u53e6\u5916\u4e00\u90e8\u5206\u8f83\u9ad8\u7684\u7075\u9b42\u5219\u8fdb\u5165\u51a5\u754c\u3002\u4e00\u6bb5\u65f6\u95f4\u4e4b\u540e\u2014\u2014\u901a\u5e38\u662f\u5341\u516d\u5e74\uff0c\u4f46\u4e5f\u53ef\u80fd\u4f1a\u65e9\u51e0\u5e74\u6216\u8005\u665a\u51e0\u5e74\u2014\u2014\u8f83\u9ad8\u7684\u7075\u9b42\u4f1a\u4ece\u51a5\u754c\u8fd4\u56de\u51e1\u95f4\uff0c\u56de\u5230\u4ed6\u7684\u8eaf\u58f3\u4e4b\u4e2d\uff0c\u91cd\u65b0\u6210\u4e3a\u795e\u738b\u3002\u8fd9\u4e2a\u8fc7\u7a0b\u88ab\u79f0\u4e4b\u4e3a\u2018\u795e\u738b\u82cf\u9192\u2019\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u662f\u8f83\u4f4e\u7684\u7075\u9b42\u3001\u8f83\u9ad8\u7684\u7075\u9b42\uff0c\u201d\u73ca\u5609\u4e0d\u660e\u767d\uff0c\u201c\u540c\u4e00\u4e2a\u4eba\u7684\u7075\u9b42\u8fd8\u5206\u6210\u4e24\u90e8\u5206\uff0c\u8fd8\u6709\u9ad8\u4f4e\u4e4b\u5206\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u4e1c\u57df\u4eba\u7684\u89c2\u5ff5\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u4e1c\u57df\u4eba\u8ba4\u4e3a\uff0c\u51e1\u4eba\u53ea\u6709\u4e00\u4e2a\u7075\u9b42\uff0c\u4f46\u795e\u738b\u4e0d\u540c\uff0c\u7075\u9b42\u5206\u4e3a\u4e24\u90e8\u5206\uff0c\u4e00\u90e8\u5206\u662f\u5916\u56f4\u7684\uff0c\u5c42\u6b21\u8f83\u4f4e\uff0c\u53e6\u4e00\u90e8\u5206\u662f\u6838\u5fc3\u7684\uff0c\u5c42\u6b21\u8f83\u9ad8\uff0c\u795e\u738b\u7684\u529b\u91cf\u3001\u8bb0\u5fc6\uff0c\u90fd\u50a8\u5b58\u5728\u540e\u8005\u4e4b\u4e2d\u3002\u201d</p><p>\u73ca\u5609\u5fae\u5fae\u76b1\u7709\uff0c\u663e\u7136\u5f88\u96be\u9002\u5e94\u8fd9\u79cd\u5947\u602a\u7684\u89c2\u5ff5\uff0c\u743c\u6069\u53cd\u5012\u80fd\u591f\u5f88\u5bb9\u6613\u5730\u7406\u89e3\uff0c\u6bd5\u7adf\u8bb0\u5fc6\u4e2d\u6709\u90a3\u4e48\u591a\u6545\u4e8b\uff0c\u5957\u7528\u4e00\u4e0b\u201c\u9b42\u9b44\u201d\u3001\u201c\u5143\u795e\u201d\u3001\u201c\u771f\u7075\u201d\u4e4b\u7c7b\uff0c\u5c31\u57fa\u672c\u660e\u767d\u4e86\u3002</p><p>\u201c\u4f46\u6211\u542c\u8bf4\u4e1c\u57df\u7684\u795e\u738b\u4e5f\u662f\u4f1a\u6b7b\u7684\u554a\u2014\u2014\u6211\u662f\u6307\u5f7b\u5e95\u6b7b\u6389\u90a3\u79cd\uff0c\u201d\u838e\u73de\u514b\u63d0\u51fa\u7591\u95ee\uff0c\u201c\u5c31\u50cf\u6069\u745f\u7684\u795e\u738b\uff0c\u51e0\u5343\u5e74\u4e0b\u6765\uff0c\u5df2\u7ecf\u6b7b\u5f97\u4e00\u5e72\u4e8c\u51c0\uff0c\u4e00\u4e2a\u90fd\u4e0d\u5269\u4e86\u3002\u4ed6\u4eec\u7684\u8f6c\u4e16\u79d8\u6cd5\uff0c\u600e\u4e48\u6ca1\u6709\u8d77\u4f5c\u7528\u5462\uff1f\u201d</p><p>\u201c\u8fd9\u91cc\u4f9d\u7136\u8fd8\u662f\u6982\u5ff5\u5206\u6b67\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6069\u745f\u795e\u5df2\u7ecf\u5168\u90e8\u9668\u843d\uff0c\u8fd9\u662f\u6211\u4eec\u7684\u770b\u6cd5\uff0c\u662f\u4ece\u6211\u4eec\u7684\u89c6\u89d2\u548c\u6807\u51c6\u5f97\u51fa\u7684\u7ed3\u8bba\uff0c\u4e1c\u57df\u4eba\u53ef\u4e0d\u662f\u8fd9\u6837\u8ba4\u4e3a\u3002\u201d</p><p>\u5728\u4e1c\u57df\u4eba\u770b\u6765\uff0c\u795e\u738b\u672c\u6765\u5c31\u662f\u4e00\u79cd\u201c\u4f1a\u6b7b\uff0c\u6b7b\u4e86\u591a\u5e74\u53c8\u4f1a\u590d\u6d3b\uff0c\u6d3b\u7740\u6d3b\u7740\u53c8\u4f1a\u6b7b\uff0c\u5982\u6b64\u5faa\u73af\u201d\u7684\u5b58\u5728\u3002\u90a3\u4e48\uff0c\u4e00\u4f4d\u795e\u738b\u6b7b\u4e86\uff0c\u4ed6\u7684\u8eaf\u58f3\u548c\u8f83\u4f4e\u7684\u7075\u9b42\u5728\u51e1\u95f4\u4e0d\u65ad\u8f6e\u56de\uff0c\u8f83\u9ad8\u7684\u7075\u9b42\u5728\u51a5\u754c\u7b49\u5f85\u56de\u5f52\u2014\u2014\u6216\u8bb8\u8fd9\u6b21\u56de\u5f52\u7684\u65f6\u95f4\u76f8\u5bf9\u957f\u4e86\u70b9\uff0c\u4f46\u8fd9\u4e5f\u5e76\u4e0d\u4ee3\u8868\u4ec0\u4e48\uff0c\u548c\u201c\u9668\u843d\u201d\u4ec0\u4e48\u7684\u66f4\u662f\u626f\u4e0d\u4e0a\u5173\u7cfb\u3002\u7528\u4e00\u53e5\u4fd7\u8bed\u89e3\u91ca\u5c31\u662f\uff1a\u795e\u738b\u4e0d\u4f1a\u6b7b\uff0c\u53ea\u662f\u7761\u7740\u4e86\u800c\u5df2\u3002</p><p>\u201c\u81f3\u4e8e\u4e3a\u4ec0\u4e48\u4f1a\u6c89\u7761\u8fd9\u4e48\u4e45\uff0c\u8fd8\u6ca1\u6709\u8fdb\u5165\u4e0b\u4e00\u4e2a\u8f6c\u4e16\u8f6e\u56de\uff0c\u79d8\u6cd5\u4e3a\u4ec0\u4e48\u4f1a\u5931\u6548\u2014\u2014\u81f3\u5c11\u6211\u4eec\u770b\u8d77\u6765\u662f\u5931\u6548\u4e86\uff0c\u8fd9\u5176\u4e2d\u7f18\u6545\uff0c\u5c31\u4e0d\u5f97\u800c\u77e5\u4e86\u3002\u8fd9\u65b9\u9762\u7684\u7814\u7a76\u6709\u4e00\u4e9b\uff0c\u4f20\u8bf4\u66f4\u591a\uff0c\u4f46\u90fd\u6ca1\u6709\u6bd4\u8f83\u9760\u8c31\u7684\u7ed3\u8bba\u3002\u8f6c\u4e16\u79d8\u6cd5\u662f\u4e1c\u57df\u795e\u738b\u6700\u5927\u7684\u79d8\u5bc6\uff0c\u5916\u4eba\u53ea\u80fd\u770b\u5230\u4e00\u4e9b\u8868\u9762\u73b0\u8c61\uff0c\u5177\u4f53\u5185\u60c5\u5982\u4f55\uff0c\u6ca1\u4eba\u80fd\u591f\u77e5\u6653\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u6709\u70b9\u81ea\u6b3a\u6b3a\u4eba\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e00\u4f4d\u795e\u738b\u6b7b\u4e86\uff0c\u7075\u9b42\u5728\u51a5\u754c\uff0c\u65e2\u7136\u4e00\u76f4\u56de\u4e0d\u6765\uff0c\u90a3\u548c\u6b7b\u4e86\u53c8\u6709\u4ec0\u4e48\u5206\u522b\u3002\u786c\u8981\u8bf4\u8fd9\u4e0d\u7b97\u9668\u843d\uff0c\u53ea\u662f\u7761\u7740\uff0c\u5c31\u7eaf\u7cb9\u662f\u6982\u5ff5\u6e38\u620f\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u4e0d\uff0c\u6b7b\u4e86\u5c31\u662f\u6b7b\u4e86\uff0c\u4e0d\u4f1a\u518d\u68a6\u6e38\uff1b\u80fd\u591f\u5076\u5c14\u68a6\u6e38\u7684\uff0c\u5c31\u4e0d\u80fd\u8bf4\u662f\u6b7b\u4e86\uff0c\u53ea\u662f\u5728\u6c89\u7761\u3002\u201d</p><p>\u68a6\u6e38\uff1f</p><p>\u201c\u6211\u8bf4\u8fc7\uff0c\u5728\u4e1c\u57df\u7684\u89c2\u5ff5\u91cc\uff0c\u51e1\u95f4\u548c\u51a5\u754c\u3001\u751f\u4e0e\u6b7b\u7684\u754c\u9650\uff0c\u5e76\u4e0d\u662f\u90a3\u4e48\u622a\u7136\u5206\u660e\u7684\u3002\u201d</p><p>\u65e0\u8bba\u51e1\u95f4\u8fd8\u662f\u51a5\u754c\uff0c\u90fd\u662f\u8fd9\u4e2a\u4e16\u754c\u7684\u4e00\u90e8\u5206\uff0c\u4eba\u6b7b\u540e\u5230\u51a5\u754c\uff0c\u53ea\u4e0d\u8fc7\u662f\u6362\u4e86\u4e2a\u5730\u65b9\u5c45\u4f4f\u751f\u6d3b\uff0c\u5e76\u975e\u5c31\u6b64\u6d88\u5931\uff0c\u751f\u8005\u7ecf\u7531\u796d\u53f8\u6c9f\u901a\uff0c\u53ef\u4ee5\u4e0e\u6b7b\u8005\u4ea4\u8c08\u3002\u800c\u6b7b\u53bb\u7684\u795e\u738b\uff0c\u4e5f\u4ecd\u7136\u53ef\u4ee5\u5728\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u4e0e\u51e1\u95f4\u7684\u4fe1\u5f92\u8054\u7cfb\u3001\u6c9f\u901a\u3002\u8fd9\u548c\u4e2d\u571f\u4eba\u7684\u89c2\u5ff5\u662f\u5b8c\u5168\u4e0d\u540c\u7684\uff0c\u4e2d\u571f\u4eba\u8ba4\u4e3a\u51e1\u4eba\u4f1a\u6b7b\uff0c\u795e\u7957\u5374\u662f\u4e0d\u673d\u7684\u5b58\u5728\uff0c\u800c\u51e1\u4eba\u4e00\u65e6\u6b7b\u540e\uff0c\u7075\u9b42\u5347\u5165\u795e\u56fd\uff0c\u6e10\u6e10\u4fbf\u4e0e\u795e\u7957\u878d\u5408\u4e3a\u4e00\uff0c\u5316\u4f5c\u795e\u56fd\u7684\u4e00\u90e8\u5206\uff0c\u795e\u7957\u867d\u7136\u4e0d\u673d\uff0c\u5374\u6709\u53ef\u80fd\u88ab\u6467\u6bc1\uff0c\u4e00\u65e6\u9668\u843d\uff0c\u5219\u5c31\u4e0d\u53ef\u80fd\u518d\u56de\u5e94\u51e1\u4eba\u7684\u547c\u5524\u3002</p><p>\u201c\u4f60\u662f\u8bf4\uff0c\u795e\u738b\u5c31\u7b97\u6b7b\u4e86\uff0c\u7075\u9b42\u5728\u51a5\u754c\u65f6\uff0c\u4f9d\u7136\u8fd8\u53ef\u4ee5\u548c\u51e1\u95f4\u7684\u4fe1\u5f92\u8054\u7cfb\u6c9f\u901a\uff1f\u201d</p><p>\u201c\u4e0d\u4ec5\u5982\u6b64\uff0c\u751a\u81f3\u8fd8\u6709\u53ef\u80fd\u9644\u5728\u4fe1\u5f92\u8eab\u4e0a\uff0c\u4e34\u65f6\u56de\u5230\u51e1\u95f4\uff0c\u8fd9\u88ab\u79f0\u4e3a\u2018\u964d\u795e\u2019\u2014\u2014\u5f53\u7136\u8fd9\u662f\u4e0a\u53e4\u65f6\u4ee3\u7684\u4f20\u8bf4\u4e86\uff0c\u4e0d\u4e00\u5b9a\u53ef\u9760\u3002\u201d</p><p>\u542c\u8d77\u6765\u6709\u70b9\u50cf\u4e2d\u571f\u5927\u9646\u795e\u660e\u7684\u201c\u5723\u8005\u964d\u4e34\u201d\uff0c\u53ea\u4e0d\u8fc7\u4e00\u4e2a\u662f\u6d3b\u7740\u7684\u795e\u964d\u4e34\uff0c\u4e00\u4e2a\u662f\u6b7b\u6389\u7684\u795e\u56de\u9b42\u3002</p><p>\u201c\u7b49\u7b49\uff0c\u201d\u743c\u6069\u7a81\u7136\u60f3\u5230\u4e00\u4e2a\u95ee\u9898\uff0c\u201c\u6309\u7167\u4f60\u7684\u8bf4\u6cd5\uff0c\u8fd9\u4e9b\u4e1c\u57df\u7684\u795e\u738b\uff0c\u6b7b\u4e86\u4e4b\u540e\u5c31\u7b49\u4e8e\u662f\u5230\u4e86\u53e6\u5916\u4e00\u4e2a\u4e16\u754c\uff0c\u4f9d\u7136\u5b58\u5728\uff0c\u4f9d\u7136\u80fd\u591f\u548c\u4fe1\u5f92\u6c9f\u901a\u8054\u7cfb\uff0c\u751a\u81f3\u5076\u5c14\u8fd8\u80fd\u5723\u8005\u964d\u4e34\uff0c\u518d\u5230\u51e1\u95f4\u6e9c\u8fbe\u6e9c\u8fbe\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u90a3\u8fd9\u79cd\u2018\u6b7b\u53bb\u7684\u795e\u738b\u2019\uff0c\u548c\u6211\u4eec\u4e2d\u571f\u89c2\u5ff5\u4e2d\u7684\u795e\u7957\uff0c\u6709\u4ec0\u4e48\u533a\u522b\uff1f\u201d</p><p>\u201c\u6709\u533a\u522b\u554a\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6b7b\u53bb\u7684\u795e\u738b\u4f4f\u5728\u51a5\u754c\uff0c\u6211\u4eec\u7684\u795e\u7957\u4f4f\u5728\u5929\u754c\u2014\u2014\u4f4f\u7684\u5730\u65b9\u540d\u5b57\u4e0d\u4e00\u6837\u3002\u201d</p><p>\u201c\u2026\u2026\u827e\u5f25\u8587\u4f60\u8d8a\u6765\u8d8a\u559c\u6b22\u8bf4\u51b7\u7b11\u8bdd\u4e86\u3002\u201d</p><p>\u73a9\u7b11\u5f52\u73a9\u7b11\uff0c\u4e8b\u5b9e\u4e0a\u5f53\u7136\u8fd8\u662f\u6709\u533a\u522b\u7684\u3002\u4e1c\u57df\u795e\u738b\u6302\u6389\u4ee5\u540e\uff0c\u867d\u7136\u4ecd\u7136\u53ef\u4ee5\u548c\u4fe1\u5f92\u6c9f\u901a\uff0c\u5374\u6ca1\u529e\u6cd5\u518d\u8d50\u4e88\u4efb\u4f55\u795e\u672f\u548c\u529b\u91cf\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u81ea\u7136\u5c31\u4f1a\u4e27\u5931\u5bf9\u51e1\u95f4\u7684\u63a7\u5236\u529b\uff0c\u796d\u53f8\u3001\u4fe1\u5f92\u6539\u6362\u95e8\u5ead\u662f\u5f88\u5bfb\u5e38\u7684\u4e8b\u60c5\u3002\u8fd9\u4e5f\u6ca1\u529e\u6cd5\uff0c\u51e1\u4eba\u603b\u662f\u73b0\u5b9e\u7684\uff0c\u4e00\u4e2a\u4e0d\u80fd\u518d\u964d\u7978\u8d50\u798f\u7684\u795e\uff0c\u4e0d\u53ef\u80fd\u6c38\u8fdc\u88ab\u4eba\u5d07\u62dc\u2014\u2014\u5c24\u5176\u662f\u65c1\u8fb9\u8fd8\u6709\u5176\u4ed6\u6d3b\u751f\u751f\u7684\u795e\u738b\u505a\u5bf9\u6bd4\u3002\u6240\u4ee5\u4e1c\u57df\u867d\u7136\u5386\u53f2\u4e0a\u6709\u5f88\u591a\u795e\u738b\uff0c\u4f46\u7edd\u5927\u90e8\u5206\u795e\u738b\u9668\u843d\u4e4b\u540e\uff0c\u5176\u6559\u4f1a\u6700\u591a\u7ee7\u7eed\u7ef4\u6301\u51e0\u5341\u5e74\u3001\u4e0a\u767e\u5e74\uff0c\u4e5f\u5c31\u70df\u6d88\u4e91\u6563\u4e86\uff0c\u73b0\u5b58\u7684\u6559\u4f1a\u4e5f\u5c31\u90a3\u4e48\u51e0\u4e2a\u3002</p><p>\u201c\u4f60\u8fd9\u4e48\u4e00\u8bf4\u6211\u5c31\u7406\u89e3\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e2d\u571f\u795e\u7957\u662f\u5e55\u540e\u8001\u677f\uff0c\u4e0d\u76f4\u63a5\u51fa\u9762\uff0c\u4f46\u4ecd\u7136\u638c\u63e1\u6700\u9ad8\u6743\u529b\uff0c\u53ef\u4ee5\u51b3\u5b9a\u624b\u4e0b\u4eba\u7684\u751f\u6b7b\u7978\u798f\uff0c\u6240\u4ee5\u4fe1\u5f92\u4e5f\u4f1a\u65e9\u8bf7\u793a\u665a\u6c47\u62a5\uff0c\u7ecf\u5e38\u6765\u5531\u8d5e\u6b4c\u62cd\u9a6c\u5c41\uff1b\u4e1c\u57df\u8fd9\u4e9b\u795e\u738b\u6302\u6389\u4ee5\u540e\uff0c\u5c31\u6210\u4e86\u9000\u4f11\u8001\u5e72\u90e8\uff0c\u867d\u7136\u4e5f\u53ef\u4ee5\u5076\u5c14\u51fa\u6765\u900f\u900f\u6c14\uff0c\u8bf4\u8bf4\u8bdd\uff0c\u4f46\u4ee5\u524d\u7684\u8001\u90e8\u4e0b\u7231\u542c\u4e0d\u542c\uff0c\u7231\u7406\u4e0d\u7406\uff0c\u65f6\u95f4\u4e00\u957f\uff0c\u5c31\u53d8\u6210\u5b64\u5bb6\u5be1\u4eba\u4e86\u3002\u201d</p><p>\u201c\u867d\u7136\u4e0d\u77e5\u9053\u4f60\u5728\u8bf4\u4ec0\u4e48\uff0c\u4f46\u5927\u6982\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\u5427\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u4e1c\u57df\u8fd9\u4e9b\u795e\u738b\u8fd8\u771f\u662f\u633a\u90c1\u95f7\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6709\u4ec0\u4e48\u6bd4\u66fe\u7ecf\u8eab\u5c45\u9ad8\u4f4d\uff0c\u524d\u547c\u540e\u62e5\uff0c\u5982\u4eca\u95e8\u524d\u51b7\u6e05\uff0c\u65e0\u4eba\u7406\u776c\u66f4\u8ba9\u4eba\u4f24\u611f\u7684\u4e8b\u60c5\u5462\uff1f\u201d</p><p>\u201c\u6709\u554a\uff0c\u6bd4\u5982\u8bf4\u66fe\u7ecf\u53ef\u4ee5\u5de6\u62e5\u53f3\u62b1\uff0c\u6e29\u9999\u8f6f\u7389\u5728\u6000\uff0c\u5982\u4eca\u4ecd\u7136\u662f\u7f8e\u4eba\u73af\u7ed5\uff0c\u5374\u6709\u5fc3\u65e0\u529b\uff0c\u771f\u662f\u4ee4\u4eba\u559c\u95fb\u4e50\u89c1\u2014\u2014\u4e0d\uff0c\u4ee4\u4eba\u4f24\u611f\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u827e\u5f25\u8587\uff0c\u4f60\u771f\u662f\u8d8a\u6765\u8d8a\u4e0d\u53ef\u7231\u4e86\u3002\u201d</p><p>\u201c\u90a3\u51a5\u795e\u5462\uff1f\u201d\u73ca\u5609\u5ffd\u7136\u95ee\uff0c\u201c\u51a5\u795e\u4e5f\u50cf\u795e\u738b\u4e00\u6837\uff0c\u4f1a\u751f\u8001\u75c5\u6b7b\uff0c\u8f6e\u56de\u8f6c\u4e16\uff1f\u201d</p><p>\u201c\u4e0d\u4f1a\uff0c\u51a5\u795e\u4e0d\u662f\u795e\u738b\uff0c\u5176\u5b9e\u8bf4\u8d77\u6765\uff0c\u4ed6\u53cd\u5012\u66f4\u8fd1\u4f3c\u4e8e\u4e2d\u571f\u7684\u795e\u660e\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6df1\u5c45\u795e\u57df\uff0c\u4e0d\u6d89\u51e1\u95f4\uff0c\u4e0d\u673d\u4e0d\u6b7b\uff0c\u53ea\u662f\u6ca1\u6709\u51e1\u95f4\u7684\u4fe1\u5f92\u548c\u6559\u4f1a\u2014\u2014\u81f3\u5c11\u6ca1\u6709\u516c\u5f00\u7684\u3002\u201d</p><p>\u201c\u8fd9\u542c\u8d77\u6765\u5176\u5b9e\u66f4\u50cf\u90aa\u9b54\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u800c\u4e14\u4f60\u4e0d\u662f\u8bf4\uff0c\u51a5\u795e\u624b\u4e0b\u6709\u5f88\u591a\u90aa\u9b54\u5417\uff1f\u6240\u4ee5\u4ed6\u5e94\u8be5\u5c31\u662f\u4e2a\u5927\u6076\u9b54\uff0c\u6216\u8005\u5730\u72f1\u9886\u4e3b\u4ec0\u4e48\u7684\u3002\u201d</p><p>\u201c\u8fd9\u4e5f\u672a\u5fc5\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u86db\u540e\u624b\u4e0b\u5c31\u6709\u4e00\u5927\u7968\u6076\u9b54\uff0c\u4f46\u4eba\u5bb6\u4f9d\u7136\u662f\u6b63\u724c\u795e\u7957\uff0c\u73b0\u5728\u90fd\u6210\u4e86\u9ed1\u6697\u7cbe\u7075\u7684\u552f\u4e00\u795e\u4e86\u3002\u201d</p><p>\u63d0\u5230\u86db\u540e\u7f57\u4e1d\u548c\u9ed1\u6697\u7cbe\u7075\uff0c\u743c\u6069\u5012\u662f\u7a81\u7136\u60f3\u8d77\u4e00\u4e9b\u4e8b\u548c\u4e00\u4e9b\u4eba\u3002</p><p>\u4ed6\u5728\u5e7d\u6697\u5730\u57df\u91cc\u524d\u540e\u5f85\u4e86\u5927\u7ea6\u4e00\u5e74\uff08\u628a\u6389\u8fdb\u4e0b\u5c42\u754c\u7684\u90a3\u6bb5\u4e5f\u7b97\u4e0a\u7684\u8bdd\uff09\uff0c\u65f6\u95f4\u867d\u7136\u4e0d\u7b97\u5f88\u957f\uff0c\u4f46\u7ecf\u5386\u66f2\u6298\uff0c\u503c\u5f97\u56de\u5fc6\u7684\u5730\u65b9\u4e0d\u5c11\uff0c\u800c\u4e14\u673a\u7f18\u5de7\u5408\u4e4b\u4e0b\uff0c\u8fd8\u5360\u4e86\u4e00\u5ea7\u5c0f\u57ce\uff0c\u6709\u4e86\u4e00\u4e9b\u90e8\u5c5e\uff0c\u4eba\u6570\u867d\u7136\u4e0d\u591a\uff0c\u5374\u90fd\u662f\u4e9b\u7cbe\u82f1\u3002\u5982\u679c\u6362\u4e86\u522b\u7684\u7a7f\u8d8a\u8005\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u4ee5\u6b64\u4e3a\u6839\u57fa\uff0c\u79cd\u7530\u5730\u9020\u5175\u8425\u6500\u5347\u79d1\u6280\u6811\uff0c\u96c4\u5fc3\u52c3\u52c3\u5730\u6253\u7b97\u5e2d\u5377\u5929\u4e0b\uff0c\u53ef\u60dc\u743c\u6069\u5fd7\u4e0d\u5728\u6b64\uff0c\u63a5\u5230\u9634\u9b42\u57ce\u7684\u8c03\u4ee4\u540e\u5c31\u76f4\u63a5\u653e\u624b\uff0c\u628a\u4ed6\u4eec\u4e22\u5728\u5e7d\u6697\u5730\u57df\u91cc\uff0c\u4e5f\u4e0d\u77e5\u9053\u73b0\u5728\u60c5\u51b5\u5982\u4f55\u4e86\u3002</p><p>\u8fd8\u6709\u90a3\u4f4d\u9ed1\u6697\u7cbe\u7075\u5c11\u5973\u3002</p><p>\u548c\u8eab\u8fb9\u7684\u5176\u4ed6\u5973\u5b69\u5b50\u6bd4\u8d77\u6765\uff0c\u7ef4\u5eb7\u5c3c\u5a05\u548c\u743c\u6069\u7684\u5173\u7cfb\u5176\u5b9e\u5f88\u6de1\uff0c\u76f8\u5904\u7684\u65f6\u95f4\u5e76\u4e0d\u957f\uff0c\u4e5f\u8c08\u4e0d\u4e0a\u4ee5\u8bda\u76f8\u5f85\uff0c\u8fd8\u63ba\u6742\u4e86\u5f88\u591a\u529f\u5229\u8272\u5f69\u3002\u743c\u6069\u539f\u672c\u5c31\u6ca1\u62b1\u5584\u610f\uff0c\u548c\u5979\u5728\u4e00\u8d77\u66f4\u591a\u662f\u51fa\u4e8e\u6b32\u671b\uff0c\u8bf4\u4e0d\u4e0a\u6709\u591a\u5c11\u771f\u7684\u611f\u60c5\uff0c\u79bb\u522b\u4e4b\u540e\uff0c\u4e5f\u51e0\u4e4e\u6ca1\u6709\u60f3\u8d77\u8fc7\u3002\u4f46\u5076\u7136\u5fc6\u8d77\uff0c\u5374\u53d1\u73b0\u539f\u6765\u8fd8\u662f\u6709\u4e9b\u6000\u5ff5\u7684\uff0c\u751a\u81f3\u6709\u4e9b\u540e\u6094\uff0c\u540e\u6094\u5f53\u65f6\u5c31\u8fd9\u6837\u8f7b\u6613\u653e\u624b\u3002</p><p>\u8fd9\u7b97\u662f\u7537\u6027\u7684\u5360\u6709\u6b32\u5427\u3002</p><p>\u6b63\u5728\u8fd9\u65f6\uff0c\u6770\u59c6\u7a81\u7136\u8dd1\u8fc7\u6765\uff0c\u8bf4\u662f\u8239\u957f\u6709\u9080\uff0c\u60f3\u8bf7\u743c\u6069\u559d\u4e0b\u5348\u8336\u3002\u743c\u6069\u6c42\u4e4b\u4e0d\u5f97\uff0c\u4e58\u673a\u6e9c\u4e86\u51fa\u6765\u3002</p><p>\u6d77\u4e0a\u822a\u884c\u4e86\u8fd9\u4e48\u4e45\uff0c\u743c\u6069\u8fd8\u6ca1\u89c1\u8fc7\u8fd9\u4f4d\u8239\u957f\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u6709\u65f6\u5019\u90fd\u5728\u6000\u7591\u8fd9\u4e2a\u4eba\u662f\u5426\u771f\u7684\u5b58\u5728\uff0c\u4f1a\u4e0d\u4f1a\u53ea\u662f\u865a\u6784\u51fa\u6765\u7684\uff0c\u6216\u8005\u65e9\u5c31\u6b7b\u4e86\uff0c\u6bd4\u5982\u8bf4\u88ab\u4ed6\u7684\u4f84\u513f\u8c0b\u6740\u4e86\u4e4b\u7c7b\u3002\u4e0d\u8fc7\u4ed6\u7684\u6027\u683c\u5411\u6765\u4e0d\u7231\u591a\u7ba1\u95f2\u4e8b\uff0c\u4e5f\u61d2\u5f97\u8fc7\u95ee\uff0c\u6ca1\u60f3\u5230\u773c\u770b\u5c31\u8981\u884c\u7a0b\u7ed3\u675f\u4e86\uff0c\u5bf9\u65b9\u7a81\u7136\u4e3b\u52a8\u9080\u8bf7\uff0c\u8fd8\u771f\u662f\u51fa\u4e4e\u610f\u6599\u3002</p><p>\u201c\u6700\u8fd1\u8eab\u4f53\u6b20\u4f73\uff0c\u4e0d\u65b9\u4fbf\u9732\u9762\uff0c\u5b9e\u5728\u662f\u5931\u793c\u4e86\u3002\u201d</p><p>\u666e\u6717\u514b\u8239\u957f\u662f\u4e2a\u8eab\u6750\u9ad8\u5927\u7684\u4e2d\u5e74\u4eba\uff0c\u6ee1\u8138\u6742\u4e71\u7684\u80e1\u987b\uff0c\u9762\u5bb9\u4e0a\u7684\u75b2\u5026\u4e4b\u8272\u5f88\u91cd\uff0c\u770b\u8d77\u6765\u6ca1\u4ec0\u4e48\u7cbe\u795e\uff0c\u4ed6\u534a\u9760\u534a\u5750\u5728\u4e00\u628a\u5927\u8eba\u6905\u4e2d\uff0c\u8eab\u4e0a\u76d6\u7740\u76ae\u8884\u3002\u743c\u6069\u7aef\u8be6\u4e86\u4e00\u4f1a\uff0c\u6709\u4e9b\u7591\u60d1\uff0c\u4ed6\u4ece\u5bf9\u65b9\u8eab\u4e0a\u770b\u4e0d\u51fa\u4efb\u4f55\u4e00\u70b9\u5deb\u5e08\u7684\u8ff9\u8c61\uff0c\u751a\u81f3\u770b\u4e0d\u51fa\u4efb\u4f55\u201c\u529b\u91cf\u201d\u7684\u75d5\u8ff9\u3002</p><p>\u743c\u6069\u4e0d\u600e\u4e48\u64c5\u957f\u5bd2\u6684\uff0c\u666e\u6717\u514b\u770b\u8d77\u6765\u4e5f\u4e0d\u662f\u4ec0\u4e48\u9633\u5149\u5f00\u6717\u7684\u4eba\uff0c\u4e24\u4e2a\u4eba\u4e0d\u54b8\u4e0d\u6de1\u5730\u5ba2\u5957\u51e0\u53e5\uff0c\u573a\u9762\u4e00\u65f6\u95f4\u6709\u4e9b\u51b7\u4e0b\u6765\u3002\u743c\u6069\u89c9\u5f97\u5c34\u5c2c\uff0c\u76ee\u5149\u56db\u5904\u6e38\u8d70\uff0c\u6253\u7b97\u627e\u70b9\u8bdd\u9898\uff0c\u4e00\u773c\u770b\u89c1\u7a97\u5916\u7684\u90a3\u5ea7\u9752\u94dc\u8272\u5927\u706f\u5854\uff0c\u5b83\u77d7\u7acb\u5728\u8f9b\u5df4\u57ce\u5916\u7684\u4e00\u5ea7\u6d77\u5c9b\u4e0a\uff0c\u4eff\u4f5b\u4e00\u6839\u64ce\u5929\u4e4b\u67f1\uff0c\u6c14\u52bf\u60ca\u4eba\uff0c\u201c\u597d\u9ad8\u554a\uff0c\u201d\u4ed6\u968f\u53e3\u8bf4\uff0c\u201c\u8fd9\u5927\u6982\u6709\u4e0a\u5343\u5c3a\u4e86\u5427\u3002\u201d</p><p>\u201c\u5854\u57fa\u4e00\u767e\u56db\u5341\u56db\u5c3a\uff0c\u5854\u8eab\u4e03\u767e\u4e94\u5341\u5c3a\uff0c\u7136\u540e\u5854\u9876\u4e0a\u8fd8\u6709\u4e00\u5ea7\u7ea2\u9f99\u5851\u50cf\uff0c\u4e03\u5341\u4e09\u5c3a\uff0c\u4e00\u5171\u4e5d\u767e\u516d\u5341\u4e03\u5c3a\uff0c\u201d\u8239\u957f\u62a5\u51fa\u4e00\u8fde\u4e32\u7684\u6570\u5b57\uff0c\u201c\u8fd9\u662f\u4e09\u767e\u5e74\u524d\u7ea2\u9f99\u738b\u5bdf\u65af\u8428\u7edf\u4e00\u5f7b\u68ee\u5854\u540e\uff0c\u4e3a\u5f70\u663e\u5176\u529f\u7ee9\u6240\u5efa\u9020\uff0c\u88ab\u79f0\u4e3a\u662f\u4e1c\u57df\u6709\u53f2\u4ee5\u6765\u6700\u9ad8\u7684\u5efa\u7b51\u7269\u3002\u201d</p><p>\u201c\u6069\u745f\u7684\u901a\u5929\u5854\u5e94\u8be5\u6bd4\u5b83\u66f4\u9ad8\u5427\u3002\u201d</p><p>\u201c\u901a\u5929\u5854\u53ea\u662f\u4f20\u8bf4\uff0c\u662f\u771f\u662f\u5047\u90fd\u6ca1\u6709\u5b9a\u8bba\u5462\u3002\u201d</p><p>\u201c\u4e0d\u662f\u8bf4\u6709\u9057\u8ff9\u5b58\u7559\u5417\uff1f\u201d</p><p>\u201c\u9057\u8ff9\u786e\u5b9e\u662f\u6709\uff0c\u4f46\u5230\u5e95\u662f\u4e0d\u662f\u901a\u5929\u5854\u7684\u9057\u8ff9\uff0c\u90a3\u53ef\u5c31\u96be\u8bf4\u4e86\uff0c\u4e5f\u53ef\u80fd\u662f\u5176\u4ed6\u4ec0\u4e48\u5efa\u7b51\u5462\uff0c\u201d\u8239\u957f\u8bf4\uff0c\u201c\u5373\u4fbf\u771f\u7684\u662f\u901a\u5929\u5854\u9057\u8ff9\uff0c\u53ea\u5269\u4e00\u7247\u6b8b\u57a3\u65ad\u58c1\uff0c\u4e5f\u770b\u4e0d\u51fa\u539f\u672c\u9ad8\u5ea6\u3002\u201d</p><p>\u201c\u4e5f\u662f\uff0c\u201d\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u8bf4\u5230\u8fd9\u4e2a\uff0c\u6211\u8bb0\u5f97\u5927\u706f\u5854\u4e5f\u66fe\u7ecf\u6bc1\u574f\u8fc7\uff0c\u73b0\u5982\u4eca\u7684\u8fd9\u5ea7\u662f\u540e\u6765\u91cd\u5efa\u7684\uff0c\u5e76\u975e\u539f\u7248\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u636e\u8bf4\u662f\u4e00\u767e\u591a\u5e74\u524d\u7684\u4e00\u6b21\u5927\u5730\u9707\uff0c\u5bfc\u81f4\u706f\u5854\u7684\u6700\u4e0a\u9762\u4e00\u622a\u7a81\u7136\u65ad\u88c2\uff0c\u558f\uff0c\u5927\u6982\u5c31\u662f\u4ece\u90a3\u4e2a\u4f4d\u7f6e\u65ad\u7684\uff0c\u201d\u8239\u957f\u7528\u624b\u9065\u9065\u5730\u6bd4\u5212\u4e86\u4e00\u4e0b\uff0c\u201c\u4e00\u76f4\u4e5f\u6ca1\u4eba\u7ba1\u3002\u76f4\u5230\u8fd9\u4e00\u4efb\u8f9b\u5df4\u57ce\u4e3b\u7ee7\u4f4d\uff0c\u624d\u51b3\u5b9a\u91cd\u4fee\u5927\u706f\u5854\uff0c\u4fee\u4e86\u5c06\u8fd1\u5341\u5e74\u65f6\u95f4\uff0c\u516d\u5e74\u524d\u5b8c\u5de5\u3002\u201d</p><p>\u6b63\u95f2\u804a\u95f4\uff0c\u838e\u73de\u514b\u7a7f\u7740\u9ad8\u8ddf\u978b\u4e00\u8def\u8e6c\u8e6c\u8e6c\u5730\u5c0f\u8dd1\u8fc7\u6765\uff0c\u9012\u7ed9\u743c\u6069\u4e00\u5f20\u5361\u7247\uff0c\u201c\u7ed9\uff0c\u54e5\u54e5\uff0c\u8fd9\u662f\u4f60\u7684\u4e34\u65f6\u901a\u884c\u8bc1\u3002\u201d</p><p>\u201c\u624b\u7eed\u529e\u5b8c\u4e86\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u8fd8\u65e9\u5462\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u8fd8\u6709\u4e03\u5f20\u7533\u8bf7\u8868\u8981\u586b\uff0c\u586b\u5b8c\u8fd8\u6709\u4e24\u4efd\u58f0\u660e\u4e66\u8981\u7b7e\uff0c\u8fd8\u8981\u7533\u62a5\u4e2a\u4eba\u8d22\u4ea7\u968f\u8eab\u7269\u54c1\u4ec0\u4e48\u7684\uff0c\u5ba0\u7269\u8fd8\u8981\u68c0\u75ab\u2014\u2014\u5f88\u591a\u6750\u6599\u9700\u8981\u4f60\u7b7e\u5b57\uff0c\u662f\u62ff\u8fc7\u6765\u8fd8\u662f\u6211\u66ff\u4f60\u7b7e\u7b97\u4e86\uff1f\u201d</p><p>\u201c\u4f60\u66ff\u6211\u7b7e\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u800c\u4e14\u6211\u4eec\u54ea\u6765\u7684\u5ba0\u7269\uff1f\u201d</p><p>\u201c\u742a\u5a05\u554a\u3002\u201d\u9b45\u9b54\u63d0\u9192\u3002</p><p>\u201c\u54e6\uff0c\u5bf9\u3002\u201d\u743c\u6069\u5dee\u70b9\u628a\u5979\u7ed9\u5fd8\u4e86\uff0c\u4e3b\u8981\u662f\u90a3\u53ea\u732b\u5973\u5e73\u65f6\u57fa\u672c\u770b\u4e0d\u5230\uff0c\u4e25\u91cd\u7f3a\u4e4f\u5b58\u5728\u611f\u3002</p><p>\u201c\u53e6\u5916\u4f60\u7528\u4ec0\u4e48\u540d\u5b57\uff1f\u201d\u838e\u73de\u514b\u53c8\u95ee\uff0c\u201c\u743c\u6069\uff0c\u8fd8\u662f\u5170\u5c3c\u65af\u7279\uff1f\u201d</p><p>\u201c\u5c31\u2018\u743c\u6069\u5170\u5c3c\u65af\u7279\u2019\u4e0d\u884c\u4e48\uff1f\u201d</p><p>\u201c\u592a\u957f\u4e86\u554a\uff0c\u201d\u838e\u73de\u514b\u62b1\u6028\uff0c\u201c\u6bcf\u5f20\u8868\u683c\u90fd\u8981\u586b\u4e00\u6b21\u540d\u5b57\uff0c\u6211\u7684\u624b\u4f1a\u5199\u62bd\u7b4b\u7684\u3002\u201d</p><p>\u201c\u90a3\u5c31\u5170\u5c3c\u65af\u7279\u5427\u3002\u201d</p><p>\u4e1c\u57df\u4eba\u6ca1\u6709\u59d3\u6c0f\uff0c\u53ea\u6709\u540d\u5b57\uff0c\u65e0\u8bba\u8d35\u65cf\u5e73\u6c11\u90fd\u662f\u5982\u6b64\u3002\u6240\u4ee5\u4e2d\u571f\u4eba\u6765\u5230\u4e1c\u57df\uff0c\u81ea\u62a5\u5bb6\u95e8\u65f6\u5f80\u5f80\u4f1a\u6bd4\u8f83\u9ebb\u70e6\uff0c\u56e0\u4e3a\u4e1c\u57df\u4eba\u4e0d\u592a\u80fd\u7406\u89e3\u8fd9\u79cd\u201c\u59d3\u6c0f\u52a0\u540d\u5b57\u201d\u7684\u7ed3\u6784\u3002\u4e3a\u4e86\u65b9\u4fbf\u8d77\u89c1\uff0c\u901a\u5e38\u5c31\u53ea\u4fdd\u7559\u59d3\u6c0f\u6216\u8005\u540d\u5b57\u7684\u5176\u4e00\uff0c\u8fd9\u6837\u5e73\u65f6\u6253\u4ea4\u9053\u6bd4\u8f83\u65b9\u4fbf\u3002</p><p>\u201c\u53eb\u743c\u6069\u4e0d\u597d\u4e48\uff0c\u6211\u89c9\u5f97\u66f4\u4eb2\u5207\u3002\u201d</p><p>\u201c\u4f46\u6211\u4f1a\u89c9\u5f97\u5f88\u5947\u602a\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6bcf\u4e2a\u4eba\u90fd\u53eb\u6211\u743c\u6069\uff0c\u641e\u5f97\u597d\u50cf\u5168\u90fd\u548c\u6211\u5f88\u719f\u4f3c\u7684\uff0c\u6211\u4e0d\u4e60\u60ef\u3002\u201d</p><p>\u201c\u90a3\u5c31\u5170\u5c3c\u65af\u7279\u5427\uff0c\u6211\u5148\u53bb\u529e\u624b\u7eed\u4e86\u3002\u201d\u838e\u73de\u514b\u8bf4\u7740\uff0c\u671d\u8239\u957f\u884c\u4e86\u4e2a\u793c\uff0c\u7136\u540e\u8dd1\u5f00\u4e86\u3002</p><p>\u743c\u6069\u62ff\u8d77\u838e\u73de\u514b\u521a\u521a\u62ff\u8fc7\u6765\u7684\u5361\u7247\uff0c\u91d1\u5c5e\u6750\u8d28\uff0c\u624b\u611f\u51b0\u51b7\u575a\u786c\uff0c\u6b63\u9762\u662f\u4e00\u4e9b\u4e2a\u4eba\u4fe1\u606f\uff0c\u80cc\u9762\u5219\u662f\u7528\u6069\u745f\u8bed\u3001\u4e2d\u571f\u901a\u7528\u8bed\u4e24\u79cd\u8bed\u8a00\u4e66\u5199\u7684\u4e00\u884c\u5b57\u201c\u5f7b\u68ee\u5854\u4e34\u65f6\u901a\u884c\u8bc1\u201d\uff0c\u8fd8\u76d6\u7740\u4e00\u679a\u84dd\u8272\u7684\u201c\u53f6\u82b1\u201d\u5370\u7ae0\uff0c\u90a3\u662f\u8f9b\u5df4\u57ce\u7684\u6807\u5fd7\u3002\u201c\u4e0b\u4e2a\u8239\u90fd\u8fd9\u4e48\u9ebb\u70e6\uff0c\u201d\u743c\u6069\u968f\u53e3\u62b1\u6028\uff0c\u201c\u6211\u53ea\u662f\u6765\u65c5\u6e38\u800c\u5df2\uff0c\u53c8\u4e0d\u662f\u7533\u8bf7\u5165\u7c4d\uff0c\u6709\u5fc5\u8981\u8fd9\u6837\u4e48\u3002\u201d</p><p>\u201c\u5c31\u56e0\u4e3a\u662f\u4e34\u65f6\u6e38\u5ba2\u6240\u4ee5\u624d\u9ebb\u70e6\u554a\uff0c\u201d\u8239\u957f\u8bf4\uff0c\u201c\u5982\u679c\u4f60\u613f\u610f\u5165\u7c4d\uff0c\u53cd\u800c\u7b80\u5355\u4e86\u3002\u201d</p><p>\u5165\u7c4d\u53cd\u800c\u7b80\u5355\uff1f</p><p>\u201c\u5bf9\uff0c\u5f7b\u68ee\u5854\u4eba\u5411\u6765\u53cb\u597d\u5305\u5bb9\uff0c\u4ece\u6765\u4e0d\u6b67\u89c6\u5916\u56fd\u4eba\u3002\u53ea\u8981\u4f60\u81ea\u613f\u5c06\u4e00\u90e8\u5206\u8d22\u4ea7\u6350\u732e\u7ed9\u57ce\u4e3b\uff0c\u5c31\u53ef\u4ee5\u5165\u7c4d\u4e86\u3002\u201d</p><p>\u2026\u2026\u8fd9\u201c\u4e00\u90e8\u5206\u201d\u5177\u4f53\u662f\u6307\u591a\u5c11\uff1f</p><p>\u201c\u4e0d\u540c\u57ce\u4e3b\u5b9a\u7684\u6807\u51c6\u4e0d\u4e00\u6837\uff0c\u8f9b\u5df4\u57ce\u5e94\u8be5\u7b97\u662f\u6bd4\u8f83\u4f4e\u7684\uff0c\u53ea\u8981\u4e94\u5206\u4e4b\u4e00\u5c31\u53ef\u4ee5\u5165\u7c4d\u4e86\u3002\u201d</p><p>\u4ec0\u4e48\u53eb\u201c\u53ea\u8981\u201d\u4e94\u5206\u4e4b\u4e00\uff1f\u4e94\u5206\u4e4b\u4e00\u5df2\u7ecf\u5f88\u591a\u4e86\u597d\u4e0d\u597d\u3002</p><p>\u201c\u76f8\u6bd4\u8d77\u5176\u4ed6\u57ce\u5e02\u5df2\u7ecf\u5f88\u4f4e\u4e86\uff0c\u201d\u8239\u957f\u89e3\u91ca\uff0c\u201c\u6309\u7167\u6069\u745f\u6cd5\u5f8b\uff0c\u6240\u6709\u81e3\u6c11\u6709\u4e49\u52a1\u6bcf\u5e74\u5c06\u5176\u6536\u5165\u7684\u4e09\u5206\u4e4b\u4e00\u8fdb\u732e\u7ed9\u795e\u738b\u3002\u5f7b\u68ee\u5854\u7684\u5176\u4ed6\u57ce\u5e02\u5927\u591a\u90fd\u9075\u7167\u8fd9\u4e2a\u6807\u51c6\u6267\u884c\u3002\u201d</p><p>\u201c\u4f60\u662f\u8bf4\uff0c\u4ee5\u524d\u6069\u745f\u7684\u795e\u738b\uff0c\u8981\u62ff\u8d70\u8001\u767e\u59d3\u6536\u5165\u7684\u4e09\u5206\u4e4b\u4e00\uff1f\u800c\u4e14\u8fd8\u662f\u6bcf\u5e74\uff1f\u201d\u743c\u6069\u89c9\u5f97\u4e0d\u53ef\u601d\u8bae\uff0c\u201c\u8fd9\u7a0e\u7387\u4e5f\u592a\u9ad8\u4e86\u70b9\u5427\u3002\u201d</p><p>\u201c\u662f\u81e3\u6c11\u6bcf\u5e74\u90fd\u2018\u81ea\u613f\u2019\u5c06\u5176\u6536\u5165\u7684\u4e09\u5206\u4e4b\u4e00\u8fdb\u732e\u7ed9\u795e\u738b\u3002\u201d\u8239\u957f\u7ea0\u6b63\u3002</p><p>\u201c\u5982\u679c\u6211\u4e0d\u81ea\u613f\u8fdb\u732e\u5462\uff1f\u201d</p><p>\u201c\u795e\u738b\u7684\u5fe0\u5b9e\u81e3\u6c11\u4e0d\u53ef\u80fd\u4e0d\u81ea\u613f\uff0c\u201d\u8239\u957f\u8bf4\uff0c\u201c\u5982\u679c\u4f60\u4e0d\u81ea\u613f\uff0c\u8bf4\u660e\u4f60\u4e0d\u5fe0\u5b9e\uff0c\u662f\u4e2a\u4f2a\u4fe1\u5f92\uff0c\u5e94\u8be5\u4e0a\u7ede\u5211\u67b6\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\uff0c\u90a3\u4e48\u6211\u4e0d\u5165\u7c4d\uff0c\u5c31\u4e0d\u4f1a\u8981\u6211\u81ea\u613f\u8fdb\u732e\u4e86\u5427\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u4f1a\uff0c\u4f46\u6e38\u5ba2\u8981\u6536\u7a0e\uff0c\u7a0e\u79cd\u5f88\u591a\uff0c\u6bd4\u5982\u4ec0\u4e48\u4eba\u5934\u7a0e\u6240\u5f97\u7a0e\u57ce\u5e02\u7eff\u5316\u7a0e\u4e4b\u7c7b\u7684\u3002\u201d</p><p>\u4ea4\u7a0e\u5c31\u4ea4\u7a0e\u5427\uff0c\u743c\u6069\u662f\u5b88\u6cd5\u5e02\u6c11\uff0c\u6ca1\u6709\u548c\u884c\u653f\u673a\u5173\u5bf9\u6297\u7684\u4e60\u60ef\uff0c\u53cd\u6b63\u94b1\u4ec0\u4e48\u7684\uff0c\u4ed6\u4e5f\u4e0d\u662f\u5f88\u5728\u4e4e\u3002</p><p>\u4e0d\u8fc7\u8bdd\u9898\u8bf4\u5230\u8fd9\u91cc\uff0c\u5012\u662f\u4e2a\u8bd5\u63a2\u7684\u673a\u4f1a\u3002\u201c\u8fd9\u4eba\u5934\u7a0e\u662f\u6309\u4ec0\u4e48\u6807\u51c6\u6536\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u6211\u7684\u610f\u601d\u662f\uff1a\u662f\u4e0d\u662f\u53ea\u6709\u2018\u51e1\u4eba\u2019\u624d\u8981\u6536\u4eba\u5934\u7a0e\uff1f\u201d</p><p>\u201c\u7406\u8bba\u4e0a\u8bf4\u5e94\u8be5\u5982\u6b64\uff0c\u4f46\u4f60\u603b\u4e0d\u4f1a\u6ee1\u5927\u8857\u5ba3\u79f0\u4f60\u90a3\u4f4d\u5c0f\u59b9\u59b9\u662f\u4e2a\u9b45\u9b54\u5427\uff1f\u201d\u8239\u957f\u4f4e\u4f4e\u5730\u7b11\u4e86\u4e24\u58f0\uff0c\u201c\u80fd\u591f\u62d8\u675f\u8fd9\u6837\u4e00\u4f4d\u5f3a\u529b\u7684\u90aa\u9b54\u4f5c\u4e3a\u4f8d\u4ece\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\u771f\u662f\u4e00\u4f4d\u4e86\u4e0d\u8d77\u7684\u5deb\u5e08\u3002\u201d</p><p>\u201c\u5f7c\u6b64\u5f7c\u6b64\uff0c\u201d\u743c\u6069\u5750\u76f4\u8eab\u4f53\uff0c\u201c\u4f60\u80fd\u4e00\u773c\u8bc6\u7834\u6211\u7684\u6cd5\u672f\u4f2a\u88c5\uff0c\u8fa8\u8ba4\u51fa\u5979\u7684\u8eab\u4efd\uff0c\u4ec5\u4ec5\u4ee5\u6b64\u800c\u8bba\uff0c\u9020\u8be3\u5e94\u5f53\u5c31\u5728\u6211\u4e4b\u4e0a\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u4f60\u5012\u662f\u8bef\u4f1a\u4e86\uff0c\u201d\u8239\u957f\u8bf4\uff0c\u201c\u5176\u5b9e\u6211\u5bf9\u9b54\u6cd5\u57fa\u672c\u4e0a\u662f\u4e00\u7a8d\u4e0d\u901a\uff0c\u6211\u4e5f\u6ca1\u6709\u6839\u672c\u770b\u7834\u4f60\u7684\u6cd5\u672f\u4f2a\u88c5\u3002\u53ea\u4e0d\u8fc7\uff0c\u201d\u4ed6\u4f38\u51fa\u53f3\u624b\uff0c\u4f38\u51fa\u4e00\u6839\u624b\u6307\uff0c\u5c55\u793a\u7ed9\u743c\u6069\u770b\uff0c\u201c\u6211\u6709\u8fd9\u4e2a\u4e1c\u897f\u800c\u5df2\u3002\u201d</p><p>\u743c\u6069\u5b9a\u775b\u770b\u53bb\uff0c\u53d1\u73b0\u5728\u4ed6\u7684\u53f3\u624b\u4e2d\u6307\u4e0a\uff0c\u6234\u7740\u4e00\u679a\u91d1\u6212\u6307\u3002</p><p>\u201c\u5b83\u80fd\u591f\u8ba9\u6211\u8fa8\u8bc6\u51fa\u9644\u8fd1\u7684\u6076\u9b54\uff0c\u65e0\u8bba\u4f2a\u88c5\u5f97\u591a\u4e48\u5de7\u5999\uff0c\u6211\u90fd\u53ef\u4ee5\u76f4\u63a5\u770b\u7a7f\u3002\u201d</p><p>\u201c\u542c\u8d77\u6765\u662f\u4e2a\u5b9d\u7269\u3002\u201d</p><p>\u201c\u5b9e\u9645\u4e0a\uff0c\u5b83\u4e0d\u662f\u5b9d\u7269\uff0c\u662f\u4e00\u4e2a\u8bc5\u5492\u3002\u201d</p><p>\u201c\u8bc5\u5492\uff1f\u201d</p><p>\u201c\u6211\u5df2\u7ecf\u4e5d\u5341\u516d\u5c81\u4e86\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u8fd9\u53ef\u771f\u770b\u4e0d\u51fa\u6765\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u770b\u8d77\u6765\u6700\u591a\u4e94\u5341\u5c81\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u8bf4\u6211\u770b\u8d77\u6765\u6bd4\u8f83\u663e\u8001\uff0c\u201d\u8239\u957f\u5475\u5475\u5730\u7b11\u8d77\u6765\uff0c\u201c\u56e0\u4e3a\u4f60\u73b0\u5728\u770b\u89c1\u7684\uff0c\u662f\u6211\u4e09\u5341\u516d\u5c81\u65f6\u7684\u6a21\u6837\u3002\u5982\u679c\u4f60\u6709\u5174\u8da3\uff0c\u6211\u60f3\u8bf4\u8bf4\u6211\u7684\u6545\u4e8b\u3002\u201d</p><p>\u201c\u8bf7\u8bb2\u3002\u201d</p><p>\u201c\u6211\u66fe\u7ecf\u662f\u4e2a\u6d77\u76d7\uff0c\u7eb5\u6a2a\u5728\u8fd9\u5760\u661f\u6d77\u4e0a\uff0c\u6bcf\u5929\u8fc7\u5f97\u65e0\u5fe7\u65e0\u8651\uff0c\u5f53\u7136\uff0c\u90a3\u662f\u5f88\u591a\u5e74\u524d\u7684\u4e8b\u60c5\u4e86\uff0c\u6240\u4ee5\u4f60\u53ef\u80fd\u6ca1\u6709\u542c\u8bf4\u8fc7\u6211\u7684\u540d\u58f0\u3002\u4f46\u4f60\u4e00\u5b9a\u542c\u8bf4\u8fc7\u91d1\u94f6\u5c9b\u7684\u4f20\u8bf4\uff0c\u6211\u5c31\u662f\u90a3\u4e2a\u6545\u4e8b\u7684\u4e3b\u89d2\uff0c\u81f3\u5c11\u662f\u539f\u578b\u4e4b\u4e00\uff0c\u91cc\u9762\u6709\u5173\u6211\u7684\u63cf\u8ff0\u5927\u90e8\u5206\u90fd\u662f\u771f\u7684\uff0c\u9664\u4e86\u8bf4\u6211\u559c\u6b22\u5c0f\u5973\u5b69\u2014\u2014\u6211\u8981\u5f3a\u8c03\uff0c\u8fd9\u4e00\u70b9\u4e25\u91cd\u4e0d\u5b9e\uff0c\u6211\u662f\u4e2a\u6709\u683c\u8c03\u6709\u54c1\u4f4d\u7684\u4eba\uff0c\u5bf9\u4f4e\u4e8e\u5341\u4e94\u5c81\u7684\u5973\u6027\u5b8c\u5168\u6ca1\u6709\u534a\u70b9\u5174\u8da3\u3002\u201d</p><p>\u201c\u6211\u89c9\u5f97\u4f60\u7684\u683c\u8c03\u548c\u54c1\u4f4d\u503c\u5f97\u5c0a\u91cd\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\uff0c\u91d1\u94f6\u5c9b\u662f\u4ec0\u4e48\u6545\u4e8b\uff1f\u201d</p><p>\u201c\u2026\u2026\u7b97\u4e86\uff0c\u8fc7\u53bb\u7684\u4e8b\u60c5\u5c31\u4e0d\u63d0\u4e86\uff0c\u201d\u8239\u957f\u6446\u4e86\u6446\u624b\uff0c\u201c\u603b\u800c\u8a00\u4e4b\uff0c\u6211\u6bcf\u5929\u5e26\u7740\u6211\u7684\u624b\u4e0b\uff0c\u5728\u8fd9\u5927\u6d77\u4e0a\u7eb5\u6a2a\u5f80\u6765\uff0c\u4e0d\u53d7\u4efb\u4f55\u7ba1\u675f\uff0c\u9189\u751f\u68a6\u6b7b\uff0c\u900d\u9065\u81ea\u5728\uff0c\u65e5\u5b50\u8fc7\u5f97\u4e0d\u77e5\u9053\u6709\u591a\u5feb\u6d3b\u3002\u76f4\u5230\u6709\u4e00\u5929\uff0c\u6211\u5ffd\u7136\u60f3\u5230\u4e00\u4e2a\u95ee\u9898\u3002\u8fd9\u4e2a\u95ee\u9898\u8ba9\u6211\u609a\u7136\u800c\u60ca\uff0c\u60b2\u4ece\u4e2d\u6765\uff0c\u6cea\u5982\u96e8\u4e0b\uff0c\u4ece\u6b64\u5403\u4e0d\u9999\u7761\u4e0d\u597d\uff0c\u534a\u4e2a\u6708\u5c31\u7626\u4e86\u4e00\u5708\u3002\u201d</p><p>\u743c\u6069\u597d\u5947\uff1a\u201c\u4f60\u5230\u5e95\u60f3\u5230\u4ec0\u4e48\u95ee\u9898\u4e86\uff1f\u201d</p><p>\u201c\u6211\u60f3\u5230\uff0c\u4eba\u7ec8\u7a76\u662f\u8981\u6b7b\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u6211\u62dc\u8bbf\u4e86\u5f88\u591a\u4eba\uff0c\u5bfb\u6c42\u957f\u751f\u4e0d\u6b7b\u7684\u65b9\u6cd5\uff0c\u7136\u800c\u4e00\u65e0\u6240\u83b7\u3002\u5f53\u7136\u6211\u6ca1\u6709\u6c14\u9981\uff0c\u4e00\u76f4\u5728\u575a\u6301\u5bfb\u627e\uff0c\u7ec8\u4e8e\uff0c\u516d\u5341\u5e74\u524d\uff0c\u8bb0\u5f97\u5f53\u65f6\u662f\u79cb\u5b63\uff0c\u6211\u62a2\u52ab\u4e86\u4e00\u8258\u53bb\u5f80\u4e1c\u57df\u7684\u5546\u8239\uff0c\u5728\u4e00\u5806\u73e0\u5b9d\u3001\u8d27\u7269\u4e2d\uff0c\u53d1\u73b0\u4e00\u5f20\u5730\u56fe\u3002\u201d</p><p>\u201c\u8ba9\u6211\u731c\u731c\uff0c\u85cf\u5b9d\u56fe\uff1f\u201d</p><p>\u201c\u662f\u7684\uff0c\u5730\u56fe\u4e0a\u8bf4\u5728\u6069\u745f\u7684\u4e00\u5ea7\u5c71\u8c37\u91cc\uff0c\u57cb\u7740\u5386\u4ee3\u6069\u745f\u795e\u738b\u6536\u96c6\u7684\u91d1\u94f6\u8d22\u5b9d\uff0c\u8fd9\u4e9b\u5012\u7f62\u4e86\uff0c\u4f46\u4e0a\u9762\u8bf4\uff0c\u8fd9\u4e9b\u8d22\u5b9d\u91cc\uff0c\u8fd8\u85cf\u6709\u795e\u738b\u80fd\u591f\u8f6e\u56de\u8f6c\u4e16\u7684\u79d8\u6cd5\u3002\u7406\u6240\u5f53\u7136\u7684\uff0c\u6211\u5fc3\u52a8\u4e86\uff0c\u6211\u5e26\u7740\u4e00\u961f\u624b\u4e0b\uff0c\u5343\u8f9b\u4e07\u82e6\uff0c\u5230\u4e86\u5730\u56fe\u6307\u793a\u7684\u5730\u65b9\uff0c\u90a3\u662f\u4e00\u5ea7\u53e4\u8001\u7684\u795e\u6bbf\uff0c\u6ca1\u6709\u795e\u50cf\uff0c\u6ca1\u6709\u796d\u53f8\uff0c\u6b8b\u7834\u4e0d\u582a\uff0c\u660e\u663e\u5df2\u7ecf\u8352\u5e9f\u591a\u5e74\u3002\u6211\u5728\u795e\u6bbf\u4e2d\u6398\u5730\u4e09\u5c3a\uff0c\u522b\u8bf4\u8d22\u5b9d\uff0c\u8fde\u74f6\u9152\u90fd\u6ca1\u627e\u5230\u3002\u6211\u53c8\u5931\u671b\uff0c\u53c8\u751f\u6c14\uff0c\u89c9\u5f97\u88ab\u9a97\u4e86\u3002\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u6211\u53d1\u73b0\u4e86\u4e00\u5ea7\u9690\u79d8\u7684\u796d\u575b\uff0c\u8fd9\u679a\u6212\u6307\u5c31\u6446\u5728\u4e0a\u9762\u3002\u201d</p><p>\u201c\u4e8e\u662f\u4f60\u53d6\u8d70\u4e86\u6212\u6307\uff1f\u201d</p><p>\u8239\u957f\u70b9\u70b9\u5934\uff0c\u201c\u6211\u53d6\u8d70\u4e86\u6212\u6307\uff0c\u5c06\u5b83\u6234\u5728\u624b\u4e0a\u3002\u8d77\u521d\u4e00\u5207\u6b63\u5e38\uff0c\u7136\u800c\u4e0d\u5230\u534a\u4e2a\u6708\uff0c\u6211\u53d1\u73b0\u81ea\u5df1\u7684\u8eab\u4f53\u5f00\u59cb\u53d1\u751f\u53d8\u5316\uff0c\u6211\u4ecd\u7136\u4f1a\u53d7\u4f24\uff0c\u5374\u4e0d\u518d\u6d41\u8840\uff0c\u4e5f\u611f\u89c9\u4e0d\u5230\u75bc\u75db\uff1b\u6211\u53d8\u5f97\u538c\u6076\u5f3a\u5149\uff0c\u559c\u6b22\u9634\u6697\uff1b\u6211\u4e0d\u4f1a\u75b2\u5026\uff0c\u4e0d\u4f1a\u9965\u6e34\uff0c\u540c\u65f6\u4e5f\u518d\u4e5f\u54c1\u5c1d\u4e0d\u51fa\u7f8e\u9152\u7684\u5473\u9053\uff0c\u95fb\u4e0d\u5230\u5973\u4eba\u7684\u9999\u6c14\u3002\u6211\u7684\u8eab\u4f53\u6c38\u8fdc\u505c\u7559\u5728\u6234\u4e0a\u6212\u6307\u7684\u90a3\u4e00\u523b\uff0c\u4ece\u6b64\u518d\u4e5f\u4e0d\u4f1a\u53d8\u8001\uff0c\u4eff\u4f5b\u51dd\u6ede\u3002\u201d</p><p>\u770b\u8d77\u6765\u4f60\u957f\u751f\u4e0d\u6b7b\u7684\u613f\u671b\u5b9e\u73b0\u4e86\uff0c\u867d\u7136\u662f\u4ee5\u4e00\u79cd\u6ca1\u6709\u9884\u6599\u5230\u7684\u65b9\u5f0f\u2014\u2014\u7b49\u7b49\uff0c\u8fd9\u5267\u60c5\u600e\u4e48\u542c\u8d77\u6765\u597d\u50cf\u6709\u70b9\u8033\u719f\uff1f</p><p>\u201c\u4f60\u5728\u6708\u5149\u4e0b\u662f\u4e0d\u662f\u8fd8\u4f1a\u53d8\u6210\u9ab7\u9ac5\uff1f\u201d\u743c\u6069\u8bd5\u63a2\u5730\u95ee\u3002</p><p>\u8239\u957f\u5927\u5403\u4e00\u60ca\uff0c\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d</p><p>\u201c\u2026\u2026\u6211\u8bf4\u6211\u731c\u7684\u4f60\u4fe1\u4e0d\u4fe1\uff1f\u201d</p><p>\u743c\u6069\u4f38\u51fa\u624b\u5728\u7a7a\u6c14\u4e2d\u865a\u6309\u4e86\u4e00\u4e0b\uff0c\u4e00\u56e2\u96fe\u6c14\u4ece\u8239\u957f\u7684\u8eab\u4e0a\u5347\u8d77\uff0c\u53d1\u51fa\u4e73\u767d\u8272\u7684\u5965\u672f\u7075\u5149\u3002\u8fd9\u4e2a\u7ed3\u679c\u5927\u5927\u51fa\u4e4e\u743c\u6069\u7684\u610f\u6599\uff0c\u4ed6\u5dee\u70b9\u4ee5\u4e3a\u81ea\u5df1\u91ca\u653e\u9519\u4e86\u6cd5\u672f\uff0c\u6b63\u51c6\u5907\u518d\u8bd5\u4e00\u6b21\uff0c\u8239\u957f\u5236\u6b62\u4e86\u4ed6\u3002\u201c\u522b\u6d6a\u8d39\u529b\u6c14\u4e86\uff0c\u201d\u8239\u957f\u8bf4\uff0c\u201c\u4f60\u7684\u6cd5\u672f\u6ca1\u6709\u51fa\u9519\uff0c\u4f60\u4e5f\u4e0d\u662f\u7b2c\u4e00\u4e2a\u9732\u51fa\u8fd9\u79cd\u53cd\u5e94\u7684\u5deb\u5e08\u3002\u6211\u662f\u88ab\u8bc5\u5492\u4e86\uff0c\u4f46\u6211\u4e0d\u662f\u4ea1\u7075\u2014\u2014\u6216\u8bb8\u770b\u8d77\u6765\u5f88\u50cf\uff0c\u4f46\u5e76\u4e0d\u662f\u3002\u201d</p><p>\u201c\u4e0d\u662f\u5417\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u559c\u6b22\u5f3a\u70c8\u7684\u65e5\u5149\uff0c\u4f46\u5e76\u4e0d\u662f\u5bb3\u6015\uff1b\u6211\u4e0d\u662f\u5584\u7c7b\uff0c\u4f46\u5e76\u975e\u53d8\u6210\u4ea1\u7075\u624d\u5760\u5165\u9ed1\u6697\uff0c\u800c\u662f\u65e9\u5728\u9ed1\u6697\u4e4b\u4e2d\uff0c\u201d\u8239\u957f\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u201c\u5982\u679c\u613f\u610f\u7684\u8bdd\uff0c\u6211\u53ef\u4ee5\u7528\u5723\u6c34\u6d17\u6fa1\uff0c\u5f53\u7136\u6211\u4e0d\u559c\u6b22\u8fd9\u4e48\u505a\uff0c\u56e0\u4e3a\u5723\u6c34\u5b9e\u5728\u592a\u8d35\u4e86\uff0c\u90a3\u5e2e\u7267\u5e08\u7b80\u76f4\u662f\u6234\u7740\u5723\u5fbd\u7684\u5438\u8840\u9b3c\u3002\u201d</p><p>\u201c\u770b\u6765\u5bf9\u6b64\u6211\u4eec\u6709\u5171\u540c\u8bed\u8a00\u2014\u2014\u90a3\u4e48\u8a00\u5f52\u6b63\u4f20\uff0c\u4f60\u4eca\u5929\u9080\u8bf7\u6211\u6765\u6b64\uff0c\u662f\u6709\u4ec0\u4e48\u4e8b\u5462\uff1f\u201d</p><p>\u201c\u7684\u786e\u6709\u4e8b\u76f8\u6c42\uff0c\u201d\u8239\u957f\u8bf4\uff0c\u201c\u6211\u60f3\u8bf7\u4f60\u5e2e\u6211\u89e3\u5f00\u8fd9\u4e2a\u8bc5\u5492\u3002\u201d</p><p>\u201c\u90a3\u4f60\u53ef\u80fd\u8981\u5931\u671b\u4e86\uff0c\u5e76\u4e0d\u662f\u5deb\u5e08\u90fd\u64c5\u957f\u89e3\u5492\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u5c24\u5176\u4e0d\u64c5\u957f\u3002\u201d</p><p>\u201c\u4f46\u4f60\u662f\u2018\u90a3\u4e2a\u4eba\u2019\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u83ab\u540d\u5176\u5999\uff0c\u201c\u8c01\uff1f\u201d</p><p>\u201c\u516d\u5341\u5e74\u6765\uff0c\u6211\u4e22\u4e0b\u6211\u7684\u8239\u548c\u6c34\u624b\uff0c\u8d70\u904d\u4e86\u6574\u4e2a\u5927\u9646\uff0c\u62dc\u8bbf\u4e86\u5f88\u591a\u8457\u540d\u7684\u5148\u77e5\u3001\u5deb\u5e08\u3001\u796d\u53f8\uff0c\u6ca1\u6709\u4efb\u4f55\u4e00\u4e2a\u4eba\u80fd\u591f\u89e3\u5f97\u5f00\u8fd9\u4e2a\u8bc5\u5492\u3002\u7ec8\u4e8e\uff0c\u5728\u5341\u516d\u5e74\u524d\uff0c\u6211\u6f2b\u65e0\u76ee\u7684\u5730\u6e38\u8361\u5230\u4e86\u5361\u4e3d\u73ca\uff0c\u88ab\u2018\u94c1\u738b\u5ea7\u2019\u6293\u4f4f\u4e86\uff0c\u4ed6\u4eec\u4ee5\u4e3a\u6211\u662f\u5978\u7ec6\uff0c\u4e25\u5211\u62f7\u6253\uff0c\u4f46\u6211\u4ec0\u4e48\u611f\u89c9\u90fd\u6ca1\u6709\uff0c\u6211\u60f3\u8fd9\u4e00\u5b9a\u8ba9\u4ed6\u4eec\u5f88\u6c14\u9981\u3002\u540e\u6765\uff0c\u94c1\u738b\u5ea7\u7684\u4eba\u628a\u6211\u9001\u5230\u4e86\u4e00\u4e2a\u5730\u65b9\uff0c\u90a3\u91cc\u50cf\u662f\u4e00\u4e2a\u5730\u4e0b\u5bab\u6bbf\uff0c\u91d1\u78a7\u8f89\u714c\uff0c\u4f46\u6b7b\u5bc2\u6c89\u6c89\uff0c\u4e00\u4e2a\u4eba\u90fd\u6ca1\u6709\uff0c\u9664\u4e86\u4e00\u5757\u5de8\u5927\u7684\u6c34\u6676\u3002\u6709\u4e2a\u58f0\u97f3\u4ece\u6c34\u6676\u91cc\u4f20\u51fa\u6765\uff0c\u662f\u4e2a\u5973\u6027\uff0c\u5979\u4eff\u4f5b\u88ab\u5c01\u5728\u6c34\u6676\u91cc\uff0c\u6211\u53ea\u80fd\u9690\u7ea6\u770b\u89c1\u5979\u7684\u8f6e\u5ed3\u3002\u5979\u81ea\u79f0\u662f\u4e00\u4f4d\u5973\u795e\uff0c\u6ca1\u6709\u544a\u8bc9\u6211\u540d\u5b57\u3002\u201d</p><p>\u201c\u94c1\u738b\u5ea7\uff1f\u6c34\u6676\uff1f\u5973\u795e\uff1f\u201d\u743c\u6069\u76b1\u7709\uff0c\u8fd9\u4e9b\u4ed6\u4ece\u6765\u6ca1\u542c\u838e\u73de\u514b\u63d0\u5230\u8fc7\uff0c\u201c\u8fd9\u4f4d\u5973\u795e\u7ed9\u4e86\u4f60\u4ec0\u4e48\u6307\u70b9\uff1f\u201d</p><p>\u201c\u5979\u4e3a\u6211\u505a\u4e86\u4e00\u4e2a\u9884\u8a00\uff1a\u5728\u672a\u6765\u7684\u67d0\u4e2a\u65f6\u5019\uff0c\u5f53\u5b63\u98ce\u5411\u897f\u5439\u7684\u65f6\u5019\uff0c\u6709\u4e00\u4f4d\u5deb\u5e08\u5c06\u4e58\u6211\u7684\u8239\u524d\u5f80\u4e1c\u57df\uff0c\u4ed6\u4f1a\u4e3a\u6211\u89e3\u9664\u8bc5\u5492\uff0c\u201d\u8239\u957f\u8bf4\uff0c\u201c\u4e8e\u662f\u6211\u8fd4\u56de\u5760\u661f\u6d77\uff0c\u8fd9\u5341\u516d\u5e74\u6765\uff0c\u6211\u4e00\u76f4\u5728\u7b49\u5f85\u90a3\u4e2a\u4eba\u7684\u51fa\u73b0\u3002\u201d</p><p>\u201c\u7b49\u7b49\uff0c\u4f60\u4e0d\u4f1a\u8bf4\uff0c\u6211\u662f\u5750\u4f60\u7684\u8239\u53bb\u4e1c\u57df\u7684\u7b2c\u4e00\u4e2a\u5deb\u5e08\u5427\uff1f\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u4f60\u662f\u7b2c\u5341\u4e2a\uff0c\u4f46\u524d\u4e5d\u4f4d\u5deb\u5e08\u90fd\u672a\u80fd\u6210\u529f\u3002\u201d</p><p>\u201c\u4f60\u6ca1\u6709\u628a\u4ed6\u4eec\u7684\u5934\u9aa8\u4e32\u6210\u5ff5\u73e0\u5427\uff1f\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d\u8239\u957f\u83ab\u540d\u5176\u5999\u3002</p><p>\u201c\u8bf4\u4e0d\u5b9a\u6211\u4e5f\u4e0d\u662f\u9884\u8a00\u91cc\u7684\u90a3\u4f4d\u5deb\u5e08\uff0c\u4f60\u8fd8\u8981\u7ee7\u7eed\u7b49\u5f85\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u8fc7\u6211\u53ef\u4ee5\u8bd5\u8bd5\u3002\u90a3\u4f4d\u6c34\u6676\u4e2d\u7684\u5973\u795e\u6709\u6ca1\u6709\u8bf4\uff0c\u5177\u4f53\u8981\u600e\u4e48\u505a\u624d\u80fd\u89e3\u9664\u8bc5\u5492\uff1f\u201d</p><p>\u201c\u6ca1\u6709\u3002\u201d</p><p>\u201c\u771f\u662f\u4e0d\u8d1f\u8d23\u4efb\u7684\u5973\u795e\uff0c\u201d\u743c\u6069\u8bc4\u8bba\u8bf4\uff0c\u4ed6\u60f3\u4e86\u60f3\uff0c\u201c\u6216\u8bb8\u6700\u7b80\u5355\u7684\u529e\u6cd5\uff0c\u5c31\u662f\u628a\u8fd9\u679a\u6212\u6307\u8fd8\u56de\u53bb\u3002\u201d</p><p>\u201c\u662f\u7684\uff0c\u6211\u4e5f\u662f\u8fd9\u6837\u60f3\uff0c\u201d\u8239\u957f\u8bf4\uff0c\u201c\u8ba9\u4e00\u5207\u91cd\u5f52\u8d77\u70b9\uff0c\u6216\u8bb8\u6211\u7684\u5669\u68a6\u53ef\u4ee5\u5c31\u6b64\u89e3\u8131\u3002\u4f46\u5b58\u5728\u4e24\u4e2a\u6280\u672f\u4e0a\u7684\u969c\u788d\uff0c\u6211\u65e0\u6cd5\u514b\u670d\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u6280\u672f\u969c\u788d\uff1f\u201d</p><p>\u8239\u957f\u4e3e\u8d77\u624b\u6307\uff0c\u201c\u7b2c\u4e00\u4e2a\u969c\u788d\u662f\uff1a\u5b83\u6839\u672c\u53d6\u4e0d\u4e0b\u6765\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u53d8\u80d6\u4e86\uff1f\u201d</p><p>\u201c\u2026\u2026\u4e0d\u662f\uff0c\u201d\u8239\u957f\u8bf4\uff0c\u201c\u5b83\u5c31\u662f\u53d6\u4e0d\u4e0b\u6765\u3002\u201d</p><p>\u201c\u628a\u90a3\u6839\u624b\u6307\u780d\u6389\uff1f\u201d\u743c\u6069\u5efa\u8bae\u3002</p><p>\u201c\u80fd\u5426\u501f\u5315\u9996\u4e00\u7528\uff1f\u201d</p><p>\u743c\u6069\u72b9\u8c6b\u4e86\u4e0b\uff0c\u4ece\u8170\u95f4\u53d6\u4e0b\u5315\u9996\uff0c\u629b\u8fc7\u53bb\u3002\u8239\u957f\u7528\u5de6\u624b\u63a5\u8fc7\uff0c\u5e72\u8106\u5229\u843d\u5730\u671d\u53f3\u624b\u624b\u8155\u7528\u529b\u65a9\u4e0b\u53bb\u3002\u5728\u5315\u9996\u7684\u950b\u5203\u63a5\u89e6\u5230\u76ae\u80a4\u7684\u90a3\u4e00\u5239\u90a3\uff0c\u743c\u6069\u770b\u89c1\u5fae\u5f31\u7684\u5149\u4ece\u6212\u6307\u4e0a\u5347\u8d77\uff0c\u5c06\u5315\u9996\u5f39\u5f00\u4e86\u3002</p><p>\u8239\u957f\u5c06\u5315\u9996\u629b\u56de\u7ed9\u743c\u6069\uff0c\u201c\u5982\u4f60\u6240\u89c1\uff0c\u5b83\u4e0d\u613f\u610f\u79bb\u5f00\u6211\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u5c1d\u8bd5\u8fc7\u5f88\u591a\u6b21\uff0c\u751a\u81f3\u51b3\u5b9a\u501f\u52a9\u6b7b\u4ea1\u6765\u9003\u79bb\u8fd9\u79cd\u6c38\u6052\u7684\u75db\u82e6\uff0c\u7136\u800c\u6211\u6295\u5165\u706b\u7130\uff0c\u706b\u7130\u5c31\u4f1a\u7184\u706d\uff0c\u6211\u8df3\u8fdb\u5927\u6d77\uff0c\u6d77\u6c34\u5c06\u6211\u6258\u8d77\uff0c\u6211\u7528\u5200\u65a9\u5411\u81ea\u5df1\u7684\u8116\u9888\uff0c\u5200\u5c31\u4f1a\u88ab\u5f39\u5f00\uff0c\u6211\u82b1\u91cd\u91d1\u4e70\u6765\u5267\u6bd2\u7684\u836f\u6c34\uff0c\u5c06\u5b83\u996e\u4e0b\uff0c\u7136\u540e\u5c31\u53d1\u73b0\u5b83\u65e9\u5df2\u8fc7\u671f\u5931\u6548\u3002\u201d</p><p>\u201c\u90a3\u8bf4\u660e\u4f60\u4e70\u5230\u5047\u8d27\u4e86\uff0c\u201d\u743c\u6069\u611f\u53f9\uff0c\u201c\u8fd9\u5e74\u5934\uff0c\u505a\u751f\u610f\u8d8a\u6765\u8d8a\u4e0d\u8bb2\u8bda\u4fe1\u4e86\u3002\u201d</p><p>\u4ed6\u7ad9\u8d77\u6765\uff0c\u8d70\u5230\u8239\u957f\u8eab\u524d\uff0c\u4ed4\u7ec6\u6253\u91cf\u7740\u90a3\u679a\u6212\u6307\u3002\u743c\u6069\u770b\u89c1\u6212\u9762\u4e0a\u6709\u4e24\u53ea\u6d6e\u96d5\u7684\u72ee\u5b50\u56fe\u6848\uff0c\u5206\u522b\u4e3e\u8d77\u4e00\u53ea\u524d\u722a\u62b5\u5728\u4e00\u8d77\uff0c\u7ebf\u6761\u7cbe\u7ec6\uff0c\u6829\u6829\u5982\u751f\u3002\u4ed6\u4e0d\u8ba4\u8bc6\u8fd9\u4e2a\u56fe\u6848\uff0c\u4e5f\u53ef\u4ee5\u786e\u5b9a\u4e4b\u524d\u4ece\u672a\u89c1\u8fc7\uff0c\u4f46\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\uff0c\u4f3c\u4e4e\u603b\u6709\u70b9\u773c\u719f\u3002\u4e0b\u610f\u8bc6\u5730\uff0c\u4ed6\u4f38\u51fa\u4e00\u6839\u624b\u6307\uff0c\u6309\u5728\u6212\u9762\u4e0a\uff0c\u76ae\u80a4\u4f20\u6765\u575a\u786c\u548c\u51b0\u51c9\u7684\u89e6\u611f\u3002</p><p>\u6212\u6307\u7a81\u7136\u4ece\u8239\u957f\u7684\u624b\u6307\u4e0a\u6ed1\u843d\u4e0b\u6765\u3002</p><p>\u743c\u6069\u53cd\u5e94\u5f88\u5feb\uff0c\u4e0d\u5047\u601d\u7d22\u5730\u4e00\u628a\u6284\u4f4f\uff0c\u5c06\u6212\u6307\u63e1\u5728\u638c\u5fc3\uff0c\u7136\u540e\u4ed6\u6014\u4f4f\u4e86\u3002\u8239\u957f\u83ab\u540d\u5176\u5999\uff0c\u53ea\u89c1\u743c\u6069\u7f13\u7f13\u5730\u5c06\u624b\u644a\u5f00\uff0c\u7136\u800c\u638c\u5fc3\u7a7a\u65e0\u4e00\u7269\uff0c\u90a3\u679a\u6212\u6307\u53d8\u5f97\u65e0\u5f71\u65e0\u8e2a\uff0c\u6ca1\u6709\u7559\u4e0b\u4efb\u4f55\u75d5\u8ff9\u3002</p><p>\u201c\u6212\u6307\u5462\uff1f\u201d\u8239\u957f\u60ca\u95ee\u3002</p><p>\u743c\u6069\u5377\u8d77\u8896\u5b50\uff0c\u9732\u51fa\u5de6\u81c2\uff0c\u90a3\u91cc\u6709\u4e00\u4e2a\u7ea2\u888d\u5deb\u5e08\u7684\u523a\u9752\uff0c\u5728\u523a\u9752\u7684\u65c1\u8fb9\uff0c\u6709\u4e00\u5c0f\u5757\u5706\u5f62\u7684\u76ae\u80a4\u6b63\u5728\u53d1\u5149\u3002\u8fc7\u4e86\u5927\u6982\u5341\u79d2\u949f\uff0c\u5149\u6d88\u5931\u4e86\uff0c\u4f46\u743c\u6069\u7684\u624b\u81c2\u4e0a\u591a\u4e86\u4e00\u9897\u91d1\u8272\u7684\u661f\u661f\u56fe\u6848\uff0c\u4eff\u4f5b\u80ce\u8bb0\u4e00\u822c\u3002</p><p>\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u201d\u743c\u6069\u540c\u6837\u56f0\u60d1\uff0c\u201c\u6211\u63a5\u4f4f\u5b83\uff0c\u5b83\u597d\u50cf\u94bb\u8fdb\u4e86\u6211\u7684\u8eab\u4f53\u91cc\uff0c\u7136\u540e\u5c31\u8fd9\u6837\u4e86\u2014\u2014\u9760\uff01\u201d\u4ed6\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u4e0d\u4f1a\u662f\u8bc5\u5492\u7f20\u4e0a\u6211\u4e86\u5427\uff1f\u201d</p><p>\u4ed6\u53cd\u624b\u556a\u5730\u7ed9\u4e86\u81ea\u5df1\u4e00\u5df4\u638c\uff0c\u7136\u540e\u534a\u8fb9\u8138\u80bf\u4e86\u8d77\u6765\uff0c\u75bc\u5f97\u4ed6\u4e1d\u4e1d\u5730\u5438\u51b7\u6c14\uff0c\u201c\u8fd8\u597d\uff0c\u201d\u4ed6\u6b23\u6170\u5730\u8bf4\uff0c\u201c\u81f3\u5c11\u6211\u8fd8\u6709\u75db\u89c9\u3002\u201d</p><p>\u201c\u8fd9\u771f\u662f\u4ee4\u4eba\u7fa1\u6155\uff0c\u201d\u8239\u957f\u8bf4\uff0c\u201c\u4f46\u662f\u73b0\u5728\u6212\u6307\u6ca1\u6709\u4e86\uff0c\u6211\u8981\u600e\u6837\u628a\u5b83\u8fd8\u56de\u53bb\uff1f\u201d</p><p>\u4e24\u4eba\u9762\u9762\u76f8\u89d1\u4e86\u51e0\u79d2\u949f\uff0c\u743c\u6069\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u201c\u81f3\u5c11\u5b83\u79bb\u5f00\u4f60\u4e86\uff0c\u8239\u957f\u3002\u201d\u4ed6\u63d0\u9192\u3002</p><p>\u201c\u5bf9\uff01\u5bf9\uff01\u201d\u8239\u957f\u5982\u68a6\u521d\u9192\uff0c\u201c\u5b83\u7ec8\u4e8e\u79bb\u5f00\u6211\u4e86\uff0c\u54c8\u54c8\uff0c\u54c8\u54c8\uff0c\u201d\u4ed6\u4ece\u8eba\u6905\u4e0a\u8df3\u8d77\u6765\uff0c\u5174\u594b\u5f97\u5728\u623f\u95f4\u91cc\u6765\u56de\u75be\u8d70\uff0c\u201c\u8bc5\u5492\u7ec8\u4e8e\u89e3\u9664\u4e86\uff0c\u6211\u53c8\u53ef\u4ee5\u53bb\u5403\u6211\u6700\u559c\u6b22\u7684\u72d7\u8089\u2014\u2014\u201d</p><p>\u6309\u7167\u5267\u672c\u4e0d\u5e94\u8be5\u662f\u82f9\u679c\u5417\uff1f\u4f60\u8fd9\u4e48\u516c\u7136\u5ba3\u79f0\u559c\u6b22\u5403\u72d7\u8089\u4f1a\u88ab\u8ffd\u6740\u7684\u554a\u3002</p><p>\u201c\u6211\u8fd8\u559c\u6b22\u5403\u8354\u679d\uff0c\u201d\u8239\u957f\u5174\u81f4\u52c3\u52c3\uff0c\u201c\u72d7\u8089\u548c\u8354\u679d\u653e\u5728\u4e00\u8d77\u7096\u6700\u597d\u4e86\u3002\u201d</p><p>\u201c\u6211\u4e0d\u60f3\u626b\u5174\uff0c\u8239\u957f\uff0c\u201d\u743c\u6069\u6253\u65ad\uff0c\u201c\u4f46\u6211\u5fc5\u987b\u8bf4\uff0c\u4f60\u4eab\u7528\u8354\u679d\u7096\u72d7\u8089\u5927\u9910\u7684\u8ba1\u5212\uff0c\u6050\u6015\u8981\u5f80\u540e\u63a8\u8fdf\u4e86\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u6307\u4e86\u6307\uff0c\u8239\u957f\u987a\u7740\u6240\u6307\u7684\u65b9\u5411\u4f4e\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u81ea\u5df1\u7684\u624b\u81c2\u4e0a\uff0c\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u63d2\u4e86\u4e00\u652f\u5315\u9996\uff0c\u9f50\u67c4\u800c\u5165\uff0c\u5374\u8fde\u534a\u70b9\u9c9c\u8840\u90fd\u6ca1\u6709\u6ea2\u51fa\uff0c\u800c\u4ed6\u4e5f\u540c\u6837\u4e00\u65e0\u6240\u89c9\u3002</p><p>\u8bc5\u5492\u4ecd\u7136\u5728\u3002</p><p>\u4f46\u4fdd\u62a4\u4ed6\u7684\u529b\u91cf\u5df2\u7ecf\u6d88\u5931\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u4e8b\u60c5\u83ab\u540d\u5176\u5999\u6f14\u53d8\u6210\u8fd9\u79cd\u7ed3\u679c\uff0c\u8fd8\u771f\u662f\u5927\u5927\u51fa\u4e4e\u743c\u6069\u7684\u610f\u6599\uff0c\u867d\u8bf4\u6682\u65f6\u770b\u4e0d\u51fa\u4ec0\u4e48\u5371\u5bb3\uff0c\u4f46\u8bf4\u4e0d\u5b9a\u662f\u8bc5\u5492\u9700\u8981\u4e00\u5b9a\u65f6\u95f4\u624d\u53d1\u4f5c\uff0c\u76ee\u524d\u662f\u5728\u6f5c\u4f0f\u671f\u5462\u3002\u4e00\u60f3\u5230\u81ea\u5df1\u4e5f\u53ef\u80fd\u53d8\u6210\u90a3\u79cd\u6708\u5149\u4e0b\u7684\u9ab7\u9ac5\uff0c\u743c\u6069\u5c31\u89c9\u4e0d\u5bd2\u800c\u6817\uff0c\u4eba\u751f\u81f3\u6b64\uff0c\u6d3b\u7740\u8fd8\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u4e0d\u5982\u81ea\u6740\u5f97\u4e86\u3002</p><p>\u8239\u957f\u663e\u7136\u4e5f\u5f88\u90c1\u95f7\uff0c\u6212\u6307\u56fa\u7136\u6d88\u5931\u4e86\uff0c\u8bc5\u5492\u5374\u4f9d\u7136\u7559\u5b58\uff0c\u95ee\u9898\u5b8c\u5168\u6ca1\u89e3\u51b3\uff0c\u53cd\u800c\u53d8\u5f97\u66f4\u9690\u853d\uff0c\u53ef\u4ee5\u8bf4\u662f\u6076\u5316\u4e86\u3002\u201c\u5b9e\u5728\u62b1\u6b49\uff0c\u6211\u4e5f\u6ca1\u60f3\u5230\u4f1a\u51fa\u73b0\u8fd9\u79cd\u60c5\u51b5\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u73b0\u5728\u8be5\u600e\u4e48\u529e\uff1f\u201d</p><p>\u5e73\u767d\u65e0\u6545\u60f9\u4e0a\u8fd9\u4e2a\u9ebb\u70e6\uff0c\u743c\u6069\u81ea\u7136\u6ca1\u4ec0\u4e48\u597d\u5fc3\u60c5\uff0c\u4f46\u4e8b\u5df2\u81f3\u6b64\uff0c\u62b1\u6028\u65e0\u76ca\uff0c\u8fd8\u662f\u60f3\u600e\u4e48\u89e3\u51b3\u624d\u662f\u6b63\u7ecf\u3002\u4ed6\u60f3\u4e86\u4e00\u4f1a\uff0c\u201c\u770b\u6765\u8fd8\u662f\u5f97\u53bb\u4f60\u62ff\u5230\u6212\u6307\u7684\u90a3\u4e2a\u795e\u6bbf\u8d70\u4e00\u8d9f\uff0c\u6216\u8bb8\u6709\u4ec0\u4e48\u529e\u6cd5\u80fd\u628a\u6212\u6307\u8fd8\u56de\u53bb\uff0c\u89e3\u9664\u8fd9\u4e2a\u8bc5\u5492\u3002\u201d</p><p>\u8239\u957f\u4ece\u684c\u4e0a\u62ff\u8d77\u4e00\u5f20\u7eb8\uff0c\u9012\u7ed9\u743c\u6069\uff0c\u201c\u8fd9\u5c31\u662f\u5f53\u65f6\u90a3\u5f20\u85cf\u5b9d\u56fe\uff0c\u6211\u6839\u636e\u8bb0\u5fc6\u5728\u4e0a\u9762\u53c8\u505a\u4e86\u4e00\u4e9b\u6807\u6ce8\uff0c\u6216\u8bb8\u5bf9\u4f60\u6709\u7528\u3002\u201d</p><p>\u201c\u4f60\u81ea\u5df1\u4e0d\u53bb\uff1f\u201d\u743c\u6069\u8be7\u5f02\u3002</p><p>\u201c\u6211\u524d\u9762\u8bf4\uff0c\u6709\u4e24\u4e2a\u6280\u672f\u4e0a\u7684\u969c\u788d\uff0c\u201d\u8239\u957f\u8bf4\uff0c\u201c\u7b2c\u4e00\u4e2a\u969c\u788d\u662f\u6211\u53d6\u4e0d\u4e0b\u6212\u6307\uff0c\u7b2c\u4e8c\u4e2a\u969c\u788d\u662f\uff1a\u6211\u6ca1\u529e\u6cd5\u8fdb\u5165\u4e1c\u57df\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u4ee5\u4e3a\u6211\u4f1a\u6ca1\u60f3\u8fc7\u518d\u627e\u5230\u90a3\u5ea7\u795e\u6bbf\uff0c\u628a\u6212\u6307\u8fd8\u56de\u53bb\u5417\uff1f\u201d\u8239\u957f\u82e6\u7b11\uff0c\u201c\u6211\u8bd5\u8fc7\u5f88\u591a\u6b21\u4e86\uff0c\u4e0d\u884c\u3002\u6211\u6839\u672c\u65e0\u6cd5\u8e0f\u5165\u4e1c\u57df\u534a\u6b65\uff0c\u5c31\u50cf\u662f\u6709\u4ec0\u4e48\u65e0\u5f62\u7684\u529b\u91cf\u5728\u963b\u6b62\u4f3c\u7684\u3002\u201d</p><p>\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u4ece\u8239\u957f\u624b\u91cc\u63a5\u8fc7\u5730\u56fe\uff0c\u201c\u6211\u8bd5\u8bd5\u770b\u5427\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u201c\u4e07\u5206\u611f\u6fc0\uff0c\u201d\u8239\u957f\u8bf4\uff0c\u201c\u4e0d\u8fc7\u5766\u767d\u5730\u8bf4\uff0c\u8fd9\u6761\u8def\u672a\u5fc5\u8d70\u5f97\u901a\u3002\u8fd9\u4e9b\u5e74\u6765\uff0c\u867d\u7136\u6211\u81ea\u5df1\u65e0\u6cd5\u8e0f\u8db3\u4e1c\u57df\uff0c\u4f46\u4e5f\u66fe\u7ecf\u591a\u6b21\u6d3e\u4eba\u53bb\u627e\u8fc7\uff0c\u7ed3\u679c\u6839\u672c\u5c31\u627e\u4e0d\u5230\u5f53\u5e74\u6211\u53d1\u73b0\u6212\u6307\u7684\u90a3\u5ea7\u795e\u6bbf\uff0c\u8fde\u4e00\u70b9\u75d5\u8ff9\u90fd\u6ca1\u6709\uff0c\u5b83\u5c31\u50cf\u662f\u4ece\u6765\u5c31\u6ca1\u6709\u5b58\u5728\u8fc7\u4f3c\u7684\u3002\u201d</p><p>\u201c\u90a3\u4f60\u6709\u4ec0\u4e48\u597d\u5efa\u8bae\uff1f\u201d</p><p>\u201c\u6211\u54ea\u6709\u4ec0\u4e48\u597d\u5efa\u8bae\uff0c\u201d\u8239\u957f\u82e6\u7b11\uff0c\u201c\u53ea\u80fd\u78b0\u78b0\u8fd0\u6c14\uff0c\u542c\u5929\u7531\u547d\u4e86\u3002\u201d</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u4ed6\u7a81\u7136\u8138\u8272\u5927\u53d8\uff0c\u4f38\u624b\u6342\u4f4f\u80f8\u53e3\u3002\u201c\u4f60\u600e\u4e48\u4e86\uff1f\u201d\u743c\u6069\u8be7\u5f02\u5730\u95ee\u3002</p><p>\u8239\u957f\u6ca1\u6709\u56de\u7b54\uff0c\u800c\u662f\u626f\u5f00\u81ea\u5df1\u7684\u8863\u9886\uff0c\u9732\u51fa\u80f8\u819b\u3002\u743c\u6069\u76b1\u7709\uff0c\u4ed6\u53ef\u6ca1\u5174\u8da3\u770b\u4e00\u4e2a\u4eba\u7537\u4eba\u7684\u80f8\u90e8\uff0c\u4f46\u4e00\u6392\u95ea\u95ea\u53d1\u5149\u7684\u5b57\u7acb\u523b\u6620\u5165\u773c\u5e18\uff0c\u5438\u5f15\u4e86\u4ed6\u7684\u6ce8\u610f\u3002</p><p>\u90a3\u662f\u4e00\u884c\u6069\u745f\u6587\u5b57\uff0c\u51fa\u73b0\u5728\u8239\u957f\u7684\u80f8\u53e3\u76ae\u80a4\u4e0a\uff0c\u4eff\u4f5b\u7194\u5ca9\u822c\u53d1\u7740\u5371\u9669\u7684\u7ea2\u5149\u3002\u743c\u6069\u638f\u51fa\u7b14\uff0c\u5feb\u901f\u628a\u6587\u5b57\u8bb0\u4e0b\u6765\uff0c\u987a\u624b\u5c31\u5199\u5728\u90a3\u5f20\u201c\u85cf\u5b9d\u56fe\u201d\u7684\u80cc\u9762\u3002\u8fc7\u4e86\u534a\u5206\u949f\uff0c\u8239\u957f\u80f8\u53e3\u7684\u95ea\u5149\u6587\u5b57\u6d88\u5931\u4e86\u3002</p><p>\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u743c\u6069\u7684\u6069\u745f\u8bed\u662f\u521a\u5b66\uff0c\u6c34\u5e73\u5f88\u5dee\uff0c\u8fa8\u8ba4\u4e0d\u51fa\u8fd9\u884c\u6587\u5b57\u7684\u610f\u601d\u3002\u8239\u957f\u663e\u7136\u662f\u61c2\u7684\uff0c\u4ed6\u770b\u4e86\u7247\u523b\uff0c\u7136\u540e\u4e00\u4e2a\u5b57\u4e00\u4e2a\u5b57\u5730\u7ffb\u8bd1\u51fa\u6765\u3002</p><p>\u201c\u82e5\u8981\u89e3\u9664\u8bc5\u5492\uff0c\u987b\u5bfb\u7fe1\u7fe0\u5973\u5deb\u3002\u201d</p><p>\u201c\u7fe1\u7fe0\u5973\u5deb\u662f\u8c01\uff1f\u201d</p><p>\u8239\u957f\u6447\u6447\u5934\uff1a\u201c\u95fb\u6240\u672a\u95fb\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u4e0e\u8239\u957f\u544a\u522b\uff0c\u56de\u5230\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u6b63\u597d\u6885\u83f2\u65af\u8fdb\u6765\uff0c\u201c\u4f60\u7684\u8138\u600e\u4e48\u4e86\uff1f\u201d\u5979\u95ee\uff0c\u201c\u5411\u8c01\u6c42\u6b22\u4e0d\u9042\u88ab\u63cd\u4e86\uff1f\u201d</p><p>\u201c\u2026\u2026\u81ea\u5df1\u63cd\u7684\u3002\u201d</p><p>\u201c\u90a3\u662f\u4f60\u7ec8\u4e8e\u826f\u5fc3\u53d1\u73b0\uff0c\u51b3\u5b9a\u5fcf\u6094\u4e86\uff1f\u201d</p><p>\u201c\u4f60\u5c31\u662f\u6211\u7684\u6551\u8d4e\uff0c\u6211\u7684\u5e0c\u671b\uff0c\u6211\u9ed1\u6697\u4e2d\u7684\u5149\uff0c\u6211\u8ff7\u832b\u4e2d\u7684\u7f57\u76d8\uff0c\u6709\u4e86\u4f60\uff0c\u6211\u5c31\u4e0d\u9700\u8981\u5fcf\u6094\u4e86\u3002\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u9700\u8981\u5fcf\u6094\uff0c\u56e0\u4e3a\u6211\u4f1a\u76f4\u63a5\u9001\u4f60\u4e0b\u5730\u72f1\u2014\u2014\u7b97\u4e86\uff0c\u8fd8\u662f\u6362\u4e2a\u5730\u65b9\u5427\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u4f60\u89c9\u5f97\u6df1\u6e0a\u5982\u4f55\u3002\u201d</p><p>\u201c\u633a\u597d\u7684\uff0c\u201d\u743c\u6069\u88c5\u4f5c\u601d\u8003\u4e86\u4e00\u4f1a\uff0c\u201c\u4e0d\u8fc7\u6216\u8bb8\u90a3\u91cc\u4e5f\u6709\u6211\u7684\u65e7\u60c5\u4eba\uff0c\u8fd9\u8c01\u8bf4\u5f97\u5b9a\u5462\u3002\u4f60\u77e5\u9053\uff0c\u4eba\u751f\u603b\u662f\u5145\u6ee1\u5404\u79cd\u610f\u5916\u7684\u60ca\u559c\u3002\u201d</p><p>\u201c\u90a3\u770b\u6765\u6211\u8fd8\u662f\u5e94\u8be5\u8d81\u65e9\u628a\u4f60\u5e72\u6389\uff0c\u4e3a\u6c11\u9664\u5bb3\uff0c\u6c38\u7edd\u540e\u60a3\u3002\u201d</p><p>\u8bf4\u7b11\u4e86\u51e0\u53e5\uff0c\u743c\u6069\u5c06\u624b\u81c2\u4e0a\u65b0\u51fa\u73b0\u7684\u90a3\u9897\u661f\u661f\u80ce\u8bb0\u7ed9\u6885\u83f2\u65af\u770b\uff0c\u540c\u65f6\u8bf4\u660e\u6765\u5386\uff0c\u628a\u6574\u4e2a\u4e8b\u60c5\u7684\u7ecf\u8fc7\u8bb2\u4e86\u4e00\u904d\uff0c\u6700\u540e\u628a\u90a3\u884c\u5b57\u5199\u7ed9\u6885\u83f2\u65af\u770b\u3002\u201c\u7ffb\u8bd1\u662f\u6ca1\u9519\uff0c\u81f3\u4e8e\u8fd9\u4e2a\u7fe1\u7fe0\u5973\u5deb\uff1f\u201d\u5c11\u5973\u60f3\u4e86\u4e00\u4f1a\uff0c\u6447\u6447\u5934\uff0c\u201c\u5b8c\u5168\u6ca1\u542c\u8bf4\u8fc7\u3002\u201d</p><p>\u201c\u4f60\u4e5f\u6ca1\u542c\u8fc7\u554a\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u5931\u671b\uff0c\u8fd9\u53e5\u8bdd\u663e\u7136\u9887\u4e0d\u5bfb\u5e38\uff0c\u751a\u81f3\u6709\u53ef\u80fd\u4e0e\u4ed6\u6709\u5173\u3002\u8981\u77e5\u9053\uff0c\u201c\u8bc5\u5492\u201d\u8fd9\u4e1c\u897f\uff0c\u53ef\u4e0d\u4ec5\u4ec5\u662f\u8239\u957f\u7684\u4e13\u5229\uff0c\u4ed6\u81ea\u5df1\u8eab\u4e0a\u4e5f\u8fd8\u6709\u4e00\u4e2a\u5462\u3002\u867d\u7136\u7ef4\u82e5\u62c9\u4fdd\u8bc1\u53ea\u8981\u627e\u5230\u90a3\u79cd\u201c\u5deb\u94c1\u201d\uff0c\u5c31\u53ef\u4ee5\u7528\u9a6c\u4f26\u7814\u7a76\u51fa\u7684\u65b9\u6cd5\u89e3\u5492\uff0c\u95ee\u9898\u662f\u6ca1\u6709\u6210\u529f\u7684\u5148\u4f8b\u554a\uff0c\u8fde\u9a6c\u4f26\u81ea\u5df1\u90fd\u662f\u7eb8\u4e0a\u8c08\u5175\uff0c\u5e76\u6ca1\u80fd\u771f\u7684\u89e3\u5492\uff08\u56e0\u4e3a\u5a1c\u5854\u8389\u838e\u6b7b\u4e86\uff0c\u5bfc\u81f4\u8bc5\u5492\u53d8\u6210\u4e86\u6b7b\u5492\uff09\u3002\u4e07\u4e00\u9a6c\u4f26\u7684\u5206\u6790\u63a8\u7b97\u6709\u8bef\uff0c\u4e2d\u95f4\u4ec0\u4e48\u5730\u65b9\u51fa\u4e86\u5c94\u5b50\uff0c\u90a3\u8981\u600e\u4e48\u529e\uff1f\u8fd9\u79cd\u65f6\u5019\uff0c\u7a81\u7136\u5192\u51fa\u4e00\u4e2a\u7fe1\u7fe0\u5973\u5deb\uff0c\u7531\u4e0d\u5f97\u743c\u6069\u4e0d\u91cd\u89c6\u3002</p><p>\u201c\u7fe1\u7fe0\u5973\u5deb\u201d\u663e\u7136\u4e0d\u662f\u540d\u5b57\uff0c\u800c\u662f\u4e00\u4e2a\u79f0\u53f7\u3002\u8fd9\u4e2a\u4e16\u754c\u4e0a\u7684\u5deb\u5e08\u666e\u904d\u6df1\u5c45\u7b80\u51fa\uff0c\u6781\u5c11\u6709\u9ad8\u8c03\u7684\uff0c\u50cf\u6df1\u6c34\u57ce\u4e3b\u51ef\u5c14\u672c\u3001\u98ce\u66b4\u5973\u5deb\u6b23\u5e03\u3001\u7ea2\u888d\u9996\u5e2d\u8428\u624e\u65af\u5766\u8fd9\u79cd\u540d\u58f0\u8d6b\u8d6b\u7684\u5927\u5deb\u5e08\uff0c\u5176\u5b9e\u662f\u5c5e\u4e8e\u53e6\u7c7b\u3002\u7edd\u5927\u90e8\u5206\u90fd\u559c\u6b22\u5b85\uff0c\u559c\u6b22\u9690\u5c45\uff0c\u559c\u6b22\u95f7\u58f0\u53d1\u5927\u8d22\uff0c\u540d\u58f0\u4e0d\u663e\u3002\u8fd9\u4e2a\u4ec0\u4e48\u7fe1\u7fe0\u5973\u5deb\uff0c\u60f3\u5fc5\u4e5f\u662f\u4e2a\u9690\u8005\uff0c\u8fd9\u8981\u5230\u54ea\u91cc\u627e\u53bb\u3002</p><p>\u201c\u65e2\u7136\u539f\u6587\u662f\u6069\u745f\u8bed\uff0c\u60f3\u5fc5\u662f\u4e0e\u6b64\u6709\u5173\uff0c\u201d\u6885\u83f2\u65af\u5206\u6790\u8bf4\uff0c\u201c\u6240\u4ee5\u8fd9\u4f4d\u5973\u5deb\u5e94\u8be5\u662f\u5728\u6069\u745f\uff0c\u6216\u8005\u5f7b\u68ee\u5854\uff0c\u53ea\u6709\u8fd9\u4e24\u4e2a\u5730\u65b9\u662f\u8bf4\u6069\u745f\u8bed\u3002\u201d</p><p>\u201c\u6069\u745f\u52a0\u4e0a\u5f7b\u68ee\u5854\u5c31\u662f\u534a\u4e2a\u4e1c\u57df\u4e86\uff0c\u8fd8\u662f\u5927\u6d77\u635e\u9488\u554a\u3002\u201d</p><p>\u201c\u5230\u4e86\u5f7b\u68ee\u5854\u518d\u6253\u542c\u5427\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u8bf4\u4e0d\u5b9a\u4f1a\u6709\u4ec0\u4e48\u7ebf\u7d22\u3002\u201d</p><p>\u7fe1\u7fe0\u5973\u5deb\u7684\u4e8b\u60c5\u6682\u4e14\u653e\u4e0b\uff0c\u4e24\u4eba\u63a5\u7740\u8bf4\u5230\u90a3\u679a\u83ab\u540d\u6d88\u5931\u7684\u6212\u6307\u3002</p><p>\u201c\u4e00\u679a\u5e26\u8bc5\u5492\u7684\u6212\u6307\uff1f\u201d\u6885\u83f2\u65af\u76b1\u7709\uff0c\u201c\u5b83\u662f\u4ec0\u4e48\u6837\u7684\uff1f\u201d</p><p>\u743c\u6069\u63cf\u8ff0\u4e86\u4e00\u4e0b\uff0c\u540e\u6765\u7d22\u6027\u76f4\u63a5\u753b\u4e86\u4e2a\u56fe\uff0c\u201c\u6212\u9762\u662f\u8fd9\u4e2a\u56fe\u6848\uff0c\u4f60\u770b\u51fa\u4ec0\u4e48\u4e86\u6ca1\u6709\uff1f\u201d</p><p>\u201c\u4e24\u53ea\u524d\u722a\u63e1\u5728\u4e00\u8d77\u7684\u72d7\uff1f\u201d\u6885\u83f2\u65af\u6c89\u541f\uff0c\u201c\u4ece\u6ca1\u89c1\u8fc7\u8fd9\u79cd\u5fbd\u8bb0\u3002\u201d</p><p>\u201c\u2026\u2026\u90a3\u662f\u4e24\u53ea\u72ee\u5b50\uff0c\u4e0d\u662f\u72d7\u3002\u201d</p><p>\u5c11\u5973\u4ed4\u7ec6\u5730\u770b\u4e86\u770b\u743c\u6069\u753b\u7684\u56fe\uff0c\u201c\u662f\u72ee\u5b50\u72d7\uff1f\u201d\u5979\u4e0d\u786e\u5b9a\u5730\u95ee\u3002</p><p>\u201c\u5c31\u662f\u72ee\u5b50\uff0c\u548c\u72d7\u6ca1\u6709\u4efb\u4f55\u5173\u7cfb\uff01\u6211\u53ea\u662f\u753b\u5f97\u7b80\u7565\u4e86\u4e00\u70b9\u800c\u5df2\uff0c\u201d\u743c\u6069\u8fa9\u89e3\uff0c\u201c\u7ed8\u753b\u91cd\u5728\u5f97\u5176\u795e\u97f5\uff0c\u4e0d\u8981\u62d8\u6ce5\u5916\u5728\u3002\u201d</p><p>\u201c\u55ef\uff0c\u662f\u633a\u6709\u72d7\u72d7\u7684\u795e\u97f5\u3002\u201d</p><p>\u201c\u2026\u2026\u597d\u5427\uff0c\u72d7\u5c31\u72d7\uff0c\u201d\u743c\u6069\u653e\u5f03\u4e86\uff0c\u201c\u6240\u4ee5\u4f60\u770b\u51fa\u4ec0\u4e48\u6ca1\u6709\uff1f\u201d</p><p>\u201c\u4e0d\u597d\u8bf4\u3002\u72ee\u5b50\u7b97\u662f\u5f88\u5e38\u89c1\u7684\u56fe\u6848\uff0c\u5f88\u591a\u795e\u660e\u3001\u5f88\u591a\u56fd\u5bb6\u3001\u5f88\u591a\u5bb6\u65cf\u4ee5\u53ca\u5386\u53f2\u4e0a\u7684\u8457\u540d\u4eba\u7269\uff0c\u90fd\u6709\u4ee5\u72ee\u5b50\u4f5c\u4e3a\u5fbd\u8bb0\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5fe0\u8bda\u4e0e\u52c7\u6c14\u4e4b\u795e\u5728\u52a8\u8361\u5e74\u4ee3\u66fe\u7ecf\u4ee5\u72ee\u5b50\u7684\u5f62\u8c61\u4e0b\u51e1\uff1b\u81ea\u7136\u8bf8\u795e\u4e2d\u6709\u4e00\u4f4d\u534a\u795e\u52aa\u6bd4\u6069\uff0c\u5723\u5fbd\u662f\u4e00\u53ea\u7eff\u8272\u7684\u72ee\u5b50\uff1b\u56e0\u5e03\u56fe\u738b\u56fd\u4ee5\u72ee\u5b50\u4f5c\u4e3a\u5723\u517d\uff0c\u56fd\u738b\u79f0\u72ee\u5b50\u738b\uff1b\u5854\u745f\u8c37\u7684\u5e9e\u7f57\u5bb6\u2014\u2014\u5c31\u662f\u6b4c\u66e6\u96c5\u548c\u5e0c\u6b27\u5bb6\u2014\u2014\u662f\u4ee5\u96c4\u72ee\u4f5c\u4e3a\u5bb6\u5fbd\uff0c\u201d\u5979\u76b1\u7740\u7709\uff0c\u201c\u603b\u4e4b\u5f88\u5e38\u89c1\uff0c\u4ec5\u51ed\u73b0\u5728\u8fd9\u70b9\u4e1c\u897f\u8fd8\u770b\u4e0d\u51fa\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u8239\u957f\u8bf4\u6212\u6307\u6765\u81ea\u4e1c\u57df\uff0c\u201d\u743c\u6069\u731c\u6d4b\uff0c\u201c\u6240\u4ee5\u5e94\u8be5\u4ece\u4e1c\u57df\u8fd9\u8fb9\u7740\u624b\u8003\u8651\u3002\u201d</p><p>\u201c\u4e1c\u57df\u7684\u8d44\u6599\u8fd8\u662f\u592a\u5c11\u4e86\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u4ece\u53f6\u5f18\u51fa\u53d1\u4e4b\u524d\uff0c\u6885\u83f2\u65af\u4ece\u6b27\u683c\u739b\u795e\u6bbf\u91cd\u91d1\u4e70\u4e86\u4e00\u6279\u6709\u5173\u4e1c\u57df\u7684\u4e66\u7c4d\uff0c\u5728\u6d77\u4e0a\u7684\u65f6\u5019\u5df2\u7ecf\u57fa\u672c\u8bfb\u5b8c\u4e86\u3002\u786e\u5b9e\u5f88\u6709\u7528\uff0c\u4f46\u4e0d\u5f97\u4e0d\u8bf4\uff0c\u8fd8\u662f\u8fc7\u5206\u7b80\u7565\uff0c\u5f88\u591a\u5730\u65b9\u90fd\u662f\u873b\u8713\u70b9\u6c34\u822c\u4e00\u5e26\u800c\u8fc7\uff0c\u6216\u8005\u5e72\u8106\u5c31\u8bed\u7109\u4e0d\u8be6\uff0c\u751a\u81f3\u524d\u540e\u77db\u76fe\u3002\u5730\u7406\u3001\u98ce\u4fd7\u65b9\u9762\u7684\u63cf\u8ff0\u8fd8\u7a0d\u5fae\u8be6\u7ec6\u4e9b\uff0c\u5386\u53f2\u8bb0\u8f7d\u65b9\u9762\u5c31\u60e8\u4e0d\u5fcd\u7779\u4e86\u3002\u6885\u83f2\u65af\u89e3\u91ca\u8bf4\uff0c\u8fd9\u662f\u56e0\u4e3a\u65e0\u8bba\u662f\u7a46\u7f55\u8fd8\u662f\u6069\u745f\uff0c\u4e1c\u57df\u4e24\u56fd\u90fd\u6ca1\u6709\u4fee\u53f2\u7684\u4f20\u7edf\uff0c\u6587\u5316\u6559\u80b2\u4e5f\u4ec5\u9650\u4e8e\u796d\u53f8\u9636\u5c42\uff0c\u522b\u8bf4\u666e\u901a\u6c11\u4f17\uff0c\u8fde\u5f88\u591a\u8d35\u65cf\u90fd\u662f\u6587\u76f2\u3002\u4e1c\u57df\u53c8\u4e0d\u592a\u5e73\uff0c\u7a46\u7f55\u548c\u6069\u745f\u7ecf\u5e38\u6253\u6765\u6253\u53bb\uff0c\u5728\u4e24\u56fd\u4f11\u6218\u7684\u65f6\u5019\uff0c\u4ed6\u4eec\u5185\u90e8\u53c8\u5f80\u5f80\u81ea\u5df1\u6253\u6765\u6253\u53bb\uff0c\u653f\u5c40\u6df7\u4e71\uff0c\u6c11\u4e0d\u804a\u751f\uff0c\u7edf\u6cbb\u8005\u90fd\u5fd9\u7740\u6574\u519b\u5907\u6218\uff0c\u8c01\u4e5f\u6ca1\u5de5\u592b\u9759\u4e0b\u5fc3\u6765\u505a\u6587\u5316\u4e8b\u4e1a\u3002\u5bfc\u81f4\u7684\u7ed3\u679c\u5c31\u662f\u4e1c\u57df\u7684\u5386\u53f2\u5b8c\u5168\u6ca1\u6709\u8bb0\u8f7d\uff0c\u5168\u9760\u4e00\u4e9b\u6c11\u95f4\u6b4c\u8c23\u3001\u4f20\u8bf4\u624d\u80fd\u7565\u7aa5\u4e00\u4e8c\u3002\u5076\u5c14\u6709\u4e2d\u571f\u7684\u65c5\u884c\u8005\u6765\u4e1c\u57df\uff0c\u5e76\u4e14\u8fd8\u80fd\u6d3b\u7740\u8fd4\u56de\uff0c\u53ef\u80fd\u4f1a\u7559\u4e0b\u4e00\u4e9b\u7b14\u8bb0\uff0c\u8fd9\u4e9b\u5c31\u662f\u975e\u5e38\u73cd\u8d35\u6709\u4ef7\u503c\u7684\u5386\u53f2\u7814\u7a76\u8d44\u6599\u4e86\uff0c\u867d\u7136\u8fd9\u4e9b\u7b14\u8bb0\u5185\u5bb9\u7684\u53ef\u4fe1\u5ea6\u672c\u8eab\u4e5f\u9887\u6210\u95ee\u9898\u3002</p><p>\u201c\u522b\u7684\u4e14\u4e0d\u8bf4\uff0c\u8fde\u6587\u5b57\u90fd\u4e0d\u77e5\u9053\u53d8\u4e86\u591a\u5c11\u6b21\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6700\u65e9\u7684\u6069\u745f\u6587\u548c\u7a46\u7f55\u6587\uff0c\u90fd\u662f\u8c61\u5f62\u6587\u5b57\uff1b\u4eca\u5929\u7684\u6069\u745f\u6587\u548c\u7a46\u7f55\u6587\uff0c\u5168\u90fd\u662f\u62fc\u97f3\u6587\u5b57\u2014\u2014\u4ec0\u4e48\u65f6\u5019\u53d8\u8fc7\u6765\u7684\uff0c\u600e\u4e48\u53d8\u8fc7\u6765\u7684\uff0c\u6e90\u6d41\u6f14\u53d8\u662f\u600e\u4e48\u6837\uff0c\u8c01\u4e5f\u8bf4\u4e0d\u6e05\uff0c\u8fde\u73b0\u5728\u7684\u90a3\u4e9b\u796d\u53f8\u4eec\u90fd\u4e0d\u8ba4\u8bc6\u53e4\u6587\u5b57\u4e86\u3002\u201d</p><p>\u603b\u4e4b\u5c31\u662f\u6682\u65f6\u6ca1\u5934\u7eea\uff0c\u597d\u5728\u867d\u7136\u624b\u81c2\u4e0a\u591a\u4e86\u4e2a\u80ce\u8bb0\uff0c\u4e5f\u6ca1\u53d1\u73b0\u6709\u4ec0\u4e48\u5b9e\u9645\u5f71\u54cd\uff0c\u81f3\u5c11\u743c\u6069\u8bd5\u9a8c\u4e86\u51e0\u6b21\uff0c\u786e\u8ba4\u81ea\u5df1\u4ecd\u7136\u662f\u6d3b\u4eba\u800c\u975e\u4ea1\u7075\uff0c\u7167\u6837\u80fd\u591f\u5403\u996d\u7761\u89c9\u547c\u5438\u8d70\u8def\uff0c\u628a\u6885\u83f2\u65af\u62b1\u5728\u6000\u4e2d\u4e00\u6837\u611f\u89c9\u6e29\u9999\u8f6f\u7389\uff0c\u9664\u4e86\u4ecd\u7136\u4e0d\u4e3e\uff0c\u5176\u4ed6\u4e00\u5207\u6b63\u5e38\uff0c\u8fd8\u662f\u5148\u4e0b\u8239\u518d\u8bf4\u5427\u3002</p></article>"}}}]);
