(window.webpackJsonp=window.webpackJsonp||[]).push([[1757],{1780:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e8c\u8282 \u8bfa\u5a03",content:"<article><h2>\u7b2c\u5341\u4e8c\u8282 \u8bfa\u5a03</h2><p>\u6885\u83f2\u65af\u82b1\u4e86\u5927\u6982\u534a\u4e2a\u5c0f\u65f6\uff0c\u7ec8\u4e8e\u8ba9\u8fd9\u51e0\u4e2a\u4e25\u91cd\u7f3a\u4e4f\u5b97\u6559\u5b66\u77e5\u8bc6\u7684\u5bb6\u4f19\u5f04\u6e05\u695a\u662f\u600e\u4e48\u56de\u4e8b\u3002</p><p>\u201c\u9996\u5148\u8981\u58f0\u660e\uff0c\u6211\u4e0b\u9762\u8981\u8bf4\u7684\u4e1c\u897f\u5e76\u4e0d\u4e00\u5b9a\u5b8c\u5168\u662f\u4e8b\u5b9e\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5176\u4e2d\u6709\u5f88\u5927\u4e00\u90e8\u5206\u662f\u731c\u6d4b\uff0c\u5e76\u65e0\u8bc1\u636e\u3002\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u4f60\u731c\u7684\u80af\u5b9a\u90fd\u5bf9\uff0c\u201d\u51db\u8bf4\uff0c\u5979\u4e0d\u77e5\u4ece\u54ea\u91cc\u5f04\u6765\u4e00\u76d8\u70b9\u5fc3\uff0c\u6b63\u5728\u6d25\u6d25\u6709\u5473\u5730\u54c1\u5c1d\uff0c\u201c\u5feb\u8bf4\u5427\uff0c\u6211\u90fd\u5df2\u7ecf\u7b49\u4e0d\u53ca\u4e86\u3002\u201d</p><p>\u201c\u6211\u7684\u610f\u601d\u662f\u8bf4\uff0c\u9009\u62e9\u51db\u7684\u6709\u53ef\u80fd\u662f\u4e94\u8272\u9f99\u540e\u63d0\u4e9a\u739b\u7279\uff0c\u8fd9\u7684\u786e\u4e0d\u50cf\u662f\u4e00\u4e2a\u90aa\u795e\u7684\u98ce\u683c\uff0c\u5979\u7684\u76ee\u7684\u6682\u65f6\u672a\u77e5\uff1b\u4f46\u66f4\u6709\u53ef\u80fd\u5c31\u662f\u2018\u5bdf\u65af\u8428\u2019\u2014\u2014\u4e0d\u662f\u4f5c\u4e3a\u9f99\u795e\u7684\u5bdf\u65af\u8428\uff0c\u66f4\u4e0d\u662f\u4f5c\u4e3a\u4e94\u8272\u9f99\u540e\u7684\u4e00\u4e2a\u5316\u8eab\u7684\u5bdf\u65af\u8428\uff0c\u800c\u662f\u4f5c\u4e3a\u5f7b\u68ee\u5854\u5b88\u62a4\u795e\u7684\u5bdf\u65af\u8428\u2014\u2014\u9f99\u795e\u7684\u795e\u683c\u5206\u88c2\u4e86\u3002\u201d</p><p>\u795e\u683c\u4e5f\u4f1a\u5206\u88c2\u5417\uff1f</p><p>\u5f53\u7136\u4f1a\uff0c\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u795e\u662f\u534a\u4eba\u683c\u534a\u89c4\u5219\uff0c\u534a\u7406\u5ff5\u534a\u4fe1\u4ef0\u7684\u5b58\u5728\uff0c\u6240\u8c13\u201c\u795e\u683c\u201d\uff0c\u53ef\u4ee5\u7406\u89e3\u4e3a\u795e\u6240\u4ee3\u8868\u7684\u89c4\u5219\u3001\u7406\u5ff5\u7684\u96c6\u5408\u3002\u4e3e\u4f8b\u6765\u8bf4\uff0c\u201c\u5149\u660e\u707f\u70c2\u201d\u662f\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u7684\u795e\u683c\uff0c\u201c\u6d88\u706d\u4ea1\u7075\u201d\u4e5f\u662f\u5170\u68ee\u5fb7\u5c14\u7684\u795e\u683c\uff0c\u800c\u201c\u606a\u5b88\u5f8b\u6cd5\u201d\u5c31\u4e0d\u662f\uff0c\u90a3\u662f\u63d0\u5c14\u7684\u795e\u683c\u3002\u6bcf\u4e00\u4f4d\u795e\u7957\u90fd\u6709\u4e00\u4e9b\u76f8\u5e94\u7684\u795e\u683c\uff0c\u6bcf\u4f4d\u795e\u7957\u6240\u62e5\u6709\u7684\u795e\u683c\u4e5f\u53ef\u80fd\u4e0d\u6b62\u4e00\u4e2a\uff0c\u4f46\u5fc5\u7136\u662f\u76f8\u8fd1\u7684\u3001\u76f8\u7c7b\u4f3c\u7684\uff0c\u6709\u5171\u901a\u4e4b\u5904\u7684\uff0c\u7edd\u65e0\u53ef\u80fd\u662f\u76f8\u53cd\u7684\u3002\u4ecd\u7136\u4ee5\u5170\u68ee\u5fb7\u5c14\u4e3a\u4f8b\uff0c\u4ed6\u4ee3\u8868\u7740\u51e1\u4eba\u5bf9\u592a\u9633\u7684\u5d07\u62dc\uff0c\u5bf9\u5149\u660e\u7684\u5411\u5f80\uff0c\u4e5f\u4ee3\u8868\u7740\u51e1\u4eba\u5bf9\u751f\u547d\u7684\u70ed\u7231\uff0c\u5bf9\u4ea1\u7075\u7684\u618e\u6076\uff0c\u4ed6\u4ee3\u8868\u7740\u9752\u6625\u3001\u6d3b\u529b\u3001\u5e0c\u671b\u548c\u61a7\u61ac\uff0c\u8fd9\u4e9b\u795e\u683c\u90fd\u662f\u76f8\u8fd1\u3001\u76f8\u878d\u7684\u3002\u4ed6\u4e0d\u53ef\u80fd\u540c\u65f6\u53c8\u4ee3\u8868\u90aa\u6076\u3001\u9ed1\u6697\u3001\u4ea1\u7075\u3001\u8150\u8d25\u3001\u8870\u673d\uff0c\u8fd9\u4e0e\u4ed6\u4e4b\u524d\u7684\u795e\u683c\u5b8c\u5168\u76f8\u53cd\uff0c\u7edd\u5bf9\u51b2\u7a81\uff0c\u683c\u683c\u4e0d\u5165\u3002</p><p>\u5c31\u50cf\u4e00\u4e2a\u4eba\uff0c\u53ef\u80fd\u65e2\u662f\u4e00\u4e2a\u5584\u826f\u7684\u4eba\uff0c\u53c8\u662f\u4e00\u4e2a\u5b88\u6cd5\u7684\u4eba\uff0c\u65e2\u662f\u4e00\u4e2a\u5bcc\u6709\u540c\u60c5\u5fc3\u7684\u4eba\uff0c\u4e5f\u662f\u4e00\u4e2a\u52e4\u594b\u5de5\u4f5c\u7684\u4eba\uff0c\u8fd9\u4e9b\u90fd\u4e0d\u51b2\u7a81\uff0c\u751a\u81f3\u6709\u76f8\u901a\u3001\u76f8\u878d\u4e4b\u5904\uff0c\u53ef\u4ee5\u540c\u65f6\u96c6\u4e2d\u5728\u4e00\u4e2a\u4eba\u8eab\u4e0a\uff1b\u4f46\u4ed6\u4e0d\u4f1a\u65e2\u662f\u5723\u4eba\uff0c\u53c8\u662f\u6076\u5f92\uff0c\u65e2\u662f\u6cd5\u5b98\uff0c\u53c8\u662f\u5f3a\u76d7\u2014\u2014\u51fa\u73b0\u8fd9\u79cd\u60c5\u5f62\uff0c\u90a3\u5c31\u662f\u4eba\u683c\u5206\u88c2\uff0c\u8eaf\u4f53\u867d\u7136\u662f\u4e00\u4e2a\u4eba\uff0c\u4f46\u5b9e\u9645\u4e0a\u5df2\u7ecf\u662f\u4e0d\u540c\u7684\u4e24\u4e2a\u4eba\u4e86\u3002</p><p>\u795e\u683c\u5206\u88c2\u7684\u542b\u4e49\u4e0e\u4e4b\u7c7b\u4f3c\u3002</p><p>\u5728\u67d0\u4e9b\u7279\u6b8a\u60c5\u51b5\u4e0b\uff0c\u795e\u7957\u4f1a\u51fa\u73b0\u4e0d\u540c\u795e\u683c\u4e4b\u95f4\u5206\u6b67\u8d8a\u6765\u8d8a\u5927\uff0c\u751a\u81f3\u4e92\u76f8\u51b2\u7a81\u7684\u60c5\u5f62\uff0c\u5176\u7ed3\u679c\u53ea\u6709\u4e00\u79cd\uff0c\u5c31\u662f\u8fd9\u4f4d\u795e\u7957\u4f1a\u201c\u5206\u88c2\u201d\uff0c\u4f1a\u53d8\u6210\u4e0d\u540c\u7684\u795e\u3002\u636e\u6885\u83f2\u65af\u7684\u731c\u6d4b\uff0c\u4e94\u8272\u9f99\u795e\u63d0\u4e9a\u9a6c\u7279\u8eab\u4e0a\uff0c\u6b63\u662f\u51fa\u73b0\u4e86\u8fd9\u79cd\u795e\u683c\u5206\u88c2\u3002</p><p>\u201c\u63d0\u4e9a\u9a6c\u7279\u662f\u9f99\u795e\uff0c\u53c8\u662f\u6807\u51c6\u7684\u90aa\u795e\uff0c\u4f46\u5979\u5374\u501f\u52a9\u5bdf\u65af\u8428\uff0c\u53c8\u62e5\u6709\u4e86\u2018\u5f7b\u68ee\u5854\u7684\u5b88\u62a4\u795e\u2019\u8fd9\u4e2a\u795e\u683c\uff0c\u800c\u540e\u8005\u663e\u7136\u662f\u4eba\u7c7b\u795e\uff0c\u662f\u5584\u795e\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5982\u6b64\u4e00\u6765\uff0c\u5979\u7684\u795e\u683c\u5c31\u53d1\u751f\u4e86\u5206\u88c2\u3002\u201d</p><p>\u51c6\u786e\u5730\u8bf4\uff0c\u63d0\u4e9a\u739b\u7279\u4ee5\u5bdf\u65af\u8428\u540d\u4e49\u83b7\u5f97\u5f7b\u68ee\u5854\u4eba\u7684\u4fe1\u4ef0\uff0c\u867d\u7136\u6536\u76ca\u5de8\u5927\uff0c\u5374\u4e5f\u57cb\u4e0b\u4e86\u795e\u683c\u5206\u88c2\u7684\u79cd\u5b50\u3002\u795e\u683c\u5206\u88c2\u662f\u4e00\u4e2a\u6f2b\u957f\u7684\u8fc7\u7a0b\uff0c\u5982\u679c\u6ca1\u6709\u5916\u529b\u5e72\u6d89\uff0c\u6216\u8bb8\u9700\u8981\u5f88\u957f\u4e00\u6bb5\u65f6\u95f4\u624d\u80fd\u6700\u7ec8\u5b8c\u6210\uff0c\u4f46\u73b0\u5728\u60c5\u51b5\u4e0d\u4e00\u6837\uff0c\u5979\u9668\u843d\u4e86\u3002\u6b7b\u4ea1\u4e0d\u4ec5\u4ec5\u8ba9\u795e\u4f4d\u7a7a\u60ac\uff0c\u540c\u65f6\u4e5f\u52a0\u901f\u4e86\u795e\u683c\u7684\u5206\u88c2\uff0c\u8ba9\u539f\u672c\u5df2\u7ecf\u5de8\u5927\u7684\u88c2\u9699\u8fc5\u901f\u6269\u5f20\u6210\u4e86\u6df1\u6df1\u7684\u9e3f\u6c9f\u3002</p><p>\u201c\u53ef\u662f\uff0c\u201d\u51db\u4e3e\u624b\u63d0\u95ee\uff0c\u201c\u8fd9\u4e2a\u9053\u7406\u9f99\u540e\u4e5f\u4e0d\u53ef\u80fd\u4e0d\u61c2\u5427\uff0c\u5979\u4e3a\u4ec0\u4e48\u8981\u5750\u89c6\u795e\u683c\u5206\u88c2\u5462\uff1f\u5979\u9700\u8981\u5f7b\u68ee\u5854\u4eba\u7684\u4fe1\u4ef0\uff0c\u4f46\u672a\u5fc5\u4e00\u5b9a\u8981\u505a\u5b88\u62a4\u795e\u554a\uff0c\u505a\u90aa\u795e\u4e5f\u53ef\u4ee5\u5427\u3002\u201d</p><p>\u201c\u9f99\u540e\u5f53\u7136\u660e\u767d\u8fd9\u4e2a\u9053\u7406\uff0c\u4f46\u5979\u65e0\u80fd\u4e3a\u529b\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\u8bf4\uff0c\u201c\u56e0\u4e3a\u795e\u7957\u5b9e\u9645\u4e0a\u662f\u7531\u51e1\u4eba\u6765\u5851\u9020\u7684\u3002\u201d</p><p>\u795e\u7957\u7531\u51e1\u4eba\u5851\u9020\uff0c\u8fd9\u53e5\u8bdd\u542c\u8d77\u6765\u9887\u4e3a\u4e0d\u656c\uff0c\u4f46\u5bf9\u4e8e\u5f88\u591a\u6559\u4f1a\u7684\u9ad8\u5c42\u800c\u8a00\uff0c\u8fd9\u4e0d\u8fc7\u662f\u4e00\u53e5\u5ba2\u89c2\u7684\u4e8b\u5b9e\u9648\u8ff0\u7f62\u4e86\u3002\u795e\u7957\u7684\u529b\u91cf\u6e90\u81ea\u51e1\u4eba\u7684\u4fe1\u4ef0\uff0c\u540c\u6837\u7684\uff0c\u795e\u7957\u4e5f\u53d7\u5230\u51e1\u4eba\u7684\u5236\u7ea6\uff0c\u8fd9\u662f\u4e00\u79cd\u5bf9\u7b49\u5173\u7cfb\u3002\u5927\u5230\u795e\u683c\uff0c\u5c0f\u5230\u5f62\u8c61\uff0c\u5b9e\u9645\u4e0a\u795e\u7957\u4e0e\u51e1\u4eba\u7684\u8054\u7cfb\u65e0\u5904\u4e0d\u5728\uff0c\u65e0\u6570\u51e1\u4eba\u8654\u8bda\u5730\u5d07\u62dc\u5170\u68ee\u5fb7\u5c14\uff0c\u76f8\u4fe1\u4ed6\u662f\u5149\u8f89\u4e4b\u795e\uff0c\u5584\u826f\u4e4b\u795e\uff0c\u5e0c\u671b\u4e4b\u795e\uff0c\u90a3\u4e48\u5170\u68ee\u5fb7\u5c14\u5c31\u5fc5\u987b\u662f\u5149\u8f89\u4e4b\u795e\uff0c\u5584\u826f\u4e4b\u795e\uff0c\u5e0c\u671b\u4e4b\u795e\uff0c\u65e0\u6570\u51e1\u4eba\u8ba4\u4e3a\u838e\u5c14\u662f\u4e00\u4f4d\u5973\u795e\uff0c\u90a3\u4e48\u838e\u5c14\u5c31\u5fc5\u987b\u662f\u5973\u6027\u3002\u540c\u6837\u7684\u9053\u7406\uff0c\u5f7b\u68ee\u5854\u7684\u51e1\u4eba\u5d07\u62dc\u5bdf\u65af\u8428\uff0c\u5e76\u975e\u56e0\u4e3a\u4ed6\u529b\u91cf\u5f3a\u5927\uff0c\u66f4\u4e0d\u77e5\u9053\u4ed6\u5176\u5b9e\u662f\u9f99\u795e\u7684\u4e00\u4e2a\u5316\u8eab\uff0c\u800c\u662f\u56e0\u4e3a\u4ed6\u662f\u66fe\u7ecf\u4e3a\u6b64\u5730\u5e26\u6765\u7edf\u4e00\u4e0e\u548c\u5e73\u7684\u5927\u82f1\u96c4\uff0c\u8fd9\u79cd\u5d07\u62dc\u548c\u4fe1\u4ef0\u4e4b\u4e2d\uff0c\u5bc4\u6258\u4e86\u51e1\u4eba\u5bf9\u548c\u5e73\u7684\u5411\u5f80\uff0c\u5bf9\u6218\u4e71\u7684\u538c\u6076\uff0c\u5bf9\u7f8e\u597d\u751f\u6d3b\u7684\u8ffd\u6c42\uff0c\u5bf9\u5149\u660e\u672a\u6765\u7684\u61a7\u61ac\u3002\u8fd9\u79cd\u5f3a\u70c8\u7684\u4fe1\u4ef0\uff0c\u5851\u9020\u6210\u4e86\u201c\u5f7b\u68ee\u5854\u4eba\u7684\u5b88\u62a4\u795e\u201d\u7684\u795e\u683c\uff0c\u800c\u8fd9\u4e0e\u63d0\u4e9a\u9a6c\u7279\u539f\u672c\u7684\u201c\u90aa\u9f99\u201d\u795e\u683c\u662f\u5b58\u5728\u660e\u663e\u51b2\u7a81\u7684\u3002</p><p>\u8fd9\u79cd\u77db\u76fe\u65e0\u6cd5\u5316\u89e3\uff0c\u51b2\u7a81\u65e0\u6cd5\u6d88\u9664\uff0c\u552f\u4e00\u800c\u4e14\u5fc5\u7136\u7684\u7ed3\u679c\uff0c\u5c31\u662f\u795e\u683c\u5206\u88c2\uff0c\u5206\u88c2\u6210\u4e24\u4e2a\u795e\uff0c\u4e00\u4e2a\u662f\u4f5c\u4e3a\u9f99\u795e\u3001\u90aa\u795e\u7684\u5bdf\u65af\u8428\uff0c\u6216\u8005\u8bf4\u63d0\u4e9a\u9a6c\u7279\uff1b\u53e6\u4e00\u4e2a\u5219\u662f\u4f5c\u4e3a\u5f7b\u68ee\u5854\u4eba\u5b88\u62a4\u795e\u7684\u5bdf\u65af\u8428\u3002\u6b7b\u4ea1\u7ec8\u7ed3\u4e86\u8fd9\u4e2a\u73b0\u72b6\uff0c\u6b7b\u4ea1\u4e5f\u52a0\u901f\u4e86\u8fd9\u4e2a\u8fc7\u7a0b\u3002\u201c\u5f7b\u68ee\u5854\u5b88\u62a4\u795e\u201d\u9668\u843d\u4e86\uff0c\u4f46\u4ed6\u4e5f\u540c\u65f6\u771f\u6b63\u5206\u79bb\u51fa\u6765\uff0c\u5f7b\u68ee\u5854\u4eba\u6570\u767e\u5e74\u7684\u5d07\u62dc\u4e0e\u547c\u5524\uff0c\u8ba9\u4ed6\u5fc5\u987b\u5c65\u884c\u81ea\u5df1\u7684\u6700\u540e\u804c\u8d23\uff0c\u8981\u4e3a\u8fd9\u4e2a\u5730\u533a\u5e26\u6765\u7edf\u4e00\u4e0e\u548c\u5e73\u3002</p><p>\u81f3\u5c11\u662f\u5e26\u6765\u5e0c\u671b\u3002</p><p>\u201c\u4e8e\u662f\u4ed6\u9009\u62e9\u4e86\u51db\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u5f88\u597d\u7406\u89e3\u5427\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u56e0\u4e3a\u51db\u6700\u9002\u5408\u554a\u3002\u201d</p><p>\u51db\u7684\u786e\u975e\u5e38\u5408\u9002\u3002\u5bdf\u65af\u8428\u662f\u4ee5\u7ea2\u9f99\u4e4b\u8eab\u6210\u4e3a\u4eba\u7c7b\u795e\u7957\uff0c\u800c\u51db\u662f\u534a\u7ea2\u9f99\u534a\u4eba\u7c7b\uff0c\u4e5f\u7684\u786e\u7ee7\u627f\u4e86\u4ed6\u7684\u4e00\u4e9b\u8840\u8109\u548c\u529b\u91cf\uff0c\u76f8\u4f3c\u7a0b\u5ea6\u975e\u5e38\u9ad8\uff1b\u800c\u4e14\u51db\u662f\u5f7b\u68ee\u5854\u4eba\uff0c\u8fd9\u91cc\u5c31\u662f\u5979\u7684\u5bb6\u4e61\uff0c\u4e5f\u7b26\u5408\u201c\u5b88\u62a4\u8005\u201d\u7684\u8eab\u4efd\u3002\u6700\u51d1\u5de7\u7684\u662f\uff0c\u6070\u6070\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u5979\u6765\u5230\u4e86\u5f7b\u68ee\u5854\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u4e3b\u52a8\u9001\u4e0a\u95e8\u4e00\u822c\u3002</p><p>\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u63d0\u4e9a\u739b\u7279\u9668\u843d\uff0c\u8ba9\u5f7b\u68ee\u5854\u5b88\u62a4\u795e\u5206\u79bb\u72ec\u7acb\u51fa\u6765\uff0c\u5728\u6700\u540e\u65f6\u523b\uff0c\u4ed6\u5411\u4fe1\u5f92\u53d1\u51fa\u795e\u8c15\uff0c\u5ba3\u5e03\u4f1a\u6709\u4e00\u4f4d\u7ea2\u9f99\u5723\u5973\u6765\u5230\u8fd9\u91cc\uff0c\u5b8c\u6210\u4ed6\u672a\u7adf\u7684\u4e8b\u4e1a\uff1f\u201d\u743c\u6069\u603b\u7ed3\u3002</p><p>\u201c\u57fa\u672c\u5c31\u662f\u5982\u6b64\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f46\u8981\u8bf4\u660e\uff1a\u8fd9\u4e9b\u662f\u6211\u7684\u7406\u89e3\u3002\u4e8b\u5b9e\u4e0a\uff0c\u5bdf\u65af\u8428\u6559\u4f1a\u7684\u770b\u6cd5\u5b8c\u5168\u4e0d\u540c\uff0c\u4ed6\u4eec\u5e76\u4e0d\u8ba4\u4e3a\u7ea2\u9f99\u738b\u5df2\u7ecf\u9668\u843d\uff0c\u6070\u6070\u76f8\u53cd\uff0c\u4ed6\u4eec\u8ba4\u4e3a\u9884\u8a00\u5df2\u7ecf\u5f00\u59cb\u5b9e\u73b0\u4e86\uff0c\u795e\u50cf\u96c6\u4f53\u5d29\u6e83\uff0c\u8fd9\u6b63\u610f\u5473\u7740\u7ea2\u9f99\u738b\u7ec8\u4e8e\u81ea\u6c89\u7761\u4e2d\u82cf\u9192\uff0c\u518d\u6b21\u56de\u5230\u51e1\u95f4\u3002\u65e2\u7136\u771f\u795e\u5df2\u7ecf\u964d\u4e34\uff0c\u6240\u4ee5\u5076\u50cf\u518d\u65e0\u7528\u5904\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd8\u53ef\u4ee5\u8fd9\u6837\u89e3\u91ca\uff1f\u201d</p><p>\u743c\u6069\u4ed4\u7ec6\u60f3\u4e86\u60f3\uff0c\u53d1\u73b0\u8fd8\u771f\u8bf4\u5f97\u901a\u3002\u56e0\u4e3a\u5bdf\u65af\u8428\u6559\u4f1a\u4e4b\u6240\u4ee5\u73b0\u5728\u4f1a\u627f\u8ba4\u201c\u5723\u5973\u201d\uff0c\u662f\u56e0\u4e3a\u795e\u8c15\uff0c\u5e76\u4e0d\u662f\u534a\u5e74\u524d\u5192\u51fa\u6765\u7684\u90a3\u4e2a\u65b0\u7248\u672c\u9884\u8a00\uff0c\u800c\u795e\u8c15\u7684\u5185\u5bb9\u53c8\u975e\u5e38\u7b80\u5355\uff0c\u53ea\u8bf4\u5723\u5973\u5c06\u81f3\uff0c\u5176\u4ed6\u4e00\u6982\u672a\u63d0\uff0c\u8fd9\u5c31\u7ed9\u4eba\u7559\u4e0b\u4e86\u5145\u8db3\u7684\u53d1\u6325\u4f59\u5730\u3002</p><p>\u201c\u4f0a\u5df4\u96f7\u59c6\u9080\u8bf7\u51db\u660e\u5929\u4e0a\u5348\u53bb\u795e\u6bbf\uff0c\u5c4a\u65f6\u4ed6\u4f1a\u53ec\u96c6\u6240\u6709\u4fe1\u5f92\uff0c\u516c\u5f00\u5ba3\u5e03\u5723\u5973\u964d\u4e34\u7684\u6d88\u606f\u3002\u6211\u6ca1\u6709\u7b54\u5e94\uff0c\u4e5f\u6ca1\u6709\u62d2\u7edd\uff0c\u4f60\u4eec\u600e\u4e48\u770b\uff1f\u201d</p><p>\u201c\u6211\u89c9\u5f97\uff0c\u201d\u51db\u7b2c\u4e00\u4e2a\u53d1\u8a00\uff0c\u201c\u7ea2\u9f99\u5723\u5973\u8fd9\u4e2a\u5934\u8854\u633a\u597d\u542c\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u6240\u6709\u4eba\u90fd\u81ea\u52a8\u5ffd\u7565\u4e86\u5979\u7684\u610f\u89c1\u3002</p><p>\u201c\u5173\u952e\u5728\u4e8e\uff0c\u2018\u5723\u5973\u2019\u80fd\u505a\u4ec0\u4e48\uff1f\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u662f\u771f\u7684\u6709\u9886\u5bfc\u6559\u4f1a\u7684\u6743\u529b\uff0c\u8fd8\u662f\u5f92\u6709\u540d\u4e49\uff0c\u5e76\u65e0\u5b9e\u6743\u3002\u201d</p><p>\u6885\u83f2\u65af\u770b\u4e86\u73ca\u5609\u4e00\u773c\uff1a\u201c\u6211\u731c\u6d4b\u662f\u540e\u8005\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\uff0c\u201d\u51db\u987f\u65f6\u5927\u5931\u6240\u671b\uff0c\u201c\u771f\u6ca1\u610f\u601d\uff0c\u6211\u624d\u4e0d\u53bb\u5462\u3002\u201d</p><p>\u201c\u90a3\u4e48\u5982\u679c\u51db\u4e0d\u53bb\u505a\u4ed6\u4eec\u7684\u7ea2\u9f99\u5723\u5973\uff0c\u4f1a\u6709\u4ec0\u4e48\u540e\u679c\uff1f\u201d</p><p>\u201c\u5982\u679c\u4e0d\u80fd\u5408\u4f5c\uff0c\u4f0a\u5df4\u96f7\u59c6\u5e0c\u671b\u6211\u4eec\u80fd\u591f\u5c3d\u5feb\u79bb\u5f00\u5f7b\u68ee\u5854\u3002\u4ed6\u4eec\u4f1a\u53e6\u5916\u627e\u5230\u4e00\u4f4d\u7ea2\u9f99\u5723\u5973\u2014\u2014\u6211\u89c9\u5f97\u4ed6\u4eec\u5176\u5b9e\u5df2\u7ecf\u6709\u4eba\u9009\u4e86\u3002\u201d</p><p>\u743c\u6069\u6c89\u541f\u4e86\u4e00\u4f1a\uff0c\u201c\u4fe1\u606f\u4e0d\u8db3\uff0c\u5b58\u5728\u98ce\u9669\uff0c\u6211\u89c9\u5f97\u6ca1\u5fc5\u8981\u5377\u8fdb\u53bb\u3002\u201d</p><p>\u201c\u6211\u5012\u8ba4\u4e3a\u53ef\u4ee5\u8003\u8651\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u4e00\u5207\u4e8b\u60c5\uff0c\u65e0\u975e\u662f\u5229\u5bb3\u8ba1\u7b97\uff0c\u8f7b\u91cd\u6743\u8861\u800c\u5df2\u3002\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u98ce\u9669\u6216\u8bb8\u662f\u6709\uff0c\u4f46\u56de\u62a5\u4e5f\u5f88\u9ad8\u554a\uff0c\u65e2\u7136\u5bf9\u65b9\u4e3b\u52a8\u4e0a\u95e8\u76f8\u9080\uff0c\u6211\u4eec\u53c8\u4f55\u5fc5\u62d2\u7edd\u3002\u636e\u6211\u6240\u77e5\uff0c\u5bdf\u65af\u8428\u6559\u4f1a\u5728\u5f7b\u68ee\u5854\u5730\u533a\u7684\u5f71\u54cd\u4e0d\u5c0f\uff0c\u4e3b\u4eba\u4e0d\u662f\u4e4b\u524d\u8bf4\u60f3\u8981\u5efa\u7acb\u4e00\u70b9\u4e2a\u4eba\u52bf\u529b\u5417\uff0c\u5982\u679c\u80fd\u591f\u501f\u52a9\u51db\u6536\u670d\u5bdf\u65af\u8428\u6559\u4f1a\uff0c\u5fc5\u7136\u662f\u4e00\u5927\u5e2e\u52a9\u3002\u201d</p><p>\u743c\u6069\u72b9\u8c6b\u4e86\u4e00\u4f1a\uff0c\u8fd8\u662f\u6447\u6447\u5934\u3002</p><p>\u4eba\u7684\u79c9\u6027\u5404\u4e0d\u76f8\u540c\uff0c\u6709\u4e9b\u4eba\u6fc0\u8fdb\uff0c\u504f\u597d\u5192\u9669\uff0c\u8ba4\u4e3a\u673a\u4f1a\u7a0d\u7eb5\u5373\u901d\uff0c\u7565\u4f5c\u72b9\u8c6b\u4fbf\u4f1a\u9519\u8fc7\uff1b\u6709\u4e9b\u4eba\u4fdd\u5b88\uff0c\u8ffd\u6c42\u7a33\u59a5\uff0c\u4e60\u60ef\u8c0b\u5b9a\u800c\u540e\u52a8\uff0c\u51c6\u5907\u5de5\u4f5c\u4e0d\u505a\u8db3\u5c31\u4e0d\u80af\u51fa\u95e8\u3002\u838e\u73de\u514b\u663e\u7136\u5c5e\u4e8e\u524d\u8005\uff0c\u81f3\u4e8e\u743c\u6069\uff0c\u5176\u5b9e\u4e24\u8005\u90fd\u4e0d\u7b97\uff0c\u4ed6\u5355\u7eaf\u5c31\u662f\u6015\u9ebb\u70e6\u800c\u5df2\u3002</p><p>\u201c\u6211\u4eec\u6765\u4e1c\u57df\u53c8\u4e0d\u662f\u65c5\u6e38\uff0c\u81ea\u5df1\u8fd8\u6709\u5f88\u591a\u4e8b\u60c5\u8981\u505a\u5462\uff0c\u6ca1\u5fc5\u8981\u8282\u5916\u751f\u679d\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5f53\u7136\u4e86\uff0c\u8fd9\u53ea\u662f\u6211\u7684\u4e2a\u4eba\u770b\u6cd5\u3002\u6bd5\u7adf\u8fd9\u662f\u51db\u7684\u4e8b\u60c5\uff0c\u8fd8\u662f\u8981\u5979\u672c\u4eba\u6765\u505a\u51b3\u5b9a\u624d\u5bf9\u3002\u201d</p><p>\u51db\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u7b97\u5566\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5723\u5973\u4ec0\u4e48\u7684\u662f\u633a\u597d\u542c\uff0c\u4f46\u6211\u521a\u521a\u60f3\u8d77\u6765\uff0c\u827e\u5f25\u8587\u4f60\u4e0d\u80fd\u53c2\u4e0e\u8fd9\u79cd\u4e8b\u5bf9\u5427\u3002\u201d</p><p>\u6885\u83f2\u65af\u70b9\u70b9\u5934\u3002</p><p>\u201c\u4f60\u4e0d\u80fd\u53c2\u4e0e\uff0c\u90a3\u6211\u4e00\u4e2a\u4eba\u53bb\u73a9\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u201d\u51db\u610f\u5174\u7d22\u7136\u5730\u6446\u6446\u624b\uff0c\u201c\u7b97\u4e86\u5427\u3002\u201d</p><p>\u4e8e\u662f\u4e8b\u60c5\u5c31\u8fd9\u4e48\u51b3\u5b9a\u4e86\u3002</p><p>\u6885\u83f2\u65af\u5199\u4e86\u5c01\u4fe1\uff0c\u5a49\u62d2\u4e86\u9080\u8bf7\uff0c\u8ba9\u65c5\u5e97\u7684\u4e00\u4f4d\u4f8d\u8005\u9001\u53bb\u7ea2\u9f99\u795e\u6bbf\u3002\u65e2\u7136\u4e0d\u5408\u4f5c\uff0c\u4e3a\u4e86\u907f\u5acc\uff0c\u4e5f\u5c31\u4e0d\u65b9\u4fbf\u5728\u6b64\u5730\u4e45\u7559\uff0c\u5927\u5bb6\u5546\u8bae\u4e86\u4e00\u4e0b\uff0c\u51b3\u5b9a\u4e0b\u5348\u5c31\u542f\u7a0b\uff0c\u52a8\u8eab\u79bb\u5f00\u8f9b\u5df4\u57ce\u3002</p><p>\u5bdf\u65af\u8428\u6559\u4f1a\u7684\u53cd\u5e94\u975e\u5e38\u8fc5\u901f\uff0c\u5728\u4e2d\u5348\u65f6\u5206\uff0c\u743c\u6069\u4fbf\u542c\u5230\u4e86\u201c\u7ea2\u9f99\u5723\u5973\u964d\u4e34\u8f9b\u5df4\u57ce\u201d\u7684\u6d88\u606f\uff0c\u8fd9\u4e5f\u9a8c\u8bc1\u4e86\u6885\u83f2\u65af\u4e4b\u524d\u7684\u5224\u65ad\u3002\u51db\u867d\u7136\u5df2\u7ecf\u51b3\u5b9a\u56de\u907f\uff0c\u4f46\u5979\u7ec8\u7a76\u8fd8\u662f\u6309\u637a\u4e0d\u4f4f\u597d\u5947\u5fc3\uff0c\u786c\u62c9\u7740\u743c\u6069\u53bb\u4e86\u4e00\u8d9f\u795e\u6bbf\uff0c\u9690\u8eab\u5728\u4eba\u7fa4\u4e2d\u8fdc\u8fdc\u89c2\u770b\u3002\u5bdf\u65af\u8428\u6559\u4f1a\u660e\u663e\u662f\u65e9\u6709\u51c6\u5907\uff0c\u5728\u51db\u62d2\u7edd\u5408\u4f5c\u4e4b\u540e\u7acb\u523b\u542f\u7528\u4e86\u5019\u9009\u65b9\u6848\uff0c\u4ed6\u4eec\u63a8\u51fa\u7684\u8fd9\u4f4d\u201c\u7ea2\u9f99\u5723\u5973\u201d\u662f\u4e2a\u4e8c\u5341\u6765\u5c81\u7684\u5e74\u8f7b\u5973\u5b50\uff0c\u4e00\u5934\u9ed1\u8272\u7684\u957f\u53d1\uff0c\u8bf4\u4e0d\u4e0a\u5f88\u6f02\u4eae\uff0c\u4f46\u8eab\u9ad8\u817f\u957f\uff0c\u7a7f\u7740\u534e\u4e3d\u7684\u796d\u53f8\u888d\uff0c\u624b\u6301\u6743\u6756\uff0c\u9887\u5177\u5a01\u4e25\uff0c\u7684\u786e\u6709\u51e0\u5206\u5723\u5973\u7684\u6d3e\u5934\u3002\u51db\u5374\u4e0d\u5c51\u5730\u6241\u4e86\u6241\u5634\uff0c\u201c\u4e00\u4e2a\u51e1\u4eba\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u534a\u70b9\u7ea2\u9f99\u8840\u7edf\u90fd\u6ca1\u6709\uff0c\u4e5f\u6562\u81ea\u79f0\u4ec0\u4e48\u7ea2\u9f99\u5723\u5973\uff0c\u771f\u662f\u53ef\u7b11\u3002\u201d</p><p>\u201c\u7ea2\u9f99\u5723\u5973\u662f\u5934\u8854\uff0c\u53c8\u4e0d\u662f\u975e\u8981\u6709\u7ea2\u9f99\u8840\u7edf\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u79d1\u7c73\u5c14\u7684\u7d2b\u9f99\u9a91\u58eb\uff0c\u4e5f\u6ca1\u8bf4\u6709\u7d2b\u9f99\u8840\u7edf\u554a\u3002\u201d</p><p>\u201c\u5582\uff0c\u743c\u6069\uff0c\u4f60\u5230\u5e95\u7ad9\u5728\u54ea\u8fb9\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u6211\u77e5\u9053\u4f60\u4e00\u5411\u91cd\u8272\u8f7b\u53cb\uff0c\u4f46\u4e5f\u4e0d\u80fd\u8fd9\u4e48\u559c\u65b0\u538c\u65e7\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u80fd\u4e0d\u80fd\u522b\u4e71\u7528\u5f62\u5bb9\u8bcd\uff0c\u201d\u743c\u6069\u6276\u7740\u989d\u5934\uff0c\u201c\u8fd9\u90fd\u4ec0\u4e48\u8ddf\u4ec0\u4e48\u554a\uff0c\u4f60\u6bd4\u5979\u6f02\u4eae\u591a\u4e86\uff0c\u6211\u91cd\u8272\u8f7b\u53cb\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8c01\u77e5\u9053\u5462\uff0c\u7537\u4eba\u90fd\u662f\u8d2a\u5f97\u65e0\u538c\uff0c\u6f02\u4eae\u7684\u559c\u6b22\uff0c\u4e0d\u6f02\u4eae\u7684\u4e5f\u559c\u6b22\uff0c\u7edf\u7edf\u90fd\u8981\u6536\u8fdb\u540e\u5bab\u624d\u6ee1\u8db3\u3002\u201d</p><p>\u201c\u8c01\u8bf4\u7684\uff01\u201d\u743c\u6069\u7acb\u523b\u6297\u8bae\uff0c\u201c\u6211\u53ef\u662f\u5f88\u6709\u539f\u5219\u7684\u7537\u4eba\uff0c\u53ea\u6536\u6f02\u4eae\u7684\uff0c\u4e0d\u6f02\u4eae\u7684\u575a\u51b3\u4e0d\u8981\u2014\u2014\u7b49\u4e0b\uff0c\u6211\u4eec\u4e3a\u4ec0\u4e48\u8981\u8ba8\u8bba\u8fd9\u4e2a\uff1f\u8dd1\u9898\u4e86\u5427\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u504f\u8892\u5979\u3002\u201d</p><p>\u201c\u6ca1\u6709\u5427\uff0c\u6211\u53ea\u662f\u5c31\u4e8b\u8bba\u4e8b\uff0c\u5ba2\u89c2\u516c\u6b63\u5730\u8bc4\u8bba\u800c\u5df2\u3002\u201d</p><p>\u201c\u6211\u662f\u4f60\u5973\u53cb\uff0c\u5979\u662f\u4e2a\u964c\u751f\u4eba\uff0c\u4f60\u5c45\u7136\u4e0d\u5e2e\u6211\uff0c\u90a3\u5c31\u7b49\u4e8e\u662f\u504f\u8892\u5979\u554a\u3002\u201d</p><p>\u201c\u6211\u9519\u4e86\u3002\u201d\u743c\u6069\u8d76\u5feb\u627f\u8ba4\u3002</p><p>\u201c\u77e5\u9519\u5c31\u597d\uff0c\u201d\u51db\u6ee1\u610f\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u65e2\u7136\u9a6c\u4e0a\u8981\u8d70\u4e86\uff0c\u8fd8\u662f\u53bb\u5411\u5580\u6d41\u5976\u5976\u9053\u4e2a\u522b\u5427\u3002\u201d</p><p>\u743c\u6069\u6ca1\u6709\u5f02\u8bae\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u5373\u4fbf\u51db\u4e0d\u8bf4\uff0c\u4ed6\u4e5f\u8981\u4e3b\u52a8\u63d0\u8bae\u8d70\u8fd9\u4e00\u8d9f\u3002</p><p>\u9f99\u5bab\u88ab\u70e7\u4e86\uff0c\u5580\u6d41\u5976\u5976\u5f53\u7136\u662f\u56de\u5230\u5bb6\u4e2d\u517b\u4f24\uff0c\u597d\u5728\u743c\u6069\u6628\u65e5\u7528\u5973\u796d\u53f8\u9b54\u50cf\u4e3a\u5979\u6cbb\u7597\u8fc7\uff0c\u6062\u590d\u72b6\u51b5\u826f\u597d\uff0c\u53ea\u662f\u6682\u65f6\u8fd8\u884c\u52a8\u4e0d\u4fbf\uff0c\u9700\u8981\u5367\u5e8a\u4f11\u606f\u51e0\u5929\u3002\u51db\u5411\u5979\u9053\u522b\uff0c\u8bf4\u662f\u4e34\u65f6\u6709\u4e8b\uff0c\u4eca\u5929\u5c31\u8981\u52a8\u8eab\u79bb\u57ce\uff0c\u7b49\u56de\u6765\u518d\u770b\u671b\u5979\u3002</p><p>\u95f2\u804a\u51e0\u53e5\uff0c\u743c\u6069\u627e\u4e86\u4e2a\u673a\u4f1a\uff0c\u95ee\u5580\u6d41\u5976\u5976\u6700\u8fd1\u662f\u4e0d\u662f\u505a\u8fc7\u4ec0\u4e48\u6709\u5173\u201c\u7ea2\u9f99\u5723\u5973\u201d\u7684\u68a6\u3002</p><p>\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u8001\u5987\u4eba\u5413\u4e86\u4e00\u8df3\u3002</p><p>\u743c\u6069\u4fbf\u627f\u8ba4\u662f\u6628\u665a\u548c\u51db\u6765\u770b\u5979\u7684\u65f6\u5019\uff0c\u5728\u95e8\u5916\u542c\u5230\u4e86\u51e0\u53e5\uff0c\u5fc3\u4e2d\u597d\u5947\uff0c\u6240\u4ee5\u60f3\u95ee\u95ee\u3002\u5580\u6d41\u5976\u5976\u770b\u4e86\u770b\u5979\uff0c\u793a\u610f\u51db\u5c06\u95e8\u5173\u4e0a\uff0c\u7136\u540e\u70b9\u70b9\u5934\uff1a\u201c\u662f\u505a\u4e86\u4e2a\u5947\u602a\u7684\u68a6\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u800c\u4e14\u4e0d\u6b62\u6211\u4e00\u4e2a\u4eba\uff0c\u5f88\u591a\u4eba\u90fd\u68a6\u89c1\u4e86\u3002\u201d</p><p>\u743c\u6069\u6b63\u8981\u7ee7\u7eed\u95ee\u68a6\u7684\u5185\u5bb9\uff0c\u7a81\u7136\u88ab\u95e8\u5916\u4f20\u6765\u7684\u4e00\u9635\u55a7\u95f9\u6253\u65ad\u4e86\u3002\u4ed6\u76b1\u7740\u7709\uff0c\u4ece\u7a97\u6237\u5f80\u5916\u770b\u53bb\uff0c\u53d1\u73b0\u4e00\u4e2a\u9752\u5e74\u4eba\u2014\u2014\u5c31\u662f\u6628\u665a\u66fe\u7ecf\u89c1\u8fc7\u7684\u90a3\u4f4d\u201c\u5c0f\u7a46\u6cd5\u201d\u2014\u2014\u6b63\u7ad9\u5728\u4e00\u5757\u5927\u77f3\u5934\u4e0a\uff0c\u9ad8\u58f0\u8bf4\u8bdd\uff0c\u7528\u7684\u662f\u6069\u745f\u8bed\u800c\u975e\u901a\u7528\u8bed\u3002\u5728\u4ed6\u5468\u56f4\u5df2\u7ecf\u805a\u96c6\u4e86\u4e03\u516b\u4e2a\u4eba\uff0c\u90fd\u662f\u548c\u4ed6\u5e74\u9f84\u5dee\u4e0d\u591a\u7684\u5e74\u8f7b\u4eba\uff0c\u8fd8\u6709\u66f4\u591a\u7684\u4eba\u6b63\u4ece\u8fdc\u5904\u8dd1\u6765\u3002</p><p>\u201c\u4ed6\u5728\u8bf4\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u95ee\uff0c\u4ed6\u7684\u6069\u745f\u8bed\u5f88\u5dee\uff0c\u5bf9\u65b9\u8bf4\u8bdd\u8bed\u901f\u53c8\u9887\u5feb\uff0c\u534a\u70b9\u90fd\u6ca1\u542c\u61c2\u3002</p><p>\u51db\u4e00\u8fb9\u542c\uff0c\u4e00\u8fb9\u5c06\u5173\u952e\u7684\u5185\u5bb9\u7b80\u8981\u7ffb\u8bd1\u7ed9\u743c\u6069\uff0c\u76f8\u5bf9\u4e0d\u91cd\u8981\u7684\u5c31\u7565\u8fc7\u4e86\uff1a\u201c\u8bf8\u4f4d\uff0c\u6211\u4eec\u90fd\u505a\u4e86\u540c\u4e00\u4e2a\u68a6\uff0c\u6211\u4eec\u90fd\u542c\u89c1\u4e86\u795e\u7684\u58f0\u97f3\u3002\u6709\u4eba\u8bf4\u5b83\u662f\u865a\u5047\u7684\u5e7b\u8c61\uff0c\u662f\u90aa\u9b54\u7684\u5f15\u8bf1\u3002\u4f46\u73b0\u5728\uff0c\u771f\u76f8\u65e0\u6cd5\u88ab\u63a9\u76d6\uff0c\u5b83\u5df2\u7ecf\u5c55\u73b0\u5728\u6211\u4eec\u9762\u524d\u4e86\u3002\u201d</p><p>\u201c\u5927\u96be\u5c06\u81f3\uff0c\u6bc1\u706d\u4e16\u754c\u7684\u70c8\u706b\u5df2\u7ecf\u5728\u5730\u5e95\u718a\u718a\u71c3\u70e7\uff0c\u6211\u4eec\u65e0\u8def\u53ef\u9003\u3002\u201d</p><p>\u201c\u51e1\u4eba\u7684\u5815\u843d\u4e0e\u80cc\u53db\u8ba9\u5929\u795e\u611f\u5230\u5931\u671b\uff0c\u795e\u7f5a\u7531\u6b64\u964d\u4e0b\u2014\u2014\u4f46\u7ea2\u9f99\u738b\u662f\u777f\u667a\u800c\u4ec1\u6148\u7684\uff0c\u5979\u770b\u89c1\u51e1\u95f4\u4ecd\u7136\u6709\u6211\u4eec\u8fd9\u4e9b\u5fe0\u8bda\u7684\u4fe1\u5f92\u5b58\u5728\uff0c\u56e0\u6b64\u7ed9\u4e88\u6211\u4eec\u6700\u540e\u7684\u62ef\u6551\u3002\u201d</p><p>\u201c\u7ea2\u9f99\u738b\u5df2\u7ecf\u5411\u6211\u4eec\u6bcf\u4e2a\u4eba\u505a\u51fa\u6e05\u695a\u7684\u8c15\u793a\uff1a\u7ee7\u627f\u4e86\u5979\u7684\u529b\u91cf\u4e0e\u610f\u5fd7\u7684\u5723\u5973\u5c06\u4f1a\u964d\u4e34\u5728\u8fd9\u7247\u571f\u5730\u4e0a\uff0c\u5e26\u9886\u6211\u4eec\u5b8c\u6210\u68a6\u60f3\u3002\u201d</p><p>\u201c\u90a3\u4e9b\u660f\u8069\u65e0\u80fd\u4e4b\u8f88\uff0c\u7d27\u7d27\u62b1\u7740\u6743\u52bf\u4e0d\u80af\u6492\u624b\uff0c\u5bf9\u9f99\u738b\u7684\u544a\u8beb\u5145\u8033\u4e0d\u95fb\uff0c\u5bf9\u9f99\u738b\u7684\u8c15\u793a\u89c6\u800c\u4e0d\u89c1\uff0c\u4ed6\u4eec\u5df2\u7ecf\u80cc\u5f03\u4e86\u7406\u60f3\uff0c\u4e0d\u914d\u518d\u81ea\u79f0\u662f\u7ea2\u9f99\u738b\u7684\u8ffd\u968f\u8005\u3002\u201d</p><p>\u201c\u5982\u4eca\uff0c\u4ed6\u4eec\u53c8\u60f3\u8981\u6545\u6280\u91cd\u65bd\uff0c\u7528\u4e00\u4e2a\u5047\u7684\u5723\u5973\u6765\u6b3a\u9a97\u6211\u4eec\u3002\u6211\u4eec\u4e0d\u80fd\u4e0a\u5f53\uff0c\u800c\u662f\u5e94\u5f53\u9f50\u5fc3\u534f\u529b\uff0c\u56e2\u7ed3\u4e00\u81f4\uff0c\u627e\u5230\u771f\u6b63\u7684\u5723\u5973\u3002\u201d</p><p>\u201c\u5723\u5973\u5c06\u4e3a\u6211\u4eec\u5e26\u6765\u795e\u7684\u65e8\u610f\uff0c\u5411\u6211\u4eec\u5c55\u793a\u795e\u8ff9\u548c\u672a\u6765\u7684\u5149\u660e\u6240\u5728\u3002\u6211\u4eec\u5c06\u5728\u5723\u5973\u7684\u7387\u9886\u4e4b\u4e0b\uff0c\u6e05\u9664\u90a3\u4e9b\u5c38\u4f4d\u7d20\u9910\u4e4b\u8f88\uff0c\u4e3a\u6211\u4eec\u7684\u7406\u60f3\u800c\u524d\u8fdb\u3002\u201d</p><p>\u4ed3\u4fc3\u4e4b\u95f4\uff0c\u51db\u7684\u7ffb\u8bd1\u5f53\u7136\u8fbe\u4e0d\u5230\u201c\u4fe1\u8fbe\u96c5\u201d\u517c\u5907\u7684\u7a0b\u5ea6\uff0c\u4f46\u610f\u601d\u8fd8\u662f\u6e05\u695a\u7684\uff0c\u81f3\u5c11\u5df2\u7ecf\u8db3\u4ee5\u8ba9\u743c\u6069\u660e\u767d\u4ed6\u60f3\u5e72\u4ec0\u4e48\uff0c\u5f52\u7eb3\u603b\u7ed3\u8d77\u6765\u5c31\u662f\u4e24\u4e2a\u5b57\u3002</p><p>\u9020\u53cd\u3002</p><p>\u663e\u7136\u5f97\u51fa\u540c\u6837\u7ed3\u8bba\u7684\u4e0d\u6b62\u743c\u6069\u4e00\u4e2a\u4eba\uff0c\u6ca1\u8fc7\u591a\u4e45\uff0c\u4e00\u4e2a\u9152\u7cdf\u9f3b\u5b50\u7684\u4e2d\u5e74\u4eba\u51b2\u8fc7\u6765\uff0c\u5c06\u6b63\u5728\u6177\u6168\u6fc0\u6602\u53d1\u8868\u6f14\u8bb2\u7684\u9752\u5e74\u4e00\u628a\u62ce\u8d70\uff0c\u4f30\u8ba1\u56de\u5bb6\u514d\u4e0d\u4e86\u4e00\u987f\u6559\u8bad\u3002\u56f4\u89c2\u7fa4\u4f17\u7eb7\u7eb7\u4f5c\u9e1f\u517d\u6563\uff0c\u5404\u56de\u5404\u5bb6\u3002\u743c\u6069\u8f6c\u8fc7\u5934\u6765\uff0c\u770b\u7740\u5580\u6d41\u5976\u5976\uff0c\u4e0d\u9700\u8981\u8001\u5987\u4eba\u8bf4\uff0c\u4ed6\u5df2\u7ecf\u5927\u6982\u77e5\u9053\u201c\u68a6\u201d\u7684\u5185\u5bb9\u662f\u4ec0\u4e48\u4e86\u3002</p><p>\u201c\u6559\u4f1a\u7684\u796d\u53f8\u8bf4\u4f60\u4eec\u7684\u68a6\u662f\u5047\u7684\uff1f\u201d</p><p>\u8001\u5987\u4eba\u70b9\u70b9\u5934\uff0c\u201c\u8bf4\u662f\u90aa\u9b54\u7684\u8be1\u8ba1\uff0c\u8ba9\u6211\u4eec\u4e0d\u8981\u8f7b\u4fe1\uff0c\u4e00\u5207\u4ee5\u795e\u6bbf\u7684\u5b98\u65b9\u901a\u544a\u4e3a\u51c6\u3002\u4eca\u5929\u4e0a\u5348\uff0c\u6709\u796d\u53f8\u8fc7\u6765\uff0c\u8bf4\u662f\u5df2\u7ecf\u627e\u5230\u5723\u5973\u3002\u4f46\u5c0f\u7a46\u6cd5\u4ed6\u4eec\u90fd\u4e0d\u80af\u76f8\u4fe1\uff0c\u8bf4\u662f\u5047\u7684\u3002\u201d</p><p>\u5f53\u7136\u662f\u5047\u7684\uff0c\u771f\u7684\u5c31\u5728\u4f60\u9762\u524d\u5462\uff0c\u4e0d\u8fc7\u743c\u6069\u4e5f\u65e0\u610f\u8bf4\u7834\u3002\u53cd\u6b63\u6765\u610f\u5df2\u7ecf\u8fbe\u5230\uff0c\u4ed6\u53c8\u966a\u8001\u5987\u4eba\u8bf4\u4e86\u4e00\u4f1a\u8bdd\uff0c\u4fbf\u8d77\u8eab\u544a\u8f9e\u3002</p><p>\u5580\u6d41\u5976\u5976\u7a81\u7136\u53eb\u4f4f\u4e86\u4ed6\u4eec\u3002</p><p>\u201c\u5c0f\u51db\uff0c\u6709\u4ef6\u4e8b\uff0c\u6211\u60f3\u95ee\u95ee\uff0c\u201d\u8001\u5987\u4eba\u72b9\u72b9\u8c6b\u8c6b\u5730\u8bf4\uff0c\u201c\u6628\u5929\u665a\u4e0a\uff0c\u662f\u4f60\u628a\u6211\u6551\u51fa\u6765\u7684\u5427\u3002\u6211\u540e\u6765\u6655\u8fc7\u53bb\u4e86\uff0c\u4f46\u8fd8\u8bb0\u5f97\u5728\u6655\u5012\u4e4b\u524d\uff0c\u5916\u9762\u7a81\u7136\u8d77\u706b\uff0c\u773c\u770b\u5c31\u8981\u70e7\u5230\u623f\u95f4\u91cc\uff0c\u8fd9\u65f6\u5019\u4f60\u4e86\u51b2\u8fdb\u6765\u3002\u201d</p><p>\u201c\u55ef\uff0c\u662f\u6211\u3002\u201d</p><p>\u201c\u53ef\u662f\u4ed6\u4eec\u90fd\u8ddf\u6211\u8bf4\uff0c\u6628\u665a\u662f\u7ea2\u9f99\u738b\u663e\u7075\u6551\u4e86\u6211\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u201c\u5c0f\u51db\uff0c\u4f60\u662f\u4e0d\u662f\u2026\u2026\u548c\u5723\u5973\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d</p><p>\u51db\u6320\u6320\u5934\uff0c\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u56de\u7b54\uff0c\u5979\u4e0d\u613f\u610f\u6b3a\u9a97\u5580\u6d41\u5976\u5976\uff0c\u4f46\u4e5f\u4e0d\u65b9\u4fbf\u627f\u8ba4\u3002\u201c\u603b\u4e4b\u6211\u5148\u8d70\u4e86\uff0c\u201d\u5979\u6700\u540e\u7d22\u6027\u907f\u800c\u4e0d\u7b54\uff0c\u201c\u8fc7\u6bb5\u65f6\u95f4\u518d\u6765\u770b\u4f60\u3002\u201d</p><p>\u8001\u5987\u4eba\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u203b\u203b\u203b</p><p>\u5f53\u5929\u4e0b\u5348\uff0c\u743c\u6069\u7b49\u4eba\u79bb\u5f00\u4e86\u8f9b\u5df4\u57ce\uff0c\u8e0f\u4e0a\u65c5\u9014\u3002</p><p>\u51fa\u57ce\u7684\u65f6\u5019\u53d1\u751f\u4e86\u4e00\u70b9\u5c0f\u63d2\u66f2\uff0c\u4ece\u65c5\u5e97\u901a\u5f80\u57ce\u95e8\u7684\u4e3b\u5e72\u9053\u88ab\u536b\u5175\u7a81\u7136\u5c01\u9501\u4e86\uff0c\u5bfc\u81f4\u4ed6\u4eec\u4e0d\u5f97\u4e0d\u7ed5\u4e86\u4e2a\u5f88\u5927\u7684\u5f2f\u3002\u8fd9\u8ba9\u51db\u5f88\u4e0d\u5f00\u5fc3\uff0c\u4e8e\u662f\u743c\u6069\u627e\u4e86\u4e2a\u8def\u4eba\u6253\u542c\uff0c\u5f97\u77e5\u662f\u524d\u6bb5\u65f6\u95f4\uff0c\u8f9b\u5df4\u57ce\u57ce\u4e3b\u5411\u5f7b\u68ee\u5854\u5176\u4ed6\u4e94\u5341\u591a\u4e2a\u57ce\u90a6\u90fd\u53d1\u51fa\u4e86\u9080\u8bf7\u51fd\uff0c\u5ba3\u5e03\u8981\u53ec\u5f00\u201c\u7b2c\u4e00\u5c4a\u5f7b\u68ee\u5854\u9ad8\u5cf0\u4f1a\u8bae\u201d\uff0c\u5171\u540c\u63a2\u8ba8\u672c\u5730\u533a\u7684\u548c\u5e73\u4e0e\u53d1\u5c55\u95ee\u9898\u3002\u8fd9\u4e00\u5021\u8bae\u5f97\u5230\u4e86\u70ed\u70c8\u54cd\u5e94\u548c\u5e7f\u6cdb\u8ba4\u540c\uff0c\u51e0\u4e4e\u6240\u6709\u7684\u57ce\u90a6\u90fd\u6d3e\u51fa\u4e86\u4ee3\u8868\uff0c\u5176\u4e2d\u6709\u5927\u7ea6\u4e09\u5206\u4e4b\u4e00\u7684\u57ce\u90a6\u662f\u7531\u57ce\u4e3b\u4eb2\u81ea\u7387\u961f\u524d\u6765\u3002\u4f1a\u8bae\u5b9a\u4e8e\u4e09\u65e5\u540e\u5f00\u5e55\uff0c\u4eca\u5929\u662f\u5404\u5730\u4ee3\u8868\u4eec\u62b5\u8fbe\u7684\u7b2c\u4e00\u5929\uff0c\u4e3a\u4e86\u4fdd\u969c\u5b89\u5168\uff0c\u6240\u4ee5\u8f9b\u5df4\u57ce\u7684\u90e8\u5206\u9053\u8def\u88ab\u4e34\u65f6\u5c01\u95ed\u4e86\u3002</p><p>\u201c\u4e0d\u53ef\u7406\u55bb\uff0c\u201d\u51db\u8bc4\u8bba\uff0c\u201c\u4ed6\u4eec\u5f00\u4f1a\u5c31\u5f00\u4f1a\uff0c\u5e72\u561b\u628a\u8def\u90fd\u5c01\u8d77\u6765\uff0c\u4e0d\u77e5\u9053\u8fd9\u6837\u4f1a\u8ba9\u5e02\u6c11\u5f88\u4e0d\u65b9\u4fbf\u5417\uff1f\u201d</p><p>\u201c\u4e0d\u8fd9\u6837\u600e\u4e48\u80fd\u663e\u793a\u81ea\u5df1\u9ad8\u4eba\u4e00\u7b49\u5462\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u5bf9\u4e8e\u8fd9\u4e9b\u5927\u4eba\u7269\u6765\u8bf4\uff0c\u548c\u5e73\u6c11\u767e\u59d3\u5171\u540c\u751f\u6d3b\u5728\u540c\u4e00\u7247\u5929\u7a7a\u4e0b\u5df2\u7ecf\u662f\u5f88\u59d4\u5c48\u4e86\uff0c\u5982\u679c\u51fa\u884c\u90fd\u8981\u6df7\u5728\u4e00\u8d77\uff0c\u90a3\u5c82\u4e0d\u662f\u989c\u9762\u626b\u5730\uff0c\u5c0a\u4e25\u5c3d\u5931\u5417\uff1f</p><p>\u201c\u90a3\u4ed6\u4eec\u7684\u5c0a\u4e25\u8fd8\u771f\u662f\u8106\u5f31\u3002\u201d\u51db\u8bf4\u3002</p><p>\u62b1\u6028\u51e0\u53e5\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002\u5b98\u50da\u5927\u62b5\u5982\u6b64\uff0c\u4ece\u53e4\u5230\u4eca\u6982\u83ab\u80fd\u5916\uff0c\u4e0d\u662f\u54ea\u4e00\u4e2a\u4eba\u80fd\u591f\u6539\u53d8\u7684\u3002\u743c\u6069\u8fd8\u6709\u4e8b\u5728\u8eab\uff0c\u4e0d\u60f3\u8282\u5916\u751f\u679d\uff0c\u7ed5\u8def\u51fa\u4e86\u57ce\uff0c\u4e00\u8def\u5f80\u5357\u8fdb\u53d1\u3002</p><p>\u5f53\u5929\u4e0b\u5348\uff0c\u4ed6\u4eec\u62b5\u8fbe\u4e86\u9c81\u65af\u5947\u3002</p><p>\u9c81\u65af\u5947\u5728\u5f7b\u68ee\u5854\u7684\u5386\u53f2\u4e0a\u66fe\u7ecf\u662f\u548c\u8f9b\u5df4\u57ce\u5e76\u9a7e\u9f50\u9a71\u7684\u540d\u57ce\uff0c\u4f46\u8fd1\u767e\u5e74\u6765\u5df2\u7ecf\u5f7b\u5e95\u8870\u5f31\u4e86\uff0c\u5927\u7ea6\u516d\u5341\u591a\u5e74\u524d\uff0c\u5b83\u88ab\u8f9b\u5df4\u57ce\u5f81\u670d\uff0c\u7136\u540e\u88ab\u5f7b\u5e95\u6539\u9020\u3002\u9c81\u65af\u5947\u7684\u5730\u7406\u4f4d\u7f6e\u5f88\u9669\u8981\uff0c\u5efa\u5728\u4e24\u5c71\u4e4b\u95f4\uff0c\u4e0e\u5176\u8bf4\u662f\u4e00\u5ea7\u57ce\u5e02\uff0c\u4e0d\u5982\u8bf4\u662f\u4e00\u5ea7\u519b\u4e8b\u8981\u585e\uff0c\u7262\u7262\u66ff\u8f9b\u5df4\u57ce\u627c\u5b88\u4f4f\u5357\u90e8\u3002</p><p>\u627e\u4e86\u5bb6\u65c5\u5e97\u4f4f\u4e0b\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u51fa\u95e8\u53bb\u627e\u4eba\uff0c\u5176\u4ed6\u4eba\u7559\u5b88\u3002</p><p>\u201c\u4f60\u786e\u5b9a\u8fd9\u5730\u5740\u6ca1\u9519\uff1f\u201d</p><p>\u534a\u4e2a\u5c0f\u65f6\u5019\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u627e\u5230\u4e86\u5730\u65b9\uff0c\u90a3\u662f\u4e00\u4e2a\u504f\u50fb\u7684\u5c0f\u5df7\u5b50\uff0c\u9633\u5149\u90fd\u4e0d\u600e\u4e48\u80fd\u900f\u8fdb\u6765\uff0c\u9752\u77f3\u677f\u94fa\u7684\u9053\u8def\u5f88\u72ed\u7a84\uff0c\u53ea\u80fd\u5bb9\u4e24\u4e2a\u4eba\u5e76\u80a9\u884c\u8d70\u3002\u5728\u5df7\u5b50\u7684\u6700\u6df1\u5904\uff0c\u6709\u4e00\u5bb6\u5e97\u94fa\uff0c\u6302\u51fa\u6765\u7684\u62db\u724c\u90fd\u5df2\u7ecf\u88ab\u70df\u718f\u9ed1\u4e86\uff0c\u770b\u4e0d\u6e05\u5b57\u6837\uff0c\u91cc\u9762\u4e5f\u6ca1\u534a\u70b9\u52a8\u9759\uff0c\u53ea\u6709\u51e0\u67c4\u80e1\u4e71\u5806\u5728\u95e8\u8fb9\u7684\u5200\u5251\u6b66\u5668\uff0c\u5411\u5ba2\u4eba\u63d0\u793a\u5b83\u662f\u4e00\u5bb6\u6b66\u5668\u5e97\u3002</p><p>\u5f7b\u68ee\u5854\u7f3a\u4e4f\u4e00\u4e2a\u7edf\u4e00\u7a33\u5b9a\u7684\u653f\u6743\uff0c\u6218\u4e71\u9891\u7e41\uff0c\u6cbb\u5b89\u5f88\u5dee\uff0c\u5bfb\u5e38\u767e\u59d3\u5bb6\u90fd\u81ea\u5907\u5200\u5251\u62a4\u8eab\uff0c\u6b66\u5668\u5e97\u662f\u5f88\u5e38\u89c1\u7684\u3002\u8fd9\u5bb6\u770b\u8d77\u6765\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u5bfb\u5e38\uff0c\u53ea\u662f\u751f\u610f\u7740\u5b9e\u51b7\u6e05\u4e86\u70b9\uff0c\u534a\u4e2a\u5ba2\u4eba\u90fd\u6ca1\u6709\uff0c\u4e00\u4f4d\u80e1\u987b\u82b1\u767d\u7684\u8001\u4eba\u5750\u5728\u67dc\u53f0\u540e\uff0c\u5934\u50cf\u5c0f\u9e21\u5544\u7c73\u822c\u4e00\u70b9\u4e00\u70b9\u7684\uff0c\u6b63\u5728\u6253\u778c\u7761\u3002</p><p>\u6885\u83f2\u65af\u5de6\u53f3\u770b\u4e86\u770b\uff0c\u53c8\u4ef0\u5934\u671b\u4e86\u671b\u62db\u724c\uff0c\u201c\u5730\u65b9\u80af\u5b9a\u6ca1\u9519\u3002\u201d</p><p>\u5979\u8d70\u5230\u67dc\u53f0\u524d\uff0c\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u8001\u4eba\u62ac\u8d77\u5934\u6765\uff0c\u8ff7\u8ff7\u8499\u8499\u7684\u53cc\u773c\u6253\u91cf\u4e86\u4e00\u4f1a\u6885\u83f2\u65af\uff0c\u53c8\u770b\u4e86\u770b\u5979\u8eab\u540e\u7684\u743c\u6069\u3002\u201c\u5ba2\u4eba\u9700\u8981\u4ec0\u4e48\uff1f\u201d\u4ed6\u6162\u541e\u541e\u5730\u5f00\u53e3\u95ee\uff0c\u201c\u672c\u5e97\u7269\u7f8e\u4ef7\u5ec9\uff0c\u5e94\u6709\u5c3d\u6709\uff0c\u672c\u5c0f\u5229\u8584\uff0c\u6982\u4e0d\u8d4a\u6b20\u3002\u201d</p><p>\u6885\u83f2\u65af\u5c06\u53f3\u624b\u653e\u5728\u67dc\u53f0\u4e0a\uff0c\u201c\u6211\u9700\u8981\u4e00\u67c4\u5728\u9ed1\u6697\u4e2d\u624d\u80fd\u770b\u89c1\u7684\u5251\u3002\u201d</p><p>\u8001\u4eba\u4f4e\u5934\u770b\u7740\u5979\u7684\u624b\uff0c\u5fae\u4e0d\u53ef\u5bdf\u5730\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u8bf7\u8fdb\uff0c\u201d\u4ed6\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u8bf4\uff0c\u201c\u8fd9\u4f4d\u662f\uff1f\u201d</p><p>\u201c\u4ed6\u662f\u6211\u5148\u751f\u3002\u201d</p><p>\u8001\u4eba\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4f4e\u4e0b\u5934\u7ee7\u7eed\u6253\u76f9\uff0c\u6885\u83f2\u65af\u548c\u743c\u6069\u4ece\u4ed6\u8eab\u8fb9\u7ecf\u8fc7\uff0c\u8d70\u8fdb\u8fd9\u5bb6\u6b66\u5668\u5e97\u7684\u95e8\u3002</p><p>\u6b64\u65f6\u5927\u7ea6\u662f\u4e0b\u5348\u4e09\u70b9\u591a\u949f\uff0c\u5929\u8272\u8fd8\u4e0d\u7b97\u592a\u665a\uff0c\u7136\u800c\u5e97\u91cc\u660e\u663e\u65e5\u7167\u4e0d\u8db3\uff0c\u9ed1\u6c89\u6c89\u7684\uff0c\u5730\u9762\u4e0a\u5806\u6ee1\u4e86\u5404\u79cd\u534a\u6210\u54c1\u548c\u5f85\u4fee\u7406\u7684\u6b66\u5668\uff0c\u52c9\u5f3a\u7a7a\u51fa\u4e00\u6761\u9053\u3002\u6885\u83f2\u65af\u8d70\u5230\u4e00\u9762\u5899\u58c1\u524d\uff0c\u5bfb\u627e\u4e86\u7247\u523b\uff0c\u6700\u540e\u53d1\u73b0\u4e00\u4e2a\u51f8\u8d77\u7684\u5730\u65b9\uff0c\u6309\u4e86\u4e0b\u53bb\u3002\u53ea\u542c\u5f97\u624e\u624e\u624e\u5730\u54cd\uff0c\u5730\u9762\u79fb\u5f00\uff0c\u9732\u51fa\u4e00\u6761\u901a\u5f80\u4e0b\u65b9\u7684\u77f3\u9636\u3002</p><p>\u201c\u4f60\u63a9\u62a4\u6211\u3002\u201d\u6885\u83f2\u65af\u8f7b\u58f0\u8bf4\u3002</p><p>\u743c\u6069\u5c06\u624b\u85cf\u5728\u8896\u4e2d\uff0c\u4e00\u76f4\u6263\u7740\u6cd5\u672f\uff0c\u7136\u800c\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u6709\u53d1\u751f\u3002\u4ed6\u4eec\u6cbf\u7740\u53f0\u9636\u8d70\u4e86\u5927\u7ea6\u4e09\u56db\u5206\u949f\uff0c\u770b\u89c1\u4e86\u4e00\u95f4\u5730\u4e0b\u5ba4\uff0c\u56db\u5468\u7684\u5899\u58c1\u4e0a\u6709\u706f\u5149\u3002\u5728\u5730\u4e0b\u5ba4\u7684\u4e2d\u592e\uff0c\u6709\u4e00\u5f20\u5706\u5f62\u7684\u684c\u5b50\uff0c\u5468\u56f4\u6446\u653e\u7740\u516d\u5f20\u6905\u5b50\u3002\u684c\u6905\u90fd\u662f\u7528\u70df\u7070\u8272\u7684\u6728\u5934\u5236\u6210\uff0c\u770b\u8d77\u6765\u5df2\u7ecf\u9887\u6709\u4e9b\u5e74\u5934\u4e86\u3002</p><p>\u6885\u83f2\u65af\u5728\u5176\u4e2d\u4e00\u5f20\u6905\u5b50\u4e0a\u5750\u4e0b\uff0c\u793a\u610f\u743c\u6069\u7ad9\u5728\u5979\u8eab\u540e\u3002</p><p>\u4e00\u9635\u98ce\u62c2\u8fc7\uff0c\u53e6\u5916\u4e94\u5f20\u6905\u5b50\u4e0a\u540c\u65f6\u51fa\u73b0\u4e86\u4eba\u5f62\u865a\u5f71\uff0c\u4f46\u770b\u4e0d\u6e05\u695a\uff0c\u53ea\u80fd\u4f9d\u7a00\u8fa8\u51fa\u8f6e\u5ed3\u3002\u6885\u83f2\u65af\u7684\u5230\u6765\u4f3c\u4e4e\u8ba9\u4ed6\u4eec\u9887\u4e3a\u60ca\u8bb6\uff0c\u743c\u6069\u542c\u89c1\u6709\u4eba\u4f4e\u4f4e\u5730\u201c\u54a6\u201d\u4e86\u4e00\u58f0\uff0c\u7d27\u63a5\u7740\uff0c\u5176\u4e2d\u56db\u4e2a\u865a\u5f71\u53c8\u6d88\u5931\u4e86\uff0c\u53ea\u5269\u4e0b\u6700\u540e\u4e00\u4eba\uff0c\u4ed6\u4ece\u865a\u5f71\u6e10\u6e10\u53d8\u6210\u5b9e\u4f53\uff0c\u662f\u4e00\u4f4d\u975e\u5e38\u7626\u7684\u4e2d\u5e74\u7537\u5b50\uff0c\u5185\u7a7f\u76ae\u7532\uff0c\u5916\u7f69\u7070\u888d\uff0c\u6b63\u7528\u5ba1\u89c6\u7684\u76ee\u5149\u770b\u7740\u6885\u83f2\u65af\uff0c\u7136\u540e\u770b\u7740\u5979\u7684\u53f3\u624b\u3002</p><p>\u201c\u4f60\u4e00\u5b9a\u662f\u6885\u83f2\u65af\u5c0f\u59d0\u3002\u201d\u6700\u540e\uff0c\u4ed6\u7ec8\u4e8e\u5f00\u53e3\uff0c\u8bf4\u7684\u662f\u901a\u7528\u8bed\u3002</p><p>\u201c\u662f\u7684\u3002\u201d</p><p>\u201c\u6211\u8bb0\u5f97\u4f60\uff0c\u4e5d\u5e74\u524d\uff0c\u4f60\u66fe\u7ecf\u8ddf\u968f\u4f60\u6bcd\u4eb2\u6765\u8fc7\uff0c\u201d\u4e2d\u5e74\u7537\u5b50\u8bf4\uff0c\u201c\u5f88\u4e45\u4e0d\u89c1\uff0c\u592b\u4eba\u8fd1\u51b5\u5982\u4f55\uff1f\u201d</p><p>\u201c\u627f\u8499\u6302\u5ff5\uff0c\u5979\u5df2\u7ecf\u53bb\u4e16\u4e86\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u4e2d\u5e74\u7537\u5b50\u970d\u7136\u4e00\u60ca\uff0c\u4e0b\u610f\u8bc6\u5730\u7ad9\u8d77\u8eab\u6765\uff0c\u968f\u5373\u610f\u8bc6\u5230\u81ea\u5df1\u7684\u5931\u6001\uff0c\u53c8\u7f13\u7f13\u5750\u4e0b\uff0c\u201c\u62b1\u6b49\uff0c\u6211\u8fd8\u4ee5\u4e3a\u5979\u662f\u8fd4\u56de\u4e86\u4e2d\u571f\u2026\u2026\u4f60\u8fd9\u6b21\u6765\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d</p><p>\u201c\u6211\u60f3\u6253\u542c\u4e00\u4e2a\u4eba\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5979\u53eb\u7fe1\u7fe0\u5973\u5deb\uff0c\u5e94\u8be5\u5728\u5f7b\u68ee\u5854\uff0c\u6216\u8005\u6069\u745f\u3002\u201d</p><p>\u201c\u8bf7\u7a0d\u7b49\u3002\u201d</p><p>\u4e2d\u5e74\u7537\u5b50\u5fae\u5fae\u6b20\u8eab\u793a\u610f\uff0c\u7136\u540e\u4ed6\u7684\u8eab\u4f53\u53d8\u5f97\u865a\u5316\uff0c\u5f88\u5feb\u6d88\u5931\u4e86\u3002\u6885\u83f2\u65af\u548c\u743c\u6069\u9759\u9759\u5730\u7b49\u5f85\u7740\uff0c\u8fc7\u4e86\u5927\u7ea6\u534a\u4e2a\u5c0f\u65f6\uff0c\u7537\u5b50\u91cd\u65b0\u51fa\u73b0\u5728\u6905\u4e2d\uff0c\u201c\u6211\u4eec\u6ca1\u6709\u4efb\u4f55\u5173\u4e8e\u2018\u7fe1\u7fe0\u5973\u5deb\u2019\u7684\u8bb0\u5f55\u3002\u201d</p><p>\u201c\u6ca1\u6709\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u201d\u7537\u5b50\u8bf4\uff0c\u201c\u4e0d\u8fc7\u6709\u4e00\u4e2a\u7ebf\u7d22\uff0c\u6216\u8bb8\u5bf9\u4f60\u6709\u7528\u3002\u53bb\u5e74\u56db\u6708\u7684\u6700\u540e\u4e00\u5929\uff0c\u6709\u4e2a\u6a35\u592b\u8bf4\u81ea\u5df1\u5728\u7070\u718a\u5c71\u91cc\u88ab\u91ce\u517d\u8ffd\u8d76\uff0c\u4ece\u60ac\u5d16\u4e0a\u6389\u4e0b\uff0c\u8ff7\u5931\u4e86\u65b9\u5411\uff0c\u8fd9\u65f6\u5019\u4ed6\u770b\u89c1\u4e00\u4f4d\u7a7f\u7740\u9ed1\u8272\u8863\u670d\u7684\u4ed9\u5973\u4ece\u6e56\u4e2d\u8d70\u51fa\u6765\uff0c\u4e3a\u4ed6\u6307\u8def\uff0c\u7136\u540e\u53c8\u6d88\u5931\u4e86\u3002\u201d</p><p>\u201c\u7070\u718a\u5c71\uff1f\u201d\u6885\u83f2\u65af\u60f3\u4e86\u4e00\u4f1a\uff0c\u201c\u90a3\u91cc\u6709\u6e56\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u6709\uff0c\u4f46\u4ed6\u4e0d\u50cf\u662f\u5728\u6492\u8c0e\uff0c\u8fd9\u5c31\u662f\u5947\u602a\u7684\u5730\u65b9\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\u4e86\uff0c\u201d\u6885\u83f2\u65af\u7ad9\u8d77\u8eab\uff0c\u5fae\u5fae\u8eac\u8eab\u8868\u793a\u611f\u8c22\uff0c\u201c\u518d\u89c1\u3002\u201d</p><p>\u201c\u518d\u89c1\uff0c\u201d\u7537\u5b50\u8bf4\uff0c\u4ed6\u7684\u8eab\u5f71\u53c8\u4e00\u6b21\u53d8\u5f97\u865a\u5316\uff0c\u201c\u6700\u540e\u63d0\u9192\u4e00\u53e5\uff0c\u6885\u83f2\u65af\u5c0f\u59d0\uff0c\u4f60\u6216\u8bb8\u6709\u6240\u8bef\u89e3\uff0c\u90a3\u679a\u6212\u6307\u4e0d\u662f\u4fe1\u7269\u3002\u6211\u4eec\u8ba4\u7684\u662f\u4eba\uff0c\u4e0d\u662f\u6212\u6307\u3002\u201d</p><p>\u6885\u83f2\u65af\u518d\u6b21\u8eac\u8eab\uff1a\u201c\u591a\u8c22\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u8d70\u51fa\u6b66\u5668\u5e97\uff0c\u743c\u6069\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u67dc\u53f0\u540e\u7684\u90a3\u4f4d\u8001\u4eba\u5df2\u7ecf\u8db4\u7740\u7761\u7740\u4e86\uff0c\u5747\u5300\u5730\u6253\u7740\u547c\u565c\u3002\u201c\u8fd9\u91cc\u7a76\u7adf\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u6d41\u4ea1\u8005\u3002\u201d\u5c11\u5973\u56de\u7b54\u3002</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u5f7b\u68ee\u5854\u662f\u4e2a\u9002\u5408\u907f\u96be\u7684\u5730\u65b9\uff0c\u201d\u5c11\u5973\u89e3\u91ca\uff0c\u201c\u603b\u4f1a\u6709\u90a3\u4e48\u4e00\u4e9b\u4eba\uff0c\u4ed6\u4eec\u7531\u4e8e\u79cd\u79cd\u539f\u56e0\uff0c\u6bd4\u5982\u8bf4\u5f97\u7f6a\u4e86\u4e0d\u8be5\u5f97\u7f6a\u7684\u4eba\uff0c\u62ff\u4e86\u4e0d\u8be5\u62ff\u7684\u4e1c\u897f\uff0c\u505a\u4e86\u60ca\u5929\u52a8\u5730\u7684\u574f\u4e8b\u88ab\u6574\u4e2a\u5927\u9646\u901a\u7f09\uff0c\u7b49\u7b49\uff0c\u65e0\u6cd5\u5728\u4e2d\u571f\u5927\u9646\u5f85\u4e0b\u53bb\uff0c\u53ea\u80fd\u8dd1\u5230\u8fd9\u91cc\u6765\u2014\u2014\u5c31\u50cf\u4f60\u4e00\u6837\uff0c\u8fd9\u5c31\u662f\u6d41\u4ea1\u8005\u3002\u201d</p><p>\u4e24\u767e\u591a\u5e74\u524d\uff0c\u4e00\u4e9b\u6d41\u4ea1\u8005\u7ec4\u5efa\u4e86\u8fd9\u4e2a\u5730\u4e0b\u7ec4\u7ec7\uff0c\u4f20\u627f\u81f3\u4eca\u3002\u5176\u5b9e\u4e25\u683c\u6765\u8bf4\uff0c\u8fd9\u90fd\u4e0d\u80fd\u53eb\u505a\u4e00\u4e2a\u7ec4\u7ec7\uff0c\u56e0\u4e3a\u5b83\u65e2\u6ca1\u6709\u4e00\u4e2a\u7edf\u4e00\u660e\u786e\u7684\u7eb2\u9886\uff0c\u4e5f\u7f3a\u4e4f\u5065\u5168\u7684\u7ec4\u7ec7\u67b6\u6784\uff0c\u66f4\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u8fdc\u5927\u76ee\u6807\u548c\u5171\u540c\u5b97\u65e8\u3002\u5b83\u66f4\u50cf\u662f\u4e00\u4e2a\u4e92\u52a9\u5e73\u53f0\uff0c\u6210\u5458\u4e4b\u95f4\u90fd\u662f\u5f88\u677e\u6563\u7684\u8054\u7cfb\uff0c\u4e3b\u4e8b\u8005\u662f\u516d\u540d\u88ab\u63a8\u9009\u51fa\u6765\u7684\u8d44\u6df1\u6d41\u4ea1\u8005\uff0c\u603b\u90e8\u4f4d\u4e8e\u9c81\u65af\u5947\u3002</p><p>\u201c\u4f2f\u6bcd\u4e5f\u662f\u5176\u4e2d\u6210\u5458\uff1f\u201d</p><p>\u201c\u5979\u6ca1\u6709\u52a0\u5165\uff0c\u53ea\u662f\u5f7c\u6b64\u6709\u5408\u4f5c\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4e92\u76f8\u4ea4\u6362\u4e00\u4e9b\u60c5\u62a5\uff0c\u4fe1\u606f\u5171\u4eab\u4ec0\u4e48\u7684\u3002\u201d</p><p>\u743c\u6069\u7275\u8d77\u5979\u7684\u53f3\u624b\uff0c\u201c\u4fe1\u7269\u662f\u4ec0\u4e48\uff1f\u8fd9\u679a\u6212\u6307\uff1f\u201d</p><p>\u6885\u83f2\u65af\u7684\u53f3\u624b\u65e0\u540d\u6307\u4e0a\u6234\u7740\u4e00\u679a\u6212\u6307\uff0c\u743c\u6069\u548c\u5979\u521a\u76f8\u8bc6\u7684\u65f6\u5019\u5c31\u89c1\u8fc7\uff0c\u53ea\u662f\u6ca1\u6709\u592a\u5728\u610f\u3002\u8fd9\u679a\u6212\u6307\u8f7b\u8584\u5c0f\u5de7\uff0c\u800c\u4e14\u4e0e\u5c11\u5973\u7684\u80a4\u8272\u8fd1\u4f3c\uff0c\u6234\u5728\u624b\u4e0a\u5f88\u96be\u88ab\u5bdf\u89c9\uff0c\u4f46\u5b83\u5e76\u4e0d\u662f\u4ec0\u4e48\u9b54\u6cd5\u7269\u54c1\uff0c\u6ca1\u6709\u8574\u542b\u4efb\u4f55\u529b\u91cf\uff0c\u53ea\u662f\u6bd4\u8f83\u7cbe\u81f4\u7f62\u4e86\u3002</p><p>\u6885\u83f2\u65af\u892a\u4e0b\u6212\u6307\uff0c\u9012\u7ed9\u743c\u6069\u770b\uff0c\u201c\u6bcd\u4eb2\u7559\u7ed9\u6211\u7684\uff0c\u201d\u5979\u89e3\u91ca\u4e86\u4e00\u53e5\uff0c\u201c\u5176\u5b9e\u6211\u4e5f\u4e0d\u6e05\u695a\u5177\u4f53\u7528\u9014\u3002\u6211\u53ea\u662f\u770b\u5230\u5979\u5c55\u793a\u6212\u6307\uff0c\u5bf9\u65b9\u5c31\u653e\u884c\uff0c\u4ee5\u4e3a\u8fd9\u662f\u4fe1\u7269\u3002\u201d</p><p>\u5f88\u663e\u7136\uff0c\u4e0e\u201c\u6d41\u4ea1\u8005\u201d\u6709\u5408\u4f5c\u7684\u662f\u6885\u83f2\u65af\u7684\u6bcd\u4eb2\uff0c\u6212\u6307\u53ea\u662f\u8ba9\u95e8\u536b\u8fa8\u8ba4\u5979\u8eab\u4efd\u7684\u6807\u8bb0\uff0c\u5e76\u975e\u4efb\u4f55\u4eba\u62ff\u7740\u8fd9\u679a\u6212\u6307\u6765\uff0c\u6d41\u4ea1\u8005\u90fd\u4f1a\u8ba4\u53ef\u3002\u5c31\u7b97\u901a\u8fc7\u4e86\u95e8\u536b\uff0c\u4e00\u65e6\u88ab\u53d1\u73b0\u4e0d\u662f\u672c\u4eba\uff0c\u6d41\u4ea1\u8005\u4e5f\u4ecd\u7136\u662f\u4e0d\u4f1a\u7ed9\u4e88\u5408\u4f5c\u7684\u2014\u2014\u539f\u5219\u4e0a\u662f\u5982\u6b64\uff0c\u8fd9\u6b21\u663e\u7136\u7b97\u662f\u7834\u4f8b\u4e86\u3002</p><p>\u201c\u5e78\u597d\u4f60\u548c\u4f2f\u6bcd\u957f\u5f97\u50cf\uff0c\u4efb\u4f55\u4eba\u4e00\u770b\u5c31\u77e5\u9053\u4f60\u4eec\u662f\u6bcd\u5973\u3002\u201d</p><p>\u201c\u4f60\u5982\u679c\u89c1\u8fc7\u6211\u6bcd\u4eb2\u5c31\u4e0d\u4f1a\u8fd9\u4e48\u8bf4\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u5979\u53ef\u6bd4\u6211\u6f02\u4eae\u591a\u4e86\u3002\u201d</p><p>\u201c\u600e\u4e48\u53ef\u80fd\uff0c\u4e16\u4e0a\u54ea\u91cc\u8fd8\u6709\u6bd4\u4f60\u66f4\u6f02\u4eae\u7684\u5973\u4eba\u3002\u201d</p><p>\u201c\u771f\u7684\uff0c\u5979\u5f53\u5e74\u7684\u8ffd\u6c42\u8005\u53ef\u4ee5\u7ec4\u5efa\u4e00\u652f\u519b\u961f\uff0c\u80fd\u591f\u8e0f\u5e73\u6df1\u6c34\u57ce\u7684\u90a3\u79cd\u3002\u6211\u662f\u5f88\u6f02\u4eae\u554a\uff0c\u6211\u53c8\u4e0d\u4f1a\u5984\u81ea\u83f2\u8584\uff0c\u4f46\u7684\u786e\u6bd4\u4e0d\u4e0a\u5979\u2014\u2014\u4e0d\u4fe1\u4f60\u53bb\u95ee\u95ee\u5927\u4e3b\u6559\u3002\u201d</p><p>\u201c\u4ed6\u7684\u610f\u89c1\u4e0d\u80fd\u4f5c\u4e3a\u53c2\u8003\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u8fd8\u4e0d\u5982\u95ee\u51db\u53bb\uff0c\u53cd\u6b63\u5979\u662f\u89c1\u8fc7\u4f2f\u6bcd\u7684\u3002\u201d</p><p>\u201c\u95ee\u6211\u4ec0\u4e48\u554a\uff1f\u201d\u51db\u7684\u58f0\u97f3\u7a81\u7136\u4ece\u80cc\u540e\u4f20\u6765\u3002</p><p>\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u4f60\u600e\u4e48\u8dd1\u6765\u4e86\uff1f\u201d</p><p>\u201c\u4f60\u4eec\u534a\u5929\u4e0d\u56de\u6765\uff0c\u6211\u5f88\u62c5\u5fc3\u554a\uff0c\u5c31\u51fa\u6765\u627e\u627e\uff0c\u201d\u51db\u7728\u7740\u5927\u773c\u775b\uff0c\u201c\u4f60\u4eec\u521a\u624d\u5728\u8bf4\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u6211\u662f\u60f3\u95ee\u4f60\uff0c\u827e\u5f25\u8587\u548c\u5979\u6bcd\u4eb2\uff0c\u4f60\u89c9\u5f97\u54ea\u4e2a\u66f4\u6f02\u4eae\uff1f\u201d</p><p>\u51db\u5f80\u540e\u8df3\u4e86\u4e00\u6b65\uff0c\u4e00\u8138\u8b66\u60d5\u5730\u76ef\u7740\u743c\u6069\uff0c\u201c\u4f60\u60f3\u5e72\u561b\uff1f\u201d</p><p>\u201c\u2026\u2026\u6211\u53ea\u662f\u597d\u5947\u95ee\u95ee\u800c\u5df2\u3002\u201d\u743c\u6069\u65e0\u5948\uff0c\u4f60\u8fd9\u8868\u60c5\u662f\u600e\u4e48\u56de\u4e8b\u554a\uff0c\u4e0d\u8981\u641e\u5f97\u597d\u50cf\u6211\u56fe\u8c0b\u4e0d\u8f68\u4f3c\u7684\uff0c\u6885\u83f2\u65af\u7684\u6bcd\u4eb2\u660e\u660e\u90fd\u5df2\u7ecf\u53bb\u4e16\u591a\u5e74\u4e86\u597d\u5427\u3002</p><p>\u201c\u6211\u60f3\u60f3\u554a\uff0c\u201d\u51db\u80cc\u7740\u53cc\u624b\uff0c\u4e00\u8fb9\u5f80\u524d\u8d70\u4e00\u8fb9\u6c89\u601d\uff0c\u201c\u6211\u5f53\u7136\u662f\u6bd4\u8f83\u559c\u6b22\u827e\u5f25\u8587\u5566\uff0c\u963f\u59e8\u6709\u70b9\u51f6\uff0c\u8fd8\u7ecf\u5e38\u5413\u552c\u6211\uff0c\u4e0d\u8fc7\u8981\u8bf4\u6f02\u4eae\u4e5f\u662f\u771f\u6f02\u4eae\uff0c\u55ef\uff0c\u53ef\u80fd\u4e5f\u5c31\u6bd4\u6211\u5988\u5988\u5dee\u4e00\u70b9\u70b9\u3002\u201d</p><p>\u201c\u2026\u2026\u7b97\u4e86\u3002\u201d</p><p>\u743c\u6069\u62ff\u7740\u6212\u6307\uff0c\u611f\u89c9\u5b83\u50cf\u662f\u5168\u65e0\u534a\u70b9\u5206\u91cf\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u7528\u4ec0\u4e48\u6750\u8d28\u5236\u6210\u7684\uff0c\u6bd4\u79d8\u94f6\u90fd\u8981\u8f7b\u3002\u4ed6\u968f\u610f\u8f6c\u52a8\u7740\u6212\u6307\uff0c\u5ffd\u7136\u53d1\u73b0\u5728\u5185\u5708\u4f3c\u4e4e\u523b\u7740\u4e00\u884c\u5b57\uff0c\u4fbf\u968f\u53e3\u8bfb\u4e86\u51fa\u6765\uff0c\u90a3\u663e\u7136\u662f\u4e00\u4e2a\u4eba\u540d\uff1a\u201c\u8bfa\u5a03\xb7\u6885\u83f2\u65af\u3002\u201d</p><p>\u201c\u8fd9\u662f\u4f2f\u6bcd\u7684\u540d\u5b57\uff1f\u201d</p><p>\u5c11\u5973\u5288\u624b\u628a\u6212\u6307\u62a2\u4e86\u56de\u53bb\u3002</p><p>\u203b\u203b\u203b</p><p>\u4f11\u606f\u4e86\u4e00\u665a\uff0c\u7b2c\u4e8c\u5929\u6e05\u6668\uff0c\u4e00\u884c\u4eba\u7ee7\u7eed\u51fa\u53d1\uff0c\u76ee\u6807\u662f\u8ff7\u5931\u68ee\u6797\u3002</p><p>\u5f7b\u68ee\u5854\u4e0e\u6069\u745f\u7684\u8fb9\u5883\uff0c\u5927\u81f4\u4e0a\u662f\u4ee5\u873f\u8712\u6cb3\u4e3a\u754c\uff0c\u6cb3\u897f\u662f\u5f7b\u68ee\u5854\uff0c\u6cb3\u4e1c\u662f\u6069\u745f\uff0c\u8ff7\u5931\u68ee\u6797\u5c31\u5728\u873f\u8712\u6cb3\u4e1c\u3002\u90a3\u662f\u4e00\u7247\u9762\u79ef\u975e\u5e38\u5927\u7684\u8fdc\u53e4\u68ee\u6797\uff0c\u636e\u8bf4\u91cc\u9762\u6709\u5f88\u591a\u602a\u517d\u51fa\u6ca1\uff0c\u8fd8\u4f4f\u7740\u4e00\u6761\u9f99\u3002\u6309\u7167\u8428\u9a6c\u65af\u7279\u7559\u7ed9\u743c\u6069\u7684\u5730\u56fe\uff0c\u90a3\u5ea7\u8010\u745f\u6d6e\u7a7a\u57ce\u5c31\u5728\u8ff7\u5931\u53f8\u68ee\u6797\u7684\u5357\u90e8\uff0c\u4e5f\u4e0d\u77e5\u9053\u51e0\u767e\u5e74\u8fc7\u53bb\u4e86\uff0c\u6709\u6ca1\u6709\u88ab\u4eba\u6377\u8db3\u5148\u767b\u3002</p><p>\u5728\u8f9b\u5df4\u57ce\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u5df2\u7ecf\u505a\u597d\u4e86\u4e00\u4efd\u884c\u7a0b\u89c4\u5212\uff0c\u5927\u81f4\u4e0a\u662f\u79bb\u5f00\u9c81\u65af\u5947\u4e4b\u540e\uff0c\u63a5\u4e0b\u6765\u662f\u5230\u7d22\u7eb3\u745e\u57ce\uff0c\u518d\u4e58\u8239\u6a2a\u7a7f\u4e00\u4e2a\u5185\u6d77\u6d77\u5ce1\uff0c\u62b5\u8fbe\u5bf9\u9762\u7684\u6469\u8fbe\u80af\u57ce\uff0c\u7136\u540e\u5c31\u8fdb\u5165\u5fa1\u5b87\u5c71\u8109\u4e86\u3002\u5fa1\u5b87\u5c71\u8109\u662f\u6885\u83f2\u65af\u548c\u51db\u7684\u7ae5\u5e74\u4f4f\u5904\uff0c\u4e24\u4f4d\u5c11\u5973\u5728\u90a3\u91cc\u76f8\u8bc6\uff0c\u6bd4\u90bb\u800c\u5c45\uff0c\u5171\u540c\u751f\u6d3b\u4e86\u5927\u6982\u4e03\u516b\u5e74\u65f6\u95f4\uff0c\u53ef\u4ee5\u7b97\u662f\u5979\u4eec\u7684\u6545\u4e61\u4e86\u3002\u5982\u4eca\u96be\u5f97\u6709\u673a\u4f1a\u56de\u5f7b\u68ee\u5854\uff0c\u5f53\u7136\u8981\u53bb\u770b\u770b\u3002</p><p>\u8fd9\u662f\u539f\u5b9a\u7684\u8def\u7ebf\uff0c\u4e0d\u8fc7\u8ba1\u5212\u603b\u662f\u8d76\u4e0d\u4e0a\u53d8\u5316\uff0c\u51db\u4e0d\u77e5\u57fa\u4e8e\u4f55\u79cd\u539f\u56e0\uff0c\u4e00\u5b9a\u8981\u743c\u6069\u4fee\u6539\u884c\u7a0b\uff0c\u907f\u5f00\u5fa1\u5b87\u5c71\u8109\uff0c\u800c\u4e14\u8fd8\u4e0d\u80fd\u5bf9\u6885\u83f2\u65af\u660e\u8bf4\u3002\u743c\u6069\u6b63\u4e0d\u77e5\u9053\u5982\u4f55\u5f00\u53e3\uff0c\u5e78\u597d\u8f66\u5230\u5c71\u524d\u5fc5\u6709\u8def\u3002\u9c81\u65af\u5947\u7684\u201c\u6d41\u4ea1\u8005\u201d\u8bf4\u4e86\u4e00\u4e2a\u5730\u65b9\uff0c\u7070\u718a\u5c71\uff0c\u8bf4\u662f\u6709\u4eba\u66fe\u7ecf\u5728\u90a3\u91cc\u770b\u5230\u8fc7\u4ec0\u4e48\u6e56\u4e2d\u4ed9\u5973\uff0c\u53ef\u60dc\u4ed6\u8fd0\u6c14\u4e0d\u597d\uff0c\u65e2\u6ca1\u6709\u5f97\u5230\u4ec0\u4e48\u738b\u8005\u5723\u5251\uff0c\u4e5f\u6ca1\u6709\u5f97\u5230\u91d1\u65a7\u5934\u548c\u94f6\u65a7\u5934\u3002\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u4e5f\u7b97\u662f\u4e00\u6761\u7ebf\u7d22\uff0c\u53ef\u4ee5\u78b0\u78b0\u8fd0\u6c14\u3002</p><p>\u201c\u7070\u718a\u5c71\u9760\u8fd1\u5357\u90e8\uff0c\u5982\u679c\u8981\u7ecf\u8fc7\u90a3\u91cc\uff0c\u5c31\u4e0d\u65b9\u4fbf\u518d\u53bb\u5fa1\u5b87\u5c71\u4e86\uff0c\u201d\u6885\u83f2\u65af\u770b\u7740\u5730\u56fe\u8bf4\uff0c\u201c\u7ed5\u5f97\u5b9e\u5728\u592a\u8fdc\u3002\u201d</p><p>\u201c\u90a3\u8981\u4e48\u2026\u2026\u5c31\u7b97\u4e86\uff1f\u201d\u743c\u6069\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u8bf4\uff0c\u201c\u6e56\u4e2d\u4ed9\u5973\u4ec0\u4e48\u7684\uff0c\u4e00\u542c\u5c31\u4e0d\u9760\u8c31\u3002\u201d</p><p>\u6885\u83f2\u65af\u6447\u6447\u5934\uff0c\u201c\u89e3\u9664\u8bc5\u5492\u6bd4\u8f83\u91cd\u8981\uff0c\u65e2\u7136\u6709\u7ebf\u7d22\uff0c\u8fd8\u662f\u53bb\u770b\u770b\u4e3a\u597d\u3002\u5fa1\u5b87\u5c71\u4e0b\u6b21\u6709\u65f6\u95f4\u518d\u53bb\u5427\u3002\u201d</p><p>\u4e8e\u662f\u5979\u91cd\u65b0\u4fee\u6539\u8def\u7ebf\uff0c\u505a\u51fa\u4e00\u4e2a\u65b0\u7684\u884c\u7a0b\u5b89\u6392\u3002\u5728\u5c11\u5973\u7684\u6307\u5f15\u4e0b\uff0c\u4e00\u884c\u4eba\u51fa\u4e86\u9c81\u65af\u5947\u4e4b\u540e\u6ca1\u591a\u4e45\uff0c\u5c31\u8f6c\u5165\u5c71\u533a\uff0c\u9053\u8def\u8d8a\u6765\u8d8a\u7a84\uff0c\u8d8a\u6765\u8d8a\u5d0e\u5c96\uff0c\u6839\u672c\u6ca1\u529e\u6cd5\u8d70\u9a6c\u8f66\uff0c\u6240\u6709\u4eba\u53ea\u597d\u6b65\u884c\u3002</p><p>\u592a\u9633\u5f88\u70c8\uff0c\u7167\u5f97\u4eba\u5934\u660f\u773c\u82b1\uff0c\u5fc3\u6d6e\u6c14\u8e81\u3002\u5f7b\u68ee\u5854\u7684\u5730\u5f62\uff0c\u591a\u5c71\uff0c\u591a\u4e18\u9675\uff0c\u591a\u6cbc\u6cfd\uff0c\u5c31\u662f\u5c11\u5e73\u539f\uff1b\u91ce\u5916\u57fa\u672c\u4e0a\u6ca1\u6709\u4eba\u7c7b\u6d3b\u52a8\uff0c\u5168\u662f\u5404\u79cd\u91ce\u517d\u3001\u602a\u7269\u7684\u5929\u4e0b\u3002\u743c\u6069\u7b49\u4eba\u8d70\u4e86\u4e00\u5929\uff0c\u5df2\u7ecf\u906d\u9047\u4e86\u516d\u4e03\u62e8\u62e6\u8def\u6253\u52ab\uff0c\u867d\u7136\u6ca1\u6709\u9020\u6210\u4ec0\u4e48\u5a01\u80c1\uff0c\u4f46\u8fd8\u662f\u8ba9\u4eba\u4e0d\u80dc\u5176\u6270\u3002\u5c31\u8fde\u665a\u4e0a\u5bbf\u8425\u90fd\u88ab\u72fc\u7fa4\u56f4\u653b\uff0c\u5e78\u597d\u5965\u6c83\u7684\u9752\u94dc\u57ce\u5821\u8fd8\u7b97\u5f3a\u529b\uff0c\u5c06\u8fd9\u4e9b\u91ce\u517d\u5168\u90fd\u963b\u62e6\u5728\u5916\uff0c\u4f46\u542c\u4e86\u4e00\u591c\u7684\u72fc\u568e\uff0c\u7b2c\u4e8c\u5929\u65e9\u4e0a\u51db\u51fa\u95e8\u65f6\uff0c\u5c31\u9876\u7740\u4e00\u5bf9\u5927\u5927\u7684\u9ed1\u773c\u5708\uff0c\u7cbe\u795e\u660e\u663e\u6709\u4e9b\u840e\u9761\u3002\u201c\u827e\u5f25\u8587\uff0c\u6211\u4eec\u5e72\u561b\u4e0d\u8d70\u5927\u8def\u5462\uff1f\u201d\u5979\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\uff0c\u5f00\u59cb\u62b1\u6028\uff0c\u201c\u8d70\u7ea2\u9f99\u5927\u9053\uff0c\u53ef\u4ee5\u76f4\u901a\u5230\u98ce\u68d8\u57ce\uff0c\u518d\u53bb\u6069\u745f\u5c31\u65b9\u4fbf\u591a\u4e86\u561b\u3002\u201d</p><p>\u201c\u98ce\u68d8\u57ce\u592a\u5371\u9669\u4e86\u3002\u201d\u743c\u6069\u66ff\u6885\u83f2\u65af\u56de\u7b54\u3002</p><p>\u98ce\u68d8\u57ce\u662f\u5f7b\u68ee\u5854\u7684\u7b2c\u4e8c\u5927\u6d77\u6e2f\uff0c\u7b2c\u56db\u5927\u57ce\u5e02\uff0c\u4e5f\u6709\u63a5\u8fd1\u4e03\u4e07\u7684\u5e38\u4f4f\u5c45\u6c11\uff0c\u5b83\u4ee5\u6392\u65a5\u5965\u672f\u95fb\u540d\u9050\u8fe9\uff0c\u53f7\u79f0\u662f\u201c\u6700\u4e0d\u6b22\u8fce\u5deb\u5e08\u7684\u57ce\u5e02\u201d\uff0c\u8fde\u743c\u6069\u5728\u4e2d\u571f\u65f6\u90fd\u6709\u6240\u8033\u95fb\u3002\u4ed6\u4ee5\u524d\u53bb\u8fc7\u5b89\u59c6\u7684\u9996\u90fd\u963f\u65af\u5361\u7279\u62c9\uff0c\u90a3\u4e5f\u662f\u4e00\u4e2a\u5bf9\u5deb\u5e08\u4e0d\u592a\u53cb\u597d\u7684\u5730\u65b9\uff0c\u4f46\u76f8\u6bd4\u8d77\u98ce\u68d8\u57ce\uff0c\u963f\u65af\u5361\u7279\u62c9\u7b80\u76f4\u53ef\u4ee5\u7b97\u662f\u5deb\u5e08\u7684\u5929\u5802\u4e86\u3002\u5728\u963f\u65af\u5361\u7279\u62c9\uff0c\u81f3\u5c11\u8fd8\u662f\u5141\u8bb8\u5deb\u5e08\u65bd\u6cd5\u7684\uff0c\u53ea\u8981\u82b1\u94b1\u4e70\u5230\u201c\u65bd\u6cd5\u8bb8\u53ef\u8bc1\u201d\u5c31\u884c\uff1b\u5c31\u7b97\u65e0\u7167\u65bd\u6cd5\uff0c\u4e5f\u4e0d\u8fc7\u662f\u88ab\u7f5a\u6b3e\uff0c\u6700\u591a\u76d1\u7981\uff1b\u4f46\u5728\u98ce\u68d8\u57ce\uff0c\u4f7f\u7528\u5965\u672f\u5c5e\u4e8e\u91cd\u7f6a\uff0c\u538b\u6839\u6ca1\u6709\u4ec0\u4e48\u8bb8\u53ef\u4e0d\u8bb8\u53ef\u7684\u8bf4\u6cd5\uff0c\u4e00\u7ecf\u53d1\u73b0\u7acb\u523b\u5904\u6b7b\u2014\u2014\u4e0d\u4ec5\u4ec5\u5982\u6b64\uff0c\u53ea\u8981\u88ab\u53d1\u73b0\uff08\u6216\u8005\u88ab\u8ba4\u4e3a\uff09\u662f\u5deb\u5e08\uff0c\u6216\u8005\u662f\u201c\u4e0e\u5deb\u5e08\u6709\u5173\u7684\u751f\u7269\u201d\uff0c\u4e5f\u4f1a\u88ab\u7acb\u523b\u9001\u4e0a\u706b\u5211\u67b6\u3002\u6240\u8c13\u201c\u4e0e\u5deb\u5e08\u6709\u5173\u7684\u751f\u7269\u201d\uff0c\u5b9a\u4e49\u6a21\u7cca\uff0c\u8303\u56f4\u5e7f\u6cdb\uff0c\u50cf\u7cbe\u7075\u3001\u77ee\u4eba\u3001\u90aa\u9b54\u4e4b\u7c7b\u90fd\u5728\u5176\u5217\uff0c\u743c\u6069\u8fd9\u4e00\u884c\u4eba\u4e2d\uff0c\u6709\u56db\u4e2a\u5deb\u5e08\uff0c\u4e00\u4e2a\u9b45\u9b54\uff0c\u90fd\u5c5e\u4e8e\u6253\u51fb\u5bf9\u8c61\uff0c\u53ea\u6709\u6885\u83f2\u65af\u4e00\u4eba\u4f8b\u5916\uff0c\u53bb\u90a3\u91cc\u7684\u786e\u5f88\u5371\u9669\u3002</p><p>\u5f7b\u68ee\u5854\u539f\u5c5e\u4e8e\u6069\u745f\uff0c\u6069\u745f\u662f\u795e\u738b\u7edf\u6cbb\u7684\u653f\u6559\u5408\u4e00\u56fd\u5bb6\uff0c\u795e\u738b\u5728\u4e16\u79f0\u795e\uff0c\u9ad8\u9ad8\u5728\u4e0a\uff0c\u796d\u53f8\u6267\u638c\u5927\u6743\uff0c\u5deb\u5e08\u5219\u662f\u6807\u51c6\u7684\u53cd\u6d3e\u89d2\u8272\uff0c\u5176\u529b\u91cf\u88ab\u8ba4\u4e3a\u6765\u81ea\u4e8e\u51a5\u754c\u7684\u90aa\u9b54\uff0c\u5c5e\u4e8e\u4e25\u5389\u6253\u51fb\u7684\u5bf9\u8c61\u3002\u5728\u65e9\u671f\u7684\u6cd5\u5f8b\u4e2d\uff0c\u4e00\u4e2a\u6069\u745f\u4eba\u5982\u679c\u88ab\u6307\u8ba4\u4e3a\u201c\u5deb\u5e08\u201d\uff0c\u90a3\u5c31\u662f\u6700\u4e25\u91cd\u7684\u7f6a\u540d\uff0c\u4f1a\u88ab\u7ed1\u8d77\u6765\u4e22\u8fdb\u6cb3\u91cc\u63a5\u53d7\u5ba1\u5224\uff0c\u5982\u679c\u6c89\u4e0b\u53bb\uff0c\u8bf4\u660e\u5176\u7f6a\u6709\u5e94\u5f97\uff1b\u5982\u679c\u6d6e\u8d77\u6765\uff0c\u8bc1\u660e\u5176\u4f7f\u7528\u4e86\u90aa\u672f\uff0c\u5e94\u8be5\u635e\u8d77\u6765\u70e7\u6b7b\u3002\u8fd9\u70b9\u4ece\u6587\u5b57\u4e0a\u4e5f\u53ef\u4ee5\u5f97\u5230\u8bc1\u660e\uff0c\u6885\u83f2\u65af\u4e4b\u524d\u5c31\u8bf4\u8fc7\uff0c\u5728\u53e4\u6069\u745f\u8bed\u4e2d\uff0c\u201c\u5deb\u5e08\u201d\u548c\u201c\u90aa\u9b54\u201d\u5c31\u662f\u540c\u4e00\u4e2a\u8bcd\u3002</p><p>\u540e\u6765\u968f\u7740\u65f6\u4ee3\u6f14\u53d8\uff0c\u5c24\u5176\u662f\u5f7b\u68ee\u5854\u8131\u79bb\u6069\u745f\u4e4b\u540e\uff0c\u795e\u738b\u6ca1\u6709\u4e86\uff0c\u796d\u53f8\u9636\u5c42\u5b8c\u5168\u5d29\u6e83\uff0c\u5deb\u5e08\u6ca1\u6709\u4e86\u538b\u5728\u5934\u4e0a\u7684\u5de8\u77f3\uff0c\u72b6\u51b5\u6709\u6240\u6539\u89c2\uff0c\u81f3\u5c11\u4e0d\u4f1a\u968f\u4fbf\u88ab\u4e22\u5230\u6cb3\u91cc\u53bb\u4e86\u3002\u65f6\u81f3\u4eca\u65e5\uff0c\u5deb\u5e08\u5728\u5f7b\u68ee\u5854\u5730\u533a\uff0c\u540d\u58f0\u80af\u5b9a\u4f9d\u7136\u662f\u4e0d\u597d\uff0c\u8d70\u5728\u8def\u4e0a\u6216\u8bb8\u4f1a\u88ab\u9e21\u86cb\u548c\u767d\u83dc\u7838\uff0c\u4f46\u81f3\u5c11\u4e0d\u4f1a\u88ab\u8b66\u5bdf\u6293\u8d77\u6765\u3002\u50cf\u98ce\u68d8\u57ce\u8fd9\u6837\u516c\u5f00\u7684\u3001\u8d64\u88f8\u88f8\u7684\u5ba3\u5e03\u8981\u4e0e\u5deb\u5e08\u52bf\u4e0d\u4e24\u7acb\uff0c\u4ee5\u527f\u6740\u5deb\u5e08\u4e3a\u672c\u804c\u7684\uff0c\u8fd8\u662f\u552f\u4e00\u4e00\u4e2a\uff0c\u7edd\u65e0\u4ec5\u6709\u3002</p><p>\u4e3a\u4ec0\u4e48\u8fd9\u5ea7\u57ce\u5e02\u5bf9\u5deb\u5e08\u548c\u5965\u672f\u5982\u6b64\u654c\u89c6\uff0c\u6ca1\u4eba\u80fd\u8bf4\u5f97\u6e05\u695a\uff0c\u552f\u4e00\u53ef\u4ee5\u80af\u5b9a\u7684\u662f\u8fd9\u5e76\u975e\u4ec0\u4e48\u5386\u53f2\u4f20\u7edf\uff0c\u56e0\u4e3a\u5927\u7ea6\u4e09\u767e\u591a\u5e74\u524d\uff0c\u98ce\u68d8\u57ce\u4e0d\u4f46\u4e0d\u654c\u89c6\u5deb\u5e08\uff0c\u53cd\u800c\u662f\u6574\u4e2a\u5f7b\u68ee\u5854\u6240\u6709\u57ce\u90a6\u4e2d\u5bf9\u5deb\u5e08\u6700\u53cb\u597d\u7684\u5730\u65b9\u3002\u5f53\u65f6\u57ce\u4e2d\u751a\u81f3\u8fd8\u6709\u4e00\u5ea7\u9b54\u6cd5\u5b66\u9662\uff0c\u867d\u7136\u89c4\u6a21\u5c0f\u4e86\u70b9\uff0c\u4eba\u6570\u5c11\u4e86\u70b9\uff0c\u6c34\u5e73\u4f4e\u4e86\u70b9\uff0c\u4f46\u6bd5\u7adf\u662f\u516c\u5f00\u8ba4\u53ef\u4e86\u5deb\u5e08\u7684\u5408\u6cd5\u8eab\u4efd\u3002\u76f4\u5230\u6709\u4e00\u5929\uff0c\u57ce\u4e3b\u5361\u62c9\u8bfa\u514b\u4e0d\u77e5\u53d7\u4e86\u4ec0\u4e48\u523a\u6fc0\uff0c\u7a81\u7136\u5ba3\u5e03\u5173\u95ed\u9b54\u6cd5\u5b66\u9662\uff0c\u5e76\u4e14\u902e\u6355\u4e86\u6240\u6709\u6559\u6388\u548c\u5b66\u751f\uff08\u603b\u6570\u5927\u7ea6\u6709\u63a5\u8fd1\u4e8c\u5341\u540d\uff09\uff0c\u5c06\u4ed6\u4eec\u90fd\u70e7\u6b7b\u4e86\uff0c\u7d27\u63a5\u7740\u53c8\u9881\u53d1\u4e00\u9053\u7981\u4ee4\uff0c\u5ba3\u79f0\u5965\u672f\u662f\u4e07\u6076\u4e4b\u6e90\uff0c\u5deb\u5e08\u662f\u4e16\u754c\u4e4b\u654c\uff0c\u5e94\u8be5\u7edf\u7edf\u7528\u706b\u7130\u52a0\u4ee5\u201c\u51c0\u5316\u201d\u3002\u6709\u4f20\u8a00\u8bf4\u662f\u56e0\u4e3a\u57ce\u4e3b\u53d7\u5230\u4e86\u4e00\u4f4d\u5deb\u5e08\u7684\u620f\u5f04\uff0c\u6240\u4ee5\u5927\u53d1\u96f7\u9706\uff1b\u4e5f\u6709\u4f20\u8a00\u8bf4\u662f\u56e0\u4e3a\u57ce\u4e3b\u4fe1\u5949\u4e86\u67d0\u4f4d\u538c\u6076\u5965\u672f\u7684\u795e\u7957\uff0c\u8fd9\u6837\u505a\u662f\u4e3a\u4e86\u8868\u793a\u5fe0\u8bda\uff1b\u8fd8\u6709\u5176\u4ed6\u5404\u79cd\u7248\u672c\u7684\u8bf4\u6cd5\uff0c\u4e94\u82b1\u516b\u95e8\uff0c\u4e0d\u4e00\u800c\u8db3\uff0c\u6ca1\u4e00\u4e2a\u542c\u8d77\u6765\u9760\u8c31\u3002</p><p>\u201c\u5173\u4e8e\u8fd9\u4e2a\u95ee\u9898\uff0c\u6211\u5012\u53ef\u4ee5\u89e3\u91ca\u3002\u201d\u7ef4\u82e5\u62c9\u7a81\u7136\u8bf4\u3002</p><p>\u55ef\uff1f</p><p>\u4e00\u8def\u4e0a\uff0c\u7ef4\u82e5\u62c9\u57fa\u672c\u90fd\u5904\u4e8e\u6df1\u5c45\u7b80\u51fa\u7684\u72b6\u6001\uff0c\u4ece\u65e9\u5230\u665a\u90fd\u628a\u81ea\u5df1\u5173\u5728\u623f\u95f4\u91cc\uff0c\u5b58\u5728\u611f\u8d85\u7ea7\u4f4e\uff0c\u4e5f\u4e0d\u600e\u4e48\u548c\u5176\u4ed6\u4eba\u63a5\u89e6\uff0c\u4fdd\u6301\u7740\u6de1\u6de1\u7684\u758f\u79bb\u3002\u800c\u4e14\u5728\u6574\u4e2a\u961f\u4f0d\u91cc\uff0c\u7ef4\u82e5\u62c9\u7684\u4f4d\u7f6e\u4e5f\u6bd4\u8f83\u5c34\u5c2c\uff0c\u5176\u4ed6\u4eba\u90fd\u662f\u743c\u6069\u7684\u5973\u53cb\uff0c\u8eab\u4efd\u7c7b\u4f3c\uff0c\u4e4b\u524d\u4e5f\u90fd\u6709\u8fc7\u4ea4\u5f80\uff0c\u6bd4\u8f83\u719f\u6089\uff0c\u76f8\u5904\u8d77\u6765\u6bd4\u8f83\u8f7b\u677e\uff0c\u7ef4\u82e5\u62c9\u7684\u8eab\u4efd\u5374\u4e0d\u597d\u5b9a\u4f4d\uff0c\u800c\u5979\u7ed9\u743c\u6069\u4e0b\u8bc5\u5492\u7684\u505a\u6cd5\uff0c\u540c\u65f6\u89e6\u72af\u4e86\u5176\u4ed6\u6240\u6709\u4eba\u7684\u5229\u76ca\uff0c\u96be\u514d\u88ab\u9690\u9690\u5730\u6392\u65a5\u3002\u5979\u81ea\u5df1\u4e5f\u6e05\u695a\u8fd9\u4e00\u70b9\uff0c\u6240\u4ee5\u5c3d\u91cf\u4f4e\u8c03\uff0c\u5f88\u5c11\u8bf4\u8bdd\u3002</p><p>\u201c\u4ec0\u4e48\u539f\u56e0\uff1f\u201d\u51db\u8d76\u5fd9\u95ee\u3002\u5979\u5fc3\u601d\u6bd4\u8f83\u5355\u7eaf\uff0c\u5bf9\u7ef4\u82e5\u62c9\u8fd9\u4e2a\u5e08\u59d0\u5012\u6ca1\u4ec0\u4e48\u610f\u89c1\uff0c\u6216\u8005\u8bf4\uff0c\u5979\u53cd\u6b63\u6709\u6885\u83f2\u65af\uff0c\u743c\u6069\u6682\u65f6\u4e0d\u4e3e\uff0c\u5bf9\u5979\u7684\u5f71\u54cd\u4e5f\u4e0d\u662f\u7279\u522b\u5927\u2026\u2026</p><p>\u201c\u9a6c\u4f26\uff0c\u672c\u6559\u5386\u53f2\u4e0a\u7684\u4e00\u4f4d\u5148\u8f88\u4f20\u9053\u5deb\u5e08\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u4ed6\u66fe\u7ecf\u5728\u98ce\u68d8\u57ce\u7684\u9b54\u6cd5\u5b66\u9662\u62c5\u4efb\u8fc7\u4e00\u6bb5\u65f6\u95f4\u7684\u6559\u6388\uff0c\u540e\u6765\u52fe\u5f15\u4e86\u57ce\u4e3b\u592b\u4eba\uff0c\u4e0e\u4e4b\u79c1\u901a\uff0c\u88ab\u53d1\u73b0\u4e86\u3002\u9a6c\u62c9\u5e26\u7740\u57ce\u4e3b\u592b\u4eba\u79c1\u5954\u5230\u6069\u745f\uff0c\u57ce\u4e3b\u65e0\u6cd5\u8ffd\u6355\uff0c\u5927\u6012\u4e4b\u4e0b\uff0c\u4ece\u6b64\u654c\u89c6\u4e00\u5207\u5deb\u5e08\u3002\u201d</p><p>\u539f\u6765\u662f\u8fd9\u5bb6\u4f19\u5e72\u7684\u597d\u4e8b\u3002</p><p>\u9a6c\u4f26\u5c31\u662f\u90a3\u4f4d\u88ab\u60c5\u4eba\u4e0b\u4e86\u4e13\u60c5\u8bc5\u5492\uff0c\u65e0\u6cd5\u7834\u89e3\uff0c\u6700\u540e\u81ea\u6740\u7684\u4f20\u9053\u5deb\u5e08\uff0c\u743c\u6069\u539f\u672c\u5bf9\u4ed6\u8fd8\u6709\u51e0\u5206\u540c\u75c5\u76f8\u601c\u4e4b\u60c5\uff0c\u5982\u4eca\u53ea\u89c9\u5f97\u8fd9\u5bb6\u4f19\u6b7b\u6709\u4f59\u8f9c\u3002\u82e5\u4e0d\u662f\u4ed6\u80e1\u4e71\u4e0b\u624b\uff0c\u52fe\u5f15\u4e86\u57ce\u4e3b\u592b\u4eba\uff0c\u98ce\u68d8\u57ce\u5c31\u4e0d\u4f1a\u8fd9\u6837\u654c\u89c6\u5deb\u5e08\uff0c\u743c\u6069\u7b49\u4eba\u5c31\u53ef\u4ee5\u8212\u8212\u670d\u670d\u5730\u5750\u5728\u9a6c\u8f66\u91cc\uff0c\u6cbf\u7740\u5927\u8def\u5954\u9a70\uff0c\u54ea\u91cc\u9700\u8981\u50cf\u8fd9\u6837\u52b3\u7d2f\u8f9b\u82e6\u3002\u8fd9\u5bb6\u4f19\u53ea\u987e\u81ea\u5df1\u723d\uff0c\u5b8c\u5168\u4e0d\u7ba1\u8fd9\u79cd\u884c\u4e3a\u4f1a\u7ed9\u540c\u884c\u5e26\u6765\u4ec0\u4e48\u540e\u679c\uff0c\u5b9e\u5c5e\u5deb\u5e08\u4e2d\u7684\u8d25\u7c7b\uff0c\u4eba\u4eba\u5f97\u800c\u8bdb\u4e4b\u3002</p><p>\u201c\u4f60\u4e0d\u4e5f\u4e00\u6837\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5230\u5904\u52fe\u642d\u5973\u5b69\u5b50\u3002\u201d</p><p>\u201c\u600e\u4e48\u53ef\u80fd\u4e00\u6837\uff0c\u201d\u743c\u6069\u7406\u76f4\u6c14\u58ee\uff0c\u201c\u6211\u4ece\u6765\u4e0d\u52fe\u642d\u6709\u592b\u4e4b\u5987\u3002\u201d</p><p>\u201c\u8fd9\u5012\u4e5f\u662f\u3002\u201d</p><p>\u743c\u6069\u662f\u4e0d\u591f\u7528\u60c5\u4e13\u4e00\uff0c\u7ecf\u5e38\u5230\u5904\u52fe\u642d\uff0c\u4f46\u4ed6\u8fd8\u662f\u6709\u4e9b\u57fa\u672c\u539f\u5219\u7684\uff0c\u6bd4\u65b9\u8bf4\u6709\u592b\u4e4b\u5987\u5c31\u4e0d\u5728\u5176\u72e9\u730e\u8303\u56f4\u4e4b\u5185\uff0c\u4e3b\u8981\u662f\u56e0\u4e3a\u8fd9\u6837\u592a\u9ebb\u70e6\uff0c\u5bb9\u6613\u88ab\u4eba\u8ffd\u6740\uff0c\u9a6c\u4f26\u5c31\u662f\u4e00\u4e2a\u5178\u578b\u7684\u53cd\u9762\u6559\u6750\u3002\u4ed6\u81ea\u5df1\u5012\u6ca1\u4e8b\uff0c\u5374\u8fde\u7d2f\u5f97\u9b54\u6cd5\u5b66\u9662\u7684\u5e08\u751f\u4eec\u5168\u88ab\u9001\u4e0a\u4e86\u706b\u5211\u67b6\u3002</p><p>\u201c\u522b\u4fe1\u4ed6\uff0c\u827e\u5f25\u8587\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4ed6\u53ea\u662f\u6ca1\u78b0\u5230\u4e2d\u610f\u7684\u7f62\u4e86\uff0c\u771f\u8981\u662f\u9047\u5230\u770b\u5f97\u4e0a\u7684\uff0c\u5c31\u4e0d\u4f1a\u8bf4\u8fd9\u79cd\u8bdd\u4e86\u3002\u201d</p><p>\u201c\u600e\u4e48\u53ef\u80fd\uff0c\u6211\u8fd9\u4e48\u6709\u539f\u5219\u7684\u4eba\u2026\u2026\u4f60\u4eec\u7b11\u4ec0\u4e48\uff1f\u6211\u771f\u7684\u662f\u5f88\u6709\u539f\u5219\u7684\u3002\u201d</p><p>\u201c\u77e5\u9053\u77e5\u9053\uff0c\u4f60\u4e00\u5411\u90fd\u5f88\u6709\u539f\u5219\u2014\u2014\u4f60\u7684\u539f\u5219\u5c31\u662f\u770b\u89c1\u7f8e\u5973\u5c31\u7edd\u4e0d\u653e\u8fc7\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u719f\u5f52\u719f\uff0c\u4f60\u4e71\u8bf4\u8bdd\u6211\u4e00\u6837\u544a\u4f60\u8bfd\u8c24\u554a\u3002\u201d</p><p>\u4e00\u8def\u8bf4\u7b11\uff0c\u65c5\u9014\u603b\u7b97\u4e0d\u662f\u90a3\u4e48\u96be\u71ac\u3002\u5176\u5b9e\u6885\u83f2\u65af\u4e4b\u6240\u4ee5\u575a\u6301\u8981\u8d70\u8fd9\u79cd\u4e61\u95f4\u9053\u8def\uff0c\u9664\u4e86\u907f\u5f00\u9ebb\u70e6\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u5f88\u91cd\u8981\u7684\u539f\u56e0\uff0c\u5c31\u662f\u201c\u953b\u70bc\u201d\u3002</p><p>\u4ece\u53f6\u5f18\u51fa\u53d1\u5f00\u59cb\uff0c\u4e00\u76f4\u5230\u767b\u9646\u8f9b\u5df4\u57ce\u4e4b\u524d\uff0c\u8fd9\u6bb5\u65f6\u95f4\u6885\u83f2\u65af\u90fd\u5728\u5f88\u8ba4\u771f\u5730\u76d1\u7763\u743c\u6069\u52aa\u529b\u953b\u70bc\uff0c\u5176\u4e2d\u4e00\u4e2a\u91cd\u8981\u5185\u5bb9\u5c31\u662f\u6bcf\u5929\u7684\u5251\u672f\u5bf9\u6218\u3002\u6700\u5f00\u59cb\u7684\u65f6\u5019\u743c\u6069\u767e\u822c\u4e0d\u613f\uff0c\u575a\u51b3\u62b5\u6297\uff0c\u5c31\u5dee\u6ca1\u6709\u8db4\u5728\u5730\u4e0a\u800d\u8d56\u4e86\uff0c\u56e0\u4e3a\u6885\u83f2\u65af\u5df2\u7ecf\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u4ed6\u5728\u8fd9\u65b9\u9762\u7684\u8d44\u8d28\u4e5f\u5c31\u90a3\u4e48\u9ad8\uff0c\u600e\u4e48\u7ec3\u90fd\u4e0d\u53ef\u80fd\u6709\u6240\u6210\u5c31\u3002\u65e2\u7136\u5982\u6b64\uff0c\u4f55\u5fc5\u767d\u767d\u6328\u6253\uff0c\u4ed6\u53c8\u6ca1\u6709\u53d7\u8650\u503e\u5411\u3002\u867d\u8bf4\u8ba9\u6885\u83f2\u65af\u63cd\u4e00\u987f\u51fa\u51fa\u6c14\uff0c\u6709\u5229\u4e8e\u5bb6\u5ead\u56e2\u7ed3\uff0c\u4f46\u5929\u5929\u88ab\u6241\u5c31\u4e0d\u597d\u4e86\uff0c\u7537\u4eba\u4e5f\u662f\u8981\u9762\u5b50\u7684\u3002</p><p>\u4e0d\u8fc7\u6885\u83f2\u65af\u8fd8\u662f\u6bd4\u8f83\u901a\u60c5\u8fbe\u7406\u7684\uff0c\u8868\u793a\u65e2\u7136\u5355\u7eaf\u7684\u5251\u672f\u7ec3\u4e60\u4f60\u4e0d\u559c\u6b22\uff0c\u90a3\u4e48\u5c31\u653e\u5f00\u9650\u5236\uff0c\u81ea\u7531\u683c\u6597\u3002\u743c\u6069\u53ef\u4ee5\u4f7f\u7528\u9b54\u6cd5\uff0c\u5f53\u7136\u5979\u4e5f\u53ef\u4ee5\u52a8\u7528\u5723\u529b\uff0c\u5927\u5bb6\u516c\u5e73\u8f83\u91cf\u3002</p><p>\u743c\u6069\u8fd8\u662f\u6709\u4e9b\u72b9\u8c6b\uff0c\u867d\u7136\u6ca1\u6253\u8fc7\uff0c\u4f46\u4ed6\u81ea\u5df1\u6e05\u695a\uff0c\u771f\u8981\u516c\u5e73\u5bf9\u51b3\u7684\u8bdd\uff0c\u4f30\u8ba1\u4e5f\u4e0d\u662f\u6885\u83f2\u65af\u7684\u5bf9\u624b\u3002\u4e0d\u8fc7\u5c11\u5973\u63a5\u4e0b\u6765\u7684\u4e00\u53e5\u8bdd\uff0c\u7acb\u523b\u8bf4\u670d\u4e86\u4ed6\u3002</p><p>\u201c\u4f60\u4e0d\u662f\u4e00\u76f4\u5e0c\u671b\u6211\u80fd\u63a5\u53d7\u4f60\u6709\u522b\u7684\u5973\u4eba\u5417\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4f60\u82e5\u80fd\u6253\u8d62\u6211\uff0c\u6211\u5c31\u63a5\u53d7\u3002\u4f60\u6bcf\u8d62\u4e00\u6b21\uff0c\u6211\u5c31\u591a\u63a5\u53d7\u4e00\u4e2a\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u201d\u5c11\u5973\u7684\u5634\u89d2\u4e0a\u7fd8\uff0c\u201c\u6211\u8bf4\u8bdd\u5411\u6765\u7b97\u6570\uff0c\u548c\u67d0\u4e2a\u7f3a\u4e4f\u4fe1\u8a89\u7684\u5bb6\u4f19\u53ef\u4e0d\u4e00\u6837\u3002\u201d</p><p>\u743c\u6069\u7684\u79ef\u6781\u6027\u7acb\u523b\u9ad8\u6da8\u5230\u9876\u70b9\uff0c\u5174\u51b2\u51b2\u5730\u5c31\u5f00\u59cb\u51b3\u6597\uff0c\u5728\u4ed6\u60f3\u6765\uff0c\u8fd9\u4e2a\u8d4c\u7ea6\u5b8c\u5168\u6709\u5229\u65e0\u5bb3\uff0c\u6253\u8f93\u4e86\u53cd\u6b63\u4e5f\u6ca1\u4ec0\u4e48\u60e9\u7f5a\uff0c\u4fa5\u5e78\u6253\u8d62\u4e00\u6b21\u5c31\u53ef\u4ee5\u591a\u4e00\u4e2a\u540e\u5bab\u540d\u989d\uff0c\u5b9e\u5728\u662f\u518d\u597d\u4e0d\u8fc7\u3002\u53ef\u60dc\u7406\u60f3\u5f88\u4e30\u6ee1\uff0c\u73b0\u5b9e\u5f88\u9aa8\u611f\uff0c\u5728\u6d77\u4e0a\u7684\u65f6\u5019\uff0c\u4ed6\u63a5\u8fde\u6311\u6218\u4e86\u4e09\u5341\u4e5d\u6b21\uff0c\u51e0\u4e4e\u6bcf\u6b21\u90fd\u8f93\u5f97\u6beb\u65e0\u60ac\u5ff5\uff0c\u5076\u5c14\u6709\u51e0\u6b21\uff0c\u660e\u660e\u773c\u770b\u5df2\u7ecf\u5360\u4e86\u4e0a\u98ce\uff0c\u7ed3\u679c\u9677\u5165\u8d25\u52bf\u7684\u6885\u83f2\u65af\u7a81\u7136\u4e00\u4e2a\u7edd\u5730\u53cd\u51fb\uff0c\u7136\u540e\u743c\u6069\u5c31\u53c8\u88ab\u6253\u5012\u4e86\u3002</p><p>\u201c\u8fd9\u4e0d\u884c\uff0c\u4f60\u800d\u8d56\uff0c\u201d\u743c\u6069\u62b1\u6028\uff0c\u201c\u4f60\u6709\u51a5\u6b65\u5728\u624b\uff0c\u6211\u6839\u672c\u4e0d\u53ef\u80fd\u8d62\u3002\u201d</p><p>\u5deb\u5e08\u6700\u6015\u88ab\u6b66\u8005\u8fd1\u8eab\uff0c\u504f\u504f\u6885\u83f2\u65af\u7684\u201c\u51a5\u6b65\u201d\u5c31\u662f\u4e00\u79cd\u77ac\u95f4\u79fb\u52a8\u5230\u76ee\u6807\u8eab\u540e\u80cc\u523a\u7684\u80fd\u529b\uff0c\u800c\u4e14\u77ac\u79fb\u65f6\u65e0\u58f0\u65e0\u606f\uff0c\u6beb\u65e0\u5f81\u5146\uff0c\u8fde\u8428\u739b\u65af\u7279\u8fd9\u79cd\u5927\u5deb\u5e08\u90fd\u7740\u4e86\u9053\uff0c\u743c\u6069\u66f4\u52a0\u6ca1\u6cd5\u62b5\u6321\uff0c\u5373\u4fbf\u662f\u63d0\u524d\u505a\u51c6\u5907\u4e5f\u6ca1\u7528\u3002\u6885\u83f2\u65af\u4e00\u5251\u780d\u6765\uff0c\u4ec0\u4e48\u9632\u5fa1\u9b54\u6cd5\u90fd\u9876\u4e0d\u4f4f\u3002</p><p>\u201c\u672c\u6765\u5c31\u662f\u5404\u5c3d\u6240\u80fd\uff0c\u516c\u5e73\u5bf9\u51b3\uff0c\u4ec0\u4e48\u53eb\u800d\u8d56\uff0c\u201d\u5c11\u5973\u7b11\u541f\u541f\u5730\u63d0\u7740\u5251\uff0c\u201c\u8f93\u4e0d\u8d77\u5c31\u522b\u73a9\u4e86\uff1f\u201d</p><p>\u201c\u8c01\u8f93\u4e0d\u8d77\u4e86\uff0c\u201d\u743c\u6069\u722c\u8d77\u6765\uff0c\u201c\u518d\u6765\uff01\u201d</p><p>\u7136\u540e\u4ed6\u53c8\u88ab\u6253\u5012\u4e86\u3002</p><p>\u5f00\u59cb\u7684\u65f6\u5019\u53ea\u662f\u4e24\u4e2a\u4eba\u8f83\u91cf\uff0c\u540e\u6765\u73ca\u5609\u6709\u65f6\u5019\u8fc7\u6765\u65c1\u89c2\uff0c\u518d\u540e\u6765\u51db\u3001\u838e\u73de\u514b\u4e5f\u8dd1\u6765\u53c2\u89c2\uff0c\u6700\u540e\u8fde\u7ef4\u82e5\u62c9\u90fd\u8fc7\u6765\u4e86\u3002\u4e8e\u662f\u8239\u4e0a\u7684\u6c34\u624b\u7ecf\u5e38\u770b\u5230\u5982\u4e0b\u573a\u666f\uff1a\u4e00\u4e2a\u9ed1\u53d1\u7684\u5c11\u5e74\u5c61\u8d25\u5c61\u6218\uff0c\u9532\u800c\u4e0d\u820d\u5730\u5411\u53e6\u4e00\u4f4d\u91d1\u53d1\u5c11\u5973\u6311\u6218\uff0c\u7136\u540e\u6bcf\u6b21\u90fd\u88ab\u6253\u8db4\u4e0b\uff0c\u800c\u5728\u4ed6\u4eec\u5468\u56f4\uff0c\u4e00\u7fa4\u6f02\u4eae\u5973\u5b69\u5b50\u6253\u7740\u906e\u9633\u4f1e\uff0c\u5206\u4eab\u7740\u51b0\u6fc0\u51cc\u548c\u96f6\u98df\uff0c\u54c1\u5934\u8bba\u8db3\uff0c\u6307\u6307\u70b9\u70b9\uff0c\u4e0d\u65f6\u53d1\u51fa\u6b22\u5feb\u7684\u7b11\u58f0\uff0c\u573a\u9762\u5341\u5206\u4e4b\u548c\u8c10\u3002</p><p>\u201c\u505a\u7537\u4eba\u771f\u4e0d\u5bb9\u6613\u554a\u3002\u201d\u4e00\u540d\u6c34\u624b\u7531\u8877\u5730\u611f\u53f9\u3002</p><p>\u8fd9\u79cd\u573a\u666f\u6bcf\u5929\u90fd\u53d1\u751f\uff0c\u4e00\u76f4\u6301\u7eed\u5230\u767b\u9646\u8f9b\u5df4\u57ce\u624d\u6682\u65f6\u505c\u6b62\u3002\u6bd5\u7adf\u4f4f\u5728\u57ce\u4e2d\u65c5\u5e97\u91cc\uff0c\u4e0d\u597d\u5927\u5f20\u65d7\u9f13\u5730\u51b3\u6597\uff0c\u743c\u6069\u7684\u201c\u5deb\u5e08\u201d\u8eab\u4efd\u4e5f\u4e0d\u65b9\u4fbf\u663e\u9732\uff0c\u514d\u5f97\u60f9\u6765\u9ebb\u70e6\u3002\u73b0\u5728\u79bb\u5f00\u4e86\u57ce\u5e02\uff0c\u6bcf\u5929\u4f4f\u5728\u8352\u90ca\u91ce\u5916\uff0c\u505c\u64ad\u4e86\u51e0\u5929\u7684\u8282\u76ee\u53c8\u53ef\u4ee5\u7ee7\u7eed\u4e0a\u6f14\u4e86\u3002</p><p>\u201c\u743c\u6069\uff0c\u4eca\u5929\u4e00\u5b9a\u8981\u52a0\u6cb9\u54e6\uff0c\u201d\u51db\u4e3e\u7740\u5c0f\u62f3\u5934\u7ed9\u4ed6\u52a9\u5a01\uff0c\u201c\u81f3\u5c11\u8981\u575a\u6301\u4e09\u5206\u949f\u624d\u80fd\u5012\u4e0b\u3002\u201d</p><p>\u201c\u2026\u2026\u8c22\u8c22\u652f\u6301\uff0c\u6211\u4f1a\u52aa\u529b\u7684\u3002\u201d</p><p>\u201c\u6211\u53ef\u662f\u548c\u5854\u59d0\u59d0\u6253\u4e86\u8d4c\uff0c\u5979\u8d4c\u4f60\u575a\u6301\u4e0d\u5230\u4e09\u5206\u949f\uff0c\u6211\u628a\u6240\u6709\u7684\u96f6\u98df\u90fd\u538b\u4e0a\u53bb\u4e86\uff0c\u4f60\u8981\u662f\u5bb3\u6211\u8f93\u4e86\uff0c\u5c31\u8981\u966a\u6211\u53cc\u4efd\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u5c31\u7b49\u7740\u77a7\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4eca\u5929\u5c31\u662f\u6211\u53cd\u8d25\u4e3a\u80dc\u7684\u8d77\u70b9\u3002\u201d</p><p>\u201c\u5c0f\u5f1f\u4f60\u8fd9\u4e48\u6709\u4fe1\u5fc3\uff1f\u201d\u73ca\u5609\u7b11\u7740\u8d70\u8fc7\u6765\uff0c\u201c\u83ab\u975e\u65b0\u5b66\u4e86\u4ec0\u4e48\u5389\u5bb3\u7684\u6cd5\u672f\u3002\u201d</p><p>\u201c\u59d0\u59d0\u4f60\u62ed\u76ee\u4ee5\u5f85\u5c31\u662f\u4e86\u3002\u201d</p><p>\u743c\u6069\u4fe1\u5fc3\u6ee1\u6ee1\u5730\u4e0a\u573a\uff0c\u7136\u540e\u4e0d\u5230\u4e24\u5206\u949f\uff0c\u4ed6\u5c31\u88ab\u6885\u83f2\u65af\u4e00\u5251\u6467\u7834\u6240\u6709\u9b54\u6cd5\u62a4\u76fe\uff0c\u6253\u51fa\u4e86\u573a\u5916\u3002</p><p>\u201c\u827e\u5f25\u8587\u4f60\u4f5c\u5f0a\uff01\u201d\u743c\u6069\u6c14\u6025\u8d25\u574f\u3002</p><p>\u201c\u6211\u600e\u4e48\u4f5c\u5f0a\u4e86\uff1f\u201d</p><p>\u201c\u4f60\u4e4b\u524d\u660e\u660e\u8bf4\u8fc7\uff0c\u4f60\u4e0d\u80fd\u5728\u4e1c\u57df\u4f7f\u7528\u4efb\u4f55\u795e\u672f\u548c\u795e\u8d50\u529b\u91cf\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u90a3\u4f60\u521a\u624d\u7684\u7834\u90aa\u65a9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u8fd9\u91cc\u5e76\u4e0d\u7b97\u662f\u8bf8\u795e\u534f\u8bae\u4e2d\u6240\u8bf4\u7684\u2018\u4e1c\u57df\u2019\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u6839\u636e\u4e2d\u571f\u8bf8\u795e\u4e0e\u4e1c\u57df\u795e\u738b\u7684\u53e4\u8001\u534f\u8bae\uff0c\u6885\u83f2\u65af\u88ab\u7981\u6b62\u5728\u4e1c\u57df\u4f7f\u7528\u4efb\u4f55\u795e\u672f\u6216\u8005\u795e\u8d50\u529b\u91cf\uff0c\u743c\u6069\u539f\u672c\u4ee5\u4e3a\u5982\u6b64\u4e00\u6765\uff0c\u4ed6\u5c31\u6709\u4e86\u7ffb\u8eab\u7684\u673a\u4f1a\u3002\u8c01\u77e5\u9053\u539f\u6765\u534f\u8bae\u4e2d\u6240\u5b9a\u4e49\u7684\u4e1c\u57df\uff0c\u6307\u7684\u662f\u201c\u795e\u738b\u7edf\u6cbb\u7684\u533a\u57df\u201d\uff0c\u800c\u5f7b\u68ee\u5854\u65e9\u5c31\u6ca1\u6709\u795e\u738b\u4e86\u3002</p><p>\u201c\u9664\u975e\u73b0\u5728\u6709\u4e00\u4f4d\u795e\u738b\u7a81\u7136\u964d\u4e34\u5f7b\u68ee\u5854\uff0c\u5426\u5219\u6211\u5c31\u4e0d\u53d7\u534f\u8bae\u7684\u9650\u5236\uff0c\u201d\u6885\u83f2\u65af\u7b11\u5f97\u5f88\u5f00\u5fc3\uff0c\u201c\u8c01\u8ba9\u4f60\u4e0d\u95ee\u6e05\u695a\u534f\u8bae\u7684\u5185\u5bb9\u3002\u201d</p><p>\u201c\u6587\u5b57\u6e38\u620f\u771f\u662f\u5bb3\u6b7b\u4eba\u554a\u3002\u201d</p><p>\u201c\u8fd9\u4e0d\u662f\u633a\u597d\u5417\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u53ea\u8981\u6211\u4eec\u8fd8\u6ca1\u8fdb\u5165\u6069\u745f\uff0c\u827e\u5f25\u8587\u7684\u529b\u91cf\u5c31\u4e0d\u4f1a\u53d7\u5230\u9650\u5236\u3002\u201d</p><p>\u201c\u5176\u5b9e\u5c31\u7b97\u5728\u6069\u745f\u4e5f\u6ca1\u4e8b\uff0c\u201d\u6885\u83f2\u65af\u7b11\u7740\u8bf4\uff0c\u201c\u6069\u745f\u6700\u540e\u4e00\u4f4d\u795e\u738b\u5409\u52d2\u4eca\uff0c\u4e5f\u5df2\u7ecf\u5728\u5341\u516d\u5e74\u524d\u9668\u843d\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u662f\u8bf4\u4ed6\u8fd8\u6709\u4e00\u4f4d\u738b\u540e\uff0c\u4ee3\u4e3a\u6267\u638c\u738b\u6743\u5417\uff1f\u201d</p><p>\u201c\u8bf8\u795e\u534f\u8bae\u4e2d\u7684\u7ea6\u5b9a\u5f88\u6e05\u695a\uff0c\u5fc5\u987b\u662f\u795e\u738b\u672c\u4eba\uff0c\u738b\u540e\u4ec0\u4e48\u7684\u90fd\u662f\u4e0d\u4f5c\u6570\u7684\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u800c\u4e14\u8fd9\u5341\u51e0\u5e74\u6765\uff0c\u4e5f\u6709\u51e0\u4f4d\u4e2d\u571f\u7684\u7267\u5e08\u8fdb\u5165\u8fc7\u6069\u745f\uff0c\u4ed6\u4eec\u90fd\u786e\u8ba4\uff0c\u9650\u5236\u5df2\u7ecf\u88ab\u89e3\u9664\u4e86\u3002\u201d</p><p>\u201c\u5728\u53f6\u5f18\u7684\u65f6\u5019\uff0c\u4f60\u8fd8\u8bf4\u4f60\u4e00\u65e6\u8fdb\u5165\u4e1c\u57df\u5c31\u4f1a\u529b\u91cf\u5927\u51cf\uff0c\u6240\u4ee5\u8981\u6211\u953b\u70bc\u5251\u672f\uff0c\u201d\u743c\u6069\u6cf1\u6cf1\u5730\u8bf4\uff0c\u201c\u641e\u4e86\u534a\u5929\uff0c\u539f\u6765\u53ea\u662f\u627e\u4e2a\u501f\u53e3\u63cd\u6211\u3002\u201d</p><p>\u201c\u6211\u624d\u4e0d\u662f\u627e\u501f\u53e3\u2014\u2014\u6211\u8981\u63cd\u4f60\u8fd8\u9700\u8981\u627e\u501f\u53e3\u5417\uff1f\u201d\u6885\u83f2\u65af\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u5f7b\u68ee\u5854\u548c\u6069\u745f\u662f\u6ca1\u5173\u7cfb\uff0c\u4f46\u7a46\u7f55\u5462\uff1f\u201d</p><p>\u5386\u7ecf\u5343\u5e74\u5174\u8870\uff0c\u7a46\u7f55\u5e1d\u56fd\u76ee\u524d\u4ecd\u7136\u6709\u4e94\u5927\u795e\u738b\u5728\u4f4d\uff0c\u800c\u743c\u6069\u6b64\u884c\u4e00\u4e2a\u91cd\u8981\u76ee\u7684\u5730\uff1a\u7d2b\u5bb8\u6c99\u6f20\uff0c\u5c31\u662f\u96b6\u5c5e\u4e8e\u7a46\u7f55\u5883\u5185\u3002</p><p>\u4e8e\u662f\u201c\u953b\u70bc\u201d\u7ee7\u7eed\u3002</p><p>\u5c31\u8fd9\u6837\u8d70\u4e86\u4e09\u5929\uff0c\u7b2c\u56db\u5929\u7684\u4e0b\u5348\uff0c\u743c\u6069\u6b63\u548c\u51db\u6597\u5634\uff0c\u8d70\u5728\u6700\u524d\u9762\u7684\u838e\u73de\u514b\u4f20\u56de\u8baf\u606f\uff0c\u8bf4\u662f\u524d\u65b9\u770b\u89c1\u4e00\u5ea7\u5c0f\u57ce\u3002\u6885\u83f2\u65af\u62ff\u51fa\u5730\u56fe\u770b\u4e86\u770b\uff0c\u201c\u788e\u6ce2\u57ce\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8d70\u5427\uff0c\u4eca\u665a\u5c31\u5728\u788e\u6ce2\u57ce\u4f11\u606f\u597d\u4e86\u3002\u51db\uff0c\u90a3\u91cc\u4e5f\u7b97\u4f60\u5bb6\u5462\u3002\u201d</p><p>\u201c\u624d\u4e0d\u662f\uff0c\u201d\u51db\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u6211\u5bb6\u5728\u963f\u594e\u90a3\u5cf0\uff0c\u6216\u8005\u5fa1\u5b87\u5c71\uff0c\u788e\u6ce2\u57ce\u662f\u4ec0\u4e48\u9b3c\u5730\u65b9\u3002\u201d</p><p>\u6885\u83f2\u65af\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u51db\u7684\u6bcd\u4eb2\u662f\u5f7b\u68ee\u5854\u4e00\u4e2a\u5c0f\u57ce\u7684\u516c\u4e3b\uff0c\u8fd9\u4e2a\u201c\u5c0f\u57ce\u201d\u5c31\u662f\u788e\u6ce2\u57ce\uff0c\u8bf4\u662f\u201c\u57ce\u201d\uff0c\u5176\u5b9e\u5c31\u662f\u4e00\u4e2a\u5c0f\u9547\uff0c\u4f9d\u6cb3\u800c\u5efa\uff0c\u5386\u53f2\u5012\u9887\u4e3a\u60a0\u4e45\uff0c\u7edf\u8f96\u7740\u5468\u56f4\u7684\u5341\u51e0\u4e2a\u6751\u5e84\uff0c\u5728\u5f7b\u68ee\u5854\u4e5f\u53ef\u4ee5\u7b97\u662f\u4e00\u4e2a\u5c0f\u57ce\u90a6\u4e86\u3002\u5c31\u4e8b\u8bba\u4e8b\uff0c\u51db\u6bcd\u4eb2\u8fd9\u4e2a\u201c\u516c\u4e3b\u201d\u7684\u79f0\u547c\u6c34\u5206\u5f88\u5927\uff0c\u6574\u4e2a\u5f7b\u68ee\u5854\u5927\u6982\u6709\u56db\u4e94\u5341\u4e2a\u57ce\u90a6\uff0c\u6bcf\u4e2a\u57ce\u4e3b\u7684\u5973\u513f\u3001\u59d0\u59b9\u90fd\u53ef\u4ee5\u79f0\u516c\u4e3b\uff0c\u6240\u4ee5\u6574\u4e2a\u5f7b\u68ee\u5854\u5927\u6982\u6709\u51e0\u767e\u4e2a\u516c\u4e3b\uff0c\u542b\u91d1\u91cf\u4e00\u70b9\u90fd\u4e0d\u9ad8\uff0c\u5982\u679c\u662f\u8f9b\u5df4\u57ce\u3001\u7d22\u7eb3\u745e\u57ce\u3001\u9c81\u65af\u5947\u5938\u57ce\u8fd9\u79cd\u5927\u57ce\u57ce\u4e3b\u7684\u5973\u513f\uff0c\u79f0\u4e00\u58f0\u516c\u4e3b\u8fd8\u7b97\u591f\u683c\uff0c\u788e\u6ce2\u57ce\u5728\u6574\u4e2a\u5f7b\u68ee\u5854\u7684\u57ce\u90a6\u4e2d\u90fd\u5c5e\u4e8e\u6700\u5c0f\u7684\u90a3\u4e00\u7c7b\uff0c\u57ce\u4e3b\u4e4b\u5973\u4e5f\u79f0\u4e3a\u201c\u516c\u4e3b\u201d\uff0c\u7b80\u76f4\u6709\u50ed\u8d8a\u4e4b\u5acc\u3002\u4f46\u6574\u4e2a\u5f7b\u68ee\u5854\u7684\u98ce\u6c14\u5c31\u662f\u5982\u6b64\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u597d\u8bf4\u7684\u3002</p><p>\u4e0d\u8fc7\u6839\u636e\u6885\u83f2\u65af\u6574\u7406\u7684\u8d44\u6599\uff0c\u5982\u4eca\u7684\u788e\u6ce2\u57ce\u57ce\u4e3b\uff0c\u5df2\u7ecf\u4e0d\u662f\u51db\u7684\u6bcd\u7cfb\u5bb6\u65cf\u4e86\u3002\u5f7b\u68ee\u5854\u662f\u4e2a\u6218\u4e71\u4e0d\u606f\u7684\u5730\u65b9\uff0c\u6bcf\u9694\u4e00\u6bb5\u65f6\u95f4\uff0c\u57ce\u4e3b\u7684\u540d\u5355\u5c31\u4f1a\u6d88\u5931\u4e00\u4e9b\u65e7\u540d\u5b57\uff0c\u6362\u4e0a\u4e00\u4e9b\u65b0\u540d\u5b57\uff0c\u53d8\u5316\u5f97\u975e\u5e38\u5feb\u3002\u5927\u7ea6\u5728\u4e94\u5e74\u524d\uff0c\u788e\u6ce2\u57ce\u88ab\u9644\u8fd1\u7684\u53e6\u4e00\u5ea7\u57ce\u5e02\u201c\u5361\u5179\u514b\u57ce\u201d\u653b\u9677\uff0c\u57ce\u4e3b\u5b9d\u5ea7\u81ea\u7136\u6362\u4e86\u65b0\u4eba\u3002\u81f3\u4e8e\u539f\u57ce\u4e3b\u5bb6\u65cf\uff0c\u6309\u7167\u5f7b\u68ee\u5854\u7684\u60ef\u4f8b\uff0c\u5e94\u8be5\u662f\u90fd\u6b7b\u5149\u4e86\u3002</p><p>\u51db\u4e5f\u77e5\u9053\u8fd9\u4ef6\u4e8b\uff0c\u4f46\u5979\u663e\u7136\u5b8c\u5168\u4e0d\u5728\u610f\uff0c\u4e1d\u6beb\u6ca1\u6709\u8bf4\u8981\u53bb\u62a5\u4ec7\u7684\u610f\u601d\u3002\u743c\u6069\u4e0d\u597d\u591a\u95ee\u51db\u7684\u5bb6\u4e8b\uff0c\u53ea\u542c\u6885\u83f2\u65af\u9690\u7ea6\u63d0\u8fc7\uff0c\u51db\u7684\u6bcd\u4eb2\u662f\u88ab\u540c\u65cf\u4f5c\u4e3a\u201c\u796d\u54c1\u201d\u732e\u7ed9\u7ea2\u9f99\u7684\uff0c\u82e5\u975e\u78b0\u5230\u7684\u8fd9\u53ea\u7ea2\u9f99\uff08\u4e5f\u5c31\u662f\u51db\u7684\u7236\u4eb2\uff09\u6bd4\u8f83\u5947\u8469\uff0c\u65e9\u5c31\u53d8\u6210\u9f99\u7684\u70b9\u5fc3\u4e86\u3002\u56e0\u6b64\u7f18\u6545\uff0c\u51db\u7684\u6bcd\u4eb2\u5bf9\u5176\u5bb6\u4eba\u96be\u514d\u6709\u6240\u6028\u6068\uff0c\u4e0e\u7ea2\u9f99\u5a5a\u540e\u4e00\u76f4\u9690\u5c45\u5728\u9f99\u7a9f\u4e4b\u4e2d\uff0c\u4ece\u6ca1\u56de\u8fc7\u5a18\u5bb6\u3002\u4ee5\u81f3\u4e8e\u51db\u7684\u5916\u7956\u7236\u7b49\u4eba\u4e00\u76f4\u90fd\u4ee5\u4e3a\u5973\u513f\u5df2\u6b7b\uff0c\u6240\u4ee5\u540e\u6765\u624d\u4f1a\u59d4\u6258\u5723\u6b66\u58eb\u53bb\u65a9\u6740\u6076\u9f99\uff0c\u4e3a\u5973\u513f\u62a5\u4ec7\u3002\u51db\u53d7\u6bcd\u4eb2\u5f71\u54cd\uff0c\u81ea\u7136\u5bf9\u5916\u7956\u7236\u7b49\u4eba\u4e5f\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u611f\u60c5\uff0c\u751a\u81f3\u4e25\u683c\u6765\u8bf4\uff0c\u5c31\u662f\u5916\u7956\u7236\u59d4\u6258\u4eba\u6765\u6740\u4e86\u5979\u7684\u7236\u4eb2\uff0c\u53cc\u65b9\u8fd8\u662f\u4ec7\u4eba\u3002\u6240\u4ee5\u788e\u6ce2\u57ce\u5982\u4f55\uff0c\u5bf9\u4e8e\u51db\u800c\u8a00\uff0c\u7684\u786e\u6ca1\u4ec0\u4e48\u6240\u8c13\uff0c\u5b8c\u5168\u7f6e\u8eab\u4e8b\u5916\u3002</p><p>\u201c\u8fc7\u4e86\u788e\u6ce2\u57ce\uff0c\u518d\u8d70\u4e24\u5929\uff0c\u5c31\u8fdb\u5165\u6069\u745f\u4e86\u3002\u201d</p><p>\u8fd9\u603b\u7b97\u662f\u4e2a\u597d\u6d88\u606f\uff0c\u8ba9\u51db\u7a0d\u5fae\u5f00\u5fc3\u4e86\u4e00\u70b9\u3002</p><p>\u4e00\u884c\u4eba\u8fdb\u4e86\u788e\u6ce2\u57ce\uff0c\u51c6\u5907\u627e\u4e86\u4e2a\u65c5\u5e97\u4f4f\u4e0b\u3002\u8fd9\u91cc\u662f\u5f7b\u68ee\u5854\u7684\u5185\u9646\uff0c\u4e0e\u8f9b\u5df4\u57ce\u8fd9\u79cd\u5927\u6d77\u6e2f\u622a\u7136\u4e0d\u540c\uff0c\u5c45\u6c11\u4e0d\u4f1a\u8bf4\u4e2d\u571f\u901a\u7528\u8bed\uff0c\u53ea\u4f1a\u8bf4\u6069\u745f\u8bed\uff0c\u90fd\u7a7f\u7740\u975e\u5e38\u4f20\u7edf\u7684\u5f7b\u68ee\u5854\u670d\u9970\uff0c\u663e\u5f97\u743c\u6069\u7b49\u4eba\u683c\u5916\u663e\u773c\u3002\u4e00\u8def\u884c\u6765\uff0c\u53d1\u73b0\u57ce\u9547\u867d\u5c0f\uff0c\u4eba\u53e3\u5012\u4e0d\u5c11\uff0c\u8857\u9053\u4e0a\u4eba\u6765\u4eba\u5f80\uff0c\u8bf4\u4e0d\u4e0a\u591a\u4e48\u7e41\u534e\uff0c\u4f46\u4e5f\u7b97\u6709\u6d3b\u529b\uff0c\u548c\u60f3\u8c61\u4e2d\u90a3\u79cd\u504f\u50fb\u5c0f\u57ce\u7684\u611f\u89c9\u4e0d\u4e00\u6837\u3002\u7531\u4e8e\u4e0d\u5904\u4e8e\u4ea4\u901a\u8981\u9053\uff0c\u4e5f\u4e0d\u662f\u4ec0\u4e48\u98ce\u666f\u540d\u80dc\uff0c\u788e\u6ce2\u57ce\u5f88\u5c11\u6709\u5916\u6765\u6e38\u5ba2\uff0c\u6574\u4e2a\u57ce\u4e2d\u53ea\u6709\u4e00\u95f4\u65c5\u5e97\uff0c\u627e\u4e86\u534a\u5929\u624d\u627e\u5230\uff0c\u5730\u65b9\u5f88\u504f\uff0c\u4e0d\u8fc7\u8fd8\u7b97\u6574\u6d01\uff0c\u8001\u677f\u662f\u4e2a\u5973\u4eba\uff0c\u5355\u8eab\u5be1\u5c45\uff0c\u770b\u8d77\u6765\u5e74\u7eaa\u4e0d\u5c0f\u4e86\uff0c\u8eab\u6750\u77ee\u80d6\uff0c\u8170\u8eab\u5706\u6eda\uff0c\u4ee4\u4eba\u6000\u7591\u5176\u6709\u77ee\u4eba\u8840\u7edf\uff0c\u5f88\u70ed\u60c5\uff0c\u9887\u4e3a\u5065\u8c08\uff0c\u800c\u4e14\u4f1a\u8bf4\u4e2d\u571f\u901a\u7528\u8bed\uff0c\u4e00\u756a\u5fd9\u788c\u5c06\u6240\u6709\u4eba\u5b89\u987f\u597d\uff0c\u4fbf\u548c\u743c\u6069\u95f2\u804a\u8d77\u6765\u3002</p><p>\u201c\u4f60\u4eec\u90fd\u662f\u4f63\u5175\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6253\u7b97\u53bb\u6069\u745f\u53c2\u519b\uff1f\u90a3\u53ef\u5f97\u5feb\u4e00\u70b9\u4e86\uff0c\u542c\u8bf4\u795e\u59ec\u6bbf\u4e0b\u548c\u90a3\u4e9b\u7a46\u7f55\u4eba\u5df2\u7ecf\u5728\u5251\u6cb3\u5bf9\u5cd9\u4e86\u5f88\u4e45\uff0c\u8bf4\u4e0d\u5b9a\u73b0\u5728\u5df2\u7ecf\u5f00\u59cb\u51b3\u6218\u4e86\u3002\u201d</p><p>\u201c\u795e\u59ec\uff1f\u201d\u743c\u6069\u4e0d\u660e\u767d\u8fd9\u4e2a\u8bcd\u7684\u610f\u601d\u3002</p><p>\u201c\u55ef\uff1f\u4f60\u4e0d\u662f\u53bb\u6295\u6548\u795e\u59ec\u6bbf\u4e0b\u7684\u5417\uff1f\u201d\u8001\u677f\u4f3c\u4e4e\u6709\u4e9b\u8be7\u5f02\uff0c\u201c\u4eca\u5e74\u5df2\u7ecf\u6709\u5341\u51e0\u62e8\u4f63\u5175\u4ece\u6211\u8fd9\u91cc\u7ecf\u8fc7\uff0c\u53bb\u6069\u745f\u53c2\u519b\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u4eec\u4e5f\u662f\u5462\u3002\u201d</p><p>\u201c\u6211\u4eec\u53ea\u662f\u6253\u7b97\u53bb\u6069\u745f\u65c5\u6e38\uff0c\u770b\u770b\u98ce\u571f\u4eba\u60c5\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6253\u4ed7\u4ec0\u4e48\u7684\uff0c\u8fd8\u662f\u5c3d\u91cf\u8fdc\u8fdc\u907f\u5f00\u4e3a\u597d\u3002\u60a8\u521a\u624d\u63d0\u5230\u2018\u795e\u59ec\u2019\uff0c\u90a3\u662f\u8c01\u554a\uff1f\u201d</p><p>\u201c\u5c31\u662f\u6069\u745f\u7684\u738b\u540e\u6bbf\u4e0b\u561b\uff0c\u201d\u8001\u677f\u8bf4\uff0c\u201c\u5979\u662f\u795e\u738b\u965b\u4e0b\u7684\u59bb\u5b50\uff0c\u4f60\u4eec\u5916\u4e61\u4eba\u4e60\u60ef\u53eb\u738b\u540e\uff0c\u5176\u5b9e\u8fd9\u662f\u4e0d\u5bf9\u7684\uff1b\u51e1\u4eba\u56fd\u738b\u7684\u59bb\u5b50\u662f\u738b\u540e\uff0c\u795e\u738b\u965b\u4e0b\u7684\u59bb\u5b50\u5c31\u662f\u795e\u59ec\u4e86\u3002\u201d</p><p>\u201c\u54e6\uff0c\u201d\u743c\u6069\u604d\u7136\u5927\u609f\uff0c\u201c\u539f\u6765\u662f\u5979\u3002\u201d</p><p>\u4ed6\u5728\u8def\u4e0a\u5df2\u7ecf\u6076\u8865\u8fc7\u8d44\u6599\uff0c\u77e5\u9053\u6069\u745f\u5982\u4eca\u6ca1\u6709\u795e\u738b\u5728\u4f4d\uff0c\u90a3\u4f4d\u201c\u795e\u59ec\u201d\u5c31\u662f\u5b9e\u9645\u4e0a\u7684\u541b\u4e3b\uff0c\u8fd9\u5341\u51e0\u5e74\u6765\u4e00\u76f4\u81f4\u529b\u4e8e\u56e2\u7ed3\u6574\u5408\u56fd\u5185\u52bf\u529b\uff0c\u5bf9\u6297\u7a46\u7f55\u745e\u5fb7\u4eba\u7684\u4fb5\u7565\u91ce\u5fc3\uff0c\u4e0d\u8fc7\u8d44\u6599\u91cc\u5012\u662f\u6ca1\u63d0\u5230\u6709\u201c\u5916\u4e61\u4eba\u201d\u8dd1\u53bb\u6069\u745f\u6295\u5954\u5176\u9ebe\u4e0b\u3002\u6709\u5173\u8fd9\u4f4d\u795e\u59ec\u6bbf\u4e0b\u7684\u4f20\u95fb\u5f88\u591a\uff0c\u6bd4\u5982\u8bf4\u5979\u7f8e\u4e3d\u7edd\u4f26\uff0c\u4efb\u4f55\u7537\u4eba\u4e00\u89c1\u90fd\u4f1a\u4e3a\u5176\u9b45\u529b\u6240\u4fd8\u83b7\uff0c\u6210\u4e3a\u5fe0\u5b9e\u7684\u88d9\u4e0b\u4e4b\u81e3\uff1b\u6bd4\u5982\u8bf4\u5979\u5341\u516d\u5c81\u65f6\u4fbf\u8273\u540d\u8fdc\u64ad\uff0c\u795e\u738b\u5409\u52d2\u4eca\u8981\u5a36\u5979\u4e3a\u59bb\uff0c\u5979\u5374\u63d0\u51fa\u201c\u8981\u4e3a\u5979\u5efa\u9020\u4e00\u5ea7\u7a7a\u4e2d\u7684\u82b1\u56ed\u5c45\u4f4f\u201d\uff0c\u5979\u65b9\u80af\u51fa\u5ac1\uff0c\u6700\u540e\u5409\u52d2\u4eca\u65bd\u5c55\u795e\u529b\uff0c\u5f53\u771f\u5efa\u8d77\u4e00\u5ea7\u7a7a\u4e2d\u82b1\u56ed\uff0c\u65b9\u624d\u6210\u529f\u8fce\u5a36\u7f8e\u4eba\uff1b\u6bd4\u5982\u8bf4\u5979\u591a\u624d\u591a\u827a\uff0c\u867d\u4e3a\u5973\u5b50\u4e4b\u8eab\uff0c\u5374\u901a\u519b\u4e8b\uff0c\u64c5\u53d1\u660e\uff0c\u80fd\u591f\u620e\u88c5\u4e0a\u9635\uff0c\u5728\u6218\u573a\u4e0a\u6240\u5411\u62ab\u9761\u3002\u8fd8\u6709\u4ef6\u4e8b\u503c\u5f97\u4e00\u63d0\uff0c\u5c31\u662f\u636e\u8bf4\u8fd9\u4f4d\u795e\u59ec\u7cbe\u901a\u6613\u5bb9\u4e4b\u672f\uff0c\u80fd\u628a\u4eba\u7c7b\u5316\u5986\u6210\u4e00\u53ea\u5730\u7cbe\uff0c\u582a\u79f0\u795e\u4e4e\u5176\u6280\u3002\u4f20\u95fb\u5982\u6b64\u5938\u5f20\uff0c\u4e0d\u514d\u8ba9\u743c\u6069\u597d\u5947\uff0c\u5982\u679c\u6709\u673a\u4f1a\u7684\u8bdd\uff0c\u4ed6\u5012\u662f\u5f88\u60f3\u89c1\u8bc6\u89c1\u8bc6\u8fd9\u4f4d\u7edd\u4e16\u7f8e\u4eba\u3002\u4e0d\u8fc7\u82e5\u8981\u4ed6\u8dd1\u53bb\u53c2\u519b\uff0c\u90a3\u5c31\u656c\u8c22\u4e0d\u654f\u4e86\u3002</p><p>\u8001\u677f\u5f88\u5065\u8c08\uff0c\u6216\u8bb8\u662f\u5f88\u5c11\u89c1\u5230\u5916\u4e61\u4eba\u7684\u7f18\u6545\uff0c\u62c9\u7740\u743c\u6069\u6ed4\u6ed4\u4e0d\u7edd\u8bf4\u4e86\u534a\u5929\uff0c\u5230\u4e86\u665a\u996d\u65f6\u95f4\u624d\u4f9d\u4f9d\u4e0d\u820d\u5730\u79bb\u5f00\u3002\u743c\u6069\u677e\u4e86\u53e3\u6c14\uff0c\u8d76\u5feb\u8dd1\u56de\u81ea\u5df1\u623f\u95f4\uff0c\u5728\u8d70\u5eca\u4e0a\u9047\u5230\u4e86\u838e\u73de\u514b\u3002</p><p>\u201c\u4e3b\u4eba\u771f\u5389\u5bb3\uff0c\u201d\u9b45\u9b54\u9732\u51fa\u5938\u5f20\u7684\u5d07\u62dc\u795e\u60c5\uff0c\u201c\u9b45\u529b\u6240\u53ca\uff0c\u65e0\u8fdc\u5f17\u5c4a\uff0c\u8fde\u4e2d\u5e74\u5987\u5973\u90fd\u62b5\u6321\u4e0d\u4f4f\u3002\u201d</p><p>\u201c\u95ed\u5634\uff01\u201d</p><p>\u838e\u73de\u514b\u4e56\u4e56\u95ed\u5634\uff0c\u7136\u540e\u5f00\u59cb\u6253\u624b\u52bf\uff0c\u743c\u6069\u5b8c\u5168\u770b\u4e0d\u61c2\uff0c\u53ea\u597d\u8bf4\uff1a\u201c\u6709\u4ec0\u4e48\u4e8b\u8d76\u5feb\u8bf4\u3002\u201d</p><p>\u201c\u90a3\u53ea\u9f99\uff0c\u51b0\u8679\uff0c\u5b83\u6709\u53e5\u8bdd\u8ba9\u6211\u8f6c\u544a\u4f60\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u5b83\u8bf4\u5b83\u60f3\u8d77\u6765\uff0c\u90a3\u4e2a\u57c3\u535c\u62c9\u8eab\u4e0a\u7684\u5473\u9053\uff0c\u50cf\u662f\u524d\u4e9b\u5929\u624d\u521a\u521a\u95fb\u5230\u8fc7\uff0c\u4f46\u5177\u4f53\u60f3\u4e0d\u8d77\u6765\u4e86\u3002\u201d</p><p>\u524d\u4e9b\u5929\u624d\u521a\u521a\u95fb\u5230\u8fc7\uff1f\u524d\u4e9b\u5929\u6211\u4eec\u4e00\u76f4\u90fd\u5728\u6d77\u4e0a\u6f02\u7740\u597d\u4e0d\u597d\uff0c\u4f60\u95fb\u5230\u7684\u662f\u6d77\u6c34\u7684\u54b8\u8165\u5473\u5427\u3002\u8f9b\u5df4\u57ce\u662f\u4e2a\u5927\u6d77\u6e2f\uff0c\u5c45\u6c11\u8eab\u4e0a\u5e26\u70b9\u6d77\u8165\u5473\u5f88\u6b63\u5e38\uff0c\u8fd9\u4e5f\u503c\u5f97\u62ff\u51fa\u6765\u8bf4\u4e48\uff0c\u8fd9\u9f99\u771f\u662f\u517b\u7740\u4e00\u70b9\u7528\u90fd\u6ca1\u6709\u3002</p><p>\u597d\u5427\uff0c\u597d\u50cf\u5b83\u4e5f\u4e0d\u9700\u8981\u4eba\u517b\u3002</p><p>\u665a\u996d\u4e4b\u540e\uff0c\u51db\u8868\u793a\u8981\u53bb\u5916\u9762\u6e9c\u8fbe\u6e9c\u8fbe\uff0c\u900f\u900f\u6c14\uff0c\u62c9\u7740\u6885\u83f2\u65af\u51fa\u95e8\u53bb\u4e86\u3002\u743c\u6069\u61d2\u5f97\u52a8\u5f39\uff0c\u539f\u672c\u6253\u7b97\u56de\u623f\u95f4\u4f11\u606f\uff0c\u4f46\u8f6c\u5ff5\u60f3\u4e86\u4e00\u60f3\uff0c\u201c\u59d0\u59d0\uff0c\u201d\u4ed6\u5bf9\u73ca\u5609\u8bf4\uff0c\u201c\u6211\u4eec\u4e5f\u51fa\u53bb\u8d70\u8d70\u5427\u3002\u201d</p><p>\u201c\u55ef\uff0c\u597d\u554a\u3002\u201d</p><p>\u51fa\u95e8\u7684\u65f6\u5019\uff0c\u6070\u597d\u53c8\u649e\u4e0a\u8001\u677f\uff0c\u201c\u8fd9\u4e48\u665a\u4e86\uff0c\u4f60\u4eec\u8fd8\u8981\u51fa\u53bb\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u662f\u554a\uff0c\u968f\u4fbf\u901b\u901b\u3002\u201d</p><p>\u201c\u8fd9\u91cc\u4e5f\u6ca1\u4ec0\u4e48\u597d\u901b\u7684\uff0c\u201d\u8001\u677f\u8bf4\uff0c\u201c\u4e0d\u8981\u592a\u665a\uff0c\u8bb0\u5f97\u5341\u70b9\u949f\u4e4b\u524d\u4e00\u5b9a\u8981\u56de\u6765\u3002\u201d</p><p>\u743c\u6069\u6709\u4e9b\u5947\u602a\uff0c\u201c\u4e3a\u4ec0\u4e48\uff1f\u5341\u70b9\u949f\u4ee5\u540e\u4f1a\u53d1\u751f\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d</p><p>\u201c\u4eca\u5929\u662f\u51a5\u795e\u65e5\u554a\uff0c\u4f60\u4eec\u4e0d\u77e5\u9053\uff1f\u201d\u8001\u677f\u53cd\u95ee\u3002</p><p>\u51a5\u795e\u65e5\u662f\u4ec0\u4e48\u4e1c\u897f\uff1f</p><p>\u201c\u5728\u4e1c\u57df\uff0c\u6bcf\u8fc7\u4e03\u5e74\uff0c\u4f1a\u6709\u4e00\u5e74\u683c\u5916\u7279\u6b8a\uff0c\u88ab\u79f0\u4e3a\u661f\u5e74\uff0c\u8fd9\u4e00\u5e74\u4e2d\u6709\u4e03\u5929\uff0c\u51a5\u795e\u4f1a\u6765\u51e1\u95f4\u5de1\u6e38\uff0c\u591c\u95f4\u884c\u4eba\u5982\u679c\u649e\u4e0a\u4e86\uff0c\u9b42\u9b44\u5c31\u4f1a\u88ab\u5e26\u8d70\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u8fd9\u88ab\u79f0\u4e3a\u51a5\u795e\u65e5\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u5bf9\u5bf9\uff0c\u4eca\u5929\u662f\u7b2c\u4e00\u5929\uff0c\u201d\u8001\u677f\u8bf4\uff0c\u201c\u8fd9\u4e03\u5929\u91cc\u53ef\u4e0d\u8981\u968f\u4fbf\u4e71\u8d70\uff0c\u767d\u5929\u4e5f\u5c31\u7f62\u4e86\uff0c\u592a\u9633\u843d\u5c71\u4ee5\u540e\u5c31\u5c3d\u91cf\u522b\u51fa\u95e8\uff1b\u5c31\u7b97\u8981\u665a\u4e0a\u51fa\u95e8\uff0c\u5341\u70b9\u949f\u4e4b\u524d\u4e00\u5b9a\u8981\u56de\u6765\uff0c\u5426\u5219\u5f88\u5bb9\u6613\u649e\u4e0a\u51a5\u795e\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u660e\u767d\u4e86\uff0c\u591a\u8c22\u3002\u201d</p><p>\u548c\u8001\u677f\u544a\u522b\uff0c\u743c\u6069\u770b\u7740\u73ca\u5609\uff0c\u63a9\u4e0d\u4f4f\u60ca\u8bb6\uff0c\u201c\u59d0\u59d0\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d</p><p>\u201c\u827e\u5f25\u8587\u4e0d\u662f\u627e\u4e86\u5f88\u591a\u4e1c\u57df\u7684\u8d44\u6599\u5417\uff0c\u5979\u4e00\u4e2a\u4eba\u770b\u4e0d\u8fc7\u6765\uff0c\u4f60\u53c8\u6ca1\u7a7a\uff0c\u6211\u5c31\u5e2e\u5979\u770b\u4e86\u4e00\u4e9b\uff0c\u91cc\u9762\u6070\u597d\u6709\u63d0\u5230\u51a5\u795e\u65e5\u7684\uff0c\u53ea\u662f\u6ca1\u6709\u5177\u4f53\u8bf4\u662f\u54ea\u4e03\u5929\uff0c\u6240\u4ee5\u6211\u4e0d\u77e5\u9053\u4eca\u5929\u5c31\u662f\u3002\u201d</p><p>\u201c\u6211\u8bb0\u5f97\u827e\u5f25\u8587\u597d\u50cf\u8bf4\u8fc7\uff0c\u51a5\u795e\u5e76\u975e\u795e\u738b\uff0c\u66f4\u8fd1\u4f3c\u4e8e\u4e2d\u571f\u7684\u795e\uff0c\u6267\u638c\u51a5\u754c\uff0c\u5374\u5e76\u4e0d\u80fd\u6765\u5230\u51e1\u95f4\u5427\u3002\u201d</p><p>\u201c\u90a3\u5c31\u4e0d\u6e05\u695a\u4e86\u3002\u201d</p><p>\u51a5\u795e\u4ec0\u4e48\u7684\uff0c\u743c\u6069\u4e5f\u4e0d\u662f\u5f88\u5728\u610f\uff0c\u4e0d\u8fc7\u7a33\u59a5\u8d77\u89c1\uff0c\u4ed6\u8fd8\u662f\u6253\u7b97\u5341\u70b9\u949f\u4e4b\u524d\u5c31\u8d76\u56de\u6765\u3002\u5c31\u5f53\u5165\u4e61\u968f\u4fd7\u597d\u4e86\uff0c\u514d\u5f97\u8282\u5916\u751f\u679d\uff0c\u4e07\u4e00\u60f9\u51fa\u4ec0\u4e48\u9ebb\u70e6\uff0c\u53cd\u4e3a\u4e0d\u7f8e\u3002</p><p>\u4e24\u4eba\u51fa\u4e86\u65c5\u5e97\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u660e\u786e\u76ee\u6807\uff0c\u53ea\u662f\u4fe1\u6b65\u95f2\u6e38\uff0c\u968f\u53e3\u804a\u5929\uff0c\u770b\u770b\u6708\u5149\u4e0b\u7684\u5c0f\u57ce\u591c\u666f\u3002\u4e0e\u8f9b\u5df4\u57ce\u4e0d\u540c\uff0c\u788e\u6ce2\u57ce\u7684\u591c\u665a\u5b89\u5b81\u9759\u8c27\uff0c\u8def\u4e0a\u884c\u4eba\u5be5\u5be5\uff0c\u9664\u4e86\u4e00\u4e24\u5bb6\u6742\u8d27\u5e97\u4e4b\u5916\uff0c\u4e5f\u6ca1\u6709\u54ea\u5ea7\u623f\u5b50\u8fd8\u5f00\u7740\u95e8\uff0c\u6240\u6709\u4eba\u90fd\u5f85\u5728\u81ea\u5df1\u5bb6\u4e2d\u3002\u743c\u6069\u548c\u73ca\u5609\u7275\u7740\u624b\uff0c\u6cbf\u7740\u8857\u9053\u8d70\u4e86\u5927\u7ea6\u534a\u4e2a\u5c0f\u65f6\uff0c\u4e0d\u77e5\u4e0d\u89c9\u5230\u4e86\u57ce\u5899\u8fb9\uff0c\u5899\u5916\u5c31\u662f\u788e\u6ce2\u6cb3\uff0c\u6d41\u6c34\u58f0\u968f\u7740\u591c\u98ce\u5439\u6765\uff0c\u6709\u4e00\u79cd\u683c\u5916\u7684\u97f5\u5f8b\u611f\u3002</p><p>\u201c\u6211\u4eec\u597d\u50cf\u5f88\u4e45\u6ca1\u6709\u8fd9\u6837\u5355\u72ec\u51fa\u6765\u8d70\u8d70\u4e86\u3002\u201d\u73ca\u5609\u8bf4\u3002</p><p>\u201c\u55ef\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4ee5\u540e\u6211\u4f1a\u5c3d\u91cf\u591a\u966a\u59d0\u59d0\u7684\u3002\u201d</p><p>\u201c\u4f60\u624d\u6ca1\u7a7a\u5462\uff0c\u201d\u73ca\u5609\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u73b0\u5728\u8981\u966a\u7684\u4eba\u5c31\u5df2\u7ecf\u5f88\u591a\u4e86\uff0c\u4ee5\u540e\u5e94\u8be5\u8fd8\u4f1a\u66f4\u591a\u3002\u5230\u65f6\u5019\u8bf4\u4e0d\u5b9a\u5bb6\u91cc\u6709\u4e0a\u767e\u4e2a\u5973\u5b69\u5b50\uff0c\u4e00\u5929\u4e00\u4e2a\uff0c\u4e5f\u5f97\u5c0f\u534a\u5e74\u624d\u80fd\u8f6e\u5230\u4e00\u6b21\uff0c\u54ea\u91cc\u8fd8\u6709\u65f6\u95f4\u6765\u966a\u6211\u3002\u201d</p><p>\u201c\u600e\u4e48\u53ef\u80fd\u4f1a\u6709\u4e0a\u767e\u4e2a\uff0c\u59d0\u59d0\u4f60\u4e5f\u592a\u5938\u5f20\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u6709\u5938\u5f20\u554a\uff0c\u6211\u5f1f\u5f1f\u5f88\u5e05\u561b\uff0c\u54ea\u4e2a\u5973\u5b69\u5b50\u4e0d\u559c\u6b22\u5462\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4e0a\u767e\u4e2a\u800c\u5df2\uff0c\u53c8\u4e0d\u7b97\u5f88\u591a\u3002\u4f60\u770b\u4f60\u8fd9\u624d\u4e24\u4e09\u5e74\uff0c\u5c31\u5df2\u7ecf\u52fe\u642d\u4e86\u5dee\u4e0d\u591a\u5341\u4e2a\u5427\u3002\u6309\u7167\u8fd9\u4e2a\u901f\u5ea6\uff0c\u518d\u8fc7\u4e8c\u5341\u5e74\u5c31\u5230\u4e00\u767e\u4e2a\u4e86\uff0c\u4e1d\u6beb\u6ca1\u6709\u96be\u5ea6\u3002\u201d</p><p>\u201c\u2026\u2026\u542c\u8d77\u6765\u786e\u5b9e\u4e0d\u96be\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6709\u59d0\u59d0\u7684\u9f13\u52b1\uff0c\u6211\u4e00\u5b9a\u4f1a\u52aa\u529b\u7684\u3002\u90a3\u4e48\u5c31\u628a\u76ee\u6807\u8ba2\u4e3a\uff1a\u56db\u5341\u5c81\u4e4b\u524d\uff0c\u540e\u5bab\u6536\u96c6\u4e00\u767e\u4eba\u2014\u2014\u59d0\u59d0\u89c9\u5f97\u5982\u4f55\uff1f\u201d</p><p>\u201c\u52a0\u6cb9\u5427\uff0c\u201d\u73ca\u5609\u7b11\u541f\u541f\u5730\u8bf4\uff0c\u201c\u6211\u662f\u4e0d\u4ecb\u610f\u5566\uff0c\u4f46\u8981\u662f\u827e\u5f25\u8587\u77e5\u9053\u4e86\u4f60\u8fd9\u4e2a\u8fdc\u5927\u7406\u60f3\uff0c\u4f1a\u4e0d\u4f1a\u6c14\u5f97\u4e00\u5251\u628a\u4f60\u780d\u6210\u4e24\u6bb5\u5462\u3002\u201d</p><p>\u201c\u4e0d\u4f1a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5979\u4f1a\u628a\u6211\u780d\u6210\u5341\u6bb5\u3002\u201d</p><p>\u59d0\u5f1f\u4fe9\u4e00\u8d77\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u201c\u597d\u5427\uff0c\u743c\u6069\uff0c\u201d\u73ca\u5609\u6536\u655b\u7b11\u5bb9\uff0c\u201c\u8bf4\u771f\u7684\uff0c\u4f60\u5230\u5e95\u600e\u4e48\u60f3\uff0c\u603b\u4e0d\u80fd\u4e00\u76f4\u8fd9\u6837\uff0c\u89c1\u4e00\u4e2a\u7231\u4e00\u4e2a\uff0c\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\u8d8a\u6765\u8d8a\u591a\uff0c\u5230\u6700\u540e\u80af\u5b9a\u4f1a\u51fa\u95ee\u9898\u3002\u201d</p><p>\u5f53\u7136\u4f1a\u51fa\u95ee\u9898\uff0c\u65e0\u975e\u662f\u95ee\u9898\u8f7b\u91cd\u800c\u5df2\uff0c\u8fd0\u6c14\u597d\u7684\u8bdd\uff0c\u6216\u8bb8\u53ea\u662f\u540e\u5bab\u6597\u4e89\uff0c\u5018\u82e5\u8fd0\u6c14\u4e0d\u597d\uff0c\u5404\u79cd\u52a8\u6f2b\u4e2d\u559c\u95fb\u4e50\u89c1\u7684\u9ed1\u5316\u7ed3\u5c40\u6307\u65e5\u53ef\u5f85\u3002\u743c\u6069\u5bf9\u6b64\u4e5f\u4e0d\u662f\u6ca1\u6709\u81ea\u89c9\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u7684\u5fc3\u6001\u4e5f\u5728\u6084\u6084\u53d1\u751f\u53d8\u5316\uff0c\u5012\u9000\u4e24\u4e09\u5e74\u524d\uff0c\u4ed6\u7684\u60f3\u6cd5\u662f\u201c\u53ea\u8981\u662f\u6f02\u4eae\u59b9\u5b50\u4e00\u5b9a\u8981\u6536\u5165\u540e\u5bab\u201d\uff0c\u4f46\u73b0\u5728\u4ed6\u5df2\u7ecf\u6ca1\u6709\u8fd9\u4e2a\u91ce\u671b\u4e86\u3002\u5973\u4eba\u591a\u4e86\u4e5f\u9ebb\u70e6\uff0c\u8981\u8ba9\u73b0\u6709\u7684\u8fd9\u4e9b\u548c\u8c10\u76f8\u5904\uff0c\u5df2\u7ecf\u591f\u8ba9\u4ed6\u5934\u75bc\u3002\u6240\u4ee5\u4ed6\u73b0\u5728\u5df2\u7ecf\u662f\u5c3d\u91cf\u514b\u5236\u4e86\u2014\u2014\u81f3\u5c11\u743c\u6069\u81ea\u5df1\u662f\u8fd9\u4e48\u89c9\u5f97\uff0c\u65e0\u5948\u4e16\u4e8b\u65e0\u5e38\uff0c\u6709\u65f6\u5019\u4f60\u4e0d\u4e3b\u52a8\u52fe\u642d\u59b9\u5b50\uff0c\u59b9\u5b50\u5374\u4f1a\u4e3b\u52a8\u8d34\u8fc7\u6765\uff0c\u800c\u4e14\u8fd8\u81ea\u79f0\u662f\u4f60\u524d\u4e16\u60c5\u4eba\u4ec0\u4e48\u7684\uff0c\u63a8\u90fd\u63a8\u4e0d\u6389\u3002</p><p>\u4eba\u751f\u771f\u662f\u5f88\u591a\u70e6\u607c\u554a\u3002</p><p>\u201c\u70e6\u607c\u4f60\u4e2a\u9b3c\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u660e\u660e\u5c31\u662f\u4f60\u81ea\u5df1\u7ba1\u4e0d\u4f4f\u81ea\u5df1\u3002\u201d</p><p>\u201c\u4e0d\u662f\u8fd9\u6837\u7684\uff0c\u201d\u743c\u6069\u5206\u8fa8\uff0c\u201c\u6211\u771f\u7684\u5df2\u7ecf\u5f88\u6ce8\u610f\u5f88\u5c0f\u5fc3\u4e86\u3002\u73b0\u5728\u8d70\u5728\u8def\u4e0a\u9047\u5230\u964c\u751f\u5973\u4eba\uff0c\u6211\u770b\u90fd\u4e0d\u6562\u770b\uff0c\u751f\u6015\u5f15\u8d77\u8bef\u4f1a\u3002\u201d</p><p>\u73ca\u5609\u7b11\u8d77\u6765\uff0c\u201c\u597d\u5566\u597d\u5566\uff0c\u77e5\u9053\u4f60\u5df2\u7ecf\u5f88\u5c0f\u5fc3\u4e86\uff0c\u201d\u5979\u60f3\u4e86\u60f3\uff0c\u201c\u603b\u4e4b\u5462\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u4f60\u8fd8\u662f\u591a\u60f3\u60f3\uff0c\u522b\u5f04\u5230\u6700\u540e\u4e0d\u53ef\u6536\u62fe\u3002\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u201d\u743c\u6069\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u6211\u4f1a\u8ba4\u771f\u5bf9\u5f85\u7684\u3002\u201d</p><p>\u8bf4\u662f\u8fd9\u4e48\u8bf4\uff0c\u4f46\u5230\u5e95\u8981\u600e\u4e48\u529e\uff0c\u743c\u6069\u8fd8\u662f\u6ca1\u6709\u5934\u7eea\u3002\u522b\u7684\u4e14\u4e0d\u8c08\uff0c\u8fd9\u6b21\u6765\u4e1c\u57df\uff0c\u5176\u4e2d\u4e00\u4e2a\u91cd\u8981\u76ee\u6807\u5c31\u662f\u53bb\u7d2b\u5bb8\u6c99\u6f20\u627e\u51ef\u745f\u7433\uff0c\u6839\u636e\u76ee\u524d\u7684\u8d44\u6599\u6765\u770b\uff0c\u81ea\u5df1\u548c\u51ef\u745f\u7433\u5173\u7cfb\u532a\u6d45\uff0c\u6e0a\u6e90\u6781\u6df1\u2014\u2014\u90a3\u4e48\u95ee\u9898\u6765\u4e86\uff0c\u627e\u5230\u51ef\u745f\u7433\uff0c\u7136\u540e\u8981\u5982\u4f55\u5b89\u6392\uff1f\u8fd9\u4ef6\u4e8b\u5355\u7eaf\u60f3\u4e00\u60f3\u90fd\u8ba9\u4eba\u5934\u75bc\uff0c\u66f4\u522b\u8bf4\u771f\u7684\u53bb\u9762\u5bf9\u4e86\u3002\u800c\u4e14\u8bf4\u4e0d\u5b9a\u8fd8\u4e0d\u4ec5\u4ec5\u662f\u51ef\u745f\u7433\u4e00\u4e2a\u4eba\uff0c\u6309\u7167\u624e\u745e\u5c14\u7684\u8bf4\u6cd5\uff0c\u743c\u6069\u524d\u4e16\u98ce\u6d41\u597d\u8272\uff0c\u6b20\u4e0b\u611f\u60c5\u503a\u65e0\u6570\uff0c\u867d\u8bf4\u5df2\u7ecf\u662f\u51e0\u5343\u5e74\u524d\u7684\u4e8b\u60c5\u4e86\uff0c\u4f46\u8c01\u77e5\u9053\u4f1a\u4e0d\u4f1a\u8fd8\u6709\u65e7\u60c5\u4eba\u6d3b\u7740\u5462\uff0c\u4eba\u7c7b\u5f53\u7136\u4e0d\u53ef\u80fd\u6d3b\u8fd9\u4e48\u4e45\uff0c\u4f46\u5bf9\u4e8e\u90aa\u9b54\u4ec0\u4e48\u7684\u800c\u8a00\uff0c\u4e5f\u5c31\u4e0d\u7b97\u4ec0\u4e48\u4e86\u3002\u60f3\u5230\u8fd9\u91cc\uff0c\u743c\u6069\u5c31\u4e0d\u7531\u5f97\u6697\u81ea\u5e86\u5e78\uff0c\u5e78\u597d\u624e\u745e\u5c14\u56de\u5730\u72f1\u4e86\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u5c40\u9762\u771f\u662f\u4e0d\u6562\u60f3\u8c61\u3002</p><p>\u7b97\u4e86\uff0c\u8d70\u4e00\u6b65\u770b\u4e00\u6b65\uff0c\u4e8b\u5230\u4e34\u5934\u518d\u8bf4\u5427\u3002</p><p>\u743c\u6069\u679c\u65ad\u9009\u62e9\u9003\u907f\uff0c\u4e0d\u518d\u53bb\u601d\u8003\u8fd9\u4e2a\u95ee\u9898\u3002\u770b\u770b\u65f6\u95f4\u4e0d\u65e9\uff0c\u4e5f\u8be5\u56de\u53bb\u4e86\uff0c\u4e24\u4eba\u539f\u8def\u8fd4\u56de\uff0c\u521a\u8d70\u5230\u4e00\u534a\uff0c\u5ffd\u7136\u624b\u4e0a\u7684\u6212\u6307\u4e00\u9635\u8f7b\u5fae\u98a4\u52a8\uff0c\u6885\u83f2\u65af\u7684\u58f0\u97f3\u4ece\u91cc\u9762\u4f20\u51fa\u6765\uff0c\u201c\u4f60\u5728\u65c5\u5e97\u5417\uff1f\u201d</p><p>\u201c\u55ef\uff1f\u201d\u743c\u6069\u4e00\u6014\uff0c\u201c\u4e0d\u5728\uff0c\u6211\u548c\u59d0\u59d0\u5728\u5916\u9762\u901b\u8857\u3002\u201d</p><p>\u4e0b\u4e00\u77ac\u95f4\uff0c\u8eab\u65c1\u7684\u7a7a\u6c14\u5267\u70c8\u6ce2\u52a8\uff0c\u6885\u83f2\u65af\u548c\u51db\u76f4\u63a5\u4f20\u9001\u4e86\u8fc7\u6765\u3002\u201c\u4f60\u80fd\u8054\u7cfb\u838e\u73de\u514b\u5417\uff1f\u201d\u6885\u83f2\u65af\u95ee\u743c\u6069\u3002</p><p>\u201c\u53ef\u4ee5\u3002\u201d</p><p>\u201c\u8ba9\u5979\u548c\u7ef4\u82e5\u62c9\u8d76\u5feb\u79bb\u5f00\u65c5\u5e97\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u548c\u6211\u4eec\u6c47\u5408\u3002\u201d</p><p>\u743c\u6069\u91ca\u653e\u4e86\u4e00\u4e2a\u4f20\u8baf\u672f\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u53d1\u751f\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u536b\u5175\u5728\u8ffd\u6355\u6211\u4eec\u3002\u201d\u51db\u8bf4\u3002</p></article>"}}}]);
