(window.webpackJsonp=window.webpackJsonp||[]).push([[1762],{1785:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e03\u8282 \u753b\u50cf",content:"<article><h2>\u7b2c\u5341\u4e03\u8282 \u753b\u50cf</h2><p>\u5728\u5e94\u5141\u5510\u7426\u62c9\uff0c\u63a5\u4e0b\u8fd9\u4e2a\u6d3b\u7684\u65f6\u5019\uff0c\u743c\u6069\u5e76\u6ca1\u6709\u628a\u8fd9\u4ef6\u4e8b\u60f3\u8c61\u5f97\u592a\u96be\u3002\u9b54\u6cd5\u4e0d\u662f\u5deb\u672f\uff0c\u4e0d\u662f\u7384\u5b66\uff0c\u5b83\u5176\u5b9e\u66f4\u8fd1\u4f3c\u4e8e\u79d1\u5b66\uff0c\u662f\u6709\u539f\u7406\u53ef\u8bb2\uff0c\u6709\u89c4\u5f8b\u53ef\u5faa\u7684\uff0c\u662f\u53ef\u4ee5\u5206\u6790\u3001\u63a8\u7406\u3001\u8ba1\u7b97\u3001\u9a8c\u8bc1\u7684\u3002\u8fd9\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\u5c31\u6015\u4e0d\u8bb2\u9053\u7406\uff0c\u53ea\u8981\u8bb2\u9053\u7406\uff0c\u800c\u4e0d\u662f\u968f\u5fc3\u6240\u6b32\u4e71\u6765\uff0c\u90a3\u5c31\u597d\u8bf4\u3002</p><p>\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u4fee\u590d\u4e00\u4e2a\u6b8b\u7f3a\u7684\u9b54\u6cd5\u9635\uff0c\u8fd9\u5e76\u4e0d\u7b97\u4ec0\u4e48\u65b0\u9c9c\u4e8b\uff0c\u5f53\u5e74\u5728\u5b66\u6821\u91cc\u5c31\u6ca1\u5c11\u7ec3\u4e60\u8fc7\u3002\u4f55\u51b5\u8fd9\u6b21\u8fd8\u6709\u8bbe\u8ba1\u8005\u81ea\u5df1\u7684\u624b\u7a3f\u5728\uff0c\u63a8\u7b97\u8fc7\u7a0b\u3001\u8bbe\u8ba1\u601d\u8def\u90fd\u6709\uff0c\u96be\u5ea6\u7406\u5f53\u662f\u4e0d\u9ad8\u7684\u3002\u603b\u800c\u8a00\u4e4b\uff0c\u4f5c\u4e3a\u8fd9\u4e16\u754c\u4e0a\u6700\u597d\u7684\u5deb\u5e08\u5b66\u6821\u7684\u9ad8\u6750\u751f\uff0c\u8fd9\u70b9\u81ea\u4fe1\u4ed6\u8fd8\u662f\u6709\u7684\u3002</p><p>\u4e8b\u5b9e\u8bc1\u660e\u4ed6\u592a\u8f7b\u654c\u4e86\u3002</p><p>\u4e0d\u77e5\u9053\u662f\u56e0\u4e3a\u5e74\u4ee3\u592a\u4e45\u8fdc\uff0c\u9b54\u6cd5\u5b66\u7684\u53d8\u8fc1\u592a\u4e25\u91cd\uff0c\u8fd8\u662f\u56e0\u4e3a\u4e2d\u571f\u548c\u4e1c\u57df\u7684\u5deb\u5e08\u601d\u7ef4\u903b\u8f91\u5dee\u522b\u592a\u5927\uff0c\u743c\u6069\u5bf9\u7740\u9b54\u6cd5\u9635\u7814\u7a76\u4e86\u534a\u5929\uff0c\u53d1\u73b0\u8d8a\u770b\u8d8a\u8ff7\u7cca\uff0c\u5f88\u591a\u5730\u65b9\u548c\u4ed6\u4e4b\u524d\u6240\u5b66\u7684\u539f\u7406\u3001\u89c4\u5219\u662f\u4e0d\u592a\u76f8\u7b26\uff0c\u751a\u81f3\u5b8c\u5168\u80cc\u9053\u800c\u9a70\u7684\u3002\u4ed6\u56de\u8fc7\u5934\u53bb\u770b\u5deb\u5e08\u624b\u7a3f\uff0c\u7136\u540e\u53d1\u73b0\u8fde\u4eba\u5bb6\u7684\u5f88\u591a\u63a8\u5bfc\u8fc7\u7a0b\u90fd\u770b\u4e0d\u660e\u767d\uff0c\u8fd9\u5c31\u6bd4\u8f83\u5c34\u5c2c\u4e86\u3002</p><p>\u773c\u770b\u5df2\u7ecf\u5230\u4e86\u4e2d\u5348\uff0c\u743c\u6069\u8fd8\u6ca1\u7422\u78e8\u51fa\u534a\u70b9\u5934\u7eea\uff0c\u53ea\u597d\u5bf9\u5510\u7426\u62c9\u8bf4\u8fd9\u91cc\u73af\u5883\u592a\u5435\uff0c\u684c\u5b50\u592a\u51b0\uff0c\u6905\u5b50\u592a\u786c\uff0c\u5149\u7ebf\u592a\u4eae\uff0c\u603b\u4e4b\u5404\u79cd\u5f71\u54cd\u53d1\u6325\uff0c\u8981\u56de\u81ea\u5df1\u4f4f\u5904\u624d\u80fd\u5de5\u4f5c\u3002\u5510\u7426\u62c9\u5012\u662f\u65e0\u6240\u8c13\uff0c\u540c\u610f\u4ed6\u628a\u6240\u6709\u8d44\u6599\u90fd\u6253\u5305\u5e26\u56de\u53bb\u3002</p><p>\u201c\u53bb\u8bf7\u7ef4\u82e5\u62c9\u5c0f\u59d0\u8fc7\u6765\uff0c\u8fd8\u6709\u51db\u5c0f\u59d0\uff0c\u201d\u76f4\u63a5\u4f7f\u7528\u4e86\u4e00\u4e2a\u4f20\u9001\u672f\u56de\u5230\u9752\u94dc\u8c6a\u5b85\uff0c\u743c\u6069\u5429\u5490\u9690\u5f62\u4ec6\u5f79\uff0c\u201c\u6211\u5c31\u4e0d\u4fe1\u5f04\u4e0d\u660e\u767d\u8fd9\u4e1c\u897f\u3002\u201d</p><p>\u201c\u5f88\u96be\u5417\uff1f\u201d\u73ca\u5609\u6709\u4e9b\u62c5\u5fc3\u5730\u770b\u7740\u743c\u6069\uff0c\u5979\u5f88\u5c11\u770b\u5230\u81ea\u5df1\u5f1f\u5f1f\u8fd9\u526f\u6c14\u6025\u8d25\u574f\u7684\u6a21\u6837\uff0c\u201c\u5982\u679c\u592a\u9ebb\u70e6\u5c31\u7b97\u4e86\u5427\u3002\u201d</p><p>\u201c\u6211\u624d\u4e0d\u4f1a\u8fd9\u4e48\u5feb\u5c31\u8ba4\u8f93\uff01\u201d</p><p>\u5982\u679c\u6ca1\u7b54\u5e94\u4e5f\u5c31\u7b97\u4e86\uff0c\u73b0\u5728\u8bdd\u90fd\u8bf4\u51fa\u53bb\uff0c\u7ed3\u679c\u5374\u53c8\u627f\u8ba4\u505a\u4e0d\u5230\uff0c\u90a3\u4e0d\u662f\u592a\u4e22\u8138\u4e86\u5417\u3002\u743c\u6069\u5f88\u5728\u4e4e\u9762\u5b50\u7684\u2014\u2014\u5c24\u5176\u662f\u5728\u5973\u5b69\u5b50\u9762\u524d\u3002</p><p>\u7ef4\u82e5\u62c9\u5f88\u5feb\u8fc7\u6765\uff0c\u51db\u5374\u4e0d\u5728\uff0c\u4f30\u8ba1\u662f\u901b\u8857\u53bb\u4e86\uff0c\u743c\u6069\u4e5f\u4e0d\u5728\u610f\uff0c\u51db\u662f\u4e3b\u8981\u9760\u5929\u8d4b\u7684\uff0c\u7406\u8bba\u6c34\u5e73\u8fd8\u4e0d\u5982\u4ed6\uff0c\u5c31\u7b97\u5728\u4e5f\u5e2e\u4e0d\u4e0a\u591a\u5c11\u5fd9\uff0c\u771f\u6b63\u503c\u5f97\u4fe1\u8d56\u7684\u662f\u4f20\u9053\u5deb\u5e08\uff0c\u5979\u624d\u662f\u4e13\u5bb6\u3002</p><p>\u4f20\u9053\u5deb\u5e08\u663e\u7136\u4e5f\u6ca1\u89c1\u8fc7\u8fd9\u4e48\u5947\u602a\u7684\u9b54\u6cd5\u9635\uff0c\u5979\u5bf9\u7740\u56fe\u7eb8\u548c\u624b\u7a3f\u7814\u7a76\u4e86\u4e00\u4e2a\u591a\u5c0f\u65f6\uff0c\u8fde\u5348\u996d\u90fd\u6ca1\u987e\u5f97\u4e0a\u5403\uff0c\u5176\u95f4\u4e0d\u65ad\u53ec\u5524\u51fa\u8679\u96fe\uff0c\u5927\u6982\u662f\u5728\u4ece\u4ee5\u5f80\u5386\u4ee3\u4f20\u9053\u5deb\u5e08\u7684\u8bb0\u5fc6\u4e2d\u5bfb\u627e\u7ebf\u7d22\uff0c\u6700\u7ec8\uff0c\u5979\u62ac\u8d77\u5934\u6765\uff0c\u8868\u793a\u521d\u6b65\u7406\u6e05\u4e86\u5934\u7eea\u3002</p><p>\u201c\u8fd9\u4e2a\u9b54\u6cd5\u9635\u6742\u7cc5\u4e86\u591a\u79cd\u4f53\u7cfb\u7684\u9b54\u6cd5\u5728\u5185\uff0c\u800c\u4e14\u90fd\u662f\u4e0a\u53e4\u65f6\u4ee3\u7684\u4f53\u7cfb\uff0c\u201d\u7ef4\u82e5\u62c9\u89e3\u91ca\uff0c\u201c\u5c31\u76ee\u524d\u5206\u6790\u51fa\u6765\u7684\uff0c\u5305\u62ec\u7cbe\u7075\u7684\u9ad8\u7b49\u9b54\u6cd5\u3001\u7eb3\u83f2\u5c14\u7684\u7f1a\u9b54\u672f\u3001\u8010\u745f\u9b54\u6cd5\uff0c\u8fd8\u6709\u4e1c\u57df\u7684\u89a1\u672f\u3002\u201d</p><p>\u7cbe\u7075\u9ad8\u7b49\u9b54\u6cd5\u743c\u6069\u81ea\u7136\u77e5\u9053\uff0c\u7eb3\u83f2\u5c14\u662f\u4e00\u4e2a\u53e4\u4ee3\u7684\u9b54\u6cd5\u5e1d\u56fd\uff0c\u548c\u8010\u745f\u745e\u5c14\u5927\u4f53\u4e0a\u540c\u65f6\u4ee3\uff0c\u4ee5\u5404\u79cd\u53ec\u5524\u3001\u63a7\u5236\u90aa\u9b54\u7684\u6cd5\u672f\u800c\u8457\u79f0\uff0c\u8010\u745f\u9b54\u6cd5\u66f4\u4e0d\u7528\u591a\u8bf4\uff0c\u4f46\u201c\u89a1\u672f\u201d\u662f\u4e2a\u4ec0\u4e48\u4e1c\u897f\uff1f\u524d\u51e0\u5929\u4ed6\u66fe\u7ecf\u542c\u6885\u83f2\u65af\u63d0\u53ca\u8fc7\uff0c\u5e94\u8be5\u662f\u4e00\u79cd\u5deb\u672f\uff0c\u4f46\u5177\u4f53\u7ec6\u8282\u5c31\u4e0d\u6e05\u695a\u4e86\u3002</p><p>\u201c\u89a1\u672f\u4e48\uff0c\u201d\u7ef4\u82e5\u62c9\u60f3\u4e86\u60f3\uff0c\u201c\u8fd9\u4e48\u8bf4\u5427\uff0c\u6211\u4eec\u901a\u5e38\u90fd\u8ba4\u4e3a\uff0c\u4e1c\u57df\u4eba\u6392\u65a5\u5deb\u5e08\uff0c\u5bfc\u81f4\u9b54\u6cd5\u5b66\u4e5f\u4e0d\u53d1\u8fbe\uff0c\u5bf9\u5427\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u90a3\u7ea2\u888d\u5deb\u5e08\u5462\uff1f\u201d\u7ef4\u82e5\u62c9\u53cd\u95ee\u3002</p><p>\u743c\u6069\u88ab\u95ee\u4f4f\u4e86\uff0c\u7ea2\u888d\u5deb\u5e08\u6240\u76d8\u8e1e\u7684\u585e\u5c14\u738b\u56fd\uff0c\u4e43\u662f\u5927\u9646\u4e0a\u7684\u9b54\u6cd5\u91cd\u9547\uff0c\u662f\u5deb\u5e08\u4eba\u53e3\u6700\u591a\u7684\u5730\u65b9\uff0c\u81f3\u5c11\u5728\u9634\u9b42\u57ce\u56de\u5f52\u4e4b\u524d\u662f\u5982\u6b64\u3002\u800c\u4f17\u6240\u5468\u77e5\uff0c\u7ea2\u888d\u5deb\u5e08\u6e90\u81ea\u7a46\u7f55\u5e1d\u56fd\uff0c\u6700\u521d\u5c31\u662f\u7a46\u7f55\u7684\u4e00\u6279\u5deb\u5e08\u4eec\u56e0\u4e3a\u4e0d\u582a\u538b\u8feb\uff0c\u4e3e\u65d7\u9020\u53cd\uff0c\u62a2\u4e0b\u4e86\u585e\u5c14\u9ad8\u539f\u8fd9\u5757\u5730\u65b9\u5e76\u4e14\u72ec\u7acb\u5efa\u56fd\u3002\u5982\u679c\u8bf4\u4e1c\u57df\u7684\u9b54\u6cd5\u5b66\u6c34\u5e73\u4f4e\u4e0b\uff0c\u90a3\u600e\u4e48\u4f1a\u4ea7\u751f\u7ea2\u888d\u5deb\u5e08\u8fd9\u4e2a\u7fa4\u4f53\uff1f</p><p>\u201c\u5b9e\u9645\u4e0a\uff0c\u8bf4\u4e1c\u57df\u4eba\u6392\u65a5\u5deb\u5e08\u5f53\u7136\u6ca1\u9519\uff0c\u4f46\u8981\u8bf4\u4e1c\u57df\u9b54\u6cd5\u5b66\u4e0d\u53d1\u8fbe\uff0c\u8fd9\u5c31\u8981\u5206\u65f6\u671f\u3001\u5206\u60c5\u51b5\u6765\u770b\u4e86\u3002\u73b0\u5728\u5f53\u7136\u662f\u5982\u6b64\uff0c\u4f46\u5728\u51e0\u767e\u5e74\u524d\uff0c\u4e1c\u57df\u7684\u9b54\u6cd5\u5b66\u5176\u5b9e\u8fd8\u4e0d\u5dee\uff0c\u7531\u4e8e\u4e0a\u53e4\u9b54\u6cd5\u5e1d\u56fd\u4f0a\u739b\u65af\u5361\u7684\u4e00\u4e9b\u9057\u7559\uff0c\u81f3\u5c11\u5728\u4ea1\u7075\u672f\u9886\u57df\u7684\u6c34\u5e73\u8fd8\u4e0d\u9519\uff0c\u6bd4\u4e2d\u571f\u53ef\u80fd\u8fd8\u8981\u5f3a\u4e00\u70b9\u3002\u201d</p><p>\u201c\u90a3\u4e2a\uff0c\u7b49\u7b49\uff0c\u201d\u743c\u6069\u63d0\u51fa\u5f02\u8bae\uff0c\u201c\u6211\u542c\u8bf4\uff0c\u4f0a\u739b\u65af\u5361\u64c5\u957f\u7684\u662f\u70bc\u91d1\u9020\u7269\u548c\u7a7a\u95f4\u6cd5\u672f\uff0c\u4f3c\u4e4e\u5e76\u4e0d\u4ee5\u4ea1\u7075\u672f\u8457\u79f0\u3002\u201d</p><p>\u201c\u4f60\u8bf4\u7684\u90a3\u662f\u4f0a\u739b\u65af\u5361\u7684\u5b98\u65b9\u60c5\u51b5\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u7687\u5ba4\u5f53\u7136\u662f\u4ee5\u5947\u68b0\u672f\u8457\u79f0\uff0c\u4f46\u90a3\u4e9b\u2018\u5b66\u8005\u2019\u5462\uff1f\u4ed6\u4eec\u53c8\u5b66\u4e0d\u5230\u90a3\u4e9b\u8840\u8109\u9650\u5b9a\u7684\u5947\u68b0\u672f\u3002\u800c\u4e14\u5728\u5e1d\u56fd\u665a\u671f\uff0c\u6709\u4e9b\u5b66\u8005\u4e3a\u4e86\u5236\u4f5c\u4e00\u79cd\u667a\u80fd\u9b54\u50cf\uff0c\u81f4\u529b\u4e8e\u4ea1\u7075\u672f\u7684\u7814\u7a76\uff0c\u4e3a\u5b98\u65b9\u6240\u4e0d\u5bb9\uff0c\u4ed6\u4eec\u53ea\u597d\u79bb\u5f00\u57ce\u5e02\uff0c\u9690\u59d3\u57cb\u540d\uff0c\u7ed3\u679c\u53cd\u800c\u56e0\u6b64\u4fa5\u5e78\u8eb2\u8fc7\u4e86\u706d\u56fd\u5927\u52ab\u3002\u4f0a\u739b\u65af\u5361\u9668\u843d\uff0c\u7687\u5ba4\u7684\u5947\u68b0\u672f\u5c31\u6b64\u5931\u4f20\uff0c\u53cd\u5012\u662f\u8fd9\u4e9b\u5e78\u5b58\u8005\u548c\u4ed6\u4eec\u7cbe\u7814\u7684\u4ea1\u7075\u672f\uff0c\u4fdd\u7559\u4e86\u4e0b\u6765\u3002\u201d</p><p>\u743c\u6069\u7a81\u7136\u60f3\u8d77\u4e86\u67d0\u53ea\u708e\u9b54\uff0c\u4ed6\u5c31\u662f\u56e0\u4e3a\u8bd5\u56fe\u7528\u4ea1\u7075\u9b54\u6cd5\u6765\u521b\u9020\u667a\u80fd\u9b54\u50cf\uff0c\u7ed3\u679c\u88ab\u653e\u9010\u5230\u4e86\u5e7d\u6697\u5730\u57df\u3002</p><p>\u201c\u7ea2\u888d\u5deb\u5e08\u73b0\u5728\u662f\u5168\u9762\u53d1\u5c55\uff0c\u516b\u7cfb\u5177\u5907\uff0c\u4f46\u6700\u521d\u5176\u5b9e\u53ea\u4f1a\u4ea1\u7075\u672f\u3002\u73b0\u5728\u7684\u7ea2\u888d\u9886\u8896\u8428\u624e\u65af\u5766\u5c31\u662f\u4ea1\u7075\u5b66\u6d3e\u7684\u9996\u5e2d\u5bfc\u5e08\uff0c\u8fd9\u4e0d\u4ec5\u4ec5\u56e0\u4e3a\u4ed6\u6700\u5f3a\uff0c\u4e5f\u662f\u6709\u5386\u53f2\u539f\u56e0\u7684\uff0c\u201d\u7ef4\u82e5\u62c9\u63a5\u7740\u8bf4\uff0c\u201c\u7ea2\u888d\u5728\u585e\u5c14\u5efa\u56fd\uff0c\u4e1c\u57df\u7684\u5deb\u5e08\u4eec\u770b\u5230\u5e0c\u671b\uff0c\u7eb7\u7eb7\u524d\u5f80\u6295\u5954\u3002\u7ed3\u679c\u5bfc\u81f4\u4e1c\u57df\u672c\u571f\u7684\u5deb\u5e08\u8fd1\u4e4e\u7edd\u8ff9\uff0c\u9b54\u6cd5\u5b66\u6c34\u5e73\u81ea\u7136\u5c31\u4e00\u843d\u5343\u4e08\u4e86\u3002\u201d</p><p>\u201c\u7ea2\u888d\u5deb\u5e08\u5efa\u56fd\u540e\uff0c\u4e0e\u4e2d\u571f\u4e92\u76f8\u4ea4\u6d41\uff0c\u9010\u6e10\u653e\u5f03\u4e86\u539f\u672c\u4f20\u7edf\uff0c\u6539\u7528\u4e2d\u571f\u7684\u9b54\u6cd5\u4f53\u7cfb\uff0c\u73b0\u5728\u5df2\u7ecf\u57fa\u672c\u770b\u4e0d\u5230\u4ee5\u5f80\u7684\u75d5\u8ff9\u4e86\u3002\u4f46\u5728\u4e1c\u57df\u672c\u571f\u2014\u2014\u4e3b\u8981\u662f\u6069\u745f\uff0c\u636e\u8bf4\u8fd8\u6b8b\u7559\u7740\u4e00\u4e9b\u5deb\u5e08\uff0c\u79d8\u5bc6\u4f20\u627f\u7740\u4ed6\u4eec\u7684\u53e4\u8001\u5deb\u672f\u3002\u201d</p><p>\u4f0a\u739b\u65af\u5361\u9057\u7559\u4e0b\u6765\u7684\u4ea1\u7075\u672f\uff0c\u5728\u6f2b\u957f\u7684\u5343\u5e74\u65f6\u5149\u4e2d\uff0c\u6742\u7cc5\u4e86\u7a46\u7f55\u3001\u6069\u745f\u548c\u4e1c\u57df\u539f\u4f4f\u6c11\u7684\u5deb\u672f\u5728\u5185\uff0c\u6f14\u5316\u51fa\u4e86\u4e00\u5957\u4ee4\u666e\u901a\u4eba\uff0c\u751a\u81f3\u5176\u4ed6\u5730\u65b9\u7684\u5deb\u5e08\u90fd\u96be\u4ee5\u7406\u89e3\uff0c\u96be\u4ee5\u5f62\u5bb9\u7684\u4e1c\u897f\uff0c\u8fd9\u5c31\u662f\u201c\u89a1\u672f\u201d\u3002</p><p>\u89a1\u672f\u662f\u4ea1\u7075\u672f\u7684\u4e00\u79cd\u6f14\u53d8\uff0c\u91cd\u4eea\u8f68\uff0c\u5584\u6bd4\u9644\uff0c\u504f\u91cd\u4e8e\u8bc5\u5492\u3002\u6bd4\u5982\u8bf4\u60f3\u8981\u6740\u4e00\u4e2a\u654c\u4eba\uff0c\u6218\u58eb\u7684\u903b\u8f91\u662f\u76f4\u63a5\u62d4\u5200\u53bb\u780d\uff0c\u666e\u901a\u6cd5\u5e08\u7684\u903b\u8f91\u662f\u653e\u4e2a\u706b\u7403\uff0c\u800c\u89a1\u672f\u7684\u903b\u8f91\uff0c\u5219\u662f\u5bfb\u627e\u4e00\u4e2a\u201c\u66ff\u8eab\u201d\uff0c\u8fd9\u4e2a\u201c\u66ff\u8eab\u201d\u4e0e\u654c\u4eba\u7684\u8054\u7cfb\u8d8a\u7d27\u5bc6\u8d8a\u597d\uff0c\u7136\u540e\u5e72\u6389\u8fd9\u4e2a\u66ff\u8eab\uff0c\u4ece\u800c\u95f4\u63a5\u5730\u6740\u4f24\u654c\u4eba\u3002</p><p>\u201c\u6bd4\u5982\u8bf4\u624e\u4e2a\u5c0f\u7eb8\u4eba\uff0c\u5199\u4e0a\u51fa\u751f\u65e5\u671f\uff1f\u201d</p><p>\u201c\u867d\u7136\u4e0d\u61c2\u4f60\u5728\u8bf4\u4ec0\u4e48\uff0c\u4f46\u5e94\u8be5\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u201d</p><p>\u201c\u4f60\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5012\u786e\u5b9e\u662f\uff0c\u201d\u743c\u6069\u60f3\u4e86\u60f3\uff0c\u201c\u4ea1\u7075\u672f\u7684\u786e\u4e5f\u6709\u8fd9\u4e2a\u98ce\u683c\uff0c\u53ea\u662f\u6ca1\u8fd9\u4e48\u5938\u5f20\u3002\u54a6\uff0c\u8428\u9a6c\u65af\u7279\u641e\u51fa\u6765\u7684\u90a3\u4e2a\u2018\u5316\u8eab\u2019\uff0c\u597d\u50cf\u4e5f\u6709\u70b9\u8fd9\u610f\u601d\u554a\u3002\u201d</p><p>\u8428\u9a6c\u65af\u7279\u539f\u672c\u6253\u7b97\u4f7f\u7528\u7684\u201c\u5316\u8eab\u201d\uff0c\u5176\u5b9e\u5c31\u662f\u62ff\u7ef4\u82e5\u62c9\u4f5c\u4e3a\u963f\u7956\u65af\u7684\u66ff\u4ee3\u54c1\uff0c\u501f\u52a9\u4e24\u8005\u4e4b\u95f4\u7684\u8054\u7cfb\u6765\u65bd\u6cd5\u3002\u743c\u6069\u5f53\u65f6\u6ca1\u6709\u591a\u60f3\uff0c\u867d\u7136\u89c9\u5f97\u6709\u70b9\u602a\uff0c\u4f46\u4e5f\u6ca1\u6562\u591a\u95ee\uff0c\u73b0\u5728\u60f3\u8d77\u6765\uff0c\u8fd9\u4e0d\u662f\u548c\u4e1c\u57df\u7684\u89a1\u672f\u9887\u4e3a\u7c7b\u4f3c\u4e48\u3002</p><p>\u201c\u6709\u53ef\u80fd\uff0c\u6839\u636e\u6211\u4eec\u5f97\u5230\u7684\u6d88\u606f\uff0c\u8428\u739b\u65af\u7279\u7684\u786e\u5b66\u8fc7\u89a1\u672f\u3002\u201d</p><p>\u597d\u5427\uff0c\u743c\u6069\u603b\u7b97\u77e5\u9053\u81ea\u5df1\u4e3a\u4ec0\u4e48\u770b\u4e0d\u61c2\u8fd9\u4e2a\u9b54\u6cd5\u9635\u4e86\uff0c\u539f\u6765\u6839\u672c\u5c31\u662f\u4e2a\u5927\u6742\u70e9\uff0c\u800c\u4e14\u8fd8\u90fd\u662f\u5343\u5e74\u4e4b\u524d\u7684\u9b54\u6cd5\u4f53\u7cfb\uff0c\u65e9\u5c31\u8fdc\u8fdc\u8d85\u51fa\u4e86\u4ed6\u7684\u77e5\u8bc6\u8303\u56f4\u3002\u4e0d\u8fc7\u8fd9\u6837\u4e1c\u62fc\u897f\u51d1\u7cc5\u5408\u8d77\u6765\u7684\u4e1c\u897f\uff0c\u771f\u7684\u80fd\u8fd0\u4f5c\u4e48\uff1f</p><p>\u201c\u4f3c\u4e4e\u662f\u53ef\u4ee5\u7684\uff0c\u201d\u7ef4\u82e5\u62c9\u8bf4\uff0c\u201c\u867d\u7136\u770b\u8d77\u6765\u6742\u4e71\uff0c\u4f46\u7ec6\u7ec6\u5206\u8fa8\u7684\u8bdd\uff0c\u5374\u662f\u975e\u5e38\u6709\u6761\u7406\uff0c\u5f7c\u6b64\u8054\u7ed3\u5951\u5408\uff0c\u6d51\u7136\u4e00\u4f53\u3002\u8fd9\u4f4d\u524d\u8f88\u771f\u662f\u4e86\u4e0d\u8d77\uff0c\u592a\u4e86\u4e0d\u8d77\u4e86\uff0c\u201d\u5979\u8fde\u58f0\u8d5e\u53f9\uff0c\u201c\u7b80\u76f4\u662f\u795e\u4e4e\u5176\u795e\uff0c\u4ee4\u4eba\u532a\u5937\u6240\u601d\u3002\u201d</p><p>\u884c\u4e86\u884c\u4e86\uff0c\u6211\u77e5\u9053\u4f60\u5f88\u4f69\u670d\u4eba\u5bb6\uff0c\u4e0d\u8fc7\u53cd\u6b63\u5bf9\u65b9\u4e5f\u542c\u4e0d\u89c1\uff0c\u5938\u5956\u7684\u8bdd\u4e0d\u59a8\u665a\u70b9\u518d\u8bb2\uff0c\u5148\u8bf4\u600e\u4e48\u628a\u8fd9\u4e2a\u9b54\u6cd5\u9635\u4fee\u590d\u3002</p><p>\u201c\u8fd9\u4e2a\u6709\u70b9\u96be\uff0c\u201d\u7ef4\u82e5\u62c9\u6c89\u541f\uff0c\u201c\u524d\u4e09\u79cd\u9b54\u6cd5\u4f53\u7cfb\u8fd8\u597d\u8bf4\uff0c\u81f3\u5c11\u6700\u5e95\u5c42\u7684\u903b\u8f91\u662f\u4e92\u901a\u7684\uff0c\u6211\u4e5f\u90fd\u61c2\u4e00\u4e9b\uff1b\u4e1c\u57df\u7684\u89a1\u672f\u81ea\u6210\u4e00\u683c\uff0c\u5185\u5728\u9053\u7406\u4e0e\u524d\u4e09\u79cd\u8fe5\u7136\u76f8\u5f02\uff0c\u6211\u53ef\u5f04\u4e0d\u660e\u767d\u3002\u201d</p><p>\u201c\u5386\u4efb\u4f20\u9053\u5deb\u5e08\uff0c\u5c31\u6ca1\u6709\u4e00\u4e2a\u61c2\u89a1\u672f\u7684\uff1f\u201d</p><p>\u201c\u6211\u4eec\u6ca1\u4e8b\u4e5f\u4e0d\u4f1a\u6765\u4e1c\u57df\u554a\uff0c\u8bf8\u795e\u534f\u5b9a\u5bf9\u6211\u4eec\u4e5f\u662f\u6709\u6548\u7684\u3002\u53ea\u6709\u9a6c\u4f26\u66fe\u7ecf\u5f85\u8fc7\u4e00\u6bb5\u65f6\u95f4\uff0c\u7565\u77e5\u4e00\u4e8c\uff0c\u6240\u4ee5\u6211\u624d\u80fd\u5206\u8fa8\u51fa\u8fd9\u662f\u89a1\u672f\uff0c\u4f46\u4e5f\u4ec5\u4ec5\u53ea\u662f\u8ba4\u8bc6\u800c\u5df2\u3002\u201d</p><p>\u201c\u6240\u4ee5\u6211\u4eec\u8fd8\u5f97\u53bb\u627e\u4e2a\u4e1c\u57df\u672c\u571f\u7684\u5deb\u5e08\u6765\u5e2e\u5fd9\uff1f\u201d</p><p>\u4e1c\u57df\u7684\u5deb\u5e08\u53ef\u4e0d\u597d\u627e\uff0c\u5982\u679c\u597d\u627e\u7684\u8bdd\uff0c\u5510\u7426\u62c9\u5c31\u4e0d\u4f1a\u75c5\u6025\u4e71\u6295\u533b\u4e86\u3002\u7b49\u7b49\uff0c\u8bf4\u8d77\u6765\uff0c\u8eab\u8fb9\u5012\u662f\u7684\u786e\u6709\u4f4d\u201c\u5973\u5deb\u201d\uff0c\u867d\u7136\u770b\u8d77\u6765\u4e0d\u592a\u9760\u8c31\uff0c\u4f46\u8bd5\u8bd5\u770b\u5427\u3002</p><p>\u201c\u53bb\u8bf7\u7fe1\u7fe0\u5c0f\u59d0\u8fc7\u6765\u3002\u201d</p><p>\u4ece\u77f3\u68fa\u91cc\u722c\u51fa\u6765\u7684\u90a3\u4f4d\u5973\u5b50\uff0c\u53ea\u77e5\u9053\u81ea\u5df1\u662f\u7fe1\u7fe0\u5973\u5deb\uff0c\u8fde\u540d\u5b57\u90fd\u4e0d\u8bb0\u5f97\uff0c\u5176\u4ed6\u4eba\u53c8\u4e0d\u597d\u603b\u662f\u53eb\u5979\u5973\u5deb\uff0c\u6700\u540e\u7d22\u6027\u5c31\u4ee5\u201c\u7fe1\u7fe0\u201d\u4ee3\u79f0\u3002\u542c\u4e86\u743c\u6069\u7684\u547d\u4ee4\uff0c\u9690\u5f62\u4ec6\u5f79\u771f\u8981\u79bb\u5f00\uff0c\u7ef4\u82e5\u62c9\u5236\u6b62\u4e86\u5b83\uff0c\u201c\u4e0d\u7528\u4e86\uff0c\u5979\u4e0d\u5728\u3002\u201d</p><p>\u201c\u5979\u53bb\u54ea\u4e86\uff1f\u201d</p><p>\u201c\u548c\u51db\u4e00\u8d77\u51fa\u53bb\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u8fd9\u56db\u79cd\u4e0d\u540c\u7684\u9b54\u6cd5\u4f53\u7cfb\u6742\u7cc5\u5728\u4e00\u8d77\uff0c\u53c8\u90fd\u5404\u6709\u6b8b\u7f3a\uff0c\u82e5\u60f3\u4fee\u590d\u9b54\u6cd5\u9635\uff0c\u5148\u5f97\u628a\u5b83\u4eec\u4e00\u4e00\u533a\u5206\u5f00\u6765\uff0c\u201d\u7ef4\u82e5\u62c9\u7814\u7a76\u4e86\u534a\u5929\uff0c\u663e\u7136\u5df2\u7ecf\u6709\u4e86\u521d\u6b65\u7684\u65b9\u6848\uff0c\u201c\u89a1\u672f\u6211\u4e0d\u61c2\uff0c\u4f46\u53ef\u4ee5\u5148\u653e\u5728\u4e00\u8fb9\uff0c\u628a\u5176\u4ed6\u4e09\u79cd\u4fee\u590d\u597d\u518d\u8bf4\u3002\u201d</p><p>\u8fd9\u4e5f\u662f\u4e2a\u529e\u6cd5\uff0c\u5148\u6613\u540e\u96be\uff0c\u8d70\u4e00\u6b65\u770b\u4e00\u6b65\uff0c\u8bf4\u4e0d\u5b9a\u524d\u9762\u505a\u5b8c\u4e86\uff0c\u540e\u9762\u4e5f\u8ddf\u7740\u6c34\u5230\u6e20\u6210\u3002</p><p>\u63a5\u4e0b\u6765\u5148\u505a\u533a\u5206\u5de5\u4f5c\uff0c\u7531\u7ef4\u82e5\u62c9\u5217\u51fa\u4e0d\u540c\u9b54\u6cd5\u4f53\u7cfb\u7684\u5404\u81ea\u7279\u5f81\uff0c\u743c\u6069\u548c\u73ca\u5609\u636e\u6b64\u5728\u56fe\u7eb8\u4e0a\u505a\u51fa\u6807\u8bc6\uff0c\u5c06\u5b83\u4eec\u5355\u72ec\u6574\u7406\u51fa\u6765\uff0c\u6700\u540e\u4ea4\u7ed9\u7ef4\u82e5\u62c9\u5ba1\u6838\u3002\u8fd9\u4e00\u6b65\u5012\u4e0d\u96be\uff0c\u53ea\u8981\u6709\u6700\u57fa\u7840\u7684\u9b54\u6cd5\u5b66\u77e5\u8bc6\u5c31\u80fd\u505a\uff0c\u53ea\u662f\u6781\u5176\u7e41\u7410\uff0c\u6885\u83f2\u65af\u5728\u65c1\u8fb9\u770b\u4e86\u4e00\u4f1a\uff0c\u4e5f\u53c2\u4e0e\u8fdb\u6765\u5e2e\u5fd9\u3002\u56db\u4eba\u6574\u6574\u5f04\u4e86\u4e00\u4e0b\u5348\uff0c\u773c\u770b\u592a\u9633\u90fd\u5df2\u7ecf\u5feb\u8981\u843d\u5c71\uff0c\u624d\u7b97\u52c9\u5f3a\u5b8c\u5de5\uff0c\u5c06\u7cbe\u7075\u3001\u7eb3\u83f2\u5c14\u548c\u8010\u745f\u4e09\u7cfb\u5206\u522b\u6807\u8bc6\u3001\u63d0\u53d6\u51fa\u6765\uff0c\u5269\u4e0b\u7684\u90a3\u4e9b\u81ea\u7136\u5c31\u662f\u89a1\u672f\u90e8\u5206\u4e86\u3002</p><p>\u201c\u4eca\u5929\u5c31\u5148\u5230\u8fd9\u91cc\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u660e\u5929\u518d\u7ee7\u7eed\u3002\u201d</p><p>\u665a\u996d\u7684\u65f6\u5019\uff0c\u743c\u6069\u53d1\u73b0\u4eba\u4e0d\u9f50\uff0c\u51db\u548c\u7fe1\u7fe0\u5973\u5deb\u8fd8\u6ca1\u56de\u6765\uff0c\u4ed6\u6709\u4e9b\u62c5\u5fc3\uff0c\u6253\u7b97\u8ba9\u838e\u73de\u514b\u53bb\u627e\u627e\uff0c\u7ed3\u679c\u624d\u53d1\u73b0\u9b45\u9b54\u4e5f\u5f88\u65e9\u5c31\u51fa\u95e8\u4e86\u3002\u4e0b\u5348\u4ed6\u4eec\u90fd\u5728\u4e13\u5fc3\u6574\u7406\u9b54\u6cd5\u9635\uff0c\u538b\u6839\u5c31\u6ca1\u6ce8\u610f\u3002</p><p>\u7b97\u4e86\uff0c\u4f30\u8ba1\u4e5f\u4e0d\u4f1a\u51fa\u4ec0\u4e48\u4e8b\u3002</p><p>\u665a\u996d\u540e\uff0c\u73ca\u5609\u56de\u81ea\u5df1\u623f\u95f4\uff0c\u5979\u6bcf\u5929\u7684\u5b66\u4e60\u8bfe\u7a0b\u6392\u5f97\u5f88\u6162\uff0c\u4ece\u4e0d\u61c8\u6020\u3002\u743c\u6069\u62c9\u7740\u6885\u83f2\u65af\u53bb\u9732\u53f0\u4e0a\u770b\u591c\u666f\u2014\u2014\u867d\u7136\u5176\u5b9e\u4e5f\u6ca1\u4ec0\u4e48\u597d\u770b\u7684\u3002\u201c\u4eca\u5929\u771f\u662f\u7d2f\u6b7b\u4e86\uff0c\u65e9\u77e5\u9053\u5c31\u4e0d\u8be5\u7b54\u5e94\u5510\u7426\u62c9\uff0c\u201d\u743c\u6069\u62b1\u6028\uff0c\u201c\u6211\u4ee5\u524d\u5728\u5b66\u6821\u91cc\uff0c\u4e5f\u7ecf\u5e38\u88ab\u5bfc\u5e08\u62c9\u53bb\u6253\u4e0b\u624b\uff0c\u53c2\u4e0e\u7ed8\u5236\u4e00\u4e9b\u5927\u578b\u9b54\u6cd5\u9635\uff0c\u4ece\u6765\u6ca1\u8fd9\u4e48\u7d2f\u8fc7\u3002\u201d</p><p>\u6885\u83f2\u65af\u5012\u662f\u6c14\u5b9a\u795e\u95f2\uff0c\u201c\u90a3\u662f\u56e0\u4e3a\u4f60\u770b\u4e0d\u61c2\u81ea\u5df1\u5728\u505a\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u660e\u767d\u81ea\u5df1\u6240\u505a\u7684\u8fd9\u4e9b\u6709\u4ec0\u4e48\u9053\u7406\u548c\u610f\u4e49\uff0c\u6240\u4ee5\u5c31\u89c9\u5f97\u683c\u5916\u9ebb\u70e6\u3002\u201d</p><p>\u743c\u6069\u7684\u786e\u770b\u4e0d\u61c2\uff0c\u4ed6\u5b66\u7684\u662f\u201c\u73b0\u4ee3\u201d\u9b54\u6cd5\u5b66\u4f53\u7cfb\uff0c\u6240\u8c13\u73b0\u4ee3\u9b54\u6cd5\u5b66\uff0c\u662f\u8010\u745f\u9668\u706d\u4e4b\u540e\uff0c\u9b54\u6cd5\u5973\u795e\u7efc\u5408\u4e86\u4e2d\u571f\u5404\u5927\u9b54\u6cd5\u5e1d\u56fd\u3001\u7cbe\u7075\u65cf\u3001\u9f99\u65cf\u7684\u9b54\u6cd5\u4f53\u7cfb\uff0c\u521b\u9020\u51fa\u6765\u7684\u4e00\u79cd\u4e1c\u897f\u3002\u5b83\u662f\u4e00\u79cd\u66f4\u7b80\u5316\u3001\u66f4\u6cdb\u7528\u7684\u7248\u672c\uff0c\u9002\u5408\u5165\u95e8\uff0c\u9002\u5408\u63a8\u5e7f\uff0c\u548c\u90a3\u4e9b\u4e0a\u53e4\u9b54\u6cd5\u4f53\u7cfb\u4e5f\u6709\u6e0a\u6e90\uff0c\u4f46\u6bd5\u7adf\u5df2\u7ecf\u4e0d\u662f\u4e00\u56de\u4e8b\u4e86\u3002</p><p>\u201c\u827e\u5f25\u8587\u4f60\u80fd\u770b\u61c2\uff1f\u201d</p><p>\u201c\u6211\u5f53\u7136\u66f4\u770b\u4e0d\u61c2\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u4f46\u6c89\u5f97\u4f4f\u6c14\uff0c\u8010\u5f97\u4f4f\u70e6\uff0c\u8fd9\u4e0d\u662f\u505a\u4e8b\u60c5\u7684\u57fa\u672c\u7d20\u8d28\u4e48\u3002\u201d</p><p>\u201c\u2026\u2026\u6211\u9519\u4e86\u3002\u201d</p><p>\u4e24\u4eba\u6b63\u5728\u95f2\u804a\uff0c\u51db\u5ffd\u7136\u8dd1\u4e86\u8fc7\u6765\uff0c\u201c\u6211\u56de\u6765\u4e86\uff01\u201d\u5979\u4e2d\u6c14\u5341\u8db3\u5730\u5ba3\u5e03\uff0c\u201c\u4f60\u4eec\u6709\u6ca1\u6709\u60f3\u6211\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u5e72\u561b\u53bb\u4e86\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u6211\u5f85\u7740\u5f88\u65e0\u804a\uff0c\u5c31\u62c9\u7740\u7fe1\u7fe0\u51fa\u53bb\u901b\u4e86\u901b\u8857\uff0c\u4f46\u8fd9\u57ce\u91cc\u8fde\u5356\u8863\u670d\u7684\u5546\u5e97\u90fd\u6ca1\u6709\uff0c\u771f\u662f\u6ca1\u52b2\u3002\u201d</p><p>\u201c\u6ca1\u52b2\u4f60\u8fd8\u5f04\u5230\u73b0\u5728\u8fd9\u4e48\u665a\u624d\u56de\u6765\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u78b0\u5230\u719f\u4eba\u4e86\u561b\uff0c\u6240\u4ee5\u591a\u804a\u4e86\u4f1a\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4f60\u4eec\u731c\u731c\u662f\u8c01\uff1f\u201d</p><p>\u4f60\u6bd5\u7adf\u662f\u5728\u8fd9\u8fb9\u51fa\u751f\u957f\u5927\u7684\uff0c\u6709\u4e9b\u719f\u4eba\u5f88\u6b63\u5e38\uff0c\u6211\u53c8\u4e0d\u8ba4\u8bc6\uff0c\u600e\u4e48\u731c\u5f97\u5230\u3002</p><p>\u201c\u743c\u6069\u4f60\u4e5f\u89c1\u8fc7\u7684\uff0c\u201d\u51db\u63d0\u793a\uff0c\u201c\u5c31\u5728\u524d\u6bb5\u65f6\u95f4\u3002\u201d</p><p>\u201c\u5580\u6d41\u5976\u5976\uff1f\u201d\u6885\u83f2\u65af\u5728\u65c1\u8fb9\u8bf4\u3002</p><p>\u201c\u731c\u5bf9\u4e86\uff0c\u8fd8\u662f\u827e\u5f25\u8587\u6bd4\u8f83\u806a\u660e\u3002\u201d</p><p>\u201c\u5979\u600e\u4e48\u6765\u4e86\uff1f\u201d\u743c\u6069\u5947\u602a\uff0c\u96be\u9053\u662f\u56e0\u4e3a\u57c3\u535c\u62c9\u5728\u8f9b\u5df4\u57ce\u641c\u6355\u7ea2\u9f99\u6559\u5f92\uff0c\u8001\u4eba\u5bb6\u4e0d\u613f\u5c48\u670d\uff0c\u5c31\u9003\u6765\u6b64\u5904\uff1f\u8fd9\u5012\u4e5f\u4e0d\u662f\u4e0d\u80fd\u7406\u89e3\uff0c\u95ee\u9898\u662f\u5979\u4e5f\u6765\u5f97\u592a\u5feb\u4e86\u5427\uff0c\u743c\u6069\u4e5f\u624d\u6628\u5929\u521a\u5230\u5462\u3002</p><p>\u201c\u5979\u662f\u88ab\u4f20\u9001\u8fc7\u6765\u7684\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4e0d\u6b62\u5979\uff0c\u8fd8\u6709\u5f88\u591a\u4eba\u5462\u3002\u201d</p><p>\u51db\u4e0a\u5348\u7761\u9192\u540e\uff0c\u743c\u6069\u3001\u73ca\u5609\u3001\u6885\u83f2\u65af\u90fd\u4e0d\u5728\uff0c\u5979\u5f85\u7740\u65e0\u804a\uff0c\u5c31\u51b3\u5b9a\u51fa\u53bb\u8d70\u8d70\u3002\u901b\u8857\u8fd9\u79cd\u4e8b\u60c5\u4e00\u5b9a\u4e0d\u80fd\u4e00\u4e2a\u4eba\uff0c\u5426\u5219\u5c31\u7d22\u7136\u65e0\u5473\uff0c\u51db\u672c\u6765\u60f3\u9080\u8bf7\u7ef4\u82e5\u62c9\u4e00\u8d77\uff0c\u7ed3\u679c\u4f20\u9053\u5deb\u5e08\u8868\u793a\u6ca1\u7a7a\uff0c\u5979\u548c\u838e\u73de\u514b\u4e00\u5411\u53c8\u4e0d\u662f\u5f88\u5bf9\u4ed8\uff0c\u4e8e\u662f\u4fbf\u62c9\u4e0a\u4e86\u7fe1\u7fe0\u5973\u5deb\u3002</p><p>\u5723\u6e0a\u57ce\u4e0d\u5927\uff0c\u6ca1\u534a\u5929\u5c31\u5168\u8f6c\u5b8c\u4e86\uff0c\u51db\u9887\u89c9\u65e0\u804a\uff0c\u6b63\u51c6\u5907\u56de\u53bb\uff0c\u5374\u88ab\u4eba\u53eb\u4f4f\u4e86\u3002\u5979\u56de\u5934\u4e00\u770b\uff0c\u5403\u4e86\u4e00\u60ca\uff0c\u539f\u6765\u662f\u5580\u6d41\u5976\u5976\u3002</p><p>\u548c\u5580\u6d41\u5976\u5976\u804a\u4e86\u804a\uff0c\u51db\u624d\u77e5\u9053\uff0c\u539f\u6765\u8f9b\u5df4\u57ce\u53d8\u4e71\u53d1\u751f\u540e\uff0c\u57c3\u535c\u62c9\u4e0a\u53f0\uff0c\u4e00\u65b9\u9762\u662f\u5168\u57ce\u641c\u6355\u7ea2\u9f99\u6559\u4f1a\u7684\u795e\u804c\u4eba\u5458\uff0c\u540c\u65f6\u8981\u6c42\u666e\u901a\u4fe1\u5f92\u516c\u5f00\u5ba3\u793a\u653e\u5f03\u4fe1\u4ef0\uff0c\u53e6\u4e00\u65b9\u9762\u5219\u662f\u9881\u5e03\u6cd5\u5f8b\uff0c\u547d\u4ee4\u65e7\u57ce\u533a\u7684\u5c45\u6c11\u5168\u90e8\u642c\u5bb6\u5230\u65b0\u57ce\u533a\uff0c\u9650\u671f\u4e09\u5929\uff0c\u903e\u671f\u4e0d\u4ece\u8005\u5f3a\u5236\u62c6\u8fc1\u3002\u8fd9\u6fc0\u8d77\u4e86\u65e7\u57ce\u533a\u5f88\u591a\u5c45\u6c11\u7684\u4e0d\u6ee1\uff0c\u8bb8\u591a\u4eba\u548c\u5580\u6d41\u5976\u5976\u4e00\u6837\uff0c\u65e2\u662f\u7ea2\u9f99\u7684\u8654\u8bda\u4fe1\u5f92\uff0c\u53c8\u5728\u65e7\u57ce\u533a\u4f4f\u4e86\u5927\u534a\u8f88\u5b50\uff0c\u4e0d\u80af\u642c\u8fc1\uff0c\u4fbf\u548c\u57c3\u535c\u62c9\u6d3e\u6765\u201c\u76d1\u7763\u201d\u642c\u8fc1\u7684\u5b98\u5458\u53d1\u751f\u4e86\u51b2\u7a81\u3002\u51b2\u7a81\u8d8a\u6765\u8d8a\u6fc0\u70c8\uff0c\u6700\u540e\u95f9\u5230\u4e86\u519b\u961f\u51fa\u52a8\u7684\u5730\u6b65\uff0c\u57c3\u535c\u62c9\u4e5f\u5f53\u771f\u662f\u6beb\u65e0\u987e\u5fcc\uff0c\u5c45\u7136\u4e0b\u4ee4\u653e\u706b\uff0c\u5c06\u65e7\u57ce\u533a\u76f4\u63a5\u4ed8\u4e4b\u4e00\u70ac\u3002</p><p>\u201c\u8fd9\u5bb6\u4f19\u4e5f\u592a\u4e27\u5fc3\u75c5\u72c2\u4e86\u5427\u3002\u201d\u743c\u6069\u5438\u4e86\u53e3\u51b7\u6c14\u3002</p><p>\u4ed6\u662f\u53bb\u8fc7\u8f9b\u5df4\u57ce\u65e7\u57ce\u533a\u7684\uff0c\u77e5\u9053\u8fd8\u7559\u5728\u90a3\u91cc\u7684\u5c45\u6c11\uff0c\u4ee5\u8001\u5f31\u5987\u5b7a\u5c45\u591a\uff0c\u817f\u811a\u90fd\u4e0d\u4f1a\u5f88\u7075\u4fbf\u3002\u57c3\u535c\u62c9\u662f\u4e0d\u662f\u6709\u4ec0\u4e48\u57ce\u533a\u6539\u9020\u7684\u8ba1\u5212\u4e14\u4e0d\u8c08\uff0c\u4f46\u8fd9\u6837\u4e00\u628a\u706b\u653e\u8d77\u6765\uff0c\u5fc5\u7136\u5bfc\u81f4\u4f24\u4ea1\u60e8\u91cd\uff0c\u4e0d\u77e5\u9053\u6709\u591a\u5c11\u4eba\u4f1a\u846c\u8eab\u706b\u6d77\u3002\u8fd9\u5bb6\u4f19\u770b\u8d77\u6765\u6587\u8d28\u5f6c\u5f6c\uff0c\u4e0b\u624b\u5c45\u7136\u5982\u6b64\u72e0\u8fa3\uff0c\u4e0d\u4f1a\u771f\u7684\u662f\u88ab\u4ea1\u7075\u9644\u4f53\u4e86\u5427\u3002</p><p>\u6885\u83f2\u65af\u4e5f\u662f\u9762\u6709\u6012\u8272\uff0c\u505c\u987f\u4e86\u4e00\u4f1a\uff0c\u95ee\uff1a\u201c\u90a3\u5580\u6d41\u5976\u5976\u662f\u600e\u4e48\u9003\u51fa\u6765\u7684\uff1f\u201d</p><p>\u201c\u5979\u4e0d\u662f\u524d\u51e0\u5929\u6454\u4f24\u4e86\u4e48\uff0c\u672c\u6765\u5728\u5bb6\u8eba\u7740\uff0c\u8d70\u53c8\u8d70\u4e0d\u52a8\uff0c\u5927\u706b\u71c3\u8d77\u65f6\uff0c\u4ee5\u4e3a\u6b7b\u5b9a\u4e86\uff0c\u7ed3\u679c\u5979\u653e\u5728\u624b\u8fb9\u7684\u90a3\u4e2a\u7ea2\u9f99\u96d5\u50cf\u7a81\u7136\u653e\u51fa\u5f3a\u5149\uff0c\u7167\u5728\u5979\u8138\u4e0a\u3002\u7b49\u5979\u518d\u7741\u5f00\u773c\u775b\uff0c\u5c31\u5230\u4e86\u5723\u6e0a\u57ce\u3002\u542c\u8bf4\u9664\u4e86\u5979\u4e4b\u5916\uff0c\u8fd8\u6709\u5f88\u591a\u4eba\uff0c\u53ea\u8981\u5bb6\u91cc\u6709\u7ea2\u9f99\u96d5\u50cf\u7684\uff0c\u90fd\u8fd9\u6837\u88ab\u4f20\u9001\u8fc7\u6765\u4e86\u3002\u5230\u4e86\u8fd9\u91cc\u4e4b\u540e\uff0c\u4f0a\u68ee\u5c31\u6d3e\u4eba\u7ec4\u7ec7\uff0c\u628a\u4ed6\u4eec\u90fd\u5b89\u987f\u5728\u57ce\u5357\u7684\u5175\u8425\u91cc\u3002\u201d</p><p>\u743c\u6069\u6c89\u541f\u7247\u523b\uff0c\u201c\u5175\u8425\u91cc\u6761\u4ef6\u5e94\u8be5\u4e0d\u4f1a\u592a\u597d\uff0c\u8001\u4eba\u5bb6\u7684\u817f\u53c8\u53d7\u4f24\u672a\u6108\uff0c\u4e0d\u5982\u8bf7\u5979\u6765\u8fd9\u91cc\u4f4f\u5427\uff0c\u201d\u4ed6\u5bf9\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6211\u53ef\u4ee5\u7528\u8428\u74e6\u68cb\u9b54\u50cf\u4e3a\u5979\u6cbb\u7597\uff0c\u800c\u4e14\u8fd9\u91cc\u4e5f\u66f4\u5b89\u5168\u4e00\u4e9b\u3002\u201d</p><p>\u201c\u6211\u5df2\u7ecf\u9080\u8bf7\u4e86\uff0c\u4f46\u5979\u4e0d\u80af\u6765\uff0c\u8bf4\u548c\u5e74\u8f7b\u4eba\u4f4f\u5728\u4e00\u8d77\u4e0d\u4e60\u60ef\uff0c\u4f1a\u6253\u6270\u6211\u4eec\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u6211\u529d\u4e86\u534a\u5929\uff0c\u5979\u575a\u6301\u4e0d\u540c\u610f\uff0c\u6700\u540e\u6211\u53ea\u597d\u9001\u5979\u56de\u53bb\u4e86\u3002\u201d</p><p>\u201c\u90a3\u597d\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u660e\u5929\u2026\u2026\u660e\u5929\u4e0d\u4e00\u5b9a\u6709\u7a7a\uff0c\u7b49\u7a7a\u4e0b\u6765\u6211\u518d\u548c\u4f60\u53bb\u770b\u671b\u5979\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u63e1\u7740\u5c11\u5973\u7684\u624b\uff0c\u201c\u5230\u65f6\u5019\u4e00\u8d77\u53bb\u5427\u3002\u201d</p><p>\u6885\u83f2\u65af\u72b9\u8c6b\u4e86\u4e0b\uff0c\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u203b\u203b\u203b</p><p>\u6765\u5230\u5723\u6e0a\u57ce\u7684\u7b2c\u4e09\u5929\uff0c\u743c\u6069\u4e00\u65e9\u8d77\u5e8a\uff0c\u627e\u5230\u7ef4\u82e5\u62c9\uff0c\u7ee7\u7eed\u6628\u5929\u672a\u5b8c\u6210\u7684\u5de5\u4f5c\u3002</p><p>\u73ca\u5609\u548c\u6885\u83f2\u65af\u4f9d\u7136\u8fc7\u6765\u5e2e\u5fd9\uff0c\u51db\u5728\u7761\u61d2\u89c9\uff0c\u838e\u73de\u514b\u4ecd\u7136\u6ca1\u770b\u89c1\u4eba\u5f71\uff0c\u4e0d\u77e5\u9053\u8dd1\u54ea\u91cc\u53bb\u4e86\uff0c\u4e0d\u8fc7\u73ca\u5609\u8bf4\u6628\u5929\u665a\u4e0a\u5979\u56de\u6765\u8fc7\u3002</p><p>\u7b2c\u4e8c\u5929\u7684\u8fdb\u5ea6\u6bd4\u7b2c\u4e00\u5929\u8981\u5feb\u5f88\u591a\uff0c\u5927\u6982\u4e5f\u662f\u6709\u4e86\u7ecf\u9a8c\u7684\u7f18\u6545\u3002\u4e0b\u5348\u4e09\u70b9\u949f\uff0c\u7ec8\u4e8e\u6240\u6709\u7684\u6574\u7406\u5de5\u4f5c\u5168\u90e8\u7ed3\u675f\u3002\u63a5\u4e0b\u6765\uff0c\u7ef4\u82e5\u62c9\u8981\u5c06\u9664\u4e86\u89a1\u672f\u4e4b\u5916\u7684\u90e8\u5206\uff0c\u6309\u7167\u4e0d\u540c\u7684\u539f\u7406\u3001\u89c4\u5219\u4fee\u590d\u5b8c\u6210\uff0c\u8fd9\u4e2a\u8fc7\u7a0b\u9884\u8ba1\u8981\u4e24\u4e09\u5929\uff0c\u743c\u6069\u5c31\u5e2e\u4e0d\u4e0a\u592a\u591a\u5fd9\u4e86\u3002</p><p>\u201c\u89a1\u672f\u7684\u90e8\u5206\u600e\u4e48\u529e\uff1f\u201d</p><p>\u743c\u6069\u4e5f\u6ca1\u4ec0\u4e48\u597d\u529e\u6cd5\uff0c\u53ea\u80fd\u62b1\u7740\u78b0\u8fd0\u6c14\u8bd5\u8bd5\u770b\u7684\u6001\u5ea6\uff0c\u53bb\u8bf7\u7fe1\u7fe0\u5973\u5deb\u3002</p><p>\u5973\u5deb\u5012\u662f\u5f88\u5feb\u5c31\u6765\u4e86\uff0c\u743c\u6069\u53d1\u73b0\u5979\u591a\u4e86\u4e00\u6761\u9879\u94fe\uff0c\u800c\u4e14\u4f3c\u4e4e\u6709\u70b9\u773c\u719f\uff0c\u60f3\u4e86\u60f3\u624d\u8bb0\u8d77\u6765\u662f\u51db\u7684\u3002\u770b\u8d77\u6765\u5979\u4eec\u7684\u611f\u60c5\u8fdb\u5c55\u5f88\u5feb\uff0c\u743c\u6069\u6697\u6697\u5410\u69fd\u4e86\u4e00\u53e5\uff0c\u7136\u540e\u8bf4\u660e\u6765\u610f\u3002</p><p>\u201c\u6211\u770b\u770b\u3002\u201d</p><p>\u5973\u5deb\u8fd9\u4e00\u770b\u5c31\u662f\u534a\u5c0f\u65f6\uff0c\u671f\u95f4\u5979\u6574\u4e2a\u4eba\u4e00\u52a8\u4e5f\u4e0d\u52a8\uff0c\u51dd\u89c6\u7740\u9762\u524d\u7684\u56fe\u7eb8\u3002\u6b63\u5f53\u743c\u6069\u4ee5\u4e3a\u5979\u53c8\u4e60\u60ef\u6027\u5730\u8fdb\u5165\u51a5\u60f3\u72b6\u6001\uff0c\u8003\u8651\u8981\u4e0d\u8981\u628a\u5979\u53eb\u9192\u65f6\uff0c\u5979\u5ffd\u7136\u8bf4\u8bdd\u4e86\uff0c\u201c\u6211\u89c1\u8fc7\u8fd9\u4e1c\u897f\u3002\u201d</p><p>\u201c\u4f60\u89c1\u8fc7\uff1f\u201d</p><p>\u201c\u55ef\uff0c\u89c1\u8fc7\uff0c\u4f46\u60f3\u4e0d\u8d77\u6765\u662f\u4ec0\u4e48\u65f6\u5019\u4e86\uff0c\u5e94\u8be5\u662f\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u597d\u50cf\u4e5f\u662f\u6709\u4eba\u62ff\u7ed9\u6211\u770b\u8fc7\u3002\u201d</p><p>\u4f60\u4e5f\u5c31\u4e8c\u5341\u6765\u5c81\u5427\uff0c\u6240\u8c13\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u662f\u6709\u591a\u4e45\u554a\u3002</p><p>\u5f53\u7136\u8fd9\u4e9b\u4e0d\u662f\u91cd\u70b9\uff0c\u201c\u90a3\u4f60\u80fd\u4fee\u590d\u5b83\u4e48\uff1f\u201d</p><p>\u201c\u5e94\u8be5\u53ef\u4ee5\uff0c\u201d\u5973\u5deb\u8bf4\uff0c\u201c\u6211\u80fd\u4e00\u70b9\u70b9\u5730\u56de\u5fc6\u8d77\u6765\u3002\u201d</p><p>\u201c\u90a3\u592a\u597d\u4e86\u3002\u201d</p><p>\u539f\u672c\u4ee5\u4e3a\u6700\u68d8\u624b\u7684\u95ee\u9898\u5c31\u8fd9\u6837\u8f7b\u6613\u89e3\u51b3\uff0c\u771f\u662f\u51fa\u4e4e\u610f\u6599\u3002\u743c\u6069\u5f88\u5e86\u5e78\u5f53\u65f6\u628a\u5973\u5deb\u5e26\u4e0a\uff0c\u5426\u5219\u73b0\u5728\u5c31\u4e0d\u597d\u529e\u4e86\u3002</p><p>\u7ef4\u82e5\u62c9\u548c\u7fe1\u7fe0\u5973\u5deb\u53bb\u4fee\u590d\u9b54\u6cd5\u9635\uff0c\u73ca\u5609\u8868\u793a\u60f3\u501f\u673a\u5b66\u4e60\uff0c\u5e2e\u5979\u4eec\u6253\u4e0b\u624b\u3002\u6885\u83f2\u65af\u5bf9\u9b54\u6cd5\u5174\u8da3\u4e0d\u5927\uff0c\u5148\u56de\u81ea\u5df1\u623f\u95f4\u4e86\uff0c\u743c\u6069\u6b63\u8003\u8651\u662f\u4e0d\u662f\u4e5f\u56de\u623f\u95f4\u53bb\u8865\u4e2a\u5348\u89c9\uff0c\u521a\u8d70\u5230\u697c\u68af\u53e3\uff0c\u5c31\u770b\u89c1\u838e\u73de\u514b\u4ece\u4e0a\u9762\u8d70\u4e0b\u6765\u3002</p><p>\u201c\u4e0b\u5348\u597d\uff0c\u4e3b\u4eba\uff0c\u201d\u9b45\u9b54\u6253\u62db\u547c\uff0c\u201c\u4f60\u8fd9\u662f\u8981\u53bb\u6349\u5978\u5417\uff1f\u201d</p><p>\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u6211\u521a\u624d\u5728\u8857\u4e0a\u770b\u5230\u51db\u4e86\uff0c\u201d\u838e\u73de\u514b\u7b11\u5f97\u4e0d\u6000\u597d\u610f\uff0c\u201c\u548c\u4e00\u4e2a\u7537\u4eba\u5728\u7ea6\u4f1a\u54e6\uff0c\u4e24\u4e2a\u4eba\u5750\u5728\u4e00\u8d77\u804a\u5929\u5403\u4e1c\u897f\uff0c\u90a3\u4e2a\u7537\u4eba\u770b\u8d77\u6765\u53c8\u5e74\u8f7b\u53c8\u5e05\u6c14\uff0c\u800c\u4e14\u633a\u6709\u94b1\u7684\u6837\u5b50\uff0c\u597d\u50cf\u53eb\u4f0a\u68ee\u4ec0\u4e48\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u8be5\u5e72\u561b\u5e72\u561b\u53bb\u3002\u201d</p><p>\u201c\u4f60\u771f\u7684\u4e0d\u53bb\u770b\u770b\u5417\uff1f\u201d\u838e\u73de\u514b\u683c\u683c\u5730\u7b11\uff0c\u201c\u867d\u7136\u51db\u4e0d\u662f\u4e3b\u4eba\u6700\u559c\u6b22\u7684\u73a9\u7269\uff0c\u4f46\u5982\u679c\u5c31\u8fd9\u6837\u88ab\u4eba\u62a2\u8d70\u4e86\uff0c\u5e94\u8be5\u8fd8\u662f\u4f1a\u5f88\u6709\u635f\u81ea\u5c0a\u5fc3\u7684\u5427\u3002\u201d</p><p>\u201c\u9996\u5148\uff0c\u51db\u4e0d\u662f\u4ec0\u4e48\u73a9\u7269\uff0c\u5979\u662f\u6211\u7684\u5973\u53cb\u2026\u2026\u4e4b\u4e00\uff0c\u662f\u6211\u559c\u6b22\u5e76\u4e14\u5c0a\u91cd\u7684\u5973\u5b69\u5b50\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5176\u6b21\uff0c\u4f0a\u68ee\u662f\u5979\u7684\u670b\u53cb\uff0c\u4ed6\u4eec\u4e45\u522b\u91cd\u9022\uff0c\u804a\u804a\u5929\u662f\u5f88\u6b63\u5e38\u7684\u4e8b\u60c5\uff1b\u6700\u540e\uff0c\u6211\u7684\u81ea\u5c0a\u5fc3\u6ca1\u90a3\u4e48\u8106\u5f31\u3002\u201d</p><p>\u201c\u4e3b\u4eba\u771f\u662f\u5fc3\u80f8\u5bbd\u5e7f\uff0c\u201d\u9b45\u9b54\u79f0\u8d5e\uff0c\u201c\u6211\u672c\u6765\u8fd8\u60f3\u53bb\u5e2e\u4f60\u76ef\u7740\u4ed6\u4eec\uff0c\u514d\u5f97\u51fa\u4ec0\u4e48\u4e8b\uff0c\u770b\u6765\u5b8c\u5168\u6ca1\u6709\u8fd9\u4e2a\u5fc5\u8981\uff0c\u662f\u6211\u60f3\u592a\u591a\u4e86\u3002\u90a3\u4e48\uff0c\u6211\u53bb\u4f11\u606f\u4e86\u3002\u201d</p><p>\u743c\u6069\u77a5\u4e86\u5979\u4e00\u773c\uff0c\u6446\u4e86\u6446\u624b\uff0c\u793a\u610f\u5979\u8d76\u5feb\u6d88\u5931\u3002</p><p>\u539f\u672c\u6253\u7b97\u56de\u623f\u95f4\u7761\u89c9\uff0c\u7136\u800c\u8eba\u5728\u5e8a\u4e0a\u534a\u5929\uff0c\u59cb\u7ec8\u6709\u4e9b\u5fc3\u795e\u4e0d\u5b81\uff0c\u600e\u4e48\u7761\u4e5f\u7761\u4e0d\u7740\uff0c\u53ea\u597d\u722c\u8d77\u6765\u770b\u4e66\u3002\u508d\u665a\u65f6\u5206\uff0c\u51db\u7ec8\u4e8e\u56de\u6765\u4e86\u3002</p><p>\u5979\u4ece\u743c\u6069\u7684\u623f\u95f4\u95e8\u53e3\u7ecf\u8fc7\uff0c\u8fdb\u4e86\u9694\u58c1\uff0c\u90a3\u662f\u6885\u83f2\u65af\u7684\u623f\u95f4\u3002\u8fc7\u4e86\u51e0\u5206\u949f\uff0c\u9690\u5f62\u4ec6\u5f79\u98d8\u8fc7\u6765\uff0c\u5bf9\u743c\u6069\u8bf4\u6885\u83f2\u65af\u5c0f\u59d0\u8bf7\u4ed6\u8fc7\u53bb\u3002\u743c\u6069\u8fdb\u95e8\u4e00\u770b\uff0c\u53d1\u73b0\u4e24\u4f4d\u5c11\u5973\u6b63\u895f\u5371\u5750\uff0c\u795e\u60c5\u90fd\u5f88\u4e25\u8083\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u53d1\u751f\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u6211\u4e0b\u5348\u53bb\u901b\u8857\uff0c\u6070\u597d\u9047\u5230\u4e86\u4f0a\u68ee\uff0c\u4e0d\uff0c\u5176\u5b9e\u662f\u4ed6\u7279\u5730\u6765\u627e\u6211\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4e00\u5f00\u59cb\u662f\u5355\u7eaf\u95f2\u804a\uff0c\u540e\u6765\u6211\u53d1\u73b0\u4ed6\u5fc3\u91cc\u6709\u4e8b\uff0c\u5c31\u95ee\u4ed6\uff0c\u4ed6\u541e\u541e\u5410\u5410\u4e86\u534a\u5929\uff0c\u6700\u540e\u8bf4\u4e86\u4e00\u4ef6\u4e8b\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u201c\u4ed6\u8bf4\uff0c\u5510\u7426\u62c9\u6709\u95ee\u9898\u3002\u201d</p><p>\u201c\u600e\u4e48\u4e2a\u6709\u95ee\u9898\u6cd5\uff1f\u201d</p><p>\u201c\u4ed6\u5176\u5b9e\u4e5f\u8bf4\u4e0d\u6e05\uff0c\u5c31\u662f\u611f\u89c9\u4e0d\u5bf9\u52b2\uff0c\u8bf4\u662f\u81ea\u4ece\u5230\u4e86\u8fd9\u91cc\u4ee5\u540e\uff0c\u5510\u7426\u62c9\u5c31\u548c\u4ee5\u524d\u4e0d\u592a\u4e00\u6837\u4e86\u3002\u4f46\u6211\u95ee\u4ed6\u7a76\u7adf\u6709\u4ec0\u4e48\u5730\u65b9\u4e0d\u4e00\u6837\uff0c\u4ed6\u53c8\u8bf4\u4e0d\u51fa\u6765\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4ed6\u73b0\u5728\u5f88\u5bb3\u6015\uff0c\u603b\u89c9\u5f97\u5510\u7426\u62c9\u50cf\u662f\u88ab\u4eba\u79d8\u5bc6\u66ff\u6362\u4e86\u8eab\u4efd\u3002\u4ed6\u60f3\u8bf7\u6211\u4eec\u5e2e\u5fd9\u8c03\u67e5\u3002\u201d</p><p>\u201c\u7b49\u4e0b\uff0c\u4f0a\u68ee\u4e0d\u662f\u5510\u7426\u62c9\u7684\u79c1\u751f\u5b50\u4e48\uff1f\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4ed6\u4eec\u7236\u5b50\u600e\u4e48\u4f1a\u5185\u8ba7\uff1f\u201d</p><p>\u51db\u4e0d\u9ad8\u5174\u5730\u77aa\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u90a3\u53ea\u662f\u6ca1\u6709\u6839\u636e\u7684\u8c23\u4f20\u800c\u5df2\uff0c\u4f60\u522b\u4e71\u8bf4\u2014\u2014\u5c24\u5176\u522b\u5f53\u7740\u4f0a\u68ee\u7684\u9762\u8bf4\uff0c\u4ed6\u7279\u522b\u5fcc\u8bb3\u8fd9\u4e2a\u7684\u3002\u201d</p><p>\u201c\u6211\u5f00\u4e2a\u73a9\u7b11\uff0c\u201d\u743c\u6069\u8d76\u5fd9\u9053\u6b49\uff0c\u201c\u65e2\u7136\u4ed6\u8fd9\u4e48\u8bf4\u4e86\uff0c\u6211\u89c9\u5f97\u90a3\u5c31\u53bb\u67e5\u67e5\u770b\u5427\u3002\u201d</p><p>\u672c\u6765\u5c31\u89c9\u5f97\u5510\u7426\u62c9\u8fd9\u4eba\u6709\u7591\u70b9\uff0c\u65e2\u7136\u73b0\u5728\u4f0a\u68ee\u4e5f\u8fd9\u4e48\u8bf4\u2014\u2014\u79c1\u751f\u5b50\u7684\u95ee\u9898\u4e0d\u8c08\uff0c\u4f0a\u68ee\u81f3\u5c11\u4e5f\u662f\u4ed6\u5b66\u751f\u2014\u2014\u90a3\u5f53\u7136\u8981\u597d\u597d\u8c03\u67e5\u4e00\u4e0b\uff0c\u4e07\u4e00\u4ed6\u771f\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u4e5f\u597d\u65e9\u505a\u51c6\u5907\u3002\u5012\u662f\u6885\u83f2\u65af\u6c89\u541f\u4e0d\u8bed\uff0c\u8ba9\u4ed6\u6709\u4e9b\u5947\u602a\u3002</p><p>\u201c\u4f0a\u68ee\u8bf4\u8bdd\u672a\u5fc5\u53ef\u9760\uff0c\u201d\u51db\u5411\u743c\u6069\u89e3\u91ca\uff0c\u201c\u8fd9\u4eba\u7591\u5fc3\u75c5\u633a\u91cd\u7684\u3002\u5c31\u50cf\u4ed6\u548c\u8d1d\u745f\u65af\uff0c\u4e5f\u7b97\u662f\u9752\u6885\u7af9\u9a6c\u4e86\uff0c\u7ed3\u679c\u4ed6\u603b\u662f\u6000\u7591\u8d1d\u745f\u65af\u548c\u522b\u7684\u7537\u4eba\u6709\u67d3\uff0c\u95f9\u8fc7\u5f88\u591a\u6b21\uff0c\u5176\u5b9e\u5168\u90fd\u662f\u4ed6\u81ea\u5df1\u81c6\u60f3\uff0c\u65e0\u51ed\u65e0\u636e\u3002\u6211\u662f\u89c9\u5f97\u4ed6\u8fd9\u6b21\u8bf4\u8bdd\u4e0d\u592a\u50cf\u5047\u7684\uff0c\u4f46\u8fd9\u4e1c\u897f\u4e5f\u5f88\u96be\u8bb2\u3002\u201d</p><p>\u201c\u539f\u6765\u4ed6\u8fd8\u6709\u8fd9\u6bdb\u75c5\uff0c\u96be\u602a\u4f60\u4e0d\u559c\u6b22\u4ed6\u3002\u201d</p><p>\u201c\u6211\u4e5f\u6ca1\u6709\u4e0d\u559c\u6b22\u4ed6\u554a\uff0c\u505a\u604b\u4eba\u80af\u5b9a\u4e0d\u884c\uff0c\u505a\u670b\u53cb\u8fd8\u662f\u53ef\u4ee5\u7684\u3002\u201d</p><p>\u201c\u6211\u8bf4\u7684\u559c\u6b22\u548c\u4f60\u8bf4\u7684\u4e0d\u662f\u4e00\u7801\u4e8b\u3002\u201d</p><p>\u6885\u83f2\u65af\u60f3\u4e86\u7247\u523b\uff0c\u505a\u51fa\u51b3\u5b9a\uff0c\u201c\u90a3\u5c31\u8c03\u67e5\u4e00\u4e0b\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8c28\u614e\u70b9\u603b\u6ca1\u9519\u3002\u201d</p><p>\u743c\u6069\u5f53\u7136\u6ca1\u6709\u5f02\u8bae\uff0c\u201c\u90a3\u4e48\u4ece\u54ea\u91cc\u5f00\u59cb\u7740\u624b\uff0c\u4f60\u4eec\u6709\u4ec0\u4e48\u4e3b\u610f\uff1f\u201d</p><p>\u201c\u5c31\u4eca\u665a\u5427\uff0c\u6211\u4eec\u53bb\u5510\u7426\u62c9\u5bb6\u91cc\u770b\u770b\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u6709\u4ec0\u4e48\u53d1\u73b0\uff0c\u201d\u51db\u63d0\u8bae\uff0c\u201c\u4f0a\u68ee\u8bf4\u4eca\u665a\u4ed6\u4eec\u8981\u4e3e\u529e\u5bb4\u4f1a\uff0c\u5510\u7426\u62c9\u4f1a\u51fa\u5e2d\uff0c\u5f88\u665a\u624d\u4f1a\u56de\u5bb6\uff0c\u662f\u4e2a\u597d\u673a\u4f1a\u3002\u201d</p><p>\u6885\u83f2\u65af\u70b9\u5934\uff0c\u4e8e\u662f\u5c31\u8fd9\u6837\u51b3\u5b9a\u4e86\u3002</p><p>\u4e03\u70b9\u949f\uff0c\u743c\u6069\u3001\u6885\u83f2\u65af\u3001\u51db\u548c\u838e\u73de\u514b\u56db\u4eba\u51fa\u95e8\uff0c\u7528\u4f2a\u88c5\u6cd5\u672f\u906e\u63a9\u76f8\u8c8c\uff0c\u7ed5\u4e86\u4e00\u4e2a\u5927\u5708\uff0c\u6765\u5230\u5510\u7426\u62c9\u5bb6\u9644\u8fd1\u3002\u51db\u672c\u6765\u4e0d\u662f\u5f88\u60f3\u5e26\u4e0a\u838e\u73de\u514b\uff0c\u4f46\u743c\u6069\u8003\u8651\u5230\u8fd9\u79cd\u4e8b\u60c5\uff0c\u8fd8\u662f\u8981\u6709\u4e13\u4e1a\u4eba\u58eb\u5728\u573a\u624d\u653e\u5fc3\uff0c\u8c01\u77e5\u9053\u5510\u7426\u62c9\u5bb6\u91cc\u4f1a\u4e0d\u4f1a\u904d\u5730\u9677\u9631\u5462\u3002</p><p>\u5510\u7426\u62c9\u662f\u57ce\u4e3b\u7684\u526f\u624b\uff0c\u6709\u81ea\u5df1\u7684\u4e00\u5ea7\u5b85\u9662\uff0c\u4e24\u5c42\u5c0f\u697c\uff0c\u8fd8\u5e26\u5730\u4e0b\u5ba4\uff0c\u4f46\u4ed6\u6ca1\u7ed3\u5a5a\uff0c\u6ca1\u5b50\u5973\uff0c\u4e5f\u6ca1\u6709\u5176\u4ed6\u5bb6\u4eba\u540c\u4f4f\uff0c\u636e\u4f0a\u68ee\u8bf4\uff0c\u5bb6\u91cc\u9664\u4e86\u4ed6\u672c\u4eba\u4e4b\u5916\uff0c\u5c31\u53ea\u6709\u4e24\u4e2a\u4ec6\u4eba\u3002\u5510\u7426\u62c9\u6b64\u65f6\u5728\u57ce\u4e3b\u5b98\u90b8\u53c2\u52a0\u5bb4\u4f1a\uff0c\u4e00\u65f6\u534a\u4f1a\u662f\u4e0d\u4f1a\u56de\u6765\u7684\uff0c\u90a3\u4e24\u4e2a\u4ec6\u4eba\u4e5f\u5f88\u5bb9\u6613\u641e\u5b9a\uff0c\u4e8b\u5148\u77e5\u9053\u4ed6\u4eec\u7684\u623f\u95f4\u4f4d\u7f6e\uff0c\u8ba9\u838e\u73de\u514b\u4e22\u4e86\u70b9\u8ff7\u836f\u8fdb\u53bb\uff0c\u4ed6\u4eec\u5c31\u8fdb\u5165\u719f\u7761\u72b6\u6001\uff0c\u5c31\u7b97\u5916\u9762\u641e\u62c6\u8fc1\u4e5f\u4e0d\u4f1a\u9192\u4e86\u3002</p><p>\u201c\u597d\u50cf\u4e5f\u6ca1\u4ec0\u4e48\u554a\uff0c\u201d\u51db\u5728\u697c\u4e0b\u8f6c\u4e86\u4e00\u5708\uff0c\u5f88\u5931\u671b\u5730\u8bf4\uff0c\u201c\u90fd\u5f88\u6b63\u5e38\u7684\u6837\u5b50\uff0c\u4f60\u4eec\u6709\u4ec0\u4e48\u53d1\u73b0\u6ca1\u6709\uff1f\u201d</p><p>\u743c\u6069\u548c\u6885\u83f2\u65af\u641c\u7d22\u7684\u662f\u5730\u4e0b\u5ba4\uff0c\u4e5f\u6ca1\u53d1\u73b0\u4ec0\u4e48\uff0c\u5510\u7426\u62c9\u770b\u8d77\u6765\u662f\u4e2a\u7b80\u6734\u7684\u4eba\uff0c\u867d\u7136\u4e5f\u7b97\u4f4d\u9ad8\u6743\u91cd\uff0c\u5bb6\u91cc\u7684\u9648\u8bbe\u5374\u5f88\u7b80\u5355\uff0c\u6ca1\u4ec0\u4e48\u591a\u4f59\u7684\u4e1c\u897f\uff0c\u5730\u4e0b\u5ba4\u91cc\u5806\u7684\u90fd\u662f\u4e00\u4e9b\u6742\u7269\u3002\u5c31\u5728\u8fd9\u65f6\u5019\uff0c\u838e\u73de\u514b\u5728\u58f0\u97f3\u4ece\u697c\u4e0a\u4f20\u6765\uff1a\u201c\u4f60\u4eec\u8fc7\u6765\u4e00\u4e0b\u3002\u201d</p><p>\u4e09\u4eba\u4e0a\u697c\uff0c\u697c\u4e0a\u662f\u5510\u7426\u62c9\u7684\u5367\u5ba4\u548c\u4e66\u623f\uff0c\u838e\u73de\u514b\u6b63\u5728\u4e66\u623f\u91cc\u3002\u89c1\u743c\u6069\u4e0a\u6765\uff0c\u5979\u6307\u4e86\u6307\u4e00\u5ea7\u4e66\u67b6\u3002\u201c\u600e\u4e48\u4e86\uff1f\u201d\u743c\u6069\u770b\u4e86\u4e24\u773c\uff0c\u6ca1\u53d1\u89c9\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u4e66\u67b6\u662f\u6728\u5934\u505a\u7684\uff0c\u56fa\u5b9a\u5728\u5899\u58c1\u4e0a\uff0c\u4e0a\u9762\u653e\u6ee1\u4e86\u4e66\uff0c\u4ed6\u968f\u4fbf\u62bd\u4e86\u51e0\u672c\u51fa\u6765\u7ffb\u4e86\u7ffb\uff0c\u90fd\u662f\u4e00\u4e9b\u5173\u4e8e\u4e2d\u571f\u5927\u9646\u5386\u53f2\u3001\u5730\u7406\u7684\u8d44\u6599\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u51fa\u5947\u7684\u3002</p><p>\u201c\u8fd9\u91cc\u6709\u673a\u5173\u3002\u201d\u838e\u73de\u514b\u8bf4\uff0c\u5728\u4e66\u67b6\u5e95\u90e8\u9760\u5730\u9762\u7684\u4e00\u4e2a\u5730\u65b9\uff0c\u7528\u529b\u5730\u538b\u4e86\u4e00\u4e0b\u3002</p><p>\u5494\u5494\u5494\u4e00\u9635\u54cd\uff0c\u4e66\u67b6\u7684\u5176\u4e2d\u56db\u683c\u5ffd\u7136\u52a8\u8d77\u6765\uff0c\u968f\u7740\u80cc\u540e\u7684\u5899\u58c1\u4e00\u540c\u7ffb\u8f6c\uff0c\u9732\u51fa\u4e00\u526f\u753b\u50cf\u3002\u90a3\u662f\u4e00\u4f4d\u8eab\u7a7f\u91cd\u7532\uff0c\u624b\u6267\u957f\u5251\u7684\u7537\u5b50\uff0c\u987b\u9aef\u6d53\u5bc6\uff0c\u53cc\u76ee\u70af\u70af\uff0c\u5728\u4ed6\u7684\u8eab\u540e\uff0c\u5e9e\u5927\u7684\u7ea2\u9f99\u6b63\u632f\u7ffc\u6b32\u98de\uff0c\u53e3\u4e2d\u55b7\u51fa\u70c8\u7130\u718a\u718a\u3002</p><p>\u8fd9\u662f\u7ea2\u9f99\u738b\u5bdf\u65af\u8428\u7684\u753b\u50cf\u3002</p><p>\u5510\u7426\u62c9\u662f\u7ea2\u9f99\u738b\u7684\u6697\u796d\uff0c\u6697\u4e2d\u6709\u4e00\u5f20\u795e\u7957\u753b\u50cf\uff0c\u4e5f\u662f\u5f88\u6b63\u5e38\u7684\u4e8b\u60c5\uff0c\u4e0d\u8db3\u4e3a\u5947\u3002\u743c\u6069\u6b63\u89c9\u5931\u671b\uff0c\u838e\u73de\u514b\u5374\u8d70\u4e0a\u524d\uff0c\u4f38\u624b\u5728\u753b\u50cf\u4e0a\u6478\u4e86\u6478\uff0c\u50cf\u662f\u53d1\u73b0\u4e86\u4ec0\u4e48\uff0c\u5c06\u5b83\u4ece\u5899\u4e0a\u53d6\u4e86\u4e0b\u6765\u3002</p><p>\u201c\u4f60\u62ff\u5b83\u5e72\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u838e\u73de\u514b\u4e0d\u7b54\uff0c\u5c06\u753b\u50cf\u7ffb\u8f6c\uff0c\u8ba9\u80cc\u9762\u9732\u51fa\u6765\u3002\u743c\u6069\u4e00\u773c\u77a5\u89c1\uff0c\u5fc3\u4e2d\u4e0d\u7531\u5f97\u5fae\u5fae\u4e00\u60ca\uff0c\u51db\u751a\u81f3\u201c\u554a\u201d\u5730\u4e00\u58f0\u60ca\u547c\u51fa\u6765\uff0c\u5c31\u8fde\u53d1\u73b0\u673a\u5173\u7684\u838e\u73de\u514b\u90fd\u663e\u7136\u51fa\u4e4e\u610f\u6599\uff0c\u4e09\u4eba\u4e0d\u7ea6\u800c\u540c\u5730\u671d\u6885\u83f2\u65af\u770b\u8fc7\u53bb\u3002</p><p>\u5728\u7ea2\u9f99\u738b\u5bdf\u65af\u8428\u753b\u50cf\u7684\u80cc\u9762\uff0c\u662f\u53e6\u4e00\u5f20\u753b\u50cf\uff0c\u90a3\u662f\u4e00\u5904\u6218\u573a\uff0c\u6fc0\u70c8\u7684\u6218\u6597\u521a\u521a\u7ed3\u675f\uff0c\u904d\u5730\u5c38\u9996\u548c\u8840\u8ff9\uff0c\u5200\u5251\u65ad\u6298\uff0c\u7532\u80c4\u6b8b\u7834\uff0c\u51e0\u9762\u65d7\u5e1c\u5728\u6ce5\u5730\u91cc\u88ab\u8df5\u8e0f\uff0c\u5728\u8fdc\u5904\u7684\u5730\u5e73\u7ebf\u4e0a\uff0c\u5915\u9633\u5982\u8840\uff0c\u6447\u6447\u6b32\u5760\u3002\u5c31\u5728\u8fd9\u6218\u573a\u4e2d\u592e\uff0c\u4e00\u4f4d\u620e\u88c5\u5c11\u5973\u957f\u8eab\u7389\u7acb\uff0c\u5979\u5927\u7ea6\u5341\u516d\u4e03\u5c81\uff0c\u91d1\u53d1\u78a7\u7738\uff0c\u7709\u5b87\u4e4b\u95f4\uff0c\u51db\u7136\u751f\u5a01\uff0c\u4e0e\u5468\u56f4\u7684\u6697\u90c1\u8272\u8c03\u5f62\u6210\u9c9c\u660e\u5bf9\u6bd4\uff0c\u5b9b\u5982\u4e00\u4f4d\u5973\u6218\u795e\u964d\u4e34\u51e1\u95f4\u3002\u770b\u90a3\u8138\u5e9e\uff0c\u5206\u660e\u4fbf\u662f\u6885\u83f2\u65af\u3002</p><p>\u6885\u83f2\u65af\u62ff\u8fc7\u753b\u50cf\uff0c\u770b\u4e86\u4e00\u4f1a\uff0c\u201c\u5148\u79bb\u5f00\u5427\uff0c\u201d\u5979\u6de1\u6de1\u8bf4\uff0c\u201c\u56de\u53bb\u518d\u8bf4\u3002\u201d</p><p>\u838e\u73de\u514b\u5c06\u753b\u50cf\u3001\u673a\u5173\u6062\u590d\u539f\u72b6\uff0c\u6e05\u9664\u4e00\u5207\u75d5\u8ff9\uff0c\u6240\u6709\u4eba\u539f\u8def\u8fd4\u56de\u3002\u6885\u83f2\u65af\u4e00\u8def\u4e0a\u90fd\u5728\u6c89\u9ed8\uff0c\u534a\u53e5\u8bdd\u90fd\u6ca1\u8bf4\uff0c\u56de\u5230\u4f4f\u5904\uff0c\u51db\u521a\u4e00\u8fdb\u95e8\u4fbf\u5fcd\u4e0d\u4f4f\u95ee\uff1a\u201c\u827e\u5f25\u8587\uff0c\u4ed6\u600e\u4e48\u4f1a\u6709\u4f60\u7684\u753b\u50cf\uff0c\u8fd8\u85cf\u5f97\u90a3\u4e48\u9690\u79d8\uff0c\u96be\u9053\u4ed6\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u6447\u6447\u5934\uff0c\u201c\u90a3\u4e0d\u662f\u6211\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u90a3\u662f\u6211\u6bcd\u4eb2\u3002\u201d</p><p>\u90a3\u5e45\u753b\u770b\u989c\u6599\u8272\u6cfd\uff0c\u4fbf\u77e5\u9053\u753b\u6210\u81f3\u5c11\u6709\u5341\u5e74\u4ee5\u4e0a\u4e86\uff0c\u751a\u81f3\u662f\u4e8c\u4e09\u5341\u5e74\u524d\u7684\u4f5c\u54c1\uff0c\u753b\u7684\u5f53\u7136\u4e0d\u53ef\u80fd\u662f\u6885\u83f2\u65af\u3002\u53ea\u662f\u56e0\u4e3a\u753b\u4e2d\u5c11\u5973\u4e0e\u9762\u524d\u7684\u6885\u83f2\u65af\u5b9e\u5728\u592a\u50cf\uff0c\u6d3b\u8131\u8131\u5c31\u662f\u4e00\u4e2a\u6a21\u5b50\u5370\u51fa\u6765\u7684\uff0c\u4e00\u65f6\u95f4\u628a\u4eba\u7ed9\u9707\u5230\u4e86\u3002\u51db\u662f\u8111\u5b50\u6ca1\u8f6c\u8fc7\u5f2f\u6765\uff0c\u743c\u6069\u3001\u838e\u73de\u514b\u90fd\u662f\u5728\u8def\u4e0a\u5c31\u60f3\u901a\uff0c\u65e2\u7136\u4e0d\u662f\u6885\u83f2\u65af\u81ea\u5df1\uff0c\u5374\u53c8\u548c\u5979\u5982\u6b64\u8096\u4f3c\uff0c\u90a3\u753b\u4e2d\u4eba\u7684\u8eab\u4efd\uff0c\u4e5f\u5c31\u4e00\u76ee\u4e86\u7136\u4e86\u3002</p><p>\u6885\u83f2\u65af\u770b\u4e86\u838e\u73de\u514b\u4e00\u773c\uff0c\u743c\u6069\u4f1a\u610f\uff0c\u8ba9\u9b45\u9b54\u53bb\u966a\u73ca\u5609\u3002\u4ed6\u77e5\u9053\u8fd9\u91cc\u9762\u5fc5\u7136\u7275\u6d89\u5230\u6885\u83f2\u65af\u548c\u5979\u6bcd\u4eb2\u7684\u5f80\u4e8b\uff0c\u5c11\u5973\u81ea\u7136\u4e0d\u559c\u6b22\u88ab\u592a\u591a\u5916\u4eba\u77e5\u6653\uff0c\u51db\u5012\u662f\u65e0\u6240\u8c13\uff0c\u838e\u73de\u514b\u7684\u5173\u7cfb\u5c31\u8fdc\u4e86\u4e00\u5c42\uff0c\u4e0d\u65b9\u4fbf\u7559\u5979\u5728\u573a\u3002</p><p>\u201c\u6211\u60f3\u8d77\u4e86\u4e00\u4e9b\u4e8b\u60c5\u3002\u201d\u89c1\u838e\u73de\u514b\u79bb\u5f00\uff0c\u6885\u83f2\u65af\u6c89\u541f\u7247\u523b\uff0c\u8bf4\u3002</p><p>\u521a\u624d\u770b\u5230\u90a3\u5e45\u753b\u7684\u65f6\u5019\uff0c\u4eff\u4f5b\u88ab\u67d0\u79cd\u4e1c\u897f\u89e6\u52a8\uff0c\u4e00\u4e9b\u5df2\u7ecf\u8fc7\u53bb\u5f88\u4e45\u7684\u8bb0\u5fc6\u7247\u6bb5\uff0c\u5ffd\u7136\u4ece\u9057\u5fd8\u4e4b\u6d77\u4e2d\u6d8c\u51fa\u6765\uff0c\u6d6e\u73b0\u5728\u5979\u7684\u8111\u6d77\u91cc\u3002</p><p>\u201c\u90a3\u4f3c\u4e4e\u662f\u4e00\u4e2a\u4e0b\u5348\uff0c\u9633\u5149\u4ece\u7a97\u6237\u7684\u7f1d\u9699\u91cc\u900f\u8fdb\u6765\uff0c\u6211\u8eba\u5728\u4e00\u5f20\u5c0f\u5e8a\u4e0a\uff0c\u5e94\u8be5\u662f\u5728\u5348\u7761\uff0c\u5ffd\u7136\u88ab\u60ca\u9192\u4e86\u3002\u6211\u542c\u5230\u623f\u95f4\u5916\u9762\u6709\u4eba\u5728\u8bf4\u8bdd\uff0c\u4ed6\u4eec\u597d\u50cf\u5728\u4e89\u6267\uff0c\u58f0\u97f3\u5f88\u5927\uff0c\u5176\u4e2d\u4e00\u4e2a\u662f\u6211\u6bcd\u4eb2\u3002\u6211\u722c\u8d77\u6765\uff0c\u7ad9\u5728\u5e8a\u8fb9\uff0c\u9694\u7740\u7a97\u6237\u770b\u4e86\u4e00\u773c\uff0c\u770b\u89c1\u4e09\u4e2a\u4eba\uff0c\u9664\u4e86\u6211\u6bcd\u4eb2\u4e4b\u5916\u8fd8\u6709\u4e24\u4e2a\u7537\u4eba\u3002\u6bcd\u4eb2\u80cc\u5bf9\u7740\u6211\uff0c\u5750\u5728\u5979\u65c1\u8fb9\u7684\u90a3\u4e2a\u7537\u4eba\u6211\u8ba4\u8bc6\uff0c\u522b\u4eba\u53eb\u4ed6\u2018\u6e21\u9e26\u2019\uff0c\u53e6\u5916\u4e00\u4f4d\u662f\u4e2a\u5b8c\u5168\u964c\u751f\u7684\u4e2d\u5e74\u4eba\uff0c\u5750\u5728\u6bcd\u4eb2\u5bf9\u9762\uff0c\u6211\u770b\u89c1\u4e86\u4ed6\u7684\u4fa7\u8138\u3002\u201d</p><p>\u201c\u5510\u7426\u62c9\uff1f\u201d</p><p>\u6885\u83f2\u65af\u70b9\u70b9\u5934\u3002</p><p>\u743c\u6069\u5410\u4e86\u53e3\u6c14\uff0c\u201c\u5982\u6b64\u8bf4\u6765\uff0c\u4e4b\u524d\u4f60\u7684\u611f\u89c9\u662f\u5bf9\u7684\uff0c\u4f60\u7684\u786e\u66fe\u7ecf\u5728\u5f88\u5c0f\u7684\u65f6\u5019\u89c1\u8fc7\u4ed6\u3002\u4ed6\u65e2\u7136\u53bb\u8fc7\u4f60\u4eec\u7684\u4f4f\u5904\uff0c\u90a3\u5e94\u8be5\u4e0d\u662f\u654c\u4eba\uff0c\u662f\u4f2f\u6bcd\u7684\u5c5e\u4e0b\u3002\u201d</p><p>\u201c\u4e0d\u662f\u5c5e\u4e0b\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u6211\u6bcd\u4eb2\u7684\u5c5e\u4e0b\u57fa\u672c\u90fd\u662f\u5df4\u5c14\u4fe1\u5f92\uff0c\u4f46\u4ed6\u5982\u679c\u662f\u5df4\u5c14\u7684\u4fe1\u5f92\uff0c\u6211\u5e94\u8be5\u4f1a\u6709\u6240\u611f\u5e94\u624d\u5bf9\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u800c\u4e14\u5728\u6211\u8bb0\u5fc6\u91cc\uff0c\u4ed6\u5f53\u65f6\u548c\u6211\u6bcd\u4eb2\u8bf4\u8bdd\u7684\u6001\u5ea6\uff0c\u4e0d\u50cf\u662f\u90e8\u5c5e\uff0c\u66f4\u50cf\u662f\u5ba2\u4eba\u3002\u201d</p><p>\u201c\u4ed6\u662f\u7ea2\u9f99\u6559\u4f1a\u7684\u6697\u796d\uff0c\u8bf4\u4e0d\u5b9a\u662f\u4ee3\u8868\u7ea2\u9f99\u6559\u4f1a\u6765\u548c\u4f2f\u6bcd\u8c08\u53cc\u65b9\u5408\u4f5c\uff1f\u201d\u743c\u6069\u731c\u6d4b\uff0c\u201c\u7b97\u662f\u76df\u53cb\uff1f\u201d</p><p>\u201c\u6211\u6bcd\u4eb2\u53ef\u4ece\u6765\u4e0d\u559c\u6b22\u7ea2\u9f99\u6559\u4f1a\uff0c\u66fe\u7ecf\u8bf4\u4ed6\u4eec\u90fd\u662f\u4e00\u7fa4\u767d\u75f4\u3002\u201d</p><p>\u201c\u5443\uff0c\u4e0d\u559c\u6b22\u4e5f\u4e0d\u4ee3\u8868\u5c31\u4e0d\u53ef\u4ee5\u5408\u4f5c\u561b\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53cd\u6b63\u4ed6\u8981\u4e48\u662f\u4f2f\u6bcd\u6614\u65e5\u7684\u624b\u4e0b\uff0c\u8981\u4e48\u662f\u76df\u53cb\u2014\u2014\u8fd8\u597d\uff0c\u53ea\u8981\u4e0d\u662f\u654c\u4eba\u5c31\u884c\u3002\u201d</p><p>\u201c\u4f60\u9519\u4e86\uff0c\u5982\u679c\u662f\u654c\u4eba\u5012\u8fd8\u597d\u4e00\u4e9b\uff0c\u662f\u6211\u6bcd\u4eb2\u7684\u5c5e\u4e0b\u53ef\u5c31\u66f4\u9ebb\u70e6\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4ed6\u4eec\u662f\u4e00\u7fa4\u75af\u5b50\uff0c\u7edd\u5bf9\u6ca1\u6709\u4e00\u4e2a\u662f\u6b63\u5e38\u4eba\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u603b\u4e4b\uff0c\u8fd9\u4e00\u6b21\u5165\u5ba4\u8c03\u67e5\uff0c\u9664\u4e86\u53d1\u73b0\u5510\u7426\u62c9\u548c\u6885\u83f2\u65af\u7684\u6bcd\u4eb2\u662f\u65e7\u8bc6\u2014\u2014\u800c\u4e14\u4f3c\u4e4e\u8fd8\u62b1\u6709\u67d0\u79cd\u7231\u6155\u4e0e\u61a7\u61ac\u2014\u2014\u4e4b\u5916\uff0c\u518d\u65e0\u5176\u4ed6\u6536\u83b7\u3002</p><p>\u201c\u4f46\u6709\u4e00\u70b9\u662f\u6bcb\u5eb8\u7f6e\u7591\u7684\uff0c\u5510\u7426\u62c9\u80af\u5b9a\u77e5\u9053\u4f60\u7684\u8eab\u4efd\uff0c\u4ed6\u5728\u7b2c\u4e00\u773c\u770b\u5230\u4f60\u7684\u65f6\u5019\u5c31\u5e94\u8be5\u77e5\u9053\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4ed6\u8ba4\u8bc6\u4f2f\u6bcd\uff0c\u4f3c\u4e4e\u5173\u7cfb\u8fd8\u4e0d\u6d45\uff0c\u5374\u4ece\u672a\u5411\u4f60\u900f\u9732\u8fc7\u534a\u70b9\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u8981\u9690\u7792\uff1f\u8fd9\u5176\u4e2d\u80af\u5b9a\u6709\u95ee\u9898\u3002\u201d</p><p>\u51db\u70b9\u5934\u8d5e\u540c\uff0c\u201c\u6ca1\u9519\uff0c\u4ed6\u80af\u5b9a\u4e0d\u6000\u597d\u610f\u3002\u201d</p><p>\u201c\u660e\u5929\u6211\u4eec\u53bb\u8bd5\u63a2\u4e00\u4e0b\u4ed6\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u770b\u770b\u4ed6\u7a76\u7adf\u60f3\u5e72\u4ec0\u4e48\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u6765\u5230\u5723\u6e0a\u57ce\u7684\u7b2c\u56db\u5929\uff0c\u743c\u6069\u6253\u7b97\u5403\u8fc7\u5348\u9910\u4e4b\u540e\u548c\u6885\u83f2\u65af\u4e00\u8d77\u53bb\u7ea2\u9f99\u795e\u6bbf\uff0c\u548c\u5510\u7426\u62c9\u6c9f\u901a\u4e00\u4e0b\u6709\u5173\u9b54\u6cd5\u9635\u7684\u4fee\u590d\u8fdb\u5ea6\uff0c\u987a\u4fbf\u4e5f\u63a2\u63a2\u5bf9\u65b9\u7684\u5e95\u3002</p><p>\u9752\u94dc\u8c6a\u5b85\u7684\u4e0a\u5348\u603b\u662f\u6bd4\u8f83\u5b89\u9759\uff0c\u7fe1\u7fe0\u5973\u5deb\u5728\u8ba4\u771f\u5730\u6574\u7406\u5206\u914d\u7ed9\u5979\u7684\u5de5\u4f5c\uff0c\u73ca\u5609\u5728\u7ed9\u5979\u5e2e\u5fd9\uff0c\u7ef4\u82e5\u62c9\u5374\u6ca1\u770b\u89c1\u4eba\u5f71\uff0c\u636e\u8bf4\u662f\u6628\u5929\u8017\u8d39\u7cbe\u529b\u592a\u591a\uff0c\u9700\u8981\u4f11\u606f\u534a\u5929\u2014\u2014\u867d\u7136\u743c\u6069\u5f88\u60f3\u95ee\u5979\u4f11\u606f\u4e3a\u4ec0\u4e48\u4e0d\u5728\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u5374\u8981\u4e00\u5927\u65e9\u8dd1\u5230\u51db\u7684\u5e8a\u4e0a\u3002</p><p>\u201c\u56e0\u4e3a\u4f60\u5df2\u7ecf\u6709\u5f88\u4e45\u90fd\u6ca1\u6765\u5b89\u6170\u6211\u4e86\u554a\uff0c\u201d\u7ef4\u82e5\u62c9\u7406\u76f4\u6c14\u58ee\uff0c\u201c\u4f5c\u4e3a\u4e00\u4e2a\u60c5\u7aa6\u521d\u5f00\u7684\u5999\u9f84\u5973\u90ce\uff0c\u6df1\u95fa\u5bc2\u5bde\uff0c\u6ca1\u6709\u7537\u4eba\u5b89\u6170\uff0c\u4e3b\u52a8\u53bb\u8ffd\u6c42\u81ea\u5df1\u7684\u5e78\u798f\u6709\u4ec0\u4e48\u9519\uff1f\u201d</p><p>\u201c\u4e5f\u5c31\u624d\u51e0\u5929\u5427\uff0c\u54ea\u6709\u5f88\u4e45\u3002\u201d</p><p>\u201c\u5df2\u7ecf\u6574\u6574\u5341\u5929\u4e86\u597d\u4e0d\u597d\uff01\u81ea\u4ece\u79bb\u5f00\u8f9b\u5df4\u57ce\u4ee5\u540e\uff0c\u4f60\u591c\u91cc\u5c31\u4e00\u6b21\u90fd\u6ca1\u6765\u8fc7\uff01\u6211\u6bcf\u5929\u665a\u4e0a\u90fd\u505a\u6625\u68a6\uff0c\u65e9\u4e0a\u9192\u6765\u5e8a\u5355\u90fd\u6e7f\u900f\u4e86\u4f60\u77e5\u4e0d\u77e5\u9053\uff01\u201d</p><p>\u201c\u522b\u6fc0\u52a8\uff0c\u6709\u8bdd\u597d\u8bf4\uff0c\u522b\u8fd9\u4e48\u5927\u58f0\uff0c\u201d\u743c\u6069\u5fd9\u4e0d\u8fed\u5730\u8bf4\uff0c\u201c\u8ba9\u522b\u4eba\u542c\u89c1\u4e86\u8bef\u4f1a\uff0c\u8fd8\u4ee5\u4e3a\u4f60\u662f\u4e2a\u6b32\u6c42\u4e0d\u6ee1\u7684\u6deb\u5a03\u5462\u3002\u201d</p><p>\u201c\u8fd9\u4e0d\u90fd\u662f\u4f60\u5bb3\u7684\u5417\uff0c\u8981\u4e0d\u662f\u4f60\u7ed9\u6211\u4e0b\u7684\u90a3\u4e9b\u4e71\u4e03\u516b\u7cdf\u7684\u90aa\u6076\u53d8\u6001\u50ac\u60c5\u6cd5\u672f\uff0c\u6211\u624d\u4e0d\u4f1a\u53d8\u6210\u8fd9\u6837\u3002\u201d</p><p>\u201c\u90a3\u4e9b\u6cd5\u672f\u660e\u660e\u662f\u4f60\u6559\u7ed9\u6211\u7684\u5427\uff1f\u201d</p><p>\u201c\u4f46\u7684\u786e\u662f\u4f60\u5bf9\u6211\u4f7f\u7528\u7684\uff0c\u8fd9\u4e2a\u6ca1\u9519\u5427\u3002\u201d</p><p>\u201c\u597d\u50cf\u662f\u6ca1\u9519\uff0c\u4f46\u662f\u2014\u2014\u201d</p><p>\u201c\u90a3\u4e0d\u5c31\u5f97\u4e86\uff0c\u7537\u4eba\u8981\u6562\u4f5c\u6562\u5f53\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u8bf4\u8bdd\u7684\u53e3\u6c14\u771f\u662f\u548c\u51db\u8d8a\u6765\u8d8a\u50cf\u4e86\u3002\u201d</p><p>\u201c\u8bf4\u5230\u51db\uff0c\u8fd9\u4e2a\u5c0f\u5bb6\u4f19\u4e5f\u662f\u8d8a\u6765\u8d8a\u8fc7\u5206\u4e86\uff0c\u201d\u7ef4\u82e5\u62c9\u62b1\u6028\uff0c\u201c\u6709\u4e86\u65b0\u6b22\u5c31\u5fd8\u4e86\u65e7\u7231\uff0c\u672c\u6765\u4ece\u65e9\u5230\u665a\u7f20\u7740\u6211\u4e0d\u653e\uff0c\u7b49\u827e\u5f25\u8587\u4e00\u56de\u6765\uff0c\u7acb\u523b\u5c31\u8f6c\u6295\u6000\u62b1\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u6211\u8981\u66ff\u5979\u8fa9\u89e3\u4e00\u4e0b\uff0c\u827e\u5f25\u8587\u662f\u65e7\u7231\uff0c\u4f60\u624d\u662f\u65b0\u6b22\uff0c\u4eba\u5bb6\u4e24\u4e2a\u624d\u662f\u539f\u914d\uff0c\u6211\u90fd\u53ea\u80fd\u7b97\u7b2c\u4e09\u8005\u63d2\u8db3\uff0c\u81f3\u4e8e\u4f60\uff0c\u8001\u8001\u5b9e\u5b9e\u5728\u540e\u9762\u6392\u961f\u5427\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\uff0c\u4f60\u4eec\u8fd9\u4e9b\u5e74\u8f7b\u4eba\u771f\u662f\u592a\u2026\u2026\u201d\u5979\u60f3\u4e86\u4e00\u4f1a\uff0c\u627e\u4e0d\u51fa\u4ec0\u4e48\u5408\u9002\u7684\u5f62\u5bb9\uff0c\u201c\u592a\u6deb\u4e71\u4e86\uff01\u201d</p><p>\u4f60\u8fd9\u4e2a\u6bcf\u5929\u5e8a\u5355\u90fd\u6e7f\u900f\u7684\u5973\u4eba\uff0c\u6709\u4ec0\u4e48\u8d44\u683c\u8bf4\u8fd9\u79cd\u8bdd\u554a\u3002</p><p>\u6700\u540e\u743c\u6069\u53ea\u597d\u5e94\u5141\uff0c\u4fdd\u8bc1\u4eca\u5929\u665a\u4e0a\u4e00\u5b9a\u53bb\u627e\u5979\uff0c\u7ec8\u4e8e\u624d\u6210\u529f\u8131\u8eab\u3002</p><p>\u8bf4\u8d77\u6765\uff0c\u743c\u6069\u8fd8\u8bb0\u5f97\u7b2c\u4e00\u6b21\u548c\u7ef4\u82e5\u62c9\u89c1\u9762\u7684\u573a\u666f\uff0c\u4f3c\u4e4e\u4e5f\u5c31\u662f\u4e00\u4e2a\u6765\u6708\u524d\u7684\u4e8b\u60c5\uff0c\u90a3\u65f6\u5019\u5973\u5deb\u5e08\u8fd8\u662f\u633a\u6b63\u7ecf\u7684\u6a21\u6837\uff0c\u600e\u4e48\u611f\u89c9\u5815\u843d\u5f97\u8fd9\u4e48\u5feb\u3002\u867d\u8bf4\u7537\u4eba\u548c\u5973\u4eba\u53d1\u751f\u8fc7\u8089\u4f53\u5173\u7cfb\u4e4b\u540e\uff0c\u7684\u786e\u5fc3\u6001\u4e5f\u4f1a\u53d1\u751f\u53d8\u5316\uff0c\u4f1a\u66f4\u52a0\u968f\u4fbf\u4e00\u4e9b\uff0c\u6bd5\u7adf\u90fd\u5df2\u7ecf\u8892\u88ce\u76f8\u89c1\u8fc7\uff0c\u4e0d\u4f1a\u518d\u50cf\u4ee5\u524d\u90a3\u6837\u77dc\u6301\uff0c\u4f46\u611f\u89c9\u8fd8\u662f\u592a\u5feb\u4e86\u4e00\u70b9\u3002\u743c\u6069\u5728\u5979\u8eab\u4e0a\u7528\u7684\u90a3\u4e9b\u7c89\u7ea2\u6cd5\u672f\u56fa\u7136\u662f\u4e00\u65b9\u9762\u56e0\u7d20\uff0c\u4f46\u6cd5\u672f\u53ea\u662f\u6539\u9020\u8eab\u4f53\uff0c\u53c8\u4e0d\u4f1a\u76f4\u63a5\u5f71\u54cd\u4eba\u683c\u5427\u3002</p><p>\u201c\u8fd9\u8bf4\u660e\u5979\u539f\u672c\u9aa8\u5b50\u91cc\u5c31\u662f\u4e2a\u6deb\u5a03\uff0c\u53ea\u4e0d\u8fc7\u4ee5\u524d\u4e00\u76f4\u88ab\u538b\u6291\u4f4f\u4e86\uff0c\u81ea\u5df1\u90fd\u6ca1\u5bdf\u89c9\u800c\u5df2\uff0c\u201d\u838e\u73de\u514b\u4e0d\u77e5\u9053\u4ece\u54ea\u91cc\u5192\u51fa\u6765\uff0c\u201c\u73b0\u5728\u88ab\u4f60\u5f00\u53d1\u51fa\u6765\uff0c\u81ea\u7136\u5c31\u98df\u9ad3\u77e5\u5473\uff0c\u4e00\u53d1\u4e0d\u53ef\u6536\u62fe\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u89e3\u91ca\u6211\u559c\u6b22\u2014\u2014\u54a6\uff0c\u4f60\u5e72\u561b\u53bb\u4e86\uff0c\u600e\u4e48\u9ed1\u773c\u5708\u8fd9\u4e48\u91cd\uff0c\u597d\u50cf\u4e00\u591c\u6ca1\u7761\u4f3c\u7684\u3002\u201d\u743c\u6069\u6000\u7591\u5730\u770b\u7740\u5979\u3002</p><p>\u201c\u653e\u5fc3\uff0c\u6211\u6ca1\u6709\u591c\u91cc\u6e9c\u51fa\u53bb\u5077\u5077\u52fe\u642d\u522b\u7684\u7537\u4eba\u3002\u201d</p><p>\u5e9f\u8bdd\uff0c\u6211\u5f53\u7136\u77e5\u9053\u6ca1\u6709\uff0c\u8fd9\u5ea7\u9752\u94dc\u8c6a\u5b85\u662f\u6709\u667a\u80fd\u95e8\u7981\u7cfb\u7edf\u7684\uff0c\u4efb\u4f55\u4eba\u7684\u51fa\u5165\u8bb0\u5f55\uff0c\u743c\u6069\u53ea\u8981\u613f\u610f\u5c31\u53ef\u4ee5\u67e5\u5230\u3002\u838e\u73de\u514b\u4ece\u6628\u5929\u665a\u4e0a\u56de\u6765\u540e\u5c31\u518d\u4e5f\u6ca1\u6709\u51fa\u8fc7\u95e8\uff0c\u800c\u5bb6\u91cc\u53ea\u6709\u4ed6\u81ea\u5df1\u4e00\u4e2a\u7537\u4eba\uff0c\u8fde\u73ca\u5609\u90a3\u53ea\u5ba0\u7269\u9ed1\u732b\u90fd\u662f\u6bcd\u7684\uff0c\u838e\u73de\u514b\u60f3\u52fe\u642d\u522b\u7684\u7537\u4eba\u4e5f\u529e\u4e0d\u5230\u3002\u95ee\u9898\u5728\u4e8e\uff0c\u743c\u6069\u73b0\u5728\u62c5\u5fc3\u7684\u4e0d\u662f\u5979\u52fe\u642d\u7537\u4eba\uff0c\u800c\u662f\u4e5f\u50cf\u51db\u3001\u7ef4\u82e5\u62c9\u5b66\u4e60\uff0c\u5c06\u76ee\u6807\u8f6c\u5411\u5973\u4eba\uff0c\u90a3\u9ebb\u70e6\u53ef\u5c31\u5927\u4e86\u3002\u597d\u4e0d\u5bb9\u6613\u6536\u96c6\u5230\u8fd9\u4e48\u591a\u6f02\u4eae\u59b9\u5b50\u805a\u5728\u4e00\u8d77\uff0c\u53c8\u6b9a\u7cbe\u7aed\u8651\u8ba9\u5979\u4eec\u80fd\u591f\u548c\u5e73\u76f8\u5904\uff0c\u773c\u770b\u540e\u5bab\u5927\u4e1a\u6307\u65e5\u53ef\u5f85\uff0c\u5982\u679c\u8fd9\u4e9b\u5973\u4eba\u81ea\u5df1\u641e\u5728\u4e00\u8d77\uff0c\u4e00\u4e2a\u4e2a\u53d8\u6210\u4e86\u857e\u4e1d\u8fb9\uff0c\u90a3\u8fd8\u6709\u743c\u6069\u4ec0\u4e48\u4e8b\uff1f</p><p>\u201c\u6211\u4e5f\u6ca1\u6709\u53bb\u5077\u5077\u52fe\u642d\u5973\u4eba\uff0c\u201d\u838e\u73de\u514b\u6ca1\u597d\u6c14\u5730\u8bf4\uff0c\u201c\u6211\u662f\u88ab\u90a3\u53ea\u8272\u9f99\u9a9a\u6270\u4e86\u4e00\u6574\u591c\u3002\u201d</p><p>\u8272\u9f99\uff1f\u54e6\uff0c\u4f60\u662f\u8bf4\u51b0\u8679\uff0c\u90a3\u8001\u5bb6\u4f19\u600e\u4e48\u4e86\uff1f</p><p>\u521a\u624d\u743c\u6069\u8bf4\u5bb6\u91cc\u53ea\u6709\u4ed6\u4e00\u4e2a\u7537\u4eba\uff0c\u5176\u5b9e\u4e0d\u51c6\u786e\uff0c\u4ed6\u5fd8\u4e86\u8fd8\u6709\u4e00\u5934\u96c4\u9f99\u3002\u4e0d\u8fc7\u51b0\u8679\u8fde\u8eab\u4f53\u90fd\u6ca1\u4e86\uff0c\u533a\u533a\u4e00\u4e2a\u5668\u7075\uff0c\u80fd\u770b\u4e0d\u80fd\u6478\uff0c\u743c\u6069\u5012\u4e0d\u662f\u5f88\u62c5\u5fc3\u3002</p><p>\u201c\u4f60\u4e0d\u662f\u628a\u5b83\u4e22\u7ed9\u6211\u4e86\u5417\uff0c\u8ba9\u6211\u53bb\u641e\u5b9a\u5b83\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u5b83\u6628\u5929\u65e9\u4e0a\u7761\u9192\u4e86\uff0c\u7136\u540e\u5c31\u95f9\u7740\u8981\u753b\u753b\uff0c\u8981\u6211\u7ed9\u5b83\u505a\u6a21\u7279\u3002\u6211\u672c\u6765\u4ee5\u4e3a\uff0c\u505a\u6a21\u7279\u5f88\u7b80\u5355\uff0c\u968f\u4fbf\u6446\u51e0\u4e2a\u59ff\u52bf\u5c31\u5b8c\u4e86\uff0c\u8c01\u77e5\u9053\u5b83\u8fd8\u8981\u6c42\u4e00\u5927\u5806\uff0c\u628a\u6211\u7d2f\u5f97\u591f\u545b\u3002\u201d</p><p>\u201c\u5b83\u90fd\u6709\u4ec0\u4e48\u8981\u6c42\uff1f\u201d</p><p>\u201c\u5b83\u4e0d\u77e5\u9053\u4ece\u54ea\u91cc\u5f04\u6765\u4e00\u5927\u5806\u7a00\u5947\u53e4\u602a\u7684\u8863\u670d\u8981\u6211\u6362\u4e0a\uff0c\u8fd8\u7528\u5e7b\u672f\u5f04\u51fa\u5404\u79cd\u5404\u6837\u4e0d\u540c\u7684\u573a\u666f\uff0c\u6700\u5938\u5f20\u7684\u662f\u5c45\u7136\u8fd8\u6709\u5267\u672c\uff0c\u8981\u6211\u8868\u6f14\u7ed9\u5b83\u770b\u3002\u4e00\u4f1a\u8ba9\u6211\u626e\u6f14\u6b66\u58eb\uff0c\u4e00\u4f1a\u8ba9\u6211\u626e\u6f14\u6740\u624b\uff0c\u4e00\u4f1a\u8ba9\u6211\u626e\u6f14\u6559\u5e08\uff0c\u6700\u540e\u8fd8\u8ba9\u6211\u626e\u6f14\u6d77\u76d7\u8239\u957f\u2014\u2014\u771f\u662f\u591f\u4e86\u3002\u201d</p><p>\u90fd\u662f\u5f88\u7ecf\u5178\u7684\u89d2\u8272\u554a\uff0c\u8fd9\u53ea\u9f99\u53e3\u5473\u4e0d\u9519\u561b\uff0c\u6709\u7a7a\u5927\u5bb6\u53ef\u4ee5\u4e92\u76f8\u4ea4\u6d41\u4e00\u4e0b\u3002</p><p>\u201c\u5b83\u751a\u81f3\u8fd8\u8981\u6211\u53bb\u627e\u827e\u5f25\u8587\u501f\u5979\u7684\u76d4\u7532\u548c\u5251\uff0c\u8bf4\u8981\u626e\u6f14\u5723\u6b66\u58eb\uff0c\u5f53\u7136\u6211\u6ca1\u542c\u5b83\u7684\uff0c\u5426\u5219\u4f60\u73b0\u5728\u5927\u6982\u5c31\u770b\u89c1\u6211\u5728\u88ab\u827e\u5f25\u8587\u8ffd\u6740\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u5b83\u662f\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u60f3\u627e\u6b7b\u4e48\u3002\u201d</p><p>\u201c\u5b83\u5df2\u7ecf\u6b7b\u4e86\u3002\u201d\u838e\u73de\u514b\u63d0\u9192\u3002</p><p>\u201c\u54e6\u5bf9\uff0c\u5b83\u5df2\u7ecf\u6b7b\u4e86\uff0c\u201d\u743c\u6069\u60f3\u8d77\u6765\uff0c\u201c\u800c\u4e14\u5b83\u8981\u4f60\u626e\u6f14\u5723\u6b66\u58eb\uff0c\u5e94\u8be5\u53bb\u501f\u5723\u5fbd\u624d\u5bf9\u5427\uff0c\u76d4\u7532\u548c\u5251\u53c8\u4e0d\u80fd\u8868\u660e\u5723\u6b66\u58eb\u7684\u8eab\u4efd\u3002\u201d</p><p>\u201c\u5723\u5fbd\u4e0d\u9700\u8981\uff0c\u5b83\u81ea\u5df1\u5c31\u6709\u3002\u201d</p><p>\u5b83\u600e\u4e48\u4f1a\u6709\u2014\u2014\u5514\uff0c\u8fd9\u6761\u9f99\u4ee5\u524d\u662f\u63d0\u5c14\u6559\u4f1a\u517b\u7684\u6253\u624b\uff0c\u4e5f\u7b97\u6559\u5185\u4eba\u58eb\uff0c\u6709\u4e2a\u5723\u5fbd\u4e5f\u4e0d\u5947\u602a\u3002</p><p>\u201c\u542c\u8bf4\u662f\u5b83\u7684\u4e0a\u4e00\u4e2a\u6a21\u7279\u7559\u7ed9\u5b83\u7684\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u5b83\u8ddf\u6211\u5439\u4e86\u534a\u5929\uff0c\u8bf4\u5b83\u4e4b\u524d\u90a3\u4e2a\u6a21\u7279\u662f\u591a\u4e48\u6709\u8eab\u6750\u6709\u6c14\u8d28\u6709\u955c\u5934\u611f\uff0c\u603b\u4e4b\u5b8c\u7f8e\u5f97\u4e00\u584c\u7cca\u6d82\u3002\u6700\u540e\u597d\u50cf\u662f\u548c\u670b\u53cb\u95f9\u7ffb\uff0c\u79bb\u5bb6\u51fa\u8d70\uff0c\u4e34\u8d70\u524d\u628a\u5723\u5fbd\u4e22\u7ed9\u5b83\u505a\u7eaa\u5ff5\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d\u743c\u6069\u4e0d\u77e5\u9053\u8bf4\u4ec0\u4e48\u597d\uff0c\u6700\u540e\u53ea\u80fd\u62cd\u4e86\u62cd\u9b45\u9b54\u7684\u80a9\u8180\uff0c\u201c\u8f9b\u82e6\u4e86\uff0c\u201d\u4ed6\u8bda\u6073\u5730\u8bf4\uff0c\u201c\u6211\u4f1a\u7ed9\u4f60\u52a0\u85aa\u7684\u3002\u201d</p><p>\u201c\u52a0\u85aa\u5c31\u4e0d\u7528\u4e86\uff0c\u4f60\u8d76\u5feb\u89e3\u9664\u8bc5\u5492\u624d\u662f\u6b63\u4e8b\uff0c\u201d\u838e\u73de\u514b\u54c0\u6028\u5730\u8bf4\uff0c\u201c\u6211\u90fd\u5df2\u7ecf\u5feb\u4e24\u4e2a\u6708\u90fd\u6ca1\u8fdb\u9910\u4e86\u3002\u201d</p><p>\u5979\u6240\u8c13\u7684\u8fdb\u9910\uff0c\u6307\u7684\u5f53\u7136\u4e0d\u662f\u51e1\u4eba\u7684\u98df\u7269\uff0c\u800c\u662f\u7537\u6027\u7684\u7cbe\u6db2\u3002\u9b45\u9b54\u53c8\u79f0\u7cbe\u6db2\u9b54\u5973\uff0c\u53ef\u4ee5\u901a\u8fc7\u4e0d\u65ad\u5438\u53d6\u7537\u6027\u7cbe\u6db2\u800c\u589e\u5f3a\u529b\u91cf\uff0c\u53cd\u4e4b\u5219\u4f1a\u8870\u9000\u3002\u6b63\u5e38\u60c5\u51b5\u4e0b\uff0c\u4e00\u4e2a\u9b45\u9b54\u6765\u5230\u51e1\u95f4\uff0c\u201c\u98df\u7269\u201d\u8981\u591a\u5c11\u6709\u591a\u5c11\uff0c\u7136\u800c\u838e\u73de\u514b\u60c5\u51b5\u7279\u6b8a\uff0c\u743c\u6069\u65e2\u4e0d\u5141\u8bb8\u5979\u53bb\u627e\u522b\u7684\u7537\u4eba\uff0c\u81ea\u5df1\u53c8\u5728\u8bc5\u5492\u4e4b\u4e2d\uff0c\u6240\u4ee5\u5979\u5c31\u53ea\u80fd\u88ab\u8feb\u201c\u8282\u98df\u201d\u4e86\u3002</p><p>\u201c\u597d\u5427\u597d\u5427\uff0c\u6211\u77e5\u9053\u4e86\u3002\u201d</p><p>\u63d0\u5230\u8fd9\u4e2a\u95ee\u9898\uff0c\u743c\u6069\u4e0d\u7531\u5f97\u6709\u4e9b\u5c34\u5c2c\uff0c\u8eab\u4e3a\u4e00\u4e2a\u7537\u4eba\uff0c\u4e0d\u80fd\u8ba9\u81ea\u5df1\u5973\u4eba\u6ee1\u8db3\uff0c\u8fd9\u663e\u7136\u662f\u5f88\u4f24\u81ea\u5c0a\u7684\u4e8b\u60c5\u3002\u4e4b\u6240\u4ee5\u51fa\u73b0\u8fd9\u79cd\u60c5\u51b5\uff0c\u90fd\u662f\u56e0\u4e3a\u90a3\u4e2a\u8bc5\u5492\u7684\u7f18\u6545\uff0c\u800c\u90a3\u4e2a\u8bc5\u5492\u662f\u7ef4\u82e5\u62c9\u4e0b\u7684\u2014\u2014\u60f3\u5230\u8fd9\u70b9\uff0c\u743c\u6069\u5c31\u6012\u706b\u4e2d\u70e7\uff0c\u51b3\u5b9a\u665a\u4e0a\u4e00\u5b9a\u8981\u7ed9\u5973\u5deb\u5e08\u4e00\u4e2a\u4ee4\u5979\u5370\u8c61\u6df1\u523b\u7684\u6559\u8bad\u3002</p><p>\u201c\u6211\u8d70\u4e86\uff0c\u4f60\u597d\u597d\u4f11\u606f\u5427\u3002\u201d</p><p>\u743c\u6069\u6b63\u51c6\u5907\u843d\u8352\u800c\u9003\uff0c\u5374\u88ab\u838e\u73de\u514b\u53eb\u4f4f\u4e86\u3002\u201c\u7b49\u4e00\u7b49\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u90a3\u53ea\u9f99\u6709\u53e5\u8bdd\u8f6c\u544a\u4f60\u3002\u201d</p><p>\u201c\u5982\u679c\u662f\u8981\u6211\u5e2e\u5b83\u53bb\u5411\u827e\u5f25\u8587\u501f\u76d4\u7532\u548c\u5251\u4ec0\u4e48\u7684\uff0c\u8ba9\u5b83\u8d81\u65e9\u6b7b\u5fc3\uff0c\u201d\u743c\u6069\u5934\u4e5f\u4e0d\u56de\u5730\u8bf4\uff0c\u201c\u5b83\u662f\u5df2\u7ecf\u6b7b\u4e86\u65e0\u6240\u8c13\uff0c\u53cd\u6b63\u53c8\u4e0d\u4f1a\u518d\u6b7b\u4e00\u6b21\uff0c\u6211\u53ef\u8fd8\u60f3\u591a\u6d3b\u51e0\u5e74\u3002\u201d</p><p>\u201c\u4e0d\u662f\u8fd9\u4e2a\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u5b83\u8ba9\u6211\u8f6c\u544a\uff1a\u5b83\u7ec8\u4e8e\u60f3\u8d77\u4e4b\u524d\u5728\u4ec0\u4e48\u5730\u65b9\uff0c\u95fb\u5230\u8fc7\u548c\u90a3\u4e2a\u57c3\u535c\u62c9\u8eab\u4e0a\u7c7b\u4f3c\u7684\u5473\u9053\u4e86\u3002\u201d</p><p>\u743c\u6069\u505c\u4f4f\u811a\u6b65\uff0c\u201c\u4ec0\u4e48\u65f6\u5019\uff1f\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u201c\u5c31\u662f\u6700\u8fd1\uff0c\u6211\u4eec\u5728\u8f9b\u5df4\u57ce\u767b\u5cb8\u7684\u90a3\u4e00\u5929\uff0c\u8239\u957f\u7684\u8eab\u4e0a\u3002\u201d</p><p>\u201c\u666e\u6717\u514b\u8239\u957f\uff1f\u201d</p><p>\u743c\u6069\u6c89\u541f\u4e86\u7247\u523b\uff0c\u201c\u6211\u77e5\u9053\u4e86\u3002\u201d</p></article>"}}}]);
