(window.webpackJsonp=window.webpackJsonp||[]).push([[1766],{1789:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e00\u8282 \u6700\u540e\u7684\u8b66\u544a",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e00\u8282 \u6700\u540e\u7684\u8b66\u544a</h2><p>\u6781\u5c11\u6709\u6cd5\u672f\u4f20\u9001\u7684\u7ecf\u5386\uff0c\u73ca\u5609\u6709\u4e9b\u4e0d\u592a\u9002\u5e94\uff0c\u8db3\u8db3\u8fc7\u4e86\u534a\u523b\u949f\uff0c\u65b9\u624d\u52c9\u5f3a\u628a\u90a3\u79cd\u5929\u65cb\u5730\u8f6c\u3001\u80f8\u95f7\u6076\u5fc3\u7684\u611f\u89c9\u538b\u4e86\u4e0b\u53bb\u3002</p><p>\u9762\u524d\u5c31\u662f\u7ea2\u9f99\u795e\u6bbf\u7684\u5927\u95e8\uff0c\u73ca\u5609\u4e4b\u524d\u6765\u8fc7\u4e00\u6b21\uff0c\u662f\u521a\u5230\u5723\u6e0a\u57ce\u7684\u7b2c\u4e8c\u5929\uff0c\u5f53\u65f6\u662f\u548c\u6885\u83f2\u65af\u4e00\u8d77\u6765\u7684\uff0c\u743c\u6069\u6bd4\u5979\u4eec\u65e9\u5230\u4e00\u4f1a\uff0c\u90a3\u5bb6\u4f19\u8fd8\u4e0d\u5c0f\u5fc3\u628a\u795e\u6bbf\u91cc\u7684\u4e00\u67c4\u77f3\u4e2d\u5251\u7ed9\u5f04\u65ad\u4e86\u3002</p><p>\u5e0c\u671b\u8fd9\u6b21\u4e0d\u8981\u51fa\u4ec0\u4e48\u610f\u5916\u3002</p><p>\u73ca\u5609\u5fc3\u60f3\u7740\uff0c\u4e0b\u610f\u8bc6\u5730\u63e1\u7d27\u624b\u4e2d\u7684\u300a\u547d\u8fd0\u957f\u591c\u300b\u3002\u867d\u7136\u5728\u743c\u6069\u9762\u524d\u8868\u73b0\u5f97\u4ece\u5bb9\u81ea\u4fe1\uff0c\u4f46\u5b9e\u9645\u4e0a\uff0c\u5979\u5fc3\u91cc\u8fd8\u662f\u6709\u4e9b\u60f4\u60f4\u4e0d\u5b89\u7684\u3002\u4e25\u683c\u6765\u8bf4\uff0c\u8fd9\u7b97\u662f\u7b2c\u4e00\u6b21\uff0c\u5979\u72ec\u81ea\u4e00\u4eba\u6765\u201c\u5192\u9669\u201d\u3002\u53ef\u4ee5\u5b8c\u5168\u4fe1\u4efb\u7684\u743c\u6069\u4e0d\u5728\u8eab\u65c1\uff0c\u8eab\u7ecf\u767e\u6218\u7ecf\u9a8c\u4e30\u5bcc\u7684\u838e\u73de\u514b\u4e5f\u4e0d\u5728\u8eab\u65c1\uff0c\u5c31\u8fde\u732b\u5973\u742a\u96c5\u90fd\u4e0d\u5728\uff0c\u53ea\u6709\u4e00\u53ea\u5815\u843d\u6b66\u50e7\u79d8\u5076\uff0c\u4e00\u53ea\u86db\u540e\u5973\u796d\u53f8\u9b54\u50cf\uff0c\u90fd\u4e0d\u662f\u5408\u683c\u7684\u4ea4\u6d41\u5bf9\u8c61\u3002</p><p>\u4e00\u5207\u90fd\u6709\u8d56\u4e8e\u81ea\u5df1\u7684\u5224\u65ad\uff0c\u6ca1\u6709\u4efb\u4f55\u5efa\u8bae\u6216\u8005\u6307\u5bfc\uff0c\u5bf9\uff0c\u6216\u8005\u9519\uff0c\u540e\u679c\u548c\u8d23\u4efb\u90fd\u8981\u81ea\u5df1\u627f\u62c5\u3002</p><p>\u73ca\u5609\u5e76\u4e0d\u754f\u60e7\uff0c\u5979\u53ea\u662f\u4e0d\u559c\u6b22\u5931\u8d25\u3002</p><p>\u743c\u6069\u7684\u8eab\u8fb9\uff0c\u4f18\u79c0\u7684\u5973\u5b69\u5b50\u5df2\u7ecf\u592a\u591a\uff0c\u4ec5\u5c31\u5979\u89c1\u8fc7\u7684\u800c\u8a00\uff0c\u575a\u6bc5\u82f1\u6b66\u7684\u6885\u83f2\u65af\uff0c\u65e0\u6bd4\u5f3a\u5927\u53c8\u5fe0\u8bda\u7684\u624e\u745e\u5c14\uff0c\u4eff\u4f5b\u7ea2\u5b9d\u77f3\u822c\u7eaf\u771f\u6676\u83b9\u7684\u51db\uff0c\u6e29\u67d4\u4e56\u5de7\u7684\u8299\u857e\u72c4\uff0c\u673a\u654f\u72e1\u8bc8\u7684\u838e\u73de\u514b\uff0c\u5b66\u8bc6\u6e0a\u535a\u7684\u7ef4\u82e5\u62c9\uff0c\u5404\u6709\u5404\u6240\u957f\u3002\u73ca\u5609\u65e0\u610f\u4e0e\u5979\u4eec\u6bcf\u4e2a\u4eba\u7ade\u4e89\uff0c\u4f46\u5979\u540c\u6837\u4e5f\u4e0d\u60f3\u88ab\u6392\u9664\u5728\u5916\u3002\u5f53\u906d\u9047\u5371\u9669\uff0c\u9047\u5230\u56f0\u96be\uff0c\u6240\u6709\u4eba\u90fd\u5728\u52aa\u529b\uff0c\u90fd\u5728\u6218\u6597\u65f6\uff0c\u5979\u4e0d\u60f3\u6c38\u8fdc\u5145\u5f53\u90a3\u4e2a\u8896\u624b\u65c1\u89c2\u7684\u89d2\u8272\u3002</p><p>\u5979\u5e0c\u671b\u8ba9\u743c\u6069\u660e\u767d\uff0c\u59d0\u59d0\u4e0d\u4ec5\u4ec5\u662f\u4ed6\u6240\u949f\u7231\u7684\uff0c\u540c\u6837\u4e5f\u662f\u4ed6\u6240\u4e0d\u53ef\u6216\u7f3a\u7684\u3002\u65e0\u8bba\u4ec0\u4e48\u65f6\u5019\uff0c\u59d0\u59d0\u90fd\u662f\u53ef\u4ee5\u4e0e\u4ed6\u5e76\u80a9\u4f5c\u6218\u7684\uff1b\u65e0\u8bba\u9047\u5230\u4ec0\u4e48\u4e8b\u60c5\uff0c\u59d0\u59d0\u90fd\u662f\u53ef\u4ee5\u4e0e\u4ed6\u5171\u540c\u627f\u62c5\u7684\u3002</p><p>\u5979\u9700\u8981\u8bc1\u660e\u81ea\u5df1\u7684\u673a\u4f1a\u3002</p><p>\u795e\u6bbf\u5927\u95e8\u655e\u5f00\uff0c\u91cc\u9762\u6ca1\u6709\u4efb\u4f55\u52a8\u9759\uff0c\u5374\u900f\u51fa\u6d53\u91cd\u7684\u8840\u8165\u6c14\uff0c\u73ca\u5609\u547d\u4ee4\u79d8\u5076\u4fdd\u6301\u8b66\u6212\uff0c\u540c\u65f6\u6fc0\u6d3b\u9f99\u9cde\u76fe\uff0c\u8ba9\u5b83\u73af\u7ed5\u5728\u81ea\u5df1\u8eab\u65c1\u3002\u521a\u8fdb\u5165\u5ead\u9662\uff0c\u4fbf\u89c1\u5730\u4e0a\u6a2a\u4e03\u7ad6\u516b\u5730\u8eba\u7740\u5341\u51e0\u5177\u5c38\u4f53\uff0c\u4ece\u670d\u9970\u4e0a\u770b\uff0c\u5927\u90e8\u5206\u90fd\u662f\u795e\u6bbf\u91cc\u7684\u796d\u53f8\uff0c\u4e5f\u6709\u57ce\u4e2d\u536b\u5175\uff0c\u4ed6\u4eec\u90fd\u662f\u88ab\u5229\u5668\u6240\u6740\u5bb3\uff0c\u8840\u6d41\u6ee1\u5730\u3002</p><p>\u201c\u5510\u7426\u62c9\u5148\u751f\uff01\u201d</p><p>\u501f\u52a9\u706f\u5149\uff0c\u5728\u4e00\u5806\u5c38\u4f53\u4e4b\u4e2d\uff0c\u73ca\u5609\u53d1\u73b0\u4e86\u8001\u796d\u53f8\uff0c\u4ed6\u4f5d\u507b\u7740\u8170\uff0c\u4fa7\u8eba\u5728\u5730\u4e0a\uff0c\u53cc\u76ee\u7d27\u95ed\uff0c\u9762\u8272\u82cd\u767d\uff0c\u4e00\u628a\u77ed\u5251\u63d2\u5728\u4ed6\u7684\u5c0f\u8179\u4e2d\uff0c\u8840\u4ece\u4f24\u53e3\u5904\u5f80\u5916\u4e0d\u65ad\u6d8c\u51fa\uff0c\u5df2\u7ecf\u5c06\u8863\u670d\u6d78\u900f\uff0c\u663e\u7136\u53d7\u521b\u4e0d\u8f7b\uff0c\u4f46\u8fd8\u6709\u547c\u5438\u3002\u73ca\u5609\u8fde\u5fd9\u8ba9\u5973\u796d\u53f8\u9b54\u50cf\u5bf9\u4ed6\u91ca\u653e\u6cbb\u7597\u6cd5\u672f\uff0c\u53c8\u7ed9\u4ed6\u704c\u4e86\u4e00\u74f6\u81ea\u5df1\u8c03\u914d\u7684\u836f\u6c34\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5510\u7426\u62c9\u7684\u8138\u8272\u603b\u7b97\u662f\u6709\u6240\u597d\u8f6c\uff0c\u547c\u5438\u6e10\u6e10\u5e73\u7a33\uff0c\u773c\u775b\u4e5f\u7741\u5f00\u4e86\u3002\u73ca\u5609\u518d\u5e2e\u4ed6\u5c06\u77ed\u5251\u62d4\u51fa\uff0c\u53c8\u8ba9\u5973\u796d\u53f8\u65bd\u6cd5\u5c01\u4f4f\u4f24\u53e3\uff0c\u963b\u6b62\u7ee7\u7eed\u6d41\u8840\u3002</p><p>\u201c\u4f60\u662f\u2026\u2026\u5170\u5c3c\u65af\u7279\u5c0f\u59d0\uff1f\u4f60\u600e\u4e48\u5728\u8fd9\u91cc\uff1f\u201d</p><p>\u201c\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u5979\u539f\u672c\u4ee5\u4e3a\u662f\u9b54\u4ec6\u5165\u4fb5\uff0c\u4f46\u770b\u60c5\u5f62\u4f3c\u4e4e\u53c8\u4e0d\u592a\u50cf\u3002\u201c\u4f0a\u68ee\uff0c\u201d\u5510\u7426\u62c9\u5598\u606f\u7740\u8bf4\uff0c\u201c\u4ed6\u53d1\u75af\u4e86\uff0c\u5feb\u70b9\u963b\u6b62\u4ed6\uff01\u4ed6\u5728\u540e\u9662\uff01\u201d</p><p>\u201c\u4f0a\u68ee\uff1f\u201d\u73ca\u5609\u76b1\u7709\uff0c\u201c\u8fd9\u4e9b\u4eba\u90fd\u662f\u4f0a\u68ee\u6740\u7684\uff1f\u4ed6\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u505a\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u77e5\u9053\uff0c\u4ed6\u5f88\u5947\u602a\uff0c\u597d\u50cf\u2026\u2026\u597d\u50cf\u88ab\u90aa\u7075\u63a7\u5236\u4e86\u3002\u201d</p><p>\u73ca\u5609\u6c89\u541f\u7247\u523b\uff0c\u7a7f\u8fc7\u6b63\u5802\uff0c\u671d\u540e\u9662\u8d70\u53bb\u3002</p><p>\u5979\u4e00\u773c\u770b\u89c1\u4e86\u4f0a\u68ee\uff0c\u5e74\u8f7b\u7684\u5723\u6e0a\u57ce\u4e3b\u6b63\u7ad9\u5728\u9662\u4e2d\uff0c\u4ed6\u7a7f\u7740\u4e00\u4ef6\u767d\u8272\u793c\u670d\uff0c\u50cf\u662f\u51c6\u5907\u51fa\u5e2d\u67d0\u4e2a\u91cd\u8981\u5bb4\u4f1a\uff0c\u7136\u800c\u8863\u670d\u4e0a\u5374\u6cbe\u6ee1\u4e86\u5927\u5757\u5927\u5757\u7684\u8840\u8ff9\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u81ea\u5df1\u7684\uff0c\u8fd8\u662f\u522b\u4eba\u7684\u3002\u4ed6\u624b\u4e2d\u63e1\u7740\u4e00\u67c4\u957f\u957f\u7684\u6728\u6756\uff0c\u6756\u5934\u4e0a\u96d5\u7740\u4e00\u6761\u873f\u8712\u76d8\u65cb\u7684\u86c7\uff0c\u5f20\u53e3\u5410\u4fe1\uff0c\u6829\u6829\u5982\u751f\u3002\u5728\u4ed6\u7684\u811a\u4e0b\uff0c\u5e9e\u5927\u7684\u9b54\u6cd5\u9635\u6cdb\u7740\u5fae\u5149\uff0c\u4eff\u4f5b\u6ce2\u6d6a\u822c\u4e00\u5c42\u53c8\u4e00\u5c42\u5730\u5f80\u4e2d\u5fc3\u6d8c\u53bb\u3002</p><p>\u201c\u665a\u4e0a\u597d\uff0c\u57ce\u4e3b\u5927\u4eba\uff0c\u201d\u73ca\u5609\u95ee\uff0c\u201c\u60a8\u5728\u8fd9\u91cc\u5e72\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u665a\u4e0a\u597d\uff0c\u5170\u5c3c\u65af\u7279\u5c0f\u59d0\uff0c\u201d\u4f0a\u68ee\u5f6c\u5f6c\u6709\u793c\u5730\u56de\u5e94\uff0c\u201c\u6211\u6b63\u5728\u51c6\u5907\u5524\u9192\u4f1f\u5927\u7684\u7ea2\u9f99\u738b\uff0c\u8ba9\u4ed6\u91cd\u8fd4\u51e1\u95f4\uff0c\u4f60\u662f\u6765\u4e3a\u6211\u505a\u89c1\u8bc1\u7684\u5417\uff1f\u201d</p><p>\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u53ea\u662f\u6070\u597d\u8def\u8fc7\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4f46\u6211\u5728\u524d\u9762\u770b\u5230\u5f88\u591a\u4eba\u88ab\u6740\u6b7b\u4e86\u2026\u2026\u201d</p><p>\u201c\u662f\u6211\u6740\u4e86\u4ed6\u4eec\uff0c\u201d\u4f0a\u68ee\u6beb\u4e0d\u5728\u610f\u5730\u8bf4\uff0c\u201c\u4ed6\u4eec\u80c6\u6562\u963b\u62e6\u6211\uff0c\u8fd9\u662f\u4ed6\u4eec\u5e94\u6709\u7684\u4e0b\u573a\u3002\u201d</p><p>\u201c\u6211\u4e0d\u592a\u660e\u767d\uff0c\u5510\u7426\u62c9\u5148\u751f\u4e5f\u662f\u60f3\u53ec\u5524\u7ea2\u9f99\u738b\u5427\u3002\u65e2\u7136\u5927\u5bb6\u76ee\u7684\u4e00\u81f4\uff0c\u4e3a\u4ec0\u4e48\u8981\u81ea\u76f8\u6b8b\u6740\u5462\uff1f\u201d</p><p>\u4f0a\u68ee\u7b11\u8d77\u6765\uff0c\u201c\u7ea2\u9f99\u738b\u53ea\u80fd\u6709\u4e00\u4f4d\uff0c\u5170\u5c3c\u65af\u7279\u5c0f\u59d0\u3002\u6211\u662f\u8f9b\u5df4\u57ce\u4e3b\u4e4b\u5b50\uff0c\u672a\u6765\u7684\u56fd\u738b\u965b\u4e0b\uff0c\u6211\u624d\u662f\u552f\u4e00\u6709\u8d44\u683c\u7684\u4eba\u3002\u6211\u4f1a\u5524\u9192\u7ea2\u9f99\u738b\uff0c\u7ea2\u9f99\u738b\u5c06\u4f1a\u501f\u52a9\u6211\u800c\u518d\u5ea6\u964d\u4e34\u6b64\u4e16\uff0c\u4ed6\u4f1a\u7ed9\u4e88\u6211\u529b\u91cf\u548c\u6c38\u751f\uff0c\u8ba9\u6211\u6210\u4e3a\u771f\u6b63\u7684\u5f7b\u68ee\u5854\u4e4b\u738b\u3002\u201d</p><p>\u201c\u539f\u6765\u5982\u6b64\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u90a3\u4e48\u9884\u795d\u4f60\u6210\u529f\uff0c\u7ea2\u9f99\u965b\u4e0b\uff0c\u6211\u5148\u544a\u9000\u4e86\u3002\u201d</p><p>\u5979\u8f6c\u8eab\u6b32\u8d70\uff0c\u4f0a\u68ee\u5374\u6162\u60a0\u60a0\u5730\u5f00\u53e3\u4e86\uff0c\u201c\u4e0d\uff0c\u4e0d\uff0c\u8bf7\u7a0d\u7b49\uff0c\u5170\u5c3c\u65af\u7279\u5c0f\u59d0\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6216\u8bb8\u4f60\u5e94\u8be5\u7559\u5728\u8fd9\u91cc\u3002\u201d</p><p>\u73ca\u5609\u8f6c\u56de\u8eab\uff0c\u201c\u600e\u4e48\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u6e05\u695a\u4f60\u7684\u7acb\u573a\uff0c\u5170\u5c3c\u65af\u7279\u5c0f\u59d0\uff0c\u6bd5\u7adf\u4f60\u4eec\u662f\u5916\u4e61\u4eba\uff0c\u201d\u4f0a\u68ee\u8bf4\uff0c\u201c\u7ea2\u9f99\u738b\u5373\u5c06\u91cd\u8fd4\u51e1\u95f4\uff0c\u5728\u8fd9\u79cd\u5173\u952e\u65f6\u5019\uff0c\u6211\u4e0d\u60f3\u51fa\u73b0\u4efb\u4f55\u610f\u5916\u3002\u5982\u679c\u65b9\u4fbf\u7684\u8bdd\uff0c\u6211\u5efa\u8bae\u4f60\u5728\u8fd9\u91cc\u7a0d\u4f5c\u7b49\u5f85\uff0c\u76f8\u4fe1\u6211\uff0c\u4e0d\u4f1a\u592a\u4e45\u3002\u201d</p><p>\u201c\u4f60\u662f\u8981\u6263\u62bc\u6211\u5417\uff1f\u201d</p><p>\u201c\u53ea\u662f\u4e00\u4e2a\u5efa\u8bae\uff0c\u6211\u5e76\u4e0d\u60f3\u5192\u72af\u60a8\u8fd9\u6837\u7f8e\u4e3d\u7684\u5973\u58eb\uff0c\u800c\u4e14\u60a8\u8fd8\u662f\u51db\u7684\u670b\u53cb\u3002\u201d</p><p>\u201c\u5982\u679c\u6211\u4e0d\u63a5\u53d7\u4f60\u7684\u5efa\u8bae\u5462\uff1f\u201d</p><p>\u201c\u90a3\u771f\u7684\u9057\u61be\u3002\u201d\u4f0a\u68ee\u8bf4\uff0c\u5ffd\u7136\u4e3e\u8d77\u624b\u4e2d\u7684\u86c7\u6756\uff0c\u671d\u7740\u73ca\u5609\u4e00\u6307\u3002</p><p>\u4e00\u9053\u9ed1\u8272\u7684\u5149\u675f\u4ece\u6756\u5934\u5c04\u51fa\uff0c\u73ca\u5609\u65e9\u6709\u9632\u5907\uff0c\u4fa7\u8eab\u907f\u5f00\uff0c\u5149\u675f\u6253\u5728\u80cc\u540e\u7684\u5899\u58c1\u4e0a\uff0c\u201c\u5657\u201d\u5730\u7a7f\u51fa\u4e00\u4e2a\u6d1e\u6765\u3002\u5c11\u5973\u5403\u4e86\u4e00\u60ca\uff0c\u8fde\u5fd9\u53d1\u52a8\u4e86\u4e00\u4e2a\u9690\u5f62\u6cd5\u672f\uff0c\u540c\u65f6\u6307\u6325\u79d8\u5076\u548c\u5973\u796d\u53f8\u9b54\u50cf\u671d\u7740\u4f0a\u68ee\u6251\u8fc7\u53bb\u3002</p><p>\u5657\uff01\u5657\uff01\u5657\uff01\u5657\uff01</p><p>\u4f0a\u68ee\u80e1\u4e71\u5730\u6325\u821e\u7740\u86c7\u6756\uff0c\u4e0d\u65ad\u671d\u56db\u9762\u516b\u65b9\u5c04\u51fa\u9ed1\u8272\u5149\u675f\uff0c\u79d8\u5076\u975e\u5e38\u7075\u6d3b\uff0c\u5b83\u5de6\u8eb2\u53f3\u95ea\uff0c\u817e\u8dc3\u7ffb\u6eda\uff0c\u8eb2\u5f00\u4e86\u6bcf\u4e00\u6b21\u653b\u51fb\uff0c\u76f8\u6bd4\u8d77\u6765\uff0c\u5973\u796d\u53f8\u9b54\u50cf\u5c31\u7b28\u62d9\u8bb8\u591a\uff0c\u5979\u88ab\u5149\u675f\u63a5\u8fde\u51fb\u4e2d\u4e86\u51e0\u6b21\uff0c\u6700\u7ec8\u54d7\u5730\u4e00\u58f0\uff0c\u56db\u5206\u4e94\u88c2\u5f00\u6765\u3002</p><p>\u79d8\u5076\u518d\u5ea6\u6251\u4e0a\uff0c\u8fd9\u6b21\u5b83\u6ca1\u80fd\u8eb2\u5f00\u5149\u675f\uff0c\u80f8\u53e3\u88ab\u6b63\u6b63\u51fb\u4e2d\uff0c\u53d1\u51fa\u201c\u94ff\u201d\u5730\u4e00\u58f0\u8106\u54cd\uff0c\u987f\u65f6\u51f9\u9677\u4e0b\u53bb\u4e00\u5757\u3002\u4f0a\u68ee\u4e3e\u8d77\u86c7\u6756\uff0c\u5bf9\u51c6\u79d8\u5076\uff0c\u51c6\u5907\u518d\u8865\u4e0a\u4e00\u53d1\uff0c\u5ffd\u7136\u5fc3\u53e3\u4e00\u9635\u5267\u75db\uff0c\u4f4e\u5934\u770b\u53bb\uff0c\u4fbf\u89c1\u4e00\u622a\u7ec6\u7ec6\u7684\u5251\u5203\uff0c\u4ece\u80f8\u53e3\u76f4\u523a\u51fa\u6765\u3002</p><p>\u73ca\u5609\u501f\u52a9\u9690\u5f62\u672f\uff0c\u5728\u5973\u796d\u53f8\u9b54\u50cf\u548c\u79d8\u5076\u7684\u63a9\u62a4\u4e0b\uff0c\u6084\u6084\u903c\u8fd1\u4f0a\u68ee\u7684\u80cc\u540e\uff0c\u4e00\u5251\u4fbf\u671d\u5fc3\u810f\u90e8\u4f4d\u523a\u8fdb\u53bb\u3002\u5979\u89c1\u4f0a\u68ee\u624b\u4e2d\u7684\u86c7\u6756\u6781\u4e3a\u53e4\u602a\uff0c\u53d1\u51fa\u7684\u9ed1\u8272\u5149\u675f\u51cc\u5389\u65e0\u6bd4\uff0c\u56e0\u6b64\u4e0d\u6562\u4e1d\u6beb\u7559\u624b\uff0c\u4e00\u5251\u523a\u51fa\uff0c\u968f\u5373\u5168\u529b\u53d1\u52a8\u5251\u4e0a\u5f02\u80fd\u3002\u73ca\u5609\u7684\u201c\u7d2b\u8346\u201d\u5251\u662f\u5965\u52a0\u83b1\u65af\u7279\u522b\u4e3a\u5979\u953b\u9020\u7684\uff0c\u9644\u5e26\u4e86\u5bd2\u51b0\u3001\u70c8\u7130\u548c\u865a\u5f31\u8bc5\u5492\u4e09\u79cd\u9b54\u6cd5\u6548\u679c\uff0c\u53ef\u4ee5\u540c\u65f6\u53d1\u52a8\uff0c\u4e5f\u53ef\u4ee5\u4efb\u610f\u53d1\u52a8\u5176\u4e2d\u4e00\u79cd\u3002\u4f0a\u68ee\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u5bd2\u6c14\u4fbf\u4ece\u5251\u5203\u4e0a\u6025\u901f\u8513\u5ef6\u51fa\u6765\uff0c\u5c06\u4ed6\u6574\u4e2a\u4eba\u5b8c\u5168\u51bb\u4f4f\u3002</p><p>\u86c7\u6756\u201c\u5f53\u201d\u5730\u4e00\u58f0\u6389\u843d\u5728\u5730\u4e0a\uff0c\u73ca\u5609\u4fef\u8eab\u6361\u8d77\uff0c\u5979\u770b\u5f97\u51fa\u6765\uff0c\u4f0a\u68ee\u81ea\u5df1\u5176\u5b9e\u672c\u9886\u5e73\u5e73\uff0c\u4ed6\u80fd\u53d1\u51fa\u90a3\u79cd\u65e0\u575a\u4e0d\u6467\u7684\u9ed1\u8272\u5149\u675f\uff0c\u5b8c\u5168\u662f\u9760\u8fd9\u4e1c\u897f\u3002\u5979\u4ece\u5934\u5230\u5c3e\u770b\u4e86\u4e00\u904d\uff0c\u53d1\u73b0\u4f3c\u4e4e\u4e5f\u6ca1\u4ec0\u4e48\u7279\u522b\u4e4b\u5904\uff0c\u7070\u70df\u8272\u7684\u6756\u8eab\u4e0a\uff0c\u65e2\u6ca1\u6709\u4efb\u4f55\u7b26\u6587\u3001\u6807\u8bb0\uff0c\u751a\u81f3\u8fde\u88c5\u9970\u7684\u82b1\u7eb9\u90fd\u6ca1\u6709\uff0c\u6756\u5934\u4e0a\u7684\u86c7\u96d5\u523b\u5f97\u5f88\u7cbe\u81f4\uff0c\u4f46\u7ec8\u7a76\u4e5f\u53ea\u662f\u96d5\u523b\u800c\u5df2\u3002</p><p>\u201c\u4f60\u2026\u2026\u4f60\u6740\u4e86\u4ed6\uff1f\u201d</p><p>\u73ca\u5609\u62ac\u5934\uff0c\u60ca\u8bb6\u5730\u770b\u89c1\u5510\u7426\u62c9\u8d70\u8fc7\u6765\u3002\u8001\u796d\u53f8\u7684\u6b65\u4f10\u6709\u4e9b\u8e52\u8dda\uff0c\u4f46\u770b\u8d77\u6765\u5df2\u7ecf\u6ca1\u6709\u5927\u788d\u3002\u201c\u62b1\u6b49\uff0c\u4f46\u6211\u522b\u65e0\u9009\u62e9\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u6084\u6084\u547d\u4ee4\u79d8\u5076\u5411\u81ea\u5df1\u9760\u8fd1\uff0c\u201c\u4ed6\u5148\u653b\u51fb\u6211\u7684\u3002\u201d</p><p>\u201c\u53ef\u601c\u7684\u4f0a\u68ee\uff0c\u6211\u60f3\u4ed6\u4e00\u5b9a\u662f\u88ab\u90aa\u7075\u9644\u4f53\u4e86\uff0c\u624d\u4f1a\u505a\u51fa\u8fd9\u4e48\u53ef\u6015\u7684\u4e8b\u60c5\u3002\u201d\u5510\u7426\u62c9\u6162\u6162\u8d70\u5230\u73ca\u5609\u9762\u524d\uff0c\u5ffd\u7136\u62ac\u624b\u4e00\u6293\uff0c\u5c06\u86c7\u6756\u62a2\u4e86\u8fc7\u6765\uff0c\u5c11\u5973\u5fae\u5fae\u4e00\u60ca\uff0c\u4e0b\u610f\u8bc6\u5730\u5f80\u540e\u9000\u4e86\u51e0\u6b65\u3002\u201c\u65e0\u8bba\u5982\u4f55\uff0c\u611f\u8c22\u4f60\u7684\u5e2e\u5fd9\uff0c\u5170\u5c3c\u65af\u7279\u5c0f\u59d0\uff0c\u201d\u8001\u796d\u53f8\u8bf4\uff0c\u201c\u73b0\u5728\u8bf7\u4f60\u518d\u5f80\u540e\u9000\u4e00\u70b9\uff0c\u6211\u8981\u5b8c\u6210\u4e4b\u524d\u6ca1\u6709\u5b8c\u6210\u7684\u5de5\u4f5c\u3002\u201d</p><p>\u73ca\u5609\u4f9d\u8a00\uff0c\u9000\u5230\u6cd5\u9635\u4e4b\u5916\uff0c\u79d8\u5076\u7d27\u7d27\u8ddf\u5728\u5979\u8eab\u4fa7\u3002\u5510\u7426\u62c9\u5728\u6cd5\u9635\u4e2d\u8d70\u4e86\u534a\u5708\uff0c\u5ffd\u7136\u4e00\u6325\u86c7\u6756\uff0c\u5341\u51e0\u6761\u9ed1\u8272\u7684\u5149\u5e26\u4ece\u6756\u4e2d\u98de\u51fa\uff0c\u7eb5\u6a2a\u4ea4\u9519\uff0c\u7f16\u7ec7\u6210\u4e00\u4e2a\u7262\u7b3c\uff0c\u5c06\u73ca\u5609\u548c\u79d8\u5076\u90fd\u56f0\u5728\u5176\u4e2d\u3002</p><p>\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u5510\u7426\u62c9\u5148\u751f\uff1f\u201d\u5c11\u5973\u9547\u5b9a\u5730\u95ee\u3002</p><p>\u201c\u53ea\u662f\u4e3a\u4e86\u5b89\u5168\u8d77\u89c1\uff0c\u6bd5\u7adf\u6211\u8fd8\u4e0d\u80fd\u786e\u5b9a\u4f60\u7684\u7acb\u573a\uff0c\u201d\u5510\u7426\u62c9\u8bf4\uff0c\u201c\u8bf7\u7a0d\u5f85\u7247\u523b\uff0c\u4ec0\u4e48\u90fd\u522b\u505a\uff0c\u5f85\u6211\u5524\u9192\u795e\u738b\uff0c\u6211\u60f3\u5979\u4f1a\u611f\u8c22\u4f60\u7684\u5e2e\u52a9\u3002\u201d</p><p>\u201c\u5979\uff1f\u201d\u73ca\u5609\u654f\u9510\u5730\u5bdf\u89c9\u5230\u4ed6\u8bdd\u8bed\u4e2d\u7684\u95ee\u9898\uff0c\u201c\u4f60\u662f\u8981\u53ec\u5524\u7ea2\u9f99\u738b\u5417\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u7ea2\u9f99\uff1f\u4e0d\uff0c\u5f53\u7136\u4e0d\uff0c\u6211\u8981\u5524\u9192\u7684\u662f\u9f99\u540e\u63d0\u4e9a\u739b\u7279\u965b\u4e0b\u3002\u201d</p><p>\u201c\u55ef\uff1f\u4f60\u4e0d\u662f\u7ea2\u9f99\u796d\u53f8\u5417\uff1f\u201d</p><p>\u201c\u7136\u800c\u7ea2\u9f99\u738b\u5e76\u975e\u771f\u795e\uff0c\u201d\u5510\u7426\u62c9\u8bf4\uff0c\u201c\u5b83\u53ea\u662f\u9f99\u540e\u7684\u4e00\u90e8\u5206\u3002\u201d</p><p>\u4ed6\u9ad8\u9ad8\u4e3e\u8d77\u86c7\u6756\uff0c\u9ed1\u8272\u7684\u5149\u4ece\u6756\u5934\u5982\u6d41\u6c34\u822c\u503e\u6cfb\u4e0b\u6765\uff0c\u51b2\u6d17\u7740\u5168\u8eab\u3002\u7ed8\u5236\u5728\u5730\u4e0a\u7684\u9b54\u6cd5\u9635\uff0c\u6bcf\u4e00\u4e2a\u7ebf\u6761\u4ea4\u6c47\u7684\u8282\u70b9\u90fd\u95ea\u70c1\u8d77\u5fae\u5149\uff0c\u5728\u9ed1\u6697\u4e2d\u4eff\u4f5b\u7480\u74a8\u7fa4\u661f\u3002</p><p>\u201c\u4f7f\u8005\u624b\u6301\u86c7\u6756\uff0c\u6253\u5f00\u4e86\u9ed1\u6697\u7684\u51a5\u754c\u901a\u9053\u3002\u201d</p><p>\u201c\u4f7f\u8005\u62ab\u8346\u65a9\u68d8\uff0c\u7a7f\u8fc7\u4e86\u66f2\u6298\u7684\u4e03\u5ea7\u957f\u5eca\u3002\u201d</p><p>\u201c\u4f7f\u8005\u70b9\u71c3\u706b\u628a\uff0c\u9a71\u9000\u4e86\u4e0d\u7518\u7684\u4ea1\u9b42\u7ea0\u7f20\u3002\u201d</p><p>\u201c\u4f7f\u8005\u62db\u6765\u5de8\u517d\uff0c\u53d6\u4e0b\u5b83\u73cd\u8d35\u7684\u9752\u94dc\u773c\u775b\u3002\u201d</p><p>\u5510\u7426\u62c9\u5728\u6cd5\u9635\u4e2d\u9006\u65f6\u9488\u65b9\u5411\u884c\u8d70\u65cb\u8f6c\uff0c\u53e3\u4e2d\u9ad8\u58f0\u541f\u8bf5\u7740\uff0c\u4ed6\u8bf4\u7684\u662f\u6069\u745f\u8bed\uff0c\u800c\u4e14\u4f3c\u4e4e\u662f\u53e4\u6069\u745f\u8bed\uff0c\u73ca\u5609\u5b8c\u5168\u542c\u4e0d\u61c2\uff0c\u4f46\u5979\u80fd\u591f\u6e05\u695a\u5730\u5bdf\u89c9\u5230\uff0c\u968f\u7740\u8001\u796d\u53f8\u7684\u541f\u8bf5\uff0c\u6709\u65e0\u5f62\u7684\u529b\u91cf\u6b63\u4ece\u56db\u9762\u516b\u65b9\u6e90\u6e90\u4e0d\u65ad\u5730\u6c47\u96c6\u8fc7\u6765\uff0c\u51dd\u805a\u5728\u4ed6\u624b\u4e2d\u7684\u86c7\u6756\u91cc\uff0c\u7136\u540e\u6ce8\u5165\u6cd5\u9635\u3002\u968f\u7740\u529b\u91cf\u7684\u6ce8\u5165\uff0c\u5730\u9762\u4e0a\u7684\u56fe\u6848\u5f00\u59cb\u6cdb\u8d77\u5149\u6765\uff0c\u81ea\u5916\u5411\u5185\uff0c\u4e00\u73af\u4e00\u73af\uff0c\u9010\u5c42\u63a8\u8fdb\u3002</p><p>\u5510\u7426\u62c9\u7ad9\u5b9a\u811a\u6b65\uff0c\u9ad8\u9ad8\u4e3e\u8d77\u5de6\u624b\uff0c\u201c\u6d77\u9f99\uff01\u201d</p><p>\u968f\u7740\u4ed6\u7684\u547c\u558a\uff0c\u873f\u8712\u6cb3\u4e2d\u5ffd\u7136\u51fa\u73b0\u4e00\u4e2a\u5de8\u5927\u7684\u6f29\u6da1\uff0c\u7136\u540e\u4e00\u5934\u6c34\u517d\u4ece\u6f29\u6da1\u4e2d\u6d6e\u51fa\u3002\u5b83\u6709\u5e9e\u5927\u81c3\u80bf\u7684\u8eab\u8eaf\u548c\u5c0f\u5c0f\u7684\u4e09\u89d2\u5f62\u8111\u888b\uff0c\u53c8\u7ec6\u53c8\u957f\u7684\u8116\u9888\u5f2f\u66f2\u5982\u86c7\uff0c\u76ae\u80a4\u662f\u6697\u9ec4\u8272\uff0c\u4f53\u578b\u4e0d\u7b97\u975e\u5e38\u5927\u3002\u4eff\u4f5b\u662f\u610f\u8bc6\u5230\u4ec0\u4e48\uff0c\u6d77\u9f99\u5728\u6781\u529b\u6323\u624e\u7740\uff0c\u4e00\u53cc\u9752\u94dc\u8272\u7684\u773c\u775b\u7741\u5f97\u5927\u5927\u7684\uff0c\u53e3\u4e2d\u53d1\u51fa\u5636\u54d1\u7684\u53eb\u58f0\uff0c\u60f3\u8981\u9003\u56de\u6c34\u5e95\uff0c\u7136\u800c\u67d0\u79cd\u65e0\u5f62\u7684\u529b\u91cf\u675f\u7f1a\u7740\u5b83\uff0c\u8ba9\u5b83\u65e0\u6cd5\u52a8\u5f39\u3002</p><p>\u5510\u7426\u62c9\u7684\u5de6\u624b\u865a\u63e1\uff0c\u53ea\u542c\u5f97\u201c\u556a\u201d\u5730\u4e00\u58f0\uff0c\u6d77\u9f99\u7684\u5de6\u773c\u7a81\u7136\u7206\u88c2\u5f00\u6765\uff0c\u5316\u4f5c\u65e0\u6570\u70b9\u6df1\u7eff\u8272\u7684\u788e\u6db2\uff0c\u5760\u5165\u6cd5\u9635\u4e2d\u3002\u6d77\u9f99\u60e8\u53eb\u7740\uff0c\u6323\u624e\u5f97\u66f4\u52a0\u5389\u5bb3\uff0c\u4f0a\u68ee\u653e\u5f00\u675f\u7f1a\uff0c\u8ba9\u5b83\u6f5c\u56de\u6c34\u4e2d\uff0c\u6f29\u6da1\u968f\u4e4b\u6d88\u5931\u4e86\u3002</p><p>\u968f\u7740\u6d77\u9f99\u773c\u7403\u788e\u6db2\u7684\u5760\u5165\uff0c\u6cd5\u9635\u4e2d\u7684\u5149\u8292\u53d8\u5f97\u66f4\u52a0\u660e\u4eae\uff0c\u4eae\u5230\u523a\u773c\u7684\u7a0b\u5ea6\uff0c\u51e0\u4e4e\u8fd1\u4e8e\u5b9e\u8d28\u3002\u5510\u7426\u62c9\u7b49\u5f85\u4e86\u7247\u523b\uff0c\u7ee7\u7eed\u8bf5\u51fa\u4e0b\u4e00\u7ec4\u7977\u8bcd\u3002</p><p>\u201c\u4f7f\u8005\u530d\u5310\u5728\u706b\u7130\u7684\u5165\u53e3\uff0c\u9047\u89c1\u4e86\u84dd\u8272\u7684\u7f8e\u4e3d\u8774\u8776\u3002\u201d</p><p>\u201c\u4f7f\u8005\u9690\u85cf\u5728\u9ed1\u6697\u7684\u957f\u5df7\uff0c\u906d\u9047\u4e86\u5168\u8eab\u5c16\u523a\u7684\u731b\u517d\u3002</p><p>\u201c\u4f7f\u8005\u7ad9\u5728\u6df1\u6e0a\u7684\u8fb9\u7f18\u5f80\u4e0b\u770b\uff0c\u4ed6\u770b\u89c1\u4e86\u873f\u8712\u7684\u5de8\u86c7\u3002\u201d</p><p>\u201c\u4f7f\u8005\u8d70\u5230\u4e86\u51a5\u754c\u7684\u4e2d\u592e\uff0c\u547c\u5524\u7740\u5931\u843d\u7684\u5723\u540d\u3002\u201d</p><p>\u968f\u7740\u7977\u8bcd\u7684\u8bf5\u51fa\uff0c\u6cd5\u9635\u4e2d\u6d8c\u51fa\u7684\u5149\u8292\u8d8a\u6765\u8d8a\u5267\u70c8\uff0c\u6e10\u6e10\u51dd\u805a\u6210\u4e00\u4e2a\u5de8\u5927\u7684\u602a\u517d\u865a\u5f71\u3002\u5b83\u770b\u8d77\u6765\u50cf\u662f\u4e00\u5934\u6d77\u86c7\uff0c\u53c8\u6709\u4e9b\u50cf\u8725\u8734\uff0c\u8fd8\u6709\u51e0\u5206\u50cf\u9f99\uff0c\u6709\u4e94\u4e2a\u5934\u9885\u548c\u957f\u957f\u7684\u3001\u5206\u53c9\u7684\u5c3e\u5df4\uff0c\u76d8\u65cb\u7740\uff0c\u51e0\u4e4e\u5360\u636e\u4e86\u6574\u4e2a\u6cd5\u9635\u3002</p><p>\u201c\u8fd9\u5c31\u662f\u9f99\u540e\uff1f\u201d\u73ca\u5609\u8f7b\u58f0\u95ee\u3002</p><p>\u201c\u8fd9\u5c31\u662f\u9f99\u540e\uff01\u201d\u5510\u7426\u62c9\u9a84\u50b2\u5730\u8bf4\uff0c\u201c\u5979\u662f\u6700\u53e4\u8001\u7684\u795e\u738b\uff0c\u638c\u63e1\u6d69\u701a\u6d77\u6d0b\u7684\u529b\u91cf\uff0c\u7387\u9886\u4f17\u795e\u6253\u8d25\u4e86\u90aa\u6076\u7684\u5deb\u5e08\u3002\u5979\u66fe\u56e0\u4e3a\u5351\u9119\u7684\u80cc\u53db\u800c\u9677\u5165\u6c89\u7761\uff0c\u4e5f\u5c06\u56e0\u4e3a\u8654\u8bda\u7684\u4fe1\u5f92\u800c\u91cd\u65b0\u82cf\u9192\u3002\u4eca\u65e5\uff0c\u9f99\u540e\u5c06\u4f1a\u518d\u5ea6\u541b\u4e34\u8fd9\u4e2a\u4e16\u754c\uff0c\u4f60\u5c06\u6210\u4e3a\u89c1\u8bc1\u8fd9\u4e00\u5207\u7684\u7b2c\u4e00\u4e2a\u51e1\u4eba\uff0c\u5170\u5c3c\u65af\u7279\u5c0f\u59d0\u3002\u201d</p><p>\u201c\u4f60\u5462\uff1f\u201d\u73ca\u5609\u95ee\uff0c\u201c\u9f99\u540e\u4e0d\u4f1a\u5956\u8d4f\u4f60\u5417\uff1f\u201d</p><p>\u201c\u6211\u4f1a\u83b7\u5f97\u5956\u8d4f\uff0c\u4e0d\u8fc7\u662f\u5728\u6b7b\u540e\uff0c\u201d\u5510\u7426\u62c9\u8bf4\uff0c\u201c\u4f7f\u8005\u4ee5\u81ea\u5df1\u7684\u8eaf\u4f53\u4f5c\u4e3a\u5bb9\u5668\uff0c\u627f\u8f7d\u795e\u738b\u8fd4\u5f52\u51e1\u95f4\uff0c\u7075\u9b42\u5219\u4f1a\u6c38\u8fdc\u7559\u5728\u51a5\u754c\u3002\u6240\u4ee5\uff0c\u6c38\u522b\u4e86\uff0c\u5170\u5c3c\u65af\u7279\u5c0f\u59d0\uff0c\u8bf7\u4ee3\u6211\u5411\u4ee4\u5f1f\u3001\u6885\u83f2\u65af\u5c0f\u59d0\u3001\u51db\u5c0f\u59d0\u95ee\u597d\uff0c\u611f\u8c22\u4ed6\u4eec\u7684\u6177\u6168\u5e2e\u52a9\uff0c\u82e5\u975e\u5982\u6b64\uff0c\u6211\u662f\u65e0\u6cd5\u5b8c\u6210\u8fd9\u4e2a\u6cd5\u9635\u7684\u3002\u201d</p><p>\u201c\u6216\u8bb8\u4f60\u4e0d\u5fc5\u611f\u8c22\u3002\u201d\u73ca\u5609\u8bf4\u3002</p><p>\u5510\u7426\u62c9\u6ca1\u6709\u542c\u61c2\u5c11\u5973\u7684\u8bdd\uff0c\u4f46\u73b0\u5728\u4ed6\u4e5f\u65e0\u6687\u591a\u60f3\u3002\u6cd5\u9635\u7684\u5149\u4e00\u6ce2\u53c8\u4e00\u6ce2\u5730\u6d8c\u8d77\uff0c\u8d8a\u6765\u8d8a\u4eae\uff0c\u9f99\u540e\u7684\u5e9e\u5927\u865a\u5f71\u5df2\u7ecf\u5feb\u8981\u51dd\u805a\u6210\u5b9e\u4f53\uff0c\u50ac\u4fc3\u4ed6\u5b8c\u6210\u4eea\u5f0f\u3002</p><p>\u5510\u7426\u62c9\u6df1\u6df1\u547c\u5438\uff0c\u5e73\u7f13\u6fc0\u52a8\u7684\u5fc3\u60c5\uff0c\u5f00\u59cb\u51c6\u5907\u8bf5\u51fa\u6700\u540e\u4e00\u6bb5\u7977\u6587\u3002</p><p>\u4ed6\u662f\u5bdf\u65af\u8428\u6559\u4f1a\u7684\u6697\u796d\uff0c\u8d44\u5386\u6781\u6df1\uff0c\u80fd\u591f\u63a5\u89e6\u5230\u6240\u6709\u7684\u673a\u5bc6\u8d44\u6599\u3001\u5c01\u5b58\u6863\u6848\uff0c\u867d\u7136\u6ca1\u6709\u53bb\u8fc7\u4e2d\u571f\uff0c\u4f46\u5728\u8f9b\u5df4\u57ce\u4e5f\u66fe\u7ecf\u548c\u4e00\u4e9b\u4e2d\u571f\u7267\u5e08\u4ea4\u6d41\u8fc7\uff0c\u773c\u754c\u6bd4\u6559\u4f1a\u91cc\u90a3\u4e9b\u611a\u6627\u987d\u56fa\u7684\u540c\u50da\u8981\u5f3a\u591a\u4e86\u3002\u7ecf\u8fc7\u957f\u671f\u7684\u7814\u7a76\u548c\u601d\u8003\uff0c\u4ed6\u7ec8\u4e8e\u786e\u8ba4\uff1a\u51e0\u767e\u5e74\u6765\uff0c\u6559\u4f1a\u4e00\u76f4\u5c0a\u5949\u3001\u5d07\u62dc\u7684\u7ea2\u9f99\u738b\uff0c\u6839\u672c\u5c31\u662f\u4e2a\u4f2a\u795e\uff0c\u5b83\u5b9e\u9645\u4e0a\u662f\u9f99\u540e\u63d0\u4e9a\u739b\u7279\u7684\u4e00\u4e2a\u795e\u76f8\u3002</p><p>\u5728\u8ba4\u8bc6\u5230\u771f\u7406\u7684\u7b2c\u4e00\u4e2a\u591c\u665a\uff0c\u9f99\u540e\u964d\u4e34\u5728\u4ed6\u7684\u68a6\u4e2d\u3002</p><p>\u9f99\u540e\u80af\u5b9a\u4e86\u4ed6\u7684\u53d1\u73b0\uff0c\u5e76\u6388\u4e88\u4ed6\u4e00\u4e2a\u79d8\u5bc6\u4efb\u52a1\u3002\u6309\u7167\u9f99\u540e\u7684\u6307\u793a\uff0c\u4e09\u5e74\u524d\uff0c\u5510\u7426\u62c9\u968f\u4f0a\u68ee\u4e00\u8d77\u6765\u5230\u5723\u6e0a\u57ce\uff0c\u5728\u7ea2\u9f99\u795e\u6bbf\u7684\u50a8\u85cf\u5ba4\u91cc\u627e\u5230\u4e86\u8fd9\u4e2a\u9b54\u6cd5\u9635\u548c\u76f8\u5173\u7b14\u8bb0\uff0c\u8fd8\u6709\u4e00\u67c4\u86c7\u6756\u3002\u636e\u9f99\u540e\u6240\u8a00\uff0c\u7528\u8fd9\u4e2a\u9b54\u6cd5\u9635\uff0c\u53ef\u4ee5\u53ec\u5524\u5979\u964d\u4e34\u51e1\u95f4\uff0c\u800c\u86c7\u6756\u5219\u6765\u81ea\u51a5\u754c\uff0c\u66fe\u7ecf\u662f\u51a5\u795e\u4e4b\u7269\uff0c\u5b83\u80fd\u53d1\u51fa\u7834\u574f\u529b\u6781\u5f3a\u7684\u5149\u675f\uff0c\u4e5f\u80fd\u5c06\u4fe1\u5f92\u7684\u4fe1\u4ef0\u3001\u5d07\u62dc\u8f6c\u5316\u4e3a\u529b\u91cf\u50a8\u5b58\u8d77\u6765\uff0c\u7528\u4e8e\u63a8\u52a8\u6cd5\u9635\u8fd0\u884c\u3002\u552f\u4e00\u7684\u9ebb\u70e6\u662f\uff0c\u9b54\u6cd5\u9635\u662f\u6b8b\u7f3a\u7684\uff0c\u9700\u8981\u4fee\u590d\u624d\u80fd\u4f7f\u7528\u3002\u5510\u7426\u62c9\u5bf9\u9b54\u6cd5\u4ec5\u4ec5\u7565\u901a\u76ae\u6bdb\uff0c\u4ed6\u5411\u4e00\u4f4d\u6069\u745f\u7684\u670b\u53cb\u5bfb\u6c42\u5e2e\u52a9\uff0c\u4fee\u590d\u4e86\u90e8\u5206\u6cd5\u9635\uff0c\u4f46\u5374\u5728\u6700\u540e\u9636\u6bb5\u51fa\u73b0\u610f\u5916\uff0c\u5931\u53bb\u4e86\u8054\u7cfb\u3002\u5e78\u597d\uff0c\u5728\u9f99\u540e\u7684\u6307\u5f15\u4e0b\uff0c\u743c\u6069\u3001\u6885\u83f2\u65af\u7b49\u4eba\u6765\u5230\u4e86\u5723\u6e0a\u57ce\uff0c\u89e3\u51b3\u4e86\u8fd9\u4e2a\u96be\u9898\u3002</p><p>\u60f3\u5230\u6885\u83f2\u65af\uff0c\u4e0d\u77e5\u4e3a\u4ec0\u4e48\uff0c\u5510\u7426\u62c9\u603b\u89c9\u5f97\u6709\u4e9b\u5947\u602a\u3002\u4ed6\u7b2c\u4e00\u6b21\u89c1\u5230\u6885\u83f2\u65af\u548c\u51db\u662f\u5728\u8f9b\u5df4\u57ce\uff0c\u8fd9\u70b9\u4ed6\u8bb0\u5f97\u5f88\u6e05\u695a\uff0c\u6b64\u524d\u80af\u5b9a\u6ca1\u89c1\u8fc7\uff0c\u81f3\u5c11\u4ed6\u6ca1\u6709\u4efb\u4f55\u8bb0\u5fc6\uff0c\u4f46\u6bcf\u6b21\u770b\u89c1\u5c11\u5973\u7684\u8138\uff0c\u4ed6\u5c31\u603b\u6709\u79cd\u5f02\u6837\u7684\u719f\u6089\u611f\uff0c\u4eff\u4f5b\u5728\u5f88\u4e45\u5f88\u4e45\u4e4b\u524d\uff0c\u66fe\u7ecf\u89c1\u8fc7\u975e\u5e38\u76f8\u4f3c\u7684\u9762\u5bb9\uff0c\u518d\u53bb\u60f3\uff0c\u5374\u53c8\u4ec0\u4e48\u90fd\u60f3\u4e0d\u8d77\u6765\u4e86\u3002</p><p>\u7b97\u4e86\uff0c\u8fd9\u4e9b\u90fd\u4e0d\u91cd\u8981\u3002\u9f99\u540e\u5373\u5c06\u964d\u4e34\uff0c\u53ea\u5dee\u8fd9\u6700\u540e\u4e00\u6b65\u4e86\u3002</p><p>\u201c\u5510\u7426\u62c9\u5148\u751f\uff0c\u201d\u6cd5\u9635\u5916\uff0c\u90a3\u4f4d\u7f8e\u4e3d\u7684\u9ed1\u53d1\u5c11\u5973\u5ffd\u7136\u8bf4\uff0c\u201c\u6211\u5efa\u8bae\u4f60\u8fd8\u662f\u505c\u4e0b\u6765\u6bd4\u8f83\u597d\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u5462\u3002\u201d</p><p>\u201c\u6216\u8bb8\u5fd8\u4e86\u81ea\u6211\u4ecb\u7ecd\uff0c\u6211\u662f\u4f4d\u9884\u8a00\u5e08\uff0c\u201d\u5c11\u5973\u5f88\u8ba4\u771f\u5730\u8bf4\uff0c\u201c\u5728\u4f60\u5934\u4e0a\uff0c\u6211\u770b\u89c1\u5203\u661f\u6b63\u5728\u5189\u5189\u5347\u8d77\uff0c\u8fd9\u53ef\u4e0d\u662f\u4ec0\u4e48\u5409\u5146\u54e6\uff0c\u9884\u793a\u4f60\u4eca\u65e5\u5c06\u4f1a\u8bf8\u4e8b\u4e0d\u5b9c\u3002\u5982\u679c\u5f3a\u884c\u575a\u6301\u7684\u8bdd\uff0c\u6211\u76f8\u4fe1\u4f60\u6700\u7ec8\u4f1a\u5931\u8d25\u7684\uff0c\u800c\u4e14\u4f1a\u5931\u8d25\u5f97\u5f88\u96be\u770b\u3002\u201d</p><p>\u5510\u7426\u62c9\u7b11\u7740\u6447\u6447\u5934\uff0c\u4e0d\u518d\u7406\u4f1a\u5c11\u5973\u3002</p><p>\u201c\u795e\u554a\uff0c\u8bf7\u8046\u542c\u4f60\u5fe0\u5b9e\u5b50\u6c11\u7684\u547c\u5524\uff0c\u4ece\u4f60\u7684\u5b9d\u5ea7\u4e0a\u82cf\u9192\u3002\u201d</p><p>\u201c\u795e\u554a\uff0c\u8bf7\u91cd\u62fe\u4f60\u7684\u76fe\u4e0e\u957f\u5251\uff0c\u6211\u5df2\u5c06\u5b83\u4eec\u64e6\u62ed\u4e00\u65b0\u3002\u201d</p><p>\u201c\u795e\u554a\uff0c\u8bf7\u8de8\u4e0a\u4f60\u7684\u5750\u9a91\uff0c\u503e\u542c\u90a3\u6218\u9f13\u5728\u5929\u7a7a\u4e2d\u8f70\u9e23\u3002\u201d</p><p>\u201c\u795e\u554a\uff0c\u8bf7\u5f20\u5f00\u4f60\u7684\u5149\u8f89\u7fbd\u7ffc\uff0c\u518d\u4e00\u6b21\u964d\u4e34\u8fd9\u7f8e\u4e3d\u7684\u51e1\u5c18\u3002\u201d</p><p>\u5510\u7426\u62c9\u5ff5\u51fa\u6700\u540e\u4e00\u6bb5\u7977\u6587\uff0c\u770b\u7740\u56db\u9762\u516b\u65b9\u7528\u6765\u7684\u5149\u5c06\u4ed6\u5b8c\u5168\u541e\u6ca1\u3002\u4ed6\u5927\u7b11\u8d77\u6765\uff0c\u611f\u89c9\u5230\u5149\u900f\u8fc7\u81ea\u5df1\u7684\u8eab\u4f53\uff0c\u51b2\u5237\u7740\u6bcf\u4e00\u5bf8\u8840\u7ba1\uff0c\u50cf\u662f\u628a\u6574\u4e2a\u4eba\u4ece\u5185\u5230\u5916\u90fd\u6e05\u6d17\u4e86\u4e00\u904d\u3002\u201c\u6765\u5427\uff0c\u201d\u4ed6\u5fc3\u60f3\uff0c\u201c\u6765\u5427\uff0c\u9f99\u540e\uff0c\u501f\u52a9\u6211\u7684\u8eab\u4f53\u91cd\u751f\u5427\uff0c\u8ba9\u6211\u4e0e\u4f60\u6c38\u8fdc\u878d\u4e3a\u4e00\u4f53\u3002\u201d</p><p>\u7136\u540e\u5149\u5982\u6f6e\u6c34\u822c\u9000\u4e86\u56de\u53bb\u3002</p><p>\u201c\u600e\u2026\u2026\u600e\u4e48\u56de\u4e8b\uff01\u201d</p><p>\u5510\u7426\u62c9\u83ab\u540d\u60ca\u8be7\uff0c\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u3002\u73ca\u5609\u5728\u6cd5\u9635\u5916\u5374\u770b\u5f97\u660e\u767d\uff0c\u90a3\u53ea\u86c7\u5f62\u5de8\u517d\u2014\u2014\u90a3\u6b63\u662f\u4e0a\u53e4\u795e\u738b\u63d0\u4e9a\u739b\u7279\u7684\u672c\u76f8\u2014\u2014\u5df2\u7ecf\u5f20\u5f00\u5927\u53e3\uff0c\u5373\u5c06\u5c06\u5510\u7426\u62c9\u541e\u566c\uff0c\u5374\u5728\u6700\u540e\u4e00\u523b\u50cf\u662f\u906d\u9047\u91cd\u51fb\uff0c\u4e94\u4e2a\u5934\u9885\u4e00\u9f50\u5782\u4e0b\uff0c\u7136\u540e\u6574\u4e2a\u8eab\u8eaf\u90fd\u7206\u70b8\u5f00\u6765\uff0c\u5d29\u788e\u6210\u65e0\u6570\u91d1\u8272\u7684\u5149\u70b9\uff0c\u6f02\u6d6e\u5728\u6cd5\u9635\u4e2d\u3002</p><p>\u5510\u7426\u62c9\u81ea\u5df1\u770b\u4e0d\u89c1\u5de8\u86c7\u7684\u5d29\u6e83\uff0c\u4f46\u4ed6\u80fd\u591f\u6e05\u695a\u5730\u611f\u89c9\u5230\u6cd5\u9635\u660e\u660e\u5df2\u7ecf\u5373\u5c06\u5b8c\u6210\uff0c\u5374\u5728\u6700\u540e\u5173\u5934\u7a81\u7136\u9006\u8f6c\u3002\u5de8\u5927\u7684\u53cd\u51b2\u529b\u5c06\u4ed6\u51fb\u5012\u4e86\uff0c\u4ed6\u7684\u773c\u524d\u662f\u4e00\u7247\u8840\u8272\uff0c\u4ec0\u4e48\u90fd\u770b\u4e0d\u6e05\uff0c\u4ed6\u611f\u89c9\u81ea\u5df1\u91cd\u91cd\u5730\u6454\u5012\u5728\u5730\uff0c\u5168\u8eab\u9ebb\u75f9\u50f5\u786c\uff0c\u5b8c\u5168\u65e0\u6cd5\u52a8\u5f39\u534a\u5206\uff0c\u4f46\u610f\u8bc6\u4ecd\u7136\u6e05\u9192\u3002</p><p>\u6709\u4eba\u8d70\u5230\u8eab\u65c1\uff0c\u201c\u5510\u7426\u62c9\u5148\u751f\uff0c\u201d\u90a3\u662f\u73ca\u5609\u7684\u58f0\u97f3\uff0c\u201c\u4f60\u8fd8\u597d\u5427\uff1f\u201d</p><p>\u5510\u7426\u62c9\u60f3\u8bf4\u8bdd\uff0c\u521a\u4e00\u5f20\u5634\uff0c\u4e00\u53e3\u8840\u5c31\u6d8c\u4e86\u51fa\u6765\uff0c\u5c06\u4ed6\u6240\u6709\u7684\u7591\u60d1\u90fd\u5835\u4e86\u56de\u53bb\u3002\u201c\u522b\u6fc0\u52a8\uff0c\u5510\u7426\u62c9\u5148\u751f\uff0c\u8fc7\u5206\u6fc0\u52a8\u5bf9\u8eab\u4f53\u4e0d\u597d\uff0c\u201d\u5c11\u5973\u67d4\u58f0\u8bf4\uff0c\u201c\u867d\u7136\u4f60\u597d\u50cf\u5feb\u8981\u6b7b\u4e86\u3002\u201d</p><p>\u201c\u4f60\u2026\u2026\u4f60\u600e\u4e48\u77e5\u9053\u2026\u2026\u201d</p><p>\u201c\u544a\u8bc9\u8fc7\u4f60\uff0c\u6211\u662f\u4e2a\u9884\u8a00\u5e08\u561b\u3002\u201d\u73ca\u5609\u8bf4\u3002</p><p>\u5510\u7426\u62c9\u8fd8\u5f85\u518d\u95ee\uff0c\u5ffd\u7136\u8116\u9888\u4e0a\u6328\u4e86\u4e00\u8bb0\u91cd\u51fb\uff0c\u7acb\u523b\u6655\u4e86\u8fc7\u53bb\u3002</p><p>\u201c\u62b1\u6b49\u3002\u201d\u73ca\u5609\u8bf4\uff0c\u8ba9\u79d8\u5076\u628a\u5510\u7426\u62c9\u62d6\u5230\u65c1\u8fb9\uff0c\u7136\u540e\u8d70\u5230\u9b54\u6cd5\u9635\u4e2d\uff0c\u5728\u6700\u4e2d\u5fc3\u7684\u4f4d\u7f6e\u5bfb\u627e\u7247\u523b\uff0c\u5c06\u4e00\u4e2a\u9690\u853d\u7684\u7b26\u6587\u62b9\u53bb\u3002</p><p>\u539f\u6765\u90a3\u5929\u591c\u91cc\uff0c\u5927\u5bb6\u53d1\u73b0\u5510\u7426\u62c9\u7684\u8fd9\u4e2a\u9b54\u6cd5\u9635\u548c\u4e4b\u524d\u9047\u5230\u8fc7\u7684\u201c\u5316\u8eab\u201d\u6709\u76f8\u4f3c\u4e4b\u5904\uff0c\u731c\u6d4b\u8fd9\u5176\u4e2d\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u6000\u7591\u5510\u7426\u62c9\u610f\u56fe\u4e0d\u8f68\u3002\u743c\u6069\u662f\u5efa\u8bae\u4e0d\u8981\u518d\u4fee\u590d\u6cd5\u9635\uff0c\u4fee\u590d\u4e86\u4e5f\u4e0d\u7ed9\u5510\u7426\u62c9\uff0c\u6885\u83f2\u65af\u8ba4\u4e3a\u65e0\u6b64\u5fc5\u8981\u3002\u73ca\u5609\u5219\u662f\u89c9\u5f97\u4eba\u4e0d\u5b9c\u8f7b\u6613\u5931\u4fe1\uff0c\u4f46\u8fd8\u662f\u5e94\u8be5\u6709\u5907\u65e0\u60a3\uff0c\u9884\u9632\u4e07\u4e00\uff0c\u4fbf\u79c1\u4e0b\u627e\u5230\u7ef4\u82e5\u62c9\uff0c\u8ba9\u5979\u5728\u6cd5\u9635\u56fe\u7eb8\u4e0a\u505a\u4e86\u4e00\u5904\u5c0f\u5c0f\u7684\u6539\u52a8\u3002</p><p>\u8fd9\u4e2a\u6539\u52a8\u5f88\u4e0d\u5bb9\u6613\uff0c\u65e2\u8981\u4e0d\u663e\u5f97\u7a81\u5140\uff0c\u8ba9\u4eba\u770b\u4e0d\u51fa\u6765\uff0c\u53c8\u8981\u8d77\u5230\u963b\u65ad\u6cd5\u9635\u8fd0\u884c\u7684\u6548\u679c\u2014\u2014\u662f\u963b\u65ad\uff0c\u800c\u4e0d\u662f\u5355\u7eaf\u7684\u7834\u574f\uff0c\u800c\u4e14\u8981\u5728\u6700\u540e\u5173\u5934\u624d\u53d1\u6325\u6548\u679c\uff0c\u514d\u5f97\u88ab\u4eba\u63d0\u524d\u5bdf\u89c9\uff0c\u6700\u540e\u8fd8\u8981\u5bb9\u6613\u6539\u56de\u6765\u3002\u82e5\u4e0d\u662f\u7ef4\u82e5\u62c9\uff0c\u4e00\u822c\u5deb\u5e08\u8fd8\u771f\u6ca1\u8fd9\u4e2a\u672c\u4e8b\u3002\u73ca\u5609\u7684\u6253\u7b97\u662f\u89c1\u673a\u884c\u4e8b\uff0c\u82e5\u662f\u5510\u7426\u62c9\u5e76\u65e0\u6076\u610f\uff0c\u4fbf\u5728\u6700\u540e\u5e2e\u4ed6\u6539\u56de\u6765\uff1b\u82e5\u662f\u4ed6\u4e0d\u6000\u597d\u610f\uff0c\u81ea\u7136\u5c31\u8981\u8ba9\u4ed6\u81ea\u98df\u5176\u679c\u3002</p><p>\u5979\u62bd\u51fa\u957f\u7b1b\uff0c\u653e\u5728\u5507\u8fb9\u8f7b\u8f7b\u5439\u54cd\uff0c\u88ab\u79d8\u5076\u6253\u6655\u7684\u5510\u7426\u62c9\u5ffd\u7136\u52a8\u4e86\u4e00\u52a8\uff0c\u7136\u540e\u7741\u5f00\u773c\u775b\uff0c\u6162\u6162\u722c\u4e86\u8d77\u6765\uff0c\u9c9c\u8840\u4ece\u53e3\u9f3b\u4e2d\u4e0d\u65ad\u6ea2\u51fa\uff0c\u4ed6\u5374\u4eff\u4f5b\u5168\u65e0\u6240\u89c9\uff0c\u773c\u4e2d\u4e00\u7247\u8ff7\u832b\uff0c\u8138\u4e0a\u6f20\u65e0\u8868\u60c5\uff0c\u624b\u4e2d\u4ecd\u7136\u63e1\u7740\u90a3\u67c4\u86c7\u6756\u3002</p><p>\u201c\u665a\u4e0a\u597d\uff0c\u5510\u7426\u62c9\u5148\u751f\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u5979\u7684\u8bed\u8c03\u8f7b\u5feb\uff0c\u4eff\u4f5b\u521a\u521a\u53d1\u751f\u7684\u4e00\u5207\u4ece\u6765\u5c31\u6ca1\u6709\u51fa\u73b0\u8fc7\uff0c\u201c\u4f60\u5728\u8fd9\u91cc\u5e72\u561b\u5462\uff1f\u201d</p><p>\u201c\u6211\u5728\u5524\u9192\u9f99\u540e\u3002\u201d\u8001\u796d\u53f8\u6728\u7136\u56de\u7b54\u3002</p><p>\u201c\u4f60\u597d\u50cf\u5931\u8d25\u4e86\uff1f\u201d</p><p>\u201c\u662f\u7684\uff0c\u6211\u5931\u8d25\u4e86\u3002\u201d</p><p>\u201c\u6216\u8bb8\u4eca\u5929\u4e0d\u662f\u9f99\u540e\u7684\u5e78\u8fd0\u65e5\uff0c\u201d\u5c11\u5973\u5efa\u8bae\uff0c\u201c\u4e3a\u4ec0\u4e48\u4e0d\u6362\u4e00\u4f4d\u5462\uff0c\u6bd4\u5982\u8bf4\uff0c\u7ea2\u9f99\u738b\uff0c\u5f7b\u68ee\u5854\u7684\u5b88\u62a4\u4e4b\u795e\u3002\u201d</p><p>\u201c\u7ea2\u9f99\u738b\uff1f\u201d</p><p>\u201c\u662f\u7684\uff0c\u7ea2\u9f99\u738b\uff0c\u201d\u73ca\u5609\u8bf4\uff0c\u201c\u4f60\u662f\u7ea2\u9f99\u796d\u53f8\uff0c\u7406\u5f53\u5524\u9192\u7ea2\u9f99\u738b\u624d\u5bf9\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u201c\u4f60\u770b\u5230\u90a3\u53ea\u866b\u5b50\u4e86\u5417\uff0c\u5c31\u5728\u90a3\u8fb9\uff0c\u5bf9\uff0c\u8138\u957f\u5f97\u5f88\u50cf\u57c3\u535c\u62c9\u7684\u90a3\u4e2a\uff0c\u7ea2\u9f99\u738b\u5f88\u8ba8\u538c\u5b83\uff0c\u4f60\u4e5f\u5f88\u8ba8\u538c\u5b83\uff0c\u5bf9\u4e0d\u5bf9\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u53bb\u628a\u5b83\u5e72\u6389\u5427\u3002\u201d</p><p>\u5510\u7426\u62c9\u72b9\u8c6b\u4e86\u4e00\u4f1a\uff0c\u4e00\u8a00\u4e0d\u53d1\u5730\u8d70\u56de\u9b54\u6cd5\u9635\u4e2d\uff0c\u91cd\u65b0\u8bf5\u51fa\u7977\u8bcd\u3002</p><p>\u201c\u4f7f\u8005\u624b\u6301\u86c7\u6756\uff0c\u6253\u5f00\u4e86\u9ed1\u6697\u7684\u51a5\u754c\u901a\u9053\u3002\u201d</p><p>\u65e0\u5f62\u7684\u529b\u91cf\u518d\u5ea6\u805a\u96c6\u5728\u86c7\u6756\u4e2d\uff0c\u8df3\u8dc3\u7684\u5149\u8292\u91cd\u65b0\u81ea\u5730\u9762\u71c3\u8d77\uff0c\u51dd\u805a\u6210\u4e00\u4e2a\u5de8\u5927\u7684\u5149\u8327\u3002\u7247\u523b\u4e4b\u540e\uff0c\u5149\u8327\u7834\u788e\uff0c\u4e00\u4e2a\u5168\u8eab\u91cd\u7532\u3001\u9f99\u9996\u4eba\u8eab\u7684\u5de8\u5927\u8eab\u5f71\u4ece\u4e2d\u8d70\u4e86\u51fa\u6765\uff0c\u5b83\u5411\u56db\u5468\u770b\u4e86\u770b\uff0c\u957f\u5578\u4e00\u58f0\uff0c\u671d\u7740\u866b\u9b54\u5954\u53bb\u3002</p><p>\u203b\u203b\u203b</p><p>\u51db\u6b63\u5728\u548c\u51e0\u4e2a\u9b54\u4ec6\u5bf9\u5cd9\u3002</p><p>\u5979\u548c\u7ef4\u82e5\u62c9\u4e24\u4eba\u62b5\u8fbe\u5175\u8425\uff0c\u63a7\u5236\u4e86\u536b\u5175\u9996\u9886\uff0c\u8ba9\u4ed6\u628a\u6240\u6709\u7684\u536b\u5175\u90fd\u6d3e\u51fa\u53bb\uff0c\u63a5\u5e94\u5c45\u6c11\u8fdb\u5165\u5175\u8425\u907f\u96be\uff0c\u7136\u540e\u6309\u7167\u743c\u6069\u7684\u4ea4\u4ee3\uff0c\u7ef4\u82e5\u62c9\u53bb\u4fee\u590d\u5e7b\u672f\u6cd5\u9635\u3002</p><p>\u201c\u6d77\u5e02\u8703\u697c\u201d\u662f\u4e16\u4e0a\u6700\u5f3a\u5927\u7684\u5e7b\u672f\u4e4b\u4e00\uff0c\u987e\u540d\u601d\u4e49\uff0c\u4fbf\u662f\u521b\u9020\u51fa\u4e00\u4e2a\u5b8c\u5168\u865a\u5e7b\u5374\u53c8\u771f\u5b9e\u65e0\u6bd4\u7684\u573a\u666f\uff0c\u4e0d\u8981\u8bf4\u666e\u901a\u4eba\uff0c\u5c31\u7b97\u662f\u6709\u7ecf\u9a8c\u7684\u5192\u9669\u8005\u4e5f\u672a\u5fc5\u80fd\u770b\u7834\u3002\u4e0e\u90a3\u4e9b\u4f4e\u7ea7\u5e7b\u672f\u4e0d\u540c\uff0c\u6d77\u5e02\u8703\u697c\u521b\u9020\u51fa\u6765\u7684\u5e7b\u666f\uff0c\u5f71\u50cf\u3001\u58f0\u97f3\u3001\u6c14\u5473\u3001\u6e29\u5ea6\u3001\u89e6\u611f\uff0c\u65e0\u4e00\u4e0d\u5168\uff0c\u5047\u5982\u4f60\u770b\u5230\u4e00\u5757\u77f3\u5934\uff0c\u90a3\u4e48\u4f60\u6478\u4e0a\u53bb\u5c31\u662f\u51b0\u51b7\u7684\uff0c\u8e22\u4e0a\u53bb\u5c31\u662f\u575a\u786c\u7684\uff0c\u62ac\u8d77\u6765\u5c31\u662f\u6c89\u91cd\u7684\uff0c\u7edd\u4e0d\u4f1a\u6709\u534a\u70b9\u201c\u4e0d\u771f\u5b9e\u201d\u7684\u611f\u89c9\uff0c\u7136\u800c\u5b83\u5176\u5b9e\u662f\u4e0d\u5b58\u5728\u7684\u3002</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u6614\u65e5\u90a3\u4f4d\u5deb\u5e08\u662f\u4ec0\u4e48\u4eba\uff0c\u4e3a\u4ec0\u4e48\u8981\u5728\u8fd9\u5ea7\u5175\u8425\u91cc\u5e03\u4e0b\u8fd9\u4e2a\u5f3a\u5927\u7684\u5e7b\u672f\u6cd5\u9635\uff0c\u6709\u53ef\u80fd\u662f\u4e00\u79cd\u9632\u5fa1\u63aa\u65bd\uff0c\u4f46\u540c\u6837\u6709\u53ef\u80fd\u662f\u60f3\u5bf9\u8425\u4e2d\u4eba\u4e0d\u5229\u3002\u743c\u6069\u66fe\u7ecf\u542c\u8bf4\u8fc7\u6545\u4e8b\uff0c\u67d0\u4f4d\u9886\u4e3b\u5f97\u7f6a\u4e86\u4e00\u4e2a\u5927\u5deb\u5e08\uff0c\u4e8e\u662f\u5deb\u5e08\u65bd\u5c55\u4e86\u4e00\u4e2a\u6d77\u5e02\u8703\u697c\uff0c\u5c06\u9886\u4e3b\u7684\u57ce\u5821\u53d8\u6210\u4e86\u4e00\u4e2a\u5de8\u5927\u7684\u8ff7\u5bab\uff0c\u57ce\u5821\u91cc\u7684\u6240\u6709\u751f\u547d\uff0c\u5305\u62ec\u9886\u4e3b\u3001\u5bb6\u5c5e\u3001\u4ec6\u4eba\u4ee5\u53ca\u9a6c\u5339\u3001\u5ba0\u7269\uff0c\u90fd\u6ca1\u80fd\u8d70\u51fa\u8fd9\u4e2a\u8ff7\u5bab\uff0c\u6700\u540e\u5168\u90e8\u6d3b\u6d3b\u997f\u6b7b\u4e86\u3002\u4e0d\u8fc7\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u6cd5\u672f\u65e0\u6b63\u90aa\uff0c\u8fd8\u662f\u770b\u4eba\u600e\u4e48\u7528\uff0c\u4e0d\u7ba1\u5b83\u6700\u521d\u7684\u7528\u9014\u662f\u4ec0\u4e48\uff0c\u73b0\u5728\u80fd\u7528\u6765\u62b5\u5fa1\u9b54\u4ec6\u5c31\u884c\u3002</p><p>\u7136\u800c\u7ef4\u82e5\u62c9\u7684\u4fee\u590d\u5de5\u4f5c\u8fdb\u884c\u5f97\u5e76\u4e0d\u987a\u5229\u3002\u743c\u6069\u4f4e\u4f30\u4e86\u8fd9\u4e2a\u6cd5\u9635\u7684\u6b8b\u7834\u7a0b\u5ea6\uff0c\u4ed6\u4ee5\u4e3a\u6240\u6709\u7684\u4e3b\u8981\u8282\u70b9\u90fd\u4fdd\u5b58\u5b8c\u6574\uff0c\u4f46\u7ef4\u82e5\u62c9\u5b9e\u5730\u67e5\u770b\u4e4b\u540e\uff0c\u624d\u53d1\u73b0\u5176\u4e2d\u6709\u4e24\u4e2a\u8868\u9762\u6ca1\u95ee\u9898\uff0c\u5b9e\u9645\u5df2\u7ecf\u88ab\u7834\u574f\u4e86\u3002\u5982\u6b64\u4e00\u6765\uff0c\u539f\u672c\u9884\u8ba1\u7684\u4fee\u590d\u65f6\u95f4\u5927\u5e45\u5ea6\u5ef6\u957f\uff0c\u76f4\u5230\u9b54\u4ec6\u7684\u8eab\u5f71\u5df2\u7ecf\u51fa\u73b0\u5728\u8857\u9053\u7684\u5c3d\u5934\uff0c\u7ef4\u82e5\u62c9\u624d\u582a\u582a\u5b8c\u6210\u6cd5\u9635\uff0c\u5c06\u5b83\u6fc0\u6d3b\u3002</p><p>\u201c\u6709\u4e24\u4e2a\u8282\u70b9\u574f\u4e86\uff0c\u8981\u4fee\u590d\u81f3\u5c11\u8981\u4e00\u5929\uff0c\u6211\u7d22\u6027\u5c06\u5b83\u4eec\u653e\u5f03\u6389\uff0c\u8c03\u6574\u4e86\u6cd5\u9635\u7ed3\u6784\uff0c\u867d\u7136\u7b3c\u7f69\u8303\u56f4\u5c0f\u4e86\u5f88\u591a\uff0c\u4f46\u5e7b\u672f\u6548\u679c\u57fa\u672c\u8fd8\u80fd\u7ef4\u6301\u3002\u201d\u5b8c\u6210\u6cd5\u9635\uff0c\u7ef4\u82e5\u62c9\u5927\u5927\u677e\u4e86\u53e3\u6c14\uff0c\u5bf9\u51db\u8bf4\u3002</p><p>\u201c\u5854\u59d0\u59d0\u771f\u5389\u5bb3\uff01\u201d\u51db\u7531\u8877\u5730\u5938\u5956\u3002</p><p>\u5e7b\u672f\u751f\u6548\uff0c\u5175\u8425\u5185\u7684\u4eba\u5e76\u4e0d\u53d7\u5f71\u54cd\uff0c\u4f46\u5728\u5175\u8425\u5916\u7684\u4eba\u773c\u91cc\u770b\u6765\uff0c\u8fd9\u91cc\u5c31\u662f\u4e00\u5ea7\u8302\u76db\u7684\u68ee\u6797\uff0c\u53c2\u5929\u5927\u6811\u906e\u853d\u4e86\u6708\u5149\uff0c\u9ed1\u6697\u4e2d\u4e0d\u65ad\u4f20\u6765\u731b\u517d\u7684\u543c\u53eb\u58f0\uff0c\u63d0\u9192\u8fd9\u91cc\u9762\u7684\u5371\u9669\u3002\u7ef4\u82e5\u62c9\u548c\u51db\u5c31\u7ad9\u5728\u5175\u8425\u95e8\u53e3\uff0c\u7136\u800c\u9646\u7eed\u8d76\u6765\u7684\u9b54\u4ec6\u5374\u5b8c\u5168\u89c6\u800c\u4e0d\u89c1\uff0c\u5b83\u4eec\u72b9\u8c6b\u4e86\u4e00\u4f1a\uff0c\u5f00\u59cb\u5206\u6563\u5f00\u6765\uff0c\u56db\u5904\u641c\u5bfb\u3002</p><p>\u4e00\u4e2a\u9b54\u4ec6\u8d70\u5230\u5175\u8425\u7684\u95e8\u524d\uff0c\u7591\u60d1\u5730\u4f38\u624b\u6478\u4e86\u6478\u5899\u58c1\u2014\u2014\u5728\u5b83\u81ea\u5df1\u7684\u773c\u4e2d\uff0c\u90a3\u662f\u4e00\u68f5\u6811\u3002\u5b83\u7684\u638c\u5fc3\u4f20\u6765\u6811\u76ae\u7684\u7c97\u7cd9\u624b\u611f\uff0c\u8fd9\u8ba9\u5b83\u653e\u5f03\u4e86\u7ee7\u7eed\u63a2\u5bfb\u7684\u60f3\u6cd5\u3002\u201c\u771f\u5947\u602a\u3002\u201d\u5b83\u561f\u54dd\u4e86\u4e00\u53e5\uff0c\u671d\u65c1\u8fb9\u8d70\u53bb\u3002</p><p>\u51db\u5f88\u60f3\u7ed9\u5b83\u6765\u4e00\u53d1\u706b\u7403\uff0c\u4f46\u53c8\u6015\u66b4\u9732\uff0c\u6b63\u5f53\u5979\u7ea0\u7ed3\u7684\u65f6\u5019\uff0c\u9b54\u4ec6\u62d6\u5728\u5730\u9762\u7684\u5f71\u5b50\u5ffd\u7136\u52a8\u8d77\u6765\uff0c\u5728\u6708\u5149\u4e0b\u626d\u66f2\u7740\uff0c\u4e00\u4e2a\u7a88\u7a95\u7684\u8eab\u5f71\u4ece\u4e2d\u8dc3\u51fa\uff0c\u4e00\u5251\u4ece\u9b54\u4ec6\u7684\u80cc\u540e\u523a\u8fdb\u53bb\uff0c\u7d27\u63a5\u7740\u957f\u97ad\u6a2a\u626b\uff0c\u7f20\u4f4f\u5b83\u7684\u8116\u9888\uff0c\u7528\u529b\u4e00\u62c9\uff0c\u53ea\u542c\u5f97\u5494\u64e6\u4e00\u58f0\uff0c\u4fbf\u5c06\u8111\u888b\u626f\u65ad\u4e0b\u6765\u3002</p><p>\u5979\u7684\u624b\u6cd5\u5e72\u8106\u5229\u843d\uff0c\u6574\u4e2a\u8fc7\u7a0b\u8fd8\u4e0d\u5230\u4e00\u79d2\u949f\uff0c\u5f53\u5176\u4ed6\u9b54\u4ec6\u51b2\u8fc7\u6765\u65f6\uff0c\u9b45\u9b54\u4e00\u4e2a\u7ffb\u6eda\uff0c\u53c8\u9690\u5165\u9ed1\u6697\u4e4b\u4e2d\u3002\u6d88\u5931\u4e4b\u524d\uff0c\u5979\u8fd8\u671d\u51db\u7728\u4e86\u7728\u773c\uff0c\u4eff\u4f5b\u662f\u5728\u6253\u62db\u547c\u3002</p><p>\u201c\u5979\u80fd\u770b\u7834\u5e7b\u672f\uff1f\u201d\u51db\u60ca\u8bb6\u3002</p><p>\u201c\u5e94\u8be5\u662f\u731c\u7684\u5427\u3002\u201d\u7ef4\u82e5\u62c9\u4e5f\u4e0d\u6562\u786e\u5b9a\uff0c\u5979\u548c\u838e\u73de\u514b\u4e5f\u4e0d\u719f\uff0c\u53ea\u77e5\u9053\u5979\u66fe\u7ecf\u662f\u94c1\u738b\u5ea7\u7684\u6740\u624b\uff0c\u6b7b\u540e\u8f6c\u5316\u6210\u9b45\u9b54\uff0c\u548c\u743c\u6069\u5173\u7cfb\u9887\u4e3a\u5bc6\u5207\u3002\u5728\u6df1\u6e0a\u7684\u6076\u9b54\u4f53\u7cfb\u4e2d\uff0c\u9b45\u9b54\u4f4d\u9636\u5e76\u4e0d\u9ad8\uff0c\u9664\u4e86\u9b45\u60d1\u4eba\u5fc3\u7684\u6280\u5de7\uff0c\u5355\u7eaf\u7684\u201c\u529b\u91cf\u201d\u5e94\u8be5\u662f\u4e0d\u5f3a\u7684\uff0c\u6ca1\u9053\u7406\u80fd\u770b\u7834\u6d77\u5e02\u8703\u697c\u8fd9\u79cd\u9ad8\u9636\u5e7b\u672f\u3002\u4f46\u6df1\u6e0a\u662f\u4e2a\u6df7\u4e71\u7684\u5730\u65b9\uff0c\u4ec0\u4e48\u4e1c\u897f\u90fd\u8bf4\u4e0d\u597d\uff0c\u4e0d\u8bb2\u89c4\u77e9\u7684\u4e8b\u60c5\u65f6\u6709\u53d1\u751f\uff0c\u6bd4\u5982\u9b45\u9b54\u5973\u738b\u7f8e\u574e\u4fee\u7279\uff0c\u867d\u7136\u662f\u4e2a\u9b45\u9b54\uff0c\u5374\u662f\u80fd\u591f\u548c\u683c\u62c9\u5179\u7279\u3001\u5965\u5580\u65af\u8fd9\u79cd\u5927\u9886\u4e3b\u8c08\u7b11\u98ce\u751f\u7684\u5b58\u5728\uff0c\u5374\u6bd4\u4ec0\u4e48\u708e\u9b54\u4e4b\u6d41\u4e0d\u77e5\u5f3a\u5230\u54ea\u91cc\u53bb\u4e86\u3002</p><p>\u838e\u73de\u514b\u7a7f\u68ad\u5728\u9ed1\u6697\u4e0e\u9634\u5f71\u4e4b\u95f4\uff0c\u730e\u6740\u7740\u4e00\u4e2a\u53c8\u4e00\u4e2a\u9b54\u4ec6\uff0c\u6bcf\u4e00\u6b21\u51fa\u73b0\u90fd\u5e26\u8d70\u4e00\u6761\u6027\u547d\uff0c\u968f\u540e\u5728\u95f4\u4e0d\u5bb9\u53d1\u95f4\u8eb2\u8fc7\u88ad\u51fb\u3002\u90aa\u9b54\u76ae\u7532\u5305\u88f9\u7740\u5979\u7684\u8eaf\u4f53\uff0c\u52fe\u52d2\u51fa\u7f8e\u4e3d\u7684\u66f2\u7ebf\uff0c\u5728\u6708\u5149\u4e0b\u770b\u8d77\u6765\u662f\u90a3\u6837\u7684\u77eb\u5065\uff0c\u5145\u6ee1\u4e86\u5371\u9669\u7684\u9b45\u529b\uff0c\u8fde\u51db\u90fd\u770b\u5f97\u6709\u4e9b\u5446\u4e86\u3002\u201c\u96be\u602a\u743c\u6069\u90a3\u4e48\u559c\u6b22\u5979\uff0c\u201d\u5c0f\u5973\u5deb\u5600\u5495\u7740\uff0c\u201c\u6362\u6211\u662f\u7537\u4eba\uff0c\u6211\u4e5f\u559c\u6b22\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u7ef4\u82e5\u62c9\u5df2\u7ecf\u4e0d\u77e5\u9053\u600e\u4e48\u8bc4\u4ef7\u8fd9\u4e2a\u5c0f\u5e08\u59b9\uff0c\u7d22\u6027\u4fdd\u6301\u6c89\u9ed8\uff0c\u5c06\u5168\u526f\u5fc3\u795e\u6295\u5165\u5230\u7ef4\u6301\u5e7b\u672f\u7684\u8fd0\u884c\u4e2d\u3002\u6d77\u5e02\u8703\u697c\u4e00\u65e6\u53d1\u52a8\uff0c\u5c31\u53ef\u4ee5\u72ec\u7acb\u8fd0\u884c\uff0c\u539f\u672c\u662f\u4e0d\u9700\u8981\u5deb\u5e08\u4e00\u76f4\u63a7\u5236\u7684\uff0c\u4f46\u73b0\u5728\u5175\u8425\u91cc\u8fd9\u4e2a\u662f\u4e34\u65f6\u6539\u9020\u8fc7\u7684\u6b8b\u7f3a\u7248\uff0c\u8fd0\u8f6c\u8d77\u6765\u96be\u514d\u6709\u70b9\u6545\u969c\uff0c\u5fc5\u987b\u8981\u5deb\u5e08\u4e0d\u65ad\u5730\u8c03\u6574\uff0c\u5426\u5219\u5c31\u5bb9\u6613\u51fa\u95ee\u9898\u3002</p><p>\u51db\u5728\u786e\u8ba4\u5e7b\u672f\u6709\u6548\uff0c\u9b54\u4ec6\u65e0\u6cd5\u8bc6\u7834\u540e\uff0c\u4fbf\u53bb\u627e\u5580\u6d41\u5976\u5976\u3002\u5982\u4eca\u5175\u8425\u91cc\u5df2\u7ecf\u805a\u96c6\u4e86\u4e03\u516b\u5343\u4eba\uff0c\u5168\u57ce\u5c45\u6c11\u4e00\u5927\u534a\u90fd\u9003\u5230\u4e86\u8fd9\u91cc\uff0c\u5269\u4e0b\u7684\u5c31\u4e0d\u77e5\u751f\u6b7b\u4e86\u3002\u5e78\u597d\u5175\u8425\u8db3\u591f\u5927\uff0c\u867d\u7136\u4e00\u4e0b\u5b50\u6d8c\u8fdb\u8fd9\u4e48\u591a\u4eba\uff0c\u8fd8\u80fd\u52c9\u5f3a\u5bb9\u7eb3\u5f97\u4e0b\uff0c\u5728\u536b\u5175\u7684\u534f\u52a9\u4e0b\uff0c\u4ed6\u4eec\u88ab\u5206\u6210\u4e94\u7ec4\uff0c\u5b89\u7f6e\u5728\u5175\u8425\u4e2d\u4e94\u4e2a\u64cd\u573a\u4e0a\uff0c\u4fbf\u4e8e\u7ba1\u7406\u3002\u7ef4\u82e5\u62c9\u521b\u9020\u51fa\u4e00\u4e9b\u5149\u7403\u6f02\u6d6e\u5728\u7a7a\u4e2d\uff0c\u4e00\u4e9b\u8001\u4eba\u81ea\u53d1\u62c5\u4efb\u4e86\u7ef4\u6301\u79e9\u5e8f\u7684\u5de5\u4f5c\uff0c\u5728\u4ed6\u4eec\u7684\u534f\u52a9\u4e0b\uff0c\u5c45\u6c11\u4eec\u7684\u60c5\u7eea\u57fa\u672c\u7a33\u5b9a\uff0c\u4e0d\u77e5\u5728\u8c01\u7684\u5e26\u52a8\u4e4b\u4e0b\uff0c\u5927\u5bb6\u90fd\u5f00\u59cb\u9ed8\u9ed8\u7948\u7977\u8d77\u6765\uff0c\u671f\u671b\u80fd\u591f\u5ea6\u8fc7\u8fd9\u573a\u7a81\u5982\u5176\u6765\u7684\u52ab\u96be\u3002</p><p>\u51db\u6765\u5230\u9760\u5175\u8425\u4e1c\u4fa7\u7684\u4e00\u4e2a\u64cd\u573a\uff0c\u8fd9\u91cc\u805a\u96c6\u4e86\u5c06\u8fd1\u4e00\u5343\u4eba\uff0c\u5979\u5728\u4eba\u7fa4\u4e2d\u627e\u5230\u5580\u6d41\u5976\u5976\uff0c\u8001\u4eba\u5bb6\u6700\u8fd1\u8fde\u906d\u53d8\u6545\uff0c\u9887\u53d7\u60ca\u5413\uff0c\u201c\u8fd9\u5230\u5e95\u662f\u600e\u4e48\u4e00\u56de\u4e8b\u554a\uff0c\u201d\u5580\u6d41\u5976\u5976\u95ee\uff0c\u201c\u57c3\u535c\u62c9\u600e\u4e48\u53d8\u6210\u4e86\u90a3\u526f\u602a\u6837\u5b50\uff1f\u201d</p><p>\u201c\u8c01\u77e5\u9053\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4ed6\u4e00\u5411\u559c\u6b22\u6298\u817e\u90a3\u4e9b\u83ab\u540d\u5176\u5999\u7684\u4e1c\u897f\u3002\u201d</p><p>\u5979\u548c\u5580\u6d41\u5976\u5976\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u7a0d\u7a0d\u5b89\u629a\u5979\u7684\u60c5\u7eea\uff0c\u6b63\u6253\u7b97\u79bb\u5f00\u53bb\u5e2e\u6885\u83f2\u65af\uff0c\u7ef4\u82e5\u62c9\u5ffd\u7136\u4f20\u6765\u8baf\u606f\uff0c\u201c\u6709\u51e0\u4e2a\u9b54\u4ec6\u95ef\u8fdb\u6765\u4e86\u3002\u201d</p><p>\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u201c\u6cd5\u9635\u51fa\u4e86\u70b9\u95ee\u9898\uff0c\u9732\u51fa\u4e00\u4e2a\u7834\u7efd\uff0c\u6070\u597d\u88ab\u5b83\u4eec\u649e\u4e0a\u4e86\u3002\u201d</p><p>\u201c\u5b83\u4eec\u5728\u54ea\u91cc\uff1f\u6709\u591a\u5c11\uff1f\u201d</p><p>\u201c\u6b63\u671d\u4f60\u90a3\u8fb9\u8fc7\u53bb\uff0c\u4e00\u961f\u4eba\uff0c\u4e0d\u8d85\u8fc7\u5341\u4e2a\uff0c\u5176\u4ed6\u4eba\u88ab\u6211\u5835\u5728\u4e86\u5916\u9762\uff0c\u4f60\u641e\u5b9a\u5b83\u4eec\u5c31\u884c\u3002\u201d</p><p>\u51db\u62c9\u7740\u5580\u6d41\u5976\u5976\u6b63\u51c6\u5907\u5f80\u91cc\u8d70\uff0c\u7136\u800c\u5979\u8fd8\u662f\u6162\u4e86\u4e00\u6b65\uff0c\u4e00\u4e2a\u9b54\u4ec6\u98de\u4e86\u8fc7\u6765\u3002\u201c\u627e\u5230\u4e86\uff01\u201d\u5b83\u5927\u558a\u7740\uff0c\u201c\u90fd\u5728\u8fd9\u91cc\uff01\u201d</p><p>\u614c\u4e71\u5728\u4eba\u7fa4\u4e2d\u8513\u5ef6\uff0c\u54ed\u558a\u548c\u5c16\u53eb\u58f0\u6b64\u8d77\u5f7c\u4f0f\u5730\u54cd\u8d77\uff0c\u8ba9\u51db\u542c\u5f97\u5fc3\u70e6\uff0c\u5979\u62ac\u624b\u4e00\u53d1\u706b\u7130\u7bad\u5c04\u5411\u9b54\u4ec6\uff0c\u51fb\u4e2d\u4e86\u5b83\u7684\u7fc5\u8180\uff0c\u5374\u6ca1\u6709\u9020\u6210\u591a\u5c11\u4f24\u5bb3\u3002\u9b54\u4ec6\u53d1\u51fa\u4e00\u58f0\u6124\u6012\u7684\u53eb\u5578\uff0c\u4f38\u51fa\u82cd\u9e70\u822c\u7684\u5229\u722a\uff0c\u671d\u7740\u51db\u4fef\u51b2\u4e0b\u6765\u3002</p><p>\u731b\u70c8\u7684\u98ce\u4ece\u51db\u7684\u811a\u4e0b\u5347\u8d77\uff0c\u5c06\u5468\u56f4\u7684\u4eba\u2014\u2014\u9664\u4e86\u5580\u6d41\u5976\u5976\u4e4b\u5916\u2014\u2014\u5168\u90e8\u5f80\u540e\u8fdc\u8fdc\u63a8\u5f00\uff0c\u9732\u51fa\u4e00\u4e2a\u5706\u5f62\u7a7a\u5730\u3002\u51db\u7ad9\u5728\u7a7a\u5730\u7684\u4e2d\u5fc3\uff0c\u4ef0\u9762\u671b\u7740\u6251\u4e0b\u6765\u7684\u9b54\u4ec6\uff0c\u5979\u7684\u7709\u5fc3\u5149\u534e\u95ea\u70c1\uff0c\u7136\u540e\u7528\u529b\u5439\u4e86\u53e3\u6c14\u3002</p><p>\u9b54\u4ec6\u4ece\u7a7a\u4e2d\u6389\u843d\u4e0b\u6765\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u51b0\u5757\u3002</p><p>\u6c89\u91cd\u800c\u6df7\u4e71\u7684\u811a\u6b65\u58f0\u54cd\u8d77\uff0c\u4e03\u516b\u4e2a\u9b54\u4ec6\u4ece\u8fdc\u5904\u7684\u9ed1\u6697\u4e2d\u51b2\u4e86\u8fc7\u6765\uff0c\u5c06\u51db\u548c\u5580\u6d41\u5976\u5976\u56e2\u56e2\u56f4\u4f4f\u3002\u5b83\u4eec\u867d\u7136\u5f62\u5982\u602a\u7269\uff0c\u4f46\u4ecd\u7136\u4fdd\u7559\u4e86\u4eba\u7c7b\u7684\u667a\u529b\uff0c\u6e05\u695a\u5730\u770b\u51fa\u8fd9\u4f4d\u7f8e\u4e3d\u7684\u9ed1\u53d1\u5c11\u5973\u4e0d\u597d\u60f9\uff0c\u5168\u8eab\u6563\u53d1\u7740\u5371\u9669\u65e0\u6bd4\u7684\u6c14\u606f\u3002\u53ea\u6562\u8fdc\u8fdc\u5305\u56f4\uff0c\u5374\u6ca1\u6709\u4e00\u4e2a\u4eba\u6562\u4e0a\u524d\u3002</p><p>\u4e00\u4e2a\u9996\u9886\u6a21\u6837\u7684\u9b54\u4ec6\u8d8a\u4f17\u800c\u51fa\uff0c\u5b83\u663e\u7136\u4e0e\u5176\u4ed6\u9b54\u4ec6\u4e0d\u540c\uff0c\u4ece\u5916\u5f62\u4e0a\u770b\u8d77\u6765\u5c31\u6709\u660e\u663e\u533a\u522b\uff0c\u4f53\u578b\u66f4\u5927\uff0c\u76d4\u7532\u66f4\u7cbe\u81f4\uff0c\u800c\u4e14\u8fd8\u62e5\u6709\u5750\u9a91\u2014\u2014\u9ed1\u8272\u7684\u3001\u773c\u7736\u91cc\u71c3\u70e7\u7740\u706b\u7403\u7684\u6218\u9a6c\u3002</p><p>\u201c\u6211\u4eec\u5355\u72ec\u8f83\u91cf\u4e00\u4e0b\uff0c\u5973\u5deb\u3002\u201d\u9996\u9886\u8bf4\uff0c\u4e3e\u8d77\u624b\u4e2d\u7684\u957f\u621f\u3002</p><p>\u51db\u5728\u5580\u6d41\u5976\u5976\u7684\u80a9\u4e0a\u8f7b\u8f7b\u62cd\u4e86\u4e24\u4e0b\uff0c\u793a\u610f\u5979\u7559\u5728\u539f\u5730\uff0c\u81ea\u5df1\u4e0a\u524d\u51e0\u6b65\u3002\u9b54\u4ec6\u9996\u9886\u76ef\u7740\u5979\u7684\u624b\u770b\u4e86\u4e00\u4f1a\uff0c\u53d1\u73b0\u5168\u65e0\u52a8\u4f5c\uff0c\u7a81\u7136\u5927\u559d\u4e00\u58f0\uff0c\u7b56\u9a6c\u671d\u7740\u51db\u76f4\u51b2\u8fc7\u6765\u3002</p><p>\u51db\u8f7b\u76c8\u5730\u6a2a\u8dc3\u4e24\u6b65\u907f\u8fc7\uff0c\u6b63\u8981\u8fd8\u51fb\uff0c\u5374\u53d1\u73b0\u9b54\u4ec6\u9996\u9886\u8d8a\u8fc7\u5979\u8eab\u65c1\uff0c\u76f4\u5954\u5580\u6d41\u5976\u5976\u800c\u53bb\uff0c\u4ece\u4e00\u5f00\u59cb\u5b83\u7684\u76ee\u6807\u5c31\u4e0d\u662f\u51db\uff0c\u6240\u8c13\u201c\u5355\u72ec\u8f83\u91cf\u201d\u53ea\u662f\u5728\u6b3a\u8bc8\u3002\u5728\u9996\u9886\u53d1\u52a8\u7684\u540c\u65f6\uff0c\u53e6\u5916\u4e24\u4e2a\u957f\u7740\u7fc5\u8180\u7684\u9b54\u4ec6\u4e5f\u4ece\u9ad8\u7a7a\u4e2d\u4fef\u51b2\u4e0b\u6765\uff0c\u76ee\u6807\u540c\u6837\u662f\u8001\u5987\u4eba\u3002</p><p>\u201c\u523a\u5566\uff01\u201d</p><p>\u9b54\u4ec6\u9996\u9886\u548c\u53e6\u5916\u4e24\u4eba\u51e0\u4e4e\u662f\u540c\u65f6\u903c\u8fd1\u5230\u8001\u5987\u4eba\u8eab\u65c1\uff0c\u7136\u800c\u8fd8\u6ca1\u7b49\u5b83\u4eec\u78b0\u89e6\u5230\u76ee\u6807\u7684\u8863\u670d\uff0c\u523a\u8033\u7684\u58f0\u97f3\u4f34\u968f\u7740\u7126\u81ed\u5473\u5347\u8d77\uff0c\u8001\u5987\u4eba\u7684\u8eab\u4f53\u8868\u9762\u9661\u7136\u7206\u51fa\u4e00\u5c42\u95ea\u7535\u548c\u70c8\u7130\u4ea4\u7ec7\u7684\u9632\u62a4\u7f51\uff0c\u5c06\u4e24\u540d\u957f\u7fc5\u8180\u7684\u9b54\u4ec6\u53d8\u6210\u4e86\u7070\u70ec\u3002\u9996\u9886\u53cd\u5e94\u76f8\u5bf9\u5feb\u4e00\u70b9\uff0c\u5728\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\u52d2\u9a6c\u540e\u9000\uff0c\u8eb2\u8fc7\u4e86\u81f4\u547d\u7684\u9677\u9631\u3002</p><p>\u201c\u867d\u7136\u743c\u6069\u603b\u662f\u8bf4\u80f8\u592a\u5927\u7684\u5973\u5b69\u5b50\u4e0d\u806a\u660e\uff0c\u4f46\u6211\u53ef\u662f\u4e2a\u7279\u4f8b\u3002\u201d\u51db\u7684\u58f0\u97f3\u4ece\u80cc\u540e\u4f20\u6765\uff0c\u60ca\u7591\u4e0d\u5b9a\u7684\u9b54\u4ec6\u9996\u9886\u8f6c\u8fc7\u8eab\uff0c\u770b\u89c1\u5c11\u5973\u6b63\u7f13\u6b65\u903c\u8fd1\uff0c\u70c8\u7130\u4ece\u5979\u7684\u5934\u53d1\u3001\u8863\u88d9\u4e2d\u817e\u8d77\uff0c\u5979\u7684\u6a21\u6837\u4f3c\u4e4e\u4e5f\u5728\u706b\u7130\u4e2d\u53d1\u751f\u53d8\u5316\uff0c\u6e56\u7eff\u8272\u7684\u773c\u775b\u53d8\u6210\u4e86\u91d1\u8272\u7ad6\u77b3\uff0c\u90a3\u662f\u86c7\u7c7b\u7684\u773c\u775b\uff0c\u6216\u8005\u662f\u2026\u2026\u9f99\uff01</p><p>\u5b83\u60f3\u8981\u9003\u8dd1\uff0c\u4f46\u50f5\u786c\u7684\u8eab\u4f53\u5b8c\u5168\u4e0d\u542c\u4f7f\u5524\uff0c\u6218\u9a6c\u66f4\u662f\u5df2\u7ecf\u88ab\u9f99\u5a01\u9707\u6151\u5f97\u5c48\u819d\u8dea\u5730\uff0c\u534a\u70b9\u4e0d\u6562\u52a8\u5f39\u3002\u51db\u7684\u53e3\u4e2d\u53d1\u51fa\u6e05\u8106\u7684\u9f99\u541f\uff0c\u5728\u573a\u6240\u6709\u7684\u9b54\u4ec6\uff0c\u5305\u62ec\u9996\u9886\u5728\u5185\uff0c\u5b83\u4eec\u7684\u5934\u9885\u4e00\u4e2a\u63a5\u4e00\u4e2a\u5730\u7206\u70b8\u5f00\u6765\uff0c\u8eab\u4f53\u81ea\u71c3\u6210\u70ec\u3002</p><p>\u654c\u4eba\u88ab\u6d88\u706d\uff0c\u521a\u624d\u56db\u6563\u9003\u5f00\u7684\u5c45\u6c11\u4eec\u6e10\u6e10\u56f4\u62e2\u8fc7\u6765\uff0c\u4f46\u53c8\u4e0d\u6562\u9760\u5f97\u592a\u8fd1\uff0c\u4ed6\u4eec\u4ee5\u656c\u754f\u7684\u773c\u795e\u770b\u7740\u51db\uff0c\u770b\u7740\u5979\u7684\u91d1\u8272\u7ad6\u77b3\uff0c\u770b\u7740\u5979\u5168\u8eab\u70c8\u7130\u718a\u718a\u3002\u51db\u671d\u4ed6\u4eec\u5fae\u5fae\u70b9\u5934\uff0c\u5979\u770b\u89c1\u4e86\u51e0\u5f20\u8ba4\u8bc6\u7684\u9762\u5b54\uff0c\u90fd\u662f\u4ee5\u524d\u8f9b\u5df4\u57ce\u65e7\u57ce\u533a\u7684\u8857\u574a\u90bb\u5c45\u3002\u5580\u6d41\u5976\u5976\u4ecd\u7136\u7ad9\u5728\u539f\u5730\uff0c\u5979\u88ab\u521a\u624d\u8fd9\u4e00\u8fde\u4e32\u53d8\u6545\u5413\u5446\u4e86\uff0c\u751a\u81f3\u8fde\u52a8\u90fd\u4e0d\u6562\u52a8\uff0c\u751f\u6015\u8eab\u4e0a\u7684\u706b\u7130\u95ea\u7535\u4f1a\u4f24\u5bb3\u5230\u81ea\u5df1\u3002\u51db\u62db\u4e86\u62db\u624b\uff0c\u5c06\u6cd5\u672f\u6536\u4e86\u56de\u6765\u3002</p><p>\u5979\u817e\u7a7a\u800c\u8d77\uff0c\u671b\u5411\u8fdc\u5904\uff0c\u866b\u9b54\u5de8\u5927\u7684\u8eab\u5f71\u5728\u9ed1\u6697\u4e2d\u82e5\u9690\u82e5\u73b0\uff0c\u4e00\u70b9\u94f6\u5149\u5728\u5b83\u5468\u56f4\u5feb\u901f\u8dc3\u52a8\uff0c\u5212\u51fa\u4eff\u4f5b\u5f57\u661f\u822c\u7684\u957f\u5c3e\uff0c\u90a3\u662f\u6885\u83f2\u65af\u3002\u5979\u6362\u4e86\u4e2a\u65b9\u5411\uff0c\u770b\u89c1\u9ed1\u538b\u538b\u7684\u9b54\u4ec6\u5df2\u7ecf\u51b2\u4e0a\u4e86\u9752\u94dc\u5821\u5792\u7684\u57ce\u5899\uff0c\u9ab7\u9ac5\u6b66\u58eb\u6b63\u5728\u594b\u529b\u62b5\u6321\uff0c\u770b\u8d77\u6765\u9632\u7ebf\u5c8c\u5c8c\u53ef\u5371\u3002</p><p>\u51db\u6df1\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u5168\u8eab\u70c8\u7130\u8f70\u7136\u7efd\u653e\uff0c\u5316\u4f5c\u4e00\u53ea\u9cde\u7532\u95ea\u95ea\u53d1\u5149\u7684\u7ea2\u9f99\uff0c\u671d\u7740\u9752\u94dc\u5821\u5792\u65b9\u5411\u6251\u53bb\u3002</p><p>\u201c\u7ea2\u9f99\uff01\u5c0f\u51db\u662f\u7ea2\u9f99\u5723\u5973\uff01\u201d</p><p>\u770b\u7740\u5de8\u5927\u7684\u7ea2\u9f99\u8eaf\u4f53\u5728\u81ea\u5df1\u773c\u524d\u817e\u7a7a\u800c\u8d77\uff0c\u5580\u6d41\u5976\u5976\u7ec8\u4e8e\u4ece\u9707\u60ca\u4e2d\u6e05\u9192\u8fc7\u6765\uff0c\u8fd9\u4e9b\u65e5\u5b50\u4ee5\u6765\u4e00\u76f4\u9690\u85cf\u5728\u5fc3\u5e95\u6df1\u5904\u7684\u6000\u7591\uff0c\u9661\u7136\u95f4\u53d8\u6210\u6d3b\u751f\u751f\u7684\u73b0\u5b9e\uff0c\u8ba9\u5979\u4e0b\u610f\u8bc6\u5730\u9ad8\u558a\u51fa\u58f0\u3002\u6240\u6709\u4eba\u5148\u662f\u60ca\u6115\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u7ee7\u800c\u53cd\u5e94\u8fc7\u6765\u3002</p><p>\u7f8e\u4e3d\u3001\u5f3a\u5927\u7684\u9ed1\u53d1\u5c11\u5973\uff0c\u7528\u70c8\u7130\u711a\u70e7\u4e86\u90aa\u6076\u602a\u7269\uff0c\u62ef\u6551\u4e86\u4ed6\u4eec\u6240\u6709\u4eba\u7684\u6027\u547d\uff0c\u5728\u4ed6\u4eec\u9762\u524d\u5316\u4f5c\u6c14\u52bf\u5a01\u731b\u7684\u7ea2\u9f99\uff0c\u7ff1\u7fd4\u5929\u9645\u2014\u2014\u773c\u524d\u53d1\u751f\u7684\u8fd9\u4e00\u5207\uff0c\u4e0e\u90a3\u4e9b\u53e4\u8001\u7684\u4f20\u8bf4\u3001\u6b4c\u8c23\u91cd\u5408\u8d77\u6765\uff0c\u8ba9\u6240\u6709\u4eba\u90fd\u5728\u5239\u90a3\u4e4b\u95f4\u76f8\u4fe1\uff0c\u81ea\u5df1\u4eb2\u773c\u770b\u89c1\u4e86\u771f\u795e\u7684\u964d\u4e34\u3002</p><p>\u5979\u5c31\u662f\u9884\u8a00\u4e2d\u7684\u7ea2\u9f99\u5723\u5973\u3002</p><p>\u203b\u203b\u203b</p><p>\u8eb2\u8fc7\u4e86\u866b\u9b54\u63a5\u8fde\u4e24\u8bb0\u65a9\u51fb\uff0c\u6885\u83f2\u65af\u8ba9\u72ec\u89d2\u517d\u7ed5\u5230\u4fa7\u9762\uff0c\u4ed4\u7ec6\u89c2\u5bdf\u7740\u8fd9\u4e2a\u5e9e\u5927\u65e0\u6bd4\u7684\u602a\u7269\uff0c\u8bd5\u56fe\u627e\u51fa\u5f31\u70b9\u6240\u5728\u3002</p><p>\u201c\u5168\u8eab\u90fd\u6709\u7532\u58f3\u8986\u76d6\uff0c\u8fde\u5173\u8282\u5904\u90fd\u6ca1\u6709\u7834\u7efd\uff0c\u9762\u90e8\u5e94\u8be5\u662f\u8584\u5f31\u90e8\u4f4d\uff0c\u4f46\u4fdd\u62a4\u5f97\u5f88\u4e25\u5bc6\uff0c\u65e0\u6cd5\u76f4\u63a5\u6253\u4e2d\uff0c\u201d\u5c11\u5973\u5fae\u5fae\u5598\u606f\u7740\uff0c\u5728\u5fc3\u4e2d\u76d8\u7b97\uff0c\u201c\u6216\u8bb8\u53ef\u4ee5\u8003\u8651\u7ed5\u5230\u80cc\u540e\u8bd5\u8bd5\u3002\u201d</p><p>\u5979\u8d1f\u8d23\u7f20\u4f4f\u866b\u9b54\uff0c\u5c3d\u91cf\u963b\u6b62\u5b83\u5bf9\u5723\u6e0a\u57ce\u9020\u6210\u66f4\u5927\u7684\u7834\u574f\u3002\u5230\u76ee\u524d\u4e3a\u6b62\uff0c\u8fd9\u4e2a\u4efb\u52a1\u5b8c\u6210\u5f97\u8fd8\u4e0d\u9519\uff0c\u866b\u9b54\u4e00\u76f4\u5728\u57ce\u5899\u9644\u8fd1\u4e0e\u5979\u7ea0\u7f20\uff0c\u6ca1\u6709\u7ee7\u7eed\u8e0f\u5165\u4e2d\u5fc3\u57ce\u533a\u3002\u5c31\u672c\u610f\u800c\u8a00\uff0c\u6885\u83f2\u65af\u5f53\u7136\u5e0c\u671b\u4e00\u52b3\u6c38\u9038\uff0c\u76f4\u63a5\u628a\u866b\u9b54\u5e72\u6389\uff0c\u4f46\u5c1d\u8bd5\u51e0\u6b21\u4e4b\u540e\uff0c\u5979\u53d1\u73b0\u8fd9\u8fd8\u771f\u4e0d\u5bb9\u6613\u3002</p><p>\u9664\u4e86\u9762\u90e8\u3001\u7fc5\u8180\u548c\u5934\u9876\u7684\u89e6\u89d2\u4e4b\u5916\uff0c\u866b\u9b54\u7684\u8eab\u8eaf\u88ab\u7532\u58f3\u5305\u88f9\u5f97\u4e25\u4e25\u5b9e\u5b9e\uff0c\u5bc6\u4e0d\u900f\u98ce\u3002\u6885\u83f2\u65af\u5c1d\u8bd5\u653b\u51fb\uff0c\u4f46\u5979\u7684\u7834\u90aa\u65a9\u6253\u5728\u866b\u9b54\u8eab\u4e0a\uff0c\u5b8c\u5168\u8d77\u4e0d\u5230\u5e94\u6709\u7684\u6548\u679c\u3002\u80cc\u90e8\u5e94\u8be5\u662f\u76f8\u5bf9\u8584\u5f31\u7684\u5730\u65b9\uff0c\u4f46\u866b\u9b54\u867d\u7136\u4f53\u578b\u5e9e\u5927\uff0c\u52a8\u4f5c\u5374\u975e\u5e38\u7075\u6d3b\uff0c\u5b8c\u5168\u4e0d\u7ed9\u6885\u83f2\u65af\u53ef\u4e58\u4e4b\u673a\u3002\u53d7\u9650\u4e8e\u6740\u622e\u795e\u529b\u7684\u5236\u7ea6\uff0c\u6885\u83f2\u65af\u5e76\u4e0d\u80fd\u53d1\u6325\u51fa\u5723\u6b66\u58eb\u7684\u5168\u90e8\u529b\u91cf\uff0c\u800c\u9274\u4e8e\u4e4b\u524d\u7684\u6559\u8bad\uff0c\u5979\u53c8\u4e0d\u6562\u8f7b\u6613\u52a8\u7528\u201c\u51a5\u6b65\u201d\uff0c\u4e00\u65f6\u95f4\u62ff\u866b\u9b54\u6ca1\u4ec0\u4e48\u592a\u597d\u7684\u529e\u6cd5\u3002</p><p>\u5bf9\u4e8e\u866b\u9b54\u6765\u8bf4\uff0c\u60c5\u5f62\u5176\u5b9e\u4e5f\u5dee\u4e0d\u591a\u3002\u5b83\u80fd\u55b7\u6d82\u95ea\u7535\u548c\u9178\u6db2\uff0c\u5374\u55b7\u4e0d\u4e2d\u76ee\u6807\uff0c\u5b83\u7684\u5203\u8db3\u950b\u9510\u65e0\u6bd4\uff0c\u5374\u65a9\u4e0d\u5230\u4eba\uff0c\u5b83\u60f3\u8981\u4f7f\u7528\u6cd5\u672f\uff0c\u5374\u5c61\u5c61\u88ab\u6253\u65ad\uff0c\u5373\u4fbf\u6210\u529f\u4e00\u4e24\u6b21\uff0c\u6253\u5728\u90a3\u4f4d\u91d1\u53d1\u94f6\u7532\u7684\u5c11\u5973\u8eab\u4e0a\uff0c\u5168\u7136\u6ca1\u6709\u534a\u70b9\u4f5c\u7528\u3002</p><p>\u53cc\u65b9\u6b63\u81ea\u50f5\u6301\uff0c\u4e00\u4e2a\u9f99\u9996\u4eba\u8eab\uff0c\u5168\u8eab\u95ea\u95ea\u53d1\u5149\u7684\u5de8\u4eba\u51b2\u8fc7\u6765\uff0c\u671d\u7740\u866b\u9b54\u8fce\u9762\u6251\u8fc7\u53bb\u3002</p><p>\u6885\u83f2\u65af\u88ab\u8fd9\u7a81\u5982\u5176\u6765\u7684\u63f4\u519b\u7ed9\u5f04\u7cca\u6d82\u4e86\uff0c\u5979\u8c28\u614e\u5730\u9000\u5f00\u4e00\u6bb5\u8ddd\u79bb\uff0c\u770b\u7740\u4e24\u53ea\u5de8\u5927\u7684\u602a\u7269\u626d\u6253\u5728\u4e00\u8d77\u3002\u866b\u9b54\u540c\u6837\u88ab\u5b8c\u5168\u6253\u61f5\u4e86\uff0c\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u5288\u5934\u76d6\u8138\u5c31\u88ab\u63cd\u4e86\u4e00\u987f\u3002</p><p>\u7a7a\u6c14\u6ce2\u52a8\uff0c\u743c\u6069\u548c\u6062\u590d\u4eba\u5f62\u7684\u51db\u540c\u65f6\u51fa\u73b0\u5728\u6885\u83f2\u65af\u8eab\u4fa7\uff0c\u4ed6\u4eec\u60ca\u8bb6\u5730\u770b\u7740\u6b63\u5728\u548c\u866b\u9b54\u53ae\u6253\u5728\u4e00\u8d77\u7684\u9f99\u4eba\uff0c\u201c\u8fd9\u662f\u8c01\u554a\uff1f\u201d\u51db\u95ee\u3002</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u6885\u83f2\u65af\u6447\u6447\u5934\uff0c\u201c\u4f60\u4eec\u90a3\u8fb9\u5982\u4f55\uff1f\u201d</p><p>\u201c\u90fd\u89e3\u51b3\u4e86\u3002\u201d</p><p>\u743c\u6069\u5728\u9752\u94dc\u5821\u5792\u91cc\u4f5c\u4e3a\u8bf1\u9975\u5438\u5f15\u9b54\u4ec6\uff0c\u539f\u672c\u5df2\u7ecf\u6709\u4e9b\u62b5\u6321\u4e0d\u4f4f\uff0c\u51db\u7684\u52a0\u5165\u8ba9\u6218\u5c40\u5b8c\u5168\u9006\u8f6c\u3002\u5168\u8eab\u71c3\u70e7\u7740\u70c8\u7130\u7684\u7ea2\u9f99\u5927\u53d1\u795e\u5a01\uff0c\u8f6c\u773c\u95f4\u4fbf\u5c06\u9b54\u4ec6\u5927\u519b\u5f7b\u5e95\u51fb\u6e83\u3002\u743c\u6069\u5f97\u4ee5\u8131\u8eab\u51fa\u6765\uff0c\u548c\u51db\u4e00\u8d77\u6765\u652f\u63f4\u6885\u83f2\u65af\uff0c\u7ed3\u679c\u4e00\u6765\u5c31\u770b\u89c1\u8fd9\u5e45\u573a\u666f\uff0c\u5012\u662f\u5927\u5927\u51fa\u4e4e\u610f\u6599\u3002</p><p>\u7532\u58f3\u80fd\u591f\u6321\u4f4f\u6885\u83f2\u65af\u7684\u7834\u90aa\u65a9\uff0c\u5374\u6321\u4e0d\u4f4f\u9f99\u4eba\u7684\u62f3\u5934\u3002\u800c\u4e14\u8fd9\u53ea\u9f99\u4eba\u4eff\u4f5b\u53d1\u4e86\u75af\u4e00\u6837\uff0c\u6a2a\u51b2\u731b\u6253\uff0c\u5168\u7136\u4e0d\u987e\u81ea\u8eab\u9632\u62a4\uff0c\u5b8c\u5168\u662f\u4e00\u526f\u540c\u5f52\u4e8e\u5c3d\u7684\u67b6\u52bf\u3002\u866b\u9b54\u62fc\u547d\u53cd\u51fb\uff0c\u5203\u8db3\u4e71\u6325\uff0c\u8fde\u780d\u4e2d\u5b83\u51e0\u5200\uff0c\u780d\u5f97\u9f99\u4eba\u8eab\u4e0a\u5149\u8292\u4e71\u6e85\uff0c\u8e09\u8dc4\u540e\u9000\uff0c\u7acb\u8db3\u4e0d\u7a33\uff0c\u6700\u540e\u5ffd\u7136\u4e00\u8dc3\u800c\u8d77\uff0c\u4e24\u53ea\u5203\u8db3\u540c\u65f6\u6325\u51fa\uff0c\u671d\u7740\u9f99\u4eba\u5f53\u5934\u5288\u4e0b\u3002</p><p>\u773c\u770b\u9f99\u4eba\u5c31\u8981\u88ab\u5288\u4e2d\uff0c\u4e00\u9053\u78a7\u7eff\u83b9\u83b9\u7684\u5149\u7ebf\u5c04\u8fc7\u6765\uff0c\u51fb\u4e2d\u866b\u9b54\u7684\u5de6\u81c2\uff0c\u5c06\u5de8\u5927\u7684\u5203\u8db3\u77ac\u95f4\u5316\u4f5c\u98de\u7070\u3002</p><p>\u4e00\u51fb\u5f97\u624b\uff0c\u743c\u6069\u4e5f\u6709\u4e9b\u60ca\u8bb6\uff0c\u8fd9\u866b\u9b54\u7684\u7532\u58f3\u6781\u4e3a\u575a\u56fa\uff0c\u8fde\u6885\u83f2\u65af\u7684\u7834\u90aa\u65a9\u90fd\u5948\u4f55\u4e0d\u5f97\uff0c\u5374\u5728\u4ed6\u7684\u89e3\u79bb\u5c04\u7ebf\u4e0b\u6beb\u65e0\u534a\u70b9\u62b5\u6297\u529b\u3002\u866b\u9b54\u81ea\u5df1\u4f3c\u4e4e\u90fd\u6ca1\u6709\u60f3\u5230\u8fd9\u4e2a\u7ed3\u679c\uff0c\u5b83\u6014\u4e86\u4e00\u4e0b\uff0c\u5ffd\u7136\u5f20\u5f00\u5634\uff0c\u53d1\u51fa\u4e00\u58f0\u5c16\u9510\u7684\u5578\u53eb\u58f0\u3002</p><p>\u743c\u6069\u53cd\u5e94\u8fc5\u901f\uff0c\u4e00\u89c1\u866b\u9b54\u52a8\u4f5c\uff0c\u7acb\u523b\u53d1\u51fa\u4e00\u9053\u9759\u97f3\u6cd5\u672f\uff0c\u5c06\u81ea\u5df1\u4e0e\u6885\u83f2\u65af\u3001\u51db\u4e09\u4eba\u90fd\u7b3c\u7f69\u5728\u5185\u3002\u866b\u9b54\u7684\u5c16\u5578\u6ca1\u6709\u88ab\u5b8c\u5168\u9694\u7edd\uff0c\u4f46\u5a01\u529b\u5df2\u7ecf\u524a\u5f31\u4e86\u5f88\u591a\uff0c\u53ea\u8ba9\u4ed6\u4eec\u7a0d\u7a0d\u6655\u7729\u4e86\u4e00\u4e24\u79d2\u949f\u3002\u9f99\u4eba\u5374\u6ca1\u6709\u8fd9\u79cd\u5f85\u9047\uff0c\u53c8\u662f\u9996\u5f53\u5176\u51b2\uff0c\u987f\u65f6\u88ab\u9707\u5f97\u7ffb\u8eab\u6454\u5012\u3002\u866b\u9b54\u8d81\u6b64\u673a\u4f1a\uff0c\u671d\u7740\u873f\u8712\u6cb3\u7684\u65b9\u5411\u8dd1\u53bb\u3002</p><p>\u201c\u5b83\u8981\u9003\uff01\u201d</p><p>\u743c\u6069\u518d\u5ea6\u53d1\u51fa\u4e00\u8bb0\u89e3\u79bb\uff0c\u4f46\u866b\u9b54\u6b65\u4f10\u6781\u5927\uff0c\u5df2\u7ecf\u8dd1\u51fa\u5f88\u8fdc\uff0c\u6cd5\u672f\u867d\u7136\u547d\u4e2d\uff0c\u5a01\u529b\u5374\u5927\u5927\u524a\u5f31\uff0c\u53ea\u70e7\u53bb\u5b83\u7684\u4e00\u5757\u76ae\u8089\u3002\u4ed6\u6b63\u5f85\u653e\u5f03\uff0c\u5ffd\u7136\u77a5\u89c1\u8fdc\u5904\u6cb3\u8fb9\u4e00\u4e2a\u719f\u6089\u7684\u5c11\u5973\u8eab\u5f71\uff0c\u987f\u65f6\u5927\u5403\u4e00\u60ca\uff0c\u4e0d\u5047\u601d\u7d22\u5730\u53d1\u52a8\u6700\u540e\u4e00\u4e2a\u4f20\u9001\uff0c\u77ac\u79fb\u5230\u524d\u65b9\u534a\u7a7a\u4e2d\uff0c\u7d2b\u6c34\u6676\u6cd5\u6756\u81ea\u8896\u4e2d\u6ed1\u51fa\uff0c\u63e1\u5728\u624b\u4e2d\uff0c\u516d\u9897\u7eef\u7ea2\u4e4b\u6cea\u5b9d\u77f3\u540c\u65f6\u6cdb\u5149\uff0c\u671d\u5730\u9762\u91ca\u653e\u4e86\u4e00\u4e2a\u6cd5\u672f\u3002</p><p>\u201c\u6ce2\uff01\u201d</p><p>\u866b\u9b54\u6b63\u5728\u5954\u8dd1\uff0c\u5ffd\u89c9\u811a\u4e0b\u7a81\u5730\u4e00\u9677\uff0c\u6574\u4e2a\u4eba\u5f80\u524d\u8dcc\u5012\uff0c\u539f\u672c\u575a\u5b9e\u7684\u5730\u9762\u4e0d\u77e5\u4f55\u65f6\u53d8\u6210\u4e86\u6ce5\u6cbc\uff0c\u866b\u9b54\u4e0d\u66fe\u9632\u5907\uff0c\u9669\u4e9b\u4fbf\u6454\u4e86\u4e2a\u8ddf\u5934\uff0c\u5b83\u4e00\u4e2a\u8e09\u8dc4\u7ad9\u7a33\uff0c\u6b63\u8981\u722c\u51fa\uff0c\u5c31\u542c\u89c1\u80cc\u540e\u4e00\u58f0\u5927\u543c\uff0c\u9f99\u4eba\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u8d76\u4e86\u4e0a\u6765\uff0c\u91cd\u91cd\u5730\u649e\u5728\u5b83\u8eab\u4e0a\uff0c\u8ba9\u5b83\u53c8\u6454\u56de\u6ce5\u6f6d\u91cc\u3002</p><p>\u201c\u6eda\u5f00\uff01\u201d</p><p>\u866b\u9b54\u53d1\u51fa\u5c16\u9510\u7684\u5636\u53eb\uff0c\u5b83\u4e0d\u60f3\u548c\u9f99\u4eba\u7ea0\u7f20\uff0c\u7136\u800c\u9f99\u4eba\u5374\u4e0d\u80af\u653e\u8fc7\u5b83\u3002\u4e24\u53ea\u5de8\u5927\u7684\u534a\u4eba\u534a\u517d\u602a\u7269\u5728\u6ce5\u6f6d\u91cc\u626d\u6253\u8d77\u6765\uff0c\u8bba\u4e2a\u5934\uff0c\u8bba\u529b\u6c14\uff0c\u866b\u9b54\u660e\u663e\u8981\u80dc\u8fc7\u4e00\u7b79\uff0c\u800c\u4e14\u5b83\u7684\u653b\u51fb\u624b\u6bb5\u4e5f\u66f4\u591a\u6837\u5316\uff0c\u4f46\u5c11\u4e86\u4e00\u53ea\u5203\u8db3\uff0c\u6253\u8d77\u6765\u96be\u514d\u5403\u4e8f\uff1b\u9f99\u4eba\u53ea\u6709\u8d64\u624b\u7a7a\u62f3\uff0c\u4f46\u5b83\u5168\u8eab\u9cde\u7532\u539a\u91cd\uff0c\u6b7b\u6218\u4e0d\u9000\uff0c\u866b\u9b54\u60f3\u8981\u5feb\u901f\u6253\u5012\u6446\u8131\u5b83\uff0c\u4e00\u65f6\u95f4\u5374\u4e5f\u529e\u4e0d\u5230\u3002</p><p>\u5b83\u4eec\u6253\u5f97\u65e0\u6bd4\u6fc0\u70c8\uff0c\u4e00\u4f1a\u513f\u662f\u866b\u9b54\u628a\u9f99\u4eba\u8e39\u5012\uff0c\u4e00\u4f1a\u662f\u9f99\u4eba\u628a\u866b\u9b54\u6309\u4f4f\uff0c\u5728\u6ce5\u6f6d\u91cc\u7ffb\u6765\u6eda\u53bb\uff0c\u6ce5\u6d46\u56db\u6e85\u3002\u6885\u83f2\u65af\u548c\u51db\u4e58\u7740\u72ec\u89d2\u517d\u4e5f\u98de\u4e86\u8fc7\u6765\uff0c\u770b\u7740\u4e24\u4e2a\u5168\u8eab\u6c61\u6ce5\u7684\u602a\u517d\uff0c\u51db\u7684\u8138\u4e0a\u9732\u51fa\u4e00\u4e2a\u660e\u663e\u7684\u538c\u6076\u8868\u60c5\uff0c\u201c\u597d\u810f\u3002\u201d\u5979\u8bf4\uff0c\u5fd9\u4e0d\u8fed\u5730\u8df3\u4e0b\u72ec\u89d2\u517d\uff0c\u907f\u5f00\u4e00\u6bb5\u8ddd\u79bb\uff0c\u514d\u5f97\u88ab\u6e85\u8d77\u7684\u6ce5\u6d46\u6cbe\u5230\u3002</p><p>\u743c\u6069\u65e0\u6687\u8bf4\u8bdd\uff0c\u4ed6\u5306\u5fd9\u5ff5\u5492\uff0c\u5bf9\u51c6\u866b\u9b54\u53d1\u51fa\u4e00\u4e2a\u77f3\u5316\u672f\uff0c\u6cd5\u672f\u6ca1\u6709\u5b8c\u5168\u751f\u6548\uff0c\u4f46\u4ecd\u7136\u8d77\u5230\u4e00\u5b9a\u4f5c\u7528\u3002\u866b\u9b54\u7684\u52a8\u4f5c\u5728\u51e0\u79d2\u949f\u5185\u53d8\u5f97\u50f5\u786c\uff0c\u6700\u540e\u4e00\u4e2a\u8eb2\u95ea\u7a0d\u6162\uff0c\u88ab\u9f99\u4eba\u6b7b\u6b7b\u6309\u4f4f\uff0c\u53cc\u81c2\u6325\u51fa\uff0c\u75be\u98ce\u66b4\u96e8\u822c\u8fde\u63cd\u4e86\u5bf9\u65b9\u5341\u51e0\u62f3\uff0c\u6bcf\u4e00\u62f3\u90fd\u91cd\u91cd\u7838\u5728\u866b\u9b54\u7684\u8138\u4e0a\uff0c\u9669\u4e9b\u5c06\u4ed6\u7684\u8111\u888b\u90fd\u7838\u6241\u4e86\u3002</p><p>\u4f46\u866b\u9b54\u5e76\u6ca1\u6709\u6b7b\uff0c\u5b83\u4ecd\u7136\u5728\u6323\u624e\u3002</p><p>\u9f99\u4eba\u7528\u53f3\u624b\u627c\u4f4f\u866b\u9b54\u7684\u8116\u9888\uff0c\u5de6\u624b\u5728\u7a7a\u4e2d\u9065\u9065\u4e00\u62db\uff0c\u4e00\u70b9\u91d1\u5149\u4f34\u968f\u7740\u547c\u5578\u58f0\uff0c\u4ece\u57ce\u4e2d\u7684\u7ea2\u9f99\u795e\u6bbf\u91cc\u98de\u5c04\u800c\u81f3\uff0c\u5316\u6210\u4e00\u67c4\u5de8\u5927\u7684\u5251\u3002\u9f99\u4eba\u63e1\u4f4f\u5251\uff0c\u5de6\u81c2\u4e0a\u91d1\u5149\u707f\u70c2\uff0c\u5c3d\u6570\u6ce8\u5165\u5251\u5203\u4e4b\u4e2d\uff0c\u7528\u529b\u7167\u7740\u866b\u9b54\u7684\u80f8\u819b\u63d2\u4e0b\u53bb\u3002</p><p>\u201c\u4e0d\u5bf9\uff01\u201d\u743c\u6069\u731b\u7136\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff0c\u201c\u8981\u7cdf\uff01\u201d</p><p>\u9f99\u4eba\u4e00\u5251\u63d2\u4e0b\uff0c\u53ea\u542c\u5f97\u201c\u556a\u201d\u5730\u4e00\u58f0\u8106\u54cd\uff0c\u90a3\u67c4\u5251\u4ece\u4e2d\u65ad\u6210\u4e24\u622a\u3002</p><p>\u8fd9\u4e2a\u53d8\u6545\u8ba9\u6240\u6709\u4eba\u90fd\u5403\u4e86\u4e00\u60ca\u2014\u2014\u9664\u4e86\u743c\u6069\u4e4b\u5916\uff0c\u9f99\u4eba\u81ea\u5df1\u4e5f\u6014\u4e86\u4e00\u4e0b\u3002\u5f97\u6b64\u5598\u606f\u826f\u673a\uff0c\u866b\u9b54\u731b\u7136\u53d1\u529b\uff0c\u4e24\u53ea\u540e\u80a2\u91cd\u91cd\u8e6c\u5728\u9f99\u4eba\u7684\u80f8\u53e3\uff0c\u5c06\u5b83\u8fdc\u8fdc\u8e39\u98de\u51fa\u53bb\uff0c\u81ea\u5df1\u4e00\u4e2a\u7ffb\u8eab\u8dc3\u8d77\uff0c\u53d1\u8db3\u72c2\u5954\u3002</p><p>\u201c\u59d0\u59d0\uff0c\u5feb\u95ea\u5f00\uff01\u201d</p><p>\u743c\u6069\u5927\u60ca\uff0c\u5374\u5df2\u7ecf\u6765\u4e0d\u53ca\u963b\u62e6\u3002\u6885\u83f2\u65af\u548c\u51db\u6b64\u65f6\u4e5f\u770b\u89c1\u4e86\u73ca\u5609\uff0c\u4e24\u4f4d\u5c11\u5973\u540c\u65f6\u9ad8\u901f\u8ffd\u6765\uff0c\u4f46\u866b\u9b54\u8dd1\u5f97\u66f4\u5feb\uff0c\u8f6c\u773c\u4e4b\u95f4\u5c31\u5df2\u7ecf\u5954\u5230\u6cb3\u8fb9\u3002\u9762\u524d\u5df2\u7ecf\u6ca1\u6709\u654c\u4eba\uff0c\u53ea\u6709\u4e00\u4f4d\u9ed1\u8272\u957f\u53d1\u7684\u7f8e\u4e3d\u5c11\u5973\uff0c\u624b\u4e2d\u63e1\u7740\u4e00\u652f\u94f6\u8272\u957f\u7b1b\uff0c\u6b63\u9759\u9759\u5730\u7ad9\u7740\uff0c\u89c1\u866b\u9b54\u5954\u6765\uff0c\u5fae\u5fae\u4ef0\u8d77\u8138\uff0c\u770b\u7740\u5b83\u3002</p><p>\u201c\u4f60\u2026\u2026\u662f\u4f60\uff01\u201d</p><p>\u866b\u9b54\u539f\u672c\u54ea\u91cc\u5c06\u8fd9\u6837\u4e00\u4f4d\u5a07\u5f31\u5c11\u5973\u653e\u5728\u773c\u91cc\uff0c\u53ea\u6253\u7b97\u4e00\u811a\u78be\u8fc7\uff0c\u8dc3\u5165\u6cb3\u4e2d\u3002\u51e1\u4eba\u65e0\u6cd5\u6e21\u8fc7\u873f\u8712\u6cb3\uff0c\u5b83\u5374\u6ca1\u6709\u8fd9\u79cd\u9650\u5236\uff0c\u7136\u800c\u4e00\u773c\u770b\u89c1\u5c11\u5973\u7684\u8138\uff0c\u5b83\u987f\u65f6\u4eff\u4f5b\u88ab\u96f7\u51fb\u4e86\u4e00\u822c\uff0c\u5168\u8eab\u5267\u70c8\u98a4\u6296\u8d77\u6765\uff0c\u4e00\u65f6\u95f4\u5413\u5f97\u8fde\u5fc3\u8df3\u90fd\u9669\u4e9b\u505c\u6b62\uff0c\u539f\u672c\u5df2\u7ecf\u8fc8\u51fa\u7684\u4e00\u6b65\u786c\u751f\u751f\u6536\u4e86\u56de\u6765\uff0c\u8f6c\u5411\u53e6\u4e00\u4e2a\u65b9\u5411\u9003\u53bb\u3002</p><p>\u773c\u89c1\u866b\u9b54\u5ffd\u7136\u7ed5\u5f00\u73ca\u5609\uff0c\u743c\u6069\u867d\u7136\u4e0d\u660e\u6240\u4ee5\uff0c\u5374\u662f\u5927\u559c\u8fc7\u671b\uff0c\u614c\u5fd9\u671d\u73ca\u5609\u8dd1\u8fc7\u6765\u3002\u6885\u83f2\u65af\u548c\u51db\u6bd4\u4ed6\u66f4\u5feb\u4e00\u6b65\uff0c\u53cc\u53cc\u98de\u5230\u73ca\u5609\u8eab\u65c1\uff0c\u6b63\u8981\u62c9\u7740\u5979\u79bb\u5f00\uff0c\u73ca\u5609\u8f7b\u8f7b\u6446\u4e86\u6446\u624b\uff0c\u5c06\u94f6\u7b1b\u8d34\u5728\u5507\u8fb9\uff0c\u5ffd\u7136\u5439\u51fa\u4e00\u4e2a\u97f3\u7b26\u3002</p><p>\u8fd9\u4e00\u58f0\u5f88\u8f7b\uff0c\u743c\u6069\u79bb\u5f97\u7a0d\u8fdc\u90fd\u6ca1\u542c\u89c1\uff0c\u6885\u83f2\u65af\u548c\u51db\u867d\u7136\u542c\u89c1\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u611f\u89c9\uff0c\u7136\u800c\u6b63\u5728\u5954\u9003\u7684\u866b\u9b54\u5374\u5982\u906d\u91cd\u51fb\uff0c\u5657\u901a\u4e00\u58f0\u6251\u5012\u5728\u5730\u3002\u53ea\u89c1\u73ca\u5609\u7389\u6307\u7ffb\u98de\uff0c\u5439\u51fa\u4e00\u9996\u66f2\u5b50\u6765\u3002</p><p>\u743c\u6069\u6c14\u5598\u5401\u5401\u5730\u8dd1\u5230\u8ddf\u524d\uff0c\u4ed6\u4ece\u672a\u542c\u8fc7\u73ca\u5609\u5439\u7684\u8fd9\u9996\u66f2\u5b50\uff0c\u53ea\u89c9\u633a\u597d\u542c\uff0c\u4f46\u4e5f\u6ca1\u4ec0\u4e48\u592a\u5927\u611f\u89c9\uff0c\u6885\u83f2\u65af\u548c\u51db\u4e5f\u5dee\u4e0d\u591a\uff0c\u7136\u800c\u866b\u9b54\u7684\u53cd\u5e94\u5374\u5267\u70c8\u65e0\u6bd4\uff0c\u5728\u5730\u9762\u4e0a\u8fde\u8fde\u7ffb\u6eda\uff0c\u53e3\u4e2d\u4e0d\u65ad\u53d1\u51fa\u51c4\u5389\u7684\u5636\u9e23\u3002</p><p>\u73ca\u5609\u8d8a\u5439\u8d8a\u6162\uff0c\u58f0\u8c03\u8d8a\u6765\u8d8a\u4f4e\uff0c\u6700\u540e\u51e0\u4e4e\u6084\u4e0d\u53ef\u95fb\uff0c\u7136\u800c\u866b\u9b54\u7684\u53cd\u5e94\u5374\u662f\u8d8a\u6765\u8d8a\u5267\u70c8\uff0c\u5ffd\u7136\u731b\u5730\u8df3\u8d77\u6765\uff0c\u671d\u7740\u6cb3\u8fb9\u5954\u53bb\uff0c\u6885\u83f2\u65af\u6b63\u5f85\u8ffd\u4e0a\u53bb\uff0c\u73ca\u5609\u5374\u6446\u4e86\u6446\u624b\uff0c\u793a\u610f\u4e0d\u7528\u3002\u679c\u7136\u866b\u9b54\u8fd8\u6ca1\u8dd1\u51fa\u51e0\u6b65\uff0c\u773c\u770b\u4e00\u53ea\u811a\u5df2\u7ecf\u8e0f\u5165\u6cb3\u4e2d\uff0c\u5ffd\u7136\u53c8\u7ad9\u5b9a\uff0c\u6602\u9996\u53d1\u51fa\u5389\u58f0\u5578\u53eb\uff0c\u9ed1\u8272\u7684\u6c14\u4f53\u4ece\u5b83\u7684\u53e3\u9f3b\u4e03\u7a8d\u4e2d\u6ea2\u51fa\uff0c\u5168\u8eab\u9aa8\u9abc\u53d1\u51fa\u7206\u88c2\u58f0\uff0c\u5e9e\u5927\u7684\u8eaf\u4f53\u4eff\u4f5b\u88ab\u6233\u7834\u7684\u6c14\u56ca\uff0c\u8fc5\u901f\u7f29\u5c0f\u3001\u59d4\u987f\u4e0b\u6765\uff0c\u6700\u540e\u5657\u901a\u4e00\u58f0\uff0c\u6454\u843d\u5728\u5730\uff0c\u53c8\u53d8\u56de\u4e86\u57c3\u535c\u62c9\u3002</p><p>\u6885\u83f2\u65af\u5feb\u6b65\u8d70\u5230\u65c1\u8fb9\uff0c\u4f38\u624b\u63a2\u4e86\u63a2\uff0c\u201c\u4ed6\u6b7b\u4e86\u3002\u201d</p><p>\u57c3\u535c\u62c9\u662f\u88ab\u5df4\u5c14\u6cfd\u5e03\u7684\u529b\u91cf\u9644\u4f53\uff0c\u53d8\u6210\u4e86\u866b\u9b54\u3002\u521a\u624d\u7684\u60c5\u5f62\uff0c\u5e94\u8be5\u662f\u73ca\u5609\u4e0d\u77e5\u7528\u4e86\u4ec0\u4e48\u65b9\u6cd5\uff0c\u786c\u751f\u751f\u628a\u5927\u9b54\u9b3c\u7684\u529b\u91cf\u9a71\u9010\u51fa\u4e86\u57c3\u535c\u62c9\u4f53\u5185\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u57c3\u535c\u62c9\u5c31\u906d\u6b83\u4e86\uff0c\u539f\u672c\u8eab\u4e0a\u88ab\u538b\u5236\u4f4f\u7684\u6240\u6709\u4f24\u52bf\u540c\u65f6\u7206\u53d1\uff0c\u987f\u65f6\u4fbf\u8981\u4e86\u4ed6\u7684\u547d\uff0c\u8fde\u62a2\u6551\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\u3002</p><p>\u201c\u73ca\u5609\u4f60\u8fd9\u662f\u4ec0\u4e48\u6cd5\u672f\u554a\uff0c\u597d\u5389\u5bb3\uff0c\u201d\u51db\u60ca\u53f9\uff0c\u201c\u4e0d\u8fc7\u4e5f\u65e9\u70b9\u7528\u561b\uff0c\u6211\u4eec\u5c31\u4e0d\u7528\u6253\u534a\u5929\u4e86\u3002\u201d</p><p>\u73ca\u5609\u62b1\u6b49\u5730\u671d\u5979\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u743c\u6069\u5fc3\u4e2d\u7591\u60d1\uff0c\u4f46\u4e5f\u4e0d\u60f3\u5f53\u7740\u6885\u83f2\u65af\u548c\u51db\u7684\u9762\u95ee\u73ca\u5609\uff0c\u6b63\u6253\u7b97\u56de\u53bb\u3002\u5ffd\u7136\u542c\u5230\u80cc\u540e\u201c\u7830\u201d\u7684\u4e00\u58f0\uff0c\u56de\u5934\u770b\u53bb\uff0c\u5374\u662f\u9f99\u4eba\u5012\u5728\u5730\u4e0a\u3002\u4e0e\u866b\u9b54\u4e00\u573a\u6076\u6218\uff0c\u9f99\u4eba\u539f\u672c\u53d7\u4f24\u5c31\u5df2\u7ecf\u5f88\u91cd\uff0c\u521a\u624d\u88ab\u866b\u9b54\u4e00\u811a\u8e39\u98de\uff0c\u6574\u4e2a\u4e0b\u534a\u8eab\u90fd\u5012\u6298\u8fc7\u6765\uff0c\u4ee5\u4e00\u79cd\u8be1\u5f02\u7684\u59ff\u52bf\u626d\u66f2\u7740\uff0c\u5168\u8eab\u4e0a\u4e0b\u90fd\u662f\u521b\u53e3\uff0c\u91d1\u8272\u7684\u8840\u5f80\u5916\u76f4\u6d41\uff0c\u6d78\u5165\u6ce5\u571f\u4e2d\u3002\u73ca\u5609\u7684\u7b1b\u58f0\u4f3c\u4e4e\u4e5f\u52a0\u91cd\u4e86\u5b83\u7684\u4f24\u52bf\uff0c\u73b0\u5728\u5b83\u8fde\u5750\u8d77\u6765\u7684\u529b\u6c14\u90fd\u6ca1\u6709\uff0c\u53ea\u80fd\u8eba\u5012\u5728\u5730\u3002</p><p>\u743c\u6069\u8d70\u5230\u5b83\u8eab\u65c1\uff0c\u201c\u4f60\u662f\u2026\u2026\u7ea2\u9f99\u738b\uff1f\u201d</p><p>\u9f99\u4eba\u4e0d\u7b54\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u5b83\u7684\u5e9e\u5927\u8eaf\u4f53\u5ffd\u7136\u6e10\u6e10\u6cdb\u8d77\u5149\u6765\u3002\u65e0\u6570\u5149\u70b9\u4ece\u5b83\u8eab\u4e0a\u9038\u51fa\uff0c\u5347\u5728\u534a\u7a7a\u4e2d\uff0c\u5ffd\u660e\u5ffd\u706d\uff0c\u4eff\u4f5b\u4e00\u7fa4\u8424\u706b\u866b\u3002\u5b83\u4eec\u6f02\u6d6e\u7740\uff0c\u6447\u6643\u4e0d\u5b9a\uff0c\u968f\u540e\u50cf\u662f\u88ab\u67d0\u79cd\u65e0\u5f62\u529b\u91cf\u7275\u5f15\uff0c\u98d8\u5230\u51db\u7684\u9762\u524d\u3002\u51db\u597d\u5947\u5730\u4f38\u51fa\u624b\uff0c\u60f3\u8981\u78b0\u89e6\uff0c\u7136\u800c\u5979\u521a\u4e00\u52a8\uff0c\u90a3\u4e9b\u5149\u70b9\u4fbf\u4e89\u5148\u6050\u540e\u5730\u4ece\u5979\u7684\u6307\u5c16\u94bb\u8fdb\u6765\uff0c\u6d8c\u5165\u5979\u7684\u8eab\u4f53\u3002</p><p>\u201c\u51db\uff1f\u201d</p><p>\u743c\u6069\u62c5\u5fc3\u6709\u53d8\uff0c\u4f46\u89c1\u51db\u5e76\u6ca1\u4ec0\u4e48\u5f02\u6837\u795e\u60c5\uff0c\u4fbf\u6682\u4e14\u653e\u4e0b\u5fc3\u6765\u3002\u5f53\u5149\u70b9\u5b8c\u5168\u6d88\u5931\uff0c\u9f99\u4eba\u4e5f\u548c\u866b\u9b54\u4e00\u6837\uff0c\u5e9e\u5927\u8eaf\u4f53\u5feb\u901f\u7f29\u6c34\uff0c\u6700\u7ec8\u53d8\u56de\u5510\u7426\u62c9\u3002\u8001\u796d\u53f8\u6bd4\u57c3\u535c\u62c9\u8fd0\u6c14\u597d\u4e00\u4e9b\uff0c\u6ca1\u6709\u5f53\u5373\u6bd9\u547d\uff0c\u4f46\u770b\u6a21\u6837\u4e5f\u5c31\u53ea\u5269\u6700\u540e\u534a\u53e3\u6c14\u4e86\u3002</p><p>\u743c\u6069\u6025\u5fd9\u53ec\u51fa\u4e00\u53ea\u5973\u796d\u53f8\u9b54\u50cf\uff0c\u4e3a\u4ed6\u4f7f\u7528\u6cbb\u7597\u6cd5\u672f\uff0c\u7136\u800c\u6548\u679c\u5e76\u4e0d\u660e\u663e\u3002\u5510\u7426\u62c9\u773c\u775b\u5b9a\u5b9a\u5730\u671b\u7740\u5929\u7a7a\uff0c\u5ffd\u7136\u8f6c\u79fb\u89c6\u7ebf\uff0c\u770b\u7740\u6885\u83f2\u65af\uff0c\u5634\u5507\u5fae\u5fae\u7fd5\u52a8\uff0c\u4f3c\u4e4e\u5728\u8bf4\u4ec0\u4e48\uff0c\u4f46\u58f0\u97f3\u975e\u5e38\u5fae\u5f31\u3002\u6885\u83f2\u65af\u4fef\u8eab\u503e\u542c\uff0c\u743c\u6069\u548c\u51db\u8ddd\u79bb\u7a0d\u8fdc\uff0c\u4e5f\u542c\u4e0d\u6e05\u695a\u5185\u5bb9\u3002\u8001\u796d\u53f8\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u5ffd\u7136\u4e00\u987f\uff0c\u63a5\u7740\u8111\u888b\u4e00\u6b6a\uff0c\u5c31\u6b64\u6c14\u7edd\u3002</p><p>\u6885\u83f2\u65af\u7ad9\u8d77\u8eab\u6765\uff0c\u9762\u8272\u53d8\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u201c\u4ed6\u8bf4\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u4ed6\u8bf4\uff1a\u2018\u827e\u5f25\u8587\uff0c\u8981\u5c0f\u5fc3\u4f60\u7684\u6bcd\u4eb2\u2019\u3002\u201d</p><p>\u201c\u2026\u2026\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u5510\u7426\u62c9\u4e0e\u6885\u83f2\u65af\u7684\u6bcd\u4eb2\u76f8\u8bc6\uff0c\u8fd9\u70b9\u743c\u6069\u548c\u6885\u83f2\u65af\u90fd\u662f\u77e5\u9053\u7684\uff0c\u4ed6\u4e34\u7ec8\u4e4b\u524d\uff0c\u5bf9\u6885\u83f2\u65af\u6709\u4ec0\u4e48\u4ea4\u4ee3\uff0c\u6d89\u53ca\u5230\u5979\u6bcd\u4eb2\uff0c\u4e5f\u5c5e\u6b63\u5e38\u3002\u4f46\u8981\u6885\u83f2\u65af\u5c0f\u5fc3\u5979\u6bcd\u4eb2\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u5979\u6bcd\u4eb2\u90fd\u53bb\u4e16\u591a\u5e74\u4e86\uff0c\u83ab\u975e\u5510\u7426\u62c9\u8fd8\u4e0d\u77e5\u9053\uff1f</p><p>\u6885\u83f2\u65af\u4e5f\u662f\u832b\u7136\uff0c\u4e00\u65f6\u4e0d\u660e\u6240\u4ee5\u3002</p><p>\u201c\u5148\u522b\u7ba1\u8fd9\u4e2a\u4e86\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u6211\u4eec\u8d76\u5feb\u56de\u57ce\u91cc\uff0c\u8fd8\u6709\u4e00\u5927\u5806\u574f\u86cb\u7b49\u7740\u6e05\u7406\u5462\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u57c3\u535c\u62c9\u8eab\u6b7b\uff0c\u57ce\u4e2d\u6b8b\u4f59\u7684\u9b54\u4ec6\u4fbf\u4eff\u4f5b\u5931\u53bb\u4e86\u63a7\u5236\uff0c\u4f46\u8fd9\u53cd\u800c\u7ed9\u527f\u706d\u589e\u52a0\u4e86\u96be\u5ea6\u3002\u7b49\u743c\u6069\u4e00\u884c\u4eba\u51fa\u624b\uff0c\u5c06\u6240\u6709\u9b54\u4ec6\u5168\u90e8\u65a9\u6740\u6b86\u5c3d\uff0c\u5df2\u7ecf\u662f\u4e00\u65e5\u4e4b\u540e\u3002\u5e78\u597d\uff0c\u5927\u90e8\u5206\u5c45\u6c11\u9003\u5f97\u5feb\uff0c\u85cf\u5728\u5175\u8425\u4e2d\u8eb2\u8fc7\u4e00\u52ab\uff0c\u4e8b\u540e\u6e05\u70b9\uff0c\u6b7b\u5728\u9b54\u4ec6\u624b\u4e2d\uff0c\u4ee5\u53ca\u6df7\u4e71\u4e2d\u81ea\u76f8\u8df5\u8e0f\u800c\u6b7b\u7684\uff0c\u52a0\u8d77\u6765\u5927\u7ea6\u6709\u4e00\u5343\u4f59\u4eba\u3002</p><p>\u62bd\u4e86\u4e2a\u7a7a\uff0c\u743c\u6069\u79c1\u4e0b\u95ee\u73ca\u5609\uff0c\u6700\u540e\u6253\u5012\u866b\u9b54\u7684\u90a3\u9996\u66f2\u5b50\u7684\u6765\u5386\u3002\u4ed6\u672c\u4ee5\u4e3a\u662f\u5965\u52a0\u83b1\u65af\u7559\u7ed9\u59d0\u59d0\u7684\uff0c\u5927\u5965\u672f\u5e08\u89c1\u591a\u8bc6\u5e7f\uff0c\u6cd5\u529b\u5353\u7edd\uff0c\u7ed9\u5973\u513f\u7559\u4e0b\u51e0\u62db\u6740\u624b\u950f\u6beb\u4e0d\u51fa\u5947\u3002\u7136\u800c\u73ca\u5609\u7684\u56de\u7b54\u5374\u8ba9\u4ed6\u51fa\u4e4e\u610f\u6599\uff0c\u201c\u5c31\u662f\u4f60\u62ff\u56de\u6765\u7684\u90a3\u4e00\u5806\u7af9\u7b80\u91cc\u770b\u5230\u7684\u554a\u3002\u201d</p><p>\u201c\u7af9\u7b80\u91cc\uff1f\u201d</p><p>\u8f9b\u5df4\u57ce\u7684\u65f6\u5019\uff0c\u743c\u6069\u4ece\u7ea2\u9f99\u795e\u6bbf\u7684\u50a8\u85cf\u5ba4\u91cc\u5f97\u5230\u4e00\u6279\u7af9\u7b80\uff0c\u4e0a\u9762\u90fd\u662f\u7528\u4f0a\u739b\u65af\u5361\u65f6\u4ee3\u7684\u5b66\u8005\u6587\u5b57\u6240\u5199\uff0c\u6839\u672c\u6ca1\u4eba\u80fd\u770b\u61c2\uff0c\u6240\u4ee5\u90fd\u4e22\u5728\u4e00\u8fb9\u3002\u73ca\u5609\u5374\u610f\u5916\u53d1\u73b0\u5176\u4e2d\u6709\u51e0\u5757\u7af9\u7b80\uff0c\u4e0a\u9762\u8bb0\u8f7d\u7684\u4e0d\u662f\u6587\u5b57\uff0c\u800c\u662f\u4e00\u9996\u7c7b\u4f3c\u66f2\u8c31\u7684\u4e1c\u897f\u3002</p><p>\u5979\u5728\u97f3\u4e50\u4e0a\uff0c\u5b9e\u6709\u8fc7\u4eba\u7684\u5929\u8d4b\u4e0e\u7231\u597d\uff0c\u82b1\u4e86\u534a\u4e2a\u6708\u65f6\u95f4\uff0c\u534a\u731c\u534a\u8499\uff0c\u53cd\u590d\u5c1d\u8bd5\uff0c\u5c45\u7136\u5f53\u771f\u5c06\u90a3\u9996\u66f2\u8c31\u8bd1\u51fa\u6765\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u73ca\u5609\u5e76\u6ca1\u6709\u628a\u5b83\u592a\u5f53\u56de\u4e8b\uff0c\u53ea\u4ee5\u4e3a\u662f\u4e00\u9996\u53e4\u8001\u7684\u6b4c\u66f2\u3002\u4eca\u5929\u9762\u5bf9\u90a3\u53ea\u866b\u9b54\uff0c\u4e5f\u4e0d\u77e5\u9053\u600e\u4e48\u56de\u4e8b\uff0c\u5ffd\u7136\u95f4\u8111\u4e2d\u7075\u5149\u95ea\u8fc7\uff0c\u4eff\u4f5b\u6709\u4eba\u63d0\u793a\u4e00\u822c\uff0c\u4fbf\u5439\u594f\u51fa\u6765\uff0c\u6ca1\u60f3\u5230\u4e00\u4e3e\u5efa\u529f\uff0c\u5012\u662f\u628a\u5979\u81ea\u5df1\u90fd\u5413\u4e86\u4e00\u8df3\u3002</p><p>\u65e0\u8bba\u5982\u4f55\uff0c\u8fd9\u4e00\u573a\u6df7\u4e71\u603b\u7b97\u843d\u4e0b\u5e37\u5e55\uff0c\u7136\u800c\u743c\u6069\u53c8\u9762\u4e34\u65b0\u7684\u96be\u9898\u3002\u4f0a\u68ee\u6b7b\u4e86\uff0c\u5510\u7426\u62c9\u6b7b\u4e86\uff0c\u8fde\u57c3\u535c\u62c9\u90fd\u6b7b\u4e86\uff0c\u8fd9\u5723\u6e0a\u57ce\u4e3b\u7684\u4f4d\u7f6e\uff0c\u770b\u8d77\u6765\u4e00\u65f6\u534a\u4f1a\u662f\u7a7a\u60ac\u7684\uff0c\u90a3\u4ed6\u8981\u6e21\u6cb3\u600e\u4e48\u529e\uff1f\u4ed6\u8bd5\u4e86\u4e00\u4e0b\uff0c\u7684\u786e\u5c31\u5982\u6885\u83f2\u65af\u6240\u8a00\uff0c\u8fd9\u6cb3\u4e0a\u6709\u67d0\u79cd\u5f3a\u5927\u7684\u529b\u91cf\u5c01\u9501\uff0c\u6ca1\u6709\u201c\u901a\u9053\u201d\uff0c\u6839\u672c\u8fc7\u4e0d\u53bb\u3002</p><p>\u4e00\u4e2a\u4e0d\u901f\u4e4b\u5ba2\u89e3\u51b3\u4e86\u4ed6\u7684\u96be\u9898\u3002</p><p>\u90a3\u662f\u4e00\u4f4d\u5c11\u5973\uff0c\u76f8\u8c8c\u5e73\u51e1\uff0c\u4f46\u6709\u4e00\u53cc\u7075\u52a8\u7684\u5927\u773c\u775b\uff0c\u4eff\u4f5b\u65e0\u6687\u7684\u7d2b\u6c34\u6676\u3002\u743c\u6069\u89c1\u5230\u5979\u7684\u65f6\u5019\uff0c\u662f\u5728\u6df7\u4e71\u5e73\u606f\u540e\u7684\u7b2c\u4e09\u5929\uff0c\u5f53\u65f6\u4ed6\u5fd9\u4e86\u534a\u5929\uff0c\u5077\u7a7a\u56de\u5230\u81ea\u5df1\u623f\u95f4\u91cc\u4f11\u606f\uff0c\u5ffd\u7136\u542c\u89c1\u540e\u9662\u6709\u52a8\u9759\u3002</p><p>\u540e\u9662\u91cc\u6ca1\u4ec0\u4e48\u522b\u7684\u4e1c\u897f\uff0c\u5012\u662f\u6709\u4e00\u5177\u68fa\u6750\u3002\u524d\u65e5\u90a3\u4e00\u573a\u5927\u6218\uff0c\u6b7b\u4f24\u65e0\u6570\uff0c\u5723\u6e0a\u57ce\u91cc\u7684\u9047\u96be\u8005\u81ea\u7136\u6709\u4eba\u6536\u6b93\uff0c\u5165\u4fb5\u8005\u5c31\u6ca1\u8fd9\u4e48\u597d\u7684\u5f85\u9047\u4e86\uff0c\u5c24\u5176\u57c3\u535c\u62c9\uff0c\u4f5c\u4e3a\u7f6a\u9b41\u7978\u9996\uff0c\u66f4\u662f\u65e0\u4eba\u7406\u776c\u3002\u8fd8\u662f\u6885\u83f2\u65af\u89c9\u5f97\u4e5f\u7b97\u662f\u76f8\u8bc6\u4e00\u573a\uff0c\u8ba9\u4ed6\u5c31\u8fd9\u6837\u66dd\u5c38\u8352\u91ce\u603b\u662f\u4e0d\u597d\uff0c\u4fbf\u5148\u8fd0\u56de\u6765\uff0c\u627e\u4e86\u4e2a\u68fa\u6750\u88c5\u8fdb\u53bb\uff0c\u6682\u65f6\u653e\u5728\u540e\u9662\uff0c\u6253\u7b97\u7b49\u5fd9\u5b8c\u8fd9\u4e24\u5929\uff0c\u5c31\u627e\u4eba\u628a\u5b83\u9001\u56de\u8f9b\u5df4\u57ce\u5b89\u846c\u3002</p><p>\u743c\u6069\u8d70\u5230\u540e\u9662\uff0c\u4fbf\u770b\u89c1\u4e86\u90a3\u4f4d\u5c11\u5973\uff0c\u6b63\u7ad9\u5728\u57c3\u535c\u62c9\u7684\u68fa\u8fb9\uff0c\u68fa\u76d6\u88ab\u63a8\u5230\u4e00\u65c1\uff0c\u5c11\u5973\u4f4e\u7740\u5934\uff0c\u4f3c\u4e4e\u5728\u5f80\u91cc\u67e5\u770b\u4ec0\u4e48\u3002</p><p>\u743c\u6069\u5fc3\u4e2d\u597d\u5947\uff0c\u8fd9\u5c11\u5973\u770b\u8d77\u6765\u4e0d\u50cf\u662f\u5723\u6e0a\u57ce\u91cc\u7684\u5c45\u6c11\uff0c\u4e0d\u77e5\u9053\u662f\u4ece\u54ea\u91cc\u6765\u7684\uff0c\u800c\u4e14\u4ed6\u8fd9\u9752\u94dc\u8c6a\u5b85\u53ef\u4e0d\u662f\u5916\u4eba\u80fd\u591f\u968f\u610f\u51fa\u5165\u7684\uff0c\u600e\u4e48\u4e00\u4e2a\u964c\u751f\u5c11\u5973\u51fa\u73b0\u5728\u8fd9\u91cc\uff0c\u4ed6\u5c45\u7136\u5168\u65e0\u611f\u5e94\u3002\u4ed6\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u5c11\u5973\u95fb\u58f0\u7ad9\u8d77\uff0c\u8f6c\u8fc7\u8138\u770b\u89c1\u743c\u6069\uff0c\u4fbf\u5fae\u5fae\u4e00\u7b11\u3002\u5979\u660e\u660e\u957f\u5f97\u4e5f\u4e0d\u5982\u4f55\u6f02\u4eae\uff0c\u4e94\u5b98\u90fd\u53ea\u7b97\u5e73\u5e38\uff0c\u8fd9\u4e00\u7b11\u5374\u4eff\u4f5b\u6709\u9b54\u529b\u4e00\u822c\uff0c\u7f8e\u5f97\u6444\u4eba\u5fc3\u9b44\uff0c\u743c\u6069\u4e5f\u7b97\u662f\u7f8e\u5973\u89c1\u5f97\u591a\u4e86\uff0c\u90fd\u4e0d\u7531\u5f97\u604d\u60da\u4e86\u4e00\u4e0b\u3002</p><p>\u201c\u4e0b\u5348\u597d\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\u3002\u201d</p><p>\u201c\u4e0b\u5348\u597d\uff0c\u5443\uff0c\u4f60\u662f\u54ea\u4f4d\uff1f\u6709\u4ec0\u4e48\u6211\u53ef\u4ee5\u5e2e\u5fd9\u7684\u5417\uff1f\u201d</p><p>\u5c11\u5973\u7b11\u7740\u6447\u4e86\u6447\u5934\uff0c\u201c\u6211\u662f\u6765\u62ff\u56de\u8fd9\u4e1c\u897f\u7684\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u638c\u5fc3\u4e2d\u6258\u7740\u4e00\u679a\u91d1\u707f\u707f\u7684\u6212\u6307\uff0c\u5c55\u793a\u7ed9\u743c\u6069\u770b\uff0c\u201c\u5df2\u7ecf\u627e\u5230\u4e86\uff0c\u4e0d\u8fc7\u8fd8\u662f\u591a\u8c22\u4f60\u3002\u201d</p><p>\u201c\u8fd9\u6212\u6307\u2026\u2026\u4f60\u600e\u4e48\u53d6\u4e0b\u6765\u7684\uff1f\u201d</p><p>\u57c3\u535c\u62c9\u90a3\u679a\u5973\u795e\u5723\u6212\uff0c\u743c\u6069\u4e5f\u66fe\u7ecf\u8003\u8651\u62ff\u6765\u7814\u7a76\u7814\u7a76\uff0c\u7136\u800c\u6212\u6307\u4eff\u4f5b\u548c\u4ed6\u7684\u76ae\u80a4\u957f\u6210\u4e00\u4f53\u4f3c\u7684\uff0c\u600e\u4e48\u90fd\u8131\u4e0d\u4e0b\u6765\u3002\u743c\u6069\u53c8\u4e0d\u60f3\u4e3a\u8fd9\u4e8b\u628a\u4ed6\u624b\u6307\u780d\u6389\uff0c\u5f04\u5f97\u5c38\u9996\u4e0d\u5168\uff0c\u4e5f\u5c31\u7f62\u4e86\u3002\u8fd9\u5c11\u5973\u662f\u4ec0\u4e48\u4eba\uff0c\u5979\u600e\u4e48\u53d6\u4e0b\u6765\u7684\uff1f</p><p>\u201c\u5b83\u672c\u6765\u5c31\u662f\u6211\u7684\u4e1c\u897f\uff0c\u7269\u5f52\u539f\u4e3b\uff0c\u6709\u4ec0\u4e48\u96be\u7684\u5462\u3002\u201d</p><p>\u743c\u6069\u4e0d\u660e\u6240\u4ee5\u3002\u201c\u542c\u8bf4\u4f60\u60f3\u53bb\u6cb3\u5bf9\u5cb8\uff1f\u201d\u5c11\u5973\u5ffd\u7136\u95ee\u3002</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u6700\u597d\u4e0d\u8981\u53bb\uff0c\u201d\u5c11\u5973\u8bf4\uff0c\u201c\u5f88\u5371\u9669\u3002\u201d</p><p>\u201c\u4f60\u5230\u5e95\u662f\u8c01\uff1f\u201d</p><p>\u5c11\u5973\u4e0d\u7b54\uff0c\u201c\u540e\u4f1a\u6709\u671f\u3002\u201d\u5979\u8bf4\uff0c\u8eab\u5f62\u5728\u7a7a\u6c14\u4e2d\u5feb\u901f\u6de1\u53bb\u3002</p><p>\u201c\u7b49\u7b49\uff01\u201d</p><p>\u7136\u800c\u5c11\u5973\u5df2\u7ecf\u6d88\u5931\uff0c\u743c\u6069\u6014\u4e86\u4e00\u4f1a\uff0c\u542c\u5230\u80cc\u540e\u811a\u6b65\u58f0\u3002\u4ed6\u8f6c\u8fc7\u8eab\uff0c\u770b\u89c1\u4e86\u7fe1\u7fe0\u5973\u5deb\uff0c\u4e00\u53cc\u6f06\u9ed1\u7684\u773c\u7738\u5b9a\u5b9a\u5730\u770b\u7740\u4ed6\u3002\u201c\u6709\u4ec0\u4e48\u4e8b\u5417\uff1f\u201d\u4ed6\u95ee\u3002\u5982\u4eca\u57ce\u4e2d\u767e\u5e9f\u5f85\u5174\uff0c\u4f0a\u68ee\u3001\u5510\u7426\u62c9\u8fd8\u8981\u4e3e\u884c\u846c\u793c\uff0c\u6885\u83f2\u65af\u548c\u51db\u5ff5\u5728\u6614\u65e5\u4ea4\u60c5\uff0c\u5e2e\u5fd9\u7ec4\u7ec7\u6253\u7406\uff0c\u5fd9\u5f97\u4e0d\u53ef\u5f00\u4ea4\uff0c\u628a\u5176\u4ed6\u4eba\u4e5f\u90fd\u62c9\u53bb\u5e2e\u5fd9\uff0c\u743c\u6069\u662f\u5077\u7a7a\u56de\u6765\u4f11\u606f\u3002\u4e5f\u5c31\u7fe1\u7fe0\u5973\u5deb\u6e05\u95f2\u4e00\u4e9b\uff0c\u7559\u5728\u5b85\u9662\u91cc\u770b\u5bb6\u3002</p><p>\u201c\u6211\u611f\u89c9\u6cb3\u4e0a\u7684\u5c01\u5370\u6d88\u5931\u4e86\uff0c\u201d\u5973\u5deb\u8bf4\uff0c\u201c\u5c31\u5728\u521a\u624d\u3002\u201d</p><p>\u3010\u4e1c\u57df\u7bc7\uff08\u4e0a\uff09\u5b8c\u3011</p><p>\u203b\u203b\u203b</p><p>\u4e1c\u57df\u7bc7\u4e0a\u5377\u5199\u5b8c\u540e\u7684\u8bdd\uff1a</p><p>\u65f6\u9694\u4e00\u5e74\u591a\uff0c\u7ec8\u4e8e\u628a\u300a\u4e1c\u57df\u7bc7\u4e0a\u5f7b\u68ee\u5854\u300b\u5199\u5b8c\u4e86\uff0c\u6bd4\u8d77\u539f\u672c\u7684\u9884\u8ba1\uff0c\u65f6\u95f4\u591a\u82b1\u4e86\u4e00\u500d\uff0c\u800c\u4e14\u56e0\u4e3a\u8d8a\u5199\u8d8a\u957f\uff0c\u8d8a\u5199\u8d8a\u63a7\u5236\u4e0d\u4f4f\u7bc7\u5e45\uff0c\u539f\u672c\u9884\u8ba1\u4e00\u5377\u5b8c\u7ed3\uff0c\u73b0\u5728\u8981\u5206\u4e0a\u4e2d\u4e0b\u4e09\u5377\uff0c\u4f30\u8ba1\u603b\u5b57\u6570\u53ef\u80fd\u4f1a\u63a5\u8fd1\u4e00\u767e\u4e07\u5b57\u2014\u2014\u5766\u767d\u5730\u8bf4\uff0c\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u6211\u81ea\u5df1\u90fd\u88ab\u5413\u5230\u4e86\u3002\u6211\u89c9\u5f97\u6211\u52aa\u529b\u52aa\u529b\uff0c\u6709\u53ef\u80fd\u51cf\u5c11\u5230\u516b\u5341\u4e07\u5b57\u3002</p><p>\u987a\u4fbf\u8bf4\u70b9\u76f8\u5173\u4ee5\u53ca\u4e0d\u76f8\u5173\u7684\u8bdd\uff0c\u8bfb\u8005\u5173\u5fc3\u4ee5\u53ca\u4e0d\u5173\u5fc3\u7684\uff0c\u7ecf\u5e38\u95ee\u6211\u4ee5\u53ca\u4e0d\u7ecf\u5e38\u95ee\u6211\u7684\uff0c\u60f3\u5230\u54ea\u8bf4\u5230\u54ea\u3002</p><p>\u4e00\u3001\u4e1c\u57df\u7bc7\u4e0a\u5377\uff0c\u57fa\u672c\u4e0a\u5c31\u662f\u5728\u8fc7\u6e21\u548c\u5e03\u7ebf\uff0c\u771f\u6b63\u63a8\u8fdb\u7684\u5267\u60c5\u90e8\u5206\u53ef\u80fd\u4e0d\u591a\uff0c\u8981\u7b49\u4e0b\u4e00\u5377\u3002\u5173\u4e8e\u4e0b\u4e00\u5377\uff0c\u540d\u5b57\u662f\u300a\u7b2c\u5341\u4e8c\u5377\u4e1c\u57df\u7bc7\u4e2d\u6069\u745f\u300b\uff0c\u4f3c\u4e4e\u6709\u70b9\u957f\u3002\u81f3\u4e8e\u66f4\u65b0\u65f6\u95f4\uff0c\u672a\u77e5\uff0c\u53cd\u6b63\u8fd8\u65e9\uff0c\u73b0\u5728\u8fd8\u57fa\u672c\u6ca1\u52a8\u7b14\u5462\uff0c\u6280\u80fdCD\u4e2d\u3002</p><p>\u4e8c\u3001\u5173\u4e8e\u4e0b\u4e00\u5377\u7684\u5267\u60c5\uff0c\u57fa\u672c\u4e0a\u770b\u4e86\u4e0a\u5377\uff0c\u4e5f\u5c31\u80fd\u731c\u5230\u540e\u9762\u7684\u5267\u60c5\u4e86\uff0c\u8be5\u57cb\u7684\u7ebf\u7d22\u90fd\u5df2\u7ecf\u57cb\u4e0b\uff0c\u8be5\u6697\u793a\u6216\u8005\u660e\u793a\u7684\u4e5f\u90fd\u6697\u793a\u660e\u793a\u4e86\uff0c\u5927\u81f4\u4e0a\u5c31\u662f\u743c\u6069\u4e00\u884c\u4eba\u627e\u5230\u4e86\u6d6e\u7a7a\u57ce\uff0c\u4e0d\u4e3e\u7684\u8bc5\u5492\u4e5f\u89e3\u9664\u4e86\uff0c\u5927\u5bb6\u90fd\u5f88\u5f00\u5fc3\uff0c\u8fc7\u4e0a\u4e86\u65e9\u4e0a\u4e09\u6b21\u665a\u4e0a\u56db\u6b21\u6216\u8005\u665a\u4e0a\u56db\u6b21\u65e9\u4e0a\u4e09\u6b21\u7684\u5feb\u4e50\u751f\u6d3b\uff0c\u7136\u540e\u2026\u2026\u7136\u540e\u743c\u6069\u548c\u5c0f\u6885\u5206\u624b\u4e86\u3002\u57fa\u672c\u5c31\u662f\u8fd9\u6837\u3002\u5f53\u7136\uff0c\u540e\u9762\u8fd8\u8981\u53bb\u5e2e\u795e\u59ec\u6241\u90a3\u5e2e\u57c3\u53ca\u795e\u4ec0\u4e48\u7684\uff0c\u5c31\u4e0d\u591a\u8bf4\u4e86\u3002</p><p>\u4e09\u3001\u6211\u5728\u82cf\u5dde\uff0c\u5177\u4f53\u5730\u5740\u5f53\u7136\u4e0d\u8bf4\uff0c\u5f88\u591a\u4eba\u8868\u793a\u8981\u7ed9\u6211\u5bc4\u53ef\u4e50\uff0c\u7136\u800c\u6211\u77e5\u9053\u4f60\u4eec\u5176\u5b9e\u662f\u60f3\u5bc4\u5200\u7247\u3002\u6211\u7684\u6b63\u5f0f\u804c\u4e1a\u5f53\u7136\u662f\u5c0f\u8bf4\u4f5c\u8005\uff0c\u517c\u804c\u505a\u5f8b\u5e08\u517b\u5bb6\u7cca\u53e3\uff0c\u6cd5\u5f8b\u54a8\u8be2\u6309\u5b57\u8282\u6536\u8d39\uff0c\u4ee5\u53ef\u4e50\u4f5c\u4e3a\u8ba1\u4ef7\u5355\u4f4d\u2014\u2014\u987a\u4fbf\u8bf4\u53e5\uff0c\u9664\u975e\u7279\u522b\u8bf4\u660e\uff0c\u5426\u5219\u6211\u8bf4\u7684\u53ef\u4e50\u9ed8\u8ba4\u662f\u767e\u4e8b\u7684\u3002\u5982\u679c\u6709\u559c\u6b22\u53ef\u53e3\u7684\u5f02\u7aef\uff0c\u54a8\u8be2\u8d39\u52a0\u500d\u3002</p><p>\u56db\u3001\u5982\u679c\u6709\u8c01\u5bf9\u5267\u60c5\u4e0d\u723d\u60f3\u6765\u6bb4\u6253\u6211\uff0c\u8bf7\u6765LOL\u7684\u76ae\u5c14\u7279\u6c83\u592b\u533a\uff0c\u6211\u7684ID\u662f\u91ce\u539f\u4e73\u4e4b\u718a\u3002</p><p>\u4e94\u3001\u5c0f\u8bf4\u5199\u5f97\u592a\u4e45\uff0c\u6709\u4e9b\u5730\u65b9\u96be\u514d\u504f\u79bb\u6700\u521d\u7684\u8bbe\u8ba1\uff0c\u9700\u8981\u6839\u636e\u5b9e\u9645\u60c5\u51b5\u505a\u4e00\u4e9b\u5fae\u8c03\u3002\u5148\u8bf4\u660e\u4e00\u4e2a\u786e\u5b9a\u8981\u8c03\u6574\u7684\u5730\u65b9\uff0c\u6700\u521d\u7684\u8bbe\u8ba1\u91cc\uff0c\u8299\u857e\u72c4\u548c\u8299\u8389\u5a05\u59d0\u59b9\u90fd\u662f\u76f8\u5bf9\u6bd4\u8f83\u91cd\u8981\u7684\u89d2\u8272\uff0c\u6700\u7ec8\u5206\u522b\u5360\u636e\u5341\u4e8c\u5bab\u4e2d\u7684\u4e00\u5e2d\uff0c\u7ed3\u679c\u5199\u7740\u5199\u7740\uff0c\u8299\u8389\u5a05\u8fb9\u7f18\u5316\u4e86\uff0c\u5c31\u76ee\u524d\u6765\u770b\uff0c\u5e94\u8be5\u4e5f\u6ca1\u6709\u673a\u4f1a\u518d\u589e\u52a0\u592a\u591a\u7684\u620f\u4efd\uff0c\u628a\u89d2\u8272\u63b0\u6b63\u56de\u6765\uff0c\u6240\u4ee5\u53ea\u80fd\u59d0\u59b9\u4e24\u4eba\u5408\u5e76\u5360\u636e\u4e00\u5e2d\u4e86\u3002\u81f3\u4e8e\u7a7a\u51fa\u7684\u4e00\u4e2a\u5e2d\u4f4d\uff0c\u9274\u4e8e\u4e0a\u4e00\u5377\uff0c\u8fc7\u5206\u72b9\u8c6b\uff0c\u4e0d\u6b62\u4e00\u4e2a\u8be5\u6740\u7684\u59b9\u5b50\u6ca1\u6740\uff0c\u65e2\u7136\u6ca1\u6740\uff0c\u53ea\u80fd\u5c3d\u91cf\u53d1\u6325\u4f5c\u7528\uff0c\u5c31\u4ece\u4e2d\u9009\u4e00\u4eba\u66ff\u8865\u4e86\uff0c\u76ee\u524d\u521d\u6b65\u8003\u8651\u662f\u4f20\u9053\u5deb\u5e08\u7ef4\u82e5\u62c9\u3002</p><p>\u516d\u3001\u622a\u6b62\u5230\u76ee\u524d\u66f4\u65b0\u4e3a\u6b62\uff0c\u672c\u4e66\u4e2d\u6240\u6709\u6709\u91cd\u8981\u5730\u4f4d\u7684\u59b9\u5b50\uff0c\u90fd\u5df2\u7ecf\u51fa\u573a\u8fc7\u4e86\uff0c\u540e\u7eed\u662f\u4e0d\u4f1a\u518d\u589e\u52a0\u7c7b\u4f3c\u7684\u6709\u91cd\u8981\u5206\u91cf\u548c\u620f\u4efd\u7684\u59b9\u5b50\u4e86\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u5341\u4e8c\u5bab\u5df2\u7ecf\u805a\u9f50\u4e86\u3002</p><p>\u4e03\u3001\u8bf4\u8fc7\u5f88\u591a\u904d\uff0c\u7136\u800c\u4e0d\u5f97\u4e0d\u518d\u8bf4\u4e00\u904d\uff1a\u5c0f\u6885\u548csaber\u6ca1\u4ec0\u4e48\u5173\u7cfb\uff0c\u9664\u4e86\u4e73\u91cf\u4f3c\u4e4e\u90fd\u4e0d\u5927\u4ee5\u5916\u3002\u51ef\u745f\u7433\u4f1a\u4e0d\u4f1a\u6b7b\uff0c\u8fd9\u4e2a\u95ee\u9898\u6211\u8fd8\u6ca1\u60f3\u597d\uff0c\u53cd\u6b63\u65e0\u8bba\u6b7b\u6d3b\uff0c\u90fd\u80af\u5b9a\u662f\u5c0f\u8bf4\u6700\u540e\u7684\u5267\u60c5\uff0c\u5148\u653e\u7740\u5427\u3002</p><p>\u516b\u3001\u5176\u4ed6\u7684\u2026\u2026\u4e00\u65f6\u60f3\u4e0d\u8d77\u6765\u8fd8\u6709\u4ec0\u4e48\u8981\u8bf4\u7684\uff0c\u90a3\u4e48\u5c31\u8fd9\u6837\u5427\u3002\u5982\u679c\u6709\u95ee\u9898\uff0c\u4e66\u8bc4\u533a\u7559\u8a00\u5373\u53ef\uff0c\u6211\u5076\u5c14\u4f1a\u8fc7\u6765\u770b\u770b\u3002</p><p>2016\u5e746\u670810\u65e5</p><p>-----======================================-----</p></article>"}}}]);
