(window.webpackJsonp=window.webpackJsonp||[]).push([[1767],{1790:function(p){p.exports={chapterTitle:"\u7b2c\u5341\u4e8c\u5377 \u4e1c\u57df\u7bc7\uff08\u4e2d\uff09\u6069\u745f",title:"\u5e8f\u7ae0",content:"<article><h1>\u7b2c\u5341\u4e8c\u5377 \u4e1c\u57df\u7bc7\uff08\u4e2d\uff09\u6069\u745f</h1><h2>\u5e8f\u7ae0</h2><p>\u4e1c\u65b9\u7684\u5929\u8fb9\u5fae\u5fae\u6cdb\u767d\uff0c\u671d\u9633\u6323\u8131\u4e86\u9ed1\u6697\u6df1\u6e0a\u7684\u675f\u7f1a\uff0c\u7f13\u7f13\u81ea\u5c71\u8109\u7684\u53e6\u4e00\u4fa7\u5347\u8d77\uff0c\u660e\u4eae\uff0c\u4f46\u4e0d\u70bd\u70c8\u3002\u6df7\u6742\u7740\u6e7f\u6da6\u6c34\u6c7d\u7684\u98ce\u4ece\u8fdc\u5904\u5439\u6765\uff0c\u6447\u6446\u7740\u6811\u7684\u679d\u53f6\uff0c\u53d1\u51fa\u7c0c\u7c0c\u7684\u58f0\u97f3\uff0c\u9ad8\u4f4e\u53c2\u5dee\uff0c\u5b9b\u5982\u541f\u5531\u3002</p><p>\u4e00\u53ea\u80d6\u4e4e\u4e4e\u7684\u677e\u9f20\u63a2\u5934\u63a2\u8111\u5730\u4ece\u6811\u6d1e\u91cc\u94bb\u51fa\u6765\uff0c\u5634\u91cc\u53fc\u7740\u4e00\u53ea\u699b\u5b50\uff0c\u5de6\u53f3\u5f20\u671b\u7247\u523b\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u6447\u6643\u7740\u6bdb\u8338\u8338\u7684\u5927\u5c3e\u5df4\uff0c\u5728\u6811\u68a2\u95f4\u5954\u8dd1\u3001\u8e66\u8df3\uff0c\u53bb\u627e\u5b83\u7684\u670b\u53cb\u73a9\u3002\u4e00\u4e0d\u7559\u795e\uff0c\u811a\u4e0b\u7684\u6811\u679d\u5ffd\u7136\u65ad\u88c2\uff0c\u677e\u9f20\u201c\u5431\u201d\u5730\u4e00\u58f0\u60ca\u53eb\uff0c\u4ece\u9ad8\u5904\u6454\u4e86\u4e0b\u6765\u3002</p><p>\u7136\u540e\u5b83\u88ab\u4e00\u53ea\u624b\u63a5\u4f4f\u4e86\u3002</p><p>\u60ca\u9b42\u672a\u5b9a\u7684\u677e\u9f20\u4e00\u628a\u62b1\u4f4f\u699b\u5b50\uff0c\u62ac\u8d77\u5934\uff0c\u7136\u540e\u5b83\u53c8\u5413\u4e86\u4e00\u8df3\u3002\u4e94\u5f20\u9762\u5b54\u6b63\u4ece\u4e0a\u65b9\u4fef\u89c6\u7740\u5b83\uff0c\u56f4\u6210\u4e00\u4e2a\u5706\u5708\uff0c\u6324\u5f97\u6ee1\u6ee1\u5f53\u5f53\uff0c\u51e0\u4e4e\u770b\u4e0d\u5230\u5929\u7a7a\u3002\u51e0\u79d2\u949f\u540e\uff0c\u5b83\u7ec8\u4e8e\u4ece\u4e2d\u5206\u8fa8\u51fa\u4e00\u4e2a\u719f\u6089\u7684\u8138\u5e9e\uff0c\u7ec6\u957f\u7684\u7709\u6bdb\uff0c\u5f2f\u5f2f\u7684\u773c\u775b\uff0c\u5728\u7709\u5fc3\u6709\u4e00\u9897\u5c0f\u5c0f\u7684\u7ea2\u70b9\uff0c\u50cf\u662f\u6591\uff0c\u53c8\u50cf\u662f\u80ce\u8bb0\u3002</p><p>\u201c\u65e9\u4e0a\u597d\uff0c\u73ab\u8389\uff0c\u201d\u677e\u9f20\u8bf4\uff0c\u628a\u699b\u5b50\u7d27\u7d27\u62b1\u5728\u6000\u91cc\uff0c\u201c\u4f60\u5403\u65e9\u9910\u4e86\u5417\uff1f\u201d</p><p>\u201c\u5b83\u4f1a\u8bf4\u8bdd\uff01\u201d</p><p>\u4e00\u7247\u60ca\u53f9\u58f0\u54cd\u8d77\uff0c\u663e\u7136\u6240\u6709\u4eba\u90fd\u5bf9\u8fd9\u53ea\u677e\u9f20\u53e3\u5410\u4eba\u8a00\u611f\u5230\u60ca\u5f02\uff0c\u9664\u4e86\u4e00\u4e2a\u4ee5\u5916\u3002\u201c\u65e9\u4e0a\u597d\uff0c\u5e03\u5170\u591a\uff0c\u201d\u73ab\u8389\u8bf4\uff0c\u201c\u4f60\u53c8\u53d8\u80d6\u4e86\u4e00\u5708\uff0c\u6216\u8bb8\u4f60\u8be5\u8003\u8651\u8003\u8651\u8282\u98df\u3002\u518d\u8fd9\u6837\u4e0b\u53bb\uff0c\u5f88\u5feb\u68ee\u6797\u91cc\u5c31\u6ca1\u6709\u4f60\u80fd\u7ad9\u5f97\u4f4f\u7684\u6811\u679d\u4e86\u3002\u201d</p><p>\u201c\u6211\u6ca1\u6709\u53d8\u80d6\uff0c\u201d\u677e\u9f20\u6297\u8bae\uff0c\u201c\u6211\u53ea\u662f......\u53ea\u662f\u6bdb\u53d1\u6709\u70b9\u84ec\u677e\uff0c\u4f60\u77e5\u9053\uff0c\u6700\u8fd1\u5929\u6c14\u592a\u5e72\u71e5\u4e86\u3002\u201d</p><p>\u201c\u6216\u8bb8\u5427\uff0c\u867d\u7136\u6211\u8bb0\u5f97\u524d\u5929\u624d\u521a\u521a\u4e0b\u8fc7\u96e8\uff0c\u201d\u73ab\u8389\u8bf4\uff0c\u201c\u4ecb\u7ecd\u4e00\u4e0b\uff0c\u5510\u6587\u3001\u83f2\u745e\u3001\u96f7\u7433\u65af\u3001\u84b2\u745e\u6728\uff0c\u4ed6\u4eec\u662f\u6211\u7684\u5b66\u751f\uff0c\u4e0a\u4e2a\u6708\u521a\u521a\u5165\u5b66\uff0c\u6211\u5e26\u4ed6\u4eec\u6765\u770b\u770b\u2018\u8fb9\u91ce\u2019\u3002\u5b69\u5b50\u4eec\uff0c\u8fd9\u662f\u5e03\u5170\u591a\u3002\u201d</p><p>\u201c\u73ab\u8389\uff0c\u5b83\u4f1a\u8bf4\u8bdd\u8036\uff01\u201d\u4e00\u4e2a\u7eff\u8272\u5934\u53d1\u7684\u7537\u5b69\u8bf4\uff0c\u677e\u9f20\u8bb0\u5f97\u4ed6\u7684\u540d\u5b57\u597d\u50cf\u662f\u96f7\u7433\u65af\u3002</p><p>\u201c\u6240\u6709\u677e\u9f20\u90fd\u4f1a\u8bf4\u8bdd\uff0c\u53ea\u662f\u4f60\u4eec\u542c\u4e0d\u61c2\u7f62\u4e86\uff1b\u800c\u6211\u80fd\u8bf4\u8ba9\u4f60\u4eec\u542c\u61c2\u7684\u8bdd\uff0c\u4ec5\u6b64\u800c\u5df2\uff0c\u201d\u5e03\u5170\u591a\u4e0d\u5c51\u5730\u6643\u4e86\u6643\u5927\u5c3e\u5df4\uff0c\u201c\u73ab\u8389\uff0c\u770b\u6765\u4f60\u7684\u5b66\u751f\u4eec\u4e0d\u662f\u5f88\u806a\u660e\uff0c\u4ed6\u4eec\u6709\u4e9b......\u5c11\u89c1\u591a\u602a\u3002\u201d</p><p>\u201c\u5e03\u5170\u591a\u662f\u7075\u777f\u677e\u9f20\uff0c\u201d\u73ab\u8389\u7b11\u7740\u89e3\u91ca\uff0c\u201c\u7075\u777f\u677e\u9f20\u4f1a\u8bf4\u6240\u6709\u68ee\u6797\u91cc\u7684\u8bed\u8a00\u3002\u201d</p><p>\u201c\u5b83\u770b\u8d77\u6765\u548c\u5176\u4ed6\u677e\u9f20\u6ca1\u4ec0\u4e48\u4e24\u6837\uff0c\u9664\u4e86\u66f4\u80d6\u4e00\u4e9b\u3002\u201d</p><p>\u201c\u84ec\u677e\uff01\u6211\u8bf4\u4e86\u662f\u84ec\u677e\uff01\u201d\u5e03\u5170\u591a\u751f\u6c14\u4e86\uff0c\u5728\u73ab\u8389\u7684\u624b\u638c\u4e0a\u8e66\u4e86\u4e24\u4e0b\uff0c\u5927\u58f0\u56b7\u56b7\u3002</p><p>\u201c\u5b83\u771f\u53ef\u7231\u3002\u201d\u96f7\u7433\u65af\u8bf4\uff0c\u4f38\u624b\u60f3\u53bb\u629a\u6478\u677e\u9f20\uff0c\u5374\u88ab\u73ab\u8389\u62e6\u4f4f\u4e86\u3002</p><p>\u201c\u5b69\u5b50\u4e0d\u80fd\u78b0\u89e6\u7075\u777f\u677e\u9f20\u7684\u6bdb\u53d1\uff0c\u4f1a\u505a\u5669\u68a6\u3002\u201d\u73ab\u8389\u8bf4\u7740\uff0c\u5c06\u5e03\u5170\u591a\u6258\u5230\u9762\u524d\uff0c\u201c\u5657\u201d\u5730\u5439\u4e86\u53e3\u6c14\uff0c\u677e\u9f20\u4e0b\u610f\u8bc6\u5730\u95ed\u4e0a\u773c\u775b\uff0c\u5ffd\u7136\u611f\u89c9\u81ea\u5df1\u50cf\u662f\u53d8\u8f7b\u4e86\u8bb8\u591a\uff0c\u5168\u8eab\u81a8\u80c0\u8d77\u6765\uff0c\u53d8\u6210\u4e86\u4e00\u4e2a\u5de8\u5927\u7684\u6bdb\u7403\uff0c\u6643\u60a0\u60a0\u5730\u5f80\u4e0a\u98d8\u8d77\uff0c\u56de\u5230\u4e86\u6811\u68a2\u4e0a\uff0c\u7136\u540e\u5657\u5730\u4e00\u58f0\uff0c\u6062\u590d\u4e86\u539f\u72b6\u3002</p><p>\u201c\u518d\u89c1\u4e86\uff0c\u73ab\u8389\uff0c\u795d\u4f60\u4eec\u73a9\u5f97\u6109\u5feb\uff0c\u201d\u677e\u9f20\u8bf4\uff0c\u8df3\u5230\u8fdc\u5904\u7684\u4e00\u6839\u6811\u679d\u4e0a\uff0c\u201c\u987a\u4fbf\u8bf4\u53e5\uff0c\u6700\u8fd1\u98ce\u91cc\u603b\u6709\u79cd\u5947\u602a\u7684\u5473\u9053\uff0c\u597d\u50cf\u6709\u4ec0\u4e48\u5927\u4e8b\u60c5\u5373\u5c06\u53d1\u751f\u3002\u201d</p><p>\u73ab\u8389\u6325\u4e86\u6325\u624b\uff0c\u770b\u7740\u5b83\u5f88\u5feb\u5730\u6d88\u5931\u5728\u6811\u53f6\u4e4b\u4e2d\u3002</p><p>\u201c\u5b83\u6700\u540e\u8bf4\u7684\u8bdd\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u4e00\u4e2a\u5b69\u5b50\u95ee\uff0c\u201c\u662f\u9884\u8a00\u5417\uff1f\u201d</p><p>\u201c\u522b\u5728\u610f\uff0c\u7075\u777f\u677e\u9f20\u80fd\u4ece\u98ce\u4e2d\u55c5\u5230\u2018\u672a\u6765\u2019\u7684\u5f81\u5146\uff0c\u51c6\u786e\u7387\u5f88\u9ad8\uff0c\u4f46\u5341\u6709\u516b\u4e5d\u4e0e\u6211\u4eec\u6beb\u65e0\u5173\u7cfb\u3002\u4e0a\u6b21\u5b83\u4e5f\u544a\u8bc9\u6211\u8bf4\uff0c\u6709\u91cd\u5927\u53d8\u6545\u5373\u5c06\u53d1\u751f\uff0c\u7ed3\u679c\u2014\u2014\u4f60\u4eec\u731c\u731c\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5b83\u5931\u604b\u4e86\u3002\u201d</p><p>\u201c......\u201d</p><p>\u201c\u2018\u8fb9\u91ce\u2019\u91cc\u4f4f\u7740\u5f88\u591a\u6709\u610f\u601d\u7684\u5c45\u6c11\uff0c\u4f60\u4eec\u4ee5\u540e\u4f1a\u6162\u6162\u8ba4\u8bc6\uff0c\u5b83\u4eec\u5927\u90e8\u5206\u90fd\u5f88\u53cb\u5584\uff0c\u5c31\u50cf\u5e03\u5170\u591a\uff0c\u4e0d\u8fc7\u4e5f\u6709\u4e00\u4e9b\u813e\u6c14\u4e0d\u662f\u5f88\u597d\uff0c\u4f60\u4eec\u8981\u591a\u52a0\u6ce8\u610f\uff0c\u201d\u73ab\u8389\u8bf4\uff0c\u201c\u4f60\u4eec\u65e2\u7136\u8fdb\u5165\u5b66\u6821\uff0c\u8fb9\u91ce\u5c31\u4e0d\u518d\u662f\u7981\u533a\uff0c\u4f46\u8fd9\u5e76\u4e0d\u610f\u5473\u7740\u4f60\u4eec\u53ef\u4ee5\u968f\u5fc3\u6240\u6b32\u5730\u4e71\u95ef\u4e71\u8dd1\uff0c\u5728\u6211\u5e26\u9886\u4f60\u4eec\u719f\u6089\u8fd9\u91cc\u4e4b\u524d\uff0c\u4e0d\u8bb8\u5355\u72ec\u8fdb\u5165\uff0c\u660e\u767d\u4e86\u5417\uff1f\u201d</p><p>\u201c\u660e\u767d\u3002\u201d</p><p>\u201c\u597d\u4e86\uff0c\u5b69\u5b50\u4eec\uff0c\u8ddf\u6211\u6765\uff0c\u63a5\u4e0b\u6765\u6211\u4eec\u5148\u53bb\u4e0a\u5165\u5b66\u7684\u7b2c\u4e00\u5802\u8bfe\uff0c\u7136\u540e\u53bb\u62dc\u8bbf\u63d0\u59c6\uff0c\u4ed6\u6700\u8fd1\u5f88\u9ad8\u5174\uff0c\u56e0\u4e3a\u59bb\u5b50\u524d\u51e0\u5929\u4e3a\u4ed6\u53c8\u751f\u4e86\u4e2a\u5c0f\u5973\u513f\u3002\u521a\u51fa\u751f\u7684\u718a\u5b9d\u5b9d\u559c\u6b22\u7761\u89c9\uff0c\u4f60\u4eec\u53ef\u4ee5\u629a\u6478\u5979\uff0c\u4f46\u4e0d\u80fd\u592a\u5927\u58f0\u8bf4\u8bdd\uff0c\u4f1a\u5435\u5979\u4f11\u606f\u3002\u201d</p><p>\u201c\u5662\uff01\u201d\u5b69\u5b50\u4eec\u6b22\u547c\u8d77\u6765\u3002</p><p>\u201c\u4e0d\u8fc7\u4e00\u5b9a\u8981\u8bb0\u4f4f\uff0c\u5f85\u4f1a\u4e0d\u8981\u4e71\u8dd1\uff0c\u4ed6\u7684\u5730\u4e0b\u6d1e\u7a9f\u975e\u5e38\u5927\uff0c\u91cc\u9762\u5c31\u50cf\u4e00\u5ea7\u8d85\u7ea7\u5927\u8ff7\u5bab\uff0c\u5982\u679c\u4f60\u8d70\u9519\u4e86\u8def\uff0c\u53ef\u80fd\u5c31\u6c38\u8fdc\u4e5f\u7ed5\u4e0d\u51fa\u6765\u4e86\u3002\u201d</p><p>\u201c\u77e5\u9053\u5566\uff01\u201d</p><p>\u4ed6\u4eec\u671d\u7740\u592a\u9633\u5347\u8d77\u7684\u65b9\u5411\u8d70\uff0c\u8d70\u51fa\u4e86\u6811\u6797\uff0c\u773c\u524d\u51fa\u73b0\u4e86\u4e00\u7247\u5e73\u5766\u7684\u8349\u5730\u3002\u5728\u8349\u5730\u4e0a\uff0c\u77d7\u7acb\u7740\u4e00\u6839\u6697\u7070\u8272\u7684\u5706\u67f1\uff0c\u5b83\u5927\u7ea6\u6709\u4e24\u4e2a\u4eba\u90a3\u4e48\u9ad8\uff0c\u53cc\u81c2\u5408\u62e2\u582a\u582a\u80fd\u62b1\u4f4f\uff0c\u8d70\u8fd1\u4e00\u4e9b\uff0c\u80fd\u770b\u89c1\u8868\u9762\u6709\u65e0\u6570\u6df1\u6df1\u6d45\u6d45\u7684\u5212\u75d5\uff0c\u6a2a\u4e03\u7ad6\u516b\uff0c\u4f3c\u4e4e\u8fd8\u6709\u56fe\u6848\uff0c\u4f46\u5728\u957f\u5e74\u7d2f\u6708\u7684\u98ce\u5439\u96e8\u6dcb\u4e4b\u4e0b\uff0c\u5df2\u7ecf\u6591\u9a73\u4e0d\u6e05\u4e86\u3002</p><p>\u73ab\u8389\u4f4e\u4e0b\u5934\uff0c\u53cc\u624b\u5728\u80f8\u524d\u4ea4\u9519\uff0c\u6c89\u9ed8\u5730\u671d\u7740\u67f1\u5b50\u884c\u793c\uff0c\u56db\u4e2a\u5b69\u5b50\u8ddf\u7740\u7167\u505a\u3002</p><p>\u201c\u8fd9\u5c31\u662f\u8352\u91ce\u4e4b\u67f1\u5417\uff1f\u201d\u4e00\u4e2a\u5b69\u5b50\u8feb\u4e0d\u53ca\u5f85\u5730\u95ee\u3002</p><p>\u201c\u975e\u5e38\u6b63\u786e\uff0c\u5510\u6587\uff0c\u8fd9\u662f\u8352\u91ce\u4e4b\u67f1\uff0c\u6211\u4eec\u7684\u5b88\u62a4\u4e03\u67f1\u4e4b\u4e00\uff0c\u201d\u73ab\u8389\u8bf4\uff0c\u201c\u90a3\u4e48\uff0c\u6709\u8c01\u77e5\u9053\u4ec0\u4e48\u662f\u2018\u4e03\u67f1\u2019\u5417\uff1f\u201d</p><p>\u201c\u6211\u77e5\u9053\uff0c\u201d\u5510\u6587\u62a2\u7740\u56de\u7b54\uff0c\u201c\u4e03\u67f1\u662f\u6211\u4eec\u7956\u5148\u9057\u7559\u4e0b\u6765\u7684\u5723\u7269\uff0c\u5b83\u4eec\u4fdd\u62a4\u7740\u6211\u4eec\u6240\u5c45\u4f4f\u7684\u2018\u7075\u58e4\u2019\uff0c\u4e0d\u53d7\u5916\u754c\u4fb5\u88ad\u3002\u201d</p><p>\u201c\u6211\u542c\u8bf4\u4e03\u67f1\u662f\u67ef\u745e\u9686\u5927\u795e\u6240\u8d50\uff0c\u4e0a\u9762\u7684\u6bcf\u4e00\u9053\u5212\u75d5\u4ee3\u8868\u7740\u67ef\u745e\u9686\u65a9\u6740\u7684\u4e00\u4e2a\u517d\u4eba\u90aa\u795e\u3002\u201d\u53e6\u4e00\u4e2a\u5b69\u5b50\u8bf4\u3002</p><p>\u201c\u600e\u4e48\u53ef\u80fd\uff0c\u201d\u5510\u6587\u55e4\u7b11\uff0c\u201c\u8fd9\u662f\u8c23\u4f20\u5566\uff0c\u6211\u54e5\u54e5\u6570\u8fc7\uff0c\u4e03\u67f1\u4e0a\u4e00\u5171\u6709\u4e94\u5343\u4e94\u767e\u591a\u9053\u5212\u75d5\uff0c\u517d\u4eba\u90aa\u795e\u6709\u90a3\u4e48\u591a\u5417\uff1f\u201d</p><p>\u201c\u636e\u8bf4\u517d\u4eba\u4e00\u751f\u5c31\u662f\u4e00\u7a9d\uff0c\u548c\u5154\u5b50\u4e00\u6837\u3002\u201d</p><p>\u201c\u90a3\u662f\u517d\u4eba\uff0c\u4e0d\u662f\u517d\u4eba\u90aa\u795e\u5566\uff0c\u795e\u600e\u4e48\u53ef\u80fd\u90a3\u4e48\u591a\u3002\u201d</p><p>\u201c\u548c\u517d\u4eba\u6ca1\u5173\u7cfb\uff0c\u201d\u96f7\u7433\u65af\u52a0\u5165\u8ba8\u8bba\uff0c\u201c\u6211\u542c\u6bcd\u4eb2\u8bf4\uff0c\u4e03\u67f1\u662f\u838e\u7f55\u59ae\u965b\u4e0b\u5728\u6253\u8d25\u963f\u841d\u8bd7\u4e4b\u540e\uff0c\u4ee5\u4e16\u754c\u7b2c\u4e00\u4e2a\u9ed1\u591c\u7684\u6708\u5149\u3001\u5927\u5730\u7b2c\u4e00\u6761\u6cb3\u6d41\u7684\u6cb3\u6c34\uff0c\u592a\u9633\u55b7\u51fa\u7684\u7b2c\u4e00\u6735\u706b\u7130\uff0c\u4e09\u8005\u878d\u5408\u5851\u6210\uff0c\u5c06\u5815\u843d\u8005\u5c01\u5370\u5728\u5730\u5e95\u6df1\u5904\uff0c\u5e87\u4f51\u6708\u7cbe\u7075\u6c38\u8fdc\u8fdc\u79bb\u9ed1\u6697\u4e0e\u5669\u68a6\u3002\u201d</p><p>\u201c\u5582\uff0c\u96f7\u7433\u65af\uff0c\u4f60\u662f\u8bf4\u4e03\u67f1\u53ea\u5e87\u4f51\u4f60\u4eec\u6708\u7cbe\u7075\uff0c\u4e0d\u4fdd\u62a4\u6211\u4eec\u6728\u7cbe\u7075\u5566\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u662f\u90a3\u4e2a\u610f\u601d\u3002\u201d</p><p>\u201c\u4f60\u5c31\u662f\uff01\u201d</p><p>\u201c\u5b89\u9759\uff01\u201d\u73ab\u8389\u963b\u6b62\u4e86\u5b69\u5b50\u4eec\u7684\u4e89\u6267\uff0c\u201c\u5927\u5bb6\u8bf4\u5f97\u90fd\u5f88\u597d\uff0c\u4f46\u90fd\u4e0d\u662f\u5f88\u51c6\u786e\u3002\u8fd9\u5c31\u662f\u6211\u8981\u7ed9\u4f60\u4eec\u4e0a\u7684\u7b2c\u4e00\u5802\u8bfe\uff1a\u4e03\u67f1\u7684\u7531\u6765\u3002\u201d</p><p>\u5979\u7ed5\u7740\u67f1\u5b50\u7f13\u7f13\u8f6c\u5708\uff0c\u5b69\u5b50\u4eec\u4e00\u4e2a\u63a5\u4e00\u4e2a\u5730\u8ddf\u5728\u8eab\u540e\uff0c\u201c\u5728\u5f88\u4e45\u5f88\u4e45\u4ee5\u524d\uff0c\u67ef\u745e\u9686\u521b\u9020\u4e86\u7cbe\u7075\uff0c\u5728\u8bf8\u795e\u7684\u6307\u5f15\u4e4b\u4e0b\uff0c\u6211\u4eec\u6253\u8d25\u4e86\u90aa\u9b54\u3001\u5de8\u9f99\u548c\u517d\u4eba\uff0c\u4ee5\u53ca\u90a3\u4e9b\u9003\u5165\u5730\u5e95\u7684\u5815\u843d\u8005\uff0c\u5efa\u7acb\u8d77\u4f1f\u5927\u7684\u7cbe\u7075\u5e1d\u56fd\u3002\u540e\u6765\uff0c\u5728\u6f2b\u957f\u7684\u5c81\u6708\u4e2d\uff0c\u7cbe\u7075\u5e1d\u56fd\u5206\u88c2\u6210\u516d\u5757\uff0c\u5206\u522b\u7531\u4e00\u4f4d\u7cbe\u7075\u738b\u6267\u638c\uff0c\u5176\u4e2d\u7684\u4e1c\u65b9\u4e4b\u56fd\uff0c\u83ab\u5c9a\uff0c\u5c31\u662f\u8fd9\u5ea7\u8ff7\u5931\u68ee\u6797\u3002\u201d</p><p>\u201c\u548c\u5e73\u6301\u7eed\u5f97\u592a\u4e45\uff0c\u8ba9\u6211\u4eec\u5ffd\u7565\u4e86\u65b0\u7684\u5a01\u80c1\u6b63\u5728\u8eab\u8fb9\u6084\u7136\u5d1b\u8d77\uff0c\u5f53\u604d\u7136\u9192\u609f\u65f6\uff0c\u4e00\u5207\u4e3a\u65f6\u5df2\u665a\u3002\u6700\u540e\uff0c\u516d\u4f4d\u7cbe\u7075\u738b\u5728\u81f3\u9ad8\u68ee\u6797\u8fbe\u6210\u51b3\u8bae\uff0c\u6452\u5f03\u6210\u89c1\uff0c\u6d88\u5f2d\u5206\u6b67\uff0c\u91cd\u65b0\u878d\u5408\u4e3a\u4e00\u3002\u968f\u540e\uff0c\u738b\u5ead\u9881\u53d1\u4e86\u2018\u5927\u64a4\u9000\u2019\u7684\u547d\u4ee4\uff0c\u79bb\u5f00\u5927\u9646\uff0c\u8fc1\u5f80\u6c38\u805a\u5c9b\u3002\u201d</p><p>\u201c\u64a4\u9000\uff0c\u5e76\u975e\u662f\u9003\u4ea1\uff0c\u738b\u5ead\u51b3\u5b9a\u7559\u4e0b\u54e8\u6240\uff0c\u7531\u52c7\u6562\u7684\u81ea\u613f\u8005\u9a7b\u5b88\u5176\u4e2d\uff0c\u76d1\u89c6\u7740\u9ed1\u6697\uff0c\u8fce\u63a5\u6709\u671d\u4e00\u65e5\u7684\u2018\u5927\u56de\u5f52\u2019\u3002\u6211\u4eec\u7684\u7956\u5148\u56e0\u6b64\u521b\u9020\u4e86\u4e03\u6839\u8574\u542b\u65e0\u6bd4\u9b54\u529b\u7684\u652f\u67f1\uff0c\u7528\u4ee5\u652f\u6491\u54e8\u6240\uff0c\u963b\u6321\u90aa\u6076\uff0c\u8fd9\u5c31\u662f\u2018\u4e03\u67f1\u2019\u7684\u7531\u6765\u3002\u201d</p><p>\u201c\u611f\u8c22\u4e03\u67f1\uff0c\u8ba9\u6211\u4eec\u80fd\u591f\u8fdc\u79bb\u9ed1\u6697\u51e1\u95f4\u7684\u4fb5\u6270\uff0c\u5b83\u5b88\u62a4\u7740\u6211\u4eec\uff0c\u76f4\u5230\u8bf8\u795e\u91cd\u65b0\u964d\u4e34\u51e1\u95f4\u3002\u201d</p><p>\u201c\u4e03\u67f1\u4e5f\u56da\u7981\u7740\u6211\u4eec\uff0c\u76f4\u5230\u4e16\u754c\u672b\u65e5\u3002\u201d\u4e00\u4e2a\u7ec6\u5c0f\u7684\u58f0\u97f3\u8bf4\u3002</p><p>\u73ab\u8389\u607c\u6012\u5730\u505c\u4f4f\u811a\u6b65\uff0c\u8f6c\u8fc7\u8138\uff0c\u76ee\u5149\u4ece\u56db\u4e2a\u5b69\u5b50\u7684\u8138\u4e0a\u626b\u8fc7\uff0c\u540e\u8005\u5168\u90fd\u4f4e\u4e0b\u5934\u3002\u201c\u521a\u624d\u90a3\u53e5\u8bdd\u662f\u8c01\u8bf4\u7684\uff1f\u201d</p><p>\u6ca1\u6709\u4eba\u56de\u7b54\u3002</p><p>\u201c\u4e03\u67f1\u662f\u6211\u4eec\u7684\u7956\u5148\u6240\u9057\uff0c\u8ba9\u7075\u58e4\u4e0e\u9ed1\u6697\u51e1\u95f4\u9694\u7edd\uff0c\u4fdd\u62a4\u6211\u4eec\u4e0d\u53d7\u90aa\u6076\u7684\u4fb5\u88ad\u4e0e\u4f24\u5bb3\u3002\u5b83\u4e0d\u662f\u675f\u7f1a\uff0c\u800c\u662f\u5e87\u4f51\uff0c\u6ca1\u6709\u4e03\u67f1\uff0c\u5c31\u6ca1\u6709\u7075\u58e4\uff0c\u6ca1\u6709\u6211\u4eec\u3002\u4f60\u4eec\u8981\u6c38\u8fdc\u94ed\u8bb0\u8fd9\u4e00\u70b9\uff0c\u201d\u73ab\u8389\u7a0d\u7a0d\u653e\u7f13\u4e86\u53e3\u6c14\uff0c\u201c\u6211\u4e0d\u5e0c\u671b\u4ee5\u540e\u518d\u542c\u5230\u8fd9\u79cd\u8352\u8c2c\u7684\u8bf4\u6cd5\u3002\u201d</p><p>\u201c\u4f46\u6211\u4eec\u786e\u5b9e\u6ca1\u529e\u6cd5\u8d70\u51fa\u7075\u58e4\uff0c\u201d\u4e00\u4e2a\u5b69\u5b50\u9f13\u8d77\u52c7\u6c14\u8bf4\uff0c\u201c\u6211\u60f3\u53bb\u5916\u9762\u770b\u770b\u3002\u201d</p><p>\u201c\u5916\u9762\u662f\u9ed1\u6697\u7684\u51e1\u95f4\uff0c\u5145\u6ee1\u4e86\u6740\u622e\u3001\u7834\u574f\u3001\u70c8\u706b\u548c\u6d53\u70df\uff0c\u76f8\u4fe1\u6211\uff0c\u83f2\u745e\uff0c\u4f60\u4e0d\u4f1a\u559c\u6b22\u7684\u3002\u201d</p><p>\u201c\u4f46\u4f60\u4e5f\u6ca1\u51fa\u53bb\u8fc7\uff0c\u4e0d\u662f\u5417\uff0c\u73ab\u8389\uff1f\u201d\u96f7\u7433\u65af\u8bf4\uff0c\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d</p><p>\u201c\u8fd9\u662f\u5e38\u8bc6\uff0c\u5e76\u4e0d\u662f\u6240\u6709\u4e1c\u897f\u90fd\u8981\u4eb2\u773c\u770b\u89c1\u624d\u80fd\u77e5\u9053\uff0c\u5c31\u50cf\u4f60\u770b\u4e0d\u89c1\u7a7a\u6c14\uff0c\u4f46\u5b83\u65e0\u5904\u4e0d\u5728\uff0c\u201d\u73ab\u8389\u7ec6\u957f\u7684\u7709\u6bdb\u62e7\u8d77\u6765\uff0c\u663e\u793a\u51fa\u5979\u7684\u5fc3\u60c5\u5df2\u7ecf\u5f88\u4e0d\u60a6\uff0c\u201c\u51e1\u95f4\u6709\u4eba\u7c7b\uff0c\u5b83\u4eec\u5c31\u662f\u4e00\u5207\u707e\u7978\u7684\u6839\u6e90\u3002\u201d</p><p>\u201c\u4eba\u7c7b\uff1f\u201d</p><p>\u201c\u542c\u8bf4\u662f\u4e00\u79cd\u5f88\u574f\u7684\u751f\u7269\uff0c\u201d\u5510\u6587\u8bf4\uff0c\u201c\u6211\u5728\u54e5\u54e5\u7684\u4e66\u4e0a\u770b\u8fc7\u753b\u50cf\uff0c\u957f\u5f97\u548c\u6211\u4eec......\u6709\u70b9\u50cf\u3002\u201d</p><p>\u201c\u4eba\u7c7b\u662f\u6df1\u6e0a\u90aa\u9b54\u6a21\u4eff\u6211\u4eec\u800c\u521b\u9020\u51fa\u6765\u7684\u62d9\u52a3\u8d5d\u54c1\uff0c\u201d\u73ab\u8389\u8bf4\uff0c\u201c\u4ed6\u4eec\u5927\u3001\u7c97\u7cd9\u3001\u8033\u6735\u4e0d\u5c16\u3001\u773c\u795e\u4e0d\u597d\uff0c\u76ae\u80a4\u6697\u6de1\uff0c\u5bff\u547d\u77ed\u6682\uff0c\u6709\u79cd\u79cd\u90aa\u6076\u79c9\u6027\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u90aa\u6076\u79c9\u6027\uff1f\u201d\u5b69\u5b50\u597d\u5947\u5730\u95ee\u3002</p><p>\u201c\u4eba\u7c7b\u8d2a\u5a6a\u3001\u6b3a\u8bc8\uff0c\u70ed\u8877\u81ea\u76f8\u6b8b\u6740\uff0c\u6bcf\u5929\u90fd\u5728\u6b3a\u9a97\u4e0e\u80cc\u53db\u4e2d\u5ea6\u8fc7\uff0c\u5373\u4fbf\u662f\u7236\u6bcd\u4e5f\u4e0d\u4f1a\u771f\u8bda\u5730\u559c\u6b22\u81ea\u5df1\u7684\u5b50\u5973\uff0c\u5373\u4fbf\u662f\u604b\u4eba\u4e5f\u4e0d\u4f1a\u5766\u8bda\u76f8\u5bf9\u3002\u4ed6\u4eec\u7ec8\u65e5\u5954\u6ce2\uff0c\u4e0d\u660e\u767d\u751f\u547d\u7684\u610f\u4e49\uff0c\u4ed6\u4eec\u7f3a\u4e4f\u7075\u6027\uff0c\u65e0\u6cd5\u4e0e\u4e07\u7269\u6c9f\u901a\uff0c\u4ed6\u4eec\u4ece\u77f3\u5934\u91cc\u63d0\u70bc\u51fa\u91d1\u5c5e\uff0c\u7528\u70c8\u7130\u548c\u5deb\u672f\u953b\u9020\u6210\u4e11\u964b\u7684\u673a\u68b0\uff0c\u6574\u7247\u6574\u7247\u5730\u780d\u4f10\u68ee\u6797\uff0c\u5c06\u7f8e\u597d\u7684\u81ea\u7136\u53d8\u6210\u8352\u829c\u4e4b\u5730\u3002\u201d</p><p>\u201c\u54c7\u54e6\uff01\u201d\u5b69\u5b50\u4eec\u4e00\u8d77\u53d1\u51fa\u60ca\u6050\u7684\u58f0\u97f3\uff0c\u201c\u4eba\u7c7b\u5c45\u7136\u780d\u4f10\u68ee\u6797\uff1f\u201d</p><p>\u201c\u4e00\u70b9\u6ca1\u9519\uff0c\u4eba\u7c7b\u7684\u6700\u90aa\u6076\u4e4b\u5904\u5c31\u5728\u4e8e\u6b64\uff0c\u4ed6\u4eec\u65e0\u6cd5\u4e0e\u5176\u4ed6\u751f\u547d\u5171\u5b58\u3002\u4f60\u4eec\u770b\uff0c\u5728\u8fd9\u7247\u68ee\u6797\u91cc\uff0c\u6709\u8349\uff0c\u6709\u82b1\uff0c\u6709\u6811\uff0c\u6709\u74e2\u866b\uff0c\u6709\u87b3\u8782\uff0c\u6709\u677e\u9f20\uff0c\u6709\u91ce\u5154\uff0c\u6709\u72d0\u72f8\u548c\u72fc\uff0c\u6709\u86c7\u548c\u718a\uff0c\u8fd8\u6709\u6211\u4eec\u7cbe\u7075\uff0c\u74e2\u866b\u4f1a\u5403\u8349\uff0c\u86c7\u4f1a\u5403\u677e\u9f20\uff0c\u72fc\u4f1a\u5403\u91ce\u5154\uff0c\u6211\u4eec\u6709\u65f6\u5019\u4e5f\u4f1a\u5403\u52a8\u7269\uff0c\u4f46\u68ee\u6797\u662f\u6211\u4eec\u5171\u540c\u7684\u5bb6\uff0c\u5343\u4e07\u5e74\u6765\uff0c\u6c38\u8fdc\u5982\u6b64\u3002\u4eba\u7c7b\u5374\u4f1a\u5c06\u68ee\u6797\u6bc1\u6389\uff0c\u8ba9\u6240\u6709\u7684\u751f\u547d\u6d41\u79bb\u5931\u6240\uff0c\u8ba9\u81ea\u7136\u7684\u7eff\u8272\u88ab\u9ed1\u4e4e\u4e4e\u7684\u5ca9\u77f3\u3001\u51b7\u51b0\u51b0\u7684\u91d1\u5c5e\u548c\u7ec8\u65e5\u71c3\u70e7\u4e0d\u7184\u7684\u70c8\u706b\u6240\u66ff\u4ee3\uff0c\u5f7b\u5e95\u6bc1\u706d\u8fd9\u4e2a\u4e16\u754c\u3002\u6240\u4ee5\uff0c\u5b69\u5b50\u4eec\uff0c\u4e00\u5b9a\u8981\u8c28\u8bb0\uff0c\u4eba\u7c7b\u662f\u6700\u53ef\u6015\u7684\u751f\u7269\uff0c\u4ed6\u4eec\u6bd4\u90aa\u9b54\u66f4\u72e1\u8bc8\uff0c\u6bd4\u517d\u4eba\u66f4\u51f6\u6b8b\uff0c\u5c31\u7b97\u662f\u90a3\u4e9b\u9ed1\u76ae\u80a4\u7684\u5815\u843d\u8005\uff0c\u6bd4\u8d77\u4ed6\u4eec\u4e5f\u8981\u7a0d\u900a\u4e00\u7b79\u3002\u201d</p><p>\u201c\u771f\u53ef\u6015\uff01\u201d</p><p>\u201c\u4e0d\u7528\u62c5\u5fc3\uff0c\u201d\u73ab\u8389\u8d76\u5feb\u5b89\u6170\uff0c\u201c\u5c31\u50cf\u6211\u524d\u9762\u8bf4\u7684\uff0c\u4e03\u67f1\u4fdd\u62a4\u7740\u6211\u4eec\u3002\u4efb\u4f55\u4eba\u7c7b\u90fd\u4e0d\u53ef\u80fd\u8fdb\u5165\u7075\u58e4\uff0c\u4ed6\u4eec\u8fde\u9760\u8fd1\u8fd9\u91cc\u90fd\u4e0d\u53ef\u80fd\u3002\u5982\u679c\u4ed6\u4eec\u8d70\u8fc7\u6765\uff0c\u90a3\u4e48\u8def\u4e0a\u7684\u8346\u68d8\u4f1a\u523a\u7834\u4ed6\u4eec\u7684\u811a\uff1b\u5982\u679c\u4ed6\u4eec\u98de\u8fc7\u6765\uff0c\u90a3\u4e48\u5929\u7a7a\u4e2d\u7684\u9e1f\u4f1a\u5544\u7834\u4ed6\u4eec\u7684\u8111\u888b\uff1b\u5c31\u7b97\u4ed6\u4eec\u4fa5\u5e78\u6765\u5230\u9644\u8fd1\uff0c\u4e5f\u4f1a\u4ec0\u4e48\u90fd\u770b\u4e0d\u89c1\u3002\u603b\u800c\u8a00\u4e4b\uff0c\u8fd9\u91cc\u7edd\u5bf9\u5b89\u5168\u3002\u597d\u4e86\uff0c\u5b69\u5b50\u4eec\uff0c\u4eca\u5929\u7684\u5386\u53f2\u8bfe\u4e0a\u5b8c\u4e86\uff0c\u6211\u4eec\u53bb\u62dc\u8bbf\u63d0\u59c6\u5427\uff0c\u8bb0\u5f97\u6211\u7684\u8bdd\uff0c\u522b\u5927\u58f0\u8bf4\u8bdd\uff0c\u522b\u4e71\u8dd1\uff0c\u8bb0\u4f4f\u4e86\u5417\uff1f\u201d</p><p>\u201c\u8bb0\u4f4f\u4e86\uff01\u201d\u5b69\u5b50\u4eec\u9f50\u58f0\u56de\u7b54\u3002</p><p>\u4ed6\u4eec\u79bb\u5f00\u4e86\u8352\u91ce\u4e4b\u67f1\uff0c\u6cbf\u7740\u592a\u9633\u5347\u8d77\u7684\u65b9\u5411\u7ee7\u7eed\u8d70\uff0c\u521a\u521a\u8d70\u51fa\u4e0d\u5230\u4e24\u5206\u949f\uff0c\u5ffd\u7136\u811a\u4e0b\u7684\u5927\u5730\u731b\u7136\u9707\u98a4\u4e86\u4e00\u4e0b\u3002\u73ab\u8389\u5413\u4e86\u4e00\u8df3\uff0c\u8d76\u7d27\u505c\u4f4f\u811a\u6b65\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u89c2\u5bdf\u56db\u5468\uff0c\u7136\u800c\u8fc7\u4e86\u534a\u54cd\uff0c\u5374\u4e5f\u6ca1\u6709\u53d1\u73b0\u4ec0\u4e48\u5f02\u6837\u3002</p><p>\u6b63\u5f53\u5979\u6000\u7591\u662f\u5426\u9519\u89c9\uff0c\u9ad8\u7a7a\u4e2d\u4f20\u6765\u4e00\u58f0\u9e23\u53eb\uff0c\u9752\u84dd\u8272\u7684\u9e70\u98de\u63a0\u8fc7\u6765\uff0c\u5728\u5373\u5c06\u843d\u5730\u65f6\u626d\u66f2\u6da8\u5927\uff0c\u53d8\u6210\u4e00\u4f4d\u7070\u8272\u957f\u53d1\u7684\u5973\u5b50\uff0c\u624b\u4e2d\u63e1\u7740\u4e00\u67c4\u957f\u957f\u7684\u3001\u7f20\u7ed5\u7740\u9752\u8272\u8513\u85e4\u7684\u6728\u6756\u3002\u201c\u4f60\u4eec\u6ca1\u9047\u5230\u4ec0\u4e48\u5371\u9669\u5427\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u6ca1\u6709\uff0c\u5c31\u662f\u521a\u521a\u5730\u9762\u9707\u52a8\u4e86\u4e00\u4e0b\uff0c\u201d\u73ab\u8389\u8bf4\uff0c\u201c\u51fa\u4ec0\u4e48\u4e8b\u4e86\u5417\uff0c\u4f0a\u8299\uff1f\u201d</p><p>\u201c\u4e0d\u592a\u6e05\u695a\uff0c\u542c\u8bf4\u662f\u6709\u5815\u843d\u8005\u5165\u4fb5\u3002\u201d</p><p>\u201c\u5815\u843d\u8005\uff1f\u201d\u73ab\u8389\u6123\u4e86\u4e00\u4e0b\uff0c\u7136\u540e\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u600e\u4e48\u53ef\u80fd\uff0c\u4ed6\u4eec\u4e0d\u662f\u5df2\u7ecf\u88ab\u8bf8\u795e\u5c01\u5370\u5728\u5730\u5e95\u6df1\u5904\u4e86\u5417\uff1f\u201d</p><p>\u201c\u8c01\u77e5\u9053\uff0c\u6211\u4e5f\u662f\u521a\u521a\u5f97\u5230\u6d88\u606f\uff0c\u6821\u957f\u6015\u4f60\u4eec\u51fa\u4e8b\uff0c\u8ba9\u6211\u6765\u63a5\u5e94\u3002\u603b\u4e4b\u73b0\u5728\u5916\u9762\u4e0d\u5b89\u5168\uff0c\u5148\u628a\u5b69\u5b50\u4eec\u5e26\u56de\u5b66\u6821\u518d\u8bf4\uff0c\u201d\u7070\u53d1\u5973\u5b50\u4f0a\u8299\u6325\u4e86\u6325\u624b\u4e2d\u7684\u6728\u6756\uff0c\u5c06\u56db\u4e2a\u5b69\u5b50\u53d8\u6210\u4e86\u56db\u53ea\u6591\u9e20\uff0c\u653e\u8fdb\u81ea\u5df1\u7684\u53e3\u888b\uff0c\u65cb\u98ce\u968f\u5373\u4ece\u8db3\u5e95\u5347\u8d77\uff0c\u5c06\u5979\u548c\u73ab\u8389\u90fd\u5305\u88f9\u8d77\u6765\uff0c\u201c\u8d70\u5427\u3002\u201d</p><p>\u7247\u523b\u4e4b\u540e\uff0c\u73ab\u8389\u56de\u5230\u4e86\u5b66\u6821\uff0c\u6240\u6709\u5b66\u751f\u90fd\u88ab\u96c6\u4e2d\u8d77\u6765\uff0c\u6218\u4e89\u53e4\u6811\u5df2\u7ecf\u4ece\u6c89\u7761\u4e2d\u5524\u9192\uff0c\u6559\u6388\u4eec\u5168\u526f\u6b66\u88c5\uff0c\u4e00\u4e2a\u4e2a\u795e\u60c5\u7d27\u5f20\uff0c\u60f6\u6050\u4e0d\u5b89\u4e2d\u53c8\u6709\u4e00\u4e9b\u597d\u5947\u3002\u6bd5\u7adf\uff0c\u5373\u4fbf\u662f\u6700\u5e74\u957f\u8005\uff0c\u4e5f\u6ca1\u6709\u8c01\u771f\u7684\u89c1\u8fc7\u5815\u843d\u8005\uff0c\u90a3\u662f\u53ea\u5b58\u5728\u4e8e\u201c\u4f20\u8bf4\u201d\u4e2d\u7684\u654c\u4eba\u3002</p><p>\u8b66\u6212\u4e00\u76f4\u6301\u7eed\u4e86\u4e00\u6574\u5929\uff0c\u7136\u540e\u88ab\u89e3\u9664\u4e86\uff0c\u6700\u65b0\u7684\u6d88\u606f\u4f20\u6765\uff0c\u73ab\u8389\u5f97\u77e5\u4e86\u5927\u81f4\u60c5\u5f62\uff1a\u4e00\u7fa4\u5815\u843d\u8005\u7a81\u7136\u51fa\u73b0\uff0c\u6570\u91cf\u5927\u7ea6\u6709\u56db\u5341\u4f59\u540d\uff0c\u9996\u9886\u662f\u4e00\u4f4d\u5973\u6027\uff0c\u4ed6\u4eec\u5f88\u5feb\u88ab\u53d1\u73b0\uff0c\u7136\u540e\u88ab\u8f7b\u6613\u5730\u5236\u670d\u4e86\uff0c\u6574\u4e2a\u8fc7\u7a0b\u4e0d\u8d85\u8fc7\u4e00\u5c0f\u65f6\uff0c\u51b2\u7a81\u5e76\u4e0d\u6fc0\u70c8\uff0c\u6ca1\u6709\u9020\u6210\u4efb\u4f55\u6b7b\u4ea1\uff0c\u53ea\u6709\u51e0\u4e2a\u8f7b\u4f24\u3002\u4fd8\u864f\u4eec\u73b0\u5728\u6b63\u56da\u7981\u5728\u76d1\u72f1\u91cc\uff0c\u7b49\u5f85\u7740\u957f\u8001\u4eec\u5546\u8bae\u5982\u4f55\u5904\u7f6e\u3002</p><p>\u201c\u771f\u5947\u602a\u3002\u201d\u4f0a\u8299\u8d70\u8fdb\u73ab\u8389\u7684\u529e\u516c\u5ba4\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u8fd9\u4e9b\u5815\u843d\u8005\u4f3c\u4e4e\u5e76\u4e0d\u662f\u9488\u5bf9\u6211\u4eec\u800c\u6765\uff0c\u201d\u4f0a\u8299\u8bf4\uff0c\u5979\u7684\u6d88\u606f\u6bd4\u73ab\u8389\u7075\u901a\u5f97\u591a\uff0c\u201c\u6211\u542c\u514b\u91cc\u65af\u8bf4\uff0c\u4ed6\u4eec\u5e76\u6ca1\u6709\u4e3b\u52a8\u53d1\u8d77\u653b\u51fb\uff0c\u751a\u81f3\u90fd\u6ca1\u6709\u575a\u51b3\u62b5\u6297\uff0c\u800c\u4e14\u8fd8\u643a\u5e26\u4e86\u5f88\u591a\u65c5\u884c\u7528\u54c1\uff0c\u66f4\u50cf\u662f\u4e00\u7fa4\u65c5\u884c\u8005\u3002\u201d</p><p>\u201c\u4e0d\u53ef\u601d\u8bae\uff0c\u201d\u73ab\u8389\u8bf4\uff0c\u201c\u786e\u5b9a\u6ca1\u5f04\u9519\u5417\uff1f\u771f\u7684\u662f\u5815\u843d\u8005\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u5e94\u8be5\u4e0d\u4f1a\u9519\uff0c\u77ee\u4e2a\u5b50\uff0c\u9ed1\u76ae\u80a4\uff0c\u4ece\u5730\u5e95\u94bb\u51fa\u6765\uff0c\u548c\u8bb0\u8f7d\u4e2d\u4e00\u6a21\u4e00\u6837\uff0c\u201d\u4f0a\u8299\u8bf4\uff0c\u201c\u8fd8\u6709\u4ef6\u66f4\u5947\u602a\u7684\u4e8b\u60c5\uff1a\u4ed6\u4eec\u5e76\u4e0d\u5168\u662f\u5815\u843d\u8005\uff0c\u5176\u4e2d\u6709\u4e24\u4e2a\u660e\u663e\u4e0d\u662f\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u770b\u8d77\u6765\u50cf\u662f\u4f20\u8bf4\u4e2d\u7684\u2018\u4eba\u7c7b\u2019\uff0c\u201d\u4f0a\u8299\u8bf4\uff0c\u201c\u4e00\u5bf9\u53cc\u80de\u80ce\u59d0\u59b9\u3002\u201d</p></article>"}}}]);
