(window.webpackJsonp=window.webpackJsonp||[]).push([[18],{41:function(p){p.exports={chapterTitle:null,title:"\u7b2c18\u7ae0 \u8bb8\u601d\u7684\u82e6\u8877",content:"<article><h2>\u7b2c18\u7ae0 \u8bb8\u601d\u7684\u82e6\u8877</h2><p>\u3000\u3000\u4e2d\u5348\u5403\u996d\u65f6\uff0c\u5f90\u5b66\u5e73\u4e0e\u79d8\u4e66\u674e\u4e49\u6c5f\u624d\u56de\u6765\uff0c\u91d1\u56fd\u6d77\u7684\u52a9\u624b\u9648\u6653\u677e\u4e5f\u8ddf\u4e86\u8fc7\u6765\u3002\u674e\u4e49\u6c5f\u5c06\u7701\u59d4\u7d27\u6025\u5904\u7f6e\u7684\u51b3\u5b9a\u8ddf\u5f20\u77e5\u884c\u7236\u5b50\u505a\u4e86\u8bf4\u660e\uff1a\u201c\u7531\u7701\u68c0\u67e5\u9662\u6210\u7acb\u4e13\u6848\u7ec4\uff0c\u6309\u7167\u539f\u6765\u7684\u8ba1\u5212\uff0c\u7531\u7701\u68c0\u67e5\u7ec4\u7ed9\u5510\u5b66\u8c26\u6848\u505a\u4e00\u4e2a\u521d\u6b65\u7684\u7ed3\u8bba\uff0c\u7136\u540e\u5c06\u6848\u4ef6\u79fb\u4ea4\u7ed9\u7701\u68c0\u5bdf\u9662\u7275\u5934\u7684\u4e13\u6848\u7ec4\uff0c\u5c06\u59dc\u660e\u8bda\u3001\u8bb8\u601d\u4e24\u4e2a\u5173\u952e\u5acc\u7591\u4eba\u5e26\u5230\u7701\u57ce\u6765\u5ba1\u8baf\uff0c\u91d1\u526f\u68c0\u5bdf\u957f\u5e0c\u671b\u4f60\u4eec\u7236\u5b50\u4fe9\u80fd\u53c2\u4e0e\u6848\u4ef6\u7684\u5ba1\u7406\u3002\u201d</p><p>\u3000\u3000\u8fd9\u662f\u5f20\u606a\u6c42\u4e4b\u4e0d\u5f97\u7684\uff0c\u5bf9\u7238\u7238\u6765\u8bf4\uff0c\u4e5f\u662f\u63a5\u8fd1\u7701\u9ad8\u5c42\u5b98\u5458\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u5728\u5f90\u5b66\u5e73\u5bb6\u5403\u8fc7\u4e2d\u996d\uff0c\u5f20\u606a\u4e0e\u7238\u7238\u5c31\u968f\u9648\u6653\u677e\u6765\u5230\u7701\u68c0\u5bdf\u9662\u6240\u5c5e\u7684\u62db\u5f85\u5bbe\u9986\uff1a\u9f99\u534e\u5bbe\u9986\uff0c\u4e13\u6848\u7ec4\u6309\u6392\u5728\u4e00\u5e62\u5c0f\u72ec\u697c\uff0c\u5f20\u606a\u8fc7\u6765\u4e4b\u524d\uff0c\u7531\u91d1\u56fd\u6d77\u7387\u9886\u7684\u4e13\u6848\u7ec4\u6210\u5458\u90fd\u5df2\u7ecf\u8fdb\u9a7b\uff0c\u6d77\u5dde\u5e02\u90a3\u8fb9\u6700\u5feb\u4e5f\u8981\u5230\u665a\u4e0a\u624d\u80fd\u4ea4\u63a5\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e0c\u671b\u5c06\u5510\u5b66\u8c26\u7684\u5973\u513f\u5510\u5a67\u4e5f\u4ee5\u534f\u52a9\u8c03\u67e5\u7684\u540d\u4e49\u63a5\u5230\u7701\u57ce\u6765\uff0c\u73b0\u5728\u4ec0\u4e48\u6d88\u606f\u90fd\u4e0d\u80fd\u5411\u5916\u754c\u900f\u9732\uff0c\u8ba9\u5510\u5a67\u4e00\u4eba\u7559\u5728\u6d77\u5dde\uff0c\u6015\u5979\u4f1a\u627f\u53d7\u4e0d\u4e86\u3002</p><p>\u3000\u3000\u6b21\u65e5\uff0c\u5510\u5b66\u8c26\u6848\u6b63\u5f0f\u79fb\u4ea4\u7701\u68c0\u5bdf\u9662\u4e13\u6848\u7ec4\u5ba1\u7406\uff0c\u5510\u5b66\u8c26\u3001\u987e\u5efa\u840d\u3001\u5510\u5a67\u3001\u8bb8\u601d\u3001\u59dc\u660e\u8bda\uff0c\u8fd8\u6709\u5510\u5b66\u8c26\u7684\u79d8\u4e66\u53f6\u65b0\u660e\u7b49\u4eba\uff0c\u4ee5\u53ca\u539f\u68c0\u67e5\u7ec4\u4eba\u5458\u4e00\u8d77\u4f4f\u8fdb\u9f99\u534e\u5bbe\u9986\u3002</p><p>\u3000\u3000\u56e0\u4e3a\u7701\u68c0\u67e5\u7ec4\u8c03\u67e5\u5510\u5b66\u8c26\u53d7\u8d3f\u6848\u8fc7\u7a0b\u4e2d\uff0c\u6709\u5411\u5916\u754c\u6cc4\u9732\u8c03\u67e5\u8fdb\u5c55\u3001\u8bef\u5bfc\u8c03\u67e5\u65b9\u5411\u7684\u5acc\u7591\uff0c\u6240\u4ee5\u6709\u5fc5\u8981\u5bf9\u539f\u68c0\u67e5\u7ec4\u6210\u5458\u8fdb\u884c\u9694\u79bb\u5ba1\u67e5\uff0c\u68c0\u67e5\u7ec4\u4e00\u884c\u5341\u4e8c\u4e2a\u4eba\uff0c\u62b5\u8fbe\u9f99\u534e\u5bbe\u9986\u5c31\u88ab\u8981\u6c42\u4f4f\u8fdb\u4e13\u6848\u7ec4\u9694\u58c1\u7684\u4e00\u680b\u72ec\u697c\uff0c\u9650\u5236\u4e0e\u5916\u754c\u8054\u7cfb\uff0c\u6bcf\u4e2a\u4eba\u90fd\u88ab\u8981\u6c42\u5c06\u5728\u6d77\u5dde\u5e02\u7684\u8c03\u67e5\u8fc7\u7a0b\u8bb0\u5f55\u4e0b\u6765\uff0c\u4f9b\u4e13\u6848\u7ec4\u6838\u67e5\uff0c\u5bf9\u5916\u5374\u5ba3\u79f0\u4ed6\u4eec\u5e76\u5165\u4e13\u6848\u7ec4\u3002</p><p>\u3000\u3000\u5f20\u606a\u51e0\u4e4e\u8bb0\u4e0d\u8d77\u5510\u5b66\u8c26\u5165\u72f1\u524d\u7684\u6837\u5b50\uff0c\u5370\u8c61\u6700\u6df1\u7684\u662f\u4e5d\u4e5d\u5e74\u6cd5\u9662\u6539\u5224\u4ed6\u65e0\u7f6a\u65f6\uff0c\u4ed6\u7684\u7cbe\u795e\u72b6\u6001\u5f88\u4e0d\u597d\uff0c\u8eab\u5b50\u4f5d\u507b\uff0c\u4e71\u84ec\u84ec\u7684\u80e1\u5b50\u6709\u4e9b\u7070\u767d\uff0c\u7adf\u50cf\u516d\u4e03\u5341\u5c81\u7684\u8001\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bb0\u5f97\u5510\u5b66\u8c26\u5c31\u6bd4\u7238\u7238\u5e74\u957f\u4e24\u5c81\u3002</p><p>\u3000\u3000\u5728\u4e8c\u697c\u7684\u4f1a\u8bae\u5ba4\u91cc\uff0c\u5f20\u606a\u518d\u4e00\u6b21\u770b\u5230\u63a5\u53d7\u68c0\u67e5\u7ec4\u9694\u79bb\u5ba1\u67e5\u6709\u534a\u4e2a\u6708\u7684\u5510\u5b66\u8c26\uff0c\u4ed6\u7684\u8138\u988a\u9677\u4e0b\u53bb\uff0c\u7626\u7684\u5389\u5bb3\uff0c\u773c\u775b\u5f88\u75b2\u60eb\uff0c\u4e0d\u8fc7\u7a7f\u7740\u5f88\u6574\u9f50\uff0c\u77ed\u8896\u767d\u886c\u886b\uff0c\u8896\u53e3\u3001\u9886\u53e3\u90fd\u6ca1\u6709\u6c61\u6e0d\uff0c\u4e0b\u5df4\u522e\u5f97\u5f88\u5e72\u51c0\uff0c\u4ed6\u73b0\u5728\u8fd8\u76f8\u4fe1\u7701\u91cc\u4f1a\u7ed9\u4ed6\u516c\u6b63\u7684\u8bf4\u6cd5\u3002</p><p>\u3000\u3000\u201c\u4f60\u7ed9\u53eb\u5230\u8fd9\u91cc\u6765\u63a5\u53d7\u8c03\u67e5\uff1f\u201d\u5510\u5b66\u8c26\u770b\u89c1\u5f20\u77e5\u884c\uff0c\u6ca1\u6709\u89c9\u5f97\u5f88\u610f\u5916\uff0c\u4ee5\u4e3a\u4ed6\u4e5f\u662f\u6765\u63a5\u53d7\u4e13\u6848\u7ec4\u7684\u8c03\u67e5\u3002</p><p>\u3000\u3000\u201c\u5f20\u526f\u79d8\u4e66\u957f\u662f\u4e13\u6848\u7ec4\u501f\u6765\u5ba1\u67e5\u4f60\u7684\u6848\u5b50\u3002\u201d\u91d1\u56fd\u6d77\u5728\u65c1\u8fb9\u7ea0\u6b63\u4ed6\u7684\u8bf4\u6cd5\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u91d1\u56fd\u6d77\u5524\u7238\u7238\u804c\u52a1\uff0c\u7eaf\u7cb9\u662f\u7ed9\u5f90\u5b66\u5e73\u7684\u9762\u5b50\uff0c\u4ed6\u662f\u7701\u68c0\u5bdf\u9662\u7684\u526f\u68c0\u5bdf\u957f\u3001\u515a\u7ec4\u526f\u4e66\u8bb0\uff0c\u7238\u7238\u53ea\u662f\u5e95\u4e0b\u5730\u533a\u7684\u5e02\u653f\u5e9c\u526f\u79d8\u4e66\u957f\uff0c\u8ddf\u4ed6\u5dee\u5f88\u8fdc\u3002</p><p>\u3000\u3000\u201c\u5510\u5e02\u957f\u2026\u2026\u201d\u5f20\u77e5\u884c\u638f\u51fa\u4e00\u5305\u7389\u6eaa\u70df\uff0c\u9012\u7ed9\u5510\u5b66\u8c26\uff0c\u201c\u60c5\u51b5\u5f88\u5feb\u5c31\u4f1a\u641e\u6e05\u695a\u7684\u3002\u201d\u53c8\u8f6c\u8fc7\u5934\u95ee\u91d1\u56fd\u6d77\uff0c\u201c\u91d1\u68c0\u5bdf\u957f\uff0c\u80fd\u4e0d\u80fd\u5c06\u5510\u5e02\u957f\u8ddf\u4ed6\u7684\u5bb6\u4eba\u5b89\u6392\u4e00\u8d77\uff1f\u201d\u5510\u5b66\u8c26\u867d\u7136\u6ca1\u6709\u5b8c\u5168\u6d17\u6e05\u5acc\u7591\uff0c\u4f46\u76ee\u524d\u7684\u7591\u70b9\u662f\u5bf9\u4ed6\u6709\u5229\u7684\uff0c\u9694\u79bb\u5ba1\u67e5\u5df2\u7ecf\u6ca1\u6709\u5fc5\u8981\u4e86\uff0c\u4f55\u51b5\u5f20\u77e5\u884c\u4e5f\u4e0d\u5fcd\u5510\u5b66\u8c26\u4e00\u5bb6\u4eba\u90fd\u5904\u5728\u60f6\u7136\u4e0d\u5b89\u4e4b\u4e2d\uff0c\u5510\u5b66\u8c26\u4e0e\u59bb\u5973\u4f4f\u5728\u4e00\u8d77\uff0c\u81f3\u5c11\u7ed9\u4ed6\u4e9b\u4fe1\u5fc3\u3002</p><p>\u3000\u3000\u201c\u89c4\u5b9a\u4e0a\u4e0d\u5141\u8bb8\uff0c\u201d\u91d1\u56fd\u6d77\u60f3\u4e86\u60f3\uff0c\u8bf4\u9053\uff0c\u201c\u8ba9\u6211\u770b\u770b\u697c\u91cc\u8fd8\u6ca1\u6709\u5927\u5957\u95f4\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u4e00\u8fb9\uff0c\u5fc3\u91cc\u60f3\uff1a\u5510\u5b66\u8c26\u7684\u6848\u5b50\u9519\u5230\u8fd9\u4e00\u6b65\uff0c\u91d1\u56fd\u6d77\u4e5f\u6709\u8d23\u4efb\uff0c\u7238\u7238\u7684\u63d0\u8bae\u662f\u7ed9\u4ed6\u5f25\u8865\u4e0e\u5510\u5b66\u8c26\u4e4b\u95f4\u5173\u7cfb\u7684\u673a\u4f1a\uff0c\u4ed6\u54ea\u91cc\u4f1a\u786c\u90a6\u90a6\u7684\u7167\u89c4\u77e9\u6765\uff1f\u5f20\u606a\u89c1\u5510\u5b66\u8c26\u8fd9\u65f6\u5019\u7ad9\u8d77\u6765\uff0c\u7d27\u7d27\u63e1\u4f4f\u7238\u7238\u7684\u624b\uff0c\u773c\u775b\u91cc\u84c4\u7740\u6cea\u6c34\uff0c\u60f3\u5fc5\u77e5\u9053\u8fd9\u4e9b\u5929\u53ea\u6709\u7238\u7238\u5728\u4e3a\u4ed6\u7684\u4e8b\u5954\u8d70\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u4fa6\u5bdf\u5458\u5c06\u987e\u5efa\u840d\u4e0e\u5510\u5a67\u5e26\u4e86\u8fdb\u6765\uff0c\u53ea\u9694\u4e86\u4e24\u5929\uff0c\u987e\u5efa\u840d\u7684\u8138\u8272\u66f4\u52a0\u96be\u770b\u4e86\uff0c\u7ed9\u4eba\u5f88\u865a\u5f31\u7684\u611f\u89c9\u3002\u5f20\u606a\u770b\u4e86\u770b\u91d1\u56fd\u6d77\uff0c\u8fd9\u6b21\u4e0d\u7528\u4eba\u63d0\u9192\uff0c\u91d1\u56fd\u6d77\u5148\u5173\u5fc3\u7684\u95ee\uff1a\u201c\u5ac2\u5b50\u7684\u8138\u8272\u600e\u4e48\u8fd9\u4e48\u5dee\uff1f\u201d\u56de\u5934\u5429\u5490\u9648\u6653\u677e\uff0c\u201c\u5feb\u53bb\u628a\u738b\u533b\u751f\u53eb\u8fc7\u6765\uff0c\u987a\u4fbf\u5e2e\u5510\u5e02\u957f\u4e5f\u68c0\u67e5\u68c0\u67e5\uff0c\u4e2d\u5348\u8ba9\u53a8\u623f\u6dfb\u51e0\u4e2a\u83dc\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u53ea\u5173\u5fc3\u5510\u5a67\uff0c\u5979\u5934\u53d1\u6709\u4e9b\u4e71\uff0c\u60f3\u5fc5\u6ca1\u6709\u5fc3\u601d\u6253\u7406\uff0c\u4f46\u662f\u96be\u63a9\u7709\u773c\u95f4\u7684\u5a9a\u6c14\uff0c\u8138\u7626\u4e86\u4e00\u4e9b\uff0c\u5374\u66f4\u52a0\u4fcf\u4e3d\u4e86\uff0c\u8138\u8272\u8fd8\u597d\uff0c\u53ea\u662f\u5634\u5507\u6709\u4e9b\u53d1\u767d\uff0c\u5f20\u606a\u5c31\u653e\u4e86\u5fc3\uff0c\u7ad9\u5728\u4eba\u540e\uff0c\u671d\u5979\u7728\u4e86\u7728\u773c\u775b\u3002\u5510\u5a67\u7591\u60d1\u7684\u770b\u7740\u5f20\u606a\uff0c\u4ee5\u4e3a\u4ed6\u8981\u7ed9\u81ea\u5df1\u4ec0\u4e48\u6697\u53f7\uff0c\u6ca1\u60f3\u5230\u5f20\u606a\u5634\u4e00\u6485\uff0c\u5979\u7684\u8138\u5fae\u7ea2\uff0c\u773c\u775b\u8f6c\u5411\u522b\u5904\u53bb\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c31\u559c\u6b22\u770b\u5510\u5a67\u7f9e\u6da9\u7684\u6837\u5b50\uff0c\u8138\u988a\u6f6e\u7ea2\uff0c\u773c\u775b\u770b\u4e0a\u53bb\u53c8\u957f\u53c8\u5a9a\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u5947\u602a\u7684\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u6ca1\u770b\u5230\u5f20\u606a\u8ddf\u5510\u5a67\u8c03\u60c5\u7684\u6837\u5b50\uff0c\u5fc3\u60f3\u8fd9\u4e48\u5b89\u6392\uff0c\u5510\u5b66\u8c26\u4e00\u5bb6\u4eba\u4e5f\u80fd\u5728\u8fd9\u91cc\u5b89\u5fc3\u7b49\u6848\u60c5\u53d6\u5f97\u8fdb\u4e00\u6b65\u7684\u8fdb\u5c55\u3002</p><p>\u3000\u3000\u5df2\u7ecf\u8d70\u5230\u8fd9\u4e00\u6b65\uff0c\u6848\u5b50\u518d\u7834\u4e0d\u4e86\uff0c\u5f20\u606a\u4e00\u5934\u649e\u6b7b\u5f97\u4e86\uff0c\u4e09\u5341\u4e03\u4e07\u5bb6\u5ead\u5b58\u6b3e\uff0c\u867d\u8bf4\u8ddf\u5510\u5b66\u8c26\u592b\u5987\u7684\u6536\u5165\u6709\u4e00\u4e9b\u51fa\u5165\uff0c\u4f46\u662f\u6ca1\u6709\u4eba\u4f1a\u53bb\u6df1\u7a76\u3002\u5bf9\u4e8e\u4e00\u4e2a\u957f\u671f\u5728\u9886\u5bfc\u5c97\u4f4d\u4e0a\u5de5\u4f5c\u7684\u4eba\uff0c\u5bb6\u5ead\u5b58\u6b3e\u624d\u4e09\u5341\u4e03\u4e07\uff0c\u5df2\u7136\u7b97\u662f\u6e05\u5ec9\u7684\u4e86\u3002</p><p>\u3000\u3000\u5510\u5a67\u4e0d\u7528\u62c5\u5fc3\uff0c\u8bb8\u601d\u7684\u4e8b\u60c5\u8be5\u600e\u4e48\u529e\uff1f</p><p>\u3000\u3000\u968f\u4e13\u6848\u7ec4\u8fdb\u9a7b\u8fd9\u91cc\u7684\u533b\u751f\u66ff\u987e\u5efa\u840d\u505a\u4e86\u7b80\u5355\u7684\u68c0\u67e5\uff0c\u6d4b\u4e86\u8840\u538b\u3001\u5fc3\u5f8b\uff0c\u8840\u538b\u6709\u4e9b\u4f4e\uff0c\u662f\u8fd9\u6bb5\u65f6\u95f4\u8eab\u5fc3\u75b2\u987f\u6240\u81f4\u3002</p><p>\u3000\u3000\u4e09\u697c\u817e\u51fa\u4e00\u95f4\u5957\u623f\uff0c\u5c06\u5510\u5b66\u8c26\u3001\u987e\u5efa\u840d\u3001\u5510\u5a67\u4e00\u5bb6\u4e09\u53e3\u9001\u5230\u4e09\u697c\u4f11\u606f\uff0c\u5f20\u606a\u4e5f\u627e\u4e0d\u5230\u673a\u4f1a\u4e0e\u5510\u5a67\u5355\u72ec\u76f8\u5904\uff0c\u968f\u7238\u7238\u3001\u91d1\u56fd\u6d77\u56de\u5230\u8bbe\u5728\u4e8c\u697c\u7684\u4e34\u65f6\u529e\u516c\u5ba4\u3002</p><p>\u3000\u3000\u5728\u5168\u4f53\u7701\u59d4\u9886\u5bfc\u7684\u5173\u6ce8\u4e0b\uff0c\u4e13\u6848\u7ec4\u7684\u6548\u7387\u5f88\u9ad8\uff0c\u77ed\u77ed\u4e00\u5929\u4e00\u591c\u7684\u65f6\u95f4\u91cc\uff0c\u5c06\u76f8\u5173\u6863\u6848\u3001\u5377\u5b97\u90fd\u62bd\u8c03\u8fc7\u6765\u3002</p><p>\u3000\u3000\u4e4b\u524d\uff0c\u5f20\u606a\u4e00\u76f4\u5728\u60f3\uff0c\u8bb8\u601d\u770b\u4e0a\u53bb\u662f\u90a3\u6837\u7684\u5355\u7eaf\uff0c\u4e3a\u4ec0\u4e48\u4f1a\u7518\u5fc3\u88ab\u4e01\u5411\u5c71\u63a7\u5236\u3001\u5229\u7528\uff1f</p><p>\u3000\u3000\u5f20\u606a\u62bd\u51fa\u4e00\u4efd\u5377\u5b97\uff0c\u7ffb\u5f00\u6765\uff0c\u9996\u9875\u53f3\u4e0a\u89d2\u8d34\u7740\u4e00\u540d\u5c11\u5973\u7684\u7167\u7247\uff0c\u7167\u7247\u91cc\u7684\u5c11\u5973\u4fca\u79c0\u598d\u4e3d\uff0c\u7a84\u988a\u5c16\u9894\uff0c\u6807\u51c6\u7684\u74dc\u5b50\u8138\uff0c\u8138\u988a\u7c89\u767d\uff0c\u7709\u6bdb\u53c8\u7ec6\u53c8\u957f\uff0c\u9ad8\u633a\u7684\u9f3b\u6881\uff0c\u773c\u775b\u5341\u5206\u7684\u8ff7\u4eba\uff0c\u5f20\u606a\u5fc3\u91cc\u60f3\uff1a\u59d0\u59d0\u957f\u5f97\u98a0\u5012\u4f17\u751f\uff0c\u59b9\u59b9\u4e5f\u8fd9\u822c\u8ff7\u4eba\uff0c\u771f\u662f\u6ca1\u5929\u7406\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u662f\u8bb8\u601d\u59b9\u59b9\u8bb8\u7ef4\u7684\u6863\u6848\uff0c\u5728\u6863\u6848\u91cc\u8bb0\u8f7d\uff0c\u8bb8\u7ef4\u53bb\u5e74\u8fdb\u5165\u4e1c\u6d77\u5927\u5b66\u624d\u534a\u5e74\u5c31\u8fdb\u5165\u7701\u519b\u533b\u603b\u9662\u8fdb\u884c\u5fc3\u80f8\u74e3\u7f3a\u5931\u4fee\u8865\u624b\u672f\uff0c\u540e\u9762\u9644\u7684\u6587\u4ef6\u662f\u4eca\u5929\u4e0a\u5348\u7d27\u6025\u4ece\u7701\u519b\u533b\u603b\u9662\u8c03\u6709\u5173\u8bb8\u7ef4\u4f4f\u9662\u6cbb\u7597\u7684\u6750\u6599\u3002\u4e09\u4e2a\u6708\u7684\u4f4f\u9662\u8d39\u7528\u52a0\u4e0a\u6cbb\u7597\u624b\u672f\u8d39\u7528\uff0c\u8bb8\u5bb6\u4e00\u5171\u82b1\u6389\u5341\u4e00\u4e07\u5143\uff0c\u6750\u6599\u8fd8\u663e\u793a\u8bb8\u5bb6\u989d\u5916\u63d0\u4f9b\u5927\u91cf\u8fdb\u53e3\u836f\u7269\u7528\u4e8e\u8bb8\u7ef4\u7684\u6cbb\u7597\uff0c\u867d\u7136\u8fd8\u6ca1\u6709\u8fdb\u4e00\u6b65\u7684\u8d44\u6599\u8bc1\u660e\u8fd9\u4e9b\u8fdb\u53e3\u836f\u7269\u7684\u4ef7\u683c\uff0c\u60f3\u5fc5\u4e0d\u4f1a\u4fbf\u5b9c\u3002</p><p>\u3000\u3000\u7b54\u6848\u6216\u8bb8\u5c31\u662f\u8fd9\u4e2a\u5427\uff1f</p><p>\u3000\u3000\u4e13\u6848\u7ec4\u7684\u6210\u5458\u5f97\u5230\u8fd9\u4efd\u6750\u6599\uff0c\u4e5f\u5f88\u5174\u594b\uff0c\u59dc\u660e\u8bda\u4ea4\u7ed9\u8bb8\u601d\u7684\u4e8c\u5341\u4e03\u4e07\uff0c\u5f88\u53ef\u80fd\u7528\u5728\u5979\u59b9\u59b9\u8bb8\u7ef4\u7684\u6cbb\u7597\u4e0a\uff0c\u800c\u4e0d\u662f\u50cf\u5979\u4e4b\u524d\u5411\u68c0\u67e5\u7ec4\u4ea4\u5f85\u7684\u90a3\u6837\uff0c\u5c06\u94b1\u8f6c\u4ea4\u7ed9\u5510\u5b66\u8c26\u3002</p></article>"}}}]);
