(window.webpackJsonp=window.webpackJsonp||[]).push([[182],{205:function(p){p.exports={chapterTitle:null,title:"\u7b2c182\u7ae0 \u660e\u5929\u7684\u5e7f\u544a\u5929\u540e",content:"<article><h2>\u7b2c182\u7ae0 \u660e\u5929\u7684\u5e7f\u544a\u5929\u540e</h2><p>\u3000\u3000\u5f97\u5230\u5b8b\u57f9\u660e\u7684\u652f\u6301\uff0c\u6709\u4e9b\u5de5\u4f5c\u5c31\u53ef\u4ee5\u63d0\u524d\u51c6\u5907\u8d77\u6765\uff1b\u867d\u7136\u5c06\u9879\u76ee\u7684\u89c4\u6a21\u63a7\u5236\u5728\u7701\u8ba1\u59d4\u5ba1\u6279\u7684\u8303\u56f4\u4e4b\u5185\uff0c\u6bd5\u7adf\u662f\u6d77\u5dde\u5e02\u8fd1\u5e74\u5c11\u6709\u7684\u5927\u9879\u76ee\uff0c\u4e00\u7ecf\u6279\u9732\uff0c\u6ce8\u5b9a\u4f1a\u5438\u5f15\u4f17\u591a\u76ee\u5149\u7684\u5173\u6ce8\uff0c\u524d\u671f\u7684\u51c6\u5907\u5de5\u4f5c\u52a1\u5fc5\u8981\u7ec6\u81f4\u5168\u9762\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u4e0e\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u5728\u7535\u8bdd\u91cc\u6c9f\u901a\u4e86\u4e00\u4e0b\uff0c\u7231\u8fbe\u7535\u5b50\u52bf\u5fc5\u8981\u5927\u8086\u7684\u62db\u5175\u4e70\u9a6c\u624d\u80fd\u5e94\u4ed8\u63a5\u4e0b\u6765\u7684\u6025\u5267\u6269\u5f20\uff0c\u76db\u946b\u4eca\u5929\u8f6c\u8fdb\u8d26\u91cc\u7684\u4e00\u5343\u4e94\u767e\u4e07\uff0c\u53ef\u771f\u662f\u6551\u6025\u7684\u94b1\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u60f3\u7ed9\u7238\u7238\u6253\u4e2a\u7535\u8bdd\u95ee\u4e00\u4e0b\u519c\u673a\u5382\u5382\u957f\u7ade\u8058\u7684\u4e8b\u60c5\uff0c\u65bd\u536b\u5fe0\u662f\u8bb8\u601d\u7684\u5c0f\u8205\uff0c\u53c8\u4e0e\u8bb8\u601d\u5bb6\u4eb2\u8fd1\uff0c\u4ed6\u8981\u771f\u80fd\u5750\u4e0a\u519c\u673a\u5382\u5382\u957f\u7684\u4f4d\u5b50\uff0c\u4e5f\u80fd\u5206\u62c5\u8bb8\u601d\u5bb6\u7684\u538b\u529b\u3002\u5750\u4e86\u534a\u5929\u80a9\u9178\u80cc\u75db\uff0c\u5f20\u606a\u7ad9\u8d77\u6765\u4f38\u5c55\u8eab\u4f53\uff0c\u8fd8\u60f3\u505a\u4e00\u8282\u5e7f\u64ad\u4f53\u64cd\u6765\u7740\uff0c\u6ca1\u7b49\u4ed6\u7ed9\u7238\u7238\u6253\u7535\u8bdd\uff0c\u5085\u4fca\u4ece\u5e7f\u544a\u516c\u53f8\u5c06\u5236\u4f5c\u597d\u7684\u5e7f\u544a\u7247\u62f7\u8d1d\u53d6\u56de\u6765\u3002\u8bb8\u7ef4\u7684\u90a3\u95f4\u7279\u62a4\u75c5\u623f\u6709\u5f55\u50cf\u673a\uff0c\u5f20\u606a\u62ff\u7740\u5f55\u50cf\u5e26\u8fc7\u53bb\uff0c\u770b\u7740\u5979\u4eec\u51e0\u4eba\u90fd\u5728\u8ba4\u771f\u7684\u6e29\u4e60\u529f\u8bfe\uff0c\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u60ed\u6127\u3002\u6731\u5c0f\u519b\u4e5f\u4e0d\u81f3\u4e8e\u4e00\u76f4\u65e0\u6240\u4e8b\u4e8b\u7559\u5728\u8fd9\u91cc\uff0c\u5f20\u606a\u79bb\u5f00\u8bb8\u7ef4\u75c5\u5e8a\u4e0d\u4e45\uff0c\u4ed6\u5c31\u6709\u4e8b\u5148\u79bb\u5f00\u4e86\uff0c\u4e0d\u8fc7\u8bf4\u597d\u665a\u4e0a\u4f1a\u8d76\u8fc7\u6765\uff0c\u770b\u65f6\u95f4\uff0c\u4ed6\u5dee\u4e0d\u591a\u4e5f\u5feb\u56de\u6765\u4e86\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u5f55\u50cf\uff1f\u201d\u4ee4\u5c0f\u71d5\u4e0d\u591f\u4e13\u5fc3\uff0c\u770b\u89c1\u5f20\u606a\u8ddf\u5085\u4fca\u8fdb\u6765\uff0c\u5c31\u63a2\u8fc7\u5934\u6765\u770b\u4ed6\u624b\u91cc\u7684\u5f55\u50cf\u5e26\uff0c\u6487\u6487\u5634\uff0c\u201c\u7adf\u6709\u5fc3\u601d\u770b\u8fd9\u79cd\u5e26\u5b50\uff01\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u8fd9\u79cd\u5e26\u5b50\uff1f\u201d\u6c5f\u9edb\u513f\u51f9\u51f8\u6709\u81f4\u7684\u8eab\u4f53\u8737\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u4fee\u957f\u7ea4\u76f4\u7684\u957f\u817f\u4f38\u76f4\u7740\uff0c\u5f88\u968f\u610f\u7684\u6401\u5728\u8bb8\u7ef4\u7684\u817f\u4e0a\uff0c\u5979\u64a9\u773c\u770b\u7740\u4ee4\u5c0f\u71d5\uff0c\u7591\u60d1\u4e0d\u89e3\u5979\u8bdd\u91cc\u7684\u610f\u601d\uff0c\u77a5\u773c\u770b\u4e86\u5f20\u606a\uff0c\u89c6\u7ebf\u78b0\u5230\u4e00\u5904\uff0c\u773c\u795e\u77ac\u65f6\u7684\u95ea\u8eb2\uff0c\u6643\u9732\u51fa\u5979\u5c11\u5973\u5a07\u7f9e\u7684\u8ff7\u4eba\u795e\u6001\uff0c\u773c\u7738\u5f02\u5e38\u7684\u59a9\u5a9a\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u4ee4\u5c0f\u71d5\u8bef\u4f1a\u4ec0\u4e48\uff0c\u8fd9\u662f\u5e7f\u544a\u516c\u53f8\u5236\u4f5c\u7684\u630e\u8d1d\u5e26\uff0c\u6ca1\u6709\u6807\u7b7e\uff0c\u5f88\u591a\u8272\u60c5\u7535\u5f71\u90fd\u4ee5\u8fd9\u79cd\u767d\u76d8\u7684\u5f62\u5f0f\u5728\u6c11\u95f4\u6d41\u4f20\uff0c\u6487\u5634\u4e00\u7b11\uff0c\u6d41\u9732\u73a9\u4e16\u4e0d\u606d\u7684\u7ea8\u7ed4\u6c14\u8d28\uff0c\u5634\u89d2\u6302\u7740\u8ff7\u4eba\u7684\u7b11\u5bb9\uff1a\u201c\u9752\u5c11\u5e74\u5f88\u82e6\u95f7\u7684\u3002\u201d\u563f\u7136\u4e00\u7b11\uff0c\u201c\u6731\u5c0f\u519b\u7ecf\u5e38\u62c9\u4f60\u63a5\u53d7\u8fd9\u79cd\u6559\u80b2\uff1f\u201d</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u5ae9\u8138\u4e00\u7ea2\uff0c\u55d4\u9a82\u9053\uff1a\u201c\u80e1\u8bf4\u516b\u9053\u4ec0\u4e48\uff0c\u6211\u4eec\u53ef\u6bd4\u4f60\u8fd9\u4e9b\u5c0f\u7537\u5b69\u5355\u7eaf\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u62f7\u8d1d\u5e26\u585e\u8fdb\u653e\u6620\u673a\u91cc\uff0c\u5750\u5230\u8bb8\u7ef4\u4e0e\u6c5f\u9edb\u513f\u4e2d\u95f4\uff0c\u8212\u670d\u7684\u8737\u8d77\u817f\uff0c\u610f\u6001\u61d2\u6563\u7684\u4f38\u5c55\u8eab\u4f53\uff0c\u8bb8\u7ef4\u56de\u5934\u7741\u5927\u773c\u775b\u770b\u7740\u4ed6\uff1a\u201c\u8fd9\u4e48\u5927\u70b9\u5730\u65b9\uff0c\u4f60\u5012\u5750\u5f97\u8212\u670d\u3002\u201d\u6c5f\u9edb\u513f\u4e0d\u597d\u610f\u601d\u7684\u8737\u8d77\u817f\u76d8\u5750\u5230\u81c0\u4e0b\uff0c\u8170\u80a2\u4f38\u76f4\u7740\uff0c\u6108\u53d1\u663e\u5f97\u8170\u7ec6\u80f8\u633a\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u771f\u5728\u8fd9\u91cc\u770b\uff1f\u201d\u4ee4\u5c0f\u71d5\u62ff\u8d77\u4e66\u7ad9\u8d77\u6765\uff0c\u201c\u8fd9\u91cc\u662f\u533b\u9662\u5462\uff01\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u4e1c\u897f\u4e0d\u80fd\u770b\uff1f\u201d\u8bb8\u7ef4\u7591\u60d1\u4e0d\u89e3\u7684\u770b\u4e86\u770b\u4ee4\u5c0f\u71d5\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u770b\u4e86\u5c31\u77e5\u9053\uff0c\u6211\u56de\u623f\u95f4\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u5c0f\u5b69\u5b50\u4e0d\u4f1a\u8981\u770b\u4e0d\u5e72\u51c0\u7684\u4e1c\u897f\u5427\uff1f\u201d\u8bb8\u7ef4\u770b\u7740\u4ee4\u5c0f\u71d5\u63f6\u63c4\u7684\u795e\u8272\uff0c\u60f3\u8d77\u5979\u8bf4\u8d77\u8fc7\u6765\u7684\u90a3\u79cd\u5f71\u5e26\u6765\uff0c\u60ca\u614c\u5931\u63aa\u7684\u62ff\u8d77\u9065\u63a7\u5668\u8981\u5c06\u653e\u6620\u673a\u5173\u6389\uff0c\u753b\u9762\u5df2\u7ecf\u95ea\u8df3\u51fa\u6765\uff0c\u5148\u662f\u98de\u9e3f\u5e7f\u544a\u516c\u53f8\u7684\u5e55\u5934\u77ed\u7247\u3002</p><p>\u3000\u3000\u771f\u6b63\u7684\u5e7f\u544a\u7247\u6bb5\u5f88\u77ed\uff0c\u53ea\u5236\u4f5c\u6807\u51c6\u5341\u4e94\u79d2\u7684\u5f71\u50cf\u3002\u6700\u521d\u95ea\u5165\u773c\u5e18\u7684\u662f\u4e00\u89d2\u5a07\u8273\u7ea2\u5507\u4ee5\u62a5\u559c\u9e4a\u7684\u65cb\u5f8b\u53cd\u590d\u7684\u8f7b\u58f0\u548f\u5531\u4e24\u4e2a\u5b57\uff1a\u201c\u7231\u8fbe\u3001\u7231\u8fbe\uff0c\u7231\u8fbe\u2026\u2026\u201d\u89c6\u8ddd\u62c9\u8fdc\uff0c\u5a07\u8273\u6210\u719f\u7684\u5973\u90ce\u5168\u8c8c\u5448\u73b0\uff0c\u753b\u9762\u7d27\u63a5\u7740\u62c9\u51fa\u865a\u5316\u7684\u5c4f\u5e55\u4e0e\u6e05\u6670\u7684\u5f71\u789f\u673a\u753b\u9762\uff0c\u5973\u90ce\u4ece\u5c4f\u5e55\u4e2d\u8df3\u51fa\u6765\uff0c\u5f71\u789f\u673a\u98de\u8dc3\u5230\u5979\u865a\u6258\u7684\u624b\u638c\u4e4b\u4e0a\uff0c\u5979\u6df1\u60c5\u7684\u51dd\u89c6\u7740\u5f71\u789f\u673a\uff0c\u4ee5\u5e73\u7f13\u58f0\u8c03\u8bf4\uff1a\u201c\u5f71\u89c6\u65b0\u9009\u62e9\u201d\uff0c\u53c8\u4ee5\u5347\u8c03\u5531\uff1a\u201c\u7231\u8fbe\u7535\u5668\u201d\uff0c\u5c4f\u5e55\u8f6c\u6210\u7231\u8fbe\u7535\u5b50\u7684\u4f01\u4e1a\u6807\u8bc6\u753b\u9762\uff0c\u96ea\u82b1\u70b9\uff0c\u7ed3\u675f\uff01</p><p>\u3000\u3000\u6807\u51c6\u7684\u4e94\u79d2\u5f71\u50cf\u53ea\u662f\u4fdd\u7559\u201c\u5f71\u89c6\u65b0\u9009\u62e9\u3001\u7231\u8fbe\u7535\u5668\u201d\u4ee5\u53ca\u7231\u8fbe\u7535\u5b50\u7684\u6807\u8bc6\u753b\u9762\u3002</p><p>\u3000\u3000\u8fd9\u662f\u6807\u51c6\u7684\u4ee5\u54c1\u724c\u544a\u77e5\u4e3a\u76ee\u7684\u7684\u5e7f\u544a\u7247\uff0c\u6240\u6709\u521b\u610f\u53ca\u753b\u9762\u8bbe\u8ba1\u90fd\u662f\u5f20\u606a\u8bbe\u5b9a\uff0c\u4f46\u662f\u9700\u8981\u5e7f\u544a\u516c\u53f8\u6309\u7167\u4ed6\u7684\u601d\u8def\u5236\u4f5c\u51fa\u6765\u3002</p><p>\u3000\u3000\u77ed\u77ed\u5341\u4e94\u79d2\u7684\u5e7f\u544a\u5f71\u50cf\u7ed3\u675f\uff0c\u4ee4\u5c0f\u71d5\u6123\u5728\u90a3\u91cc\uff1a\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u8f7b\u5439\u4e86\u4e00\u58f0\u53e3\u54e8\uff0c\u62ff\u7740\u62a5\u559c\u9e4a\u7684\u65cb\u5f8b\u620f\u8c11\u7684\u8bf4\uff1a\u201c\u5355\u7eaf\u7684\u9752\u5e74\u7537\u5973\u554a\uff01\u201d\u770b\u7740\u4ee4\u5c0f\u71d5\u8138\u4e0a\u60ca\u6ede\u7684\u8868\u60c5\u8f6c\u77ac\u95f4\u5d29\u6e83\uff0c\u6797\u51b0\u3001\u8bb8\u7ef4\u3001\u6c5f\u9edb\u513f\u5374\u662f\u660e\u767d\u8fc7\u6765\uff0c\u54c4\u7b11\u8d77\u6765\uff0c\u6797\u51b0\u8df3\u8d77\u6765\u6293\u4f4f\u5979\uff1a\u201c\u90a3\u79cd\u5e26\u5b50\u5230\u5e95\u662f\u4ec0\u4e48\uff0c\u5feb\u8bf4\u6765\u542c\u542c\uff1f\u201d</p><p>\u3000\u3000\u539f\u6765\u4ee4\u5c0f\u71d5\u4e5f\u6709\u63aa\u624b\u4e0d\u53ca\u3001\u6ee1\u9762\u7f9e\u7ea2\u7684\u65f6\u5019\uff0c\u5f20\u606a\u4ece\u53d1\u6123\u7684\u8bb8\u7ef4\u624b\u91cc\u62ff\u8fc7\u9065\u63a7\u5668\uff0c\u53c8\u5c06\u90a3\u6bb5\u5341\u4e94\u79d2\u5e7f\u544a\u53cd\u590d\u64ad\u653e\u4e86\u51e0\u904d\uff0c\u4e0d\u7406\u4f1a\u5728\u53e6\u4e00\u7ec4\u6c99\u53d1\u4e0a\u626d\u6253\u7684\u5973\u5b69\u5b50\u4eec\uff0c\u7ad9\u8d77\u6765\u5c06\u5f55\u50cf\u5e26\u9000\u51fa\u6765\uff0c\u4ea4\u7ed9\u5085\u4fca\u8bf4\uff1a\u201c\u6253\u56de\u53bb\uff0c\u5e7f\u544a\u5973\u90ce\u591f\u593a\u76ee\uff0c\u4f46\u662f\u773c\u795e\u6da3\u6563\uff0c\u8138\u4e0a\u7684\u8868\u60c5\u6709\u4e9b\u5446\u6ede\uff0c\u6ca1\u6709\u6c81\u4eba\u5fc3\u813e\u7684\u6e05\u65b0\u5f62\u8c61\uff0c\u58f0\u7ebf\u6709\u4e9b\u5355\u8c03\uff0c\u7f3a\u4e4f\u6e05\u4eae\u3001\u6d1e\u5f7b\u7684\u90a3\u79cd\u611f\u89c9\uff0c\u4f60\u8ba9\u4ed6\u4eec\u627e\u90a3\u79cd\u6709\u7740\u6d77\u8c5a\u9f3b\u97f3\u7684\u4eba\u914d\u97f3\uff0c\u5e7f\u544a\u5973\u90ce\u8981\u662f\u53ea\u4f1a\u629b\u5a9a\u773c\uff0c\u8bf7\u5e7f\u544a\u516c\u53f8\u8003\u8651\u6362\u4eba\u2026\u2026\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u89c6\u7ebf\u8f6c\u79fb\u5230\u6b63\u7b11\u5f97\u5598\u6c14\u7684\u6c5f\u9edb\u513f\u8eab\u4e0a\uff0c\u5373\u4f7f\u538b\u6291\u7740\u7684\u7b11\u58f0\u4e5f\u6709\u6c81\u4eba\u5fc3\u813e\u7684\u611f\u89c9\uff0c\u604d\u7136\u95f4\u773c\u775b\u4eae\u4e86\u8d77\u6765\uff0c\u773c\u524d\u4e0d\u662f\u5c31\u6709\u5408\u9002\u7684\u4eba\u9009\uff1f\u5f20\u606a\u5567\u5567\u7684\u5482\u7740\u5634\uff0c\u8d70\u5230\u6c5f\u9edb\u513f\u8eab\u8fb9\uff0c\u51dd\u89c6\u7740\u5979\u6e05\u4e3d\u7edd\u7f8e\u7684\u8138\u86cb\uff0c\u5507\u5f62\u6781\u7f8e\uff0c\u5374\u4e0d\u4e4f\u4fcf\u76ae\u7684\u611f\u89c9\uff0c\u8fd9\u6837\u7684\u5634\u5507\u8f7b\u54fc\u7740\u62a5\u559c\u9e4a\u7684\u65cb\u5f8b\u8df3\u5165\u753b\u9762\u7edd\u5bf9\u80fd\u5438\u5f15\u4eba\u7684\u773c\u7403\u2026\u2026</p><p>\u3000\u3000\u201c\u4f60\u770b\u4ec0\u4e48\uff1f\u201d\u6c5f\u9edb\u513f\u7f9e\u6da9\u95ea\u8eb2\u5f20\u606a\u707c\u4eba\u7684\u89c6\u7ebf\u3002</p><p>\u3000\u3000\u201c\u9edb\u513f\u59d0\u4e0d\u662f\u7b54\u5e94\u7ed9\u6211\u505a\u6691\u671f\u5de5\uff1f\u201d\u5f20\u606a\u8f6c\u8eab\u5bf9\u5085\u4fca\u8bf4\uff0c\u201c\u6211\u89c9\u5f97\u9edb\u513f\u53ef\u4ee5\uff0c\u4f60\u9001\u5979\u8fc7\u53bb\u8bd5\u955c\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u7ef4\u542c\u51fa\u6765\u4e86\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u4ec0\u4e48\u8bd5\u955c\u4e0d\u8bd5\u955c\uff0c\u4f60\u4e0d\u4f1a\u8ba9\u9edb\u513f\u5e2e\u4f60\u62cd\u5e7f\u544a\u7247\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u53ef\u4ee5\u5417\uff1f\u201d\u5f20\u606a\u7709\u5934\u5fae\u626c\uff0c\u626d\u5934\u7ee7\u7eed\u51dd\u89c6\u7740\u6c5f\u9edb\u513f\u5a07\u8273\u7684\u6807\u51c6\u7f8e\u4eba\u8138\uff0c\u624b\u538b\u7740\u5979\u7a0d\u6709\u4e9b\u84ec\u677e\u7684\u79c0\u53d1\uff0c\u201c\u6211\u89c9\u5f97\u53ef\u4ee5\u62cd\u51fa\u6211\u6240\u9700\u8981\u7684\u6548\u679c\u6765\uff0c\u4e0d\u4ec5\u5e7f\u544a\u7247\uff0c\u6240\u6709\u7684\u5ba3\u4f20\u62db\u8d34\u3001\u5f62\u8c61\u63a8\u5e7f\u90fd\u8981\u6362\u6210\u9edb\u513f\u59d0\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u90a3\u6de1\u6de1\u7684\u5374\u6709\u7740\u4e0d\u5bb9\u7f6e\u7591\u7684\u575a\u5b9a\u8bed\u6c14\uff0c\u8ba9\u6c5f\u9edb\u513f\u6ca1\u6709\u60f3\u7740\u8981\u62d2\u7edd\uff0c\u53ea\u662f\u5fc3\u865a\u7684\u8bf4\uff1a\u201c\u6211\u600e\u4e48\u505a\u5f97\u6765\u2026\u2026\u201d\u5fd8\u4e86\u8981\u8ba9\u5f20\u606a\u538b\u5728\u5979\u5934\u4e0a\u7684\u624b\u79fb\u5f00\uff0c\u8ba9\u4ed6\u707c\u4eba\u7684\u773c\u795e\u5b9a\u775b\u770b\u7740\u53c8\u6709\u4e9b\u5fc3\u614c\uff0c\u5fc3\u91cc\u672a\u5c1d\u6ca1\u6709\u4e00\u4e1d\u671f\u5f85\uff0c\u4f46\u662f\u4ece\u5c0f\u5230\u5927\uff0c\u6ca1\u6709\u5c1d\u8bd5\u7740\u53bb\u505a\u4ec0\u4e48\u4e8b\uff0c\u96be\u514d\u6ca1\u6709\u81ea\u4fe1\u3002</p><p>\u3000\u3000\u201c\u53ef\u4ee5\u7684\uff0c\u9edb\u513f\u4ece\u5c0f\u5c31\u5b66\u8fc7\u58f0\u4e50\u3001\u5f62\u4f53\uff0c\u8fd9\u4e48\u6f02\u4eae\uff0c\u4e0d\u5f53\u660e\u661f\u771f\u662f\u6d6a\u8d39\u4e86\uff0c\u201d\u4ee4\u5c0f\u71d5\u5174\u594b\u7684\u8bf4\uff0c\u201c\u4ee5\u524d\u5728\u8857\u4e0a\u5c31\u9047\u5230\u4eba\u8981\u62c9\u9edb\u513f\u53bb\u8bd5\u955c\uff0c\u8fd8\u82e6\u82e6\u7ea0\u7f20\u4e86\u597d\u4e45\uff0c\u53ea\u662f\u6211\u4eec\u90fd\u62c5\u5fc3\u90a3\u4eba\u53e6\u6709\u610f\u56fe\uff0c\u624d\u6ca1\u9f13\u52a8\u9edb\u513f\u53bb\u8bd5\u955c\uff0c\u8fd9\u6b21\u65e0\u6240\u8c13\u4e86\uff0c\u6307\u4e0d\u5b9a\u9edb\u513f\u5fc3\u91cc\u671f\u5f85\u7ed9\u67d0\u7537\u62d0\u8d70\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u80e1\u8bf4\u516b\u9053\u4ec0\u4e48\u2026\u2026\u201d\u6c5f\u9edb\u513f\u5a07\u55d4\u7740\u6253\u65ad\u4ee4\u5c0f\u71d5\u7684\u620f\u5f04\uff0c\u5a07\u67d4\u800c\u8f7b\u7075\u7684\u58f0\u97f3\uff0c\u8ba9\u5f20\u606a\u6108\u53d1\u80af\u5b9a\u81ea\u5df1\u7684\u5224\u65ad\uff0c\u4f46\u662f\u6c5f\u9edb\u513f\u7684\u4e0d\u81ea\u4fe1\u4f3c\u4e4e\u4e0e\u751f\u4ff1\u6765\uff0c\u201c\u6211\u600e\u4e48\u505a\u5f97\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u4e2a\u4e16\u754c\u5bf9\u4e8e\u7f8e\u4e3d\u7684\u5973\u4eba\uff0c\u9664\u4e86\u5e0c\u671b\u5979\u4eec\u66f4\u6f02\u4eae\u4e00\u4e9b\uff0c\u4f3c\u4e4e\u4e0d\u518d\u6709\u522b\u7684\u671f\u5f85\uff0c\u5373\u4f7f\u6c5f\u9edb\u513f\u7684\u7236\u6bcd\u5bf9\u5979\u4e5f\u6ca1\u592a\u591a\u7684\u671f\u5f85\uff0c\u4f18\u52bf\u7684\u5bb6\u5ead\u73af\u5883\uff0c\u8ba9\u5979\u65e0\u6cd5\u50cf\u8bb8\u7ef4\u3001\u4ee4\u5c0f\u71d5\u90a3\u6837\u81ea\u7acb\uff0c\u4e00\u65e6\u7f8e\u4e3d\u7684\u5916\u8868\u6210\u4e3a\u7d2f\u8d58\uff0c\u5927\u6982\u6c5f\u9edb\u513f\u81ea\u8eab\u4e5f\u6709\u4e00\u65e0\u662f\u5904\u7684\u4e0d\u81ea\u4fe1\u5427\uff1b\u5373\u4f7f\u5e94\u8058\u6691\u671f\u5de5\uff0c\u4e5f\u4f1a\u987e\u8651\u7740\u5bf9\u65b9\u662f\u4e0d\u662f\u53ea\u5728\u610f\u81ea\u5df1\u7684\u5916\u8c8c\u800c\u60ca\u60f6\u8eb2\u907f\u3002</p><p>\u3000\u3000\u201c\u9edb\u513f\u5012\u662f\u53ef\u4ee5\u8bd5\u4e00\u8bd5\u2026\u2026\u201d\u8bb8\u7ef4\u4e5f\u529d\u6c5f\u9edb\u513f\uff0c\u201c\u4f60\u590f\u5929\u7559\u5728\u5b66\u6821\uff0c\u603b\u8981\u627e\u4e9b\u4e8b\u60c5\u6253\u53d1\u65f6\u95f4\u2026\u2026\u201d</p><p>\u3000\u3000\u4ed4\u7ec6\u4f53\u5473\u8d77\u6765\uff0c\u5fc3\u91cc\u8fd8\u6709\u4e00\u5c42\u6015\u8ba9\u5f20\u606a\u5931\u671b\u7684\u62c5\u5fe7\uff0c\u4f46\u662f\u5927\u5bb6\u90fd\u5c3d\u5fc3\u529d\u5979\uff0c\u4ee4\u5c0f\u71d5\u66f4\u662f\u4e0d\u9057\u4f59\u529b\u7684\u9f13\u52a8\u5979\uff0c\u6c5f\u9edb\u513f\u6700\u7ec8\u72b9\u8c6b\u7740\u7b54\u5e94\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u65f6\u95f4\u5f88\u7d27\uff0c\u6068\u4e0d\u80fd\u7acb\u5373\u62c9\u7740\u6c5f\u9edb\u513f\u9a6c\u4e0a\u5c31\u53bb\u8bd5\u955c\uff0c\u8d81\u70ed\u6253\u94c1\u5c06\u4e8b\u60c5\u5b9a\u4e0b\u6765\u3002\u6731\u5c0f\u519b\u4e0d\u9002\u65f6\u7684\u51fa\u73b0\uff0c\u624b\u91cc\u8fd8\u63d0\u7740\u8ba9\u5973\u5b69\u5b50\u5c16\u53eb\u7684\u81ed\u8c46\u8150\uff0c\u9a6c\u4e0a\u5c31\u62c9\u6c5f\u9edb\u513f\u53bb\u8bd5\u955c\u7684\u5ff5\u5934\u53ea\u5f97\u803d\u6401\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ec8\u7a76\u62c5\u5fc3\u8bb8\u7ef4\u7684\u8eab\u4f53\u72b6\u51b5\uff0c\u6ca1\u6709\u7559\u4e0b\u6765\u8ddf\u8bb8\u7ef4\u3001\u6c5f\u9edb\u513f\u4e89\u98df\u81ed\u8c46\u8150\uff0c\u5c06\u5e7f\u544a\u7247\u7684\u62f7\u8d1d\u7559\u4e0b\u6765\uff0c\u501f\u53e3\u53bb\u5b89\u6392\u665a\u9910\u7684\u4e8b\u60c5\u8131\u8eab\u53bb\u627e\u533b\u9662\u7ed9\u8bb8\u7ef4\u5b89\u6392\u7684\u8d1f\u8d23\u533b\u751f\u3002</p><p>\u3000\u3000\u4eca\u5929\u4e0a\u5348\u5b89\u6392\u7684\u68c0\u67e5\u5e76\u4e0d\u4e50\u89c2\uff0c\u56e0\u4e3a\u957f\u671f\u7684\u8425\u517b\u4e0d\u826f\u4e0e\u75b2\u5026\uff0c\u5fc3\u810f\u627f\u53d7\u7684\u538b\u529b\uff0c\u8ba9\u8bb8\u7ef4\u5728\u7701\u519b\u533b\u5927\u6240\u65bd\u884c\u7684\u5fc3\u810f\u4e8c\u5c16\u74e3\u624b\u672f\u5df2\u7ecf\u9010\u6e10\u5931\u53bb\u6548\u679c\uff0c\u867d\u7136\u4e0d\u81f3\u4e8e\u4e00\u4e0b\u5b50\u5d29\u574f\uff0c\u4f46\u662f\u518d\u53d1\u5c55\u4e0b\u53bb\u7edd\u4e0d\u5bb9\u4e50\u89c2\uff0c\u5373\u4f7f\u8bb8\u7ef4\u81ea\u8eab\u4e5f\u5e94\u8be5\u611f\u89c9\u5230\u8eab\u4f53\u51fa\u73b0\u4e00\u4e9b\u624b\u672f\u524d\u7684\u75c7\u72b6\uff0c\u53ea\u662f\u5979\u7792\u7740\u6ca1\u8bf4\u3002\u533b\u9662\u627e\u6765\u8bb8\u7ef4\u5728\u7701\u519b\u533b\u5927\u6cbb\u7597\u65f6\u7684\u4e3b\u6cbb\u533b\u751f\uff0c\u4e00\u8d77\u7814\u7a76\u8bb8\u7ef4\u76ee\u524d\u7684\u72b6\u51b5\uff0c\u624d\u80fd\u7ed9\u51fa\u6700\u7ec8\u7684\u610f\u89c1\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u90fd\u4e0d\u80fd\u518d\u8ba9\u8bb8\u7ef4\u7531\u7740\u81ea\u5df1\u7684\u6027\u5b50\u6765\uff0c\u5f20\u606a\u53ef\u4e0d\u60f3\u770b\u5230\u8bb8\u601d\u4f24\u6b32\u7edd\u7684\u6837\u5b50\uff0c\u4f1a\u8ba9\u4ed6\u81ea\u5df1\u7684\u5fc3\u5148\u788e\u6389\uff0c\u8bb8\u601d\u5df2\u7ecf\u4e3a\u6b64\u627f\u53d7\u592a\u591a\u4e86\u3002</p></article>"}}}]);
