(window.webpackJsonp=window.webpackJsonp||[]).push([[185],{207:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e8c\u8282 \u6311\u6218",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e8c\u8282 \u6311\u6218</h2><p>\u8fd9\u4e2a\u5ff5\u5934\u9661\u7136\u95ea\u8fc7\uff0c\u518d\u8054\u60f3\u8d77\u51db\u7684\u5bf9\u624b\u4eec\uff0c\u65e0\u4e00\u4f8b\u5916\u5168\u90fd\u662f\u4e0a\u6765\u5c31\u4e13\u95e8\u9488\u5bf9\u706b\u7130\u9b54\u6cd5\u5e03\u7f6e\u9632\u5fa1\uff0c\u743c\u6069\u987f\u65f6\u6709\u4e9b\u660e\u767d\u8fc7\u6765\u3002\u5982\u679c\u8fd9\u4e2a\u63a8\u6d4b\u5c5e\u5b9e\u7684\u8bdd\uff0c\u8fd9\u4e9b\u770b\u8d77\u6765\u4e0d\u53ef\u7406\u89e3\u7684\u884c\u4e3a\u5012\u90fd\u662f\u6709\u4e86\u7b54\u6848\u3002\u7136\u800c\u4e16\u754c\u4e0a\u6709\u8fd9\u6837\u7684\u5deb\u5e08\u4e48\uff0c\u504f\u79d1\u4e5f\u672a\u514d\u504f\u5f97\u592a\u4e25\u91cd\u4e86\u5427\u3002</p><p>\u674e\u65af\u7279\u7684\u53ec\u5524\u602a\u7269\u5927\u519b\u5df2\u7ecf\u57fa\u672c\u6210\u578b\uff0c\u6570\u91cf\u8d85\u8fc7\u4e86\u5341\u53ea\uff0c\u4e00\u58f0\u53f7\u4ee4\u4e4b\u4e0b\uff0c\u9f50\u9f50\u51b2\u51fa\u4e86\u6291\u706b\u6cd5\u9635\u7684\u7b3c\u7f69\u8303\u56f4\uff0c\u671d\u51db\u6251\u4e86\u8fc7\u53bb\u3002\u51db\u540e\u9000\u534a\u6b65\uff0c\u53d6\u51fa\u4e00\u679a\u6df1\u7ea2\u8272\u692d\u5706\u5f62\u5b9d\u77f3\u5f80\u5730\u4e0a\u4e00\u63b7\uff0c\u5657\u5730\u817e\u8d77\u4e00\u9053\u706b\u5899\u6765\uff0c\u5c06\u602a\u7269\u6321\u4f4f\u3002</p><p>\u706b\u5899\u5f88\u957f\uff0c\u5c06\u6574\u4e2a\u64c2\u53f0\u5207\u5f00\uff0c\u602a\u7269\u4eec\u65e0\u6cd5\u4ece\u65c1\u8fb9\u7ed5\u8fc7\u53bb\u3002\u674e\u65af\u7279\u4f4e\u58f0\u8bf5\u5492\uff0c\u547d\u4ee4\u602a\u7269\u4eec\u6682\u65f6\u540e\u64a4\u3002\u706b\u5899\u672f\u662f\u6ca1\u6709\u65f6\u95f4\u9650\u5236\u7684\uff0c\u4ece\u7406\u8bba\u4e0a\u6765\u8bf4\uff0c\u53ea\u8981\u5deb\u5e08\u80fd\u4e00\u76f4\u96c6\u4e2d\u7cbe\u529b\u53bb\u7ef4\u6301\u6cd5\u672f\uff0c\u706b\u5899\u5c31\u80fd\u4e00\u76f4\u71c3\u70e7\u4e0b\u53bb\u3002\u51db\u4f5c\u4e3a\u4e13\u7cbe\u5851\u80fd\u5deb\u5e08\uff0c\u521b\u9020\u51fa\u6765\u7684\u706b\u5899\u6740\u4f24\u529b\u5de8\u5927\uff0c\u674e\u65af\u7279\u7684\u53ec\u5524\u602a\u7269\u4eec\u6839\u672c\u4e0d\u6562\u786c\u95ef\u3002</p><p>\u201c\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u62db\u706b\u7cfb\u751f\u7269\uff1f\u201d</p><p>\u743c\u6069\u7591\u60d1\u7740\uff0c\u4ee5\u8fd9\u4f4d\u5492\u6cd5\u5deb\u5e08\u7684\u80fd\u529b\uff0c\u5b8c\u5168\u53ef\u4ee5\u53ec\u5524\u6765\u8db3\u591f\u5f3a\u529b\u7684\u706b\u5143\u7d20\u6216\u8005\u706b\u8725\u8734\u4e4b\u7c7b\uff0c\u8fd9\u4e9b\u602a\u7269\u662f\u4e0d\u6015\u706b\u7684\u3002\u65e2\u7136\u77e5\u9053\u51db\u559c\u6b22\u7528\u706b\u7130\u9b54\u6cd5\u653b\u51fb\uff0c\u90a3\u4e48\u9488\u5bf9\u6027\u5730\u53ec\u5524\u706b\u7cfb\u751f\u7269\u663e\u7136\u662f\u6700\u5408\u9002\u7684\u9009\u62e9\u5427\u3002</p><p>\u89c1\u5bf9\u624b\u7684\u53ec\u5524\u602a\u7269\u7f13\u6162\u9000\u5374\uff0c\u51db\u7a81\u7136\u62ac\u8d77\u81ea\u5df1\u7684\u5de6\u624b\uff0c\u5979\u7684\u624b\u81c2\u4e0a\u900f\u51fa\u4e00\u56e2\u767d\u5149\uff0c\u649e\u5165\u706b\u5899\u4e4b\u4e2d\uff0c\u968f\u5373\u7a81\u7136\u72c2\u98ce\u5927\u4f5c\u3002\u706b\u5899\u88ab\u8fd9\u72c2\u98ce\u4e00\u63a8\u4e00\u5377\uff0c\u8f70\u7136\u9f13\u80c0\uff0c\u7ffb\u7ffb\u6eda\u6eda\uff0c\u5f80\u524d\u65b9\u6251\u53bb\uff0c\u77ac\u95f4\u5c06\u90a3\u4e9b\u9000\u5374\u4e0d\u53ca\u7684\u53ec\u5524\u602a\u7269\u5c3d\u6570\u541e\u566c\u3002</p><p>\u674e\u65af\u7279\u89c1\u8f9b\u8f9b\u82e6\u82e6\u62db\u51fa\u7684\u602a\u7269\u5168\u519b\u8986\u6ca1\uff0c\u8138\u4e0a\u4e0d\u7531\u5f97\u4e5f\u5fae\u5fae\u53d8\u8272\uff0c\u624b\u6307\u5feb\u901f\u98a4\u52a8\uff0c\u5728\u7a7a\u6c14\u4e2d\u6025\u901f\u5212\u51fa\u4e00\u4e2a\u7b26\u5370\u7684\u8f6e\u5ed3\u3002\u4e0e\u6b64\u540c\u65f6\uff0c\u5bf9\u9762\u7684\u51db\u7a81\u7136\u53c8\u5f39\u51fa\u4e00\u9897\u6d45\u7ea2\u8272\u5b9d\u77f3\u6765\uff0c\u534a\u7a7a\u70b8\u5f00\uff0c\u5316\u6210\u56db\u4e2a\u706b\u5806\uff0c\u52a0\u4e0a\u5979\u4e00\u5f00\u59cb\u767b\u573a\u5c31\u70b9\u71c3\u7684\u56db\u4e2a\uff0c\u64c2\u53f0\u4e0a\u5df2\u7ecf\u6709\u4e86\u516b\u4e2a\u706b\u5806\uff0c\u904d\u5e03\u5404\u5904\u3002\u743c\u6069\u6b63\u4e0d\u77e5\u9053\u5979\u5230\u5e95\u60f3\u5e72\u4ec0\u4e48\uff0c\u5374\u89c1\u5492\u6cd5\u5deb\u5e08\u5df2\u7ecf\u5b8c\u6210\u4e86\u6cd5\u672f\uff0c\u4e00\u4e2a\u5df4\u638c\u5927\u5c0f\u7684\u5706\u5f62\u7b26\u5370\u5728\u4ed6\u7684\u6307\u5c16\u5f62\u6210\uff0c\u201c\u55e4\u201d\u5730\u5f39\u51fa\u4e00\u652f\u9752\u78a7\u8272\u7684\u7bad\u77e2\u6765\uff0c\u671d\u51db\u75be\u5c04\u800c\u53bb\u3002</p><p>\u201c\u5f3a\u9178\u7bad\u3002\u201d</p><p>\u5492\u6cd5\u5b66\u6d3e\u88ab\u79f0\u4e3a\u201c\u6700\u5168\u80fd\u201d\u7684\u5b66\u6d3e\uff0c\u5176\u539f\u56e0\u5c31\u5728\u4e8e\u8fd9\u4e00\u5b66\u6d3e\u7684\u6cd5\u672f\u5e94\u7528\u6781\u5e7f\uff0c\u80fd\u653b\u51fb\u80fd\u9632\u5fa1\u80fd\u5e03\u9b54\u6cd5\u9635\u8f85\u52a9\uff0c\u80fd\u53ec\u5524\u602a\u7269\u51b2\u950b\uff0c\u80fd\u4f20\u9001\u9003\u8131\u3002\u8fd9\u9053\u5f3a\u9178\u7bad\u5c31\u662f\u5492\u6cd5\u5b66\u6d3e\u7684\u76f4\u63a5\u653b\u51fb\u624b\u6bb5\u4e4b\u4e00\uff0c\u7c7b\u4f3c\u706b\u7130\u7bad\u6216\u8005\u5bd2\u51b0\u7bad\u90a3\u79cd\u6cd5\u672f\uff0c\u53ea\u662f\u6548\u679c\u662f\u5f3a\u9178\u8150\u8680\u7f62\u4e86\u3002</p><p>\u51db\u70b9\u8d77\u706b\u5806\uff0c\u89c1\u5f3a\u9178\u7bad\u5c04\u6765\uff0c\u654f\u6377\u5730\u6a2a\u8de8\u4e00\u6b65\uff0c\u8d70\u5230\u65c1\u8fb9\u7684\u706b\u5806\u91cc\u53bb\u4e86\u3002</p><p>\u201c\u554a\uff1f\u201d</p><p>\u743c\u6069\u5403\u4e86\u4e00\u60ca\uff0c\u5979\u53c8\u4e0d\u66fe\u7ed9\u81ea\u5df1\u65bd\u52a0\u4efb\u4f55\u9632\u62a4\uff0c\u76f4\u63a5\u5c31\u8fd9\u6837\u8d70\u8fdb\u706b\u7130\uff0c\u5c82\u4e0d\u4f1a\u6d3b\u6d3b\u70e7\u6b7b\u3002\u65c1\u8fb9\u7684\u6885\u83f2\u65af\u5374\u5b89\u5750\u4e0d\u52a8\uff0c\u5168\u4e0d\u60ca\u8bb6\uff0c\u663e\u7136\u65e9\u5c31\u77e5\u9053\u5979\u4f1a\u5982\u6b64\u3002\u53ea\u89c1\u51db\u8d70\u5165\u706b\u5806\uff0c\u8eab\u5f62\u77ac\u95f4\u6d88\u5931\uff0c\u968f\u5373\u8fdc\u5904\u53e6\u5916\u4e00\u4e2a\u706b\u5806\u91cc\u4eba\u5f71\u4e00\u6643\uff0c\u51db\u4ece\u4e2d\u8d70\u4e86\u51fa\u6765\u3002</p><p>\u743c\u6069\u770b\u5f97\u76ee\u77aa\u53e3\u5446\uff0c\u4e0d\u77e5\u9053\u8fd9\u662f\u4ec0\u4e48\u6cd5\u672f\uff0c\u5374\u89c1\u5979\u8f6c\u8eab\u53c8\u8d70\u5165\u706b\u7130\u4e2d\uff0c\u4e0b\u4e00\u77ac\u95f4\u53c8\u4ece\u53e6\u5916\u4e00\u4e2a\u706b\u5806\u91cc\u5192\u51fa\u6765\uff0c\u8f6c\u773c\u4e4b\u95f4\u5979\u5df2\u7ecf\u53d8\u5e7b\u4e86\u56db\u4e94\u4e2a\u4f4d\u7f6e\u3002\u5492\u6cd5\u5deb\u5e08\u8fde\u8fde\u50ac\u52a8\u7b26\u5370\uff0c\u4e00\u9053\u53c8\u4e00\u9053\u7684\u5f3a\u9178\u7bad\u8fde\u73af\u5c04\u51fa\uff0c\u5374\u603b\u88ab\u5979\u8f7b\u677e\u501f\u52a9\u706b\u5806\u77ac\u79fb\u907f\u8fc7\u3002\u743c\u6069\u603b\u7b97\u77e5\u9053\u5979\u4e3a\u4ec0\u4e48\u4e00\u4e0a\u6765\u5c31\u5148\u70b9\u706b\u5806\u4e86\u2014\u2014\u8fd9\u64c2\u53f0\u4e0a\u516b\u4e2a\u706b\u5806\uff0c\u5bf9\u5979\u6765\u8bf4\u5c31\u76f8\u5f53\u4e8e\u516b\u4e2a\u4f20\u9001\u95e8\uff0c\u77ac\u95f4\u8df3\u8dc3\uff0c\u968f\u610f\u8f6c\u79fb\uff0c\u6709\u8fd9\u6837\u5f3a\u5927\u7684\u673a\u52a8\u529b\uff0c\u54ea\u91cc\u8fd8\u754f\u60e7\u5bf9\u624b\u7684\u653b\u51fb\uff0c\u54ea\u91cc\u8fd8\u9700\u8981\u4ec0\u4e48\u9632\u5fa1\u9b54\u6cd5\u3002\u9664\u975e\u5bf9\u624b\u80fd\u7528\u5927\u8303\u56f4\u6cd5\u672f\u628a\u6574\u4e2a\u64c2\u53f0\u90fd\u8f70\u4e0b\uff0c\u4f46\u90a3\u6837\u4e00\u6765\u81ea\u8eab\u4e5f\u96be\u4fdd\u4e86\u3002</p><p>\u8f6c\u5ff5\u4e4b\u95f4\uff0c\u51db\u53c8\u5f39\u51fa\u4e00\u679a\u5b9d\u77f3\u6765\uff0c\u4e8e\u662f\u64c2\u53f0\u4e0a\u53c8\u589e\u52a0\u4e86\u56db\u5806\u706b\u7130\uff0c\u5176\u4e2d\u4e00\u5806\u6b63\u5c31\u843d\u5728\u674e\u65af\u7279\u7684\u65c1\u8fb9\u3002\u6b63\u60f3\u518d\u6b21\u53ec\u5524\u602a\u7269\u7684\u5492\u6cd5\u5deb\u5e08\u8fde\u5fd9\u653e\u5f03\u65bd\u6cd5\uff0c\u614c\u614c\u5f20\u5f20\u60f3\u907f\u5f00\uff0c\u4f46\u5df2\u7ecf\u8fdf\u4e86\u3002\u706b\u7130\u4e00\u7ffb\uff0c\u51db\u7684\u8eab\u5f71\u4ece\u4e2d\u8dc3\u51fa\uff0c\u4e00\u811a\u8e22\u5728\u5492\u6cd5\u5deb\u5e08\u7684\u819d\u5f2f\u4e0a\uff0c\u767b\u65f6\u628a\u4ed6\u8e22\u8db4\u4e86\u4e0b\u6765\u3002</p><p>\u201c\u8ba4\u8f93\uff01\u201d\u5deb\u5e08\u5927\u558a\u3002</p><p>\u201c\u771f\u6ca1\u52b2\u3002\u201d\u51db\u53f9\u6c14\uff0c\u6325\u624b\u6563\u53bb\u5df2\u7ecf\u5728\u6307\u5c16\u805a\u96c6\u6210\u5f62\u7684\u56db\u9897\u7ea2\u8272\u98de\u5f39\u3002\u5982\u679c\u5bf9\u624b\u518d\u665a\u534a\u79d2\u949f\u8ba4\u8f93\u7684\u8bdd\uff0c\u8fd9\u56db\u9897\u9b54\u6cd5\u98de\u5f39\u5c31\u8981\u7ed3\u7ed3\u5b9e\u5b9e\u5730\u7838\u5728\u4ed6\u7684\u8111\u888b\u4e0a\u3002\u4f46\u5982\u4eca\u4ed6\u65e2\u7136\u5df2\u7ecf\u8ba4\u8f93\uff0c\u51db\u5c0f\u59d0\u81ea\u7136\u4e5f\u5c31\u4e0d\u4e3a\u5df1\u751a\uff0c\u5979\u64a4\u9500\u6cd5\u672f\uff0c\u6602\u9996\u633a\u80f8\u5730\u5728\u5931\u8d25\u8005\u8eab\u65c1\u8d70\u8fc7\uff0c\u4e0d\u77e5\u662f\u6709\u610f\u8fd8\u662f\u65e0\u610f\u5730\u5728\u4ed6\u8170\u4e0a\u53c8\u8e29\u4e86\u4e00\u811a\u3002</p><p>\u201c\u54ce\u5440\uff0c\u5bf9\u4e0d\u8d77\u5462\uff0c\u201d\u5979\u5de7\u7b11\u5ae3\u7136\u5730\u4f4e\u5934\u5bf9\u8db4\u5728\u5730\u4e0a\u7684\u5deb\u5e08\u8bf4\uff0c\u201c\u6ca1\u770b\u89c1\uff0c\u4e0d\u8fc7\u4f60\u4e5f\u592a\u80d6\u4e86\u70b9\u5462\uff0c\u8be5\u51cf\u80a5\u4e86\u3002\u201d</p><p>\u64c2\u53f0\u4e0b\u6b22\u547c\u96f7\u52a8\uff0c\u6570\u767e\u53f7\u7ea2\u888d\u5deb\u5e08\u548c\u5b66\u5f92\u6b22\u8fce\u7740\u4ed6\u4eec\u7684\u5929\u624d\u7f8e\u5c11\u5973\u5927\u83b7\u5168\u80dc\u3002\u51db\u7684\u8138\u4e0a\u4f9d\u65e7\u5e26\u7740\u90a3\u751c\u751c\u7b11\u5bb9\uff0c\u6325\u624b\u5411\u56db\u9762\u81f4\u610f\uff0c\u5979\u8d70\u5230\u64c2\u53f0\u4e2d\u95f4\uff0c\u6b63\u51c6\u5907\u8bf4\u8bdd\uff0c\u773c\u5149\u4e00\u8f6c\uff0c\u7a81\u7136\u770b\u89c1\u4e86\u5750\u5728\u53f0\u4e0b\u7684\u743c\u6069\u548c\u6885\u83f2\u65af\u4e24\u4eba\u3002</p><p>\u743c\u6069\u81ea\u7136\u7a7f\u7740\u4ed6\u7684\u9ed1\u8272\u5deb\u5e08\u888d\uff0c\u6885\u83f2\u65af\u5219\u7a7f\u7740\u6de1\u84dd\u8272\u7684\u886c\u886b\u548c\u6d45\u7070\u8272\u957f\u88e4\uff0c\u4e24\u4eba\u7684\u6253\u626e\u90fd\u8bf4\u4e0d\u4e0a\u591a\u4e48\u7279\u522b\uff0c\u4f46\u5728\u4e00\u7fa4\u7ea2\u888d\u5deb\u5e08\u91cc\u5c31\u683c\u5916\u663e\u773c\u4e86\uff0c\u4f55\u51b5\u8fd8\u5c31\u5750\u5728\u53f0\u4e0b\u3002\u521a\u624d\u6bd4\u8d5b\u7684\u65f6\u5019\uff0c\u51db\u5168\u795e\u8d2f\u6ce8\uff0c\u4e0d\u66fe\u5728\u610f\uff0c\u5982\u4eca\u5df2\u7ecf\u83b7\u80dc\uff0c\u5fc3\u60c5\u653e\u677e\uff0c\u4e00\u773c\u626b\u8fc7\u4fbf\u53d1\u73b0\u4e86\u3002</p><p>\u5979\u5148\u662f\u4f3c\u4e4e\u6123\u4e86\u4e0b\uff0c\u5927\u7ea6\u4e5f\u538b\u6839\u6ca1\u60f3\u5230\u4f1a\u5728\u8fd9\u91cc\u770b\u89c1\u6885\u83f2\u65af\uff0c\u4f46\u968f\u5373\u53c8\u5fae\u7b11\u4e86\u8d77\u6765\uff0c\u4eff\u4f5b\u770b\u5230\u4e86\u4ec0\u4e48\u7279\u522b\u5f00\u5fc3\u7684\u4e8b\u60c5\u3002\u5979\u8d70\u5230\u64c2\u53f0\u8fb9\uff0c\u8f7b\u76c8\u5730\u8df3\u4e86\u4e0b\u6765\uff0c\u957f\u53d1\u98d8\u6d12\uff0c\u8d85\u77ed\u88d9\u88ab\u98ce\u6380\u5f00\uff0c\u9732\u51fa\u91cc\u9762\u6625\u5149\uff0c\u5979\u6beb\u4e0d\u5728\u610f\u5730\u4f38\u624b\u6309\u4e86\u6309\uff0c\u7b11\u772f\u772f\u5730\u8d70\u5230\u743c\u6069\u548c\u6885\u83f2\u65af\u7684\u9762\u524d\u6765\u3002</p><p>\u201c\u597d\u4e45\u4e0d\u89c1\u554a\uff0c\u827e\u5f25\u8587\uff0c\u6ca1\u60f3\u5230\u4f1a\u5728\u8fd9\u91cc\u770b\u5230\u4f60\u5462\u3002\u201d</p><p>\u201c\u597d\u4e45\u4e0d\u89c1\uff0c\u51db\u3002\u201d\u6885\u83f2\u65af\u6c89\u9759\u5730\u56de\u7b54\u3002\u743c\u6069\u5728\u65c1\u8fb9\u89c9\u5f97\u5f88\u5947\u602a\uff0c\u5728\u521a\u542c\u5230\u51db\u7684\u6d88\u606f\u65f6\uff0c\u6885\u83f2\u65af\u7684\u53cd\u5e94\u9887\u4e3a\u5f3a\u70c8\uff0c\u73b0\u5728\u771f\u89c1\u5230\u4e86\uff0c\u5374\u53c8\u4f3c\u4e4e\u6de1\u6f20\u5f97\u5f88\uff0c\u771f\u4e0d\u77e5\u9053\u8fd9\u4e24\u4eba\u5230\u5e95\u662f\u4ec0\u4e48\u5173\u7cfb\u3002</p><p>\u201c\u54ce\u5440\uff0c\u54ce\u5440\uff0c\u201d\u51db\u6447\u5934\uff0c\u201c\u4f60\u8fd8\u662f\u90a3\u4e48\u6ca1\u52b2\u5462\uff0c\u8fd9\u6837\u4f1a\u4e0d\u8ba8\u7537\u5b69\u5b50\u559c\u6b22\u7684\u2026\u2026\u5514\uff1f\u201d\u5979\u6ce8\u610f\u5230\u4e86\u65c1\u8fb9\u7684\u743c\u6069\uff0c\u201c\u8fd9\u4f4d\u662f\u2026\u2026\u201d</p><p>\u201c\u743c\u6069\xb7\u5170\u5c3c\u65af\u7279\u3002\u201d\u743c\u6069\u8d77\u8eab\u884c\u793c\uff0c\u81ea\u6211\u4ecb\u7ecd\u3002\u8ddd\u79bb\u5982\u6b64\u8fd1\uff0c\u8ba9\u4ed6\u66f4\u6e05\u695a\u5730\u770b\u6e05\u4e86\u51db\u7684\u76f8\u8c8c\uff0c\u4ed6\u53d1\u73b0\u8fd9\u4f4d\u5c0f\u5973\u5deb\u7684\u773c\u7738\u4e5f\u662f\u78a7\u7eff\u8272\u7684\uff0c\u548c\u6885\u83f2\u65af\u5f88\u50cf\uff0c\u4f46\u989c\u8272\u8f83\u6d45\uff0c\u7565\u5e26\u4e9b\u6e56\u84dd\u8272\uff0c\u77b3\u5b54\u4e2d\u5374\u4f3c\u4e4e\u5e26\u7740\u5fae\u5fae\u7684\u8df3\u8dc3\u706b\u5149\u3002</p><p>\u201c\u5170\u5c3c\u65af\u7279\uff1f\u4f60\u770b\u6837\u5b50\u4e5f\u662f\u4e2a\u5deb\u5e08\u5427\u2026\u2026\u54e6\uff0c\u201d\u51db\u601d\u7d22\u7740\uff0c\u7a81\u7136\u53cc\u624b\u4e00\u62cd\uff0c\u201c\u6211\u77e5\u9053\u4e86\uff0c\u4f60\u662f\u4ece\u9634\u9b42\u57ce\u6765\u7684\u5bf9\u4e0d\u5bf9\u3002\u201d</p><p>\u201c\u6211\u6765\u81ea\u9634\u9b42\u57ce\u3002\u201d</p><p>\u51db\u504f\u7740\u5934\uff0c\u6253\u91cf\u7740\u743c\u6069\uff0c\u518d\u770b\u770b\u6885\u83f2\u65af\uff0c\u5634\u89d2\u5ffd\u7136\u9732\u51fa\u8c03\u76ae\u7684\u7b11\u610f\u6765\uff0c\u201c\u827e\u5f25\u8587\uff0c\u770b\u6765\u4f60\u7ec8\u4e8e\u6709\u6539\u53d8\u4e86\u5462\uff0c\u201d\u5979\u5938\u5956\u7740\uff0c\u201c\u6211\u4ece\u6ca1\u60f3\u8c61\u5230\u4f60\u4e5f\u4f1a\u559c\u6b22\u4e0a\u7537\u5b69\u5b50\uff0c\u800c\u4e14\u8fd8\u662f\u4e2a\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u3002\u201d</p><p>\u201c\u8fd9\u4e5f\u6ca1\u4ec0\u4e48\u5947\u602a\u7684\u5427\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u5e76\u4e0d\u5426\u8ba4\uff0c\u201c\u5012\u662f\u4f60\uff0c\u4f3c\u4e4e\u8fd8\u662f\u4e00\u4e2a\u4eba\u5462\u3002\u201d</p><p>\u51db\u683c\u683c\u5a07\u7b11\u7740\uff0c\u6b63\u8981\u8bf4\u8bdd\uff0c\u53f0\u4e0a\u7684\u88c1\u5224\u5374\u5df2\u7ecf\u5728\u53eb\u5979\u7684\u540d\u5b57\u3002\u201c\u90a3\u4e48\uff0c\u7b49\u4e0b\u5348\u6211\u518d\u6765\u627e\u4f60\u3002\u201d\u5979\u5bf9\u6885\u83f2\u65af\u8bf4\uff0c\u671d\u743c\u6069\u70b9\u70b9\u5934\uff0c\u8f7b\u76c8\u5730\u8d70\u5230\u64c2\u53f0\u8fb9\uff0c\u4e00\u8dc3\u800c\u4e0a\uff0c\u5728\u4e2d\u592e\u7ad9\u5b9a\u4e86\u3002</p><p>\u201c\u4eca\u5929\u6211\u5f88\u9ad8\u5174\uff0c\u56e0\u4e3a\u6211\u83b7\u5f97\u4e86\u51a0\u519b\uff0c\u867d\u7136\u8fd9\u672c\u6765\u4e5f\u5c31\u6ca1\u4ec0\u4e48\u60ac\u5ff5\uff0c\u4e0d\u8fc7\u540d\u81f3\u5b9e\u5f52\u603b\u662f\u4ee4\u4eba\u611f\u89c9\u6109\u5feb\u561b\uff0c\u201d\u5979\u7b11\u541f\u541f\u5730\uff0c\u8eac\u8eab\u5411\u8bc4\u59d4\u5e2d\u4e0a\u884c\u793c\uff0c\u201c\u5728\u6b64\u6211\u60f3\u611f\u8c22\u6211\u7684\u8001\u5e08\u8ff0\u5c14\u5148\u751f\uff0c\u611f\u8c22\u4ed6\u4e00\u76f4\u4ee5\u6765\u5bf9\u6211\u7684\u8f9b\u52e4\u6307\u70b9\uff1b\u611f\u8c22\u5404\u4f4d\u9996\u5e2d\u548c\u8d35\u5bbe\u5728\u767e\u5fd9\u4e2d\u62e8\u5197\u524d\u6765\uff0c\u89c2\u770b\u6211\u7684\u4e2a\u4eba\u8868\u6f14\uff1b\u611f\u8c22\u5728\u573a\u7684\u5404\u4f4d\u4e00\u76f4\u4e3a\u6211\u52a0\u6cb9\u52a9\u5a01\uff0c\u867d\u7136\u6211\u77e5\u9053\u4f60\u4eec\u66f4\u559c\u6b22\u6211\u7684\u88d9\u5b50\u518d\u53d8\u77ed\u4e00\u70b9\u70b9\uff0c\u53ef\u60dc\u8fd9\u6b21\u5df2\u7ecf\u6ca1\u673a\u4f1a\u4e86\uff0c\u90a3\u4e48\u7b49\u4e0b\u6b21\u5427\uff1b\u6700\u540e\uff0c\u6211\u8981\u7279\u522b\u611f\u8c22\u7684\uff0c\u662f\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u6bbf\u4e0b\u3002\u201d</p><p>\u5728\u573a\u6240\u6709\u4eba\u90fd\u6014\u4f4f\uff0c\u5979\u524d\u9762\u51e0\u53e5\u8bdd\uff0c\u867d\u7136\u8bf4\u5f97\u81ea\u8d1f\u6ee1\u6ee1\uff0c\u50b2\u6c14\u903c\u4eba\uff0c\u603b\u8fd8\u7b97\u80fd\u7406\u89e3\uff0c\u4f46\u6700\u540e\u8fd9\u53e5\u8bdd\u5374\u662f\u4ec0\u4e48\u610f\u601d\u3002\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u4e0d\u8fc7\u662f\u4e2a\u65c1\u89c2\u8005\uff0c\u4ece\u5934\u5230\u5c3e\u4ec0\u4e48\u90fd\u6ca1\u505a\uff0c\u611f\u8c22\u4ed6\u505a\u4ec0\u4e48\u3002</p><p>\u201c\u76f8\u4fe1\u5728\u5ea7\u5404\u4f4d\u90fd\u548c\u6211\u4e00\u6837\uff0c\u4ece\u8e0f\u4e0a\u5deb\u5e08\u9053\u8def\u7684\u90a3\u4e00\u5929\u8d77\uff0c\u5c31\u662f\u5728\u53e4\u9b54\u6cd5\u5e1d\u56fd\u8010\u745f\u745e\u5c14\u7684\u8f89\u714c\u4f20\u8bf4\u4e2d\u957f\u5927\uff0c\u201d\u5979\u5411\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u6df1\u6df1\u97a0\u8eac\uff0c\u8bed\u6c14\u975e\u5e38\u8bda\u631a\uff0c\u201c\u867d\u7136\u6211\u4eec\u7ea2\u888d\u5deb\u5e08\u5df2\u7ecf\u5efa\u8d77\u4eca\u65e5\u7684\u9b54\u6cd5\u5e1d\u56fd\uff0c\u4f46\u6614\u65e5\u7684\u5149\u8f89\u707f\u70c2\u5374\u662f\u6c38\u6052\u7684\u61a7\u61ac\u548c\u68a6\u60f3\u3002\u8eab\u4e3a\u5deb\u5e08\uff0c\u6211\u4e00\u76f4\u4ee5\u4e0d\u80fd\u751f\u5728\u4e24\u5343\u5e74\u524d\uff0c\u4eb2\u773c\u89c1\u8bc6\u53e4\u5e1d\u56fd\u795e\u5947\u7684\u9b54\u6cd5\u6280\u827a\u4e3a\u9057\u61be\uff0c\u4f46\u5e78\u8fd0\u7684\u662f\uff0c\u4eca\u5929\u738b\u5b50\u6bbf\u4e0b\u53ef\u4ee5\u5e2e\u6211\u8fbe\u6210\u8fd9\u4e2a\u4e00\u76f4\u4ee5\u6765\u7684\u5c0f\u5c0f\u613f\u671b\uff0c\u201d\u5979\u751c\u751c\u5730\u7b11\u7740\uff0c\u8bf4\u4e0d\u51fa\u7684\u5a07\u4fcf\u53ef\u7231\uff0c\u201c\u6211\u76f8\u4fe1\u60a8\u4e00\u5b9a\u4e0d\u4f1a\u5fcd\u5fc3\u62d2\u7edd\u3002\u201d</p></article>"}}}]);
