(window.webpackJsonp=window.webpackJsonp||[]).push([[185],{208:function(p){p.exports={chapterTitle:null,title:"\u7b2c1161\u7ae0 \u706b\u8f66\u4e0a\u72ed\u8def\u76f8\u9022",content:"<article><h2>\u7b2c1161\u7ae0 \u706b\u8f66\u4e0a\u72ed\u8def\u76f8\u9022</h2><p>\u3000\u3000\u5f20\u606a\u5728\u6d77\u5dde\u5dee\u4e0d\u591a\u4f4f\u4e86\u6709\u534a\u4e2a\u6708\uff0c\u56de\u5230\u5efa\u90ba\u65f6\uff0c\u5c31\u6709\u6d88\u606f\u8bf4\u53ef\u80fd\u5728\u4e91\u6797\u8bc1\u5238\u6316\u51fa\u5927\u6848\u6765\uff0c\u5bfc\u706b\u7d22\u5c31\u662f\u9648\u5176\u658c\u6848\u3002</p><p>\u3000\u3000\u9648\u5176\u658c\u4e4b\u5b50\u9648\u52c7\u901a\u8fc7\u7f34\u7eb3\u4fdd\u8bc1\u91d1\u7684\u5f62\u884c\u5411\u4e91\u6797\u8bc1\u5238\u5efa\u90ba\u73af\u6e56\u5357\u8def\u8425\u4e1a\u90e8\u878d\u8d44\u4e09\u5343\u4e07\u64cd\u4f5c\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u7968\uff0c\u8fd9\u8fdd\u53cd\u4e86\u8bc1\u76d1\u4f1a\u7981\u6b62\u8bc1\u5238\u516c\u53f8\u5411\u5ba2\u6237\u63d0\u4f9b\u878d\u8d44\u878d\u5238\u4e1a\u52a1\u7684\u7981\u4ee4\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u95ee\u9898\u672c\u8eab\u5e76\u4e0d\u7b97\u5341\u5206\u4e25\u91cd\uff0c\u5f88\u591a\u8bc1\u5238\u516c\u53f8\u90fd\u641e\u8fd9\u6837\u7684\u64e6\u8fb9\u7403\u3002\u7531\u4e8e\u6d77\u7c9f\u79d1\u6280\u80a1\u4ef7\u8fd9\u6bb5\u65f6\u95f4\u5f3a\u52bf\u56de\u5347\uff0c\u586b\u8865\u524d\u6bb5\u65f6\u95f4\u7684\u5927\u8dcc\u5e45\uff0c\u5728\u5f97\u5230\u5185\u90e8\u6d88\u606f\u540e\u4e91\u6797\u8bc1\u5238\u5bf9\u9648\u52c7\u7684\u7092\u80a1\u8d26\u6237\u8fdb\u884c\u5e73\u4ed3\u64cd\u4f5c\uff0c\u4e8f\u635f\u5e76\u4e0d\u4e25\u91cd\uff0c\u4e91\u6797\u8bc1\u5238\u4e5f\u53ca\u65f6\u8c03\u52a8\u8d44\u91d1\u5c06\u8fd9\u4e8f\u635f\u7684\u7a7a\u7f3a\u7ed9\u586b\u8865\u4e0a\u3002\u867d\u7136\u4f1a\u9762\u4e34\u8bc1\u76d1\u90e8\u95e8\u7684\u60e9\u7f5a\uff0c\u4f46\u4e5f\u7b97\u4e0d\u4e0a\u4e25\u91cd\u7684\u6253\u51fb\u3002</p><p>\u3000\u3000\u5f88\u53ef\u60dc\uff0c\u6709\u4e9b\u4eba\u5ffd\u89c6\u6389\u6c5f\u654f\u4e4b\u5bf9\u91d1\u878d\u8bc1\u5238\u5de5\u4f5c\u7684\u719f\u6089\u4ee5\u53ca\u4ed6\u60f3\u5728\u4e1c\u6d77\u6316\u6848\u5b50\u7684\u51b3\u5fc3\u3002</p><p>\u3000\u3000\u4e91\u6797\u8bc1\u5238\u5efa\u90ba\u73af\u6e56\u5357\u8def\u8425\u4e1a\u90e8\u7684\u81ea\u6709\u8d44\u91d1\u4e0d\u8fc7\u4ebf\u5143\uff0c\u5411\u5ba2\u6237\u5355\u7b14\u63d0\u4f9b\u878d\u8d44\u91d1\u989d\u5c31\u9ad8\u8fbe\u4e09\u5343\u4e07\uff0c\u5c31\u8db3\u4ee5\u8ba9\u4eba\u6000\u7591\u8425\u4e1a\u90e8\u5b9e\u9645\u62e5\u6709\u8d44\u91d1\u91cf\u4e25\u91cd\u4e0d\u5339\u914d\uff0c\u6c5f\u654f\u4e4b\u6566\u4fc3\u4e91\u6797\u8bc1\u5238\u7684\u4e3b\u7ba1\u90e8\u95e8\u7701\u7ecf\u8d38\u59d4\u4ee5\u53ca\u7701\u8bc1\u76d1\u5c40\u7b49\u90e8\u95e8\u901a\u8fc7\u8fdb\u4e00\u6b65\u7684\u8c03\u67e5\u53d1\u73b0\u4e91\u6797\u8bc1\u5238\u5b58\u5728\u975e\u6cd5\u5438\u6536\u516c\u4f17\u5b58\u6b3e\u7684\u5acc\u7591\uff0c\u800c\u4e14\u6d89\u6848\u91d1\u989d\u4e4b\u5de8\u662f\u4e1c\u6d77\u8fd1\u5e74\u6765\u7f55\u89c1\u3002</p><p>\u3000\u3000\u201c\u5f90\u7701\u957f\u5728\u4e1c\u6d77\u65f6\uff0c\u5bf9\u7701\u7ea7\u4e0b\u5c5e\u91d1\u878d\u516c\u53f8\u8fdb\u884c\u8fc7\u68b3\u7406\uff0c\u6ca1\u60f3\u5230\u73b0\u5728\u6345\u51fa\u8fd9\u4e48\u5927\u7684\u95ee\u9898\uff0c\u201d\u9646\u6587\u592b\u8f7b\u8f7b\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u521d\u6b65\u8c03\u67e5\uff0c\u4e91\u6797\u8bc1\u5238\u975e\u6cd5\u5438\u6536\u516c\u4f17\u5b58\u6b3e\u6570\u989d\u53ef\u80fd\u8d85\u8fc7\u5341\u4ebf\u2026\u2026\u201d</p><p>\u3000\u3000\u4e03\u6708\u4e0b\u65ec\uff0c\u5efa\u90ba\u6b63\u503c\u708e\u590f\u5b63\u8282\uff0c\u5c71\u4e0b\u6691\u6c14\u903c\u4eba\uff0c\u6c5f\u7554\u72ee\u5b50\u5cf0\u5317\u5d16\u4e0a\u7684\u96c5\u81f4\u8336\u820d\u91cc\u51c9\u98ce\u4e60\u4e60\uff0c\u5374\u662f\u5efa\u90ba\u708e\u590f\u907f\u6691\u7684\u597d\u5730\u65b9\u3002\u6700\u8fd1\u7684\u4e8b\u60c5\u4e5f\u95f9\u5f97\u4eba\u5fc3\u60f6\u60f6\uff0c\u7279\u522b\u662f\u6c5f\u654f\u4e4b\u6709\u610f\u5bf9\u7701\u653f\u5e9c\u73ed\u5b50\u6210\u5458\u7684\u5206\u7ba1\u5de5\u4f5c\u8fdb\u884c\u8c03\u6574\uff0c\u4f1a\u76f4\u63a5\u6d89\u53ca\u5230\u9526\u6e56\u7684\u5229\u76ca\uff0c\u5f20\u606a\u5728\u6d77\u5dde\u65f6\uff0c\u5c31\u63a5\u8fc7\u9646\u6587\u592b\u4e24\u56de\u7535\u8bdd\uff0c\u56de\u5230\u5efa\u90ba\uff0c\u4fbf\u8bf7\u4ed6\u5230\u5c71\u4e0a\u6765\u5750\u4e00\u5750\u3002\u6070\u5de7\u8bb8\u9e3f\u4f2f\u4e5f\u5728\u5efa\u90ba\uff0c\u4fbf\u4e00\u8d77\u4e0a\u5c71\u6765\u907f\u6691\u559d\u8336\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u9646\u6587\u592b\u8bf4\u8d77\u8fd9\u4e24\u5929\u95f9\u817e\u5f97\u6700\u70ed\u95f9\u7684\u4e91\u6797\u8bc1\u5238\u7684\u6848\u5b50\uff0c\u624b\u91cc\u7aef\u7740\u7ec6\u74f7\u676f\u5b50\uff0c\u773c\u775b\u671b\u4e86\u671b\u8fdc\u5904\u972d\u6c14\u9752\u79bb\u7684\u6c5f\u6c34\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u80fd\u591f\u94bb\u7684\u7a7a\u5b50\u592a\u591a\u3002\u5c31\u8bf4\u8fd9\u4ef6\u6848\u5b50\uff0c\u6c5f\u654f\u4e4b\u63ea\u4f4f\u4e0d\u653e\uff0c\u90a3\u5c31\u662f\u5927\u6848\u8981\u6848\uff1b\u5728\u4e00\u822c\u4eba\u770b\u6765\uff0c\u4e91\u6797\u8bc1\u5238\u901a\u8fc7\u59d4\u6258\u7406\u8d22\u7684\u5f62\u5f0f\u5438\u63fd\u516c\u4f17\u5b58\u6b3e\uff0c\u53ea\u8981\u4e0d\u6345\u4ec0\u4e48\u7bd3\u5b50\uff0c\u4e5f\u65e0\u4f24\u5927\u96c5\u3002\u4e91\u6797\u8bc1\u5238\u5438\u63fd\u8fd9\u4e48\u591a\u7684\u516c\u4f17\u5b58\u6b3e\u901a\u8fc7\u653e\u8d37\u6216\u76f4\u63a5\u5750\u5e84\u7b49\u5f62\u5f0f\u5728\u8bc1\u5238\u5e02\u573a\u64cd\u4f5c\uff0c\u5728\u4e00\u4e9b\u4eba\u770b\u6765\u66f4\u662f\u8bc1\u5238\u516c\u53f8\u5e94\u6709\u7684\u63fd\u8d22\u4e4b\u9053\u3002\u5373\u4f7f\u6345\u51fa\u4ec0\u4e48\u7bd3\u5b50\uff0c\u63a8\u51fa\u4e00\u4e2a\u66ff\u7f6a\u7f8a\u51fa\u6765\u4e5f\u4e0d\u4f1a\u53d7\u5230\u591a\u5c11\u4e25\u91cd\u7684\u60e9\u7f5a\u2026\u2026\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u7b11\u4e86\u7b11\uff0c\u4ed6\u4ece\u5f20\u606a\u7684\u8bed\u6c14\u91cc\u542c\u4e0d\u51fa\u592a\u591a\u6f5c\u53f0\u8bcd\u51fa\u6765\uff0c\u5fc3\u60f3\u5f20\u606a\u5e94\u8be5\u77e5\u9053\u6c5f\u654f\u4e4b\u7684\u5fc3\u601d\uff0c\u5218\u95fb\u6d9b\u6700\u8fd1\u5230\u5efa\u90ba\u6d3b\u52a8\u9891\u7e41\uff0c\u671f\u95f4\u751a\u81f3\u8fd8\u56de\u4e86\u4e00\u8d9f\u5317\u4eac\uff0c\u751a\u81f3\u6709\u5c0f\u9053\u6d88\u606f\u4f20\u51fa\u6765\u4f1a\u8c03\u5218\u95fb\u6d9b\u5230\u7701\u91cc\u62c5\u4efb\u526f\u7701\u957f\uff0c\u96be\u9053\u5f20\u606a\u771f\u4e0d\u62c5\u5fc3\u6c5f\u654f\u4e4b\u6700\u7ec8\u4f1a\u5c06\u77db\u76fe\u6307\u5411\u9526\u6e56\uff1f</p><p>\u3000\u3000\u8fd9\u6869\u975e\u6cd5\u5438\u7eb3\u516c\u4f17\u5b58\u6b3e\u6848\u4e5f\u662f\u9648\u5176\u658c\u6848\u7275\u626f\u51fa\u6765\u7684\uff0c\u6309\u7167\u4ee5\u5f80\u7684\u60ef\u4f8b\uff0c\u529e\u6848\u90e8\u95e8\u4f1a\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u63a7\u5236\u67e5\u6848\u8fdb\u7a0b\u4ee5\u514d\u6848\u4ef6\u7275\u626f\u592a\u591a\u95f9\u5f97\u4e0d\u53ef\u6536\u62fe\uff0c\u4f46\u662f\u9648\u5176\u658c\u6848\u5728\u6c5f\u654f\u4e4b\u7684\u63a8\u52a8\u4e0b\uff0c\u6709\u8d8a\u6f14\u8d8a\u70c8\u7684\u8d8b\u52bf\uff0c\u6682\u65f6\u4e5f\u6ca1\u6709\u505c\u6b47\u7684\u610f\u601d\uff0c\u6c5f\u654f\u4e4b\u4e5f\u5df2\u7ecf\u5728\u7701\u653f\u5e9c\u529e\u516c\u4f1a\u8bae\u516c\u5f00\u8981\u6c42\u5bf9\u5168\u7701\u91d1\u878d\u8bc1\u5238\u673a\u6784\u8fdb\u884c\u4e00\u6b21\u7ec6\u81f4\u7684\u6392\u67e5\u3002\u4e0e\u5f53\u5e74\u5f90\u5b66\u5e73\u6574\u8083\u7701\u6797\u4e1a\u3001\u6c34\u5229\u7cfb\u7edf\u4e0d\u540c\uff0c\u6c5f\u654f\u4e4b\u6574\u8083\u7701\u91d1\u878d\u7cfb\u7edf\u7684\u5fc3\u601d\u8c08\u4e0d\u4e0a\u5f88\u6b63\uff0c\u4ed6\u4e5f\u8bb8\u66f4\u60f3\u501f\u673a\u6405\u52a8\u4e00\u4e0b\u4ee5\u6539\u53d8\u4ed6\u5230\u4e1c\u6d77\u5de5\u4f5c\u4ee5\u6765\u88ab\u52a8\u7684\u5c40\u9762\u3002</p><p>\u3000\u3000\u867d\u8bf4\u4e8b\u52bf\u6709\u6108\u6f14\u6108\u70c8\u7684\u8d8b\u52bf\uff0c\u65b0\u4efb\u7701\u59d4\u4e66\u8bb0\u674e\u8fdc\u6e56\u5bf9\u9648\u5176\u658c\u6848\u3001\u4e91\u6797\u8bc1\u5238\u975e\u6cd5\u5438\u63fd\u516c\u4f17\u5b58\u6b3e\u6848\u7684\u8c03\u67e5\u8fd8\u662f\u6301\u652f\u6301\u6001\u5ea6\uff0c\u81f3\u4e8e\u6d89\u53ca\u5230\u5176\u4ed6\u4e8b\u60c5\u7279\u522b\u662f\u4eba\u4e8b\u8c03\u52a8\u7684\u95ee\u9898\u4e0a\uff0c\u4ed6\u5374\u59cb\u7ec8\u4fdd\u6301\u6c89\u9ed8\u7684\u59ff\u6001\uff0c\u8bb8\u591a\u4eba\u5bf9\u6b64\u90fd\u731c\u6d4b\u4e0d\u900f\u3002</p><p>\u3000\u3000\u6240\u8c13\u4e00\u671d\u5929\u5b50\u4e00\u671d\u81e3\uff0c\u4e8b\u5b9e\u4e0a\u5728\u9676\u664b\u79bb\u5f00\u4e1c\u6d77\u3001\u674e\u8fdc\u6e56\u63a5\u66ff\u7701\u59d4\u4e66\u8bb0\u7684\u4f4d\u5b50\u3001\u6c5f\u654f\u4e4b\u7a7a\u964d\u8fc7\u6765\u62c5\u4efb\u7701\u957f\u4e4b\u65f6\uff0c\u8bb8\u591a\u4eba\u90fd\u671f\u5f85\u4e1c\u6d77\u653f\u6cbb\u683c\u5c40\u4f1a\u6709\u8f83\u5927\u5e45\u5ea6\u7684\u8c03\u6574\u3002\u5b9e\u9645\u7684\u60c5\u51b5\u5374\u662f\u4e1c\u6d77\u5b98\u573a\u4e00\u76f4\u62d6\u5230\u73b0\u5728\u90fd\u6ca1\u6709\u4ec0\u4e48\u5927\u7684\u8c03\u6574\uff0c\u8fd9\u5df2\u7ecf\u5f88\u51fa\u4e4e\u8bb8\u591a\u4eba\u7684\u610f\u6599\u4e86\uff0c\u9648\u5176\u658c\u6848\u9002\u65f6\u7684\u5c06\u50f5\u5c40\u6405\u52a8\u8d77\u6765\uff0c\u5012\u662f\u8ba9\u4e00\u4e9b\u4eba\u770b\u5230\u4e86\u673a\u4f1a\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u7ed9\u674e\u8fdc\u6e56\u5f53\u8fc7\u79d8\u4e66\u957f\uff0c\u53c8\u662f\u674e\u8fdc\u6e56\u63d0\u62d4\u4ed6\u5f53\u526f\u7701\u957f\u7684\uff0c\u4ed6\u7422\u78e8\u7740\u674e\u8fdc\u6e56\u7684\u6c89\u9ed8\u59ff\u6001\uff0c\u8ddf\u5f20\u606a\u773c\u524d\u8fd9\u79cd\u6a21\u68f1\u4e24\u53ef\u7684\u6001\u5ea6\u5012\u662f\u6709\u4e9b\u7c7b\u4f3c\uff0c\u5fc3\u60f3\u8fd9\u4e5f\u7b97\u662f\u4e00\u79cd\u9ed8\u5951\u5427\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u4e8b\u52a1\u7e41\u91cd\uff0c\u5e76\u6ca1\u6709\u592a\u591a\u7684\u95f2\u60c5\u9038\u81f4\u5728\u5c71\u9e93\u8336\u820d\u907f\u6691\u7eb3\u51c9\uff0c\u4e0b\u5348\u56db\u70b9\u8bb8\u5c31\u6709\u4e8b\u60c5\u7f20\u4e0a\u8eab\u6765\uff0c\u4ece\u72ee\u5b50\u5cf0\u5317\u5d16\u8336\u820d\u79bb\u5f00\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u8bb8\u9e3f\u4f2f\u9047\u4e0a\uff0c\u6709\u65f6\u95f4\u603b\u8981\u6740\u4e0a\u4e00\u76d8\uff0c\u9646\u6587\u592b\u8d70\u540e\u4ed6\u4eec\u4e5f\u4e0d\u6025\u4e8e\u4e0b\u5c71\u53bb\uff0c\u8ba9\u9648\u5983\u84c9\u5e2e\u5fd9\u62ff\u6765\u68cb\u76d8\uff0c\u5c31\u5728\u4e34\u7a97\u7684\u7af9\u69bb\u77ee\u51e0\u4e0a\u8ddf\u8bb8\u9e3f\u4f2f\u5bf9\u5f08\u4e00\u5c40\u3002</p><p>\u3000\u3000\u201c\u5c0f\u4e2b\u5934\u8fd9\u7b97\u662f\u4ece\u5b66\u6821\u6bd5\u4e1a\u4e86\uff1f\u201d\u8bb8\u9e3f\u4f2f\u7b11\u7740\u95ee\u9648\u5983\u84c9\uff0c\u201c\u4eba\u751f\u8fc7\u5f97\u771f\u662f\u5306\u5306\uff0c\u6211\u90fd\u8fd8\u8bb0\u5f97\u5c0f\u4e2b\u5934\u6d41\u7740\u9f3b\u6d95\u5728\u68cb\u9662\u75af\u8dd1\u7684\u6837\u5b50\uff0c\u4ece\u5c0f\u5c31\u662f\u8981\u5f3a\u7684\u6027\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u77e5\u9053\u9648\u5983\u84c9\u6682\u65f6\u8fd8\u6ca1\u6709\u878d\u5165\u7ed9\u4ed6\u5f53\u52a9\u624b\u7684\u89d2\u8272\u4e2d\u53bb\uff0c\u624b\u4f38\u5230\u68cb\u76d2\u91cc\u62ff\u4e86\u4e00\u628a\u68cb\u5b50\u8bf7\u8bb8\u9e3f\u4f2f\u731c\u5b50\u51b3\u5b9a\u5148\u540e\u624b\uff0c\u4ed6\u6267\u9ed1\u5b50\u5148\u884c\uff0c\u4ee5\u4e09\u8fde\u661f\u5f00\u5c40\uff0c\u8fb9\u843d\u5b50\u8fb9\u8bf4\u9053\uff1a\u201c\u4e1c\u6d77\u8fd9\u8fb9\u592a\u6b63\u5e38\u4e86\u4e5f\u5c31\u4e0d\u6b63\u5e38\u4e86\uff0c\u9648\u5176\u658c\u6848\u6405\u4e00\u6405\u4e5f\u662f\u597d\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u6253\u7b97\u9000\u4e00\u5927\u6b65\uff1f\u201d\u8bb8\u9e3f\u4f2f\u95ee\u9053\uff0c\u201c\u7167\u5c40\u52bf\u53d1\u5c55\u4e0b\u53bb\uff0c\u9646\u6587\u592b\u5206\u7ba1\u7684\u8fd9\u4e00\u5757\uff0c\u6c5f\u654f\u4e4b\u4f1a\u5361\u4e00\u4e2a\u4eba\u8fdb\u6765\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u5218\u95fb\u6d9b\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u5728\u653f\u6cbb\u4e0a\u4ece\u6765\u90fd\u6ca1\u6709\u4ec0\u4e48\u91ce\u5fc3\uff0c\u4e5f\u66f4\u4e0d\u60f3\u8868\u73b0\u51fa\u53bb\u4ec0\u4e48\u91ce\u5fc3\u6765\uff0c\u201d\u5f20\u606a\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u201c\u9526\u6e56\u63a8\u52a8\u4e00\u4e9b\u5730\u65b9\u4e0a\u7684\u7ecf\u6d4e\u5de5\u4f5c\uff0c\u5e76\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u5229\u76ca\u8bc9\u6c42\uff0c\u4f46\u662f\u8fd9\u4e2a\u793e\u4f1a\u4e0d\u662f\u6e05\u8005\u81ea\u6e05\u7684\u793e\u4f1a\uff0c\u65e2\u7136\u4ed6\u4eec\u8ba4\u4e3a\u5361\u4e00\u4e2a\u4eba\u8fdb\u6765\u4f1a\u662f\u5bf9\u9526\u6e56\u4e0d\u5c0f\u7684\u6253\u538b\uff0c\u90a3\u5c31\u8ba9\u4ed6\u4eec\u5361\u4e00\u4e2a\u4eba\u8fdb\u6765\u597d\u4e86\u2026\u2026\u8981\u662f\u4e1c\u6d77\u771f\u53d8\u6210\u6ca1\u6709\u7a7a\u5b50\u53ef\u94bb\u7684\u94c1\u677f\u4e00\u5757\uff0c\u8bf4\u4e0d\u5b9a\u4e0a\u9762\u5bf9\u9526\u6e56\u7684\u6212\u5fc3\u66f4\u5f3a\u3002\u6c5f\u654f\u4e4b\u8981\u6b65\u6b65\u7d27\u903c\uff0c\u9526\u6e56\u5c31\u6b65\u6b65\u9000\u8ba9\uff0c\u603b\u8981\u8ba9\u4e0a\u9762\u6709\u4e9b\u4eba\u5b89\u5fc3\u624d\u884c\u3002\u201d</p><p>\u3000\u3000\u201c\u674e\u8fdc\u6e56\u5fc3\u91cc\u4e5f\u662f\u8fd9\u4e48\u60f3\u7684\u5427\uff1f\u201d\u8bb8\u9e3f\u4f2f\u8f7b\u8f7b\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u4ed6\u6709\u7740\u66f4\u8fdc\u5927\u7684\u653f\u6cbb\u62b1\u8d1f\uff0c\u4e0d\u4f1a\u987d\u56fa\u7684\u5c06\u4e1c\u6d77\u89c6\u4f5c\u4ed6\u7684\u3001\u7edd\u4e0d\u5bb9\u4ed6\u4eba\u67d3\u6307\u7684\u5730\u76d8\u3002\u6c5f\u654f\u4e4b\u8981\u6405\u4e00\u6405\u5c40\u9762\uff0c\u4ed6\u81ea\u7136\u53ef\u4ee5\u987a\u6c34\u63a8\u821f\u4e00\u628a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u654f\u4e4b\u771f\u8981\u5c06\u5218\u95fb\u6d9b\u7eb3\u5165\u7701\u653f\u5e9c\u73ed\u5b50\u6210\u5458\uff0c\u751a\u81f3\u8ba9\u5218\u95fb\u6d9b\u53d6\u4ee3\u9646\u6587\u592b\u6765\u534f\u52a9\u4ed6\u4e3b\u6301\u4e1c\u6d77\u7701\u7684\u7ecf\u6d4e\u5de5\u4f5c\uff0c\u674e\u8fdc\u6e56\u591a\u534a\u4e5f\u4f1a\u9ed8\u8ba4\u7684\uff0c\u751a\u81f3\u6c5f\u654f\u4e4b\u5bf9\u4e1c\u6d77\u7701\u7ecf\u6d4e\u5de5\u4f5c\u653f\u7b56\u8fdb\u884c\u8c03\u6574\uff0c\u53ea\u8981\u662f\u5f80\u6709\u5229\u7684\u65b9\u9762\u53bb\u63a2\u7d22\uff0c\u4e5f\u672a\u5c1d\u4e0d\u53ef\u3002\u6211\u73b0\u5728\u5c31\u7b49\u7740\u770b\u8fd9\u6f6d\u6d51\u6c34\u80fd\u4e0d\u80fd\u6405\u5230\u6d77\u7c9f\u79d1\u6280\u7684\u5934\u4e0a\u53bb\uff0c\u4e0d\u77e5\u9053\u4e25\u5bb6\u4eba\u5fc3\u91cc\u5934\u8fd9\u65f6\u5019\u6b63\u60f3\u4e9b\u4ec0\u4e48\u2026\u2026\u4e0d\u8fc7\u5c31\u7b97\u6405\u4e0d\u5230\u6d77\u7c9f\u79d1\u6280\u7684\u5934\u4e0a\uff0c\u4e25\u5bb6\u589e\u53d1\u8fd9\u6b65\u68cb\u4e5f\u6ca1\u6709\u8db3\u591f\u7684\u65f6\u95f4\u53bb\u8d70\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u7ecf\u6d4e\u4e0a\u7684\u4e8b\u60c5\uff0c\u6211\u4e0d\u662f\u5f88\u61c2\uff0c\u201d\u8bb8\u9e3f\u4f2f\u95ee\u9053\uff0c\u201c\u8981\u4e0d\u662f\u5b8f\u4fe1\u592a\u4e0d\u5b88\u89c4\u77e9\uff0c\u4e8b\u5b9e\u4e0a\u6709\u90a3\u4e48\u51e0\u5bb6\u89c4\u6a21\u8ddf\u9526\u6e56\u5927\u5dee\u4e0d\u5dee\u7684\u6c11\u8425\u96c6\u56e2\uff0c\u4e5f\u80fd\u5206\u6563\u6389\u4e00\u4e9b\u4e0a\u9762\u5bf9\u9526\u6e56\u7684\u5173\u6ce8\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5012\u4e0d\u662f\u6e05\u9ad8\uff0c\u8ddf\u4ed6\u4eec\uff0c\u803b\u4e8e\u4e3a\u4f0d\u7684\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u665a\u4e0a\u8981\u5750\u706b\u8f66\u53bb\u4e00\u8d9f\u5317\u4eac\uff0c\u5927\u6982\u662f\u62bd\u4e0d\u51fa\u65f6\u95f4\u966a\u8bb8\u8001\u5e08\u60a8\u5403\u996d\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u7709\u5934\u7d27\u9501\uff0c\u773c\u775b\u770b\u7740\u5218\u5947\u5cfb\uff0c\u8bf4\u9053\uff1a\u201c\u6c5f\u654f\u4e4b\u6e05\u67e5\u4e1c\u6d77\u7701\u4e0b\u5c5e\u7684\u8bc1\u5238\u91d1\u878d\u673a\u6784\uff0c\u661f\u6cb3\u8bc1\u5238\u96b6\u5c5e\u4e8e\u8d22\u653f\u90e8\uff0c\u8ddf\u4e1c\u6d77\u7701\u516b\u6746\u5b50\u6253\u4e0d\u5230\u4e00\u5757\uff0c\u4ed6\u4eec\u5fc3\u865a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u7ecf\u8fc7\u8fd1\u4e8c\u5341\u4e2a\u4ea4\u6613\u65e5\u7684\u53cd\u5f39\uff0c\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u4ef7\u7ec8\u4e8e\u91cd\u65b0\u7ad9\u5230\u6bcf\u80a1\u4e09\u5341\u56db\u5143\u7684\u4ef7\u4f4d\u4e4b\u4e0a\uff0c\u8fbe\u5230\u5728\u4e8c\u7ea7\u5e02\u573a\u516c\u5f00\u589e\u53d1\u7684\u6761\u4ef6\uff0c\u6b63\u51c6\u5907\u542f\u52a8\u516c\u5f00\u589e\u53d1\u65b9\u6848\u4e4b\u65f6\uff0c\u4e3b\u627f\u9500\u5546\u661f\u6cb3\u8bc1\u5238\u8fd9\u65f6\u5019\u5374\u6709\u9000\u7f29\u4e4b\u610f\uff0c\u8868\u793a\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u4ef7\u4e00\u65e6\u8dcc\u7834\u4e0b\u9650\u4ef7\u81ea\u7136\u7ec8\u6b62\u589e\u53d1\u8fdb\u7a0b\uff0c\u661f\u6cb3\u8bc1\u5238\u5bf9\u672a\u53d1\u884c\u51fa\u53bb\u7684\u65b0\u80a1\u4e0d\u5c65\u884c\u5305\u9500\u4e49\u52a1\u3002</p><p>\u3000\u3000\u8fd9\u4e0e\u4e4b\u524d\u7684\u627f\u8bfa\u5b8c\u5168\u4e0d\u540c\uff0c\u4e4b\u524d\u661f\u6cb3\u8bc1\u5238\u7b54\u5e94\u4f1a\u4e0e\u6d77\u7c9f\u79d1\u6280\u7b7e\u7f72\u589e\u53d1\u65b0\u80a1\u5305\u9500\u534f\u8bae\u3002\u6839\u636e\u4e4b\u524d\u7684\u627f\u8bfa\uff0c\u53ea\u8981\u5b9e\u65bd\u589e\u53d1\uff0c\u6d77\u7c9f\u79d1\u6280\u8ba1\u5212\u901a\u8fc7\u4e8c\u7ea7\u5e02\u573a\u516c\u5f00\u589e\u53d1\u7684\u4e00\u4ebf\u56db\u5343\u4e07\u80a1\u65b0\u80a1\u5c06\u5b8c\u5168\u7531\u661f\u6cb3\u8bc1\u5238\u5305\u9500\uff0c\u589e\u53d1\u8fc7\u7a0b\u4e2d\u53ef\u80fd\u56e0\u4e3a\u80a1\u4ef7\u4e0b\u8dcc\u5bfc\u81f4\u589e\u53d1\u7ec8\u6b62\u7684\u98ce\u9669\u4e5f\u5b8c\u5168\u7531\u661f\u6cb3\u8bc1\u5238\u627f\u62c5\uff0c\u661f\u6cb3\u8bc1\u5238\u53ea\u662f\u76f8\u5e94\u7684\u8981\u6c42\u6d77\u7c9f\u79d1\u6280\u652f\u4ed8\u66f4\u9ad8\u7684\u53d1\u884c\u8d39\u7528\u3002</p><p>\u3000\u3000\u5305\u9500\u624d\u662f\u5bf9\u6d77\u7c9f\u79d1\u6280\u6700\u6709\u5229\u7684\u589e\u53d1\u65b9\u6848\uff0c\u867d\u7136\u5168\u7403\u8bc1\u5238\u5e02\u573a\u65b0\u79d1\u6280\u677f\u5757\u53cd\u5f39\u5f3a\u52b2\uff0c\u4f46\u662f\u6d77\u7c9f\u79d1\u6280\u4e4b\u6240\u4ee5\u80fd\u53cd\u5f39\u5230\u6bcf\u80a1\u4e09\u5341\u56db\u5143\u4ee5\u4e0a\u3001\u53cd\u5f39\u5e45\u5ea6\u7ffb\u500d\uff0c\u662f\u5b8f\u4fe1\u3001\u7cbe\u5178\u8c03\u52a8\u4e8c\u5341\u4ebf\u8d44\u91d1\u8fdb\u5165\u4e8c\u7ea7\u5e02\u573a\u6258\u5e02\u7684\u7ed3\u679c\uff0c\u4e25\u6587\u4ecb\u4e4b\u524d\u8ba1\u5212\u662f\u5c06\u70c2\u644a\u5b50\u5b8c\u5168\u4e22\u5230\u661f\u6cb3\u8bc1\u5238\u624b\u4e2d\u4e4b\u540e\uff0c\u4ed6\u53ef\u4ee5\u53ca\u65f6\u5c06\u4e8c\u5341\u4ebf\u8d44\u91d1\u4ece\u4e8c\u7ea7\u5e02\u573a\u62bd\u56de\u6765\u3002</p><p>\u3000\u3000\u661f\u6cb3\u8bc1\u5238\u53d8\u4e86\u5366\uff0c\u5b8f\u4fe1\u975e\u4f46\u4e0d\u80fd\u5c06\u8d44\u91d1\u4ece\u4e8c\u7ea7\u5e02\u573a\u62bd\u51fa\u6765\uff0c\u751a\u81f3\u8fd8\u8981\u7ee7\u7eed\u8c03\u96c6\u8d44\u91d1\u9884\u9632\u5728\u589e\u53d1\u8fc7\u7a0b\u4e2d\u6d77\u7c9f\u79d1\u6280\u80a1\u4ef7\u8dcc\u7834\u4e0b\u9650\u4ef7\u5bfc\u81f4\u589e\u53d1\u5931\u8d25\u3002</p><p>\u3000\u3000\u661f\u6cb3\u8bc1\u5238\u7684\u9000\u7f29\u4ee4\u4e25\u6587\u4ecb\u89c9\u5f97\u76f8\u5f53\u610f\u5916\u3002</p><p>\u3000\u3000\u201c\u6c5f\u654f\u4e4b\u6307\u793a\u7701\u8bc1\u5238\u5c40\u7b2c\u4e00\u65f6\u95f4\u5c06\u4e91\u6797\u8bc1\u5238\u7684\u6848\u5b50\u6c47\u62a5\u5230\u8bc1\u76d1\u4f1a\uff0c\u6628\u5929\u5305\u62ec\u661f\u6cb3\u8bc1\u5238\u7684\u6a0a\u603b\u90fd\u7ed9\u53eb\u5230\u8bc1\u76d1\u4f1a\u5f00\u4e86\u5185\u90e8\u4f1a\u8bae\uff0c\u4eca\u5929\u5c31\u6d41\u9732\u51fa\u9000\u7f29\u7684\u610f\u601d\uff0c\u201d\u5218\u5947\u5cfb\u8bf4\u9053\uff0c\u201c\u6211\u62c5\u5fc3\u7701\u8bc1\u5238\u5c40\u4e0d\u53ea\u662f\u2018\u5c0f\u5c0f\u7684\u6c47\u62a5\u2019\u90a3\u4e48\u7b80\u5355\u2026\u2026\u201d</p><p>\u3000\u3000\u661f\u6cb3\u8bc1\u5238\u4e4b\u524d\u627f\u8bfa\u5305\u9500\uff0c\u4e5f\u4e0d\u662f\u610f\u8bc6\u4e0d\u5230\u5176\u4e2d\u5b58\u5728\u4ec0\u4e48\u98ce\u9669\uff0c\u53ea\u662f\u518d\u5927\u7684\u98ce\u9669\uff0c\u8ddf\u661f\u6cb3\u8bc1\u5238\u7684\u9ad8\u5c42\u53c8\u6709\u591a\u5927\u7684\u5173\u7cfb\uff1f\u53ea\u8981\u8868\u9762\u4e0a\u5408\u4e4e\u7a0b\u5e8f\uff0c\u5373\u4f7f\u661f\u6cb3\u8bc1\u5238\u5c06\u6765\u4f1a\u627f\u53d7\u4e00\u5b9a\u7684\u635f\u5931\uff0c\u4e5f\u53ef\u4ee5\u63a8\u8bf4\u4e70\u4e2a\u5e02\u573a\u6559\u8bad\uff0c\u603b\u4e0d\u8981\u661f\u6cb3\u8bc1\u5238\u7684\u9ad8\u5c42\u81ea\u5df1\u638f\u8170\u5305\u8865\u8d34\u8fd9\u4e9b\u635f\u5931\u3002\u53ea\u662f\u5173\u952e\u5934\u4e0a\uff0c\u5185\u90e8\u88ab\u8b66\u544a\uff0c\u7ed9\u8bc1\u76d1\u4f1a\u76ef\u4e0a\uff0c\u661f\u6cb3\u8bc1\u5238\u7684\u9ad8\u5c42\u672a\u5fc5\u6709\u80c6\u91cf\u914d\u5408\u4ed6\u4eec\u9876\u98ce\u4f5c\u6d6a\u3002\u8fd9\u4e0d\u662f\u591a\u5c11\u94b1\u80fd\u6446\u5e73\u7684\u95ee\u9898\uff0c\u5bf9\u4e8e\u661f\u6cb3\u8bc1\u5238\u7684\u9ad8\u5c42\u6765\u8bf4\uff0c\u53ea\u8981\u5750\u5728\u4f4d\u5b50\u4e0a\uff0c\u635e\u94b1\u7684\u673a\u4f1a\u6709\u7684\u662f\u3002</p><p>\u3000\u3000\u5173\u952e\u8fd8\u662f\u6d77\u7c9f\u79d1\u6280\u7684\u65b0\u80a1\u589e\u53d1\u7ed9\u4eba\u76ef\u4e0a\u4e86\u3002</p><p>\u3000\u3000\u5218\u5947\u5cfb\u5fc3\u60f3\uff1a\u8fd9\u5173\u952e\u5934\u4e0a\u7ed9\u76ef\u4e0a\u771f\u662f\u68d8\u624b\uff0c\u4ed6\u4eec\u6446\u5e73\u661f\u6cb3\u8bc1\u5238\u4e0a\u5c5e\u90e8\u95e8\u8d22\u653f\u90e8\u7684\u5173\u7cfb\uff0c\u5374\u6ca1\u6709\u60f3\u5230\u8bc1\u7ba1\u90e8\u95e8\u8bc1\u76d1\u4f1a\u4f1a\u76ef\u4e0a\u6765\u3002</p><p>\u3000\u3000\u201c\u6c5f\u654f\u4e4b\u8981\u5728\u4e1c\u6d77\u6405\u4e8b\uff0c\u60f3\u62ff\u6211\u4eec\u4e25\u5bb6\u5f00\u5200\uff1f\u201d\u4e25\u6587\u4ecb\u7709\u5934\u8df3\u4e86\u8df3\uff0c\u773c\u775b\u655b\u7740\uff0c\u4ed6\u4e4b\u524d\u5012\u6ca1\u6709\u60f3\u5230\u8fd9\u4e2a\u53ef\u80fd\u6027\uff0c\u5c31\u7b97\u56db\u6708\u4e0b\u65ec\u7eb3\u6307\u66b4\u8dcc\u65f6\u6c5f\u654f\u4e4b\u5bf9\u5a92\u4f53\u53d1\u8868\u8fc7\u4e0d\u5229\u7f51\u7edc\u80a1\u7684\u8a00\u8bba\uff0c\u4ed6\u4e5f\u6ca1\u6709\u60f3\u592a\u591a\uff0c\u6c5f\u654f\u4e4b\u6210\u4e3a\u62e6\u8def\u864e\u662f\u4ed6\u6599\u60f3\u4e0d\u5230\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u6c5f\u654f\u4e4b\u5728\u90e8\u59d4\u91cc\u867d\u7136\u575a\u6301\u56fd\u4f01\u6539\u5236\u7684\uff0c\u4f46\u662f\u5bf9\u56fd\u4f01\u79c1\u6709\u5316\u6539\u5236\u662f\u76f8\u5f53\u62b5\u5236\u7684\uff0c\u4ed6\u662f\u90e8\u59d4\u5185\u63d0\u51fa\u5f3a\u5316\u56fd\u6709\u8d44\u672c\u5730\u4f4d\u7684\u4ee3\u8868\u5b98\u5458\u4e4b\u4e00\uff0c\u4ed6\u8981\u662f\u6709\u5fc3\u60f3\u5c06\u6d77\u7c9f\u79d1\u6280\u5f53\u6210\u56fd\u4f01\u79c1\u6709\u5316\u6539\u5236\u7684\u5931\u8d25\u5178\u578b\u6765\u6293\uff0c\u672a\u5fc5\u4e0d\u4f1a\u505a\u4e9b\u5c0f\u52a8\u4f5c\u554a\uff1f\u201d\u5218\u5947\u5cfb\u5c06\u4ed6\u60f3\u5230\u7684\u95ee\u9898\u5206\u6790\u7ed9\u4e25\u6587\u4ecb\u542c\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u5218\u95fb\u6d9b\u6700\u8fd1\u8ddf\u6c5f\u654f\u4e4b\u8d70\u5f97\u8fd1\uff0c\u80fd\u4e0d\u80fd\u901a\u8fc7\u4ed6\u2026\u2026\u201d</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u611f\u5230\u4e07\u5206\u5934\u75bc\uff0c\u8bf4\u9053\uff1a\u201c\u672a\u5fc5\u884c\u5f97\u901a\u554a\uff0c\u542c\u8bf4\u6c5f\u654f\u4e4b\u5728\u90e8\u59d4\u91cc\u5c31\u9887\u8ba9\u522b\u4eba\u611f\u5230\u5934\u75bc\uff0c\u53ea\u6015\u4e0d\u80fd\u8ba9\u4ed6\u5c06\u6cfc\u51fa\u53bb\u7684\u6c34\u518d\u6536\u56de\u53bb\uff1b\u518d\u8bf4\u8001\u7237\u5b50\u5728\u4e2d\u592e\u90e8\u59d4\u65f6\uff0c\u8ddf\u6c5f\u5bb6\u7684\u90a3\u4f4d\u5173\u7cfb\u4e5f\u8c08\u4e0d\u4e0a\u878d\u6d3d\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8981\u662f\u4e0d\u80fd\u8ddf\u661f\u6cb3\u8bc1\u5238\u7b7e\u7f72\u5305\u9500\u534f\u8bae\uff0c\u4e0d\u80fd\u5c06\u589e\u53d1\u98ce\u9669\u8f6c\u5230\u661f\u6cb3\u8bc1\u5238\u5934\u4e0a\uff0c\u90a3\u6211\u4eec\u53ea\u80fd\u4fee\u6539\u65e2\u5b9a\u7684\u8ba1\u5212\uff0c\u6211\u662f\u62c5\u5fc3\u53cd\u5f39\u52bf\u6001\u4f1a\u75b2\u8f6f\uff0c\u5230\u90a3\u65f6\u538b\u529b\u4f1a\u975e\u5e38\u7684\u5927\u2026\u2026\u201d\u5218\u5947\u5cfb\u8bf4\u9053\uff0c\u4f46\u662f\u7bad\u5728\u5f26\u4e0a\u4e0d\u5f97\u4e0d\u53d1\uff0c\u4e3a\u4e86\u80fd\u591f\u589e\u53d1\u6210\u529f\uff0c\u5b8f\u4fe1\u4e0e\u7cbe\u5178\u524d\u540e\u6295\u5165\u4e09\u5341\u4ebf\u7684\u8d44\u91d1\uff0c\u4e00\u65e6\u653e\u5f03\u589e\u53d1\uff0c\u8fd9\u4e09\u5341\u4ebf\u8d44\u91d1\u52bf\u5fc5\u8981\u7ed9\u5265\u6389\u4e24\u5c42\u76ae\u624d\u80fd\u8131\u8eab\uff0c\u53ea\u8981\u589e\u53d1\u6210\u529f\u4e86\uff0c\u8fd9\u4e09\u5341\u4ebf\u8d44\u91d1\u5c31\u9700\u7ed9\u5265\u6389\u4e09\u5c42\u76ae\u4e5f\u5b8c\u5168\u6ca1\u6709\u5173\u7cfb\u3002</p><p>\u3000\u3000\u201c\u6211\u518d\u60f3\u60f3\uff0c\u8fd9\u4e2a\u6d88\u606f\u7b49\u5230\u665a\u4e0a\u5f00\u4f1a\u7684\u65f6\u5019\u518d\u901a\u544a\u6797\u5c0f\u59d0\uff0c\u201d\u4e25\u6587\u4ecb\u5429\u5490\u9053\uff0c\u201c\u4f60\u5148\u60f3\u60f3\u6709\u4ec0\u4e48\u522b\u7684\u5bf9\u7b56\u6ca1\u6709\uff0c\u867d\u8bf4\u53ef\u80fd\u6027\u4e0d\u5927\uff0c\u6c5f\u654f\u4e4b\u90a3\u8fb9\uff0c\u6211\u8fd8\u662f\u4eb2\u81ea\u53bb\u62dc\u8bbf\u4e00\u4e0b\u7684\u597d\uff0c\u660e\u5929\u6211\u518d\u53bb\u4e00\u8d9f\u5317\u4eac\uff0c\u8bf4\u4e0d\u5b9a\u661f\u6cb3\u8bc1\u5238\u90a3\u8fb9\u8fd8\u662f\u53ef\u4ee5\u505a\u4e00\u505a\u5de5\u4f5c\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u77e5\u9053\u3002\u201d\u5218\u5947\u5cfb\u70b9\u70b9\u5934\u8bf4\u9053\uff0c\u4ed6\u521a\u624d\u867d\u7136\u5efa\u8bae\u901a\u8fc7\u5218\u95fb\u6d9b\u8054\u7cfb\u6c5f\u654f\u4e4b\uff0c\u4f46\u662f\u4ed6\u77e5\u9053\u8ba9\u6c5f\u654f\u4e4b\u8f6c\u53d8\u7684\u53ef\u80fd\u6027\u6781\u5fae\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u6ca1\u6709\u89c1\u5230\u6c5f\u654f\u4e4b\uff0c\u6c5f\u654f\u4e4b\u5f53\u5929\u5230\u65b0\u6cf0\u3001\u6c5f\u6c82\u89c6\u5bdf\u53bb\u4e86\uff0c\u5f53\u591c\u5c31\u4f4f\u5728\u65b0\u6cf0\uff0c\u4e25\u6587\u4ecb\u4e0d\u53ef\u80fd\u8ffd\u5230\u65b0\u6cf0\u53bb\uff0c\u5c31\u7b97\u8ffd\u5230\u65b0\u6cf0\u53bb\uff0c\u6c5f\u654f\u4e4b\u4e5f\u4f1a\u7ed9\u5730\u65b9\u5b98\u5458\u56f4\u5f97\u6c34\u6cc4\u4e0d\u901a\uff0c\u4e0d\u4e00\u5b9a\u4f1a\u7ed9\u4ed6\u89c1\u9762\u673a\u4f1a\u3002</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u89c9\u5f97\u661f\u6cb3\u8bc1\u5238\u90a3\u8fb9\u8fd8\u662f\u53ef\u4ee5\u505a\u5de5\u4f5c\u7684\uff0c\u7b49\u4e0d\u53ca\u62d6\u5230\u7b2c\u4e8c\u5929\u4e58\u98de\u673a\u53bb\u5317\u4eac\uff0c\u51b3\u5b9a\u4e58\u591c\u95f4\u7684\u706b\u8f66\u524d\u5f80\u5317\u4eac\uff0c\u8fd9\u6837\u80fd\u8d76\u5728\u4e0a\u73ed\u65f6\u95f4\u4e4b\u524d\u5230\u5317\u4eac\u3002\u8fd9\u662f\u6d77\u7c9f\u79d1\u6280\u516c\u5f00\u589e\u53d1\u524d\u6700\u540e\u4e00\u6b21\u5173\u952e\u6027\u7684\u516c\u5173\u5de5\u4f5c\uff0c\u9664\u4e86\u5218\u5947\u5cfb\u4e4b\u5916\uff0c\u4e25\u6587\u4ecb\u8fd8\u8bf7\u6797\u96ea\u3001\u738b\u6d77\u7c9f\u8ddf\u4ed6\u4eec\u540c\u884c\u3002\u6b64\u5916\uff0c\u661f\u6cb3\u8bc1\u5238\u5efa\u90ba\u5206\u516c\u53f8\u7684\u8d1f\u8d23\u4eba\u4e5f\u968f\u4ed6\u4eec\u4e00\u8d77\u5230\u5317\u4eac\u505a\u603b\u90e8\u9ad8\u5c42\u7684\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u4ed6\u4eec\u8fdb\u5165\u5217\u8f66\u8f6f\u5367\u8f66\u53a2\u770b\u5230\u5085\u4fca\u4ee5\u53ca\u5176\u4ed6\u9526\u6e56\u7684\u5de5\u4f5c\u4eba\u5458\u7ad9\u5728\u8fc7\u9053\u91cc\u4e4b\u540e\uff0c\u624d\u77e5\u9053\u5f20\u606a\u4e5f\u5750\u540c\u4e00\u8d9f\u5217\u8f66\uff0c\u4ed6\u5f53\u7136\u6ca1\u6709\u8981\u53bb\u62dc\u8bbf\u7684\u610f\u601d\uff0c\u5750\u5230\u5305\u53a2\u91cc\uff0c\u4ed6\u5fc3\u91cc\u5374\u4e0d\u7531\u81ea\u4e3b\u7684\u731c\u6d4b\u8d77\u5f20\u606a\u56e0\u4e3a\u4ec0\u4e48\u4e8b\u60c5\u53bb\u5317\u4eac\u3002\u9526\u6e56\u773c\u4e0b\u6b63\u5728\u8fdb\u884c\u7684\u4e24\u4e2a\u5927\u9879\u76ee\uff0c\u4e00\u662f\u65b0\u5149\u7eb8\u4e1a\u5c06\u5728\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u6240\u516c\u5f00\u4e0a\u5e02\u4ea4\u6613\uff0c\u4e00\u662f\u7231\u8fbe\u96c6\u56e2\u5c06\u901a\u8fc7\u97e9\u56fd\u5b50\u516c\u53f8\u6536\u8d2d\u73b0\u4ee3\u534a\u5bfc\u4f53\u96c6\u56e2\u65d7\u4e0b\u7684\u6db2\u6676\u4e1a\u52a1\u3002\u65b0\u5149\u7eb8\u4e1a\u5728\u9999\u6e2f\u4e0a\u5e02\u5dee\u4e0d\u591a\u5df2\u7ecf\u786e\u5b9a\u4e0b\u6765\uff0c\u8fd9\u4ee4\u4e25\u6587\u4ecb\u5fc3\u91cc\u76f8\u5f53\u7684\u4e0d\u75db\u5feb\uff0c\u65b0\u5149\u7eb8\u4e1a\u8ba1\u5212\u5728\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u516c\u5f00\u878d\u8d44\u516d\u5341\u4ebf\u6e2f\u5143\uff0c\u5df2\u7ecf\u5f97\u5230\u9ad8\u76db\u3001\u6c47\u4e30\u3001\u56fd\u88d5\u7b49\u56fd\u9645\u6295\u8d44\u673a\u6784\u660e\u786e\u7684\u652f\u6301\uff0c\u5f20\u606a\u4f3c\u4e4e\u6ca1\u6709\u5fc5\u8981\u4e3a\u8fd9\u4e8b\u53bb\u5317\u4eac\u6e38\u8bf4\u4ec0\u4e48\uff0c\u8bf4\u4e0d\u5b9a\u662f\u4e3a\u6536\u8d2d\u73b0\u4ee3\u534a\u5bfc\u4f53\u96c6\u56e2\u65d7\u4e0b\u6db2\u6676\u4e1a\u52a1\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u97e9\u56fd\u7535\u5b50\u4f01\u4e1a\u5bf9\u7231\u8fbe\u96c6\u56e2\u7684\u5d1b\u8d77\u5341\u5206\u8b66\u60d5\uff0c\u7231\u8fbe\u96c6\u56e2\u60f3\u5230\u901a\u8fc7\u97e9\u56fd\u5b50\u516c\u53f8\u6536\u8d2d\u73b0\u4ee3\u534a\u5bfc\u4f53\u6db2\u6676\u4e1a\u52a1\u7684\u96be\u5ea6\u53ef\u60f3\u800c\u77e5\uff0c\u751a\u5230\u8fde\u65e5\u7cfb\u7535\u5b50\u5de8\u5934\u4e5f\u5728\u6697\u4e2d\u963b\u6320\u3002\u5728\u7231\u8fbe\u96c6\u56e2\u5411\u73b0\u4ee3\u534a\u5bfc\u4f53\u63d0\u51fa\u6536\u8d2d\u8981\u7ea6\u4e4b\u540e\uff0c\u65e5\u672c\u7d22\u5c3c\u516c\u53f8\u5c31\u5411\u73b0\u4ee3\u534a\u5bfc\u4f53\u63d0\u51fa\u6db2\u6676\u4e13\u5229\u4fb5\u6743\u7684\u8bc9\u8bbc\uff0c\u6253\u7b97\u4ee5\u4e13\u5229\u8bc9\u8bbc\u7ea0\u7eb7\u6765\u963b\u6b62\u7231\u8fbe\u6536\u8d2d\u73b0\u4ee3\u534a\u5bfc\u4f53\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e5f\u975e\u7701\u6cb9\u7684\u706f\uff0c\u516d\u6708\u4e0b\u65ec\u901a\u8fc7\u9526\u6e56\u5546\u4e8b\u5411\u534e\u7a00\u77ff\u4e1a\u63d0\u4f9b\u4e00\u4ebf\u7f8e\u5143\u8d44\u91d1\u7684\u501f\u8d37\u4f5c\u4e3a\u7a00\u6563\u91d1\u5c5e\u7684\u50a8\u5907\u8d44\u91d1\uff0c\u534e\u7a00\u77ff\u4e1a\u7d27\u63a5\u7740\u5c31\u5ba3\u5e03\u8fd9\u7b14\u8d44\u91d1\u5c06\u9996\u5148\u7528\u4e8e\u7a00\u6563\u91d1\u5c5e\u94df\u7684\u50a8\u5907\u3002</p><p>\u3000\u3000\u7531\u4e8e\u6db2\u6676\u5e02\u573a\u8fd8\u6ca1\u6709\u771f\u6b63\u7684\u53d1\u5c55\u8d77\u6765\uff0c\u4f5c\u4e3a\u6db2\u6676\u663e\u793a\u8bbe\u5907\u7684\u5fc5\u9700\u539f\u6599\u91d1\u5c5e\u94df\u5168\u7403\u5e74\u603b\u8d38\u6613\u989d\u624d\u4e09\u5343\u4e07\u7f8e\u5143\u591a\u4e9b\u3002\u534e\u7a00\u77ff\u4e1a\u4ece\u4e5d\u516b\u5e74\u4e4b\u540e\u5c31\u9010\u6e10\u63a7\u5236\u4e86\u5168\u7403\u7ea670\uff05\u91d1\u5c5e\u94df\u7684\u751f\u4ea7\u4e0e\u51fa\u53e3\u8d38\u6613\uff0c\u534e\u7a00\u77ff\u4e1a\u8fd9\u6b21\u5ba3\u5e03\u62ff\u51fa\u4e00\u4ebf\u7f8e\u5143\u7528\u4e8e\u91d1\u5c5e\u94df\u7684\u50a8\u5907\uff0c\u4e00\u4e0b\u5b50\u5c31\u523a\u6fc0\u91d1\u5c5e\u94df\u56fd\u9645\u4ef7\u683c\u4e0a\u6da8\u4e8660\uff05\u3002</p><p>\u3000\u3000\u6839\u636e\u56fd\u9645\u7535\u5b50\u534f\u4f1a\u5bf9\u6db2\u6676\u4ea7\u4e1a\u7684\u9884\u6d4b\uff0c\u5168\u7403\u6db2\u6676\u4ea7\u4e1a\u4eca\u540e\u4e94\u5e74\u5185\u5bf9\u91d1\u5c5e\u94df\u7684\u5e74\u9700\u6c42\u91cf\u4f1a\u4ece\u5f53\u524d\u7684\u4e00\u767e\u5428\u66b4\u589e\u5230\u4e94\u767e\u5428\uff0c\u5168\u7403\u91d1\u5c5e\u94df\u4f9b\u5e94\u4e5f\u5c06\u4ece\u5f53\u524d\u7684\u4e24\u767e\u5428\u66b4\u589e\u5230\u516b\u767e\u5428\uff0c\u589e\u52a0\u8fd9\u4e48\u591a\u7684\u94df\u9700\u6c42\u91cf\u5927\u591a\u6570\u81ea\u7136\u4e5f\u5c06\u7531\u534e\u7a00\u77ff\u4e1a\u63a7\u5236\u7684\u94df\u5de5\u5382\u4f9b\u5e94\u3002\u8981\u662f\u534e\u7a00\u77ff\u4e1a\u6536\u50a8\u4e00\u5e74\u7684\u6240\u4ea7\u91cf\uff0c\u5230\u65f6\u5019\u5173\u952e\u95ee\u9898\u4e0d\u662f\u91d1\u5c5e\u94df\u7684\u4ef7\u683c\u4f1a\u66b4\u6da8\u5230\u591a\u5c11\uff0c\u800c\u662f\u6db2\u6676\u4ea7\u4e1a\u6839\u672c\u5c31\u83b7\u53d6\u4e0d\u5230\u8db3\u591f\u7684\u91d1\u5c5e\u94df\u7528\u4e8e\u751f\u4ea7\u3002</p><p>\u3000\u3000\u5373\u4f7f\u534e\u7a00\u77ff\u4e1a\u6ca1\u6709\u4e1d\u6beb\u5a01\u80c1\u7740\u8981\u5b8c\u5168\u64cd\u7eb5\u91d1\u5c5e\u94df\u8d38\u6613\u7684\u610f\u601d\uff0c\u4f46\u662f\u534e\u7a00\u77ff\u4e1a\u629b\u51fa\u4e00\u4ebf\u7f8e\u5143\u50a8\u5907\u57fa\u91d1\u8ba1\u5212\uff0c\u5c31\u5f15\u8d77\u6d77\u5916\u52bf\u529b\u5f3a\u70c8\u7684\u53cd\u5f39\u3002\u4e5f\u4e0d\u4ec5\u4ec5\u662f\u62c5\u5fe7\u56fd\u9645\u91d1\u5c5e\u94df\u8d38\u6613\u7ed9\u5b8c\u5168\u63a7\u5236\uff0c\u6d77\u5916\u52bf\u529b\u5bf9\u534e\u7a00\u77ff\u4e1a\u7684\u5b58\u5728\u4e00\u76f4\u90fd\u9887\u6709\u5fae\u8f9e\u3002</p><p>\u3000\u3000\u81ea\u4ece\u4ee5\u6574\u5408\u56fd\u5185\u7a00\u6563\u91d1\u5c5e\u51fa\u53e3\u8d44\u6e90\u4e3a\u76ee\u6807\u7684\u534e\u7a00\u77ff\u4e1a\u7ec4\u5efa\u4ee5\u6765\uff0c\u7a00\u6563\u91d1\u5c5e\uff0c\u7279\u522b\u662f\u56fd\u5185\u50a8\u5907\u5360\u636e\u5168\u7403\u50a8\u5907\u6bd4\u4f8b\u8fc7\u534a\u7684\u7a00\u6563\u91d1\u5c5e\u56fd\u9645\u8d38\u6613\u4ef7\u683c\u5728\u8fd9\u4e24\u5e74\u65f6\u95f4\u91cc\u90fd\u5927\u5e45\u4e0a\u5347\u3002\u50cf\u91d1\u5c5e\u94df\u56fd\u9645\u4ef7\u683c\u5c31\u4ece\u4e5d\u4e03\u5e74\u5e95\u7684\u6bcf\u516c\u65a4\u516d\u5341\u7f8e\u5143\u5feb\u901f\u4e0a\u5347\u5230\u73b0\u5728\u7684\u6bcf\u516c\u65a4\u4e00\u767e\u516d\u5341\u7f8e\u5143\uff0c\u53c8\u56e0\u4e3a\u534e\u7a00\u77ff\u4e1a\u8fd9\u6b21\u7684\u5efa\u50a8\u58f0\u660e\uff0c\u53c8\u6fc0\u589e\u5230\u6bcf\u516c\u65a4\u4e8c\u767e\u4e94\u5341\u7f8e\u5143\u4ee5\u4e0a\u3002\u8fd9\u4e9b\u7684\u786e\u8fd8\u662f\u523a\u6fc0\u5230\u4e00\u4e9b\u4eba\u7684\u795e\u7ecf\uff0c\u751a\u81f3\u6709\u4e9b\u56fd\u5bb6\u5c06\u8fd9\u4e2a\u95ee\u9898\u4ee5\u4e2d\u56fd\u80fd\u5426\u987a\u5229\u52a0\u5165\u4e16\u8d38\u7ec4\u7ec7\u6765\u5a01\u808b\u3002</p><p>\u3000\u3000\u867d\u7136\u5728\u5f53\u524d\u56fd\u9645\u5f62\u52bf\u4e0b\uff0c\u4e2d\u592e\u653f\u5e9c\u90fd\u6ca1\u6709\u901a\u8fc7\u534e\u7a00\u77ff\u4e1a\u64cd\u7eb5\u56fd\u9645\u7a00\u6563\u91d1\u5c5e\u5e02\u573a\u4ef7\u683c\u7684\u53ef\u80fd\uff0c\u9526\u6e56\u66f4\u4e0d\u53ef\u80fd\u901a\u8fc7\u534e\u7a00\u77ff\u4e1a\u771f\u6b63\u7684\u6390\u65ad\u91d1\u5c5e\u94df\u4e00\u5230\u4e24\u5e74\u7684\u4f9b\u5e94\uff0c\u4f46\u662f\u9526\u6e56\u5546\u4e8b\u8fd9\u6b21\u4e3e\u63aa\u4f9d\u65e7\u6709\u6572\u8b66\u949f\u7684\u610f\u5473\uff0c\u4e0d\u7136\u9526\u6e56\u5546\u4e8b\u5728\u81ea\u8eab\u8d44\u91d1\u90fd\u7d27\u7f3a\u4e4b\u65f6\u4f55\u9700\u5411\u534e\u7a00\u77ff\u4e1a\u63d0\u4f9b\u4e00\u4ebf\u7f8e\u5143\u7684\u50a8\u5907\u8d44\u91d1\uff1f\u9526\u6e56\u624d\u5728\u534e\u7a00\u77ff\u4e1a\u5360\u4e0d\u523012\uff05\u7684\u80a1\u6743\u3002</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u6000\u7591\u9526\u6e56\u7684\u8fd9\u4e00\u4e3e\u63aa\u80fd\u4e0d\u80fd\u771f\u6b63\u7684\u89e6\u52a8\u65e5\u97e9\u7535\u5b50\u5de8\u5934\u3001\u6d88\u51cf\u5176\u6536\u8d2d\u73b0\u4ee3\u534a\u5bfc\u4f53\u65d7\u4e0b\u6db2\u6676\u4e1a\u52a1\u7684\u52aa\u529b\uff0c\u4f46\u662f\u5176\u4ed6\u516c\u5f00\u89c1\u62a5\u7684\u6d88\u606f\u53c8\u592a\u5c11\uff0c\u5fc3\u60f3\u5f20\u606a\u8fd9\u6b21\u53bb\u5317\u4eac\u4e5f\u8bb8\u5c31\u662f\u4e3a\u8fd9\u4e8b\u800c\u53bb\u3002\u6700\u8fd1\u97e9\u56fd\u97e9\u5174\u94f6\u884c\u9ad8\u5c42\u5728\u5317\u4eac\u8bbf\u95ee\uff0c\u800c\u97e9\u56fd\u97e9\u5174\u94f6\u884c\u53c8\u662f\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u4e3b\u8981\u503a\u6743\u4eba\uff0c\u53c8\u662f\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u5b9e\u9645\u63a7\u5236\u8005\u4e4b\u4e00\uff0c\u5f20\u606a\u8d76\u5230\u5317\u4eac\u4e0e\u97e9\u5174\u94f6\u884c\u9ad8\u5c42\u63a5\u89e6\uff0c\u90a3\u662f\u518d\u6b63\u5e38\u4e0d\u8fc7\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u523b\u610f\u56de\u907f\u76f8\u9047\uff0c\u4e25\u6587\u4ecb\u4e0a\u8f66\u540e\u5c31\u8eb2\u5728\u81ea\u5df1\u7684\u8f6f\u5367\u5305\u53a2\uff0c\u751a\u81f3\u90fd\u6709\u4e9b\u540e\u6094\u7740\u6025\u5750\u5217\u8f66\u53bb\u5317\u4eac\uff0c\u6797\u96ea\u3001\u5218\u5947\u5cfb\u3001\u738b\u6d77\u7c9f\u4e5f\u90fd\u95f7\u5728\u4e25\u6587\u4ecb\u7684\u8f6f\u5367\u5305\u53a2\u91cc\u5546\u8bae\u660e\u5929\u7684\u516c\u5173\u884c\u52a8\uff0c\u90fd\u5bf9\u8ddf\u5f20\u606a\u540c\u4e58\u4e00\u5217\u706b\u8f66\u611f\u5230\u4e0d\u81ea\u5728\uff0c\u4eff\u4f5b\u9488\u8292\u523a\u80cc\u3002</p><p>\u3000\u3000\u5750\u5728\u4e25\u6587\u4ecb\u7684\u5305\u53a2\u91cc\uff0c\u6797\u96ea\u90fd\u5fcd\u4e0d\u4f4f\u5f00\u53e3\u62b1\u6028\uff1a\u201c\u4ed6\u6ca1\u4e8b\u60c5\u5750\u4ec0\u4e48\u706b\u8f66\uff1f\u201d\u5979\u6700\u7ec8\u4e4b\u6240\u4ee5\u540c\u610f\u62ff\u7cbe\u5178\u5730\u4ea7\u62b5\u62bc\u4ece\u4fe1\u901a\u94f6\u884c\u62b5\u62bc\u8d37\u6b3e\u5341\u4e2a\u4ebf\u8fdb\u5165\u4e8c\u7ea7\u5e02\u573a\u6258\u5e02\uff0c\u4e5f\u662f\u5b8c\u5168\u53d7\u5230\u5f20\u606a\u7684\u523a\u6fc0\uff0c\u589e\u53d1\u6ca1\u6709\u4e00\u5e06\u98ce\u987a\uff0c\u518d\u6b21\u9762\u4e34\u65b0\u7684\u5371\u673a\uff0c\u6797\u96ea\u867d\u7136\u6ca1\u6709\u6028\u5929\u5c24\u4eba\u7684\u610f\u601d\uff0c\u77e5\u9053\u5f20\u606a\u5728\u540c\u4e00\u5217\u706b\u8f66\u91cc\u611f\u89c9\u4e0a\u603b\u4e0d\u5bf9\u5473\uff0c\u5546\u8bae\u4e8b\u60c5\u4e5f\u96c6\u4e2d\u4e0d\u4e86\u6ce8\u610f\u529b\u3002</p><p>\u3000\u3000\u591c\u91cc\u7761\u68a6\u4e2d\uff0c\u6797\u96ea\u9690\u7ea6\u542c\u89c1\u738b\u6d77\u7c9f\u5728\u60ca\u60f6\u5931\u63aa\u7684\u6572\u9694\u58c1\u5305\u53a2\u7684\u95e8\uff1a\u201c\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u5c3e\u76d8\u65f6\u5927\u5e45\u8df3\u6c34\u2026\u2026\u201d</p></article>"}}}]);
