(window.webpackJsonp=window.webpackJsonp||[]).push([[186],{209:function(p){p.exports={chapterTitle:null,title:"\u7b2c186\u7ae0 \u7f8e\u5973\u4e5f\u6709\u5185\u5fc3\u4e16\u754c",content:"<article><h2>\u7b2c186\u7ae0 \u7f8e\u5973\u4e5f\u6709\u5185\u5fc3\u4e16\u754c</h2><p>\u3000\u3000\u5f20\u606a\u662f\u76db\u946b\u8d38\u6613\u516c\u53f8\u5e02\u573a\u90e8\u603b\u76d1\u90b5\u5fc3\u6587\u90d1\u91cd\u5176\u4e8b\u7684\u4ecb\u7ecd\u7ed9\u656c\u98de\u9e3f\u7684\uff0c\u656c\u98de\u9e3f\u5bf9\u5f20\u606a\u4ee5\u53ca\u7231\u8fbe\u7535\u5b50\u7684\u5e95\u7ec6\u4e0d\u6e05\u695a\uff0c\u65e2\u7136\u662f\u8001\u540c\u5b66\u90b5\u5fc3\u6587\u90d1\u91cd\u5176\u4e8b\u7684\u4ecb\u7ecd\uff0c\u4ed6\u7edd\u6ca1\u6709\u9a6c\u864e\u7684\u9053\u7406\u3002\u9664\u4e86\u5bf9\u98de\u9e3f\u516c\u53f8\u7684\u5236\u4f5c\u6c34\u5e73\u5341\u5206\u6311\u5254\u5916\uff0c\u5f20\u606a\u8fd8\u662f\u6781\u597d\u76f8\u5904\u7684\u4eba\uff0c\u5f53\u7136\uff0c\u656c\u98de\u9e3f\u4e5f\u8ba4\u53ef\u5f20\u606a\u6709\u6311\u5254\u7684\u8d44\u683c\uff0c\u98de\u9e3f\u5e7f\u544a\u56e0\u4e3a\u5f20\u606a\u7684\u6311\u5254\u800c\u5f97\u76ca\u532a\u6d45\uff0c\u56e0\u4e3a\u4ed6\u6bcf\u6bcf\u90fd\u4e00\u9488\u89c1\u8840\u7684\u6307\u51fa\u5e7f\u544a\u5236\u4f5c\u4e2d\u7684\u4e0d\u8db3\uff0c\u53c8\u7edd\u4e0d\u653e\u8fc7\u4ed6\u770b\u4e0d\u987a\u773c\u7684\u7ec6\u8282\uff0c\u98de\u9e3f\u5e7f\u544a\u7684\u5236\u4f5c\u6c34\u51c6\uff0c\u8fd9\u6bb5\u65f6\u95f4\u53ef\u4ee5\u8bf4\u662f\u88ab\u5f3a\u5236\u6027\u7684\u63d0\u9ad8\u4e86\u4e00\u622a\u3002</p><p>\u3000\u3000\u6709\u8fd9\u6837\u7684\u4eba\u97ad\u82d4\u624b\u4e0b\u7684\u5236\u4f5c\u56e2\u961f\uff0c\u656c\u98de\u9e3f\u591c\u91cc\u505a\u68a6\u90fd\u8981\u7b11\u9192\uff0c\u53ea\u662f\u5408\u6210\u7684\u62f7\u8d1d\u4e00\u6b21\u6b21\u7ed9\u9000\u56de\u65f6\u7684\u5fc3\u60c5\u5e76\u4e0d\u597d\u53d7\uff0c\u5e78\u597d\u8fd9\u4e00\u5173\u71ac\u8fc7\u53bb\u4e86\u3002</p><p>\u3000\u3000\u98de\u9e3f\u5e7f\u544a\u7684\u5236\u4f5c\u603b\u76d1\u6709\u4e9b\u5fe4\u5f20\u606a\uff0c\u4eca\u5929\u7684\u6837\u5e26\u5236\u4f5c\u51fa\u6765\uff0c\u4ed6\u5c31\u501f\u53e3\u6e9c\u4e4b\u5927\u5409\uff1a\u201c\u8c01\u60f3\u770b\u5230\u81ea\u5df1\u7684\u4f5c\u54c1\u7ed9\u6233\u5f97\u9c9c\u8840\u6dcb\u6f13\u7684\uff1f\u7231\u8fbe\u7535\u5b50\u4ee5\u540e\u7684\u5e7f\u544a\u5236\u4f5c\uff0c\u54b1\u4e0d\u63a5\u4e86\u6210\u4e0d\uff1f\u6d6a\u8d39\u8fd9\u4e48\u591a\u65f6\u95f4\uff0c\u5236\u4f5c\u6210\u672c\u53c8\u9ad8\uff0c\u6a21\u7279\u5c31\u6362\u4e86\u56db\u4e2a\uff0c\u6839\u672c\u8d5a\u4e0d\u4e86\u94b1\u3002\u201d</p><p>\u3000\u3000\u656c\u98de\u9e3f\u5fc3\u91cc\u53ef\u662f\u6253\u5b9a\u4e3b\u610f\u5c31\u662f\u4e8f\u94b1\u4e5f\u8981\u63a5\u7231\u8fbe\u7535\u5b50\u7684\u5e7f\u544a\u5236\u4f5c\uff0c\u4ed6\u7684\u8001\u540c\u5b66\u3001\u76db\u946b\u5e02\u573a\u90e8\u603b\u76d1\u90b5\u5fc3\u6587\u8ddf\u4ed6\u8bf4\u8d77\u8fc7\u8fd9\u4e8b\uff1a\u201c\u4f60\u4eec\u7684\u5236\u4f5c\u6c34\u51c6\u8981\u80fd\u8ba9\u7231\u8fbe\u7535\u5b50\u6ee1\u610f\uff0c\u5c31\u5177\u5907\u4e00\u6d41\u5e7f\u544a\u516c\u53f8\u7684\u6f5c\u8d28\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u4ed6\u4e0d\u77e5\u9053\u90b5\u5fc3\u6587\u8fd9\u53e5\u8bdd\u662f\u5bf9\u5f20\u606a\u4e2a\u4eba\u7684\u63a8\u5d07\uff0c\u5374\u4ee5\u4e3a\u90b5\u5fc3\u6587\u6697\u793a\u7231\u8fbe\u7535\u5b50\u6709\u7740\u5927\u516c\u53f8\u7684\u6c14\u9b44\u3002\u8981\u662f\u7d22\u5c3c\u3001\u98de\u5229\u6d66\u8fd9\u6837\u7684\u5927\u516c\u53f8\u627e\u98de\u9e3f\u516c\u53f8\u5236\u4f5c\u5e7f\u544a\uff0c\u656c\u98de\u9e3f\u5c31\u662f\u5012\u8d34\u4e5f\u613f\u610f\u554a\u3002\u5e78\u597d\u4ed6\u6ca1\u6709\u63d0\u51fa\u53bb\u7231\u8fbe\u7535\u5b50\u5b9e\u666f\u62cd\u6444\uff0c\u4e0d\u7136\u8bf4\u4e0d\u5b9a\u4f1a\u5bf9\u90b5\u5fc3\u6587\u5927\u53d1\u7262\u9a9a\uff1a\u201c\u5c31\u8fd9\u5bb6\u7834\u5382\u5b50\uff0c\u5c31\u7ed9\u8fd9\u4e48\u70b9\u94b1\uff0c\u8fd8\u6562\u8fd9\u4e48\u6311\u5254\uff01\u201d</p><p>\u3000\u3000\u656c\u98de\u9e3f\u5fc3\u91cc\u53ea\u5f53\u7231\u8fbe\u7535\u5b50\u662f\u5bb6\u5927\u516c\u53f8\uff0c\u62cd\u6444\u7684\u5e7f\u544a\u7247\u53c8\u76f4\u63a5\u62ff\u5230\u4e2d\u592e\u7535\u89c6\u53f0\u64ad\u653e\uff0c\u5bf9\u5f20\u606a\u81ea\u7136\u662f\u5c3d\u5fc3\u5df4\u7ed3\u3002\u5085\u4fca\u5e26\u6c5f\u9edb\u513f\u6765\u8bd5\u955c\uff0c\u4ed6\u8ba4\u51fa\u662f\u4ed6\u66fe\u7ecf\u5728\u8857\u5934\u5076\u7136\u60f3\u53d1\u6398\u5374\u7ed9\u65e0\u60c5\u62d2\u7edd\u7684\u4e3d\u4eba\uff0c\u4ed6\u5fc3\u91cc\u8fd8\u662f\u6709\u4e9b\u610f\u89c1\u3002\u6bd5\u7adf\u524d\u4e00\u4e2a\u5e7f\u544a\u5973\u90ce\uff0c\u4ed6\u8fd8\u7b54\u5e94\u8981\u6367\u4eba\u5bb6\u505a\u5e7f\u544a\u660e\u661f\uff0c\u597d\u5904\u8fd8\u6ca1\u6709\u6cbe\u5230\uff0c\u5c31\u8ba9\u5f20\u606a\u585e\u79c1\u4eba\u8fdb\u6765\uff0c\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u4e0d\u723d\u3002\u656c\u98de\u9e3f\u5374\u4ee5\u4e3a\u6c5f\u9edb\u513f\u4e0e\u5f20\u606a\u6709\u7740\u7279\u6b8a\u5173\u7cfb\u624d\u7a81\u7136\u80fd\u4e0a\u4f4d\u7684\uff0c\u5236\u4f5c\u65b9\u603b\u62e7\u4e0d\u8fc7\u5382\u5546\u7684\u610f\u5fd7\uff0c\u53ea\u5f97\u6362\u4eba\uff0c\u8ba9\u5979\u8bd5\u955c\u3002\u8c01\u66fe\u60f3\u5230\u6c5f\u9edb\u513f\u4e00\u7ad9\u5230\u955c\u5934\u7adf\u6d0b\u6ea2\u51fa\u8000\u773c\u593a\u76ee\u7684\u5149\u5f69\u6765\uff0c\u58f0\u7ebf\u66f4\u662f\u8ff7\u4eba\u5f97\u4e0d\u884c\uff0c\u8fd9\u6837\u7684\u4eba\u4e0d\u8fdb\u5165\u6f14\u827a\u5708\u53d1\u5c55\u7b80\u503c\u662f\u66b4\u6b84\u5929\u73cd\u3002\u5728\u5e7f\u544a\u516c\u53f8\u4ed6\u5c31\u529d\u6c5f\u9edb\u513f\u8fdb\u5165\u6f14\u827a\u5708\uff0c\u98de\u9e3f\u5e7f\u544a\u53ef\u4ee5\u5e2e\u5979\u5305\u88c5\uff0c\u6c5f\u9edb\u513f\u53ea\u662f\u8bd5\u62cd\u5e7f\u544a\u7247\uff0c\u5176\u4ed6\u7684\u4e00\u6982\u4e0d\u5e94\uff0c\u656c\u98de\u9e3f\u5374\u6108\u53d1\u80af\u5b9a\u5f20\u606a\u4e0e\u6c5f\u9edb\u513f\u4e4b\u95f4\u7684\u7279\u6b8a\u5173\u7cfb\uff0c\u6ca1\u51e0\u4e2a\u5bcc\u8c6a\u613f\u610f\u81ea\u5df1\u7684\u5973\u4eba\u8fdb\u5165\u6f14\u827a\u5708\u7684\uff0c\u8fd9\u4f1a\u513f\u89c1\u5230\u5f20\u606a\uff0c\u53c8\u5fcd\u4e0d\u4f4f\u529d\u8bf4\u8d77\u6765\u3002</p><p>\u3000\u3000\u65e0\u8bba\u656c\u98de\u9e3f\u7684\u8d5e\u8a89\u4e0e\u9f13\u52a8\u591a\u4e48\u6709\u8bf1\u60d1\u6027\uff0c\u6c5f\u9edb\u513f\u53ea\u662f\u9759\u9759\u7684\u5750\u5728\u5f20\u606a\u7684\u8eab\u8fb9\u4e0d\u8bf4\u8bdd\uff0c\u62bf\u7740\u5634\uff0c\u8138\u4e0a\u85cf\u7740\u6d45\u6d45\u7684\u7b11\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u7f6e\u53ef\u5426\uff0c\u8fd9\u6bd5\u7adf\u662f\u6c5f\u9edb\u513f\u81ea\u5df1\u7684\u4e8b\u60c5\uff0c\u5c06\u62f7\u8d1d\u5e26\u9000\u51fa\u6765\uff0c\u5bf9\u656c\u98de\u9e3f\u8bf4\u9053\uff1a\u201c\u63a5\u4e0b\u6765\u7684\u4e8b\u60c5\u8fd8\u8981\u62dc\u6258\u656c\u603b\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u662f\u5f53\u7136\uff0c\u606a\u5c11\u5429\u5490\u7684\u4e8b\u54ea\u80fd\u4e0d\u5c3d\u5fc3\uff1f\u201d\u656c\u98de\u9e3f\u4e34\u4e0b\u697c\u8fd8\u56de\u5934\u770b\u5230\u6c5f\u9edb\u513f\u4e00\u773c\uff0c\u201c\u606a\u5c11\u4e0d\u518d\u8003\u8651\u8003\u8651\u9edb\u513f\u5c0f\u59d0\u7684\u4e8b\u60c5\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6e29\u548c\u7684\u7b11\u4e86\u7b11\uff0c\u53c8\u4e0d\u5173\u81ea\u5df1\u7684\u4e8b\u60c5\uff0c\u4f38\u624b\u8bf7\u656c\u98de\u9e3f\u5148\u4e0b\u697c\uff0c\u4ed6\u6293\u7740\u6276\u68af\u4e5f\u8981\u8ddf\u7740\u4e0b\u697c\uff0c\u5374\u542c\u89c1\u6c5f\u9edb\u513f\u5728\u540e\u9762\u5c0f\u58f0\u7684\u8bf4\uff1a\u201c\u5f20\u606a\uff0c\u4f60\u89c9\u5f97\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u6c5f\u9edb\u513f\u5fc3\u91cc\u4f1a\u6709\u6323\u624e\uff0c\u96be\u4e3a\u5979\u5f88\u5b89\u9759\u7684\u5750\u5728\u90a3\u91cc\u4e00\u76f4\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5374\u6ca1\u60f3\u5230\u5979\u4f1a\u5f81\u8be2\u81ea\u5df1\u7684\u610f\u89c1\uff0c\u56de\u5934\u770b\u7740\u6c5f\u9edb\u513f\u660e\u7738\u91cc\u671f\u5f85\u800c\u4fe1\u4efb\u7684\u773c\u795e\uff0c\u5f20\u606a\u5374\u662f\u5fc3\u52a8\u4e86\uff0c\u4ed6\u7ad9\u5728\u697c\u68af\u4e0d\u52a8\uff0c\u7b49\u7740\u656c\u98de\u9e3f\u5148\u4e0b\u697c\u53bb\uff0c\u624d\u5bf9\u6c5f\u9edb\u513f\u8bf4\uff1a\u201c\u4f60\u662f\u600e\u4e48\u60f3\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u65f6\u5019\u8ddf\u8bb8\u7ef4\u3001\u4ee4\u5c0f\u71d5\u5979\u4eec\u6bd4\uff0c\u53d1\u73b0\u81ea\u5df1\u771f\u7684\u4e00\u65e0\u662f\u5904\u2026\u2026\u201d\u6c5f\u9edb\u513f\u54ac\u7740\u5634\u5507\uff0c\u5374\u6ca1\u6709\u907f\u5f00\u5f20\u606a\u6e05\u4eae\u7684\u76ee\u5149\uff0c\u201c\u6211\u53c8\u627e\u4e0d\u5230\u4eba\u80fd\u5546\u91cf\u2026\u2026\u201d</p><p>\u3000\u3000\u60f3\u4e0d\u5230\u6c5f\u9edb\u513f\u62e5\u6709\u8fd9\u4e48\u5b8c\u7f8e\u7684\u5916\u8c8c\u8fd8\u6709\u81ea\u5351\u7684\u5fc3\u7406\uff0c\u5f20\u606a\u5c55\u989c\u4e00\u7b11\uff0c\u53cc\u624b\u62b1\u7740\u80f8\u770b\u6c5f\u9edb\u513f\u7ee7\u7eed\u8bf4\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u201c\u6211\u2026\u2026\u201d\u6c5f\u9edb\u513f\u5fc3\u91cc\u767e\u8f6c\u5343\u56de\uff0c\u5374\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u8bf4\u51fa\u53e3\uff0c\u7279\u522b\u697c\u4e0b\u4eba\u58f0\u9f0e\u6cb8\uff0c\u6b64\u65f6\u53c8\u4e0d\u662f\u8868\u9732\u5fc3\u8ff9\u7684\u73af\u5883\uff0c\u54ac\u7740\u5634\u5507\u8d70\u8fc7\u6765\uff0c\u5c06\u5f20\u606a\u62c9\u5230\u623f\u95f4\u91cc\uff0c\u53c8\u5c06\u95e8\u53cd\u9501\u4e0a\u3002</p><p>\u3000\u3000\u770b\u7740\u6c5f\u9edb\u513f\u5438\u4e86\u51e0\u53e3\u6c14\uff0c\u773c\u775b\u4f3c\u8981\u6ef4\u51fa\u6c34\u4f3c\u7684\u8bf1\u4eba\uff0c\u4e30\u633a\u7684\u80f8\u90e8\u8bf1\u4eba\u4e4b\u6781\uff0c\u5f20\u606a\u4e0d\u6653\u5f97\u5979\u7a81\u7136\u62c9\u81ea\u5df1\u8fdb\u623f\u95f4\u505a\u4ec0\u4e48\uff0c\u4f46\u89c1\u5979\u8fd9\u822c\u8bf1\u4eba\u7684\u6a21\u6837\uff0c\u5fc3\u91cc\u4e5f\u6709\u671f\u5f85\u3002</p><p>\u3000\u3000\u201c\u6211\u5531\u51fa\u6765\u4f60\u542c\u542c\u2026\u2026\u201d\u6c5f\u9edb\u513f\u597d\u4e0d\u5bb9\u6613\u8bf4\u51fa\u8fd9\u53e5\u8bdd\uff0c\u624d\u9661\u7136\u653e\u677e\u4f3c\u7684\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u597d\u50cf\u5728\u522b\u4eba\u9762\u524d\u5531\u6b4c\u6bd4\u780d\u5979\u7684\u5934\u90fd\u96be\u3002</p><p>\u3000\u3000\u201c\u2026\u2026</p><p>\u3000\u3000\u4e0d\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\u2026\u2026</p><p>\u3000\u3000\u5fe7\u6089\u5b83\u56f4\u7ed5\u7740\u6211\u2026\u2026</p><p>\u3000\u3000\u6211\u6bcf\u5929\u90fd\u5728\u7948\u7977\u2026\u2026</p><p>\u3000\u3000\u5feb\u8d76\u8d70\u7231\u7684\u5bc2\u5bde\u2026\u2026</p><p>\u3000\u3000\u90a3\u5929\u8d77\u2026\u2026</p><p>\u3000\u3000\u4f60\u5bf9\u6211\u8bf4\u2026\u2026</p><p>\u3000\u3000\u6c38\u8fdc\u7684\u7231\u7740\u6211\u2026\u2026</p><p>\u3000\u3000\u5343\u8a00\u548c\u4e07\u8bed\u968f\u6d6e\u4e91\u63a0\u8fc7\u2026\u2026</p><p>\u3000\u3000\u4e0d\u77e5\u9053\u4e3a\u4e86\u4ec0\u4e48\u2026\u2026</p><p>\u3000\u3000\u5fe7\u6089\u5b83\u56f4\u7ed5\u7740\u6211\u2026\u2026</p><p>\u3000\u3000\u6211\u6bcf\u5929\u90fd\u5728\u7948\u7977\u2026\u2026</p><p>\u3000\u3000\u5feb\u8d76\u8d70\u7231\u7684\u5bc2\u5bde\u2026\u2026</p><p>\u3000\u3000\u90a3\u5929\u8d77\u2026\u2026</p><p>\u3000\u3000\u4f60\u5bf9\u6211\u8bf4\u2026\u2026</p><p>\u3000\u3000\u6c38\u8fdc\u7684\u7231\u7740\u6211\u2026\u2026</p><p>\u3000\u3000\u5343\u8a00\u548c\u4e07\u8bed\u968f\u6d6e\u4e91\u63a0\u8fc7\u2026\u2026\u201d</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u90a3\u4e9b\u8574\u7740\u65e0\u9650\u843d\u5bde\u7684\u58f0\u7ebf\uff0c\u5c31\u4eff\u4f5b\u6e05\u6f88\u6cc9\u6c34\u91cc\u7684\u7ec6\u5c0f\u6c14\u6ce1\uff0c\u7f8e\u4e3d\u7684\u4e0d\u65ad\u76db\u5f00\uff0c\u6d6e\u5230\u6c34\u9762\u5374\u77ac\u95f4\u7684\u7834\u5f00\u6d88\u901d\uff0c\u9093\u4e3d\u541b\u7684\u300a\u5343\u8a00\u4e07\u8bed\u300b\u5c31\u8fd9\u6837\u5b8c\u7f8e\u7684\u8ba9\u6c5f\u9edb\u513f\u6f14\u7ece\u51fa\u6765\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u5fae\u5fae\u9894\u9996\uff0c\u5c06\u767d\u6da6\u5c24\u5982\u7f8e\u7389\u7684\u9888\u8116\u5b50\u9732\u5728\u5f20\u606a\u773c\u91cc\uff0c\u4ece\u4ed6\u7684\u89d2\u5ea6\u6b63\u597d\u597d\u53ef\u4ee5\u770b\u89c1\u6c5f\u9edb\u513f\u7f8e\u5230\u6781\u81f4\u7684\u773c\u7ebf\uff0c\u7adf\u8638\u7740\u5927\u9897\u7684\u6cea\u6c34\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u5f97\u4e0d\u627f\u8ba4\u6c5f\u9edb\u513f\u90a3\u4e00\u523b\u5f7b\u5e95\u6253\u52a8\u81ea\u5df1\uff0c\u53ea\u6015\u518d\u4e5f\u65e0\u6cd5\u5c06\u6b64\u65f6\u7684\u5370\u8c61\u62b9\u53bb\uff0c\u7adf\u6ca1\u6709\u52c7\u6c14\u76f4\u89c6\u5979\u90a3\u5bb9\u5149\u707c\u4eba\u7684\u5bb9\u989c\uff0c\u4e0e\u90a3\u53cc\u8638\u7740\u6cea\u6c34\u7684\u660e\u7738\u3002</p><p>\u3000\u3000\u9093\u4e3d\u541b\u65e0\u53ef\u907f\u514d\u7684\u4e8e\u4eca\u5e745\u67088\u65e5\u5728\u6cf0\u56fd\u56e0\u6c14\u5598\u901d\u4e16\uff0c\u65f6\u5e74\u56db\u5341\u4e8c\u5c81\uff0c\u90a3\u4e9b\u6bcf\u6bcf\u5f15\u8d77\u98ce\u9761\u7684\u6f14\u5531\u4f1a\u5df2\u6210\u7edd\u54cd\uff0c\u8ba9\u4eba\u4e0d\u80dc\u7a00\u5618\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u660e\u7738\u91cc\u8638\u7740\u90a3\u6ef4\u6676\u83b9\u7684\u6cea\u73e0\u6216\u4e3a\u60cb\u60dc\u5de8\u661f\u6b92\u843d\u6216\u662f\u5185\u5fc3\u7ea2\u989c\u81ea\u4f24\uff0c\u5f20\u606a\u5374\u77e5\u9053\u5979\u8ff7\u4eba\u7684\u5916\u8868\u8fd8\u85cf\u7740\u4e00\u9897\u80fd\u6253\u52a8\u4eba\u7684\u5fc3\uff0c\u8f7b\u8f7b\u5410\u4e86\u53e3\u6c14\uff0c\u8d70\u8fc7\u53bb\u6253\u5f00\u95e8\uff0c\u770b\u89c1\u8bb8\u7ef4\u6b63\u4e0b\u697c\u68af\uff0c\u5927\u6982\u662f\u4e0a\u697c\u6765\u5524\u4ed6\u4eec\uff0c\u542c\u5230\u9edb\u513f\u7684\u6b4c\u58f0\u4e4b\u540e\u53c8\u6298\u8eab\u4e0b\u697c\u7684\u5427\uff0c\u5f20\u606a\u56de\u5934\u770b\u6c5f\u9edb\u513f\u4e00\u773c\uff0c\u6c5f\u9edb\u513f\u6602\u7740\u5934\u770b\u7740\u4ed6\uff0c\u773c\u7738\u56e0\u542b\u7740\u7684\u6cea\u73e0\u66f4\u52a0\u6e05\u6f88\u3001\u575a\u5b9a\uff0c\u5f20\u606a\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u770b\u5230\u9edb\u513f\u4e00\u76f4\u8fd9\u6837\u5b50\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u771f\u7684\u53ef\u4ee5\u5417\uff1f\u201d\u6c5f\u9edb\u513f\u773c\u795e\u6709\u4e9b\u8ff7\u79bb\uff0c\u5f20\u606a\u8f7b\u8f7b\u7684\u4e00\u58f0\u201c\u9edb\u513f\u201d\u7adf\u8ba9\u5979\u5fc3\u60b8\u7684\u8df3\u52a8\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u201c\u53ef\u4ee5\u7684\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u9edb\u513f\u7684\u773c\u795e\u7f8e\u5f97\u8ba9\u4eba\u4e0d\u6562\u76f4\u89c6\uff0c\u201c\u4f60\u53ef\u4ee5\u7684\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u4e8b\u4eba\u4f3c\u7684\u4e0b\u4e86\u697c\uff0c\u6c5f\u9edb\u513f\u597d\u4e0d\u5bb9\u6613\u9f13\u8d77\u52c7\u6c14\uff0c\u51fa\u4e86\u623f\u95f4\uff0c\u8fd9\u80a1\u5b50\u52c7\u6c14\u4e5f\u5c31\u6cc4\u5c3d\u4e86\uff0c\u53c8\u6210\u4e86\u4e0d\u751a\u5a07\u7f9e\u7684\u5973\u5b69\u5b50\uff0c\u8bb8\u7ef4\u4e5f\u5f53\u4f5c\u6ca1\u53d1\u751f\u8fc7\u4ec0\u4e48\u4e8b\uff0c\u5f20\u606a\u6ca1\u6709\u7559\u656c\u98de\u9e3f\u4e0b\u6765\u5403\u665a\u996d\u7684\u610f\u601d\uff0c\u5f85\u4ed6\u4e00\u676f\u8336\u559d\u4e86\u4e00\u534a\uff0c\u5c31\u6d41\u9732\u51fa\u9001\u5ba2\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u656c\u98de\u9e3f\u4e00\u8d70\uff0c\u4ee4\u5c0f\u71d5\u5979\u4eec\u624d\u95ee\u5e7f\u544a\u7247\u62cd\u7684\u6548\u679c\u5982\u4f55\uff0c\u5f20\u606a\u8ba9\u5085\u4fca\u5c06\u5f55\u50cf\u673a\u6367\u5230\u697c\u4e0b\u7684\u5ba2\u5385\u6765\u64ad\u653e\uff0c\u6709\u5468\u53d4\u60e0\u5728\u573a\uff0c\u4ee4\u5c0f\u71d5\u8fd8\u6536\u655b\u4e9b\uff0c\u53ea\u95ee\u5f20\u606a\uff1a\u201c\u600e\u4e48\u6837\uff0c\u6211\u5c31\u8bf4\u9edb\u513f\u4e00\u5b9a\u884c\u7684\uff0c\u8fd9\u5e7f\u544a\u62ff\u5230\u4e2d\u592e\u7535\u89c6\u53f0\u653e\u90fd\u591f\u8d44\u683c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u672c\u6765\u5c31\u662f\u8981\u62ff\u5230\u4e2d\u592e\u7535\u89c6\u53f0\u64ad\u653e\u7684\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u4e2d\u592e\u7535\u89c6\u53f0\u64ad\u653e\u7684\u53ea\u662f\u6700\u540e5\u79d2\u949f\u5e7f\u544a\uff0c15\u79d2\u5e7f\u544a\u4f1a\u62ff\u5230\u5404\u5730\u7684\u5730\u65b9\u53f0\u64ad\u653e\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u9edb\u513f\u8fd9\u4e0b\u5b50\u4e0d\u662f\u8981\u6210\u540d\u4e86\uff1f\u201d\u4ee4\u5c0f\u71d5\u5174\u594b\u7684\u8bf4\uff0c\u201c\u4f60\u5bb6\u7684\u516c\u53f8\u786e\u5b9a\u8981\u8bf7\u9edb\u513f\u505a\u5f62\u8c61\u4ee3\u8a00\u4eba\u55bd\uff1f\u201d</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u63a5\u53d7\u5f20\u606a\u5b66\u7740\u66ff\u4ed6\u5bb6\u91cc\u6253\u7406\u516c\u53f8\u4e8b\u52a1\u7684\u8bf4\u6cd5\u3002</p><p>\u3000\u3000\u5f20\u606a\u626d\u5934\u770b\u4e86\u9edb\u513f\u4e00\u773c\uff0c\u5979\u773c\u775b\u91cc\u85cf\u7740\u7f9e\u6da9\u4e0e\u671f\u5f85\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u76ee\u524d\u56fd\u5185\u6bd4\u8f83\u597d\u7684\u5e7f\u544a\u6a21\u7279\u4e00\u5e74\u7684\u4ee3\u8a00\u8d39\u7528\u5927\u7ea6\u5728\u516b\u4e07\u5de6\u53f3\uff0c\u6211\u4e0d\u4f1a\u5f00\u51fa\u66f4\u9ad8\u7684\u4ef7\u683c\uff0c\u5de5\u4f5c\u8fd8\u662f\u86ee\u8f9b\u82e6\u7684\uff0c\u4f60\u53ef\u4ee5\u76f4\u63a5\u8ddf\u6211\u4eec\u516c\u53f8\u7b7e\u5e7f\u544a\u4ee3\u8a00\u5408\u7ea6\uff0c\u4e5f\u53ef\u4ee5\u627e\u7ecf\u7eaa\u4eba\u4ee3\u7406\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u516b\u4e07\u554a\uff0c\u201d\u6ca1\u89c1\u8fc7\u5927\u94b1\uff0c\u5f20\u606a\u62a5\u51fa\u7684\u6570\u5b57\u8ba9\u4ee4\u5c0f\u71d5\u5174\u594b\u53eb\u4e86\u4e00\u58f0\uff0c\u201c\u6731\u5c0f\u519b\u5728\u7ecf\u8d38\u59d4\u5de5\u4f5c\uff0c\u4e00\u4e2a\u6708\u57fa\u672c\u5de5\u8d44\u624d\u56db\u767e\uff0c\u4ed6\u4e0d\u662f\u8981\u5de5\u4f5c\u4e8c\u5341\u5e74\uff0c\u624d\u62b5\u5f97\u4e0a\u4f60\u4e00\u5e74\uff1f\u201d</p></article>"}}}]);
