(window.webpackJsonp=window.webpackJsonp||[]).push([[188],{211:function(p){p.exports={chapterTitle:null,title:"\u7b2c1164\u7ae0 \u8ba9\u4eba\u62c5\u5fe7\u7684\u9526\u6e56",content:"<article><h2>\u7b2c1164\u7ae0 \u8ba9\u4eba\u62c5\u5fe7\u7684\u9526\u6e56</h2><p>\u3000\u3000\u674e\u5728\u6d19\u79bb\u5f00\u97e9\u5174\u94f6\u884c\u6734\u771f\u660c\u4f1a\u957f\u4e0b\u69bb\u9152\u5e97\u65f6\u6709\u4e9b\u610f\u5174\u74d3\u73ca\u4e86\uff0c\u6709\u4e9b\u8bdd\u6ca1\u6709\u8a00\u660e\uff0c\u4f46\u662f\u6734\u771f\u660c\u6d41\u9732\u51fa\u7684\u6001\u5ea6\u5df2\u7ecf\u76f8\u5f53\u660e\u663e\u4e86\uff0c\u97e9\u5174\u94f6\u884c\u4e0d\u4f1a\u4e3a\u6240\u8c13\u7684\u97e9\u56fd\u7535\u5b50\u4ea7\u4e1a\u7684\u6574\u4f53\u5229\u76ca\u4e70\u5355\u7684\u3002\u9664\u975e\u4e09\u661f\u7535\u5b50\u6216\u8005\u5176\u4ed6\u54ea\u5bb6\u4f01\u4e1a\u534a\u9053\u6740\u51fa\uff0c\u62a2\u5728\u9526\u6e56\u524d\u9762\u5c06\u73b0\u4ee3\u534a\u5bfc\u4f53\u7684\u6db2\u6676\u4e1a\u52a1\u6536\u8d2d\u8fc7\u6765\u3002\u4f46\u662f\u73b0\u4ee3\u534a\u5bfc\u4f53\u65d7\u4e0b\u7684\u6db2\u6676\u4e1a\u52a1\u6838\u5fc3\u8d44\u4ea7\u90fd\u662f\u4e09\u661f\u7535\u5b50\u8ba1\u5212\u8fd1\u671f\u6536\u7f29\u4ea7\u80fd\u7684TFT\u8584\u819c\u6db2\u6676\u7b2c\u4e09\u4ee3\u751f\u4ea7\u7ebf\uff0c\u5bf9\u4e09\u661f\u7535\u5b50\u6765\u8bf4\uff0c\u6ca1\u6709\u591a\u5927\u7684\u4ef7\u503c\uff0c\u751a\u81f3\u8981\u8d1f\u4ed8\u4e0a\u4e00\u4e2a\u5305\u88b1\u3002\u4e09\u661f\u7535\u5b50\u4e0d\u53ef\u80fd\u4e3a\u6b82\u51fb\u9526\u6e56\u652f\u4ed8\u56db\u4ebf\u7f8e\u5143\u751a\u81f3\u66f4\u9ad8\u7684\u4ee3\u4ef7\uff0c\u66f4\u4f55\u51b5\u5c31\u7b97\u4e09\u661f\u7535\u5b50\u9009\u62e9\u8fd9\u4e48\u505a\uff0c\u4e5f\u4e0d\u80fd\u5c31\u5f7b\u5e95\u5835\u6b7b\u9526\u6e56\u8fdb\u519b\u6db2\u6676\u7535\u5b50\u4ea7\u4e1a\u7684\u8def\u3002</p><p>\u3000\u3000\u5168\u7403\u5171\u6709\u5341\u516b\u5bb6\u7535\u5b50\u4f01\u4e1a\u62e5\u6709TFT\u8584\u819c\u6db2\u6676\u6280\u672f\uff0c\u7531\u4e8eTFT\u8584\u819c\u6db2\u6676\u662f\u9ad8\u6295\u5165\uff0c\u6280\u672f\u3001\u8d44\u672c\u53cc\u5bc6\u96c6\u578b\u7684\u4ea7\u4e1a\uff0c\u53c8\u7531\u4e8eTFT\u8584\u819c\u6db2\u6676\u7684\u5e02\u573a\u8fd8\u6ca1\u6709\u771f\u6b63\u7684\u6253\u5f00\uff0c\u8fc4\u4eca\u9c9c\u6709\u54ea\u5bb6\u4f01\u4e1a\u83b7\u5f97\u7a33\u5b9a\u7684\u89c4\u6a21\u8d62\u5229\uff0c\u4e09\u661f\u5728\u8fd9\u65b9\u9762\u7684\u4e1a\u52a1\u4e5f\u8fde\u7eed\u4e8f\u635f\u4e86\u4e03\u5e74\uff0c\u53bb\u5e74\u624d\u7a0d\u6709\u4e9b\u8d77\u8272\u3002</p><p>\u3000\u3000\u53e6\u4e00\u65b9\u9762\uff0c\u5728\u5e73\u9762\u7535\u89c6\u5e02\u573a\uff0c\u7b49\u79bb\u5b50\u76f8\u6bd4\u8f83\u6db2\u6676\u8fd8\u62e5\u6709\u4e00\u5b9a\u7684\u6280\u672f\u4f18\u52bf\uff0c\u4e24\u79cd\u663e\u793a\u7535\u5b50\u6280\u672f\u5728\u672a\u6765\u9e7f\u6b7b\u8c01\u624b\u8fd9\u65f6\u5019\u4e0b\u5b9a\u8bba\u8fd8\u592a\u65e9\u4e86\u3002</p><p>\u3000\u3000\u5373\u4f7f\u80fd\u80af\u5b9aTFT\u8584\u819c\u6db2\u6676\u662f\u672a\u6765\u663e\u793a\u7535\u5b50\u5e02\u573a\u7684\u738b\u8005\uff0c\u4f46\u662f\u5728\u5982\u6b64\u5bc6\u96c6\u7684\u9ad8\u8d44\u672c\u6295\u5165\u9762\u524d\uff0c\u6280\u672f\u3001\u8d44\u672c\u7ade\u4e89\u53c8\u662f\u8fd9\u6837\u7684\u6fc0\u70c8\uff0c\u6700\u540e\u771f\u6b63\u80fd\u5b9e\u73b0\u89c4\u6a21\u8d62\u5229\u7684\u6db2\u6676\u5382\u5bb6\u4e5f\u53ea\u6709\u4e94\u516d\u5bb6\uff0c\u5341\u516b\u5bb6\u662f\u7684\u786e\u6709\u4e9b\u591a\u4e86\u3002</p><p>\u3000\u3000\u5728\u8fd9\u79cd\u60c5\u51b5\uff0c\u53ea\u8981\u9526\u6e56\u80fd\u62ff\u51fa\u8db3\u591f\u9ad8\u7684\u7b79\u7801\uff0c\u4e5f\u80af\u5b9a\u4f1a\u6709\u5176\u4ed6\u7535\u5b50\u4f01\u4e1a\u613f\u610f\u5c06\u6db2\u6676\u4e1a\u52a1\u62f1\u624b\u76f8\u8ba9\u7684\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u5750\u8fdb\u8f66\u91cc\uff0c\u5934\u75bc\u7684\u6324\u4e86\u6324\u7709\u95f4\uff0c\u5fc3\u91cc\u60f3\u9053\uff1a\u8fd9\u65f6\u5019\u8981\u505a\u7684\uff0c\u5c31\u662f\u5c3d\u53ef\u80fd\u7684\u62d6\u5ef6\u9526\u6e56\u8fdb\u519b\u8584\u819c\u6db2\u6676\u4ea7\u4e1a\u7684\u6b65\u4f10\uff0c\u5c3d\u53ef\u4ee5\u8ba9\u9526\u6e56\u4e3a\u8fdb\u519b\u8584\u819c\u6db2\u6676\u4ea7\u4e1a\u652f\u4ed8\u66f4\u591a\u7684\u4ee3\u4ef7\uff0c\u538b\u7f29\u9526\u6e56\u7684\u7ade\u4e89\u6f5c\u529b\u3002</p><p>\u3000\u3000\u867d\u8bf4\u4e09\u661f\u7535\u5b50\u5728\u8584\u819c\u6db2\u6676\u4e0a\u8fde\u7eed\u4e8f\u635f\u4e86\u4e03\u5e74\uff0c\u4f46\u662f\u501f\u52a9\u4e5d\u4e03\u5e74\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u4e2d\u97e9\u5143\u5927\u5e45\u8d2c\u503c\u6709\u5229\u51fa\u53e3\u7684\u4f18\u52bf\uff0c\u4e09\u661f\u7535\u5b50\u5728\u5168\u7403\u6db2\u6676\u5e02\u573a\u6392\u540d\u5df2\u7ecf\u4e0a\u5347\u5230\u7b2c\u4e00\u4f4d\uff0c\u53bb\u5e74\u4e5f\u987a\u5229\u5b9e\u73b0\u4e86\u8d62\u5229\u3002</p><p>\u3000\u3000\u5728\u789f\u673a\u3001\u6570\u5b57\u624b\u673a\u4ee5\u53ca\u4fbf\u643a\u5f0f\u97f3\u4e50\u64ad\u653e\u5668\u5e02\u573a\uff0c\u4e09\u661f\u7535\u5b50\u5df2\u7ecf\u5145\u5206\u9886\u7565\u5230\u9526\u6e56\u90a3\u79cd\u4e00\u65e6\u66b4\u53d1\u51fa\u6765\u5c31\u6c14\u52bf\u78c5\u7934\u7684\u7ade\u4e89\u6f5c\u529b\uff0c\u5728\u674e\u5728\u6d19\u770b\u6765\uff0c\u9526\u6e56\u6bcf\u8de8\u8fdb\u4e00\u4e2a\u65b0\u7684\u9886\u57df\uff0c\u7279\u522b\u662f\u8ddf\u4e09\u661f\u5b58\u5728\u7ade\u4e89\u7684\u9886\u57df\uff0c\u5c31\u8981\u5f15\u8d77\u5343\u4e07\u5206\u7684\u5c0f\u5fc3\u3002</p><p>\u3000\u3000\u91d1\u5357\u52c7\u5750\u5728\u526f\u9a7e\u9a76\u4f4d\u4e0a\uff0c\u4ed6\u7684\u624b\u673a\u8fd9\u65f6\u5019\u54cd\u4e86\u8d77\u6765\uff0c\u4ed6\u62ff\u8d77\u6765\u770b\u4e86\u770b\uff0c\u56de\u8fc7\u5934\u6765\u8ddf\u674e\u5728\u6d19\u8bf4\u4e86\u4e00\u58f0\u201c\u5b8f\u4fe1\u4e25\u6587\u4ecb\u6253\u6765\u7684\u7535\u8bdd\u2026\u2026\u201d\u5c31\u5750\u5728\u526f\u9a7e\u9a76\u4f4d\u4e0a\u63a5\u8d77\u7535\u8bdd\u6765\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\u6342\u7740\u8bdd\u7b52\u53c8\u8f6c\u56de\u5934\u8ddf\u674e\u5728\u6d19\u8bf4\uff1a\u201c\u4e25\u6587\u4ecb\u4e5f\u5728\u5317\u4eac\uff0c\u95ee\u662f\u5426\u65b9\u4fbf\u89c1\u9762\u3002\u201d</p><p>\u3000\u3000\u674e\u5728\u6d19\u5fae\u5fae\u6447\u4e86\u6447\u5934\uff0c\u91d1\u5357\u52c7\u4fbf\u5bf9\u7740\u624b\u673a\u8bf4\u9053\uff1a\u201c\u771f\u5bf9\u4e0d\u8d77\uff0c\u674e\u5728\u6d19\u541b\u5728\u5317\u4eac\u7684\u65e5\u7a0b\u90fd\u5b89\u6392\u6ee1\u4e86\uff0c\u53ea\u662f\u56de\u5efa\u90ba\u540e\u518d\u627e\u673a\u4f1a\u89c1\u9762\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u7740\u91d1\u5357\u52c7\u6302\u4e86\u7535\u8bdd\uff0c\u674e\u5728\u6d19\u624d\u8ddf\u4ed6\u89e3\u91ca\u4e0d\u4e0e\u4e25\u6587\u4ecb\u7b49\u4eba\u89c1\u9762\u7684\u539f\u56e0\uff1a\u201c\u5b8f\u4fe1\u5373\u4f7f\u80fd\u5728\u6d77\u7c9f\u79d1\u6280\u4e0a\u5f97\u624b\uff0c\u4e5f\u5b8c\u5168\u6ca1\u6709\u8ddf\u9526\u6e56\u8f83\u91cf\u7684\u8d44\u683c\u2014\u2014\u4e4b\u524d\u867d\u7136\u5bf9\u5b8f\u4fe1\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5962\u671b\u7684\u671f\u5f85\uff0c\u4f46\u662f\u8fd8\u4ee5\u4e3a\u5b8f\u4fe1\u81f3\u5c11\u80fd\u5728\u4e2d\u56fd\u7a0d\u7a0d\u7275\u5236\u4e00\u4e0b\u9526\u6e56\uff0c\u8c01\u80fd\u60f3\u5230\u4ed6\u4eec\u4f1a\u8fd9\u4e48\u5f31\u667a\uff1f\u65e2\u7136\u5df2\u7ecf\u62e5\u6709\u8fd9\u4e48\u5927\u89c4\u6a21\u7684\u5b9e\u4e1a\uff0c\u5728\u4ea7\u4e1a\u6295\u8d44\u4e0e\u8bc1\u5238\u6295\u673a\u4e4b\u95f4\u5b70\u8f7b\u5b70\u91cd\u4ecd\u7136\u627e\u4e0d\u5230\u5206\u5bf8\uff0c\u8fd9\u6837\u7684\u5408\u4f5c\u5bf9\u8c61\u771f\u8ba9\u4eba\u5931\u671b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5373\u4f7f\u5982\u6b64\uff0c\u4e09\u661f\u5b8f\u82af\u7684\u9879\u76ee\u8fd8\u8981\u8fdb\u884c\u4e0b\u53bb\uff0c\u5b8f\u4fe1\u90a3\u8fb9\u4e5f\u9700\u8981\u6577\u884d\u3002\u201d\u91d1\u5357\u52c7\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u90a3\u53ea\u5269\u4e0b\u6577\u884d\u4e86\uff0c\u201d\u674e\u5728\u6d19\u51b7\u6f20\u7684\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u4e09\u661f\u5b8f\u82af\u9879\u76ee\u7684\u63a7\u5236\u6743\uff0c\u6211\u4eec\u8981\u7d27\u7d27\u7684\u6293\u5728\u624b\u91cc\uff0c\u5207\u4e0d\u8981\u7ed9\u611a\u8822\u7684\u5408\u4f5c\u5bf9\u8c61\u7ed9\u62d6\u4e0b\u6c34\u2026\u2026\u201d</p><p>\u3000\u3000\u91d1\u5357\u52c7\u70b9\u70b9\u5934\uff0c\u5750\u6b63\u8eab\u5b50\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u8eab\u5b50\u9760\u5728\u67d4\u8f6f\u7684\u6905\u80cc\u4e0a\uff0c\u5fc3\u91cc\u9ed8\u9ed8\u56de\u60f3\u7740\u9526\u6e56\u8fd9\u4e9b\u5e74\u7684\u5d1b\u8d77\u6b65\u4f10\uff0c\u6709\u79cd\u58ee\u58eb\u627c\u8155\u7684\u60cb\u60dc\uff0c\u4e09\u661f\u672c\u6709\u673a\u4f1a\u5c06\u9526\u6e56\u6390\u6b7b\u5728\u6447\u7bee\u91cc\u7684\u3002</p><p>\u3000\u3000\u4e09\u661f\u4f5c\u4e3a\u5168\u7403\u6700\u65e9\u7684\u789f\u673a\u5382\u5546\u4e4b\u4e00\uff0c\u65e9\u5728\u4e5d\u56db\u5e74\u5e95\u5c31\u5728\u4e2d\u56fd\u63a8\u51faVCD\u789f\u673a\uff0c\u5e76\u8fc5\u901f\u53d6\u4ee3\u4e07\u71d5\uff0c\u51e0\u4e4e\u5784\u65ad\u4e86\u5f53\u65f6\u789f\u673a\u5e02\u573a\u3002\u4e5f\u662f\u5f53\u65f6\u7684\u4e09\u661f\u4e2d\u56fd\u516c\u53f8\u8fc7\u4e8e\u6025\u5207\uff0c\u4e5f\u8bb8\u662f\u8fc7\u4e8e\u8f7b\u89c6\u4f5c\u4e3a\u8fc7\u6e21\u4ea7\u54c1VCD\u789f\u673a\u7684\u5e02\u573a\u6f5c\u529b\uff0c\u5f53\u65f6\u6ca1\u6709\u6295\u5165\u592a\u591a\u7684\u6280\u672f\u529b\u91cf\uff0c\u4ee5\u81f4\u4e09\u661f\u7684\u789f\u673a\u4ea7\u54c1\u5728\u4e5d\u4e94\u5e74\u65f6\u51fa\u73b0\u9891\u9891\u51fa\u73b0\u8d28\u91cf\u95ee\u9898\uff0c\u4e3a\u4e86\u7ef4\u6301\u4e09\u661f\u7684\u54c1\u724c\u5546\u8a89\uff0c\u88ab\u8feb\u9000\u51fa\u789f\u673a\u5e02\u573a\u3002\u9526\u6e56\u5374\u53ca\u65f6\u91c7\u7528\u5f53\u65f6\u6700\u65b0\u7684VCD\u89e3\u7801\u6280\u672f\u586b\u8865\u4e86\u4e09\u661f\u9000\u51fa\u540e\u7559\u4e0b\u7684\u7a7a\u767d\uff0c\u6210\u4e3aVCD\u789f\u673a\u5e02\u573a\u7684\u9738\u4e3b\u3002</p><p>\u3000\u3000VCD\u789f\u673a\u672c\u6765\u5c31\u662fDVD\u789f\u673a\u63a8\u51fa\u4e4b\u524d\u7684\u8fc7\u6e21\u6280\u672f\uff0c\u800c\u4e14\u8fdb\u5165\u7684\u6280\u672f\u95e8\u69db\u53c8\u5f88\u4f4e\uff0c\u5b58\u5728\u8fc7\u5ea6\u7ade\u4e89\u7684\u95ee\u9898\uff0c\u9526\u6e56\u5373\u4f7f\u6210\u4e3aVCD\u789f\u673a\u5e02\u573a\u7684\u9738\u4e3b\uff0c\u5728\u5168\u7403\u7535\u5b50\u4ea7\u4e1a\u4e5f\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u5f71\u54cd\u529b\u3002</p><p>\u3000\u3000\u8c01\u80fd\u60f3\u5230\u9526\u6e56\u4f1a\u8054\u5408\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u5347\u7ea7VCD\u789f\u673a\u6280\u672f\u53d1\u5c55\u51fa\u65b0\u4e00\u4ee3\u7684SVCD\u789f\u673a\u6280\u672f\uff1f\u867d\u7136SVCD\u789f\u673a\u5728\u6280\u672f\u4e0a\u72b9\u900aDVD\u4e00\u7b79\uff0c\u4f46\u662f\u9526\u6e56\u4e5d\u4e03\u5e74\u5c31\u5bf9SVCD\u8fdb\u884c\u591a\u65b9\u9762\u7684\u4ea7\u4e1a\u5e03\u5c40\uff0c\u5229\u7528VCD\u789f\u673a\u7684\u751f\u4ea7\u3001\u8425\u9500\u751a\u81f3\u76d7\u7248\u5149\u789f\u4ea7\u4e1a\u94fe\u7b49\u591a\u79cd\u4f18\u52bf\u6e20\u9053\uff0c\u5728DVD\u789f\u673a\u59d7\u59d7\u6765\u8fdf\u4e4b\u9645\uff0c\u4ee5\u8fc5\u96f7\u4e0d\u53ca\u63a9\u8033\u4e4b\u52bf\u8fdb\u884c\u5168\u4ea7\u4e1a\u7684\u5347\u7ea7\u3002\u81f3\u5c11\u5728\u66f4\u5148\u8fdb\u7684\u6280\u672f\u666e\u53ca\u4e4b\u524d\uff0c\u674e\u5728\u6d19\u770b\u4e0d\u5230\u789f\u673a\u5e02\u573aSVCD\u6709\u88ab\u53d6\u4ee3\u7684\u53ef\u80fd\u3002\u9664\u4e86\u65b0\u5174\u56fd\u5bb6\u4e0e\u5730\u533aSVCD\u789f\u673a\u3002</p><p>\u3000\u3000\u73b0\u5728\u6bcf\u5e74SVCD\u5168\u7403\u9500\u91cf\u8fbe\u5230\u4e24\u5343\u516b\u767e\u4e07\u53f0\uff0c\u9500\u552e\u989d\u8fbe\u5230\u56db\u767e\u4ebf\uff0c\u9526\u6e56\u65d7\u4e0b\u7684\u4e0a\u5e02\u516c\u53f8\u7231\u8fbe\u7535\u5b50\u76f4\u63a5\u63a7\u523626\uff05\u7684\u789f\u673a\u5e02\u573a\u4efd\u989d\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u674e\u5728\u6d19\u77e5\u9053\u9526\u6e56\u8fd8\u4ee5\u6d77\u5916\u516c\u53f8\u7684\u540d\u4e49\u8054\u5408\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u8fd8\u63a7\u5236\u774080\uff05\u7684SVCD\u89e3\u7801\u82af\u7247\u7684\u4f9b\u5e94\uff0c\u7231\u8fbe\u7535\u5b50\u4e0e\u5fb7\u4eea\u7684\u5408\u8d44\u5de5\u5382\u8fd8\u63a7\u5236SVCD60\uff05\u7684\u673a\u82af\u63a7\u5e94\u3002\u9664\u4e86SVCD\u4e4b\u5916\uff0c\u9526\u6e56\u8fd8\u67d3\u6307DVD\u789f\u673a\u5e02\u573a\uff0c\u6210\u4e3a\u5168\u7403\u516b\u5927DVD\u5382\u5bb6\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u9526\u6e56\u5728\u789f\u673a\u4ea7\u4e1a\u4e0a\u7684\u5e03\u5c40\uff0c\u6709\u4e9b\u662f\u76f4\u63a5\u4ee5\u7231\u8fbe\u7535\u5b50\u7684\u540d\u4e49\uff0c\u6709\u4e9b\u662f\u4ee5\u5176\u4ed6\u5408\u8d44\u4e0e\u72ec\u8d44\u516c\u53f8\u7684\u540d\u4e49\u8fdb\u884c\uff0c\u4e0d\u8fc7\u5728\u516c\u4f17\u773c\u91cc\uff0c\u7231\u8fbe\u7535\u5b50\u4f9d\u65e7\u96c6\u4e2d\u4e86\u9526\u6e56\u5728\u789f\u673a\u4ea7\u4e1a\u4e0a\u7684\u4e3b\u8981\u8d44\u6e90\u3002</p><p>\u3000\u3000\u867d\u7136\u789f\u673a\u5e02\u573a\u7684\u7ade\u4e89\u9887\u4e3a\u6fc0\u70c8\uff0c\u4f46\u662f\u7231\u8fbe\u7535\u5b50\u53bb\u5e74\u7684\u51c0\u5229\u6da6\u4ecd\u7136\u8fbe\u5230\u5341\u4e8c\u4ebf\u6e2f\u5143\u4e4b\u591a\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u4e5d\u4e03\u5e74\u4e94\u6708\u501f\u58f3\u4e0a\u5e02\u65f6\uff0c\u5e02\u503c\u4e00\u5ea6\u7a81\u7834\u767e\u4ebf\u6e2f\u5143\uff0c\u53d7\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u5f71\u54cd\uff0c\u5e02\u503c\u66fe\u8dcc\u7834\u4e94\u5341\u4ebf\u6e2f\u5143\uff0c\u4f46\u662f\u8fd9\u4e24\u5e74\u6765\u53d7\u65b0\u7ecf\u6d4e\u6d6a\u6f6e\u7684\u523a\u6fc0\u6027\u5f71\u54cd\uff0c\u4f5c\u4e3a\u65b0\u79d1\u6280\u8303\u7574\u5185\u7684\u7231\u8fbe\u7535\u5b50\u5e02\u503c\u4e00\u5ea6\u7a81\u7834\u4e09\u767e\u516b\u5341\u4ebf\u6e2f\u5143\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5373\u4f7f\u7231\u8fbe\u7535\u5b50\u7684\u5e02\u503c\u7a81\u7834\u4e09\u767e\u516b\u5341\u4ebf\u6e2f\u5143\uff0c\u5e02\u76c8\u7387\u4e5f\u8fd8\u4e0d\u8db3\u4e09\u5341\u4e8c\u500d\uff0c\u8981\u8fdc\u8fdc\u4f4e\u4e8e\u5f53\u65f6\u8054\u5408\u8bc1\u5238\u5e02\u573a\u65b0\u79d1\u6280\u677f\u5757\u5e73\u5747\u56db\u5341\u516d\u500d\u7684\u5e02\u76c8\u7387\u3002\u8fd9\u4e3b\u8981\u662f\u7531\u4e8e\u9526\u6e56\u8c28\u5b88\u63a7\u80a1\u5927\u80a1\u4e1c\u7684\u804c\u8d23\uff0c\u79ef\u6781\u6291\u5236\u4e86\u80a1\u4ef7\u7684\u8fc7\u5ea6\u4e0a\u626c\uff0c\u4e5f\u56e0\u6b64\u88ab\u6295\u673a\u5ba2\u6240\u4e0d\u559c\u3002\u867d\u7136\u8fd9\u6b21\u4e5f\u5c06\u6ce8\u5b9a\u4f1a\u53d7\u5230\u4e92\u8054\u7f51\u6ce1\u6cab\u5371\u673a\u7684\u5f71\u54cd\uff0c\u4e0d\u8fc7\u4ee5\u7231\u8fbe\u7535\u5b50\u7a33\u5065\u7684\u8d62\u5229\u80fd\u529b\u3001\u5728\u4ea7\u4e1a\u5e03\u5c40\u4e0a\u7684\u5148\u673a\u53ca\u6210\u672c\u4f18\u52bf\u4ee5\u53ca\u672c\u8eab\u5c31\u4e0d\u9ad8\u5f97\u8fc7\u5206\u7684\u5e02\u76c8\u7387\uff0c\u76f8\u4fe1\u7231\u8fbe\u7535\u5b50\u7684\u5e02\u503c\u4e5f\u4e0d\u4f1a\u8dcc\u5f97\u592a\u6df1\u3002</p><p>\u3000\u3000\u5728\u8fd9\u65b9\u9762\uff0c\u9526\u6e56\u5c31\u8868\u73b0\u51fa\u8ddf\u5b8f\u4fe1\u8fe5\u7136\u4e0d\u540c\u7684\u6c14\u5ea6\u4e0e\u64cd\u5b88\u6765\uff0c\u9526\u6e56\u603b\u662f\u5c3d\u53ef\u80fd\u62b5\u5236\u65d7\u4e0b\u4e0a\u5e02\u516c\u53f8\u80a1\u4ef7\u5927\u8d77\u5927\u843d\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u60f3\u5230\u8fc7\u8981\u64cd\u7eb5\u65d7\u4e0b\u4e0a\u5e02\u516c\u53f8\u80a1\u4ef7\u83b7\u5229\u3002\u5f53\u7136\uff0c\u674e\u5728\u6d19\u76f8\u4fe1\u8fd9\u8ddf\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u5e02\u573a\u7684\u76d1\u7ba1\u4e25\u82db\u6709\u5f88\u5927\u7684\u5173\u7cfb\uff0c\u4e0d\u8fc7\u9526\u6e56\u4e5f\u4e0d\u5c51\u64cd\u7eb5\u80a1\u4ef7\u6e14\u5229\u8fd9\u79cd\u4f4e\u5c42\u6b21\u7684\u655b\u8d22\u624b\u6bb5\uff0c\u6700\u76f4\u63a5\u7684\u4f8b\u5b50\u83ab\u8fc7\u4e8e\u9526\u6e56\u4ece\u6765\u90fd\u6ca1\u6709\u8fdb\u884c\u8ba9\u65d7\u4e0b\u4f01\u4e1a\u5728\u4e2d\u56fd\u8bc1\u5238\u5e02\u573a\u516c\u5f00\u4e0a\u5e02\u7684\u52aa\u529b\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u5df2\u7ecf\u6df1\u523b\u8ba4\u8bc6\u5230\u9526\u6e56\u662f\u4e00\u5bb6\u8ddf\u4e2d\u56fd\u5176\u4ed6\u4f01\u4e1a\u6c14\u8d28\u8fe5\u5f02\u7684\u96c6\u56e2\uff0c\u8d85\u8fc7\u4e4b\u524d\u4eba\u4eec\u5bf9\u4e2d\u56fd\u4f01\u4e1a\u4e00\u822c\u6027\u7684\u8ba4\u77e5\u3002\u5c24\u662f\u5982\u6b64\uff0c\u5c24\u8981\u91cd\u89c6\u8d77\u6765\uff0c\u66f4\u4f55\u51b5\u8fd9\u5bb6\u4f01\u4e1a\u8981\u6bd4\u5e38\u4eba\u60f3\u8c61\u4e2d\u8fd8\u8981\u5f3a\u5927\u5f97\u591a\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e5d\u516b\u5e74\u63a8\u51faDMP-iplayer\u4fbf\u643a\u5f0f\u97f3\u4e50\u64ad\u653e\u5668\u6280\u672f\u76f4\u63a5\u5bf9\u65e5\u672c\u7535\u5b50\u4f01\u4e1a\u7684MD\u64ad\u653e\u5668\u9635\u8425\u53d1\u8d77\u4e86\u51b2\u950b\u3002\u901a\u8fc7\u5e7f\u6cdb\u7684\u6280\u672f\u6388\u6743\uff0c\u624d\u4e24\u5e74\u7684\u65f6\u95f4\u8fc7\u53bb\uff0cDMP-iplayer\u5c31\u5934\u89d2\u5ce5\u5d58\u7684\u98ce\u5149\u5927\u8d77\uff0c\u5728\u5317\u7f8e\u3001\u6b27\u6d32\u751a\u81f3\u5728\u65e5\u672c\u672c\u571f\u5e02\u573a\uff0cDMP-iplayer\u7684\u9500\u91cf\u90fd\u9646\u7eed\u8d85\u8fc7MD\u3002\u968f\u7740\u95ea\u5b58\u6280\u672f\u7684\u5347\u7ea7\uff0c\u9ad8\u7aefDMP-iplayer\u7684\u50a8\u5b58\u5bb9\u91cf\u5df2\u7ecf\u8fbe\u52301G\uff0c\u50a8\u5b58\u5bb9\u6613\u5c0f\u7684\u4f4e\u7aefDMP-iplayer\u751a\u81f3\u5bf9\u5361\u5e26\u5f0f\u4fbf\u643a\u97f3\u4e50\u64ad\u653e\u5668\u90fd\u9020\u6210\u5f3a\u70c8\u7684\u51b2\u51fb\u3002\u9526\u6e56\u4eca\u5e74\u6625\u5b63\u5c31\u5728\u4e2d\u56fd\u5e02\u573a\u63a8\u51fa\u552e\u4ef7\u4e00\u767e\u4e8c\u5341\u7f8e\u5143\u5de6\u53f3\u7684\u4f4e\u7aefiplayer\u4ea7\u54c1\uff0c\u5f00\u59cb\u4ece\u7d22\u5c3cwalkman\u90a3\u91cc\u4e89\u593a\u5728\u4e2d\u56fd\u7684\u4fbf\u643a\u5f0f\u97f3\u4e50\u64ad\u653e\u5668\u7684\u9ad8\u7aef\u5e02\u573a\u3002</p><p>\u3000\u3000\u5728DMP-iplayer\u4e0eMD\u6280\u672f\u4e4b\u95f4\uff0c\u4e09\u661f\u7535\u5b50\u4e5f\u5f88\u65e9\u5c31\u9009\u62e9\u5012\u5411DMP-iplayer\u9635\u8425\uff0c\u5168\u7403\u9500\u91cf\u4ec5\u5728\u9526\u6e56\u3001\u4e1c\u829d\u3001\u65af\u9ad8\u67cf\u4e4b\u540e\uff0c\u6392\u540d\u7b2c\u56db\uff0c\u4f46\u662f\u6240\u5360\u5e02\u573a\u4efd\u989d\u90fd\u4e0d\u5230\u9526\u6e56\u7684\u4e09\u5206\u4e4b\u4e00\u3002\u9526\u6e56iplayer\u5728\u5168\u7403\u4fbf\u643a\u5f0f\u97f3\u4e50\u5e02\u573a\u4e2d\u6240\u5360\u7684\u4efd\u989d\u751a\u81f3\u8981\u8d85\u8fc7\u4e1c\u829d\uff0c\u5c31\u7b97DMP-iplayer\u6280\u672f\u6e90\u51fa\u9526\u6e56\uff0c\u8fd9\u4e5f\u662f\u5f53\u521d\u8c01\u90fd\u6ca1\u6709\u9884\u6599\u5230\u7684\u7ed3\u679c\u3002</p><p>\u3000\u3000\u7ec6\u7a76\u8d77\u6765\uff0c\u4ee5\u6253\u9020\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u4e3a\u76ee\u6807\u7684\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u4ee5\u53ca\u4e5d\u4e5d\u5e74\u4e0e\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u7684\u90a3\u573a\u6301\u7eed\u534a\u5e74\u7684\u4fb5\u6743\u5b98\u53f8\u4ee4\u9526\u6e56\u5728\u4fbf\u643a\u5f0f\u97f3\u4e50\u64ad\u653e\u5668\u5e02\u573a\u4e00\u98de\u51b2\u5929\u3002</p><p>\u3000\u3000\u5e74\u521d\u65f6\u8fd8\u662f\u9884\u6d4b\uff0c\u6b64\u65f6\u8d8b\u52bf\u5df2\u7ecf\u76f8\u5f53\u660e\u663e\uff0c\u9526\u6e56\u4eca\u5e74iplayer\u7d2f\u8ba1\u603b\u9500\u91cf\u5f88\u6709\u53ef\u80fd\u4f1a\u7a81\u7834\u4e94\u767e\u4e07\u4ef6\uff0c\u9884\u8ba1\u9526\u6e56\u5728iplayer\u4ea7\u54c1\u4e0a\u7684\u5168\u7403\u51c0\u5229\u6da6\u5c06\u7a81\u7834\u56db\u4ebf\u7f8e\u5143\uff0c\u660e\u5e74\u66f4\u6709\u53ef\u80fd\u7a81\u7834\u516d\u4ebf\u7f8e\u5143\u3002</p><p>\u3000\u3000\u7531\u4e8e\u9526\u6e56\u65d7\u4e0b\u4e24\u5927\u6838\u5fc3\u6210\u5458\u4f01\u4e1a\u7231\u8fbe\u96c6\u56e2\u8ddf\u9526\u6e56\u5546\u4e8b\u90fd\u662f\u79c1\u4eba\u516c\u53f8\uff0c\u4e0d\u9700\u8981\u5bf9\u5916\u516c\u5e03\u8d22\u52a1\u6570\u636e\uff0c\u53e6\u5916\uff0c\u9526\u6e56\u53c8\u5229\u7528\u5176\u4f53\u7cfb\u5185\u4f17\u591a\u679d\u7e41\u53f6\u8302\u3001\u9519\u7efc\u590d\u6742\u7684\u516c\u53f8\u6765\u627f\u62c5\u4e0d\u540c\u73af\u8282\u4e4b\u95f4\u7684\u4e1a\u52a1\uff0c\u8981\u4e0d\u662f\u4e09\u661f\u7684\u5185\u90e8\u751f\u4ea7\u7ec4\u7ec7\u6a21\u5f0f\u8ddf\u9526\u6e56\u8bb8\u591a\u76f8\u4f3c\u4e4b\u5916\u53c8\u540c\u65f6\u91cd\u89c6iplayer\u7684\u5e02\u573a\uff0c\u4e0d\u7136\u4e5f\u4f30\u7b97\u4e0d\u51fa\u9526\u6e56\u5728iplayer\u4ea7\u54c1\u4e0a\u7684\u5229\u6da6\u89c4\u6a21\u3002</p><p>\u3000\u3000\u9526\u6e56\u8fd8\u901a\u8fc7\u6280\u672f\u4ea4\u6362\u7684\u5f62\u5f0f\u4ece\u4e1c\u829d\u83b7\u5f97\u975e\u6613\u5931\u578b\u95ea\u5b58\u4e13\u5229\u7684\u5b8c\u6574\u6388\u6743\uff0c\u4ece\u800c\u5728\u975e\u6613\u5931\u578b\u95ea\u5b58\u5e02\u573a\u4e0e\u4e09\u661f\u3001\u4e1c\u829d\u4e09\u5206\u5929\u4e0b\u3002\u5728iplayer\u95ee\u4e16\u4e4b\u524d\uff0c\u95ea\u5b58\u7684\u5e94\u7528\u8303\u7574\u867d\u7136\u8f83\u5e7f\uff0c\u9700\u6c42\u91cf\u5374\u6781\u4e3a\u6709\u9650\uff0ciplayer\u4ea7\u54c1\u7684\u7545\u9500\u76f4\u63a5\u4fc3\u8fdb\u4e86\u975e\u6613\u5931\u578b\u95ea\u5b58\u5e02\u573a\u7684\u53d1\u5c55\uff0c\u9884\u8ba1\u4eca\u5e74\u7684\u5e02\u573a\u603b\u9700\u6c42\u5c06\u63a5\u8fd1\u4e09\u5341\u4ebf\u7f8e\u5143\u3002</p><p>\u3000\u3000\u9526\u6e56\u3001\u4e09\u661f\u3001\u4e1c\u829d\u8fd8\u6709\u5c06\u5e73\u5206\u8fd9\u4e09\u5341\u4ebf\u7f8e\u5143\u5e02\u573a\u768490\uff05\u4efd\u989d\uff0c\u674e\u5728\u6d19\u5f53\u7136\u80fd\u6e05\u695a\u7684\u610f\u8bc6\u5230\u9526\u6e56\u5728\u975e\u6613\u5931\u578b\u95ea\u5b58\u4e0a\u7684\u5229\u6da6\u7387\u662f\u4f55\u7b49\u7684\u60ca\u4eba\uff0c\u81f3\u5c11\u4e0d\u4f1a\u6bd4\u4e09\u661f40\uff05\u7684\u5229\u6da6\u7387\u66f4\u4f4e\u3002</p><p>\u3000\u3000\u4e5f\u8bb8\u9526\u6e56\u5728iplayer\u4e0a\u6240\u83b7\u5f97\u7684\u5168\u90e8\u5229\u6da6\u5e76\u4e0d\u89c1\u5f97\u6bd4\u624b\u673a\u4e1a\u52a1\u4f4e\uff0c\u4f46\u662f\u5f15\u8d77\u516c\u4f17\u5173\u6ce8\u7684\u8fd8\u662f\u9526\u6e56\u5728\u56fd\u5185\u7684\u624b\u673a\u4e1a\u52a1\u3002\u7231\u8fbe\u624b\u673a\u53bb\u5e74\u4ee517\uff05\u7684\u5e02\u573a\u4efd\u989d\u5728\u4e2d\u56fd\u5e02\u573a\u4ec5\u6b21\u4e8e\u6469\u6258\u7f57\u62c9\u8ddf\u8bfa\u57fa\u4e9a\uff0c\u4e09\u661f\u4ec5\u4ee5\u7f9e\u4ee5\u89c1\u4eba\u76846\uff05\u5e02\u573a\u4efd\u989d\u5728\u4e2d\u56fd\u5e02\u573a\u6392\u540d\u7b2c\u516d\u3002\u5728\u8fc7\u53bb\u516d\u4e2a\u6708\uff0c\u4e5f\u5c31\u662f\u4e24\u5343\u5e74\u7684\u5934\u534a\u5e74\u65f6\u95f4\u91cc\uff0c\u9526\u6e56\u5c31\u5728\u4e2d\u56fd\u5e02\u573a\u9500\u552e\u4e86\u56db\u767e\u4e07\u90e8\u624b\u673a\uff0c\u5c06\u4e2d\u56fd\u5e02\u573a\u4efd\u989d\u63d0\u9ad8\u4e863\u4e2a\u70b9\uff0c\u800c\u4e09\u661f\u52aa\u529b\u4e86\u534a\u5e74\uff0c\u624d\u5c06\u5360\u636e\u5e02\u573a\u4efd\u989d\u63d0\u9ad8\u4e861\u70b9\uff0c\u770b\u6765\u4e0d\u7ba1\u4ec0\u4e48\u9886\u57df\uff0c\u6ca1\u6709\u5438\u5f15\u4eba\u6c14\u7684\u6838\u5fc3\u4ea7\u54c1\uff0c\u4e1a\u7ee9\u603b\u662f\u96be\u4ee5\u6709\u4ec0\u4e48\u8d77\u8272\u3002\u9664\u4e86\u4e2d\u56fd\u56fd\u5185\u5e02\u573a\u5916\uff0c\u9526\u6e56\u5728\u4e1c\u5357\u4e9a\u624b\u673a\u5e02\u573a\u6392\u540d\u7b2c\u4e94\u540d\uff0c\u4e09\u661f\u6392\u7b2c\u56db\u540d\uff1b\u5916\u90e8\u624b\u673a\u4f01\u4e1a\u5f88\u96be\u8fdb\u5165\u65e5\u672c\u5e02\u573a\u53d1\u5c55\uff0c\u9526\u6e56\u8ddf\u4e09\u661f\u90fd\u4e0d\u4f8b\u5916\uff1b\u4e0d\u8fc7\u5bf9\u6b27\u7f8e\u53d1\u8fbe\u56fd\u5bb6\u5e02\u573a\u7684\u5f00\u62d3\uff0c\u9526\u6e56\u4e0e\u4e09\u661f\u90fd\u6709\u76f8\u5f53\u4e0d\u9519\u7684\u6210\u7ee9\uff0c\u7279\u522b\u662f\u9526\u6e56\u53bb\u5e74\u8d77\u5728\u5317\u7f8e\u5730\u533a\u7684\u624b\u673a\u9500\u552e\u5c31\u9010\u6e10\u6709\u4e86\u8d77\u8272\u3002</p><p>\u3000\u3000\u7531\u4e8e\u9526\u6e56\u65d7\u4e0b\u7684\u7535\u5b50\u4ea7\u4e1a\u90fd\u662f\u9ad8\u5229\u6da6\u7387\u7684\u4ea7\u4e1a\u8303\u7574\uff0c\u518d\u52a0\u4e0a\u9526\u6e56\u5c06\u65d7\u4e0b\u7684\u7535\u5b50\u4ea7\u4e1a\u5206\u6563\u5230\u51e0\u4e2a\u6210\u5458\u4f01\u4e1a\u4e4b\u95f4\uff0c\u9526\u6e56\u5728\u7535\u5b50\u4ea7\u4e1a\u4e0a\u7684\u5e74\u5229\u6da6\u989d\u76f8\u5bf9\u60ca\u4eba\uff0c\u4f46\u662f\u9500\u552e\u989d\u8ddd\u5168\u7403\u4e94\u767e\u5f3a\u7684\u6807\u51c6\u8fd8\u6709\u4e9b\u8ddd\u79bb\u3002\u5f53\u7136\u4e86\uff0c\u8981\u662f\u5c06\u9526\u6e56\u65d7\u4e0b\u6240\u6709\u7684\u4e1a\u52a1\u90fd\u8ba1\u7b97\u8fdb\u53bb\uff0c\u90a3\u65e9\u5c31\u5e94\u8be5\u6324\u5165\u5168\u7403\u4e94\u767e\u5f3a\u7684\u6392\u540d\u4e4b\u5217\u3002</p><p>\u3000\u3000\u8fd9\u6837\u7684\u9526\u6e56\u773c\u775b\u53c8\u7784\u51c6\u6db2\u6676\u4ea7\u4e1a\uff0c\u5982\u4f55\u8ba9\u4eba\u4e0d\u62c5\u5fe7\uff1f</p></article>"}}}]);
