(window.webpackJsonp=window.webpackJsonp||[]).push([[19],{42:function(p){p.exports={chapterTitle:null,title:"\u7b2c19\u7ae0 \u8bf7\u5c06\u5fc3\u66f2\u5411\u6211\u8bc9",content:"<article><h2>\u7b2c19\u7ae0 \u8bf7\u5c06\u5fc3\u66f2\u5411\u6211\u8bc9</h2><p>\u3000\u3000\u51c6\u5907\u4e0b\u5348\u5c31\u5f00\u59cb\u5bf9\u59dc\u660e\u8bda\u3001\u8bb8\u601d\u8fdb\u884c\u9884\u5ba1\uff0c\u53f6\u65b0\u660e\u6682\u65f6\u4e0d\u80fd\u78b0\u4ed6\uff0c\u6848\u60c5\u770b\u4e0a\u53bb\u975e\u5e38\u590d\u6742\uff0c\u53ef\u80fd\u8fd8\u6d89\u53ca\u5230\u547d\u6848\uff08\u66fe\u5efa\u534e\u7684\u5931\u8e2a\uff09\uff0c\u4e13\u6848\u7ec4\u628a\u9884\u5ba1\u7684\u56f0\u96be\u60f3\u7684\u5f88\u5927\u3002\u5403\u4e2d\u996d\u7684\u65f6\u5019\uff0c\u5f20\u606a\u63d0\u8d77\u6765\u8981\u8ddf\u8bb8\u601d\u5148\u5355\u72ec\u89c1\u4e00\u9762\u3002</p><p>\u3000\u3000\u4e13\u6848\u7ec4\u6210\u5458\u90fd\u505c\u4e0b\u6765\uff0c\u5947\u602a\u7684\u770b\u7740\u5f20\u606a\uff0c\u5728\u8fd9\u77ed\u77ed\u4e00\u5929\u7684\u63a5\u89e6\u65f6\u95f4\u91cc\uff0c\u8fd9\u4e2a\u5341\u516d\u5c81\u7684\u5c11\u5e74\u8868\u73b0\u5f97\u76f8\u5f53\u6210\u719f\uff0c\u5bf9\u6848\u60c5\u7684\u5206\u6790\u5f88\u8001\u9053\uff0c\u7b80\u503c\u5c31\u50cf\u6f2b\u753b\u3001\u5c0f\u8bf4\u91cc\u624d\u4f1a\u6709\u7684\u5929\u624d\u5c11\u5e74\uff0c\u4f55\u51b5\u6848\u60c5\u80fd\u8fdb\u5c55\u5230\u8fd9\u4e00\u6b65\uff0c\u90fd\u662f\u4ed6\u7684\u529f\u52b3\uff0c\u4f46\u662f\u60f3\u5fc5\u4ed6\u4e5f\u80fd\u77e5\u9053\uff0c\u529e\u6848\u4eba\u5458\u662f\u7981\u6b62\u4e0e\u5acc\u7591\u72af\u5355\u72ec\u63a5\u89e6\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u5927\u5bb6\u90fd\u62ff\u5947\u602a\u7684\u76ee\u5149\u770b\u7740\u81ea\u5df1\uff0c\u7238\u7238\u4e5f\u5728\u5bf9\u9762\u4f7f\u773c\u8272\u8ba9\u4ed6\u4e0d\u8981\u8bf4\u75af\u8bdd\uff0c\u5f20\u606a\u7b11\u4e86\u7b11\uff1a\u201c\u6211\u624d\u5341\u516d\u5c81\u800c\u5df2\uff0c\u53c8\u4e0d\u662f\u529e\u6848\u4eba\u5458\uff0c\u6211\u53ea\u662f\u60f3\u53ef\u80fd\u6709\u529e\u6cd5\u8bf4\u670d\u8bb8\u601d\u4e3b\u52a8\u4ea4\u5f85\u95ee\u9898\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u771f\u6709\u529e\u6cd5\u8bf4\u670d\u5979\u4e3b\u52a8\u4ea4\u5f85\u95ee\u9898\uff1f\u201d\u91d1\u56fd\u6d77\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u603b\u8981\u8bd5\u4e00\u8bd5\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u59dc\u660e\u8bda\u5728\u6d77\u5dde\u5c5e\u4e8e\u4e3b\u52a8\u5411\u68c0\u67e5\u7ec4\u4ea4\u5f85\u95ee\u9898\uff0c\u8fd9\u8fb9\u641e\u5f97\u592a\u4e45\uff0c\u4e01\u5411\u5c71\u4f1a\u4e0d\u4f1a\u6709\u6240\u8b66\u89c9\uff1f\u201d</p><p>\u3000\u3000\u91d1\u56fd\u6d77\u5fc3\u91cc\u60f3\uff1a\u8fd9\u5c0f\u5b50\u5012\u662f\u770b\u51fa\u6211\u8eab\u4e0a\u7684\u538b\u529b\u5f88\u5927\u3002\u65f6\u95f4\u5f88\u6025\u8feb\uff0c\u5c06\u59dc\u660e\u8bda\u62d8\u62bc\u592a\u4e45\uff0c\u4e01\u5411\u5c71\u4f1a\u6709\u8b66\u89c9\uff0c\u4f55\u51b5\u80fd\u5c3d\u5feb\u7684\u6d17\u8131\u5510\u5b66\u8c26\u53d7\u8d3f\u7684\u5acc\u7591\uff0c\u628a\u68c0\u5bdf\u9662\u524d\u671f\u7684\u5de5\u4f5c\u5931\u8bef\u5c3d\u5feb\u5f25\u8865\u8fc7\u6765\uff0c\u4e5f\u7b97\u7ed9\u7701\u59d4\u6709\u4e2a\u521d\u6b65\u7684\u4ea4\u5f85\uff1a\u201c\u597d\uff0c\u8bd5\u4e00\u8bd5\u4e5f\u597d\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5403\u8fc7\u4e2d\u996d\uff0c\u5f20\u606a\u63a8\u5f00\u4e34\u65f6\u62d8\u7559\u5ba4\u7684\u95e8\uff0c\u8bb8\u601d\u6b63\u5750\u5728\u7a97\u53f0\u524d\u53d1\u5446\uff0c\u79c0\u53d1\u5782\u4e0b\u6765\uff0c\u906e\u4f4f\u534a\u8fb9\u8138\uff0c\u9732\u51fa\u5706\u6da6\u7684\u4e0b\u9894\uff0c\u5979\u7a7f\u7740\u8584\u7ed2\u7ebf\u886b\uff0c\u9732\u51fa\u7c89\u85d5\u4e00\u6837\u6d01\u767d\u7684\u624b\u81c2\uff0c\u8eab\u4f53\u66f2\u7ebf\u67d4\u7f8e\u52a8\u4eba\u3002</p><p>\u3000\u3000\u201c\u8bb8\u601d\u59d0\u2026\u2026\u201d\u5f20\u606a\u8f7b\u8f7b\u5524\u4e86\u4e00\u58f0\u3002</p><p>\u3000\u3000\u8bb8\u601d\u8f6c\u8fc7\u5934\uff0c\u9732\u51fa\u65e0\u6cd5\u7f6e\u4fe1\u7684\u795e\u60c5\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\uff1f\u201d</p><p>\u3000\u3000\u7a97\u53f0\u524d\u6446\u7740\u4e24\u5f20\u7ea2\u6728\u89d2\u6905\u4e0e\u4e00\u5f20\u8336\u51e0\uff0c\u5f20\u606a\u8d70\u8fc7\u53bb\u5750\u4e0b\u6765\uff1a\u201c\u8bb8\u601d\u59d0\uff0c\u770b\u5230\u6211\u5f88\u610f\u5916\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u8fd9\u4e2a\u5c11\u5e74\u7ed9\u81ea\u5df1\u7559\u4e0b\u5f88\u6df1\u7684\u5370\u8c61\uff0c\u8bb8\u601d\u4e00\u70b9\u4e5f\u4e0d\u63a9\u9970\u773c\u775b\u91cc\u7684\u610f\u5916\u559c\u60a6\uff0c\u201c\u4f60\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\uff1f\u201d</p><p>\u3000\u3000\u201c\u770b\u5230\u8bb8\u601d\u59d0\u54ed\u7684\u65f6\u5019\uff0c\u6211\u5c31\u5728\u60f3\uff0c\u8bb8\u601d\u59d0\u8fd9\u4e48\u505a\u4e00\u5b9a\u6709\u4e07\u4e0d\u5f97\u5df2\u7684\u82e6\u8877\uff0c\u8bb8\u601d\u59d0\u5e76\u4e0d\u60f3\u8fd9\u4e48\u505a\uff0c\u624d\u4f1a\u5fcd\u4e0d\u4f4f\u75db\u54ed\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u554a\uff0c\u4f60\u8bf4\u4ec0\u4e48\uff0c\u4eba\u5c0f\u9b3c\u5927\uff0c\u600e\u4e48\u8001\u5b66\u5927\u4eba\u8bed\u6c14\u8bf4\u8bdd\u554a\uff0c\u6211\u4e5f\u542c\u4e0d\u61c2\u4f60\u8bf4\u4ec0\u4e48\uff1f\u201d\u8bb8\u601d\u4fa7\u7740\u5934\u770b\u7740\u5f20\u606a\uff0c\u773c\u795e\u6d41\u9732\u51fa\u6beb\u4e0d\u505a\u4f5c\u7684\u8bb6\u5f02\u3002</p><p>\u3000\u3000\u201c\u8bb8\u601d\u59d0\u8ba4\u4e0d\u8ba4\u8bc6\u66fe\u5efa\u534e\uff0c\u65b0\u4e30\u96c6\u56e2\u7684\u8d22\u52a1\u90e8\u957f\uff1f\u201d</p><p>\u3000\u3000\u201c\u8ba4\u8bc6\uff0c\u4f60\u95ee\u4ed6\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u89c1\u8bb8\u601d\u773c\u795e\u6ca1\u6709\u60ca\u614c\u7684\u8eb2\u95ea\u5f00\uff0c\u5fc3\u91cc\u6700\u5927\u7684\u60ac\u77f3\u843d\u4e0b\u4e86\uff0c\u770b\u6765\u5979\u9677\u8fdb\u53bb\u5e76\u4e0d\u6df1\u3002</p><p>\u3000\u3000\u201c\u59dc\u660e\u8bda\u534a\u4e2a\u6708\u524d\u5411\u6d77\u5dde\u5e02\u516c\u5b89\u5c40\u62a5\u6848\uff0c\u8bf4\u66fe\u5efa\u534e\u5377\u8d70\u65b0\u4e30\u96c6\u56e2\u8d26\u4e0a\u56db\u767e\u591a\u4e07\u5c31\u5931\u8e2a\u4e86\uff0c\u6d77\u5dde\u5e02\u516c\u5b89\u5c40\u4e00\u76f4\u5728\u79d8\u5bc6\u4fa6\u67e5\u8fd9\u4e8b\uff0c\u8bb8\u601d\u59d0\u77e5\u4e0d\u77e5\u9053\u8fd9\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e8b\uff0c\u65b0\u4e30\u96c6\u56e2\u7684\u7ba1\u7406\u5c42\u90fd\u77e5\u9053\uff0c\u54a6\uff0c\u4f60\u8fd8\u6ca1\u6709\u8bf4\u4f60\u4e3a\u4ec0\u4e48\u5728\u8fd9\u91cc\uff0c\u600e\u4e48\u7a81\u7136\u95ee\u8d77\u8fd9\u4e9b\u83ab\u540d\u5176\u5999\u7684\u95ee\u9898\uff1f\u201d</p><p>\u3000\u3000\u201c\u8bb8\u601d\u59d0\u4e4b\u524d\u60f3\u77e5\u9053\u6211\u5bb6\u91cc\u7684\u60c5\u51b5\uff0c\u6211\u6ca1\u6709\u8bf4\uff0c\u56e0\u4e3a\u6211\u4e4b\u524d\u4e0d\u80fd\u8ba9\u8bb8\u601d\u59d0\u77e5\u9053\u6211\u7238\u66fe\u7ecf\u662f\u5510\u5b66\u8c26\u526f\u5e02\u957f\u7684\u79d8\u4e66\uff0c\u73b0\u5728\u662f\u6d77\u5dde\u5e02\u653f\u5e9c\u526f\u79d8\u4e66\u957f\u5f20\u77e5\u884c\uff0c\u6211\u4e0d\u80fd\u8ba9\u8bb8\u601d\u59d0\u77e5\u9053\u6211\u8ba4\u5f97\u90a3\u5929\u627e\u4f60\u7684\u4eba\u5c31\u662f\u5510\u5b66\u8c26\u526f\u5e02\u957f\u73b0\u5728\u7684\u79d8\u4e66\u53f6\u65b0\u660e\u2026\u2026\u201d\u5f20\u606a\u770b\u7740\u8bb8\u601d\u8138\u4e0a\u7684\u559c\u60a6\u9010\u6e10\u51dd\u56fa\u5d29\u89e3\uff0c\u5fc3\u91cc\u4e0d\u662f\u6ecb\u5473\uff0c\u201c\u5728\u8c61\u5c71\u68ee\u6797\u516c\u56ed\u8ddf\u8bb8\u601d\u59d0\u76f8\u9047\u4e4b\u524d\uff0c\u6211\u5c31\u77e5\u9053\u8bb8\u601d\u59d0\u8ddf\u5510\u5b66\u8c26\u526f\u5e02\u957f\u6ca1\u6709\u4efb\u4f55\u5173\u7cfb\uff0c\u8ddf\u8bb8\u601d\u59d0\u6709\u5173\u7cfb\u7684\u662f\u6d77\u5dde\u5e02\u59d4\u4e66\u8bb0\u4e01\u5411\u5c71\uff0c\u8bb8\u601d\u59d0\u8fd8\u8bb0\u5f97\u6211\u5f53\u65f6\u624b\u91cc\u62ff\u7740\u7167\u76f8\u673a\u5427\uff1f\u5728\u90a3\u4e4b\u524d\uff0c\u6211\u5c31\u7ad9\u5728\u540e\u5c71\u7684\u7ea2\u7816\u522b\u5885\u524d\uff0c\u770b\u7740\u4e01\u5411\u5c71\u5750\u8f66\u8fdb\u53bb\uff0c\u540e\u6765\uff0c\u53c8\u770b\u7740\u4e01\u5411\u5c71\u4e0e\u8bb8\u601d\u59d0\u5148\u540e\u5f00\u8f66\u51fa\u6765\u3002\u8fd8\u6709\uff0c\u6211\u4ece\u8bb8\u601d\u59d0\u90a3\u91cc\u62ff\u4e86\u4e00\u5f20\u4f60\u8ddf\u5510\u5b66\u8c26\u526f\u5e02\u957f\u7684\u5408\u5f71\uff0c\u4f60\u662f\u4e0d\u662f\u53d1\u73b0\u5c11\u4e86\u4e00\u5f20\u611f\u89c9\u5f88\u5947\u602a\uff0c\u6ca1\u6709\u60f3\u5230\u4f1a\u662f\u6211\u62ff\u7684\u5427\uff1f\u6211\u8fd8\u77e5\u9053\uff0c\u8bb8\u601d\u59d0\u7684\u59b9\u59b9\u505a\u5fc3\u810f\u624b\u672f\u65f6\uff0c\u82b1\u4e86\u4e00\u5927\u7b14\u94b1\uff0c\u8fd9\u7b14\u94b1\u662f\u59dc\u660e\u8bda\u66ff\u4e01\u5411\u5c71\u7ed9\u4f60\u7684\uff0c\u5e76\u4e0d\u662f\u4f60\u5411\u68c0\u67e5\u7ec4\u4ea4\u5f85\u7684\u90a3\u6837\u8fd9\u662f\u5510\u5b66\u8c26\u5411\u59dc\u660e\u8bda\u7d22\u53d6\u7684\u8d3f\u8d42\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u4e86\u5f20\u606a\u7684\u8bdd\uff0c\u8bb8\u601d\u611f\u89c9\u8fd9\u4e9b\u5929\u5904\u4e8e\u5d29\u6e83\u8fb9\u7f18\u7684\u795e\u7ecf\u5728\u55e1\u55e1\u4f5c\u54cd\u3002</p><p>\u3000\u3000\u201c\u8bb8\u601d\u7ec4\uff0c\u6211\u77e5\u9053\u4f60\u5fc3\u91cc\u5f88\u82e6\uff0c\u4f46\u662f\u4f60\u6ca1\u6709\u5fc5\u8981\u505a\u4e01\u5411\u5c71\u7684\u5de5\u5177\uff0c\u5510\u5b66\u8c26\u526f\u5e02\u957f\u662f\u6e05\u767d\u65e0\u8f9c\u7684\uff0c\u8fd9\u6b21\u5c06\u4f60\u4eec\u8f6c\u79fb\u5230\u7701\u57ce\u6765\uff0c\u5e76\u4e0d\u662f\u8bf4\u5510\u5b66\u8c26\u6848\u8fdb\u5165\u4e0b\u4e00\u9636\u6bb5\uff0c\u800c\u662f\u8981\u5bf9\u59dc\u660e\u8bda\u4e0e\u4e01\u5411\u5c71\u5408\u4f19\u4fb5\u541e\u56fd\u6709\u8d44\u4ea7\u6848\u5c55\u5f00\u8c03\u67e5\uff0c\u7701\u91cc\u4e3a\u6b64\u4e13\u95e8\u6210\u7acb\u4e13\u6848\u7ec4\uff0c\u6211\u8ddf\u6211\u7238\u7238\u90fd\u53c2\u4e0e\u8fdb\u6765\uff0c\u6240\u4ee5\u4f60\u4f1a\u5728\u8fd9\u91cc\u770b\u5230\u6211\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u8bb8\u601d\u5f20\u4e86\u5f20\u5634\uff0c\u6ca1\u6709\u5410\u51fa\u4e00\u4e2a\u97f3\u8282\u3002</p><p>\u3000\u3000\u201c\u8bb8\u601d\u59d0\uff0c\u6211\u5e0c\u671b\u4f60\u5411\u4e13\u6848\u7ec4\u4e3b\u52a8\u4ea4\u5f85\u6240\u6709\u7684\u4e8b\u60c5\uff0c\u4f60\u7684\u95ee\u9898\u4e0d\u4e25\u91cd\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u601d\u5934\u57cb\u5728\u819d\u95f4\uff0c\u653e\u58f0\u5927\u54ed\u8d77\u6765\uff0c\u5f20\u606a\u7ad9\u8d77\u6765\uff0c\u5c06\u5979\u67d4\u8f6f\u7684\u5934\u9885\u62b1\u5728\u6000\u91cc\uff0c\u8f7b\u58f0\u8bf4\uff1a\u201c\u8bb8\u601d\u59d0\uff0c\u6211\u8ddf\u4f60\u8bf4\u8fc7\uff0c\u6211\u4e0d\u4f1a\u8ba9\u522b\u4eba\u4f24\u5bb3\u4f60\u7684\uff0c\u4f60\u8981\u76f8\u4fe1\u6211\u3002\u201d\u8bb8\u601d\u53cd\u624b\u6402\u4f4f\u4ed6\u7684\u8170\uff0c\u7d27\u7d27\u6263\u4f4f\uff0c\u5f20\u606a\u7684\u886c\u886b\u8fc5\u901f\u7ed9\u6cea\u6c34\u6fe1\u6e7f\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f85\u8bb8\u601d\u6162\u6162\u6536\u4f4f\u54ed\u58f0\uff0c\u624d\u5c06\u5979\u653e\u5f00\uff0c\u63a8\u95e8\u8d70\u4e86\u51fa\u53bb\uff0c\u7238\u7238\u3001\u91d1\u56fd\u6d77\u3001\u9648\u6653\u677e\u4ed6\u4eec\u90fd\u7ad9\u5728\u8d70\u5eca\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\uff1a\u201c\u80fd\u4e0d\u80fd\u7ed9\u5979\u70b9\u65f6\u95f4\uff1f\u6709\u4e00\u4ef6\u4e8b\uff0c\u6211\u4e00\u76f4\u6ca1\u6709\u8ddf\u4f60\u4eec\u8bf4\uff0c\u6211\u5728\u8c61\u5c71\u68ee\u6797\u516c\u56ed\u5357\u95e8\u5e7f\u573a\u9047\u5230\u8bb8\u601d\u65f6\uff0c\u5e76\u6ca1\u6709\u7acb\u5373\u62ff\u5230\u5510\u4f2f\u4f2f\u8ddf\u5979\u7684\u5408\u5f71\uff0c\u5979\u65e0\u610f\u95f4\u5c06\u996e\u6599\u6cfc\u5230\u6211\u8eab\u4e0a\uff0c\u5f88\u8fc7\u610f\u4e0d\u53bb\uff0c\u5c06\u6211\u5e26\u56de\u4f4f\u5904\u8981\u5e2e\u6211\u6d17\u8863\u670d\u3002\u5979\u53d7\u4e01\u5411\u5c71\u80c1\u8feb\u53bb\u8bec\u9677\u5510\u4f2f\u4f2f\uff0c\u4e0d\u662f\u51fa\u81ea\u5979\u7684\u672c\u613f\uff0c\u5979\u5fc3\u91cc\u538b\u529b\u5f88\u5927\uff0c\u5f88\u96be\u53d7\uff0c\u5728\u5979\u7684\u4f4f\u5904\uff0c\u5979\u628a\u6211\u5f53\u6210\u9047\u5230\u7684\u964c\u751f\u4eba\uff0c\u9646\u9646\u7eed\u7eed\u7684\u8ddf\u6211\u63d0\u8d77\u4e00\u4e9b\u6709\u5173\u7684\u4e8b\u60c5\uff0c\u5c31\u7b97\u6211\u62ff\u8d70\u4e00\u5f20\u5979\u8ddf\u5510\u4f2f\u4f2f\u7684\u5408\u5f71\uff0c\u5979\u4e5f\u6545\u4f5c\u4e0d\u77e5\u3002\u5979\u6ca1\u6709\u52c7\u6c14\u53cd\u6297\u4e01\u5411\u5c71\u7684\u80c1\u8feb\uff0c\u4f46\u662f\u5979\u5341\u5206\u8feb\u5207\u7684\u5e0c\u671b\u80fd\u6709\u4eba\u53bb\u63ed\u7a7f\u4e01\u5411\u5c71\u7684\u9634\u8c0b\uff0c\u673a\u7f18\u5de7\u5408\uff0c\u6240\u4ee5\u6211\u624d\u4f1a\u5f88\u5feb\u7684\u5c31\u7406\u6e05\u6574\u4ef6\u6848\u5b50\u7684\u6765\u9f99\u53bb\u8109\u2026\u2026\u5bf9\u5979\u63d0\u8d77\u516c\u8bc9\u65f6\uff0c\u6211\u5e0c\u671b\u80fd\u5145\u5206\u8003\u8651\u8fd9\u4e9b\u60c5\u8282\uff0c\u6211\u5f88\u660e\u767d\u5979\u7684\u7cbe\u795e\u72b6\u6001\uff0c\u6240\u4ee5\u6709\u628a\u63e1\u8bf4\u670d\u5979\u4e3b\u52a8\u4ea4\u5f85\u95ee\u9898\uff0c\u5982\u679c\u6709\u5fc5\u8981\uff0c\u7b49\u5979\u4ea4\u5f85\u6e05\u95ee\u9898\u4e4b\u540e\uff0c\u6211\u4e5f\u53ef\u4ee5\u505a\u4e00\u4efd\u7b14\u5f55\u3002\u201d\u5f20\u606a\u5c06\u4e0e\u8bb8\u601d\u76f8\u9047\u7684\u8fc7\u7a0b\uff0c\u4e5d\u5206\u771f\u4e00\u5206\u5047\uff0c\u91cd\u65b0\u7f16\u6392\u4e86\u4e00\u4e0b\uff0c\u8bf4\u4e86\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u8fd8\u662f\u5f88\u96be\u6d88\u5316\u81ea\u5df1\u7684\u513f\u5b50\u62ff\u5982\u6b64\u6210\u719f\u7684\u8bed\u6c14\u6765\u8bf4\u8fd9\u4ef6\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u91d1\u56fd\u6d77\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u6ca1\u6709\u6000\u7591\u5f20\u606a\u7684\u8bdd\uff0c\u786e\u5b9e\u4e5f\u6ca1\u6709\u503c\u5f97\u6000\u7591\u7684\u5730\u65b9\uff0c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u89c9\u5f97\u66f4\u5408\u7406\u4e86\u3002\u91d1\u56fd\u6d77\u8bf4\u9053\uff1a\u201c\u8bb8\u601d\u80fd\u4e3b\u52a8\u4ea4\u5f85\u95ee\u9898\uff0c\u534f\u52a9\u6211\u4eec\u5c3d\u65e9\u7834\u83b7\u6848\u5b50\uff0c\u4f60\u8bf4\u7684\u4e8b\u60c5\uff0c\u6211\u4eec\u4f1a\u7ed9\u4e88\u5145\u5206\u7684\u8003\u8651\uff0c\u6bd5\u7adf\u5979\u5728\u5ba2\u89c2\u4e0a\uff0c\u9884\u5148\u9632\u6b62\u4e86\u8fd9\u8d77\u51a4\u5047\u9519\u6848\u3002\u201d</p></article>"}}}]);
