(window.webpackJsonp=window.webpackJsonp||[]).push([[19],{41:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u516b\u8282 \u9047\u88ad",content:"<article><h2>\u7b2c\u5341\u516b\u8282 \u9047\u88ad</h2><p>\u9634\u9b42\u57ce\u65e2\u7136\u6f02\u6d6e\u5728\u7a7a\u4e2d\uff0c\u519b\u961f\u51fa\u53bb\u4f5c\u6218\uff0c\u6765\u56de\u5f80\u8fd4\uff0c\u6d6e\u7a7a\u8247\u5c31\u662f\u5fc5\u4e0d\u53ef\u5c11\u7684\u4ea4\u901a\u5de5\u5177\u3002</p><p>\u5728\u8fd9\u79cd\u4f4d\u9636\u68ee\u4e25\u7684\u57ce\u5e02\uff0c\u4ec0\u4e48\u4e1c\u897f\u90fd\u8981\u5206\u7b49\u7ea7\u3002\u5c31\u50cf\u743c\u6069\uff0c\u5f53\u4e00\u5e74\u7ea7\u5b66\u751f\u7684\u65f6\u5019\uff0c\u5b66\u6821\u914d\u53d1\u7684\u5deb\u5e08\u888d\u3001\u6cd5\u6756\u7b49\u88c5\u5907\uff0c\u5168\u90fd\u662f\u4f4e\u7ea7\u8d27\u8272\uff1b\u5982\u4eca\u4ed6\u5347\u4e0a\u4e94\u5e74\u7ea7\uff0c\u5f85\u9047\u4e5f\u8ddf\u7740\u4e00\u8def\u63d0\u5347\uff0c\u4e0d\u4f46\u5deb\u5e08\u888d\u3001\u6cd5\u6756\u5df2\u7ecf\u6362\u6210\u9644\u5e26\u91cd\u91cd\u589e\u76ca\u795d\u798f\u9b54\u6cd5\u7684\u9ad8\u7ea7\u8d27\uff0c\u8116\u5b50\u4e0a\u8fd8\u6302\u4e86\u9b54\u6cd5\u9879\u94fe\uff0c\u5de6\u624b\u98df\u6307\u4e0a\u6234\u7740\u9b54\u6cd5\u6212\u6307\uff0c\u8fde\u8eab\u4e0a\u62ab\u7684\u7070\u8272\u6597\u7bf7\u90fd\u9644\u5e26\u9632\u62a4\u7bad\u77e2\u7684\u9b54\u529b\u3002\u81f3\u4e8e\u811a\u4e0a\u7a7f\u7684\u6d6e\u7a7a\u9774\uff0c\u5012\u662f\u51fa\u53d1\u524d\u519b\u961f\u7edf\u4e00\u914d\u53d1\u7684\uff0c\u80fd\u6709\u77ed\u6682\u7684\u6d6e\u7a7a\u98de\u884c\u80fd\u529b\uff0c\u9632\u6b62\u6709\u4eba\u4e00\u6b65\u8e0f\u9519\uff0c\u4ece\u8247\u4e0a\u6389\u4e0b\u53bb\u6454\u6b7b\u3002</p><p>\u6d6e\u7a7a\u8247\u4e5f\u662f\u4e00\u6837\uff0c\u5206\u9ad8\u4e2d\u4f4e\u4e0d\u540c\u7684\u6863\u6b21\u3002\u6700\u9876\u7ea7\u7684\u90a3\u8258\u6d6e\u7a7a\u8247\uff0c\u7b80\u76f4\u4fbf\u5982\u4e00\u5ea7\u592a\u7a7a\u5821\u5792\uff0c\u552f\u4e00\u7684\u9057\u61be\u662f\u6ca1\u6709\u91c7\u7528\u591a\u70ae\u5854\u8bbe\u8ba1\uff0c\u636e\u8bf4\u6574\u4e2a\u8230\u4f53\u90fd\u662f\u7eaf\u7cbe\u91d1\u6253\u9020\u7684\uff0c\u6c14\u52bf\u5b8f\u4f1f\uff0c\u4ee4\u4eba\u4e00\u89c1\u800c\u751f\u819c\u62dc\u4e4b\u5fc3\uff0c\u4e43\u662f\u9634\u9b42\u57ce\u4e3b\u7684\u5ea7\u8230\uff0c\u5e73\u5e38\u5c31\u505c\u5728\u738b\u5bab\u524d\u7684\u5e7f\u573a\u4e0a\uff0c\u57fa\u672c\u662f\u6ca1\u6709\u52a8\u7528\u7684\u673a\u4f1a\u3002\u6b21\u4e00\u7b49\u7684\uff0c\u4e5f\u6709\u8fd1\u4f3c\u9a71\u9010\u8230\u5927\u5c0f\uff0c\u6574\u4e2a\u519b\u961f\u91cc\u4e5f\u6ca1\u914d\u5907\u51e0\u8258\uff0c\u7b97\u662f\u6700\u5f3a\u6b66\u5668\u4e4b\u4e00\uff0c\u4e00\u822c\u53ea\u4f1a\u5728\u6bcf\u5e74\u4e00\u5ea6\u7684\u5927\u89c4\u6a21\u8fdc\u5f81\u4e2d\u4f7f\u7528\u3002</p><p>\u518d\u6b21\u4e00\u7b49\u7684\uff0c\u5c31\u6bd4\u8f83\u5bd2\u9178\u4e86\uff0c\u611f\u89c9\u50cf\u662f\u80fd\u98de\u884c\u7684\u8239\u8236\uff0c\u88c5\u5907\u4e86\u4e00\u4e9b\u9b54\u6cd5\u5f29\u70ae\uff0c\u4ec5\u6b64\u800c\u5df2\u3002\u8230\u8eab\u4e3b\u8981\u662f\u94c1\u548c\u6728\u8d28\u7ed3\u6784\uff0c\u91cd\u8981\u5730\u65b9\u4ee5\u7cbe\u91d1\u79d8\u94f6\u7145\u5236\uff0c\u5728\u519b\u961f\u4e2d\u5927\u91cf\u88c5\u5907\uff0c\u7b97\u662f\u4e3b\u529b\u4ea4\u901a\u5de5\u5177\u4e86\u3002\u6bd5\u7adf\uff0c\u5e7d\u5f71\u754c\u4e0d\u540c\u4e8e\u7269\u8d28\u754c\uff0c\u6ca1\u6709\u591a\u5c11\u77ff\u85cf\uff0c\u91d1\u5c5e\u5236\u54c1\u90fd\u662f\u6bd4\u8f83\u7a00\u5c11\u7f55\u89c1\u7684\u3002</p><p>\u6700\u5dee\u7684\u4e00\u79cd\uff0c\u5c31\u662f\u5982\u4eca\u743c\u6069\u6b63\u4e58\u5750\u7684\u8fd9\u79cd\u2026\u2026\u4ec0\u4e48\u6d6e\u7a7a\u8230\uff01\u6839\u672c\u5c31\u662f\u5757\u80fd\u98de\u884c\u7684\u5927\u6728\u7b4f\u800c\u5df2\uff01</p><p>\u56e0\u4e3a\u662f\u6700\u4f4e\u7ea7\u522b\u7684\u201c\u519b\u4e8b\u6f14\u4e60\u201d\uff0c\u519b\u961f\u81ea\u7136\u4e0d\u4f1a\u9063\u51fa\u591a\u4e48\u4f18\u826f\u7684\u6b66\u5668\u88c5\u5907\uff0c\u80fd\u6709\u8fd9\u79cd\u98de\u884c\u5927\u6728\u7b4f\u4ee3\u6b65\uff0c\u5df2\u7ecf\u53ef\u4ee5\u8c22\u5929\u8c22\u5730\u4e86\u3002</p><p>\u53cd\u6b63\uff0c\u5df2\u7ecf\u662f\u5728\u8fd4\u822a\u9014\u4e2d\uff0c\u518d\u8fc7\u534a\u5c0f\u65f6\uff0c\u5c31\u80fd\u56de\u5230\u9634\u9b42\u57ce\u4e86\u3002</p><p>\u8fd9\u6b21\u201c\u519b\u4e8b\u6f14\u4e60\u201d\uff0c\u867d\u7136\u662f\u4f8b\u884c\u516c\u4e8b\uff0c\u6210\u7ee9\u5012\u8fd8\u4e0d\u9519\u3002\u5728\u4e00\u7247\u5d07\u5c71\u5cfb\u5cad\u4e2d\uff0c\u4ed6\u4eec\u53d1\u73b0\u4e86\u4e00\u7fa4\u5e7d\u5f71\u725b\u5934\u4eba\uff0c\u5927\u7ea6\u56db\u4e94\u5341\u4e2a\uff0c\u5df2\u7ecf\u5c3d\u6570\u88ab\u6d3b\u6349\uff0c\u6b64\u65f6\u5c31\u62bc\u5728\u540e\u9762\u7684\u51e0\u8258\u6d6e\u7a7a\u8247\u4e0a\u3002</p><p>\u9634\u5f71\u725b\u5934\u4eba\uff0c\u6709\u667a\u6167\u3001\u80fd\u4ea4\u6d41\uff0c\u800c\u4e14\u61e6\u5f31\u542c\u8bdd\uff0c\u53c8\u6709\u4e00\u8eab\u86ee\u529b\uff0c\u662f\u975e\u5e38\u9002\u5408\u7684\u5974\u96b6\u6750\u6599\uff0c\u9634\u9b42\u57ce\u7684\u77ff\u573a\u3001\u5de5\u574a\u91cc\u5927\u91cf\u4f7f\u7528\u3002\u4e0d\u8fc7\u4e00\u60f3\u8d77\u201c\u9634\u5f71\u725b\u5934\u4eba\u201d\u8fd9\u540d\u5b57\uff0c\u743c\u6069\u5c31\u5f88\u60f3\u7b11\u3002</p><p>\u6240\u8c13\u9634\u5f71\u725b\u5934\u4eba\uff0c\u5176\u5b9e\u662f\u5e7d\u5f71\u754c\u7684\u72ec\u6709\u751f\u7269\uff0c\u548c\u7269\u8d28\u754c\u7684\u725b\u5934\u4eba\u6ca1\u6709\u534a\u70b9\u5173\u7cfb\uff0c\u53ea\u4e0d\u8fc7\u5934\u4e0a\u540c\u6837\u4e5f\u957f\u4e24\u53ea\u5f2f\u89d2\u7f62\u4e86\u3002\u5f53\u5e74\u9634\u9b42\u57ce\u8fdb\u5165\u5e7d\u5f71\u754c\uff0c\u5c45\u6c11\u4eec\u9047\u4e0a\u5f88\u591a\u5e7d\u5f71\u754c\u7684\u571f\u8457\u751f\u7269\uff0c\u56e0\u4e3a\u8fd9\u4e9b\u5947\u602a\u751f\u7269\u5728\u7269\u8d28\u754c\u662f\u538b\u6839\u5c31\u6ca1\u6709\uff0c\u6240\u4ee5\u5c45\u6c11\u4eec\u7684\u8bcd\u6c47\u91cc\uff0c\u5bf9\u8fd9\u4e9b\u751f\u7269\u4e5f\u6839\u672c\u6ca1\u6709\u76f8\u5e94\u7684\u540d\u79f0\uff0c\u4e0d\u77e5\u5982\u4f55\u79f0\u547c\u3002</p><p>\u540e\u6765\u662f\u67d0\u4e2a\u9ad8\u5b98\u5efa\u8bae\uff0c\u4e8e\u662f\u653f\u5e9c\u9881\u5e03\u6cd5\u4ee4\uff0c\u7edf\u4e00\u4e3a\u5e7d\u5f71\u754c\u91cc\u7684\u751f\u7269\u5b9a\u540d\u3002\u5b9a\u540d\u7684\u65b9\u6cd5\u5f88\u7b80\u5355\uff1a\u5b83\u50cf\u7269\u8d28\u754c\u7684\u4ec0\u4e48\u751f\u7269\uff0c\u5c31\u4e5f\u7528\u540c\u6837\u7684\u540d\u5b57\u547d\u540d\u3002\u6bd4\u5982\u8fd9\u79cd\u5934\u4e0a\u957f\u5f2f\u89d2\u7684\u602a\u7269\uff0c\u5c31\u76f4\u63a5\u5b9a\u540d\u4e3a\u725b\u5934\u4eba\u2014\u2014\u4f46\u56e0\u4e3a\u786e\u5b9e\u548c\u725b\u5934\u4eba\u4e0d\u662f\u4e00\u56de\u4e8b\uff0c\u4e8e\u662f\u518d\u5728\u524d\u9762\u52a0\u201c\u9634\u5f71\u201d\u4e24\u4e2a\u5b57\uff0c\u5c31\u6210\u4e86\u9634\u5f71\u725b\u5934\u4eba\u3002</p><p>\u9634\u5f71\u725b\u5934\u4eba\u3001\u9634\u5f71\u9f20\u3001\u9634\u5f71\u516d\u8db3\u9a6c\u3001\u9634\u5f71\u4e09\u5934\u72ac\u3001\u9634\u5f71\u9f99\u2026\u2026\u8bf4\u5b9e\u8bdd\uff0c\u771f\u662f\u6709\u591f\u7f3a\u4e4f\u60f3\u8c61\u529b\u7684\u3002\u6bcf\u6bcf\u60f3\u5230\u8fd9\u4e00\u70b9\uff0c\u743c\u6069\u5c31\u5fcd\u4e0d\u4f4f\u60f3\u7b11\uff0c\u96be\u9053\u5f53\u5e74\u9634\u9b42\u57ce\u7684\u524d\u8f88\u4eec\uff0c\u5728\u53d6\u540d\u5b57\u8fd9\u65b9\u9762\u662f\u5982\u6b64\u7f3a\u4e4f\u5929\u8d4b\u4e48\u3002</p><p>\u4e0d\u8fc7\u60f3\u8fd9\u4e9b\u6ca1\u6709\u610f\u4e49\uff0c\u53cd\u6b63\u4e00\u5343\u591a\u5e74\u90fd\u5df2\u7ecf\u8fd9\u4e48\u8fc7\u6765\uff0c\u5927\u5bb6\u65e9\u5c31\u4e60\u60ef\u3002\u5c31\u7b97\u73b0\u5728\u6709\u4eba\u80fd\u5b9a\u51fa\u66f4\u5408\u9002\u66f4\u597d\u542c\u7684\u540d\u5b57\uff0c\u4e5f\u4e0d\u53ef\u80fd\u53d6\u4ee3\u4f20\u7edf\u3002</p><p>\u743c\u6069\u81ea\u7136\u4e5f\u61d2\u5f97\u5728\u610f\u8fd9\u4e9b\uff0c\u53cd\u6b63\u4e0d\u5173\u81ea\u5df1\u7684\u4e8b\u60c5\u3002\u4ed6\u73b0\u5728\u8111\u4e2d\u6240\u60f3\u7684\uff0c\u662f\u660e\u5929\u5c31\u53c8\u662f\u6708\u672b\u653e\u5047\u4e86\uff0c\u8fd9\u6b21\u56de\u5bb6\uff0c\u5e94\u8be5\u7ed9\u73ca\u5609\u5e26\u4ec0\u4e48\u793c\u7269\u5462\u3002</p><p>\u6b63\u601d\u7d22\u95f4\uff0c\u9661\u7136\u89c9\u5f97\u773c\u524d\u4e00\u4eae\uff0c\u62ac\u5934\u770b\u53bb\uff0c\u832b\u832b\u9634\u5f71\u8ff7\u96fe\u4e4b\u4e2d\uff0c\u6709\u51e0\u70b9\u660e\u4eae\u7684\u5149\u8292\u95ea\u8fc7\u3002\u743c\u6069\u5fc3\u4e2d\u5fae\u5fae\u4e00\u51db\uff0c\u8fd9\u662f\u524d\u9762\u5f00\u8def\u7684\u6d6e\u7a7a\u8247\u5728\u5411\u5168\u961f\u53d1\u8b66\u6212\u4fe1\u53f7\uff0c\u610f\u601d\u662f\u9047\u4e0a\u4e86\u654c\u4eba\u3002</p><p>\u5947\u602a\u3002</p><p>\u8fd9\u91cc\u5df2\u7ecf\u79bb\u9634\u9b42\u57ce\u4e0d\u8fdc\u4e86\uff0c\u7406\u5e94\u6ca1\u6709\u5927\u7fa4\u7684\u9634\u5f71\u602a\u7269\u805a\u5c45\u3002\u4f46\u5018\u82e5\u53ea\u662f\u9047\u4e0a\u51e0\u4e2a\u96f6\u661f\u6563\u5c45\u7684\u602a\u7269\uff0c\u524d\u9762\u7684\u5148\u950b\u90e8\u961f\u65e9\u5c31\u76f4\u63a5\u626b\u8361\u4e86\uff0c\u6839\u672c\u65e0\u9700\u5411\u5168\u961f\u53d1\u8b66\u6212\u4fe1\u53f7\u3002</p><p>\u6d6e\u7a7a\u8247\u4e0a\u7684\u5176\u4ed6\u4eba\u4e5f\u90fd\u770b\u5230\u4e86\u90a3\u51e0\u70b9\u4eae\u5149\uff0c\u8138\u8272\u987f\u65f6\u5168\u90fd\u51dd\u91cd\u8d77\u6765\u3002</p><p>\u8fd9\u6b21\u51fa\u6765\u8fdb\u884c\u201c\u519b\u4e8b\u6f14\u4e60\u201d\u7684\u5c0f\u5206\u961f\uff0c\u4e00\u5171\u5341\u8258\u6d6e\u7a7a\u8247\uff0c\u6bcf\u8258\u6d6e\u7a7a\u8247\u4e0a\u6709\u4e00\u540d\u519b\u961f\u7684\u9ad8\u9636\u519b\u5b98\u5750\u9547\uff0c\u4e24\u540d\u58eb\u5175\u4e3a\u526f\uff0c\u7136\u540e\u5c31\u662f\u516d\u5230\u4e03\u540d\u4e0d\u7b49\u7684\u201c\u5b9e\u4e60\u8005\u201d\u3002\u5b9e\u4e60\u8005\u5305\u62ec\u5deb\u5e08\u5b66\u6821\u7684\u5b66\u751f\u548c\u795e\u6bbf\u7684\u89c1\u4e60\u7267\u5e08\u3002\u743c\u6069\u8fd9\u8258\u6d6e\u7a7a\u8247\u4e0a\uff0c\u8fde\u4ed6\u81ea\u5df1\u5728\u5185\uff0c\u5b9e\u4e60\u8005\u4e00\u5171\u516d\u540d\uff0c\u5206\u522b\u662f\u56db\u540d\u5deb\u5e08\u5b66\u6821\u7684\u5b66\u751f\uff0c\u4e24\u540d\u89c1\u4e60\u7267\u5e08\u3002</p><p>\u5750\u9547\u7684\u519b\u5b98\u662f\u4e00\u540d\u9ed1\u888d\u5deb\u5e08\uff0c\u4e00\u76f4\u7aef\u5750\u5728\u6d6e\u7a7a\u8247\u4e0a\u552f\u4e00\u7684\u4e00\u628a\u6905\u5b50\u4e0a\uff0c\u515c\u5e3d\u538b\u5f97\u4f4e\u4f4e\u7684\uff0c\u9762\u5e9e\u9690\u85cf\u5728\u9634\u5f71\u4e2d\u3002\u4ece\u4ed6\u624b\u4e2d\u6cd5\u6756\u4e0a\u7684\u9ab7\u9ac5\u5934\u88c5\u9970\u548c\u5468\u8eab\u9634\u68ee\u623e\u6c14\u6765\u63a8\u6d4b\uff0c\u743c\u6069\u57fa\u672c\u5c31\u53ef\u4ee5\u65ad\u5b9a\u4ed6\u7684\u7cbe\u7814\u9886\u57df\u662f\u4ea1\u7075\u9b54\u6cd5\u3002\u5728\u6b64\u524d\u7684\u519b\u4e8b\u6f14\u4e60\u4e2d\uff0c\u4ed6\u4e00\u51fa\u624b\u5c31\u662f\u5927\u8303\u56f4\u7684\u6050\u60e7\u672f\uff0c\u628a\u90a3\u7fa4\u9634\u5f71\u725b\u5934\u4eba\u5168\u90fd\u5413\u5f97\u53cc\u817f\u53d1\u8f6f\uff0c\u62b1\u5934\u8dea\u5012\uff0c\u4e5f\u8bc1\u660e\u4e86\u743c\u6069\u7684\u5224\u65ad\u3002</p><p>\u80fd\u628a\u6050\u60e7\u672f\u8fd0\u7528\u5f97\u5982\u6b64\u7089\u706b\u7eaf\u9752\uff0c\u975e\u4ea1\u7075\u5deb\u5e08\u83ab\u5c5e\u3002</p><p>\u770b\u89c1\u524d\u9762\u6d6e\u7a7a\u8247\u7684\u793a\u8b66\uff0c\u9ed1\u888d\u5deb\u5e08\u7f13\u7f13\u7ad9\u8d77\u8eab\uff0c\u62ac\u8d77\u5934\u5411\u524d\u65b9\u7684\u6c89\u6c89\u8ff7\u96fe\u4e2d\u770b\u53bb\uff0c\u515c\u5e3d\u4e0b\u7684\u9634\u5f71\u91cc\uff0c\u4e24\u70b9\u78a7\u83b9\u83b9\u4eff\u4f5b\u9b3c\u706b\u7684\u4eae\u5149\u95ea\u70c1\uff0c\u90a3\u5e94\u8be5\u662f\u4ed6\u7684\u773c\u775b\u3002</p><p>\u201c\u51c6\u5907\uff01\u201d\u4ed6\u4f4e\u4f4e\u8bf4\uff0c\u58f0\u97f3\u5636\u54d1\uff0c\u867d\u7136\u4e0d\u5927\uff0c\u5374\u6e05\u695a\u4f20\u5230\u6bcf\u4e2a\u4eba\u7684\u8033\u6735\u91cc\u3002</p><p>\u62c5\u4efb\u526f\u624b\u7684\u4e24\u540d\u58eb\u5175\uff0c\u7acb\u523b\u4e00\u5de6\u4e00\u53f3\u62a4\u536b\u5728\u524d\uff0c\u5c06\u516d\u4e2a\u5b9e\u4e60\u8005\u4fdd\u62a4\u5728\u8eab\u540e\u3002\u743c\u6069\u5f00\u59cb\u51dd\u805a\u5fc3\u795e\uff0c\u8111\u4e2d\u6d6e\u73b0\u51fa\u4eca\u5929\u65e9\u4e0a\u51c6\u5907\u597d\u7684\u9b54\u6cd5\u3002</p><p>\u6709\u70b9\u5c0f\u9ebb\u70e6\uff0c\u4ed6\u601d\u5fd6\u7740\uff0c\u56e0\u4e3a\u662f\u5728\u8fd4\u822a\u9014\u4e2d\uff0c\u8fd9\u6b21\u519b\u4e8b\u6f14\u4e60\u5df2\u7ecf\u63a5\u8fd1\u5c3e\u58f0\u3002\u6240\u4ee5\u867d\u7136\u6bcf\u4e2a\u4eba\u51fa\u53d1\u524d\u90fd\u51c6\u5907\u597d\u4e86\u9b54\u6cd5\uff0c\u4f46\u5df2\u7ecf\u5728\u524d\u9762\u7684\u6218\u6597\u4e2d\u7528\u5f97\u5dee\u4e0d\u591a\u4e86\u3002\u5bf9\u4e8e\u5deb\u5e08\u6765\u8bf4\uff0c\u4e8b\u5148\u51c6\u5907\u597d\u7684\u9b54\u6cd5\u4e00\u65e6\u8017\u5c3d\uff0c\u4ed6\u53c8\u65e0\u6cd5\u4e34\u65f6\u5851\u6210\u9b54\u6cd5\uff0c\u90a3\u5c31\u7b49\u4e8e\u662f\u4e00\u6746\u6ca1\u6709\u5b50\u5f39\u7684\u706b\u67aa\uff0c\u5b8c\u5168\u4e27\u5931\u4e86\u6740\u4f24\u529b\u3002</p><p>\u867d\u7136\u961f\u4f0d\u91cc\u8fd8\u6709\u89c1\u4e60\u7267\u5e08\uff0c\u4e0d\u8fc7\u60c5\u51b5\u548c\u5deb\u5e08\u4e5f\u5dee\u4e0d\u591a\u3002\u7267\u5e08\u7684\u9b54\u6cd5\uff0c\u4e5f\u5c31\u662f\u795e\u672f\uff0c\u5fc5\u987b\u901a\u8fc7\u5411\u795e\u7947\u7948\u7977\u8bf7\u6c42\uff0c\u7531\u795e\u7947\u8d50\u4e88\uff0c\u540c\u6837\u4e5f\u662f\u6709\u6570\u91cf\u9650\u5236\u7684\u2014\u2014\u795e\u7947\u8d50\u4e88\u5f97\u591a\u4e86\uff0c\u7267\u5e08\u7684\u8eab\u4f53\u4e5f\u5bb9\u7eb3\u4e0d\u4e0b\u3002\u6b64\u65f6\u8fd9\u4e9b\u89c1\u4e60\u7267\u5e08\u7684\u795e\u672f\uff0c\u4e5f\u5e94\u8be5\u5dee\u4e0d\u591a\u7528\u5c3d\u4e86\u3002</p><p>\u5982\u679c\u8fd9\u4e2a\u6765\u88ad\u7684\u654c\u4eba\uff0c\u662f\u770b\u51c6\u4e86\u8fd9\u4e2a\u673a\u4f1a\uff0c\u7b97\u597d\u4e86\u4ed6\u4eec\u7684\u8fd4\u56de\u8def\u7ebf\uff0c\u5728\u8fd9\u91cc\u6253\u57cb\u4f0f\u2026\u2026\u90a3\u4e48\uff0c\u6050\u6015\u8fd8\u771f\u6709\u4e9b\u9ebb\u70e6\u3002\u5e0c\u671b\u2026\u2026\u4ec5\u4ec5\u662f\u4e2a\u5de7\u5408\u5427\uff0c\u4ec5\u4ec5\u662f\u5076\u7136\u649e\u4e0a\u7684\u2026\u2026</p><p>\u6b63\u60f3\u7740\uff0c\u53c8\u662f\u201c\u7830\u201d\u7684\u4e00\u58f0\u4f20\u6765\uff0c\u524d\u65b9\u8ff7\u96fe\u4e4b\u4e2d\uff0c\u817e\u8d77\u4e00\u70b9\u767d\u5149\uff0c\u5728\u9ad8\u7a7a\u4e2d\u7efd\u653e\u5f00\u6765\uff0c\u5316\u4f5c\u4e03\u5f69\u8679\u70b9\uff0c\u7eb7\u6e85\u5982\u96e8\u3002\u743c\u6069\u5fae\u5fae\u70b9\u5934\uff0c\u8fd9\u662f\u5728\u5411\u9634\u9b42\u57ce\u53d1\u4fe1\u53f7\uff0c\u8981\u6c42\u51fa\u52a8\u63f4\u519b\u3002</p><p>\u770b\u6765\u771f\u7684\u662f\u78b0\u4e0a\u9ebb\u70e6\u4e86\u3002</p><p>\u540e\u9762\u7684\u51e0\u8258\u6d6e\u7a7a\u8247\u4e5f\u90fd\u52a0\u901f\u8d76\u4e0a\u524d\u6765\uff0c\u548c\u743c\u6069\u6240\u5728\u7684\u8fd9\u8258\u8054\u6210\u5f2f\u6708\u9635\u578b\uff0c\u51c6\u5907\u8fce\u654c\u3002</p><p>\u4e0d\u77e5\u524d\u9762\u9047\u5230\u7684\u662f\u4ec0\u4e48\u654c\u4eba\u3002</p><p>\u4eff\u4f5b\u7279\u5730\u4e3a\u56de\u7b54\u743c\u6069\u7684\u7591\u8651\uff0c\u524d\u65b9\u53c8\u662f\u7830\u7684\u4e00\u58f0\uff0c\u7130\u706b\u5f39\u51fa\u3002\u8fd9\u6b21\u5374\u4e0d\u662f\u5411\u9634\u9b42\u57ce\u6c42\u63f4\uff0c\u660e\u663e\u662f\u5728\u5411\u540e\u65b9\u90e8\u961f\u4f20\u8fbe\u8baf\u606f\u3002\u53ea\u89c1\u9634\u6c89\u6c89\u7684\u8ff7\u96fe\u4e4b\u4e2d\uff0c\u90a3\u70b9\u7130\u706b\u5728\u534a\u7a7a\u4e2d\u7efd\u5f00\uff0c\u5316\u4f5c\u4e00\u5f20\u9762\u5177\u5f62\u72b6\u3002</p><p>\u4e00\u5f20\u9ed1\u8272\u7684\u5e03\u5236\u9762\u5177\u3002</p><p>\u743c\u6069\u5fc3\u4e2d\u5fae\u5fae\u4e00\u51db\uff0c\u540c\u65f6\u4ed6\u542c\u5230\u4e86\u8eab\u65c1\u540c\u4f34\u5012\u5438\u51b7\u6c14\u7684\u58f0\u97f3\u3002</p><p>\u9ed1\u8272\u7684\u9762\u5177\uff0c\u8fd9\u662f\u9634\u5f71\u4e0e\u76d7\u8d3c\u4e4b\u795e\u9a6c\u65af\u514b\u7684\u5723\u5fbd\u3002</p><p>\u654c\u4eba\u662f\u9a6c\u65af\u514b\u7684\u90e8\u4e0b\uff1f</p></article>"}}}]);
