(window.webpackJsonp=window.webpackJsonp||[]).push([[190],{213:function(p){p.exports={chapterTitle:null,title:"\u7b2c1166\u7ae0 \u624b\u673a\u6218\u7565",content:"<article><h2>\u7b2c1166\u7ae0 \u624b\u673a\u6218\u7565</h2><p>\u3000\u3000\u201c\u4f60\u8ba4\u4e3a\u8054\u901a\u4f1a\u6709\u591a\u5927\u7684\u80c3\u53e3\uff1f\u201d\u5728\u53bb\u89c1\u6768\u84c9\u5e73\u7684\u8def\u4e0a\uff0c\u5f20\u606a\u5750\u5728\u8f66\u91cc\u95ee\u5f20\u4e9a\u5e73\u3002</p><p>\u3000\u3000\u201c\u5e74\u521d\u65f6\uff0c\u8054\u901a\u5236\u5b9a\u4eca\u5e74\u8981\u53d1\u5c55\u4e00\u5343\u4e07\u65b0\u7528\u6237\u7684\u6269\u5f20\u8ba1\u5212\uff0c\u6c14\u52bf\u5b9e\u5728\u60ca\u4eba\uff0c\u201d\u5f20\u4e9a\u5e73\u8bf4\u9053\uff0c\u201c\u6211\u60f3\u8054\u901a\u8fd9\u6b21\u4ece\u6d77\u5916\u62ff\u56de\u8fd9\u4e48\u591a\u8d44\u91d1\uff0c\u4ed6\u4eec\u9ad8\u5c42\u7684\u5fc3\u601d\u5927\u6982\u53c8\u70ed\u4e86\u8d77\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd8\u771f\u662f\u5934\u75bc\u554a\uff0c\u4ed6\u4eec\u4e5f\u4e0d\u7f3a\u94b1\u4e86\uff0c\u5c31\u7f3a\u4e9b\u8010\u5fc3\u3002\u8981\u662f\u6709\u80fd\u534a\u5e74\u7684\u8010\u5fc3\u5728\u57fa\u7840\u4e0a\u591a\u4e0b\u4e9b\u5de5\u592b\uff0c\u8054\u901a\u7684\u5c40\u52bf\u5c31\u4f1a\u5927\u4e3a\u6539\u89c2\uff0c\u8fd9\u65f6\u5019\u4e0d\u5b9c\u592a\u5f3a\u52bf\u554a\u3002\u201d\u5f20\u606a\u6320\u4e86\u6320\u8111\u888b\uff0c\u76b1\u7740\u7709\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u8054\u901a\u4e00\u5b9a\u8981\u53bb\u9017\u6311\u79fb\u52a8\u516c\u53f8\u7684\u795e\u7ecf\uff0c\u8fd9\u4e5f\u662f\u65c1\u4eba\u65e0\u6cd5\u8bf4\u670d\u7684\u4e8b\u60c5\uff0c\u201d\u5f20\u4e9a\u5e73\u8bf4\u9053\uff0c\u201c\u4e5f\u8bb8\u8054\u901a\u9ad8\u5c42\u90fd\u6653\u5f97\u6253\u8349\u60ca\u86c7\u662f\u4e00\u62db\u81ed\u68cb\uff0c\u4f46\u662f\u8054\u901a\u8fd8\u57cb\u5934\u53bb\u505a\u57fa\u7840\u5de5\u4f5c\uff0c\u4e0d\u5728\u65b0\u7528\u6237\u589e\u6da8\u4e0a\u62ff\u51fa\u4e00\u4e9b\u6f02\u4eae\u7684\u6570\u636e\u51fa\u6765\uff0c\u5404\u65b9\u9762\u90fd\u4e0d\u597d\u4ea4\u4ee3\u2026\u2026\u8981\u7406\u89e3\u4ed6\u4eec\u7684\u96be\u5904\uff0c\u4ed6\u4eec\u53ef\u4e0d\u4ec5\u4ec5\u8981\u4e3a\u8054\u901a\u7684\u672a\u6765\u505a\u4ec0\u4e48\u4ea4\u5f85\u3002\u201d</p><p>\u3000\u3000\u201c\u55ef\u2026\u2026\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u77e5\u9053\u5f20\u4e9a\u5e73\u5bf9\u4e2d\u56fd\u7535\u4fe1\u4e1a\u7684\u53cc\u5be1\u5934\u5e02\u573a\u683c\u5c40\u4e0d\u6ee1\u610f\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7262\u9a9a\u597d\u53d1\uff0c\u5c31\u4ed6\u81ea\u5df1\u7684\u7acb\u573a\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u5e94\u8be5\u4e3a\u8054\u901a\u675e\u4eba\u5fe7\u5929\uff1b\u53ea\u662f\u8fd9\u79cd\u5c40\u9762\u672a\u5fc5\u5c31\u5bf9\u9526\u6e56\u6709\u5229\u3002</p><p>\u3000\u3000\u53bb\u5e74\u56fd\u5185\u79fb\u52a8\u901a\u8baf\u65b0\u589e\u7528\u6237\u624d\u4e00\u5343\u56db\u767e\u4e07\uff0c\u9884\u8ba1\u4eca\u5e74\u65b0\u589e\u7528\u6237\u4f1a\u8d85\u8fc7\u4e24\u5343\u4e94\u767e\u4e07\u4ee5\u4e0a\uff0c\u8054\u901a\u63d0\u51fa\u53d1\u5c55\u4e00\u5343\u4e07\u65b0\u7528\u6237\u7684\u8ba1\u5212\u8c8c\u4f3c\u4e0d\u5938\u5f20\uff0c\u5374\u8d85\u8fc7\u4e86\u8054\u901aGSM\u7f51\u7edc\u7684\u627f\u8f7d\u80fd\u529b\uff1b\u77e5\u9053\u8054\u901a\u53bb\u5e74\u5168\u5e74\u53d1\u5c55\u7684\u65b0\u7528\u6237\u6570\u91cf\u53ea\u6709\u4e24\u767e\u516d\u5341\u4e07\u3002</p><p>\u3000\u3000\u8054\u901a\u5e74\u521d\u63a5\u53d7\u5f20\u606a\u7684\u5efa\u8bae\u51b3\u5b9a\u6682\u7f13\u5b9e\u65bd\u4e4b\u524d\u5236\u5b9a\u7684\u6fc0\u8fdb\u6269\u5f20\u8ba1\u5212\uff0c\u5374\u6ca1\u6709\u5f7b\u5e95\u7684\u653e\u5f03\u6216\u8005\u62ff\u51fa\u4fee\u6539\u7a3f\u6765\u3002\u4eca\u5e741\u52307\u6708\u4efd\uff0c\u8054\u901a\u53d1\u5c55\u4e09\u767e\u4e07\u65b0\u7528\u6237\uff1b\u8981\u662f\u8054\u901a\u8fd8\u662f\u4ee5\u4e00\u5343\u4e07\u65b0\u7528\u6237\u4e3a\u76ee\u6807\uff0c\u4e5f\u5c31\u662f\u8bf4\u8054\u901a\u5728\u4eca\u5e74\u5269\u4e0b\u7684\u4e94\u4e2a\u6708\u65f6\u95f4\u91cc\u8fd8\u8981\u518d\u53d1\u5c55700\u4e07\u65b0\u7528\u6237\u3002</p><p>\u3000\u3000\u60f3\u5230\u8fd9\u4e2a\uff0c\u5f20\u606a\u5fae\u5fae\u7684\u76b1\u8d77\u7709\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u8054\u901a\u771f\u8981\u8fd9\u4e48\u641e\uff0c\u4e2d\u79fb\u52a8\u4e0d\u4f1a\u5750\u4ee5\u5f85\u6bd9\u7684\uff0c\u6211\u60f3\u4e2d\u79fb\u52a8\u5269\u4e0b\u4e94\u4e2a\u6708\u7684\u65b0\u7528\u6237\u589e\u6da8\u8ba1\u5212\u4e5f\u5c31\u4e03\u767e\u5230\u516b\u767e\u4e07\u4e4b\u95f4\uff0c\u4e2d\u79fb\u52a8\u600e\u4e48\u4f1a\u5141\u8bb8\u5728\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\u91cc\u5728\u65b0\u7528\u6237\u589e\u6da8\u6570\u636e\u4e0a\u7ed9\u8054\u901a\u62c9\u5e73\u2026\u2026\u4e2d\u79fb\u52a8\u80af\u5b9a\u4f1a\u56de\u51fb\uff0c\u4e5f\u591a\u534a\u6709\u53ef\u80fd\u5c06\u538b\u529b\u8f6c\u79fb\u5230\u6211\u4eec\u5934\u4e0a\u6765\u3002\u201d</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u5b8c\u5168\u77e5\u9053\u5c06\u4f1a\u6709\u600e\u6837\u7684\u538b\u529b\u4f1a\u8f6c\u79fb\u5230\u9526\u6e56\u7684\u5934\u4e0a\u6765\u3002</p><p>\u3000\u3000\u9526\u6e56\u8ddf\u8054\u901a\u6709\u89c1\u4e0d\u4e86\u5149\u7684\u79c1\u4e0b\u534f\u8bae\uff0c\u4e24\u5e74\u65f6\u95f4\u5185ESS\u57fa\u9891\u82af\u7247\u7ec4\u5b9e\u9645\u53ea\u63d0\u4f9b\u7ed9\u8054\u901a\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\uff0c\u4ee5\u5e2e\u52a9\u8054\u901a\u5728\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u83b7\u5f97\u4e2d\u79fb\u52a8\u65e0\u6cd5\u76f8\u6bd4\u7684\u6210\u672c\u4f18\u52bf\u3002\u4e2d\u79fb\u52a8\u8981\u60f3\u590d\u5236\u8054\u901a\u7684\u6a21\u5f0f\uff0c\u4e5f\u9700\u8981\u8ddf\u9526\u6e56\u8fdb\u884c\u5408\u4f5c\u624d\u884c\u3002</p><p>\u3000\u3000\u4e4b\u524d\u5728\u8054\u901a\u8fd8\u53ea\u662f\u6709\u91cd\u70b9\u7684\u6311\u9009\u5c11\u6570\u57ce\u5e02\u8fdb\u884c\u8bd5\u70b9\uff0c\u6ca1\u6709\u6253\u8349\u60ca\u86c7\uff0c\u5373\u4f7f\u6709\u4e9b\u6210\u5c31\uff0c\u8fd8\u6839\u672c\u65e0\u6cd5\u5a01\u80c1\u4e2d\u79fb\u52a8\u5728\u901a\u4fe1\u5e02\u573a\u4e0a\u7684\u5730\u4f4d\uff0c\u9526\u6e56\u4e5f\u5b8c\u5168\u53ef\u4ee5\u62ff\u4ea7\u80fd\u4e0d\u8db3\u5f53\u501f\u53e3\u62d2\u7edd\u8ddf\u4e2d\u79fb\u52a8\u5408\u4f5c\u3002</p><p>\u3000\u3000\u4f46\u662f\uff0c\u8054\u901a\u8981\u5168\u9762\u7684\u8ddf\u4e2d\u79fb\u52a8\u6253\u54cd\u4e89\u593a\u65b0\u7528\u6237\u7684\u6218\u4e89\uff0c\u60c5\u52bf\u5c31\u5b8c\u6210\u4e0d\u4e00\u6837\u4e86\uff1b\u9526\u6e56\u5c31\u65e0\u6cd5\u518d\u62ff\u8fd9\u6837\u7684\u501f\u53e3\u53bb\u6577\u884d\u4e2d\u79fb\u52a8\u2014\u2014\u8fd9\u4e8b\u60c5\u60f3\u60f3\u5c31\u8ba9\u4eba\u5934\u75bc\u3002</p><p>\u3000\u3000\u201c\u6211\u770b\u6211\u4eec\u8fd8\u662f\u9700\u8981\u7ee7\u7eed\u9650\u5236\u4e00\u4e0b\u8054\u901a\u7684\u6b65\u4f10\uff0cESS\u57fa\u9891\u82af\u7247\u7ec4\uff0c\u6211\u4eec\u4eca\u5e74\u5c31\u53ea\u518d\u4f9b\u5e94\u4e09\u767e\u4e07\u5957\uff0c\u8054\u901a\u8981\u53d1\u5c55\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\uff0c\u4e5f\u4e0d\u80fd\u5c06\u773c\u775b\u90fd\u76ef\u5728\u4e2d\u4f4e\u7aef\u624b\u673a\u4e0a\u2026\u2026\u201d\u5f20\u4e9a\u5e73\u8bf4\u9053\uff0c\u201c\u96c6\u56e2\u53ef\u4ee5\u538b\u4f4e\u5229\u6da6\u76f4\u63a5\u5411\u8054\u901a\u63d0\u4f9b\u4e00\u90e8\u5206\u4e2d\u9ad8\u7aef\u624b\u673a\uff0c\u4e2d\u79fb\u52a8\u82e5\u662f\u6709\u9700\u8981\uff0c\u4e5f\u53ef\u4ee5\u538b\u4f4e\u5229\u6da6\u5411\u4e2d\u79fb\u52a8\u63d0\u4f9b\u4e00\u90e8\u5206\u4e2d\u9ad8\u7aef\u624b\u673a\u3002\u4e0d\u8fc7\uff0c\u6211\u60f3\u554a\uff0c\u4e2d\u79fb\u52a8\u8981\u53d1\u5c55\u4e2d\u9ad8\u7aef\u7684\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\uff0c\u672a\u5fc5\u4f1a\u8ddf\u6211\u4eec\u5408\u4f5c\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u770bESS\u57fa\u9891\u82af\u7247\u7ec4\u4f9b\u5e94\u4e0a\uff0c\u6211\u4eec\u8fd8\u8981\u518d\u6536\u7d27\u4e00\u4e9b\uff0c\u6bcf\u6708\u4f9b\u5e94\u4e94\u5341\u4e07\u5957\u5c31\u591f\u4e86\uff0c\u79d1\u738b\u9ad8\u79d1\u3001\u8054\u4fe1\u6bcf\u5bb6\u5404\u4e8c\u5341\u4e94\u4e07\u5957\u2026\u2026\u4f60\u662f\u5143\u5668\u4ef6\u90e8\u603b\u88c1\uff0c\u4f60\u4eca\u5929\u53ef\u4ee5\u76f4\u63a5\u8ddf\u6768\u84c9\u5e73\u644a\u724c\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u8054\u901a\u82e5\u771f\u60f3\u5728\u4eca\u5e74\u5269\u4e0b\u6765\u7684\u4e94\u4e2a\u6708\u65f6\u95f4\u91cc\u53d1\u5c55\u4e03\u767e\u4e07\u7684\u65b0\u7528\u6237\uff0c\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u81f3\u5c11\u6bcf\u6708\u8981\u627f\u62c5\u516b\u5341\u4e07\u5230\u4e00\u767e\u4e07\u7684\u7528\u6237\u589e\u6da8\u8ba1\u5212\uff0c\u9526\u6e56\u53ea\u627f\u8bfa\u653e\u5f00\u4f9b\u5e94\u4e94\u5341\u4e07\u5957\uff0c\u8054\u901a\u6bcf\u6708\u8fd8\u5c06\u8981\u6709\u4e09\u5341\u4e07\u5230\u4e94\u5341\u4e07\u7684\u7f3a\u53e3\u8981\u586b\u8865\uff1b\u5373\u4f7f\u5982\u6b64\uff0c\u76f8\u6bd4\u4eca\u5e74\u524d\u4e03\u4e2a\u6708\u5e73\u5747\u6bcf\u6708\u5341\u4e94\u4e07\u5957\u7684\u4f9b\u5e94\u91cf\uff0c\u589e\u6da8\u5e45\u5ea6\u5df2\u7ecf\u7b97\u662f\u76f8\u5f53\u53ef\u89c2\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u6b21\u4e0d\u662f\u4e13\u7a0b\u4e3a\u8fd9\u4ef6\u4e8b\u5230\u5317\u4eac\u6765\uff0c\u5750\u8f66\u5230\u8054\u901a\u603b\u90e8\u6768\u84c9\u5e73\u7684\u529e\u516c\u5ba4\u91cc\u7a0d\u5750\u7247\u523b\uff0c\u5c31\u5148\u544a\u8f9e\u79bb\u5f00\uff0c\u5177\u4f53\u7684\u4f1a\u6664\u7531\u5f20\u4e9a\u5e73\u8d1f\u8d23\u3002</p><p>\u3000\u3000\u5728\u56de\u522b\u5885\u7684\u8def\u4e0a\uff0c\u5f20\u606a\u7ed9\u9648\u9759\u6253\u7535\u8bdd\u63d0\u4f9b\u8054\u901a\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u8981\u6536\u7d27\u4e00\u4e0b\uff0c\u4e2d\u79fb\u52a8\u4e5f\u65e0\u6cd5\u8d28\u7591\u3002\u6362\u4f5c\u522b\u4eba\uff0c\u90fd\u6068\u4e0d\u5f97\u4e00\u4e0b\u5b50\u5c06\u6240\u6709\u7684\u94b1\u90fd\u8d5a\u5b8c\u2026\u2026\u201d\u9648\u9759\u5728\u7535\u8bdd\u90a3\u5934\u5a07\u7b11\u7740\u8bf4\u9053\uff0c\u53c8\u95ee\u5f20\u606a\u51e0\u65f6\u56de\u5efa\u90ba\uff0c\u201c\u542c\u8bf4\u9648\u5983\u84c9\u90a3\u53ea\u5c0f\u5996\u7cbe\u4ee5\u540e\u5c31\u5e38\u5e74\u8ddf\u4f60\u8eab\u8fb9\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u9648\u5983\u84c9\u4e00\u773c\uff0c\u5c06\u624b\u673a\u542c\u7b52\u53c8\u8d34\u7d27\u4e86\u8033\u6735\u4e00\u4e9b\uff0c\u514d\u5f97\u9648\u9759\u8bf4\u8bdd\u7684\u58f0\u97f3\u7ed9\u5979\u542c\u8fc7\u53bb\uff0c\u53c8\u5047\u6a21\u5047\u6837\u7684\u8ddf\u9648\u9759\u5728\u7535\u8bdd\u8ba8\u8bba\u5546\u52a1\u4e0a\u7684\u8bdd\u9898\uff1a\u201c\u5728\u6280\u672f\u3001\u751f\u4ea7\u4ee5\u53ca\u8425\u9500\u7b49\u8bf8\u591a\u65b9\u9762\u6ca1\u6709\u505a\u597d\u5145\u8db3\u7684\u51c6\u5907\u4e4b\u524d\uff0c\u8fc7\u5ea6\u7684\u6269\u5f20\u5e76\u4e0d\u80fd\u7b97\u662f\u4e00\u4ef6\u597d\u4e8b\uff0c\u94b1\u662f\u8d5a\u4e0d\u5b8c\u7684\uff0c\u8981\u60f3\u7740\u7ec6\u6c34\u957f\u6d41\u2026\u2026\u201d\u53c8\u8c08\u4e86\u4e00\u4e9b\u91d1\u5c71\u5de5\u5382\u7684\u4e8b\u60c5\uff0c\u5c31\u6302\u4e86\u7535\u8bdd\u3002\u53c8\u8ddf\u8054\u4fe1\u7684\u8096\u745e\u6c11\u901a\u4e86\u7535\u8bdd\uff0c\u4e0e\u8096\u745e\u6c11\u901a\u7535\u8bdd\u53ea\u662f\u8ddf\u4ed6\u8bf4ESS\u57fa\u9891\u82af\u7247\u7ec4\u867d\u7136\u80fd\u6210\u529f\u5347\u7ea7\u5230\u4e8c\u4ee3\u589e\u5f3a\u578b\uff0c\u4f46\u662f\u4ea7\u80fd\u8fd8\u662f\u6709\u9650\uff0c\u65e0\u6cd5\u655e\u5f00\u53e3\u5b50\u4f9b\u5e94\u3002\u9664\u4e86\u79d1\u738b\u9ad8\u79d1\u3001\u8054\u4fe1\u4e4b\u5916\uff0c\u8fd8\u9700\u8981\u5411\u79d1\u738b\u4f9b\u5e94\u4e00\u5b9a\u91cf\u7684ESS\u57fa\u9891\u82af\u7247\u7ec4\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u6127\u3001\u5f20\u4e9a\u5e73\u7b49\u4eba\u5728\u5236\u5b9a\u4eca\u5e74\u624b\u673a\u9500\u552e\u76ee\u6807\u65f6\uff0c\u56fd\u5185\u76ee\u6807\u5b9a\u5728\u516b\u767e\u4e07\u53f0\uff0c\u73b0\u5728\u524d\u516d\u4e2a\u6708\u5df2\u7ecf\u5b8c\u6210\u56db\u767e\u4e07\u53f0\u7684\u9500\u552e\uff0c\u5373\u4f7f\u6ca1\u6709\u7279\u522b\u7684\u8425\u9500\u8ba1\u5212\uff0c\u6309\u7167\u81ea\u7136\u589e\u6da8\u5e45\u5ea6\uff0c\u4eca\u5e74\u5b8c\u6210\u4e00\u5343\u4e07\u53f0\u7684\u9500\u552e\u4e0d\u4f1a\u6709\u592a\u5927\u7684\u96be\u5ea6\u3002\u9884\u8ba1\u56fd\u5185\u624b\u673a\u5e02\u573a\u5168\u5e74\u603b\u9500\u552e\u5c06\u8fbe\u5230\u4e94\u5343\u4e07\u53f0\uff0c\u6210\u4e3a\u4ec5\u6b21\u4e8e\u7f8e\u56fd\u3001\u6b27\u6d32\u4ee5\u53ca\u65e5\u672c\u7684\u7b2c\u56db\u5927\u624b\u673a\u5e02\u573a\uff0c\u9526\u6e56\u5728\u56fd\u5185\u5e02\u573a\u7684\u5360\u6709\u7387\u5c06\u8fbe\u523020\uff05\u3002\u5176\u4ed6\u56fd\u4ea7\u624b\u673a\u5382\u5546\u53c8\u5c06\u4ee5\u8054\u4fe1\u3001\u79d1\u738b\u9ad8\u79d1\u4e3a\u4ee3\u8868\uff0c\u8fd9\u4e24\u5bb6\u4f01\u4e1a\u4eca\u5e74\u7684\u624b\u673a\u9500\u91cf\u90fd\u5c06\u7a81\u7834\u4e09\u767e\u4e07\u53f0\uff0c\u5176\u4ed6\u5341\u4e8c\u5bb6\u56fd\u4ea7\u624b\u673a\u5382\u5546\u7684\u9500\u552e\u603b\u91cf\u4e5f\u5c06\u7a81\u7834\u56db\u767e\u4e07\u53f0\uff0c\u4e5f\u8bf4\u610f\u5473\u7740\u4eca\u5e74\u56fd\u5185\u624b\u673a\u5e02\u573a\u7684\u56fd\u4ea7\u5316\u7387\u5c06\u7a81\u783440\uff05\u3002</p><p>\u3000\u300040\uff05\u7684\u56fd\u4ea7\u5316\u7387\u5bf9\u4e8e\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u57fa\u4e9a\u3001\u98de\u5229\u6d66\u3001\u4e09\u661f\u3001\u7d22\u5c3c\u3001\u7231\u7acb\u4fe1\u7b49\u56fd\u5916\u624b\u673a\u5382\u5546\u6765\u8bf4\u53ea\u6015\u662f\u4e00\u4e2a\u5f88\u96be\u63a5\u53d7\u7684\u6570\u636e\u3002</p><p>\u3000\u3000\u53ea\u6015\u7b49\u4e2d\u79fb\u52a8\u7684GPRS\u7f51\u7edc\u5347\u7ea7\u5b8c\u6210\uff0c\u673a\u6d77\u5927\u6218\u5c31\u4f1a\u5f7b\u5e95\u5c55\u5f00\u5427\uff1f\u6bd5\u7adf\u5728GPRS\u901a\u8baf\u7f51\u7edc\u4e0a\u624b\u673a\u80fd\u591f\u6709\u66f4\u4e30\u5bcc\u591a\u5f69\u7684\u5e94\u7528\uff0c\u8fd9\u65f6\u5019\u5c31\u80fd\u5b8c\u5168\u53d1\u6325\u6d77\u5916\u624b\u673a\u5382\u5546\u5728\u8bbe\u8ba1\u4e0e\u6280\u672f\u4e0a\u7684\u5b9e\u529b\u3002</p><p>\u3000\u3000\u4e2d\u79fb\u52a8\u73b0\u5728\u5df2\u7ecf\u5728\u591a\u5ea7\u4e2d\u5fc3\u57ce\u5e02\u5347\u7ea7GPRS\u901a\u8baf\u7f51\u7edc\u5e76\u8bd5\u9a8c\u6027\u7684\u5f00\u901a\u4e00\u4e9b\u670d\u52a1\uff0c\u79bb\u5168\u9762\u5f00\u901aGPRS\u7f51\u7edc\u7684\u65f6\u95f4\u5df2\u7ecf\u4e0d\u957f\u4e86\uff0c\u8bfa\u57fa\u4e9a\u3001\u6469\u6258\u7f57\u62c9\u4ee5\u53ca\u4e09\u661f\u7b49\u6d77\u5916\u5382\u5546\u4e5f\u90fd\u78e8\u62f3\u970d\u970d\uff0c\u90fd\u5148\u671f\u63a8\u51fa\u5e94\u7528GPRS\u7f51\u7edc\u7684\u9ad8\u7aef\u624b\u673a\uff0c\u5c31\u7b49\u7740\u7f51\u7edc\u5168\u9762\u5347\u7ea7\u540e\u597d\u65bd\u5c55\u62f3\u811a\u3002</p><p>\u3000\u3000\u5f20\u606a\u51e0\u4e4e\u80fd\u60f3\u8c61\u5230\u673a\u6d77\u5927\u6218\u7684\u6b8b\u9177\u6027\uff0c\u5728\u4ed6\u8bb0\u5fc6\u4e2d\u7684\u524d\u5c18\u5f80\u4e8b\u65f6\uff0c\u624b\u673a\u56fd\u4ea7\u5316\u7387\u4e00\u5ea6\u63a5\u8fd156\uff05\uff0c\u4e0d\u8fc7\u5728\u6d77\u5916\u624b\u673a\u5382\u5546\u65bd\u5c55\u7684\u673a\u6d77\u6218\u672f\u4e0b\uff0c\u51e0\u4e4e\u9020\u6210\u56fd\u4ea7\u624b\u673a\u5382\u5546\u5168\u884c\u4e1a\u4e8f\u635f\uff0c\u5e76\u4f7f\u56fd\u4ea7\u5316\u7387\u964d\u523030\uff05\u4ee5\u4e0b\u3002\u5c31\u7b9730\uff05\u7684\u56fd\u4ea7\u5316\u7387\u4e5f\u662f\u6c34\u5206\u7684\uff0c\u56e0\u4e3a\u5f53\u65f6\u5f88\u591a\u56fd\u4ea7\u624b\u673a\u5382\u5546\u6839\u672c\u5c31\u6ca1\u6709\u751f\u4ea7\u80fd\u529b\uff0c\u53ea\u662f\u62ff\u5230\u624b\u673a\u724c\u7167\u540e\u7ed9\u56fd\u5916\u624b\u673a\u505a\u8d34\u724c\u751f\u610f\u3002\u624b\u673a\u5404\u90e8\u5206\u96f6\u4ef6\u5927\u591a\u4ece\u65e5\u97e9\u4e24\u56fd\u8fd0\u8fdb\u6765\uff0c\u4e24\u53ea\u96c6\u88c5\u7bb1\uff0c\u4e00\u53ea\u96c6\u88c5\u7bb1\u88c5\u673a\u8eab\uff0c\u4e00\u53ea\u96c6\u88c5\u7bb1\u88c5\u7535\u8bdd\u5929\u7ebf\uff0c\u88c5\u4e0a\u7acb\u5373\u53ef\u4ee5\u4e0a\u67dc\u53f0\uff0c\u8fd9\u6837\u751f\u4ea7\u51fa\u6765\u7684\u624b\u673a\u4e5f\u53eb\u56fd\u4ea7\u624b\u673a\u3002</p><p>\u3000\u3000\u5c31\u7b97\u4eca\u5e74\u7684\u56fd\u4ea7\u5316\u7387\u8fbe\u523040\uff05\uff0c\u6838\u5fc3\u5143\u4ef6\u5668\u7684\u56fd\u4ea7\u5316\u7387\u8fd8\u662f\u76f8\u5f53\u4f4e\uff0c\u4e3b\u8981\u5c31\u662f\u57fa\u9891\u82af\u7247\u7ec4\uff0c\u4e5f\u5c31\u5411\u8054\u901a\u63d0\u4f9b\u7684ESS\u5b9a\u5236\u624b\u673a\u80fd\u79f0\u5f97\u771f\u6b63\u7684\u56fd\u4ea7\u624b\u673a\u3002</p><p>\u3000\u3000\u56fd\u5185\u624b\u673a\u5e02\u573a\u5dee\u4e0d\u591a\u5c06\u4e24\u5343\u4e94\u767e\u5143\u4ee5\u4e0b\u7684\u624b\u673a\u5f52\u7c7b\u4e3a\u4e2d\u4f4e\u7aef\u624b\u673a\uff0c\u8054\u4fe1\u3001\u79d1\u738b\u9ad8\u79d1\u7684ESS\u624b\u673a\u5e02\u573a\u9500\u552e\u4ef7\u683c\u4e3a\u4e00\u5343\u4e8c\u767e\u5143\u5230\u4e00\u5343\u4e94\u767e\u5143\u4e0d\u7b49\uff0c\u5411\u8054\u901a\u4f9b\u5e94\u7684\u5b9a\u5236\u624b\u673a\u4ef7\u683c\u4ece\u4e00\u5343\u5143\u5230\u4e00\u5343\u4e8c\u767e\u5143\u4e0d\u7b49\uff0c\u53ef\u4ee5\u8bf4\u662f\u5f53\u524d\u56fd\u5185\u624b\u673a\u5e02\u4e2d\u7684\u7edd\u5bf9\u4f4e\u7aef\u624b\u673a\u3002</p><p>\u3000\u3000\u82e5\u662f\u4ee5\u9500\u552e\u6570\u91cf\u8ba1\u7b97\uff0cESS\u624b\u673a\u5c06\u5360\u4eca\u5e74\u56fd\u5185\u624b\u673a\u5e02\u573a\u7ea68\uff05\u7684\u4efd\u989d\uff0c\u8fd9\u5df2\u7ecf\u662f\u76f8\u5f53\u4e0d\u9519\u7684\u6210\u7ee9\u4e86\u3002\u4f46\u662f\u8981\u4ee5\u9500\u552e\u989d\u8ba1\u5212\uff0cESS\u624b\u673a\u9500\u552e\u989d\u8fd8\u5360\u4e0d\u5230\u4eca\u5e74\u56fd\u5185\u624b\u673a\u5e02\u573a\u9500\u552e\u603b\u989d\u76843\uff05\uff0c\u5c31\u6709\u4e9b\u5fae\u4e0d\u8db3\u9053\u4e86\u3002</p><p>\u3000\u3000\u9664\u4e86\u8054\u4fe1\u3001\u79d1\u738b\u9ad8\u79d1\u5916\uff0c\u9526\u6e56\u8fd8\u5411\u79d1\u738b\u63d0\u4f9bESS\u57fa\u9891\u82af\u7247\u7ec4\u7528\u4e8e\u751f\u4ea7\u9500\u5f80\u6d77\u5916\u7684\u624b\u673a\u3002\u79d1\u738b\u8fd1\u51e0\u4e2a\u6708\u6765\u505a\u4e86\u4e9b\u5f00\u62d3\u6027\u7684\u5de5\u4f5c\uff0c\u4e5f\u6bd5\u7adf\u57fa\u4e8eESS\u57fa\u9891\u82af\u7247\u7ec4\u751f\u4ea7\u7684\u624b\u673a\u80fd\u591f\u5b9e\u73b0\u5b8c\u5168\u7684\u56fd\u4ea7\u5316\uff0c\u5236\u9020\u6210\u672c\u4f4e\u5ec9\uff0c\u8d28\u91cf\u53c8\u80fd\u8fc7\u5173\uff0c\u5728\u90a3\u4e9b\u5e02\u573a\u6f5c\u529b\u6bd4\u4e2d\u56fd\u8fd8\u7a0d\u5dee\u7684\u56fd\u5bb6\u4e0e\u5730\u533a\uff0c\u81ea\u7136\u4e5f\u5927\u53d7\u6b22\u8fce\u3002\u79d1\u738b\u622a\u6b62\u5230\u4e03\u6708\u4e2d\u65ec\uff0c\u5df2\u7ecf\u7d2f\u8ba1\u51fa\u53e3\u8fd1\u4e8c\u5341\u4e07\u90e8\u624b\u673a\uff0c\u6210\u7ee9\u751a\u81f3\u8981\u8fdc\u8fdc\u597d\u8fc7\u90a3\u4e9b\u4e1a\u7ee9\u4e2d\u7b49\u7684\u56fd\u4ea7\u624b\u673a\u5382\u5546\u3002</p><p>\u3000\u3000\u672a\u6765\u4fbf\u643a\u5f0f\u6570\u5b57\u5a31\u4e50\u5e73\u53f0\u90fd\u6709\u5411\u624b\u673a\u96c6\u4e2d\u7684\u8d8b\u52bf\uff0c\u901a\u8fc7\u8fd9\u4e9b\u5e74\u7684\u52aa\u529b\uff0c\u6a61\u6811\u56ed\u7684\u624b\u673a\u7814\u53d1\u90e8\u95e8\u6210\u529f\u7684\u5f00\u53d1\u51fa\u57fa\u9891\u82af\u7247\u52a0\u5fae\u5904\u7406\u5668\u7684\u53cc\u6838\u624b\u673a\u786c\u4ef6\u67b6\u6784\uff0c\u5e76\u5728\u6b64\u786c\u4ef6\u67b6\u6784\u57fa\u7840\u4e0a\u6210\u529f\u7684\u5c06iplayer\u64ad\u653e\u5668\u529f\u80fd\u96c6\u6210\u5230\u624b\u673a\u4e0a\u3002\u6a61\u6811\u56ed\u5728\u5f69\u8272STN\u6db2\u6676\u5c4f\u6280\u672f\u4e0a\u4e5f\u6709\u7a81\u7834\uff0c\u4e0d\u7528\u62c5\u5fc3\u5728\u624b\u673a\u5f69\u5c4f\u65f6\u4ee3\u6765\u4e34\u4e4b\u540e\u4f1a\u7ed9\u8bfa\u57fa\u4e9a\u3001\u6469\u6258\u7f57\u62c9\u7ed9\u7529\u5f00\u3002</p></article>"}}}]);
