(window.webpackJsonp=window.webpackJsonp||[]).push([[191],{214:function(p){p.exports={chapterTitle:null,title:"\u7b2c191\u7ae0 \u6240\u8c13\u7684\u516c\u9053",content:"<article><h2>\u7b2c191\u7ae0 \u6240\u8c13\u7684\u516c\u9053</h2><p>\u3000\u3000\u5085\u4fca\u6253\u4e86\u4e24\u4e09\u4e2a\u7535\u8bdd\uff0c\u624d\u6258\u4eba\u8054\u7cfb\u4e0a\u9a6c\u6d77\u9f99\u7684\u5bb6\u4eba\u3002\u9a6c\u6d77\u9f99\u59bb\u5b50\u4e0e\u4ed6\u4e24\u4e2a\u59bb\u5f1f\u8d76\u8fc7\u6765\uff0c\u4ed6\u8fd8\u5728\u6025\u6551\u5ba4\u6551\u6cbb\uff0c\u901a\u8fc7\u9a6c\u6d77\u9f99\u59bb\u5b50\u7684\u54ed\u6ce3\uff0c\u5f20\u606a\u624d\u4e86\u89e3\u4e8b\u60c5\u7684\u6765\u9f99\u53bb\u8109\u3002\u9a6c\u6d77\u9f99\u7b97\u662f\u5165\u8d58\uff0c\u5c31\u4f4f\u5728\u4ed6\u4e08\u4eba\u5bb6\uff0c\u8ddf\u4ed6\u4e24\u4e2a\u59bb\u5f1f\u7684\u5bb6\u4eba\u8fd8\u6709\u4e08\u4eba\u3001\u4e08\u6bcd\u90fd\u4f4f\u5728\u8857\u5e02\u4e0e\u524d\u8857\u4ea4\u53c9\u53e3\u7684\u4e8c\u5c42\u5c0f\u697c\u91cc\u3002\u5e02\u91cc\u53ec\u5f00\u62c6\u8fc1\u52a8\u5458\u4f1a\uff0c\u62c6\u8fc1\u516c\u53f8\u6839\u672c\u5c31\u6ca1\u6709\u505a\u4ec0\u4e48\u5de5\u4f5c\uff0c\u53ea\u662f\u7ed9\u5404\u5bb6\u5404\u6237\u53d1\u4e86\u4e00\u4efd\u901a\u77e5\uff0c\u901a\u77e5\u8fd9\u4e00\u5730\u5757\u4f1a\u57287\u67081\u65e5\u524d\u5b8c\u5168\u62c6\u9664\uff0c\u8981\u6c42\u5404\u5bb6\u5404\u6237\u5230\u62c6\u8fc1\u516c\u53f8\u7b7e\u7f6e\u62c6\u8fc1\u534f\u8bae\uff0c\u901a\u77e5\u4e0a\u7ed9\u51fa\u7684\u5efa\u7b51\u9762\u79ef\u3001\u62c6\u8fc1\u8d39\u8bc4\u4f30\u4e5f\u6839\u672c\u6ca1\u6709\u7ecf\u8fc7\u5546\u91cf\uff0c\u53ea\u662f\u62c6\u8fc1\u516c\u53f8\u968f\u610f\u4f30\u7b97\uff0c\u6ca1\u6709\u7ed9\u62c6\u8fc1\u6237\u5546\u91cf\u7684\u4f59\u5730\u3002\u56e0\u4e3a\u9a6c\u6d77\u9f99\u5f53\u8fc7\u51e0\u5e74\u5175\uff0c\u62c6\u8fc1\u6237\u5c31\u9009\u4ed6\u4e0e\u53e6\u5916\u51e0\u540d\u4ee3\u8868\u8ddf\u62c6\u8fc1\u516c\u53f8\u4ea4\u6d89\u3002\u62c6\u8fc1\u516c\u53f8\u7684\u7ecf\u7406\u662f\u4e2a\u86ee\u6a2a\u7684\u4eba\u7269\uff0c\u5bf9\u4ed6\u4eec\u8fd9\u4e9b\u7ed9\u63a8\u9009\u51fa\u6765\u7684\u4ee3\u8868\u8bf4\uff1a\u201c\u67aa\u6253\u51fa\u5934\u9e1f\uff0c\u8001\u5b50\u5c31\u8ba9\u4f60\u4eec\u77e5\u9053\u8fd9\u53e5\u8bdd\u7684\u9053\u7406\u2026\u2026\u201d\u4ed6\u4eec\u51e0\u5bb6\u7684\u62c6\u8fc1\u8d39\u6beb\u65e0\u7406\u7531\u7684\u90fd\u7ed9\u62c6\u8fc1\u516c\u53f8\u524a\u51cf\u4e86\u4e24\u4e07\uff0c\u8fd8\u626c\u8a00\u8bf4\uff1a\u201c\u8c01\u4ed6\u5988\u521a\u627e\u4e0a\u95e8\u6765\u4e00\u6b21\uff0c\u5c31\u6263\u4ed6\u5a18\u7684\u4e24\u4e07\uff0c\u6263\u5230\u4f60\u4eec\u4e00\u5206\u94b1\u90fd\u62ff\u4e0d\u5230\uff0c\u4f60\u4eec\u4e5f\u5f97\u7ed9\u8001\u5b50\u8001\u5b9e\u7684\u62c6\u6389\u2026\u2026\u201d</p><p>\u3000\u3000\u9a6c\u6d77\u9f99\u4e0d\u4fe1\u8fd9\u4e2a\u90aa\uff0c\u518d\u6b21\u627e\u5230\u62c6\u8fc1\u516c\u53f8\u8bf4\u7406\uff0c\u6070\u9022\u8d75\u9526\u8363\u4e5f\u5728\u62c6\u8fc1\u516c\u53f8\u91cc\uff0c\u9a6c\u6d77\u9f99\u4e0e\u62c6\u8fc1\u516c\u53f8\u4e89\u6267\u4e0d\u4e0b\uff0c\u8d77\u4e86\u80a2\u4f53\u51b2\u7a81\u3002\u9a6c\u6d77\u9f99\u67b6\u4e0d\u4f4f\u4eba\u591a\uff0c\u4eba\u7ed9\u6263\u5728\u62c6\u8fc1\u516c\u53f8\uff0c\u8d75\u9526\u5bb9\u8ba9\u4eba\u67b6\u7740\u9a6c\u6d77\u9f99\u5728\u62c6\u8fc1\u534f\u8bae\u4e0a\u6309\u4e0b\u624b\u5370\uff0c\u8fd9\u624d\u901a\u77e5\u8b66\u65b9\u5c06\u9a6c\u6d77\u9f99\u62d8\u8d70\u3002</p><p>\u3000\u3000\u90fd\u8bf4\u62c6\u8fc1\u516c\u53f8\u7ed9\u5f00\u53d1\u5546\u5145\u5f53\u6253\u624b\uff0c\u4f46\u662f\u9526\u57ce\u8fd9\u6b21\u9009\u62e9\u7684\u62c6\u8fc1\u516c\u53f8\u662f\u5916\u7ecf\u8d38\u59d4\u4e0b\u5c5e\u7684\u5355\u4f4d\uff0c\u662f\u4e07\u52c7\u7684\u5bb6\u79c1\uff0c\u6709\u4e9b\u4e8b\u60c5\u8fd8\u5f97\u8981\u8d75\u9526\u8363\u4eb2\u81ea\u89e3\u51b3\u3002</p><p>\u3000\u3000\u9a6c\u6d77\u9f99\u7ed9\u8b66\u65b9\u5e26\u8d70\u65f6\uff0c\u8bf4\u4e86\u72e0\u8bdd\uff0c\u8ba9\u8d75\u9526\u8363\u5c0f\u5fc3\u4ed6\u5bb6\u91cc\u4eba\u3002\u9a6c\u6d77\u9f99\u7ed9\u62d8\u7559\u671f\u95f4\uff0c\u4ed6\u5bb6\u90a3\u7247\u5df2\u7ecf\u7ed9\u5f3a\u5236\u62c6\u9664\u3002\u5f3a\u62c6\u65f6\uff0c\u5e02\u91cc\u8c03\u52a8\u4e00\u5343\u591a\u4eba\u7684\u5e9e\u5927\u5de5\u4f5c\u7ec4\u914d\u5408\u62c6\u8fc1\u516c\u53f8\u884c\u52a8\uff0c\u5f53\u65f6\u62c6\u8fc1\u6237\u7ec4\u7ec7\u8d77\u6765\u7ed3\u6210\u4eba\u5899\u8bd5\u56fe\u963b\u6b62\u62c6\u8fc1\u961f\u4f0d\u5165\u5185\uff0c\u4f46\u662f\u5c31\u5728\u5802\u5802\u526f\u5e02\u957f\u4e07\u52c7\u7684\u9762\u524d\uff0c\u62c6\u8fc1\u961f\u4f0d\u8c03\u6765\u6d88\u9632\u7528\u9ad8\u538b\u6c34\u67aa\uff0c\u51b2\u51fb\u4eba\u5899\uff0c\u9a6c\u6d77\u9f99\u4ed6\u4e08\u592b\u4e0e\u5341\u51e0\u540d\u8001\u5f31\u5987\u5b7a\u7ed9\u6c34\u67aa\u51b2\u660f\u5728\u5730\u3002\u62c6\u8fc1\u4eba\u5458\u53ea\u662f\u5c06\u88ab\u6c34\u67aa\u51b2\u6563\u7684\u4eba\u62d6\u5230\u5de5\u4f5c\u7ec4\u6210\u7684\u4eba\u5899\u5916\u3002</p><p>\u3000\u3000\u9a6c\u6d77\u9f99\u51fa\u6765\u4e4b\u540e\uff0c\u4ed6\u5bb6\u7684\u5c0f\u697c\u8fd8\u6ca1\u6709\u6700\u540e\u62c6\u9664\uff0c\u5f53\u65f6\u7ed9\u5f3a\u62c6\u65f6\uff0c\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u5c06\u4e1c\u897f\u642c\u51fa\u6765\uff0c\u4ed6\u60f3\u6f5c\u56de\u53bb\u62ff\u8d77\u4e1c\u897f\u51fa\u6765\u3002\u5177\u4f53\u5982\u4f55\u4e0e\u8d75\u9526\u8363\u53ca\u4ed6\u7684\u6253\u624b\u649e\u4e0a\uff0c\u8fd9\u5c31\u8981\u7b49\u9a6c\u6d77\u9f99\u9192\u8fc7\u6765\u4e4b\u540e\u624d\u77e5\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u5085\u4fca\u5148\u9001\u65bd\u536b\u5fe0\u56de\u5bb6\uff0c\u4ed6\u4e0e\u5085\u4fca\u5728\u533b\u9662\u7b49\u5230\u51cc\u6668\u4e24\u70b9\u949f\uff0c\u533b\u9662\u5bf9\u9a6c\u6d77\u9f99\u7684\u62a2\u6551\u624d\u544a\u4e00\u6bb5\u843d\uff0c\u5de6\u5c0f\u81c2\u9aa8\u6298\uff0c\u80f8\u808b\u5904\u9aa8\u6298\u591a\u5904\uff0c\u8138\u90e8\u867d\u7136\u9f3b\u9752\u8138\u80bf\uff0c\u5374\u6ca1\u6709\u906d\u53d7\u4ec0\u4e48\u91cd\u521b\u3002\u5012\u4e0d\u662f\u8d75\u9526\u8363\u4e0d\u591f\u72e0\u5fc3\uff0c\u800c\u662f\u5f20\u606a\u90a3\u65f6\u5c06\u8f66\u505c\u5230\u8b66\u6212\u7ebf\u5916\uff0c\u4e24\u540d\u6253\u624b\u60f3\u51fa\u6765\u5c06\u5f20\u606a\u4ed6\u4eec\u8d76\u8d70\uff0c\u5374\u8ba9\u9a6c\u6d77\u9f99\u501f\u673a\u51b2\u51fa\u5c4b\u5b50\u5f15\u8d77\u5f20\u606a\u4ed6\u4eec\u7684\u6ce8\u610f\u3002</p><p>\u3000\u3000\u636e\u9a6c\u6d77\u9f99\u6240\u8ff0\uff0c\u5f20\u606a\u5fc3\u60f3\u62c6\u8fc1\u516c\u53f8\u5c06\u90a3\u4e00\u7247\u623f\u5b50\u5dee\u4e0d\u591a\u5df2\u7ecf\u62c6\u5149\uff0c\u4ec5\u9a6c\u6d77\u9f99\u5bb6\u4e0e\u9644\u8fd1\u7684\u4e24\u680b\u697c\u623f\u6682\u65f6\u4fdd\u7559\u4e0b\u6765\uff0c\u591a\u534a\u662f\u8d75\u9526\u8363\u8bbe\u4e0b\u7684\u5708\u5957\u3002\u9a6c\u6d77\u9f99\u5f97\u7f6a\u8d75\u9526\u8363\u8fd8\u662f\u5176\u6b21\uff0c\u5173\u952e\u9a6c\u6d77\u9f99\u8fd9\u4eba\u4e0d\u4f1a\u5c48\u670d\uff0c\u800c\u4e14\u5728\u4ed6\u5bb6\u9644\u8fd1\u62e5\u6709\u5f88\u9ad8\u7684\u5a01\u671b\uff0c\u867d\u7136\u90a3\u7247\u533a\u57df\u5df2\u7ecf\u7ed9\u5f3a\u884c\u62c6\u9664\uff0c\u4f46\u662f\u8d75\u9526\u8363\u4e5f\u62c5\u5fc3\u62c6\u8fc1\u6237\u7ee7\u7eed\u95f9\u4e0b\u53bb\uff0c\u6216\u8bb8\u5468\u5bcc\u660e\u6216\u8005\u4e07\u52c7\u8981\u6c42\u4ed6\u63a7\u5236\u8d1f\u9762\u5f71\u54cd\uff0c\u4ed6\u4eec\u624d\u60f3\u5230\u62ff\u9a6c\u6d77\u9f99\u6765\u6740\u9e21\u5106\u7334\u3002\u9a6c\u6d77\u5c71\u6f5c\u56de\u5bb6\u91cc\u60f3\u62ff\u4e9b\u4e1c\u897f\u51fa\u6765\uff0c\u5374\u7ed9\u5835\u5728\u5c4b\u91cc\u7ed9\u8bec\u9677\u4e3a\u5c0f\u5077\u3002\u9a6c\u6d77\u9f99\u62f3\u811a\u786c\uff0c\u7ed9\u56db\u540d\u6253\u624b\u5835\u5728\u5c4b\u5b50\u91cc\u8d77\u521d\u4e5f\u6ca1\u6709\u5403\u4e8f\uff0c\u76f4\u5230\u8d75\u9526\u8363\u9886\u7740\u53e6\u56db\u540d\u6253\u624b\u8fc7\u6765\uff0c\u7279\u522b\u662f\u8d75\u9526\u8363\u8eab\u8fb9\u7684\u90a3\u540d\u4fdd\u9556\u4e5f\u5b66\u8fc7\u62f3\u811a\u529f\u592b\uff0c\u9a6c\u6d77\u9f99\u624d\u5403\u4e86\u4e8f\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u9a6c\u6d77\u9f99\u8bf4\u9053\uff1a\u201c\u4f60\u4e5f\u4e0d\u8981\u60f3\u7740\u5f88\u5feb\u80fd\u8ba8\u56de\u4ec0\u4e48\u516c\u9053\uff0c\u533b\u7597\u8d39\u7528\u4ec0\u4e48\u7684\uff0c\u4f60\u4eec\u4e0d\u7528\u62c5\u5fc3\uff0c\u6211\u8ba9\u5085\u4fca\u7ed9\u4f60\u4eec\u9001\u6765\u3002\u201d</p><p>\u3000\u3000\u5bf9\u6c99\u7530\u8857\u5e02\u8fd9\u4e00\u7247\u5730\u6bb5\u7684\u5f3a\u5236\u62c6\u8fc1\uff0c\u591a\u534a\u662f\u5f97\u5230\u5468\u5bcc\u660e\u7684\u9ed8\u8bb8\uff0c\u4e0d\u7136\u4e07\u52c7\u4e5f\u4e0d\u6562\u8fd9\u4e48\u8086\u65e0\u5fcc\u60ee\uff0c\u5f3a\u5236\u62c6\u8fc1\u65f6\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7167\u7247\u8d44\u6599\u7559\u4e0b\u6765\uff0c\u901a\u8fc7\u6b63\u5e38\u7684\u7a0b\u5e8f\u53bb\u7533\u8bc9\uff0c\u4e5f\u5f88\u96be\u5f7b\u67e5\u4e0b\u53bb\u3002\u5c31\u7b97\u5f90\u5b66\u5e73\u60f3\u53bb\u8c03\u67e5\u8fd9\u6b21\u5f3a\u62c6\u4e8b\u4ef6\uff0c\u4e5f\u4f1a\u9047\u5230\u5f88\u5927\u7684\u963b\u529b\uff0c\u4e0d\u4ec5\u4ec5\u662f\u6765\u81ea\u6d77\u5dde\u5e02\u65b9\u9762\uff0c\u4e00\u65e6\u7ed9\u62c6\u8fc1\u6237\u4e3b\u6301\u7684\u516c\u9053\uff0c\u5f88\u591a\u4eba\u5c31\u4f1a\u62c5\u5fc3\u5f00\u53d1\u5546\u7684\u5229\u76ca\u4f1a\u4e0d\u4f1a\u906d\u53d7\u635f\u5931\u3002</p><p>\u3000\u3000\u770b\u7740\u9a6c\u6d77\u9f99\u76ee\u5149\u575a\u5b9a\uff0c\u5f20\u606a\u4e5f\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u53bb\u529d\u4ed6\uff0c\u4ed6\u5bf9\u4e5d\u5341\u5e74\u4ee3\u6b63\u5e38\u7684\u6cd5\u5f8b\u624b\u6bb5\u4e5f\u6ca1\u6709\u4fe1\u5fc3\u3002\u8981\u60f3\u6b63\u4e49\u5f97\u5230\u5f70\u663e\uff0c\u90a3\u53ea\u6709\u7b49\u5230\u4ed6\u4eec\u6c14\u7130\u7ed9\u6251\u706d\u7684\u65f6\u5019\u624d\u6709\u53ef\u80fd\u3002</p><p>\u3000\u3000\u76f4\u5230\u51cc\u6668\u5929\u8499\u8499\u9752\u4eae\uff0c\u5f20\u606a\u4e0e\u5085\u4fca\u624d\u79bb\u5f00\u533b\u9662\uff0c\u5728\u5bcc\u8d35\u56ed\u7a0d\u4f5c\u4f11\u606f\uff0c\u665a\u4e0a\u56de\u5230\u5bb6\u91cc\u8ddf\u7238\u7238\u8c08\u8d77\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u542c\u5230\u5f20\u606a\u8bf4\u8d77\u6c99\u7530\u8857\u5e02\u5f3a\u62c6\u4e8b\u60c5\uff0c\u5f20\u77e5\u884c\u53ea\u662f\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u4e8b\u4ef6\u7684\u524d\u540e\uff0c\u4ed6\u662f\u77e5\u9053\u7684\uff0c\u8bf4\u9053\uff1a\u201c\u5173\u4e8e\u52a0\u5feb\u6c99\u7530\u65e7\u57ce\u6539\u9020\u8fdb\u7a0b\u7684\u51b3\u8bae\u662f\u5e02\u5e38\u59d4\u4f1a\u8bae\u51b3\u5b9a\u7684\uff0c\u5468\u5bcc\u660e\u751a\u81f3\u5728\u4f1a\u4e0a\u8bf4\uff1a\u4e3a\u4e86\u57ce\u5e02\u5efa\u8bbe\u7684\u5927\u5c40\uff0c\u53d1\u6325\u5c0f\u90e8\u5206\u4eba\u9769\u547d\u5fd8\u6211\u3001\u81ea\u6211\u727a\u7272\u7684\u7cbe\u795e\u662f\u503c\u5f97\u63d0\u5021\u7684\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u77e5\u9053\u5510\u5b66\u8c26\u5bf9\u5f3a\u5236\u62c6\u8fc1\u7684\u6001\u5ea6\uff0c\u5c31\u7b97\u5510\u5b66\u8c26\u5bf9\u5f3a\u5236\u62c6\u8fc1\u5f88\u6124\u6068\uff0c\u4f46\u662f\u4ed6\u4e5f\u672a\u5fc5\u4f1a\u5e2e\u9a6c\u6d77\u9f99\u4ee5\u53ca\u88ab\u5f3a\u62c6\u7684\u56db\u5341\u591a\u6237\u62c6\u8fc1\u6237\u8ba8\u56de\u516c\u9053\uff0c\u90a3\u6837\u4f1a\u8ba9\u5e02\u91cc\u7684\u77db\u76fe\u8fc5\u901f\u6fc0\u5316\u3002</p><p>\u3000\u3000\u6b63\u503c\u7231\u8fbe\u5c06\u7b2c\u4e00\u6b3e\u5f71\u789f\u673a\u63a8\u5411\u5e02\u573a\u4e4b\u9645\uff0c\u5f20\u606a\u4e5f\u4e0d\u60f3\u8282\u5916\u751f\u679d\uff0c\u773c\u4e0b\u4e5f\u6ca1\u6709\u4f59\u6687\u53bb\u7ba1\u592a\u591a\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u662f\u6b63\u4e49\u65e0\u9650\u81a8\u80c0\u7684\u4eba\uff0c\u4ed6\u6709\u4ed6\u7684\u5229\u76ca\u8ffd\u6c42\uff0c\u8981\u4e0d\u662f\u9a6c\u6d77\u9f99\u9002\u5de7\u662f\u5085\u4fca\u7684\u6218\u53cb\uff0c\u5f20\u606a\u591a\u534a\u662f\u51b7\u773c\u65c1\u89c2\u4e00\u4f1a\u513f\uff0c\u7136\u540e\u82e5\u65e0\u5176\u4e8b\u7684\u8d70\u5f00\u3002</p></article>"}}}]);
