(window.webpackJsonp=window.webpackJsonp||[]).push([[194],{216:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e00\u8282 \u4e0d\u53ef\u4ee5\u8f93\u7ed9\u5bf9\u65b9",content:"<article><h2>\u7b2c\u4e09\u5341\u4e00\u8282 \u4e0d\u53ef\u4ee5\u8f93\u7ed9\u5bf9\u65b9</h2><p>\u4e24\u4eba\u4e92\u76f8\u62e5\u62b1\u7740\uff0c\u5f7c\u6b64\u504e\u4f9d\uff0c\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u6885\u83f2\u65af\u8f7b\u8f7b\u4ece\u743c\u6069\u6000\u91cc\u6323\u8131\uff0c\u8d77\u8eab\u4e0b\u5e8a\u5012\u4e86\u676f\u6c34\uff0c\u201c\u5f88\u665a\u4e86\uff0c\u7761\u5427\uff0c\u201d\u5979\u5bf9\u743c\u6069\u8bf4\uff0c\u201c\u660e\u5929\u8fd8\u8981\u8d76\u8def\u5462\u3002\u201d</p><p>\u201c\u5514\uff0c\u201d\u743c\u6069\u4e5f\u5df2\u7ecf\u6709\u4e9b\u534a\u7761\u534a\u9192\uff0c\u6b63\u8ff7\u8ff7\u7cca\u7cca\u4e2d\uff0c\u7a81\u7136\u60f3\u8d77\u6765\uff0c\u4eca\u665a\u6765\u627e\u6885\u83f2\u65af\uff0c\u8fd8\u6709\u4ef6\u4e8b\u60c5\u5462\u3002\u201c\u5bf9\u4e86\uff0c\u827e\u5f25\u8587\uff0c\u4f60\u548c\u51db\u5230\u5e95\u662f\u600e\u4e48\u8ba4\u8bc6\u7684\u554a\u3002\u201d</p><p>\u738b\u5b50\u8bf4\u51db\u7684\u8001\u5e08\u8ff0\u5c14\u53ef\u80fd\u5c31\u662f\u8428\u624e\u65af\u5766\u7684\u6697\u4e2d\u5bf9\u624b\uff0c\u6240\u4ee5\u53ee\u5631\u743c\u6069\u8981\u5c0f\u5fc3\u51db\u3002\u8fd9\u4e00\u8def\u8d70\u8fc7\u6765\uff0c\u51db\u4f3c\u4e4e\u4e5f\u6ca1\u4ec0\u4e48\u5f02\u6837\uff0c\u8868\u73b0\u5f97\u5b8c\u5168\u6b63\u5e38\uff0c\u867d\u7136\u5982\u6b64\uff0c\u743c\u6069\u7ec8\u7a76\u8fd8\u662f\u4e0d\u592a\u653e\u5fc3\u3002</p><p>\u8981\u63d0\u9632\u4e00\u4e2a\u4eba\uff0c\u81ea\u7136\u8981\u5bf9\u8fd9\u4e2a\u4eba\u7684\u60c5\u51b5\u5c3d\u53ef\u80fd\u4e86\u89e3\u624d\u597d\uff0c\u77e5\u5df1\u77e5\u5f7c\u624d\u80fd\u767e\u6218\u4e0d\u6b86\uff0c\u7136\u800c\u51db\u7684\u6765\u5386\u80cc\u666f\u8fc7\u4e8e\u795e\u79d8\uff0c\u5728\u52a0\u5165\u7ea2\u888d\u5deb\u5e08\u4f1a\u4e4b\u524d\u7684\u7ecf\u5386\u65e0\u4eba\u77e5\u6653\uff0c\u5c31\u8fde\u738b\u5b50\u90fd\u641c\u96c6\u4e0d\u5230\u4ec0\u4e48\u8d44\u6599\u3002\u4e0d\u8fc7\u6ca1\u5173\u7cfb\uff0c\u6885\u83f2\u65af\u548c\u5979\u662f\u597d\u53cb\uff0c\u80af\u5b9a\u662f\u6e05\u695a\u7684\u3002</p><p>\u201c\u5979\u554a\uff0c\u4ece\u5c0f\u548c\u6211\u4e00\u8d77\u957f\u5927\u7684\uff0c\u201d\u6885\u83f2\u65af\u4e5f\u6ca1\u592a\u5728\u610f\uff0c\u968f\u53e3\u56de\u7b54\uff0c\u201c\u5c0f\u65f6\u5019\uff0c\u6211\u548c\u6211\u6bcd\u4eb2\u9690\u5c45\u5728\u5f7b\u68ee\u5854\u7684\u5fa1\u5b87\u5c71\u8109\u91cc\uff0c\u5979\u548c\u5979\u6bcd\u4eb2\u4e5f\u5728\u90a3\u91cc\uff0c\u4e24\u5bb6\u4f4f\u5f97\u5f88\u8fd1\uff0c\u7ecf\u5e38\u6765\u5f80\uff0c\u4e8e\u662f\u5c31\u8ba4\u8bc6\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u6162\u6162\u8bb2\u8ff0\uff0c\u743c\u6069\u8fd9\u624d\u77e5\u9053\uff0c\u539f\u6765\u5c0f\u65f6\u5019\uff0c\u6885\u83f2\u65af\u7ecf\u5e38\u88ab\u6bcd\u4eb2\u5e26\u7740\u53bb\u8ffd\u6740\u90a3\u4e9b\u5df4\u5c14\u4e4b\u5b50\uff0c\u5f53\u7136\u8fd9\u4e5f\u62db\u81f4\u4e86\u5f88\u591a\u4ec7\u654c\uff0c\u4e00\u5e74\u4e2d\u5927\u6982\u6709\u534a\u5e74\u7684\u65f6\u95f4\uff0c\u4ed6\u4eec\u5c31\u4f1a\u56de\u5230\u5fa1\u5b87\u5c71\u8109\u7684\u5bb6\u91cc\u8eb2\u907f\u4f11\u606f\u3002\u56e0\u4e3a\u662f\u9690\u5c45\uff0c\u6df1\u5c71\u91cc\u4e5f\u6ca1\u4ec0\u4e48\u4eba\uff0c\u552f\u6709\u9644\u8fd1\u5c45\u4f4f\u7740\u4e00\u5bb6\uff0c\u662f\u51db\u548c\u5979\u7684\u6bcd\u4eb2\uff0c\u4e24\u4e2a\u5c0f\u5973\u5b69\u7684\u53cb\u8c0a\u4ece\u6b64\u5f00\u59cb\u3002\u540e\u6765\u51db\u7684\u6bcd\u4eb2\u91cd\u75c5\u53bb\u4e16\u4e86\uff0c\u51db\u5c31\u76f4\u63a5\u642c\u5230\u6885\u83f2\u65af\u5bb6\u4e2d\u6765\u5c45\u4f4f\u3002</p><p>\u518d\u540e\u6765\u2026\u2026</p><p>\u201c\u518d\u540e\u6765\uff0c\u654c\u4eba\u8ffd\u6740\u5230\u6211\u4eec\u4f4f\u7684\u5730\u65b9\uff0c\u6bcd\u4eb2\u6b7b\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u6211\u548c\u51db\u9003\u4e86\u51fa\u6765\uff0c\u55ef\uff0c\u4e24\u4e2a\u4eba\u5728\u4e00\u8d77\u6d41\u6d6a\u5427\u3002\u5927\u6982\u5c31\u8fd9\u6837\u8fc7\u4e86\u4e00\u5e74\u591a\uff0c\u6211\u9047\u4e0a\u4e86\u5927\u4e3b\u6559\uff0c\u968f\u4ed6\u52a0\u5165\u4e86\u6559\u4f1a\uff0c\u51db\u4e0d\u613f\u610f\u53bb\u8ff7\u65af\u5353\u8bfa\uff0c\u5c31\u5206\u624b\u4e86\u3002\u201d</p><p>\u8fd9\u4e48\u8bf4\uff0c\u4e24\u4eba\u662f\u81ea\u5c0f\u76f8\u8bc6\uff0c\u4e00\u8d77\u957f\u5927\uff0c\u540e\u6765\u53c8\u66fe\u7ecf\u5171\u7ecf\u60a3\u96be\uff0c\u5f7c\u6b64\u6276\u6301\u7684\u81f3\u4ea4\u597d\u53cb\uff0c\u96be\u602a\u80fd\u6709\u8fd9\u6837\u7684\u53cb\u8c0a\u5427\u3002\u4f46\u4e3a\u4ec0\u4e48\u603b\u8fd8\u662f\u89c9\u5f97\u5979\u4eec\u4e4b\u95f4\u7684\u5173\u7cfb\u6709\u70b9\u602a\u602a\u7684\u3002</p><p>\u201c\u90a3\u4e2a\u4e48\uff0c\u53ef\u80fd\u662f\u56e0\u4e3a\u6211\u4eec\u90fd\u592a\u9a84\u50b2\u5427\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u90fd\u4e0d\u613f\u610f\u505a\u5f97\u6bd4\u5bf9\u65b9\u5dee\uff0c\u4e0d\u613f\u610f\u5728\u5bf9\u65b9\u9762\u524d\u793a\u5f31\uff0c\u603b\u60f3\u80fd\u6bd4\u5bf9\u65b9\u66f4\u5f3a\u4e00\u70b9\uff0c\u6211\u4eec\u8fd8\u66fe\u7ecf\u6709\u8fc7\u7ea6\u5b9a\u5462\uff0c\u7ea6\u5b9a\u8bf4\uff1a\u6c38\u8fdc\u6c38\u8fdc\uff0c\u8c01\u90fd\u4e0d\u53ef\u4ee5\u8f93\u7ed9\u5bf9\u65b9\uff0c\u201d\u5979\u56de\u5230\u5e8a\u4e0a\uff0c\u8eba\u5728\u743c\u6069\u8eab\u8fb9\uff0c\u60f3\u8d77\u5f80\u4e8b\u5fcd\u4e0d\u4f4f\u5634\u89d2\u5fae\u5fae\u542b\u7740\u8f7b\u7b11\uff0c\u201c\u73b0\u5728\u60f3\u8d77\u6765\uff0c\u771f\u5e7c\u7a1a\u554a\u3002\u4f46\u90a3\u65f6\u5019\uff0c\u6211\u4eec\u4e24\u4e2a\u5c0f\u5b69\u5b50\uff0c\u7236\u6bcd\u90fd\u4e0d\u5728\u4e16\u4e86\uff0c\u6211\u7684\u5251\u672f\u8fd8\u6ca1\u7ec3\u6210\uff0c\u5979\u7684\u9b54\u6cd5\u4e5f\u5f88\u5dee\u52b2\uff0c\u6bcf\u5929\u90fd\u8981\u8eb2\u907f\u654c\u4eba\u8ffd\u6740\uff0c\u65e0\u6570\u6b21\u6b7b\u91cc\u9003\u751f\uff0c\u5982\u679c\u4e0d\u662f\u8fd9\u6837\u5f7c\u6b64\u6276\u6301\uff0c\u5f7c\u6b64\u9f13\u52b1\uff0c\u751a\u81f3\u5f7c\u6b64\u7ade\u4e89\u7740\uff0c\u52aa\u529b\u8981\u6bd4\u5bf9\u65b9\u505a\u5f97\u66f4\u597d\u7684\u8bdd\uff0c\u53ea\u6015\u65e9\u5c31\u6d3b\u4e0d\u4e0b\u6765\u4e86\u5427\u3002\u201d</p><p>\u201c\u8c01\u90fd\u4e0d\u53ef\u4ee5\u8f93\u7ed9\u5bf9\u65b9\uff1f\u662f\u6307\u54ea\u65b9\u9762\u5462\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u5404\u65b9\u9762\u5427\uff0c\u6bd4\u5982\u8bf4\u4e0d\u80fd\u6bd4\u5bf9\u65b9\u505a\u5f97\u5dee\uff0c\u4e0d\u80fd\u8ba9\u5bf9\u65b9\u770b\u5230\u81ea\u5df1\u8f6f\u5f31\u548c\u4e0d\u597d\u7684\u6837\u5b50\u2026\u2026\u5514\uff0c\u5176\u5b9e\u4f60\u771f\u8981\u6211\u8bf4\uff0c\u6211\u4e5f\u8bf4\u4e0d\u6e05\u695a\uff0c\u4f46\u6211\u4eec\u90fd\u77e5\u9053\u662f\u4ec0\u4e48\u610f\u601d\u5c31\u884c\u4e86\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u201c\u4f1a\u4e0d\u4f1a\u5728\u9009\u62e9\u7537\u4eba\u8fd9\u65b9\u9762\uff0c\u4e5f\u8981\u4e92\u76f8\u6bd4\u8f83\u4e00\u4e0b\u554a\u3002\u201d</p><p>\u201c\u4f60\u731c\u5bf9\u4e86\uff0c\u5979\u8fd8\u771f\u5bf9\u6211\u8fd9\u6837\u8bf4\u8fc7\u3002\u201d\u6885\u83f2\u65af\u70b9\u5934\u3002</p><p>\u201c\u90a3\u6211\u771f\u540c\u60c5\u5979\u672a\u6765\u7684\u60c5\u4eba\uff0c\u201d\u743c\u6069\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\uff0c\u201c\u8981\u88ab\u62ff\u6765\u548c\u6211\u8fd9\u6837\u4f18\u79c0\u7684\u4eba\u76f8\u6bd4\u3002\u201d</p><p>\u6885\u83f2\u65af\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4e0d\u5bb3\u7f9e\u4e48\uff0c\u201d\u5979\u5a07\u55d4\u7740\uff0c\u201c\u4f60\u79bb\u4f18\u79c0\u4e24\u4e2a\u5b57\u7684\u8ddd\u79bb\u5c31\u50cf\u4e5d\u72f1\u5230\u5929\u5802\u5c71\u5427\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u5e94\u8be5\u8fd9\u4e48\u6253\u51fb\u4f60\u7684\u60c5\u4eba\u5427\u3002\u201d</p><p>\u201c\u5b9e\u8bdd\u5b9e\u8bf4\u7f62\u4e86\uff0c\u5f88\u591a\u4eba\u90fd\u8bf4\u6211\u6700\u5927\u7684\u7f3a\u70b9\u5c31\u662f\u592a\u8bda\u5b9e\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\uff0c\u90a3\u4f60\u5c31\u53ea\u80fd\u7948\u7977\u5979\u4f1a\u559c\u6b22\u4e0a\u4e00\u4e2a\u6bd4\u6211\u66f4\u5dee\u52b2\u7684\u4eba\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u7b11\u7740\uff0c\u94bb\u8fdb\u7537\u4eba\u6000\u91cc\uff0c\u743c\u6069\u6402\u7740\u5979\uff0c\u629a\u6478\u7740\u5979\u7684\u91d1\u53d1\uff0c\u201c\u90a3\u6bb5\u65f6\u65e5\u5df2\u7ecf\u5f88\u8f9b\u82e6\u5427\u3002\u201d\u4ed6\u4f4e\u58f0\u8bf4\u3002</p><p>\u201c\u55ef\uff0c\u662f\u5f88\u8f9b\u82e6\u5462\uff0c\u4e0d\u8fc7\u73b0\u5728\u56de\u60f3\u8d77\u6765\uff0c\u5176\u5b9e\u4e5f\u5c31\u6ca1\u4ec0\u4e48\u4e86\u3002\u6211\u8fd9\u4eba\u633a\u6ca1\u8bb0\u6027\u7684\uff0c\u4e8b\u60c5\u8fc7\u53bb\u4e86\u5c31\u8fc7\u53bb\uff0c\u5f88\u5bb9\u6613\u5370\u8c61\u5c31\u6de1\u4e86\uff0c\u201d\u5979\u5fae\u7b11\u7740\uff0c\u201c\u5927\u4e3b\u6559\u8bf4\uff0c\u6b63\u56e0\u4e3a\u6211\u7ecf\u5386\u8fc7\u8fd9\u4e9b\uff0c\u6240\u4ee5\u624d\u80fd\u5b66\u5230\u5f88\u591a\u4e1c\u897f\uff0c\u624d\u4f1a\u548c\u5176\u4ed6\u4eba\u4e0d\u4e00\u6837\uff0c\u80fd\u6210\u4e3a\u5723\u6b66\u58eb\u5462\u3002\u201d</p><p>\u201c\u80e1\u8bf4\uff0c\u201d\u743c\u6069\u62b1\u7740\u5979\uff0c\u201c\u5b69\u5b50\u5c31\u662f\u5b69\u5b50\uff0c\u5c31\u5e94\u8be5\u8fc7\u5b69\u5b50\u7684\u751f\u6d3b\uff0c\u5feb\u5feb\u4e50\u4e50\uff0c\u65e0\u5fe7\u65e0\u8651\u3002\u50cf\u90a3\u79cd\u751f\u6d3b\uff0c\u5c31\u7b97\u771f\u7684\u80fd\u4ece\u4e2d\u5f97\u5230\u4ec0\u4e48\uff0c\u5c31\u7b97\u56e0\u6b64\u624d\u6210\u4e3a\u5723\u6b66\u58eb\uff0c\u90a3\u4e5f\u662f\u7edd\u4e0d\u5e94\u8be5\u53bb\u8fc7\u7684\u3002\u201d</p><p>\u201c\u4f46\u6ca1\u6709\u51e0\u4e2a\u4eba\u80fd\u591f\u81ea\u7531\u9009\u62e9\u81ea\u5df1\u7684\u751f\u6d3b\u5427\uff0c\u201d\u6885\u83f2\u65af\u9759\u9759\u8bf4\uff0c\u201c\u4f60\u80fd\u9009\u62e9\u5417\uff1f\u201d</p><p>\u743c\u6069\u95ed\u7740\u5634\uff0c\u4ed6\u4e0d\u80fd\u3002</p><p>\u201c\u8fc7\u53bb\u4e0d\u80fd\uff0c\u4f46\u5c06\u6765\u4e00\u5b9a\u80fd\u3002\u201d\u4ed6\u6700\u540e\u52c9\u5f3a\u8bf4\u3002</p><p>\u6885\u83f2\u65af\u201c\u55ef\u201d\u4e86\u4e00\u58f0\uff0c\u6ca1\u6709\u518d\u8bf4\u8bdd\u3002</p><p>\u201c\u4f60\u7684\u5934\u53d1\u957f\u957f\u4e86\u5462\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8bb0\u5f97\u521a\u8ba4\u8bc6\u4f60\u7684\u65f6\u5019\u5f88\u77ed\u7684\u3002\u201d</p><p>\u201c\u5df2\u7ecf\u4e09\u4e2a\u591a\u6708\u4e86\uff0c\u81ea\u7136\u4e5f\u8be5\u957f\u957f\u4e86\uff0c\u201d\u5979\u56de\u7b54\uff0c\u201c\u4f60\u559c\u6b22\u6211\u957f\u53d1\u8fd8\u662f\u77ed\u53d1\uff1f\u201d</p><p>\u201c\u8fd8\u662f\u957f\u53d1\u5427\uff0c\u770b\u8d77\u6765\u6bd4\u8f83\u50cf\u5973\u5b69\u5b50\u3002\u201d</p><p>\u201c\u610f\u601d\u662f\u8bf4\u6211\u4ee5\u524d\u4e0d\u50cf\u5973\u5b69\u5b50\uff1f\u201d</p><p>\u201c\u5443\uff0c\u90a3\u4e2a\u554a\uff0c\u4f60\u4ee5\u524d\u66f4\u50cf\u4e2a\u5723\u6b66\u58eb\u561b\uff0c\u592a\u521a\u786c\u4e86\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u8bf4\u4f60\u559c\u6b22\u6211\u6c38\u8fdc\u662f\u5723\u6b66\u58eb\u5417\uff1f\u201d</p><p>\u201c\u662f\u5723\u6b66\u58eb\uff0c\u5e76\u4e0d\u662f\u8bf4\u5c31\u8981\u50cf\u5723\u6b66\u58eb\uff0c\u8fd9\u5b8c\u5168\u4e24\u7801\u4e8b\u3002\u201d</p><p>\u201c\u5514\uff0c\u968f\u4f60\u559c\u6b22\u5427\u3002\u201d</p><p>\u201c\u771f\u4e56\uff0c\u201d\u743c\u6069\u8f7b\u8f7b\u629a\u6478\u5979\u7cbe\u81f4\u7684\u8138\u86cb\uff0c\u201c\u5bf9\u4e86\uff0c\u827e\u5f25\u8587\uff0c\u6709\u4ef6\u4e8b\u60c5\uff0c\u6211\u5f97\u5bf9\u4f60\u8bf4\u3002\u201d</p><p>\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8428\u624e\u65af\u5766\u6000\u7591\uff0c\u51db\u7684\u8001\u5e08\u8ff0\u5c14\uff0c\u5c31\u662f\u4e0a\u6b21\u7834\u574f\u53cc\u71c3\u4eea\u5f0f\u7684\u5e55\u540e\u6307\u793a\u8005\u3002\u201d</p><p>\u6885\u83f2\u65af\u95fb\u8a00\u4e00\u6014\uff0c\u968f\u5373\u660e\u767d\u743c\u6069\u8bf4\u8fd9\u53e5\u8bdd\u7684\u610f\u601d\uff0c\u201c\u4f60\u662f\u6000\u7591\u51db\u2026\u2026\u201d</p><p>\u201c\u4e0d\u662f\u6211\u6000\u7591\uff0c\u662f\u8428\u624e\u65af\u5766\u548c\u6bbf\u4e0b\u6000\u7591\u3002\u201d</p><p>\u4ed6\u539f\u672c\u4e0d\u60f3\u544a\u8bc9\u6885\u83f2\u65af\uff0c\u4f46\u8003\u8651\u518d\u4e09\uff0c\u8fd8\u662f\u8bf4\u4e86\u51fa\u6765\u3002\u6885\u83f2\u65af\u4e0d\u662f\u90a3\u79cd\u5206\u4e0d\u6e05\u4e8b\u60c5\u8f7b\u91cd\u7684\u5c0f\u4e2b\u5934\uff0c\u5979\u77e5\u9053\u81ea\u5df1\u5e94\u8be5\u7ad9\u7684\u7acb\u573a\u548c\u6001\u5ea6\uff0c\u800c\u4e8b\u5b9e\u4e5f\u8bc1\u660e\u6b63\u662f\u5982\u6b64\u3002</p><p>\u6885\u83f2\u65af\u6c89\u601d\u4e86\u4e00\u4f1a\uff0c\u6447\u6447\u5934\uff0c\u201c\u4e0d\u592a\u53ef\u80fd\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u201c\u8ff0\u5c14\u6709\u53ef\u80fd\u662f\u8428\u624e\u65af\u5766\u7684\u654c\u4eba\uff0c\u8fd9\u4e2a\u6211\u4e0d\u6e05\u695a\uff1b\u4f46\u51db\u5e94\u8be5\u6ca1\u6709\u53d7\u4ed6\u6307\u4f7f\u3002\u6216\u8005\u8bf4\uff0c\u8ff0\u5c14\u6307\u4f7f\u4e0d\u4e86\u51db\u505a\u8fd9\u79cd\u4e8b\u60c5\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u80af\u5b9a\uff1f\u201d</p><p>\u201c\u6211\u542c\u51db\u63d0\u8d77\u8fc7\u8ff0\u5c14\uff0c\u5979\u5bf9\u8fd9\u4e2a\u8001\u5e08\u6ca1\u4ec0\u4e48\u656c\u610f\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u51db\u90a3\u5bb6\u4f19\uff0c\u662f\u4e2a\u9a84\u50b2\u5230\u9aa8\u5b50\u91cc\u7684\u4eba\uff0c\u4e0d\u662f\u5979\u771f\u5fc3\u559c\u6b22\u6216\u8005\u4f69\u670d\u7684\u4eba\uff0c\u7edd\u5bf9\u6ca1\u529e\u6cd5\u6307\u4f7f\u5979\u6765\u505a\u8fd9\u79cd\u4e8b\u60c5\u3002\u5176\u5b9e\u8981\u8bf4\u8d77\u6765\uff0c\u6211\u90fd\u633a\u5947\u602a\uff0c\u5979\u600e\u4e48\u4f1a\u5728\u7ea2\u888d\u5deb\u5e08\u4f1a\u5446\u8fd9\u4e48\u4e45\u3002\u6309\u9053\u7406\u8bf4\uff0c\u5979\u662f\u90a3\u79cd\u6ca1\u8010\u6027\u7684\u4eba\uff0c\u9664\u975e\u6709\u4ec0\u4e48\u7279\u522b\u5438\u5f15\u5979\u7684\u4eba\u6216\u8005\u4e1c\u897f\uff0c\u5426\u5219\u5979\u80af\u5b9a\u4e09\u5929\u5c31\u6362\u4e2a\u5730\u65b9\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\u3002\u201d</p><p>\u743c\u6069\u5c06\u4fe1\u5c06\u7591\uff0c\u867d\u7136\u6885\u83f2\u65af\u8bf4\u5f97\u5f88\u80af\u5b9a\uff0c\u4f46\u8fd9\u4e48\u5927\u7684\u4e8b\u60c5\uff0c\u7ec8\u7a76\u4e0d\u80fd\u51ed\u5979\u8fd9\u6837\u7684\u201c\u611f\u89c9\u201d\u5c31\u4e0b\u65ad\u8a00\u3002\u6885\u83f2\u65af\u81ea\u7136\u4e5f\u6e05\u695a\u8fd9\u70b9\uff0c\u5979\u867d\u7136\u76f8\u4fe1\u51db\u4e0d\u4f1a\u53c2\u4e0e\u5176\u4e2d\uff0c\u4f46\u7ec8\u7a76\u4e5f\u4e0d\u6562\u8bf4\u5c31\u6709\u5341\u6210\u7684\u628a\u63e1\u3002\u66f4\u4f55\u51b5\uff0c\u5c31\u7b97\u51db\u6ca1\u6709\u88ab\u8ff0\u5c14\u6307\u4f7f\uff0c\u5979\u4e0d\u77e5\u60c5\uff0c\u4f46\u8ff0\u5c14\u8eab\u4e3a\u5851\u80fd\u9996\u5e2d\u5927\u5deb\u5e08\uff0c\u96be\u4fdd\u6ca1\u6709\u5728\u51db\u8eab\u4e0a\u505a\u4ec0\u4e48\u624b\u811a\uff0c\u6697\u4e2d\u76d1\u89c6\u6216\u8005\u4e0b\u6307\u4f7f\u672f\u4e4b\u7c7b\uff0c\u8fd9\u662f\u5f88\u6709\u53ef\u80fd\u7684\u4e8b\u60c5\u3002</p><p>\u201c\u6211\u77e5\u9053\u4e86\uff0c\u4f1a\u5c0f\u5fc3\u7684\u3002\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u4e8e\u662f\u743c\u6069\u4fbf\u4e0d\u518d\u8bf4\u8bdd\u3002</p><p>\u4e24\u4eba\u6402\u62b1\u7740\uff0c\u8eab\u4f53\u8d34\u5728\u4e00\u8d77\uff0c\u743c\u6069\u6709\u5f88\u591a\u5929\u90fd\u6ca1\u4eb2\u8fd1\u8fc7\u5979\u4e86\uff0c\u5982\u4eca\u8fd9\u6837\u6e29\u9999\u8f6f\u7389\u62b1\u5728\u6000\u91cc\uff0c\u95fb\u7740\u5979\u8eab\u4e0a\u900f\u51fa\u6765\u7684\u6de1\u6de1\u9999\u6c14\uff0c\u8eab\u4f53\u6e10\u6e10\u5c31\u6709\u4e86\u53cd\u5e94\u3002\u6885\u83f2\u65af\u7acb\u523b\u611f\u89c9\u5230\u4e86\uff0c\u201c\u60f3\u8981\u4e86\uff1f\u201d\u5979\u8f7b\u58f0\u95ee\u3002</p><p>\u201c\u5514\u3002\u201d</p><p>\u5c11\u5973\u7684\u5634\u5507\u8d34\u4e0a\u6765\uff0c\u4e3b\u52a8\u7d22\u543b\uff0c\u4e24\u4eba\u5507\u820c\u7ede\u7f20\u7740\uff0c\u611f\u53d7\u5230\u5bf9\u65b9\u7684\u547c\u5438\u6e10\u6e10\u53d8\u5f97\u6eda\u70eb\uff0c\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u6885\u83f2\u65af\u604b\u604b\u4e0d\u820d\u5730\u4ef0\u8d77\u8138\uff0c\u5728\u4ed6\u8138\u4e0a\u4eb2\u4e86\u4e00\u53e3\uff0c\u7136\u540e\u6162\u6162\u94bb\u5230\u6bef\u5b50\u4e0b\u9762\u53bb\u4e86\u3002</p><p>\u743c\u6069\u53d1\u51fa\u6ee1\u8db3\u7684\u53f9\u606f\u58f0\uff0c\u4ed6\u611f\u89c9\u81ea\u5df1\u4eff\u4f5b\u6574\u4e2a\u4eba\u90fd\u88ab\u6d78\u6ce1\u5728\u70ed\u6c34\u4e2d\uff0c\u8bf4\u4e0d\u51fa\u7684\u8212\u670d\u3002\u6216\u8bb8\u662f\u521a\u521a\u70ed\u70c8\u63a5\u543b\u8fc7\u7684\u7f18\u6545\uff0c\u611f\u89c9\u5c11\u5973\u7684\u53e3\u8154\u6bd4\u5e73\u5e38\u683c\u5916\u7684\u6e29\u70ed\u6e7f\u6da6\uff0c\u7ecf\u8fc7\u8fd9\u4e48\u957f\u65f6\u95f4\u7684\u201c\u6559\u80b2\u201d\uff0c\u5979\u7684\u6280\u5de7\u4e5f\u8d8a\u6765\u8d8a\u597d\uff0c\u820c\u5c16\u7684\u7075\u5de7\u6311\u9017\uff0c\u8ba9\u743c\u6069\u6709\u79cd\u7075\u9b42\u90fd\u8981\u88ab\u5438\u51fa\u6765\u7684\u611f\u89c9\u3002</p><p>\u6b63\u5728\u5c06\u8981\u7206\u53d1\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u7a81\u7136\u505c\u6b62\u4e86\u52a8\u4f5c\uff0c\u53c8\u4ece\u4e0b\u9762\u94bb\u4e86\u4e0a\u6765\uff0c\u201c\u559c\u6b22\u5427\uff0c\u201d\u5979\u4f4e\u58f0\u95ee\u743c\u6069\uff0c\u4e00\u8138\u5b69\u5b50\u6c14\u7684\u7b11\u5bb9\uff0c\u201c\u5728\u4e0b\u9762\u5c31\u542c\u89c1\u4f60\u5f88\u6ee1\u8db3\u7684\u58f0\u97f3\u5462\u3002\u201d</p><p>\u743c\u6069\u4eb2\u543b\u7740\u5979\u7684\u989d\u5934\uff0c\u201c\u5230\u5e95\u56e0\u4e3a\u4ec0\u4e48\u4e0d\u80af\u548c\u6211\u771f\u505a\u5462\uff1f\u201d\u4ed6\u5f88\u60f3\u95ee\u8fd9\u53e5\u8bdd\uff0c\u4f46\u6700\u540e\u8fd8\u662f\u5fcd\u4f4f\u4e86\uff0c\u6885\u83f2\u65af\u8bf4\u8ba9\u5979\u518d\u60f3\u60f3\uff0c\u90a3\u4e48\u5c31\u518d\u60f3\u60f3\u5427\uff0c\u6ca1\u5fc5\u8981\u8fd9\u79cd\u65f6\u5019\u7834\u574f\u6c14\u6c1b\u3002</p><p>\u5bf9\u4e86\uff0c\u521a\u624d\u51db\u8bf4\u201c\u53cd\u6b63\u4f60\u4eec\u53c8\u4e0d\u80fd\u771f\u505a\u201d\uff0c\u4ee5\u5979\u548c\u6885\u83f2\u65af\u7684\u5173\u7cfb\uff0c\u5341\u6709\u516b\u4e5d\u662f\u77e5\u9053\u5185\u60c5\u7684\uff0c\u6709\u673a\u4f1a\u7684\u8bdd\uff0c\u5012\u53ef\u4ee5\u627e\u5979\u95ee\u95ee\u3002</p><p>\u201c\u8fd8\u60f3\u8981\u7ee7\u7eed\uff1f\u201d\u5c11\u5973\u7b11\u541f\u541f\u5730\u95ee\u3002</p><p>\u201c\u5f53\u7136\u3002\u201d</p><p>\u201c\u7b49\u7740\u3002\u201d</p><p>\u5c11\u5973\u518d\u6b21\u5c48\u8eab\u4e0b\u53bb\uff0c\u5f20\u5f00\u5c0f\u5634\u6162\u6162\u5c06\u90a3\u5df2\u7ecf\u81a8\u80c0\u5230\u6781\u9650\u7684\u5de8\u7269\u542b\u4e86\u8fdb\u53bb\uff0c\u52aa\u529b\u541e\u54bd\u7740\u3002\u5979\u7684\u52a8\u4f5c\u8d8a\u6765\u8d8a\u5feb\uff0c\u8d8a\u6765\u8d8a\u6fc0\u70c8\uff0c\u5f88\u5feb\u5c31\u8ba9\u743c\u6069\u5f7b\u5e95\u7206\u53d1\u4e86\u51fa\u6765\u3002\u5bb3\u6015\u5f04\u5230\u5e8a\u5355\u548c\u6bdb\u6bef\u4e0a\uff0c\u5979\u5e76\u6ca1\u6709\u5728\u6700\u540e\u5173\u5934\u79fb\u5f00\uff0c\u800c\u662f\u5168\u90e8\u541e\u54bd\u4e86\u4e0b\u53bb\u3002\u55b7\u5c04\u5f97\u592a\u591a\u592a\u731b\u70c8\uff0c\u51e0\u4e4e\u8ba9\u5979\u545b\u4e86\u8d77\u6765\uff0c\u6700\u7ec8\uff0c\u4e00\u5207\u5e73\u606f\uff0c\u5979\u6162\u6162\u4ece\u4e0b\u9762\u94bb\u4e0a\u6765\u3002</p><p>\u201c\u7830\u7830\uff01\u201d</p><p>\u5c31\u5728\u6b64\u65f6\uff0c\u95e8\u7a81\u7136\u88ab\u6572\u54cd\u4e86\uff0c\u7d27\u63a5\u7740\u88ab\u76f4\u63a5\u63a8\u5f00\uff0c\u7a7f\u7740\u5c0f\u718a\u7761\u8863\u62b1\u7740\u6795\u5934\u7684\u5c0f\u9b54\u5973\u7761\u773c\u60fa\u5fea\u5730\u8d70\u8fdb\u6765\u3002\u201c\u4f60\u4eec\u8be5\u8c08\u5b8c\u4e86\u5427\uff0c\u6211\u8981\u7761\u89c9\u4e86\u3002\u201d\u5979\u8ff7\u8ff7\u7cca\u7cca\u5730\u8bf4\uff0c\u4e00\u773c\u6b63\u770b\u89c1\u6885\u83f2\u65af\u4ece\u6bef\u5b50\u91cc\u94bb\u51fa\uff0c\u5634\u89d2\u8fd8\u5e26\u7740\u6765\u4e0d\u53ca\u64e6\u62ed\u6389\u7684\u6df7\u6d4a\u767d\u8272\u6db2\u4f53\u3002</p><p>\u201c\u5514\uff0c\u827e\u5f25\u8587\uff0c\u4f60\u2026\u2026\u554a\uff01\u201d\u534a\u7761\u534a\u9192\u72b6\u6001\u7684\u51db\u603b\u7b97\u662f\u53cd\u5e94\u8fc7\u6765\uff0c\u77aa\u7740\u5e8a\u4e0a\u7684\u4e00\u7537\u4e00\u5973\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u5c31\u7b97\u5979\u518d\u7b28\u4e5f\u8be5\u660e\u767d\u600e\u4e48\u56de\u4e8b\u4e86\uff0c\u201c\u827e\u5f25\u8587\uff0c\u4f60\u4eec\u2026\u2026\u201d</p><p>\u201c\u56de\u53bb\u7761\u89c9\u5427\uff0c\u201d\u6885\u83f2\u65af\u82e5\u65e0\u5176\u4e8b\u5730\u7528\u624b\u5e15\u64e6\u53bb\u5507\u8fb9\u7684\u75d5\u8ff9\uff0c\u201c\u4eca\u665a\u4e0d\u80fd\u966a\u4f60\u7761\u4e86\u3002\u201d</p><p>\u51db\u6014\u6014\u5730\u770b\u7740\u4e24\u4eba\uff0c\u8fc7\u4e86\u5927\u7ea6\u5341\u79d2\u949f\uff0c\u8138\u4e0a\u7a81\u7136\u53c8\u9732\u51fa\u90a3\u79cd\u62db\u724c\u5f0f\u7684\u72e1\u9ee0\u7b11\u5bb9\u6765\uff0c\u201c\u77e5\u9053\u5566\u77e5\u9053\u5566\uff0c\u201d\u5979\u7528\u5c0f\u6076\u9b54\u822c\u53ef\u7231\u7684\u58f0\u97f3\u8bf4\uff0c\u201c\u90a3\u4e48\uff0c\u665a\u5b89\u54e6\uff0c\u4e24\u4f4d\u3002\u201d\u6325\u6325\u624b\uff0c\u62b1\u7740\u6795\u5934\u51fa\u4e86\u623f\u95f4\uff0c\u987a\u624b\u628a\u95e8\u5173\u4e0a\u4e86\u3002</p><p>\u743c\u6069\u4e5f\u88ab\u8fd9\u7a81\u5982\u5176\u6765\u7684\u53d8\u6545\u7ed9\u60ca\u5230\u4e86\uff0c\u4ed6\u660e\u660e\u8bb0\u5f97\u81ea\u5df1\u662f\u5173\u4e0a\u4e86\u95e8\u7684\uff0c\u600e\u4e48\u4f1a\u88ab\u51db\u4e00\u63a8\u5c31\u5f00\u4e86\uff0c\u96be\u9053\u95e8\u9501\u574f\u4e86\u4e48\u3002\u867d\u8bf4\u4ed6\u8138\u76ae\u5f88\u539a\uff0c\u4f46\u8fd9\u79cd\u4e8b\u60c5\u88ab\u770b\u5230\uff0c\u603b\u8fd8\u662f\u9887\u6709\u4e9b\u96be\u582a\uff0c\u201c\u5bf9\u4e0d\u8d77\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u9053\u6b49\u7740\uff0c\u201c\u6211\u2026\u2026\u201d</p><p>\u201c\u6ca1\u4e8b\uff0c\u201d\u6885\u83f2\u65af\u4f9d\u65e7\u5f88\u51b7\u9759\uff0c\u201c\u6211\u4eec\u505a\u4ec0\u4e48\u4e8b\u60c5\u53c8\u4e0d\u7528\u5979\u7ba1\u2026\u2026\u771f\u662f\u7684\uff0c\u201d\u5979\u7ec8\u7a76\u8fd8\u662f\u6709\u4e9b\u61ca\u6094\u5730\u6447\u6447\u5934\uff0c\u201c\u521a\u624d\u6211\u90a3\u6837\u5b50\u4e00\u5b9a\u5f88\u96be\u770b\u5427\uff0c\u88ab\u5979\u770b\u89c1\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u7b2c\u4e8c\u5929\u65e9\u9910\u7684\u65f6\u5019\uff0c\u51db\u7684\u795e\u60c5\u4fbf\u6709\u4e9b\u8be1\u5f02\uff0c\u8d81\u7740\u6885\u83f2\u65af\u8f6c\u8eab\u53bb\u53d6\u5976\u6cb9\u7684\u65f6\u5019\uff0c\u5979\u6084\u6084\u51d1\u5230\u743c\u6069\u8eab\u8fb9\u6765\uff0c\u201c\u5582\uff0c\u743c\u6069\uff0c\u4f60\u4eec\u4e00\u76f4\u90fd\u50cf\u6628\u665a\u90a3\u6837\u89e3\u51b3\u554a\uff1f\u201d</p><p>\u743c\u6069\u95ed\u7740\u5634\uff0c\u62d2\u7edd\u56de\u7b54\u8fd9\u79cd\u95ee\u9898\u3002</p><p>\u8fd9\u79cd\u6001\u5ea6\u7406\u6240\u5f53\u7136\u88ab\u89c6\u4e3a\u9ed8\u8ba4\uff0c\u5f53\u7136\u4e8b\u5b9e\u4e5f\u786e\u5b9e\u5982\u6b64\u3002\u201c\u771f\u53ef\u601c\u5462\uff0c\u201d\u51db\u70b9\u70b9\u5934\uff0c\u8138\u4e0a\u9732\u51fa\u6df1\u8868\u540c\u60c5\u7684\u6837\u5b50\uff0c\u201c\u6709\u8fd9\u6837\u6f02\u4eae\u7684\u60c5\u4eba\uff0c\u5374\u53ea\u80fd\u770b\u4e0d\u80fd\u5403\uff0c\u4e00\u5b9a\u5f88\u90c1\u95f7\u5427\u3002\u201d</p><p>\u201c\u6211\u8bf4\uff0c\u51db\u5c0f\u59d0\uff0c\u201d\u743c\u6069\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u5f00\u53e3\uff0c\u201c\u4f60\u5149\u987e\u7740\u8bf4\u8bdd\uff0c\u725b\u5976\u90fd\u51c9\u4e86\u3002\u201d</p><p>\u201c\u54e6\uff0c\u6211\u4e0d\u559d\u725b\u5976\u4e86\uff0c\u201d\u51db\u751c\u751c\u5730\u7b11\u7740\uff0c\u201c\u770b\u5230\u6628\u665a\u90a3\u79cd\u60c5\u666f\uff0c\u6211\u8106\u5f31\u7684\u5fc3\u7075\u88ab\u8499\u4e0a\u4e86\u9634\u5f71\uff0c\u6682\u65f6\u662f\u559d\u4e0d\u4e0b\u53bb\u725b\u5976\u8fd9\u79cd\u5f62\u6001\u76f8\u8fd1\u7684\u6db2\u4f53\u4e86\u3002\u201d</p><p>\u743c\u6069\u611f\u89c9\u81ea\u5df1\u5934\u4e0a\u5728\u5192\u9ed1\u7ebf\uff0c\u201c\u51db\u5c0f\u59d0\uff0c\u4f5c\u4e3a\u5973\u5b69\u5b50\uff0c\u4f60\u4e0d\u89c9\u5f97\u4f60\u8bf4\u8fd9\u79cd\u8bdd\u6709\u635f\u6dd1\u5973\u5f62\u8c61\u4e48\u3002\u201d</p><p>\u201c\u7136\u800c\u6211\u4ece\u6765\u5c31\u4e0d\u662f\u6dd1\u5973\u5440\uff0c\u201d\u5979\u7ee7\u7eed\u5c55\u9732\u7740\u90a3\u5c0f\u6076\u9b54\u5f0f\u7684\u7b11\u5bb9\uff0c\u201c\u827e\u5f25\u8587\u624d\u662f\u6dd1\u5973\u561b\uff0c\u6240\u4ee5\u4f1a\u548c\u4f60\u505a\u90a3\u79cd\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u201c\u51db\u3002\u201d</p><p>\u6885\u83f2\u65af\u7684\u58f0\u97f3\u4ece\u8eab\u540e\u4f20\u6765\uff0c\u5e73\u9759\u5982\u6c34\uff0c\u4e0d\u5e26\u4e1d\u6beb\u8d77\u4f0f\uff0c\u4eff\u4f5b\u53ea\u662f\u968f\u53e3\u53eb\u5979\u4e00\u58f0\uff0c\u4f46\u51db\u7acb\u523b\u683c\u683c\u7b11\u7740\uff0c\u56de\u5230\u81ea\u5df1\u4f4d\u7f6e\u4e0a\uff0c\u4e56\u4e56\u5730\u5bf9\u4ed8\u9762\u524d\u7684\u65e9\u9910\uff0c\u201c\u522b\u751f\u6c14\uff0c\u827e\u5f25\u8587\uff0c\u201d\u5979\u4e00\u8fb9\u5f80\u5634\u91cc\u585e\u9762\u5305\u4e00\u8fb9\u975e\u5e38\u65e0\u8f9c\u5730\u8bf4\uff0c\u201c\u6211\u53d1\u8a93\u6211\u6ca1\u6709\u52fe\u5f15\u4f60\u7684\u60c5\u4eba\u54e6\uff0c\u53ea\u662f\u63a2\u8ba8\u4e00\u4e9b\u7406\u8bba\u95ee\u9898\u7f62\u4e86\u3002\u201d</p><p>\u201c\u54e6\uff0c\u52fe\u5f15\u4ed6\u6211\u4e5f\u4e0d\u4ecb\u610f\u3002\u201d</p><p>\u201c\u6ca1\u5174\u8da3\uff0c\u6ca1\u5174\u8da3\uff0c\u201d\u51db\u6446\u6446\u624b\uff0c\u5938\u5f20\u5730\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u201c\u7cdf\u4e86\uff0c\u6628\u665a\u6ca1\u7761\u597d\uff0c\u8fd9\u6837\u5b50\u4f1a\u6ca1\u7cbe\u795e\u51c6\u5907\u9b54\u6cd5\u5462\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u6ca1\u7761\u597d\uff1f\u4f60\u4e0d\u662f\u4e00\u5411\u7761\u89c9\u5f88\u6c89\u7684\u4e48\u3002\u201d</p><p>\u201c\u8fd9\u8fd8\u7528\u95ee\u561b\u827e\u5f25\u8587\uff0c\u201d\u51db\u7ee7\u7eed\u751c\u751c\u5730\u7b11\uff0c\u201c\u770b\u5230\u6628\u665a\u4f60\u7684\u8868\u73b0\uff0c\u6211\u5982\u679c\u7761\u5f97\u7740\u624d\u6bd4\u8f83\u5947\u602a\u5427\u3002\u201d</p><p>\u201c\u90a3\u771f\u662f\u62b1\u6b49\u4e86\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4e0d\u8fc7\u53cd\u6b63\u8def\u4e0a\u4e5f\u6ca1\u4e8b\uff0c\u8981\u4e0d\u8981\u5728\u8f66\u4e0a\u518d\u7761\u4f1a\uff1f\u6211\u4fdd\u8bc1\u80fd\u8ba9\u4f60\u505a\u4e2a\u597d\u68a6\u7684\u3002\u201d</p><p>\u542c\u5230\u8fd9\u53e5\u9690\u542b\u5a01\u80c1\u7684\u8bdd\uff0c\u51db\u4e56\u4e56\u95ed\u4e0a\u5634\uff0c\u5f00\u59cb\u5bf9\u4ed8\u65e9\u9910\u3002</p><p>\u4ed6\u4eec\u6b64\u65f6\u662f\u5728\u4f0a\u65af\u5361\u5170\u7279\u57ce\uff0c\u6309\u7167\u884c\u7a0b\uff0c\u4eca\u5929\u4e0b\u5348\u5c31\u80fd\u62b5\u8fbe\u963f\u683c\u62c9\u9686\u7684\u8fb9\u5883\u5173\u5361\u3002\u7ea2\u888d\u5deb\u5e08\u4f1a\u6d3e\u6765\u7684\u90a3\u4e2a\u8f66\u592b\u6253\u9053\u56de\u5e9c\uff0c\u4ed6\u4eec\u5728\u57ce\u91cc\u53e6\u5916\u53c8\u96c7\u4e86\u4e00\u4e2a\u3002\u7a0d\u4f5c\u4fee\u6574\uff0c\u4e00\u884c\u4eba\u5373\u4fbf\u4e0a\u8def\u3002</p><p>\u4e00\u8def\u5e73\u5b89\u65e0\u4e8b\uff0c\u4e0b\u5348\u4e09\u70b9\u949f\uff0c\u4ed6\u4eec\u62b5\u8fbe\u8fb9\u5883\u3002\u6885\u83f2\u65af\u9012\u4e0a\u4f2a\u9020\u7684\u8eab\u4efd\u8bc1\u660e\uff0c\u5979\u73b0\u5728\u662f\u585e\u5c14\u827e\u5c14\u5854\u67cf\u7701\u7684\u4e00\u4e2a\u4e09\u6d41\u8d35\u65cf\u4e4b\u5973\uff0c\u524d\u6765\u963f\u683c\u62c9\u9686\u65c5\u6e38\u3002\u4e24\u56fd\u867d\u7136\u4e16\u4ec7\uff0c\u5982\u4eca\u5df2\u7ecf\u7b7e\u7f72\u548c\u5e73\u534f\u8bae\uff0c\u963f\u683c\u62c9\u9686\u5bf9\u7ea2\u888d\u5deb\u5e08\u9887\u4e3a\u8b66\u60d5\uff0c\u4f46\u5bf9\u666e\u901a\u7684\u585e\u5c14\u4eba\u5012\u4e5f\u5e76\u4e0d\u591a\u4e48\u654c\u89c6\u3002\u585e\u5c14\u867d\u7136\u662f\u5deb\u5e08\u79c9\u653f\uff0c\u5374\u5e76\u4e0d\u7b49\u4e8e\u8bf4\u6240\u6709\u7684\u8d35\u65cf\u90fd\u662f\u7ea2\u888d\u5deb\u5e08\uff0c\u533a\u533a\u4e00\u5343\u6b63\u5f0f\u6210\u5458\u7684\u7ea2\u888d\u5deb\u5e08\u4f1a\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5b8c\u5168\u5360\u636e\u4e94\u767e\u4e07\u4eba\u53e3\u5927\u56fd\u7684\u7edf\u6cbb\u9636\u5c42\uff0c\u66f4\u4f55\u51b5\u5deb\u5e08\u7684\u672c\u804c\u7ec8\u7a76\u662f\u7814\u7a76\u5965\u6cd5\u800c\u4e0d\u662f\u884c\u653f\u5de5\u4f5c\u3002\u6885\u83f2\u65af\u7684\u8fd9\u4f2a\u9020\u8eab\u4efd\uff0c\u65e2\u6ca1\u6709\u5f15\u8d77\u6000\u7591\uff0c\u4e5f\u6ca1\u6709\u5f15\u8d77\u6ce8\u610f\uff0c\u5f88\u7b80\u5355\u5730\u5c31\u653e\u884c\u4e86\u3002\u963f\u683c\u62c9\u9686\u4ee5\u524d\u662f\u7cbe\u7075\u738b\u56fd\uff0c\u5386\u53f2\u60a0\u4e45\uff0c\u8bba\u6587\u660e\u53d1\u6e90\u8fd8\u5728\u4eba\u7c7b\u738b\u56fd\u4e4b\u524d\uff0c\u56fd\u5185\u6709\u4f17\u591a\u53e4\u8001\u9057\u8ff9\uff0c\u591a\u6709\u6e38\u5ba2\u6765\u5f80\uff0c\u4e5f\u662f\u5f88\u5e73\u5e38\u7684\u4e8b\u60c5\u3002</p><p>\u8fc7\u4e86\u8fb9\u5883\uff0c\u987a\u7740\u5927\u8def\u4e00\u76f4\u524d\u884c\uff0c\u5de6\u8fb9\u662f\u7ef5\u5ef6\u4e0d\u7edd\u7684\u8fdc\u53e4\u68ee\u6797\uff0c\u53f3\u8fb9\u662f\u4e00\u671b\u65e0\u9645\u7684\u5927\u5e73\u539f\uff0c\u4e00\u8def\u4e0a\u90fd\u6ca1\u770b\u5230\u4ec0\u4e48\u4eba\u3002\u963f\u683c\u62c9\u9686\u7684\u56fd\u571f\u9762\u79ef\u548c\u585e\u5c14\u76f8\u5f53\uff0c\u4eba\u53e3\u5374\u53ea\u6709\u585e\u5c14\u7684\u4e94\u5206\u4e4b\u4e00\uff0c\u800c\u4e14\u5927\u90e8\u5206\u90fd\u96c6\u4e2d\u5728\u4e1c\u90e8\u548c\u5317\u90e8\uff0c\u7cbe\u7075\u65cf\u85cf\u8eab\u6797\u6d77\u4e4b\u4e2d\u3002\u743c\u6069\u7b49\u4eba\u8d70\u7684\u662f\u5357\u90e8\u5927\u9053\uff0c\u81ea\u7136\u770b\u4e0d\u5230\u4ec0\u4e48\u4eba\u5f71\u3002\u4e09\u4eba\u90fd\u5728\u9a6c\u8f66\u8f66\u53a2\u91cc\uff0c\u6885\u83f2\u65af\u4f5c\u4e3a\u961f\u957f\uff0c\u5728\u57cb\u5934\u7814\u7a76\u90a3\u4e00\u5927\u5806\u8d44\u6599\uff0c\u5b89\u6392\u65e5\u540e\u884c\u7a0b\u3002\u51db\u5219\u7f20\u7740\u743c\u6069\uff0c\u60f3\u5b66\u4e60\u90a3\u201c\u9006\u8f6c\u7206\u70b8\u201d\uff0c\u8fd9\u6cd5\u672f\u867d\u7136\u7528\u9014\u6781\u5176\u72ed\u7a84\uff0c\u7136\u800c\u5bf9\u4e8e\u5979\u8fd9\u79cd\u53ea\u7231\u7528\u706b\u7130\u9b54\u6cd5\u653b\u51fb\u7684\u5deb\u5e08\u6765\u8bf4\u6070\u597d\u662f\u514b\u661f\uff0c\u81ea\u7136\u4e5f\u60f3\u638c\u63e1\u3002\u743c\u6069\u88ab\u5979\u7f20\u5f97\u6ca1\u529e\u6cd5\uff0c\u6700\u540e\u7b54\u5e94\u53cc\u65b9\u4e92\u76f8\u6284\u5f55\u9b54\u6cd5\u4e66\uff0c\u51db\u53ef\u4ee5\u4ece\u743c\u6069\u7684\u9b54\u6cd5\u4e66\u91cc\u6284\u5f55\u90a3\u4efd\u9006\u8f6c\u7206\u70b8\u7684\u8d44\u6599\uff0c\u4f5c\u4e3a\u56de\u62a5\uff0c\u743c\u6069\u53ef\u4ee5\u4ece\u51db\u7684\u9b54\u6cd5\u4e66\u91cc\u6284\u5f55\u4e09\u4e2a\u6cd5\u672f\u3002</p><p>\u51db\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u5979\u81ea\u4ece\u5f53\u5e74\u548c\u6885\u83f2\u65af\u5206\u624b\u540e\uff0c\u4e00\u76f4\u5728\u8fd9\u5927\u9646\u4e1c\u57df\u8bf8\u56fd\u6e38\u5386\uff0c\u540e\u6765\u624d\u5230\u4e86\u585e\u5c14\u3002\u4ece\u5979\u7684\u9b54\u6cd5\u4e66\u6765\u770b\u4e5f\u786e\u5b9e\u5982\u6b64\uff0c\u9664\u4e86\u5404\u79cd\u7ea2\u888d\u5deb\u5e08\u53d1\u660e\u7684\u6cd5\u672f\u4e4b\u5916\uff0c\u8fd8\u8bb0\u8f7d\u4e86\u5f88\u591a\u4e1c\u57df\u5176\u4ed6\u8bf8\u56fd\u6d41\u884c\u7684\u9b54\u6cd5\u3002\u743c\u6069\u5148\u6284\u5f55\u4e86\u4e00\u4efd\u201c\u706b\u4e2d\u8df3\u8dc3\u201d\u7684\u8d44\u6599\uff0c\u518d\u6284\u5f55\u4e86\u4e00\u4efd\u201c\u8fdb\u9636\u5deb\u5e08\u62a4\u7532\u201d\uff0c\u5deb\u5e08\u62a4\u7532\u662f\u79cd\u7b80\u5355\u6cd5\u672f\uff0c\u80fd\u4e3a\u5deb\u5e08\u63d0\u4f9b\u4e00\u5b9a\u7684\u529b\u573a\u4fdd\u62a4\uff0c\u6548\u679c\u5f88\u5e73\u5e73\uff0c\u743c\u6069\u4ee5\u524d\u5c31\u89c1\u8fc7\u8d44\u6599\uff0c\u4f46\u4e5f\u61d2\u5f97\u82b1\u65f6\u95f4\u53bb\u5b66\uff1b\u51db\u7684\u9b54\u6cd5\u4e66\u4e0a\u6240\u8bb0\u8f7d\u7684\uff0c\u5374\u662f\u67d0\u4f4d\u5927\u5deb\u5e08\u6539\u8fdb\u8fc7\u7684\u5f3a\u5316\u5347\u7ea7\u7248\u672c\uff0c\u743c\u6069\u4e8e\u662f\u4fbf\u6284\u5f55\u4e86\u4e00\u4efd\u3002</p><p>\u6700\u540e\u6284\u5f55\u7684\u662f\u201c\u82df\u62c9\u53f8\u6258\u65af\u78c1\u5316\u672f\u201d\uff08Ghorus Toth's Magnetism\uff09\uff0c\u4e00\u79cd\u975e\u5e38\u9634\u9669\u7684\u53d8\u5316\u5b66\u6d3e\u6cd5\u672f\u2014\u2014\u8bdd\u8bf4\u56de\u6765\uff0c\u53d8\u5316\u5b66\u6d3e\u7684\u6cd5\u672f\u539f\u672c\u4e5f\u5c31\u5927\u591a\u9634\u9669\u3002\u4e5f\u4e0d\u77e5\u9053\u8fd9\u4f4d\u53d1\u660e\u8005\u82df\u62c9\u53f8\u6258\u65af\u5230\u5e95\u662f\u4f55\u8bb8\u4eba\u4e5f\uff0c\u4f30\u8ba1\u548c\u743c\u6069\u4e00\u6837\u662f\u4f4d\u7cbe\u7814\u53d8\u5316\u9b54\u6cd5\u7684\u8001\u524d\u8f88\u3002\u8fd9\u9053\u6cd5\u672f\u5e76\u4e0d\u4f24\u4eba\uff0c\u4f46\u5374\u80fd\u5c06\u91d1\u5c5e\u7269\u4f53\u78c1\u5316\uff0c\u5728\u5468\u56f4\u9020\u6210\u4e00\u5708\u5f3a\u5927\u78c1\u573a\uff0c\u5927\u7ea6\u534a\u5f84\u4e09\u5341\u5c3a\u5185\u7684\u91d1\u5c5e\u7269\u4f53\u90fd\u4f1a\u88ab\u731b\u529b\u5438\u649e\u8fc7\u6765\u2014\u2014\u6216\u8005\u78c1\u5316\u7269\u4f53\u88ab\u5438\u8fc7\u53bb\u3002\u53ea\u8981\u73af\u5883\u5408\u9002\uff0c\u8fd9\u6cd5\u672f\u7528\u6765\u5bf9\u4ed8\u90a3\u4e9b\u559c\u6b22\u5168\u8eab\u91d1\u5c5e\u91cd\u7532\u7684\u6218\u58eb\u4eec\u662f\u6700\u597d\u4e0d\u8fc7\u4e86\u3002\u53ea\u662f\u96be\u5ea6\u5f88\u9ad8\uff0c\u743c\u6069\u6682\u65f6\u662f\u5b66\u4e0d\u4e86\uff0c\u5148\u6284\u5f55\u4e86\u518d\u8bf4\u3002</p><p>\u521a\u5c06\u4e09\u4e2a\u6cd5\u672f\u6284\u5b8c\uff0c\u9a6c\u8f66\u7a81\u7136\u505c\u4e0b\u4e86\uff0c\u524d\u65b9\u4f20\u6765\u8f70\u9686\u8f70\u9686\u7684\u9707\u52a8\u58f0\uff0c\u5730\u9762\u90fd\u5728\u9690\u9690\u98a4\u52a8\u3002\u8f66\u592b\u7684\u60ca\u53eb\u58f0\u4ece\u524d\u9762\u4f20\u6765\uff0c\u968f\u5373\u8fde\u6eda\u5e26\u722c\u5730\u8dd1\u4e0b\u9a6c\u8f66\uff0c\u94bb\u8fdb\u65c1\u8fb9\u7684\u68ee\u6797\u91cc\u53bb\u4e86\uff0c\u52a8\u4f5c\u654f\u6377\u5f97\u4ee4\u4eba\u77a0\u76ee\u7ed3\u820c\u3002\u743c\u6069\u4ece\u8f66\u53a2\u4e2d\u94bb\u51fa\uff0c\u8fd8\u6ca1\u770b\u6e05\u695a\u662f\u4ec0\u4e48\u56de\u4e8b\uff0c\u5c31\u611f\u89c9\u81ea\u5df1\u88ab\u4e00\u7247\u9634\u5f71\u7b3c\u7f69\uff1b\u4ed6\u540e\u9000\u4e24\u6b65\uff0c\u62ac\u5934\u4e00\u770b\uff0c\u53d1\u73b0\u9053\u8def\u524d\u65b9\u6709\u4e00\u4e2a\u6050\u6016\u7684\u5de8\u5927\u9ed1\u5f71\uff0c\u5c11\u8bf4\u4e5f\u6709\u4e09\u5c42\u697c\u9ad8\uff0c\u8eab\u62ab\u91cd\u7532\uff0c\u53cc\u773c\u900f\u7740\u8840\u7ea2\u7684\u51f6\u5149\uff0c\u624b\u4e2d\u63d0\u7740\u4e00\u67c4\u5ca9\u77f3\u822c\u7684\u780d\u5200\uff0c\u6b63\u4e00\u6b65\u4e00\u6b65\u5730\u671d\u8fd9\u8fb9\u903c\u6765\u3002</p><p>\u743c\u6069\u76b1\u7709\uff0c\u8fce\u7740\u9633\u5149\u4ed6\u5df2\u7ecf\u770b\u5f97\u5f88\u6e05\u695a\uff0c\u8fd9\u4e2a\u9ed1\u8272\u5de8\u4eba\u7684\u989d\u5934\u4e0a\uff0c\u6b63\u6709\u4e00\u6839\u7a81\u51fa\u7684\u9aa8\u523a\u5c16\u89d2\uff0c\u6beb\u65e0\u7591\u95ee\u8fd9\u53c8\u662f\u4e00\u4e2a\u827e\u5c14\u5854\u67cf\u5974\u4ec6\u3002\u4ed6\u4f38\u624b\u4ece\u53e3\u888b\u91cc\u53d6\u51fa\u6750\u6599\uff0c\u6212\u5907\u7740\uff0c\u867d\u8bf4\u65e9\u5c31\u6599\u5230\u4f1a\u6709\u9ebb\u70e6\uff0c\u4e5f\u60f3\u5230\u827e\u5c14\u5854\u67cf\u8fd9\u4e2a\u5927\u6076\u9b54\u4e0d\u4f1a\u4e00\u6b21\u5f92\u52b3\u65e0\u529f\u540e\u5c31\u6b64\u653e\u5f03\uff0c\u80af\u5b9a\u8fd8\u8981\u518d\u6d3e\u4eba\u6765\uff0c\u4f46\u5374\u4e0d\u66fe\u60f3\u5230\u4f1a\u649e\u4e0a\u8fd9\u6837\u4e00\u4e2a\u5de8\u65e0\u9738\uff0c\u770b\u8d77\u6765\uff0c\u6709\u70b9\u5c0f\u9ebb\u70e6\u4e86\u3002</p></article>"}}}]);
