(window.webpackJsonp=window.webpackJsonp||[]).push([[195],{218:function(p){p.exports={chapterTitle:null,title:"\u7b2c195\u7ae0 \u7b2c\u4e00\u6b65\u7684\u6c14\u9b44",content:"<article><h2>\u7b2c195\u7ae0 \u7b2c\u4e00\u6b65\u7684\u6c14\u9b44</h2><p>\u3000\u3000\u5e02\u767e\u4e00\u5e97\u5bf9\u9762\u7684\u5e7f\u544a\u724c\u53f7\u79f0\u8fd9\u5ea7\u57ce\u5e02\u6700\u5177\u4ef7\u503c\u7684\u6237\u5916\u5e7f\u544a\uff0c\u5f20\u606a\u82b1\u4e86\u4e00\u767e\u4e8c\u5341\u4e07\uff0c\u5c31\u662f\u8981\u7ecf\u8fc7\u8fd9\u5ea7\u57ce\u5e02\u6700\u7e41\u534e\u8857\u53e3\u7684\u4eba\u6d41\u62ac\u5934\u5c31\u80fd\u8d6b\u7136\u770b\u5230\u7231\u8fbe\u7535\u5b50\u7684\u6807\u8bc6\u3002</p><p>\u3000\u3000\u5e02\u767e\u4e00\u5e97\u9644\u8fd1\u7684\u4eba\u6d41\u8ba9\u591c\u7a7a\u4e0a\u7a81\u7136\u95ea\u4eae\u7684\u96ea\u767d\u5c04\u706f\u5149\u67f1\u5438\u5f15\u4f4f\uff0c\u4e0d\u7ea6\u800c\u540c\u7684\u770b\u5411\u5c04\u706f\u805a\u7126\u5904\u3002\u5e7f\u544a\u724c\u53f3\u4fa7\u4fcf\u7acb\u5982\u6625\u7684\u6c5f\u9edb\u513f\u51dd\u7738\u6311\u89c6\uff0c\u5728\u5979\u89c6\u7ebf\u7684\u524d\u65b9\uff0c\u6309\u6bd4\u4f8b\u653e\u5927\u7684\u5f71\u789f\u673a\u7acb\u4f53\u6a21\u578b\u8d6b\u7136\u51f8\u51fa\uff0c\u5360\u636e\u5de6\u4e0a\u89d2\u5e7f\u544a\u724c\u4e09\u5206\u4e4b\u4e00\u5de6\u53f3\u7684\u7248\u9762\uff0c\u7ed9\u4eba\u5f3a\u70c8\u7684\u89c6\u89c9\u51b2\u51fb\u611f\u3002</p><p>\u3000\u3000\u4ece\u516b\u697c\u7684\u843d\u5730\u5927\u7a97\u770b\u8fc7\u53bb\uff0c\u8fd9\u79cd\u5e73\u9762\u5e7f\u544a\u5ba3\u4f20\u753b\u4e0e\u7acb\u4f53\u6a21\u578b\u76f8\u7ed3\u5408\u7684\u5e7f\u544a\u724c\u5c24\u5176\u7684\u70ab\u76ee\uff0c\u4e0e\u4f1a\u7684\u5a92\u4f53\u8bb0\u8005\u90fd\u4e0d\u7ea6\u800c\u540c\u4e3e\u8d77\u624b\u91cc\u7684\u957f\u77ed\u955c\u5934\uff0c\u4fbf\u662f\u8fd9\u5e7f\u544a\u724c\u5c31\u8ba9\u4eba\u7f16\u51fa\u5f88\u591a\u7684\u8bdd\u9898\u3002</p><p>\u3000\u3000\u98de\u5229\u6d66\u5143\u5668\u4ef6\u90e8\u603b\u7ecf\u7406\u5f20\u4e9a\u5e73\u4ece\u8fd9\u4e00\u523b\u5c31\u771f\u6b63\u611f\u89c9\u5230\u7231\u8fbe\u7535\u5b50\u7684\u4e0d\u540c\u51e1\u54cd\uff0c\u8fd9\u4ec5\u4ec5\u662f\u7231\u8fbe\u7535\u5b50\u5168\u56fd\u5e02\u573a\u63a8\u5e7f\u7684\u7b2c\u4e00\u6b65\uff0c\u56fd\u5185\u8fd8\u6ca1\u6709\u54ea\u5bb6\u7535\u5668\u5236\u9020\u5546\u6709\u8fd9\u4e48\u5927\u7684\u624b\u7b14\u3002</p><p>\u3000\u3000\u201c\u5404\u4f4d\u5a92\u4f53\u670b\u53cb\u2026\u2026\u201d\u6c89\u5bc2\u4e24\u5206\u949f\u4e4b\u540e\uff0c\u5f20\u606a\u7684\u5145\u6ee1\u78c1\u6027\u7684\u58f0\u97f3\u518d\u5ea6\u4ece\u626c\u58f0\u5668\u4f20\u51fa\uff0c\u8ba9\u4eba\u610f\u8bc6\u5230\u8fd8\u5904\u4e8e\u53d1\u5e03\u4f1a\u73b0\u573a\uff0c\u201c\u4ece\u4e5d\u4e09\u5e74\u56fd\u5185\u63a8\u51fa\u7b2c\u4e00\u53f0\u5f71\u789f\u673a\uff0c\u5f71\u789f\u673a\u5e02\u573a\u6301\u7eed\u5feb\u901f\u589e\u957f\uff0c\u7231\u8fbe\u7535\u5b50\u6709\u5e78\u4e0e\u76db\u946b\u8d38\u6613\u5728\u9ad8\u5ea6\u4fe1\u4efb\u7684\u57fa\u7840\u4e0a\u5efa\u7acb\u5408\u4f5c\u4f19\u4f34\u5173\u7cfb\uff0c\u5c06\u8054\u5408\u5411\u534e\u4e1c\u5730\u533a\u5e02\u573a\u63a8\u51fa\u7231\u8fbe\u7535\u5b50\u7684\u5f71\u789f\u673a\u4ea7\u54c1\u2026\u2026\u201d\u5f20\u606a\u6d0b\u6d0b\u6d12\u6d12\u8bf4\u4e86\u8fd1\u6709\u4e94\u5206\u949f\u7684\u53d1\u8a00\u8f9e\uff0c\u624d\u5728\u4f17\u4eba\u5982\u96f7\u8f70\u9e23\u7684\u638c\u58f0\u4e2d\u8d70\u4e0b\u53f0\u6765\u3002</p><p>\u3000\u3000\u4f1a\u573a\u4e1c\u5317\u89d2\u8bbe\u7acb\u5c0f\u5c0f\u7684\u5c55\u793a\u53f0\uff0c\u6446\u51fa\u7231\u8fbe\u7535\u5b50\u76ee\u524d\u63a8\u51fa\u7684\u7b2c\u4e00\u6b3e\u5f71\u789f\u673aSD-920\u94f6\u7070\u3001\u73cd\u73e0\u767d\u3001\u70e4\u6f06\u9ed1\u4e09\u53f0\u4e0d\u540c\u989c\u8272\u7684\u6837\u673a\uff0c\u5c55\u53f0\u65c1\u8fb9\u662f\u653e\u6620\u533a\uff0c\u6c5f\u9edb\u513f\u7531\u4ee4\u5c0f\u71d5\u3001\u8bb8\u7ef4\u966a\u7740\u7ad9\u5728\u90a3\u91cc\uff0c\u7ecf\u8fc7\u6237\u5916\u5e7f\u544a\u7684\u5f3a\u70c8\u51b2\u51fb\u4e4b\u540e\uff0c\u5a92\u4f53\u8bb0\u8005\u4f3c\u4e4e\u5bf9\u7231\u8fbe\u7535\u5b50\u7684\u5f62\u8c61\u4ee3\u8a00\u4eba\u66f4\u611f\u5174\u8da3\uff0c\u95ea\u5149\u706f\u95ea\u70c1\u4e0d\u65ad\uff0c\u6c5f\u9edb\u513f\u8fd8\u6ca1\u6709\u767b\u4e0a\u5766\u8361\u661f\u9014\uff0c\u5c31\u8981\u63a5\u53d7\u95ea\u5149\u706f\u7684\u8003\u9a8c\u3002</p><p>\u3000\u3000\u7b80\u77ed\u53d1\u5e03\u4f1a\u7ed3\u675f\u4e4b\u540e\uff0c\u662f\u7231\u8fbe\u7535\u5b50\u4e3e\u529e\u7684\u9152\u4f1a\uff0c\u53d1\u5e03\u4f1a\u4e0e\u9152\u4f1a\u73b0\u573a\u5c31\u9694\u7740\u4e00\u9053\u95e8\u6249\uff0c\u53f6\u5c0f\u6850\u5ba3\u5e03\u9152\u4f1a\u5f00\u59cb\uff0c\u90a3\u9053\u95e8\u6249\u624d\u6084\u7136\u6253\u5f00\uff0c\u5c06\u4e0e\u4f1a\u8005\u5206\u6d41\u5230\u9694\u58c1\u66f4\u52a0\u5f00\u9614\u7684\u9152\u4f1a\u73b0\u573a\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u53ea\u7a7f\u7740\u683c\u5b50\u886c\u886b\uff0c\u7ad9\u5728\u5f20\u606a\u8eab\u8fb9\uff0c\u5012\u663e\u5f97\u5f20\u606a\u4eca\u5929\u7684\u7a7f\u7740\u8fc7\u4e8e\u4e25\u8083\u3002</p><p>\u3000\u3000\u201c\u9884\u795d\u7231\u8fbe\u7535\u5b50\u5d1b\u8d77\u4e8e\u660e\u65e5\u3002\u201d\u53f6\u5efa\u658c\u542b\u7740\u6d45\u7b11\uff0c\u5f20\u606a\u9002\u624d\u5728\u53f0\u4e0a\u7684\u81f4\u8f9e\u8001\u7ec3\u4ece\u5bb9\uff0c\u53ef\u4e0d\u662f\u4e00\u822c\u4eba\u591a\u6f14\u4e60\u51e0\u904d\u5c31\u80fd\u505a\u5230\u7684\uff0c\u5927\u6982\u4ed6\u662f\u90a3\u79cd\u5929\u751f\u5c31\u80fd\u4ece\u5bb9\u5e94\u5bf9\u4f17\u4eba\u77a9\u76ee\u7684\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u5c06\u7a0d\u6709\u4e9b\u95f7\u70ed\u7684\u897f\u670d\u89e3\u5f00\uff0c\u4e00\u9762\u4e0e\u4e0a\u524d\u6765\u795d\u8d3a\u7684\u5e94\u9080\u5609\u5bbe\u53ca\u5a92\u4f53\u8bb0\u8005\u70b9\u5934\u793a\u610f\uff0c\u4e00\u8fb9\u4e0e\u53f6\u5efa\u658c\u8bf4\u9053\uff1a\u201c\u6709\u7a7a\u518d\u4e0e\u4f60\u4e92\u76f8\u5439\u6367\uff0c\u98de\u5229\u6d66\u7684\u5f20\u4e9a\u5e73\u8fd8\u5728\u90a3\u8fb9\uff0c\u4f60\u8981\u4e00\u8d77\u8fc7\u53bb\uff1f\u201d</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u662f\u98de\u5229\u6d66\u5143\u5668\u4ef6\u90e8\u7684\u91cd\u81e3\uff0c\u4ed6\u662f\u5e94\u76db\u946b\u7684\u9080\u8bf7\u8fc7\u6765\u53c2\u52a0\u53d1\u5e03\u4f1a\uff0c\u4f46\u662f\u76db\u946b\u8ddf\u7535\u5b50\u4ea7\u4e1a\u7684\u4e0a\u6e38\u4f9b\u5e94\u5546\u6ca1\u6709\u592a\u5927\u7684\u5173\u8054\uff0c\u4ed6\u53c8\u4e0d\u662f\u98de\u5229\u6d66\u79fb\u52a8\u7535\u8bdd\u90e8\u4e2d\u56fd\u533a\u7684\u603b\u7ecf\u7406\uff0c\u53f6\u5efa\u658c\u5fae\u5fae\u4e00\u7b11\uff0c\u8fd8\u6709\u5176\u4ed6\u91cd\u8981\u4eba\u7269\u8981\u62db\u547c\uff0c\u8ba9\u5f20\u606a\u5148\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u7231\u8fbe\u7535\u5b50\u4e0d\u540c\u51e1\u54cd\u554a\uff0c\u5f88\u5c11\u770b\u5230\u56fd\u5185\u5382\u5546\u6709\u8fd9\u4e48\u5927\u7684\u624b\u7b14\u2026\u2026\u201d\u5f20\u4e9a\u5e73\u6e29\u548c\u7b11\u7740\uff0c\u4ed6\u59cb\u7ec8\u7531\u82cf\u6d25\u4e1c\u966a\u7740\u3002</p><p>\u3000\u3000\u201c\u7231\u8fbe\u7535\u5b50\u80fd\u4e0d\u80fd\u4e0d\u540c\u51e1\u54cd\uff0c\u8fd8\u8981\u9760\u5f20\u5148\u751f\u7684\u652f\u6301\uff01\u201d\u5f20\u606a\u8fd9\u53e5\u8bdd\u5374\u4e0d\u662f\u606d\u7ef4\u4e4b\u8f9e\uff0c\u53ea\u6015\u5f20\u4e9a\u5e73\u4ed6\u73b0\u5728\u8fd8\u6ca1\u6709\u610f\u8bc6\u5230\u98de\u5229\u6d66\u8bbe\u5728\u56fd\u5185\u7684\u673a\u82af\u751f\u4ea7\u57fa\u5730\u662f\u4f55\u7b49\u7684\u91cd\u8981\u3002</p><p>\u3000\u3000\u89e3\u7801\u677f\u4e00\u7ecf\u7814\u5236\u51fa\u6765\uff0c\u751f\u4ea7\u6d41\u7a0b\u76f8\u5f53\u7b80\u5355\u4e00\u4e9b\uff0c\u53ea\u8981\u5e02\u573a\u6709\u591a\u5927\u7684\u9700\u6c42\uff0c\u65af\u9ad8\u67cf\uff08C-CUBE\uff09\u516c\u53f8\u5c31\u80fd\u63a8\u51fa\u591a\u5927\u91cf\u7684\u89e3\u7801\u677f\uff0c\u4e0d\u5b58\u5728\u4ea7\u80fd\u74f6\u9888\u7684\u5236\u7ea6\u95ee\u9898\uff1b\u4f46\u662f\u673a\u82af\u751f\u4ea7\u76f8\u5bf9\u5de5\u5e8f\u590d\u6742\u5f97\u591a\uff0c\u5b58\u5728\u4ea7\u80fd\u74f6\u9888\u5236\u7ea6\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u5c31\u5f20\u606a\u6240\u4e86\u89e3\uff0c\u516b\u4e2a\u6708\u4e4b\u540e\uff0c\u4e5f\u5c31\u4e5d\u516d\u5e74\u6625\u8282\u4e4b\u540e\u7684\u56db\u4e2a\u6708\u5185\uff0c\u672c\u5e94\u6301\u7eed\u7206\u53d1\u7684\u5f71\u789f\u673a\u5e02\u573a\u9500\u91cf\u5374\u6ca1\u6709\u660e\u663e\u7684\u589e\u6da8\uff0c\u6700\u4e3b\u8981\u7684\u539f\u56e0\u5c31\u662f\u98de\u5229\u6d66\u5728\u56fd\u5185\u7684\u751f\u4ea7\u57fa\u5730\u8fbe\u5230\u4ea7\u80fd\u74f6\u9888\uff0c\u673a\u82af\u4f9b\u5e94\u4e0d\u4e0a\u3002\u90a3\u6bb5\u65f6\u95f4\u6709\u7684\u4f01\u4e1a\u9760\u7092\u5356\u98de\u5229\u6d66\u7684\u673a\u82af\u5c31\u53d1\u4e86\u5927\u8d22\u3002</p><p>\u3000\u3000\u98de\u5229\u6d66\u673a\u82af\u57fa\u5730\u516d\u6708\u4efd\u7684VCD\u673a\u82af\u4ea7\u9500\u91cf\u5df2\u7ecf\u8fbe\u5230\u516d\u4e07\u5957\uff0c\u7231\u8fbe\u7535\u5b50\u624d\u7d2f\u79ef\u91c7\u8d2d\u4e00\u5343\u591a\u5957\u673a\u82af\u7247\uff0c\u5b9e\u5728\u7b97\u4e0d\u4e0a\u4ec0\u4e48\u5927\u5ba2\u6237\uff0c\u8981\u4e0d\u662f\u4eca\u5929\u7684\u53d1\u5e03\u4f1a\u7ed9\u5f20\u4e9a\u5e73\u6df1\u523b\u7684\u5370\u8c61\uff0c\u5927\u6982\u7528\u4e0d\u4e86\u591a\u4e45\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u5370\u8c61\u5c31\u7ed9\u7e41\u5fd9\u7684\u4e8b\u52a1\u6240\u78e8\u706d\u4e86\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u7b11\u7740\u8bf4\uff1a\u201c\u98de\u5229\u6d66\u4e00\u76f4\u5728\u56fd\u5185\u81f4\u529b\u5bfb\u627e\u6709\u6f5c\u529b\u7684\u5382\u5546\u5408\u4f5c\uff0c\u4eca\u65e5\u4e00\u884c\uff0c\u8ba9\u6211\u4ece\u7231\u8fbe\u7535\u5b50\u7684\u8eab\u4e0a\u770b\u5230\u5e0c\u671b\u3002\u201d</p><p>\u3000\u3000\u98de\u5229\u6d66\u4f5c\u4e3a\u4e0a\u6e38\u5143\u4ef6\u4f9b\u5e94\u5546\uff0c\u66f4\u5e0c\u671b\u6276\u6301\u6709\u6f5c\u529b\u7684\u4e0b\u6e38\u5236\u9020\u5546\uff0c\u8981\u662f\u8ba9\u98de\u5229\u6d66\u77e5\u9053\u7231\u8fbe\u7535\u5b50\u6b63\u4e3a\u7814\u5236\u673a\u82af\uff0c\u90a3\u5c31\u4f1a\u6362\u4e0a\u53e6\u4e00\u79cd\u6001\u5ea6\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u5f20\u4e9a\u5e73\u7684\u8bdd\u4e0d\u662f\u865a\u4f2a\u5ba2\u5957\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u7231\u8fbe\u7535\u5b50\u8981\u8d70\u7684\u8def\u5f88\u957f\uff0c\u4eca\u5929\u53ea\u662f\u8d70\u51fa\u7b2c\u4e00\u6b65\uff0c\u63a5\u4e0b\u6765\u5c31\u8981\u53bb\u4e0a\u6d77\u3001\u5e7f\u5dde\u3001\u5317\u4eac\u53bb\u6253\u5e02\u573a\uff0c\u76f4\u5230\u5c06\u80dc\u5229\u65d7\u5e1c\u63d2\u904d\u5168\u56fd\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u521a\u624d\u542c\u82cf\u6d25\u4e1c\u4ecb\u7ecd\u4e8e\u7231\u8fbe\u7535\u5b50\u5bf9\u5e02\u573a\u7684\u5206\u6790\u4e0e\u5224\u65ad\uff0c\u4ece\u7231\u8fbe\u7535\u5b50\u4eca\u5929\u8d70\u51fa\u7684\u7b2c\u4e00\u6b65\uff0c\u77e5\u9053\u5f20\u606a\u662f\u4e2a\u505a\u5e02\u573a\u7684\u5929\u624d\u3002\u4e0d\u4ec5\u4ec5\u662f\u4eca\u5929\u7684\u53d1\u5e03\u4f1a\uff0c\u4ed6\u4ece\u82cf\u6d25\u4e1c\u7684\u4ecb\u7ecd\u4e2d\u5f97\u77e5\u7231\u8fbe\u7535\u5b50\u7684\u5e7f\u544a\u4eca\u5929\u665a\u4e0a\u5c06\u5728\u4e2d\u592e\u7535\u89c6\u53f0\u516b\u70b9\u6863\u7535\u89c6\u5267\u7247\u5934\u64ad\u653e\uff0c\u8fd9\u6bb5\u5341\u4e94\u79d2\u7684\u5e7f\u544a\u7247\u6bb5\u73b0\u5728\u6b63\u901a\u8fc7\u653e\u6620\u4eea\u6295\u653e\u5230\u96ea\u767d\u7684\u5899\u58c1\u4e0a\uff0c\u5f3a\u52b2\u7684\u5e7f\u544a\u653b\u52bf\u5373\u5c06\u5c55\u5f00\u2026\u2026</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u505a\u5e02\u573a\u7684\u6c14\u9b44\uff0c\u4e0d\u5f97\u4e0d\u8ba9\u5f20\u4e9a\u5e73\u60ca\u670d\uff0c\u5c31\u7b97\u97e9\u56fd\u4e09\u661f\u96c6\u56e2\u5bf9\u65d7\u4e0b\u5f71\u789f\u673a\u4ea7\u4e1a\u7684\u5e7f\u544a\u6295\u653e\u4e5f\u6ca1\u6709\u8fd9\u4e48\u5927\u7684\u5f3a\u5ea6\u3002</p><p>\u3000\u3000\u76ee\u524d\u4e3a\u6b62\u76f4\u63a5\u91c7\u53d6\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u7247\u7684\u5382\u5546\u5f88\u5c11\uff0c\u6bd5\u7adf\u524d\u671f\u8fdb\u884c\u7cfb\u7edf\u63a7\u5236\u5f00\u53d1\u7684\u6295\u5165\u6bd4\u8f83\u5927\uff0c\u6240\u9700\u65f6\u95f4\u4e5f\u6bd4\u8f83\u957f\uff0c\u636e\u5f20\u4e9a\u5e73\u4e86\u89e3\uff0c\u6709\u5b9e\u529b\u7684\u5382\u5546\u53ea\u6709\u65b0\u79d1\u3001\u6b65\u6b65\u9ad8\u4e24\u5bb6\u76f4\u63a5\u91c7\u7528\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u7247\u6280\u672f\uff0c\u4f46\u662f\u4ed6\u4eec\u7684\u52a8\u4f5c\u90fd\u8981\u6bd4\u7231\u8fbe\u7535\u5b50\u6162\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u65e9\u5728\u4eca\u5e74\u5e74\u521d\u76f4\u63a5\u5728\u65af\u9ad8\u67cf\uff08C-CUBE\uff09\u516c\u53f8\u63a8\u51fa\u7684\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u7247\u4e0a\u5f00\u53d1\u5f71\u789f\u673a\u4ea7\u54c1\uff0c\u53ea\u82b1\u4e86\u534a\u5e74\u65f6\u95f4\u5c31\u63a8\u51fa\u6210\u719f\u7684\u4ea7\u54c1\uff0c\u8ba9\u5f20\u4e9a\u5e73\u6709\u4e9b\u60ca\u559c\uff0c\u98de\u5229\u6d66\u7684\u673a\u82af\u57fa\u5730\u6240\u751f\u4ea7\u7684\u673a\u82af\u66f4\u591a\u7684\u8003\u8651\u4e0e\u65af\u9ad8\u67cf\u7684\u7b2c\u4e8c\u4ee3\u89e3\u7801\u677f\u914d\u5957\uff0c\u5bf9\u4e8e\u7b2c\u4e00\u4ee3\u89e3\u7801\u677f\u800c\u8a00\uff0c\u98de\u5229\u6d66\u7684\u673a\u82af\u529f\u80fd\u8fc7\u4e8e\u5f3a\u5927\uff0c\u4ee5\u81f4\u6709\u4e9b\u6d6a\u8d39\u4e86\u3002\u8fd9\u4f7f\u5f97\u4e09\u661f\u7b49\u8bb8\u591a\u5382\u5546\u751f\u4ea7\u5f71\u789f\u673a\u5e76\u4e0d\u4ece\u98de\u5229\u6d66\u91c7\u8d2d\u673a\u82af\uff0c\u4ed6\u4eec\u9009\u62e9\u66f4\u4fbf\u5b9c\u3001\u4f46\u662f\u8d28\u91cf\u4e0d\u7a33\u5b9a\u7684\u4ea7\u54c1\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u867d\u7136\u5bf9\u7231\u8fbe\u7535\u5b50\u8fd8\u4e0d\u719f\u6089\uff0c\u4f46\u662f\u4ece\u521d\u6b21\u63a5\u89e6\u4e2d\u611f\u53d7\u5230\u7231\u8fbe\u7535\u5b50\u5904\u5904\u9886\u5148\u522b\u4eba\u7684\u7406\u5ff5\uff0c\u76f8\u4fe1\u7231\u8fbe\u7535\u5b50\u6210\u529f\u7684\u53ef\u80fd\u6027\u76f8\u5f53\u5927\uff0c\u4ed6\u4e5f\u671f\u5f85\u7231\u8fbe\u7535\u5b50\u80fd\u6210\u529f\uff0c\u5c31\u5c06\u4fc3\u8fdb\u7b2c\u4e8c\u4ee3\u89e3\u7801\u677f\u5728\u5f71\u789f\u673a\u4e2d\u7684\u5e94\u7528\uff0c\u98de\u5229\u6d66\u4e3a\u7b2c\u4e8c\u4ee3\u89e3\u7801\u677f\u8bbe\u8ba1\u751f\u4ea7\u7684\u673a\u82af\u4e5f\u5c06\u7edf\u6cbb\u6574\u4e2a\u673a\u82af\u5e02\u573a\uff0c\u5f20\u4e9a\u5e73\u5f53\u7136\u5e0c\u671b\u8fd9\u4e00\u65f6\u523b\u8d8a\u65e9\u5230\u6765\u8d8a\u597d\uff0c\u770b\u7740\u5f20\u606a\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u7231\u8fbe\u7535\u5b50\u6709\u4fe1\u5fc3\u505a\u4e2d\u56fd\u7684\u5f71\u789f\u673a\u7b2c\u4e00\u54c1\u724c\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff1a\u201c\u6211\u4eec\u5728\u5e74\u5e95\u4e4b\u524d\u5c06\u5411\u98de\u5229\u6d66\u91c7\u8d2d\u5341\u4e07\u5957\u673a\u82af\uff0c\u5230\u660e\u5e74\uff0c\u6211\u6bcf\u4e2a\u6708\u8981\u5341\u4e07\u5957\u673a\u82af\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7684\u8bdd\u8fd8\u771f\u8ba9\u5f20\u4e9a\u5e73\u5413\u4e86\u4e00\u8df3\uff0c\u76ee\u524d\u97e9\u56fd\u4e09\u661f\u6bcf\u4e2a\u6708\u624d\u80fd\u751f\u4ea72\u4e07\u53f0\u673a\u5b50\u3002\u7231\u8fbe\u7535\u5b50\u7adf\u7136\u60f3\u4e00\u4e3e\u8d85\u8d8a\u4e09\u661f\uff0c\u800c\u98de\u5229\u6d66\u76ee\u524d\u7684\u4ea7\u80fd\u4e0d\u8fdb\u884c\u6269\u5f20\uff0c\u660e\u5e74\u5f00\u8db3\u9a6c\u529b\uff0c\u4e5f\u5c31\u53ea\u80fd\u4f9b\u5e94\u4e0d\u5230\u4e24\u767e\u4e07\u5957\u673a\u82af\uff0c\u7231\u8fbe\u7535\u5b50\u5f20\u53e3\u5c31\u8981\u5927\u534a\u7684\u4efd\u989d\uff0c\u8981\u4e0d\u662f\u4eca\u5929\u7565\u89c1\u7231\u8fbe\u7535\u5b50\u7684\u6c14\u9b44\uff0c\u5f20\u4e9a\u5e73\u53ea\u6015\u8981\u5f20\u606a\u7684\u8bdd\u5f53\u6210\u73a9\u7b11\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u4e9a\u5e73\u4e5f\u662f\u6709\u6db5\u517b\u7684\u4eba\uff0c\u6de1\u6de1\u7684\u7b11\u7740\u8bf4\uff1a\u201c\u4eca\u5e74\u5341\u4e07\u5957\u673a\u82af\uff0c\u7231\u8fbe\u7535\u5b50\u6709\u80fd\u529b\u6d88\u5316\uff0c\u6211\u81ea\u7136\u4f1a\u4f9b\u7ed9\u4f60\uff0c\u660e\u5e74\u4ea7\u9500\u8ba1\u5212\uff0c\u73b0\u5728\u8ba8\u8bba\u8fd8\u65e9\u4e86\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u4e0d\u6653\u5f97\u5f20\u606a\u7684\u6253\u7b97\uff0c\u66f4\u770b\u4e0d\u5230\u660e\u5e74\u6625\u8282\u540e\u7684\u673a\u82af\u4f9b\u5e94\u4f1a\u7a81\u7136\u5403\u7d27\u3002\u7231\u8fbe\u7535\u5b50\u76ee\u524d\u7684\u4ea7\u80fd\u6bcf\u4e2a\u6708\u624d\u4e24\u5343\u53f0\uff0c\u9664\u4e86\u5728\u5382\u623f\u524d\u6269\u5efa\uff0c\u62db\u8058\u5927\u91cf\u719f\u7ec3\u5de5\u5b9e\u884c\u5012\u73ed\u5236\u4e0d\u95f4\u6b47\u751f\u4ea7\uff0c\u4ea7\u80fd\u6bcf\u4e2a\u6708\u80fd\u7a81\u7834\u4e00\u4e07\u53f0\u5c31\u7b97\u4e0d\u9519\u4e86\uff0c\u65b0\u7684\u751f\u4ea7\u57fa\u5730\u8fd8\u4e0d\u6653\u5f97\u51e0\u65f6\u6709\u80fd\u529b\u542f\u52a8\uff0c\u5f20\u606a\u5f20\u53e3\u5c31\u8981\u5341\u4e07\u5957\u673a\u82af\uff0c\u8fd8\u771f\u5413\u4e86\u82cf\u6d25\u4e1c\u4e00\u8df3\u3002\u4f46\u662f\u6bd4\u8d77\u8fd9\u4e2a\u6765\uff0c\u5f20\u606a\u660e\u5e74\u6bcf\u4e2a\u6708\u5341\u4e07\u5957\u673a\u82af\u7684\u8981\u6c42\uff0c\u66f4\u662f\u8ba9\u4ed6\u77aa\u76ee\u7ed3\u820c\uff0c\u4ed6\u8fd8\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u5bf9\u5f71\u789f\u673a\u5e02\u573a\u7adf\u7136\u4e50\u89c2\u5230\u5982\u6b64\u7a0b\u5ea6\uff01</p><p>\u3000\u3000\u5f20\u606a\u62bf\u5634\u7b11\u4e86\u7b11\uff0c\u4eca\u5929\u53ea\u662f\u4ea7\u54c1\u53d1\u5e03\u4f1a\uff0c\u8fd9\u4e00\u6ce2\u7684\u5e7f\u544a\u653b\u52bf\u8fd8\u624d\u521a\u521a\u62c9\u5f00\u5e8f\u5e55\uff0c\u4e4b\u524d\u53ea\u6709\u51e0\u5bb6\u5546\u573a\u52c9\u5f3a\u51b2\u7740\u76db\u946b\u8d38\u6613\u7684\u9762\u5b50\u5c06\u7231\u8fbeVCD\u6446\u4e0a\u67dc\u53f0\u9500\u552e\uff0c\u9500\u552e\u60c5\u51b5\u81ea\u7136\u60e8\u6de1\uff0c\u8981\u662f\u8fd9\u4e9b\u5546\u573a\u7684\u5bb6\u7535\u90e8\u7ecf\u7406\u542c\u5230\u521a\u624d\u8fd9\u756a\u8c08\u8bdd\uff0c\u5927\u6982\u5927\u7259\u90fd\u4f1a\u7b11\u6389\u5427\uff0c\u53ef\u60dc\u554a\uff0c\u5e02\u573a\u7684\u5947\u5999\uff0c\u6ca1\u6709\u7ecf\u5386\u8fc7\u7684\u4eba\u6c38\u8fdc\u4f53\u4f1a\u4e0d\u5230\uff01</p></article>"}}}]);
