(window.webpackJsonp=window.webpackJsonp||[]).push([[196],{218:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e09\u8282 \u5b9d\u77f3\u9b54\u6cd5\u4e4b\u79d8",content:"<article><h2>\u7b2c\u4e09\u5341\u4e09\u8282 \u5b9d\u77f3\u9b54\u6cd5\u4e4b\u79d8</h2><p>\u6240\u8c13\u9f99\u8109\u8005\uff0c\u662f\u6307\u90a3\u4e9b\u62e5\u6709\u5de8\u9f99\u8840\u8109\u7684\u4eba\u3002</p><p>\u4f17\u6240\u5468\u77e5\uff0c\u9f99\u662f\u62e5\u6709\u5929\u8d4b\u65bd\u6cd5\u80fd\u529b\u7684\u751f\u7269\uff0c\u9f99\u8bed\u4e2d\u5929\u7136\u8574\u542b\u9b54\u529b\u3002\u9f99\u6709\u65f6\u5019\u4f1a\u548c\u4eba\u7c7b\u4ea4\u5408\uff0c\u8bde\u4e0b\u540e\u4ee3\uff0c\u5f80\u5f80\u4e5f\u4f1a\u62e5\u6709\u5f02\u4e4e\u5bfb\u5e38\u7684\u9b54\u6cd5\u5929\u8d4b\uff0c\u8fd9\u4e9b\u4eba\u5927\u591a\u4f1a\u8d70\u4e0a\u672f\u58eb\u6216\u8005\u541f\u6e38\u8bd7\u4eba\u7684\u9053\u8def\uff0c\u4ed6\u4eec\u88ab\u901a\u79f0\u4e3a\u201c\u9f99\u8109\u8005\u201d\u3002</p><p>\u51db\u5c31\u662f\u4e00\u4f4d\u9f99\u8109\u8005\u3002</p><p>\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d\u51db\u77aa\u7740\u743c\u6069\uff0c\u201c\u8fd9\u79cd\u4e8b\u60c5\u827e\u5f25\u8587\u4e0d\u5e94\u8be5\u4f1a\u5bf9\u4f60\u8bf4\u2026\u2026\u201d</p><p>\u201c\u6211\u6ca1\u8bf4\u3002\u201d\u6885\u83f2\u65af\u7684\u58f0\u97f3\u4ece\u8f66\u53a2\u91cc\u4f20\u51fa\u3002</p><p>\u201c\u5979\u6ca1\u8bf4\uff0c\u6211\u731c\u7684\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u5fae\u7b11\uff0c\u201c\u4e0d\u8fc7\u770b\u6837\u5b50\u731c\u5bf9\u4e86\u3002\u201d</p><p>\u5929\u8d4b\u5f02\u80fd\uff0c\u81ea\u7136\u610f\u5473\u7740\u795e\u79d8\u8840\u8109\uff0c\u4f55\u51b5\u738b\u5b50\u5df2\u7ecf\u6e05\u695a\u8bf4\u8fc7\uff0c\u51db\u4e0d\u662f\u201c\u7eaf\u7cb9\u7684\u4eba\u7c7b\u201d\u3002\u80fd\u914d\u5f97\u4e0a\u51db\u8fd9\u6837\u5f3a\u5927\u7684\u5929\u8d4b\u5f02\u80fd\u7684\u751f\u7269\u5e76\u4e0d\u591a\uff0c\u6885\u83f2\u65af\u53c8\u5df2\u7ecf\u6e05\u695a\u8bf4\u8fc7\u5979\u4e0d\u662f\u795e\u5b50\uff0c\u90a3\u4e48\u6700\u6709\u53ef\u80fd\u7684\uff0c\u81ea\u7136\u5c31\u662f\u9f99\u8109\u8005\u4e86\u3002</p><p>\u5982\u679c\u4ec5\u4ec5\u662f\u8fd9\u6837\u51ed\u7a7a\u731c\u6d4b\uff0c\u90a3\u743c\u6069\u5012\u8fd8\u4e0d\u6562\u65ad\u8a00\uff0c\u4f46\u518d\u914d\u5408\u4e0a\u5176\u4ed6\u7684\u8bc1\u636e\uff0c\u90a3\u4ed6\u5c31\u6709\u51e0\u5206\u628a\u63e1\u4e86\u3002\u90a3\u6b21\u51db\u53d8\u6210\u6885\u83f2\u65af\u7684\u6a21\u6837\u6765\u9a97\u4ed6\uff0c\u88ab\u4ed6\u6309\u4f4f\u4e86\u80a9\u8180\uff0c\u4f46\u5374\u8f7b\u800c\u6613\u4e3e\u6323\u8131\u4e86\uff0c\u5f53\u65f6\u4ed6\u611f\u89c9\u6309\u5230\u4e86\u4ec0\u4e48\u51b0\u51b7\u575a\u786c\u7684\u4e1c\u897f\uff0c\u50cf\u662f\u91d1\u5c5e\u62a4\u7532\u4e4b\u7c7b\uff1b\u540e\u6765\u5728\u64c2\u53f0\u4e0a\uff0c\u9006\u8f6c\u7206\u70b8\u628a\u51db\u7684\u706b\u7403\u53cd\u5f39\u56de\u53bb\uff0c\u706b\u5149\u4e2d\u4ed6\u9690\u7ea6\u770b\u89c1\u51db\u7684\u624b\u81c2\u4e0a\u6cdb\u8d77\u4e00\u5c42\u51b0\u51b7\u7684\u91d1\u5c5e\u5149\u6cfd\uff0c\u6321\u4f4f\u4e86\u706b\u7130\u707c\u70e7\uff0c\u5f53\u65f6\u4ed6\u4ee5\u4e3a\u662f\u542f\u52a8\u4e86\u4ec0\u4e48\u9632\u5fa1\u6cd5\u672f\uff0c\u6216\u8005\u662f\u7a7f\u4e86\u9690\u5f62\u62a4\u7532\u3002\u4f46\u4e8b\u540e\u518d\u60f3\uff0c\u64c2\u53f0\u6bd4\u8d5b\u4e0d\u51c6\u7528\u88c5\u5907\uff0c\u51db\u4e0d\u592a\u53ef\u80fd\u5728\u8863\u670d\u91cc\u6697\u4e2d\u518d\u7a7f\u4ec0\u4e48\u9690\u5f62\u62a4\u7532\uff0c\u4e5f\u4e0d\u50cf\u662f\u4ec0\u4e48\u6cd5\u672f\uff0c\u5012\u66f4\u50cf\u662f\u67d0\u79cd\u751f\u7269\u7684\u201c\u9cde\u7532\u201d\u3002</p><p>\u56e0\u4e3a\u6709\u5de8\u9f99\u8840\u8109\uff0c\u9f99\u8109\u8005\u867d\u7136\u770b\u4e0a\u53bb\u548c\u5e38\u4eba\u65e0\u5f02\uff0c\u4f46\u5728\u751f\u7406\u7ed3\u6784\u4e0a\u4f1a\u7ee7\u627f\u4e00\u4e9b\u5de8\u9f99\u7684\u7279\u6027\uff0c\u6700\u5e38\u89c1\u7684\u4e00\u79cd\uff0c\u5c31\u662f\u8eab\u4e0a\u4f1a\u957f\u51fa\u9f99\u9cde\u6765\u3002\u9f99\u9cde\u575a\u6108\u91d1\u94c1\uff0c\u5200\u67aa\u4e0d\u5165\uff0c\u81ea\u7136\u4e0d\u6015\u533a\u533a\u62f3\u811a\u4f24\u5bb3\u3002\u6709\u4e9b\u9f99\u8109\u8005\u7684\u9f99\u9cde\u66b4\u9732\u5728\u5916\uff0c\u65e0\u6cd5\u63a9\u9970\uff0c\u6709\u4e9b\u5374\u80fd\u968f\u7740\u5fc3\u610f\u9690\u73b0\uff0c\u4e0d\u9700\u8981\u7684\u65f6\u5019\u5c31\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u51db\u5e94\u8be5\u5c31\u662f\u5c5e\u4e8e\u540e\u8005\u3002</p><p>\u60f3\u660e\u767d\u8fd9\u4e9b\uff0c\u743c\u6069\u5bf9\u51db\u7684\u8eab\u4efd\u4e5f\u5c31\u9690\u7ea6\u731c\u5230\u51e0\u5206\u4e86\uff0c\u867d\u7136\u4e0d\u80fd\u80af\u5b9a\uff0c\u4f46\u501f\u8fd9\u4e2a\u673a\u4f1a\u8bd5\u63a2\uff0c\u679c\u7136\u88ab\u4ed6\u731c\u4e2d\u3002</p><p>\u51db\u6ca1\u6709\u518d\u8bf4\u8bdd\uff0c\u56de\u5230\u8f66\u53a2\u91cc\u3002\u743c\u6069\u5148\u68c0\u67e5\u4e86\u4e00\u4e0b\u5077\u88ad\u8005\u7684\u5c38\u4f53\uff0c\u53d1\u73b0\u53c8\u662f\u4e00\u4e2a\u827e\u5c14\u5854\u67cf\u5974\u4ec6\uff0c\u770b\u6837\u5b50\u662f\u51c6\u5907\u7528\u524d\u9762\u5de8\u4eba\u540c\u4f34\u5438\u5f15\u6ce8\u610f\u529b\uff0c\u5728\u540e\u9762\u6084\u6084\u5077\u88ad\uff0c\u6293\u4f4f\u51db\u5f53\u4eba\u8d28\u903c\u95ee\u60c5\u62a5\u3002\u827e\u5c14\u5854\u67cf\u5974\u4ec6\u88ab\u8d4b\u4e88\u6076\u9b54\u7684\u529b\u91cf\uff0c\u8eab\u4f53\u4e5f\u4f1a\u53d1\u751f\u5f02\u53d8\uff0c\u9664\u4e86\u5934\u4e0a\u957f\u9aa8\u523a\u5c16\u89d2\uff0c\u624b\u4e5f\u4f1a\u53d8\u6210\u5229\u722a\uff0c\u5584\u4e8e\u6398\u7a74\u6316\u571f\uff0c\u5012\u662f\u9669\u4e9b\u88ab\u4ed6\u5077\u88ad\u6210\u529f\u4e86\u3002</p><p>\u743c\u6069\u4ece\u68ee\u6797\u91cc\u628a\u5413\u5f97\u745f\u745f\u53d1\u6296\u7684\u8f66\u592b\u627e\u56de\u6765\uff0c\u4e00\u884c\u4eba\u7ee7\u7eed\u4e0a\u8def\u3002\u63a5\u4e0b\u6765\u7684\u4e09\u5929\u65f6\u95f4\u5012\u662f\u5e73\u5b89\u65e0\u4e8b\uff0c\u5728\u7b2c\u56db\u5929\u7684\u4e0b\u5348\uff0c\u4ed6\u4eec\u62b5\u8fbe\u6b64\u884c\u7684\u76ee\u7684\u5730\uff0c\u745e\u5361\u4e4b\u8db3\u3002</p><p>\u203b\u203b\u203b</p><p>\u745e\u5361\u4e4b\u8db3\u662f\u4e00\u5ea7\u5c0f\u57ce\uff0c\u4f4d\u4e8e\u68ee\u6797\u8fb9\u7f18\uff0c\u5c45\u6c11\u5927\u591a\u662f\u534a\u7cbe\u7075\uff0c\u6574\u4e2a\u57ce\u5e02\u56f4\u7ed5\u7740\u56db\u9897\u5de8\u5927\u53e4\u6811\u800c\u5efa\u3002\u56e0\u4e3a\u5e38\u6709\u8c7a\u72fc\u4eba\u524d\u6765\u88ad\u51fb\uff0c\u57ce\u4e2d\u6c11\u5175\u6212\u5907\u68ee\u4e25\u3002\u4f5c\u4e3a\u7cbe\u7075\u53e4\u57ce\uff0c\u745e\u5361\u4e4b\u8db3\u57ce\u5916\u9887\u6709\u4e9b\u8fdc\u53e4\u9057\u8ff9\uff0c\u6765\u5f80\u6e38\u5ba2\u4e0d\u5c11\uff0c\u6240\u4ee5\u743c\u6069\u4e09\u4eba\u4f4f\u8fdb\u57ce\u91cc\uff0c\u4e5f\u6ca1\u600e\u4e48\u5f15\u4eba\u6ce8\u610f\u3002</p><p>\u201c\u6b7b\u6708\u6cd5\u73e0\u5c31\u843d\u5728\u8fd9\u5730\u65b9\uff1f\u201d\u743c\u6069\u5fc3\u91cc\u6709\u70b9\u5600\u5495\u3002</p><p>\u738b\u5b50\u7684\u9884\u8a00\u5df2\u7ecf\u8bf4\u5f97\u5f88\u6e05\u695a\uff0c\u6b7b\u6708\u6cd5\u73e0\u662f\u843d\u5230\u4e86\u4e00\u4e2a\u795e\u672f\u65bd\u6cd5\u8005\u624b\u4e2d\u3002\u6240\u8c13\u795e\u672f\u65bd\u6cd5\u8005\uff0c\u65e0\u975e\u662f\u6307\u7267\u5e08\u548c\u5fb7\u9c81\u4f9d\uff0c\u6e38\u4fa0\u548c\u5723\u6b66\u58eb\u867d\u7136\u4e5f\u53ef\u80fd\u7565\u901a\u795e\u672f\uff0c\u4f46\u90a3\u70b9\u9020\u8be3\u5b8c\u5168\u53ef\u4ee5\u5ffd\u7565\u4e0d\u8ba1\uff0c\u4ed6\u4eec\u4e5f\u538b\u6839\u79f0\u4e0d\u4e0a\u4ec0\u4e48\u795e\u672f\u65bd\u6cd5\u8005\uff0c\u738b\u5b50\u6240\u6307\u7684\u663e\u7136\u4e0d\u4f1a\u662f\u4ed6\u4eec\u3002</p><p>\u745e\u5361\u4e4b\u8db3\u5f88\u5c0f\uff0c\u5c45\u6c11\u4e0d\u8fc7\u4e94\u5343\uff0c\u4e16\u4ee3\u5c45\u4f4f\u6b64\u5730\uff0c\u60c5\u51b5\u4e5f\u5f88\u5bb9\u6613\u6478\u6e05\u3002\u201c\u57ce\u91cc\u6ca1\u6709\u795e\u6bbf\uff0c\u4e5f\u6ca1\u6709\u5fb7\u9c81\u4f9d\uff0c\u53ea\u6709\u4e00\u4f4d\u7267\u5e08\uff0c\u201d\u6885\u83f2\u65af\u5bf9\u743c\u6069\u548c\u51db\u8bf4\uff0c\u8fd9\u662f\u5979\u63a2\u542c\u6765\u7684\u60c5\u62a5\uff0c\u201c\u540d\u53eb\u8428\u74e6\u5c14\uff0c\u867d\u7136\u662f\u4e2a\u7267\u5e08\uff0c\u4f46\u4e0d\u8d1f\u8d23\u7948\u7977\u548c\u796d\u7940\u8fd9\u4e9b\u795e\u804c\u5de5\u4f5c\uff0c\u800c\u662f\u7f16\u5165\u6c11\u5175\u4e2d\uff0c\u7b97\u662f\u6218\u6597\u4eba\u5458\u3002\u201d</p><p>\u201c\u4e0d\u8d1f\u8d23\u795e\u804c\u5de5\u4f5c\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u4ed6\u662f\u6885\u4e3d\u51ef\u7684\u7267\u5e08\u3002\u201d</p><p>\u963f\u683c\u62c9\u9686\u4eba\u6c11\u6240\u5d07\u62dc\u7684\u795e\u7947\u662f\u5927\u5730\u5973\u795e\u88f3\u63d0\u963f\u3001\u6708\u5973\u795e\u82cf\u4f26\u548c\u7cbe\u7075\u8bf8\u795e\uff0c\u56e0\u4e3a\u662f\u534a\u5c9b\u5730\u533a\uff0c\u9760\u8fd1\u6d77\u8fb9\uff0c\u6240\u4ee5\u6d77\u6d0b\u5973\u795e\u5b89\u535a\u7406\u548c\u6d77\u6218\u4e4b\u795e\u7ef4\u5c14\u5bc7\u4e5f\u6709\u4e9b\u4fe1\u5f92\uff0c\u4f46\u52bf\u529b\u5f88\u5c0f\u3002\u68ee\u6797\u5973\u795e\u6885\u4e3d\u51ef\u5728\u6b64\u5730\u5e76\u65e0\u534a\u70b9\u5f71\u54cd\u529b\uff0c\u65e2\u6ca1\u4ec0\u4e48\u4fe1\u5f92\uff0c\u4e5f\u6ca1\u6559\u4f1a\u548c\u795e\u6bbf\uff0c\u8fd9\u4f4d\u8428\u74e6\u5c14\u65e2\u7136\u662f\u6885\u4e3d\u51ef\u7267\u5e08\uff0c\u81ea\u7136\u4e5f\u5c31\u6ca1\u4ec0\u4e48\u795e\u804c\u5de5\u4f5c\u9700\u8981\u505a\u4e86\u3002</p><p>\u201c\u4e00\u4e2a\u6885\u4e3d\u51ef\u7267\u5e08\u8dd1\u5230\u8fd9\u91cc\u6765\uff1f\u201d\u743c\u6069\u7591\u60d1\u3002</p><p>\u201c\u6211\u6253\u542c\u8fc7\u4e86\uff0c\u4ed6\u662f\u672c\u5730\u4eba\uff0c\u51fa\u751f\u4e8e\u6b64\uff0c\u5341\u5c81\u7684\u65f6\u5019\u79bb\u5bb6\u51fa\u8d70\uff0c\u5728\u5916\u9762\u95ef\u8361\u4e86\u4e09\u5341\u591a\u5e74\uff0c\u9760\u5f53\u4f63\u5175\u4e3a\u751f\uff0c\u542c\u8bf4\u5251\u672f\u4e0d\u9519\uff0c\u540e\u6765\u88ab\u4e00\u4f4d\u8001\u6e38\u4fa0\u5f15\u5bfc\uff0c\u7688\u4f9d\u4e86\u68ee\u6797\u5973\u795e\uff0c\u6210\u4e86\u7267\u5e08\uff0c\u5982\u4eca\u5df2\u7ecf\u5e74\u8fd1\u4e94\u5341\uff0c\u4e0d\u613f\u7ee7\u7eed\u5728\u5916\u6f02\u6cca\uff0c\u4e8e\u662f\u5728\u524d\u51e0\u5e74\u56de\u5230\u4e86\u5bb6\u4e61\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\u3002\u201d</p><p>\u542c\u8d77\u6765\u5012\u4e5f\u6ca1\u4ec0\u4e48\u7834\u7efd\uff0c\u5e74\u8001\u7684\u4f63\u5175\u56de\u5f52\u5bb6\u4e61\uff0c\u4e5f\u662f\u987a\u7406\u6210\u7ae0\u7684\u4e8b\u60c5\u3002\u201c\u90a3\u4ed6\u6709\u6ca1\u6709\u53ef\u80fd\u5c31\u662f\u6211\u4eec\u8981\u627e\u7684\u4eba\uff1f\u201d</p><p>\u65e2\u7136\u6b64\u5730\u6ca1\u6709\u5fb7\u9c81\u4f9d\uff0c\u4ec5\u6709\u8fd9\u4e00\u4e2a\u7267\u5e08\uff0c\u6000\u7591\u7684\u76ee\u6807\u81ea\u7136\u5c31\u8981\u5b9a\u5728\u4ed6\u8eab\u4e0a\u4e86\u3002</p><p>\u201c\u5e94\u8be5\u4e0d\u662f\uff0c\u201d\u6885\u83f2\u65af\u6447\u5934\uff0c\u201c\u4ed6\u8eab\u4e0a\u6ca1\u6709\u591a\u5c11\u795e\u804c\u8005\u7684\u611f\u89c9\u3002\u201d</p><p>\u540c\u4e3a\u65bd\u6cd5\u8005\uff0c\u5deb\u5e08\u548c\u7267\u5e08\u4e0d\u540c\uff0c\u5deb\u5e08\u662f\u9700\u8981\u81ea\u5df1\u5236\u9020\u5f39\u836f\uff08\u51c6\u5907\u9b54\u6cd5\uff09\uff0c\u9700\u8981\u7684\u65f6\u5019\u53d1\u5c04\uff08\u65bd\u6cd5\uff09\uff0c\u800c\u7267\u5e08\u5219\u662f\u901a\u8fc7\u7948\u7977\uff0c\u7531\u795e\u7947\u76f4\u63a5\u8d50\u4e0b\u795e\u672f\uff0c\u704c\u6ce8\u4f53\u5185\uff0c\u9700\u8981\u7684\u65f6\u5019\u91ca\u653e\u3002\u7c97\u7565\u6bd4\u65b9\u7684\u8bdd\uff0c\u5deb\u5e08\u662f\u5c0f\u624b\u5de5\u4e1a\u8005\uff0c\u7267\u5e08\u5219\u662f\u5bb9\u5668\u6216\u8005\u8bf4\u84c4\u7535\u6c60\u2014\u2014\u5f53\u5bb9\u5668\u5f53\u5f97\u4e45\u4e86\uff0c\u7ecf\u53d7\u7684\u795e\u5723\u529b\u91cf\u8d8a\u591a\uff0c\u4eba\u81ea\u7136\u800c\u7136\u4e5f\u4f1a\u6cbe\u67d3\u4e0a\u4e00\u4e9b\u795e\u7684\u6c14\u606f\uff0c\u4e5f\u5c31\u662f\u6885\u83f2\u65af\u6240\u8bf4\u7684\u201c\u795e\u804c\u8005\u7684\u611f\u89c9\u201d\u4e86\uff0c\u6216\u8005\u6076\u6bd2\u70b9\u8bf4\uff0c\u5c31\u662f\u795e\u68cd\u6c14\u8d28\u3002\u8d8a\u9ad8\u9636\u7684\u7267\u5e08\uff0c\u4f8d\u5949\u795e\u7947\u65f6\u95f4\u8d8a\u957f\u7684\u7267\u5e08\uff0c\u8fd9\u79cd\u795e\u68cd\u6c14\u8d28\u8d8a\u91cd\uff0c\u8fd9\u662f\u65e0\u6cd5\u63a9\u76d6\u7684\uff0c\u743c\u6069\u548c\u51db\u81ea\u7136\u770b\u4e0d\u51fa\u6765\uff0c\u4f46\u6885\u83f2\u65af\u662f\u5723\u6b66\u58eb\uff0c\u4ee5\u524d\u6210\u5929\u548c\u7267\u5e08\u6253\u4ea4\u9053\uff0c\u5979\u662f\u80fd\u6e05\u695a\u611f\u89c9\u5230\u7684\u3002</p><p>\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ed6\u867d\u7136\u662f\u4e2a\u7267\u5e08\uff0c\u4f46\u5374\u662f\u4e2a\u5f88\u4f4e\u9636\u7684\u7267\u5e08\uff0c\u4e0d\u53ef\u80fd\u62e5\u6709\u538b\u5236\u6b7b\u6708\u6cd5\u73e0\u7684\u529b\u91cf\uff0c\u662f\u8fd9\u4e2a\u610f\u601d\u5427\u3002\u201d</p><p>\u201c\u5bf9\u3002\u201d\u6885\u83f2\u65af\u80af\u5b9a\u5730\u8bf4\u3002</p><p>\u201c\u5514\u3002\u201d\u743c\u6069\u53f9\u6c14\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u552f\u4e00\u6709\u5acc\u7591\u7684\u5bf9\u8c61\u4e5f\u88ab\u5e72\u8106\u5229\u843d\u5730\u6392\u9664\u4e86\uff0c\u8fd9\u5374\u8981\u5230\u54ea\u91cc\u627e\u90a3\u4e2a\u6b7b\u6708\u6cd5\u73e0\u53bb\u3002</p><p>\u5728\u63a5\u4e0b\u6765\u7684\u4e24\u5929\u91cc\uff0c\u4e09\u4eba\u5206\u5934\u884c\u52a8\uff0c\u56db\u5904\u65c1\u6572\u4fa7\u51fb\u5730\u6253\u542c\uff0c\u5f88\u5feb\u628a\u8fd9\u5ea7\u5c0f\u57ce\u7684\u60c5\u51b5\u7ed9\u6478\u4e86\u4e2a\u904d\u3002\u5927\u7ea6\u662f\u56e0\u4e3a\u957f\u5e74\u50fb\u5c45\u7684\u7f18\u6545\uff0c\u57ce\u4e2d\u5c45\u6c11\u5341\u5206\u6df3\u6734\uff0c\u6216\u8005\u8bf4\u5355\u7eaf\uff0c\u5bf9\u743c\u6069\u7b49\u4eba\u4e1d\u6beb\u6ca1\u6709\u4ec0\u4e48\u7591\u5fc3\u3002\u8c03\u67e5\u5de5\u4f5c\u56e0\u6b64\u4e5f\u8fdb\u884c\u5f97\u5f88\u987a\u5229\uff0c\u7136\u800c\u7ed3\u679c\u66f4\u52a0\u4ee4\u4eba\u5931\u671b\u3002</p><p>\u6574\u5ea7\u57ce\u91cc\uff0c\u770b\u4e0d\u51fa\u534a\u4e2a\u7a0d\u6709\u53ef\u80fd\u7684\u5acc\u7591\u5206\u5b50\u3002</p><p>\u201c\u738b\u5b50\u4f1a\u4e0d\u4f1a\u662f\u9884\u8a00\u51fa\u9519\u4e86\uff1f\u201d\u743c\u6069\u5fc3\u91cc\u5600\u5495\u7740\uff0c\u7136\u800c\u8fd9\u79cd\u53ef\u80fd\u6027\u4e5f\u4e0d\u5927\u3002\u8fd9\u4e48\u91cd\u5927\u7684\u4e8b\u60c5\uff0c\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u82e5\u662f\u6ca1\u6709\u628a\u63e1\uff0c\u600e\u4e48\u6562\u4e71\u6307\u65b9\u5411\u3002</p><p>\u201c\u53ef\u80fd\u4e0d\u5728\u57ce\u91cc\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u5979\u4e5f\u7591\u60d1\u5f97\u7d27\uff0c\u201c\u4f1a\u4e0d\u4f1a\u662f\u5728\u57ce\u5916\uff1f\u201d</p><p>\u745e\u5361\u4e4b\u8db3\u5728\u6538\u6728\u68ee\u6797\u7684\u8fb9\u7f18\uff0c\u4e00\u9762\u662f\u5e73\u539f\uff0c\u57ce\u4e2d\u5c45\u6c11\u8015\u79cd\u4e4b\u5904\uff0c\u4e09\u9762\u662f\u832b\u832b\u6797\u6d77\uff0c\u6df1\u4e0d\u53ef\u6d4b\uff0c\u636e\u8bf4\u5176\u4e2d\u6f5c\u4f0f\u7740\u5404\u79cd\u602a\u7269\uff0c\u6700\u51fa\u540d\u7684\u662f\u5de8\u6811\u5996\u548c\u6811\u5996\u7cbe\uff0c\u5c31\u8fde\u57ce\u4e2d\u6700\u8001\u7ec3\u7684\u730e\u624b\u90fd\u4e0d\u6562\u8fc7\u5206\u6df1\u5165\uff0c\u53ea\u5728\u57ce\u6c60\u5468\u56f4\u72e9\u730e\u3002\u6885\u83f2\u65af\u7684\u610f\u601d\uff0c\u662f\u8bf4\u90a3\u4e2a\u6301\u6709\u6b7b\u6708\u6cd5\u73e0\u7684\u5bb6\u4f19\u5728\u57ce\u5916\u5bc6\u6797\u91cc\u9690\u5c45\uff1f\u90a3\u91cc\u7684\u751f\u5b58\u73af\u5883\u4e5f\u672a\u514d\u592a\u6076\u52a3\u4e86\u5427\u3002</p><p>\u201c\u5982\u679c\u662f\u4e2a\u5fb7\u9c81\u4f9d\u7684\u8bdd\uff0c\u90a3\u4e48\u4e5f\u662f\u5f88\u53ef\u80fd\u7684\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u8fd9\u8bdd\u4e5f\u6709\u9053\u7406\uff0c\u4e8e\u662f\u63a5\u4e0b\u6765\u4e09\u4eba\u91cd\u65b0\u5206\u914d\u4efb\u52a1\u3002\u6885\u83f2\u65af\u4f9d\u65e7\u5728\u57ce\u4e2d\u6253\u63a2\u6d88\u606f\uff0c\u743c\u6069\u548c\u51db\u5219\u53bb\u57ce\u5916\u68ee\u6797\u4e2d\u641c\u7d22\uff0c\u4e4b\u6240\u4ee5\u5982\u6b64\u5b89\u6392\uff0c\u662f\u56e0\u4e3a\u5deb\u5e08\u8131\u8eab\u7684\u65b9\u6cd5\u591a\uff0c\u5c31\u7b97\u9047\u4e0a\u4ec0\u4e48\u5371\u9669\uff0c\u6d6e\u7a7a\u98de\u884c\u4f20\u9001\u6c14\u5316\u5f62\u4f53\uff0c\u600e\u4e48\u6837\u90fd\u80fd\u9003\u56de\u6765\uff0c\u6362\u4e86\u6885\u83f2\u65af\u5c31\u4e0d\u884c\u4e86\u3002</p><p>\u201c\u62ff\u7740\u3002\u201d\u743c\u6069\u53d6\u51fa\u4e24\u5f20\u5377\u8f74\u9012\u7ed9\u51db\u3002</p><p>\u201c\u8fd9\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u4f20\u9001\u672f\uff0c\u4e07\u4e00\u6709\u5e94\u4ed8\u4e0d\u4e86\u7684\u5371\u9669\uff0c\u7528\u5b83\u56de\u6765\u3002\u201d</p><p>\u201c\u54e6\u54e6\uff0c\u4f60\u8fd8\u771f\u4f53\u8d34\u5462\uff0c\u201d\u51db\u7b11\u772f\u772f\u7684\uff0c\u201c\u6211\u73b0\u5728\u6709\u70b9\u660e\u767d\u827e\u5f25\u8587\u4e3a\u4ec0\u4e48\u559c\u6b22\u4f60\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u60f3\u4f60\u767d\u767d\u9001\u547d\u7f62\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e00\u5171\u56db\u5f20\uff0c\u4f60\u6211\u4e00\u4eba\u4e00\u534a\uff0c\u522b\u6d6a\u8d39\u554a\uff0c\u7528\u5b8c\u5c31\u6ca1\u4e86\u3002\u201d</p><p>\u201c\u77e5\u9053\u77e5\u9053\u3002\u201d</p><p>\u63a5\u4e0b\u6765\u7684\u4e24\u5929\u91cc\uff0c\u641c\u5bfb\u5de5\u4f5c\u4f9d\u65e7\u662f\u4e00\u65e0\u6240\u83b7\u3002\u6885\u83f2\u65af\u5728\u57ce\u5185\u4e5f\u6ca1\u63a2\u542c\u51fa\u4ec0\u4e48\u6709\u4ef7\u503c\u7684\u6d88\u606f\uff0c\u743c\u6069\u548c\u51db\u4e5f\u6ca1\u627e\u5230\u4ec0\u4e48\u53ef\u7591\u4eba\u7269\uff0c\u5404\u79cd\u4ece\u672a\u89c1\u8fc7\u7684\u602a\u7269\u5012\u662f\u649e\u4e0a\u4e0d\u5c11\u3002\u743c\u6069\u610f\u5916\u5730\u53d1\u73b0\u4e86\u51e0\u79cd\u5f88\u7f55\u89c1\u7684\u8349\u836f\uff0c\u53ef\u4ee5\u7528\u6765\u914d\u5236\u5f3a\u529b\u5a9a\u836f\u7684\uff0c\u4ed6\u4e00\u65f6\u5174\u8d77\uff0c\u91c7\u4e86\u653e\u5728\u6b21\u5143\u888b\u91cc\uff0c\u51c6\u5907\u4ee5\u540e\u6709\u65f6\u95f4\u914d\u51e0\u74f6\u836f\u6c34\u73a9\u73a9\u3002</p><p>\u8fd9\u5929\u665a\u4e0a\uff0c\u743c\u6069\u4ece\u57ce\u5916\u56de\u6765\uff0c\u4ed6\u9047\u4e0a\u4e86\u4e24\u53ea\u5de8\u6811\u5996\uff0c\u8d39\u4e86\u534a\u5929\u529f\u592b\u624d\u6446\u8131\u7ea0\u7f20\uff0c\u7ed3\u679c\u803d\u8bef\u4e86\u65f6\u95f4\uff0c\u7b49\u8fdb\u57ce\u7684\u65f6\u5019\uff0c\u5df2\u7ecf\u662f\u7e41\u661f\u6f2b\u5929\u4e86\u3002\u5fd9\u788c\u4e86\u4e00\u5929\uff0c\u75b2\u5026\u5f97\u5f88\uff0c\u51c6\u5907\u5148\u56de\u623f\u95f4\u597d\u597d\u4f11\u606f\uff0c\u4ece\u51db\u7684\u623f\u95f4\u95e8\u53e3\u7ecf\u8fc7\u65f6\uff0c\u7a81\u7136\u9690\u7ea6\u542c\u89c1\u91cc\u9762\u4f20\u6765\u67d0\u79cd\u5947\u602a\u7684\u58f0\u97f3\u3002</p><p>\u662f\u90a3\u79cd\u5973\u6027\u5728\u6781\u5ea6\u6109\u60a6\u65f6\u7684\u5a07\u541f\u5598\u606f\u58f0\u3002</p><p>\u201c\u600e\u4e48\u4f1a\uff0c\u51db\u53c8\u6ca1\u60c5\u4eba\u2026\u2026\u96be\u9053\u662f\u548c\u827e\u5f25\u8587\uff1f\u201d</p><p>\u8fd9\u4e2a\u5ff5\u5934\u95ea\u8fc7\uff0c\u743c\u6069\u8111\u4e2d\u7acb\u523b\u6d6e\u73b0\u51fa\u4e00\u526f\u4ee4\u4eba\u55b7\u8840\u7684\u7f8e\u666f\u6765\uff0c\u4e24\u4e2a\u7f8e\u4e3d\u7684\u5c11\u5973\u8d64\u88f8\u7740\u767d\u7389\u822c\u5149\u6d01\u65e0\u6687\u7684\u8eab\u4f53\uff0c\u5f7c\u6b64\u4ea4\u7f20\u5728\u4e00\u8d77\uff0c\u4e92\u76f8\u4eb2\u543b\u629a\u6170\u2026\u2026\u6253\u4f4f\u6253\u4f4f\uff0c\u518d\u60f3\u8c61\u4e0b\u53bb\u8981\u4e0d\u884c\u4e86\u3002</p><p>\u4ed6\u770b\u770b\u56db\u5468\u65e0\u4eba\uff0c\u5148\u9ed8\u53d1\u5492\u8bed\uff0c\u8ba9\u81ea\u5df1\u9690\u5f62\uff0c\u63a5\u7740\u5728\u95e8\u677f\u4e0a\u627e\u5230\u4e86\u4e00\u6761\u7f1d\u9699\u3002\u743c\u6069\u9ed8\u8bf5\u5492\u8bed\uff0c\u5c06\u90a3\u6761\u7f1d\u9699\u5468\u56f4\u7684\u6728\u5934\u8f7b\u5fae\u53d8\u5f62\uff0c\u9732\u51fa\u4e00\u4e2a\u72ed\u957f\u7684\u5b54\u6765\uff0c\u8db3\u591f\u7528\u6765\u5077\u7aa5\u4e86\u3002\u7136\u540e\u4ed6\u8d34\u5728\u95e8\u677f\u4e0a\uff0c\u628a\u773c\u775b\u51d1\u8fc7\u53bb\u4e00\u671b\u2026\u2026</p><p>\u201c\u5514\uff0c\u539f\u6765\u4e0d\u662f\u767e\u5408\uff0c\u662f\u5c0f\u5973\u5b69\u81ea\u5df1\u5728\u5b89\u6170\u81ea\u5df1\u2026\u2026\u201d</p><p>\u867d\u8bf4\u770b\u4e0d\u5230\u9884\u60f3\u4e2d\u7684\u767e\u5408\u6709\u70b9\u5931\u671b\uff0c\u4e0d\u8fc7\u773c\u524d\u7684\u7f8e\u666f\u5374\u4e5f\u7740\u5b9e\u4e0d\u9519\u3002\u51db\u8d64\u88f8\u7740\u7389\u4f53\uff0c\u6d53\u5bc6\u7684\u9ed1\u8272\u957f\u53d1\u94fa\u6d12\u5728\u8eab\u4e0b\uff0c\u4eff\u4f5b\u6bef\u5b50\u4e00\u822c\uff0c\u8d8a\u53d1\u886c\u6258\u8eab\u4f53\u7684\u5149\u6d01\u767d\u7699\uff0c\u6795\u5934\u8fb9\u653e\u7740\u4e03\u516b\u9897\u7ea2\u5b9d\u77f3\uff0c\u5374\u4e0d\u77e5\u9053\u505a\u4ec0\u4e48\u7528\u7684\u3002\u80f8\u53e3\u4e24\u56e2\u7855\u5927\u7684\u67d4\u8f6f\u7c89\u5ae9\u9a84\u50b2\u5730\u633a\u7acb\u51fa\u6765\uff0c\u5927\u7ea6\u662f\u56e0\u4e3a\u76f8\u4e92\u6324\u538b\u7740\uff0c\u770b\u8d77\u6765\u6bd4\u5e73\u65f6\u66f4\u5927\u4e86\u4e00\u500d\uff0c\u5979\u7528\u5de6\u624b\u6258\u7740\uff0c\u4f4e\u5934\u4f38\u51fa\u820c\u5c16\uff0c\u8214\u8210\u7740\u81ea\u5df1\u90a3\u7c89\u8272\u7684\u84d3\u857e\uff0c\u53f3\u624b\u5219\u6df1\u57cb\u5728\u7d27\u5e76\u7684\u53cc\u817f\u4e4b\u95f4\uff0c\u6b63\u8f7b\u5fae\u800c\u5feb\u901f\u5730\u98a4\u52a8\u7740\uff0c\u900f\u660e\u7684\u6db2\u4f53\u6b63\u987a\u7740\u624b\u80cc\u7f13\u7f13\u6d41\u4e0b\uff0c\u5df2\u7ecf\u5c06\u8eab\u4e0b\u5e8a\u5355\u6253\u6e7f\u4e86\u4e00\u7247\u3002</p><p>\u770b\u5230\u8fd9\u6837\u9999\u8273\u6deb\u7cdc\u7684\u573a\u666f\uff0c\u743c\u6069\u611f\u89c9\u81ea\u5df1\u7684\u547c\u5438\u4e00\u4e0b\u5b50\u90fd\u6025\u4fc3\u8d77\u6765\uff0c\u201c\u8be5\u2026\u2026\u8be5\u6b7b\uff0c\u4e0d\u80fd\u518d\u770b\u4e86\uff0c\u518d\u770b\u8981\u5fcd\u4e0d\u4f4f\u51b2\u8fdb\u53bb\u4e86\u3002\u201d\u4ed6\u5728\u5fc3\u91cc\u544a\u8beb\u7740\u81ea\u5df1\uff0c\u52aa\u529b\u60f3\u8981\u8f6c\u8eab\u79bb\u5f00\uff0c\u7136\u800c\u8eab\u4f53\u5b8c\u5168\u4e0d\u542c\u6307\u4ee4\u3002\u6b63\u5728\u8fd9\u5185\u5fc3\u6fc0\u70c8\u51b2\u7a81\u7684\u65f6\u5019\uff0c\u623f\u95f4\u91cc\u7684\u5c0f\u5973\u5b69\u5374\u53c8\u6709\u4e86\u65b0\u7684\u4e3e\u52a8\uff0c\u5979\u5c06\u53f3\u624b\u4ece\u817f\u95f4\u62bd\u51fa\u6765\uff0c\u7ea4\u957f\u7389\u6307\u62e3\u8d77\u6446\u653e\u5728\u6795\u8fb9\u7684\u4e00\u9897\u7ea2\u5b9d\u77f3\uff0c\u5148\u9001\u5230\u53e3\u4e2d\uff0c\u7528\u820c\u5c16\u6fe1\u6e7f\u4e86\uff0c\u63a5\u7740\u9001\u5230\u53cc\u817f\u4e4b\u95f4\uff0c\u627f\u53d7\u7740\u6b63\u4e0d\u65ad\u6e17\u51fa\u7684\u7231\u6db2\u3002</p><p>\u201c\u8fd9\u2026\u2026\u8fd9\u662f\u5728\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u743c\u6069\u6709\u4e9b\u8bb6\u5f02\uff0c\u4e0d\u77e5\u9053\u51db\u5230\u5e95\u5728\u505a\u4ec0\u4e48\u3002\u623f\u95f4\u91cc\u7684\u5c0f\u5973\u5b69\u5b8c\u5168\u6ca1\u610f\u8bc6\u5230\u6709\u4eba\u5077\u7aa5\uff0c\u5979\u62e3\u8d77\u4e00\u9897\u4e00\u9897\u7684\u5b9d\u77f3\uff0c\u8f6e\u6d41\u91cd\u590d\u7740\u524d\u9762\u7684\u52a8\u4f5c\u3002\u90a3\u4e9b\u7ea2\u5b9d\u77f3\u5438\u6536\u4e86\u5973\u5b69\u7684\u7231\u6db2\uff0c\u987f\u65f6\u90fd\u53d8\u5f97\u707f\u707f\u751f\u5149\u8d77\u6765\uff0c\u6bd4\u5e73\u5e38\u683c\u5916\u7480\u74a8\u660e\u4eae\u4e86\u51e0\u500d\u3002\u6700\u7ec8\uff0c\u6240\u6709\u7684\u5b9d\u77f3\u90fd\u63a5\u53d7\u4e86\u201c\u6d17\u793c\u201d\uff0c\u53ea\u7559\u4e86\u6700\u540e\u4e00\u9897\u6df1\u7ea2\u8272\u7684\u83f1\u5f62\u5b9d\u77f3\u3002</p><p>\u51db\u6240\u7528\u7684\u5b9d\u77f3\uff0c\u5927\u591a\u90fd\u662f\u6253\u78e8\u6210\u692d\u5706\u5f62\uff0c\u6781\u5c11\u6709\u8fd9\u79cd\u83f1\u5f62\u7684\u3002\u743c\u6069\u53ea\u89c1\u8fc7\u4e00\u6b21\uff0c\u5c31\u662f\u7b2c\u4e00\u6b21\u89c1\u5979\u548c\u90a3\u4e2a\u9632\u62a4\u5deb\u5e08\u51b3\u6597\u65f6\uff0c\u4e00\u53d1\u706b\u7403\u653b\u7834\u4e86\u5bf9\u65b9\u7684\u91cd\u91cd\u9632\u62a4\uff0c\u628a\u4ed6\u6253\u4e0b\u64c2\u53f0\u3002\u4e8b\u540e\u8fde\u738b\u5b50\u90fd\u5947\u602a\uff0c\u8fd9\u706b\u7403\u672f\u600e\u4e48\u4f1a\u6ca1\u6709\u88ab\u6b21\u7ea7\u6cd5\u672f\u65e0\u6548\u754c\u7ed9\u6321\u4e0b\u6765\uff0c\u53ea\u80fd\u731c\u6d4b\u662f\u5979\u6709\u4ec0\u4e48\u79d8\u5bc6\u6b66\u5668\u3002</p><p>\u201c\u96be\u9053\u2026\u2026\u201d</p><p>\u743c\u6069\u5ff5\u5934\u672a\u8f6c\uff0c\u5c31\u89c1\u51db\u518d\u5ea6\u5c06\u624b\u63a2\u5165\u7d27\u5e76\u7684\u817f\u95f4\uff0c\u7ea4\u957f\u7684\u7389\u6307\u7075\u5de7\u5730\u6311\u9017\u629a\u6469\u7740\u654f\u611f\u7684\u90e8\u4f4d\u3002\u6ca1\u8fc7\u591a\u4e45\uff0c\u5979\u7684\u547c\u5438\u5c31\u53d8\u5f97\u8d8a\u6765\u8d8a\u6025\u4fc3\uff0c\u8eab\u4f53\u660e\u663e\u5730\u7d27\u7ef7\u8d77\u6765\uff0c\u624b\u7684\u52a8\u4f5c\u4e5f\u8d8a\u6765\u8d8a\u5feb\u3002\u6700\u7ec8\uff0c\u4f34\u968f\u7740\u4e00\u58f0\u60a0\u957f\u6ee1\u8db3\u7684\u53f9\u606f\uff0c\u5979\u6574\u4e2a\u4eba\u677e\u5f1b\u4e0b\u6765\uff0c\u8f6f\u8f6f\u5730\u762b\u5728\u5e8a\u4e0a\uff0c\u7a0d\u4e8b\u5598\u606f\uff0c\u8fde\u5fd9\u62ff\u8d77\u6700\u540e\u5269\u4e0b\u7684\u90a3\u9897\u83f1\u5f62\u5b9d\u77f3\uff0c\u653e\u5728\u817f\u95f4\u6d78\u67d3\u7740\uff0c\u53d1\u51fa\u8d8a\u6765\u8d8a\u4eae\u7684\u7ea2\u5149\u3002</p></article>"}}}]);
