(window.webpackJsonp=window.webpackJsonp||[]).push([[196],{219:function(p){p.exports={chapterTitle:null,title:"\u7b2c196\u7ae0 \u5927\u6e20\u9053\u5546",content:"<article><h2>\u7b2c196\u7ae0 \u5927\u6e20\u9053\u5546</h2><p>\u3000\u3000\u5e94\u76db\u946b\u9080\u8bf7\u800c\u6765\u7684\u7ecf\u9500\u5546\u53ca\u5404\u5927\u5546\u573a\u5bb6\u7535\u90e8\u7684\u7ecf\u7406\u4eec\u81ea\u7136\u7531\u76db\u946b\u7684\u4eba\u53bb\u62db\u5f85\uff0c\u5173\u952e\u7684\u4eba\u7269\uff0c\u53f6\u5efa\u658c\u4f1a\u4e3a\u5f20\u606a\u5f15\u8350\u3002</p><p>\u3000\u3000\u65bd\u6bc5\u662f\u4e0a\u6d77\u505a\u5bb6\u7535\u6279\u53d1\u7684\u5927\u6e20\u9053\u5546\uff0c\u4e0a\u6d77\u5e02\u6c38\u57ce\u4ea4\u5bb6\u7535\u80a1\u4efd\u6709\u9650\u516c\u53f8\u7684\u603b\u7ecf\u7406\uff0c\u56db\u5341\u5c81\uff0c\u4e2a\u5b50\u4e0d\u9ad8\uff0c\u4e09\u89d2\u773c\uff0c\u773c\u795e\u5f88\u9510\u5229\uff0c\u770b\u8d77\u6765\u4e5f\u4e0d\u662f\u90a3\u4e48\u7325\u7410\u3002</p><p>\u3000\u3000\u542c\u53f6\u5efa\u658c\u4ecb\u7ecd\u5b8c\u65bd\u6bc5\uff0c\u5f20\u606a\u5634\u89d2\u6302\u7740\u4ece\u5bb9\u7684\u6d45\u7b11\uff0c\u7b11\u9053\uff1a\u201c\u4e0a\u6d77\u662f\u534e\u4e1c\u5730\u533a\u6700\u91cd\u8981\u7684\u5e02\u573a\uff0c\u80fd\u4e0e\u6c38\u57ce\u8fd9\u6837\u7684\u5927\u6e20\u9053\u5546\u5168\u4f5c\uff0c\u662f\u7231\u8fbe\u7684\u8363\u5e78\u3002\u201d</p><p>\u3000\u3000\u5bf9\u4e8e\u7231\u8fbe\u7535\u5b50\u8fd9\u6837\u9ed8\u9ed8\u65e0\u95fb\u7684\u4f01\u4e1a\uff0c\u65bd\u6bc5\u5fc3\u91cc\u5b9e\u5728\u662f\u63d0\u4e0d\u51fa\u5174\u8da3\uff0c\u5173\u952e\u7231\u8fbe\u7535\u5b50\u7adf\u7136\u6562\u5411\u6c38\u57ce\u63d0\u51fa\u73b0\u6b3e\u8ba2\u8d27\u7684\u8981\u6c42\uff0c\u8ba2\u5355\u6253\u8fc7\u53bb\u7684\u540c\u65f6\uff0c\u8d27\u6b3e\u5c31\u8981\u5373\u65f6\u7ed3\u7b97\u3002</p><p>\u3000\u3000\u65bd\u6bc5\u4e4b\u524d\u662f\u62b9\u4e0d\u4e0b\u9762\u5b50\u62d2\u7edd\u53f6\u5efa\u658c\uff0c\u624d\u7b54\u5e94\u505a\u7231\u8fbe\u7684\u4ee3\u7406\u5546\uff0c\u4ed6\u5c31\u968f\u610f\u8ba2\u4e86\u4e94\u5341\u53f0\u673a\u5b50\uff0c\u7ed9\u7231\u8fbe\u7535\u5b50\u6253\u8fc7\u53bb\u5341\u51e0\u4e07\u8d27\u6b3e\uff0c\u4e5f\u7b97\u662f\u8fd8\u53f6\u5efa\u658c\u7684\u4eba\u60c5\u3002</p><p>\u3000\u3000\u770b\u5230\u7231\u8fbe\u7684\u4ea7\u54c1\u53d1\u5e03\u4f1a\uff0c\u65bd\u6bc5\u6709\u4e9b\u52a8\u5bb9\uff0c\u81ea\u6301\u5927\u6e20\u9053\u5546\u7684\u8eab\u4efd\uff0c\u6f2b\u4e0d\u7ecf\u5fc3\u7684\u5728\u9152\u4f1a\u73b0\u573a\u4e0e\u540c\u884c\u4eec\u804a\u7740\u5929\uff0c\u5f53\u5e02\u767e\u4e00\u5e97\u603b\u7ecf\u7406\u82b1\u5efa\u9f99\u63a5\u5230\u4e00\u4e2a\u7535\u8bdd\u4e4b\u540e\uff0c\u8bf7\u53f6\u5efa\u658c\u5e2e\u5fd9\u518d\u8c03\u4e09\u5341\u53f0\u673a\u5b50\u8fc7\u6765\u652f\u63f4\uff0c\u65bd\u6bc5\u5c31\u6709\u4e9b\u5750\u4e0d\u4f4f\u4e86\u3002</p><p>\u3000\u3000\u5e02\u767e\u4e00\u5e97\u5360\u636e\u8fd9\u680b\u5927\u53a6\u5730\u4e0a\u516d\u5c42\uff0c\u5bb6\u7535\u90e8\u5356\u573a\u8bbe\u5728\u4e09\u697c\uff0c\u65bd\u6bc5\u4e0e\u5176\u4ed6\u5e94\u9080\u8bf7\u800c\u6765\u7684\u6e20\u9053\u5546\u90fd\u6709\u4e9b\u8feb\u4e0d\u53ca\u5f85\u7684\u8d76\u5230\u7231\u8fbe\u5f71\u789f\u673a\u5728\u4e09\u697c\u7684\u4fc3\u9500\u73b0\u573a\u3002</p><p>\u3000\u3000\u5728\u5c04\u706f\u7167\u4eae\u5e7f\u544a\u724c\u90a3\u4e00\u523b\u5f00\u59cb\uff0c\u5e02\u767e\u4e00\u5e97\u4e3a\u4eca\u5929\u4fc3\u9500\u6d3b\u52a8\u5907\u7684\u4e09\u5341\u53f0\u673a\u5b50\u5728\u4e00\u4e2a\u534a\u5c0f\u65f6\u5185\u5168\u90e8\u9500\u5b8c\u3002</p><p>\u3000\u3000\u6ca1\u6709\u5728\u5e02\u767e\u4e00\u5e97\u591a\u5907\u8d27\uff0c\u800c\u73b0\u5728\u79bb\u665a\u4e0a\u7ed3\u675f\u8425\u4e1a\u53ea\u5269\u4e0b\u4e0d\u5230\u534a\u4e2a\u5c0f\u65f6\u7684\u65f6\u95f4\uff0c\u5c31\u662f\u8981\u95f9\u51fa\u4e9b\u52a8\u9759\u5438\u5f15\u4eca\u5929\u5e94\u9080\u800c\u6765\u7684\u7ecf\u9500\u5546\u4eec\u7684\u6ce8\u610f\u3002</p><p>\u3000\u3000\u9664\u4e86\u5404\u79cd\u4f20\u5a92\u53ef\u89c1\u7684\u663e\u6027\u5e7f\u544a\u5f62\u5f0f\u4e4b\u5916\uff0c\u7ecf\u9500\u5546\u672c\u8eab\u5177\u5907\u7684\u9690\u6027\u5e7f\u544a\u4f5c\u7528\uff0c\u6700\u4e0d\u5bb9\u5ffd\u89c6\u3002</p><p>\u3000\u3000\u7ecf\u9500\u5546\u7684\u79ef\u6781\u4e0e\u5426\uff0c\u51b3\u5b9a\u7ec8\u7aef\u5e02\u573a\u7684\u94fa\u8d27\u5f3a\u5ea6\u3002</p><p>\u3000\u3000\u4e00\u65e6\u7231\u8fbe\u7684\u5f71\u789f\u673a\u904d\u53ca\u4e00\u5ea7\u57ce\u5e02\u4e3b\u8981\u5546\u573a\u7684\u5bb6\u7535\u67dc\u53f0\uff0c\u5e76\u4e14\u5360\u636e\u76f8\u5bf9\u8f83\u4f73\u7684\u5c55\u793a\u4f4d\u7f6e\uff0c\u53c8\u5f97\u67dc\u53f0\u4eba\u5458\u7684\u91cd\u70b9\u63a8\u8350\uff0c\u6548\u679c\u7edd\u5bf9\u6bd4\u4e0a\u4e2d\u592e\u7535\u89c6\u53f0\u6253\u5e7f\u544a\u8fd8\u8981\u5f3a\u51e0\u5206\u3002</p><p>\u3000\u3000\u5404\u5730\u7ecf\u9500\u5546\u79ef\u6781\u914d\u5408\uff0c\u53ef\u4ee5\u6700\u5927\u9650\u5ea6\u7684\u964d\u4f4e\u5e02\u573a\u63a8\u5e7f\u4e0e\u5ba3\u4f20\u7684\u6210\u672c\uff0c\u6700\u5927\u9650\u5ea6\u7684\u63d0\u9ad8\u5e02\u573a\u63a8\u5e7f\u4e0e\u5ba3\u4f20\u7684\u6548\u679c\uff0c\u4ee5\u7231\u8fbe\u63d0\u51fa\u73b0\u6b3e\u8ba2\u8d27\u6a21\u5f0f\uff0c\u7ecf\u9500\u5546\u8d8a\u662f\u79ef\u6781\u4e3b\u52a8\uff0c\u8d8a\u80fd\u5411\u7231\u8fbe\u63d0\u524d\u9884\u652f\u8ba2\u8d27\u6b3e\u3002</p><p>\u3000\u3000\u4e0d\u6653\u5f97\u662f\u77ac\u606f\u95f4\u7efd\u51fa\u593a\u76ee\u7cbe\u5f69\u7684\u5927\u578b\u6237\u5916\u5e7f\u544a\u6380\u8d77\u65cb\u98ce\uff0c\u8fd8\u662f\u5a07\u598d\u660e\u8273\u6c5f\u9edb\u513f\u7684\u8bf1\u4eba\u9b45\u529b\uff0c\u5e02\u767e\u4e00\u5e97\u4e09\u697c\u5bb6\u7535\u90e8\u4ece\u7535\u6276\u68af\u53e3\u5230\u4fc3\u9500\u5c55\u53f0\u7684\u7a7a\u95f4\u91cc\uff0c\u6324\u6324\u6328\u6328\u90fd\u662f\u4eba\u3002\u6c5f\u9edb\u513f\u5229\u7528\u4ec5\u5269\u4e0b\u6765\u7684\u4e00\u53f0\u6837\u673a\u5411\u56f4\u5835\u5728\u5c55\u53f0\u524d\u7684\u987e\u5ba2\u8bb2\u89e3\u7231\u8fbe\u5f71\u789f\u673a\u7684\u529f\u80fd\u4ee5\u53ca\u5f71\u50cf\u6548\u679c\uff0c\u8bb8\u7ef4\u3001\u4ee4\u5c0f\u71d5\u5219\u4e0e\u5e02\u767e\u4e00\u5e97\u7684\u5de5\u4f5c\u4eba\u5458\u4e00\u8d77\u5206\u53d1\u4f20\u5355\uff0c\u4fdd\u5b89\u5728\u7ef4\u6301\u79e9\u5e8f\u3002\u7231\u8fbe\u63a8\u51fa\u8fd9\u6b3e\u5f71\u789f\u673a\u5355\u673a\u552e\u4ef73688\u5143\u8d60\u9001\u4e8c\u767e\u5143\u793c\u54c1\u5238\uff0c\u867d\u7136\u662f\u76ee\u524d\u5e02\u573a\u4e0a\u7b2c\u4e00\u6b3e\u5b9e\u9645\u4ef7\u683c\u8dcc\u78343500\u5143\u7684\u5f71\u789f\u673a\uff0c\u4f46\u5bf9\u4e8e\u666e\u901a\u5bb6\u5ead\u6765\u8bf4\uff0c\u4e5f\u7b97\u662f\u5962\u4f88\u54c1\uff0c\u56f4\u5835\u7684\u987e\u5ba2\u4e2d\u6709\u8bb8\u591a\u4eba\u624b\u634f\u7740\u4e00\u53e0\u949e\u7968\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u556a\u5566\u556a\u5566\u7684\u6643\u7740\uff1a\u201c\u7ed9\u6211\uff0c\u6700\u540e\u4e00\u53f0\u673a\u5b50\u6211\u8981\u4e86\uff0c\u793c\u54c1\u5238\u6211\u4e5f\u4e0d\u7528\u2026\u2026\u201d</p><p>\u3000\u3000\u65bd\u6bc5\u4ed6\u4eec\u7ad9\u5728\u56db\u697c\u7684\u5973\u88c5\u90e8\u51ed\u680f\u89c2\u671b\u4e09\u697c\u7684\u76db\u51b5\uff0c\u8d6b\u7136\u89c9\u5f97\u5148\u524d\u53ea\u4ece\u7231\u8fbe\u9884\u8d2d\u4e94\u5341\u53f0\u673a\u5b50\u7b80\u76f4\u5c31\u662f\u4e00\u4e2a\u7b11\u8bdd\uff0c\u8981\u662f\u5e02\u767e\u4e00\u5e97\u5907\u8d27\u5145\u8db3\uff0c\u4eca\u5929\u665a\u4e0a\u5c31\u80fd\u9500\u51fa\u4e94\u5341\u53f0\u53bb\u3002</p><p>\u3000\u3000\u4e0e\u65bd\u6bc5\u540c\u6837\u6094\u6068\u4ea4\u52a0\u7684\u8fd8\u6709\u4e1c\u65b9\u5e7f\u573a\u7684\u5bb6\u7535\u90e8\u7ecf\u7406\u3002</p><p>\u3000\u3000\u4e1c\u65b9\u5e7f\u573a\u662f\u8fd9\u5ea7\u57ce\u5e02\u7684\u5546\u4e1a\u9f99\u5934\uff0c\u81f4\u529b\u7ecf\u8425\u4e2d\u9ad8\u6863\u5546\u54c1\uff0c\u76db\u946b\u8d77\u5148\u6709\u610f\u9009\u62e9\u4e0e\u4e1c\u65b9\u5e7f\u573a\u5408\u4f5c\u4e3e\u529e\u6b64\u6b21\u4ea7\u54c1\u53d1\u5e03\u4f1a\uff0c\u4f46\u662f\u4e1c\u65b9\u5e7f\u573a\u4ee5\u7231\u8fbe\u7535\u5b50\u5e76\u975e\u77e5\u540d\u54c1\u724c\u62d2\u7edd\u4e86\u76db\u946b\u7684\u8981\u6c42\uff0c\u53ea\u8c61\u5f81\u6027\u7684\u7ed9\u4e86\u4e00\u5904\u5f88\u4e0d\u8d77\u773c\u7684\u5bb6\u7535\u67dc\u53f0\u3002</p><p>\u3000\u3000\u4e1c\u65b9\u5e7f\u573a\u7684\u5bb6\u7535\u90e8\u7ecf\u7406\u521a\u521a\u63a5\u5230\u52a9\u7406\u7684\u7535\u8bdd\uff0c\u4e1c\u65b9\u5e7f\u573a\u7684\u4e94\u53f0\u673a\u5b50\u5df2\u7ecf\u7ed9\u5e02\u767e\u4e00\u5e97\u7684\u5bb6\u7535\u90e8\u7ecf\u7406\u62a2\u5148\u4e00\u6b65\u8c03\u8d70\u3002\u4ed6\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u5e7f\u544a\u653b\u52bf\u624d\u5c55\u5f00\uff0c\u8981\u662f\u660e\u5929\u987e\u5ba2\u53d1\u73b0\u7701\u57ce\u6700\u5927\u7684\u5546\u573a\u7adf\u7136\u65ad\u8d27\uff0c\u4ed6\u8fd8\u4e0d\u5f97\u7ed9\u603b\u7ecf\u7406\u8bad\u5f97\u72d7\u8840\u6dcb\u5934\uff0c\u8d76\u7d27\u5c06\u76db\u946b\u7684\u5e02\u573a\u603b\u76d1\u90b5\u5fc3\u6587\u62c9\u5230\u4e00\u8fb9\uff0c\u603b\u8981\u5148\u62ff\u4e8c\u4e09\u5341\u53f0\u673a\u5b50\u5e94\u6025\u3002</p><p>\u3000\u3000\u4e1c\u65b9\u5e7f\u573a\u5bb6\u7535\u90e8\u7ecf\u7406\u7684\u9ebb\u70e6\u53ea\u8981\u627e\u90b5\u5fc3\u6587\u5c31\u80fd\u89e3\u51b3\uff1b\u8ba9\u65bd\u6bc5\u5934\u75bc\u7684\u95ee\u9898\uff0c\u53f6\u5efa\u658c\u4e5f\u65e0\u6cd5\u8f7b\u6613\u89e3\u51b3\uff0c\u4ed6\u76f4\u63a5\u62d6\u7740\u53f6\u5efa\u658c\u56de\u5230\u516b\u697c\u7684\u9152\u4f1a\u73b0\u573a\u6765\u627e\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u53d1\u5e03\u4f1a\u7684\u81f4\u8f9e\u8ba9\u4eba\u9887\u80fd\u9886\u4f1a\u5f20\u5148\u751f\u7684\u98ce\u91c7\u3002\u201d\u65bd\u6bc5\u5bf9\u5f20\u606a\u7684\u5370\u8c61\u53ea\u505c\u7559\u5728\u521a\u624d\u4ed6\u5728\u53f0\u4e0a\u5341\u5206\u5f97\u4f53\u7684\u81f4\u8f9e\uff0c\u8fd9\u5012\u4e0d\u662f\u5355\u7eaf\u7684\u606d\u7ef4\u4e4b\u8bed\uff1b\u81f4\u8f9e\u7684\u6548\u679c\u51b3\u975e\u4e8b\u540e\u9605\u8bfb\u6240\u80fd\u9886\u4f1a\u7684\uff0c\u5f20\u606a\u6717\u8bfb\u3001\u505c\u987f\u3001\u5bf9\u58f0\u97f3\u7684\u63a7\u5236\u90fd\u6781\u5176\u8bb2\u7a76\uff0c\u8ba9\u542c\u4f17\u5bf9\u5404\u79cd\u8a00\u5916\u4e4b\u610f\u53ca\u5fae\u5999\u4e4b\u5916\u90fd\u5fc3\u9886\u795e\u4f1a\uff0c\u8fd9\u672c\u8eab\u5c31\u4f53\u73b0\u5f20\u606a\u5728\u6f14\u8bb2\u4e0a\u7684\u9ad8\u8d85\u6280\u5de7\u3002</p><p>\u3000\u3000\u201c\u627f\u8499\u5938\u5956\uff0c\u53f6\u603b\u77e5\u9053\u6211\u8fd9\u4e2a\u4eba\uff0c\u5e73\u65e5\u90fd\u5c11\u8a00\u5be1\u8bed\u7684\uff0c\u4e3a\u4e86\u4eca\u5929\u665a\u4e0a\u7684\u81f4\u8f9e\uff0c\u53ef\u662f\u51e0\u5929\u6ca1\u7761\u8e0f\u5b9e\u2026\u2026\u201d</p><p>\u3000\u3000\u65bd\u6bc5\u7b11\u4e86\u7b11\uff0c\u4ed6\u6b23\u8d4f\u6027\u60c5\u51b2\u548c\u3001\u8c26\u865a\u8c28\u614e\u7684\u5e74\u8f7b\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u7231\u8fbe\u51c6\u5907\u4ec0\u4e48\u65f6\u5019\u5230\u4e0a\u6d77\u6253\u5e02\u573a\uff0c\u6c38\u57ce\u4f1a\u5168\u529b\u914d\u5408\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e1c\u6d77\u7684\u5e02\u573a\u63a8\u5e7f\u5de5\u4f5c\u5dee\u4e0d\u591a\u8981\u534a\u4e2a\u6708\u624d\u80fd\u5b8c\u6210\uff0c\u4e0a\u6d77\u90a3\u8fb9\u6700\u5feb\u4e5f\u8981\u4e03\u6708\u4e0b\u65ec\uff0c\u5177\u4f53\u7684\u65f6\u95f4\u7531\u76db\u946b\u5b89\u6392\uff0c\u5f53\u7136\u4e5f\u8981\u770b\u6c38\u57ce\u90a3\u8fb9\u7684\u51c6\u5907\u60c5\u51b5\u2026\u2026\u201d</p><p>\u3000\u3000\u65bd\u6bc5\u8bf4\u9053\uff1a\u201c\u6c38\u57ce\u90a3\u8fb9\u6ca1\u6709\u95ee\u9898\uff0c\u4e94\u767e\u4e07\u7684\u6b3e\u5b50\uff0c\u660e\u5929\u5c31\u80fd\u6253\u5230\u7231\u8fbe\u7684\u8d26\u4e0a\uff0c\u8fd8\u8981\u8bf7\u7231\u8fbe\u4e0e\u76db\u946b\u8d76\u7d27\u5236\u5b9a\u597d\u65f6\u95f4\u8868\uff0c\u6c38\u57ce\u65b9\u4fbf\u65e9\u505a\u51c6\u5907\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u76f8\u89c6\u800c\u82e6\u7b11\u3002</p><p>\u3000\u3000\u7231\u8fbe\u8fd9\u6b21\u53ea\u62ff\u51fa\u4e00\u5343\u53f0\u673a\u5b50\uff0c\u5bf9\u534e\u4e1c\u5730\u533a\u7684\u94fa\u8d27\u91cf\u53ea\u63a7\u5236\u5728\u4e94\u767e\u53f0\u5de6\u53f3\uff0c\u53ea\u5269\u4e0b\u4e94\u767e\u53f0\u673a\u5b50\u62ff\u6765\u505a\u7701\u5185\u7684\u5e02\u573a\u3002\u5728\u731b\u70c8\u7684\u5e7f\u544a\u653b\u52bf\u4e4b\u4e0b\uff0c\u5e02\u573a\u4e00\u7ecf\u6253\u5f00\uff0c\u53ea\u8981\u7ef4\u6301\u4e00\u5b9a\u7684\u5e7f\u544a\u529b\u5ea6\u3001\u6301\u7eed\u7684\u5b8c\u5584\u552e\u540e\u670d\u52a1\u4f53\u7cfb\uff0c\u8d28\u91cf\u7a33\u5b9a\u7684\u4ea7\u54c1\u9500\u91cf\u5c31\u4f1a\u63a7\u5236\u5728\u4e00\u5b9a\u7684\u6c34\u5e73\u4e4b\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4f30\u7b97\u7231\u8fbe\u7535\u5b50\u6bcf\u4e2a\u6708\u4ec5\u4e24\u5343\u53f0\u673a\u5b50\u7684\u4ea7\u80fd\u4ec5\u80fd\u7ef4\u6301\u7701\u5185\u5e02\u573a\u7684\u4f9b\u5e94\uff0c\u90fd\u4e0d\u6653\u5f97\u534a\u4e2a\u6708\u540e\u80fd\u4e0d\u80fd\u6512\u4e0b\u8db3\u591f\u7684\u673a\u5b50\u53bb\u6253\u4e0a\u6d77\u7684\u5e02\u573a\u3002</p><p>\u3000\u3000\u6240\u5e78\u76db\u946b\u4e0a\u56de\u53ca\u65f6\u5c06\u4e00\u5343\u4e94\u767e\u4e07\u7684\u4fdd\u8bc1\u91d1\u6253\u5165\u7231\u8fbe\u7684\u8d26\u6237\uff0c\u800c\u7231\u8fbe\u5728\u62ff\u5230\u94b1\u7684\u90a3\u4e00\u523b\u5c31\u523b\u4e0d\u5bb9\u7f13\u7684\u65b0\u5efa\u5382\u623f\u3001\u62db\u8058\u5458\u5de5\uff0c\u539f\u5148\u7684\u8f66\u95f4\u5df2\u7ecf\u80fd\u5b9e\u73b0\u4e24\u73ed\u5bf9\u5012\u4e8c\u5341\u56db\u5c0f\u65f6\u4e0d\u505c\u95f4\u65ad\u7684\u8fd0\u4f5c\u3002</p><p>\u3000\u3000\u5b9e\u73b0\u5012\u73ed\u5236\u751f\u4ea7\uff0c\u4ea7\u80fd\u5c31\u6269\u5f20\u4e00\u500d\u591a\uff0c\u65b0\u5efa\u5382\u623f\u6708\u5e95\u65bd\u5de5\u5b8c\u6210\uff0c\u4e3b\u8981\u7528\u4e8e\u7ec4\u88c5\u5f71\u789f\u673a\u7684\u751f\u4ea7\u7ebf\u5e76\u4e0d\u590d\u6742\uff0c\u6700\u5feb\u5230\u516b\u6708\u4e2d\u5c31\u80fd\u6295\u4ea7\uff0c\u4f46\u662f\u5bf9\u4ea7\u80fd\u5236\u7ea6\u6700\u660e\u663e\u7684\u8fd8\u662f\u7ebf\u8def\u677f\u7684\u751f\u4ea7\uff0c\u7ebf\u8def\u677f\u4ea7\u80fd\u6269\u5f20\u5728\u516b\u6708\u4e2d\u4e4b\u524d\u540c\u6b65\u89e3\u51b3\u7684\u53ef\u80fd\u6027\u6781\u5c0f\uff0c\u9664\u975e\u5e76\u8d2d\u7ebf\u8def\u677f\u5236\u9020\u5382\u5546\u8fdb\u884c\u5feb\u901f\u8f6c\u4ea7\u3002</p><p>\u3000\u3000\u8981\u60f3\u5728\u516b\u6708\u4efd\u5c06\u4ea7\u80fd\u6269\u5f20\u5230\u516b\u5343\u53f0\uff0c\u82cf\u6d25\u4e1c\u7684\u538b\u529b\u6781\u5927\u3002</p><p>\u3000\u3000\u4ece\u5f20\u606a\u4e0e\u98de\u5229\u6d66\u5143\u5668\u4ef6\u90e8\u4e2d\u56fd\u533a\u603b\u7ecf\u7406\u5f20\u4e9a\u5e73\u7684\u4ea4\u8c08\u4e2d\uff0c\u5f20\u606a\u53ef\u662f\u5e0c\u671b\u4eca\u5e74\u7684\u4ea7\u91cf\u5c31\u7a81\u7834\u5341\u4e07\u53f0\u3002</p><p>\u3000\u3000\u65bd\u6bc5\u867d\u7136\u6177\u6168\u7684\u627f\u8bfa\u660e\u5929\u5c31\u5c06\u4e94\u767e\u4e07\u6b3e\u5b50\u6253\u8fdb\u7231\u8fbe\u7684\u8d26\u4e0a\uff0c\u4f46\u5f20\u606a\u5374\u6ca1\u6709\u7ed9\u4ed6\u660e\u786e\u7684\u7b54\u590d\uff0c\u8fd9\u5927\u6982\u662f\u65bd\u6bc5\u8fd1\u5e74\u6781\u5c11\u9047\u5230\u7684\u5f85\u9047\uff0c\u770b\u7740\u6709\u4eba\u8fc7\u6765\u627e\u5f20\u606a\u3001\u53f6\u5efa\u658c\uff0c\u4ed6\u53ea\u5f97\u65bd\u65bd\u7136\u7684\u8d70\u5f00\u3002</p><p>\u3000\u3000\u8d70\u8fc7\u6765\u7684\u662f\u76ee\u524d\u8d1f\u8d23\u7231\u8fbe\u4eba\u529b\u8d44\u6e90\u90e8\u7684\u5468\u4e00\u5e73\uff0c\u4e0e\u4ed6\u4e00\u8d77\u7684\u662f\u60e0\u5c71\u65e0\u7ebf\u7535\u5143\u5668\u4ef6\u4e8c\u5382\u7684\u526f\u5382\u957f\u9c81\u5e86\u751f\uff0c\u5728\u5230\u7701\u57ce\u53c2\u52a0\u53d1\u5e03\u4f1a\u7684\u524d\u5915\u4ed6\u5df2\u7ecf\u51b3\u5b9a\u52a0\u76df\u7231\u8fbe\u7535\u5b50\u62c5\u4efb\u751f\u4ea7\u8fd0\u8425\u90e8\u526f\u7ecf\u7406\uff0c\u5b9e\u9645\u534f\u52a9\u82cf\u6d25\u4e1c\u8d1f\u8d23\u7231\u8fbe\u7535\u5b50\u7684\u751f\u4ea7\u8fd0\u8425\uff0c\u968f\u4ed6\u4ece\u60e0\u5c71\u65e0\u7ebf\u7535\u5143\u4ef6\u5668\u4e8c\u5382\u52a0\u76df\u7231\u8fbe\u7535\u5b50\u7684\u8fd8\u6709\u4e94\u540d\u751f\u4ea7\u5de5\u7a0b\u5e08\uff0c\u6682\u65f6\u80fd\u7f13\u89e3\u7231\u8fbe\u7535\u5b50\u5bf9\u751f\u4ea7\u8fd0\u8425\u7ba1\u7406\u4eba\u5458\u7684\u6025\u7f3a\u3002</p><p>\u3000\u3000\u5468\u4e00\u5e73\u4e5f\u662f\u5f97\u7a7a\u624d\u6709\u673a\u4f1a\u5411\u9c81\u5e86\u751f\u4ecb\u7ecd\u7231\u8fbe\u7535\u5b50\u771f\u6b63\u7684\u638c\u95e8\u4eba\u5f20\u606a\uff0c\u7a0d\u4f5c\u505c\u7559\uff0c\u4ea4\u8c08\u4e86\u51e0\u53e5\uff0c\u5c31\u5f15\u9886\u4ed6\u4e0e\u5176\u4ed6\u5728\u573a\u7684\u7231\u8fbe\u7ba1\u7406\u5c42\u4f5c\u975e\u6b63\u5f0f\u7684\u4f1a\u9762\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u611f\u6168\u9053\uff1a\u201c\u51e0\u4e4e\u968f\u65f6\u968f\u5730\u90fd\u80fd\u770b\u5230\u7231\u8fbe\u7535\u5b50\u5728\u98de\u901f\u7684\u6269\u5f20\uff0c\u7528\u53e5\u5938\u5f20\u7684\u8bdd\u8bf4\uff0c\u7231\u8fbe\u7535\u5b50\u5728\u4ee5\u7528\u8089\u773c\u770b\u5f97\u5230\u7684\u901f\u5ea6\u6210\u957f\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u603b\u8981\u5bf9\u5f97\u8d77\u53f6\u603b\u7684\u4fe1\u4efb\u554a\u3002\u201d\u5f20\u606a\u865a\u4f2a\u7684\u606d\u7ef4\u4e86\u4e00\u53e5\uff0c\u81ea\u5df1\u5012\u5fcd\u4e0d\u4f4f\u5148\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8f7b\u7b11\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u53c8\u8ba9\u4f60\u518d\u6b21\u6293\u5bf9\u7684\u65f6\u673a\uff0c\u52a0\u4e0a\u6c38\u57ce\u7684\u8fd9\u7b14\uff0c\u4eca\u5929\u665a\u4e0a\u8ffd\u52a0\u7684\u8ba2\u5355\u603b\u989d\u8fbe\u5230\u4e24\u5343\u4e07\uff0c\u76db\u946b\u603b\u7b97\u5bf9\u4e0d\u8d77\u4f60\u5427\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u538b\u7740\u7b11\u8bf4\u9053\uff1a\u201c\u53ef\u662f\u5728\u4eca\u5929\u4e4b\u524d\uff0c\u76db\u946b\u534a\u4e2a\u6708\u53ea\u5728\u534e\u4e1c\u5730\u533a\u62c9\u6765\u4e94\u767e\u4e07\u7684\u8ba2\u5355\u2026\u2026\u201d</p><p>\u3000\u3000\u52a0\u4e0a\u76db\u946b\u7684\u4e00\u5343\u4e94\u767e\u4e07\uff0c\u7231\u8fbe\u7535\u5b50\u5f88\u5feb\u5c31\u80fd\u7b79\u96c6\u5230\u603b\u989d\u56db\u5343\u4e07\u7684\u5de8\u989d\u8d44\u91d1\uff0c\u5728\u76ca\u9686\u4e1c\u9996\u7b79\u5efa\u5927\u578b\u4ea7\u4e1a\u57fa\u5730\u5df2\u7ecf\u4e0d\u518d\u662f\u75f4\u4eba\u8bf4\u68a6\u4e86\uff01</p></article>"}}}]);
