(window.webpackJsonp=window.webpackJsonp||[]).push([[197],{220:function(p){p.exports={chapterTitle:null,title:"\u7b2c197\u7ae0 \u9152\u4f1a\u4e0a\u7684\u9634\u8c0b",content:"<article><h2>\u7b2c197\u7ae0 \u9152\u4f1a\u4e0a\u7684\u9634\u8c0b</h2><p>\u3000\u3000\u5510\u5a67\u5c0f\u7ffc\u7684\u63d0\u7740\u665a\u793c\u670d\u88d9\u7684\u66f3\u5730\u957f\u6446\uff0c\u867d\u7136\u6574\u665a\u4e0a\u534e\u4e3d\u3001\u9ad8\u8d35\u5b9b\u82e5\u516c\u4e3b\uff0c\u4f46\u6bd4\u8d77\u817b\u5728\u5f20\u606a\u8eab\u8fb9\u7684\u751c\u871c\u611f\u89c9\uff0c\u7ec8\u6709\u4e0d\u5982\u3002\u5f20\u606a\u4ece\u7b80\u77ed\u7684\u53d1\u5e03\u4f1a\u5f00\u59cb\uff0c\u6574\u573a\u9152\u4f1a\u90fd\u5468\u8f6c\u4e8e\u5609\u5bbe\u4e4b\u95f4\uff0c\u6324\u4e0d\u51fa\u65f6\u95f4\u6765\u966a\u5510\u5a67\uff0c\u665a\u6674\u5374\u662f\u95f2\u6687\u81ea\u5f97\uff0c\u770b\u7740\u53d1\u5e03\u4f1a\u73b0\u573a\u7684\u6c14\u6c1b\uff0c\u4e3a\u5f20\u606a\u6697\u6697\u81ea\u8c6a\uff1a\u4e00\u4e0d\u5c0f\u5fc3\uff0c\u9526\u6e56\u5c31\u8981\u8ba9\u7231\u8fbe\u8d85\u8fc7\u53bb\u4e86\u3002</p><p>\u3000\u3000\u201c\u611f\u89c9\u600e\u4e48\u90fd\u8ffd\u4e0d\u4e0a\u4ed6\u2026\u2026\u201d\u5510\u5a67\u82e5\u6709\u6240\u601d\u7684\u51dd\u89c6\u7740\u676f\u4e2d\u9999\u69df\u9152\u4e0d\u65ad\u6d6e\u8d77\u6765\u7684\u6ce1\u6cab\uff0c\u5728\u6de1\u7425\u73c0\u8272\u7684\u9152\u6db2\u91cc\u8fde\u6210\u4e1d\u7ebf\uff0c\u4e0d\u65ad\u7684\u6d6e\u8d77\uff0c\u53c8\u4e0d\u65ad\u7684\u7834\u88c2\uff0c\u6709\u79cd\u5f92\u52b3\u65e0\u529f\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u665a\u6674\u8f7b\u547c\u4e86\u4e00\u58f0\uff0c\u90fd\u4e0d\u6653\u5f97\u5510\u5a67\u4e3a\u4ec0\u4e48\u4f1a\u6709\u8fd9\u6837\u7684\u611f\u6168\uff0c\u5728\u5979\u7684\u5370\u8c61\u91cc\uff0c\u5510\u5a67\u5e94\u8be5\u662f\u90a3\u79cd\u5341\u516d\u4e03\u5c81\u65e0\u5fe7\u65e0\u8651\u7684\u5973\u5b69\u5b50\u3002</p><p>\u3000\u3000\u201c\u6211\u8001\u7f20\u7740\u4ed6\uff0c\u5f20\u606a\u4f1a\u4e0d\u4f1a\u70e6\u6211\uff1f\u201d\u5510\u5a67\u660e\u4eae\u7684\u773c\u7738\u91cc\u6d41\u9732\u51fa\u6de1\u6de1\u7684\u5fe7\u6101\uff0c\u201c\u4f46\u662f\u770b\u4e0d\u5230\u4ed6\uff0c\u5c31\u611f\u89c9\u4ed6\u5f88\u9065\u8fdc\u3002\u665a\u6674\u59d0\u4e0a\u5b66\u65f6\u8c08\u604b\u7231\uff0c\u6709\u8fd9\u6837\u7684\u611f\u89c9\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u554a\uff0c\u6211\u521a\u5230\u4f60\u8fd9\u4e48\u5927\u5e74\u9f84\u65f6\uff0c\u5c31\u8ba9\u5bb6\u4eba\u9001\u53bb\u82f1\u56fd\u8bfb\u5973\u6821\uff0c\u6574\u95f4\u5b66\u6821\u9664\u4e86\u6253\u626b\u536b\u751f\u7684\u8001\u4f2f\u4f2f\uff0c\u53ef\u6ca1\u6709\u80fd\u601d\u6155\u7684\u5f02\u6027\uff0c\u6bd4\u8d77\u4f60\u7684\u5fe7\u6101\u6765\uff0c\u6211\u7684\u5c11\u5973\u65f6\u4ee3\u8fc7\u5f97\u5c31\u6709\u4e9b\u51c4\u60e8\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5a67\u6251\u54e7\u7b11\u4e86\u51fa\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u539f\u6765\u665a\u6674\u59d0\u4e5f\u4e0d\u662f\u597d\u7684\u503e\u8bc9\u5bf9\u8c61\uff0c\u90fd\u5bb3\u6211\u72b9\u8c6b\u4e86\u534a\u5929\uff0c\u624d\u8ddf\u4f60\u8bf4\u8fd9\u4e8b\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8ddf\u6211\u8bf4\u8bf4\u4e5f\u597d\u554a\uff0c\u9152\u4f1a\u86ee\u65e0\u804a\u7684\uff0c\u8fd8\u4e0d\u5982\u627e\u4e2a\u5730\u65b9\u8bf4\u8bf4\u8bdd\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5a67\u770b\u4e86\u4e00\u773c\u8fdc\u5904\u7ed9\u4f17\u4eba\u56f4\u4f4f\u7684\u5f20\u606a\uff0c\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\u8bf4\uff1a\u201c\u5f20\u606a\u665a\u4e0a\u4e5f\u8131\u4e0d\u4e86\u8eab\uff0c\u8fd8\u4e0d\u5982\u6211\u4eec\u5148\u56de\u53bb\u627e\u82b7\u5f64\u2026\u2026\u201d</p><p>\u3000\u3000\u665a\u6674\u9886\u7740\u5510\u5a67\u5148\u79bb\u5f00\u9152\u4f1a\u73b0\u573a\uff0c\u9752\u5c71\u516c\u5bd3\u7ed9\u9e29\u5360\u4e86\u9e4a\u5de2\uff0c\u5979\u4eec\u4e5f\u53ea\u80fd\u56de\u65b0\u6885\u82d1\u3002</p><p>\u3000\u3000\u5510\u5a67\u968f\u665a\u6674\u79bb\u53bb\uff0c\u5f20\u606a\u5fc3\u91cc\u4e8f\u6b49\uff0c\u5374\u65e0\u53ef\u5948\u4f55\uff0c\u540e\u5929\u5c31\u8981\u968f\u76db\u946b\u7684\u5de5\u4f5c\u4eba\u5458\u53bb\u524d\u5f80\u60e0\u5c71\u505a\u5e02\u573a\uff0c\u660e\u5929\u4e5f\u6ca1\u6709\u65f6\u95f4\u7a7a\u4e0b\u6765\u966a\u5510\u5a67\u597d\u597d\u7684\u73a9\u4e00\u5929\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u772f\u7740\u773c\u775b\u770b\u7740\u79bb\u5f00\u9152\u4f1a\u4f1a\u573a\u7684\u5510\u5a67\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u606a\u5c11\u53ef\u4e0d\u8981\u50cf\u6211\u8fd9\u6837\u65e9\u65e9\u7ed9\u4e00\u4e2a\u5973\u4eba\u7ed1\u4f4f\u624b\u811a\uff0c\u5510\u5b66\u8c26\u7684\u5973\u513f\u554a\uff0c\u538b\u529b\u662f\u86ee\u5927\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u7740\u53f6\u5efa\u658c\u6324\u7709\u5f04\u773c\uff0c\u5f20\u606a\u4f1a\u5fc3\u7684\u7b11\u4e86\u7b11\uff0c\u4ed6\u8001\u5a46\u4e01\u6587\u6021\u5a18\u5bb6\u80cc\u666f\u4e5f\u5f88\u539a\u5b9e\uff0c\u6240\u4ee5\u53f6\u5efa\u658c\u4e00\u76f4\u7ed9\u4ed6\u8001\u5a46\u538b\u5236\u5230\u8fde\u79d8\u4e66\u90fd\u4e0d\u80fd\u81ea\u884c\u51b3\u5b9a\u7684\u5730\u6b65\uff0c\u66f4\u9051\u8bba\u62c8\u82b1\u60f9\u8349\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u81ea\u5df1\u4eca\u751f\u600e\u4e48\u4e5f\u65e0\u6cd5\u820d\u5f03\u4e0b\u5510\u5a67\u7684\uff0c\u5012\u4e0e\u5510\u5b66\u8c26\u7684\u5730\u4f4d\u6ca1\u6709\u592a\u5927\u7684\u5173\u7cfb\uff0c\u4f55\u51b5\u5510\u5a67\u7684\u5e74\u9f84\u8fd8\u53ea\u6709\u5341\u516d\u5c81\uff0c\u8003\u8651\u8fd9\u4e9b\u672a\u5fc5\u8fc7\u4e8e\u4e25\u8083\u4e86\u3002</p><p>\u3000\u3000\u201c\u53f6\u603b\uff0c\u76db\u946b\u6536\u8d2d\u6d77\u5dde\u4ea4\u5bb6\u7535\u516c\u53f8\u8fdb\u884c\u5230\u54ea\u4e00\u6b65\u4e86\uff1f\u201d\u5f20\u606a\u5f00\u53e3\u95ee\u9053\uff0c\u4ee5\u53f6\u5bb6\u7684\u5f3a\u52bf\uff0c\u76db\u946b\u53ea\u8981\u5f00\u51fa\u5408\u9002\u7684\u4ef7\u683c\uff0c\u4e0d\u6101\u5e02\u91cc\u4f1a\u62d2\u7edd\uff0c\u4f46\u662f\u6536\u8d2d\u8fdb\u5ea6\uff0c\u5374\u8981\u770b\u76db\u946b\u7684\u52aa\u529b\u7a0b\u5ea6\u4e86\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u8fd9\u65f6\u5019\u7a81\u7136\u5173\u5fc3\u8d77\u8fd9\u4e2a\u6765\u4e86\uff1f\u201d\u53f6\u5efa\u658c\u4eb2\u81ea\u524d\u5f80\u5317\u4eac\u5bf9\u5317\u4eac\u56fd\u7f8e\u7535\u5668\u7684\u7ecf\u8425\u6a21\u5f0f\u8fdb\u884c\u521d\u6b65\u8003\u5bdf\u4e4b\u540e\uff0c\u5c31\u51b3\u5b9a\u4e0b\u76db\u946b\u7531\u6e20\u9053\u5546\u5411\u5bb6\u7535\u8fde\u9501\u8fdb\u884c\u65b9\u5411\u8f6c\u53d8\u7684\u53d1\u5c55\u601d\u8def\uff0c\u5728\u5bb6\u7535\u9886\u57df\u5360\u636e\u4e00\u5b9a\u7684\u6e20\u9053\u8d44\u6e90\u5374\u662f\u8f6c\u53d8\u7684\u7b2c\u4e00\u6b65\uff0c\u6536\u8d2d\u8fd1\u5e74\u6765\u9677\u5165\u4e8f\u635f\u3001\u4e13\u8425\u5bb6\u7535\u6279\u53d1\u7684\u6d77\u5dde\u5e02\u4ea4\u5bb6\u7535\u516c\u53f8\u662f\u6761\u6377\u5f84\u3002\u8fd9\u79cd\u6a21\u5f0f\u672c\u5c31\u662f\u5f20\u606a\u66ff\u76db\u946b\u6700\u5148\u63d0\u51fa\u6765\u7684\uff0c\u53f6\u5efa\u658c\u5012\u662f\u4e0d\u5fcc\u8bb3\u5f20\u606a\u95ee\u8fd9\u4e2a\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff1a\u201c\u6536\u8d2d\u6d77\u5dde\u5e02\u4ea4\u5bb6\u7535\u516c\u53f8\uff0c\u80fd\u591f\u83b7\u5f97\u4e00\u5b9a\u7684\u6e20\u9053\u8d44\u6e90\uff0c\u63a5\u4e0b\u6765\uff0c\u5c31\u8981\u7b79\u5907\u4e13\u8425\u5bb6\u7535\u7684\u5927\u5356\u573a\uff0c\u6211\u66f4\u5173\u5fc3\u76db\u946b\u7b79\u5907\u7684\u7b2c\u4e00\u5bb6\u5bb6\u7535\u5356\u573a\u5c06\u8bbe\u5728\u54ea\u5ea7\u57ce\u5e02\uff1f\u201d</p><p>\u3000\u3000\u201c\u6d77\u5dde\u4ea4\u5bb6\u7535\u516c\u53f8\u5c31\u6709\u8425\u4e1a\u90e8\uff0c\u89c4\u6a21\u867d\u7136\u5c0f\u4e00\u4e9b\uff0c\u603b\u4e4b\u6682\u65f6\u5148\u5e94\u4ed8\u6d77\u5dde\u7684\u5e02\u573a\u662f\u8db3\u591f\u4e86\u3002\u4e0d\u4f1a\u5728\u6d77\u5dde\u7b79\u5efa\u65b0\u7684\u5bb6\u7535\u5356\u573a\uff0c\u6211\u4f1a\u5728\u7701\u57ce\u4e0e\u60e0\u5c71\u4e4b\u95f4\u4efb\u9009\u4e00\u5ea7\u57ce\u5e02\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u89c9\u5f97\u8fd1\u4e24\u5e74\u76db\u946b\u4e0d\u4f1a\u9009\u62e9\u6d77\u5dde\uff0c\u4f46\u662f\u6211\u5e0c\u671b\u53f6\u603b\u80fd\u5e2e\u6211\u4e00\u4e2a\u5fd9\uff01\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u5fd9\uff1f\u201d</p><p>\u3000\u3000\u201c\u76db\u946b\u80fd\u4e0d\u80fd\u88c5\u51fa\u5728\u6d77\u5dde\u79ef\u6781\u7b79\u5907\u5927\u578b\u5bb6\u7535\u5356\u573a\u7684\u59ff\u6001\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u53f6\u5efa\u658c\u770b\u7740\u5f20\u606a\u7684\u773c\u775b\u91cc\u85cf\u7740\u5f02\u6837\u7684\u5149\u8292\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u6c99\u7530\u8857\u5e02\u975e\u6cd5\u5f3a\u5236\u62c6\u8fc1\u7684\u4e8b\u60c5\u8bf4\u7ed9\u53f6\u5efa\u658c\u542c\uff1a\u201c\u53f6\u603b\u5bf9\u8fd9\u6837\u7684\u4e8b\u60c5\u6709\u4ec0\u4e48\u770b\u6cd5\uff1f\u201d</p><p>\u3000\u3000\u201c\u5475\uff0c\u201d\u53f6\u5efa\u658c\u5476\u5476\u5634\uff0c\u6709\u4e9b\u8f7b\u63cf\u6de1\u5199\u7684\u8bf4\uff0c\u201c\u5012\u4e0d\u662f\u7ad9\u7740\u8bf4\u8bdd\u4e0d\u8170\u75bc\uff0c\u4f60\u6240\u8bf4\u7684\u9526\u57ce\u96c6\u56e2\u884c\u4e8b\u8fd9\u4e48\u6fc0\u70c8\uff0c\u8981\u5c06\u6d77\u5dde\u641e\u5f97\u4e00\u56e2\u7cdf\uff0c\u4ed6\u4eec\u8fd9\u6837\u505a\u4f1a\u5bf9\u8c01\u6709\u597d\u5904\uff1f\u201d</p><p>\u3000\u3000\u201c\u9664\u4e86\u4ed6\u4eec\u81ea\u5df1\uff0c\u5bf9\u8c01\u90fd\u6ca1\u6709\u597d\u5904\uff0c\u5c16\u9510\u7684\u793e\u4f1a\u77db\u76fe\uff0c\u4e0d\u53ef\u5229\u4e8e\u5927\u5bb6\u53d1\u8d22\u81f4\u5bcc\uff0c\u5373\u4f7f\u662f\u6211\u4eec\u8fd9\u4e9b\u7ea8\u7ed4\u5b50\u5f1f\uff0c\u505a\u4e8b\u90fd\u662f\u8bb2\u539f\u5219\u7684\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u54c8\u54c8\u5927\u7b11\u8d77\u6765\uff0c\u8981\u8bf4\u7ea8\u7ed4\u5b50\u5f1f\uff0c\u4ed6\u4ee5\u524d\u53ef\u662f\u6b63\u5b97\u7684\u7ea8\u7ed4\u5b50\u5f1f\uff0c\u884c\u4e8b\u8086\u65e0\u5fcc\u60ee\uff0c\u7ed9\u5bb6\u91cc\u60f9\u4e0b\u65e0\u6570\u9ebb\u70e6\uff0c\u5374\u662f\u7ed3\u5a5a\u4e4b\u540e\uff0c\u624d\u77e5\u9053\u6536\u655b\uff0c\u5373\u4f7f\u4e4b\u524d\u4efb\u610f\u5984\u4e3a\uff0c\u4e5f\u4e0d\u8fc7\u662f\u79c1\u751f\u6d3b\u4e0a\u4e0d\u68c0\u70b9\uff0c\u53ef\u6ca1\u6709\u60f3\u7740\u53bb\u4e3a\u975e\u4f5c\u6b79\u3002</p><p>\u3000\u3000\u201c\u7684\u786e\u8981\u6709\u8c01\u7ad9\u51fa\u6765\u8ba9\u4ed6\u4eec\u6536\u655b\u4e00\u4e9b\uff0c\u4f60\u60f3\u6211\u600e\u4e48\u5e2e\u4f60\uff1f\u201d</p><p>\u3000\u3000\u201c\u9526\u57ce\u96c6\u56e2\u62ff\u90a3\u5757\u5730\u5c31\u662f\u60f3\u7740\u5f00\u53d1\u5546\u4e1a\u5730\u4ea7\u725f\u5229\uff0c\u5546\u4e1a\u5730\u4ea7\u5f62\u52bf\u4e0d\u660e\u6717\u65f6\uff0c\u4ed6\u4eec\u53ef\u80fd\u4f1a\u91c7\u53d6\u89c2\u671b\u7684\u6001\u5ea6\uff0c\u4e0d\u6025\u7740\u5f00\u53d1\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u8c01\u4e5f\u65e0\u6cd5\u5948\u4f55\u4ed6\u4eec\uff0c\u53cd\u800c\u4f1a\u8ba9\u4ed6\u4eec\u65e5\u540e\u4ece\u90a3\u5757\u5730\u91cc\u83b7\u53d6\u66b4\u5229\uff0c\u50cf\u6211\u4eec\u8fd9\u6837\u6709\u6b63\u4e49\u611f\u7684\u4eba\u600e\u4e48\u7518\u5fc3\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u4e0e\u53f6\u5efa\u658c\u4e00\u8d77\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u5e0c\u671b\u76db\u946b\u5728\u6d77\u5dde\u5e02\u6446\u51fa\u79ef\u6781\u7b79\u5907\u5927\u578b\u5bb6\u7535\u5356\u573a\u7684\u59ff\u6001\uff0c\u524d\u95e8\u4e00\u65f6\u8fd8\u627e\u4e0d\u5230\u9002\u5408\u505a\u5bb6\u7535\u5356\u573a\u7684\u5730\u65b9\uff0c\u8fd9\u5c06\u8bf1\u4f7f\u4ed6\u4eec\u63d0\u524d\u5f00\u53d1\u90a3\u4e00\u5730\u6bb5\uff0c\u8bf4\u4e0d\u5b9a\u4ed6\u4eec\u4f1a\u4e3b\u52a8\u627e\u76db\u946b\u5408\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5f97\uff0c\u6211\u77e5\u9053\u4e86\uff0c\u4ed6\u4eec\u627e\u4e0a\u95e8\u6765\uff0c\u6211\u8ba9\u4ed6\u4eec\u5148\u5c06\u697c\u76d6\u597d\u518d\u8bf4\uff0c\u201d\u53f6\u5efa\u658c\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u5c0f\u5b50\u5751\u4eba\u7684\u6c34\u5e73\u4e5f\u662f\u4e00\u6d41\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c06\u4ed6\u4eec\u7684\u8d44\u91d1\u9501\u6b7b\u5728\u90a3\u4e2a\u9879\u76ee\u91cc\uff0c\u81f3\u5c11\u53ef\u4ee5\u8ba9\u4ed6\u4eec\u5c11\u9020\u4e9b\u5b7d\uff01\u201d</p><p>\u3000\u3000\u201c\u6d77\u5dde\u7684\u65e7\u57ce\u6539\u9020\uff0c\u6211\u4e5f\u77e5\u9053\u4e00\u4e9b\uff0c\u897f\u7247\u65e7\u57ce\u6ca1\u6709\u4e24\u4e09\u5e74\u5f00\u53d1\u4e0d\u8d77\u6765\u2026\u2026\u201d\u53f6\u5efa\u53c8\u8bf4\u9053\uff0c\u201c\u5bf9\u4e86\uff0c\u4eb2\u5144\u5f1f\u8fd8\u8981\u660e\u7b97\u8d26\uff0c\u8981\u6211\u5728\u6d77\u5dde\u6446\u51fa\u79ef\u6781\u7684\u59ff\u6001\uff0c\u603b\u8981\u6709\u4e2a\u7b79\u5907\u7ec4\u6027\u8d28\u4e4b\u7c7b\u7684\u5b58\u5728\uff0c\u4eba\u529b\u6210\u672c\u600e\u4e48\u4e5f\u8981\u7b97\u5230\u4f60\u5934\u4e0a\u5427\uff01\u201d</p><p>\u3000\u3000\u8d76\u4e0a\u53d7\u4e5d\u4e03\u5e74\u4e1c\u5357\u4e9a\u91d1\u878d\u98ce\u66b4\u7684\u5f71\u54cd\uff0c\u6c99\u7530\u65e7\u57ce\u6539\u9020\u8fd8\u8981\u5f80\u540e\u62d6\u4e00\u6bb5\u65f6\u95f4\uff0c\u53ea\u662f\u73b0\u5728\u6ca1\u6709\u5fc5\u8981\u8bf4\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u989d\u5916\u5728\u6d77\u5dde\u517b\u4e03\u516b\u4e2a\u95f2\u4eba\uff0c\u65b9\u4fbf\u4f60\u65e5\u540e\u5c06\u770b\u4e0d\u987a\u773c\u3001\u53c8\u4e0d\u65b9\u4fbf\u8bf7\u51fa\u76db\u946b\u7684\u4eba\u4e00\u811a\u8e22\u8fc7\u53bb\uff0c\u6709\u8fd9\u79cd\u597d\u5904\uff0c\u4f60\u5374\u6765\u8ddf\u6211\u65a4\u65a4\u8ba1\u8f83\u8fd9\u4e9b\u5c0f\u94b1\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u563f\u563f\u4e00\u7b11\uff0c\u53f6\u5bb6\u7684\u5173\u7cfb\u4e5f\u590d\u6742\uff0c\u4ed6\u5728\u76db\u946b\u603b\u4e5f\u6709\u4e9b\u4e0d\u5982\u610f\u7684\u5730\u65b9\uff0c\u5728\u6d77\u5dde\u5f00\u8f9f\u4e00\u5904\u51b7\u5bab\uff0c\u5012\u4e5f\u662f\u5fc5\u8981\uff0c\u514d\u5f97\u6709\u4e9b\u4eba\u5728\u773c\u524d\u6643\u7740\u5934\u75bc\uff0c\u8fd8\u4e0d\u80fd\u723d\u723d\u5229\u5229\u7684\u5c06\u4ed6\u4eec\u8bf7\u8d70\u3002\u5374\u4e0d\u6653\u5f97\u5f20\u606a\u600e\u4e48\u5c31\u80fd\u94bb\u5230\u81ea\u5df1\u7684\u809a\u80a0\u91cc\u53bb\uff1f\u6446\u660e\u4e86\u662f\u914d\u5408\u4ed6\u5751\u4eba\uff0c\u8fd9\u8bdd\u9898\u8f7b\u8f7b\u4e00\u62e8\uff0c\u5230\u6210\u4e86\u81ea\u5df1\u786e\u5b9e\u60f3\u5c06\u4e9b\u95f2\u4eba\u627e\u4e2a\u7531\u5934\u4e22\u6d77\u5dde\u53bb\uff0c\u81ea\u5df1\u8981\u771f\u8fd9\u4e48\u505a\uff0c\u5c82\u4e0d\u662f\u8fd9\u4e2a\u90fd\u4e0d\u7b97\u7ed9\u5f20\u606a\u7684\u4eba\u60c5\u4e86\uff1f</p><p>\u3000\u3000\u53f6\u5efa\u658c\u62cd\u62cd\u8111\u888b\uff0c\u6307\u7740\u5f20\u606a\u8bf4\uff1a\u201c\u8c01\u8ddf\u4f60\u73a9\u5fc3\u773c\uff0c\u8c01\u5012\u516b\u8f88\u5b50\u9709\u4e86\u3002\u201d</p><p>\u3000\u3000\u53ea\u8981\u6c99\u7530\u897f\u7247\u7684\u5f00\u53d1\u6539\u9020\u8fdf\u8fdf\u65e0\u6cd5\u6700\u7ec8\u5b8c\u6210\uff0c\u9526\u57ce\u90a3\u5757\u5904\u4e8e\u6c99\u7530\u8179\u5fc3\u7684\u5730\u6bb5\u5f00\u53d1\u5546\u4e1a\u5c06\u65e0\u6cd5\u56de\u7b3c\u8d44\u91d1\u3002\u9526\u57ce\u96c6\u56e2\u8d8a\u65e9\u5f00\u53d1\u90a3\u4e00\u5730\u6bb5\uff0c\u4ed6\u4eec\u6240\u53d7\u5230\u7684\u8d44\u91d1\u538b\u529b\u8d8a\u5927\u3002</p><p>\u3000\u3000\u6015\u5c31\u6015\u4ed6\u4eec\u82b1\u4e86\u4e00\u5343\u591a\u4e07\u9738\u5360\u7740\u90a3\u5757\u65e5\u540e\u7684\u9ec4\u91d1\u5730\u6bb5\u7b49\u6c99\u7530\u897f\u7247\u5f00\u53d1\u6210\u719f\u4e4b\u540e\u518d\u5f00\u53d1\uff0c\u90a3\u65f6\u9526\u57ce\u96c6\u56e2\u5374\u8981\u53cd\u8fc7\u6765\u72e0\u8d5a\u4e00\u7b14\uff0c\u4e00\u5b9a\u8981\u8ba9\u4ed6\u4eec\u63d0\u524d\u4e0b\u6c34\uff0c\u9664\u4e86\u8bf7\u53f6\u5efa\u658c\u4ee5\u5bb6\u7535\u8fde\u9501\u5356\u573a\u4e3a\u9975\uff0c\u8fd8\u8981\u8ba9\u5510\u5b66\u8c26\u5728\u5e02\u91cc\u65bd\u52a0\u538b\u529b\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u613f\u610f\u914d\u5408\uff0c\u52a0\u4e0a\u5e02\u91cc\u65bd\u538b\uff0c\u9526\u57ce\u6709\u516b\u6210\u7684\u53ef\u80fd\u9003\u4e0d\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u4ee3\u8868\u7231\u8fbe\u7535\u5b50\u81f4\u7b54\u8c22\u8f9e\u4e4b\u540e\uff0c\u9152\u4f1a\u5c31\u7b97\u7ed3\u675f\u4e86\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u7ad9\u5728\u4f1a\u573a\u5165\u53e3\u5185\u4fa7\uff0c\u606d\u8fce\u5e94\u9080\u5609\u5bbe\u4e0e\u5a92\u4f53\u8bb0\u8005\u79bb\u573a\u3002</p><p>\u3000\u3000\u770b\u7740\u4eba\u8d70\u4e86\u5dee\u4e0d\u591a\uff0c\u8bb8\u7ef4\u3001\u6c5f\u9edb\u513f\u3001\u4ee4\u5c0f\u71d5\u5979\u4eec\u624d\u8d70\u8fc7\u6765\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u8bf4\u9053\uff1a\u201c\u8981\u6ca1\u6709\u4ec0\u4e48\u989d\u5916\u7684\u4e8b\u60c5\uff0c\u6211\u4eec\u5c31\u5148\u8d70\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u5728\u4e00\u65c1\u7b11\u7740\u8bf4\uff1a\u201c\u8981\u662f\u606a\u5c11\u6709\u989d\u5916\u7684\u4e8b\u60c5\uff0c\u4f60\u5012\u662f\u613f\u610f\u7559\u4e0b\u6765\uff1f\u201d</p><p>\u3000\u3000\u6ca1\u60f3\u5230\u53f6\u5efa\u658c\u6709\u5fc3\u60c5\u5f00\u8fd9\u79cd\u7684\u73a9\u7b11\uff0c\u770b\u7740\u6362\u4e0a\u666e\u901a\u88c5\u675f\u7684\u6c5f\u9edb\u513f\u7c89\u8138\u901a\u7ea2\uff0c\u4ed6\u4e0d\u81ea\u7136\u7684\u54b3\u55fd\u8d77\u6765\u3002</p><p>\u3000\u3000\u8bb8\u7ef4\u64a9\u8d77\u773c\u5e18\uff0c\u770b\u7740\u5f20\u606a\uff1a\u201c\u4f60\u7684\u5c0f\u60c5\u4eba\u597d\u50cf\u65e9\u5c31\u79bb\u573a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u8ddf\u665a\u6674\u59d0\u5148\u56de\u53bb\u4e86\uff0c\u9152\u4f1a\u633a\u95f7\u7684\u3002\u201d</p><p>\u3000\u3000\u89c1\u5f20\u606a\u5012\u4e0d\u5426\u8ba4\uff0c\u4ee4\u5c0f\u71d5\u60cb\u60dc\u7684\u671d\u6c5f\u9edb\u513f\u5482\u5482\u5634\uff0c\u5374\u4ee4\u6c5f\u9edb\u513f\u66f4\u662f\u7a98\u8feb\uff0c\u62d8\u675f\u7684\u7ad9\u5728\u90a3\u91cc\u660e\u7738\u6ceb\u7136\uff0c\u5374\u53e6\u6709\u4e00\u756a\u4e0d\u751a\u601c\u60dc\u7684\u98ce\u5473\u3002</p><p>\u3000\u3000\u4e09\u4e2a\u5973\u5b69\u5b50\u5148\u884c\u79bb\u5f00\uff0c\u53f6\u5efa\u658c\u770b\u7740\u5979\u4eec\u7684\u80cc\u5f71\uff0c\u5482\u5482\u5634\uff0c\u60cb\u60dc\u7684\u8bf4\uff1a\u201c\u5973\u4eba\u591a\u4e86\u4e5f\u5934\u75bc\uff0c\u4e3a\u8fd9\u4e8b\uff0c\u6587\u6021\u90fd\u8ddf\u6211\u95f9\u51e0\u56de\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53f6\u603b\u8a00\u4e0b\u4e4b\u610f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u522b\u53f6\u603b\u53f6\u603b\u7684\u4e86\uff0c\u6211\u957f\u4f60\u51e0\u5c81\uff0c\u4f60\u53eb\u6211\u54e5\uff0c\u4e0d\u5360\u4f60\u4fbf\u5b9c\uff0c\u591c\u6765\u65e0\u4e8b\uff0c\u6211\u4eec\u5230\u522b\u5904\u518d\u559d\u4e00\u6ee9\u53bb\u2026\u2026\u201d</p></article>"}}}]);
