(window.webpackJsonp=window.webpackJsonp||[]).push([[198],{220:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e94\u8282 \u7ec8\u4e8e\u63a8\u5012\u4e86",content:"<article><h2>\u7b2c\u4e09\u5341\u4e94\u8282 \u7ec8\u4e8e\u63a8\u5012\u4e86</h2><p>\u610f\u5916\u7684\u53d1\u73b0\uff0c\u8ba9\u5927\u5bb6\u7684\u7cbe\u795e\u90fd\u632f\u594b\u8d77\u6765\u3002\u867d\u8bf4\u6309\u7167\u6885\u83f2\u65af\u6240\u8a00\uff0c\u8fd9\u4e2a\u7267\u5e08\u4e0d\u53ef\u80fd\u6301\u6709\u6b7b\u6708\u6cd5\u73e0\uff0c\u4f46\u4ed6\u662f\u76ee\u524d\u552f\u4e00\u7684\u7591\u70b9\uff0c\u987a\u7740\u627e\u4e0b\u53bb\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u53d1\u6398\u51fa\u4ec0\u4e48\u4e1c\u897f\u4e5f\u672a\u53ef\u77e5\uff0c\u81f3\u5c11\u603b\u6bd4\u73b0\u5728\u8fd9\u6837\u832b\u65e0\u5934\u7eea\u5730\u4e71\u627e\u8981\u597d\u591a\u4e86\u3002</p><p>\u201c\u90a3\u4e2a\uff0c\u6211\u542c\u8bf4\u88f3\u63d0\u963f\u548c\u6885\u4e3d\u51ef\u7684\u6559\u4f1a\u7d20\u6765\u5173\u7cfb\u878d\u6d3d\uff0c\u5408\u4f5c\u5f88\u591a\u7684\uff0c\u201d\u51db\u63d0\u51fa\u4e86\u4e00\u4e2a\u770b\u6cd5\uff0c\u5979\u6beb\u4e0d\u5ba2\u6c14\u5730\u76f4\u547c\u4e24\u4f4d\u5973\u795e\u7684\u540d\u8bb3\uff0c\u201c\u4f1a\u4e0d\u4f1a\u4ed6\u5176\u5b9e\u662f\u4e2a\u88f3\u63d0\u963f\u7267\u5e08\uff0c\u4f46\u56e0\u4e3a\u8981\u6267\u884c\u4ec0\u4e48\u79d8\u5bc6\u4efb\u52a1\uff0c\u6240\u4ee5\u9690\u85cf\u8eab\u4efd\u3002\u201d</p><p>\u8fd9\u4e2a\u63a8\u8bba\u4e5f\u4e0d\u80fd\u8bf4\u5b8c\u5168\u6ca1\u6709\u9053\u7406\uff0c\u5c31\u5e38\u7406\u6765\u8bf4\uff0c\u8fd9\u4e2a\u7267\u5e08\u867d\u7136\u6302\u7740\u6885\u4e3d\u51ef\u7684\u5723\u5fbd\uff0c\u4f46\u4ed6\u65e2\u7136\u80fd\u4f7f\u7528\u5927\u5730\u5973\u795e\u8d50\u4e0b\u7684\u7279\u6709\u795e\u672f\uff0c\u90a3\u5e94\u8be5\u5c31\u662f\u88f3\u63d0\u963f\u7684\u7267\u5e08\u6ca1\u9519\u3002\u7267\u5e08\u7684\u795e\u672f\u6765\u6e90\u4e8e\u795e\u8d50\uff0c\u5e76\u975e\u81ea\u8eab\u7814\u4e60\u6240\u5f97\uff0c\u5982\u679c\u4ed6\u4e0d\u662f\u88f3\u63d0\u963f\u7267\u5e08\uff0c\u6ca1\u6709\u5927\u5730\u5973\u795e\u7684\u8ba4\u53ef\uff0c\u662f\u6ca1\u9053\u7406\u80fd\u4f7f\u7528\u8fd9\u9053\u795e\u672f\u7684\u3002</p><p>\u201c\u4e0d\u8fc7\u53e6\u5916\u6709\u4ef6\u4e8b\u60c5\u4e5f\u5f88\u5947\u602a\u554a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u65e2\u7136\u4ed6\u4f2a\u88c5\u6210\u6885\u4e3d\u51ef\u7267\u5e08\uff0c\u8981\u9690\u85cf\u8eab\u4efd\uff0c\u600e\u4e48\u4f1a\u8fd9\u4e48\u4e0d\u5c0f\u5fc3\uff0c\u5927\u5ead\u5e7f\u4f17\u4e4b\u4e0b\u5c31\u7528\u51fa\u8fd9\u79cd\u795e\u672f\u6765\uff0c\u5c82\u4e0d\u662f\u592a\u5bb9\u6613\u66b4\u9732\u4e86\u5417\uff1f\u201d</p><p>\u201c\u5927\u6982\u662f\u56e0\u4e3a\u8fd9\u662f\u5ea7\u5c0f\u57ce\uff0c\u5c31\u4ed6\u4e00\u4e2a\u7267\u5e08\uff0c\u6240\u4ee5\u4e5f\u4e0d\u62c5\u5fc3\u88ab\u4eba\u53d1\u89c9\u5427\u3002\u201d\u51db\u8bf4\u3002</p><p>\u743c\u6069\u770b\u7740\u6885\u83f2\u65af\uff0c\u540e\u8005\u5fae\u5fae\u6447\u5934\uff0c\u201c\u4e0d\u6e05\u695a\uff0c\u53ef\u80fd\u5c31\u50cf\u51db\u8bf4\u7684\u5427\uff0c\u57ce\u91cc\u5c31\u4ed6\u4e00\u4e2a\u7267\u5e08\uff0c\u4e5f\u6ca1\u4eba\u53d1\u89c9\uff0c\u65f6\u95f4\u957f\u4e86\u5c31\u758f\u5ffd\u5927\u610f\u4e86\u3002\u201d</p><p>\u8bf4\u4e0d\u5b9a\u662f\u6545\u610f\u7684\u5462\uff0c\u743c\u6069\u9ed8\u60f3\u7740\uff0c\u5982\u679c\u8fd9\u4e2a\u7267\u5e08\u8eab\u4e0a\u771f\u6709\u4ec0\u4e48\u9690\u79d8\uff0c\u90a3\u4e48\u5fc5\u5b9a\u975e\u5e38\u8b66\u60d5\u5468\u56f4\u7684\u4eba\u7fa4\uff0c\u81ea\u5df1\u7b49\u4e09\u4eba\u8fd9\u4e9b\u5929\u6765\u5728\u57ce\u91cc\u57ce\u5916\u8c03\u67e5\uff0c\u867d\u7136\u6ca1\u5f15\u8d77\u5c45\u6c11\u6ce8\u610f\uff0c\u4f46\u96be\u4fdd\u4e0d\u88ab\u6709\u5fc3\u4eba\u5bdf\u89c9\u5427\u3002</p><p>\u201c\u603b\u800c\u8a00\u4e4b\uff0c\u5148\u67e5\u67e5\u8fd9\u4e2a\u4eba\u518d\u8bf4\u3002\u201d\u6885\u83f2\u65af\u505a\u51fa\u6700\u540e\u7ed3\u8bba\u3002</p><p>\u5927\u5bb6\u6ca1\u6709\u5f02\u8bae\u3002</p><p>\u56e0\u4e3a\u6709\u4e86\u660e\u786e\u76ee\u6807\uff0c\u8c03\u67e5\u5de5\u4f5c\u8fdb\u5c55\u6bd4\u4ee5\u524d\u987a\u5229\u5f97\u591a\uff0c\u4e24\u5929\u4e4b\u540e\uff0c\u661f\u671f\u516b\u7684\u665a\u4e0a\uff0c\u4e09\u4eba\u518d\u5ea6\u805a\u5728\u6885\u83f2\u65af\u623f\u95f4\u91cc\uff0c\u8ba8\u8bba\u5404\u81ea\u63a2\u542c\u6765\u7684\u60c5\u62a5\u3002</p><p>\u9996\u5148\uff0c\u8fd9\u4e2a\u53eb\u8428\u74e6\u5c14\u7684\u5bb6\u4f19\u786e\u5b9e\u662f\u4e2a\u7267\u5e08\u6ca1\u9519\uff0c\u4ed6\u591a\u6b21\u7ed9\u6218\u6597\u4e2d\u8d1f\u4f24\u7684\u6c11\u5175\u7528\u6cd5\u672f\u6cbb\u7597\u2014\u2014\u4f17\u6240\u5468\u77e5\uff0c\u795e\u672f\u548c\u5965\u672f\u7684\u4e00\u5927\u533a\u522b\uff0c\u5c31\u662f\u5965\u672f\u4e2d\u6ca1\u6709\u771f\u6b63\u80fd\u6cbb\u7597\u7684\u6cd5\u672f\uff0c\u6700\u591a\u53ea\u6709\u5438\u8840\u9b3c\u4e4b\u89e6\u8fd9\u79cd\u8f6c\u79fb\u4f24\u5bb3\u7684\u3002\u80fd\u7ecf\u5e38\u4e3a\u4eba\u7528\u6cd5\u672f\u6cbb\u7597\uff0c\u8fd9\u53ea\u6709\u795e\u672f\u80fd\u529e\u5230\u3002</p><p>\u5176\u6b21\uff0c\u8fd9\u5bb6\u4f19\u884c\u8e2a\u5f88\u8be1\u79d8\u3002</p><p>\u201c\u4ed6\u4eca\u5929\u51fa\u57ce\uff0c\u5bf9\u4eba\u8bf4\u662f\u53bb\u91c7\u8349\u836f\uff0c\u4f46\u6211\u4e00\u8def\u6084\u6084\u8ddf\u7740\uff0c\u53d1\u73b0\u4ed6\u5230\u57ce\u4e1c\u5927\u6982\u4e8c\u5341\u82f1\u91cc\u7684\u5730\u65b9\uff0c\u90a3\u91cc\u6709\u5f88\u591a\u5947\u602a\u7684\u67f1\u72b6\u5de8\u77f3\uff0c\u5e03\u6210\u4e00\u4e2a\u4e00\u4e2a\u7684\u5706\u9635\uff0c\u201d\u743c\u6069\u63cf\u8ff0\u7740\uff0c\u201c\u4ed6\u5728\u90a3\u4e9b\u5de8\u77f3\u9635\u91cc\u5446\u4e86\u5f88\u4e45\uff0c\u7b49\u4ed6\u8d70\u4e86\u4ee5\u540e\u6211\u8fdb\u53bb\u67e5\u770b\uff0c\u53d1\u73b0\u5730\u4e0a\u6709\u4e03\u516b\u4e2a\u521a\u7184\u706d\u7684\u706b\u5806\uff0c\u4f46\u4e5f\u770b\u4e0d\u51fa\u6709\u4ec0\u4e48\u540d\u5802\uff0c\u201d\u4ed6\u76b1\u7740\u7709\u5934\uff0c\u201c\u6211\u6000\u7591\u4f1a\u4e0d\u4f1a\u662f\u4ec0\u4e48\u796d\u7940\u4eea\u5f0f\uff0c\u4f46\u6211\u597d\u50cf\u542c\u8bf4\u81ea\u7136\u8bf8\u795e\u7684\u6559\u4e49\uff0c\u4e00\u822c\u90fd\u7981\u6b62\u5728\u68ee\u6797\u91cc\u7528\u706b\u7684\u3002\u201d</p><p>\u201c\u662f\u8fd9\u6837\u3002\u201d\u6885\u83f2\u65af\u70b9\u5934\u3002</p><p>\u6700\u540e\u4e5f\u662f\u6700\u91cd\u8981\u7684\u4e00\u4e2a\u6d88\u606f\uff0c\u662f\u6885\u83f2\u65af\u6253\u542c\u5230\u7684\uff0c\u201c\u4ed6\u6bcf\u661f\u671f\u7684\u6700\u540e\u4e00\u5929\uff0c\u4e5f\u5c31\u662f\u661f\u671f\u5341\uff0c\u90fd\u4f1a\u53bb\u4e00\u8d9f\u5a01\u666e\u6797\u5854\u62c9\u57ce\uff0c\u4e0d\u77e5\u9053\u505a\u4ec0\u4e48\u3002\u201d</p><p>\u201c\u5a01\u666e\u6797\u5854\u62c9\u57ce\uff1f\u963f\u683c\u62c9\u9686\u7684\u9996\u90fd\uff1f\u201d</p><p>\u743c\u6069\u644a\u5f00\u5730\u56fe\uff0c\u5f88\u5feb\u627e\u5230\u4e86\u5730\u65b9\uff0c\u5a01\u666e\u6797\u5854\u62c9\u57ce\u4f4d\u4e8e\u745e\u5361\u4e4b\u8db3\u5317\u65b9\uff0c\u662f\u963f\u683c\u62c9\u9686\u7684\u9996\u90fd\uff0c\u4e5f\u662f\u6b64\u56fd\u6700\u5927\u7684\u57ce\u5e02\u548c\u6e2f\u53e3\uff0c\u6b23\u5e03\u5973\u738b\u7684\u7eff\u77f3\u57ce\u5821\u5c31\u5ea7\u843d\u5728\u90a3\u91cc\u3002\u743c\u6069\u9ed8\u9ed8\u56de\u60f3\u7740\u738b\u5b50\u7ed9\u4ed6\u7684\u8d44\u6599\uff0c\u4ece\u4e2d\u641c\u5bfb\u6240\u6709\u6709\u5173\u5a01\u666e\u6797\u5854\u62c9\u7684\u8baf\u606f\uff0c\u6700\u540e\u2026\u2026</p><p>\u201c\u5927\u5730\u4e4b\u6bcd\u795e\u6bbf\uff1f\u201d\u4ed6\u548c\u6885\u83f2\u65af\u540c\u65f6\u8131\u53e3\u800c\u51fa\u3002</p><p>\u963f\u683c\u62c9\u9686\u7684\u6559\u4f1a\u52bf\u529b\u4e0d\u76db\uff0c\u7267\u5e08\u6781\u5c11\uff0c\u4e5f\u6ca1\u591a\u5c11\u5ea7\u795e\u6bbf\u3002\u7cbe\u7075\u8bf8\u795e\u7684\u795e\u6bbf\u90fd\u5ea7\u843d\u5728\u90a3\u4e9b\u8fdc\u53e4\u6797\u6d77\u4e4b\u4e2d\uff0c\u6d77\u6d0b\u5973\u795e\u5b89\u535a\u7406\u548c\u6d77\u6218\u4e4b\u795e\u7ef4\u5c14\u5bc7\u4e5f\u6709\u51e0\u5ea7\u795e\u6bbf\uff0c\u4f4d\u4e8e\u5357\u90e8\u6cbf\u6d77\u7684\u51e0\u4e2a\u5c0f\u57ce\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u6708\u5973\u795e\u82cf\u4f26\u548c\u5927\u5730\u5973\u795e\u88f3\u63d0\u963f\uff0c\u5404\u6709\u4e00\u5ea7\u89c4\u6a21\u8f83\u5927\u7684\u795e\u6bbf\uff0c\u5c31\u5ea7\u843d\u5728\u5a01\u666e\u6797\u5854\u62c9\u57ce\u4e2d\u3002</p><p>\u8fd9\u4e2a\u53eb\u8428\u74e6\u5c14\u7684\u7267\u5e08\uff0c\u5e94\u8be5\u5c31\u662f\u53bb\u5927\u5730\u5973\u795e\u88f3\u63d0\u963f\u7684\u795e\u6bbf\u5427\u3002\u6bcf\u5468\u90fd\u53bb\u4e00\u6b21\uff0c\u5982\u6b64\u770b\u6765\uff0c\u8bf4\u4e0d\u5b9a\u51db\u731c\u5f97\u6ca1\u9519\uff0c\u4ed6\u8fd8\u771f\u662f\u5949\u4e86\u5927\u5730\u5973\u795e\u6559\u4f1a\u7684\u547d\u4ee4\uff0c\u5728\u6267\u884c\u4ec0\u4e48\u79d8\u5bc6\u4efb\u52a1\u5462\u3002</p><p>\u201c\u4eca\u5929\u662f\u661f\u671f\u516b\uff0c\u6309\u7167\u60ef\u4f8b\uff0c\u4ed6\u540e\u5929\u53c8\u4f1a\u53bb\u4e00\u8d9f\u5bf9\u5427\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u60f3\u6211\u4eec\u53ef\u4ee5\u8ddf\u8fc7\u53bb\u770b\u770b\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u53d1\u73b0\u70b9\u4ec0\u4e48\u3002\u201d</p><p>\u6885\u83f2\u65af\u70b9\u5934\uff0c\u201c\u6211\u4e5f\u8fd9\u4e48\u60f3\u3002\u201d</p><p>\u201c\u6211\u53bb\u3002\u201d\u51db\u81ea\u544a\u594b\u52c7\u3002</p><p>\u743c\u6069\u548c\u6885\u83f2\u65af\u5bf9\u89c6\u4e00\u773c\uff0c\u663e\u7136\u4e0d\u80fd\u8ba9\u51db\u4e00\u4e2a\u4eba\u53bb\uff0c\u6bd5\u7adf\u5979\u662f\u8ff0\u5c14\u7684\u5b66\u751f\uff0c\u6f5c\u5728\u7684\u201c\u654c\u4eba\u201d\u5462\u3002\u201c\u6211\u548c\u4f60\u4e00\u8d77\u53bb\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u827e\u5f25\u8587\u4e0d\u65b9\u4fbf\u79bb\u5f00\uff0c\u5c31\u7559\u5728\u57ce\u91cc\u597d\u4e86\uff0c\u6211\u548c\u51db\u53bb\u4e00\u8d9f\u3002\u4e24\u4e2a\u4eba\u8f6e\u6d41\u8ddf\u8e2a\uff0c\u4e5f\u6bd4\u8f83\u4e0d\u5bb9\u6613\u88ab\u53d1\u89c9\u3002\u201d</p><p>\u51db\u6b63\u51c6\u5907\u6297\u8bae\uff0c\u6885\u83f2\u65af\u5df2\u7ecf\u6295\u4e86\u8d5e\u540c\u7968\uff0c\u201c\u5c31\u8fd9\u4e48\u529e\u5427\u3002\u201d</p><p>\u4e09\u4e2a\u4eba\uff0c\u4e8c\u6bd4\u4e00\uff0c\u6885\u83f2\u65af\u53c8\u662f\u961f\u957f\uff0c\u62e5\u6709\u5b89\u6392\u884c\u7a0b\u7684\u6743\u529b\u3002\u51db\u5658\u5658\u5634\uff0c\u4e5f\u5c31\u6ca1\u518d\u8bf4\u4ec0\u4e48\u4e86\u3002</p><p>\u5b9a\u4e0b\u5b89\u6392\uff0c\u4e5f\u5c31\u522b\u65e0\u4ed6\u4e8b\uff0c\u53ea\u7b49\u661f\u671f\u5341\u53bb\u73a9\u5c3e\u884c\u8ddf\u8e2a\u3002\u5012\u662f\u56e0\u4e3a\u8fd9\u5076\u7136\u7684\u53d1\u73b0\uff0c\u743c\u6069\u548c\u51db\u5bc6\u8c0b\u7684\u63a8\u5012\u8ba1\u5212\u7ed9\u803d\u6401\u4e86\u3002\u539f\u672c\u662f\u8bf4\u597d\u661f\u671f\u4e03\u7684\u665a\u4e0a\u8fdb\u884c\uff0c\u5982\u4eca\u90fd\u5df2\u7ecf\u661f\u671f\u516b\u4e86\uff0c\u4f9d\u65e7\u8fd8\u6ca1\u5f00\u59cb\u52a8\u624b\u5462\uff0c\u8fd9\u53ef\u771f\u662f\u4ee4\u4eba\u5931\u671b\u3002</p><p>\u5e78\u597d\uff0c\u51db\u8fd8\u6ca1\u5fd8\u8bb0\u81ea\u5df1\u7684\u627f\u8bfa\u3002\u201c\u660e\u665a\uff0c\u505a\u597d\u51c6\u5907\u54e6\u3002\u201d\u5728\u79bb\u5f00\u7684\u65f6\u5019\uff0c\u8d81\u7740\u6885\u83f2\u65af\u6ca1\u6ce8\u610f\uff0c\u5979\u4f4e\u58f0\u5728\u743c\u6069\u8033\u8fb9\u8bf4\uff0c\u7136\u540e\u683c\u683c\u5a07\u7b11\u8d77\u6765\u3002</p><p>\u201c\u4ec0\u4e48\u4e8b\u8fd9\u4e48\u9ad8\u5174\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u201c\u6ca1\u4ec0\u4e48\u6ca1\u4ec0\u4e48\uff0c\u201d\u51db\u8bf4\uff0c\u8138\u4e0a\u4f9d\u65e7\u662f\u6291\u5236\u4e0d\u4f4f\u7684\u7b11\u610f\uff0c\u201c\u597d\u5566\uff0c\u6211\u4e0d\u6253\u6270\u4f60\u4eec\u4e86\uff0c\u665a\u5b89\u4e24\u4f4d\u3002\u201d\u5979\u6446\u6446\u624b\uff0c\u8e66\u8e66\u8df3\u8df3\u51fa\u95e8\u53bb\u4e86\u3002</p><p>\u201c\u5979\u600e\u4e48\u4e86\uff1f\u201d\u6885\u83f2\u65af\u5947\u602a\u5730\u95ee\u743c\u6069\u3002</p><p>\u743c\u6069\u644a\u624b\uff0c\u201c\u6211\u600e\u4e48\u77e5\u9053\uff0c\u4e0d\u8fc7\u5979\u4e0d\u662f\u4e00\u5411\u90fd\u8fd9\u6837\u4e48\uff0c\u6210\u5929\u5f00\u5f00\u5fc3\u5fc3\u7684\u3002\u201d</p><p>\u201c\u4e0d\u662f\uff0c\u201d\u6885\u83f2\u65af\u6447\u5934\uff0c\u201c\u5979\u521a\u624d\u90a3\u6837\u5b50\uff0c\u50cf\u662f\u6709\u4ec0\u4e48\u7279\u522b\u5f00\u5fc3\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u201c\u4f30\u8ba1\u662f\u60f3\u5230\u540e\u5929\u80fd\u53bb\u5a01\u666e\u6797\u5854\u62c9\u57ce\u73a9\uff0c\u6240\u4ee5\u9ad8\u5174\u5427\uff0c\u201d\u743c\u6069\u968f\u53e3\u80e1\u626f\u7740\uff0c\u201c\u5979\u4e0d\u662f\u5f88\u559c\u6b22\u901b\u8857\u4e48\u3002\u201d</p><p>\u6885\u83f2\u65af\u663e\u7136\u4e0d\u592a\u76f8\u4fe1\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u518d\u591a\u95ee\u3002</p><p>\u203b\u203b\u203b</p><p>\u7b2c\u4e8c\u5929\u665a\u4e0a\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u90fd\u5728\u623f\u95f4\u91cc\u770b\u4e66\uff0c\u743c\u6069\u662f\u5728\u7814\u8bfb\u65b0\u5b66\u4f1a\u7684\u51e0\u4e2a\u6cd5\u672f\uff0c\u6885\u83f2\u65af\u5219\u662f\u5728\u7ffb\u9605\u8d44\u6599\u3002\u6b63\u9759\u6084\u6084\u95f4\uff0c\u623f\u95e8\u88ab\u6572\u54cd\u4e86\u3002</p><p>\u743c\u6069\u5f00\u95e8\uff0c\u51db\u62b1\u7740\u6795\u5934\u8e66\u8e66\u8df3\u8df3\u5730\u94bb\u8fdb\u6765\uff0c\u201c\u665a\u4e0a\u597d\uff0c\u4e24\u4f4d\uff0c\u201d\u5979\u7b11\u772f\u772f\u7684\uff0c\u201c\u6211\u6ca1\u6253\u6270\u5230\u4f60\u4eec\u5427\u3002\u201d</p><p>\u201c\u6709\u4e8b\u5417\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u201c\u54e6\uff0c\u6ca1\u4e8b\uff0c\u5c31\u662f\u6211\u4e00\u4e2a\u4eba\u7761\u4e0d\u7740\uff0c\u201d\u51db\u62b1\u7740\u6795\u5934\uff0c\u6446\u51fa\u4e00\u526f\u53ef\u601c\u516e\u516e\u7684\u9020\u578b\uff0c\u201c\u6ca1\u6709\u827e\u5f25\u8587\u966a\u7740\uff0c\u6211\u6bcf\u665a\u90fd\u505a\u6076\u68a6\u7684\uff0c\u5413\u5f97\u6839\u672c\u4e0d\u6562\u7761\u89c9\uff0c\u201d\u5979\u4e00\u8fb9\u8bf4\u7740\uff0c\u4e00\u8fb9\u4ee5\u8d85\u7ea7\u53ef\u7231\u7684\u59ff\u52bf\u8e66\u4e0a\u4e86\u5e8a\uff0c\u6251\u5728\u6885\u83f2\u65af\u6000\u91cc\uff0c\u201c\u4eca\u665a\u966a\u6211\u7761\u597d\u4e0d\u597d\uff0c\u827e\u5f25\u8587\u3002\u201d</p><p>\u6885\u83f2\u65af\u6709\u4e9b\u72b9\u8c6b\u5730\u770b\u7740\u743c\u6069\uff0c\u201c\u53ef\u662f\u2026\u2026\u201d</p><p>\u201c\u6ca1\u6709\u4ec0\u4e48\u53ef\u662f\uff01\u201d\u51db\u795e\u6c14\u5341\u8db3\u5730\u8bf4\uff0c\u201c\u4ed6\u5df2\u7ecf\u9738\u5360\u4f60\u8fd9\u4e48\u591a\u5929\u4e86\uff0c\u5076\u5c14\u4e5f\u5e94\u8be5\u5206\u4e00\u665a\u4e0a\u7ed9\u6211\u561b\uff0c\u5c31\u7b97\u6709\u4e86\u60c5\u4eba\uff0c\u4e5f\u4e0d\u80fd\u8fd9\u4e48\u91cd\u8272\u8f7b\u53cb\u7684\u5440\u3002\u5bf9\u4e0d\u5bf9\uff0c\u743c\u6069\u3002\u201d\u5979\u72e1\u9ee0\u5730\u51b2\u7740\u743c\u6069\u7728\u7728\u773c\u3002</p><p>\u201c\u55ef\uff0c\u90a3\u4e48\u6211\u5148\u544a\u9000\uff0c\u201d\u743c\u6069\u5f88\u8bc6\u8da3\u5730\u8bf4\uff0c\u628a\u9b54\u6cd5\u4e66\u6536\u8fdb\u6000\u91cc\uff0c\u201c\u4e24\u4f4d\u665a\u5b89\u3002\u201d\u4ed6\u8bf4\uff0c\u8d70\u51fa\u623f\u95f4\uff0c\u987a\u4fbf\u628a\u95e8\u5e26\u4e0a\u3002</p><p>\u4ed6\u56de\u5230\u81ea\u5df1\u623f\u95f4\uff0c\u7ffb\u5f00\u9b54\u6cd5\u4e66\u60f3\u8981\u7ee7\u7eed\u770b\uff0c\u7136\u800c\u538b\u6839\u518d\u4e5f\u770b\u4e0d\u8fdb\u53bb\uff0c\u6ee1\u8111\u5b50\u91cc\u60f3\u7684\u90fd\u662f\u5373\u5c06\u53d1\u751f\u7684\u573a\u666f\u3002\u201c\u771f\u662f\u7684\uff0c\u201d\u4ed6\u6447\u5934\u81ea\u5632\uff0c\u201c\u53c8\u4e0d\u662f\u7b2c\u4e00\u6b21\u51c6\u5907\u548c\u5973\u4eba\u4e0a\u5e8a\uff0c\u6709\u5fc5\u8981\u8fd9\u4e48\u7d27\u5f20\u4e48\u3002\u201d</p><p>\u7136\u800c\u771f\u7684\u662f\u5f88\u7d27\u5f20\u554a\u3002</p><p>\u65e2\u7136\u770b\u4e0d\u4e0b\u53bb\u4e66\uff0c\u7d22\u6027\u5c31\u6254\u4e0b\uff0c\u4ed6\u4ece\u6000\u4e2d\u6478\u7d22\u7740\uff0c\u62ff\u51fa\u4e00\u74f6\u6d45\u7eff\u8272\u7684\u900f\u660e\u836f\u6c34\u6765\u3002\u8fd9\u662f\u4ed6\u8fd9\u4e24\u5929\u6084\u6084\u914d\u597d\u7684\uff0c\u6709\u5f3a\u529b\u7684\u50ac\u60c5\u4f5c\u7528\uff0c\u5c31\u662f\u51c6\u5907\u5728\u4eca\u665a\u6d3e\u4e0a\u7528\u573a\u3002\u8fd9\u6b21\u53ef\u662f\u5fd7\u5728\u5fc5\u5f97\uff0c\u518d\u4e0d\u4f1a\u5bb9\u8bb8\u6885\u83f2\u65af\u9003\u8131\u4e86\u3002</p><p>\u4ed6\u6709\u4e9b\u7126\u8e81\u4e0d\u5b89\u5730\u7b49\u5f85\u7740\uff0c\u65f6\u95f4\u4eff\u4f5b\u6d41\u52a8\u5f97\u683c\u5916\u7f13\u6162\uff0c\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u7684\u8111\u4e2d\u7a81\u7136\u60f3\u8d77\u51db\u7684\u58f0\u97f3\u3002\u201c\u8fc7\u6765\u5427\uff0c\u201d\u5979\u7528\u6b22\u5feb\u7684\u58f0\u97f3\u8bf4\uff0c\u201c\u827e\u5f25\u8587\u7b54\u5e94\u4e86\u3002\u201d</p><p>\u743c\u6069\u8d70\u5230\u9694\u58c1\uff0c\u63a8\u95e8\u8fdb\u5165\uff0c\u53d1\u73b0\u623f\u95f4\u91cc\u5e76\u6ca1\u6709\u4eba\uff0c\u5012\u662f\u6d74\u5ba4\u91cc\u6709\u6c34\u58f0\u3002\u4ed6\u5fc3\u77e5\u4e24\u4f4d\u5c11\u5973\u5e94\u8be5\u6b63\u5728\u6d74\u5ba4\u91cc\uff0c\u4e5f\u4e0d\u8bf4\u8bdd\uff0c\u770b\u770b\u5e8a\u5934\u6885\u83f2\u65af\u7ecf\u5e38\u559d\u6c34\u7528\u7684\u676f\u5b50\u662f\u7a7a\u7684\uff0c\u5fc3\u5ff5\u4e00\u52a8\uff0c\u638f\u51fa\u6000\u91cc\u7684\u836f\u6c34\u5012\u4e86\u8fdb\u53bb\uff0c\u7136\u540e\u7528\u52fa\u5b50\u6405\u5300\uff0c\u539f\u672c\u6d45\u7eff\u8272\u7684\u6db2\u4f53\u6e10\u6e10\u5c31\u53d8\u5f97\u65e0\u8272\u900f\u660e\u8d77\u6765\uff0c\u4eff\u4f5b\u767d\u5f00\u6c34\u4e00\u822c\uff0c\u4e0d\u4ed4\u7ec6\u5206\u8fa8\u7edd\u5bf9\u770b\u4e0d\u51fa\u7834\u7efd\u3002</p><p>\u521a\u505a\u5b8c\u8fd9\u4e9b\uff0c\u6d74\u5ba4\u7684\u95e8\u6253\u5f00\uff0c\u6c34\u6c14\u84b8\u817e\u4e2d\uff0c\u6885\u83f2\u65af\u88f9\u7740\u6d74\u5dfe\u8d70\u51fa\u6765\uff0c\u5979\u7684\u8138\u901a\u7ea2\uff0c\u4eff\u4f5b\u88ab\u70ed\u6c14\u70eb\u719f\u4e00\u822c\uff0c\u8d70\u5230\u743c\u6069\u8ddf\u524d\uff0c\u9ed8\u9ed8\u770b\u7740\uff0c\u7136\u540e\u6402\u4f4f\u4ed6\u7684\u8170\uff0c\u4ef0\u9762\u770b\u7740\u4ed6\u3002</p><p>\u201c\u5f88\u60f3\u662f\u4e0d\u662f\uff1f\u201d</p><p>\u201c\u827e\u5f25\u8587\u2026\u2026\u201d</p><p>\u201c\u5176\u5b9e\u6211\u4e5f\u60f3\u8fc7\u7684\uff0c\u4f46\u4e00\u76f4\u6709\u70b9\u5bb3\u6015\uff0c\u201d\u5979\u4f4e\u58f0\u8bf4\uff0c\u201c\u4f46\u51db\u8bf4\uff0c\u5982\u679c\u662f\u4f60\u7684\u8bdd\uff0c\u6211\u5e94\u8be5\u53ef\u4ee5\u8bd5\u8bd5\u770b\u7684\u3002\u6211\u559c\u6b22\u4f60\uff0c\u603b\u5e94\u8be5\u518d\u591a\u4ed8\u51fa\u4e00\u4e9b\u3002\u6211\u51b3\u5b9a\u542c\u5979\u7684\u8bdd\u3002\u201d</p><p>\u743c\u6069\u6ca1\u6709\u518d\u8bf4\u8bdd\uff0c\u628a\u5979\u62b1\u8d77\u8f7b\u8f7b\u653e\u5728\u5e8a\u4e0a\uff0c\u4eb2\u543b\u5979\u7684\u989d\u5934\u3001\u7709\u6bdb\u3001\u9f3b\u5c16\u548c\u8138\u988a\uff0c\u6700\u540e\u543b\u4e0a\u56e0\u4e3a\u7d27\u5f20\u800c\u6709\u4e9b\u53d1\u70eb\u7684\u5634\u5507\uff0c\u201c\u522b\u6015\uff0c\u827e\u5f25\u8587\uff0c\u6211\u4f1a\u5f88\u6e29\u67d4\u7684\uff0c\u201d\u4ed6\u4fdd\u8bc1\u8bf4\uff0c\u201c\u4e0d\u4f1a\u5f88\u75bc\u7684\u3002\u201d</p><p>\u201c\u5514\u3002\u201d\u6885\u83f2\u65af\u4e56\u4e56\u5730\u95ed\u4e0a\u773c\uff0c\u957f\u957f\u7684\u776b\u6bdb\u98a4\u52a8\u7740\uff0c\u663e\u793a\u5979\u5fc3\u91cc\u5176\u5b9e\u8fd8\u662f\u5341\u5206\u7d27\u5f20\u3002</p><p>\u743c\u6069\u5f3a\u6291\u7740\u51b2\u52a8\uff0c\u6e29\u67d4\u5730\u629a\u6478\u7740\u5979\uff0c\u5728\u5979\u8033\u8fb9\u8bf4\u7740\u60c5\u8bdd\u3002\u4ed6\u6084\u6084\u5c06\u5e8a\u5934\u7684\u676f\u5b50\u62ff\u8fc7\u6765\uff0c\u6084\u6084\u559d\u4e86\u4e00\u53e3\uff0c\u7136\u540e\u4f4e\u5934\u4eb2\u543b\u5c11\u5973\u7684\u5634\u5507\uff0c\u8f7b\u8f7b\u62b5\u5f00\u5979\u7684\u7259\u9f7f\uff0c\u5659\u4f4f\u820c\u5c16\u8f7b\u8f7b\u542e\u5438\u7740\uff0c\u7136\u540e\u5c0f\u5fc3\u5730\u4e00\u70b9\u4e00\u70b9\u5c06\u53e3\u4e2d\u7684\u836f\u6c34\u5ea6\u7ed9\u5c11\u5973\u3002\u6885\u83f2\u65af\u8ff7\u4e71\u5730\u627f\u53d7\u7740\u70ed\u543b\uff0c\u4e56\u4e56\u5730\u5c06\u7537\u4eba\u53e3\u4e2d\u4f20\u6765\u7684\u6e05\u51c9\u6db2\u4f53\u541e\u54bd\u4e0b\u53bb\u3002\u5a9a\u836f\u5f88\u5feb\u53d1\u6325\u4f5c\u7528\uff0c\u5979\u7684\u8eab\u4f53\u6e10\u6e10\u653e\u677e\uff0c\u547c\u5438\u5374\u53d8\u5f97\u6025\u4fc3\uff0c\u610f\u8bc6\u4e5f\u5f00\u59cb\u53d8\u5f97\u6a21\u7cca\u8d77\u6765\uff0c\u8eab\u4f53\u7684\u6e29\u5ea6\u9010\u6e10\u5347\u9ad8\u3002\u743c\u6069\u89c1\u65f6\u673a\u5dee\u4e0d\u591a\uff0c\u8f7b\u8f7b\u626f\u6389\u6885\u83f2\u65af\u8eab\u4e0a\u7684\u6d74\u5dfe\uff0c\u4e22\u5230\u4e00\u8fb9\uff0c\u96ea\u767d\u7684\u88f8\u4f53\u4fbf\u5b8c\u5b8c\u5168\u5168\u5730\u5448\u73b0\u5728\u9762\u524d\u3002</p><p>\u591c\u665a\u7684\u5c0f\u57ce\u5f88\u5b89\u9759\uff0c\u768e\u6d01\u7684\u6708\u5149\u4ece\u7a97\u5916\u6d41\u6cfb\u8fdb\u6765\uff0c\u6d12\u5728\u5c11\u5973\u8eab\u4e0a\uff0c\u5979\u6574\u4e2a\u4eba\u770b\u8d77\u6765\u4eff\u4f5b\u4e00\u5177\u6d01\u767d\u65e0\u6687\u7684\u96d5\u50cf\uff0c\u6cdb\u7740\u67d4\u548c\u7684\u71a0\u71a0\u5fae\u5149\u3002\u6de1\u91d1\u8272\u957f\u53d1\u88ab\u6c34\u84b8\u6c14\u6e7f\u900f\uff0c\u51cc\u4e71\u5730\u8d34\u5728\u8116\u9888\u548c\u80a9\u5934\uff0c\u8d8a\u53d1\u663e\u5f97\u808c\u80a4\u767d\u7699\u7c89\u5ae9\uff0c\u900f\u7740\u7ea2\u6655\uff0c\u8ba9\u4eba\u5fcd\u4e0d\u4f4f\u5c31\u6709\u60f3\u4eb2\u543b\u7684\u51b2\u52a8\uff1b\u7cbe\u81f4\u7ea4\u5de7\u7684\u9501\u9aa8\u5728\u80f8\u524d\u4ea4\u6c47\uff0c\u5f62\u6210\u4e00\u4e2a\u5c0f\u5c0f\u7684\u8bf1\u4eba\u51f9\u9677\uff0c\u5979\u7684\u80f8\u53e3\u968f\u7740\u547c\u5438\u7f13\u7f13\u8d77\u4f0f\uff0c\u4e24\u56e2\u8bf1\u4eba\u7684\u9999\u8f6f\u50b2\u7136\u633a\u7acb\uff0c\u5e76\u4e0d\u5341\u5206\u7855\u5927\u4f46\u575a\u633a\u5f02\u5e38\uff0c\u800c\u4e14\u5f39\u6027\u5341\u8db3\uff0c\u8fd9\u70b9\u743c\u6069\u5df2\u7ecf\u591a\u6b21\u4eb2\u624b\u9a8c\u8bc1\u8fc7\u3002\u4ed6\u54bd\u4e86\u53e3\u53e3\u6c34\uff0c\u6b63\u51c6\u5907\u4f4e\u5934\u53bb\u4eb2\u543b\u90a3\u4e24\u9897\u7c89\u7ea2\u8272\u7684\u84d3\u857e\uff0c\u7a81\u7136\u611f\u89c9\u5e8a\u4e00\u9707\uff0c\u63a5\u7740\u88f9\u7740\u6d74\u5dfe\u7684\u9ed1\u53d1\u5c0f\u9b54\u5973\u4e5f\u8df3\u4e0a\u5e8a\u6765\u3002</p><p>\u201c\u51db\uff0c\u4f60\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5514\uff1f\u5f53\u7136\u662f\u6765\u5e2e\u5fd9\u554a\uff0c\u201d\u51db\u8bf4\u5f97\u7406\u6240\u5f53\u7136\uff0c\u201c\u827e\u5f25\u8587\u53ef\u662f\u7b2c\u4e00\u6b21\u5462\uff0c\u6211\u600e\u4e48\u653e\u5fc3\u628a\u5979\u4ea4\u7ed9\u4f60\u3002\u201d</p><p>\u2026\u2026\u8fd9\u8bdd\u542c\u8d77\u6765\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u5947\u602a\u3002</p><p>\u201c\u4f46\u8fd9\u79cd\u4e8b\u60c5\u4e0d\u9700\u8981\u4f60\u6765\u5e2e\u5fd9\u5427\u3002\u201d</p><p>\u201c\u54e6\u54e6\uff0c\u8981\u8fc7\u6cb3\u62c6\u6865\u5462\uff0c\u743c\u6069\uff0c\u201d\u51db\u6307\u8d23\u7740\uff0c\u201c\u827e\u5f25\u8587\u5230\u4e86\u624b\uff0c\u5c31\u8981\u628a\u6211\u8fd9\u4e2a\u6069\u4eba\u8d76\u8d70\u662f\u5417\uff1f\u201d</p><p>\u201c\u6211\u6ca1\u8fd9\u4e2a\u610f\u601d\uff0c\u53ef\u662f\u4f60\u5728\u8fd9\u91cc\u2026\u2026\u201d</p><p>\u201c\u653e\u5fc3\uff0c\u4f60\u4eec\u505a\u4f60\u4eec\u7684\u4e8b\u60c5\uff0c\u5c31\u5f53\u6211\u4e0d\u5b58\u5728\u597d\u4e86\uff0c\u6211\u4e0d\u4f1a\u4ecb\u610f\u7684\u3002\u201d</p><p>\u53ef\u662f\u6211\u4ecb\u610f\u554a\uff01</p><p>\u201c\u827e\u5f25\u8587\u4e5f\u4e0d\u4f1a\u4ecb\u610f\u7684\uff0c\u6211\u4eec\u4e24\u4e2a\u5973\u5b69\u5b50\u90fd\u4e0d\u4ecb\u610f\uff0c\u4f60\u4e00\u4e2a\u5927\u7537\u4eba\u4ecb\u610f\u4ec0\u4e48\uff1f\u771f\u662f\u7684\uff0c\u201d\u51db\u7528\u7406\u6240\u5f53\u7136\u7684\u8bed\u6c14\u8bf4\u7740\uff0c\u4fef\u8eab\u8dea\u5728\u6885\u83f2\u65af\u65c1\u8fb9\uff0c\u4f4e\u5934\u4eb2\u543b\u5979\u7684\u8138\u86cb\uff0c\u201c\u827e\u5f25\u8587\uff0c\u4f60\u7684\u8138\u5f88\u70eb\u5462\u3002\u201d</p><p>\u201c\u5514\u2026\u2026\u201d\u6885\u83f2\u65af\u8ff7\u8ff7\u7cca\u7cca\u5730\u5e94\u7740\uff0c\u4e5f\u4e0d\u77e5\u9053\u6709\u6ca1\u6709\u542c\u89c1\u3002</p><p>\u201c\u6211\u597d\u559c\u6b22\u4f60\u73b0\u5728\u7684\u6837\u5b50\u5462\uff0c\u827e\u5f25\u8587\uff0c\u6bd4\u4f60\u5e73\u65f6\u53ef\u7231\u591a\u4e86\uff0c\u201d\u51db\u8f7b\u8f7b\u54ac\u7740\u6885\u83f2\u65af\u7684\u8033\u5782\uff0c\u5728\u5979\u8033\u8fb9\u8f7b\u8f7b\u8bf4\uff0c\u4e00\u53ea\u624b\u8d81\u673a\u6e38\u4e0a\u4e86\u6885\u83f2\u65af\u7684\u80f8\u53e3\uff0c\u8f7b\u8f7b\u63e1\u4f4f\u63c9\u634f\uff0c\u201c\u5176\u5b9e\u6211\u4e00\u76f4\u5c31\u60f3\u8fd9\u6837\u6b3a\u8d1f\u4f60\u5462\uff0c\u53ef\u60dc\u603b\u662f\u4e0d\u8ba9\u3002\u201d</p><p>\u201c\u51db\u2026\u2026\u201d</p><p>\u201c\u522b\u7740\u6025\uff0c\u743c\u6069\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u827e\u5f25\u8587\u7684\u8eab\u4f53\u8fd8\u6ca1\u6709\u5b8c\u5168\u51c6\u5907\u597d\u5462\uff0c\u73b0\u5728\u5c31\u8d38\u7136\u95ef\u8fdb\u53bb\uff0c\u662f\u4f1a\u5f04\u4f24\u5979\u7684\u54e6\u3002\u5148\u8ba9\u6211\u6765\u5e2e\u5979\u5b8c\u5168\u653e\u677e\u4e0b\u6765\u5427\u3002\u201d</p><p>\u4e0d\u7b49\u743c\u6069\u56de\u7b54\uff0c\u5979\u5df2\u7ecf\u4fef\u8eab\u538b\u5728\u6885\u83f2\u65af\u8eab\u4e0a\uff0c\u5bbd\u677e\u7cfb\u7740\u7684\u6d74\u5dfe\u65e9\u5df2\u7ecf\u8131\u5f00\uff0c\u5979\u4e5f\u5b8c\u5168\u6ca1\u6709\u5728\u610f\u3002\u4e8e\u662f\u743c\u6069\u76ee\u77aa\u53e3\u5446\u5730\u770b\u7740\u773c\u524d\u7684\u60c5\u666f\uff0c\u4e24\u4e2a\u7edd\u7f8e\u7684\u5c11\u5973\u8d64\u88f8\u7740\u8eab\u4f53\uff0c\u5728\u5e8a\u4e0a\u4ea4\u53e0\u7740\uff0c\u51db\u4eb2\u543b\u7740\u6885\u83f2\u65af\u7684\u5634\u5507\uff0c\u5de6\u624b\u7ee7\u7eed\u4fb5\u88ad\u5979\u7684\u80f8\u90e8\uff0c\u53f3\u624b\u5219\u6162\u6162\u629a\u6478\u5979\u7684\u80a9\u80cc\uff0c\u8f7b\u67d4\u5730\u6311\u9017\u7740\u3002\u5728\u5a9a\u836f\u7684\u4f5c\u7528\u4e0b\uff0c\u6885\u83f2\u65af\u5b8c\u5168\u6ca1\u6709\u62b5\u6297\uff0c\u53cd\u800c\u66f4\u52a0\u70ed\u70c8\u5730\u8fce\u5408\u7740\uff0c\u4e24\u4e2a\u5973\u5b69\u90fd\u5728\u6025\u4fc3\u5598\u606f\uff0c\u5979\u4eec\u62e5\u62b1\u7740\uff0c\u7cbe\u81f4\u7684\u6a31\u5507\u8d34\u5728\u4e00\u8d77\uff0c\u4e24\u6761\u4e01\u9999\u5c0f\u820c\u7f20\u7ed5\uff0c\u6885\u83f2\u65af\u7684\u808c\u80a4\u5df2\u7ecf\u88ab\u60c5\u6b32\u70e7\u7ea2\uff0c\u800c\u51db\u7684\u4fcf\u8138\u4e5f\u8d8a\u6765\u8d8a\u6eda\u70eb\u53d1\u70ed\u4e86\uff0c\u900f\u7740\u660e\u5a9a\u7684\u7ea2\u6655\uff0c\u4e00\u5934\u9ed1\u53d1\u4eff\u4f5b\u7f0e\u5b50\u4f3c\u7684\u62ab\u6d12\u5728\u81ea\u5df1\u548c\u6885\u83f2\u65af\u8eab\u4e0a\uff0c\u66f4\u52a0\u886c\u6258\u51fa\u4e24\u4f4d\u5c11\u5973\u7684\u96ea\u767d\u808c\u80a4\u3002</p><p>\u6bd4\u8d77\u7f3a\u4e4f\u7ecf\u9a8c\u7684\u6885\u83f2\u65af\uff0c\u6bcf\u5929\u90fd\u8981\u201c\u51c6\u5907\u5b9d\u77f3\u201d\u7684\u51db\u7406\u6240\u5f53\u7136\u5730\u638c\u63e1\u4e86\u4e3b\u52a8\u6743\uff0c\u540c\u4e3a\u5973\u5b69\u5b50\uff0c\u53c8\u662f\u591a\u5e74\u597d\u53cb\uff0c\u5979\u5bf9\u6885\u83f2\u65af\u7684\u8eab\u4f53\u53cd\u5e94\u975e\u5e38\u719f\u6089\uff0c\u6e05\u695a\u5730\u77e5\u9053\u5e94\u8be5\u600e\u6837\u624d\u80fd\u8ba9\u5979\u611f\u53d7\u5230\u5feb\u4e50\u3002\u773c\u89c1\u603b\u662f\u6c89\u7a33\u51b7\u9759\u7684\u5723\u6b66\u58eb\u5728\u81ea\u5df1\u7684\u6311\u9017\u4e0b\u60c5\u6b32\u52c3\u53d1\uff0c\u51db\u5f97\u610f\u5730\u5a07\u7b11\u7740\uff0c\u7d22\u6027\u76f4\u63a5\u7ffb\u8eab\u9a91\u5728\u5979\u8eab\u4e0a\uff0c\u4e00\u8fb9\u7ee7\u7eed\u653b\u51fb\u7740\u6885\u83f2\u65af\u7684\u80f8\u90e8\uff0c\u4e00\u8fb9\u624b\u6307\u6162\u6162\u6e38\u8d70\uff0c\u6ed1\u8fc7\u5e73\u5766\u7684\u5c0f\u8179\uff0c\u6162\u6162\u5f80\u4e0b\u63a2\u7d22\u7740\u2026\u2026</p><p>\u7ea4\u957f\u7075\u5de7\u7684\u7389\u6307\u629a\u6478\u7740\u5c11\u5973\u4e30\u8174\u7684\u803b\u4e18\uff0c\u6de1\u91d1\u8272\u7684\u6d45\u6d45\u7ed2\u6bdb\u5728\u6708\u5149\u4e0b\u53d8\u5f97\u4eff\u4f5b\u900f\u660e\uff0c\u6885\u83f2\u65af\u7684\u547c\u5438\u6025\u4fc3\u8d77\u6765\uff0c\u5979\u7684\u8eab\u4f53\u5fae\u5fae\u7ef7\u7d27\uff0c\u4f46\u88ab\u8de8\u5750\u5728\u8eab\u4e0a\u7684\u51db\u538b\u4f4f\u4e86\uff0c\u65e0\u6cd5\u52a8\u5f39\u3002\u51db\u4e0d\u4f9d\u4e0d\u9976\u5730\u7ee7\u7eed\u7ee7\u7eed\u5f80\u4e0b\u6df1\u5165\uff0c\u7389\u6307\u62e8\u5f00\u5a07\u5ae9\u7684\u8089\u5507\uff0c\u5728\u6084\u6084\u51f8\u8d77\u7684\u654f\u611f\u7ea2\u8c46\u4e0a\u7f13\u7f13\u6253\u8f6c\u7740\u3002</p><p>\u201c\u6e7f\u900f\u4e86\u5462\uff0c\u827e\u5f25\u8587\u3002\u201d\u5979\u4f4e\u58f0\u5bf9\u8eab\u4e0b\u7684\u91d1\u53d1\u5c11\u5973\u8bf4\u3002</p><p>\u6885\u83f2\u65af\u6ca1\u6709\u56de\u7b54\uff0c\u51db\u4e5f\u4e0d\u9700\u8981\u56de\u7b54\uff0c\u5979\u7684\u624b\u6307\u7ee7\u7eed\u6df1\u5165\uff0c\u4e00\u70b9\u4e00\u70b9\u5730\u63a2\u8fdb\u4e86\u90a3\u7d27\u7a84\u6e29\u70ed\u7684\u871c\u6d1e\u91cc\u3002\u5973\u5b69\u5b50\u7684\u624b\u6307\u7ea4\u7ec6\u67d4\u8f6f\uff0c\u800c\u4e14\u51db\u7684\u52a8\u4f5c\u4e5f\u5f88\u5c0f\u5fc3\uff0c\u4f46\u5bf9\u4e8e\u6885\u83f2\u65af\u4ece\u672a\u7ecf\u8fc7\u4eba\u4e8b\u7684\u871c\u7a74\u6765\u8bf4\uff0c\u8fd8\u662f\u6709\u4e9b\u4e0d\u582a\u627f\u53d7\u3002\u5a07\u5ae9\u7684\u81a3\u8089\u5c42\u5c42\u5305\u88f9\u4e0a\u6765\uff0c\u867d\u7136\u67d4\u8f6f\u5374\u5c06\u5165\u4fb5\u7684\u624b\u6307\u7d27\u7d27\u6324\u538b\u7740\uff0c\u8ba9\u51db\u51e0\u4e4e\u90fd\u62bd\u4e0d\u51fa\u6765\u3002\u201c\u827e\u5f25\u8587\u7684\u8eab\u4f53\u771f\u6deb\u8361\u5462\uff0c\u628a\u6211\u7684\u624b\u6307\u90fd\u5438\u4f4f\u4e86\u3002\u201d\u51db\u6084\u58f0\u8bf4\uff0c\u4fef\u8eab\u4eb2\u543b\u7740\u6885\u83f2\u65af\u7684\u5634\u5507\uff0c\u8ba9\u5979\u6162\u6162\u653e\u677e\u4e0b\u6765\uff0c\u5c06\u624b\u6307\u7f13\u7f13\u4ece\u5ae9\u8089\u7ea0\u7f20\u4e2d\u9000\u51fa\uff0c\u7136\u540e\u987a\u7740\u6e7f\u6ed1\u7684\u7231\u6db2\u5f80\u4e0b\u79fb\u52a8\uff0c\u6307\u5c16\u505c\u7559\u5728\u90a3\u7cbe\u81f4\u5c0f\u5de7\u7684\u83ca\u82b1\u4e0a\u3002</p><p>\u8f7b\u8f7b\u4e00\u78b0\uff0c\u6885\u83f2\u65af\u7684\u8eab\u4f53\u5c31\u731b\u5730\u98a4\u6296\u4e86\u4e00\u4e0b\uff0c\u201c\u827e\u5f25\u8587\u8fd9\u91cc\u771f\u654f\u611f\u3002\u201d\u51db\u8bc4\u4ef7\u7740\uff0c\u624b\u6307\u5212\u7740\u5708\uff0c\u76f4\u5230\u6cbe\u4e0a\u8db3\u591f\u7684\u7231\u6db2\u6da6\u6ed1\uff0c\u7136\u540e\u6162\u6162\u5730\u5730\u6324\u4e86\u8fdb\u6765\u3002\u548c\u524d\u9762\u7684\u871c\u7a74\u611f\u89c9\u53c8\u6709\u4e0d\u540c\uff0c\u5c11\u5973\u7684\u809b\u80a0\u7d27\u7a84\u5f97\u4e0d\u53ef\u601d\u8bae\uff0c\u51e0\u4e4e\u8981\u5c06\u5979\u7684\u624b\u6307\u52d2\u65ad\u4e00\u822c\uff0c\u800c\u4e14\u5341\u5206\u7684\u706b\u70ed\u6eda\u70eb\u3002\u5979\u4e00\u70b9\u4e00\u70b9\u5730\u8fdb\u5165\uff0c\u5148\u662f\u98df\u6307\u6307\u5c16\uff0c\u63a5\u7740\u662f\u7b2c\u4e00\u4e2a\u6307\u8282\uff0c\u7136\u540e\u6162\u6162\u63a2\u5165\uff0c\u76f4\u5230\u7b2c\u4e8c\u4e2a\u6307\u8282\u3002\u6885\u83f2\u65af\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u547b\u541f\u8d77\u6765\uff0c\u201c\u4e0d\uff0c\u4e0d\u8981\u2026\u2026\u201d\u5979\u6323\u624e\u7740\uff0c\u4f46\u5a9a\u836f\u5df2\u7ecf\u5f00\u59cb\u5b8c\u5168\u53d1\u4f5c\uff0c\u5979\u7684\u8eab\u4f53\u7528\u4e0d\u51fa\u4e00\u4e1d\u4e00\u6beb\u7684\u529b\u6c14\uff0c\u53ea\u80fd\u4efb\u7531\u51db\u4e0d\u7d27\u4e0d\u6162\u5730\u5c06\u6574\u6839\u98df\u6307\u90fd\u63d2\u4e86\u8fdb\u53bb\u3002</p><p>\u88ab\u5f02\u7269\u63d2\u5165\uff0c\u80a0\u58c1\u7d27\u7d27\u5305\u88f9\u7740\uff0c\u8815\u52a8\u7740\uff0c\u81ea\u7136\u800c\u7136\u5730\u505a\u51fa\u6392\u65a5\u53cd\u5e94\u3002\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u51db\u7684\u624b\u6307\u88ab\u4e00\u70b9\u70b9\u5730\u6324\u4e86\u51fa\u6765\u3002\u6885\u83f2\u65af\u7684\u5598\u606f\u5a07\u541f\u7740\uff0c\u58f0\u97f3\u8d8a\u6765\u8d8a\u5927\uff0c\u4e5f\u8d8a\u6765\u8d8a\u67d4\u5a9a\u8bf1\u4eba\u3002\u201c\u743c\u6069\u2026\u2026\u201d\u5979\u7d27\u95ed\u7740\u773c\uff0c\u8d1d\u9f7f\u7d27\u54ac\u4e0b\u5507\uff0c\u65e0\u610f\u8bc6\u5730\u53eb\u7740\u60c5\u4eba\u7684\u540d\u5b57\u3002</p><p>\u201c\u771f\u662f\u7684\uff0c\u201d\u51db\u5658\u8d77\u5c0f\u5634\uff0c\u201c\u5c31\u8bb0\u5f97\u4ed6\u3002\u201d</p><p>\u5979\u56de\u5934\u770b\u770b\u5df2\u7ecf\u6309\u637a\u4e0d\u4f4f\u7684\u743c\u6069\uff0c\u604b\u604b\u4e0d\u820d\u5730\u4ece\u6885\u83f2\u65af\u8eab\u4e0a\u722c\u4e0b\u6765\u3002\u201c\u597d\u5566\uff0c\u201d\u5979\u5ba3\u5e03\uff0c\u201c\u827e\u5f25\u8587\u5df2\u7ecf\u53ef\u4ee5\u5403\u4e86\uff0c\u5979\u7b49\u7740\u4f60\u5462\u3002\u201d</p><p>\u743c\u6069\u4fef\u8eab\u62b1\u8d77\u6885\u83f2\u65af\uff0c\u4eb2\u543b\u5979\u7684\u6a31\u5507\uff0c\u611f\u53d7\u5230\u719f\u6089\u7684\u7537\u4eba\u6c14\u606f\uff0c\u91d1\u53d1\u5c11\u5973\u53cc\u81c2\u73af\u7ed5\u6263\u5728\u743c\u6069\u8116\u9888\u4e0a\uff0c\u70ed\u70c8\u5730\u56de\u543b\u7740\u3002\u743c\u6069\u8dea\u5728\u5c11\u5973\u817f\u95f4\uff0c\u7f13\u7f13\u633a\u8fdb\u7740\uff0c\u903c\u8feb\u5979\u5c06\u53cc\u817f\u5927\u5927\u5206\u5f00\uff0c\u4ed6\u80ef\u4e0b\u6602\u7136\u633a\u7acb\u7684\u5de8\u7269\u5df2\u7ecf\u6740\u6c14\u817e\u817e\uff0c\u8feb\u4e0d\u53ca\u5f85\u60f3\u8981\u54c1\u5c1d\u8eab\u4e0b\u7edd\u8272\u5c11\u5973\u7684\u6ecb\u5473\u3002</p><p>\u201c\u522b\u63d2\u9519\u5730\u65b9\u4e86\u554a\uff0c\u201d\u51db\u8b66\u544a\uff0c\u201c\u524d\u9762\u90a3\u91cc\u662f\u4e0d\u80fd\u78b0\u7684\uff0c\u4f1a\u6709\u5f88\u4e25\u91cd\u540e\u679c\u7684\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u8003\u8651\u5230\u662f\u7b2c\u4e00\u6b21\uff0c\u743c\u6069\u51b3\u5b9a\u91c7\u53d6\u6bd4\u8f83\u5bb9\u6613\u7684\u59ff\u52bf\uff0c\u4ed6\u6276\u7740\u6885\u83f2\u65af\uff0c\u5728\u8033\u8fb9\u8f7b\u58f0\u547d\u4ee4\u5979\u8dea\u4f0f\u5728\u5e8a\u4e0a\u3002\u5c11\u5973\u4e56\u4e56\u5730\u7167\u505a\uff0c\u4e0a\u534a\u8eab\u8d34\u7740\u5e8a\u5355\uff0c\u7528\u624b\u81c2\u652f\u6491\u7740\uff0c\u81c0\u90e8\u9ad8\u9ad8\u7fd8\u8d77\uff0c\u7b49\u5f85\u7740\u7537\u4eba\u7684\u8fdb\u5165\u3002</p><p>\u743c\u6069\u63e1\u7740\u5979\u7684\u7ea4\u8170\uff0c\u6b63\u51c6\u5907\u633a\u8eab\u800c\u5165\uff0c\u51db\u7a81\u7136\u53c8\u963b\u6b62\u4e86\u4ed6\u3002\u201c\u7b49\u4e00\u4e0b\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u5e94\u8be5\u5148\u5f04\u6e7f\u5427\uff0c\u4e0d\u7136\u827e\u5f25\u8587\u4f1a\u53d7\u4e0d\u4e86\u7684\u3002\u201d</p><p>\u9053\u7406\u662f\u6ca1\u9519\uff0c\u4f46\u73b0\u5728\u4e00\u65f6\u534a\u523b\uff0c\u4ece\u54ea\u91cc\u5f04\u6da6\u6ed1\u7684\u4e1c\u897f\u53bb\u3002\u51db\u7565\u4e00\u8e0c\u8e87\uff0c\u8dea\u5728\u743c\u6069\u8eab\u524d\uff0c\u4f38\u624b\u5c06\u6563\u843d\u4e0b\u6765\u7684\u51e0\u7f15\u5934\u53d1\u62c2\u5230\u8033\u540e\uff0c\u5f20\u5f00\u6a31\u5507\u5c31\u5c06\u90a3\u6839\u6eda\u70eb\u7684\u5de8\u7269\u542b\u4e86\u8fdb\u53bb\u3002</p><p>\u743c\u6069\u88ab\u8fd9\u610f\u5916\u7684\u53d8\u6545\u7ed9\u60ca\u5446\u4e86\uff0c\u4e00\u65f6\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\uff0c\u51db\u5374\u4f3c\u4e4e\u5168\u4e0d\u5728\u610f\uff0c\u5979\u5356\u529b\u5730\u7528\u5507\u820c\u8214\u8210\u7740\uff0c\u76f4\u5230\u5c06\u6574\u4e2a\u8089\u68d2\u90fd\u6e7f\u6da6\u900f\u4e86\u3002\u201c\u597d\u4e86\uff0c\u201d\u5979\u5410\u51fa\u6765\uff0c\u201c\u53ef\u4ee5\u4e86\u5427\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u743c\u6069\u5982\u68a6\u65b9\u9192\uff0c\u770b\u7740\u81ea\u5df1\u90a3\u901a\u7ea2\u72f0\u72de\u7684\u5de8\u7269\uff0c\u4e0a\u9762\u5df2\u7ecf\u88ab\u88f9\u4e0a\u4e86\u4e00\u5c42\u900f\u660e\u7684\u553e\u6db2\uff0c\u90a3\u662f\u51db\u521a\u521a\u52aa\u529b\u7684\u529f\u52b3\u3002\u201c\u771f\u662f\u4e2a\u5c0f\u9b54\u5973\u554a\u3002\u201d\u4ed6\u5728\u5fc3\u91cc\u611f\u53f9\u7740\uff0c\u518d\u4e0d\u8fdf\u7591\uff0c\u6162\u6162\u5c06\u8089\u68d2\u5c16\u7aef\u62b5\u5728\u6885\u83f2\u65af\u7684\u83ca\u7a74\u4e0a\uff0c\u4e00\u70b9\u4e00\u70b9\u7528\u529b\u6324\u4e86\u8fdb\u53bb\u3002</p><p>\u201c\u554a\u2026\u2026\u554a\u2026\u2026\u554a\uff01\u201d</p><p>\u91d1\u53d1\u5c11\u5973\u5fcd\u4e0d\u4f4f\u5927\u58f0\u547b\u541f\u8d77\u6765\uff0c\u5979\u611f\u89c9\u5230\u6bd4\u521a\u624d\u51db\u7684\u624b\u6307\u7c97\u58ee\u5343\u767e\u500d\u7684\u5de8\u7269\u51f6\u72e0\u5730\u95ef\u5165\uff0c\u706b\u8fa3\u8fa3\u7684\u611f\u89c9\u4f20\u6765\uff0c\u4e00\u5b9a\u662f\u90a3\u91cc\u7684\u808c\u8089\u88ab\u6495\u5f00\u4e86\u3002\u5979\u672c\u80fd\u5730\u60f3\u8981\u5f80\u524d\u9003\u8131\uff0c\u4f46\u8170\u80a2\u88ab\u7537\u4eba\u7684\u53cc\u624b\u7d27\u7d27\u6263\u7740\uff0c\u5206\u6beb\u52a8\u5f39\u4e0d\u5f97\uff0c\u53ea\u80fd\u7528\u80f3\u818a\u6795\u7740\u5934\uff0c\u65e0\u529b\u5730\u8db4\u4f0f\u5728\u5e8a\u4e0a\uff0c\u8fce\u63a5\u7740\u7537\u4eba\u7684\u7c97\u66b4\u3002</p><p>\u743c\u6069\u7f13\u7f13\u7528\u529b\uff0c\u770b\u7740\u81ea\u5df1\u7684\u8089\u68d2\u4e00\u70b9\u70b9\u6324\u8fdb\u5c11\u5973\u7684\u8eab\u4f53\uff0c\u539f\u672c\u7cbe\u5de7\u7684\u83ca\u82b1\u88ab\u5927\u5927\u6491\u5f00\uff0c\u6de1\u8910\u8272\u7684\u8936\u76b1\u629a\u5e73\u4e86\u3002\u706b\u70ed\u6eda\u70eb\u7684\u809b\u80a0\u5728\u8815\u52a8\u7740\uff0c\u672c\u80fd\u5730\u62b5\u6297\u7740\u8089\u68d2\u7684\u5165\u4fb5\uff0c\u4f46\u8fd9\u53cd\u800c\u4ee4\u7537\u4eba\u66f4\u52a0\u8212\u723d\u4e0d\u5df2\uff0c\u611f\u89c9\u8089\u68d2\u4eff\u4f5b\u5728\u88ab\u56db\u9762\u516b\u65b9\u5730\u7d27\u7d27\u5305\u88f9\u6309\u6469\u7740\uff0c\u51e0\u4e4e\u90fd\u8981\u7acb\u523b\u53d1\u6cc4\u51fa\u6765\u3002\u4ed6\u6df1\u5438\u4e86\u53e3\u6c14\uff0c\u7f13\u7f13\u7a33\u4f4f\u9635\u811a\uff0c\u6162\u6162\u524d\u8fdb\uff0c\u6700\u7ec8\u5c06\u7c97\u957f\u7684\u8089\u68d2\u5b8c\u5b8c\u5168\u5168\u6ca1\u5165\u5c11\u5973\u7684\u4f53\u5185\u3002</p><p>\u201c\u771f\u723d\u3002\u201d\u743c\u6069\u6ee1\u8db3\u5730\u53f9\u606f\u7740\uff0c\u7f13\u7f13\u62bd\u51fa\uff0c\u7136\u540e\u518d\u4e00\u6b21\u6df1\u6df1\u8fdb\u5165\u3002\u6885\u83f2\u65af\u53ea\u89c9\u773c\u524d\u91d1\u661f\u4e71\u5192\uff0c\u4e00\u9635\u4e00\u9635\u5730\u6655\u7729\u4f20\u6765\uff0c\u81ea\u5df1\u7684\u8eab\u4f53\u4eff\u4f5b\u8981\u88ab\u6495\u88c2\u4e00\u822c\u3002\u5979\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u65e0\u58f0\u5730\u54ed\u6ce3\u8d77\u6765\uff0c\u6cea\u6c34\u6253\u6e7f\u4e86\u80f3\u818a\u3002\u7136\u800c\u5df2\u7ecf\u8fdb\u5165\u75af\u72c2\u72b6\u6001\u7684\u743c\u6069\u5e76\u6ca1\u6709\u5bdf\u89c9\uff0c\u4ed6\u4f9d\u65e7\u91cd\u590d\u7740\u6d3b\u585e\u8fd0\u52a8\uff0c\u800c\u4e14\u52a8\u4f5c\u8d8a\u6765\u8d8a\u5feb\uff0c\u8d8a\u6765\u8d8a\u6fc0\u70c8\uff0c\u556a\u556a\u556a\u556a\u7684\u8089\u4f53\u649e\u51fb\u58f0\u8d8a\u6765\u8d8a\u5bc6\u96c6\uff0c\u6885\u83f2\u65af\u67d4\u5f31\u7684\u8eab\u4f53\u4eff\u4f5b\u60ca\u6d9b\u9a87\u6d6a\u4e2d\u7684\u4e00\u53f6\u5c0f\u821f\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u88ab\u649e\u98de\u4f3c\u7684\u3002</p><p>\u51db\u6709\u4e9b\u62c5\u5fc3\u5730\u770b\u7740\uff0c\u5979\u54ac\u4e86\u54ac\u5634\u5507\uff0c\u4ece\u65c1\u8fb9\u62b1\u4f4f\u4e86\u6885\u83f2\u65af\u7684\u80a9\uff0c\u6162\u6162\u79fb\u52a8\u8eab\u4f53\uff0c\u8ba9\u81ea\u5df1\u94bb\u5230\u4e86\u91d1\u53d1\u5c11\u5973\u7684\u8eab\u4e0b\u3002\u5979\u6402\u7740\u6885\u83f2\u65af\u7684\u8116\u9888\uff0c\u4eb2\u543b\u5979\u7684\u8138\u86cb\uff0c\u8214\u53bb\u5979\u7684\u6cea\u6c34\uff0c\u201c\u6ca1\u4e8b\uff0c\u522b\u54ed\uff0c\u201d\u5979\u5b89\u6170\u7740\uff0c\u201c\u8fc7\u4e00\u4f1a\u5c31\u597d\u4e86\u3002\u201d</p><p>\u4f3c\u4e4e\u662f\u9a8c\u8bc1\u4e86\u51db\u7684\u8bdd\uff0c\u6885\u83f2\u65af\u7684\u6cea\u6c34\u6e10\u6e10\u6b62\u4f4f\uff0c\u540e\u5ead\u4f20\u6765\u7684\u75bc\u75db\u548c\u4e0d\u9002\u5df2\u7ecf\u6e10\u6e10\u6d88\u5931\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u65e0\u53ef\u8a00\u55bb\u7684\u5145\u5b9e\u5feb\u611f\uff0c\u5c06\u5979\u6574\u4e2a\u5305\u56f4\u7740\uff0c\u611f\u89c9\u81ea\u5df1\u5b8c\u5b8c\u5168\u5168\u88ab\u8eab\u540e\u7684\u7537\u4eba\u6240\u5360\u6709\u3002\u5979\u518d\u6b21\u547b\u541f\u8d77\u6765\uff0c\u4f46\u5176\u4e2d\u4e0d\u518d\u542b\u6709\u75db\u82e6\uff0c\u800c\u662f\u5f7b\u5e95\u653e\u5f00\u8eab\u5fc3\u7684\u6109\u60a6\u3002</p><p>\u5378\u4e0b\u4e00\u5207\u7684\u9632\u5907\u548c\u77dc\u6301\uff0c\u628a\u81ea\u5df1\u5b8c\u5168\u4ea4\u7ed9\u8fd9\u4e2a\u6b63\u5728\u81ea\u5df1\u8eab\u4f53\u91cc\u6a2a\u51b2\u76f4\u649e\u7684\u7537\u4eba\u3002</p><p>\u51db\u5ac9\u5992\u5730\u770b\u7740\u743c\u6069\uff0c\u518d\u6b21\u543b\u4e0a\u4e86\u6885\u83f2\u65af\u7684\u5634\u5507\uff0c\u53cc\u624b\u79fb\u5230\u5979\u7684\u80f8\u524d\uff0c\u8086\u610f\u73a9\u5f04\u7740\u3002\u88ab\u60c5\u6b32\u7684\u706b\u7130\u707c\u70e7\u8fd9\u4e48\u4e45\uff0c\u6885\u83f2\u65af\u7684\u5634\u5507\u5df2\u7ecf\u6709\u4e9b\u5e72\u4e86\uff0c\u51db\u81ea\u5df1\u4e5f\u6709\u4e9b\u53e3\u6e34\uff0c\u5979\u773c\u89d2\u4f59\u5149\u4e00\u77a5\uff0c\u89c1\u5e8a\u5934\u653e\u7740\u534a\u676f\u6c34\uff0c\u4e8e\u662f\u4f38\u81c2\u53d6\u8fc7\u6765\uff0c\u81ea\u5df1\u5148\u559d\u4e86\u51e0\u53e3\uff0c\u7136\u540e\u518d\u555c\u4e00\u53e3\uff0c\u5374\u4e0d\u541e\u4e0b\uff0c\u800c\u662f\u542b\u7740\u9001\u5230\u6885\u83f2\u65af\u7684\u53e3\u4e2d\u3002</p><p>\u6708\u5149\u6084\u6084\u8f6c\u79fb\u4e86\u65b9\u4f4d\uff0c\u76f4\u5c04\u5728\u5e8a\u4e0a\u7684\u4e09\u4e2a\u4eba\u8eab\u4e0a\u3002\u9ed1\u53d1\u5c11\u5973\u8eba\u5728\u6700\u4e0b\u9762\uff0c\u6b63\u548c\u8dea\u4f0f\u5728\u8eab\u4e0a\u7684\u91d1\u53d1\u5c11\u5973\u751c\u871c\u5730\u63a5\u543b\uff1b\u800c\u5728\u91d1\u53d1\u5c11\u5973\u8eab\u540e\uff0c\u4e00\u4e2a\u7537\u4eba\u6b63\u7d27\u6263\u7740\u5979\u7684\u7ea4\u8170\uff0c\u51f6\u731b\u5730\u649e\u51fb\u51b2\u7a81\u7740\uff0c\u52a8\u4f5c\u8d8a\u6765\u8d8a\u5267\u70c8\u3002</p><p>\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u743c\u6069\u4f4e\u543c\u4e86\u4e00\u58f0\uff0c\u7ec8\u4e8e\u6ee1\u8db3\u5730\u5728\u5c11\u5973\u4f53\u5185\u53d1\u6cc4\u51fa\u6765\u3002\u53d7\u6b64\u51b2\u51fb\uff0c\u6885\u83f2\u65af\u7684\u8eab\u4f53\u5267\u70c8\u7d27\u7ef7\u8d77\u6765\uff0c\u8db3\u8db3\u6301\u7eed\u4e86\u5341\u79d2\u949f\uff0c\u6700\u7ec8\u4eff\u4f5b\u8017\u5c3d\u4e86\u5168\u8eab\u6700\u540e\u4e00\u4e1d\u529b\u6c14\u4f3c\u7684\uff0c\u624b\u81c2\u548c\u819d\u76d6\u518d\u4e5f\u652f\u6491\u4e0d\u4f4f\u8eab\u4f53\uff0c\u8f6f\u8f6f\u762b\u4e0b\u6765\uff0c\u4ece\u51db\u7684\u8eab\u4e0a\u7ffb\u4e0b\uff0c\u8eba\u5728\u5e8a\u4e0a\u9759\u9759\u5598\u606f\u3002\u743c\u6069\u4e5f\u6709\u4e9b\u5934\u6655\u773c\u82b1\uff0c\u4e24\u817f\u53d1\u8f6f\uff0c\u4ed6\u6b63\u51c6\u5907\u8eba\u4e0b\u6765\u4f11\u606f\u4e00\u4f1a\uff0c\u5374\u611f\u89c9\u6709\u4eba\u4ece\u8eab\u540e\u5c06\u81ea\u5df1\u62b1\u4f4f\u4e86\uff0c\u4e24\u56e2\u7855\u5927\u67d4\u8f6f\u7684\u7c89\u5ae9\u8089\u4f53\u62b5\u5728\u81ea\u5df1\u80cc\u540e\uff0c\u6eda\u70eb\u7684\u547c\u5438\u9635\u9635\u5439\u8fdb\u8033\u6735\u91cc\u3002</p><p>\u201c\u60f3\u8981\uff0c\u201d\u51db\u8bf4\uff0c\u5979\u610f\u4e71\u60c5\u8ff7\u5730\u8f7b\u54ac\u7740\u743c\u6069\u7684\u8033\u5782\uff0c\u4f4e\u58f0\u54c0\u6c42\u7740\uff0c\u201c\u51db\u4e5f\u60f3\u8981\u561b\uff0c\u4e5f\u7ed9\u51db\u597d\u4e0d\u597d\u3002\u201d</p></article>"}}}]);
