(window.webpackJsonp=window.webpackJsonp||[]).push([[198],{221:function(p){p.exports={chapterTitle:null,title:"\u7b2c1173\u7ae0 \u4e8c\u5341\u4e00\u4e16\u7eaa\u4ec0\u4e48\u6700\u8d35",content:"<article><h2>\u7b2c1173\u7ae0 \u4e8c\u5341\u4e00\u4e16\u7eaa\u4ec0\u4e48\u6700\u8d35</h2><p>\u3000\u3000\u859b\u660e\u697c\u968f\u6c5f\u654f\u4e4b\u79bb\u5f00\u9644\u697c\u7684\u4f1a\u8bae\u5ba4\uff0c\u6c5f\u654f\u4e4b\u7684\u529e\u516c\u5ba4\u5728\u4e3b\u697c\u516d\u697c\uff0c\u4e3b\u697c\u4e0e\u9644\u697c\u4e4b\u95f4\u7684\u8fc7\u9053\u9634\u6697\u72ed\u957f\uff0c\u8fc7\u9053\u4e24\u9053\u7684\u529e\u516c\u5ba4\u90fd\u95e8\u5ead\u7d27\u95ed\uff0c\u4e0d\u6653\u5f97\u7701\u653f\u5e9c\u7684\u5de5\u4f5c\u4eba\u5458\u5c06\u81ea\u5df1\u4e25\u4e25\u5b9e\u5b9e\u7684\u5173\u5728\u529e\u516c\u5ba4\u91cc\u505a\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u79bb\u5f00\u4f1a\u8bae\u5ba4\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u8138\u4e0a\u8fd8\u6302\u7740\u7b11\u5bb9\u7684\uff0c\u8f6c\u8fc7\u8eab\uff0c\u8f7b\u8f7b\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u4f3c\u4e4e\u8138\u4e0a\u7684\u7b11\u5bb9\u4e5f\u5728\u8fd9\u8f7b\u54b3\u4e4b\u95f4\u5c31\u5206\u5d29\u79bb\u6790\u4e86\u2014\u2014\u7a7f\u8fc7\u8fc7\u9053\u65f6\uff0c\u6c5f\u654f\u4e4b\u9634\u6c89\u7740\u8138\uff0c\u859b\u660e\u697c\u8ddf\u5728\u6c5f\u654f\u4e4b\u8eab\u540e\u4e5f\u9ed8\u4e0d\u505a\u58f0\u3002</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u6700\u8fd1\u624d\u5728\u5218\u95fb\u6d9b\u7684\u8c03\u52a8\u95ee\u9898\u4e0a\u677e\u53e3\uff0c\u5bf9\u7701\u653f\u5e9c\u7684\u5206\u7ba1\u5de5\u4f5c\u5b89\u6392\u4e5f\u6697\u793a\u4e0d\u4f1a\u8fc7\u591a\u7684\u5e72\u6d89\uff0c\u6c5f\u654f\u4e4b\u4ee5\u4e3a\u9648\u5176\u658c\u6848\u3001\u4e91\u6797\u8bc1\u5238\u6848\u7684\u538b\u529b\u8ba9\u674e\u8fdc\u6e56\u7684\u9635\u811a\u677e\u52a8\uff0c\u4ee5\u4e3a\u8fd9\u662f\u4ed6\u5230\u4e1c\u6d77\u540e\u7684\u7b2c\u4e00\u4e2a\u5927\u80dc\u6218\u3002\u4ed6\u4e4b\u524d\u8981\u6c42\u9646\u6587\u592b\u53c2\u52a0\u4eca\u5929\u4e0b\u5348\u7684\u78b0\u5934\u4f1a\uff0c\u4e3b\u8981\u662f\u4e3a\u4eca\u540e\u8c03\u6574\u9646\u6587\u592b\u53bb\u5206\u7ba1\u6559\u80b2\u5de5\u4f5c\u3001\u5218\u95fb\u6d9b\u8c03\u5230\u7701\u91cc\u4e4b\u540e\u63a5\u624b\u9646\u6587\u592b\u4e4b\u524d\u5206\u7ba1\u9886\u57df\u505a\u94fa\u57ab\u3002\u6c5f\u654f\u4e4b\u8fd8\u4ee5\u4e3a\u5df2\u7ecf\u5728\u4e1c\u6d77\u8d62\u5f97\u4e00\u5b9a\u7684\u4e3b\u52a8\uff0c\u9526\u6e56\u4eca\u5929\u7684\u6177\u6168\u89e3\u56ca\uff0c\u5374\u8ba9\u4ed6\u6709\u9971\u53d7\u632b\u6298\u7684\u5931\u843d\u611f\uff0c\u7279\u522b\u8fd8\u662f\u674e\u8fdc\u6e56\u4eb2\u81ea\u6253\u7535\u8bdd\u6765\u8981\u4ed6\u53c2\u52a0\u4e0b\u5348\u7684\u4f1a\u8bae\u3002</p><p>\u3000\u3000\u5230\u4e86\u529e\u516c\u5ba4\uff0c\u6c5f\u654f\u4e4b\u63e1\u7740\u95e8\u628a\u624b\uff0c\u72b9\u8c6b\u4e86\u7247\u523b\uff0c\u624d\u5429\u5490\u859b\u660e\u697c\uff1a\u201c\u4e0b\u5348\u8981\u6ca1\u6709\u4ec0\u4e48\u597d\u7d27\u7684\u4e8b\u60c5\uff0c\u90fd\u5e2e\u63a8\u6389\u3002\u201d</p><p>\u3000\u3000\u201c\u55ef\u3002\u201d\u859b\u660e\u697c\u70b9\u5934\u5e94\u9053\uff0c\u770b\u7740\u6c5f\u654f\u4e4b\u8d70\u8fdb\u91cc\u95f4\u7684\u529e\u516c\u5ba4\uff0c\u4ed6\u8d70\u56de\u81ea\u5df1\u7684\u529e\u516c\u684c\uff0c\u4ece\u7535\u8111\u91cc\u8c03\u51fa\u9526\u6e56\u7684\u8d44\u6599\u6765\u770b\u3002</p><p>\u3000\u3000\u516b\u5343\u4e07\u7f8e\u5143\u5c31\u662f\u516d\u4ebf\u4e94\u5343\u4e07\u4eba\u6c11\u5e01\u3002</p><p>\u3000\u3000\u5728\u5168\u7701\u9ad8\u6821\u8d22\u653f\u62e8\u6b3e\u5e74\u5ea6\u9884\u7b97\u603b\u989d\u8fd8\u53ea\u6709\u5341\u516d\u4ebf\u5143\u7684\u4eca\u5929\uff0c\u9526\u6e56\u5e74\u5185\u5c31\u8981\u5411\u9ad8\u6821\u5b66\u79d1\u5efa\u8bbe\u4fc3\u8fdb\u57fa\u91d1\u6ce8\u5165\u8d85\u8fc7\u516d\u4ebf\u5143\u7684\u8d44\u91d1\uff0c\u660e\u5e74\u7684\u9884\u7b97\u66f4\u662f\u9ad8\u8fbe\u516b\u4ebf\u5143\uff0c\u4ec5\u51ed\u8fd9\u70b9\uff0c\u5c31\u8db3\u4ee5\u5c01\u5835\u4f4f\u73b0\u5728\u6240\u6709\u5bf9\u9526\u6e56\u4e0d\u6ee1\u7684\u62b1\u6028\u3002</p><p>\u3000\u3000\u6ca1\u6709\u4eba\u80fd\u62d2\u7edd\u9526\u6e56\u7684\u6177\u6168\u6350\u6b3e\uff0c\u9526\u6e56\u4e5f\u6bd4\u4efb\u4f55\u4eba\u60f3\u8c61\u4e2d\u66f4\u4e3a\u5f3a\u5927\uff0c\u81f3\u5c11\u6c5f\u654f\u4e4b\u5728\u4ed6\u8fd9\u5c4a\u7701\u957f\u4efb\u5185\u4e0d\u8981\u60f3\u627e\u5230\u6253\u51fb\u9526\u6e56\u7684\u673a\u4f1a\uff0c\u5c31\u7b97\u5c06\u5218\u95fb\u6d9b\u8c03\u5230\u7701\u91cc\u6765\u9876\u66ff\u9646\u6587\u592b\u7684\u4f4d\u7f6e\uff0c\u5b9e\u9645\u4e0a\u80fd\u8d77\u5230\u5bf9\u9526\u6e56\u7684\u94b3\u5236\u4f5c\u7528\u4e5f\u6781\u4e3a\u6709\u9650\u3002</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u4e2d\u5348\u7684\u90a3\u901a\u7535\u8bdd\u8fd8\u771f\u662f\u503c\u5f97\u73a9\u5473\u554a\uff0c\u65e0\u7591\u662f\u544a\u8bc9\u6c5f\u654f\u4e4b\uff0c\u5728\u4e1c\u6d77\u6709\u4e9b\u754c\u7ebf\u662f\u4ed6\u8d8a\u4e0d\u8fc7\u53bb\u7684\u3002</p><p>\u3000\u3000\u859b\u660e\u697c\u770b\u7740\u7535\u8111\u5c4f\u5e55\u4e0a\u663e\u793a\u7684\u9526\u6e56\u8d44\u6599\uff0c\u63c9\u7740\u592a\u9633\u7a74\u3002</p><p>\u3000\u3000\u5e74\u521d\u65f6\u6709\u5a92\u4f53\u5f15\u7528\u7231\u8fbe\u96c6\u56e2\u6280\u672f\u603b\u8d1f\u8d23\u4eba\u4e01\u69d0\u7684\u8868\u8ff0\uff0c\u8bf4\u6a61\u6811\u56ed\u4eca\u5e74\u7684\u79d1\u7814\u9884\u7b97\u5c06\u8fbe\u5230\u56db\u4ebf\u7f8e\u5143\uff0c\u8fd9\u5728\u5f53\u65f6\u5c31\u5f15\u8d77\u4e00\u9635\u60ca\u53f9\u4e0e\u55a7\u54d7\u3002\u6839\u636e\u4fe1\u4ea7\u90e8\u6536\u96c6\u5230\u7684\u6570\u636e\uff0c\u7231\u8fbe\u96c6\u56e2\u4e5d\u4e5d\u5e74\u9500\u989d\u63a5\u8fd1\u4e24\u767e\u4ebf\uff0c\u540d\u5217\u7535\u5b50\u767e\u5f3a\u699c\u699c\u773c\uff0c\u51c0\u5229\u6da6\u8fbe\u5230\u56db\u5341\u4ebf\uff0c\u9526\u6e56\u5dee\u4e0d\u591a\u62ff\u51fa\u56db\u5206\u4e4b\u4e09\u7684\u5229\u6da6\u3001\u9500\u552e\u989d\u768416\uff05\u6295\u5165\u5230\u65b0\u7684\u6280\u672f\u7814\u53d1\u4e2d\u53bb\uff0c\u751a\u81f3\u6709\u5a92\u4f53\u8bc4\u4ef7\u9526\u6e56\u5728\u6280\u672f\u6295\u5165\u6709\u4e9b\u94bb\u725b\u89d2\u5c16\u4e86\u3002</p><p>\u3000\u3000\u73b0\u5728\u770b\u6765\u8fd9\u4e9b\u6570\u636e\u8fd8\u662f\u7ecf\u8fc7\u4e25\u91cd\u8131\u6c34\u5904\u7406\u7684\uff0c\u4e0d\u4ec5\u79d1\u7814\u9884\u7b97\u4e25\u91cd\u7f29\u6c34\uff0c\u859b\u660e\u697c\u76f8\u4fe1\u4fe1\u4ea7\u90e8\u516c\u5e03\u7684\u7231\u8fbe\u96c6\u56e2\u7684\u9500\u552e\u6536\u5165\u53ca\u5e74\u51c0\u5229\u6da6\u6570\u636e\u4e5f\u662f\u7ecf\u8fc7\u4e25\u91cd\u7f29\u6c34\u7684\u3002\u4e5f\u8bb8\u4fe1\u4ea7\u90e8\u77e5\u9053\u9526\u6e56\u5728\u6570\u636e\u4e0a\u5f04\u865a\u4f5c\u5047\uff0c\u4e5f\u8bb8\u4ed6\u4eec\u9700\u8981\u9526\u6e56\u5728\u6570\u636e\u4e0a\u5f04\u865a\u4f5c\u5047\uff0c\u8981\u8ba9\u534e\u590f\u7535\u5b50\u7ef4\u6301\u5728\u7535\u5b50\u767e\u5f3a\u699c\u7b2c\u4e00\u7684\u4f4d\u5b50\u3002</p><p>\u3000\u3000\u6c5f\u654f\u4e4b\u8981\u67e5\u9526\u6e56\u5728\u56fd\u5185\u7684\u7a0e\u6536\u6570\u636e\uff0c\u5f53\u7136\u80fd\u67e5\u5f97\u5230\uff0c\u5f88\u53ef\u60dc\u9526\u6e56\u679d\u7cfb\u590d\u6742\u7684\u4f01\u4e1a\u6210\u5458\u4f53\u7cfb\u91cc\uff0c\u5dee\u4e0d\u591a\u6709\u534a\u6570\u516c\u53f8\u90fd\u662f\u6d77\u5916\u6ce8\u518c\u7684\uff0c\u60f3\u8981\u67e5\u770b\u9526\u6e56\u6d77\u5916\u516c\u53f8\u7684\u8d22\u52a1\u6570\u636e\uff0c\u5374\u662f\u5f02\u5e38\u7684\u56f0\u96be\u3002</p><p>\u3000\u3000\u9526\u6e56\u5728\u56fd\u5185\u7684\u7a0e\u6536\u6570\u636e\u4e0e\u9526\u6e56\u5bf9\u5916\u516c\u5f00\u7684\u8d22\u52a1\u6570\u636e\u662f\u57fa\u672c\u543b\u5408\u7684\uff0c\u4e5f\u5c31\u7ed9\u522b\u4eba\u9020\u6210\u4e00\u4e2a\u5047\u8c61\uff0c\u5c31\u662f\u9526\u6e56\u6ce8\u518c\u7684\u4f17\u591a\u6d77\u5916\u516c\u53f8\u53ea\u662f\u5de5\u5177\u578b\u516c\u53f8\uff0c\u4e5f\u8bb8\u662f\u4e3a\u4e86\u4ea4\u53c9\u6301\u80a1\u7684\u65b9\u4fbf\uff0c\u8fd9\u65f6\u5019\u770b\u6765\u8fd9\u4e9b\u516c\u53f8\u5e76\u4e0d\u5168\u662f\u7a7a\u58f3\u516c\u53f8\uff0c\u53ea\u662f\u51fa\u4e8e\u79cd\u79cd\u539f\u56e0\uff0c\u90fd\u9690\u85cf\u5728\u6697\u5904\u3002</p><p>\u3000\u3000\u859b\u660e\u697c\u60f3\u5230\u53e6\u4e00\u79cd\u53ef\u80fd\u6027\uff0c\u8981\u662f\u56fd\u5185\u5bf9\u9526\u6e56\u7684\u6253\u538b\u624b\u6bb5\u8fc7\u4e8e\u62d9\u52a3\uff0c\u5f15\u8d77\u9526\u6e56\u7684\u5f3a\u70c8\u53cd\u5f39\uff0c\u90a3\u8fd9\u4e9b\u6b64\u65f6\u9690\u85cf\u5728\u6697\u5904\u7684\u6d77\u5916\u516c\u53f8\u4f1a\u4e0d\u4f1a\u9732\u51fa\u6c34\u9762\u6765\u53d1\u6325\u4f5c\u7528\uff1f</p><p>\u3000\u3000\u90fd\u8bf4\u7231\u8fbe\u662f\u9526\u6e56\u7684\u6838\u5fc3\u4f01\u4e1a\uff0c\u4ece\u53e6\u4e00\u79cd\u5c42\u6b21\u4e0a\u6765\u8bf4\uff0c\u9526\u6e56\u5546\u4e8b\u624d\u662f\u9526\u6e56\u7684\u6838\u5fc3\uff0c\u9526\u6e56\u5546\u4e8b\u6210\u7acb\u4e4b\u521d\u5c31\u5c06\u603b\u90e8\u8bbe\u5728\u9999\u6e2f\uff0c\u5927\u6982\u4e5f\u662f\u60f3\u501f\u52a9\u9999\u6e2f\u4e0e\u5185\u5730\u4e0d\u540c\u7684\u6cd5\u5f8b\u4f53\u7cfb\u5427\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u521b\u7acb\u6a61\u6811\u56ed\u4e4b\u521d\uff0c\u5c31\u786e\u5b9a\u4e86\u4ea7\u5b66\u7814\u4e00\u4f53\u5316\u53d1\u5c55\u7684\u8def\u7ebf\uff0c\u9526\u6e56\u4e0d\u53ef\u80fd\u65e0\u9650\u5236\u7684\u4ece\u6d77\u5916\u5f15\u8fdb\u9ad8\u7aef\u4eba\u624d\uff0c\u672c\u571f\u4eba\u624d\u7684\u57f9\u517b\u624d\u662f\u6839\u672c\uff0c\u800c\u4e14\u4e5f\u8981\u8ba9\u9ad8\u6821\u7684\u79d1\u7814\u529b\u91cf\u53d1\u6325\u51fa\u5e94\u7528\u7684\u4f5c\u7528\u6765\u3002</p><p>\u3000\u3000\u65e0\u8bba\u662f\u5f20\u606a\uff0c\u8fd8\u662f\u9526\u6e56\u5176\u4ed6\u9ad8\u5c42\uff0c\u90fd\u610f\u8bc6\u5230\uff0c\u7231\u8fbe\u96c6\u56e2\u4f1a\u66f4\u6ce8\u91cd\u5e94\u7528\u6280\u672f\u7684\u53d1\u5c55\uff0c\u4ece\u800c\u5ffd\u89c6\u66f4\u4e3a\u57fa\u7840\u7684\u79d1\u5b66\u7814\u7a76\u3002\u8fd9\u51e0\u4e4e\u662f\u5fc5\u7136\u7684\uff0c\u9526\u6e56\u6240\u6709\u7684\u6210\u5458\u4f01\u4e1a\u90fd\u5fc5\u987b\u56f4\u7ed5\u76c8\u5229\u8fd9\u6839\u9a71\u52a8\u8f74\u6765\u8f6c\u52a8\uff0c\u4e5f\u5c31\u65e0\u6cd5\u907f\u514d\u7684\u4f7f\u5f97\u773c\u5149\u53d7\u5230\u9650\u5236\uff0c\u65e0\u6cd5\u770b\u5230\u66f4\u957f\u8fdc\u7684\u53d1\u5c55\u65b9\u5411\u2014\u2014\u9ad8\u6821\u5e94\u8be5\u5728\u57fa\u7840\u79d1\u5b66\u7814\u7a76\u4e0e\u9ad8\u7aef\u4eba\u624d\u57f9\u517b\u4e0a\u53d1\u6325\u66f4\u91cd\u8981\u7684\u4f5c\u7528\u3002</p><p>\u3000\u3000\u4e1c\u5927\u867d\u7136\u4f4d\u5217\u56fd\u5185\u9876\u5c16\u540d\u6821\uff0c\u6709\u7740\u4e0a\u767e\u5e74\u7684\u5386\u53f2\uff0c\u4e5f\u6d8c\u73b0\u51fa\u8bf8\u591a\u6587\u5316\u4e0e\u5b66\u672f\u540d\u4eba\uff0c\u4f46\u662f\u8ddf\u4e16\u754c\u7ea7\u7684\u4e00\u6d41\u540d\u6821\u76f8\u6bd4\uff0c\u8fd8\u662f\u6709\u6781\u5927\u7684\u5dee\u8ddd\u3002</p><p>\u3000\u3000\u65e0\u8bba\u662f\u4ece\u53cc\u5bfc\u4f53\u5236\u7b49\u76f8\u5173\u7684\u57f9\u517b\u4f53\u5236\uff0c\u8fd8\u662f\u6a61\u6811\u56ed\u521b\u4e1a\u57fa\u91d1\u7b49\u76f8\u5173\u7684\u79d1\u7814\u521b\u4e1a\u6fc0\u52b1\u4f53\u5236\uff0c\u9526\u6e56\u5728\u4ea7\u5b66\u7814\u4e00\u4f53\u5316\u53d1\u5c55\u4e0a\u505a\u4e86\u8bb8\u591a\u7684\u5c1d\u8bd5\uff0c\u4e5f\u505a\u4e86\u8bb8\u591a\u7684\u57fa\u7840\u5de5\u4f5c\u5e76\u5353\u6709\u6210\u6548\uff0c\u773c\u4e0b\u662f\u5230\u63a8\u52a8\u76f8\u5173\u5de5\u4f5c\u5feb\u901f\u5f80\u524d\u53d1\u5c55\u5e76\u6b63\u89c4\u5316\u7684\u65f6\u5019\u4e86\u3002\u8fd9\u624d\u8fd1\u671f\u505a\u51fa\u51b3\u7b56\u5728\u6a61\u6811\u56ed\u540d\u4e0b\u6210\u7acb\u9ad8\u6821\u5b66\u79d1\u5efa\u8bbe\u4fc3\u8fdb\u57fa\u91d1\uff0c\u6bcf\u5e74\u62ff\u51fa\u4e00\u5b9a\u6bd4\u4f8b\u7684\u79d1\u7814\u7ecf\u8d39\u9884\u7b97\u6ce8\u5165\u8be5\u57fa\u91d1\uff0c\u4e3b\u8981\u7528\u6765\u5e2e\u52a9\u4e1c\u5927\u8fc5\u901f\u6210\u957f\u4e3a\u4e16\u754c\u4e00\u6d41\u7684\u7814\u7a76\u578b\u5927\u5b66\uff0c\u4f7f\u4e1c\u5927\u6210\u4e3a\u9526\u6e56\u672a\u6765\u771f\u6b63\u7684\u80fd\u4f9d\u9760\u7684\u6839\u57fa\u3002</p><p>\u3000\u3000\u674e\u9e3f\u94ed\u4e0b\u5348\u53c2\u4e0e\u4f1a\u8bae\u4e4b\u524d\uff0c\u53ea\u5e0c\u671b\u9526\u6e56\u7684\u6350\u52a9\u6b3e\u8fd8\u4fdd\u6301\u8ddf\u4e0a\u4e00\u5b66\u5e74\u540c\u7b49\u7684\u6c34\u5e73\uff0c\u8fd9\u6837\u5c31\u80fd\u8ba9\u4e1c\u5927\u4e0b\u4e00\u4e2a\u5b66\u5e74\u6709\u8f83\u4e3a\u8f7b\u677e\u7684\u9884\u7b97\u73af\u5883\uff0c\u80fd\u591f\u591a\u505a\u51e0\u4ef6\u4e8b\u60c5\u3002\u8c01\u66fe\u60f3\u5230\u9526\u6e56\u4e0b\u534a\u5e74\u5c31\u8981\u5f80\u5b66\u79d1\u5efa\u8bbe\u4fc3\u8fdb\u57fa\u91d1\u91cc\u6ce8\u5165\u516b\u5343\u4e07\u7f8e\u5143\u7684\u8d44\u91d1\uff0c\u5dee\u4e0d\u591a\u7b49\u4e8e\u4e1c\u5927\u4e00\u5e74\u7684\u6536\u5165\u603b\u548c\u3002</p><p>\u3000\u3000\u674e\u9e3f\u94ed\u4e0e\u9648\u4fe1\u751f\u521d\u6b65\u4ea4\u6362\u8fc7\u610f\u89c1\uff0c\u5b66\u79d1\u5efa\u8bbe\u4fc3\u8fdb\u57fa\u91d1\u5c31\u662f\u4e3a\u4e86\u5e2e\u52a9\u4e1c\u5927\u5c3d\u5feb\u53d1\u5c55\u6210\u4e3a\u4e16\u754c\u7ea7\u7684\u4e00\u6d41\u540d\u6821\uff0c\u57fa\u91d1\u8bc4\u5ba1\u59d4\u5458\u4f1a\u6210\u5458\u4e5f\u591a\u4ece\u4e1c\u5927\u6559\u6388\u4e2d\u6311\u9009\u7ec4\u6210\u3002\u9648\u4fe1\u751f\u6ca1\u6709\u8ddf\u674e\u9e3f\u94ed\u8bf4\u57fa\u91d1\u6295\u5165\u5360\u6a61\u6811\u56ed\u79d1\u7814\u9884\u7b97\u7684\u6bd4\u4f8b\uff0c\u53ea\u662f\u8ddf\u4ed6\u627f\u8bfa\u4e94\u5e74\u5185\u6295\u5165\u4e0d\u4f4e\u4e8e\u516b\u4ebf\u7f8e\u5143\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u4e0e\u5f20\u606a\u3001\u53f6\u5c0f\u6850\u4e0b\u5348\u5728\u6a61\u6811\u56ed\u5185\u90e8\u8fd8\u6709\u4f1a\u8bae\u8981\u53c2\u52a0\uff0c\u5148\u5750\u8f66\u79bb\u5f00\u4e86\u3002</p><p>\u3000\u3000\u674e\u9e3f\u94ed\u7684\u5fc3\u60c5\u8fc7\u4e86\u8bb8\u4e45\u624d\u7a0d\u5e73\u590d\u4e0b\u6765\uff0c\u5750\u8fdb\u8f66\u91cc\uff0c\u5927\u58f0\u8ddf\u5d14\u56fd\u6052\u3001\u79e6\u521a\u4e24\u4eba\u62b1\u6028\uff1a\u201c\u4f60\u4eec\u4fe9\u4e2a\u554a\uff0c\u8fc7\u6765\u4e4b\u524d\u8fd8\u8ddf\u6211\u6253\u9a6c\u864e\u773c\uff01\u201d</p><p>\u3000\u3000\u79e6\u521a\u644a\u4e86\u644a\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u542c\u8bf4\u662f\u8ddf\u53bb\u5e74\u53d8\u5316\u4e0d\u5927\uff0c\u5374\u662f\u4e0d\u77e5\u9053\u5c06\u53bb\u5e74\u7231\u8fbe\u6587\u5316\u57fa\u91d1\u7684\u6350\u6b3e\u4e5f\u8ba1\u7b97\u5728\u5185\u3002\u201d</p><p>\u3000\u3000\u53bb\u5e74\u9526\u6e56\u5411\u9ad8\u6821\u6559\u80b2\u7cfb\u7edf\u6350\u6b3e\u4e24\u4ebf\u5143\uff0c\u6350\u6b3e\u56db\u4ebf\u6210\u7acb\u7231\u8fbe\u6587\u5316\u57fa\u91d1\uff0c\u603b\u8ba1\u516d\u4ebf\u4eba\u6c11\u5e01\uff0c\u4e0e\u516b\u5343\u4e07\u7f8e\u5143\u5012\u662f\u76f8\u5dee\u65e0\u51e0\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u771f\u4e0d\u77e5\u9053\u8be6\u60c5\uff0c\u201d\u5d14\u56fd\u6052\u7b11\u7740\u8bf4\uff0c\u201c\u516d\u6708\u4efd\uff0c\u884c\u653f\u5ba4\u8981\u7ecf\u7814\u4e2d\u5fc3\u51fa\u4e00\u4efd\u56fd\u5185\u9ad8\u6821\u5341\u5e74\u529e\u5b66\u6295\u5165\u7684\u8bc4\u4f30\u62a5\u544a\uff0c\u6211\u4eec\u9884\u6d4b\u6e05\u534e\u5927\u5b66\u5341\u5e74\u540e\u7684\u529e\u5b66\u7ecf\u8d39\u6295\u5165\u5c06\u8fbe\u5230\u6bcf\u5e74\u516d\u4ebf\u7f8e\u5143\uff0c\u4e1c\u5927\u5927\u6982\u53ea\u80fd\u8fbe\u5230\u6e05\u534e\u5927\u5b66\u4e00\u534a\u7684\u6c34\u5e73\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u73b0\u5728\u7684\u529e\u5b66\u7ecf\u8d39\u6295\u5165\u4e5f\u53ea\u6709\u6e05\u534e\u5927\u5b66\u4e00\u534a\u7684\u6c34\u5e73\uff0c\u201d\u674e\u9e3f\u94ed\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u6709\u5b66\u79d1\u5efa\u8bbe\u4fc3\u8fdb\u57fa\u91d1\u7684\u8865\u5145\uff0c\u8fd9\u4e2a\u5dee\u8ddd\u5c31\u62b9\u5e73\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u5927\u6982\u4e5f\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\uff0c\u201d\u5d14\u56fd\u6052\u8bf4\u9053\uff0c\u201c\u5f00\u59cb\u4e5f\u6ca1\u6709\u8bf4\u660e\u6570\u989d\uff0c\u53ea\u662f\u8ba8\u8bba\u8fc7\u6350\u6b3e\u662f\u96c6\u4e2d\u7ed9\u4e1c\u5927\uff0c\u8fd8\u662f\u5206\u6563\u7ed9\u5404\u9ad8\u6821\u3002\u6700\u7ec8\u8fd8\u662f\u51b3\u5b9a\u96c6\u4e2d\u7ed9\u4e1c\u5927\uff0c\u56e0\u4e3a\u4e1c\u5927\u6709\u66f4\u591a\u7684\u529e\u5b66\u81ea\u4e3b\u6743\uff0c\u6709\u4e86\u8fd9\u7b14\u8d44\u91d1\uff0c\u53ef\u4ee5\u505a\u66f4\u591a\u5c1d\u8bd5\u6027\u7684\u5de5\u4f5c\uff0c\u8981\u662f\u4e1c\u5927\u80fd\u5148\u4e8e\u6e05\u534e\u53d1\u5c55\u6210\u4e16\u754c\u7ea7\u7684\u4e00\u6d41\u540d\u6821\uff0c\u90a3\u4e1c\u5927\u7684\u529e\u5b66\u7ecf\u9a8c\u5c06\u66f4\u6709\u6307\u5bfc\u610f\u89c1\u2026\u2026\u201d</p><p>\u3000\u3000\u674e\u9e3f\u94ed\u624b\u629a\u7740\u989d\u5934\u601d\u8003\u4e00\u4e9b\u95ee\u9898\uff0c\u4ed6\u4e4b\u524d\u6839\u672c\u5c31\u6ca1\u6709\u8003\u8651\u5230\u80fd\u4ece\u9526\u6e56\u62ff\u5230\u8fd9\u4e48\u591a\u94b1\uff0c\u4ed6\u4e4b\u524d\u7ed9\u4e1c\u5927\u4e94\u5e74\u529e\u5b66\u7ecf\u8d39\u6295\u5165\u505a\u51fa\u4e00\u4e2a\u8f83\u4e3a\u5927\u80c6\u7684\u89c4\u5212\uff0c\u4e5f\u5c31\u516b\u4ebf\u7f8e\u5143\u591a\u4e9b\u4e03\u5341\u4ebf\u4eba\u6c11\u5e01\u7684\u89c4\u6a21\uff0c\u4ece\u4e2d\u592e\u53ca\u7701\u91cc\u83b7\u5f97\u76f4\u63a5\u62e8\u6b3e\u4e0d\u4f1a\u8d85\u8fc7\u4e09\u5341\u4e94\u4ebf\uff0c\u5b66\u8d39\u6536\u5165\u4e0d\u4f1a\u8d85\u8fc7\u4e94\u4ebf\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u4e1c\u5927\u8fd8\u9700\u8981\u81ea\u7b79\u4e09\u5341\u4ebf\u7684\u529e\u5b66\u8d44\u91d1\u3002\u8003\u8651\u5230\u4e1c\u5927\u8fd9\u4e48\u5927\u7684\u793e\u4f1a\u5f71\u54cd\u529b\u4ee5\u53ca\u6821\u5c5e\u4ea7\u4e1a\u6295\u8d44\u96c6\u56e2\u8fd1\u4e24\u5e74\u7684\u5de5\u4f5c\u4e5f\u5353\u6709\u6210\u6548\uff0c\u4e94\u5e74\u81ea\u7b79\u4e09\u5341\u4ebf\u529e\u5b66\u8d44\u91d1\u867d\u7136\u6709\u4e00\u5b9a\u7684\u96be\u5ea6\uff0c\u4e5f\u5e76\u975e\u529e\u4e0d\u5230\u3002</p><p>\u3000\u3000\u674e\u9e3f\u94ed\u5e38\u5e38\u81ea\u5632\u81ea\u5df1\u662f\u4e10\u5e2e\u5f1f\u5b50\uff0c\u6574\u5929\u60f3\u7740\u8ba8\u94b1\u7684\u95ee\u9898\uff0c\u6ca1\u60f3\u5230\u9526\u6e56\u627f\u8bfa\u4e94\u5e74\u5185\u901a\u8fc7\u5b66\u79d1\u5efa\u8bbe\u4fc3\u8fdb\u57fa\u91d1\u5411\u4e1c\u5927\u6ce8\u5165\u516b\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\uff0c\u8981\u662f\u4e94\u5e74\u5185\u80fd\u62e5\u6709\u8fd9\u7b14\u5de8\u989d\u9884\u7b97\uff0c\u674e\u9e3f\u94ed\u6709\u4fe1\u5fc3\u5e26\u9886\u4e1c\u5927\u51b2\u51fb\u6e05\u534e\u5927\u5b66\u5728\u56fd\u5185\u3001\u56fd\u9645\u4e0a\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u79e6\u521a\u8bdd\u5f88\u5c11\uff0c\u6d89\u53ca\u5230\u4e1c\u5927\u672a\u6765\u7684\u53d1\u5c55\u5927\u8ba1\uff0c\u6ca1\u4eba\u95ee\u4ed6\u8bdd\uff0c\u4ed6\u81ea\u7136\u4e5f\u63d2\u4e0d\u4e0a\u8bdd\uff0c\u4ed6\u4e4b\u524d\u4e5f\u6ca1\u60f3\u5230\u9526\u6e56\u4f1a\u5411\u4e1c\u5927\u6295\u5165\u8fd9\u4e48\u5927\u7684\u8d44\u91d1\uff0c\u60f3\u60f3\u4e5f\u91ca\u7136\u4e86\u3002</p><p>\u3000\u3000\u4ed6\u77e5\u9053\u6a61\u6811\u56ed\u4eca\u5e74\u79d1\u7814\u7ecf\u8d39\u9884\u7b97\u5728\u516b\u4ebf\u7f8e\u5143\u5de6\u53f3\uff0c\u4e94\u5e74\u5185\u7684\u79d1\u7814\u7ecf\u8d39\u9884\u7b97\u66f4\u662f\u9ad8\u8fbe\u516d\u5341\u4ebf\u5230\u516b\u5341\u7f8e\u5143\u4e4b\u5de8\u3002\u8fd9\u662f\u4e00\u4e2a\u5413\u7834\u4eba\u773c\u7403\u7684\u6570\u5b57\uff0c\u4f46\u662f\u4e5f\u5fc5\u987b\u8fbe\u5230\u8fd9\u4e2a\u6807\u51c6\uff0c\u7231\u8fbe\u96c6\u56e2\u624d\u80fd\u5728\u4e94\u5e74\u540e\u771f\u6b63\u7684\u8de8\u5165\u56fd\u9645\u4e00\u6d41\u7684\u7535\u5b50\u4f01\u4e1a\u884c\u5217\u3002\u4e94\u5e74\u5185\uff0c\u5728\u6570\u5b57\u624b\u673a\u3001\u4fbf\u643a\u5f0f\u97f3\u4e50\u64ad\u653e\u5668\u7b49\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u4e1a\u4e0a\u7684\u5229\u6da6\u4e5f\u5c06\u652f\u6491\u9526\u6e56\u5728\u79d1\u7814\u4e0a\u8fdb\u884c\u5982\u6b64\u5927\u89c4\u6a21\u7684\u6295\u5165\u3002\u82e5\u662f\u5c06\u652f\u6301\u4e1c\u5927\u53d1\u5c55\u6210\u4e3a\u4e16\u754c\u7ea7\u4e00\u6d41\u540d\u6821\u4f5c\u4e3a\u9526\u6e56\u4e00\u9879\u957f\u671f\u65b9\u5411\u4e0a\u7684\u6218\u7565\u6765\u6293\uff0c\u4ece\u79d1\u7814\u7ecf\u8d39\u91cc\u62ff\u51fa10\uff05\u7684\u9884\u7b97\u51fa\u6765\uff0c\u662f\u4e2a\u5408\u9002\u7684\u6bd4\u4f8b\u3002</p><p>\u3000\u3000\u9664\u4e86\u6a61\u6811\u56ed\u53ca\u9526\u6e56\u5176\u4ed6\u6210\u5458\u4f01\u4e1a\u4ece\u4e1c\u5927\u76f4\u63a5\u5438\u5f15\u9ad8\u7d20\u8d28\u4eba\u624d\uff0c\u4e0e\u4e1c\u5927\u5404\u9662\u7cfb\u3001\u5b9e\u9a8c\u5ba4\u8fdb\u884c\u5e7f\u6cdb\u6280\u672f\u5408\u4f5c\u4e4b\u5916\uff0c\u6a61\u6811\u56ed\u521b\u4e1a\u57fa\u91d1\u4ee5\u53ca\u6a61\u6811\u56ed\u4e0e\u6606\u817e\u5728\u7ebf\u3001\u4e1c\u5927\u521b\u57df\u3001\u4e1c\u5927\u79d1\u6280\u603b\u516c\u53f8\u8054\u5408\u6210\u7acb\u4e86\u591a\u5bb6\u79d1\u6280\u578b\u521b\u4e1a\u6276\u6301\u57fa\u91d1\uff0c\u51e0\u4e4e\u90fd\u662f\u4ee5\u4e1c\u5927\u4e3a\u4eba\u624d\u3001\u6280\u672f\u4f9d\u6258\uff0c\u8fd9\u4e9b\u57fa\u91d1\u7684\u8d44\u4ea7\u89c4\u6a21\u52a0\u8d77\u6765\u5df2\u7ecf\u8d85\u8fc7\u4e94\u5341\u4ebf\u3002\u53ea\u8981\u4e1c\u5927\u5c06\u6765\u5bf9\u8fd9\u4e9b\u57fa\u91d1\u76f4\u63a5\u4fc3\u8fdb\u7684\u8d44\u4ea7\u589e\u6da8\u5e45\u5ea6\u8d85\u5230\u516d\u5341\u4e94\u4ebf\u5143\uff0c\u90a3\u9526\u6e56\u8fde\u7eed\u4e94\u5e74\u5411\u4e1c\u5927\u6295\u5165\u5de8\u989d\u8d44\u91d1\u5c31\u7b97\u662f\u6709\u4e86\u8db3\u989d\u7684\u56de\u62a5\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u9526\u6e56\u73b0\u5728\u6b63\u5728\u505a\u7684\u4e8b\u60c5\uff0c\u5c31\u662f\u8ba9\u4e1c\u5927\u4e5f\u76f4\u63a5\u53c2\u4e0e\u5230\u8fd9\u4e9b\u79d1\u6280\u578b\u521b\u4e1a\u6276\u6301\u57fa\u91d1\u4e2d\u6765\uff0c\u6a61\u6811\u56ed\u521b\u4e1a\u57fa\u91d1\u4e5f\u5411\u4e1c\u5927\u79d1\u6280\u603b\u516c\u53f8\u65d7\u4e0b\u7684\u521b\u4e1a\u4f01\u4e1a\u63d0\u4f9b\u878d\u8d44\u670d\u52a1\uff0c\u4e0d\u4ec5\u662f\u4e3a\u4e86\u8ba9\u53cc\u65b9\u7684\u5408\u4f5c\u5173\u7cfb\u66f4\u52a0\u7684\u5bc6\u5207\uff0c\u4e5f\u662f\u5e0c\u671b\u4e1c\u5927\u80fd\u76f4\u63a5\u4ece\u8fd9\u79cd\u5408\u4f5c\u6a21\u5f0f\u4e2d\u83b7\u5f97\u66f4\u591a\u66f4\u76f4\u63a5\u7684\u6536\u76ca\uff0c\u4e3a\u4e1c\u5927\u7684\u529e\u5b66\u7ecf\u8d39\u6765\u6e90\u83b7\u5f97\u66f4\u5e7f\u9614\u7684\u6e20\u9053\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9ad8\u6821\u5b66\u79d1\u5efa\u8bbe\u4fc3\u8fdb\u57fa\u91d1\u7684\u6210\u7acb\u9700\u8981\u89e3\u51b3\u4e00\u7cfb\u5217\u7684\u95ee\u9898\uff0c\u9526\u6e56\u8fd9\u8fb9\u662f\u5e0c\u671b\u674e\u9e3f\u94ed\u3001\u5d14\u56fd\u6052\u5171\u540c\u63a8\u8350\u59d4\u5458\u4f1a\u7684\u6210\u5458\u4eba\u9009\uff0c\u5f53\u7136\uff0c\u6a61\u6811\u56ed\u8fd9\u8fb9\u4e5f\u6709\u4e00\u6279\u79d1\u5b66\u5bb6\u4f1a\u8fdb\u5165\u59d4\u5458\u4f1a\u6210\u5458\u540d\u5355\uff0c\u57fa\u91d1\u7684\u7ba1\u7406\u7ec6\u5219\u7279\u522b\u662f\u6bcf\u5e74\u8d44\u91d1\u7684\u5212\u62e8\u6807\u51c6\u4e0e\u8bc4\u8bae\u7ec6\u5219\u4e5f\u8981\u7b49\u59d4\u5458\u4f1a\u6210\u7acb\u4e4b\u540e\u4e0e\u6a61\u6811\u56ed\u65b9\u9762\u5171\u540c\u5546\u8bae\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u56de\u5230\u5b66\u6821\uff0c\u674e\u9e3f\u94ed\u5c06\u5d14\u56fd\u6052\u63ea\u4f4f\u56de\u529e\u516c\u5ba4\u5546\u8bae\u4e8b\u60c5\uff0c\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u90fd\u4e0d\u4f1a\u53c2\u4e0e\u592a\u7ec6\u7684\u5de5\u4f5c\u4e4b\u4e2d\uff0c\u5d14\u56fd\u6052\u672c\u8eab\u5c31\u662f\u4e1c\u5927\u4e0e\u9526\u6e56\u4e4b\u95f4\u7684\u6865\u6881\uff0c\u674e\u9e3f\u94ed\u4e0d\u6293\u4f4f\u4ed6\u6293\u4f4f\u8c01\uff1f</p><p>\u3000\u3000\u79e6\u521a\u56de\u5230\u5b66\u6821\u53d6\u4e86\u8f66\uff0c\u4ed6\u513f\u5b50\u591c\u91cc\u7761\u89c9\u5c06\u88ab\u5b50\u8e6c\u6389\u5f97\u4e86\u611f\u5192\uff0c\u4ed6\u59bb\u5b50\u4e0b\u5348\u9001\u513f\u5b50\u53bb\u533b\u9662\u6302\u6c34\uff0c\u4ed6\u5148\u8d76\u53bb\u533b\u9662\u5c06\u59bb\u5b50\u8ddf\u513f\u5b50\u63a5\u56de\u5bb6\u6765\uff0c\u8ddf\u59bb\u5b50\u8bf4\u6700\u8fd1\u4e00\u6bb5\u65f6\u95f4\u53ef\u80fd\u53c8\u8981\u5fd9\u788c\u8d77\u6765\uff0c\u8981\u4ed6\u59bb\u5b50\u5c06\u5979\u5988\u63a5\u8fc7\u6765\u4e00\u8d77\u5e2e\u7740\u7167\u987e\u624d\u5440\u5440\u5b66\u8bed\u7684\u513f\u5b50\u3002</p><p>\u3000\u3000\u201c\u6211\u7238\u4e5f\u9000\u4f11\u4e86\uff0c\u6211\u5988\u8fc7\u6765\uff0c\u4f60\u8ba9\u6211\u7238\u4e00\u4eba\u5446\u5bb6\u91cc\u5e72\u4ec0\u4e48\uff1f\u201d\u79e6\u521a\u59bb\u5b50\u8bf4\u9053\u3002</p><p>\u3000\u3000\u79e6\u521a\u8ddf\u4ed6\u59bb\u5b50\u8fd8\u4f4f\u5728\u9752\u5e74\u6559\u5e08\u516c\u5bd3\u91cc\uff0c\u4e8c\u5ba4\u4e8c\u5385\u7684\u7cbe\u81f4\u6237\u578b\uff0c\u4e5d\u5341\u5e73\u65b9\uff0c\u4e00\u4e2a\u8001\u4eba\u8fc7\u6765\uff0c\u8fd8\u80fd\u8ddf\u5c0f\u5b69\u5b50\u6324\u4e00\u6324\uff0c\u4e24\u4e2a\u8001\u4eba\u4e00\u8d77\u8fc7\u6765\uff0c\u5c31\u7f3a\u4e86\u4e2a\u623f\u95f4\u3002</p><p>\u3000\u3000\u79e6\u521a\u77e5\u9053\u4ed6\u7231\u4eba\u60f3\u63d0\u6362\u623f\u5b50\u7684\u4e8b\u60c5\uff0c\u4ed6\u60f3\u7740\u4e0d\u540c\u62c5\u4efb\u7f51\u6821\u603b\u7ecf\u7406\uff0c\u8ddf\u4e1c\u5927\u7684\u5173\u7cfb\u53c8\u758f\u8fdc\u4e86\u4e00\u5c42\uff0c\u518d\u5360\u7740\u623f\u5b50\uff0c\u4e1c\u5927\u90a3\u4e9b\u8fd8\u6ca1\u6709\u623f\u5b50\u5206\u7684\u9752\u5e74\u6559\u5e08\u4e5f\u4f1a\u6709\u610f\u89c1\uff0c\u53ea\u662f\u8fd1\u671f\u5b9e\u5728\u6ca1\u6709\u7cbe\u529b\u53bb\u6362\u623f\u5b50\uff0c\u8ddf\u4ed6\u59bb\u5b50\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u662f\u4f60\u5c06\u54b1\u5988\u5148\u63a5\u8fc7\u6765\uff0c\u4f60\u4eec\u770b\u7740\u54ea\u91cc\u623f\u5b50\u5408\u9002\uff0c\u5148\u642c\u8fdb\u53bb\uff0c\u518d\u5c06\u54b1\u7238\u63a5\u8fc7\u6765\uff1f\u6211\u8981\u6362\u5de5\u4f5c\u4e86\uff0c\u8fc7\u6bb5\u65f6\u95f4\u5c31\u4e0d\u8d1f\u8d23\u7f51\u6821\u8fd9\u8fb9\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u8c03\u4f60\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u6682\u65f6\u8fd8\u6ca1\u6709\u5b9a\u4e0b\u6765\uff0c\u53ef\u80fd\u5148\u8ddf\u7740\u6253\u6742\uff0c\u201d\u79e6\u521a\u542b\u6df7\u7684\u8bf4\u4e86\u5927\u6982\uff0c\u201c\u4e0d\u518d\u8d1f\u8d23\u7f51\u6821\uff0c\u6211\u8ddf\u4e1c\u5927\u5c31\u6ca1\u6709\u76f4\u63a5\u5173\u7cfb\u4e86\uff0c\u8fd9\u8fb9\u7684\u623f\u5b50\u4e5f\u8981\u8fd8\u7ed9\u5b66\u6821\uff0c\u4e5f\u7684\u786e\u9700\u8981\u53e6\u627e\u623f\u5b50\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u4f60\u7ed9\u4e1c\u5927\u5de5\u4f5c\u4e86\u8fd9\u4e48\u591a\u5e74\uff0c\u8fd9\u600e\u4e48\u7b97\uff1f\u201d\u79e6\u521a\u59bb\u5b50\u6709\u4e9b\u4e0d\u820d\u5f97\u5c06\u623f\u5b50\u8fd8\u7ed9\u5b66\u6821\u3002</p><p>\u3000\u3000\u79e6\u521a\u7b11\u7740\u8bf4\uff1a\u201c\u5c31\u4e0d\u6015\u7ed9\u522b\u4eba\u6307\u7740\u810a\u6881\u9a82\uff1f\u8d2a\u8fd9\u4e2a\u503c\u5f97\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u71d5\u56ed\u6ca1\u6307\u671b\u4e0a\uff0c\u6e56\u4e1c\u516c\u5bd3\u53c8\u6324\u4e0d\u8fdb\u53bb\uff0c\u4f60\u8ba9\u6211\u53bb\u54ea\u91cc\u6311\u623f\u5b50\uff1f\u201d\u79e6\u521a\u59bb\u5b50\u95ee\u9053\u3002</p><p>\u3000\u3000\u57ce\u9547\u804c\u5de5\u4f4f\u623f\u798f\u5229\u6539\u9769\u4ee5\u6765\uff0c\u5efa\u90ba\u7684\u623f\u4ef7\u4e0a\u6da8\u5f88\u5feb\uff0c\u71d5\u5f52\u56ed\u533a\u57df\u7684\u623f\u4ef7\u4e0a\u6da8\u5230\u6bcf\u5e73\u65b9\u7c73\u4e24\u5343\u4e94\u767e\u5143\uff0c\u4f46\u662f\u76f8\u5bf9\u79e6\u521a\u56db\u5341\u4e07\u7684\u5e74\u85aa\uff0c\u8d2d\u623f\u4e1d\u6beb\u6ca1\u6709\u538b\u529b\uff0c\u518d\u8bf4\u4ed6\u5728\u4e1c\u5927\u521b\u57df\u8fd8\u6301\u6709\u4e00\u4e9b\u80a1\u4efd\u3002\u73b0\u5728\u5012\u4e0d\u662f\u62c5\u5fc3\u4e70\u4e0d\u8d77\u623f\u5b50\uff0c\u53ea\u662f\u6101\u4e70\u4e0d\u5230\u5408\u9002\u7684\u623f\u5b50\u3002</p><p>\u3000\u3000\u4e16\u7eaa\u9526\u6e56\u5728\u5efa\u90ba\u53ea\u5f00\u53d1\u4e00\u5904\u9ad8\u6863\u522b\u5885\u533a\u71d5\u56ed\uff0c\u90a3\u91cc\u7684\u623f\u5b50\u65e9\u5c31\u5206\u5b8c\u4e86\uff0c\u800c\u4e14\u7edd\u5927\u591a\u6570\u662f\u5206\u7ed9\u6a61\u6811\u56ed\u4e2d\u9ad8\u7ea7\u7814\u7a76\u5458\u7684\uff0c\u4e1c\u5927\u521b\u57df\u8fd9\u8fb9\u9664\u4e86\u675c\u98de\u62ff\u4e86\u4e00\u5957\u522b\u5885\uff0c\u5c31\u6ca1\u6709\u8c01\u518d\u6709\u80fd\u529b\u4ece\u90a3\u91cc\u62ff\u4e00\u5957\u522b\u5885\u51fa\u6765\u3002</p><p>\u3000\u3000\u6e56\u4e1c\u516c\u5bd3\u5f00\u53d1\u5341\u4e8c\u680b\u4e09\u5341\u5c42\u7684\u9ad8\u5c42\u516c\u5bd3\uff0c\u5171\u8ba1\u4e09\u5343\u591a\u5957\u516c\u5bd3\uff0c\u5f00\u59cb\u505a\u89c4\u5212\u65f6\u4ee5\u4e3a\u5f88\u5bbd\u88d5\u4e86\uff0c\u8c01\u80fd\u60f3\u5230\u4eca\u5e74\u590f\u5929\u4e4b\u540e\u6a61\u6811\u56ed\u6b63\u5f0f\u7814\u7a76\u5458\u5c31\u5c06\u8d85\u8fc7\u516d\u5343\u4eba\uff1f\u52a0\u4e0a\u7231\u8fbe\u96c6\u56e2\u5176\u4ed6\u5728\u5efa\u90ba\u7684\u5176\u4ed6\u5206\u652f\u673a\u6784\u7684\u5de5\u4f5c\u4eba\u5458\uff0c\u5dee\u4e0d\u591a\u5c31\u63a5\u8fd1\u4e07\u4eba\u89c4\u6a21\uff0c\u8fd9\u8fd8\u4e0d\u8ba1\u7b97\u76db\u946b\u7535\u5668\u3001\u76db\u946b\u7535\u8baf\u3001\u65b0\u5149\u7eb8\u4e1a\u3001\u4e2d\u6676\u5fae\u82af\u3001\u4e1c\u5927\u521b\u57df\u7b49\u9526\u6e56\u6210\u5458\u4f01\u4e1a\u5728\u5efa\u90ba\u7684\u5de5\u4f5c\u4eba\u5458\uff0c\u4e16\u7eaa\u9526\u6e56\u5728\u5efa\u90ba\u5f00\u53d1\u7684\u4f4f\u5b85\u533a\u90fd\u5df2\u7ecf\u4e0d\u80fd\u6ee1\u8db3\u9526\u6e56\u5185\u90e8\u5458\u5de5\u7684\u4f4f\u623f\u9700\u6c42\u4e86\u3002\u9664\u4e86\u7ee7\u7eed\u5b8c\u6210\u6e56\u4e1c\u5546\u5708\u9879\u76ee\u4e4b\u5916\uff0c\u53c8\u5728\u71d5\u5f52\u6e56\u4e1c\u9e93\uff0c\u6e56\u4e1c\u5546\u5708\u7684\u4e1c\u5357\u89d2\u4e0a\uff0c\u4e16\u7eaa\u9526\u6e56\u62ff\u4e0b\u4e00\u7247\u5730\uff0c\u5728\u90a3\u91cc\u5efa\u9020\u4e00\u5ea7\u767e\u4e07\u5e73\u7c73\u7684\u5927\u578b\u4f4f\u5b85\u793e\u533a\u3002\u53ea\u662f\u8fd9\u4e2a\u9879\u76ee\u624d\u5f00\u59cb\u542f\u52a8\uff0c\u80fd\u591f\u4f4f\u4eba\u5c11\u8bf4\u4e5f\u8981\u4e00\u5e74\u4e4b\u540e\u3002</p><p>\u3000\u3000\u9664\u4e86\u9752\u5e74\u516c\u5bd3\u3001\u6e56\u4e1c\u516c\u5bd3\u4ee5\u53ca\u71d5\u56ed\u5916\uff0c\u79e6\u521a\u5728\u5efa\u90ba\u8fd8\u6ca1\u6709\u770b\u5230\u6709\u591a\u5408\u9002\u7684\u65b0\u697c\u76d8\u53ef\u4f9b\u9009\u62e9\uff0c\u53ef\u60dc\u9752\u5e74\u516c\u5bd3\u3001\u6e56\u4e1c\u516c\u5bd3\u4ee5\u53ca\u71d5\u56ed\u8fd9\u65f6\u5019\u90fd\u6ca1\u6709\u4ed6\u7684\u4efd\u4e86\u3002</p><p>\u3000\u3000\u79e6\u521a\u6320\u4e86\u6320\u5934\uff0c\u8ddf\u59bb\u5b50\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u6211\u8ddf\u9646\u5065\u6253\u7535\u8bdd\u95ee\u4e00\u4e0b\uff0c\u770b\u770b\u4ed6\u4eec\u516c\u53f8\u6709\u6ca1\u6709\u6700\u8fd1\u6709\u6ca1\u6709\u623f\u5b50\u653e\u51fa\u6765\u3002\u201d</p><p>\u3000\u3000\u9646\u5065\u5f03\u5b98\u4ece\u5546\u540e\u4ee3\u8868\u5609\u91cc\u5efa\u8bbe\u8fdb\u9a7b\u5efa\u90ba\u5df2\u6709\u4e24\u5e74\u7684\u65f6\u95f4\u4e86\uff0c\u5728\u7b2c\u4e00\u9879\u76ee\u987a\u5229\u64cd\u4f5c\u5b8c\u7ed3\u4e4b\u540e\uff0c\u53c8\u540c\u65f6\u542f\u52a8\u4e00\u4e2a\u4f4f\u5b85\u9879\u76ee\u8ddf\u4e00\u4e2a\u5199\u5b57\u697c\u9879\u76ee\uff0c\u89c4\u6a21\u5df2\u7ecf\u8d85\u8fc7\u540c\u671f\u5728\u5317\u4eac\u8fd0\u4f5c\u7684\u9879\u76ee\u3002</p><p>\u3000\u3000\u4e0d\u8bf4\u5176\u4ed6\u7684\uff0c\u6a61\u6811\u56ed\u521b\u4e1a\u57fa\u91d1\u652f\u6301\u7684\u521b\u4e1a\u4f01\u4e1a\u90fd\u591a\u8fbe\u516b\u767e\u4f59\u5bb6\uff0c\u53e6\u5916\u8fd8\u6709\u5b75\u5316\u6210\u529f\u642c\u51fa\u56ed\u533a\u7684\u516d\u5341\u591a\u5bb6\u79d1\u6280\u516c\u53f8\uff0c\u6700\u521d\u7684\u4e24\u5ea7\u521b\u4e1a\u56ed\u533a\u5df2\u7ecf\u8fdc\u8fdc\u4e0d\u80fd\u6ee1\u8db3\u6240\u9700\uff0c\u6e56\u4e1c\u5546\u5708\u9879\u76ee\u5185\u7684\u5199\u5b57\u697c\u8d44\u6e90\u81ea\u7136\u8981\u5148\u6ee1\u8db3\u521b\u4e1a\u4f01\u4e1a\u4ee5\u53ca\u9526\u6e56\u5185\u90e8\u6210\u5458\u4f01\u4e1a\u6240\u9700\uff0c\u8fd9\u4e5f\u53ea\u662f\u52c9\u5f3a\u591f\u7528\u3002\u8fd9\u51e0\u5e74\u6765\uff0c\u5efa\u90ba\u6570\u5b57\u957f\u5eca\u6784\u60f3\u6218\u7565\u53d6\u5f97\u5de8\u5927\u7684\u6210\u5c31\uff0c\u65e0\u6570\u5bb6\u79d1\u6280\u4f01\u4e1a\u6d8c\u5165\u5efa\u90ba\u9ad8\u65b0\u533a\uff0c\u5bf9\u5199\u5b57\u697c\u7684\u9700\u6c42\u81ea\u7136\u4e5f\u662f\u6fc0\u589e\uff0c\u5728\u71d5\u5f52\u6e56\u533a\u57df\uff0c\u9664\u4e86\u4e16\u7eaa\u9526\u6e56\u4e4b\u5916\uff0c\u50cf\u5609\u91cc\u5efa\u8bbe\u3001\u7cbe\u5178\u5730\u4ea7\u8fd9\u4e9b\u5730\u4ea7\u516c\u53f8\u8fd8\u627f\u62c5\u4e86\u8fd1\u4e09\u5206\u4e4b\u4e8c\u89c4\u6a21\u7684\u5199\u5b57\u697c\u7684\u6295\u8d44\u5efa\u9020\uff0c\u5728\u4f4f\u5b85\u5f00\u53d1\u4e0a\uff0c\u5dee\u4e0d\u591a\u4e5f\u5360\u5230\u540c\u6837\u7684\u6bd4\u4f8b\u3002</p><p>\u3000\u3000\u53ea\u4e0d\u8fc7\u522b\u5885\u8d44\u6e90\u4e00\u5411\u90fd\u662f\u7d27\u7f3a\u7684\uff0c\u4e0d\u65e9\u65e9\u8ba1\u5212\uff0c\u4e34\u65f6\u60f3\u8981\u5728\u71d5\u5f52\u6e56\u533a\u57df\u4e70\u680b\u522b\u5885\u662f\u79cd\u5962\u4f88\uff0c\u4f46\u662f\u666e\u901a\u4f4f\u5b85\u5f00\u53d1\uff0c\u5176\u4ed6\u5730\u4ea7\u90fd\u7f3a\u4e4f\u4e16\u7eaa\u9526\u6e56\u90a3\u79cd\u7cbe\u76ca\u6c42\u7cbe\u7684\u4e13\u4e1a\u7cbe\u795e\u3002</p><p>\u3000\u3000\u79e6\u521a\u59bb\u5b50\u76b1\u7740\u7709\u5934\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u518d\u7b49\u7b49\uff1f\u9e93\u56ed\u90a3\u8fb9\u518d\u7b49\u4e00\u5e74\u603b\u8be5\u6709\u6307\u671b\u2026\u2026\u201d</p><p>\u3000\u3000\u9e93\u56ed\u4fbf\u662f\u4e16\u7eaa\u9526\u6e56\u5728\u71d5\u5f52\u6e56\u4e1c\u9e93\u7684\u65b0\u9879\u76ee\u3002</p><p>\u3000\u3000\u79e6\u521a\u5fc3\u60f3\u4e5f\u8bb8\u53ea\u80fd\u518d\u7b49\u7b49\uff0c\u4ed6\u53c8\u60f3\u5230\u674e\u9e3f\u94ed\u4e5f\u4e00\u76f4\u60f3\u5f7b\u5e95\u6539\u5584\u6559\u804c\u5de5\u7684\u5c45\u4f4f\u6761\u4ef6\uff0c\u4e4b\u524d\u9650\u4e8e\u8d44\u91d1\uff0c\u4e00\u76f4\u6ca1\u6709\u5927\u7684\u52a8\u4f5c\uff0c\u9526\u6e56\u627f\u8bfa\u4e94\u5e74\u6295\u5165\u516b\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\uff0c\u80af\u5b9a\u4f1a\u6709\u4e00\u90e8\u5206\u8d44\u91d1\u8981\u6d41\u5411\u8fd9\u65b9\u9762\u3002\u5f20\u606a\u5411\u6765\u7684\u89c2\u70b9\uff0c\u8981\u5438\u5f15\u4eba\u624d\uff0c\u73b0\u5728\u5df2\u7ecf\u4e0d\u662f\u8c08\u65e0\u79c1\u4e30\u732e\u7684\u65f6\u4ee3\u4e86\uff0c\u6700\u4f18\u79c0\u7684\u4eba\u624d\u5c31\u8981\u7ed9\u4e88\u6700\u4f18\u79c0\u7684\u5f85\u9047\uff0c\u5438\u5f15\u4e0d\u6765\u4e00\u6d41\u7684\u5b66\u672f\u4eba\u624d\uff0c\u8c08\u5efa\u8bbe\u4e16\u754c\u7ea7\u4e00\u6d41\u540d\u6821\u5c31\u662f\u5962\u60f3\u3002\u5f88\u591a\u4eba\u90fd\u620f\u79f0\u7231\u8fbe\u96c6\u56e2\u8fd9\u51e0\u5e74\u6765\u5bf9\u4e1c\u5927\u7684\u6350\u6b3e\u76f8\u5f53\u4e00\u90e8\u5206\u662f\u76f4\u63a5\u6350\u5230\u5b66\u8005\u3001\u6559\u6388\u7684\u53e3\u888b\u91cc\u3002</p><p>\u3000\u3000\u79e6\u521a\u5728\u4e66\u623f\u67e5\u4e86\u4e00\u4e9b\u5173\u4e8e\u97f3\u4e50\u5b66\u9662\u7684\u8d44\u6599\uff0c\u76f8\u6bd4\u8f83\u8ba1\u5212\u4e94\u5e74\u5185\u5411\u4e1c\u5927\u6295\u5165\u7684\u516b\u4ebf\u7f8e\u5143\u8d44\u91d1\uff0c\u7ed9\u97f3\u4e50\u5b66\u9662\u7684\u4e09\u5343\u4e07\u6350\u6b3e\u5c31\u6709\u4e9b\u5fae\u4e0d\u8db3\u9053\u4e86\uff0c\u5f53\u7136\uff0c\u97f3\u4e50\u5b66\u9662\u7684\u9886\u5bfc\u4e0d\u4f1a\u8fd9\u4e48\u60f3\u3002</p><p>\u3000\u3000\u79e6\u521a\u5728\u5bb6\u91cc\u5403\u8fc7\u665a\u996d\uff0c\u770b\u7740\u513f\u5b50\u7684\u70e7\u9000\u4e86\u70ed\uff0c\u675c\u98de\u7684\u7535\u8bdd\u53c8\u6253\u4e86\u8fdb\u6765\uff0c\u5c31\u8d76\u52301978\u89c1\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u5f20\u606a\u3001\u675c\u98de\u3001\u8499\u4e50\u5728\u89d2\u843d\u91cc\u6253\u65af\u8bfa\u514b\uff0c\u4e00\u8fb9\u6253\u7403\u4e00\u8fb9\u8c08\u8bba\u56fd\u5185\u9ad8\u6821\u5230\u5e95\u54ea\u4e9b\u65b9\u9762\u5e94\u8be5\u6700\u5148\u6539\u9769\uff0c\u5b59\u9759\u6aac\u53cc\u624b\u73af\u62e5\u6709\u80f8\u524d\u770b\u7740\u4ed6\u4eec\u6253\u7403\uff0c\u6253\u65af\u8bfa\u514b\u4ed6\u4eec\u4e09\u4e2a\u4eba\u52a0\u8d77\u6765\u4e5f\u672a\u5fc5\u662f\u5b59\u9759\u6aac\u7684\u5bf9\u624b\u3002</p><p>\u3000\u3000\u8499\u4e50\u6b63\u8bf4\u56fd\u5185\u7684\u535a\u58eb\u751f\u53ef\u601c\uff0c\u6bcf\u6708\u624d\u62ff\u90a3\u4e9b\u70b9\u751f\u6d3b\u8865\u52a9\uff0c\u5f88\u591a\u535a\u58eb\u751f\u5728\u5165\u5b66\u524d\u90fd\u5df2\u7ecf\u62d6\u5bb6\u5e26\u53e3\uff0c\u751f\u6d3b\u5f88\u662f\u6e05\u8d2b\u3002</p><p>\u3000\u3000\u79e6\u521a\u5012\u662f\u60f3\u8d77\u81ea\u5df1\u8bfb\u535a\u58eb\u65f6\u7684\u6e05\u5bd2\u6765\uff0c\u4ed6\u90a3\u65f6\u6bcf\u6708\u624d\u4e00\u767e\u516b\u5341\u5143\u7684\u751f\u6d3b\u8865\u52a9\uff0c\u5e78\u4e8f\u5728\u5b66\u6821\u91cc\u517c\u804c\u505a\u8f85\u5bfc\u5458\u3001\u52a9\u6559\uff0c\u6bcf\u6708\u80fd\u62ff\u4e09\u767e\u591a\u7684\u5c97\u4f4d\u6d25\u8d34\uff0c\u624d\u52c9\u5f3a\u591f\u7528\uff0c\u4e0d\u8fc7\u5360\u4e86\u4ed6\u5f88\u5927\u7684\u7cbe\u529b\u3002\u535a\u58eb\u751f\u751f\u6d3b\u8865\u52a9\u6807\u51c6\u53bb\u5e74\u8c03\u6574\u4e86\u4e00\u6b21\uff0c\u6bcf\u6708\u4e5f\u53ea\u6709\u4e8c\u767e\u56db\u5341\u5143\uff0c\u76f8\u6bd4\u8f83\u5efa\u90ba\u7684\u7269\u4ef7\u4e0a\u6da8\u4ee5\u53ca\u4eba\u5747\u5de5\u8d44\u589e\u6da8\uff0c\u535a\u58eb\u751f\u7684\u751f\u6d3b\u6c34\u51c6\u53c8\u964d\u4f4e\u4e86\u4e0d\u5c11\u3002\u5373\u4f7f\u8bf4\u53ef\u4ee5\u53bb\u4ece\u4e8b\u517c\u804c\uff0c\u5374\u662f\u5360\u636e\u592a\u591a\u7684\u65f6\u95f4\u4e0e\u7cbe\u529b\uff0c\u4e0e\u5c06\u535a\u58eb\u751f\u57f9\u517b\u6210\u9ad8\u7aef\u7814\u7a76\u578b\u4eba\u624d\u7684\u76ee\u6807\u6709\u5f88\u5927\u7684\u62b5\u89e6\u3002\u8fd9\u5927\u6982\u4e5f\u662f\u56fd\u5185\u9ad8\u6821\u867d\u7136\u80fd\u57f9\u517b\u51fa\u4e00\u6d41\u7684\u5b66\u58eb\uff0c\u5374\u57f9\u517b\u4e0d\u51fa\u4e00\u6d41\u535a\u58eb\u7684\u91cd\u8981\u56e0\u7d20\u5427\uff1f</p><p>\u3000\u3000\u5f20\u606a\u62ff\u7740\u7403\u6746\uff0c\u9760\u58c1\u7ad9\u7740\uff0c\u8bf4\u9053\uff1a\u201c\u5b66\u79d1\u5efa\u8bbe\u4fc3\u8fdb\u57fa\u91d1\u5ba1\u6279\u901a\u8fc7\u7684\u6bcf\u4e00\u7b14\u79d1\u7814\u7ecf\u8d39\u9884\u7b97\u4e2d\u90fd\u4f1a\u5305\u542b\u79d1\u7814\u4eba\u5458\u85aa\u8d44\u7684\u9884\u7b97\uff0c\u751a\u81f3\u53ef\u4ee5\u5355\u72ec\u5212\u51fa\u4e00\u90e8\u5206\u8d44\u91d1\u51fa\u6765\u7ed9\u7814\u7a76\u751f\u9662\u505a\u535a\u58eb\u751f\u5956\u5b66\u91d1\u4f7f\u7528\uff0c\u81f3\u5c11\u4f7f\u535a\u58eb\u751f\u7684\u5b9e\u9645\u751f\u6d3b\u8865\u52a9\u6807\u51c6\u63d0\u9ad8\u5230\u5efa\u90ba\u5e73\u5747\u5de5\u8d44\u6c34\u5e73\u4ee5\u4e0a\u2026\u2026\u4e8c\u5341\u4e00\u4e16\u7eaa\u4ec0\u4e48\u6700\u8d35\uff0c\u662f\u4eba\u624d\uff01\u54b1\u4e5f\u4e0d\u80fd\u59d4\u5c48\u4e86\u4eba\u624d\u3002\u201d</p></article>"}}}]);
