(window.webpackJsonp=window.webpackJsonp||[]).push([[200],{223:function(p){p.exports={chapterTitle:null,title:"\u7b2c200\u7ae0 \u518d\u884c\u60e0\u5c71",content:"<article><h2>\u7b2c200\u7ae0 \u518d\u884c\u60e0\u5c71</h2><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\uff0c\u666e\u901a\u5e02\u6c11\u5bf9\u5e7f\u544a\u8fd8\u6ca1\u6709\u4ec0\u4e48\u514d\u75ab\u529b\uff0c\u5f53\u65f6\u751a\u81f3\u6709\u201c\u5e7f\u544a\u529b\u5ea6\u6709\u591a\u5927\uff0c\u5e02\u573a\u5c31\u6709\u591a\u5927\u201d\u7684\u8bf4\u6cd5\uff0c\u53ea\u662f\u5229\u6da6\u7387\u4e0e\u5e02\u573a\u8fd0\u8425\u6210\u672c\u7684\u63a7\u5236\u6210\u4e3a\u5f53\u65f6\u4f01\u4e1a\u6700\u68d8\u624b\u7684\u95ee\u9898\uff0c\u5f88\u591a\u4f01\u4e1a\u7684\u9500\u552e\u989d\u53ef\u4ee5\u505a\u5230\u51e0\u5341\u4e2a\u4ebf\uff0c\u4f46\u662f\u5229\u6da6\u5374\u88ab\u65e0\u5e8f\u7684\u5e02\u573a\u8fd0\u8425\u6240\u541e\u566c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e76\u975e\u65e0\u7f18\u65e0\u6545\u8003\u8651\u8fd9\u4e2a\u95ee\u9898\uff0c\u4ed6\u8ba9\u5085\u4fca\u5f00\u8f66\u9001\u4ed6\u53bb\u76db\u946b\u516c\u53f8\uff0c\u611f\u89c9\u5230\u6709\u4e9b\u53e3\u6e34\uff0c\u5728\u697c\u4e0b\u7684\u4fbf\u5229\u5e97\u4e70\u4e00\u74f6\u65ed\u65e5\u5347\u51b0\u8336\uff0c\u60f3\u8d77\u8fd9\u5bb6\u4e24\u5e74\u540e\u540d\u58f0\u5927\u9707\u3001\u9500\u552e\u989d\u77ed\u77ed\u4e24\u4e09\u5e74\u65f6\u95f4\u5185\u505a\u5230\u4e09\u5341\u4ebf\u3001\u4e61\u9547\u6742\u8d27\u94fa\u7684\u94fa\u5934\u90fd\u94fa\u6ee1\u4ed6\u4eec\u7684\u5e7f\u544a\u7684\u4f01\u4e1a\u3002\u5c31\u662f\u8fd9\u5bb6\u4f01\u4e1a\uff0c\u6700\u5174\u76db\u65f6\uff0c\u5728\u5168\u56fd\u5404\u5730\u5171\u6210\u7acb\u56db\u5341\u516b\u5bb6\u9500\u552e\u516c\u53f8\uff0c8600\u591a\u540d\u9500\u552e\u4eba\u5458\u90fd\u7531\u603b\u516c\u53f8\u6d3e\u51fa\uff0c\u80a5\u4e86\u4e00\u5927\u6279\u4e2d\u9971\u79c1\u56ca\u7684\u4eba\uff0c\u8fd9\u5bb6\u4f01\u4e1a\u5230\u6700\u540e\u9664\u4e86\u5341\u6570\u4ebf\u7684\u4e8f\u635f\u4e0e\u6b20\u503a\uff0c\u4ece\u5934\u5230\u5c3e\u5c31\u6ca1\u6709\u4ea7\u751f\u8fc7\u4e00\u5206\u94b1\u7684\u5229\u6da6\u3002</p><p>\u3000\u3000\u5f20\u606a\u62e7\u5f00\u74f6\u76d6\u5c0f\u996e\u4e86\u4e00\u53e3\uff0c\u5c31\u62ff\u7740\u996e\u6599\u74f6\u8fdb\u4e86\u76db\u946b\u516c\u53f8\uff0c\u76f4\u63a5\u8fdb\u8d70\u76db\u946b\u516c\u53f8\u4e13\u95e8\u7ed9\u7231\u8fbe\u5e02\u573a\u63a8\u5e7f\u56e2\u961f\u51c6\u5907\u7684\u529e\u516c\u5ba4\uff0c\u5c06\u996e\u6599\u74f6\u6446\u5230\u684c\u5b50\uff0c\u63a5\u8fc7\u53f6\u5c0f\u6850\u9012\u8fc7\u6765\u7684\u62a5\u544a\uff0c\u8fd9\u662f\u6628\u5929\u5404\u5730\u533a\u7ecf\u9500\u5546\u9012\u4ea4\u4e0a\u6765\u7684\u9500\u552e\u6570\u636e\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u5b66\u5f20\u606a\u5728\u9526\u6e56\u7684\u505a\u6cd5\uff0c\u7ed9\u5458\u5de5\u51c6\u5907\u6709\u5496\u5561\u4e4b\u7c7b\u7684\u996e\u6599\uff0c\u53f6\u5c0f\u6850\u770b\u7740\u5f20\u606a\u6446\u5728\u684c\u4e0a\u7684\u5851\u6599\u74f6\uff0c\u4e5d\u4e94\u5e74\u4e2d\uff0c\u65ed\u65e5\u5347\u8fd8\u4e0d\u5e7f\u4e3a\u4eba\u77e5\uff0c\u95ee\u4ed6\uff1a\u201c\u600e\u4e48\u60f3\u8d77\u8981\u559d\u8fd9\u4e2a\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff1f\u201d\u5f20\u606a\u62ff\u8d77\u65ed\u65e5\u5347\u51b0\u8336\uff0c\u6487\u4e86\u6487\u5634\uff0c\u8bf4\u9053\uff0c\u201c\u4f20\u7edf\u7eff\u8336\u4e0e\u78b3\u9178\u996e\u6599\u7684\u7ed3\u5408\u4f53\uff0c\u4f20\u7edf\u74f6\u88c5\u996e\u6599\u4e4b\u5916\u7684\u65b0\u54c1\u79cd\uff0c\u8fd9\u51b0\u8336\u6216\u8bb8\u4f1a\u5927\u70ed\u8d77\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u559d\u5b83\u5c31\u56e0\u4e3a\u8fd9\u4e2a\uff1f\u201d\u5468\u6e38\u4ece\u5f20\u606a\u624b\u91cc\u5c06\u51b0\u8336\u62ff\u8fc7\u6765\uff0c\u4ed4\u7ec6\u770b\u4e86\u770b\u6807\u7b7e\uff0c\u6253\u5f00\u74f6\u76d6\u559d\u4e86\u4e00\u53e3\uff0c\u201c\u55ef\uff0c\u611f\u89c9\u4e0d\u9519\u3002\u201d</p><p>\u3000\u3000\u9526\u6e56\u5728\u77ed\u77ed\u56db\u4e94\u4e2a\u6708\u5185\u53c8\u627f\u5305\u4e86\u4e94\u5bb6\u9020\u7eb8\u5382\uff0c\u65e0\u8bba\u662f\u4ece\u8d44\u91d1\u4e0a\u3001\u6280\u672f\u89c4\u6a21\u4e0a\u3001\u8fd8\u662f\u4ece\u73b0\u6709\u7684\u7ba1\u7406\u6a21\u5f0f\u4e0a\uff0c\u90fd\u5904\u4e8e\u4e00\u4e2a\u74f6\u9888\u671f\uff0c\u975e\u8981\u597d\u597d\u6d88\u5316\u4e00\u756a\u624d\u80fd\u7ee7\u7eed\u4e0b\u4e00\u6b65\u7684\u6269\u5f20\u3002\u6240\u751f\u4ea7\u7684\u6587\u5316\u7528\u7eb8\uff0c\u7701\u5185\u5e02\u573a\u5c31\u6d88\u5316\u4e86\u5927\u534a\uff0c\u5468\u6e38\u4e3a\u9526\u6e56\u5efa\u7acb\u8d77\u7ecf\u9500\u5546\u7f51\u7edc\u4e4b\u540e\uff0c\u5728\u5e02\u573a\u9500\u552e\u65b9\u9762\u5e76\u6ca1\u6709\u592a\u5927\u7684\u538b\u529b\u3002\u5f20\u606a\u4e4b\u524d\u5c31\u63d0\u51fa\u8981\u628a\u5468\u6e38\u501f\u7528\u8fc7\u6765\uff0c\u7231\u8fbe\u7684\u5e02\u573a\u8fd0\u4f5c\u5df2\u7136\u542f\u52a8\uff0c\u8c22\u665a\u6674\u56de\u5230\u6d77\u5dde\u4e4b\u540e\uff0c\u5c31\u7acb\u5373\u5c06\u5468\u6e38\u6d3e\u5230\u7701\u57ce\u6765\u534f\u52a9\u5f20\u606a\uff0c\u53ea\u662f\u5979\u80a9\u4e0a\u7684\u62c5\u5b50\u53c8\u91cd\u4e86\u8bb8\u591a\u3002</p><p>\u3000\u3000\u201c\u4eca\u5929\u53ef\u4e0d\u662f\u6765\u8ba8\u8bba\u51b0\u8336\u70ed\u8336\u7684\u3002\u65e2\u7136\u7b54\u5e94\u4e03\u6708\u4e0b\u65ec\u53bb\u4e0a\u6d77\u505a\u5e02\u573a\uff0c\u4e3a\u4ec0\u4e48\u8fc7\u4e86\u4e00\u4e2a\u661f\u671f\u8fd8\u7559\u5728\u7701\u57ce\uff0c\u7701\u5185\u5176\u4ed6\u57ce\u5e02\u8fd8\u8981\u4e0d\u8981\u505a\u4e86\uff1f\u201d\u53f6\u5c0f\u6850\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u76db\u946b\u8d1f\u8d23\u7231\u8fbe\u5f71\u789f\u673a\u5e02\u573a\u63a8\u5e7f\u7684\u56e2\u961f\u7531\u53f6\u5c0f\u6850\u8d1f\u8d23\uff0c\u5979\u8fd9\u6b21\u662f\u4e3b\u52a8\u8bf7\u7f28\uff0c\u4e0d\u518d\u62c5\u4efb\u53f6\u5efa\u658c\u7684\u52a9\u7406\uff1b\u53f6\u5efa\u658c\u81ea\u89c9\u7ed9\u81ea\u5df1\u627e\u4e86\u4e2a\u4e94\u5927\u4e09\u7c97\u7684\u7537\u79d8\u4e66\u3002</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u53ef\u662f\u618b\u7740\u4e00\u53e3\u6c14\u60f3\u4ece\u5f20\u606a\u8eab\u4e0a\u770b\u770b\u5546\u4e1a\u5929\u624d\u4e0e\u666e\u901a\u4eba\u5230\u5e95\u6709\u4ec0\u4e48\u4e0d\u540c\uff0c\u539f\u8ba1\u5212\u662f\u53d1\u5e03\u4f1a\u4e4b\u540e\uff0c\u8fc5\u901f\u5bf9\u7701\u5185\u7684\u5e02\u573a\u8fdb\u884c\u5e7f\u544a\u8986\u76d6\uff0c\u8c01\u66fe\u60f3\u5230\u5f20\u606a\u7559\u5728\u7701\u57ce\u5cbf\u7136\u4e0d\u52a8\u6709\u4e00\u4e2a\u661f\u671f\uff0c\u6bcf\u5929\u9664\u4e86\u7ef4\u6301\u7701\u57ce\u7684\u5e7f\u544a\u6295\u653e\u529b\u5ea6\uff0c\u8fd8\u6709\u5c31\u662f\u8ba9\u53f6\u5c0f\u6850\u6c47\u603b\u5404\u5730\u7ecf\u9500\u5546\u62a5\u4e0a\u6765\u7684\u9500\u552e\u6570\u636e\u3002</p><p>\u3000\u3000\u201c\u505a\uff0c\u5f53\u7136\u8981\u505a\uff0c\u201d\u5f20\u606a\u5750\u4e0b\u6765\u9605\u8bfb\u6628\u5929\u7684\u9500\u989d\u6570\u636e\uff0c\u201c\u4f46\u662f\u5728\u90a3\u4e4b\u524d\u8981\u77e5\u9053\u600e\u4e48\u505a\uff0c\u5e76\u4e0d\u662f\u7b80\u5355\u7684\u590d\u5236\u7701\u57ce\u5e02\u573a\u7684\u65b9\u6848\uff0c\u8981\u662f\u90a3\u6837\u7684\u8bdd\uff0c\u6211\u4f55\u82e6\u8ddf\u7740\u4f60\u4eec\u5403\u8fd9\u82e6\u5934\uff1f\u201d</p><p>\u3000\u3000\u201c\u53d1\u5e03\u4f1a\u624d\u8fc7\u53bb\u4e03\u5929\uff0c\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\u91cc\uff0c\u9500\u552e\u6570\u636e\u80fd\u770b\u51fa\u4e9b\u4ec0\u4e48\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u76ee\u524d\u6295\u5165\u7684\u5e7f\u544a\uff0c\u79cd\u7c7b\u8fd8\u5f88\u6709\u9650\uff0c\u53c8\u6709\u65f6\u57df\u4e0e\u5730\u57df\u7684\u5c40\u9650\uff0c\u624d\u80fd\u66f4\u52a0\u51c6\u786e\u7684\u5224\u65ad\u5404\u7c7b\u5e7f\u544a\u7684\u6548\u679c\u4ee5\u53ca\u5e45\u5c04\u8303\u56f4\u3002\u201d</p><p>\u3000\u3000\u63d0\u8d77\u9500\u552e\u6570\u636e\uff0c\u53f6\u5c0f\u6850\u90fd\u5fcd\u4e0d\u4f4f\u81ea\u8c6a\u8d77\u6765\uff1a\u201c\u7231\u8fbe\u7684\u673a\u5b50\uff0c\u77ed\u77ed\u4e03\u5929\uff0c\u5728\u7701\u57ce\u5c31\u9500\u552e\u4e86\u4e09\u767e\u53f0\uff0c\u751a\u81f3\u8d85\u8d8a\u4e09\u661f\uff0c\u6210\u4e3a\u5f71\u789f\u673a\u7684\u7b2c\u4e00\u54c1\u724c\uff0c\u8981\u662f\u7ef4\u6301\u8fd9\u6837\u7684\u5e7f\u544a\u5f3a\u5ea6\uff0c\u6574\u4e2a\u534e\u4e1c\u5730\u533a\u5c31\u80fd\u6bcf\u4e2a\u6708\u5c31\u80fd\u8fbe\u5230\u4e00\u4e07\u53f0\u7684\u9500\u91cf\uff0c\u53ef\u4ee5\u4e0e\u4e09\u661f\u5f71\u789f\u673a\u5728\u534e\u4e1c\u5730\u533a\u5206\u5ead\u6297\u793c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u533a\u57df\u5c40\u9650\u6027\u5f88\u5f3a\uff0c\u800c\u4e14\u7231\u8fbe\u5728\u534e\u4e1c\u5730\u533a\u6295\u5165\u7684\u5e7f\u544a\u8d39\u7528\u5c06\u662f\u4e09\u661f\u7684\u4e09\u500d\u4e4b\u591a\uff0c\u5e76\u6ca1\u6709\u503c\u5f97\u9a84\u50b2\u7684\u5730\u65b9\u2026\u2026\u201d\u5f20\u606a\u5e76\u4e0d\u754f\u60e7\u4e09\u661f\uff0c\u4e09\u661f\u5f71\u789f\u673a\u57fa\u4e8e\u7b2c\u4e00\u4ee3\u89e3\u7801\u82af\u7247\u7684\u4ea7\u54c1\uff0c\u8d28\u91cf\u5f88\u4e0d\u7a33\u5b9a\uff0c\u8fd4\u4fee\u7387\u5f88\u9ad8\uff0c\u867d\u7136\u662f\u56fd\u9645\u5927\u5236\u9020\u5546\uff0c\u4f46\u662f\u7ed9\u5f71\u789f\u673a\u7684\u8d28\u91cf\u95ee\u9898\u8499\u4e0a\u4e00\u5c42\u9634\u5f71\uff0c\u4ee5\u81f4\u4e09\u661f\u4f1a\u5f88\u5feb\u9000\u51fa\u5f71\u789f\u673a\u5e02\u573a\u7684\u89d2\u9010\u3002</p><p>\u3000\u3000\u201c\u8fd9\u5012\u662f\u6ca1\u6709\u529e\u6cd5\uff0c\u4e09\u661f\u54c1\u724c\u5728\u56fd\u5185\u7684\u77e5\u540d\u5ea6\u53ef\u4e0d\u662f\u7231\u8fbe\u80fd\u6bd4\u7684\uff0c\u4ed6\u4eec\u5728\u54c1\u724c\u5ba3\u4f20\u4e0a\uff0c\u7701\u529b\u4e0d\u5c11\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5e7f\u544a\u5bf9\u5229\u6da6\u7684\u5e2e\u52a9\uff0c\u5e76\u6ca1\u6709\u50cf\u5927\u5bb6\u6240\u60f3\u8c61\u7684\u90a3\u4e48\u795e\u5947\uff0c\u6211\u4eec\u8ba1\u5212\u5728\u8fd9\u5ea7\u57ce\u5e02\u4e00\u5e74\u5185\u6295\u5165\u56db\u767e\u4e07\u7684\u5e7f\u544a\u8d39\u7528\uff0c\u4ee5\u76ee\u524d\u7684\u5229\u6da6\u7a7a\u95f4\u8ba1\u7b97\uff0c\u610f\u5473\u7740\u6211\u4eec\u8981\u5728\u8fd9\u91cc\u9500\u552e\u4e94\u5343\u53f0\u673a\u5b50\uff0c\u624d\u80fd\u5f25\u8865\u5e7f\u544a\u8d39\u7528\u4e0a\u7684\u4e8f\u7a7a\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u8fd8\u8981\u5e73\u644a\u5168\u56fd\u6027\u4f20\u5a92\u4e0a\u7684\u5e7f\u544a\u8d39\u7528\uff0c\u8003\u8651\u5230\u4e00\u5e74\u4e4b\u4e2d\u5229\u6da6\u7a7a\u95f4\u4f1a\u53d7\u5230\u538b\u7f29\u7684\u539f\u56e0\uff0c\u5e7f\u544a\u6295\u5165\u7684\u95ee\u9898\u4f1a\u66f4\u52a0\u590d\u6742\u4e00\u4e9b\u3002\u5982\u679c\u4e0d\u80fd\u7cbe\u51c6\u7684\u8ba1\u7b97\u5e7f\u544a\u6295\u5165\u7684\u5f3a\u5ea6\u4ee5\u53ca\u5e94\u4ea7\u751f\u7684\u5e02\u573a\u6548\u679c\uff0c\u518d\u4f18\u79c0\u7684\u5e7f\u544a\uff0c\u4e00\u65e6\u8fc7\u591a\u8fc7\u6ee5\uff0c\u5bf9\u4f01\u4e1a\u672c\u8eab\u800c\u5df2\uff0c\u90fd\u662f\u906d\u6765\u707e\u96be\u6027\u7684\u540e\u679c\u3002\u6240\u4ee5\u6211\u4eec\u8981\u541d\u556c\u5e7f\u544a\u4e0a\u7684\u6bcf\u5206\u6295\u5165\uff0c\u4f46\u662f\u4e5f\u8981\u5c3d\u53ef\u80fd\u62ff\u51fa\u6bcf\u4e00\u5206\u94b1\u90fd\u7528\u6765\u6253\u5e7f\u544a\uff0c\u8fd9\u771f\u662f\u8ba9\u4eba\u5934\u75bc\u7684\u95ee\u9898\u3002\u201d\u5f20\u606a\u5e26\u7740\u82e6\u7b11\u8bf4\u3002</p><p>\u3000\u3000\u76db\u946b\u63a8\u51fa\u7231\u7acb\u4fe1398\u65f6\uff0c\u5e02\u573a\u63a8\u5e7f\u65b9\u6848\u7531\u5f20\u606a\u5236\u5b9a\u597d\uff0c\u76db\u946b\u5e02\u573a\u90e8\u53ea\u662f\u7eaf\u7cb9\u7684\u6267\u884c\u8005\uff0c\u7231\u7acb\u4fe1398\u83b7\u5f97\u5de8\u5927\u7684\u5e02\u573a\u6210\u529f\uff0c\u8ba9\u53f6\u5c0f\u6850\u3001\u90b5\u5fc3\u6587\u7b49\u4eba\u5f7b\u5e95\u8ba4\u540c\u5f20\u606a\u5728\u5e02\u573a\u8fd0\u8425\u4e0a\u62e5\u6709\u5929\u624d\u822c\u7684\u8000\u773c\u624d\u534e\u3002</p><p>\u3000\u3000\u679c\u7136\uff0c\u5f20\u606a\u5bf9\u5e02\u573a\u7684\u7cbe\u51c6\u5224\u65ad\u5e76\u975e\u6765\u4e4b\u76f4\u89c9\uff0c\u53f6\u5c0f\u6850\u5c31\u6015\u5f20\u606a\u662f\u90a3\u79cd\u5929\u751f\u5bf9\u5e02\u573a\u654f\u9510\u4f46\u7f3a\u4e4f\u7406\u6027\u8ba4\u8bc6\u7684\u4eba\uff0c\u90a3\u6837\u7684\u8bdd\uff0c\u5979\u8ddf\u7740\u5f20\u606a\u8fd8\u80fd\u5b66\u5230\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u201c\u56fd\u5916\u5efa\u7acb\u7684\u6570\u636e\u6a21\u578b\uff0c\u4f3c\u4e4e\u90fd\u5927\u9002\u7528\u4e8e\u56fd\u5185\u7684\u5e02\u573a\u5206\u6790\uff0c\u4f60\u7a76\u7adf\u662f\u600e\u4e48\u5206\u6790\u7684\uff1f\u201d</p><p>\u3000\u3000\u56fd\u5185\u4e5d\u5341\u5e74\u4ee3\u7684\u5546\u54c1\u5e02\u573a\u88ab\u79f0\u4e3a\u5927\u9500\u552e\u65f6\u4ee3\uff0c\u8bb8\u591a\u57fa\u4e8e\u56fd\u5185\u5546\u54c1\u5e02\u573a\u7684\u7279\u5f81\u800c\u5efa\u7acb\u8d77\u6765\u7684\u5206\u6790\u624b\u6bb5\uff0c\u90fd\u662f\u5728\u5927\u9500\u552e\u65f6\u4ee3\u672b\u671f\u5efa\u7acb\u8d77\u6765\u7684\uff0c\u773c\u524d\u505a\u5e02\u573a\u7684\u4eba\u601d\u8def\u8981\u6df7\u4e71\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u53ea\u8003\u8651\u5355\u4e2a\u7684\u5e7f\u544a\u56e0\u7d20\uff0c\u5c06\u5404\u5730\u7684\u9500\u552e\u6570\u636e\u753b\u65b9\u683c\u9635\uff0c\u5c31\u53ef\u4ee5\u7cbe\u7565\u7684\u770b\u51fa\u533a\u57df\u4e2d\u5fc3\u57ce\u5e02\u8fdb\u884c\u5e7f\u544a\u6295\u5165\u5411\u5468\u8fb9\u5730\u533a\u7684\u5e45\u5c04\u4f5c\u7528\u3002\u6211\u4eec\u867d\u7136\u8fd8\u6ca1\u6709\u5bf9\u60e0\u5c71\u8fdb\u884c\u5e7f\u544a\u6295\u653e\uff0c\u4f46\u662f\u60e0\u5c71\u7684\u9500\u91cf\u660e\u663e\u9ad8\u4e8e\u7701\u5185\u5176\u4ed6\u540c\u7c7b\u57ce\u5e02\uff0c\u56e0\u4e3a\u60e0\u5c71\u4e0e\u7701\u57ce\u4e4b\u95f4\u4fe1\u606f\u4ea4\u6d41\u66f4\u5bc6\u5207\u7684\u7f18\u6545\uff0c\u7701\u57ce\u5404\u7c7b\u4fe1\u606f\u76f8\u5bf9\u5bb9\u6613\u4f20\u64ad\u5230\u60e0\u5c71\u53bb\u3002\u5e7f\u544a\u5f62\u5f0f\u5bf9\u6548\u679c\u7684\u5236\u7ea6\uff0c\u6211\u4eec\u76ee\u524d\u5728\u7701\u57ce\u6295\u653e\u7684\u5e7f\u544a\u5f62\u5f0f\u5305\u62ec\u4e2d\u592e\u7535\u89c6\u53f0\u3001\u4e1c\u6d77\u7535\u89c6\u53f0\u3001\u4e1c\u6d77\u665a\u62a5\u3001\u4eca\u65e5\u6668\u62a5\u3001\u53d1\u5e03\u4f1a\u3001\u706f\u7bb1\u5e7f\u544a\u4ee5\u53ca\u4e00\u4e9b\u6237\u5916\u5ba3\u4f20\u6d3b\u52a8\uff0c\u8fd9\u79cd\u79cd\u5e7f\u544a\u7684\u5f62\u5f0f\u90fd\u8981\u8ba4\u771f\u7684\u8003\u5bdf\uff1b\u8fd8\u6709\u4e00\u70b9\u5f88\u91cd\u8981\uff0c\u56e0\u4e3a\u60e0\u5c71\u7684\u7ecf\u9500\u5546\u77e5\u9053\u6211\u4eec\u4e0b\u4e00\u6b65\u5e02\u573a\u63a8\u5e7f\u5c31\u662f\u5728\u60e0\u5c71\uff0c\u6240\u4ee5\u4ed6\u4eec\u4e5f\u5728\u79ef\u6781\u7684\u51c6\u5907\uff0c\u53ea\u8981\u4ed6\u4eec\u80fd\u5c06\u7231\u8fbe\u5f71\u789f\u673a\u6446\u4e0a\u4e3b\u8981\u5546\u573a\u7684\u5bb6\u7535\u67dc\u53f0\uff0c\u6700\u7ec8\u6210\u7ee9\u4e5f\u4e0d\u662f\u90a3\u4e9b\u4e0d\u91cd\u89c6\u7ec8\u7aef\u6e20\u9053\u5efa\u8bbe\u7684\u7ecf\u9500\u5546\u80fd\u6bd4\u7684\u3002\u201d</p><p>\u3000\u3000\u867d\u7136\u53f6\u5c0f\u6850\u81ea\u8ba4\u4e3a\u662f\u5e02\u573a\u8425\u9500\u7684\u7cbe\u82f1\u5206\u5b50\uff0c\u5374\u4e0d\u5f97\u4e0d\u627f\u8ba4\u5f20\u606a\u8d85\u51fa\u4ed6\u4eec\u592a\u591a\uff0c\u6324\u4e86\u6324\u7709\u5934\uff0c\u82e6\u607c\u7684\u8bf4\uff1a\u201c\u8981\u8003\u8651\u8fd9\u4e48\u591a\u7684\u56e0\u7d20\uff0c\u6211\u5b81\u53ef\u76f8\u4fe1\u76f4\u89c9\u3002\u201d</p><p>\u3000\u3000\u5e02\u573a\u8fd0\u4f5c\u6c14\u9b44\u4e4b\u5927\uff0c\u5f88\u96be\u8ba9\u4eba\u76f8\u4fe1\u4ed6\u5bf9\u5e02\u573a\u8fd0\u4f5c\u6709\u7740\u7cbe\u51c6\u7684\u628a\u63e1\uff0c\u800c\u4e8b\u5b9e\u5c31\u662f\u4ed6\u5bf9\u4e00\u5207\u90fd\u4e86\u7136\u4e8e\u5fc3\uff0c\u8fd9\u6837\u7684\u5929\u624d\u4e4b\u6c34\u51c6\uff0c\u53f6\u5c0f\u6850\u53ea\u80fd\u671b\u5176\u9879\u80cc\u800c\u53f9\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u4ed6\u4e3a\u4ec0\u4e48\u575a\u6301\u8981\u8ba9\u5468\u6e38\u8fc7\u6765\u5e2e\u4ed6\uff1f\u56e0\u4e3a\u4ed6\u4e0d\u4f1a\u4e00\u76f4\u90fd\u5f88\u6709\u65f6\u95f4\u5bf9\u5e02\u573a\u6e20\u9053\u8fdb\u884c\u7cbe\u7ec6\u5316\u8fd0\u4f5c\uff0c\u5fc5\u987b\u8981\u6709\u4e2a\u771f\u6b63\u80fd\u5728\u8fd9\u65b9\u9762\u5e2e\u4ed6\u7684\u4eba\u3002\u5468\u6e38\u8981\u6bd4\u5218\u660e\u8f89\u53ef\u9760\u591a\u4e86\uff0c\u6240\u4ee5\u5f20\u606a\u8ba9\u5468\u6e38\u76f4\u63a5\u7559\u5728\u8eab\u8fb9\u534f\u52a9\u81ea\u5df1\uff0c\u800c\u8ba9\u5218\u660e\u8f89\u53bb\u534e\u4e1c\u5730\u533a\u4ee5\u5916\u7b79\u5efa\u7ecf\u9500\u5546\u7f51\u7edc\u3002\u5218\u660e\u8f89\u770b\u4e0a\u53bb\u662f\u72ec\u6321\u4e00\u9762\uff0c\u4f46\u662f\u80fd\u505a\u51fa\u7684\u6210\u7ee9\u4f1a\u5341\u5206\u6709\u9650\uff0c\u6700\u540e\u8fd8\u8981\u5f20\u606a\u6216\u5468\u6e38\u5bf9\u8fd9\u4e9b\u5e02\u573a\u8fdb\u884c\u4e00\u6b21\u6216\u6570\u6b21\u7684\u6df1\u5ea6\u6316\u6398\u624d\u80fd\u771f\u6b63\u7684\u5c06\u5e02\u573a\u505a\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ef0\u7740\u8eab\u5b50\uff0c\u5c06\u6905\u80cc\u538b\u5f97\u5dee\u70b9\u8d34\u5230\u5730\u4e0a\uff0c\u4ed6\u4ef0\u7740\u5934\u95ee\u8fdc\u8fdc\u5750\u5728\u4f1a\u8bae\u684c\u524d\u7684\u8bb8\u7ef4\u3001\u6c5f\u9edb\u513f\u3001\u4ee4\u5c0f\u71d5\u4e09\u4e2a\u5973\u5b69\u5b50\uff1a\u201c\u4f60\u4eec\u53bb\u4e0d\u53bb\u60e0\u5c71\uff1f\u6211\u4eec\u660e\u5929\u5c31\u53bb\u60e0\u5c71\u201d</p><p>\u3000\u3000\u5f20\u606a\u6bcf\u5929\u5230\u76db\u946b\u516c\u53f8\uff0c\u4f8b\u884c\u90fd\u662f\u5148\u8ddf\u53f6\u5c0f\u6850\u3001\u5468\u6e38\u827a\u5ba4\u8ba8\u8bba\u4e00\u4f1a\u513f\uff0c\u518d\u8ddf\u56e2\u961f\u5176\u4ed6\u4eba\u505a\u4ea4\u6d41\uff0c\u4ee4\u5c0f\u71d5\u4f5c\u4e3a\u53f6\u5c0f\u6850\u7684\u52a9\u624b\uff0c\u76f4\u63a5\u52a0\u5165\u8fd9\u4e2a\u56e2\u961f\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u8981\u914d\u5408\u5e02\u573a\u5ba3\u4f20\u6d3b\u52a8\uff0c\u5979\u4e0e\u8bb8\u7ef4\u4e5f\u4e34\u65f6\u52a0\u5165\u8fd9\u4e2a\u56e2\u961f\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u7701\u57ce\u6ede\u7559\u4e86\u4e00\u5468\u65f6\u95f4\uff0c\u5bf9\u4e8e\u4ed6\u6765\u8bf4\uff0c\u6b9a\u7cbe\u7aed\u8651\u7684\u8003\u8651\u8be5\u5982\u4f55\u63a7\u5236\u5e02\u573a\u63a8\u5e7f\u7684\u5f3a\u5ea6\u4e0e\u8282\u594f\uff0c\u4f46\u662f\u6c5f\u9edb\u513f\u3001\u8bb8\u7ef4\u3001\u4ee4\u5c0f\u71d5\u5374\u76f8\u5bf9\u8f7b\u677e\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u60e0\u5c71\u7684\u5e7f\u544a\u6295\u653e\u5f3a\u5ea6\u76f8\u6bd4\u7701\u57ce\u8981\u5f31\u8bb8\u591a\uff0c\u5728\u5f20\u606a\u4ed6\u4eec\u62b5\u8fbe\u60e0\u5c71\u4e4b\u524d\uff0c\u5c31\u7531\u60e0\u5c71\u7ecf\u9500\u5546\u53ec\u96c6\u5356\u573a\u4e0e\u5a92\u4f53\u53ec\u5f00\u4ea7\u54c1\u4e0a\u5e02\u9884\u70ed\u53d1\u5e03\u4f1a\uff0c\u9080\u8bf715\u5bb6\u5a92\u4f53\u91c7\u8bbf\u3001\u520a\u53d1\u65b0\u95fb\u7a3f\uff0c\u5728\u5f20\u606a\u3001\u53f6\u5c0f\u6850\u3001\u5468\u6e38\u62b5\u8fbe\u60e0\u5c71\u4e4b\u524d\uff0c\u520a\u53d1\u4e86\u4e24\u8f6e\u517135\u7bc7\u7684\u65b0\u95fb\u7a3f\uff0c\u51e0\u4e4e\u8986\u76d6\u60e0\u5c71\u5e02\u6240\u6709\u5e73\u9762\u5a92\u4f53\uff0c\u5e76\u5728\u8fd9\u4e9b\u5e73\u9762\u5a92\u4f53\u4e0a\u520a\u53d11/4\u7248\u7684\u8f6f\u6587\u5e7f\u544a\u5171\u6b2160\u7bc7\u6b21\u3002</p><p>\u3000\u3000\u52a0\u4e0a\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u4e94\u79d2\u949f\u5e7f\u544a\uff0c\u5e7f\u544a\u8986\u76d6\u7a0b\u5ea6\u4e5f\u7b97\u4e0d\u5dee\uff0c\u4f46\u662f\u8981\u5c06\u4ee5\u54c1\u724c\u544a\u77e5\u4e3a\u76ee\u6807\u7684\u7535\u89c6\u5a92\u4f53\u3001\u5e73\u9762\u5a92\u4f53\u5e7f\u544a\u4e0e\u9500\u552e\u7ec8\u7aef\u8fde\u63a5\u8d77\u6765\uff0c\u5219\u9700\u8981\u66f4\u76f4\u63a5\u7684\u5e7f\u544a\u5f62\u5f0f\u4e00\u76f4\u6253\u52a8\u666e\u901a\u5e02\u6c11\u7684\u5fc3\u3002</p><p>\u3000\u30007\u670813\u65e5\uff0c\u5f20\u606a\u4e0e\u53f6\u5c0f\u6850\u3001\u5468\u6e38\u7b49\u4eba\u62b5\u8fbe\u60e0\u5c71\uff0c\u53c2\u52a0\u665a\u4e0a\u6b63\u5f0f\u4e3e\u884c\u7684\u4ea7\u54c1\u53d1\u5e03\u4f1a\u3002</p><p>\u3000\u3000\u7ecf\u8fc7\u77ed\u65f6\u95f4\u7684\u5408\u4f5c\u4e4b\u540e\uff0c\u5f20\u606a\u4e5f\u6b63\u5f0f\u9009\u62e9\u98de\u9e3f\u5e7f\u544a\u516c\u53f8\u4f5c\u4e3a\u7231\u8fbe\u7684\u5e7f\u544a\u4ee3\u7406\u5546\u3002</p><p>\u3000\u3000\u5728\u7701\u57ce\u7684\u53d1\u5e03\u4f1a\u7b79\u5907\u662f\u4ee5\u76db\u946b\u7684\u56e2\u961f\u4e3a\u4e3b\u4f53\uff0c\u4f46\u662f\u5176\u4ed6\u57ce\u5e02\u7684\u63a8\u5e7f\u6d3b\u52a8\uff0c\u5c06\u4ee5\u98de\u9e3f\u516c\u53f8\u7684\u56e2\u961f\u4e3a\u4e3b\uff0c\u5f20\u606a\u3001\u53f6\u5c0f\u6850\u3001\u5468\u6e38\u7387\u9886\u7684\u56e2\u961f\u53ea\u8d1f\u8d23\u7edf\u7b79\u51b3\u7b56\u65b9\u9762\u7684\u5de5\u4f5c\uff0c\u7531\u98de\u9e3f\u516c\u53f8\u8054\u5408\u5f53\u5730\u7684\u7ecf\u9500\u5546\u5177\u4f53\u6267\u884c\u5e02\u573a\u63a8\u5e7f\u65b9\u6848\uff0c\u6bd5\u7adf\u5927\u91cf\u5e7f\u544a\u5236\u4f5c\u4e0e\u53d1\u5e03\u5de5\u4f5c\u91cf\u6781\u5927\uff0c\u4e13\u4e1a\u4e0a\u7684\u8981\u6c42\u4e5f\u76f8\u5f53\u9ad8\u3002</p><p>\u3000\u3000\u8f66\u5b50\u76f4\u63a5\u5f00\u60e0\u5c71\u5e02\u4e2d\u5fc3\u918b\u574a\u6865\uff0c\u5728\u5999\u7384\u89c2\u524d\u5e7f\u573a\u505c\u4e0b\u6765\uff0c\u56de\u60f3\u8d77\u5e74\u521d\u4e0e\u8bb8\u601d\u591c\u91cc\u7ecf\u8fc7\u8fd9\u91cc\uff0c\u5f20\u606a\u5634\u89d2\u6302\u7740\u6d45\u6d45\u7684\u5fae\u7b11\uff0c\u770b\u7740\u6c5f\u9edb\u513f\u3001\u8bb8\u7ef4\u5979\u4eec\u4ece\u540e\u9762\u7684\u5546\u52a1\u8f66\u91cc\u6d8c\u51fa\u6765\uff0c\u5979\u4eec\u4e00\u51fa\u6765\u5c31\u62ac\u5934\u770b\u89c2\u524d\u5e7f\u573a\u524d\u6700\u9ad8\u3001\u6700\u9192\u76ee\u7684\u5e7f\u544a\u724c\uff0c\u8fd8\u662f\u5218\u5fb7\u534e\u624b\u6301\u7231\u7acb\u4fe1398\u624b\u673a\u7684\u82f1\u4fca\u5f62\u8c61\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u8d70\u8fc7\u6765\u6572\u4e86\u6572\u8f66\u7a97\uff0c\u6307\u7740\u90a3\u5757\u8def\u724c\u5e7f\u544a\u95ee\u5f20\u606a\uff1a\u201c\u8fd8\u4ee5\u4e3a\u9edb\u513f\u7684\u5de8\u5e45\u5f69\u7167\u4f1a\u66ff\u6362\u4e0b\u5218\u5929\u738b\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u592a\u5355\u8c03\u5c31\u4f53\u73b0\u4e0d\u51fa\u6c34\u5e73\u6765\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u6309\u4e0b\u8f66\u7a97\u63a2\u5934\u95ee\u540e\u9762\u7684\u6c5f\u9edb\u513f\uff1a\u201c\u56de\u60e0\u5c71\u6709\u6ca1\u6709\u538b\u529b\uff1f\u201d</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u6342\u7740\u80f8\u53e3\u8bf4\uff1a\u201c\u8fd8\u597d\uff0c\u5728\u8f66\u4e0a\u8fd8\u62c5\u5fc3\u8fd9\u4e2a\u2026\u2026\u201d\u660e\u7738\u77a5\u7740\u90a3\u5757\u8def\u724c\u5e7f\u544a\uff0c\u4e4b\u524d\u62c5\u5fe7\u5f20\u606a\u8fc7\u4e8e\u534e\u4e3d\u7684\u98ce\u683c\uff0c\u5c31\u6015\u4ed6\u4f1a\u5728\u89c2\u524d\u5e7f\u573a\u7684\u8fd9\u5757\u8def\u724c\u5e7f\u544a\u4e0a\u6362\u4e0a\u5979\u7684\u5927\u5e45\u5f62\u8c61\u7167\u3002\u5979\u7ed9\u7231\u8fbe\u505a\u5f62\u8c61\u4ee3\u8a00\u4eba\u7684\u4e8b\u60c5\u8fd8\u6ca1\u6709\u8ddf\u5bb6\u8bf4\u8d77\u8fc7\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u4e94\u79d2\u949f\u5e7f\u544a\uff0c\u5373\u4f7f\u8ba9\u5988\u5988\u770b\u5230\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4ee5\u4e3a\u662f\u76f8\u8c8c\u76f8\u4f3c\u7684\u5973\u5b69\u5b50\uff0c\u8981\u662f\u5728\u5bb6\u95e8\u53e3\u641e\u51fa\u592a\u5927\u7684\u52a8\u9759\uff0c\u8ba9\u5bb6\u91cc\u53d1\u89c9\uff0c\u8fd8\u4e0d\u6653\u5f97\u4f1a\u6709\u600e\u4e48\u7684\u53cd\u5e94\uff1f\u5f53\u7136\uff0c\u5f53\u5979\u770b\u5230\u8fd9\u5757\u60e0\u5c71\u5e02\u6700\u6709\u4ef7\u503c\u7684\u6237\u5916\u5e7f\u544a\u5e76\u6ca1\u6709\u6362\u4e0a\u81ea\u5df1\u7684\u5f62\u8c61\u7167\uff0c\u4e5f\u514d\u4e0d\u4e86\u6709\u4e9b\u5fae\u7684\u5931\u611f\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u8bb8\u7ef4\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u5c16\u53eb\u4e86\u4e00\u58f0\uff0c\u5fcd\u4e0d\u4f4f\u6342\u7740\u5634\uff0c\u4eff\u4f5b\u770b\u5230\u5f88\u6050\u6016\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u3001\u4ee4\u5c0f\u71d5\u540c\u65f6\u56de\u5934\u770b\u8fc7\u53bb\uff0c\u90fd\u5403\u60ca\u7684\u6123\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0b\u4e86\u8f66\uff0c\u6276\u7740\u8f66\u95e8\u5411\u540e\u9762\uff0c\u4e00\u8f86\u53cc\u5c42\u5df4\u58eb\u4ece\u9163\u574a\u6865\u65b9\u5411\u7f13\u7f13\u9a76\u8fc7\u89c2\u524d\u5e7f\u573a\uff0c\u5927\u5e45\u55b7\u7ed8\u7740\u6c5f\u9edb\u513f\u4fef\u8eab\u51dd\u89c6\u5f71\u789f\u673a\u7684\u5e7f\u544a\u7167\u8986\u7740\u53cc\u5c42\u5df4\u58eb\u7684\u8f66\u8eab\uff0c\u8272\u5f69\u9c9c\u8273\u593a\u76ee\uff0c\u753b\u9762\u4e0a\u7684\u6c5f\u9edb\u513f\u6829\u6829\u5982\u751f\uff0c\u7a7f\u7740\u6854\u9ec4\u8272\u7684\u540a\u5e26\u88d9\uff0c\u5a07\u598d\u660e\u8273\uff0c\u5374\u7ed9\u4eba\u708e\u708e\u9177\u6691\u6e05\u51c9\u6c81\u4eba\u7684\u611f\u89c9\uff0c\u753b\u9762\u4e0a\u6c5f\u9edb\u513f\u4fef\u8eab\u51dd\u89c6\u7740\u5f71\u789f\u673a\u6709\u8d28\u611f\u7684\u673a\u8eab\uff0c\u5a07\u6da6\u7ea2\u8273\u7684\u5634\u5507\u4ee5\u5947\u5999\u7684\u5f27\u5ea6\u7ed9\u4eba\u5979\u6b63\u8f7b\u8f7b\u671d\u5f71\u789f\u673a\u5439\u6c14\u7684\u611f\u89c9\uff0c\u5927\u6982\u8c01\u90fd\u4f1a\u6709\u4e0e\u5f71\u789f\u673a\u6362\u4e00\u6362\u4f4d\u7f6e\u7684\u5962\u671b\u3002</p><p>\u3000\u3000\u56db\u7c73\u9ad8\u7684\u753b\u9762\uff0c\u5373\u4f7f\u8fdc\u79bb\u516d\u5341\u7c73\u4e5f\u6e05\u6670\u53ef\u89c1\u3002</p><p>\u3000\u3000\u201c\u611f\u89c9\u600e\u4e48\u6837\uff1f\u201d\u770b\u7740\u6c5f\u9edb\u513f\u5979\u4eec\u5403\u60ca\u7684\u8868\u60c5\uff0c\u5f20\u606a\u8f7b\u8f7b\u7684\u5439\u4e86\u58f0\u53e3\u54e8\uff0c\u201c\u8d76\u4e0a\u60e0\u5c71\u5728\u918b\u574a\u6865\u3001\u5999\u7384\u89c2\u7ebf\u8def\u4e0a\u63a8\u51fa\u5168\u65b0\u7684\u53cc\u5c42\u5df4\u58eb\uff0c\u6211\u5c31\u5c06\u5341\u8f86\u53cc\u5c42\u5df4\u58eb\u7684\u8f66\u8eab\u5e7f\u544a\u53d1\u5e03\u6743\u90fd\u4e70\u4e86\u4e0b\u6765\uff0c\u8f66\u8eab\u5e7f\u544a\u7684\u5236\u4f5c\u7a0d\u9ebb\u70e6\u4e9b\uff0c\u6240\u4ee5\u624d\u62d6\u5230\u4eca\u5929\u53ec\u5f00\u6b63\u5f0f\u7684\u4ea7\u54c1\u53d1\u5e03\u4f1a\u2026\u2026\u201d</p><p>\u3000\u3000\u867d\u7136\u65e9\u5728\u6c11\u56fd\u65f6\u671f\u7684\u4e0a\u6d77\uff0c\u5c31\u80fd\u4ece\u5230\u5904\u90fd\u8dd1\u7740\u7684\u6709\u8f68\u7535\u8f66\u4e0a\u770b\u5230\u8001\u5b57\u53f7\u7684\u8f66\u8eab\u5e7f\u544a\uff0c\u8fd9\u79cd\u60c5\u5f62\u7535\u5f71\u91cc\u4e5f\u5bfb\u5e38\u89c1\uff0c\u4f46\u5728\u56fd\u5185\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\uff0c\u5bf9\u8f66\u8eab\u5e7f\u544a\u8fd9\u79cd\u6700\u91cd\u8981\u7684\u6237\u5916\u5e7f\u544a\u5f62\u5f0f\u8fd8\u4e0d\u5f88\u91cd\u89c6\uff0c\u4e5d\u4e94\u5e74\u56fd\u5185\u51e0\u4e4e\u770b\u4e0d\u5230\u8986\u76d6\u5168\u8f66\u7684\u8f66\u8eab\u5e7f\u544a\u3002</p><p>\u3000\u3000\u8f66\u8eab\u5e7f\u544a\u7684\u5927\u53d1\u5c55\uff0c\u8fd8\u662f\u4e5d\u4e5d\u5e74\u6574\u4e2a\u7f51\u7edc\u884c\u4e1a\u5bf9\u8f66\u8eab\u5e7f\u544a\u7684\u70ed\u6367\uff0c\u5f20\u606a\u5bf9\u516c\u4ea4\u8f66\u8eab\u4e0a\u90a3\u4e2a\u9192\u76ee\u7684\u641c\u72d0\u7684\u72d0\u72f8\u811a\u5370\u4e0e\u5c3e\u5df4\u4e00\u76f4\u90fd\u6709\u7740\u5f88\u6df1\u7684\u5370\u8c61\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u60df\u4e00\u53ef\u79fb\u52a8\u7684\u6237\u5916\u5a92\u4f53\uff0c\u8f66\u8eab\u5e7f\u544a\u53ef\u4ee5\u4e3b\u52a8\u7684\u51fa\u73b0\u5728\u666e\u901a\u5e02\u6c11\u7684\u89c6\u91ce\u4e4b\u5185\uff0c\u6210\u4e3a\u7535\u89c6\u4f20\u5a92\u4e4b\u5916\u6700\u6709\u6548\u7684\u5e7f\u544a\u5f62\u5f0f\uff0c\u7531\u4e8e\u4e5d\u4e94\u5e74\u8f66\u8eab\u5e7f\u544a\u521a\u521a\u8d77\u6b65\uff0c\u8fd8\u5f88\u4e0d\u53d7\u91cd\u89c6\uff0c\u8f66\u8eab\u5e7f\u544a\u7684\u6295\u653e\u8d39\u7528\u51fa\u4e8e\u610f\u6599\u7684\u4f4e\u5ec9\uff0c\u6295\u653e\u8f66\u8eab\u5e7f\u544a\u7684\u8d39\u7528\u751a\u81f3\u6bd4\u5728\u5e7f\u64ad\u7535\u53f0\u505a\u5e7f\u544a\u8fd8\u8981\u4f4e\u8bb8\u591a\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e9\u5c31\u51b3\u5b9a\u4ee5\u8f66\u8eab\u5e7f\u544a\u4f5c\u4e3a\u592e\u89c6\u5e7f\u544a\u4e4b\u5916\u6700\u4e3b\u8981\u7684\u5e02\u573a\u5ba3\u4f20\u624b\u6bb5\uff0c\u53ea\u662f\u7701\u57ce\u90a3\u8fb9\u4e3e\u529e\u53d1\u5e03\u4f1a\u65f6\uff0c\u65f6\u95f4\u4e0a\u8d76\u4e0d\u4e0a\u8d9f\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u4e0b\u4e86\u8f66\u7b49\u7b2c\u4e8c\u8f86\u53cc\u5c42\u5df4\u58eb\u901a\u8fc7\uff0c\u8fd9\u4e5f\u662f\u60e0\u5c71\u5e02\u516c\u4ea4\u7cfb\u7edf\u9996\u6b21\u5f15\u8fdb\u8fd9\u79cd\u53cc\u5c42\u7a7a\u8c03\u5df4\u58eb\uff0c\u9996\u6b21\u5c31\u8d2d\u8fdb\u5341\u8f86\uff0c\u9ad8\u5927\u52a0\u957f\u7684\u8f66\u8eab\uff0c\u52a0\u4e0a\u8272\u5f69\u70ab\u76ee\u7684\u5e7f\u544a\u7cbe\u7f8e\u56fe\u6848\uff0c\u4e00\u4e0b\u5b50\u5c31\u5438\u5f15\u4f4f\u5e7f\u573a\u4e0a\u6e38\u5ba2\u4e0e\u5e02\u6c11\u7684\u76ee\u5149\u3002</p><p>\u3000\u3000\u5f88\u591a\u4eba\u7ecf\u8fc7\u8eab\u8fb9\uff0c\u90fd\u8fd8\u5728\u8bae\u8bba\uff1a\u201c\u73b0\u5728\u7684\u5546\u5bb6\u771f\u4e0d\u5f97\u4e86\uff0c\u5e7f\u544a\u90fd\u505a\u5230\u516c\u4ea4\u8f66\u4e0a\u53bb\u4e86\u3002\u201d</p><p>\u3000\u3000\u98de\u9e3f\u5e7f\u544a\u7684\u603b\u7ecf\u7406\u656c\u98de\u9e3f\u4e0e\u60e0\u5c71\u7ecf\u9500\u5546\u5f88\u5feb\u5c31\u5f00\u8f66\u8fc7\u6765\u6c47\u5408\uff0c\u4ed6\u4eec\u6cbf\u8def\u4e5f\u770b\u5230\u53cc\u5c42\u5df4\u58eb\u5f15\u4eba\u77a9\u76ee\u7684\u76db\u51b5\uff0c\u656c\u98de\u9e3f\u4e0b\u8f66\u8d70\u5230\u5f20\u606a\u7684\u8eab\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5e7f\u544a\u4e00\u5236\u4f5c\u597d\uff0c\u516c\u4ea4\u516c\u53f8\u5c31\u540e\u6094\u4e0d\u5df2\u554a\uff0c\u5f00\u4ef7\u4e5f\u8d3c\u4f4e\u4e86\u4e00\u4e9b\u3002\u201d</p><p>\u3000\u3000\u5341\u8f86\u53cc\u5c42\u5df4\u58eb\u8fdb\u884c\u5e7f\u544a\u6295\u653e\uff0c\u4e00\u5e74\u7684\u8d39\u7528\u624d\u4e8c\u5341\u4e07\uff0c\u53ea\u662f\u5236\u4f5c\u6210\u672c\u504f\u9ad8\u4e00\u4e9b\uff0c\u5373\u4f7f\u5982\u6b64\uff0c\u5728\u7ecf\u8fc7\u60e0\u5c71\u5e02\u6700\u7e41\u534e\u7684\u516c\u4ea4\u7ebf\u8def\u4e0a\u6295\u5165\u8f66\u8eab\u5e7f\u544a\uff0c\u4e00\u5e74\u7684\u5e7f\u544a\u6210\u672c\u624d\u4e0d\u8fc7\u4e09\u5341\u4e07\uff0c\u800c\u5728\u7701\u57ce\u5e02\u767e\u4e00\u5e97\u5bf9\u9762\u7684\u5927\u578b\u8def\u724c\u5e7f\u544a\uff0c\u4e00\u5e74\u7684\u8d39\u7528\u5c31\u8981\u4e0a\u767e\u4e07\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u8fd8\u7b97\u53ef\u4ee5\u5427\uff0c\u76ee\u524d\u7684\u8f66\u8eab\u5e7f\u544a\u5c31\u8fd9\u4e2a\u4ef7\uff0c\u53ef\u80fd\u5168\u65b0\u7684\u53cc\u5c42\u5df4\u58eb\u6bd4\u8f83\u593a\u76ee\u4e00\u4e9b\uff0c\u6240\u4ee5\u663e\u773c\u4e00\u4e9b\uff0c\u4f46\u662f\u80fd\u4e0d\u80fd\u53d6\u5f97\u9884\u671f\u7684\u6548\u679c\uff0c\u8fd8\u8981\u7b49\u4e00\u6bb5\u65f6\u95f4\u624d\u77e5\u9053\uff0c\u4f60\u4eec\u8981\u505a\u597d\u5e02\u573a\u53cd\u9988\u8c03\u67e5\uff0c\u4e0a\u6d77\u90a3\u8fb9\u7684\u5236\u4f5c\u4e5f\u8981\u6293\u7d27\u4e00\u4e9b\u3002\u201d</p><p>\u3000\u3000\u656c\u98de\u9e3f\u7b11\u7740\u8bf4\uff1a\u201c\u8981\u662f\u80fd\u62ff\u4e0b\u7701\u57ce\u7684\u516c\u4ea4\u8f66\u8eab\u5e7f\u544a\u53d1\u5e03\u6743\uff0c\u4f1a\u4e0d\u4f1a\u5bf9\u98de\u9e3f\u516c\u53f8\u6709\u6240\u5e2e\u52a9\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u7701\u57ce\u4e2d\u5fc3\u57ce\u533a\u6709\u8fd1\u4e09\u767e\u4e07\u4eba\u53e3\uff0c\u56db\u5343\u591a\u8f86\u516c\u4ea4\u8f66\uff0c\u4ee5\u6bcf\u8f86\u516c\u4ea4\u8f66\u4e24\u4e07\u5143\u7684\u8f66\u8eab\u5e7f\u544a\u53d1\u5e03\u8d39\uff0c\u6574\u5ea7\u57ce\u5e02\u6709\u8fd1\u4e00\u4ebf\u7684\u5e02\u573a\u6f5c\u529b\u53ef\u6316\uff0c\u8fd8\u662f\u86ee\u6709\u524d\u9014\u7684\uff0c\u5c31\u8981\u770b\u98de\u9e3f\u516c\u53f8\u8ddf\u516c\u4ea4\u7cfb\u7edf\u7684\u5173\u7cfb\u5982\u4f55\u4e86\u3002\u201d</p><p>\u3000\u3000\u6ca1\u6709\u8fc7\u786c\u7684\u5173\u7cfb\uff0c\u8981\u5c06\u6574\u5ea7\u57ce\u5e02\u7684\u8f66\u8eab\u5e7f\u544a\u4ee3\u7406\u6743\u62ff\u5230\u624b\uff0c\u7edd\u65e0\u53ef\u80fd\uff0c\u656c\u98de\u9e3f\u8baa\u7136\u7684\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u606a\u5c11\u80fd\u5e2e\u5fd9\u81ea\u7136\u5c31\u6709\u5e0c\u671b\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u4ed6\u53ef\u4e0d\u60f3\u628a\u624b\u4f38\u5f97\u592a\u957f\uff0c\u60f3\u628a\u597d\u5904\u5360\u5c3d\u7684\u884c\u4e3a\u4f1a\u5f88\u60f9\u4eba\u538c\u7684\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u53bb\u95ee\u53f6\u54e5\u6709\u6ca1\u6709\u5174\u8da3\uff0c\u6211\u6ca1\u90a3\u4e2a\u7cbe\u529b\u2026\u2026\u201d</p><p>\u3000\u3000\u656c\u98de\u9e3f\u4e0e\u60e0\u5c71\u7684\u7ecf\u9500\u5546\u8fc7\u6765\u6c47\u5408\uff0c\u5c31\u8ba9\u4ed6\u4eec\u5728\u524d\u9762\u5f15\u8def\uff0c\u5f80\u4ea7\u54c1\u53d1\u5e03\u4f1a\u73b0\u573a\u60e0\u5c71\u5927\u9152\u5e97\u9a76\u53bb\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u53d1\u5e03\u4f1a\uff0c\u6ca1\u6709\u9080\u8bf7\u5176\u4ed6\u7684\u7ecf\u9500\u5546\uff0c\u4e3b\u8981\u662f\u60e0\u5c71\u5730\u65b9\u7684\u5a92\u4f53\u4e0e\u60e0\u5c71\u5bb6\u5404\u5927\u5546\u573a\u7684\u5bb6\u7535\u90e8\u7ecf\u7406\u4ee5\u53ca\u4ece\u4e8b\u5bb6\u7535\u7ecf\u8425\u6279\u53d1\u4e1a\u52a1\u7684\u79c1\u8425\u4f01\u4e1a\u4e3b\uff0c\u89c4\u6a21\u4e0e\u6c34\u51c6\uff0c\u90fd\u65e0\u6cd5\u8ddf\u7701\u57ce\u7684\u90a3\u6b21\u53d1\u5e03\u76f8\u63d0\u5e76\u8bba\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5f20\u606a\u4e5f\u53ea\u9700\u8981\u8fd9\u6837\u7684\u89c4\u6a21\u4e0e\u6c34\u5e73\uff0c\u4e0d\u7136\u4ed6\u53e3\u888b\u91cc\u5c31\u4f1a\u5403\u7d27\u3002</p><p>\u3000\u3000\u5374\u5728\u4ea7\u54c1\u53d1\u5e03\u4f1a\u73b0\u573a\uff0c\u610f\u6599\u770b\u5230\u4e0d\u671f\u800c\u81f3\u7684\u4e24\u4e2a\u4eba\uff1a\u53f6\u9e3f\u4e0e\u8c22\u5251\u5357\u3002</p><p>\u3000\u3000\u5f20\u606a\u6709\u4e9b\u60ca\u8bb6\uff0c\u4f4e\u5934\u5bf9\u5468\u6e38\u8bf4\uff1a\u201c\u53ef\u6ca1\u8bb0\u5f97\u9080\u8bf7\u60e0\u5c71\u5e02\u7684\u8fd9\u4e24\u4f4d\u5927\u5c11\u554a\uff1f\u201d</p><p>\u3000\u3000\u5468\u6e38\u4e0d\u77e5\u9053\u53f6\u9e3f\u7ea0\u7f20\u6c5f\u9edb\u513f\u7684\u4e8b\u60c5\uff0c\u6307\u7740\u53f6\u9e3f\u95ee\u5f20\u606a\uff1a\u201c\u90a3\u4e2a\u662f\u8c01\uff0c\u76ef\u7740\u6c5f\u9edb\u513f\uff0c\u597d\u50cf\u8981\u628a\u5979\u7ed9\u5403\u4e0b\u53bb\u3002\u201d</p><p>\u3000\u3000\u201c\u4ed6\u6562\u60c5\u662f\u6709\u8fd9\u4e2a\u60f3\u6cd5\uff0c\u201d\u5f20\u606a\u8f7b\u8511\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u6c5f\u5929\u96c6\u56e2\u7684\u5c11\u4e1c\u5bb6\uff0c\u90a3\u8f86\u88ab\u6211\u7838\u5f97\u9762\u76ee\u5168\u975e\u7684\u5954\u9a70\uff0c\u53ef\u80fd\u662f\u8c22\u5251\u5357\u56de\u56fd\u540e\u4ece\u4ed6\u90a3\u91cc\u501f\u7684\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u4ed6\u4eec\u5927\u6982\u662f\u770b\u5230\u53cc\u5c42\u5df4\u58eb\u4e0a\u7684\u8f66\u8eab\u5e7f\u544a\u4e4b\u540e\u6309\u56fe\u7d22\u9aa5\u800c\u6765\uff0c\u5374\u6ca1\u6709\u60f3\u5230\u53f6\u9e3f\u795e\u60c5\u6fc0\u52a8\u7684\u7ad9\u5728\u4f1a\u573a\u5165\u53e3\u76ef\u4e86\u6c5f\u9edb\u513f\u597d\u4e00\u4f1a\u513f\uff0c\u624d\u624b\u6307\u7740\u6c5f\u9edb\u513f\uff0c\u5634\u5507\u54c6\u55e6\u7740\u8bf4\uff1a\u201c\u60f3\u4e0d\u5230\u4f60\u4f1a\u629b\u5934\u9732\u9762\u62cd\u5e7f\u544a\uff0c\u4f60\u770b\u770b\u90a3\u4e9b\u7535\u89c6\u91cc\u7684\u5e7f\u544a\u660e\u661f\uff0c\u54ea\u4e2a\u4e0d\u662f\u9760\u9732\u8089\u5403\u996d\uff1f\u6216\u8bb8\u4f60\u8fd8\u60f3\u7740\u5f53\u4ec0\u4e48\u5c0f\u660e\u661f\uff0c\u4f60\u4e0d\u89c9\u5f97\u90a3\u4e9b\u7535\u89c6\u91cc\u8ddf\u7537\u4eba\u63a5\u543b\u62e5\u62b1\u7684\u5973\u660e\u661f\u90fd\u5f88\u6076\u5fc3\u5417\uff0c\u4f60\u4ee5\u540e\u662f\u4e0d\u662f\u4e5f\u8981\u8fd9\u6837\u5b50\uff0c\u4f60\u8fd9\u6837\u5b50\u600e\u4e48\u5bf9\u5f97\u8d77\u6211\uff0c\u600e\u4e48\u914d\u5f97\u4e0a\u6211\u4eec\u53f6\u5bb6\uff1f\u201d</p></article>"}}}]);
