(window.webpackJsonp=window.webpackJsonp||[]).push([[201],{224:function(p){p.exports={chapterTitle:null,title:"\u7b2c201\u7ae0 \u53c8\u662f\u7838\u8f66",content:"<article><h2>\u7b2c201\u7ae0 \u53c8\u662f\u7838\u8f66</h2><p>\u3000\u3000\u9080\u8bf7\u7684\u5609\u5bbe\u4e0e\u5a92\u4f53\u8bb0\u8005\u90fd\u9646\u7eed\u8fdb\u573a\uff0c\u79bb\u53d1\u5e03\u4f1a\u5f00\u59cb\u65f6\u95f4\u4e5f\u5c31\u5dee\u5341\u591a\u5206\u949f\u5de6\u53f3\uff0c\u53f6\u9e3f\u4e0e\u8c22\u5251\u5357\u7a81\u7136\u51fa\u73b0\u5728\u4f1a\u573a\u5165\u53e3\u3002\u53f6\u9e3f\u5bf9\u6c5f\u9edb\u513f\u5288\u5934\u76d6\u8138\u7684\u4e00\u901a\u6307\u8d23\uff0c\u8ba9\u5728\u573a\u7684\u4eba\u8be7\u5f02\u4e07\u5206\uff0c\u90fd\u83ab\u660e\u5176\u5999\u7684\u76ef\u7740\u6c5f\u9edb\u513f\uff0c\u7eb7\u7eb7\u731c\u6d4b\u5979\u8ddf\u4f1a\u573a\u5165\u53e3\u7684\u8fd9\u4f4d\u9752\u5e74\u5230\u5e95\u662f\u4ec0\u4e48\u5173\u7cfb\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u4e5f\u5b8c\u5168\u4e0d\u77e5\u6240\u4ee5\u7136\uff0c\u6709\u4e9b\u53d1\u8499\uff0c\u6123\u5728\u90a3\u91cc\uff0c\u4f46\u662f\u4f1a\u573a\u4e0a\u6240\u6709\u7684\u4eba\u90fd\u62ff\u72d0\u7591\u7684\u773c\u795e\u76ef\u7740\u5979\uff0c\u8ba9\u5979\u6ee1\u5fc3\u7684\u59d4\u5c48\uff0c\u7c89\u8138\u6da8\u5f97\u901a\u7ea2\uff0c\u660e\u7738\u542b\u6cea\uff0c\u6ceb\u7136\u6b32\u6ef4\uff0c\u5374\u4e0d\u77e5\u9053\u600e\u6837\u4e3a\u81ea\u5df1\u8fa9\u767d\u3002</p><p>\u3000\u3000\u5f20\u606a\u7709\u5934\u5fae\u626c\uff0c\u770b\u7740\u8bb8\u591a\u5a92\u4f53\u8bb0\u8005\u4e0b\u610f\u8bc6\u7684\u4e3e\u8d77\u624b\u91cc\u7684\u957f\u77ed\u955c\u5934\uff0c\u8f7b\u54b3\u4e86\u4e00\u58f0\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u76ee\u5149\u5341\u5206\u4e0d\u8010\u7684\u770b\u7740\u4f1a\u573a\u5165\u53e3\u5904\u7684\u8fce\u5bbe\u5c0f\u59d0\uff0c\u4e0d\u60a6\u7684\u8bf4\uff1a\u201c\u8fd9\u91cc\u4e0d\u4f1a\u968f\u4fbf\u4ec0\u4e48\u4eba\u90fd\u80fd\u8fdb\u6765\u7684\uff0c\u4ed6\u4eec\u662f\u8c01\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u2026\u2026\u201d\u53f6\u9e3f\u9661\u7136\u770b\u89c1\u5f20\u606a\u4ece\u89d2\u843d\u91cc\u7ad9\u51fa\u6765\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u8c22\u5251\u5357\u4e5f\u5413\u4e86\u4e00\u8df3\uff0c\u4ed6\u4eec\u5f00\u8f66\u7ecf\u8fc7\u918b\u574a\u6865\uff0c\u770b\u5230\u53cc\u5c42\u5df4\u58eb\u7684\u8f66\u8eab\u5e7f\u544a\u3002\u5e7f\u544a\u4e0a\u5199\u7740\u672c\u5730\u7ecf\u9500\u5546\u7684\u7535\u8bdd\uff0c\u53f6\u9e3f\u6253\u7535\u8bdd\u624d\u77e5\u9053\u53d1\u5e03\u4f1a\u4ee5\u53ca\u6c5f\u9edb\u513f\u4f1a\u4eb2\u4e34\u53d1\u5e03\u4f1a\u73b0\u573a\u7684\u4e8b\u60c5\uff0c\u4e5f\u6ca1\u6709\u591a\u8003\u8651\uff0c\u5c31\u62c9\u7740\u8c22\u5251\u5357\u5c31\u8d76\u5230\u60e0\u5c71\u5927\u9152\u5e97\u7684\u73b0\u573a\u3002</p><p>\u3000\u3000\u4ed6\u4eec\u6765\u4e4b\u524d\u751a\u81f3\u90fd\u6ca1\u641e\u6e05\u695a\u7231\u8fbe\u7535\u5b50\u662f\u54ea\u91cc\u7684\u4f01\u4e1a\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u7684\u5b58\u5728\uff0c\u53f6\u9e3f\u4e0e\u8c22\u5251\u5357\u751a\u81f3\u8fd8\u6ca1\u6709\u5c06\u5e74\u521d\u9001\u6c5f\u9edb\u513f\u56de\u5bb6\u7684\u7537\u5b50\u4e0e\u5f20\u606a\u8054\u7cfb\u8d77\u6765\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5012\u662f\u77ac\u65f6\u731c\u6d4b\u5f20\u606a\u5c31\u662f\u90a3\u4e2a\u8ba9\u53f6\u9e3f\u53c8\u5fcc\u53c8\u6068\u7684\u7537\u4eba\uff0c\u53f6\u9e3f\u5374\u6ca1\u6709\u60f3\u5230\u4ed6\u501f\u7ed9\u8c22\u5251\u5357\u5f00\u5f80\u6d77\u5dde\u7684\u90a3\u8f86\u5954\u9a70\u5c31\u6bc1\u773c\u524d\u8fd9\u4eba\u4e4b\u624b\uff0c\u4ed6\u89c1\u8c22\u5251\u5357\u5634\u91cc\u4e5f\u53d1\u4e00\u58f0\u8f7b\u547c\uff0c\u95ee\u4ed6\uff1a\u201c\u4f60\u4e5f\u8ba4\u8bc6\u8fd9\u762a\u4e09\u2026\u2026\u201d</p><p>\u3000\u3000\u867d\u7136\u5bf9\u5f20\u606a\u7684\u6068\u610f\u8c22\u5251\u5357\u72b9\u80dc\u8fc7\u53f6\u9e3f\uff0c\u4f46\u662f\u4ed6\u6bd5\u7adf\u77e5\u9053\u5f20\u606a\u80cc\u540e\u7ad9\u7740\u5f90\u5b66\u5e73\uff0c\u867d\u7136\u5728\u60e0\u5c71\uff0c\u8c22\u5251\u5357\u53ef\u4e0d\u8ba4\u4e3a\u95f9\u8d77\u6765\u4ed6\u5c31\u80fd\u5360\u4e3b\u573a\u4f18\u52bf\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u54ac\u7740\u7259\u9f7f\uff0c\u4f4e\u58f0\u5bf9\u53f6\u9e3f\u8bf4\uff1a\u201c\u4ed6\u5c31\u662f\u7838\u4f60\u8f66\u7684\u5f20\u606a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u762a\u4e09\uff1f\u8bf4\u6211\u5417\uff1f\u201d\u5f20\u606a\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u76ef\u7740\u53f6\u9e3f\uff0c\u8bf4\u9053\uff1a\u201c\u8c22\u8c22\u4f60\u7ed9\u6211\u4e00\u4e2a\u5c06\u4f60\u4eec\u8bf7\u51fa\u53bb\u7684\u501f\u53e3\uff0c\u4f46\u5728\u8bf7\u4f60\u4eec\u51fa\u53bb\u4e4b\u524d\uff0c\u514d\u4e0d\u4e86\u8981\u544a\u8beb\u4f60\u4e00\u53e5\uff0c\u867d\u7136\u6c5f\u9edb\u513f\u62d2\u7edd\u4e0e\u4f60\u8c08\u604b\u7231\uff0c\u4f46\u8fd9\u4e0d\u662f\u4f60\u7ee7\u7eed\u7ea0\u7f20\u5979\u7684\u501f\u53e3\u2026\u2026\u201d\u84e6\u7136\u8f6c\u8eab\u671d\u7740\u4f1a\u573a\u5185\u7684\u8bb0\u8005\u4ecb\u7ecd\u4ed6\u4eec\uff0c\u201c\u5404\u4f4d\u5a92\u4f53\u670b\u53cb\u4eec\uff0c\u5bb9\u6211\u5411\u5927\u5bb6\u4ecb\u7ecd\u8fd9\u4e24\u4f4d\u51fa\u8a00\u4e0d\u900a\u7684\u670b\u53cb\uff0c\u8fd9\u4f4d\u662f\u5468\u747e\u745c\u5468\u5e02\u957f\u7684\u516c\u5b50\u3001\u6b63\u6cf0\u96c6\u56e2\u6700\u5e74\u8f7b\u7684\u6267\u884c\u8463\u4e8b\u8c22\u5251\u5357\uff0c\u4ed6\u8eab\u8fb9\u8fd9\u4f4d\u53f6\u9e3f\u5148\u751f\uff0c\u662f\u60e0\u5c71\u5e02\u5927\u540d\u9f0e\u9f0e\u6c5f\u5929\u96c6\u56e2\u8463\u4e8b\u957f\u53f6\u6c5f\u5929\u7684\u516c\u5b50\u3002\u4ed6\u4eec\u4e8c\u4f4d\u4f3c\u4e4e\u8ba4\u4e3a\u56fd\u5185\u7684\u6f14\u827a\u754c\u4eba\u58eb\u90fd\u662f\u80ae\u810f\u4e0d\u582a\u7684\uff0c\u5927\u5bb6\u53ef\u4ee5\u8bf7\u4ed6\u4eec\u53d1\u8868\u4e00\u4e0b\u5177\u4f53\u7684\u770b\u6cd5\u2026\u2026\u201d</p><p>\u3000\u3000\u660e\u660e\u662f\u53f6\u9e3f\u6fc0\u52a8\u4e4b\u4f59\u5931\u8a00\uff0c\u5f20\u606a\u5374\u5c06\u6ce8\u610f\u529b\u8f6c\u79fb\u5230\u4ed6\u5934\u4e0a\u6765\uff0c\u770b\u7740\u4f1a\u573a\u91cc\u7684\u5a92\u4f53\u8bb0\u8005\u4e3e\u8d77\u624b\u91cc\u7684\u955c\u5934\u5c31\u8981\u56f4\u8fc7\u6765\uff0c\u8c22\u5251\u5357\u614c\u5fd9\u906e\u4f4f\u8138\uff0c\u4ed6\u5012\u662f\u77e5\u9053\u5206\u5bf8\uff0c\u5fc3\u91cc\u867d\u7136\u607c\u6068\uff0c\u5374\u77e5\u9053\u5148\u8981\u6446\u8131\u773c\u4e0b\u7684\u56f0\u5883\u624d\u884c\uff0c\u4ed6\u591a\u5927\u7684\u80c6\u5b50\u4e5f\u4e0d\u6562\u516c\u5f00\u53d1\u8868\u8fd9\u6837\u7684\u8a00\u8bba\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u8d70\u2026\u2026\u201d\u8c22\u5251\u5357\u8f6c\u8eab\u5c31\u8d70\uff0c\u53f6\u9e3f\u53cd\u5e94\u6162\u4e00\u4e9b\uff0c\u6028\u6068\u7684\u76ef\u4e86\u6c5f\u9edb\u513f\u4e00\u773c\uff0c\u624d\u8ddf\u7740\u79bb\u53bb\uff0c\u770b\u8d77\u6765\u5f88\u4e0d\u7518\u5fc3\u3002</p><p>\u3000\u3000\u5f20\u606a\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u5c31\u7b97\u90a3\u79cd\u6f5c\u89c4\u5219\u7684\u65b0\u95fb\u6f2b\u5929\u4f20\u64ad\u7684\u65f6\u5019\uff0c\u4e5f\u6ca1\u6709\u54ea\u4e2a\u6709\u5934\u6709\u8138\u7684\u4eba\u58eb\u6562\u7ad9\u51fa\u6765\u6307\u8d23\u6574\u4e2a\u6f14\u827a\u754c\uff0c\u4ed6\u5012\u662f\u5e0c\u671b\u8c22\u5251\u5357\u51b2\u52a8\u4e00\u4e0b\uff0c\u8fd8\u60f3\u770b\u770b\u4ed6\u5988\u6709\u591a\u5927\u7684\u80fd\u8010\u5c06\u8fd9\u4e8b\u538b\u4e0b\u53bb\uff0c\u60f3\u4e0d\u5230\u8c22\u5251\u5357\u8fd8\u662f\u77e5\u9053\u5206\u5bf8\uff0c\u4ece\u5934\u5230\u5c3e\u90fd\u662f\u53f6\u9e3f\u90a3\u4e2a\u9614\u5c11\u7237\u4e0d\u77e5\u597d\u6b79\u3002</p><p>\u3000\u3000\u5f20\u606a\u5524\u6765\u53d1\u5e03\u4f1a\u7684\u7ec4\u7ec7\u8005\u656c\u98de\u9e3f\uff0c\u5429\u5490\u4ed6\uff1a\u201c\u79c1\u4e0b\u91cc\u8ddf\u5728\u573a\u7684\u5a92\u4f53\u670b\u53cb\u6253\u58f0\u62db\u547c\uff0c\u521a\u521a\u53ea\u662f\u5c0f\u5c0f\u7684\u610f\u5916\u2026\u2026\u201d\u4e5d\u5341\u5e74\u4e2d\u671f\uff0c\u7edd\u5927\u591a\u6570\u5a92\u4f53\u8fd8\u5f88\u5faa\u89c4\u8e48\u77e9\uff0c\u7279\u522b\u8fd8\u90fd\u662f\u60e0\u5c71\u5e02\u7684\u4f20\u5a92\uff0c\u53ea\u8981\u5c06\u8c22\u5251\u5357\u626f\u8fdb\u6765\uff0c\u53ea\u6015\u4ed6\u4eec\u8fd8\u6ca1\u6709\u80c6\u91cf\u79c1\u81ea\u64ad\u62a5\u5e02\u957f\u4eb2\u5c5e\u7684\u8d1f\u9762\u6d88\u606f\uff0c\u4f46\u662f\u4e5f\u6015\u6709\u4e00\u4e24\u4e2a\u5403\u4e86\u718a\u5fc3\u8c79\u5b50\u80c6\u7684\u3002</p><p>\u3000\u3000\u4e09\u8a00\u4e24\u8bed\u5c06\u53f6\u9e3f\u4e0e\u8c22\u5251\u5357\u6253\u53d1\u8d70\uff0c\u5728\u573a\u7684\u5a92\u4f53\u8bb0\u8005\u4e5f\u81ea\u52a8\u5c06\u5c0f\u63d2\u66f2\u758f\u6f0f\u6389\uff0c\u53d1\u5e03\u4f1a\u7167\u5e38\u8fdb\u884c\uff0c\u53d1\u5e03\u4f1a\u540e\u7b49\u5609\u5bbe\u3001\u5a92\u4f53\u8bb0\u8005\uff0c\u5f20\u606a\u4ed6\u4eec\u4e5f\u79bb\u5f00\u60e0\u5c71\u5927\u9152\u5e97\u3002\u5f20\u606a\u6068\u4e0d\u80fd\u5c06\u6bcf\u4e00\u5206\u90fd\u8282\u7ea6\u4e0b\u6765\u53bb\u6253\u5e7f\u544a\uff0c\u5f53\u7136\u4e0d\u4f1a\u5962\u4f88\u5230\u96c6\u4f53\u5165\u4f4f\u60e0\u5c71\u5927\u9152\u5e97\u3002</p><p>\u3000\u3000\u51fa\u9152\u5e97\u5927\u95e8\uff0c\u5f20\u606a\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u8fd8\u662f\u94bb\u8fdb\u53f6\u5c0f\u6850\u7684VOLVO\u8f7f\u8f66\u91cc\uff1a\u201c\u60f3\u8d77\u6765\uff0c\u6709\u4ef6\u4e8b\u8981\u8ddf\u4f60\u8bf4\u3002\u201d</p><p>\u3000\u3000\u8fd9\u91cc\u79bb\u5165\u4f4f\u7684\u5bbe\u9986\u4e5f\u5c31\u51e0\u5206\u949f\u7684\u8f66\u7a0b\uff0c\u53f6\u5c0f\u6850\u8be7\u5f02\u7684\u56de\u5934\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff1a\u201c\u5c31\u7b49\u4e0d\u53ca\u51e0\u5206\u949f\uff1f\u201d\u5230\u5165\u4f4f\u7684\u5bbe\u9986\uff0c\u4e5f\u6ca1\u89c1\u5f20\u606a\u8bf4\u4ec0\u4e48\uff0c\u201c\u4ec0\u4e48\u4e8b\uff0c\u8ba9\u4f60\u72b9\u8c6b\u8fd9\u4e48\u4e45\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u5f20\u606a\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u8bf4\u9053\uff0c\u201c\u8bdd\u5230\u5634\u8fb9\u5c31\u5fd8\u6389\u4e86\uff0c\u6539\u5929\u60f3\u8d77\u6765\u518d\u8ddf\u4f60\u8bf4\u3002\u201d</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u83ab\u660e\u5176\u5999\u7684\u76ef\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5f04\u4e0d\u6e05\u4ed6\u5728\u641e\u4ec0\u4e48\u660e\u5802\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ffd\u4e0a\u524d\u8d70\u5230\u524d\u9762\u7684\u6c5f\u9edb\u513f\uff1a\u201c\u9edb\u513f\uff0c\u8981\u4e0d\u8981\u6211\u8ba9\u4eba\u9001\u4f60\u56de\u5bb6\uff1f\u201d</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u72b9\u8c6b\u7740\u7684\u6447\u6447\u5934\uff1a\u201c\u8fd8\u662f\u7b49\u6211\u7238\u5988\u627e\u4e0a\u95e8\u518d\u8bf4\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u563f\u7b11\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6562\u60c5\u4f60\u771f\u662f\u88ab\u6211\u62d0\u8dd1\u4e86\u4f3c\u7684\u2026\u2026\u201d\u770b\u7740\u6c5f\u9edb\u513f\u6709\u4e9b\u5a07\u7f9e\uff0c\u5374\u63a9\u4e0d\u53bb\u773c\u775b\u91cc\u7684\u62c5\u5fe7\uff0c\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\uff0c\u5c31\u5404\u81ea\u53d6\u4e86\u623f\u5361\uff0c\u56de\u623f\u95f4\u4f11\u606f\u3002</p><p>\u3000\u3000\u7761\u5230\u534a\u591c\uff0c\u5c31\u542c\u89c1\u549a\u549a\u6709\u4eba\u6572\u95e8\uff0c\u5f20\u606a\u7761\u773c\u60fa\u677e\u7684\u6253\u5f00\u706f\uff0c\u5c31\u542c\u53f6\u5c0f\u6850\u5728\u95e8\u5916\u53d1\u98da\u7684\u58f0\u97f3\uff1a\u201c\u5f20\u606a\uff0c\u4f60\u7ed9\u6211\u8d77\u6765\uff0c\u65e9\u77e5\u9053\u4f60\u6ca1\u5b89\u4ec0\u4e48\u597d\u5fc3\uff0c\u4f60\u5feb\u53bb\u770b\u6211\u7684\u8f66\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u63c9\u63c9\u773c\u775b\uff0c\u6253\u5f00\u95e8\u770b\u89c1\u4e00\u7fa4\u4eba\u90fd\u5835\u4ed6\u95e8\u53e3\uff0c\u8be7\u5f02\u7684\u8bf4\uff1a\u201c\u8f66\u88ab\u7838\u4e86\uff1f\u665a\u4e0a\u6ca1\u542c\u89c1\u52a8\u9759\u554a\uff0c\u88ab\u7838\u4e86\u51e0\u8f86\u8f66\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u5e0c\u671b\u51e0\u8f86\u8f66\uff1f\u201d\u53f6\u5c0f\u6850\u89c1\u5f20\u606a\u5012\u662f\u4e00\u70b9\u90fd\u4e0d\u88c5\u6a21\u88c5\uff0c\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u201c\u5c31\u4f60\u56de\u5bbe\u9986\u8def\u4e0a\u5750\u7684\u90a3\u8f86\u8f66\u88ab\u7838\u4e86\uff0c\u6211\u7684VOLVO\uff0c\u4f60\u5feb\u53bb\u770b\u770b\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u5c31\u53bb\u770b\u770b\u5427\u3002\u201d\u5f20\u606a\u4e00\u8138\u8f7b\u677e\u7684\u644a\u644a\u624b\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u8d70\u5728\u540e\u9762\u9876\u4e86\u9876\u6c5f\u9edb\u513f\u7684\u5c0f\u8170\uff0c\u62bf\u7740\u5634\u7b11\u7740\u8bf4\uff1a\u201c\u53f6\u9614\u5c11\u918b\u52b2\u771f\u5927\uff0c\u51b2\u51a0\u4e00\u6012\u7838\u540d\u8f66\uff0c\u5947\u602a\u4e86\uff0c\u201d\u53c8\u6709\u4e9b\u7591\u60d1\uff0c\u201c\u5f20\u606a\u600e\u4e48\u5c31\u77e5\u9053\u53f6\u9614\u5c11\u591c\u91cc\u4f1a\u6765\u7838\u8f66\uff0c\u504f\u504f\u56de\u5bbe\u9986\u7684\u8def\u5750\u53f6\u5c0f\u59d0\u7684\u8f66\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u54ed\u7b11\u4e0d\u5f97\uff0c\u8981\u77e5\u9053\u5f20\u606a\u5ac1\u7978\u5979\u7684\u5fc3\u601d\uff0c\u6253\u6b7b\u90fd\u4e0d\u4f1a\u8ba9\u4ed6\u4e0a\u81ea\u5df1\u7684\u8f66\uff0c\u56de\u5934\u53c9\u8170\u5bf9\u4ee4\u5c0f\u71d5\u5979\u4eec\u8bf4\uff1a\u201c\u4e89\u98ce\u5403\u918b\u4e5f\u8981\u6709\u4e2a\u9650\u5ea6\uff0c\u5173\u952e\u662f\u5f20\u606a\u5728\u6d77\u5dde\u5148\u5c06\u4eba\u5bb6\u7684\u5954\u9a70\u7ed9\u7838\u4e86\u7a00\u5df4\u70c2\uff0c\u73b0\u5728\u5230\u4eba\u5bb6\u7684\u5730\u76d8\uff0c\u8fd8\u4e0d\u8981\u7ed9\u4eba\u5bb6\u627e\u56de\u9762\u5b50\u53bb\uff1f\u6211\u600e\u4e48\u5c31\u5fd8\u4e86\u8fd9\u4e00\u70b9\uff1f\u201d</p><p>\u3000\u3000\u90a3\u8f86\u88ab\u7838\u7684\u5954\u7840\u73b0\u5728\u8fd8\u505c\u5728\u65b0\u6d77\u901a\u5927\u53a6\u7684\u505c\u8f66\u573a\u91cc\uff0c\u9526\u6e56\u4e0e\u76db\u946b\u7684\u4eba\u90fd\u5f53\u4f5c\u7b11\u8c08\uff0c\u6c5f\u9edb\u513f\u3001\u4ee4\u5c0f\u71d5\u3001\u8bb8\u7ef4\u5979\u4eec\u5f53\u7136\u4e0d\u77e5\u9053\uff1b\u5f20\u606a\u662f\u4e0d\u6562\u8ba9\u8bb8\u7ef4\u77e5\u9053\uff0c\u8981\u8ba9\u5979\u77e5\u9053\u7838\u8f66\u8ddf\u8bb8\u601d\u6709\u5173\uff0c\u4fdd\u4e0d\u5b9a\u5979\u4f1a\u8054\u60f3\u5230\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u201c\u5954\u9a70\uff0c\u53f6\u9614\u5c11\u90a3\u8f86\u65e0\u6bd4\u62c9\u98ce\u7684\u5954\u9a70\uff1f\u201d\u4ee4\u5c0f\u71d5\u8be7\u5f02\u7684\u8bf4\u3002</p><p>\u3000\u3000\u6562\u60c5\u53f6\u9e3f\u5f00\u7740\u5954\u9a70\u5230\u4e1c\u6d77\u5927\u5b66\u70ab\u8000\u8fc7\uff0c\u770b\u4ee4\u5c0f\u71d5\u63f6\u63c4\u7684\u8bed\u6c14\uff0c\u4ee5\u53ca\u8bb8\u7ef4\u7709\u773c\u8f7b\u76b1\u7684\u6a21\u6837\uff0c\u53ef\u89c1\u53f6\u9e3f\u6ca1\u7ed9\u5979\u4eec\u7559\u4e0b\u6765\u4ec0\u4e48\u597d\u5370\u8c61\u3002</p><p>\u3000\u3000\u53ea\u662f\u5979\u641e\u4e0d\u61c2\u5f20\u606a\u4e3a\u4ec0\u4e48\u4f1a\u8ddf\u53f6\u9e3f\u7ed3\u4e0b\u8fd9\u4e48\u6df1\u7684\u6069\u6028\uff0c\u7adf\u7136\u5230\u4e86\u7838\u8f66\u6bc1\u4eba\u7684\u5730\u6b65\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u53ea\u5f53\u662f\u5979\u7684\u7f18\u6545\uff0c\u4e00\u8138\u7684\u5185\u759a\u3002</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u7684VOLVO\u5c31\u505c\u5728\u505c\u8f66\u573a\u7684\u5165\u53e3\u5904\uff0c\u8981\u8bf4\u90a3\u8f86\u5954\u9a70\u88ab\u7838\u540e\u8fd8\u80fd\u503c\u90e8\u590f\u5229\u7684\u94b1\uff0c\u53f6\u5c0f\u6850\u7684\u8fd9\u8f86VOLVO\u5c31\u503c\u4e00\u5428\u5e9f\u94c1\u7684\u94b1\u4e86\uff0c\u5173\u952e\u56fd\u5185\u7684\u8f66\u8f86\u62a5\u5e9f\u5236\u5ea6\uff0c\u4f60\u8fd8\u4e0d\u80fd\u62ff\u8fd9\u8f66\u5f53\u5e9f\u94c1\u53bb\u5356\uff0c\u8ba9\u4eba\u770b\u4e86\u6000\u7591\u80fd\u4e0d\u80fd\u627e\u5230\u4e00\u4ef6\u5b8c\u6574\u7684\u96f6\u4ef6\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u5185\u759a\u7684\u8bf4\uff1a\u201c\u5bf9\u4e0d\u8d77\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u770b\u7740\u6c5f\u9edb\u513f\u5185\u759a\u7684\u6837\u5b50\uff0c\u5343\u5a07\u767e\u5a9a\uff0c\u5c31\u7b97\u5973\u4eba\u4e5f\u4f1a\u751f\u51fa\u4e0d\u751a\u601c\u60dc\u7684\u611f\u89c9\uff0c\u5c31\u7b97\u771f\u662f\u5979\u7684\u539f\u56e0\uff0c\u4e5f\u8ba9\u4eba\u4e0d\u5fcd\u5fc3\u8d23\u5907\u5979\uff0c\u4f55\u51b5\u90fd\u662f\u5f20\u606a\u5728\u8ddf\u8c22\u5251\u5357\u3001\u53f6\u9e3f\u4ed6\u4eec\u73a9\u5fc3\u773c\u3002\u521a\u8981\u5b89\u6170\u6c5f\u9edb\u513f\u4e24\u53e5\uff0c\u5374\u542c\u89c1\u5f20\u606a\u5c0f\u58f0\u8ddf\u5468\u6e38\u8bf4\uff1a\u201c\u7b49\u5230\u534a\u591c\u90fd\u6ca1\u542c\u89c1\u52a8\u9759\uff0c\u96be\u9053\u4ed6\u4eec\u5c06\u8f66\u62d6\u51fa\u53bb\u7838\u4e86\u518d\u9001\u56de\u6765\u793a\u5a01\uff1f\u201d</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u77aa\u7740\u5f20\u606a\u8bf4\uff1a\u201c\u4f60\u600e\u4e48\u4e0d\u77e2\u53e3\u5426\u8ba4\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6413\u6413\u624b\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8981\u662f\u6211\u7684\u5e15\u8428\u7279\u7ed9\u7838\u4e86\uff0c\u9762\u5b50\u7ed9\u4e22\u5149\u4e86\u4e0d\u8bf4\uff0c\u6211\u8fd8\u6ca1\u5904\u7533\u51a4\u53bb\uff0c\u522b\u4eba\u53ea\u4f1a\u8bf4\uff1a\u7b97\u4e86\u5427\uff0c\u4e0d\u8981\u518d\u4e89\u4ec0\u4e48\u610f\u6c14\u4e86\uff0c\u4f60\u8fd9\u7834\u8f66\uff0c\u4e03\u516b\u8f86\u624d\u62b5\u4eba\u5bb6\u4e00\u8f86\u5927\u5954\u3002\u773c\u4e0b\u5c31\u8981\u770b\u53f6\u5927\u5c0f\u59d0\u80fd\u4e0d\u80fd\u5fcd\u6c14\u541e\u58f0\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u90a3\u4e9b\u7834\u4e8b\uff0c\u4e3a\u4ec0\u4e48\u8981\u6211\u5e2e\u4f60\u51fa\u5934\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f97\uff0c\u8fd9\u4e8b\u7b97\u6211\u5934\u4e0a\uff0c\u6539\u5929\u8fd8\u4f60\u4e00\u8f86\u65b0\u8f66\uff0c\u201d\u5f20\u606a\u6beb\u4e0d\u4ecb\u610f\u7684\u8bf4\u9053\uff0c\u201c\u5230\u4eba\u5bb6\u7684\u5730\u76d8\uff0c\u603b\u8981\u7ed9\u4eba\u5bb6\u627e\u8fc7\u4e9b\u9762\u5b50\uff0c\u6ca1\u4e8b\u56de\u53bb\u5c31\u7761\u89c9\u5427\uff0c\u660e\u5929\u8fd8\u8981\u6709\u5176\u4ed6\u4e8b\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e8b\u5c31\u8fd9\u6837\u7b97\u4e86\uff1f\u201d\u53f6\u5c0f\u6850\u72d0\u7591\u7684\u76ef\u7740\u5f20\u606a\uff0c\u4ece\u4ed6\u7684\u773c\u775b\u91cc\u53ef\u770b\u4e0d\u51fa\u4ed6\u6709\u4e1d\u6beb\u7684\u5f02\u6837\uff0c\u597d\u50cf\u4ed6\u56de\u623f\u95f4\u4e00\u5934\u683d\u5e8a\u4e0a\u5c31\u80fd\u9a6c\u4e0a\u7761\u8fc7\u53bb\u4e00\u6837\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u8038\u8038\u80a9\uff0c\u671d\u6c5f\u9edb\u513f\u6487\u6487\u5634\u8bf4\uff1a\u201c\u770b\uff0c\u8fd9\u5c31\u662f\u7ea8\u7ed4\u5b50\u5f1f\u7684\u4f5c\u98ce\uff0c\u6362\u4f5c\u666e\u901a\u4eba\u4e89\u98ce\u5403\u918b\uff0c\u901a\u5e38\u4f1a\u7ea6\u51fa\u6765\u6253\u4e00\u67b6\uff0c\u4ed6\u4eec\u7adf\u7136\u76f8\u4e92\u7838\u8f66\u73a9\uff0c\u90fd\u8bf4\u7ea2\u989c\u7978\u6c34\uff0c\u8fd9\u53e5\u8bdd\u5012\u662f\u4e0d\u5047\uff0c\u77a7\u4e00\u77a7\uff0c\u56e0\u4e3a\u4f60\u8fd9\u7978\u6c34\u8981\u6bc1\u4e86\u591a\u5c11\u8f66\uff1f\u201d</p><p>\u3000\u3000\u201c\u80e1\u8bf4\u4ec0\u4e48\uff1f\u201d\u867d\u7136\u5f20\u606a\u6beb\u4e0d\u5728\u610f\uff0c\u6c5f\u9edb\u513f\u603b\u8ba4\u4e3a\u8fd9\u4e8b\u56e0\u5979\u800c\u8d77\uff0c\u5fc3\u91cc\u6ee1\u662f\u5185\u759a\uff0c\u5374\u4e0d\u77e5\u9053\u600e\u4e48\u529e\u624d\u597d\uff0c\u504f\u504f\u4ee4\u5c0f\u71d5\u8fd9\u65f6\u5019\u8fd8\u62ff\u8fd9\u4e8b\u8bf4\u7b11\uff0c\u66f4\u8ba9\u5979\u5fc3\u91cc\u96be\u53d7\uff0c\u60f3\u8d77\u53d1\u5e03\u4f1a\u4e0a\u65e0\u7aef\u7ed9\u53f6\u9e3f\u6307\u8d23\uff0c\u53c8\u4e0d\u6653\u5f97\u8ddf\u5bb6\u91cc\u600e\u4e48\u63d0\u8d77\u5e7f\u544a\u4ee3\u8a00\u7684\u4e8b\u60c5\uff0c\u6ee1\u5fc3\u7684\u59d4\u5c48\uff0c\u7ad9\u5728\u90a3\u91cc\u90fd\u60f3\u54ed\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u5230\u6c5f\u9edb\u513f\u660e\u7738\u542b\u7740\u6cea\u6c34\u6ceb\u7136\u6b32\u6ef4\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u59d3\u53f6\u7684\u8fd8\u6ca1\u6709\u80fd\u8010\u5c06\u8f66\u62d6\u51fa\u53bb\u7838\u70c2\u4e86\u518d\u9001\u56de\u6765\uff0c\u8fd9\u662f\u6211\u8ddf\u522b\u4eba\u4e4b\u95f4\u7684\u6069\u6028\u3002\u201d\u626d\u5934\u76ef\u7740\u5bbe\u9986\u91cc\u624d\u8d76\u6765\u7684\u7ecf\u7406\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u4eec\u5bbe\u9986\u7684\u4fdd\u5b89\u63aa\u65bd\u8fd8\u771f\u662f\u5468\u5230\u554a\uff0c\u6709\u6ca1\u6709\u62a5\u8b66\uff1f\u201d</p><p>\u3000\u3000\u5bbe\u9986\u7ecf\u7406\u8ba9\u4ed6\u76ef\u7740\u5fc3\u91cc\u53d1\u865a\uff0c\u4eff\u4f5b\u5fc3\u810f\u7ed9\u65e0\u6570\u652f\u8292\u5c16\u62b5\u7740\uff0c\u8bf4\u8bdd\u90fd\u4e0d\u6e9c\u987a\uff0c\u8bf4\u9053\uff1a\u201c\u6d3e\u51fa\u6240\u7684\u6c11\u8b66\u9a6c\u4e0a\u5c31\u5230\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6536\u655b\u8d77\u5484\u5484\u903c\u4eba\u7684\u76ee\u5149\uff0c\u8c22\u5251\u5357\u771f\u8981\u903c\u7740\u4ed6\u95ed\u7740\u773c\u775b\u4e0d\u7ba1\u95f2\u4e8b\uff0c\u4ed6\u8fd8\u771f\u4e0d\u6562\u7741\u5f00\u773c\u6765\uff0c\u6487\u6487\u5634\uff0c\u8bf4\u9053\uff1a\u201c\u6c11\u8b66\u6765\u4e86\uff0c\u4ed6\u4eec\u60f3\u600e\u4e48\u5904\u7f6e\u5c31\u600e\u4e48\u5904\u7f6e\uff0c\u4e0d\u8981\u6253\u6270\u6211\u4eec\u4f11\u606f\u5c31\u53ef\u4ee5\u4e86\u2026\u2026\u201d\u8bdd\u97f3\u6ca1\u843d\uff0c\u4eba\u5c31\u5f80\u5bbe\u9986\u91cc\u9762\u8d70\u3002</p><p>\u3000\u3000\u7559\u4e0b\u5176\u4ed6\u4eba\u9762\u9762\u76f8\u89d1\uff0c\u5468\u6e38\u95ee\u53f6\u5c0f\u6850\uff1a\u201c\u5c31\u8fd9\u6837\u7b97\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f20\u606a\uff0c\u201d\u53f6\u5c0f\u6850\u62ff\u5f20\u606a\u6ca1\u529e\u6cd5\uff0c\u770b\u6837\u5b50\u4ed6\u771f\u80fd\u5f53\u8fd9\u4ef6\u4e8b\u6ca1\u53d1\u751f\u8fc7\uff0c\u660e\u6446\u662f\u5f20\u606a\u7ed9\u81ea\u5df1\u8bbe\u4e86\u5708\u5957\uff0c\u773c\u770b\u7740\u8fd8\u6ca1\u6709\u529e\u6cd5\u4e0d\u8df3\u4e0b\u53bb\uff0c\u201c\u4f60\u8fd9\u6df7\u86cb\uff01\u4f60\u8bb0\u5f97\u6b20\u6211\u8fd9\u4e2a\u4eba\u60c5\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5934\u4e5f\u4e0d\u56de\uff0c\u6325\u4e86\u6325\u624b\uff1a\u201c\u4e0d\u6b20\uff0c\u8f66\u5b50\u53c8\u4e0d\u662f\u6211\u7838\u7684\u3002\u201d</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u5934\u4e5f\u4e0d\u56de\u7684\u8fdb\u4e86\u5bbe\u9986\u5927\u697c\u7684\u95e8\u5385\uff0c\u53f6\u5c0f\u6850\u6068\u5f97\u76f4\u8dfa\u811a\uff0c\u95ee\u5468\u6e38\uff1a\u201c\u5f20\u606a\u4ec0\u4e48\u65f6\u5019\u53d8\u5f97\u8fd9\u4e48\u65e0\u8d56\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u201d\u5468\u6e38\u644a\u624b\u7b11\u4e86\u7b11\uff1a\u201c\u8981\u6ca1\u4ec0\u4e48\u4e8b\uff0c\u6211\u4e5f\u60f3\u56de\u53bb\u7761\u89c9\u4e86\u3002\u201d</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u8f6c\u8eab\u77aa\u7740\u5bbe\u9986\u7684\u7ecf\u7406\uff0c\u5389\u58f0\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e0d\u6562\u5f97\u7f6a\u5468\u747e\u745c\u7684\u513f\u5b50\uff0c\u53ef\u4e5f\u4e0d\u8981\u4ee5\u4e3a\u6211\u4eec\u662f\u597d\u6b3a\u8d1f\u7684\u3002\u6d3e\u51fa\u6240\uff1f\u54ea\u91cc\u7684\u6d3e\u51fa\u6240\u6562\u7ba1\u5468\u747e\u745c\u7684\u513f\u5b50\uff1f\u201d\u6c14\u51b2\u51b2\u7684\u8d70\u8fdb\u95e8\u5385\uff0c\u770b\u89c1\u5f20\u606a\u95ea\u8eab\u8fdb\u4e86\u7535\u68af\uff0c\u7535\u68af\u95e8\u5408\u4e0a\u7684\u77ac\u95f4\uff0c\u4ed6\u671d\u81ea\u5df1\u6324\u773c\u775b\uff0c\u53f6\u5c0f\u6850\u54ed\u7b11\u4e0d\u5f97\uff0c\u60f3\u60f3\u8c22\u5251\u5357\u8fd8\u771f\u53ef\u601c\uff0c\u597d\u4e0d\u5bb9\u6613\u80fd\u5728\u81ea\u5df1\u7684\u5730\u76d8\u4e0a\u627e\u56de\u4e9b\u9762\u5b50\uff0c\u504f\u504f\u8fd8\u7838\u9519\u4e86\u8f66\u3002\u53f6\u5c0f\u6850\u62ff\u8d77\u670d\u52a1\u53f0\u7684\u7535\u8bdd\uff0c\u5389\u58f0\u95ee\u8d76\u4e0a\u6765\u7684\u5bbe\u9986\u7ecf\u7406\uff1a\u201c\u8fd9\u7535\u8bdd\u80fd\u4e0d\u80fd\u6253\u957f\u9014\uff1f\u201d\u8bd5\u62e8\u4e86\u4e00\u4e2a\u53f7\uff0c\u561f\u561f\u561f\u7684\u6ca1\u53cd\u5e94\u3002</p><p>\u3000\u3000\u5bbe\u9986\u7ecf\u7406\u6ee1\u8138\u6101\u5bb9\uff0c\u53f6\u5c0f\u6850\u8ba4\u51c6\u662f\u5468\u747e\u745c\u7684\u513f\u5b50\u7838\u7684\u8f66\uff0c\u8fd9\u65f6\u5019\u8fd8\u5f80\u5e02\u5916\u6253\u7535\u8bdd\uff0c\u4e0d\u7ba1\u8bf7\u8c01\u51fa\u9762\uff0c\u90fd\u4e0d\u662f\u4ed6\u8fd9\u5bb6\u5c0f\u5c0f\u7684\u5bbe\u9986\u80fd\u625b\u4e0b\u6765\u7684\uff0c\u8ba9\u53f6\u5c0f\u6850\u674f\u773c\u77aa\u5f97\u53d1\u865a\uff0c\u4e07\u7aef\u65e0\u5948\uff0c\u5927\u4f6c\u6390\u67b6\uff0c\u4ed6\u4eec\u8fd9\u4e9b\u5c0f\u55bd\u55bd\u6ce8\u5b9a\u53ea\u80fd\u5f53\u70ae\u7070\uff0c\u5c06\u624b\u673a\u501f\u7ed9\u5979\uff0c\u4e5f\u53ea\u80fd\u8d70\u4e00\u6b65\u770b\u4e00\u6b65\u3002</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u5148\u628a\u4ed6\u5802\u5144\u53f6\u5efa\u658c\u4ece\u7761\u68a6\u7740\u5435\u9192\uff0c\u53f6\u5efa\u658c\u63a5\u8fc7\u7535\u8bdd\u4e4d\u542c\u89c1\u5973\u4eba\u7684\u58f0\u97f3\uff0c\u5413\u4e86\u4e00\u6fc0\u7075\uff0c\u58f0\u97f3\u90fd\u6709\u53d1\u98a4\uff0c\u53f6\u5c0f\u6850\u89c9\u5bdf\u5230\uff0c\u6ca1\u597d\u6c14\u8bf4\uff1a\u201c\u53f6\u603b\u73b0\u5728\u8eba\u54ea\u4e2a\u5973\u4eba\u7684\u88ab\u7a9d\u91cc\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u662f\u4f60\u554a\uff0c\u5413\u6211\u4e00\u8df3\uff0c\u778e\u8bf4\u4ec0\u4e48\u2026\u2026\u5c0f\u6850\u95ee\u5019\u4f60\u5462\uff0c\u201d\u53f6\u5efa\u658c\u542c\u51fa\u53f6\u5c0f\u6850\u7684\u58f0\u97f3\uff0c\u7f13\u4e86\u4e00\u53e3\u6c14\uff0c\u540e\u9762\u4e00\u53e5\u662f\u5bf9\u8eab\u8fb9\u7684\u4e01\u6587\u6021\u8bf4\u7684\uff0c\u201c\u4ec0\u4e48\u4e8b\uff0c\u6df1\u66f4\u534a\u591c\u7684\u6253\u7535\u8bdd\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u5728\u60e0\u5c71\uff0c\u8f66\u5b50\u7ed9\u4eba\u5bb6\u7838\u4e86\uff0c\u5c31\u5728\u5bbe\u9986\u7684\u505c\u8f66\u573a\u91cc\uff0c\u4f60\u8bf4\u662f\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u53f6\u5efa\u658c\u6123\u4e86\u4e00\u4f1a\u513f\uff0c\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u8bf4\u591a\u5927\u7684\u4e8b\uff01\u5f20\u606a\u90a3\u8f86\u7834\u5e15\u8428\u7279\uff0c\u4e03\u516b\u8f86\u624d\u62b5\u4eba\u5bb6\u4e00\u8f86\u5927\u5954\uff0c\u6211\u8981\u7761\u89c9\u4e86\uff0c\u6709\u4ec0\u4e48\u4e8b\u660e\u5929\u518d\u8bf4\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u53f6\u5efa\u658c\u8fd9\u4e48\u8bf4\uff0c\u53f6\u5c0f\u6850\u6709\u4e9b\u53cd\u8499\uff0c\u53f6\u5efa\u658c\u5728\u90a3\u5934\u6302\u4e86\u7535\u8bdd\uff0c\u5979\u90fd\u6ca1\u6709\u53cd\u5e94\u8fc7\u6765\uff0c\u6562\u60c5\u6362\u4f5c\u5f20\u606a\u7684\u8f66\u7ed9\u7838\u4e86\u8fd8\u771f\u6ca1\u6709\u4eba\u4f1a\u8ba4\u771f\u7406\u776c\uff0c\u53ef\u662f\u73b0\u5728\u2026\u2026\u53f6\u5c0f\u6850\u7259\u9f88\u53c8\u75d2\u75d2\u4e86\uff0c\u7ee7\u7eed\u6253\u901a\u53f6\u5efa\u658c\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u201c\u53c8\u6709\u4ec0\u4e48\u4e8b\uff1f\u603b\u8981\u7ed9\u4eba\u5bb6\u627e\u56de\u70b9\u9762\u5b50\uff0c\u5f20\u606a\u8981\u662f\u60f3\u4e0d\u901a\uff0c\u4f60\u8ba9\u4ed6\u6765\u63a5\u7535\u8bdd\u3002\u201d</p><p>\u3000\u3000\u201c\u5f20\u606a\u90a3\u6df7\u86cb\u56de\u5bbe\u9986\u642d\u6211\u7684\u8f66\uff0c\u662f\u6211\u7684VOLVO\u7ed9\u7838\u4e86\uff01\u201d\u53f6\u5c0f\u6850\u51e0\u4e4e\u8981\u5bf9\u7535\u8bdd\u543c\u8d77\u6765\u2026\u2026</p><p>\u3000\u3000\u7535\u8bdd\u90a3\u5934\u6084\u7136\u65e0\u58f0\uff0c\u90fd\u65e0\u6cd5\u786e\u8ba4\u53f6\u5efa\u658c\u8fd8\u5728\u4e0d\u5728\u542c\uff0c\u7a81\u7136\u7535\u8bdd\u90a3\u5934\u7206\u51fa\u53f6\u5efa\u658c\u6291\u5236\u4e0d\u4f4f\u7684\u72c2\u7b11\uff1a\u201c\u5f20\u606a\u771f\u662f\u4e2a\u6df7\u86cb\uff0c\u5f20\u606a\u4eba\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u56de\u623f\u95f4\u7761\u89c9\u53bb\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ed6\u7761\u89c9\u53bb\u4e86\uff0c\u201d\u53f6\u5efa\u658c\u90fd\u89c9\u5f97\u81ea\u5df1\u7684\u7259\u9f88\u53d1\u75d2\u4e86\uff0c\u201c\u8fd9\u5bb6\u4f19\u771f\u53bb\u7761\u89c9\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u8fd9\u6df7\u86cb\u7684\u786e\u7761\u89c9\u53bb\u4e86\uff0c\u6211\u62ff\u4ed6\u6ca1\u6709\u529e\u6cd5\u3002\u201d\u53f6\u5c0f\u6850\u65e0\u529b\u7684\u8bf4\uff0c\u201c\u4f60\u8bf4\u600e\u4e48\u529e\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u5bb6\u4f19\uff0c\u201d\u53f6\u5efa\u658c\u4e5f\u53ea\u80fd\u65e0\u529b\u7684\u53f9\u7740\u6c14\uff0c\u201c\u867d\u7136\u8001\u7237\u8bf4\u5728\u5916\u9762\u8981\u4f4e\u8c03\uff0c\u4f46\u4e5f\u4e0d\u80fd\u8ba9\u522b\u4eba\u722c\u5230\u5934\u4e0a\u6765\u62c6\u9762\u5b50\uff0c\u4e5f\u6ca1\u6709\u591a\u5927\u7684\u4e8b\uff0c\u73b0\u5728\u6df1\u66f4\u534a\u591c\u7684\uff0c\u4f60\u4e0d\u8981\u6025\u7740\u60ca\u52a8\u8c01\uff0c\u5148\u8ba9\u4ed6\u4eec\u6298\u817e\u5427\u3002\u201d</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u5c06\u624b\u673a\u8fd8\u7ed9\u5bbe\u9986\u7ecf\u7406\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u5148\u7b49\u60e0\u5c71\u8b66\u65b9\u8fc7\u6765\u5904\u7406\u5427\u3002\u201d</p><p>\u3000\u3000\u90a3\u5bbe\u9986\u7ecf\u7406\u8fdc\u8fdc\u7ad9\u7740\uff0c\u770b\u7740\u53f6\u5c0f\u6850\u8138\u4e0a\u6302\u7740\u65e0\u5948\u7684\u8868\u60c5\uff0c\u8fd8\u4ee5\u4e3a\u5979\u627e\u7684\u4eba\u542c\u5230\u5468\u747e\u745c\u7684\u540d\u5b57\u4e0d\u613f\u610f\u51fa\u5934\uff0c\u63a5\u8fc7\u624b\u673a\uff0c\u795e\u60c5\u4e4b\u95f4\u514d\u4e0d\u4e86\u53c8\u6709\u4e9b\u8f7b\u6162\u3002</p><p>\u3000\u3000\u660e\u5929\u8fd8\u6709\u5f88\u591a\u5de5\u4f5c\uff0c\u53f6\u5c0f\u6850\u53ea\u8ba9\u5979\u7684\u52a9\u624b\u7559\u4e0b\u6765\u966a\u5979\uff0c\u8ba9\u5176\u4ed6\u4eba\u90fd\u56de\u623f\u95f4\u4f11\u606f\u53bb\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u5927\u6982\u4e8c\u5341\u5206\u949f\uff0c\u9644\u8fd1\u6d3e\u51fa\u6240\u7684\u6240\u957f\u9886\u7740\u4e24\u4e2a\u6c11\u8b66\u624d\u59d7\u59d7\u800c\u6765\uff0c\u5230\u505c\u8f66\u573a\u524d\u8f6c\u4e86\u4e00\u5708\uff0c\u5c31\u5bf9\u53f6\u5c0f\u6850\u8bf4\uff1a\u201c\u4f60\u8bf7\u8f86\u62d6\u8f66\uff0c\u628a\u8fd9\u8f66\u62d6\u5230\u6211\u4eec\u6240\u91cc\u5c31\u5e2e\u4f60\u7acb\u6848\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u7684\u5de5\u4f5c\u7a0b\u5e8f\u5c31\u662f\u8fd9\u6837\uff1f\u201d\u53f6\u5c0f\u6850\u8be7\u5f02\u7684\u95ee\uff0c\u201c\u4e0d\u518d\u4ed4\u7ec6\u52d8\u5bdf\u4e00\u4e0b\u73b0\u573a\u6216\u8bb8\u627e\u5bbe\u9986\u503c\u73ed\u4eba\u5458\u8be2\u95ee\u4e00\u4e0b\u6848\u4ef6\u53d1\u751f\u7684\u7ecf\u8fc7\uff1f\u201d</p><p>\u3000\u3000\u90a3\u540d\u6240\u957f\u7709\u5934\u626c\u4e86\u626c\uff0c\u77a5\u773c\u770b\u7740\u53f6\u5c0f\u6850\uff1a\u201c\u6211\u4eec\u600e\u4e48\u529e\u6848\uff0c\u8fd8\u8981\u4f60\u6765\u6307\u70b9\uff1f\u5feb\u53bb\u8bf7\u62d6\u8f66\uff0c\u5927\u534a\u591c\u7684\uff0c\u8c01\u6709\u5fc3\u60c5\u5e2e\u4f60\u4eec\u7acb\u6848\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u662f\u6211\u8bf7\u4e0d\u8d77\u62d6\u8f66\uff0c\u6d3e\u51fa\u6240\u5c31\u4e0d\u4e88\u7acb\u6848\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u5462\uff1f\u201d\u90a3\u540d\u6240\u957f\u5c06\u5377\u5b97\u4e00\u5408\uff0c\u6709\u4e9b\u4e0d\u8010\u70e6\u7684\u770b\u7740\u53f6\u5c0f\u6850\uff0c\u201c\u4f60\u8981\u8bf7\u4e0d\u8d77\u62d6\u8f66\uff0c\u8f66\u5b50\u4e0d\u62d6\u5230\u6211\u4eec\u6240\u91cc\u53bb\uff0c\u90a3\u5c31\u4e0d\u5177\u5907\u7acb\u6848\u6761\u4ef6\u2026\u2026\u201d\u4ed6\u5728\u6240\u91cc\u7b49\u4e86\u5f88\u4e45\u624d\u51fa\u8b66\uff0c\u5c31\u6015\u8fd9\u51e0\u4e2a\u5916\u4e61\u4eba\u6709\u4ec0\u4e48\u8fc7\u786c\u7684\u5173\u7cfb\uff0c\u90a3\u65f6\u5c31\u8981\u5c45\u4e2d\u8c03\u89e3\uff0c\u7b49\u5230\u73b0\u5728\u90fd\u6ca1\u6709\u7535\u8bdd\u6253\u8fdb\u6765\uff0c\u4ed6\u96be\u514d\u8981\u751f\u51fa\u8f7b\u6162\u4e4b\u5fc3\uff0c\u6b3a\u8d1f\u5916\u5730\u4eba\uff0c\u54ea\u4e2a\u5730\u65b9\u90fd\u6709\u8fd9\u901a\u75c5\u3002</p><p>\u3000\u3000\u201c\u90a3\u4f60\u4eec\u5c31\u770b\u7740\u529e\u5427\uff01\u201d\u53f6\u5c0f\u6850\u4e22\u4e0b\u4e00\u53e5\u8bdd\uff0c\u4e5f\u8d1f\u6c14\u56de\u5230\u623f\u95f4\uff0c\u8fd9\u65f6\u5019\u8ddf\u4ed6\u4eec\u7ea0\u7f20\u4e89\u8fa9\uff0c\u8fd8\u4e0d\u5982\u660e\u5929\u4e00\u4e2a\u7535\u8bdd\u723d\u5229\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u5728\u4e1a\u5185\u53ea\u80fd\u7b97\u9ed8\u9ed8\u65e0\u540d\u7684\u5c0f\u8f88\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u5e7f\u544a\u8fd8\u624d\u521a\u521a\u64ad\u653e\uff0c\u8c22\u5251\u5357\u4e0e\u53f6\u9e3f\u5374\u4ece\u6ca1\u6709\u770b\u7535\u89c6\u7684\u4e60\u60ef\uff0c\u8bf4\u4e0d\u4e00\u5b57\u4e0d\u82df\u4f1a\u987e\u5fcc\u4e00\u4e9b\u3002\u5176\u5b9e\u4e5f\u6028\u4e0d\u5f97\u8c22\u5251\u5357\u4f1a\u627e\u9519\u5bf9\u8c61\uff0c\u53d1\u5e03\u4f1a\u7684\u5ba3\u4f20\u8d44\u6599\u91cc\u53ea\u63d0\u5230\u7231\u8fbe\u7535\u5b50\u4e0e\u60e0\u5c71\u5f53\u5730\u7684\u7ecf\u9500\u5546\u4ee5\u53ca\u7ec4\u7ec7\u65b9\u98de\u9e3f\u5e7f\u544a\u516c\u53f8\uff0c\u5f53\u770b\u5230\u5f20\u606a\u94bb\u8fdbVOLVO\u8f66\u91cc\uff0c\u800c\u4e14VOLVO\u662f\u4ed6\u4eec\u90a3\u51e0\u90e8\u8f66\u4e2d\u6863\u6b21\u6700\u9ad8\u7684\uff0c\u8c22\u5251\u5357\u60f3\u5f53\u7136\u7684\u5c06\u90a3\u8f86\u8f66\u770b\u6210\u5f20\u606a\u7684\u5ea7\u9a7e\u3002</p><p>\u3000\u3000\u5c31\u50cf\u8c22\u5251\u5357\u5728\u6d77\u5dde\u5403\u4e86\u54d1\u5df4\u4e8f\u4e00\u6837\uff0c\u8c22\u5251\u5357\u94c1\u5fc3\u8981\u8ba9\u5f20\u606a\u5728\u60e0\u5c71\u4e5f\u5403\u8fd9\u54d1\u5df4\u4e8f\uff0c\u4e5f\u4e0d\u6015\u5f90\u5b66\u5e73\u4f1a\u4e3a\u8fd9\u70b9\u5c0f\u4e8b\u5c31\u51fa\u9762\u8892\u62a4\u5f20\u606a\uff0c\u54ea\u91cc\u6709\u60f3\u5230\u90a3\u8f86VOLVO\u6839\u672c\u5c31\u4e0d\u662f\u5f20\u606a\u7684\u8f66\uff1f</p><p>\u3000\u3000\u6309\u7167\u539f\u6765\u7684\u8ba1\u5212\uff0c\u5f20\u606a\u4e0e\u5468\u6e38\u7559\u5728\u60e0\u5c71\u63a8\u52a8\u540e\u7eed\u7684\u5e02\u573a\u63a8\u5e7f\u6d3b\u52a8\uff0c\u53f6\u5c0f\u6850\u5219\u7387\u9886\u76db\u946b\u7684\u56e2\u961f\u8f6c\u6218\u5176\u4ed6\u57ce\u5e02\uff0c\u4e89\u53d6\u5341\u5929\u65f6\u95f4\u5185\uff0c\u5c06\u7701\u5185\u5176\u4ed6\u57ce\u5e02\u8d70\u4e00\u904d\u3002\u95f9\u51fa\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u53f6\u5c0f\u6850\u53ea\u5f97\u7559\u4e0b\u6765\u914d\u5408\u5f20\u606a\uff0c\u8ba9\u5468\u6e38\u9886\u7740\u4eba\u4e0e\u98de\u9e3f\u5e7f\u544a\u516c\u53f8\u7684\u4eba\u5148\u53bb\u5176\u4ed6\u57ce\u5e02\u505a\u5e02\u573a\u3002</p><p>\u3000\u3000\u5f20\u606a\u9192\u6765\u4e4b\u540e\uff0c\u4e0d\u7740\u6025\u51fa\u95e8\uff0c\u8eba\u5728\u5e8a\u5934\u770b\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u65e9\u95f4\u65b0\u95fb\uff0c\u4e00\u76f4\u5230\u8bb8\u7ef4\u4e0e\u4ee4\u5c0f\u71d5\u8fc7\u6765\u6572\u95e8\u5524\u4ed6\u3002</p><p>\u3000\u3000\u8bb8\u7ef4\u7684\u8138\u8272\u4e0d\u597d\u770b\uff0c\u62c5\u5fe7\u7684\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u9edb\u513f\u5979\u7238\u7238\u627e\u8fc7\u6765\uff0c\u9edb\u513f\u505a\u5e7f\u544a\u4ee3\u8a00\u7684\u4e8b\u60c5\uff0c\u538b\u6839\u5c31\u6ca1\u6709\u8ddf\u5979\u5bb6\u91cc\u63d0\u8d77\u8fc7\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u60f3\u8d77\u90a3\u4e2a\u6e29\u6587\u5112\u96c5\u7684\u4e2d\u5e74\u4eba\uff0c\u770b\u7740\u53f6\u5c0f\u6850\u4ece\u9694\u58c1\u623f\u95f4\u91cc\u63a2\u51fa\u5934\u6765\uff0c\u95ee\u5979\uff1a\u201c\u8f66\u5b50\u7684\u4e8b\u60c5\uff0c\u6628\u5929\u665a\u4e0a\u662f\u600e\u4e48\u5904\u7406\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5148\u7ba1\u597d\u4f60\u7684\u4e8b\u60c5\u5427\uff0c\u5bb6\u957f\u90fd\u627e\u4e0a\u95e8\u6765\u4e86\u3002\u201d\u53f6\u5c0f\u6850\u6ca1\u597d\u6c14\u7684\u6a2a\u773c\u770b\u7740\u4ed6\uff0c\u4e00\u4f1a\u513f\u628a\u5934\u7f29\u4e86\u56de\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u542c\u5979\u8bf4\u8d77\u6765\u597d\u50cf\u81ea\u5df1\u62d0\u9a97\u826f\u5bb6\u5c11\u5973\u4f3c\u7684\uff0c\u8bb8\u7ef4\u544a\u8bc9\u6628\u5929\u60e0\u5c71\u8b66\u65b9\u6765\u4e86\u4e4b\u540e\u5374\u62d2\u7edd\u7acb\u6848\uff0c\u5f20\u606a\u51b7\u51b7\u7684\u4e00\u7b11\uff1a\u201c\u60e0\u5c71\u5e02\u8b66\u65b9\u8981\u80fd\u628a\u9762\u5b50\u4e0a\u7684\u5de5\u592b\u505a\u8db3\uff0c\u8fd9\u79cd\u627e\u4e0d\u5230\u4eba\u8bc1\u7269\u8bc1\u7684\u4e8b\u60c5\uff0c\u8bf4\u4e0d\u5b9a\u4e5f\u5c31\u548c\u7a00\u6ce5\u548c\u6389\u4e86\uff0c\u4ed6\u4eec\u6446\u660e\u628a\u6211\u4eec\u5f53\u6210\u5916\u4e61\u4eba\u6b3a\u8d1f\uff0c\u90a3\u5c31\u591f\u4ed6\u4eec\u597d\u53d7\u7684\uff01\u201d\u77e5\u9053\u53f6\u5bb6\u6709\u5bf9\u4ed8\u8fd9\u79cd\u4e8b\u60c5\u7684\u624b\u6bb5\uff0c\u800c\u4e14\u60e0\u5c71\u5e02\u8b66\u65b9\u5982\u6b64\u660e\u76ee\u5f20\u80c6\u7684\u4e0d\u4f5c\u4e3a\uff0c\u7b49\u4e8e\u5c06\u628a\u67c4\u4ea4\u5230\u53f6\u5bb6\u624b\u91cc\uff0c\u8fd9\u79cd\u4e8b\u6839\u672c\u5c31\u4e0d\u7528\u4ed6\u62c5\u5fc3\u4e86\uff0c\u8c22\u5251\u5357\u5c31\u7b49\u7740\u7ed9\u6572\u7af9\u6760\u5427\u3002</p></article>"}}}]);
