(window.webpackJsonp=window.webpackJsonp||[]).push([[201],{224:function(p){p.exports={chapterTitle:null,title:"\u7b2c1176\u7ae0 \u6e29\u6cc9\u6e56\u5b09\u620f",content:"<article><h2>\u7b2c1176\u7ae0 \u6e29\u6cc9\u6e56\u5b09\u620f</h2><p>\u3000\u3000\u65b0\u829c\u590f\u5b63\u7684\u5929\u6c14\u51c9\u723d\uff0c\u5728\u5e02\u91cc\u9732\u5929\u6e38\u6cf3\u6c60\u6e38\u6cf3\u8fd8\u6709\u4e9b\u51c9\uff0c\u5929\u4e91\u5c71\u4e0a\u7684\u6e29\u6cc9\u6e56\u9664\u4e86\u51ac\u5929\u4e0d\u9002\u5408\u6e38\u6cf3\u4e4b\u5916\uff0c\u6625\u590f\u79cb\u4e09\u5b63\u90fd\u5dee\u4e0d\u591a\u6052\u5b9a\u5728\u6700\u9002\u5b9c\u7684\u6c34\u6e29\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5b59\u9759\u6aac\u6765\u5230\u5c71\u4e0a\uff0c\u4e70\u4e86\u6cf3\u8863\u5750\u8239\u5230\u6e56\u5fc3\u5c9b\uff0c\u60f3\u8ddf\u5b59\u9759\u6aac\u8d56\u5728\u4e00\u4e2a\u623f\u95f4\u91cc\u6362\u8863\u670d\uff0c\u5374\u7ed9\u8fde\u8e22\u5e26\u6253\u7684\u8d76\u4e86\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5148\u6362\u597d\u6cf3\u88e4\u5750\u5728\u6c34\u53f0\u4e0a\u7b49\u5b59\u9759\u6aac\u51fa\u6765\uff0c\u811a\u4f38\u5728\u6e29\u70ed\u7684\u6e56\u6c34\u91cc\u6643\u8361\uff0c\u5728\u8fdc\u5904\uff0c\u5728\u6e29\u6cc9\u6e56\u8fd1\u5cb8\u6709\u597d\u4e9b\u4eba\u5728\u6e38\u6cf3\uff0c\u6c34\u9762\u8fbd\u9614\uff0c\u4e5f\u6709\u5c11\u6570\u4eba\u5728\u5c1d\u8bd5\u7740\u6a2a\u7a7f\u6574\u4e2a\u6e56\u9762\u3002\u5b59\u9759\u6aac\u88f9\u7740\u5927\u6d74\u5dfe\u8d70\u4e86\u51fa\u6765\uff0c\u5979\u7684\u8eab\u6750\u5e76\u4e0d\u5341\u5206\u9ad8\u6311\uff0c\u81f3\u5c11\u5728\u90a3\u7fa4\u8df3\u821e\u7684\u5973\u5b69\u5b50\u5f53\u4e2d\uff0c\u8981\u7b97\u662f\u8eab\u6750\u5a07\u5c0f\u7684\uff0c\u6d74\u5dfe\u4e0b\u9732\u51fa\u7684\u53cc\u817f\u7ea4\u957f\u5706\u6da6\uff0c\u53cc\u817f\u5408\u5e76\u8d77\u6765\u4e0d\u9732\u4e00\u4e1d\u7f1d\u9699\uff0c\u808c\u80a4\u96ea\u767d\u4f3c\u8102\u3002\u5f20\u606a\u772f\u773c\u770b\u7740\uff0c\u4ed6\u89c9\u5f97\u5c0f\u59ae\u5b50\u9664\u4e86\u90a3\u5f20\u7cbe\u81f4\u8ddf\u5996\u9b45\u4f3c\u7684\u8138\u86cb\u4e4b\u4e2d\uff0c\u5c31\u8fd9\u53cc\u817f\u5374\u8ff7\u4eba\u3002</p><p>\u3000\u3000\u201c\u770b\u4e0d\u778e\u4f60\u7684\u773c\u775b\uff0c\u8fd8\u770b\uff1f\u201d\u5b59\u9759\u6aac\u5fae\u55d4\u7684\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u6328\u7740\u4ed6\u7684\u8eab\u5b50\u8981\u5750\u4e0b\u6765\uff0c\u5374\u731b\u7684\u4e00\u628a\u5c06\u5f20\u606a\u63a8\u6c34\u91cc\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u51b7\u4e0d\u53ca\u9632\uff0c\u683d\u6c34\u91cc\u8fd8\u545b\u4e86\u4e00\u53e3\u6c34\uff0c\u5934\u9732\u51fa\u6c34\u9762\uff0c\u770b\u7740\u5b59\u9759\u6aac\u7ad9\u5728\u6c34\u53f0\u4e0a\u7b11\u5f97\u5f88\u6b22\u4e50\u3002</p><p>\u3000\u3000\u4e0b\u5348\u56db\u70b9\u949f\uff0c\u592a\u9633\u5e76\u4e0d\u7099\u70c8\uff0c\u6e56\u6c34\u4eff\u4f5b\u67d3\u4e86\u6d53\u7fe0\u4e00\u6837\uff0c\u6e56\u5149\u8000\u5728\u4eba\u8138\u4e0a\u4e5f\u71a0\u71a0\u751f\u8f89\uff0c\u5f20\u606a\u5728\u6e56\u6c34\u91cc\u6e38\u4e86\u4e00\u5708\uff0c\u89c1\u5b59\u9759\u6aac\u8fd8\u4e0d\u4e0b\u6c34\u6765\uff0c\u6e38\u5230\u5979\u8eab\u8fb9\uff0c\u53cc\u624b\u6401\u5728\u5979\u5ae9\u6ed1\u7684\u5927\u817f\u4e0a\uff0c\u95ee\u5979\uff1a\u201c\u600e\u4e48\u4e0d\u4e0b\u6c34\uff1f\u201d</p><p>\u3000\u3000\u201c\u770b\u7740\u4f60\u6e38\u6cf3\u4e5f\u597d\u554a\u2026\u2026\u201d\u5b59\u9759\u6aac\u4fcf\u76ae\u7684\u5c06\u53cc\u817f\u6401\u5728\u5f20\u606a\u7684\u80a9\u8180\u4e0a\uff0c\u5939\u7740\u4ed6\u7684\u8116\u5b50\uff0c\u811a\u540e\u8ddf\u8e6d\u5728\u4ed6\u7684\u8170\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u8138\u988a\u8d34\u5728\u5979\u7ec6\u6ed1\u7684\u5927\u817f\u4e0a\uff0c\u62ac\u8d77\u5934\u5f80\u4e0a\u770b\uff0c\u770b\u7740\u5979\u5929\u84dd\u8272\u7684\u6cf3\u8863\u5c06\u633a\u7fd8\u7684\u80f8\u90e8\u515c\u5f97\u6d51\u5706\u5b8c\u7f8e\uff0c\u80fd\u770b\u51fa\u5979\u91cc\u9762\u6ca1\u6709\u518d\u7a7f\u6234\u4ec0\u4e48\uff0c\u60c5\u4e0d\u81ea\u7981\u7684\u53cc\u624b\u6402\u8fc7\u5979\u5f39\u6027\u60ca\u4eba\u7684\u8170\u81c0\u3002</p><p>\u3000\u3000\u201c\u770b\u4ec0\u4e48\u770b\uff1f\u201d\u5b59\u9759\u6aac\u5c06\u6d74\u5dfe\u62ab\u7d27\u4e86\u4e00\u4e9b\uff0c\u53cc\u817f\u5939\u7d27\u60f3\u628a\u5f20\u606a\u7684\u80a9\u8180\u5f80\u4e0b\u538b\uff0c\u60f3\u8ba9\u4ed6\u8001\u5b9e\u4e00\u4e9b\u773c\u775b\u4e0d\u8981\u76ef\u7740\u5979\u7684\u80f8\u90e8\u770b\uff0c\u5374\u4e0d\u6599\u8fd9\u4e2a\u52a8\u4f5c\u5c06\u5f20\u606a\u7684\u9f3b\u5c16\u76f4\u63a5\u538b\u5728\u81ea\u5df1\u67d4\u8f6f\u7684\u5c0f\u8179\u4e0a\uff0c\u611f\u89c9\u5f88\u8212\u670d\uff0c\u4e5f\u6ca1\u6709\u8ba9\u5f00\uff0c\u6ca1\u60f3\u5230\u63a5\u4e0b\u6765\u5c31\u611f\u89c9\u5230\u4e00\u6837\u67d4\u8f6f\u7684\u4e1c\u897f\u9694\u7740\u6cf3\u88e4\u62b5\u5728\u4e24\u817f\u4e4b\u95f4\u8214\u5f04\u5f97\u90a3\u91cc\u9165\u9ebb\u5f02\u5e38\uff0c\u626f\u7740\u5f20\u606a\u7684\u8033\u6735\uff0c\u60f3\u5c06\u4ed6\u7684\u5934\u5f80\u5916\u62c9\uff0c\u5f20\u606a\u5374\u6402\u7d27\u5979\u7684\u8170\u4e0d\u677e\u5f00\uff0c\u5b59\u9759\u6aac\u53c8\u820d\u4e0d\u5f97\u7528\u529b\u626f\u4ed6\u7684\u8033\u6735\uff0c\u62b1\u7740\u4ed6\u7684\u5934\u731b\u7684\u5f80\u6c34\u91cc\u5012\u53bb\uff0c\u6251\u817e\u51fa\u4e00\u7247\u6c34\u82b1\u3002</p><p>\u3000\u3000\u5f20\u606a\u731d\u4e0d\u53ca\u9632\uff0c\u8116\u5b50\u53c8\u7ed9\u5b59\u9759\u6aac\u7684\u53cc\u817f\u5939\u7740\uff0c\u6574\u4e2a\u4eba\u7ed9\u538b\u5728\u6c34\u91cc\uff0c\u95ed\u4e0d\u4e86\u591a\u4e45\u7684\u6c14\uff0c\u5c31\u6323\u624e\u7740\u5934\u94bb\u51fa\u6c34\u9762\uff0c\u8fd9\u65f6\u5019\u5b59\u9759\u6aac\u5df2\u7ecf\u50cf\u6761\u7f8e\u4eba\u9c7c\u4f3c\u7684\u5f80\u8fdc\u5904\u6e38\u53bb\uff0c\u5f20\u606a\u722c\u5230\u6c34\u53f0\u4e0a\u5750\u7740\u4f11\u606f\uff0c\u770b\u7740\u9759\u6aac\u5728\u6e05\u6f88\u7684\u6e56\u6c34\u91cc\u6e38\u52a8\uff0c\u81c0\u90e8\u9971\u6ee1\u4e30\u5f39\uff0c\u7ea4\u8170\u67d4\u8f6f\uff0c\u4fee\u957f\u7684\u53cc\u817f\u5939\u7740\u6c34\uff0c\u52a8\u4f5c\u4f18\u7f8e\uff0c\u4f53\u6001\u8ff7\u4eba\uff0c\u6e38\u5230\u8fdc\u5904\uff0c\u624d\u505c\u5728\u6c34\u91cc\uff0c\u53cc\u811a\u8e29\u7740\u6c34\uff0c\u671d\u5f20\u606a\u558a\u9053\uff1a\u201c\u56f4\u7740\u5c0f\u5c9b\u6e38\u4e00\u5708\uff0c\u4f60\u8981\u662f\u6e38\u8fc7\u6211\uff0c\u4f60\u505a\u4ec0\u4e48\u4e8b\u60c5\u90fd\u7b54\u5e94\u4f60\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u771f\u7684\uff1f\u201d\u5f20\u606a\u7ad9\u8d77\u6765\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8fd9\u91cc\u5c31\u6211\u4eec\u4e24\u4e2a\u4eba\uff0c\u6211\u60f3\u9a97\u4f60\u4e5f\u884c\u554a\uff1f\u201d\u5b59\u9759\u6aac\u5728\u6c34\u91cc\u6446\u4e86\u6446\u5934\u3002</p><p>\u3000\u3000\u201c\u9a97\u6211\u5c31\u5f3a\u5978\u4f60\u3002\u201d\u5f20\u606a\u53ef\u4e0d\u7ba1\u771f\u5047\uff0c\u4e00\u4e2a\u731b\u5b50\u624e\u8fdb\u6c34\u91cc\uff0c\u5b59\u9759\u6aac\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u4e2a\u65e0\u8d56\uff0c\u6211\u8fd8\u6ca1\u6709\u5f00\u59cb\u5462\u2026\u2026\u201d\u5634\u91cc\u7b11\u9a82\u7740\uff0c\u8eab\u4f53\u50cf\u7f8e\u4eba\u9c7c\u4eba\u4f3c\u7684\u6e38\u52a8\u8d77\u6765\uff0c\u8fd8\u662f\u5728\u5f20\u606a\u524d\u9762\u4e00\u5927\u622a\u3002</p><p>\u3000\u3000\u5c0f\u5c9b\u4e0d\u5927\uff0c\u56f4\u7740\u5c0f\u5c9b\u51b2\u523a\u6027\u7684\u6e38\u4e00\u5708\uff0c\u4e5f\u7d2f\u5f97\u591f\u545b\uff0c\u5173\u952e\u5f20\u606a\u7d2f\u5f97\u8ddf\u53ea\u72d7\u4f3c\u7684\u722c\u4e0a\u6c34\u53f0\uff0c\u5b59\u9759\u6aac\u5df2\u7ecf\u7ad9\u5728\u6c34\u53f0\u4e0a\u62ff\u6bdb\u5dfe\u64e6\u5934\u53d1\uff0c\u5632\u7b11\u4ed6\u8bf4\uff1a\u201c\u4f60\u8fd8\u6709\u529b\u6c14\u5f3a\u5978\u6211\u4e0d\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u731b\u7684\u4e00\u8de8\u5c06\u9759\u6aac\u7684\u624b\u8155\u6293\u4f4f\uff0c\u5c06\u5979\u5a07\u5c0f\u8ff7\u4eba\u7684\u8eab\u5b50\u6402\u6000\u91cc\uff0c\u6441\u4f4f\u5979\u7684\u53cc\u81c2\u8ba9\u5979\u65e0\u529b\u53cd\u6297\uff0c\u4eb2\u543b\u7740\u5979\u67d4\u8f6f\u7684\u5634\u5507\uff0c\u53c8\u543b\u7740\u5979\u79c0\u7f8e\u7684\u4e0b\u5df4\u4e0e\u6676\u83b9\u7389\u6da6\u7684\u7389\u9888\uff0c\u543b\u5f97\u5979\u6c14\u5598\u5401\u5401\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u53ea\u662f\u6293\u4f4f\u81ea\u5df1\u7684\u6cf3\u8863\uff0c\u4e0d\u8ba9\u5f20\u606a\u517d\u6027\u5927\u53d1\u7684\u6252\u4e0b\u6765\uff0c\u6e56\u91cc\u8fd8\u6709\u5176\u4ed6\u4eba\u5728\u6e38\u6cf3\uff0c\u867d\u7136\u9694\u5f97\u8fdc\uff0c\u8981\u662f\u6cf3\u8863\u7ed9\u6252\u4e0b\u6765\uff0c\u8fd8\u662f\u4f1a\u8d70\u5149\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u8fdb\u53bb\u597d\u4e0d\u597d\uff1f\u201d\u5f20\u606a\u5728\u9759\u6aac\u8033\u7554\u5583\u5583\u8f7b\u8bed\uff0c\u820c\u5c16\u8fd8\u987a\u5e26\u7740\u8214\u5f04\u7740\u5979\u6676\u83b9\u5254\u900f\u7684\u8033\u5ed3\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u5b59\u9759\u6aac\u6d51\u8eab\u9165\u8f6f\uff0c\u51e0\u4e4e\u8981\u762b\u5728\u5f20\u606a\u7684\u6000\u91cc\uff0c\u53cc\u624b\u6402\u7740\u4ed6\u7684\u8116\u5b50\uff0c\u8bf4\u9053\uff0c\u201c\u4f46\u4e0d\u8bb8\u4f60\u5f3a\u5978\u6211\u2026\u2026\u201d\u58f0\u97f3\u5a07\u67d4\u751c\u5a9a\uff0c\u8ba9\u5f20\u606a\u9aa8\u5b50\u90fd\u786c\u4e86\u4e09\u5206\uff0c\u4e24\u4eba\u5c0f\u8179\u5c31\u9694\u7740\u8584\u8584\u7684\u6cf3\u8863\u8d34\u5728\u4e00\u8d77\uff0c\u9759\u6aac\u81ea\u7136\u80fd\u611f\u89c9\uff0c\u4f38\u624b\u6293\u4f4f\u90a3\u6839\u50f5\u76f4\u5927\u5f97\u5413\u4eba\u7684\u4e1c\u897f\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u8981\u5f3a\u5978\u6211\uff0c\u522b\u4eba\u4f1a\u770b\u51fa\u6765\u7684\uff0c\u6211\u7528\u624b\u5e2e\u4f60\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e0d\u884c\uff0c\u4f60\u5f97\u7528\u8fd9\u4e2a\u3002\u201d\u5f20\u606a\u7259\u9f84\u54ac\u7740\u5b59\u9759\u6aac\u7ea2\u6da6\u7684\u5507\u5b50\uff0c\u4e0d\u4f9d\u4e0d\u6320\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5b59\u9759\u6aac\u624b\u7528\u529b\u5728\u90a3\u6839\u786c\u4e1c\u897f\u4e0a\u6390\u4e86\u4e00\u4e0b\uff0c\u5012\u4e5f\u6ca1\u8bf4\u4e0d\u540c\u610f\uff0c\u5c31\u7ed9\u5f20\u606a\u62b1\u8fdb\u4e86\u6728\u5c4b\u91cc\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7f20\u7ef5\u8bb8\u4e45\uff0c\u76f4\u5230\u5915\u9633\u5782\u7167\u5728\u6e29\u6cc9\u6e56\u4e0a\uff0c\u5f20\u606a\u624d\u8ddf\u9759\u6aac\u5750\u8239\u8fd4\u56de\u5cb8\u4e0a\u3002\u5728\u9a73\u8239\u4e0a\uff0c\u5f20\u606a\u8f7b\u6402\u7740\u9759\u6aac\u7ea4\u8f6f\u7684\u5c0f\u8170\uff0c\u770b\u7740\u5c71\u5c16\u8ff7\u4eba\u7684\u5915\u9633\u5149\uff0c\u5728\u5979\u8033\u7554\u8f7b\u58f0\u7684\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u771f\u6b63\u7684\u505a\u4e00\u56de\uff1f\u201d</p><p>\u3000\u3000\u201c\u521a\u624d\u4f60\u8981\u771f\u5f3a\u5978\u6211\uff0c\u6211\u4e5f\u6ca1\u6709\u610f\u89c1\u554a\uff0c\u201d\u9759\u6aac\u72e1\u9ee0\u7684\u7b11\u4e86\u7b11\uff0c\u53c8\u610f\u5473\u6df1\u957f\u7684\u8bf4\u4e86\u4e00\u53e5\uff0c\u201c\u90a3\u5c31\u7b49\u4ee5\u540e\u55bd\uff01\u4ee5\u540e\u53ef\u80fd\u4f1a\u662f\u5f88\u4e45\u4e4b\u540e\u7684\u4ee5\u540e\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u63ea\u7740\u5934\u53d1\uff0c\u505a\u51fa\u75db\u82e6\u7684\u8981\u5c06\u5934\u5f80\u8237\u67f1\u4e0a\u649e\u7684\u6837\u5b50\uff0c\u9759\u6aac\u5a07\u7b11\u7740\u8dd1\u5230\u522b\u5904\u53bb\u4e0d\u518d\u7ba1\u5f20\u606a\u3002</p><p>\u3000\u3000\u591c\u91cc\uff0c\u5f20\u606a\u8ba9\u9648\u5983\u84c9\u966a\u4ed6\u7238\u5988\u4e00\u8d77\u5230\u5c71\u4e0a\u6765\u5403\u996d\u3002\u5728\u5403\u665a\u996d\u65f6\uff0c\u5b59\u9759\u6aac\u624d\u4e00\u6b63\u672c\u7ecf\u7684\u5728\u9910\u684c\u4e0a\u8c08\u8d77\u5979\u5173\u4e8e\u9009\u79c0\u8282\u76ee\u300a\u660e\u661f\u68a6\u5de5\u5382\u300b\u7684\u6784\u60f3\uff0c\u5979\u5bf9\u8fd9\u65b9\u9762\u8003\u8651\u8fc7\u5f88\u957f\u65f6\u95f4\u4e86\uff0c\u60f3\u6cd5\u4e5f\u6bd4\u8f83\u5168\u9762\uff0c\u597d\u50cf\u5979\u8ddf\u5f20\u606a\u4e0b\u5348\u5728\u5c71\u4e0a\u5c31\u5728\u8ba8\u8bba\u8fd9\u4e2a\u95ee\u9898\uff0c\u6ca1\u6709\u5e72\u522b\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u53f0\u6e7e\u3001\u65b0\u52a0\u5761\u7b49\u6d77\u5916\u5730\u533a\u5728\u9009\u900f\u8282\u76ee\u4e0a\u6709\u60a0\u957f\u7684\u5386\u53f2\u4f20\u7edf\uff0c\u5b59\u9759\u6aac\u5230\u65b0\u52a0\u5761\u4e4b\u540e\u63a5\u89e6\u76f8\u5173\u7684\u8282\u76ee\u5236\u4f5c\uff0c\u6bd4\u8d77\u56fd\u5185\u90a3\u79cd\u523b\u677f\u3001\u6ca1\u6709\u4ec0\u4e48\u751f\u673a\u7684\u827a\u4eba\u57f9\u517b\u4e0e\u6210\u957f\u8def\u7ebf\uff0c\u5979\u5c31\u60f3\u7740\u5728\u5185\u5730\u5236\u4f5c\u4e00\u6863\u878d\u5408\u9009\u79c0\u3001\u620f\u5267\u4ee5\u53ca\u771f\u4eba\u5b9e\u5883\u79c0\u7684\u957f\u5468\u671f\u7535\u89c6\u8282\u76ee\u2026\u2026</p><p>\u3000\u3000\u201c\u5185\u5730\u867d\u7136\u6709\u592e\u89c6\u7684\u9752\u6625\u6b4c\u624b\u5927\u5956\u8d5b\u4e4b\u7c7b\u6b4c\u5531\u7ade\u8d5b\u8282\u76ee\uff0c\u8282\u76ee\u5355\u5143\u8bbe\u5236\u8fc7\u4e8e\u5355\u4e00\uff0c\u5a31\u4e50\u6027\u3001\u60c5\u5883\u6027\u3001\u7efc\u827a\u6027\u3001\u65f6\u5c1a\u6027\u90fd\u8fdc\u8fdc\u4e0d\u591f\u5438\u5f15\u5f53\u4ee3\u7684\u9752\u5e74\u4eba\uff0c\u5236\u4f5c\u5355\u4f4d\u7684\u8bbe\u8ba1\u6a21\u5f0f\uff0c\u8bb2\u7a76\u706f\u5149\u7684\u8868\u6f14\u573a\u5730\u3001\u5bf9\u53c2\u8d5b\u8005\u7684\u4e13\u4e1a\u9020\u578b\u8bbe\u8ba1\u4ee5\u53ca\u4e8b\u5148\u5f69\u6392\u3001\u540e\u671f\u526a\u63a5\u3001\u4e50\u56e2\u4f34\u594f\u7b49\u7b49\u5236\u4f5c\u7ec6\u8282\u4e0a\u90fd\u4e0d\u5982\u4eba\u610f\u2026\u2026\u201d\u5b59\u9759\u6aac\u63cf\u8ff0\u7740\u5979\u5bf9\u9009\u79c0\u8282\u76ee\u7684\u8ba4\u8bc6\u4e0e\u671f\u5f85\uff0c\u8bf4\u5b8c\u53c8\u626d\u5934\u95ee\u5f20\u606a\uff0c\u201c\u4f60\u89c9\u5f97\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u7684\u610f\u89c1\u662f\u6ca1\u610f\u89c1\uff0c\u5b59\u59d0\u7684\u610f\u89c1\uff1f\u201d\u5f20\u606a\u644a\u644a\u624b\uff0c\u4e0d\u7f6e\u53ef\u5426\uff0c\u8ba9\u8fd9\u59ae\u5b50\u4e0b\u5348\u6ca1\u8ba9\u81ea\u5df1\u5f97\u901e\u3002</p><p>\u3000\u3000\u201c\u6211\u59d0\u5f53\u7136\u652f\u6301\u6211\u55bd\uff0c\u201d\u5b59\u9759\u6aac\u8bf4\u9053\uff0c\u201c\u53ea\u662f\u771f\u8981\u8fd0\u4f5c\uff0c\u8fd8\u8981\u4ece\u53f0\u6e7e\u6216\u65b0\u52a0\u5761\u8058\u8bf7\u6709\u7ecf\u9a8c\u7684\u5236\u4f5c\u4eba\uff0c\u5c31\u6015\u5185\u5730\u7684\u8282\u76ee\u5ba1\u67e5\u5236\u5ea6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7efc\u827a\u8282\u76ee\u53ea\u8981\u4e0d\u6d89\u53ca\u653f\u6cbb\uff0c\u5176\u4ed6\u65b9\u9762\u90fd\u53ef\u4ee5\u5bbd\u677e\u4e00\u4e9b\uff0c\u4e0d\u7528\u62c5\u5fc3\u8fd9\u4e2a\u538b\u529b\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u56fd\u5185\u73b0\u5728\u4e5f\u6b63\u5c1d\u8bd5\u7740\u6a21\u4eff\u6d77\u5916\u505a\u4e00\u4e9b\u5a31\u4e50\u6027\u5f3a\u7684\u7efc\u827a\u8282\u76ee\uff0c\u53ea\u662f\u56fd\u5185\u7535\u89c6\u8282\u76ee\u5236\u4f5c\u7684\u5ba1\u67e5\u5236\u5ea6\u8fc7\u4e8e\u4fdd\u5b88\uff0c\u4f7f\u5f97\u56fd\u5185\u7684\u7efc\u827a\u8282\u76ee\u96be\u6709\u6d77\u5916\u7684\u8f7b\u677e\u6d3b\u6cfc\uff0c\u6709\u4e9b\u5546\u4e1a\u6027\u7efc\u827a\u8282\u76ee\u53c8\u5bb9\u6613\u8d70\u5411\u8fc7\u4e8e\u5546\u4e1a\u6027\u7684\u6781\u7aef\uff0c\u8fc7\u5ea6\u7684\u5eb8\u4fd7\u5316\uff0c\u5728\u5ba1\u67e5\u4e0e\u76c8\u5229\u4e0a\uff0c\u5f20\u606a\u76f8\u4fe1\u4e16\u7eaa\u534e\u5a31\u65e0\u9700\u627f\u5f53\u592a\u5927\u7684\u538b\u529b\uff0c\u4e0d\u8fc7\u4ed6\u62c5\u5fc3\u73b0\u4ee3\u793e\u4f1a\u7684\u5ba1\u4e11\u610f\u8bc6\u7a7a\u524d\u81a8\u80c0\uff0c\u4f1a\u4f7f\u5f97\u4e00\u4e9b\u6709\u6096\u4e8e\u5e38\u4eba\u5ba1\u7f8e\u89c2\u5ff5\u7684\u827a\u4eba\u5f02\u519b\u7a81\u8d77\uff0c\u4ed6\u53ef\u5b8c\u5168\u6ca1\u6276\u6301\u8fd9\u7c7b\u827a\u4eba\u5927\u7ea2\u5927\u7d2b\u7684\u6b32\u671b\u3002\u4e16\u7eaa\u534e\u5a31\u8ddf\u5185\u5730\u7684\u7535\u89c6\u53f0\u5408\u4f5c\u5236\u4f5c\u7efc\u827a\u9009\u79c0\u8282\u76ee\uff0c\u4e3b\u8981\u4e0d\u662f\u6d3b\u8dc3\u5185\u5730\u7684\u7535\u89c6\u5e02\u573a\uff0c\u4e00\u662f\u53ef\u4ee5\u901a\u8fc7\u900f\u79c0\u8282\u76ee\u6269\u5927\u4e16\u7eaa\u534e\u5a31\u7684\u5f71\u54cd\uff0c\u518d\u4e2a\u53ef\u4ee5\u901a\u8fc7\u9009\u79c0\u8282\u76ee\u6765\u6316\u6398\u6709\u53d1\u5c55\u6f5c\u529b\u7684\u827a\u4eba\u3002</p><p>\u3000\u3000\u201c\u8282\u76ee\u5236\u4f5c\u76f4\u63a5\u8bf7\u9526\u6e56\u7684\u6210\u5458\u4f01\u4e1a\u505a\u54c1\u724c\u8d5e\u52a9\u5546\u884c\u4e0d\u884c\uff1f\u201d\u5b59\u9759\u6aac\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u8981\u6709\u505a\u6210\u529f\u7684\u4fe1\u5fc3\uff0c\u8d5e\u52a9\u91d1\u603b\u662f\u4e0d\u7f3a\u7684\u2014\u2014\u7231\u8fbe\u4eca\u5e74\u5728\u56fd\u5185\u7684\u5e7f\u544a\u9884\u7b97\u5c31\u6709\u5341\u4e94\u4ebf\uff0c\u4ece\u5927\u817f\u62d4\u6839\u6c57\u6bdb\u5c31\u591f\u4f60\u62b1\u7740\u8d70\u4e86\uff0c\u201d\u5f20\u606a\u8146\u8138\u7b11\u7740\u8bf4\uff0c\u201c\u8fd8\u6709\u9999\u96ea\u6d77\u3001\u76db\u946b\u3001\u65b0\u5149\u4e09\u4e2a\u54c1\u724c\uff0c\u770b\u4f60\u4eec\u6709\u4fe1\u5fc3\u8ddf\u54ea\u4e2a\u4f01\u4e1a\u5408\u4f5c\u4e86\u2026\u2026\u201d\u9526\u6e56\u65d7\u4e0b\u8fd0\u8425\u56db\u4e2a\u54c1\u724c\uff0c\u5e7f\u544a\u6295\u653e\u529b\u5ea6\u4e0e\u6295\u653e\u5bf9\u8c61\u90fd\u6709\u4e0d\u540c\u7684\u4fa7\u91cd\u9762\uff0c\u5bf9\u7efc\u827a\u9009\u79c0\u7c7b\u8282\u76ee\u7684\u8d5e\u52a9\u8d44\u91d1\u529b\u5ea6\u4e5f\u6709\u6240\u4e0d\u540c\uff0c\u8981\u60f3\u62ff\u6700\u591a\u7684\u8d5e\u52a9\u91d1\uff0c\u81ea\u7136\u662f\u7231\u8fbe\u5f53\u54c1\u724c\u8d5e\u52a9\u5546\uff0c\u8fd9\u5f53\u7136\u4e5f\u9700\u8981\u7efc\u827a\u9009\u79c0\u8282\u76ee\u7684\u5f71\u54cd\u529b\u80fd\u8fbe\u5230\u7231\u8fbe\u6240\u8981\u6c42\u7684\u7a0b\u5ea6\u624d\u884c\u3002</p><p>\u3000\u3000\u73b0\u5728\u9526\u6e56\u65d7\u4e0b\u5404\u6210\u5458\u4f01\u4e1a\u90fd\u8fdb\u884c\u5355\u72ec\u7684\u8d22\u52a1\u6838\u7b97\uff0c\u8d44\u6e90\u6574\u5408\u5229\u7528\u662f\u4e00\u56de\u4e8b\uff0c\u4f46\u662f\u4e5f\u8981\u9632\u6b62\u8fc7\u5ea6\u7684\u4f9d\u8d56\u5185\u90e8\u8d44\u6e90\u4ece\u800c\u524a\u5f31\u5bf9\u5916\u9762\u7684\u7ade\u4e89\u529b\uff0c\u7279\u522b\u662f\u96c6\u56e2\u65e5\u76ca\u5e9e\u5927\u4e4b\u540e\uff0c\u4f1a\u6709\u5b98\u50da\u5316\u7684\u503e\u5411\uff0c\u5f20\u606a\u5f00\u59cb\u91cd\u89c6\u63d0\u5021\u5185\u90e8\u7ade\u4e89\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u4e0d\u5c51\u7684\u55c5\u4e86\u55c5\u9f3b\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u7b49\u6211\u5c06\u7b56\u5212\u4e66\u62ff\u51fa\u6765\uff0c\u4f60\u5c31\u77e5\u9053\u8c01\u6c42\u8c01\u4e86\uff01\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u3001\u6881\u683c\u73cd\u5728\u4e00\u65c1\u7b11\u800c\u4e0d\u8bed\uff0c\u5f85\u5f20\u606a\u4e0e\u5b59\u9759\u6aac\u6597\u5b8c\u5634\uff0c\u9648\u5983\u84c9\u624d\u8bf4\u8d77\u5510\u5a67\u5979\u5988\u987e\u5efa\u840d\u5f97\u4e86\u611f\u5192\uff0c\u5510\u5a67\u8981\u7559\u5728\u91d1\u5c71\u966a\u5979\u5988\u5988\uff0c\u540e\u5929\u518d\u4e00\u8d77\u53bb\u9999\u6e2f\u3002</p><p>\u3000\u3000\u5510\u5a67\u4e0d\u80fd\u5230\u65b0\u829c\u6765\uff0c\u5b59\u9759\u6aac\u5374\u4e5f\u4e0d\u7ed9\u5f20\u606a\u5355\u72ec\u63a5\u89e6\u7684\u673a\u4f1a\uff0c\u5230\u4e86\u9999\u6e2f\u53c2\u52a0\u65b0\u5149\u7eb8\u4e1a\u6210\u529f\u4e0a\u5e02\u7684\u5e86\u795d\u9152\u4f1a\uff0c\u5f20\u606a\u4e00\u662f\u6709\u5510\u5a67\u770b\u7740\uff0c\u518d\u8bf4\u5b59\u9759\u6aac\u56de\u9999\u6e2f\u5c31\u4f4f\u56de\u5bb6\u91cc\uff0c\u4ed6\u66f4\u662f\u627e\u4e0d\u5230\u505a\u8d3c\u4e86\u673a\u4f1a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u53d7\u7f51\u7edc\u6ce1\u6cab\u5371\u673a\u5f71\u54cd\uff0c\u9999\u6e2f\u8bc1\u5238\u4ea4\u6613\u5e02\u573a\u8fd1\u65e5\u4e5f\u9677\u5165\u840e\u9761\u4e4b\u4e2d\uff0c\u65b0\u5149\u7eb8\u4e1a\u4e0a\u5e02\u5374\u6ca1\u6709\u53d7\u591a\u5927\u7684\u5f71\u54cd\uff0c\u516c\u5f00\u4e0a\u5e02\u5f53\u5929\u7684\u6536\u76d8\u4ef7\u76f8\u6bd4\u53d1\u884c\u4ef7\u4e0a\u6da8\u4e86\u7ea615\uff05\uff0c\u6210\u4e3a\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u5f53\u5929\u5c11\u6570\u7684\u4eae\u70b9\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u901a\u8fc7\u4e5d\u516b\u5e74\u6765\u6536\u8d2d\u91d1\u5c71\u9020\u7eb8\u5382\u7b49\u4e00\u7cfb\u5217\u7684\u8d44\u4ea7\u4e0e\u5e02\u573a\u6574\u5408\u8fd0\u4f5c\uff0c\u65b0\u5149\u7eb8\u4e1a\u7684\u9020\u7eb8\u5e74\u4ea7\u91cf\u4eca\u5e74\u9884\u8ba1\u8d85\u8fc7\u4e09\u767e\u4e07\u5428\u3001\u5e74\u9500\u552e\u989d\u7a81\u7834\u4e00\u767e\u516d\u5341\u4ebf\u3001\u5e74\u51c0\u5229\u6da6\u8fbe\u5230\u5341\u4e94\u4ebf\u3001\u5458\u5de5\u603b\u4eba\u6570\u8d85\u8fc7\u4e00\u4e07\u516b\u5343\u4eba\uff0c\u5f53\u4e4b\u65e0\u6127\u7684\u6210\u4e3a\u56fd\u5185\u9020\u7eb8\u884c\u4e1a\u7684\u9f99\u5934\u4f01\u4e1a\uff0c\u5e74\u4ea7\u91cf\u4e0e\u5e74\u9500\u552e\u989d\u4ee5\u53ca\u5e74\u51c0\u5229\u6da6\u989d\u90fd\u8981\u8d85\u8fc7\u7b2c\u4e8c\u3001\u7b2c\u4e09\u540d\u7684\u603b\u548c\uff0c\u4f46\u662f\u5168\u7403\u7eb8\u4e1a\u5de8\u5934\u76f8\u6bd4\uff0c\u65b0\u5149\u7eb8\u4e1a\u7684\u5b9e\u529b\u8fd8\u5f88\u5f31\u5c0f\uff0c\u5e74\u4ea7\u91cf\u3001\u9500\u552e\u989d\u90fd\u53ea\u6709\u5168\u7403\u6392\u540d\u7b2c\u5341\u7684\u7eb8\u4e1a\u96c6\u56e2\u60e0\u597d\u516c\u53f8\u7684\u4e09\u5206\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u901a\u8fc7\u65b0\u5149\u7eb8\u4e1a\u7684\u4e0a\u5e02\uff0c\u76f4\u63a5\u516d\u5341\u516d\u4ebf\u6e2f\u5143\u7684\u8d44\u91d1\uff0c\u9664\u4e86\u5341\u516d\u4ebf\u6e2f\u5143\u6ce8\u5165\u65b0\u5149\u6797\u4e1a\u4e4b\u5916\uff0c\u5269\u4f59\u7684\u4e94\u5341\u516d\u4ebf\u6e2f\u5143\uff0c\u518d\u4ece\u56fd\u5bb6\u5f00\u53d1\u94f6\u884c\u501f\u8d37\u4e8c\u5341\u4ebf\uff0c\u5411\u9526\u6e56\u5546\u4e8b\u53d1\u884c\u4e8c\u5341\u4ebf\u5143\u7684\u516c\u53f8\u503a\uff0c\u51d1\u8db3\u4e5d\u5341\u4ebf\u7528\u4e8e\u542f\u52a8\u5236\u6d46\u9879\u76ee\uff0c\u8fd9\u4e5f\u662f\u5185\u5730\u8fc4\u4eca\u4e3a\u6b62\u6295\u8d44\u6700\u5927\u7684\u5236\u6868\u9879\u76ee\uff0c\u5efa\u6210\u540e\u5c06\u5e74\u4ea7\u4e00\u767e\u4e07\u5428\u4f18\u8d28\u6728\u6d46\u3002</p><p>\u3000\u3000\u5236\u6d46\u9879\u76ee\u7684\u5efa\u8bbe\u5468\u671f\u662f\u4e09\u5e74\uff0c\u5c4a\u65f6\u65b0\u5149\u6797\u4e1a\u7684\u7b2c\u4e00\u6279\u901f\u751f\u6797\u4e5f\u5230\u4e86\u53ef\u4ee5\u8f6e\u4f10\u7684\u6210\u719f\u671f\u3002</p><p>\u3000\u3000\u65b0\u5149\u6797\u4e1a\u901f\u751f\u6797\u80b2\u6797\u9762\u79ef\u5df2\u7ecf\u591a\u8fbe\u4e03\u767e\u4e07\u4ea9\uff0c\u6839\u636e\u8f6e\u4f10\u5468\u671f\uff0c\u6bcf\u5e74\u53ef\u4f9b\u4e24\u767e\u4e07\u5428\u7ea7\u7684\u5236\u6d46\u9879\u76ee\u6240\u7528\uff0c\u800c\u65b0\u5149\u7eb8\u4e1a\u4eca\u5e74\u9664\u4e86\u91c7\u7528\u5927\u91cf\u7684\u5e9f\u7eb8\u6d46\u4e4b\u5916\uff0c\u8fd8\u5c06\u8fdb\u53e3\u4e24\u767e\u4e07\u5428\u6728\u6d46\uff0c\u8981\u586b\u8865\u4e0a\u4e0b\u6e38\u7684\u7a7a\u7f3a\uff0c\u7b2c\u4e00\u4e2a\u5236\u6d46\u9879\u76ee\u5b8c\u6210\u4e4b\u540e\u5c31\u9700\u8981\u7acb\u5373\u5728\u91d1\u5c71\u542f\u52a8\u7b2c\u4e8c\u4e2a\u767e\u4e07\u5428\u7ea7\u7684\u5236\u6d46\u9879\u76ee\uff0c\u6240\u9700\u8d44\u91d1\u53c8\u662f\u767e\u4ebf\u3002</p><p>\u3000\u3000\u65b0\u5149\u6797\u4e1a\u7684\u80b2\u6797\u9762\u79ef\u5df2\u7ecf\u8fbe\u5230\u4e03\u767e\u4e07\u4ea9\uff0c\u80b2\u6797\u3001\u6797\u5730\u79df\u91d1\u3001\u4eba\u5458\u85aa\u8d44\u7b49\u7efc\u5408\u6210\u672c\u6bcf\u5e74\u5c31\u9ad8\u8fbe\u5341\u4ebf\u5143\u4e4b\u5de8\uff0c\u8fd9\u6b21\u5411\u65b0\u5149\u6797\u4e1a\u6295\u5165\u5341\u516d\u4ebf\u6e2f\u5143\u7684\u8d44\u91d1\uff0c\u4e5f\u4ec5\u4ec5\u591f\u65e5\u5e38\u7ef4\u6301\u6240\u7528\u3002\u60f3\u8981\u8fdb\u4e00\u6b65\u7684\u6269\u5f20\uff0c\u5c31\u9700\u8981\u6295\u5165\u66f4\u5927\u91cf\u7684\u8d44\u91d1\uff1b\u8981\u4e48\u5c31\u8981\u7b49\u5230\u901f\u751f\u6797\u8fdb\u5165\u6210\u719f\u671f\u6709\u4e86\u8d62\u5229\u624d\u80fd\u8fdb\u884c\u4e0b\u4e00\u6b65\u7684\u6269\u5f20\u3002</p><p>\u3000\u3000\u65e2\u7136\u65b0\u5149\u6797\u4e1a\u90fd\u5728\u9999\u6e2f\u4e0a\u5e02\u4e86\uff0c\u8d44\u91d1\u7684\u95ee\u9898\u5c31\u8981\u5c3d\u53ef\u80fd\u7684\u901a\u8fc7\u516c\u5f00\u5e02\u573a\u8fdb\u884c\u52df\u96c6\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u7684\u6210\u529f\u4e0a\u5e02\uff0c\u8981\u7b97\u662f\u9ad8\u76db\u4e9a\u6d32\u516c\u53f8\u526f\u603b\u88c1\u66fe\u5b50\u8f89\u7684\u5f97\u610f\u4e4b\u4f5c\u3002\u5728\u534a\u5c9b\u9152\u5e97\u9876\u697c\u5bb4\u4f1a\u5385\u91cc\uff0c\u66fe\u5b50\u8f89\u7a7f\u7740\u6574\u6d01\u7684\u886c\u886b\uff0c\u62ff\u7740\u7ea2\u9152\u676f\uff0c\u4e0e\u65b0\u5149\u7eb8\u4e1a\u8463\u4e8b\u5c40\u4e3b\u5e2d\u517c\u603b\u88c1\u5468\u6e38\u4ee5\u53ca\u65b0\u5149\u7eb8\u4e1a\u8463\u4e8b\u3001\u9a6c\u6765\u897f\u4e9a\u4e91\u6e90\u96c6\u56e2\u603b\u88c1\u90ed\u677e\u5ef6\u7ed9\u4f17\u4eba\u7fa4\u661f\u62f1\u6708\u4f3c\u7684\u56f4\u5728\u4e2d\u95f4\uff0c\u706f\u5149\u8ff7\u79bb\uff0c\u9152\u676f\u4e2d\u7684\u8165\u7ea2\u8ff7\u4e3d\uff0c\u4ed6\u4eec\u4e09\u4e2a\u4eba\u770b\u5230\u5f20\u606a\u8ddf\u5510\u5a67\u8d70\u8fdb\u6765\uff0c\u5fd9\u5206\u5f00\u4f17\u4eba\u8fce\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u6211\u8ddf\u66fe\u603b\u8ba8\u8bba\uff0c\u65b0\u5149\u7eb8\u4e1a\u8981\u60f3\u6709\u5927\u7684\u53d1\u5c55\uff0c\u8fd8\u9700\u8981\u4e24\u767e\u4ebf\u7684\u8d44\u91d1\u3002\u201d\u5468\u6e38\u7b11\u7740\u8ddf\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u66fe\u603b\u4e5f\u7ed9\u89e3\u51b3\u8fd9\u4e24\u767e\u4ebf\u8d44\u91d1\u95ee\u9898\u8bbe\u8ba1\u4e86\u4e00\u4e2a\u5927\u6982\u7684\u878d\u8d44\u8def\u94b1\uff1a\u8fd9\u6b21\u65b0\u5149\u7eb8\u4e1a\u6210\u529f\u4e0a\u5e02\u4e86\uff0c\u53ef\u901a\u8fc7\u5411\u5404\u6301\u80a1\u516c\u53f8\u5b9a\u5411\u53d1\u884c\u65b0\u80a1\u7684\u5f62\u5f0f\u4ece\u6536\u8d2d\u65b0\u5149\u6797\u4e1a\u7684\u80a1\u6743\uff0c\u5c06\u65b0\u5149\u6797\u4e1a\u5f7b\u5e95\u7684\u53d8\u6210\u4e0a\u5e02\u516c\u53f8\u4e0b\u5c5e\u7684\u72ec\u8d44\u5206\u516c\u53f8\uff0c\u7136\u800c\u518d\u516c\u5f00\u901a\u8fc7\u8bc1\u5238\u5e02\u573a\u589e\u53d1\u65b0\u80a1\u52df\u96c6\u8d44\u91d1\u2014\u2014\u6301\u80a1\u516c\u53f8\u901a\u8fc7\u8bc1\u5238\u5e02\u573a\u51cf\u6301\u65b0\u5149\u7eb8\u4e1a\u7684\u80a1\u7968\uff0c\u7b79\u96c6\u8d44\u91d1\u7528\u4e8e\u5176\u9886\u57df\u7684\u6295\u8d44\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u66fe\u603b\u7684\u60f3\u6cd5\u603b\u662f\u8ba9\u4eba\u6709\u8c41\u7136\u5f00\u6717\u7684\u611f\u89c9\uff0c\u4f60\u4eec\u8981\u597d\u597d\u8003\u8651\u4e00\u4e0b\u66fe\u603b\u7684\u5efa\u8bae\u3002\u201d\u5f20\u606a\u968f\u610f\u8ddf\u5468\u6e38\u8bf4\u9053\uff0c\u4ece\u7ecf\u8fc7\u7684\u4f8d\u5e94\u751f\u90a3\u91cc\u62ff\u6765\u4e24\u676f\u7ea2\u9152\uff0c\u5206\u4e86\u4e00\u676f\u7ed9\u5510\u5a67\uff0c\u8ddf\u66fe\u5b50\u8f89\u3001\u90ed\u677e\u5ef6\u656c\u9152\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u5df2\u7ecf\u901a\u8fc7\u8bc1\u5238\u5e02\u573a\u5206\u5f00\u878d\u5f97\u516d\u5341\u516d\u4ebf\u6e2f\u5143\u7684\u8d44\u91d1\uff0c\u8fd8\u60f3\u8fdb\u4e00\u6b65\u878d\u5f97\u4e24\u767e\u4ebf\u7684\u5de8\u8d44\uff0c\u4e0d\u662f\u4e00\u5e74\u4e24\u5e74\u80fd\u5b8c\u6210\u7684\u4efb\u52a1\uff0c\u53ea\u80fd\u4f5c\u4e3a\u65b0\u5149\u7eb8\u4e1a\u7684\u4e00\u4e2a\u8d44\u672c\u6218\u7565\u65b9\u5411\u53bb\u52aa\u529b\uff0c\u4e0d\u80fd\u5b8c\u5168\u7684\u5c06\u5e0c\u671b\u5bc4\u6258\u5728\u8fd9\u4e0a\u9762\u3002</p><p>\u3000\u3000\u5f85\u5236\u6d46\u9879\u76ee\u5efa\u6210\uff0c\u5c31\u7b97\u662f\u521d\u6b65\u5b8c\u6210\u6797\u7eb8\u4e00\u4f53\u5316\u5e03\u5c40\uff0c\u4ea7\u4e1a\u6a21\u5f0f\u53ca\u53d1\u5c55\u8d8a\u662f\u5b8c\u6574\u3001\u6210\u719f\uff0c\u878d\u8d44\u7684\u963b\u529b\u8d8a\u5c0f\u3002\u4e2d\u56fd\u662f\u56fd\u6c11\u50a8\u84c4\u5927\u56fd\uff0c\u56fd\u6c11\u50a8\u84c4\u7387\u9ad8\u5f97\u60ca\u4eba\uff0c\u518d\u8fc7\u4e09\u4e94\u5e74\uff0c\u56fd\u5185\u8d44\u672c\u5145\u88d5\u4e4b\u540e\uff0c\u50cf\u65b0\u5149\u7eb8\u4e1a\u62e5\u6709\u7a33\u5b9a\u76c8\u5229\u53ca\u6210\u719f\u4ea7\u4e1a\u6a21\u5f0f\u7684\u4f01\u4e1a\uff0c\u8d44\u91d1\u6765\u6e90\u5374\u4e0d\u7528\u592a\u6101\u2014\u2014\u8fd9\u65f6\u5019\u5374\u65e0\u6cd5\u8ddf\u56fd\u6709\u4f01\u4e1a\u5728\u56fd\u5185\u53bb\u4e89\u91d1\u878d\u8d44\u6e90\u3002</p><p>\u3000\u3000\u867d\u7136\u8fd9\u4e24\u5e74\u9a6c\u6765\u897f\u4e9a\u7ecf\u6d4e\u5f00\u59cb\u590d\u82cf\uff0c\u4f46\u662f\u53d1\u5c55\u901f\u5ea6\u4ee5\u53ca\u5e02\u573a\u6f5c\u529b\u8ddf\u5185\u5730\u8fd8\u662f\u65e0\u6cd5\u76f8\u6bd4\uff0c\u90ed\u677e\u5ef6\u8fd8\u60f3\u5927\u5e45\u5ea6\u7684\u5411\u5185\u5730\u8ffd\u52a0\u6295\u8d44\u3002</p><p>\u3000\u3000\u9526\u6e56\u65d7\u4e0b\u7231\u8fbe\u96c6\u56e2\u4e0e\u9526\u6e56\u5546\u4e8b\u4e24\u5927\u6838\u5fc3\u4f01\u4e1a\u5bf9\u4ed6\u4eec\u5f00\u7684\u95e8\u7f1d\u5f88\u5c0f\uff0c\u4f46\u662f\u65b0\u5149\u7eb8\u4e1a\u3001\u65b0\u5149\u6797\u4e1a\u3001\u9999\u96ea\u6d77\u3001\u4e16\u7eaa\u9526\u6e56\u3001\u76db\u946b\u7535\u5668\u3001\u76db\u946b\u7535\u8baf\u8fd9\u4e9b\u516c\u53f8\u672c\u8eab\u5c31\u4e00\u76f4\u5728\u52aa\u529b\u8c0b\u6c42\u516c\u5f00\u4e0a\u5e02\uff0c\u5bf9\u90ed\u677e\u5ef6\u8fd9\u4e9b\u4e1c\u5357\u4e9a\u534e\u5546\u7684\u5408\u4f5c\u6295\u8d44\u81ea\u7136\u5c31\u4e0d\u4f1a\u6709\u591a\u5927\u7684\u9650\u5236\u3002</p><p>\u3000\u3000\u90ed\u6c0f\u8ddf\u9526\u6e56\u8fd9\u51e0\u5e74\u7684\u5408\u4f5c\u76f8\u5f53\u6109\u5feb\uff0c\u8fd9\u51e0\u5e74\u6765\u5411\u65b0\u5149\u7eb8\u4e1a\u6ce8\u5165\u8d44\u91d1\u3001\u56fa\u5b9a\u8d44\u4ea7\u7b49\u603b\u8ba1\u6709\u5341\u4e94\u4ebf\u5143\uff0c\u4e0a\u5e02\u540e\u6301\u6709\u65b0\u5149\u7eb8\u4e1a20\uff05\u7684\u80a1\u6743\uff0c\u5e02\u503c\u9ad8\u8fbe\u4e94\u5341\u4ebf\u6e2f\u5143\u3002</p><p>\u3000\u3000\u4e91\u6e90\u96c6\u56e2\u65d7\u4e0b\u7684\u6838\u5fc3\u4e1a\u52a1\u5c31\u6709\u5236\u6d46\u9020\u7eb8\uff0c\u90ed\u677e\u5ef6\u5fc3\u91cc\u5f53\u7136\u6e05\u695a\u65b0\u5149\u7eb8\u4e1a\u5728\u5927\u9646\u6797\u7eb8\u4e00\u4f53\u5316\u4e0a\u5360\u636e\u7684\u5148\u673a\u6709\u591a\u9ad8\u7684\u4ef7\u503c\uff0c\u81f3\u5c11\u6bd4\u5176\u4ed6\u7eb8\u4e1a\u5de8\u5934\u5728\u4e2d\u56fd\u5e02\u573a\u591a\u8d62\u5f97\u56db\u5230\u4e94\u5e74\u7684\u65f6\u95f4\uff0c\u4ed6\u975e\u4f46\u6ca1\u6709\u51cf\u6301\u65b0\u5149\u7eb8\u4e1a\u80a1\u7968\u83b7\u5229\u7684\u5fc3\u601d\uff0c\u53cd\u800c\u6709\u7ee7\u7eed\u8ffd\u52a0\u6295\u8d44\u7684\u6253\u7b97\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u4e0d\u662f\u7f3a\u8d44\u91d1\u5417\uff1f\u4e91\u6e90\u96c6\u56e2\u6bcf\u5e74\u603b\u80fd\u62bd\u51fa\u4e00\u4e24\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\u51fa\u6765\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u90ed\u677e\u5ef6\u90fd\u8ddf\u5f20\u606a\u3001\u5b59\u5c1a\u4e49\u79c1\u4e0b\u91cc\u6709\u4ea4\u6d41\uff0c\u5012\u4e0d\u4f1a\u5728\u66fe\u5b50\u8f89\u9762\u524d\u91cd\u63d0\uff0c\u8ddf\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u9526\u6e56\u5546\u4e8b\u65d7\u4e0b\u5df2\u7ecf\u6709\u4e09\u5bb6\u4e0a\u5e02\u516c\u53f8\uff0c\u4ec0\u4e48\u65f6\u5019\u4f1a\u6709\u7b2c\u56db\u5bb6\u3001\u7b2c\u4e94\u5bb6\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u8fd8\u8981\u770b\u66fe\u603b\u600e\u4e48\u5b89\u6392\u5de5\u4f5c\u8fdb\u5ea6\u4e86\u3002\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u6709\u89c9\u5f97\u6211\u4e0d\u5728\u5c3d\u529b\u5417\uff1f\u201d\u66fe\u5b50\u8f89\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u65e0\u8bba\u9999\u96ea\u6d77\u8fd8\u662f\u76db\u946b\u7535\u5668\uff0c\u90fd\u5df2\u7ecf\u5177\u5907\u516c\u5f00\u4e0a\u5e02\u7684\u6761\u4ef6\u4e86\uff0c\u66fe\u5b50\u8f89\u751a\u81f3\u77e5\u9053\u9526\u6e56\u524d\u6bb5\u65f6\u95f4\u8ddf\u56fd\u5185\u7684\u8bc1\u5238\u90e8\u95e8\u6709\u8fc7\u63a5\u89e6\uff0c\u60f3\u8ba9\u76db\u946b\u7535\u5668\u5728\u56fd\u5185\u4e0a\u5e02\uff0c\u53ea\u53ef\u60dc\u8bc1\u5238\u90e8\u95e8\u53ea\u540c\u610f\u76db\u946b\u7535\u5668\u7684\u90e8\u5206\u8d44\u4ea7\u5206\u62c6\u51fa\u6765\u5230\u4e2d\u5c0f\u677f\u4e0a\u5e02\u4ea4\u6613\uff0c\u5e02\u503c\u8fd8\u9650\u5236\u5728\u4e94\u5341\u4ebf\u4ee5\u5185\uff0c\u878d\u8d44\u989d\u5ea6\u4e0d\u8d85\u8fc7\u5341\u4e94\u4ebf\u3002</p><p>\u3000\u3000\u66fe\u5b50\u8f89\u5fae\u5fae\u4e00\u7b11\uff0c\u9526\u6e56\u5927\u6982\u4e0d\u613f\u610f\u4e3a\u8fd9\u4e2a\u5341\u4e94\u4ebf\u5927\u52a8\u5e72\u6208\u3002</p><p>\u3000\u3000\u5f20\u606a\u793a\u610f\u6027\u7684\u4e3e\u4e86\u4e3e\u676f\u5b50\uff1a\u201c\u66fe\u603b\u518d\u600e\u4e48\u52aa\u529b\uff0c\u6211\u4eec\u4e5f\u662f\u89c9\u5f97\u8fdc\u8fdc\u4e0d\u591f\u7684\uff0c\u8c01\u8ba9\u6211\u4eec\u50cf\u8df3\u4e0a\u5cb8\u7684\u9c7c\u5462\uff0c\u8d44\u91d1\u9965\u6e34\u75c7\u662f\u5f88\u96be\u6cbb\u7684\u2026\u2026\u201d\u4ed6\u5fc3\u91cc\u60f3\u6d77\u5916\u8d44\u672c\u5012\u662f\u5145\u8db3\uff0c\u4f46\u662f\u5728\u6d77\u5916\u4e0a\u5e02\uff0c\u5c31\u8981\u7ed9\u9ad8\u76db\u8fd9\u4e9b\u6295\u8d44\u673a\u6784\u4ece\u4e2d\u5272\u4e00\u5200\uff0c\u65b0\u5149\u7eb8\u4e1a\u4f5c\u4e3a\u56fd\u5185\u7b2c\u4e00\u5bb6\u5b8c\u6210\u6797\u7eb8\u4e00\u4f53\u5316\u521d\u6b65\u5e03\u5c40\u7684\u4f01\u4e1a\uff0c\u53d1\u884c\u4ef7\u4e5f\u53ea\u6309\u716715\u500d\u7684\u5e02\u76c8\u7387\u8ba1\u7b97\uff0c\u8fd9\u8fd8\u662f\u9526\u6e56\u4e0e\u9ad8\u76db\u5728\u6606\u817e\u5728\u7ebf\u7684\u95ee\u9898\u8fdb\u884c\u6697\u76d8\u4ea4\u6613\u540e\u7684\u83b7\u5f97\u7684\u4f18\u60e0\uff0c\u6700\u521d\u8c08\u5224\u65f6\uff0c\u9ad8\u76db\u4e0e\u6c47\u4e30\u751a\u81f3\u5c06\u53d1\u884c\u4ef7\u5e02\u76c8\u7387\u538b\u523012\u500d\u4e0d\u80af\u8ba9\u6b65\u3002</p><p>\u3000\u3000\u73b0\u5728\u7b97\u662f\u53cc\u8d62\uff0c\u66fe\u5b50\u8f89\u770b\u5230\u5f20\u606a\u4e5f\u662f\u6ee1\u9762\u6625\u98ce\u3002\u6606\u817e\u5728\u7ebf\u5728\u4e94\u6708\u4e2d\u65ec\u4e4b\u540e\u7684\u5927\u53cd\u5f39\u4e2d\uff0c\u80a1\u4ef7\u4e00\u5ea6\u56de\u5347\u6478\u5230\u6bcf\u80a1\u56db\u5341\u7f8e\u5143\u7684\u9ad8\u4f4d\uff0c\u9ad8\u76db\u3001\u534e\u767b\u7b49\u98ce\u9669\u57fa\u91d1\u90fd\u987a\u5229\u5c06\u624b\u91cc\u6240\u6301\u6606\u817e\u5728\u7ebf\u7684\u5927\u90e8\u5206\u80a1\u7968\u90fd\u51cf\u6301\u5957\u73b0\uff0c\u83b7\u5f97\u6781\u4e30\uff0c\u6b64\u65f6\u7eb3\u65af\u8fbe\u514b\u6307\u6570\u98ce\u96e8\u98d8\u6447\uff0c\u5df2\u7ecf\u4f24\u4e0d\u4e86\u4ed6\u4eec\u7684\u7b4b\u9aa8\u4e86\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u7531\u4e8e\u9ad8\u76db\u59ff\u6001\u7684\u9000\u7f29\uff0c\u65b0\u5149\u7eb8\u4e1a\u4e0a\u5e02\u5f97\u5230\u4e00\u4e2a\u76f8\u5bf9\u6765\u8bf4\u4e0d\u9519\u7684\u53d1\u884c\u4ef7\u683c\uff0c\u5c11\u8bf4\u591a\u83b7\u5f9713\u4ebf\u7684\u8d44\u91d1\u3002</p><p>\u3000\u3000\u5728\u63a5\u89e6\u9526\u6e56\u4e4b\u524d\uff0c\u66fe\u5b50\u8f89\u4e0d\u4f1a\u76f8\u4fe1\u4f1a\u6709\u8fd9\u4e48\u4e00\u5bb6\u5e74\u8f7b\u7684\u8d22\u56e2\u5b58\u5728\uff0c\u4ed6\u5bf9\u7231\u8fbe\u96c6\u56e2\u7684\u8d22\u52a1\u4e0d\u5927\u4e86\u89e3\uff0c\u9526\u6e56\u5bf9\u7231\u8fbe\u96c6\u56e2\u7684\u8d22\u52a1\u66f4\u9690\u853d\uff0c\u5012\u662f\u9526\u6e56\u5546\u4e8b\u4f5c\u4e3a\u9526\u6e56\u5728\u9999\u6e2f\u7684\u878d\u8d44\u5e73\u53f0\uff0c\u5fc5\u987b\u8981\u5411\u878d\u8d44\u5bf9\u8c61\u63d0\u4f9b\u53ef\u4fe1\u53ef\u67e5\u7684\u8d22\u52a1\u62a5\u544a\uff0c\u66fe\u5b50\u8f89\u5bf9\u9526\u6e56\u5546\u4e8b\u7684\u8d22\u52a1\u4e86\u82e5\u6307\u638c\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u4e0a\u5e02\u540e\uff0c\u4ee5\u9996\u65e5\u6536\u76d8\u4ef7\u8ba1\u7b97\uff0c\u5e02\u503c\u9ad8\u8fbe\u4e8c\u767e\u4e94\u5341\u4e09\u4ebf\u6e2f\uff0c\u9526\u6e56\u6301\u6709\u65b0\u5149\u7eb8\u4e1a49\uff05\u7684\u80a1\u6743\uff0c\u5e02\u503c\u7ea6\u4e00\u767e\u4e8c\u5341\u4e94\u4ebf\u6e2f\u5143\uff0c\u4e3a\u4e86\u65b9\u4fbf\u9526\u6e56\u5546\u4e8b\u878d\u8d44\uff0c\u8fd1\u767e\u4ebf\u5e02\u503c\u7684\u65b0\u5149\u7eb8\u4e1a\u80a1\u6743\u90fd\u96c6\u4e2d\u5728\u9526\u6e56\u5546\u4e8b\u540d\u4e0b\u3002</p><p>\u3000\u3000\u9664\u4e86\u65b0\u5149\u7eb8\u4e1a\u4e4b\u5916\uff0c\u9526\u6e56\u5546\u4e8b\u8fd8\u76f4\u63a5\u6301\u6709\u7231\u8fbe\u7535\u5b5045\uff05\u7684\u80a1\u6743\uff0c\u7231\u8fbe\u7535\u5b50\u867d\u7136\u53d7\u5230\u7f51\u7edc\u6ce1\u6cab\u7834\u574f\u5371\u673a\u7684\u4e25\u91cd\u5f71\u54cd\uff0c\u80a1\u4ef7\u76f8\u6bd4\u6700\u9ad8\u70b9\u4e0b\u632b20\uff05\uff0c\u5e02\u503c\u4e5f\u8d85\u8fc7\u4e09\u767e\u4ebf\u6e2f\u5143\u3002</p><p>\u3000\u3000\u4e16\u7eaa\u9526\u6e56\u5e76\u8d2d\u4e0a\u5e02\u516c\u53f8\u5609\u4fe1\u5730\u4ea7\uff0c\u5e76\u501f\u58f3\u5c06\u90e8\u5206\u5546\u4e1a\u5730\u4ea7\u7f6e\u5165\u4e0a\u5e02\u516c\u53f8\uff0c\u7ea6\u670920\uff05\u7684\u80a1\u7968\u7531\u9526\u6e56\u5546\u4e8b\u76f4\u63a5\u6301\u6709\uff0c\u7531\u4e8e\u4e16\u7eaa\u9526\u6e56\u8fd8\u4ec5\u4ec5\u662f\u5c06\u4e00\u5c0f\u90e8\u5206\u7684\u5546\u4e1a\u8d44\u4ea7\u7f6e\u5165\u4e0a\u5e02\u516c\u53f8\uff0c\u8fd9\u90e8\u5206\u7684\u80a1\u6743\u5e02\u503c\u6bd4\u8f83\u4f4e\uff0c\u624d10\u4ebf\u6e2f\u5143\u3002</p><p>\u3000\u3000\u5373\u4f7f\u4e0d\u8003\u8651\u5176\u4ed6\uff0c\u9526\u6e56\u5546\u4e8b\u6240\u62e5\u6709\u7684\u4e0a\u5e02\u516c\u53f8\u80a1\u6743\u8d44\u4ea7\u5e02\u503c\u5c31\u8d85\u8fc7\u4e24\u767e\u4e94\u5341\u4ebf\u6e2f\u5143\uff0c\u867d\u7136\u8d1f\u503a\u989d\u5ea6\u8d85\u8fc7\u4e09\u5341\u4ebf\u7f8e\u5143\uff0c\u81f3\u5c11\u4ece\u8d26\u9762\u4e0a\u6765\u770b\uff0c\u9526\u6e56\u5546\u4e8b\u7684\u8d22\u52a1\u5df2\u7ecf\u7a33\u5b9a\u4e0b\u6765\uff0c\u6c47\u4e30\u3001\u9ad8\u76db\u7b49\u56fd\u9645\u6295\u8d44\u673a\u6784\u4e5f\u7eb7\u7eb7\u8c03\u9ad8\u5bf9\u9526\u6e56\u5546\u4e8b\u7684\u8d22\u52a1\u8bc4\u4f30\uff0c\u5c31\u7b97\u8fd9\u6b21\u9526\u6e56\u5546\u4e8b\u80fd\u591f\u8f83\u4e3a\u987a\u5229\u7684\u5229\u7528\u65b0\u5149\u7eb8\u4e1a\u7684\u80a1\u6743\u4f5c\u62b5\u62bc\u518d\u6c47\u4e30\u94f6\u884c\u8d37\u51fa\u516b\u5341\u4ebf\u6e2f\u5143\u7684\u8d44\u91d1\u51fa\u6765\uff0c\u5dee\u4e0d\u591a\u5c06\u9526\u6e56\u5546\u4e8b\u7684\u8d1f\u503a\u6c34\u5e73\u63d0\u9ad8\u5230\u56db\u5341\u4ebf\u7f8e\u5143\uff0c\u5f53\u7136\u6bcf\u5e74\u4e5f\u8981\u4e3a\u8fd9\u56db\u5341\u4ebf\u7f8e\u5143\u7684\u503a\u6743\u652f\u4ed8\u4e24\u4e09\u4ebf\u7f8e\u5143\u7684\u8d22\u52a1\u6210\u672c\u3002</p><p>\u3000\u3000\u8981\u662f\u8fd9\u65f6\u5019\u7ed9\u4e9a\u6d32\u5927\u578b\u8d22\u56e2\u6027\u8d28\u7684\u4f01\u4e1a\u6392\u4e2a\u6b21\u5e8f\uff0c\u9526\u6e56\u5e94\u8be5\u6709\u8d44\u683c\u51fa\u73b0\u5728\u699c\u5355\u4e2d\u4e86\u3002</p></article>"}}}]);
