(window.webpackJsonp=window.webpackJsonp||[]).push([[201],{223:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u516b\u8282 \u7a83\u6cd5\u8005",content:"<article><h2>\u7b2c\u4e09\u5341\u516b\u8282 \u7a83\u6cd5\u8005</h2><p>\u91ca\u653e\u4e86\u4e00\u4e2a\u79d8\u6cd5\u773c\u5728\u524d\u9762\u76d1\u89c6\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u8fdc\u8fdc\u5730\u8ddf\u7740\u8428\u74e6\u5c14\u3002\u7267\u5e08\u4f3c\u4e4e\u4e00\u65e0\u6240\u77e5\uff0c\u5168\u7136\u6ca1\u53d1\u89c9\u81ea\u5df1\u5df2\u7ecf\u88ab\u4eba\u76ef\u4e0a\u4e86\uff0c\u4ed6\u7a7f\u7740\u90a3\u8eab\u7070\u8272\u76ae\u7532\uff0c\u8170\u95f4\u6302\u7740\u4e00\u67c4\u77ed\u5251\uff0c\u50cf\u5f80\u5e38\u4e00\u6837\uff0c\u4e0d\u51b7\u4e0d\u70ed\u5730\u548c\u57ce\u95e8\u7684\u536b\u5175\u6253\u4e86\u62db\u547c\uff0c\u60a0\u95f2\u5730\u8d70\u8fdb\u5bc6\u6797\uff0c\u4e00\u8def\u8dcb\u6d89\uff0c\u5176\u95f4\u7a7f\u8fc7\u4e86\u4e24\u7247\u6cbc\u6cfd\uff0c\u6700\u540e\u5728\u4e2d\u5348\u65f6\u5206\uff0c\u62b5\u8fbe\u4e86\u743c\u6069\u8bf4\u7684\u90a3\u4e2a\u5de8\u77f3\u5706\u9635\u3002</p><p>\u90a3\u786e\u5b9e\u662f\u4e2a\u5f88\u53e4\u602a\u7684\u5de8\u77f3\u5706\u9635\uff0c\u4e00\u770b\u5c31\u89c9\u5f97\u50cf\u662f\u67d0\u79cd\u53e4\u8001\u6587\u660e\u7559\u4e0b\u7684\u9057\u8ff9\u2014\u2014\u7136\u800c\u8981\u8bf4\u53e4\u8001\u6587\u660e\u7684\u8bdd\uff0c\u6b64\u5730\u539f\u672c\u662f\u7cbe\u7075\u738b\u56fd\uff0c\u9057\u8ff9\u4e5f\u5e94\u8be5\u662f\u7cbe\u7075\u4eec\u7684\u9057\u8ff9\u3002\u4f46\u4f17\u6240\u5468\u77e5\uff0c\u7cbe\u7075\u4eec\u5e76\u4e0d\u70ed\u7231\u96d5\u5851\uff0c\u8fd9\u662f\u77ee\u4eba\u4eec\u7684\u4e13\u957f\u3002</p><p>\u6240\u4ee5\u743c\u6069\u5f88\u6709\u7406\u7531\u6000\u7591\u8fd9\u662f\u67d0\u4e2a\u90aa\u795e\u6559\u4f1a\u5f04\u7684\u8054\u7edc\u70b9\u4e4b\u7c7b\u3002</p><p>\u773c\u770b\u7740\u8428\u74e6\u5c14\u8fdb\u4e86\u5de8\u77f3\u5706\u9635\uff0c\u743c\u6069\u638f\u51fa\u4e24\u74f6\u9690\u5f62\u836f\u6c34\uff0c\u9012\u7ed9\u6885\u83f2\u65af\u4e00\u74f6\uff0c\u5269\u4e0b\u4e00\u74f6\u81ea\u5df1\u704c\u4e86\u4e0b\u53bb\u3002\u836f\u6c34\u5473\u9053\u6709\u70b9\u6da9\uff0c\u56e0\u4e3a\u662f\u8d76\u5236\u51fa\u6765\u7684\uff0c\u4e0d\u8fc7\u6548\u679c\u5e76\u6ca1\u6709\u6253\u6298\u6263\uff0c\u4ed6\u548c\u6885\u83f2\u65af\u7684\u8eab\u4f53\u90fd\u53d8\u5f97\u900f\u660e\u8d77\u6765\uff0c\u63a5\u7740\u6d88\u5931\u5728\u7a7a\u6c14\u4e2d\u3002</p><p>\u9690\u5f62\u5b8c\u6210\uff0c\u56e0\u4e3a\u770b\u4e0d\u89c1\u5f7c\u6b64\uff0c\u4ed6\u4eec\u624b\u7275\u7740\u624b\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u4e5f\u8ddf\u7740\u8d70\u8fdb\u4e86\u5de8\u77f3\u5706\u9635\u3002\u8f6c\u4e86\u4e24\u5708\uff0c\u4ed6\u4eec\u770b\u89c1\u4e86\u5148\u8fdb\u6765\u7684\u8428\u74e6\u5c14\u3002\u7267\u5e08\u5e76\u6ca1\u6709\u610f\u8bc6\u5230\u6709\u4eba\u8ddf\u8fc7\u6765\uff0c\u4ed6\u4fef\u8eab\u5728\u5730\u4e0a\u70b9\u4e86\u56db\u4e94\u4e2a\u706b\u5806\uff0c\u7136\u540e\u9ed8\u9ed8\u5750\u5728\u4e2d\u592e\uff0c\u4f3c\u4e4e\u5728\u7b49\u5f85\u4ec0\u4e48\u4eba\u3002</p><p>\u743c\u6069\u548c\u6885\u83f2\u65af\u8eb2\u5728\u4e00\u5757\u5de8\u77f3\u540e\u9762\uff0c\u9759\u9759\u7b49\u5f85\u7740\uff0c\u5e0c\u671b\u80fd\u770b\u5230\u6709\u4ec0\u4e48\u4eba\u6765\u548c\u4ed6\u63a5\u5934\uff0c\u7136\u800c\u7ed3\u679c\u5931\u671b\u4e86\u3002\u8db3\u8db3\u7b49\u4e86\u5927\u7ea6\u4e00\u5c0f\u65f6\uff0c\u9690\u5f62\u836f\u6c34\u7684\u6548\u529b\u90fd\u5df2\u7ecf\u6d88\u5931\u4e86\uff0c\u5468\u56f4\u4f9d\u65e7\u5168\u65e0\u534a\u70b9\u52a8\u9759\uff0c\u706b\u5806\u8fd8\u5728\u71c3\u70e7\u7740\uff0c\u4e00\u76f4\u5012\u6ca1\u6709\u7184\u706d\u7684\u8ff9\u8c61\u3002\u5750\u5728\u706b\u5806\u4e2d\u592e\u7684\u7267\u5e08\u7ad9\u4e86\u8d77\u6765\uff0c\u770b\u6837\u5b50\u4f3c\u4e4e\u662f\u60f3\u79bb\u5f00\u4e86\u3002</p><p>\u771f\u5947\u602a\uff0c\u4ed6\u5927\u8001\u8fdc\u8dd1\u5230\u8fd9\u91cc\u4e00\u8d9f\uff0c\u96be\u9053\u5c31\u662f\u4e3a\u4e86\u547c\u5438\u8fd9\u91cc\u7684\u65b0\u9c9c\u7a7a\u6c14\u4e48\u3002</p><p>\u743c\u6069\u6b63\u4e0d\u5f97\u5176\u89e3\uff0c\u7267\u5e08\u7a81\u7136\u8f6c\u8138\u671d\u8fd9\u8fb9\u770b\u8fc7\u6765\uff0c\u773c\u795e\u51db\u51db\u3002\u743c\u6069\u5fc3\u4e2d\u4e00\u60ca\uff0c\u4ed6\u4e00\u76f4\u5c0f\u5fc3\u5730\u8eb2\u5728\u5de8\u77f3\u540e\u9762\uff0c\u521a\u624d\u5e76\u6ca1\u6709\u52a8\u5f39\uff0c\u4e5f\u6ca1\u6709\u53d1\u51fa\u4ec0\u4e48\u58f0\u97f3\uff0c\u600e\u4e48\u5bf9\u65b9\u5374\u50cf\u662f\u53d1\u73b0\u4e86\u81ea\u5df1\u4f3c\u7684\u3002\u6b63\u5728\u60f3\u4ed6\u662f\u4e0d\u662f\u968f\u610f\u4e71\u770b\uff0c\u5374\u542c\u89c1\u7267\u5e08\u7528\u90a3\u51b7\u51b0\u51b0\u9634\u68ee\u68ee\u7684\u58f0\u97f3\u8bf4\uff1a\u201c\u90a3\u8fb9\u7684\u5deb\u5e08\u670b\u53cb\uff0c\u8fd8\u4e0d\u51fa\u6765\u5417\uff1f\u201d</p><p>\u65e2\u7136\u5df2\u7ecf\u88ab\u4eba\u53eb\u7834\uff0c\u518d\u8eb2\u85cf\u4e5f\u6ca1\u6709\u610f\u4e49\uff0c\u4e0d\u8fc7\u542c\u4ed6\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u53ea\u53d1\u73b0\u4e86\u743c\u6069\uff0c\u5374\u6ca1\u6709\u53d1\u73b0\u6885\u83f2\u65af\uff0c\u8fd9\u662f\u4e2a\u597d\u6d88\u606f\u3002\u743c\u6069\u8f7b\u8f7b\u6309\u4e86\u6309\u6885\u83f2\u65af\u7684\u624b\uff0c\u793a\u610f\u5979\u7ee7\u7eed\u8eb2\u7740\uff0c\u81ea\u5df1\u4ece\u5de8\u77f3\u540e\u8d70\u4e86\u51fa\u6765\u3002</p><p>\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff1f\u201d\u8428\u74e6\u5c14\u6253\u91cf\u7740\u743c\u6069\uff0c\u53eb\u51fa\u540d\u5b57\uff0c\u4ed6\u4f3c\u4e4e\u5e76\u4e0d\u663e\u5f97\u591a\u4e48\u5403\u60ca\uff0c\u4eff\u4f5b\u4e00\u5207\u90fd\u5728\u9884\u6599\u4e4b\u4e2d\u3002</p><p>\u201c\u4e0b\u5348\u597d\uff0c\u8428\u74e6\u5c14\u5148\u751f\u3002\u201d</p><p>\u8428\u74e6\u5c14\u7684\u5634\u89d2\u5fae\u5fae\u62bd\u52a8\uff0c\u62c9\u626f\u51fa\u4e00\u4e2a\u96be\u770b\u7684\u7b11\u5bb9\u6765\uff0c\u201c\u4e0b\u5348\u597d\uff0c\u5deb\u5e08\u5148\u751f\uff0c\u4f60\u6765\u6b64\u6709\u4f55\u8d35\u5e72\uff1f\u201d</p><p>\u201c\u6211\u4e5f\u6b63\u597d\u5947\u8fd9\u4e2a\u95ee\u9898\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u4ed6\u89c2\u5bdf\u7740\u5bf9\u65b9\u7684\u53cd\u5e94\uff0c\u201c\u4e00\u4e2a\u4f2a\u88c5\u6210\u68ee\u6797\u5973\u795e\u4fe1\u5f92\u7684\u90aa\u795e\u7267\u5e08\uff0c\u8dd1\u5230\u8fd9\u91cc\u6765\uff0c\u53c8\u662f\u4ec0\u4e48\u76ee\u7684\u5462\uff1f\u201d</p><p>\u5bf9\u4e8e\u4f2a\u88c5\u7684\u8eab\u4efd\u88ab\u63ed\u7a7f\uff0c\u8428\u74e6\u5c14\u9762\u8272\u4e0d\u53d8\uff1b\u5bf9\u4e8e\u201c\u90aa\u795e\u7267\u5e08\u201d\u7684\u6307\u63a7\uff0c\u4ed6\u540c\u6837\u4e5f\u4e0d\u7f6e\u4e00\u8bcd\uff0c\u4f3c\u4e4e\u662f\u9ed8\u8ba4\u4e86\u3002\u201c\u5404\u4eba\u6709\u5404\u4eba\u7684\u79d8\u5bc6\uff0c\u5deb\u5e08\uff0c\u6b63\u5982\u6211\u4ece\u6765\u4e0d\u66fe\u5e72\u6d89\u4f60\u5728\u57ce\u91cc\u56db\u5904\u6253\u63a2\u6d88\u606f\u3002\u6211\u60f3\u51fa\u4e8e\u793c\u8c8c\uff0c\u4f60\u4e5f\u4e0d\u5e94\u8be5\u591a\u7ba1\u6211\u7684\u95f2\u4e8b\u3002\u201d</p><p>\u201c\u53ef\u60dc\u8fd9\u5bf9\u6211\u800c\u8a00\u5e76\u975e\u95f2\u4e8b\uff0c\u201d\u743c\u6069\u865a\u5f20\u58f0\u52bf\u5730\u8bf4\uff0c\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u6b63\u662f\u53d7\u4eba\u59d4\u6258\u800c\u6765\uff0c\u8c03\u67e5\u4f60\u7684\u72af\u7f6a\u52fe\u5f53\u3002\u201d</p><p>\u201c\u662f\u4e48\uff0c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u662f\u4e3a\u4e86\u5bfb\u627e\u6b7b\u6708\u6cd5\u73e0\u800c\u6765\u5462\u3002\u201d</p><p>\u9aa4\u95fb\u6b64\u8a00\uff0c\u743c\u6069\u5fae\u5fae\u4e00\u60ca\uff0c\u5bf9\u624b\u5219\u8d81\u8fd9\u4e2a\u673a\u4f1a\u53d1\u52a8\u4e86\u7a81\u7136\u88ad\u51fb\uff0c\u4ed6\u4e5f\u4e0d\u518d\u50cf\u5e73\u65f6\u4e00\u6837\u8fd8\u88c5\u6a21\u4f5c\u6837\u5730\u53d6\u6885\u4e3d\u51ef\u5723\u5fbd\u505a\u63a9\u9970\uff0c\u76f4\u622a\u4e86\u5f53\u5730\u53cc\u624b\u4ea4\u53e0\uff0c\u671d\u743c\u6069\u865a\u865a\u4e00\u63a8\uff0c\u4e00\u56e2\u6697\u9ec4\u8272\u7684\u6c14\u4f53\u4ece\u638c\u5fc3\u55b7\u51fa\uff0c\u51dd\u6210\u5de8\u5927\u7684\u624b\u638c\u5f62\u72b6\uff0c\u671d\u743c\u6069\u4e00\u628a\u6293\u6765\u3002</p><p>\u743c\u6069\u81ea\u6043\u6709\u795e\u672f\u514d\u75ab\u7684\u7279\u6b8a\u4f53\u8d28\uff0c\u4e0d\u95ea\u4e0d\u907f\uff0c\u53cd\u51b2\u4e0a\u524d\uff0c\u6296\u624b\u5f39\u51fa\u4e00\u9897\u77f3\u5f39\uff0c\u7167\u7740\u8428\u74e6\u5c14\u7684\u817f\u90e8\u7838\u53bb\u3002\u4ed6\u6253\u7b97\u7559\u7740\u6d3b\u53e3\u903c\u4f9b\uff0c\u6240\u4ee5\u5e76\u6ca1\u6709\u4f7f\u7528\u66f4\u6709\u6740\u4f24\u529b\u7684\u6cd5\u672f\uff0c\u53ea\u60f3\u8ba9\u5bf9\u624b\u4e27\u5931\u53cd\u6297\u80fd\u529b\u5c31\u884c\u3002\u6697\u9ec4\u8272\u5de8\u624b\u649e\u4e0a\u4ed6\u7684\u8eab\u4f53\uff0c\u968f\u5373\u5982\u610f\u6599\u4e2d\u4e00\u822c\u5206\u5d29\u79bb\u6790\uff0c\u6563\u5f52\u65e0\u5f62\uff0c\u5b8c\u5168\u6ca1\u6709\u53d1\u6325\u534a\u70b9\u4f5c\u7528\u3002\u4f46\u743c\u6069\u7684\u653b\u51fb\u5374\u4e5f\u6ca1\u6709\u594f\u6548\uff0c\u8428\u74e6\u5c14\u5feb\u6377\u65e0\u6bd4\u5730\u62d4\u5251\u5728\u624b\uff0c\u4e00\u5251\u659c\u6311\u5c06\u7838\u6765\u7684\u77f3\u5f39\u9694\u5f00\uff0c\u5728\u8eab\u524d\u534a\u82f1\u5c3a\u5904\u7206\u70b8\uff0c\u4ed6\u672c\u4eba\u4e1d\u6beb\u6ca1\u6709\u53d7\u4f24\u3002</p><p>\u679c\u7136\u5982\u4f20\u95fb\u6240\u8bf4\uff0c\u8fd9\u5bb6\u4f19\u7684\u5251\u672f\u786e\u5b9e\u4e0d\u9519\u3002</p><p>\u4e00\u51fb\u4e0d\u66fe\u5f97\u624b\uff0c\u53cc\u65b9\u90fd\u6682\u505c\u8fdb\u653b\uff0c\u8c28\u614e\u5730\u6253\u91cf\u7740\u5bf9\u65b9\u3002\u8428\u74e6\u5c14\u4f3c\u4e4e\u662f\u5728\u4e3a\u5bf9\u65b9\u6beb\u4e0d\u8d39\u529b\u5730\u6d88\u89e3\u6389\u4e86\u4ed6\u7684\u6cd5\u672f\u800c\u60ca\u8bb6\uff0c\u56e0\u4e3a\u8fd9\u5df2\u7ecf\u8d85\u51fa\u4e86\u666e\u901a\u7684\u9b54\u6cd5\u9632\u5fa1\u7684\u8303\u7574\uff0c\u201c\u4f60\u80fd\u514d\u75ab\u6cd5\u672f\uff1f\u201d\u4ed6\u76b1\u7740\u7709\u5934\uff0c\u6709\u4e9b\u4e0d\u6562\u7f6e\u4fe1\u5730\u95ee\u3002</p><p>\u743c\u6069\u95ed\u53e3\u4e0d\u7b54\uff0c\u4ed6\u601d\u5fd6\u7740\uff0c\u8003\u8651\u662f\u5426\u8981\u52a8\u7528\u6000\u4e2d\u7684\u89e3\u79bb\u672f\u5377\u8f74\uff0c\u4f46\u968f\u5373\u653e\u5f03\u4e86\u8fd9\u4e2a\u60f3\u6cd5\uff0c\u89e3\u79bb\u672f\u7684\u5a01\u529b\u592a\u5927\uff0c\u4e00\u4e0d\u5c0f\u5fc3\u628a\u8fd9\u5bb6\u4f19\u8f70\u6210\u4e86\u9f51\u7c89\uff0c\u5374\u5230\u54ea\u91cc\u53bb\u627e\u6b7b\u6708\u6cd5\u73e0\u7684\u4e0b\u843d\u3002\u5982\u679c\u7528\u53d8\u5f62\u672f\u628a\u4ed6\u53d8\u6210\u67d0\u79cd\u5c0f\u52a8\u7269\uff0c\u5012\u662f\u5e94\u8be5\u80fd\u5236\u5f97\u4f4f\u4ed6\uff0c\u4f46\u8c01\u53c8\u80fd\u548c\u52a8\u7269\u4ea4\u6d41\u3002</p><p>\u770b\u6765\u53ea\u6709\u7528\u5b9a\u8eab\u672f\u4e86\u3002</p><p>\u5e78\u597d\u65e9\u5c31\u9884\u6599\u5230\u8fd9\u79cd\u60c5\u51b5\uff0c\u65e9\u4e0a\u51fa\u53d1\u524d\u5c31\u51c6\u5907\u4e86\u8fd9\u4e2a\u6cd5\u672f\u3002\u743c\u6069\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u4e2a\u5c0f\u94c1\u5757\u5f80\u5730\u4e0a\u4e00\u7838\uff0c\u9ed8\u8bf5\u5492\u8bed\uff0c\u8111\u4e2d\u5df2\u7ecf\u6d6e\u73b0\u51fa\u5b9a\u8eab\u672f\u7684\u5492\u6587\u6765\u3002\u8fd9\u79cd\u9644\u9b54\u5b66\u6d3e\u7684\u6cd5\u672f\u4ed6\u5e76\u4e0d\u7cbe\u7814\uff0c\u7528\u8d77\u6765\u4fbf\u4e0d\u5982\u53d8\u5316\u6cd5\u672f\u90a3\u822c\u7eaf\u719f\uff0c\u65bd\u6cd5\u901f\u5ea6\u4e5f\u8981\u7a0d\u7a0d\u6162\u4e0a\u534a\u62cd\uff0c\u773c\u770b\u6cd5\u672f\u5c31\u8981\u5b8c\u6210\uff0c\u9aa4\u7136\u95f4\u5bf9\u9762\u7684\u8428\u74e6\u5c14\u4f3c\u7b11\u975e\u7b11\u5730\u4f38\u624b\uff0c\u9065\u9065\u671d\u4ed6\u4e00\u6307\u3002</p><p>\u4e00\u6307\u4e4b\u4e0b\uff0c\u743c\u6069\u53ea\u89c9\u8111\u4e2d\u5fae\u5fae\u4e00\u9635\u6655\u7729\uff0c\u77ac\u95f4\u4fbf\u53c8\u6062\u590d\uff0c\u5e76\u4e0d\u66fe\u611f\u89c9\u53d7\u4f24\u3002\u4f46\u4ed6\u60ca\u9a87\u5730\u53d1\u73b0\u539f\u672c\u5df2\u7ecf\u6e05\u695a\u81ea\u8111\u4e2d\u6d6e\u73b0\uff0c\u84c4\u52bf\u5f85\u53d1\u7684\u5b9a\u8eab\u672f\u5492\u6587\u5374\u51ed\u7a7a\u6d88\u5931\u4e86\uff0c\u4eff\u4f5b\u88ab\u65e0\u5f62\u4e4b\u624b\u7a81\u7136\u62b9\u53bb\u4f3c\u7684\u3002</p><p>\u7136\u540e\u4ed6\u542c\u89c1\u8428\u74e6\u5c14\u9634\u68ee\u68ee\u7684\u58f0\u97f3\uff1a\u201c\u5b9a\u8eab\uff01\u201d</p><p>\u5468\u56f4\u7684\u7a7a\u6c14\u9aa4\u7136\u5316\u4f5c\u65e0\u5f62\u7684\u9501\u94fe\uff0c\u91cd\u91cd\u53e0\u53e0\u5730\u6324\u538b\u56f4\u56f0\u4e0a\u6765\uff0c\u4f46\u743c\u6069\u5e76\u6ca1\u6709\u5f53\u771f\u88ab\u5b9a\u4f4f\u3002\u8010\u745f\u6cd5\u672f\u62a4\u7f69\u6212\u6307\u518d\u4e00\u6b21\u53d1\u6325\u4e86\u4f5c\u7528\uff0c\u4e00\u5708\u770b\u4e0d\u89c1\u7684\u9b54\u6cd5\u5c4f\u969c\u5728\u4ed6\u7684\u8eab\u4f53\u5468\u56f4\u5e03\u4e0b\uff0c\u6084\u6084\u62b5\u4f4f\u4e86\u5b9a\u8eab\u672f\u7684\u538b\u5236\u3002</p><p>\u4f46\u743c\u6069\u4fdd\u6301\u7740\u4e00\u52a8\u4e0d\u52a8\u7684\u59ff\u52bf\uff0c\u4e0d\u4ec5\u5982\u6b64\uff0c\u4ed6\u7684\u8138\u4e0a\u6070\u5230\u597d\u5904\u5730\u9732\u51fa\u60ca\u614c\u7684\u795e\u8272\u6765\u3002</p><p>\u201c\u4f60\u2026\u2026\u521a\u624d\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u8428\u74e6\u5c14\u563f\u563f\u51b7\u7b11\u7740\uff0c\u4ed6\u663e\u7136\u5bf9\u81ea\u5df1\u7684\u6cd5\u672f\u5f88\u6709\u4fe1\u5fc3\uff0c\u5e76\u4e0d\u66fe\u53d1\u89c9\u743c\u6069\u5176\u5b9e\u6ca1\u6709\u771f\u6b63\u88ab\u5b9a\u4f4f\u3002\u201c\u89c1\u8bc6\u6d45\u8584\u7684\u7b28\u86cb\uff0c\u201d\u4ed6\u51b7\u54fc\u7740\uff0c\u201c\u96be\u9053\u4f60\u73b0\u5728\u8fd8\u4e0d\u660e\u767d\u2026\u2026\u201d</p><p>\u8bdd\u97f3\u672a\u843d\uff0c\u4e00\u4e2a\u94f6\u767d\u8272\u4eba\u5f71\u4ece\u4ed6\u80cc\u540e\u63a0\u51fa\uff0c\u6325\u8d77\u94f6\u5251\u671d\u4ed6\u80a9\u8180\u76f4\u5288\u4e0b\u6765\uff0c\u6b63\u662f\u6885\u83f2\u65af\u3002\u5979\u8d81\u7740\u743c\u6069\u548c\u8428\u74e6\u5c14\u8bf4\u8bdd\u7684\u65f6\u5019\uff0c\u6084\u6084\u79fb\u4e86\u65b9\u4f4d\uff0c\u8f6c\u5230\u8428\u74e6\u5c14\u80cc\u540e\uff0c\u539f\u672c\u662f\u51c6\u5907\u7a81\u7136\u88ad\u51fb\uff0c\u5374\u89c1\u743c\u6069\u88ab\u5b9a\u8eab\u672f\u5236\u4f4f\uff0c\u53ea\u5f97\u4ed3\u4fc3\u51fa\u624b\u3002\u548c\u743c\u6069\u4e00\u6837\uff0c\u5979\u4e5f\u8fd8\u662f\u5b58\u4e86\u7559\u6d3b\u53e3\u903c\u4f9b\u7684\u5fc3\u601d\uff0c\u94f6\u5251\u907f\u5f00\u4e86\u8981\u5bb3\u90e8\u4f4d\uff0c\u53ea\u6253\u7b97\u5378\u4e0b\u7267\u5e08\u4e00\u6761\u80f3\u818a\u3002</p><p>\u7136\u800c\u8428\u74e6\u5c14\u4f3c\u4e4e\u65e9\u6709\u63d0\u9632\uff0c\u4ed6\u95f4\u4e0d\u5bb9\u53d1\u5730\u8f6c\u8eab\uff0c\u4e3e\u8d77\u77ed\u5251\u683c\u6321\u3002\u77ac\u606f\u4e4b\u95f4\uff0c\u4e24\u67c4\u5251\u5df2\u7ecf\u649e\u51fb\u4e86\u4e03\u516b\u6b21\uff0c\u8428\u74e6\u5c14\u5251\u672f\u786e\u5b9e\u4e0d\u9519\uff0c\u4f46\u6885\u83f2\u65af\u663e\u7136\u66f4\u52a0\u9ad8\u660e\uff0c\u6700\u540e\u5979\u4e00\u5251\u628a\u8428\u74e6\u5c14\u7684\u77ed\u5251\u7838\u5f00\uff0c\u548c\u8eab\u649e\u5165\uff0c\u80a9\u90e8\u91cd\u91cd\u5730\u9876\u5728\u7267\u5e08\u7684\u80f8\u53e3\uff0c\u5c06\u4ed6\u6574\u4e2a\u4eba\u90fd\u649e\u8dcc\u51fa\u53bb\u3002</p><p>\u8428\u74e6\u5c14\u88ab\u649e\u5f97\u8e09\u8dc4\u540e\u9000\uff0c\u9887\u4e3a\u72fc\u72c8\uff0c\u77ed\u5251\u4e5f\u5df2\u7ecf\u88ab\u51fb\u98de\u3002\u6885\u83f2\u65af\u4e00\u6b65\u4e0d\u505c\uff0c\u7d27\u8ddf\u7740\u903c\u8feb\u4e0a\u6765\uff0c\u8428\u74e6\u5c14\u6b64\u65f6\u6b63\u9000\u5230\u4e00\u4e2a\u706b\u5806\u524d\uff0c\u773c\u770b\u907f\u65e0\u53ef\u907f\uff0c\u5c31\u8981\u88ab\u5236\u4f4f\u3002\u4ed6\u7a81\u7136\u5927\u53eb\u4e00\u58f0\uff0c\u9000\u540e\u4e00\u6b65\u649e\u5165\u706b\u5806\u4e2d\u3002</p><p>\u8fce\u7740\u743c\u6069\u548c\u6885\u83f2\u65af\u60ca\u8be7\u7684\u76ee\u5149\uff0c\u4ed6\u7684\u8eab\u5f62\u77ac\u95f4\u88ab\u706b\u7130\u541e\u6ca1\uff0c\u968f\u5373\u53c8\u4ece\u8fdc\u5904\u53e6\u5916\u4e00\u4e2a\u706b\u5806\u91cc\u6beb\u53d1\u65e0\u4f24\u5730\u8df3\u51fa\u6765\u3002</p><p>\u706b\u4e2d\u8df3\u8dc3\uff1f\u8fd9\u662f\u7ea2\u888d\u7684\u79d8\u672f\uff0c\u4ed6\u600e\u4e48\u4e5f\u4f1a\u2026\u2026\u800c\u4e14\u8fd9\u5206\u660e\u662f\u5965\u672f\uff0c\u4ed6\u5374\u662f\u4e2a\u7267\u5e08\u2026\u2026</p><p>\u6885\u83f2\u65af\u60ca\u8bb6\u4e4b\u4e0b\uff0c\u63d0\u5251\u524d\u51b2\uff0c\u5374\u5df2\u7ecf\u8fdf\u4e86\u534a\u6b65\uff0c\u8428\u74e6\u5c14\u53cc\u8db3\u79bb\u5730\uff0c\u5feb\u901f\u5347\u4e0a\u534a\u7a7a\uff0c\u5c45\u9ad8\u4e34\u4e0b\u5730\u4fef\u89c6\u7740\u4e24\u4e2a\u5bf9\u624b\u3002</p><p>\u201c\u73b0\u5728\u4f60\u4eec\u5e94\u8be5\u660e\u767d\u4e86\u5427\uff0c\u201d\u7ad9\u5728\u5b89\u5168\u4e4b\u5904\uff0c\u8428\u74e6\u5c14\u7684\u8bed\u6c14\u4e5f\u5e73\u548c\u4e0b\u6765\uff0c\u4ed6\u7684\u5634\u89d2\u62bd\u52a8\u7740\uff0c\u8138\u4e0a\u9732\u51fa\u6709\u4e9b\u81ea\u5f97\u7684\u7b11\u5bb9\uff0c\u201c\u4f60\u4eec\u8bf4\u5f97\u6ca1\u9519\uff0c\u6211\u786e\u5b9e\u4e0d\u662f\u4ec0\u4e48\u6885\u4e3d\u51ef\u7267\u5e08\uff0c\u4f46\u4e5f\u4e0d\u662f\u4ec0\u4e48\u90aa\u795e\u7267\u5e08\uff0c\u56e0\u4e3a\u6211\u6839\u672c\u5c31\u4e0d\u662f\u4ec0\u4e48\u7267\u5e08\u2014\u2014\u6211\u662f\u4e2a\u5deb\u5e08\uff0c\u4e0d\u6298\u4e0d\u6263\u7684\u5deb\u5e08\uff0c\u201d\u4ed6\u563f\u563f\u51b7\u7b11\u7740\uff0c\u201c\u4e0d\u8fc7\u6211\u66f4\u559c\u6b22\u6211\u4e3a\u81ea\u5df1\u53d6\u7684\u540d\u5b57\uff0c\u7a83\u6cd5\u8005\u3002\u201d</p><p>\u201c\u6211\u662f\u4e2a\u7a83\u6cd5\u8005\u3002\u201d\u4ed6\u8bf4\u3002</p><p>\u743c\u6069\u548c\u6885\u83f2\u65af\u90fd\u4e0d\u66fe\u542c\u8fc7\u7a83\u6cd5\u8005\u8fd9\u4e2a\u8bcd\uff0c\u4f46\u4e5f\u65e0\u9700\u8428\u74e6\u5c14\u89e3\u91ca\uff0c\u6b64\u60c5\u6b64\u666f\uff0c\u5b83\u7684\u610f\u601d\u663e\u7136\u975e\u5e38\u6e05\u695a\u3002\u8428\u74e6\u5c14\u6709\u67d0\u79cd\u7279\u6b8a\u7684\u80fd\u529b\uff0c\u80fd\u591f\u7a83\u53d6\u4ed6\u4eba\u7684\u6cd5\u672f\uff0c\u4ed6\u6bcf\u6b21\u53bb\u5a01\u666e\u6797\u5854\u62c9\u57ce\u7684\u795e\u6bbf\uff0c\u4e3a\u4e86\u5c31\u662f\u7a83\u53d6\u90a3\u4e9b\u7267\u5e08\u7684\u795e\u672f\uff0c\u6240\u4ee5\u624d\u80fd\u4e00\u76f4\u6210\u529f\u4f2a\u88c5\u6210\u7267\u5e08\uff1b\u4e0d\u4ec5\u5982\u6b64\uff0c\u4ed6\u540c\u6837\u4e5f\u80fd\u7a83\u53d6\u5965\u672f\uff0c\u521a\u624d\u7684\u5b9a\u8eab\u672f\u548c\u706b\u4e2d\u8df3\u8dc3\u5c31\u662f\u660e\u8bc1\u3002</p><p>\u7b49\u7b49\u2026\u2026</p><p>\u6b64\u524d\u743c\u6069\u548c\u6885\u83f2\u65af\u6240\u6709\u7684\u63a8\u8bba\uff0c\u90fd\u5efa\u7acb\u5728\u8428\u74e6\u5c14\u662f\u4e2a\u201c\u7267\u5e08\u201d\u7684\u524d\u63d0\u4e0a\uff0c\u65e0\u8bba\u662f\u6885\u4e3d\u51ef\u7267\u5e08\u6216\u8005\u88f3\u63d0\u963f\u7267\u5e08\u6216\u8005\u90aa\u795e\u7267\u5e08\u2014\u2014\u6240\u4ee5\u8ba4\u5b9a\u4ed6\u4e0d\u53ef\u80fd\u6301\u6709\u6b7b\u6708\u6cd5\u73e0\uff0c\u6700\u591a\u53ea\u628a\u4ed6\u5f53\u4f5c\u4e00\u6761\u7ebf\u7d22\u3002\u4f46\u5982\u679c\u4ed6\u538b\u6839\u5c31\u4e0d\u662f\u7267\u5e08\uff0c\u90a3\u4e48\u8fd9\u4e2a\u63a8\u8bba\u5c31\u5927\u6709\u4fee\u6b63\u7684\u5fc5\u8981\u4e86\u3002</p><p>\u4eff\u4f5b\u662f\u4e3a\u4e86\u5370\u8bc1\u8fd9\u4e2a\u731c\u6d4b\uff0c\u60ac\u6d6e\u5728\u7a7a\u4e2d\u7684\u8428\u74e6\u5c14\u5fae\u7b11\u7740\uff0c\u4ece\u6000\u4e2d\u53d6\u51fa\u4e00\u4e2a\u95ea\u70c1\u7740\u5fae\u5149\u7684\u7403\u4f53\uff0c\u9ed1\u8272\u4e0e\u7d2b\u8272\u4e0d\u7a33\u5b9a\u5730\u5728\u8868\u9762\u76f8\u4e92\u65cb\u7ed5\u7740\uff0c\u4eff\u4f5b\u5c06\u5468\u56f4\u7684\u5149\u7ebf\u90fd\u6536\u655b\u5438\u7eb3\u4e86\u8fdb\u53bb\u3002\u4e00\u80a1\u5e7d\u6697\u54c0\u4f24\u7684\u611f\u89c9\u4ece\u4e2d\u51db\u51db\u5f25\u6f2b\u51fa\u6765\uff0c\u65e0\u9700\u89e3\u91ca\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u5c31\u77e5\u9053\u4e86\u8fd9\u4e2a\u7403\u4f53\u662f\u4ec0\u4e48\u3002</p><p>\u6b7b\u6708\u6cd5\u73e0\u3002</p></article>"}}}]);
