(window.webpackJsonp=window.webpackJsonp||[]).push([[202],{224:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e5d\u8282 \u5973\u738b\u965b\u4e0b",content:"<article><h2>\u7b2c\u4e09\u5341\u4e5d\u8282 \u5973\u738b\u965b\u4e0b</h2><p>\u6b7b\u6708\u6cd5\u73e0\u6258\u5728\u8428\u74e6\u5c14\u7684\u638c\u5fc3\uff0c\u5b83\u4eff\u4f5b\u6709\u751f\u547d\u4e00\u822c\uff0c\u4e0d\u65ad\u53d8\u5e7b\u7740\u5927\u5c0f\uff0c\u521a\u62ff\u51fa\u6765\u7684\u65f6\u5019\u5927\u7ea6\u5df4\u638c\u5927\u5c0f\uff0c\u4f46\u8fc7\u4e86\u4e24\u79d2\u949f\u9aa4\u7136\u81a8\u80c0\u4e24\u500d\uff0c\u6b63\u5f53\u743c\u6069\u4ee5\u4e3a\u5b83\u8981\u70b8\u5f00\u7684\u65f6\u5019\uff0c\u6cd5\u73e0\u53c8\u731b\u5730\u7f29\u4e0b\u53bb\uff0c\u6bd4\u5bfb\u5e38\u7684\u6c34\u6676\u7403\u90fd\u8981\u5c0f\u4e0a\u51e0\u5206\u3002</p><p>\u7d2b\u8272\u548c\u9ed1\u8272\u7684\u5149\u5f71\u65cb\u7ed5\uff0c\u4e0d\u65ad\u81ea\u884c\u53d8\u5e7b\u5927\u5c0f\uff0c\u548c\u738b\u5b50\u7ed9\u7684\u8d44\u6599\u4e0a\u5bf9\u6b7b\u6708\u6cd5\u73e0\u7684\u63cf\u8ff0\u4e00\u6a21\u4e00\u6837\u3002</p><p>\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\u771f\u662f\u5947\u5999\u3002</p><p>\u4ece\u4e00\u8fdb\u745e\u5361\u4e4b\u8db3\uff0c\u743c\u6069\u7b49\u4eba\u5c31\u6ce8\u610f\u5230\u4e86\u8fd9\u4e2a\u8428\u74e6\u5c14\uff0c\u6000\u7591\u4ed6\u7684\u8eab\u4efd\uff0c\u4f46\u968f\u5373\u53c8\u5f88\u5feb\u6392\u9664\u4e86\u5acc\u7591\u3002\u8fd9\u4e9b\u5929\u6765\u5728\u57ce\u5185\u63a2\u8bbf\uff0c\u5728\u57ce\u5916\u641c\u7d22\uff0c\u6298\u817e\u4e86\u534a\u5929\u4e00\u65e0\u6240\u83b7\u3002\u6700\u540e\u5f2f\u5b50\u7ed5\u56de\u6765\uff0c\u53d1\u73b0\u8fd8\u662f\u8fd9\u8428\u74e6\u5c14\u5c31\u662f\u4e00\u76f4\u8981\u627e\u7684\u4eba\u3002</p><p>\u4ed6\u770b\u8d77\u6765\u4f3c\u4e4e\u5f88\u5f97\u610f\u2014\u2014\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u975e\u5e38\u975e\u5e38\u7684\u5f97\u610f\u3002\u743c\u6069\u5df2\u7ecf\u88ab\u4ed6\u5b9a\u4f4f\uff0c\u800c\u6885\u83f2\u65af\u5251\u672f\u518d\u9ad8\u8d85\uff0c\u4e5f\u65e0\u6cd5\u6253\u5230\u8eab\u5728\u7a7a\u4e2d\u7684\u654c\u4eba\uff0c\u6240\u4ee5\u4ed6\u73b0\u5728\u5df2\u7ecf\u7a33\u7a33\u7acb\u4e8e\u4e0d\u8d25\u4e4b\u5730\uff0c\u6709\u4ece\u5bb9\u7684\u65f6\u95f4\u6765\u70ab\u8000\u4ed6\u7684\u80dc\u5229\u3002</p><p>\u201c\u6211\u4ece\u4e8c\u5341\u4e03\u5c81\u7684\u65f6\u5019\u5f00\u59cb\uff0c\u6f5c\u5fc3\u7814\u4e60\u7a83\u6cd5\u7684\u5965\u79d8\uff0c\u81f3\u4eca\u5df2\u7ecf\u4e8c\u5341\u5e74\uff0c\u53ea\u5dee\u6700\u540e\u4e00\u6b65\u5c31\u8981\u5927\u529f\u544a\u6210\uff0c\u201d\u4ed6\u4fef\u89c6\u7740\u5730\u4e0a\u7684\u4e24\u4e2a\u5bf9\u624b\uff0c\u8e0c\u8e87\u6ee1\u5fd7\uff0c\u201c\u4ece\u6b64\u4e4b\u540e\uff0c\u9b54\u6cd5\u7684\u5386\u53f2\u5c06\u8981\u91cd\u65b0\u6539\u5199\uff0c\u6211\u594e\u56e0\xb7\u8428\u74e6\u5c14\u7684\u5927\u540d\u5c06\u4f1a\u6c38\u8f7d\u53f2\u518c\uff0c\u4e07\u4e16\u6d41\u4f20\u2026\u2026\u201d</p><p>\u201c\u7a83\u8d3c\u7684\u540d\u58f0\u4f3c\u4e4e\u5e76\u4e0d\u591a\u4e48\u7f8e\u597d\u3002\u201d\u743c\u6069\u51b7\u51b7\u6253\u65ad\u4ed6\u7684\u8bdd\u3002</p><p>\u8428\u74e6\u5c14\u54c8\u54c8\u5927\u7b11\uff0c\u201c\u7b28\u86cb\uff0c\u201d\u4ed6\u5389\u58f0\u559d\u65a5\uff0c\u201c\u7a83\u6cd5\u7684\u771f\u6b63\u8981\u4e49\uff0c\u5728\u4e8e\u628a\u63e1\u9b54\u6cd5\u7684\u672c\u8d28\uff0c\u81ea\u7531\u5730\u8f6c\u6362\u5965\u672f\u548c\u795e\u672f\uff0c\u8de8\u8d8a\u8fd9\u4e9b\u4eba\u4e3a\u7684\u969c\u788d\u548c\u4f20\u7edf\u7684\u58c1\u5792\uff0c\u987a\u7740\u8fd9\u6761\u9053\u8def\u524d\u8fdb\uff0c\u4f60\u5c31\u80fd\u771f\u6b63\u89e6\u6478\u5230\u9b54\u6cd5\u7684\u672c\u6e90\u6240\u5728\u3002\u4f60\u4e5f\u662f\u4e2a\u5deb\u5e08\uff0c\u8fde\u8fd9\u70b9\u90fd\u770b\u4e0d\u51fa\u6765\u5417\uff1f\u201d</p><p>\u743c\u6069\u5fc3\u4e2d\u5fae\u52a8\uff0c\u4ed6\u610f\u8bc6\u5230\u5bf9\u65b9\u6240\u8bf4\u7684\u8bdd\u786e\u5b9e\u6709\u51e0\u5206\u9053\u7406\u3002\u795e\u672f\u5965\u672f\uff0c\u867d\u7136\u901a\u79f0\u9b54\u6cd5\uff0c\u4f46\u5374\u662f\u622a\u7136\u8fe5\u5f02\u7684\u4e24\u79cd\u7c7b\u578b\uff0c\u4e24\u6761\u9053\u8def\u3002\u5728\u6b64\u4e4b\u524d\uff0c\u4ece\u672a\u6709\u4eba\u60f3\u8fc7\u80fd\u591f\u5c06\u795e\u672f\u548c\u5965\u672f\u76f8\u4e92\u8f6c\u6362\uff0c\u8fd9\u4e2a\u8428\u74e6\u5c14\u5374\u80fd\u540c\u65f6\u7a83\u53d6\u795e\u672f\u548c\u5965\u672f\uff0c\u8fd0\u7528\u81ea\u5982\uff0c\u8fd9\u5176\u4e2d\u7684\u610f\u5473\uff0c\u786e\u5b9e\u662f\u5f88\u503c\u5f97\u6df1\u7a76\u3002</p><p>\u6216\u8005\u4fbf\u6b63\u5982\u4ed6\u81ea\u5df1\u6240\u8bf4\uff0c\u8fd9\u5df2\u7ecf\u89e6\u53ca\u5230\u4e86\u9b54\u6cd5\u7684\u771f\u6b63\u672c\u6e90\u6240\u5728\u3002</p><p>\u201c\u4f60\u4eec\u5f88\u5e78\u8fd0\uff0c\u56e0\u4e3a\u6211\u5e76\u4e0d\u559c\u6b22\u6740\u4eba\uff0c\u800c\u4e14\u6211\u73b0\u5728\u9700\u8981\u90e8\u4e0b\uff0c\u201d\u8428\u74e6\u5c14\u8bf4\uff0c\u4ed6\u7684\u8138\u4e0a\u9732\u51fa\u6291\u5236\u4e0d\u4f4f\u7684\u5f97\u610f\u4e4b\u8272\uff0c\u201c\u5f88\u5feb\u6211\u5c31\u8981\u79bb\u5f00\u8fd9\u4e2a\u5730\u65b9\uff0c\u771f\u6b63\u5f00\u521b\u5c5e\u4e8e\u6211\u7684\u8f89\u714c\u5927\u4e1a\uff0c\u6211\u9700\u8981\u51e0\u4e2a\u5f97\u529b\u7684\u52a9\u624b\uff0c\u800c\u4f60\u4eec\u770b\u8d77\u6765\u4f3c\u4e4e\u4e0d\u5dee\u3002\u201d</p><p>\u201c\u5982\u679c\u6211\u4eec\u62d2\u7edd\u5462\uff1f\u201d</p><p>\u201c\u6ca1\u5173\u7cfb\uff0c\u56e0\u4e3a\u6211\u672c\u6765\u5c31\u6ca1\u6709\u6253\u7b97\u5f81\u6c42\u4f60\u4eec\u7684\u610f\u89c1\uff0c\u201d\u8428\u74e6\u5c14\u9634\u9669\u5730\u7b11\u7740\uff0c\u201c\u4f60\u4eec\u65e2\u7136\u6765\u627e\u6b7b\u6708\u6cd5\u73e0\uff0c\u60f3\u5fc5\u5e94\u8be5\u77e5\u9053\u5b83\u7684\u6765\u5386\u3002\u6614\u65e5\u7684\u5927\u5965\u672f\u5e08\u62c9\u6c83\u514b\u5148\u751f\u521b\u9020\u51fa\u5b83\uff0c\u6700\u521d\u7684\u76ee\u7684\u53ef\u662f\u4e3a\u4e86\u80fd\u4fdd\u8bc1\u5c5e\u4e0b\u6c38\u8fdc\u7684\u5fe0\u8bda\uff0c\u201d\u4ed6\u9ad8\u4e3e\u8d77\u624b\u4e2d\u7684\u6b7b\u6708\u6cd5\u73e0\uff0c\u9065\u9065\u5bf9\u7740\u5730\u9762\u4e0a\u7684\u6885\u83f2\u65af\uff0c\u201c\u4f60\u662f\u4e2a\u51fa\u8272\u7684\u5251\u672f\u5bb6\uff0c\u5c0f\u59d0\uff0c\u800c\u4e14\u6211\u77e5\u9053\u4f60\u8eab\u4e0a\u7684\u76ae\u7532\u6709\u62b5\u6297\u9b54\u6cd5\u7684\u80fd\u529b\uff0c\u53ef\u60dc\u8fd9\u4e9b\u5728\u6b7b\u6708\u6cd5\u73e0\u9762\u524d\u4e0d\u582a\u4e00\u51fb\u3002\u53ea\u8981\u7ed9\u6211\u4e94\u79d2\u949f\uff0c\u4f60\u5c31\u4f1a\u5fd8\u6389\u6240\u6709\u7684\u575a\u6301\uff0c\u6210\u4e3a\u6211\u5fe0\u8bda\u7684\u4ec6\u4eba\u2014\u2014\u90a3\u4e48\uff0c\u73b0\u5728\u597d\u597d\u4eab\u53d7\u4f60\u6700\u540e\u7684\u6e05\u9192\u610f\u8bc6\u5427\u3002\u201d</p><p>\u4ed6\u5ff5\u51fa\u4e00\u4e2a\u5b57\u7b26\uff0c\u6b7b\u6708\u6cd5\u73e0\u4e0a\u7684\u7d2b\u8272\u548c\u9ed1\u8272\u5149\u5f71\u7f20\u7ed5\u878d\u5408\u8d77\u6765\uff0c\u4eff\u4f5b\u6c34\u6c14\u4e00\u822c\u5189\u5189\u817e\u8d77\uff0c\u7f13\u7f13\u51dd\u7ed3\uff0c\u773c\u770b\u5c31\u8981\u6210\u578b\u3002\u9aa4\u7136\u4e4b\u95f4\uff0c\u4e00\u9053\u7fe0\u7eff\u8272\u7684\u5c04\u7ebf\u4ece\u4ed6\u4fa7\u4e0b\u65b9\u51fb\u6765\uff0c\u6b63\u6253\u5728\u4ed6\u63e1\u7740\u6b7b\u6708\u6cd5\u73e0\u7684\u53f3\u81c2\u4e0a\u3002</p><p>\u8fd9\u662f\u89e3\u79bb\u672f\uff0c\u582a\u79f0\u53d8\u5316\u5b66\u6d3e\u6700\u5177\u6740\u4f24\u529b\u7684\u9b54\u6cd5\uff0c\u5982\u679c\u6210\u529f\u7684\u8bdd\uff0c\u5b83\u80fd\u76f4\u63a5\u5c06\u6307\u5b9a\u7684\u76ee\u6807\uff08\u65e0\u8bba\u662f\u4eba\u662f\u7269\uff09\u77ac\u95f4\u8f70\u6210\u9f51\u7c89\uff1b\u5c31\u7b97\u5bf9\u65b9\u8db3\u591f\u5f3a\u58ee\uff0c\u80fd\u591f\u786c\u6297\u4e0b\u8fd9\u9053\u6cd5\u672f\u4e0d\u6b7b\uff0c\u4e5f\u540c\u6837\u4f1a\u53d7\u5230\u5de8\u5927\u7684\u4f24\u5bb3\u3002\u4ee5\u743c\u6069\u76ee\u524d\u7684\u9020\u8be3\uff0c\u8fdc\u8fdc\u4e0d\u8db3\u4ee5\u638c\u63e1\u8fd9\u4e48\u5f3a\u529b\u7684\u6cd5\u672f\uff0c\u4ed6\u501f\u52a9\u7684\u662f\u5377\u8f74\u3002\u4ece\u827e\u5c14\u5854\u67cf\u51fa\u53d1\u4e4b\u524d\uff0c\u4ed6\u7d22\u8981\u4e86\u4e00\u5806\u5377\u8f74\uff0c\u5176\u4e2d\u6709\u4e24\u5f20\u89e3\u79bb\u672f\uff0c\u5c31\u662f\u4e3a\u4e86\u5bf9\u4ed8\u5389\u5bb3\u654c\u624b\u800c\u51c6\u5907\u7684\u3002</p><p>\u4ed6\u5e76\u6ca1\u6709\u771f\u6b63\u88ab\u5b9a\u8eab\u672f\u538b\u5236\u4f4f\uff0c\u53ea\u662f\u9707\u60ca\u4e8e\u5bf9\u65b9\u7684\u201c\u7a83\u6cd5\u201d\u80fd\u529b\uff0c\u60f3\u8981\u5957\u51fa\u66f4\u591a\u7684\u4fe1\u606f\u4ee5\u53ca\u6b7b\u6708\u6cd5\u73e0\u7684\u4e0b\u843d\u3002\u5982\u4eca\u89c1\u8428\u74e6\u5c14\u5df2\u7ecf\u62ff\u51fa\u6b7b\u6708\u6cd5\u73e0\uff0c\u987f\u65f6\u559c\u51fa\u671b\u5916\uff0c\u8d81\u7740\u7a83\u6cd5\u8005\u8ddf\u6885\u83f2\u65af\u5e9f\u8bdd\uff0c\u4ed6\u6084\u6084\u634f\u788e\u4e86\u94f6\u767d\u80f8\u9488\uff0c\u7136\u540e\u4ece\u6000\u91cc\u53d6\u51fa\u5377\u8f74\u5c55\u5f00\uff0c\u7528\u6781\u4f4e\u7684\u58f0\u97f3\u5feb\u901f\u5ff5\u5b8c\uff0c\u6210\u529f\u91ca\u653e\u51fa\u4e86\u6cd5\u672f\u3002\u8428\u74e6\u5c14\u5927\u7ea6\u5728\u8fd9\u5c0f\u57ce\u4e2d\u5b89\u9038\u65e5\u5b50\u8fc7\u5f97\u4e45\u4e86\uff0c\u5f97\u610f\u5fd8\u5f62\uff0c\u5168\u7136\u4e0d\u66fe\u9632\u5907\uff0c\u88ab\u89e3\u79bb\u672f\u4e00\u51fb\u800c\u4e2d\u3002</p><p>\u5f88\u53ef\u60dc\uff0c\u5927\u7ea6\u662f\u743c\u6069\u9020\u8be3\u7ec8\u7a76\u4e0d\u8db3\uff0c\u7eb5\u7136\u501f\u52a9\u5377\u8f74\u4e5f\u65e0\u6cd5\u53d1\u6325\u8fd9\u9053\u6cd5\u672f\u7684\u5168\u90e8\u5a01\u529b\u3002\u53ea\u542c\u5f97\u8428\u74e6\u5c14\u5927\u53eb\u4e00\u58f0\uff0c\u6574\u6761\u53f3\u81c2\u77ac\u95f4\u5316\u4f5c\u7c89\u672b\u6563\u843d\uff0c\u539f\u672c\u6258\u5728\u638c\u5fc3\u84c4\u52bf\u5f85\u53d1\u7684\u6b7b\u6708\u6cd5\u73e0\u6389\u843d\u5728\u5730\uff0c\u5495\u565c\u5495\u565c\u6eda\u5230\u4e00\u5757\u5de8\u77f3\u65c1\u8fb9\u53bb\u4e86\u3002\u9aa4\u7136\u88ad\u6765\u7684\u5267\u521b\u8ba9\u4ed6\u7ef4\u6301\u4e0d\u4f4f\u6d6e\u7a7a\u6cd5\u672f\uff0c\u6574\u4e2a\u4eba\u4e5f\u8ddf\u7740\u6454\u4e0b\u6765\u3002</p><p>\u4e00\u76f4\u6212\u5907\u7740\u7684\u6885\u83f2\u65af\u63d0\u5251\u51b2\u4e0a\uff0c\u743c\u6069\u5219\u6025\u901f\u4ece\u6000\u91cc\u518d\u6b21\u6478\u51fa\u4e00\u5f20\u5377\u8f74\u6765\uff0c\u4ed6\u6015\u81ea\u5df1\u65bd\u6cd5\u53c8\u88ab\u8fd9\u7a83\u6cd5\u8005\u7a83\u53bb\uff0c\u7d22\u6027\u53ea\u7528\u5377\u8f74\u3002\u9b54\u6cd5\u5377\u8f74\u662f\u5c06\u6cd5\u672f\u56fa\u5b9a\u5316\u50a8\u5b58\u5728\u7f8a\u76ae\u7eb8\u4e0a\uff0c\u6599\u60f3\u4ed6\u603b\u4e0d\u53ef\u80fd\u8fd8\u80fd\u7a83\u53d6\u3002</p><p>\u8428\u74e6\u5c14\u88ab\u89e3\u79bb\u672f\u7c89\u788e\u4e86\u4e00\u6761\u80f3\u818a\uff0c\u4ece\u7a7a\u4e2d\u6454\u4e0b\u5730\u6765\uff0c\u6599\u60f3\u53d7\u4f24\u4e0d\u8f7b\uff0c\u4f46\u795e\u667a\u5374\u8fd8\u6e05\u9192\uff0c\u773c\u770b\u6885\u83f2\u65af\u51b2\u5230\uff0c\u8fde\u5fd9\u4f38\u624b\u5f80\u5730\u4e0a\u4e00\u62cd\u3002\u8f70\u9686\u9686\u4e00\u58f0\u5de8\u54cd\uff0c\u4ed6\u8eab\u524d\u7684\u5730\u9762\u9aa4\u7136\u88ab\u62c9\u9ad8\uff0c\u8038\u8d77\u4e00\u9762\u539a\u91cd\u7684\u571f\u5899\u6765\u3002</p><p>\u743c\u6069\u6b63\u597d\u8bfb\u5b8c\u4e86\u7b2c\u4e8c\u5f20\u5377\u8f74\uff0c\u518d\u5ea6\u5c04\u51fa\u4e00\u9053\u94f6\u8272\u5149\u7ebf\uff0c\u5374\u88ab\u8fd9\u7a81\u7136\u8038\u8d77\u7684\u571f\u5899\u7ed9\u6321\u4f4f\u4e86\u3002\u6885\u83f2\u65af\u524d\u51b2\u4e4b\u52bf\u4e5f\u88ab\u963b\u4f4f\uff0c\u5979\u7684\u94f6\u5251\u867d\u7136\u950b\u9510\u65e0\u6bd4\uff0c\u4f46\u62ff\u6765\u5288\u8fd9\u571f\u5899\u5374\u6ca1\u4ec0\u4e48\u6548\u679c\u3002\u8428\u74e6\u5c14\u8d81\u7740\u673a\u4f1a\u7ffb\u8eab\u4e00\u6eda\uff0c\u8e09\u8dc4\u7ad9\u4e86\u8d77\u6765\uff0c\u56e0\u4e3a\u7a81\u7136\u5931\u53bb\u4e00\u53ea\u80f3\u818a\uff0c\u4ed6\u4f3c\u4e4e\u6709\u4e9b\u7ad9\u7acb\u4e0d\u7a33\uff0c\u4e00\u65f6\u65e0\u6cd5\u9002\u5e94\u65b0\u7684\u5e73\u8861\uff0c\u4f46\u591a\u5e74\u7684\u4f63\u5175\u751f\u6daf\u4f9d\u65e7\u8ba9\u4ed6\u80fd\u4fdd\u6301\u8d77\u7801\u7684\u51b7\u9759\u3002</p><p>\u6885\u83f2\u65af\u5df2\u7ecf\u4ece\u571f\u5899\u8fb9\u7ed5\u8fc7\u6765\uff0c\u743c\u6069\u6b63\u5728\u51c6\u5907\u53d6\u51fa\u7b2c\u4e09\u5f20\u5377\u8f74\uff0c\u800c\u53d8\u6210\u72ec\u81c2\u7684\u8428\u74e6\u5c14\u8fde\u65bd\u6cd5\u90fd\u5f88\u56f0\u96be\u3002\u4ed6\u867d\u7136\u5e94\u8be5\u662f\u4e2a\u5f88\u9ad8\u660e\u7684\u5deb\u5e08\uff0c\u4e5f\u53ef\u80fd\u51c6\u5907\u4e86\u4e00\u4e9b\u6cd5\u672f\uff0c\u4f46\u90a3\u90fd\u662f\u9700\u8981\u53cc\u624b\u914d\u5408\u5b8c\u6210\u7cbe\u5de7\u7684\u65bd\u6cd5\u52a8\u4f5c\u624d\u80fd\u6fc0\u53d1\u7684\u3002\u5982\u4eca\u7684\u4ed6\u4ec5\u4ec5\u80fd\u5b8c\u6210\u4e00\u4e9b\u7b80\u5355\u7684\u6cd5\u672f\uff0c\u800c\u8fd9\u663e\u7136\u4e0d\u8db3\u4ee5\u62b5\u6321\u5bf9\u624b\u3002</p><p>\u6240\u4ee5\u4ed6\u660e\u667a\u5730\u9009\u62e9\u4e86\u9003\u8dd1\u3002</p><p>\u4e00\u628a\u626f\u4e0b\u8116\u5b50\u4e0a\u7684\u4f2a\u9020\u5723\u5fbd\uff0c\u63e1\u5728\u638c\u5fc3\u634f\u788e\uff0c\u94f6\u8272\u7684\u4f20\u9001\u95e8\u5728\u4ed6\u8eab\u540e\u6025\u901f\u5212\u5f00\u3002\u201c\u6211\u5927\u610f\u4e86\uff0c\u201d\u4ed6\u72e0\u72e0\u5730\u8bf4\uff0c\u201c\u6211\u4eec\u540e\u4f1a\u6709\u671f\uff01\u201d</p><p>\u6885\u83f2\u65af\u7684\u94f6\u5251\u5288\u5f00\uff0c\u4f46\u5df2\u7ecf\u8fdf\u4e86\u534a\u6b65\uff0c\u8428\u74e6\u5c14\u9000\u8fdb\u4e86\u4f20\u9001\u95e8\u4e2d\uff0c\u8eab\u5f71\u77ac\u95f4\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u203b\u203b\u203b</p><p>\u7a83\u6cd5\u8005\u867d\u7136\u9003\u8d70\uff0c\u4f46\u5bf9\u4e8e\u743c\u6069\u548c\u6885\u83f2\u65af\u6765\u8bf4\u5e76\u975e\u5931\u8d25\uff0c\u4ed6\u4eec\u7684\u76ee\u7684\u539f\u672c\u4e5f\u5c31\u662f\u6b7b\u6708\u6cd5\u73e0\uff0c\u800c\u5982\u4eca\u90a3\u4e1c\u897f\u6b63\u9759\u9759\u5730\u8eba\u5728\u4e00\u5757\u5de8\u77f3\u811a\u4e0b\u3002</p><p>\u5954\u6ce2\u4e00\u573a\uff0c\u7ec8\u4e8e\u6709\u4e86\u7ed3\u679c\u3002</p><p>\u6885\u83f2\u65af\u662f\u5723\u6b66\u58eb\uff0c\u540c\u6837\u4e0d\u80fd\u8f7b\u6613\u78b0\u6b7b\u6708\u6cd5\u73e0\uff0c\u6240\u4ee5\u743c\u6069\u7406\u6240\u5f53\u7136\u5730\u4e0a\u524d\u51c6\u5907\u53bb\u62e3\uff0c\u7136\u800c\u5c31\u5728\u4ed6\u8fd8\u5dee\u51e0\u6b65\u8fdc\u7684\u65f6\u5019\uff0c\u5de8\u77f3\u540e\u9762\u8df3\u51fa\u4e00\u4e2a\u7ea2\u8863\u5973\u5b69\u6765\uff0c\u4e00\u628a\u5c06\u6cd5\u73e0\u6293\u5728\u624b\u91cc\u3002</p><p>\u201c\u6211\u62ff\u5230\u4e86\uff01\u201d\u5979\u5f97\u610f\u5730\u5411\u743c\u6069\u548c\u6885\u83f2\u65af\u5ba3\u5e03\u3002</p><p>\u201c\u51db\uff1f\u201d\u743c\u6069\u4e00\u6014\uff0c\u201c\u4f60\u600e\u4e48\u2026\u2026\u201d</p><p>\u201c\u54fc\uff0c\u60f3\u4e22\u4e0b\u6211\u4e00\u4e2a\u4eba\uff0c\u54ea\u6709\u90a3\u4e48\u5bb9\u6613\uff0c\u201d\u5979\u795e\u6c14\u5730\u6307\u7740\u743c\u6069\uff0c\u201c\u4f60\u4eec\u8fd8\u6ca1\u51fa\u57ce\u6211\u5c31\u53d1\u73b0\u4e86\uff0c\u4e00\u8def\u8ddf\u7740\u5462\u3002\u201d</p><p>\u8fd9\u771f\u662f\u87b3\u8782\u6355\u8749\u9ec4\u96c0\u5728\u540e\u4e86\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u8ddf\u8e2a\u7740\u8428\u74e6\u5c14\uff0c\u6ca1\u60f3\u5230\u540e\u9762\u5c45\u7136\u53c8\u8ddf\u8e2a\u7740\u51db\u3002\u4e00\u8def\u4e0a\u8fd9\u4e48\u957f\u65f6\u95f4\u5c45\u7136\u90fd\u6ca1\u53d1\u89c9\uff0c\u5b9e\u5728\u662f\u6709\u591f\u5931\u8d25\u7684\u3002\u201c\u90a3\u4f60\u521a\u624d\u90fd\u4e0d\u5e2e\u5fd9\uff1f\u201d</p><p>\u201c\u6211\u77e5\u9053\u4f60\u6709\u90a3\u9897\u6212\u6307\uff0c\u6839\u672c\u5c31\u6ca1\u88ab\u5b9a\u4f4f\uff0c\u5f53\u7136\u5c31\u4e0d\u7528\u6211\u5e2e\u5fd9\u4e86\u3002\u201d</p><p>\u201c\u597d\u5427\u597d\u5427\uff0c\u201d\u743c\u6069\u4e0d\u60f3\u591a\u8bf4\uff0c\u201c\u628a\u6cd5\u73e0\u7ed9\u6211\u3002\u201d</p><p>\u201c\u51ed\u4ec0\u4e48\uff0c\u201d\u51db\u50cf\u4e2a\u5b69\u5b50\u4f3c\u7684\u628a\u7d27\u7d27\u62a4\u7740\u6b7b\u6708\u6cd5\u73e0\uff0c\u4eff\u4f5b\u90a3\u90aa\u6076\u5371\u9669\u7684\u795e\u5668\u662f\u4e2a\u4ec0\u4e48\u4e86\u4e0d\u8d77\u7684\u73a9\u5177\u4f3c\u7684\uff0c\u201c\u8fd9\u662f\u6211\u627e\u5230\u7684\uff0c\u522b\u8ddf\u6211\u62a2\u3002\u201d</p><p>\u201c\u8c01\u8ddf\u4f60\u62a2\uff0c\u201d\u743c\u6069\u90fd\u6709\u4e9b\u65e0\u5948\u4e86\uff0c\u201c\u8fd9\u4e1c\u897f\u6211\u4eec\u53c8\u7528\u4e0d\u4e86\uff0c\u662f\u8981\u62ff\u56de\u53bb\u4ea4\u5dee\u7684\u3002\u201d</p><p>\u201c\u90a3\u4e5f\u8981\u6211\u6765\u4fdd\u7ba1\uff0c\u5230\u65f6\u5019\u6709\u4ec0\u4e48\u5956\u52b1\u90fd\u5f52\u6211\uff0c\u4f60\u4e0d\u51c6\u62a2\u3002\u201d</p><p>\u743c\u6069\u76b1\u7740\u7709\uff0c\u6b62\u4f4f\u51c6\u5907\u8bf4\u8bdd\u7684\u6885\u83f2\u65af\uff0c\u53cd\u6b63\u4ed6\u5df2\u7ecf\u634f\u788e\u4e86\u80f8\u9488\uff0c\u738b\u5b50\u5e94\u8be5\u5f97\u5230\u8baf\u606f\uff0c\u7acb\u523b\u5c31\u4f1a\u8d76\u6765\uff0c\u9664\u975e\u4ed6\u4e0d\u8981\u6b7b\u6708\u6cd5\u73e0\u4e86\uff0c\u201c\u884c\u884c\uff0c\u90a3\u4f60\u4fdd\u7ba1\u597d\uff0c\u201d\u4ed6\u8bf4\uff0c\u7b49\u5f85\u7740\uff0c\u201c\u6ca1\u4eba\u8ddf\u4f60\u62a2\u3002\u201d</p><p>\u201c\u4e00\u8a00\u4e3a\u5b9a\uff1f\u201d\u51db\u8ffd\u95ee\u3002</p><p>\u201c\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d\u743c\u6069\u80cc\u540e\u7a81\u7136\u6709\u4e2a\u964c\u751f\u7684\u58f0\u97f3\u8bf4\u3002</p><p>\u90a3\u58f0\u97f3\u4f4e\u6c89\u4e2d\u7565\u5e26\u6c99\u54d1\uff0c\u9690\u9690\u6709\u91d1\u5c5e\u94ff\u9535\u78b0\u649e\u7684\u8282\u594f\uff0c\u5e76\u4e0d\u54cd\u4eae\uff0c\u5374\u9707\u5f97\u4eba\u5fc3\u4e2d\u9690\u9690\u53d1\u98a4\u3002\u743c\u6069\u611f\u89c9\u81ea\u5df1\u5168\u8eab\u7684\u6bdb\u5b54\u9aa4\u7136\u95f4\u90fd\u6536\u7f29\u8d77\u6765\uff0c\u4e00\u80a1\u5bd2\u6c14\u4ece\u80cc\u4e0a\u5347\u8d77\uff0c\u4ed6\u52aa\u529b\u904f\u5236\u7740\u60f3\u8981\u98a4\u6296\u7684\u8eab\u4f53\uff0c\u6162\u6162\u8f6c\u8fc7\u8eab\u6765\uff0c\u7136\u540e\u4ed6\u770b\u89c1\u4e86\u4e00\u4e2a\u5168\u8eab\u7ea2\u888d\u7684\u5bb6\u4f19\u6b63\u60a0\u95f2\u5730\u5750\u5728\u4e00\u5757\u5de8\u77f3\u7684\u9876\u4e0a\u3002</p><p>\u5149\u5934\u4e0a\u7eb9\u7740\u523a\u9752\uff0c\u6d53\u91cd\u7c97\u9ed1\u7684\u7709\u6bdb\u2026\u2026\u6709\u70b9\u50cf\u8721\u7b14\u5c0f\u65b0\uff0c\u9ad8\u9ad8\u8038\u8d77\u7684\u98a7\u9aa8\u548c\u7d27\u62bf\u7740\u7684\u5634\u5507\uff0c\u900f\u51fa\u4e00\u80a1\u51f6\u5389\u4e4b\u6c14\uff0c\u7d2b\u9ed1\u8272\u7684\u8138\u4e0a\u6ca1\u4ec0\u4e48\u8868\u60c5\u3002\u743c\u6069\u9690\u7ea6\u597d\u50cf\u8bb0\u5f97\u4ee5\u524d\u89c1\u8fc7\u8fd9\u4e2a\u4eba\uff0c\u4f46\u6ca1\u4ec0\u4e48\u592a\u6df1\u5370\u8c61\uff0c\u5e94\u8be5\u53ea\u662f\u4e00\u9762\u4e4b\u7f18\uff0c\u4e00\u65f6\u95f4\u60f3\u4e0d\u8d77\u6765\uff0c\u65c1\u8fb9\u7684\u51db\u5df2\u7ecf\u53eb\u51fa\u4e86\u8fd9\u4e2a\u4eba\u7684\u540d\u5b57\u3002</p><p>\u201c\u8ff0\u5c14\u5148\u751f\uff1f\u201d</p><p>\u51db\u7684\u8001\u5e08\uff0c\u7ea2\u888d\u5deb\u5e08\u4f1a\u5851\u80fd\u5b66\u6d3e\u9996\u5e2d\uff0c\u963f\u5179\u7eb3\xb7\u8ff0\u5c14\u3002</p><p>\u203b\u203b\u203b</p><p>\u743c\u6069\u540e\u9000\u4e00\u6b65\uff0c\u62c9\u4f4f\u4e86\u6885\u83f2\u65af\uff0c\u6b64\u65f6\u53d6\u4f20\u9001\u672f\u5377\u8f74\u5df2\u7ecf\u6765\u4e0d\u53ca\uff0c\u4f46\u4ed6\u8fd8\u51c6\u5907\u4e86\u4efb\u610f\u95e8\u3002\u5492\u6587\u6025\u901f\u81ea\u4ed6\u8111\u4e2d\u6d6e\u73b0\uff0c\u53ea\u8981\u8bf4\u51fa\u4e00\u4e2a\u5b57\uff0c\u4ed6\u5c31\u80fd\u5e26\u7740\u6885\u83f2\u65af\u5148\u79bb\u5f00\u6b64\u5730\uff0c\u518d\u505a\u6253\u7b97\u3002</p><p>\u4f46\u5c31\u5728\u6cd5\u672f\u5b8c\u6210\u7684\u524d\u4e00\u77ac\u95f4\uff0c\u4ed6\u5fc3\u5934\u9aa4\u7136\u95ea\u8fc7\u4e00\u4e1d\u8b66\u5146\uff0c\u4eff\u4f5b\u81ea\u5df1\u6b63\u5728\u505a\u4e00\u4ef6\u6781\u5176\u5371\u9669\u7684\u4e8b\u60c5\u3002\u7d27\u63a5\u7740\uff0c\u8ff0\u5c14\u8f7b\u8f7b\u6447\u4e86\u6447\u5934\u3002</p><p>\u201c\u522b\u51b2\u52a8\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6b64\u5730\u5df2\u7ecf\u5e03\u4e0b\u7a7a\u95f4\u67b7\u9501\uff0c\u4efb\u4f55\u4f20\u9001\u6cd5\u672f\u90fd\u4e0d\u4f1a\u6210\u529f\u7684\u3002\u201d</p><p>\u8ff0\u5c14\u4f38\u624b\u671d\u521a\u624d\u8428\u74e6\u5c14\u6d88\u5931\u7684\u5730\u65b9\u4e00\u6307\uff0c\u201c\u89e3\u9664\u3002\u201d\u4e00\u9635\u767d\u8272\u96fe\u6c14\u63a0\u8fc7\uff0c\u539f\u672c\u7a7a\u65e0\u4e00\u4eba\u7684\u5730\u4e0a\u7a81\u7136\u73b0\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u6b63\u662f\u5e94\u8be5\u5df2\u7ecf\u4f20\u9001\u8d70\u7684\u8428\u74e6\u5c14\uff0c\u4ed6\u4f3c\u4e4e\u88ab\u4ec0\u4e48\u6cd5\u672f\u5b9a\u4f4f\u4e86\uff0c\u6014\u6014\u7ad9\u5728\u539f\u5730\u4e00\u52a8\u4e0d\u52a8\u3002</p><p>\u201c\u53e6\u5916\u518d\u8bf4\u4e00\u53e5\uff0c\u6240\u6709\u7684\u4f20\u8baf\u6cd5\u672f\u540c\u6837\u4e5f\u88ab\u963b\u9694\u4e86\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5e03\u96f7\u7eb3\u65af\u6bbf\u4e0b\u5e76\u4e0d\u4f1a\u5f97\u5230\u4f60\u7684\u8baf\u606f\uff0c\u201d\u8ff0\u5c14\u4e0d\u7d27\u4e0d\u6162\u5730\u9648\u8ff0\u7740\uff0c\u4ece\u5de8\u77f3\u4e0a\u7ad9\u4e86\u8d77\u6765\uff0c\u201c\u6240\u4ee5\u6211\u5efa\u8bae\u4f60\u548c\u4f60\u7684\u670b\u53cb\u6700\u597d\u80fd\u4fdd\u6301\u5b89\u9759\uff0c\u56e0\u4e3a\u6211\u5e76\u4e0d\u60f3\u88ab\u8feb\u7834\u574f\u548c\u9634\u9b42\u57ce\u7684\u53cb\u8c0a\u3002\u4e8b\u5b9e\u4e0a\uff0c\u6211\u76f8\u4fe1\u5982\u679c\u6ca1\u6709\u8428\u624e\u65af\u5766\uff0c\u6211\u4eec\u4e24\u56fd\u7684\u5408\u4f5c\u4f1a\u66f4\u52a0\u878d\u6d3d\u548c\u6109\u5feb\u3002\u201d</p><p>\u4ed6\u62ac\u624b\u865a\u865a\u4e00\u6309\uff0c\u7a7a\u6c14\u4e2d\u51fa\u73b0\u4e24\u53ea\u529b\u573a\u5851\u6210\u7684\u94f6\u767d\u8272\u5de8\u638c\uff0c\u540c\u65f6\u6293\u4e0b\uff0c\u5c06\u743c\u6069\u548c\u6885\u83f2\u65af\u7262\u7262\u56f0\u4f4f\u3002\u63a5\u7740\u4ed6\u8f6c\u8fc7\u8138\u6765\uff0c\u770b\u5411\u6b63\u62b1\u7740\u6b7b\u6708\u6cd5\u73e0\u4e0d\u77e5\u6240\u63aa\u7684\u51db\u3002</p><p>\u201c\u7ed9\u6211\u3002\u201d\u8ff0\u5c14\u8bf4\u3002</p><p>\u51db\u5374\u6ca1\u6709\u542c\u4ed6\u7684\u8bdd\uff0c\u53cd\u800c\u5f80\u540e\u9000\u7740\uff0c\u4f3c\u4e4e\u60f3\u8981\u9003\u8d70\u3002\u8ff0\u5c14\u4e5f\u4e0d\u591a\u8bf4\uff0c\u4f9d\u65e7\u91ca\u653e\u51fa\u4e00\u4e2a\u529b\u573a\u9b54\u6cd5\u6765\uff0c\u5c06\u51db\u4e5f\u6b7b\u6b7b\u5b9a\u4f4f\uff0c\u4f38\u624b\u4e00\u6307\uff0c\u6b7b\u6708\u6cd5\u73e0\u4fbf\u81ea\u52a8\u98de\u4e86\u8d77\u6765\uff0c\u60ac\u5728\u7a7a\u4e2d\u3002</p><p>\u5468\u56f4\u5de8\u77f3\u9876\u4e0a\uff0c\u53c8\u73b0\u51fa\u4e24\u4e2a\u7ea2\u888d\u5deb\u5e08\u7684\u8eab\u5f71\u6765\uff0c\u5176\u4e2d\u4e00\u4f4d\u662f\u7537\u6027\uff0c\u957f\u5f97\u9887\u4e3a\u82f1\u4fca\uff1b\u53e6\u5916\u4e00\u4f4d\u5219\u662f\u4e2a\u5973\u6027\uff0c\u9762\u5bb9\u6194\u60b4\uff0c\u8863\u886b\u4e0d\u6574\uff0c\u770b\u8d77\u6765\u50cf\u662f\u6ca1\u7761\u9192\u4f3c\u7684\u3002</p><p>\u7ea2\u888d\u5deb\u5e08\u4f1a\u9644\u9b54\u5b66\u6d3e\u9996\u5e2d\u52b3\u6cfd\u745e\u5c14\u5148\u751f\u548c\u9632\u62a4\u5b66\u6d3e\u9996\u5e2d\u857e\u62c9\u82ae\u5973\u58eb\u3002</p><p>\u52b3\u6cfd\u745e\u5c14\u7d20\u6765\u662f\u8428\u624e\u65af\u5766\u7684\u575a\u5b9a\u53cd\u5bf9\u8005\uff0c\u857e\u62c9\u82ae\u5219\u548c\u8ff0\u5c14\u4e00\u6837\uff0c\u539f\u672c\u7b97\u662f\u8428\u624e\u65af\u5766\u4e00\u6d3e\uff0c\u53ea\u662f\u5e76\u4e0d\u53ef\u9760\u3002\u73b0\u5728\u770b\u6765\uff0c\u8fd9\u4e24\u4f4d\u7ea2\u888d\u9996\u5e2d\u90fd\u5df2\u7ecf\u6697\u4e2d\u8f6c\u6295\u9635\u8425\u3002\u5982\u6b64\u4e00\u6765\uff0c\u53bb\u6389\u6e38\u79bb\u4e8b\u5916\u7684\u4e24\u4f4d\u9996\u5e2d\uff0c\u8428\u624e\u65af\u5766\u4e00\u6d3e\u548c\u53cd\u8428\u624e\u65af\u5766\u4e00\u6d3e\u4eba\u6570\u76f8\u5f53\uff0c\u52bf\u5747\u529b\u654c\u4e86\u3002</p><p>\u4e0d\uff0c\u4e0d\u662f\u52bf\u5747\u529b\u654c\u3002</p><p>\u4e00\u4e2a\u53c8\u4e00\u4e2a\u9762\u8272\u82cd\u767d\u5f62\u5bb9\u67af\u69c1\u7684\u5bb6\u4f19\u4ece\u5bc6\u6797\u4e2d\u8d70\u8fdb\u6765\uff0c\u624b\u4e2d\u90fd\u62c4\u7740\u767d\u9aa8\u624b\u6756\uff0c\u9886\u5934\u7684\u662f\u4e00\u4f4d\u7a7f\u7740\u7d2b\u888d\u7684\u8001\u4eba\uff0c\u4ed6\u7684\u80f8\u53e3\u7ee3\u7740\u4e00\u4e2a\u5934\u6234\u738b\u51a0\u54a7\u5634\u5927\u7b11\u7684\u9ab7\u9ac5\u5934\uff0c\u90a3\u662f\u5deb\u5996\u4e4b\u795e\u7ef4\u6c99\u4f26\u7684\u5723\u5fbd\u3002\u4ed6\u62ac\u8d77\u5934\uff0c\u671d\u7ad9\u5728\u5de8\u77f3\u9876\u4e0a\u7684\u4e09\u4f4d\u7ea2\u888d\u9996\u5e2d\u70b9\u5934\u884c\u793c\u3002</p><p>\u201c\u6211\u4eec\u5148\u56de\u666e\u6765\u4e9a\u94ce\u2026\u2026\u201d</p><p>\u201c\u4e0d\uff0c\u201d\u8ff0\u5c14\u6253\u65ad\u4e86\u7ef4\u6c99\u4f26\u9996\u5e2d\u7267\u5e08\u7684\u8bdd\uff0c\u201c\u5c31\u5728\u8fd9\u91cc\u4e3e\u884c\u4eea\u5f0f\u3002\u201d</p><p>\u201c\u4f46\u8fd9\u91cc\u5e76\u975e\u795e\u6bbf\uff0c\u201d\u9996\u5e2d\u7267\u5e08\u4e0d\u60a6\uff0c\u201c\u8fd9\u662f\u5bf9\u6211\u4e3b\u7684\u4e0d\u656c\u3002\u201d</p><p>\u201c\u6211\u4eec\u6ca1\u6709\u65f6\u95f4\uff0c\u201d\u8ff0\u5c14\u8bf4\uff0c\u201c\u857e\u62c9\u82ae\u5973\u58eb\u7684\u6cd5\u672f\u5e76\u4e0d\u80fd\u6c38\u4e45\u963b\u9694\u9884\u8a00\u5e08\u7684\u5360\u535c\uff0c\u4ed6\u4eec\u6700\u591a\u534a\u5929\u5c31\u80fd\u627e\u5230\u6211\u4eec\uff0c\u5728\u6b64\u4e4b\u524d\u6211\u4eec\u5fc5\u987b\u5b8c\u6210\u51c6\u5907\u5de5\u4f5c\u3002\u800c\u5e26\u7740\u6b7b\u6708\u6cd5\u73e0\uff0c\u6211\u4eec\u662f\u6ca1\u529e\u6cd5\u4f7f\u7528\u4f20\u9001\u7684\u3002\u201d</p><p>\u53e6\u5916\u4e24\u4f4d\u7ea2\u888d\u9996\u5e2d\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u201c\u597d\u5427\uff0c\u201d\u9996\u5e2d\u7267\u5e08\u59a5\u534f\uff0c\u201c\u4f46\u6211\u4eec\u8fd8\u7f3a\u5c11\u796d\u54c1\u3002\u201d</p><p>\u201c\u8fd9\u4e0d\u662f\u4e48\uff0c\u201d\u8ff0\u5c14\u6307\u5411\u743c\u6069\u7b49\u4eba\uff0c\u201c\u9664\u4e86\u5170\u5c3c\u65af\u7279\u5148\u751f\u548c\u8fd9\u4f4d\u7a83\u6cd5\u8005\uff0c\u53e6\u5916\u4e24\u4f4d\u5c0f\u59d0\u90fd\u662f\u975e\u5e38\u5408\u9002\u7684\u796d\u54c1\u2014\u2014\u6211\u76f8\u4fe1\u7ef4\u6c99\u4f26\u9601\u4e0b\u4e00\u5b9a\u4f1a\u559c\u6b22\u7684\u3002\u201d</p><p>\u51db\u662f\u8ff0\u5c14\u7684\u5b66\u751f\uff0c\u4f46\u8ff0\u5c14\u8a00\u8bed\u4e4b\u95f4\uff0c\u5bf9\u8fd9\u4f4d\u5b66\u751f\u7684\u6027\u547d\u4f3c\u4e4e\u5168\u4e0d\u5728\u610f\uff0c\u8054\u60f3\u5230\u521a\u624d\u51db\u79f0\u547c\u8ff0\u5c14\uff0c\u4e5f\u662f\u76f4\u547c\u5176\u540d\u800c\u4e0d\u662f\u8bf4\u201c\u8001\u5e08\u201d\uff0c\u8fd9\u5bf9\u5e08\u751f\u7684\u5173\u7cfb\uff0c\u770b\u6765\u9887\u6709\u4e9b\u8e4a\u8df7\u3002</p><p>\u7ef4\u6c99\u4f26\u7684\u7267\u5e08\u88ab\u8bf4\u670d\u4e86\uff0c\u4ed6\u5f00\u59cb\u6307\u6325\u624b\u4e0b\u5f00\u59cb\u5e03\u7f6e\u4e34\u65f6\u7684\u796d\u575b\uff0c\u51c6\u5907\u4e3e\u884c\u4eea\u5f0f\u7948\u8bf7\u795e\u7947\u964d\u4e34\u3002\u743c\u6069\u7b49\u4eba\u88ab\u63a8\u5230\u4e00\u8fb9\uff0c\u4ed6\u4eec\u88ab\u8ff0\u5c14\u7684\u529b\u573a\u9b54\u6cd5\u675f\u7f1a\u4f4f\u4e86\uff0c\u5b8c\u5168\u65e0\u6cd5\u52a8\u5f39\uff0c\u81ea\u7136\u4e5f\u6ca1\u529e\u6cd5\u65bd\u6cd5\u9003\u8131\u3002</p><p>\u8f6c\u773c\u4e4b\u95f4\uff0c\u4e34\u65f6\u796d\u575b\u5df2\u7ecf\u5e03\u6210\u3002\u6240\u6709\u7ef4\u6c99\u4f26\u6559\u5f92\u530d\u5310\u5728\u5730\uff0c\u9ad8\u58f0\u7948\u7977\uff0c\u8d5e\u9882\u795e\u7947\u3002\u743c\u6069\u5bf9\u8fd9\u5957\u795e\u68cd\u628a\u620f\u6beb\u65e0\u5174\u8da3\uff0c\u4ed6\u7126\u6025\u7684\u662f\u4e00\u65e6\u7948\u7977\u5b8c\u6210\uff0c\u53ea\u6015\u5c31\u8981\u5f00\u59cb\u796d\u7940\uff0c\u5230\u65f6\u5019\u6885\u83f2\u65af\u548c\u51db\u5c31\u8981\u88ab\u5f53\u4f5c\u796d\u54c1\u6740\u6b7b\u4e86\u3002</p><p>\u201c\u8be5\u6b7b\uff0c\u4e00\u70b9\u90fd\u52a8\u4e0d\u4e86\u2026\u2026\u201d</p><p>\u4e8b\u5b9e\u4e0a\uff0c\u5c31\u7b97\u80fd\u591f\u52a8\u5f39\uff0c\u4ed6\u4e5f\u5168\u65e0\u628a\u63e1\u5728\u4e09\u4f4d\u7ea2\u888d\u9996\u5e2d\u5927\u5deb\u5e08\u548c\u4e00\u7fa4\u7ef4\u6c99\u4f26\u6559\u5f92\u7684\u91cd\u91cd\u5305\u56f4\u4e0b\u8131\u8eab\uff0c\u66f4\u522b\u8bf4\u6551\u4eba\u4e86\u3002\u4e09\u4f4d\u7ea2\u888d\u9996\u5e2d\u4e2d\uff0c\u857e\u62c9\u82ae\u5973\u58eb\u7cbe\u7814\u9632\u62a4\u6cd5\u672f\uff0c\u5979\u5728\u8fd9\u4e00\u533a\u57df\u5e03\u4e0b\u4e86\u7a7a\u95f4\u67b7\u9501\uff0c\u5c01\u95ed\u4e86\u4e00\u5207\u4f20\u9001\u6cd5\u672f\uff0c\u8ba9\u743c\u6069\u5c31\u7b97\u60f3\u9003\u547d\u90fd\u529e\u4e0d\u5230\u3002</p><p>\u6b63\u81ea\u7126\u6025\u7684\u65f6\u5019\uff0c\u7a81\u7136\u6709\u4e00\u53ea\u4e4c\u9e26\u4ece\u5934\u4e0a\u98de\u4e86\u8fc7\u6765\uff0c\u843d\u5230\u4e00\u5757\u5de8\u77f3\u9876\u4e0a\u3002\u539f\u672c\u9ed8\u5750\u4e0d\u52a8\u7684\u4e09\u4f4d\u7ea2\u888d\u9996\u5e2d\u4eff\u4f5b\u88ab\u60ca\u52a8\u4e86\u4e00\u822c\uff0c\u5982\u4e34\u5927\u654c\u5730\u4e00\u8d77\u7ad9\u4e86\u8d77\u6765\uff0c\u76ef\u7740\u90a3\u53ea\u60a0\u7136\u81ea\u5f97\u7684\u4e4c\u9e26\u3002\u5404\u79cd\u8000\u773c\u7684\u9b54\u6cd5\u7075\u5149\u4ece\u4ed6\u4eec\u8eab\u4e0a\u7efd\u653e\u6d6e\u73b0\uff0c\u8fd9\u662f\u542f\u52a8\u5404\u79cd\u6cd5\u672f\u9632\u5fa1\u7684\u8baf\u53f7\u3002</p><p>\u743c\u6069\u770b\u5f97\u6115\u7136\u60ca\u8bb6\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e09\u4f4d\u7ea2\u888d\u9996\u5e2d\u5728\u641e\u4ec0\u4e48\u540d\u5802\uff0c\u6709\u5fc5\u8981\u5bf9\u4e00\u53ea\u4e4c\u9e26\u5982\u6b64\u9632\u5907\uff0c\u773c\u89c1\u4f59\u5149\u77a5\u89c1\u51db\u7684\u8138\u4e0a\u9732\u51fa\u5f00\u5fc3\u7684\u7b11\u5bb9\u6765\uff0c\u7d27\u63a5\u7740\u5374\u53c8\u6362\u6210\u4e86\u6cae\u4e27\u795e\u8272\uff0c\u201c\u8fd9\u4e0b\u5b50\u5b8c\u86cb\u4e86\uff0c\u201d\u5979\u561f\u56d4\u7740\uff0c\u201c\u88ab\u8001\u5e08\u627e\u5230\u4e86\u3002\u201d</p><p>\u5f88\u663e\u7136\uff0c\u5979\u53e3\u4e2d\u6240\u8bf4\u7684\u8001\u5e08\uff0c\u5e76\u4e0d\u662f\u7ea2\u888d\u5851\u80fd\u9996\u5e2d\u8ff0\u5c14\u5148\u751f\u3002</p><p>\u4f34\u968f\u7740\u9690\u7ea6\u4f20\u6765\u7684\u97f3\u4e50\u58f0\uff0c\u90a3\u53ea\u4e4c\u9e26\u7684\u8eab\u4f53\u626d\u66f2\u7740\u9010\u6e10\u53d8\u5927\uff0c\u6700\u7ec8\u5316\u4f5c\u4e00\u4f4d\u8eab\u6750\u9ad8\u6311\u7684\u5973\u5b50\uff0c\u5979\u7a7f\u7740\u4e00\u4ef6\u7834\u7834\u70c2\u70c2\u7684\u9ed1\u888d\uff0c\u94f6\u8272\u79c0\u53d1\u7eb7\u4e71\u62ab\u6563\u5728\u80a9\u5934\uff0c\u53cc\u773c\u4e2d\u4eff\u4f5b\u6709\u706b\u7130\u5728\u8df3\u8dc3\u7740\uff0c\u95ea\u70c1\u7740\u4ee4\u4eba\u754f\u60e7\u7684\u5149\u8292\u3002\u5979\u65c1\u82e5\u65e0\u4eba\u5730\u5750\u5728\u5de8\u77f3\u9876\u4e0a\uff0c\u867d\u7136\u8863\u9970\u4e0d\u6574\uff0c\u795e\u6001\u5374\u7aef\u5e84\u4f18\u96c5\u5f97\u4eff\u4f5b\u4e00\u4f4d\u5973\u738b\u3002</p><p>\u8ff0\u5c14\u7684\u8138\u4e0a\u9732\u51fa\u5fae\u7b11\u6765\uff0c\u5fae\u5fae\u8eac\u8eab\u884c\u793c\uff0c\u201c\u5f88\u4e45\u4e0d\u89c1\u4e86\uff0c\u98ce\u66b4\u5973\u738b\u965b\u4e0b\uff0c\u201d\u4ed6\u5ba2\u6c14\u5730\u8bf4\uff0c\u201c\u60a8\u6765\u6b64\u6709\u4f55\u8d35\u5e72\u3002\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u201d\u5973\u5b50\u8bf4\uff0c\u58f0\u97f3\u5e73\u6de1\u4e2d\u5e26\u7740\u4e9b\u6175\u61d2\u7684\u611f\u89c9\uff0c\u201c\u53ea\u662f\uff0c\u6765\u9001\u4f60\u4e0b\u5730\u72f1\u7f62\u4e86\u3002\u201d</p></article>"}}}]);
