(window.webpackJsonp=window.webpackJsonp||[]).push([[204],{227:function(p){p.exports={chapterTitle:null,title:"\u7b2c1178\u7ae0 \u53eb\u7834\u5589\u5499\u4e5f\u6ca1\u6709\u7528",content:"<article><h2>\u7b2c1178\u7ae0 \u53eb\u7834\u5589\u5499\u4e5f\u6ca1\u6709\u7528</h2><p>\u3000\u3000\u542c\u9648\u5983\u84c9\u8f6c\u8ff0\u5979\u4e0e\u5510\u5a67\u65e0\u95f4\u610f\u542c\u5230\u7684\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u73cf\u592b\u5987\u7684\u79c1\u623f\u8bed\uff0c\u5f20\u606a\u7ad9\u5728\u7a97\u6237\u65c1\u6c57\u6d41\u6d43\u80cc\uff0c\u4efb\u4ed6\u518d\u539a\u7684\u8138\u76ae\uff0c\u518d\u8d70\u5230\u5b59\u5c1a\u4e49\u7684\u8ddf\u524d\uff0c\u90fd\u89c9\u5f97\u8138\u76ae\u6709\u4e9b\u53d1\u70eb\u3002\u4ed6\u672c\u6765\u671f\u5f85\u7740\u80fd\u6709\u673a\u4f1a\u8ddf\u5b59\u9759\u6aac\u6345\u7834\u7537\u5973\u4e4b\u95f4\u6700\u540e\u90a3\u5c42\u7a97\u6237\u7eb8\uff0c\u54ea\u91cc\u60f3\u5230\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u73cf\u592b\u5987\u773c\u5149\u8001\u9053\u65e9\u6709\u8b66\u89c9\uff1f\u504f\u504f\u8fd9\u6837\u7684\u79c1\u623f\u8bed\u8fd8\u4f20\u5230\u5510\u5a67\u7684\u8033\u6735\u91cc\uff0c\u96be\u602a\u5510\u5a67\u901b\u8857\u56de\u6765\u7684\u6001\u5ea6\u8ddf\u5e73\u65e5\u6709\u4e9b\u4e0d\u4e00\u6837\u3002</p><p>\u3000\u3000\u5f20\u606a\u987f\u65f6\u89c9\u5f97\u5934\u5927\u4e86\u4e09\u5206\uff0c\u8fd9\u6b21\u662f\u5f7b\u5e95\u7684\u7edd\u4e86\u8fd9\u4e2a\u5fc3\u601d\u2014\u2014\u4ed6\u5728\u5510\u5a67\u9762\u524d\u65e2\u65e0\u6cd5\u5766\u767d\u4ea4\u5f85\u4ec0\u4e48\uff0c\u4e5f\u65e0\u6cd5\u5426\u8ba4\u4ec0\u4e48\uff0c\u518d\u8bf4\u4ed6\u8fd9\u6b21\u6765\u9999\u6e2f\u8fd8\u60f3\u7740\u8ddf\u5b59\u5c1a\u4e49\u3001\u53f6\u5efa\u658c\u5546\u8bae\u600e\u4e48\u8c03\u914d\u4e00\u4e0b\u5728\u9526\u6e56\u4f53\u7cfb\u5185\u7684\u5206\u5de5\u95ee\u9898\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8ddf\u5b59\u9759\u9999\u7684\u5173\u7cfb\u6ca1\u6709\u6311\u660e\uff0c\u53f6\u5efa\u658c\u4e0e\u5b59\u5c1a\u4e49\u62c5\u4efb\u9526\u6e56\u5546\u4e8b\u7684\u8054\u5408\u603b\u88c1\u5206\u7ba1\u8d1f\u8d23\u9526\u6e56\u5546\u4e8b\u7684\u65e5\u5e38\u8fd0\u8425\u8ddf\u6d77\u5916\u878d\u8d44\u91cd\u4efb\uff0c\u4f46\u662f\u53f6\u5efa\u658c\u4e0e\u5b59\u9759\u9999\u7ed3\u5a5a\u4e4b\u540e\uff0c\u53f6\u5efa\u658c\u4e0e\u5b59\u5c1a\u4e49\u5c31\u662f\u5973\u5a7f\u8ddf\u5cb3\u7236\u5173\u7cfb\uff0c\u8fd8\u7ee7\u7eed\u5171\u540c\u62c5\u4efb\u8054\u5408\u603b\u88c1\u5373\u4f7f\u5b9e\u8d28\u4e0a\u6ca1\u6709\u591a\u5927\u7684\u533a\u522b\uff0c\u4f46\u662f\u5728\u516c\u53f8\u5185\u5916\u7684\u89c6\u91ce\u91cc\u603b\u4f1a\u6709\u4e9b\u602a\u5f02\u3002</p><p>\u3000\u3000\u91d1\u878d\u8d44\u4ea7\u4f5c\u4e3a\u6838\u5fc3\u4e1a\u52a1\u4e4b\u4e00\u5386\u6765\u90fd\u662f\u8d22\u56e2\u4f53\u7cfb\u8fd0\u4f5c\u6709\u4e00\u4e2a\u9c9c\u660e\u7684\u7279\u5f81\u3002\u9526\u6e56\u9664\u4e86\u8bf8\u591a\u6210\u5458\u4f01\u4e1a\u4e4b\u5916\uff0c\u8fd8\u6301\u6709\u4e1c\u6d77\u3001\u6c5f\u5357\u4e24\u7701\u7684\u591a\u5bb6\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\u768420\uff05\u523030\uff05\u4e0d\u7b49\u7684\u80a1\u6743\u3002\u53d7\u56fd\u5185\u653f\u5e9c\u5f71\u54cd\uff0c\u9664\u4e86\u5c11\u6570\u50cf\u4fe1\u901a\u94f6\u884c\u90a3\u6837\u7684\u5168\u56fd\u6027\u6c11\u8425\u5546\u4e1a\u673a\u6784\u4e4b\u5916\uff0c\u56fd\u5185\u53ea\u6709\u5730\u65b9\u6027\u8d28\u7684\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\u5141\u8bb8\u6c11\u8425\u8d44\u672c\u8fdb\u5165\uff0c\u8fd8\u89c4\u5b9a\u540c\u4e00\u5bb6\u6216\u5173\u8054\u6c11\u8425\u8d44\u672c\u7684\u6301\u80a1\u4e0a\u9650\u3002\u4eca\u5e74\u56fd\u52a1\u9662\u5bf9\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\u518d\u5ea6\u653e\u5f00\u53e3\u5b50\uff0c\u5141\u8bb8\u5916\u8d44\u8fdb\u5165\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\uff0c\u6301\u80a1\u6bd4\u4f8b\u4e0a\u9650\u53ef\u8fbe10\uff05\u3002\u5f20\u606a\u8ba1\u5212\u7740\u6536\u8d2d\u4e00\u5bb6\u4e2d\u5c0f\u578b\u6d77\u5916\u94f6\u884c\uff0c\u901a\u8fc7\u8fd9\u5bb6\u6d77\u5916\u94f6\u884c\u5bf9\u4e1c\u6d77\u3001\u6c5f\u5357\u4e24\u7701\u7684\u591a\u5bb6\u57ce\u5e02\u5546\u4e1a\u94f6\u884c\u8fdb\u884c\u6ce8\u8d44\u6301\u80a1\uff0c\u5bf9\u4e24\u7701\u7684\u57ce\u5546\u884c\u4f53\u7cfb\u8fdb\u884c\u8fdb\u4e00\u6b65\u6e17\u900f\u3001\u6269\u5f20\uff0c\u4e0b\u4e00\u6b65\u56fd\u5185\u5c06\u5141\u8bb8\u57ce\u5546\u884c\u8de8\u533a\u57df\u8054\u5408\u7ecf\u8425\uff0c\u9526\u6e56\u5230\u65f6\u5019\u5c31\u53ef\u4ee5\u5728\u56fd\u5185\u76f4\u63a5\u63a7\u5236\u4e00\u5bb6\u533a\u57df\u6027\u8d28\u7684\u4e2d\u578b\u5546\u4e1a\u94f6\u884c\uff0c\u5bf9\u5730\u65b9\u3001\u5bf9\u4ea7\u4e1a\u53d1\u6325\u66f4\u5927\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u5f20\u606a\u662f\u6307\u671b\u5b59\u5c1a\u4e49\u6765\u505a\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4e5f\u8003\u8651\u5230\u4ed6\u8fd9\u51e0\u5e74\u4e1c\u5954\u897f\u8dd1\u7740\u5b9e\u8f9b\u82e6\u4e86\uff0c\u8d1f\u8d23\u8fd9\u4e9b\u4e8b\u60c5\u8981\u8f7b\u677e\u4e00\u4e9b\uff0c\u6bd5\u7adf\u9526\u6e56\u5f53\u524d\u7684\u91cd\u5fc3\u8fd8\u4e0d\u5728\u8fd9\u4e0a\u9762\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u6bd4\u8d77\u9526\u6e56\u5546\u4e8b\u8054\u5408\u603b\u88c1\u7684\u4f4d\u5b50\uff0c\u6536\u8d2d\u4e2d\u5c0f\u578b\u6d77\u5916\u94f6\u884c\u5e76\u7ecf\u8425\u4e4b\u76f8\u5bf9\u4e0d\u662f\u90a3\u4e48\u91cd\u8981\uff0c\u65e0\u8bba\u5728\u8c01\u770b\u6765\uff0c\u5b59\u5c1a\u4e49\u6709\u7ed9\u524a\u6743\u7684\u5acc\u7591\u3002\u4e0d\u8fc7\u5f20\u606a\u8fd8\u662f\u6709\u628a\u63e1\u8bf4\u670d\u5b59\u5c1a\u4e49\u7684\uff0c\u53ea\u662f\u8fd9\u65f6\u5019\u4ed6\u4e0e\u9759\u6aac\u7684\u90a3\u4e9b\u4e8b\u7ed9\u7aa5\u7834\uff0c\u4ed6\u8138\u76ae\u518d\u539a\uff0c\u65e0\u6cd5\u6d51\u7136\u65e0\u77e5\u4f3c\u7684\u53bb\u8ddf\u5b59\u5c1a\u4e49\u8c08\u8fd9\u4e2a\u95ee\u9898\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u60f3\uff1a\u5f97\uff0c\u5f97\uff0c\u5f97\uff0c\u8fd8\u662f\u7b49\u53f6\u5efa\u658c\u7684\u5a5a\u793c\u8fc7\u540e\u518d\u4ece\u957f\u8ba1\u8bae\u5427\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u6210\u5458\u90fd\u8981\u53c2\u52a0\u51fa\u5e2d\u53f6\u5efa\u658c\u4e0e\u5b59\u9759\u9999\u7684\u5a5a\u793c\uff0c\u6240\u4ee5\u8fd9\u4e00\u5b63\u5ea6\u7684\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u5c31\u5728\u9999\u6e2f\u8ddf\u9a6c\u5c14\u4ee3\u592b\u4e24\u5730\u8fdb\u884c\u3002</p><p>\u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u5b59\u9759\u6aac\u3001\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u76db\u590f\u3001\u5e2d\u82e5\u7433\u7b49\u5973\u5b69\u5b50\u966a\u540c\u65b0\u5a5a\u8ddf\u53f6\u5bb6\u4eba\u4ee5\u53ca\u5b59\u5bb6\u4eba\u5148\u671f\u4e58\u5750\u4e13\u673a\u524d\u5f80\u9a6c\u5c14\u4ee3\u592b\u51c6\u5907\u5a5a\u793c\u4e00\u4e8b\uff0c\u675c\u98de\u8ddf\u8499\u4e50\u6682\u65f6\u8fd8\u6ca1\u6709\u8d44\u683c\u53c2\u52a0\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\uff0c\u4ed6\u4eec\u4e5f\u968f\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u6210\u5458\u7684\u59bb\u5b50\u6216\u4e08\u592b\u4e5f\u5148\u884c\u968f\u98de\u673a\u5148\u884c\u524d\u5f80\u9a6c\u5c14\u4ee3\u592b\u3002\u5f20\u606a\u3001\u53f6\u5efa\u658c\u3001\u5b59\u5c1a\u4e49\u8fd8\u6709\u5176\u4ed6\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u7684\u6210\u5458\u5148\u7559\u5728\u9526\u6e56\u5546\u4e8b\u5728\u9999\u6e2f\u7684\u603b\u90e8\u5927\u53a6\u91cc\u53ec\u5f00\u5b63\u5ea6\u4f1a\u8bae\uff0c\u8fd9\u8fd8\u662f\u9526\u6e56\u6838\u5fc3\u7ba1\u7406\u5c42\u9f50\u805a\u9999\u6e2f\uff0c\u5728\u9999\u6e2f\u505c\u7559\u4e00\u5929\uff0c\u518d\u524d\u5f80\u9a6c\u5c14\u4ee3\u592b\uff0c\u5728\u9a6c\u5c14\u4ee3\u592b\u7684\u5bbe\u9986\u91cc\u63a5\u7740\u53ec\u5f00\u4f1a\u8bae\uff0c\u7b2c\u56db\u5929\u624d\u662f\u53f6\u5efa\u658c\u4e0e\u5b59\u9759\u9999\u7684\u5a5a\u793c\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u8ddf\u5b59\u5c1a\u4e49\u4e8b\u5148\u5546\u8bae\u804c\u52a1\u8c03\u52a8\u7684\u4e8b\u60c5\uff0c\u81ea\u7136\u4e5f\u4e0d\u4f1a\u5728\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u63d0\u8d77\u8fd9\u4e8b\uff0c\u5012\u662f\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u9690\u7ea6\u731c\u5230\u5f20\u606a\u4f1a\u63d0\u6b64\u4e8b\u5374\u6ca1\u89c1\u5f20\u606a\u5c06\u6b64\u4e8b\u63d0\u4e0a\u8bae\u7a0b\u800c\u89c9\u5f97\u4e07\u5206\u5947\u602a\uff0c\u53ea\u662f\u5f20\u606a\u4e0d\u63d0\uff0c\u800c\u5b59\u5c1a\u4e49\u5728\u9526\u6e56\u5185\u7684\u5730\u4f4d\u53c8\u4ec5\u6b21\u4e8e\u5f20\u606a\u8ddf\u53f6\u5bb6\uff0c\u5176\u4ed6\u4eba\u81ea\u7136\u4e5f\u4e0d\u4f1a\u4e3b\u52a8\u63d0\u51fa\u6765\u8fd9\u4e2a\u95ee\u9898\u6765\u3002</p><p>\u3000\u3000\u5728\u5f20\u606a\u62b5\u8fbe\u9a6c\u5c14\u4ee3\u592b\u6b63\u53c2\u52a0\u5a5a\u793c\u524d\u7684\u5f69\u6392\uff0c\u4ed6\u8981\u7ed9\u53f6\u5efa\u658c\u5f53\u4f34\u90ce\uff0c\u5f69\u6392\u81ea\u7136\u4e0d\u80fd\u7f3a\u5e2d\u2014\u2014\u4ed6\u6b63\u7ad9\u5728\u53f6\u5efa\u658c\u7684\u8eab\u8fb9\u660f\u660f\u6b32\u7761\u7684\u542c\u795e\u7236\u5ff5\u5a5a\u793c\u8a93\u8bcd\u65f6\uff0c\u5750\u5728\u53f0\u4e0b\u89c2\u770b\u7684\u5510\u5a67\u8df3\u7740\u8d70\u8fc7\u6765\u8ddf\u4ed6\u8bf4\uff1a\u201c\u665a\u6674\u59d0\u8ddf\u8bb8\u601d\u5979\u4eec\u5230\u673a\u573a\u4e86\uff0c\u8bf4\u662f\u82b7\u5f64\u6628\u5929\u5439\u7a7a\u8c03\u592a\u5389\u5bb3\u53d7\u4e86\u51c9\uff0c\u591c\u91cc\u53d1\u9ad8\u70e7\uff0c\u5c31\u7ed9\u7559\u5728\u91d1\u5c71\u5979\u5976\u5976\u90a3\u91cc\u6ca1\u6709\u8ddf\u7740\u8fc7\u6765\u2026\u2026\u6211\u8ddf\u9648\u5983\u84c9\u5750\u8239\u53bb\u673a\u573a\u63a5\u665a\u6674\u59d0\u3002\u201d</p><p>\u3000\u3000\u9a6c\u5c14\u4ee3\u592b\u662f\u7531\u591a\u4e2a\u73ca\u745a\u5c9b\u7ec4\u6210\u7684\u56fd\u5bb6\uff0c\u6c7d\u8f66\u5728\u8fd9\u91cc\u6ca1\u6709\u7528\u6b66\u4e4b\u5730\uff0c\u673a\u573a\u5728\u4e34\u8fd1\u9996\u90fd\u9a6c\u7d2f\u5c9b\u4e1c\u5317\u90e8\u7684\u745a\u6e56\u5c14\u5c9b\u4e0a\uff0c\u8ddd\u5a5a\u793c\u4e3e\u529e\u5730\u7684\u591a\u5c3c\u5c9b\u76f8\u9694\u4e24\u516c\u91cc\u3002\u8fd9\u65f6\u5019\u5176\u4ed6\u4eba\u5927\u90fd\u5728\u6563\u5728\u591a\u5c3c\u5c9b\u7684\u5404\u4e2a\u89d2\u843d\u91cc\u4eab\u53d7\u7740\u9a6c\u4ee3\u5c14\u592b\u4e0b\u5348\u7684\u660e\u5a9a\u9633\u5149\u4e0e\u5fae\u8165\u6e29\u6da6\u7684\u6d77\u6d0b\u6c14\u606f\u2014\u2014\u4e3a\u4e3e\u529e\u5a5a\u793c\uff0c\u53f6\u5efa\u658c\u5c06\u6574\u5ea7\u591a\u5c3c\u5c9b\u8ddf\u5c9b\u4e0a\u7684\u591a\u5c3c\u56fd\u9645\u5ea6\u5047\u9152\u5e97\u90fd\u5305\u4e86\u4e0b\u6765\u2014\u2014\u8fd9\u8fd8\u662f\u5f20\u606a\u5728\u897f\u6fb3\u6d32\u7684\u4e09\u5ea7\u79c1\u4eba\u5c9b\u5c7f\u6ca1\u6709\u5efa\u8bbe\u597d\uff0c\u4e0d\u8fc7\u897f\u6fb3\u6d32\u7684\u79c1\u4eba\u5c9b\u5c7f\u624d\u662f\u4e3e\u529e\u5a5a\u793c\u7684\u5929\u5802\uff0c\u867d\u7136\u8fd9\u65f6\u5019\u897f\u6fb3\u6d32\u6b63\u662f\u4e00\u5e74\u4e2d\u7684\u51ac\u5b63\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e00\u76f4\u90fd\u4e0d\u77e5\u9053\u5a5a\u793c\u9080\u8bf7\u4e86\u90a3\u4e9b\u4eba\uff0c\u4ed6\u4e00\u5f00\u59cb\u4ee5\u4e3a\u5b59\u9759\u9999\u4f1a\u9080\u8bf7\u665a\u6674\u8ddf\u8bb8\u601d\uff0c\u53ea\u662f\u7b49\u4ed6\u5230\u9999\u6e2f\u4e4b\u540e\u8ddf\u665a\u6674\u8fd8\u6709\u8bb8\u601d\u901a\u8bdd\u90fd\u6ca1\u89c1\u5979\u4eec\u63d0\u8d77\u8fd9\u4e8b\uff0c\u8fd8\u4ee5\u4e3a\u5b59\u9759\u9999\u5c06\u5979\u4eec\u4ece\u540d\u5355\u91cc\u5254\u9664\u6389\u4e86\u2014\u2014\u8981\u4e0d\u662f\u6b63\u8d76\u4e0a\u5b63\u5ea6\u4f1a\u8bae\uff0c\u53f6\u5efa\u658c\u3001\u5b59\u9759\u9999\u5a5a\u793c\u5c31\u6ca1\u6709\u6253\u7b97\u9080\u8bf7\u4ec0\u4e48\u4eba\u51fa\u5e2d\uff0c\u5f20\u606a\u89c9\u5f97\u5c31\u7b97\u4e0d\u9080\u8bf7\u665a\u6674\u8ddf\u8bb8\u601d\u4e5f\u6ca1\u6709\u4ec0\u4e48\u610f\u5916\u7684\uff0c\u6ca1\u60f3\u5230\u5979\u4eec\u8fd8\u662f\u7ed9\u9080\u8bf7\u8fc7\u6765\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u5510\u5a67\u62c9\u7740\u9648\u5983\u84c9\u8d70\u51fa\u53bb\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u73cf\u592b\u5987\u5750\u5728\u53f0\u4e0b\u770b\u7740\u4ed6\u4eec\u5f69\u6392\u5a5a\u793c\uff0c\u5f20\u606a\u89c9\u5f97\u4ed6\u7684\u80cc\u810a\u53c8\u5f00\u59cb\u6d41\u6c57\u4e86\u2014\u2014\u9a6c\u5c14\u4ee3\u592b\u867d\u7136\u5c5e\u4e8e\u70ed\u5e26\u56fd\u5bb6\uff0c\u4f46\u662f\u4e0b\u5348\u7684\u5929\u6c14\u6e29\u6da6\u5b9c\u4eba\uff0c\u611f\u89c9\u4e0d\u5230\u708e\u70ed\u2014\u2014\u4ed6\u4e0e\u8bb8\u601d\u7684\u5173\u7cfb\u81f3\u5c11\u5bf9\u5b59\u5c1a\u4e49\u592b\u5987\u4ee5\u53ca\u9526\u6e56\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u7684\u5176\u4ed6\u6210\u5458\u90fd\u662f\u534a\u516c\u5f00\u7684\uff0c\u60f3\u5230\u9648\u5983\u84c9\u8f6c\u8ff0\u7684\u5b59\u5c1a\u4e49\u592b\u5987\u80cc\u5730\u91cc\u7684\u79c1\u623f\u8bdd\uff0c\u5f20\u606a\u7684\u5fc3\u7406\u7d20\u8d28\u518d\u9ad8\uff0c\u4e5f\u6709\u4e9b\u625b\u4e0d\u4f4f\uff0c\u518d\u8bf4\u5b59\u9759\u6aac\u7a7f\u7740\u7c89\u8272\u7684\u793c\u670d\u88d9\u7ad9\u5728\u53e6\u4e00\u4fa7\u5462\u3002</p><p>\u3000\u3000\u6328\u8fc7\u534a\u4e2a\u5c0f\u65f6\uff0c\u5a5a\u793c\u5f69\u6392\u7ec8\u4e8e\u7ed3\u675f\uff0c\u5f20\u606a\u770b\u4e86\u770b\u624b\u8868\uff0c\u60f3\u7740\u665a\u6674\u8ddf\u8bb8\u601d\u5e94\u8be5\u4e0a\u5c9b\u4e86\uff0c\u8ddf\u53f6\u5efa\u658c\u8bf4\u4e86\u4e00\u58f0\uff0c\u5c31\u5148\u8d70\u51fa\u6559\u5802\uff0c\u8fdc\u8fdc\u770b\u89c1\u5510\u5a67\u8ddf\u665a\u6674\u3001\u8bb8\u601d\u5728\u4e00\u8d77\u3002\u5979\u4eec\u5df2\u7ecf\u5230\u9152\u5e97\u91cc\u6362\u4e86\u8eab\u8863\u670d\u51fa\u6765\uff0c\u8bb8\u601d\u7a7f\u7740\u788e\u82b1\u957f\u88d9\u5728\u767d\u6c99\u8def\u4e0a\u8d70\u7740\uff0c\u5934\u4e0a\u8fd8\u6234\u7740\u4e00\u9876\u5bbd\u6cbf\u5927\u8349\u5e3d\u2014\u2014\u6628\u5929\u9ec4\u660f\u65f6\u5206\u53bb\u9a6c\u7d2f\u5c9b\u901b\u9c7c\u5e02\u65f6\u5510\u5a67\u4e70\u4e86\u597d\u51e0\u9876\u8fd9\u79cd\u5927\u8349\u5e3d\u906e\u9633\u2014\u2014\u8bb8\u7ef4\u7ad9\u5728\u8fb9\u4e0a\u671d\u8bb8\u601d\u62b1\u6028\uff1a\u201c\u4e0d\u662f\u8bf4\u62bd\u4e0d\u51fa\u65f6\u95f4\u8fc7\u6765\u5417\uff1f\u6211\u6628\u5929\u8fd8\u60f3\u5462\uff0c\u4ec0\u4e48\u65f6\u5019\u8ba9\u4f60\u8fd8\u6709\u7238\u8ddf\u5988\u4e00\u8d77\u5230\u9a6c\u5c14\u4ee3\u592b\u6765\u5ea6\u5047\u2026\u2026\u201d\u6c5f\u9edb\u513f\u7b11\u76c8\u76c8\u7684\u7ad9\u5728\u4e00\u65c1\uff0c\u773c\u775b\u8fce\u7740\u5c06\u5782\u7684\u9633\u5149\u5fae\u772f\u7740\uff0c\u50cf\u662f\u591c\u7a7a\u91cc\u7684\u65b0\u6708\uff0c\u671b\u7740\u8d70\u51fa\u6559\u5802\u7684\u5f20\u606a\u2014\u2014\u6c5f\u9edb\u513f\u8ddf\u8bb8\u7ef4\u662f\u6628\u5929\u624d\u4ece\u9a6c\u6765\u897f\u4e9a\u8d76\u5230\u9a6c\u5c14\u4ee3\u592b\u7684\uff0c\u6c5f\u9edb\u513f\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u5f02\u5e38\u7684\u53d7\u6b22\u8fce\uff0c\u97f3\u4e50\u4e13\u8f91\u5728\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u9500\u91cf\u8981\u8d85\u8fc7\u5185\u5730\u4e00\u5927\u622a\uff0c\u65b0\u4e13\u8f91\u4e5f\u53ea\u80fd\u5148\u5728\u4e1c\u5357\u4e9a\u505a\u5ba3\u4f20\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0b\u610f\u8bc6\u7684\u60f3\u6e9c\u8fdb\u6559\u5802\u91cc\uff0c\u6ca1\u7b49\u4ed6\u8f6c\u8eab\u5462\uff0c\u5b59\u9759\u6aac\u5c31\u5728\u95e8\u53e3\u60ca\u8bb6\u7684\u558a\uff1a\u201c\u665a\u6674\u59d0\u5979\u4eec\u5230\u4e86\uff0c\u82b7\u5f64\u5979\u4eba\u5462\uff1f\u201d\u770b\u7740\u9759\u6aac\u5c0f\u8dd1\u7740\u8fce\u8fc7\u53bb\uff0c\u5f20\u606a\u5fc3\u60c5\u6709\u4e9b\u5c0f\u538b\u6291\uff1a\u5e73\u65f6\u4e00\u4e2a\u4e2a\u90fd\u4e0d\u5728\u8eab\u8fb9\uff0c\u8fd9\u4f1a\u513f\u4e00\u4e0b\u5b50\u53c8\u5192\u51fa\u8fd9\u4e48\u591a\uff0c\u5173\u952e\u4e5f\u53ea\u6709\u8bb8\u601d\u662f\u534a\u516c\u5f00\u72b6\u6001\uff0c\u4f46\u8fd8\u7792\u7740\u8bb8\u7ef4\u2014\u2014\u8fd9\u51fa\u620f\u8981\u600e\u4e48\u6f14\u4e0b\u53bb\uff1f\u5e78\u4e8f\u9648\u9759\u8ddf\u7fdf\u4e39\u9752\u6ca1\u6709\u7ed9\u9080\u8bf7\uff0c\u4e0d\u7136\u53ef\u5c31\u592a\u70ed\u95f9\u4e86\u3002</p><p>\u3000\u3000\u201c\u592a\u70ed\u4e86\uff0c\u6211\u8981\u5148\u56de\u623f\u95f4\u6362\u8863\u670d\u53bb\uff0c\u7bdd\u706b\u5bb4\u4f1a\u5c31\u8981\u5f00\u59cb\u4e86\uff0c\u4f60\u4eec\u5148\u8fc7\u53bb\u5427\u3002\u201d\u53c2\u52a0\u5f69\u6392\u4e5f\u8981\u7a7f\u6b63\u5f0f\u7684\u897f\u670d\uff0c\u867d\u8bf4\u9a6c\u5c14\u4ee3\u592b\u6c14\u5019\u5b9c\u4eba\u5374\u4e5f\u662f\u70ed\u5e26\u56fd\u5bb6\uff0c\u7a7f\u897f\u670d\u8fd8\u662f\u592a\u70ed\u4e86\uff0c\u5f20\u606a\u4e5f\u53ea\u80fd\u62ff\u8fd9\u4e2a\u5f53\u501f\u53e3\u8eb2\u56de\u623f\u95f4\u53bb\u3002\u4ed6\u7684\u623f\u95f4\u662f\u4f4d\u4e8e\u68d5\u6988\u6797\u8fb9\u7f18\u7684\u539f\u6728\u5c0f\u697c\uff0c\u6e5b\u84dd\u7684\u6d77\u6c34\u4f3c\u4e4e\u5c31\u5728\u843d\u5730\u7a97\u524d\u6c79\u6d8c\uff0c\u5f20\u606a\u5728\u623f\u95f4\u91cc\u78e8\u8e6d\u4e86\u534a\u5929\u624d\u6362\u4e86\u5370\u82b1\u5927\u88e4\u8869\u8d70\u51fa\u6765\u3002</p><p>\u3000\u3000\u867d\u8bf4\u90fd\u5728\u5c9b\u4e0a\uff0c\u665a\u6674\u3001\u8bb8\u601d\u3001\u6c5f\u9edb\u513f\u3001\u5b59\u9759\u6aac\u90fd\u6709\u610f\u8bc6\u7684\u907f\u5f00\u5f20\u606a\uff0c\u5510\u5a67\u4e5f\u62c9\u7740\u9648\u5983\u84c9\u8ddf\u5176\u4ed6\u5973\u5b69\u5b50\u8e6d\u5728\u4e00\u8d77\u4e0d\u7406\u4f1a\u5f20\u606a\uff0c\u5f20\u606a\u4e5f\u4e50\u5f97\u8f7b\u95f2\uff0c\u603b\u4e4b\u4ed6\u4e5f\u80fd\u968f\u4fbf\u627e\u4e2a\u501f\u53e3\u62c9\u4eba\u8c08\u5de5\u4f5c\u4e0a\u7684\u4e8b\u60c5\u2014\u2014\u9526\u6e56\u6b63\u503c\u5d1b\u8d77\u6210\u957f\u4e3a\u8d22\u56e2\u6700\u5173\u952e\u7684\u5951\u673a\uff0c\u6838\u5fc3\u7ba1\u7406\u6210\u5458\u53c8\u90fd\u5728\u5c9b\u4e0a\uff0c\u867d\u7136\u8fd9\u6b21\u5b63\u5ea6\u4f1a\u8bae\u66f4\u591a\u6709\u5ea6\u5047\u7684\u6027\u8d28\uff0c\u4f46\u662f\u5927\u5bb6\u805a\u5728\u4e00\u8d77\u5e38\u5e38\u4e09\u53e5\u8bdd\u5c31\u8c08\u5230\u5de5\u4f5c\u4e0a\u7684\u4e8b\u60c5\uff0c\u8fd9\u4e5f\u65b9\u4fbf\u5f20\u606a\u5b66\u9e35\u9e1f\u5c06\u5934\u57cb\u5728\u6ce5\u571f\u91cc\u6682\u65f6\u4e0d\u7ba1\u773c\u524d\u7684\u4e71\u5c40\u3002</p><p>\u3000\u3000\u4e03\u6708\u4e8c\u5341\u516b\u65e5\u4e0b\u5348\uff0c\u53f6\u5efa\u658c\u4e0e\u5b59\u9759\u9999\u7684\u5a5a\u793c\u5982\u671f\u5728\u9a6c\u5c14\u4ee3\u592b\u591a\u5c3c\u5c9b\u7684\u6559\u5802\u91cc\u4e3e\u884c\uff0c\u6559\u5802\u5a5a\u793c\u8fc7\u540e\uff0c\u4e3b\u5bb4\u5219\u5b89\u6392\u5728\u9152\u5e97\u7684\u4e3b\u5bb4\u4f1a\u5385\u3002\u5f20\u606a\u8ddf\u5927\u5bb6\u8d70\u56de\u5230\u4e3b\u5bb4\u4f1a\u5385\uff0c\u53d1\u73b0\u81ea\u5df1\u8fd8\u7a7f\u7740\u4f34\u90ce\u5c0f\u793c\u670d\u6709\u4e9b\u50bb\uff0c\u60f3\u7740\u4f34\u90ce\u7684\u804c\u8d23\u5df2\u7ecf\u5b8c\u6210\uff0c\u6362\u5bbd\u677e\u7684\u5927\u88e4\u8869\u518d\u6740\u56de\u6765\u4eab\u53d7\u4e30\u76db\u7684\u7f8e\u98df\u4f1a\u8ba9\u4eba\u66f4\u6109\u5feb\uff0c\u4ed6\u521a\u8981\u8d70\u51fa\u5bb4\u4f1a\u5385\uff0c\u5728\u62d0\u89d2\u8fdc\u8fdc\u7684\u770b\u89c1\u5b59\u9759\u6aac\u5728\u53e6\u4e00\u5934\u7684\u89d2\u843d\u91cc\u671d\u4ed6\u4f7f\u773c\u8272\uff0c\u786e\u5b9a\u5f20\u606a\u770b\u5230\u5979\uff0c\u5979\u5148\u671d\u8fc7\u9053\u90a3\u5934\u8d70\u53bb\u3002</p><p>\u3000\u3000\u4e0d\u6653\u5f97\u5b59\u9759\u6aac\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5f20\u606a\u8ddf\u4e86\u8fc7\u53bb\uff0c\u66f2\u66f2\u6298\u6298\u7684\u8fc7\u9053\u5c3d\u5934\u662f\u6d17\u624b\u95f4\uff0c\u6d17\u624b\u95f4\u524d\u7684\u5730\u9762\u4e0a\u8fd8\u653e\u7740\u4e00\u53ea\u201c\u6b63\u5728\u6253\u626b\u201d\u7684\u82f1\u6587\u6807\u8bc6\u724c\uff0c\u770b\u4e0d\u89c1\u5c0f\u59ae\u5b50\u7684\u8eab\u5f71\u3002</p><p>\u3000\u3000\u201c\u8fdb\u6765\uff0c\u201d\u5b59\u9759\u6aac\u7a81\u7136\u4ece\u5973\u5395\u6240\u91cc\u63a2\u51fa\u6765\u5c06\u5f20\u606a\u62c9\u4e86\u8fdb\u6765\uff0c\u4e00\u76f4\u8d70\u5230\u6700\u91cc\u9762\uff0c\u5c06\u4e00\u8138\u60ca\u8c14\u7684\u4ed6\u63a8\u5230\u9694\u95f4\u91cc\u53bb\uff0c\u8f7b\u54ac\u7740\u5634\u5507\uff0c\u660e\u4eae\u8ddf\u661f\u5b50\u7684\u773c\u775b\u76ef\u7740\u5f20\u606a\uff0c\u8bf4\u9053\uff0c\u201c\u6ca1\u6709\u4eba\u4f1a\u8fc7\u6765\uff0c\u4f60\u53eb\u7834\u5589\u5499\u4e5f\u6ca1\u6709\u7528\u2026\u2026\u201d</p></article>"}}}]);
