(window.webpackJsonp=window.webpackJsonp||[]).push([[207],{230:function(p){p.exports={chapterTitle:null,title:"\u7b2c1181\u7ae0 \u673a\u6d77\u6218\u672f\u6240\u8bf1\u53d1\u7684\u5371\u673a",content:"<article><h2>\u7b2c1181\u7ae0 \u673a\u6d77\u6218\u672f\u6240\u8bf1\u53d1\u7684\u5371\u673a</h2><p>\u3000\u3000\u5f00\u9614\u800c\u5962\u534e\u7684\u5c45\u5ba4\uff0c\u5e8a\u88d9\u3001\u6c99\u53d1\u90fd\u662f\u8000\u773c\u7684\u6854\u7ea2\uff0c\u4e00\u5177\u96ea\u767d\u5a07\u5ae9\u7684\u5973\u4eba\u8eaf\u4f53\u6a2a\u9648\u5728\u7f0e\u8d28\u88ab\u8925\u4e0a\uff0c\u5341\u8db3\u7684\u9999\u8273\uff0c\u70c8\u7130\u822c\u7684\u7ea2\u5507\u53d1\u51fa\u5fae\u5fae\u7684\u5a07\u541f\uff0c\u5a07\u8eaf\u5fae\u8737\u6a2a\u9648\uff0c\u6446\u51fa\u6027\u611f\u7684\u59ff\u6001\uff0c\u8bf1\u60d1\u7740\u522b\u4eba\u6625\u98ce\u4e8c\u5ea6\u3002</p><p>\u3000\u3000\u6c60\u4f50\u79c0\u85cf\u53ef\u6ca1\u6709\u5fc3\u60c5\u6625\u98ce\u4e8c\u5ea6\uff0c\u5750\u8d77\u6765\u7a7f\u4e0a\u7761\u888d\uff0c\u5bf9\u5e8a\u4e0a\u7f8e\u5973\u7684\u8bf1\u60d1\u89c6\u800c\u4e0d\u89c1\uff0c\u53cd\u800c\u751f\u4e86\u4e9b\u538c\u6076\uff0c\u5fc3\u5934\u53c8\u6d6e\u73b0\u8d77\u674e\u99a8\u4e88\u7684\u8eab\u5f71\uff0c\u90a3\u98a6\u7b11\u95f4\u7684\u98ce\u60c5\uff0c\u4efb\u662f\u4e00\u4e07\u4e2a\u5973\u4eba\u90fd\u62b5\u4e0d\u4e0a\uff0c\u5e8a\u4e0a\u7684\u5eb8\u59ff\u4fd7\u7c89\uff0c\u4ed6\u4e5f\u53ef\u4ee5\u968f\u4fbf\u627e\u6765\u4e00\u4e07\u4e2a\u3002\u6c60\u4f50\u79c0\u85cf\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u6325\u624b\u8ba9\u5973\u4eba\u4ece\u4ed6\u773c\u524d\u6d88\u5931\uff0c\u90a3\u5973\u4eba\u4e5f\u53ea\u6562\u762a\u4e0b\u5634\uff0c\u62b1\u8d77\u8863\u670d\u6321\u4f4f\u80f8\u524d\u3001\u5c0f\u8179\u7684\u6625\u5149\uff0c\u5149\u7740\u767d\u767d\u7684\u80a5\u5c41\u80a1\u3001\u8d64\u8db3\u4ece\u6c60\u4f50\u79c0\u85cf\u7684\u5367\u5ba4\u91cc\u79bb\u5f00\u3002</p><p>\u3000\u3000\u6c60\u4f50\u79c0\u85cf\u7ad9\u5728\u7a97\u524d\uff0c\u770b\u7740\u591c\u5e55\u4e0b\u6df1\u6c89\u9ed1\u8272\u7684\u6d77\u6d0b\uff0c\u4ed6\u5fc3\u60f3\u90a3\u4e2a\u4eba\u4e5f\u8bb8\u8eba\u5728\u7f8e\u4eba\u6000\u62b1\u91cc\u4e5f\u770b\u7740\u6d77\u6d0b\uff0c\u771f\u662f\u53ef\u6068\uff0c\u8fd9\u4e2a\u5bb6\u4f19\u4e5f\u662f\u98ce\u6d41\u597d\u8272\uff0c\u504f\u504f\u99a8\u4e88\u5c06\u5fc3\u601d\u90fd\u5bc4\u5728\u4ed6\u8eab\u4e0a\uff01</p><p>\u3000\u3000\u4e00\u672c\u7ea2\u8272\u6587\u4ef6\u5939\u9759\u9759\u7684\u8eba\u5728\u5367\u5ba4\u4e00\u4fa7\u7684\u529e\u516c\u684c\u4e0a\uff0c\u91cc\u9762\u6709\u8d22\u56e2\u6700\u9ad8\u51b3\u7b56\u673a\u6784\u4e8c\u76ee\u4f1a\u7684\u7ed3\u8bba\u3002\u5168\u7403\u7ecf\u6d4e\u9762\u4e34\u7d27\u7f29\uff0c\u94a2\u94c1\u4ea7\u4e1a\u5728\u4eca\u5e74\u4e94\u5e74\u5185\u96be\u6709\u590d\u82cf\u7684\u8ff9\u8c61\uff0c\u9526\u6e56\u5728\u897f\u6fb3\u6d32\u7684\u6295\u8d44\u5b8c\u5168\u4f9d\u8d56\u5176\u65d7\u4e0b\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u4e1a\u7684\u5229\u6da6\u652f\u6491\uff0c\u529b\u62d3\u4e0e\u6bd5\u548c\u6bd5\u62d3\u4e24\u5927\u77ff\u5546\u5e55\u540e\u5176\u4ed6\u5927\u80a1\u4e1c\uff0c\u7279\u522b\u662f\u82f1\u7cfb\u8d22\u56e2\u5373\u4f7f\u62c5\u5fe7\u9526\u6e56\u6709\u53ef\u80fd\u6210\u4e3a\u529b\u62d3\u4e0e\u6bd5\u548c\u6bd5\u62d3\u5f3a\u52b2\u7684\u5bf9\u624b\uff0c\u6b64\u65f6\u5374\u4e0d\u613f\u610f\u5927\u8d39\u5468\u7ae0\u3002\u529b\u62d3\u5012\u4e5f\u6446\uff0c\u5173\u952e\u6bd5\u548c\u6bd5\u62d3\u521a\u521a\u7531\u4e24\u5bb6\u5927\u578b\u77ff\u4e1a\u5408\u5e76\u800c\u6210\uff0c\u5185\u90e8\u4e89\u6743\u52bf\u4e0d\u4f11\uff0c\u4e0e\u529b\u62d3\u7ade\u4e89\u8fd8\u6765\u4e0d\u53ca\uff0c\u66f4\u6ca1\u6709\u7cbe\u529b\u53bb\u538b\u5236\u7a81\u7136\u5d1b\u8d77\u7684\u9526\u6e56\u3002\u5173\u952e\u7684\u95ee\u9898\u5728\u4e8e\uff0c\u9526\u6e56\u6784\u9020\u6210\u4e86\u4ece\u94a2\u94c1\u5de5\u5382\u3001\u6d77\u6e2f\u3001\u6d77\u8fd0\u3001\u94c1\u8def\u3001\u77ff\u533a\u7684\u5168\u9762\u4f53\u7cfb\uff0c\u867d\u7136\u6bcf\u4e2a\u73af\u8282\u770b\u4e0a\u53bb\u8fd8\u5f88\u8584\u5f31\uff0c\u5357\u6d0b\u6d77\u8fd0\u624d\u521a\u521a\u6dfb\u7f6e\u4e24\u8258\u8d85\u5927\u578b\u8fdc\u6d0b\u77ff\u77f3\u8239\u5f00\u50fb\u6fb3\u5927\u5229\u4e9a\u5230\u4e2d\u56fd\u7684\u8fdc\u6d0b\u822a\u7ebf\uff0c\u9526\u6e56\u5728\u897f\u6fb3\u6d32\u6295\u8d44\u7684\u94c1\u8def\u3001\u7801\u5934\u5efa\u6210\uff0c\u4e5f\u53ea\u6709\u4e00\u5343\u4e07\u5428\u7684\u8fd0\u529b\uff0c\u4f46\u662f\u8fd9\u4e2a\u4f53\u7cfb\u662f\u5b8c\u6574\u7684\uff0c\u6ca1\u6709\u8116\u5b50\u5361\u5728\u4ed6\u4eec\u624b\u91cc\u3002\u8fd9\u65f6\u5019\u8981\u6253\u538b\u9526\u6e56\uff0c\u9664\u975e\u82b1\u8d39\u65e0\u6cd5\u60f3\u8c61\u7684\u6210\u672c\u4e4b\u5916\uff0c\u53ea\u80fd\u5bc4\u5e0c\u671b\u9526\u6e56\u81ea\u884c\u7ed9\u6574\u4e2a\u4f53\u7cfb\u62d6\u57ae\u3002</p><p>\u3000\u3000\u4ee5\u73b0\u5728\u7684\u6d77\u8fd0\u4ef7\u683c\uff0c\u5357\u6d0b\u6d77\u8fd0\u5f00\u8f9f\u6fb3\u5927\u5229\u4e9a\u5230\u4e2d\u56fd\u7684\u8fdc\u6d0b\u822a\u8def\u662f\u51c0\u4e8f\u635f\u7684\uff0c\u5f85\u9526\u6e56\u7684\u7801\u5934\u3001\u94c1\u8def\u3001\u77ff\u533a\u5efa\u6210\u4e4b\u540e\uff0c\u4ee5\u4e00\u5343\u4e07\u5428\u7684\u51fa\u6e2f\u91cf\u8ba1\u7b97\uff0c\u4e5f\u5c06\u4f1a\u5927\u5e45\u4e8f\u635f\uff0c\u5f88\u591a\u4eba\u90fd\u671f\u5f85\u770b\u7740\u9526\u6e56\u81ea\u5df1\u5148\u6491\u4e0d\u8fc7\u53bb\uff0c\u6240\u4ee5\u8896\u624b\u65c1\u89c2\u3002</p><p>\u3000\u3000\u4e00\u7fa4\u9f20\u76ee\u5bf8\u5149\u7684\u5bb6\u4f19\uff01\u6c60\u4f50\u79c0\u85cf\u5fc3\u91cc\u6697\u9a82\u9053\u3002</p><p>\u3000\u3000\u9526\u6e56\u80cc\u540e\u7ad9\u7740\u7684\u662f\u4e2d\u56fd\uff0c\u867d\u7136\u5f88\u591a\u4eba\u5224\u65ad\u4e2d\u56fd\u653f\u5e9c\u5bf9\u4e0d\u80fd\u88ab\u5b8c\u5168\u63a7\u5236\u7684\u56fd\u5185\u79c1\u6709\u8d22\u56e2\u4e0d\u4f1a\u6709\u7279\u522b\u7684\u597d\u611f\uff0c\u4f46\u662f\u4e2d\u56fd\u653f\u5e9c\u5e76\u4e0d\u662f\u90a3\u79cd\u987d\u56fa\u4e0d\u5316\u7684\u6b7b\u8111\u7b4b\u3002\u4e2d\u56fd\u653f\u5e9c\u6b64\u65f6\u8fd8\u6ca1\u6709\u80fd\u529b\u8d70\u51fa\u6765\u6253\u9020\u8fd9\u4e48\u4e00\u6761\u8d44\u6e90\u8f93\u5165\u4f53\u7cfb\uff0c\u4f46\u662f\u5e76\u4e0d\u610f\u5473\u7740\u4e2d\u56fd\u6ca1\u6709\u8fd9\u79cd\u60f3\u6cd5\u3002\u9526\u6e56\u867d\u7136\u4e0d\u4f1a\u53d7\u4e2d\u56fd\u653f\u5e9c\u5b8c\u5168\u63a7\u5236\uff0c\u4f46\u5bf9\u4e2d\u56fd\u653f\u5e9c\u6765\u8bf4\uff0c\u4e5f\u6709\u804a\u80dc\u4e8e\u65e0\u7684\u8c61\u5f81\u610f\u4e49\uff0c\u9526\u6e56\u5728\u77ff\u4ea7\u4e0a\u7684\u6295\u8d44\u771f\u8981\u6491\u4e0d\u8fc7\u53bb\uff0c\u4e2d\u56fd\u653f\u5e9c\u600e\u4e48\u53ef\u80fd\u8896\u624b\u65c1\u89c2\uff1f\u5f53\u7136\u4e5f\u6709\u53ef\u80fd\u4e2d\u56fd\u653f\u5e9c\u501f\u673a\u4f1a\u5c06\u9526\u6e56\u5728\u6d77\u5916\u7684\u8d44\u6e90\u6295\u8d44\u4f53\u7cfb\u9738\u5360\u8fc7\u53bb\u3001\u636e\u4e3a\u5df1\u6709\uff0c\u4f46\u662f\u8fd9\u5bf9\u65e5\u7cfb\u8d22\u56e2\u6765\u8bf4\u662f\u4e00\u4e2a\u66f4\u7cdf\u7cd5\u7684\u7ed3\u679c\u3002</p><p>\u3000\u3000\u6c60\u4f50\u79c0\u85cf\u7ad9\u5728\u7a97\u524d\u60f3\u4e86\u7247\u523b\uff0c\u8d70\u5230\u529e\u516c\u684c\u524d\uff0c\u627e\u51fa\u4e2d\u6751\u88d5\u4e4b\u5728\u4e2d\u56fd\u7684\u7535\u8bdd\u53f7\u7801\u62e8\u8fc7\u53bb\uff0c\u4e2d\u6751\u88d5\u4e4b\u73b0\u5728\u5168\u9762\u8d1f\u8d23\u7d22\u5c3c\u5728\u4e2d\u56fd\u7684\u4e8b\u52a1\uff0c\u8fd8\u6709NEC\u4e2d\u56fd\u603b\u8d1f\u8d23\u4eba\u5c0f\u6cfd\u96c4\uff0c\u6c60\u4f50\u79c0\u85cf\u80fd\u501a\u4e4b\u4e3a\u81c2\u52a9\u7684\u4e5f\u53ea\u662f\u8fd9\u4e48\u51e0\u4e2a\u4eba\u3002</p><p>\u3000\u3000\u201c\u4e8c\u76ee\u4f1a\u7684\u7ed3\u8bba\u5df2\u7ecf\u51fa\u6765\u4e86\uff0c\u90a3\u4e9b\u8001\u5bb6\u4f19\u4eec\u8fdf\u65e9\u4f1a\u540e\u6094\u7684\u2026\u2026\u201d\u4e2d\u6751\u88d5\u4e4b\u5728\u7535\u8bdd\u8ddf\u6c60\u4f50\u79c0\u85cf\u53d1\u6cc4\u4ed6\u5fc3\u91cc\u7684\u4e0d\u6ee1\u3002</p><p>\u3000\u3000\u4e2d\u6751\u88d5\u4e4b\u5634\u91cc\u7684\u90a3\u4e9b\u8001\u5bb6\u4f19\u5c31\u662f\u64cd\u6301\u8d22\u56e2\u7684\u90a3\u4e9b\u6700\u9ad8\u5c42\uff0c\u4ed6\u4eec\u53c8\u662f\u8d22\u56e2\u6210\u5458\u4f01\u4e1a\u7684\u6838\u5fc3\u7ba1\u7406\u8005\uff0c\u65e5\u672c\u4f01\u4e1a\u5728\u4e16\u754c\u867d\u7136\u8fd8\u662f\u5e9e\u7136\u5927\u7269\uff0c\u4f46\u662f\u5df2\u7ecf\u6709\u4e9b\u8fdf\u66ae\u7684\u611f\u89c9\uff0c\u8fdb\u53d6\u8ddf\u9510\u6c14\u4e0d\u8db3\u2014\u2014\u6c60\u4f50\u79c0\u85cf\u8ddf\u4e2d\u6751\u88d5\u4e4b\u79c1\u4e0b\u91cc\u4e5f\u8fd9\u4e48\u8bc4\u4ef7\u3002</p><p>\u3000\u3000\u201c\u4e5d\u6708\uff0c\u4e2d\u56fd\u79fb\u52a8\u901a\u8baf\u7f51\u7edc\u5c31\u5c06\u5168\u9762\u5347\u7ea7\uff0c\u7d22\u5c3c\u7684\u51c6\u5907\u5982\u4f55\uff1f\u201d\u6c60\u4f50\u79c0\u85cf\u66f4\u5173\u5fc3\u5982\u4f55\u53bb\u538b\u5236\u9526\u6e56\uff0c\u9526\u6e56\u5df2\u521d\u5177\u8d22\u56e2\u6c14\u8c61\uff0c\u4f46\u662f\u6b64\u65f6\u7684\u9526\u6e56\u8fd8\u662f\u8106\u5f31\u7684\uff0c\u4e3b\u8981\u8fd8\u662f\u4f9d\u8d56\u5176\u65d7\u4e0b\u7684\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u4e1a\u652f\u6301\uff0c\u8981\u662f\u80fd\u5c06\u7231\u8fbe\u96c6\u56e2\u6253\u57ae\uff0c\u9526\u6e56\u5c31\u4f1a\u81ea\u7136\u5d29\u57ae\u3002\u5f53\u7136\uff0c\u7231\u8fbe\u96c6\u56e2\u4e5f\u4e0d\u662f\u90a3\u4e48\u8106\u5f31\uff0c\u81f3\u5c11\u5728\u9526\u6e56\u5df2\u6709\u7684\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u9886\u57df\uff0c\u7d22\u5c3c\u90fd\u8981\u663e\u5f97\u6709\u4e9b\u9893\u52bf\u3002</p><p>\u3000\u3000\u7531\u4e8e\u65e5\u672c\u662f\u5168\u7403\u79fb\u52a8\u901a\u8baf\u7684\u7b2c\u4e09\u5927\u5e02\u573a\uff0c\u7d22\u5c3c\u51ed\u501f\u5728\u65e5\u672c\u5e02\u573a\u4e0a\u7684\u5730\u4f4d\uff0c\u624b\u673a\u5168\u7403\u9500\u552e\u8981\u8d85\u8fc7\u7231\u8fbe\u3002\u4f46\u662f\u5728\u65e5\u672c\u5e02\u573a\u4e4b\u5916\uff0c\u7d22\u5c3c\u5374\u8981\u6bd4\u7231\u8fbe\u900a\u8272\u3002\u7279\u522b\u662f\u5168\u7403\u79fb\u52a8\u901a\u8baf\u7684\u7b2c\u56db\u5927\u5e02\u573a\u4e2d\u56fd\uff0c\u7231\u8fbe\u624b\u673a\u7684\u5e02\u573a\u5360\u6709\u7387\u9ad8\u8fbe16\uff05\uff0c\u7d22\u5c3c\u5374\u4e0d\u8db36\uff05\uff1b\u5728\u4e1c\u5357\u4e9a\u5e02\u573a\uff0c\u7231\u8fbe\u624b\u673a\u5e02\u573a\u5360\u6709\u7387\u9ad8\u8fbe10\uff05\uff0c\u7d22\u5c3c\u4e5f\u6bd4\u7231\u8fbe\u7565\u4f4e\uff0c\u624d9\uff05\u3002</p><p>\u3000\u3000\u8fd9\u6837\u7684\u6210\u7ee9\u771f\u662f\u4ee4\u4eba\u96be\u582a\uff0c\u7231\u8fbe\u7684\u5386\u53f2\u624d\u77ed\u77ed\u4e0d\u8db3\u516d\u5e74\uff0c\u8fdb\u5165\u624b\u673a\u4ea7\u4e1a\u7684\u5386\u53f2\u624d\u4e09\u5e74\u3002</p><p>\u3000\u3000\u4e2d\u56fd\u79fb\u52a8\u901a\u8baf\u7f51\u7edc\u5728\u4e5d\u6708\u8981\u5168\u7ebf\u4eceGSM\u5347\u5230GPRS\u7f51\u7edc\uff0c\u6280\u672f\u4e0a\u7684\u5347\u7ea7\uff0c\u610f\u5473\u7740\u6280\u672f\u50a8\u5907\u96c4\u539a\u7684\u516c\u53f8\u66f4\u5360\u4f18\u52bf\uff0c\u8fd9\u7edd\u5bf9\u662f\u7d22\u5c3c\u5728\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u8d85\u8d8a\u7231\u8fbe\u7684\u4e00\u6b21\u826f\u673a\uff0c\u7231\u8fbe\u8fdb\u5165\u624b\u673a\u884c\u4e1a\u624d\u4e09\u5e74\u7684\u65f6\u95f4\uff0c\u6bd4\u6280\u672f\u50a8\u5907\uff0c\u7edd\u5bf9\u4e0d\u53ef\u80fd\u6bd4\u5f97\u8fc7\u7d22\u5c3c\u7684\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u4e2d\u6751\u88d5\u4e4b\u6709\u82e6\u8bf4\u4e0d\u51fa\uff0c\u4ed6\u867d\u7136\u8d1f\u8d23\u7d22\u5c3c\u5728\u4e2d\u56fd\u7684\u4e1a\u52a1\uff0c\u4f46\u662f\u7f3a\u4e4f\u603b\u90e8\u7684\u652f\u63f4\uff0c\u4ed6\u80fd\u591f\u505a\u7684\u4f9d\u65e7\u6709\u9650\uff0c\u6bd4\u8d77\u4e09\u661f\uff0c\u603b\u90e8\u5bf9\u4e2d\u56fd\u5e02\u573a\u7684\u91cd\u89c6\u8ddf\u53cd\u5e94\u662f\u8fdc\u8fdc\u4e0d\u5982\uff0c\u4e09\u661f\u53bb\u5e74\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u60e8\u8d25\u4e4b\u540e\u5c31\u75db\u5b9a\u601d\u75db\uff0c\u4e09\u661f\u5728\u4e2d\u56fd\u7684\u4ea7\u54c1\u8bbe\u8ba1\u7814\u53d1\u673a\u6784\u7814\u7a76\u5458\u5df2\u7ecf\u8d85\u8fc7\u4e24\u5343\u4eba\uff0c\u7d22\u5c3c\u5728\u4e2d\u56fd\u8fd8\u6ca1\u6709\u4e00\u5bb6\u50cf\u6a21\u50cf\u6837\u7684\u7814\u7a76\u673a\u6784\uff0c\u5bf9\u6c60\u4f50\u79c0\u85cf\u7684\u671f\u5f85\uff0c\u4e2d\u6751\u88d5\u4e4b\u771f\u4e0d\u597d\u56de\u7b54\uff0c\u4ed6\u77e5\u9053\u5728\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u80fd\u5bf9\u7231\u8fbe\u5f62\u6210\u538b\u529b\u7684\u4e0d\u662f\u7d22\u5c3c\uff0c\u800c\u662f\u4e09\u661f\uff1b\u540c\u6837\uff0c\u7d22\u5c3c\u5728\u5168\u7403\u624b\u673a\u5e02\u573a\u5374\u8981\u540c\u65f6\u9762\u5bf9\u7231\u8fbe\u4e0e\u4e09\u661f\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u4ece\u4e2d\u6751\u88d5\u4e4b\u7684\u6c89\u9ed8\u91cc\uff0c\u6c60\u4f50\u79c0\u85cf\u611f\u89c9\u6cae\u4e27\u7684\u6c14\u606f\uff0c\u4ed6\u65e0\u6cd5\u8d23\u4efb\u4e2d\u6751\u88d5\u4e4b\uff0c\u4ed6\u4e0d\u662f\u611f\u5230\u540c\u6837\u7684\u6cae\u4e27\u5417\uff1f\u90a3\u4e9b\u8001\u5bb6\u4f19\u592a\u81ea\u4ee5\u4e3a\u662f\u4e86\u3002</p><p>\u3000\u3000\u867d\u7136\u4e09\u661f\u7684\u6210\u7ee9\u5f97\u5230\u4e2d\u6751\u88d5\u4e4b\u7684\u80af\u5b9a\uff0c\u4f46\u662f\u674e\u5728\u6d19\u7684\u538b\u529b\u5e76\u4e0d\u5c0f\uff0c\u8fd9\u6bb5\u65f6\u95f4\uff0c\u4ed6\u4eba\u4e00\u76f4\u90fd\u5728\u5317\u4eac\u3001\u5efa\u90ba\u3001\u6c49\u57ce\u4e09\u5730\u5954\u6ce2\uff0c\u4eca\u5929\u624d\u56de\u5230\u5efa\u90ba\uff0c\u6253\u7b97\u6b47\u4e00\u53e3\u6c14\uff0c\u8eb2\u56de\u79c1\u5b85\u91cc\u5904\u7406\u516c\u52a1\u3002</p><p>\u3000\u3000\u201c\u8fd9\u6b21\u56de\u9996\u5c14\u6709\u6ca1\u6709\u770b\u5230\u99a8\u4e88\uff0c\u99a8\u4e88\u771f\u7684\u5c31\u8981\u63d0\u524d\u7ed3\u675f\u8fd9\u8fb9\u7684\u5b66\u4e1a\uff1f\u201d\u91d1\u5357\u52c7\u8fd9\u6b21\u6ca1\u6709\u968f\u674e\u5728\u6d19\u56de\u97e9\u56fd\u53bb\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u5e76\u4e0d\u559c\u6b22\u91d1\u5357\u52c7\u8fc7\u95ee\u5bb6\u65cf\u91cc\u7684\u4e8b\u60c5\uff0c\u62ac\u5934\u770b\u4e86\u91d1\u5357\u52c7\u4e00\u773c\uff0c\u5fc3\u60f3\uff1a\u96be\u9053\u4f60\u8fd8\u6709\u4ec0\u4e48\u5ff5\u5934\u4e0d\uff1f\u653e\u4e0b\u94a2\u7b14\uff0c\u5374\u4e0d\u9732\u58f0\u8272\u7684\u8bf4\uff1a\u201c\u4e8b\u60c5\u5df2\u7ecf\u4e0d\u53ef\u6536\u62fe\u4e86\uff0c\u96be\u9053\u8981\u771f\u95f9\u51fa\u4ec0\u4e48\u5927\u4e11\u95fb\u51fa\u6765\u4e0d\u6210\uff1f\u201d</p><p>\u3000\u3000\u91d1\u5357\u52c7\u6ca1\u6709\u542d\u58f0\uff0c\u674e\u5065\u7199\u4e4b\u5973\u7adf\u7136\u7518\u5fc3\u5145\u5f53\u4ed6\u4eba\u7684\u79d8\u5bc6\u60c5\u4eba\uff0c\u8fd9\u8981\u662f\u4f20\u51fa\u6765\uff0c\u7684\u786e\u662f\u8ba9\u4e09\u661f\u751a\u81f3\u4ee4\u97e9\u56fd\u90fd\u6781\u4e3a\u96be\u582a\u4e0e\u72fc\u72c8\u7684\u5927\u4e11\u95fb\u3002</p><p>\u3000\u3000\u4e1c\u5927\u7559\u5b66\u751f\u4e8b\u4ef6\u4e4b\u540e\uff0c\u674e\u99a8\u4e88\u4e0e\u5f20\u606a\u4ea4\u5f80\u8fc7\u5bc6\u7684\u6d88\u606f\u5c31\u7531\u5f53\u65f6\u53d7\u5230\u5904\u5206\u7684\u4e09\u661f\u5458\u5de5\u4f20\u56de\u6c49\u57ce\uff0c\u81ea\u7136\u8981\u63a7\u5236\u4e8b\u6001\u7684\u6076\u5316\uff0c\u674e\u5728\u6d19\u5fc3\u60f3\u80fd\u6709\u8fd9\u6837\u7684\u7ed3\u5c40\u6700\u597d\uff0c\u4e0d\u7136\u4ed6\u5f88\u96be\u4ea4\u5f85\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u81ea\u4ee5\u4e3a\u53ea\u6709\u4ed6\u8ddf\u91d1\u5357\u52c7\u77e5\u9053\u4e8b\u60c5\u7684\u771f\u76f8\uff0c\u5173\u952e\u99a8\u4e88\u79d8\u5bc6\u4ea4\u5f80\u7684\u5bf9\u8c61\u8fd8\u662f\u4e09\u661f\u8fd1\u5e74\u6765\u7684\u5f3a\u52b2\u7ade\u4e89\u5bf9\u624b\u7684\u5e55\u540e\u7ecf\u8425\u8005\uff0c\u800c\u4e0e\u4ed6\u4ea4\u5f80\u7684\u5973\u5b50\u53c8\u4e0d\u6b62\u674e\u99a8\u4e88\u4e00\u4eba\uff0c\u8fd9\u6837\u7684\u4e8b\u5b9e\u8981\u662f\u88ab\u516c\u5f00\uff0c\u6210\u4e3a\u7b11\u67c4\u90a3\u662f\u80af\u5b9a\u7684\uff0c\u8fd8\u4f1a\u4e25\u91cd\u632b\u4f24\u4e09\u661f\u5458\u5de5\u7684\u58eb\u6c14\u2014\u2014\u8bf4\u5b9e\u8bdd\uff0c\u8981\u662f\u9526\u6e56\u6ca1\u6709\u4ec0\u4e48\u5b9e\u529b\uff0c\u90a3\u4e5f\u5c31\u7f62\u4e86\uff1b\u9526\u6e56\u8d8a\u662f\u5bf9\u4e09\u661f\u6784\u6210\u5a01\u80c1\uff0c\u8fd9\u6837\u7684\u4e11\u95fb\u8d8a\u662f\u4f1a\u7ed9\u5a92\u4f53\u6076\u7092\u3002</p><p>\u3000\u3000\u4e5f\u50cf\u4e00\u56e2\u8305\u8349\u585e\u5728\u91d1\u5357\u52c7\u7684\u5fc3\u91cc\u8ba9\u4ed6\u611f\u89c9\u96be\u53d7\uff0c\u5fc3\u60f3\u99a8\u4e88\u7559\u5728\u97e9\u56fd\uff0c\u4e0d\u518d\u5230\u4e2d\u56fd\u6765\uff0c\u4e5f\u8bb8\u662f\u6700\u597d\u7684\u9009\u62e9\u3002</p><p>\u3000\u3000\u91d1\u5357\u52c7\u6682\u65f6\u5c06\u8fd9\u4ef6\u4e8b\u4e22\u5230\u4e00\u8fb9\uff0c\u8ddf\u674e\u5728\u6d19\u8ba8\u8bba\u8d77\u65b0\u624b\u673a\u53d1\u5e03\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u4e2d\u56fd\u79fb\u52a8\u5c06\u79fb\u52a8\u901a\u8baf\u7f51\u7edc\u5347\u7ea7\u63d0\u524d\u5230\u4e5d\u6708\uff0c\u6bd4\u9884\u671f\u63d0\u524d\u4e86\u4e09\u4e2a\u6708\uff0c\u8fd9\u771f\u662f\u7f55\u89c1\u2014\u2014\u4e09\u661f\u6700\u65e9\u751a\u81f3\u9884\u6d4b\u4e2d\u56fd\u79fb\u52a8\u7684\u7f51\u7edc\u5347\u7ea7\u4f1a\u62d6\u5ef6\u4e09\u4e2a\u6708\u5230\u534a\u5e74\u65f6\u95f4\u624d\u662f\u6b63\u5e38\u7684\u72b6\u51b5\u3002\u4e09\u661f\u4e4b\u524d\u6ca1\u6709\u5f97\u5230\u6d88\u606f\uff0c\u591a\u5c11\u6709\u4e9b\u88ab\u52a8\uff0c\u4e5f\u8bb8\u80cc\u540e\u6709\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u57fa\u4e9a\u3001\u7231\u7acb\u4fe1\u7b49\u6b27\u7f8e\u7535\u5b50\u5de8\u5934\u5e55\u540e\u63a8\u52a8\u7684\u56e0\u7d20\u5728\u91cc\u9762\u3002</p><p>\u3000\u3000\u5728GSM\u65f6\u4ee3\uff0c\u4e2d\u56fd\u56fd\u4ea7\u624b\u673a\u5728\u7231\u8fbe\u3001\u79d1\u738b\u9ad8\u79d1\u3001\u8054\u4fe1\u7b49\u4f01\u4e1a\u7684\u5e26\u9886\u4e0b\u53d6\u5f97\u4e86\u8f89\u714c\u7684\u6210\u5c31\uff0c\u77ed\u77ed\u4e09\u5e74\u7684\u65f6\u95f4\u91cc\uff0c\u56fd\u4ea7\u624b\u673a\u5e02\u573a\u5360\u6709\u7387\u4ece0\u4e0a\u5347\u5230\u8d85\u8fc740\uff05\uff0c\u800c\u4e14\u7231\u8fbe\u5df2\u7ecf\u5168\u9762\u7a81\u7834GSM\u624b\u673a\u7684\u6280\u672f\uff0c\u5e76\u5f15\u9886\u5176\u4ed6\u56fd\u4ea7\u624b\u673a\u5382\u5546\u5927\u8e0f\u6b65\u7684\u53d1\u5c55\uff0c\u79d1\u738b\u9ad8\u79d1\u3001\u8054\u4fe1\u8fd9\u4e24\u5e74\u6765\u5728\u624b\u673a\u6280\u672f\u4e0a\u7684\u6295\u5165\u4e5f\u76f8\u5f53\u7684\u5927\uff0c\u76f8\u4fe1\u4ed6\u4eec\u5728GSM\u624b\u673a\u4e0a\u7684\u6280\u672f\u50a8\u5907\u5df2\u7ecf\u6709\u4e86\u4e00\u5b9a\u7684\u6c34\u5e73\u3002\u9526\u6e56\u901a\u8fc7\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\uff0c\u5411\u56fd\u5185\u8bf8\u591a\u624b\u673a\u4f01\u4e1a\u4e0e\u96f6\u914d\u4ef6\u5546\u63d0\u4f9b\u5927\u91cf\u7684\u6570\u5b57\u624b\u673a\u6280\u672f\uff0c\u4fc3\u8fdb\u4e86\u6280\u672f\u4f20\u64ad\u4e0e\u53d1\u5c55\u3002\u82e5\u662f\u505c\u7559\u5728GSM\u65f6\u4ee3\uff0c\u4e0d\u8981\u8bf4\u4e09\u661f\u3001\u7d22\u5c3c\u4e86\uff0c\u5c31\u662f\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u57fa\u4e9a\u4e5f\u65e0\u529b\u6539\u53d8\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u5c06\u9010\u6b65\u7ed9\u4e2d\u56fd\u56fd\u4ea7\u624b\u673a\u8695\u98df\u7684\u5c40\u9762\u2014\u2014\u8981\u662f\u6539\u53d8\u8fd9\u4e2a\u5c40\u9762\uff0c\u65e0\u7591\u5c31\u662f\u7a81\u5982\u5176\u6765\u7684\u5168\u9762\u5347\u7ea7\u5230GPRS\u65f6\u4ee3\u3002\u5982\u6b64\u4e00\u6765\uff0c\u5c31\u51e0\u4e4e\u80fd\u5c06\u9664\u7231\u8fbe\u4e4b\u5916\u7684\u5176\u4ed6\u4e2d\u56fd\u624b\u673a\u5382\u5546\u90fd\u9010\u51fa\u9ad8\u7aef\u624b\u673a\u5e02\u573a\uff0c\u5373\u4f7f\u5728\u4e2d\u7aef\u624b\u673a\u5e02\u573a\uff0c\u5927\u6982\u4e5f\u53ea\u6709\u79d1\u738b\u9ad8\u79d1\u3001\u8054\u4fe1\u80fd\u591f\u5b58\u6d3b\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u770b\u5411\u7a97\u5916\uff0c\u5bf9\u65b0\u624b\u673a\u53d1\u5e03\u7684\u7b56\u5212\u6848\u4e0d\u7f6e\u53ef\u5426\uff0c\u91d1\u5357\u52c7\u77e5\u9053\u674e\u5728\u6d19\u5728\u62c5\u5fc3\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u81f3\u5c11\u5728\u4e2d\u56fd\u624b\u673a\u5e02\u573a\uff0c\u4e09\u661f\u624b\u673a\u7684\u7ade\u4e89\u5bf9\u624b\u4e0d\u662f\u522b\u4eba\uff0c\u5c31\u662f\u7231\u8fbe\u3002</p><p>\u3000\u3000\u4e09\u661f\u5728\u624b\u673a\u6280\u672f\u4e0a\u7684\u50a8\u5907\u4e5f\u6709\u6240\u4e0d\u8db3\uff0c\u5bf9\u5de5\u827a\u4e0e\u4ea7\u54c1\u8bbe\u8ba1\u7684\u82db\u523b\u8981\u6c42\uff0c\u4f7f\u4e09\u661f\u5728\u5168\u7403\u624b\u673a\u5e02\u573a\u8d62\u5f97\u4e00\u5b9a\u7684\u5730\u4f4d\u2014\u2014\u7231\u8fbe\u7684\u7279\u8d28\u8ddf\u4e09\u661f\u5341\u5206\u7684\u76f8\u4f3c\uff0c\u6280\u672f\u50a8\u5907\u4e0d\u8db3\uff0c\u4f46\u662f\u5728\u5de5\u827a\u4e0e\u4ea7\u54c1\u8bbe\u8ba1\u751a\u81f3\u505a\u5f97\u6bd4\u4e09\u661f\u66f4\u597d\u3002</p><p>\u3000\u3000\u53bb\u5e74\uff0c\u4e09\u661f\u4fe1\u5fc3\u7206\u68da\u7684\u5728\u4e2d\u56fd\u63a8\u51fa\u663e\u793a\u5c4f\u8bbe\u8ba1\u5728\u4e0a\u7ffb\u76d6\u7684800C\u624b\u673a\uff0c\u4e0a\u5e02\u4e4b\u524d\uff0c800C\u4ee5\u8ff7\u4eba\u9020\u578b\u8ddf\u5c0a\u8d35\u7684\u6c14\u8d28\u8d62\u5f97\u6b22\u8fce\uff0c\u4f46\u662f\u597d\u666f\u4e0d\u957f\uff0c\u79d1\u738b\u9ad8\u79d1\u968f\u65f6\u63a8\u51fa\u5b9d\u77f3\u624b\u673a\uff0c\u5c31\u4ee4800C\u7a0d\u900a\u989c\u8272\uff0c\u7231\u8fbe\u63a8\u51fa\u7684\u53cc\u5c4f\u7ffb\u76d6\u624b\u673a\uff0c\u66f4\u662f\u8ba9800C\u5bb9\u989c\u5931\u8272\uff0c\u81f4\u4f7f\u4e09\u661f\u624b\u673a\u53bb\u5e74\u7684\u4e2d\u56fd\u5e02\u573a\u76ee\u6807\u624d\u5b8c\u6210\u4e00\u534a\u3002\u4e09\u661f\u76f4\u5230\u6625\u8282\u524d\u540e\u624d\u63a8\u51fa\u81ea\u5df1\u7684\u53cc\u5c4f\u624b\u673a\uff0c\u867d\u8bf4\u5728\u8bbe\u8ba1\u53ca\u914d\u7f6e\u529b\u6c42\u6709\u6240\u7a81\u7834\uff0c\u4f46\u662f\u7231\u8fbe\u7d27\u63a5\u7740\u63a8\u51fa\u8bbe\u8ba1\u66f4\u52a0\u6210\u719f\u5927\u6c14\u7684\u53cc\u5c4f\u624b\u673a\u5347\u7ea7\u7248\u518d\u6b21\u538b\u4f4f\u4e09\u661f\u7684\u5149\u8292\u3002</p><p>\u3000\u3000\u201c\u90fd\u4e0d\u5230\u4e00\u4e2a\u6708\u4e86\uff0c\u201d\u674e\u5728\u6d19\u8f7b\u8f7b\u7684\u4e00\u53f9\uff0c\u5c06\u7b56\u5212\u6848\u9000\u8fd8\u7ed9\u91d1\u5357\u52c7\uff0c\u8bf4\u9053\uff0c\u201c\u8ba9\u4ed6\u4eec\u51c6\u5907\u5f97\u518d\u5145\u5206\u4e00\u4e9b\uff0c\u5ba3\u4f20\u9884\u7b97\u518d\u5bbd\u677e\u4e00\u4e9b\u2026\u2026\u201d\u53c8\u95ee\u91d1\u5357\u52c7\uff0c\u201c\u4f60\u8bf4\u4ed6\u4eec\u8fd9\u6b21\u4f1a\u4e0d\u4f1a\u518d\u5413\u4eba\u4e00\u8df3\uff1f\u201d</p><p>\u3000\u3000\u4e0d\u4f1a\u6240\u6709\u7684\u624b\u673a\u5382\u5546\u90fd\u7b49\u5230\u901a\u8baf\u7f51\u7edc\u6b63\u5f0f\u5347\u7ea7\u4e4b\u540e\u518d\u63a8\u51fa\u65b0\u624b\u673a\uff0c\u785d\u70df\u5df2\u7ecf\u5f00\u59cb\u5f25\u6f2b\uff0c\u5f00\u59cb\u4e89\u593a\u79cb\u5b63\u7684\u5e7f\u544a\u8d44\u6e90\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u95ee\u9898\u771f\u96be\u56de\u7b54\uff0c\u7231\u8fbe\u65e9\u5728\u53bb\u5e74\u5e74\u4e2d\u65f6\u5c31\u5728\u65b0\u52a0\u5761\u3001\u9999\u6e2f\u63a8\u51faGPRS\u624b\u673a\uff0c\u8868\u660e\u7231\u8fbe\u5df2\u7ecf\u505a\u597d\u6280\u672f\u51c6\u5907\uff0c\u5373\u4f7f\u5c06\u4ed6\u4eec\u5728\u65b0\u52a0\u5761\u3001\u9999\u6e2f\u53d1\u552e\u7684GPRS\u624b\u673a\u4e5d\u6708\u4efd\u62ff\u5230\u56fd\u5185\u6765\u4e0a\u5e02\u4e5f\u4e0d\u5bd2\u9178\u3002\u76f8\u6bd4\u8f83\u53bb\u5e74\uff0c\u53c8\u6574\u6574\u8fc7\u53bb\u4e00\u5e74\uff0c\u9526\u6e56\u7684\u51c6\u5907\u5e94\u8be5\u66f4\u5145\u5206\u5427\uff1f</p><p>\u3000\u3000\u674e\u5728\u6d19\u6325\u4e86\u6325\u624b\uff0c\u8ba9\u91d1\u5357\u52c7\u51fa\u53bb\uff0c\u4ed6\u77e5\u9053\u4ed6\u81ea\u5df1\u4e5f\u6709\u4e9b\u4fe1\u5fc3\u4e0d\u8db3\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u897f\u6fb3\u6d32\u505c\u7559\u4e03\u5929\uff0c\u5f20\u606a\u624d\u8ddf\u8bb8\u601d\u4e00\u8d77\u8fd4\u56de\u56fd\u5185\uff0c\u5728\u5317\u4eac\u8f6c\u673a\u65f6\u7ed9\u8096\u745e\u6c11\u622a\u4f4f\u3002</p><p>\u3000\u3000\u516b\u6708\u4e2d\u65ec\uff0c\u5728\u4e2d\u56fd\u79fb\u52a8\u5168\u9762\u5347\u7ea7\u79fb\u52a8\u901a\u8baf\u7f51\u7edc\u524d\u5915\uff0c\u56fd\u5185\u624b\u673a\u5e02\u573a\u65e9\u5df2\u7ecf\u98ce\u8d77\u4e91\u6d8c\uff0c\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u57fa\u4e9a\u3001\u7231\u7acb\u4fe1\u3001\u98de\u5229\u6d66\u7b49\u7535\u5b50\u5de8\u5934\u90fd\u7eb7\u7eb7\u63a8\u51fa\u65b0\u6b3e\u80fd\u5145\u5206\u5e94\u7528\u65b0\u4e00\u4ee3GPRS\u901a\u8baf\u7f51\u7edc\u7279\u6027\u7684\u9ad8\u7aef\u5546\u52a1\u624b\u673a\u3002\u65e9\u5728\u524d\u5e74\uff0c\u5728\u56fd\u4ea7\u624b\u673a\u4ea7\u4e1a\u8fd8\u6ca1\u6709\u53d1\u5c55\u8d77\u6765\u4e4b\u521d\uff0cGPRS\u6982\u5ff5\u8ddf\u65b0\u95fb\u5c31\u6ee1\u5929\u98de\uff0c\u4eca\u5e74\u4e0a\u534a\u5e74\u7684\u65b0\u95fb\u70ed\u5ea6\u5219\u66f4\u9ad8\uff0c\u90fd\u6ca1\u6709\u60f3\u5230\u4e2d\u56fd\u79fb\u52a8\u80fd\u63d0\u524d\u4e09\u4e2a\u6708\u505a\u597d\u7f51\u7edc\u5347\u7ea7\u51c6\u5907\u3002</p><p>\u3000\u3000\u5728\u4e2d\u56fd\u79fb\u52a8\u660e\u786e\u5ba3\u5e03\u4e5d\u6708\u4e0a\u65ec\u5168\u9762\u5347\u7ea7\u66f4\u9ad8\u7ea7\u7684GPRS\u901a\u8baf\u7f51\u7edc\u4e4b\u540e\uff0c\u5230\u4e03\u6708\u4e0b\u65ec\uff0c\u968f\u7740\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u57fa\u4e9a\u7b49\u6d77\u5916\u624b\u673a\u5de8\u5934\u90fd\u52a0\u5927\u65b0\u624b\u673a\u7684\u5e02\u573a\u524d\u671f\u5ba3\u4f20\u529b\u5ea6\uff0c\u56fd\u5185\u624b\u673a\u5e02\u573a\u7684\u9500\u552e\u91cf\u5f00\u59cb\u6ede\u6da8\u3002\u5230\u4e86\u516b\u6708\u4e2d\u4e0a\u65ec\uff0c\u968f\u7740\u8d8a\u6765\u8d8a\u591a\u7684\u6d77\u5916\u624b\u673a\u5de8\u5934\u5728\u4e2d\u56fd\u5883\u5185\u63a8\u51fa\u65b0\u6b3e\u624b\u673a\uff0c\u56fd\u4ea7\u4e2d\u9ad8\u7aef\u624b\u673a\u9500\u91cf\u5df2\u7ecf\u4e0b\u964d\uff0c\u867d\u7136\u8fd8\u4e0d\u662f\u975e\u5e38\u7684\u5389\u5bb3\uff0c\u4e5f\u5df2\u7ecf\u662f\u4e2a\u975e\u5e38\u5371\u9669\u7684\u4fe1\u53f7\u3002</p><p>\u3000\u3000\u201c\u7231\u8fbe\u53ef\u80fd\u6ca1\u6709\u611f\u89c9\uff0c\u8054\u4fe1\u5c31\u5df2\u7ecf\u611f\u89c9\u5230\u538b\u529b\uff0c\u539f\u4ee5\u4e3a\u4e2d\u79fb\u52a8\u4f1a\u63a8\u5ef6\u4e09\u4e2a\u6708\uff0c\u54ea\u91cc\u60f3\u5230\u4f1a\u63d0\u5230\u4e5d\u6708\u5c31\u5168\u9762\u5347\u7ea7\u7f51\u7edc\uff0c\u8981\u662f\u80fd\u591a\u534a\u5e74\u7684\u65f6\u95f4\uff0c\u60c5\u51b5\u5c31\u80fd\u597d\u4e00\u4e9b\u2026\u2026\u201d\u8096\u745e\u6c11\u60cb\u60dc\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u7b11\u4e86\u7b11\uff0c\u8096\u745e\u6c11\u514b\u5236\u7740\u4e0d\u5bf9\u8fd9\u4e9b\u505a\u8bc4\u4ef7\uff0c\u4ed6\u81ea\u7136\u4e5f\u4e0d\u53ef\u80fd\u6028\u58f0\u8f7d\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u5317\u4eac\u57ce\u5357\u7684\u522b\u5885\u91cc\u8ddf\u8096\u745e\u6c11\u89c1\u9762\uff0c\u5f53\u65f6\u665a\u6674\u4e70\u4e86\u4e24\u680b\u5e76\u5217\u7684\u522b\u5885\uff0c\u90fd\u662f\u4f5c\u5728\u5317\u4eac\u77ed\u6682\u505c\u7559\u6240\u7528\uff0c\u5f20\u606a\u5728\u4e00\u680b\u522b\u5885\u91cc\u8ddf\u8096\u745e\u6c11\u89c1\u9762\uff0c\u8bb8\u601d\u5219\u5728\u53e6\u4e00\u680b\u697c\u524d\u7684\u9662\u5b50\u91cc\u770b\u4e66\u6d88\u9063\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u8bf4\u7684\u60c5\u51b5\uff0c\u5f20\u606a\u5df2\u7ecf\u62ff\u5230\u7b2c\u4e00\u624b\u7684\u8d44\u6599\uff0c\u6d77\u5916\u624b\u673a\u4f01\u4e1a\u51ed\u501f\u6280\u672f\u79ef\u7d2f\u4e0a\u7684\u4f18\u52bf\u9488\u5bf9\u56fd\u4ea7\u624b\u673a\u5d1b\u8d77\u800c\u91c7\u53d6\u7684\u673a\u6d77\u6218\u672f\u7b97\u662f\u6b63\u5f0f\u62c9\u5f00\u5e37\u5e55\u4e86\u3002\u5728\u79fb\u52a8\u901a\u8baf\u7f51\u7edc\u5168\u9762\u5347\u7ea7\u524d\u5915\uff0c\u6469\u6258\u7f57\u62c9\u4e0e\u8bfa\u57fa\u4e9a\u90fd\u4e00\u4e3e\u5404\u63a8\u51fa\u4e09\u6b3e\u4e0d\u540c\u6863\u6b21\u7684\u65b0\u6b3e\u624b\u673a\uff0c\u53c8\u662f\u524d\u6240\u672a\u6709\u5ba3\u4f20\u529b\u5ea6\uff0c\u518d\u52a0\u4e0a\u5176\u4ed6\u7d22\u5c3c\u3001\u4e09\u661f\u3001\u98de\u5229\u6d66\u3001\u677e\u4e0b\u3001\u7231\u7acb\u4fe1\u7b49\u6d77\u5916\u624b\u673a\u5de8\u5934\u90fd\u76f8\u7ee7\u63a8\u51fa\u65b0\u6b3e\u624b\u673a\uff0c\u516b\u6708\u4e2d\u4e0a\u65ec\uff0c\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u4e0a\u5e02\u65b0\u673a\u79cd\u7c7b\u8d85\u8fc7\u4e8c\u5341\u79cd\uff0c\u800c\u56fd\u4ea7\u624b\u673a\u9635\u8425\u53ea\u6709\u7231\u8fbe\u3001\u8054\u4fe1\u3001\u79d1\u738b\u9ad8\u79d1\u4e09\u5bb6\u5171\u63a8\u51fa\u56db\u6b3e\u65b0\u624b\u673a\uff0c\u9ad8\u4e0b\u4e4b\u6bd4\uff0c\u9661\u7136\u5206\u660e\uff0c\u56fd\u4ea7\u624b\u673a\u7684\u52a3\u52bf\u5728\u65b0\u4e00\u4ee3\u7684\u901a\u8baf\u6280\u672f\u9762\u524d\u662d\u7136\u82e5\u63ed\u3002</p><p>\u3000\u3000\u4e2d\u79fb\u52a8\u6bd4\u8ba1\u5212\u63d0\u524d\u4e09\u4e2a\u6708\u5b8c\u6210\u901a\u8baf\u7f51\u7edc\u7684\u5347\u7ea7\uff0c\u5728\u666e\u901a\u4eba\u7684\u5370\u8c61\u91cc\uff0c\u4e2d\u79fb\u52a8\u7b97\u662f\u8868\u73b0\u51fa\u8272\u3002\u56e0\u4e3a\u8981\u907f\u514d\u8054\u901a\u8ba1\u5212\u660e\u5e74\u5e74\u4e2d\u5efa\u6210\u7684CDMA\u901a\u8baf\u7f51\u7edc\u7684\u7ade\u4e89\uff0c\u4e2d\u79fb\u52a8\u8d8a\u65e9\u5b8c\u6210\u901a\u8baf\u7f51\u7edc\u5347\u7ea7\uff0c\u5728\u7ade\u4e89\u4e2d\u5c31\u8d8a\u80fd\u5360\u636e\u8d8a\u6709\u5229\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ece\u6613\u4e91\u98de\u90a3\u91cc\u77e5\u9053\u803f\u91cd\u9633\u5bf9\u6b64\u4e8b\u76f8\u5f53\u4e0d\u60a6\uff0c\u5b8c\u5168\u6ca1\u6709\u8981\u5609\u5956\u4e2d\u79fb\u52a8\u7684\u610f\u601d\uff0c\u4e2d\u79fb\u52a8\u7684\u884c\u4e3a\u53ea\u80fd\u8ba9\u56fd\u4ea7\u624b\u673a\u4ea7\u4e1a\u9677\u5165\u88ab\u52a8\u4e4b\u4e2d\uff1b\u53ea\u662f\u4f5c\u4e3a\u5e74\u76c8\u5229\u7834\u4e09\u767e\u4ebf\u7684\u4e2d\u79fb\u52a8\uff0c\u5bf9\u4fe1\u4ea7\u90e8\u5df2\u7ecf\u4e0d\u50cf\u5f80\u6614\u90a3\u6837\u987a\u4ece\u4e86\u3002</p><p>\u3000\u3000\u82e5\u662f\u6240\u6599\u4e0d\u5dee\uff0c\u516b\u6708\u56fd\u4ea7\u624b\u673a\u4f01\u4e1a\u7684\u65e5\u5b50\u5c31\u76f8\u5bf9\u96be\u6328\u4e86\u2014\u2014\u4e0a\u534a\u5e74\u56fd\u5185\u624b\u673a\u5e02\u573a\u7684\u5f62\u52bf\u76f8\u5f53\u597d\uff0c\u8bf8\u591a\u56fd\u4ea7\u624b\u673a\u5382\u5546\u4e3a\u907f\u514d\u53bb\u5e74\u7f3a\u8d27\u5bfc\u81f4\u4f9b\u8d27\u91cf\u4e25\u91cd\u4e0d\u8db3\u7684\u5c40\u9762\uff0c\u90fd\u7eb7\u7eb7\u63d0\u4f9b\u96f6\u90e8\u4ef6\u7684\u5e93\u5b58\u91cf\uff0c\u5938\u5f20\u7684\u751a\u81f3\u6709\u5382\u5bb6\u5907\u5b58\u540c\u4e00\u6b3e\u624b\u673a\u5143\u4ef6\u80fd\u62b5\u4e00\u5e74\u7684\u9500\u552e\u3002\u73b0\u5728\u624b\u673a\u66f4\u65b0\u6362\u4ee3\u9661\u7136\u63d0\u901f\uff0c\u4e0a\u534a\u5e74\u53ef\u80fd\u8fd8\u662f\u70ed\u9500\u673a\u578b\u7684\u624b\u673a\u7acb\u65f6\u51fa\u73b0\u6ede\u9500\uff0c\u5c31\u7b97\u8fd9\u4e2a\u6708\u4f1a\u6709\u624b\u673a\u4f01\u4e1a\u51fa\u73b0\u4e8f\u635f\uff0c\u5f20\u606a\u90fd\u4e0d\u4f1a\u89c9\u5f97\u610f\u5916\u3002</p><p>\u3000\u3000\u8fd9\u6070\u6070\u662f\u8096\u745e\u6c11\u53bb\u5e74\u5c31\u9884\u6599\u7684\u5c40\u9762\uff0c\u8054\u4fe1\u867d\u7136\u65e0\u6cd5\u77ed\u65f6\u95f4\u91cc\u5728\u6280\u672f\u50a8\u5907\u4e0a\u6709\u5927\u7684\u8fdb\u6b65\uff0c\u4f46\u662f\u4e25\u683c\u63a7\u5236\u5e93\u5b58\uff0c\u53c8\u627f\u63a5\u8054\u901a\u8fd1\u534a\u6570\u7684\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u2014\u2014\u4e0a\u534a\u5e74\uff0c\u8054\u901a\u7684\u5b9a\u5236\u624b\u673a\u4e1a\u52a1\u8fd8\u88ab\u5176\u4ed6\u56fd\u4ea7\u624b\u673a\u5382\u5546\u89c6\u4e3a\u9e21\u808b\uff0c\u6b64\u65f6\u5374\u80fd\u7ed9\u8054\u4fe1\u63d0\u4f9b\u7a33\u5b9a\u7684\u5229\u6da6\u6765\u6e90\uff0c\u6210\u4e3a\u8054\u4fe1\u62b5\u5fa1\u8fd9\u6b21\u5371\u673a\u7684\u6700\u575a\u5f3a\u540e\u76fe\u3002</p></article>"}}}]);
